hinsuperobject.biblemap.put("b20_3_18", new hinsuperobject(20,3,18,"जो बुद्धि को ग्रहण कर लेते हैं, उनके लिये वह जीवन का वृक्ष बनती है; और जो उसको पकड़े रहते हैं, वह धन्य हैं।।"));
hinsuperobject.biblemap.put("b20_3_19", new hinsuperobject(20,3,19,"यहोवा ने पृथ्वी की नेव बुद्धि ही से डाली; और स्वर्ग को समझ ही के द्वारा स्थिर किया।"));
hinsuperobject.biblemap.put("b20_3_20", new hinsuperobject(20,3,20,"उसी के ज्ञान के द्वारा गहिरे सागर फूट निकले, और आकाशमण्डल से ओस टपकती है।।"));
hinsuperobject.biblemap.put("b20_3_21", new hinsuperobject(20,3,21,"हे मेरे पुत्रा, ये बातें तेरी दृष्टि की ओट न हाने पाएं; खरी बुद्धि और विवेक की रक्षा कर,"));
hinsuperobject.biblemap.put("b20_3_22", new hinsuperobject(20,3,22,"तब इन से तुझे जीवन मिलेगा, और ये तेरे गले का हार बनेंगे।"));
hinsuperobject.biblemap.put("b20_3_23", new hinsuperobject(20,3,23,"और तू अपने मार्ग पर निडर चलेगा, और तेरे पांव में ठेस न लगेगी।"));
hinsuperobject.biblemap.put("b20_3_24", new hinsuperobject(20,3,24,"जब तू लेटेगा, तब भय न खाएगा, जब तू लेटेगा, तब सुख की नींद आएगी।"));
hinsuperobject.biblemap.put("b20_3_25", new hinsuperobject(20,3,25,"अचानक आनेवाले भय से न डरना, और जब दुष्टों पर विपत्ति आ पड़े, तब न घबराना;"));
hinsuperobject.biblemap.put("b20_3_26", new hinsuperobject(20,3,26,"क्योंकि यहोवा तुझे सहारा दिया करेगा, और तेरे पांव को फन्दे में फंसने न देगा।"));
hinsuperobject.biblemap.put("b20_3_27", new hinsuperobject(20,3,27,"जिनका भला करना चाहिये, यदि तुझ में शक्ति रहे, तो उनका भला करने से न रूकना।।"));
hinsuperobject.biblemap.put("b20_3_28", new hinsuperobject(20,3,28,"यदि तेरे पास देने को कुछ हो, तो अपने पड़ोसी से न कहना कि जा कल फिर आना, कल मैं तुझे दूंगा।"));
hinsuperobject.biblemap.put("b20_3_29", new hinsuperobject(20,3,29,"जब तेरा पड़ोसी तेरे पास बेखटके रहता है, तब उसके विरूद्ध बुरी युक्ति न बान्धना।"));
hinsuperobject.biblemap.put("b20_3_30", new hinsuperobject(20,3,30,"जिस मनुष्य ने तुझ से बुरा व्यवहार न किया हो, उस से अकारण मुक मा खड़ा न करना।"));
hinsuperobject.biblemap.put("b20_3_31", new hinsuperobject(20,3,31,"उपद्रवी पुरूष के विषय में डाह न करना, न उसकी सी चाल चलना;"));
hinsuperobject.biblemap.put("b20_3_32", new hinsuperobject(20,3,32,"क्योंकि यहोवा कुटिल से घृणा करता है, परन्तु वह अपना भेद सीधे लोगों पर खोलता है।।"));
hinsuperobject.biblemap.put("b20_3_33", new hinsuperobject(20,3,33,"दुष्ट के घर पर यहोवा का शाप और धर्मियों के वासस्थान पर उसकी आशीष होती है।"));
hinsuperobject.biblemap.put("b20_3_34", new hinsuperobject(20,3,34,"ठट्ठा करनेवालों से वह निश्चय ठट्ठा करता है और दीनों पर अनुग्रह करता है।"));
hinsuperobject.biblemap.put("b20_3_35", new hinsuperobject(20,3,35,"बुद्धिमान महिमा को पाएंगे, और मूर्खों की बढ़ती अपमान ही की होगी।।"));
hinsuperobject.biblemap.put("b20_4_1", new hinsuperobject(20,4,1,"हे मेरे पुत्रो, पिता की शिक्षा सुनो, और समझ प्राप्त करने में मन लगाओ।"));
hinsuperobject.biblemap.put("b20_4_2", new hinsuperobject(20,4,2,"क्योंकि मैं ने तुम को उत्तम शिक्षा दी है; मेरी शिक्षा को न छोड़ो।"));
hinsuperobject.biblemap.put("b20_4_3", new hinsuperobject(20,4,3,"देखो, मैं भी अपने पिता का पुत्रा था, और माता का अकेला दुलारा था,"));
hinsuperobject.biblemap.put("b20_4_4", new hinsuperobject(20,4,4,"और मेरा पिता मुझे यह कहकर सिखाता था, कि तेरा मन मेरे वचन पर लगा रहे; तू मेरी आज्ञाओं का पालन कर, तब जीवित रहेगा।"));
hinsuperobject.biblemap.put("b20_4_5", new hinsuperobject(20,4,5,"बुद्धि को प्राप्त कर, समझ को भी प्राप्त कर; उनको भूल न जाना, न मेरी बातों को छोड़ना।"));
hinsuperobject.biblemap.put("b20_4_6", new hinsuperobject(20,4,6,"बुद्धि को न छोड़, वह तेरी रक्षा करेगी; उस से प्रीति रख, वह तेरा पहरा देगी।"));
hinsuperobject.biblemap.put("b20_4_7", new hinsuperobject(20,4,7,"बुद्धि श्रेष्ठ है इसलिये उसकी प्राप्ति के लिये यत्न कर; जो कुछ तू प्राप्त करे उसे प्राप्त तो कर परन्तु समझ की प्राप्ति का यत्न घटने न पाए।"));
hinsuperobject.biblemap.put("b20_4_8", new hinsuperobject(20,4,8,"उसकी बड़ाई कर, वह तुझ को बढ़ाएगी; जब तू उस से लिपट जाए, तब वह तेरी महिमा करेगी।"));
hinsuperobject.biblemap.put("b20_4_9", new hinsuperobject(20,4,9,"वह तेरे सिर पर शोभायमान भूषण बान्धेगी; और तुझे सुन्दर मुकुट देगी।।"));
hinsuperobject.biblemap.put("b20_4_10", new hinsuperobject(20,4,10,"हे मेरे पुत्रा, मेरी बातें सुनकर ग्रहण कर, तब तू बहुत वर्ष तब जीवित रहेगा।"));
hinsuperobject.biblemap.put("b20_4_11", new hinsuperobject(20,4,11,"मैं ने तुझे बुद्धि का मार्ग बताया है; और सीधाई के पथ पर चलाया है।"));
hinsuperobject.biblemap.put("b20_4_12", new hinsuperobject(20,4,12,"चलने में तुझे रोक टोक न होगी, और चाहे तू दौड़े, तौभी ठोकर न खाएगा।"));
hinsuperobject.biblemap.put("b20_4_13", new hinsuperobject(20,4,13,"शिक्षा को पकड़े रह, उसे छोड़ न दे; उसकी रक्षा कर, क्योंकि वही तेरा जीवन है।"));
hinsuperobject.biblemap.put("b20_4_14", new hinsuperobject(20,4,14,"दुष्टों की बाट में पांव न धरना, और न बुरे लोगों के मार्ग पर चलना।"));
hinsuperobject.biblemap.put("b20_4_15", new hinsuperobject(20,4,15,"उसे छोड़ दे, उसके पास से भी न चल, उसके निकट से मुड़कर आगे बढ़ जा।"));
hinsuperobject.biblemap.put("b20_4_16", new hinsuperobject(20,4,16,"क्योंकि दुष्ट लोग यदि बुराई न करें, तो उनको नींद नहीं आती; और जब तक वे किसी को ठोकर न खिलाएं, तब तक उन्हें नींद नहीं मिलती।"));
hinsuperobject.biblemap.put("b20_4_17", new hinsuperobject(20,4,17,"वे तो दुष्टता से कमाई हुई रोटी खाते, और उपद्रव के द्वारा पाया हुआ दाखमधु पीते हैं।"));
hinsuperobject.biblemap.put("b20_4_18", new hinsuperobject(20,4,18,"परन्तु धर्मियों की चाल उस चमकती हुई ज्योति के समान है, जिसका प्रकाश दोपहर तक अधिक अधिक बढ़ता रहता है।"));
hinsuperobject.biblemap.put("b20_4_19", new hinsuperobject(20,4,19,"दुष्टों का मार्ग घोर अन्धकारमय है; वे नहीं जानते कि वे किस से ठोकर खाते हैं।।"));
hinsuperobject.biblemap.put("b20_4_20", new hinsuperobject(20,4,20,"हे मेरे पुत्रा मेरे वचन ध्यान धरके सुन, और अपना कान मेरी बातों पर लगा।"));
hinsuperobject.biblemap.put("b20_4_21", new hinsuperobject(20,4,21,"इनको अपनी आंखों की ओट न होने दे; वरन अपने मन में धारण कर।"));
hinsuperobject.biblemap.put("b20_4_22", new hinsuperobject(20,4,22,"क्योंकि जिनकों वे प्राप्त होती हैं, वे उनके जीवित रहने का, और उनके सारे शरीर के चंगे रहने का कारण होती हैं।"));
hinsuperobject.biblemap.put("b20_4_23", new hinsuperobject(20,4,23,"सब से अधिक अपने मन की रक्षा कर; क्योंकि जीवन का मूल द्दॊत वही है।"));
hinsuperobject.biblemap.put("b20_4_24", new hinsuperobject(20,4,24,"टेढ़ी बात अपने मुंह से मत बोल, और चालबाजी की बातें कहना तुझ से दूर रहे।"));
hinsuperobject.biblemap.put("b20_4_25", new hinsuperobject(20,4,25,"तेरी आंखें साम्हने ही की ओर लगी रहें, और तेरी पलकें आगे की ओर खुली रहें।"));
hinsuperobject.biblemap.put("b20_4_26", new hinsuperobject(20,4,26,"अपने पांव धरने के लिये मार्ग को समथर कर, और तेरे सब मार्ग ठीक रहें।"));
hinsuperobject.biblemap.put("b20_4_27", new hinsuperobject(20,4,27,"न तो दहिनी ओर मुढ़ना, और न बाईं ओर; अपने पांव को बुराई के मार्ग पर चलने से हटा ले।।"));
hinsuperobject.biblemap.put("b20_5_1", new hinsuperobject(20,5,1,"हे मेरे पुत्रा, मेरी बुद्धि की बातों पर ध्यान दे, मेरी समझ की ओर कान लगा;"));
hinsuperobject.biblemap.put("b20_5_2", new hinsuperobject(20,5,2,"जिस से तेरा विवेक सुरक्षित बना रहे, और तू ज्ञान के वचनों को थामें रहे।"));
hinsuperobject.biblemap.put("b20_5_3", new hinsuperobject(20,5,3,"क्योंकि पराई स्त्री के ओठों से मधु टपकता है, और उसकी बातें तेल से भी अधिक चिकनी होती हैं;"));
hinsuperobject.biblemap.put("b20_5_4", new hinsuperobject(20,5,4,"परन्तु इसका परिणाम नागदौना सा कडुवा और दोधारी तलवार सा पैना होता है।"));
hinsuperobject.biblemap.put("b20_5_5", new hinsuperobject(20,5,5,"उसके पांव मृत्यु की ओर बढ़ते हैं; और उसके पग अधोलोक तक पहुंचते हैं।।"));
hinsuperobject.biblemap.put("b20_5_6", new hinsuperobject(20,5,6,"इसलिये उसे जीवन का समथर पथ नहीं मिल पाता; उसके चालचलन में चंचलता है, परन्तु उसे वह आप नहीं जानती।।"));
hinsuperobject.biblemap.put("b20_5_7", new hinsuperobject(20,5,7,"इसलिये अब हे मेरे पुत्रों, मेरी सुनो, और मेरी बातों से मुंह न मोड़ो।"));
hinsuperobject.biblemap.put("b20_5_8", new hinsuperobject(20,5,8,"ऐसी स्त्री से दूर ही रह, और उसकी डेवढ़ी के पास भी न जाना;"));
hinsuperobject.biblemap.put("b20_5_9", new hinsuperobject(20,5,9,"कहीं ऐसा न हो कि तू अपना यश औरों के हाथ, और अपना जीवन क्रूर जन के वश में कर दे;"));
hinsuperobject.biblemap.put("b20_5_10", new hinsuperobject(20,5,10,"या पराए तेरी कमाई से अपना पेट भरें, और परेदशी मनुष्य तेरे परिश्रम का फल अपने घर में रखें;"));
hinsuperobject.biblemap.put("b20_5_11", new hinsuperobject(20,5,11,"और तू अपने अन्तिम समय में जब कि तेरा शरीर क्षीण हो जाए तब यह कहकर हाय मारने लगे, कि"));
hinsuperobject.biblemap.put("b20_5_12", new hinsuperobject(20,5,12,"मैं ने शिक्षा से कैसा बैर किया, और डांटनेवाले का कैसा तिरस्कार किया!"));
hinsuperobject.biblemap.put("b20_5_13", new hinsuperobject(20,5,13,"मैं ने अपने गुरूओं की बातें न मानी और अपने सिखानेवालों की ओर ध्यान न लगाया।"));
hinsuperobject.biblemap.put("b20_5_14", new hinsuperobject(20,5,14,"मैं सभा और मण्डली के बीच में प्राय: सब बुराइयों में जा पड़ा।।"));
hinsuperobject.biblemap.put("b20_5_15", new hinsuperobject(20,5,15,"तू अपने ही कुण्ड से पानी, और अपने ही कूंए से सोते का जल पिया करना।"));
hinsuperobject.biblemap.put("b20_5_16", new hinsuperobject(20,5,16,"क्या तेरे सोतों का पानी सड़क में, और तेरे जल की धारा चौकों में बह जाने पाए?"));
hinsuperobject.biblemap.put("b20_5_17", new hinsuperobject(20,5,17,"यह केवल तेरे ही लिये रहे, और तेरे संग औरों के लिये न हो।"));
hinsuperobject.biblemap.put("b20_5_18", new hinsuperobject(20,5,18,"तेरा सोता धन्य रहे; और अपनी जवानी की पत्नी के साथ आनन्दित रह,"));
hinsuperobject.biblemap.put("b20_5_19", new hinsuperobject(20,5,19,"प्रिय हरिणी वा सुन्दर सांभरनी के समान उसके स्तन सर्वदा तुझे संतुष्ट रखे, और उसी का प्रेम नित्य तुझे आकर्षित करता रहे।"));
hinsuperobject.biblemap.put("b20_5_20", new hinsuperobject(20,5,20,"हे मेरे पुत्रा, तू अपरिचित स्त्री पर क्यों मोहित हो, और पराई को क्यों छाती से लगाए?"));
hinsuperobject.biblemap.put("b20_5_21", new hinsuperobject(20,5,21,"क्योंकि मनुष्य के मार्ग यहोवा की दृष्टि से छिपे नहीं हैं, और वह उसके सब मार्गों पर ध्यान करता है।"));
hinsuperobject.biblemap.put("b20_5_22", new hinsuperobject(20,5,22,"दुष्ट अपने ही अधर्म के कर्मों से फंसेगा, और अपने ही पाप के बन्धनों में बन्धा रहेगा।"));
hinsuperobject.biblemap.put("b20_5_23", new hinsuperobject(20,5,23,"वह शिक्षा प्राप्त किए बिना मर जाएगा, और अपनी ही मूर्खता के कारण भटकता रहेगा।।"));
hinsuperobject.biblemap.put("b20_6_1", new hinsuperobject(20,6,1,"हे मरे पुत्रा, यदि तू अपने पड़ोसी का उत्तरदायी हुआ हो, अथवा परदेशी के लिये हाथ पर हाथ मार कर उत्तरदायी हुआ हो,"));
hinsuperobject.biblemap.put("b20_6_2", new hinsuperobject(20,6,2,"तो तू अपने ही मूंह के वचनों से फंसा, और अपने ही मुंह की बातों से पकड़ा गया।"));
hinsuperobject.biblemap.put("b20_6_3", new hinsuperobject(20,6,3,"इसलिये हे मेरे पुत्रा, एक काम कर, अर्थात् तू जो अपने पड़ोसी के हाथ में पड़ चुका है, तो जा, उसको साष्टांग प्रणाम करके मना ले।"));
hinsuperobject.biblemap.put("b20_6_4", new hinsuperobject(20,6,4,"तू ने तो अपनी आखों में नींद, और न अपनी पलकों में झपकी आने दे;"));
hinsuperobject.biblemap.put("b20_6_5", new hinsuperobject(20,6,5,"और अपने आप को हरिणी के समान शिकारी के हाथ से, और चिड़िया के समान चिड़िमार के हाथ से छुड़ा।।"));
hinsuperobject.biblemap.put("b20_6_6", new hinsuperobject(20,6,6,"हे आलसी, च्यूंटियों के पास जा; उनके काम पर ध्यान दे, और बुद्धिमान हो।"));
hinsuperobject.biblemap.put("b20_6_7", new hinsuperobject(20,6,7,"उनके न तो कोई न्यायी होता है, न प्रधान, और न प्रभुता करनेवाला,"));
hinsuperobject.biblemap.put("b20_6_8", new hinsuperobject(20,6,8,"तौभी वे अपना आहार धूपकाल में संचय करती हैं, और कटनी के समय अपनी भोजनवस्तु बटोरती हैं।"));
hinsuperobject.biblemap.put("b20_6_9", new hinsuperobject(20,6,9,"हे आलसी, तू कब तक सोता रहेगा? तेरी नींद कब टूटेगी?"));
hinsuperobject.biblemap.put("b20_6_10", new hinsuperobject(20,6,10,"कुछ और सो लेना, थोड़ी सी नींद, एक और झपकी, थोड़ा और छाती पर हाथों रखे लेटे रहना,"));
hinsuperobject.biblemap.put("b20_6_11", new hinsuperobject(20,6,11,"तब तेरा कंगालपन बटमार की नाई और तेरी घटी हथियारबन्द के समान आ पड़ेगी।।"));
hinsuperobject.biblemap.put("b20_6_12", new hinsuperobject(20,6,12,"ओछे और अनर्थकारी को देखो, वह टेढ़ी टेढ़ी बातें बकता फिरता है,"));
hinsuperobject.biblemap.put("b20_6_13", new hinsuperobject(20,6,13,"वह नैन से सैन और पांव से इशारा, और अपनी अगुंलियों से सकेंत करता है,"));
hinsuperobject.biblemap.put("b20_6_14", new hinsuperobject(20,6,14,"उसके मन में उलट फेर की बातें रहतीं, वह लगातार बुराई गढ़ता है और झगड़ा रगड़ा उत्पन्न करता है।"));
hinsuperobject.biblemap.put("b20_6_15", new hinsuperobject(20,6,15,"इस कारण उस पर विपत्ति अचानक आ पड़ेगी, वह पल भर में ऐसा नाश हो जाएगा, कि बचने का कोई उपाय न रहेगा।।"));
hinsuperobject.biblemap.put("b20_6_16", new hinsuperobject(20,6,16,"छ: वस्तुओं से यहोवा बैर रखता है, वरन सात हैं जिन से उसको धृणा है'"));
hinsuperobject.biblemap.put("b20_6_17", new hinsuperobject(20,6,17,"अर्थात् घमण्ड से चढ़ी हुई आंखें, झूठ बोलनेवाली जीभ, और निर्दोष का लोहू बहानेवाले हाथ,"));
hinsuperobject.biblemap.put("b20_6_18", new hinsuperobject(20,6,18,"अनर्थ कल्पना गढ़नेवाला मन, बुराई करने को वेग दौड़नेवाले पांव,"));
hinsuperobject.biblemap.put("b20_6_19", new hinsuperobject(20,6,19,"झूठ बोलनेवाला साक्षी और भाइयों के बीच में झगड़ा उत्पन्न करनेवाला मनुष्य।"));
hinsuperobject.biblemap.put("b20_6_20", new hinsuperobject(20,6,20,"हे मेरे पुत्रा, मेरी आज्ञा को मान, और अपनी माता की शिक्षा का न तज।"));
hinsuperobject.biblemap.put("b20_6_21", new hinsuperobject(20,6,21,"इन को अपने हृदय में सदा गांठ बान्धे रख; और अपने गले का हार बना ले।"));
hinsuperobject.biblemap.put("b20_6_22", new hinsuperobject(20,6,22,"वह तेरे चलने में तेरी अगुवाई, और सोते समय तेरी रक्षा, और जागते समय तुझ से बातें करेगी।"));
hinsuperobject.biblemap.put("b20_6_23", new hinsuperobject(20,6,23,"आज्ञा तो दीपक है और शिक्षा ज्योति, और सिखानेवाले की डांट जीवन का मार्ग है,"));
hinsuperobject.biblemap.put("b20_6_24", new hinsuperobject(20,6,24,"ताकि तुझ को बुरी स्त्री से बचाए और पराई स्त्री की चिकनी चुपड़ी बातों से बचाए।"));
hinsuperobject.biblemap.put("b20_6_25", new hinsuperobject(20,6,25,"उसकी सुन्दरता देखकर अपने मन में उसकी अभिलाषा न कर; वह तुझे अपने कटाक्ष से फंसाने न पाए;"));
hinsuperobject.biblemap.put("b20_6_26", new hinsuperobject(20,6,26,"क्योंकि वेश्यागमन के कारण मनुष्य टुकड़ों का भिखारी हो जाता है, परन्तु व्यभिचारिणी अनमोल जीवन का अहेर कर लेती है।"));
hinsuperobject.biblemap.put("b20_6_27", new hinsuperobject(20,6,27,"क्या हो सकता है कि कोई अपनी छाती पर आग रख ले; और उसके कपड़े न जलें?"));
hinsuperobject.biblemap.put("b20_6_28", new hinsuperobject(20,6,28,"क्या हो सकता है कि कोई अंगारे पर चले, और उसके पांव न झुलसें?"));
hinsuperobject.biblemap.put("b20_6_29", new hinsuperobject(20,6,29,"जो पराई स्त्री के पास जाता है, उसकी दशा ऐसी है; वरन जो कोई उसको छूएगा वह दण्ड से न बचेगा।"));
hinsuperobject.biblemap.put("b20_6_30", new hinsuperobject(20,6,30,"जो चारे भूख के मारे अपना पेट भरने के लिये चोरी करे, उसके तो लोग तुच्छ नहीं जानते;"));
hinsuperobject.biblemap.put("b20_6_31", new hinsuperobject(20,6,31,"तौभी यदि वह पकड़ा जाए, तो उसको सातगुणा भर देना पड़ेगा; वरन अपने घर का सारा धन देना पड़ेगा।"));
hinsuperobject.biblemap.put("b20_6_32", new hinsuperobject(20,6,32,"परनतु जो परस्त्रीगमन करता है वह निरा निर्बुद्ध है; जो अपने प्राणों को नाश करना चाहता है, वह ऐसा करता है।।"));
hinsuperobject.biblemap.put("b20_6_33", new hinsuperobject(20,6,33,"उसको घायल और अपमानित होना पड़ेगा, और उसकी नामधराई कभी न मिटेगी।"));
hinsuperobject.biblemap.put("b20_6_34", new hinsuperobject(20,6,34,"क्योंकि जलन से पुरूष बहुत ही क्रोधित हो जाता है, और पलटा लेने के दिन वह कुछ कोमलता नहीं दिखाता।"));
hinsuperobject.biblemap.put("b20_6_35", new hinsuperobject(20,6,35,"वह घूस पर दृष्टि न करेगा, और चाहे तू उसको बहुत कुछ दे, तौभी वह न मानेगा।।"));
hinsuperobject.biblemap.put("b20_7_1", new hinsuperobject(20,7,1,"हे मेरे पुत्रा, मेरी बातों को माना कर, और मेरी आज्ञाओं को अपने मन में रख छोड़।"));
hinsuperobject.biblemap.put("b20_7_2", new hinsuperobject(20,7,2,"मेरी आज्ञाओं को मान, इस से तू जीवित रहेगा, और मेरी शिक्षा को अपनी आंख की पुतली जान;"));
hinsuperobject.biblemap.put("b20_7_3", new hinsuperobject(20,7,3,"उनको अपनी उंगलियों में बान्ध, और अपने हृदय की पटिया पर लिख ले।"));
hinsuperobject.biblemap.put("b20_7_4", new hinsuperobject(20,7,4,"बुद्धि से कह कि, तू मेरी बहिन है, और समझ को अपनी साथिन बना;"));
hinsuperobject.biblemap.put("b20_7_5", new hinsuperobject(20,7,5,"तब तू पराई स्त्री से बचेगा, जो चिकनी चुपड़ी बातें बोलती है।।"));
hinsuperobject.biblemap.put("b20_7_6", new hinsuperobject(20,7,6,"मैं ने एक दिन अपने घर की खिड़की से, अर्थात् अपने झरोखे से झांका,"));
hinsuperobject.biblemap.put("b20_7_7", new hinsuperobject(20,7,7,"तब मैं ने भोले लोगों में से एक निर्बुद्धि जवान को देखा;"));
hinsuperobject.biblemap.put("b20_7_8", new hinsuperobject(20,7,8,"वह उस स्त्री के घर के कोने के पास की सड़क पर चला जाता था, और उस ने उसके घर का मार्ग लिया।"));
hinsuperobject.biblemap.put("b20_7_9", new hinsuperobject(20,7,9,"उस समय दिन ढल गया, और संध्याकाल आ गया था, वरन रात का घोर अन्धकार छा गया था।"));
hinsuperobject.biblemap.put("b20_7_10", new hinsuperobject(20,7,10,"और उस से एक स्त्री मिली, जिस का भेष वेश्या का सा था, और वह बड़ी धूर्त थी।"));
hinsuperobject.biblemap.put("b20_7_11", new hinsuperobject(20,7,11,"वह शान्तिरहित और चंचल थी, और अपने घर में न ठहरती थी;"));
hinsuperobject.biblemap.put("b20_7_12", new hinsuperobject(20,7,12,"कभी वह सड़क में, कभी चौक में पाई जाती थी, और एक एक कोने पर वह बाट जोहती थी।"));
hinsuperobject.biblemap.put("b20_7_13", new hinsuperobject(20,7,13,"तब उस ने उस जवान को पकड़कर चूमा, और निर्लज्जता की चेष्टा करके उस से कहा,"));
hinsuperobject.biblemap.put("b20_7_14", new hinsuperobject(20,7,14,"मुझे मेलबलि चढ़ाने थे, और मैं ने अपनी मन्नते आज ही पूरी की हैं;"));
hinsuperobject.biblemap.put("b20_7_15", new hinsuperobject(20,7,15,"इसी कारण मैं तुझ से भेंट करने को निकली, मैं तेरे दर्शन की खोजी थी, सो अभी पाया है।"));
hinsuperobject.biblemap.put("b20_7_16", new hinsuperobject(20,7,16,"मैं ने अपने पलंग के बिछौने पर मि  के बेलबूटेवाले कपड़े बिछाए हैं;"));
hinsuperobject.biblemap.put("b20_7_17", new hinsuperobject(20,7,17,"मैं ने अपने बिछौने पर गन्घरस, अगर और दालचीनी छिड़की है।"));
hinsuperobject.biblemap.put("b20_7_18", new hinsuperobject(20,7,18,"इसलिये अब चल हम प्रेम से भोर तक जी बहलाते रहें; हम परस्पर की प्रीति से आनन्दित रहें।"));
hinsuperobject.biblemap.put("b20_7_19", new hinsuperobject(20,7,19,"क्योंकि मेरा पति घर में नहीं है; वह दूर देश को चला गया है;"));
hinsuperobject.biblemap.put("b20_7_20", new hinsuperobject(20,7,20,"वह चान्दी की थैली ले गया है; और पूर्णमासी को लौट आएगा।।"));
hinsuperobject.biblemap.put("b20_7_21", new hinsuperobject(20,7,21,"ऐसी ही बातें कह कहकर, उस ने उसको अपनी प्रबल माया में फंसा लिया; और अपनी चिकनी चुपड़ी बातों से उसको अपने वश में कर लिया।"));
hinsuperobject.biblemap.put("b20_7_22", new hinsuperobject(20,7,22,"वह तुरन्त उसके पीछे हो लिया, और बैल कसाई- खाने को, वा जैासे बेड़ी पहिने हुए कोई मूढ़ ताड़ना पाने को जाता है।"));
hinsuperobject.biblemap.put("b20_7_23", new hinsuperobject(20,7,23,"अन्त में उस जवान का कलेजा तीर से बेधा जाएगा; वह उस चिड़िया के समान है जो फन्दे की ओर वेग से उड़े और न जानती हो कि उस में मेरे प्राण जाएंगे।।"));
hinsuperobject.biblemap.put("b20_7_24", new hinsuperobject(20,7,24,"अब हे मेरे पुत्रों, मेरी सुनो, और मेरी बातों पर मन लगाओ।"));
hinsuperobject.biblemap.put("b20_7_25", new hinsuperobject(20,7,25,"तेरा मन ऐसी स्त्री के मार्ग की ओर न फिरे, और उसकी डगरों में भूल कर न जाना;"));
hinsuperobject.biblemap.put("b20_7_26", new hinsuperobject(20,7,26,"क्योंकि बहुत लोग उस से मारे पड़े हैं; उसके घात किए हुओं की एक बड़ी संख्या होगी।"));
hinsuperobject.biblemap.put("b20_7_27", new hinsuperobject(20,7,27,"उसका घर अधोलोक का मार्ग है, वह मृत्यु के घर में पहुंचाता है।।"));
hinsuperobject.biblemap.put("b20_8_1", new hinsuperobject(20,8,1,"क्या बुद्धि नहीं पुकारती है, क्या समझ ऊंचे शब्द से नहीं बोलती है?"));
hinsuperobject.biblemap.put("b20_8_2", new hinsuperobject(20,8,2,"वह तो ऊंचे स्थानों पर मार्ग की एक ओर ओर तिर्मुहानियों में खड़ी होती है;"));
hinsuperobject.biblemap.put("b20_8_3", new hinsuperobject(20,8,3,"फाटकों के पास नगर के पैठाव में, और द्वारों ही में वह ऊंचे स्वर से कहती है,"));
hinsuperobject.biblemap.put("b20_8_4", new hinsuperobject(20,8,4,"हे मनुष्यों, मैं तुम को पुकारती हूं, और मेरी बात सब आदमियों के लिये है।"));
hinsuperobject.biblemap.put("b20_8_5", new hinsuperobject(20,8,5,"हे भोलो, चतुराई सीखो; और हे मूर्खों, अपने मन में समझ लों"));
hinsuperobject.biblemap.put("b20_8_6", new hinsuperobject(20,8,6,"सुनो, क्योंकि मैं उत्तम बातें कहूंगी, और जब मुंह खोलूंगी, तब उस से सीधी बातें निकलेंगी;"));
hinsuperobject.biblemap.put("b20_8_7", new hinsuperobject(20,8,7,"क्योंकि मुझ से सच्चाई की बातों का वर्णन होगा; दुष्टता की बातों से मुझ को घृणा आती है।।"));
hinsuperobject.biblemap.put("b20_8_8", new hinsuperobject(20,8,8,"मेरे मुंह की सब बातें धर्म की होती हैं, उन में से कोई टेढ़ी वा उलट फेर की बात नहीं निकलती है।"));
hinsuperobject.biblemap.put("b20_8_9", new hinsuperobject(20,8,9,"समझवाले के लिये वे सब सहज, और ज्ञान के प्राप्त करनेवालों के लिये अति सीधी हैं।"));
hinsuperobject.biblemap.put("b20_8_10", new hinsuperobject(20,8,10,"चान्दी नहीं, मेरी शिक्षा ही को लो, और उत्तम कुन्दन से बढ़कर ज्ञान को ग्रहण करो।"));
hinsuperobject.biblemap.put("b20_8_11", new hinsuperobject(20,8,11,"क्योंकि बुद्धि, मूंगे से भी अच्छी है, और सारी मनभावनी वस्तुओं में कोई भी उसके तुल्य नहीं है।"));
hinsuperobject.biblemap.put("b20_8_12", new hinsuperobject(20,8,12,"मैं जो बुद्धि हूं, सो चतुराई में वास करती हूं, और ज्ञान और विवेक को प्राप्त करती हूं।"));
hinsuperobject.biblemap.put("b20_8_13", new hinsuperobject(20,8,13,"यहोवा का भय मानना बुराई से बैर रखना है। घमण्ड, अंहकार, और बुरी चाल से, और उलट फेर की बात से भी मैं बैर रखती हूं।"));
hinsuperobject.biblemap.put("b20_8_14", new hinsuperobject(20,8,14,"उत्तम युक्ति, और खरी बुद्धि मेरी ही है, मैं तो समझ हूं, और पराक्रम भी मेरा है।"));
hinsuperobject.biblemap.put("b20_8_15", new hinsuperobject(20,8,15,"मेरे ही द्वारा राजा राज्य करते हैं, और अधिकारी धर्म से विचार करते हैं;"));
hinsuperobject.biblemap.put("b20_8_16", new hinsuperobject(20,8,16,"मेरे ही द्वारा राजा हाकिम और रईस, और पृथ्वी के सब न्यायी शासन करते हैं।"));
hinsuperobject.biblemap.put("b20_8_17", new hinsuperobject(20,8,17,"जो मुझ से प्रेम रखते हैं, उन से मैं भी प्रेम रखती हूं, और जो मुझ को यत्न से तड़के उठकर खोजते हैं, वे मुझे पाते हैं।"));
hinsuperobject.biblemap.put("b20_8_18", new hinsuperobject(20,8,18,"धन और प्रतिष्ठा मेरे पास है, वरन ठहरनेवाला धन और धर्म भी हैं।"));
hinsuperobject.biblemap.put("b20_8_19", new hinsuperobject(20,8,19,"मेरा फल चोखे सोने से, वरन कुन्दन से भी उत्तम है, और मेरी उपज उत्तम चान्दी से अच्छी है।"));
hinsuperobject.biblemap.put("b20_8_20", new hinsuperobject(20,8,20,"मैं धर्म की बाट में, और न्याय की डगरों के बीच में चलती हूं,"));
hinsuperobject.biblemap.put("b20_8_21", new hinsuperobject(20,8,21,"जिस से मैं अपने प्रेमियों को परमार्थ के भागी करूं, और उनके भण्डारों को भर दूं।"));
hinsuperobject.biblemap.put("b20_8_22", new hinsuperobject(20,8,22,"यहोवा ने मुझे काम करते के आरम्भ में, वरन अपने प्राचीनकाल के कामों से भी पहिले उत्पन्न किया।"));
hinsuperobject.biblemap.put("b20_8_23", new hinsuperobject(20,8,23,"मैं सदा से वरन आदि ही से पृथ्वी की सृष्टि के पहिले ही से ठहराई गई हूं।"));
hinsuperobject.biblemap.put("b20_8_24", new hinsuperobject(20,8,24,"जब न तो गहिरा सागर था, और न जल के सोते थे तब ही से मैं उत्पन्न हुई।"));
hinsuperobject.biblemap.put("b20_8_25", new hinsuperobject(20,8,25,"जब पहाड़ वा पहाड़ियां स्थिर न की गई थीं,"));
hinsuperobject.biblemap.put("b20_8_26", new hinsuperobject(20,8,26,"जब यहोवा ने न तो पृथ्वी और न मैदान, न जगत की धूलि के परमाणु बनाए थे, इन से पहिले मैं उत्पन्न हुई।"));
hinsuperobject.biblemap.put("b20_8_27", new hinsuperobject(20,8,27,"जब उस ने अकाश को स्थिर किया, तब मैं वहां थी, जब उस ने गहिरे सागर के ऊपर आकाशमण्डल ठहराया,"));
hinsuperobject.biblemap.put("b20_8_28", new hinsuperobject(20,8,28,"जब उस ने आकाशमण्डल को ऊपर से स्थिर किया, और गहिरे सागर के सोते फूटने लगे,"));
hinsuperobject.biblemap.put("b20_8_29", new hinsuperobject(20,8,29,"जब उस ने समुद्र का सिवाना ठहराया, कि जल उसकी आज्ञा का उल्लंघन न कर सके, और जब वह पृथ्वी की नेव की डोरी लगाता था,"));
hinsuperobject.biblemap.put("b20_8_30", new hinsuperobject(20,8,30,"तब मैं कारीगर सी उसके पास थी; और प्रति दिन मैं उसकी प्रसन्नता थी, और हस समय उसके साम्हने आनन्दित रहती थी।"));
hinsuperobject.biblemap.put("b20_8_31", new hinsuperobject(20,8,31,"मैं उसकी बसाई हुई पृथ्वी से प्रसन्न थी और मेरा सुख मनुष्यों की संगति से होता था।।"));
hinsuperobject.biblemap.put("b20_8_32", new hinsuperobject(20,8,32,"इसलिये अब हे मेरे पुत्रों, मेरी सुनो; क्या ही धन्य हैं वे जो मेरे मार्ग को पकड़े रहते हैं।"));
hinsuperobject.biblemap.put("b20_8_33", new hinsuperobject(20,8,33,"शिक्षा को सुनो, और बुद्धिमान हो जाओ, उसके विषय में अनसुनी न करो।"));
hinsuperobject.biblemap.put("b20_8_34", new hinsuperobject(20,8,34,"क्या ही धन्य है वह मनुष्य जो मेरी सुनता, वरन मेरी डेवढ़ी पर प्रति दिन खड़ा रहता, और मेरे द्वारों के खंभों के पास दृष्टि लगाए रहता है।"));
hinsuperobject.biblemap.put("b20_8_35", new hinsuperobject(20,8,35,"क्योंकि जो मुझे पाता है, वह जीवन को पाता है, और यहोवा उस से प्रसन्न होता है।"));
hinsuperobject.biblemap.put("b20_8_36", new hinsuperobject(20,8,36,"परन्तु जो मेरा अपराध करता है, वह अपने ही पर उपद्रव करता है; जितने मुझ से बैर रखते वे मृत्यु से प्रीति रखते हैं।।"));
hinsuperobject.biblemap.put("b20_9_1", new hinsuperobject(20,9,1,"बुद्धि ने अपना घर बनाया और उसके सातों खंभे गढ़े हुए हैं।"));
hinsuperobject.biblemap.put("b20_9_2", new hinsuperobject(20,9,2,"उस ने अपने पशु वध करके, अपने दाखमधु में मसाला मिलाया है, और अपनी मेज़ लगाई है।"));
hinsuperobject.biblemap.put("b20_9_3", new hinsuperobject(20,9,3,"उस ने अपनी सहेलियां, सब को बुलाने के लिये भेजी है; वह नगर के ऊंचे स्थानों की चोटी पर पुकारती है,"));
hinsuperobject.biblemap.put("b20_9_4", new hinsuperobject(20,9,4,"जो कोई भोला हे वह मुड़कर यहीं आए! और जो निर्बुद्धि है, उस से वह कहती है,"));
hinsuperobject.biblemap.put("b20_9_5", new hinsuperobject(20,9,5,"आओ, मेरी रोटी खाओ, और मेरे मसाला मिलाए हुए दाखमधु को पीओ।"));
hinsuperobject.biblemap.put("b20_9_6", new hinsuperobject(20,9,6,"भोलों का संग छोड़ो, और जीवित रहो, समझ के मार्ग में सीधे चलो।"));
hinsuperobject.biblemap.put("b20_9_7", new hinsuperobject(20,9,7,"जो ठट्ठा करनेवाले को शिक्षा देता है, सो अपमानित होता है, और जो दुष्ट जन को डांटता है वह कलंकित होता है।।"));
hinsuperobject.biblemap.put("b20_9_8", new hinsuperobject(20,9,8,"ठट्ठा करनेवाले को न डांट ऐसा न हो कि वह तुझ से बैर रखे, बुद्धिमान को डांट, वह तो तुझ से प्रेम रखेगा।"));
hinsuperobject.biblemap.put("b20_9_9", new hinsuperobject(20,9,9,"बुद्धिमान को शिक्षा दे, वह अधिक बुद्धिमान होगा; धर्मी को चिता दे, वह अपनी विद्या बढ़ाएगा।"));
hinsuperobject.biblemap.put("b20_9_10", new hinsuperobject(20,9,10,"यहोवा का भय मानना बुद्धि का आरम्भ है, और परमपवित्रा ईश्वर को जानना ही समझ है।"));
hinsuperobject.biblemap.put("b20_9_11", new hinsuperobject(20,9,11,"मेरे द्वारा तो तेरी आयु बढ़ेगी, और तेरे जीवन के वर्ष अधिक होंगे।"));
hinsuperobject.biblemap.put("b20_9_12", new hinsuperobject(20,9,12,"यदि तू बुद्धिमान हो, ते बुद्धि का फल तू ही भोगेगा; और यदि तू ठट्ठा करे, तो दण्ड केवल तू ही भोगेगा।।"));
hinsuperobject.biblemap.put("b20_9_13", new hinsuperobject(20,9,13,"मूर्खतारूपी स्त्री हौरा मचानेवाली है; वह तो भोली है, और कुछ नहीं जानती।"));
hinsuperobject.biblemap.put("b20_9_14", new hinsuperobject(20,9,14,"वह अपने घर के द्वार में, और नगर के ऊंचे स्थानों में मचिया पर बैठी हुई"));
hinsuperobject.biblemap.put("b20_9_15", new hinsuperobject(20,9,15,"जो बटोही अपना अपना मार्ग पकड़े हुए सीधे चले जाते हैं, उनको यह कह कहकर पुकारती है,"));
hinsuperobject.biblemap.put("b20_9_16", new hinsuperobject(20,9,16,"जो कोई भोला है, वह मुड़कर यहीं आए; जो निर्बुद्धि है, उस से वह कहती है,"));
hinsuperobject.biblemap.put("b20_9_17", new hinsuperobject(20,9,17,"चोरी का पानी मीठा होता है, और लुके छिपे की रोटी अच्छी लगती है।"));
hinsuperobject.biblemap.put("b20_9_18", new hinsuperobject(20,9,18,"और वह नहीं जानता है, कि वहां मरे हुए पड़े हैं, और उस स्त्री के नेवतहारी अधोलोक के निचले स्थानों में पहुंचे हैं।।"));
hinsuperobject.biblemap.put("b20_10_1", new hinsuperobject(20,10,1,"सुलैमान के नीतिवचन।। बुद्धिमान पुत्रा से पिता आनन्दित होता है, परन्तु मूर्ख पुत्रा के कारण माता उदास रहती है।"));
hinsuperobject.biblemap.put("b20_10_2", new hinsuperobject(20,10,2,"दुष्टों के रखे हुए धन से लाभ नही होता, परन्तु धर्म के कारण मृत्यु से बचाव होता है।"));
hinsuperobject.biblemap.put("b20_10_3", new hinsuperobject(20,10,3,"धर्मी को यहोवा भूखों मरने नहीं देता, परन्तु दुष्टों की अभिलाषा वह पूरी होने नहीं देता।"));
hinsuperobject.biblemap.put("b20_10_4", new hinsuperobject(20,10,4,"जो काम में ढिलाई करता है, वह निर्धन हो जाता है, परन्तु काममाजु लोग अपने हाथों के द्वारा धनी होते हैं।"));
hinsuperobject.biblemap.put("b20_10_5", new hinsuperobject(20,10,5,"जो बेटा धूपकाल में बटोरता है वह बुद्धि से काम करनेवाला है, परन्तु जो बेटा कटनी के समय भारी नींद में पड़ा रहता है, वह लज्जा का कारण होता है।"));
hinsuperobject.biblemap.put("b20_10_6", new hinsuperobject(20,10,6,"धर्मी पर बहुत से आर्शीवाद होते हैं, परन्तु उपद्रव दुष्टों का मुंह छा लेता है।"));
hinsuperobject.biblemap.put("b20_10_7", new hinsuperobject(20,10,7,"धर्मी को स्मरण करके लोग आशीर्वाद देते हैं, परन्तु दुष्टों का नाम मिट जाता है।"));
hinsuperobject.biblemap.put("b20_10_8", new hinsuperobject(20,10,8,"जो बुद्धिमान है, वह आज्ञाओं को स्वीकार करता है, परन्तु जो बकवादी और मूढ़ है, वह पछाड़ खाता है।"));
hinsuperobject.biblemap.put("b20_10_9", new hinsuperobject(20,10,9,"जो खराई से चलता है वह निडर चलता है, परन्तु जो टेढ़ी चाल चलता है उसकी चाल प्रगट हो जाती है।"));
hinsuperobject.biblemap.put("b20_10_10", new hinsuperobject(20,10,10,"जो नैन से सैन करता है उस से औरों को दुख मिलता है, और जो बकवादी और मूढ़ है, वह पछाड़ खाता है।"));
hinsuperobject.biblemap.put("b20_10_11", new hinsuperobject(20,10,11,"धर्मी का मुंह तो जीवन का सोता है, परन्तु उपद्रव दुष्टों का मुंह छा लेता है।"));
hinsuperobject.biblemap.put("b20_10_12", new hinsuperobject(20,10,12,"बैर से तो झगड़े उत्पन्न होते हैं, परन्तु प्रेम से सब अपराध ढंप जाते हैं।"));
hinsuperobject.biblemap.put("b20_10_13", new hinsuperobject(20,10,13,"समझवालों के वचनों में बुद्धि पाई जाती है, परन्तु निर्बुद्धि की पीठ के लिये कोड़ा है।"));
hinsuperobject.biblemap.put("b20_10_14", new hinsuperobject(20,10,14,"बुद्धिमान लोग ज्ञान को रख छोड़ते हैं, परन्तु मूढ़ के बोलने से विनाश निकट आता है।"));
hinsuperobject.biblemap.put("b20_10_15", new hinsuperobject(20,10,15,"धनी का धन उसका दृढ़ नगर है, परन्तु कंगाल लोग निर्धन होने के कारण विनाश होते हैं।"));
hinsuperobject.biblemap.put("b20_10_16", new hinsuperobject(20,10,16,"धर्मी का परिश्रम जीवन के लिये होता है, परन्तु दुष्ट के लाभ से पाप होता है।"));
hinsuperobject.biblemap.put("b20_10_17", new hinsuperobject(20,10,17,"जो शिक्षा पर चलता वह जीवन के मार्ग पर है, परन्तु जो डांट से मुंह मोड़ता, वह भटकता है।"));
hinsuperobject.biblemap.put("b20_10_18", new hinsuperobject(20,10,18,"जो बैर को छिपा रखता है, वह झूठ बोलता है, और जो अपवाद फैलाता है, वह मूर्ख है।"));
hinsuperobject.biblemap.put("b20_10_19", new hinsuperobject(20,10,19,"जहां बहुत बातें होती हैं, वहां अपराध भी होता है, परन्तु जो अपने मुंह को बन्द रखता है वह बुद्धि से काम करता है।"));
hinsuperobject.biblemap.put("b20_10_20", new hinsuperobject(20,10,20,"धर्मी के वचन तो उत्तम चान्दी हैं; परन्तु दुष्टों का मन बहुत हलका होता है।"));
hinsuperobject.biblemap.put("b20_10_21", new hinsuperobject(20,10,21,"धर्मी के वचनों से बहुतों का पालनपोषण होता है, परन्तु मूढ़ लोग निर्बुद्धि होने केस्रारण मर जाते हैं।"));
hinsuperobject.biblemap.put("b20_10_22", new hinsuperobject(20,10,22,"धन यहोवा की आशीष ही से मिलता है, और वह उसके साथ दु:ख नहीं मिलाता।"));
hinsuperobject.biblemap.put("b20_10_23", new hinsuperobject(20,10,23,"मूर्ख को तो महापाप करना हंसी की बात जान पड़ती है, परन्तु समझवाले पुरूष में बुद्धि रहती है।"));
hinsuperobject.biblemap.put("b20_10_24", new hinsuperobject(20,10,24,"दुष्ट जन जिस विपत्ति से डरता है, वह उस पर आ पड़ती है, परन्तु धर्मियों की लालसा पूरी होती है।"));
hinsuperobject.biblemap.put("b20_10_25", new hinsuperobject(20,10,25,"बवण्डर निकल जाते ही दुष्ट जन लोप हो जाता है, परन्तु धर्मी सदा लों स्थिर है।"));
hinsuperobject.biblemap.put("b20_10_26", new hinsuperobject(20,10,26,"जैसे दांत को सिरका, और आंख को धूंआ, वैसे आलसी उनको लगात है जो उसको कहीं भेजते हैं।"));
hinsuperobject.biblemap.put("b20_10_27", new hinsuperobject(20,10,27,"यहोवा के भय मानने से आयु बढ़ती है, परन्तु दुष्टों का जीवन थोड़े ही दिनों का होता है।"));
hinsuperobject.biblemap.put("b20_10_28", new hinsuperobject(20,10,28,"धर्मियों को आशा रखने में आनन्द मिलता है, परन्तु दुष्टों की आशा टूट जाती है।"));
hinsuperobject.biblemap.put("b20_10_29", new hinsuperobject(20,10,29,"यहोवा खरे मनुष्य का गढ़ ठहरता है, परन्तु अनर्थकारियों का विनाश होता है।"));
hinsuperobject.biblemap.put("b20_10_30", new hinsuperobject(20,10,30,"धर्मी सदा अटल रहेगा, परन्तु दुष्ट पृथ्वी पर बसने न पाएंगे।"));
hinsuperobject.biblemap.put("b20_10_31", new hinsuperobject(20,10,31,"धर्मी के मुंह से बुद्धि टपकती है, पर उलट फेर की बात कहने वाले की जीभ काटी जायेगी।"));
hinsuperobject.biblemap.put("b20_10_32", new hinsuperobject(20,10,32,"धर्मी गहणयोग्य बात समझ कर बोलता है, परन्तु दुष्टों के मुंह से उलट फेर की बातें निकलती हैं।।"));
hinsuperobject.biblemap.put("b20_11_1", new hinsuperobject(20,11,1,"छल के तराजू से यहोवा को घृणा आती है, परन्तु वह पूरे बटखरे से प्रसन्न होता है।"));
hinsuperobject.biblemap.put("b20_11_2", new hinsuperobject(20,11,2,"जब अभिमान होता, तब अपमान भी होता है, परन्तु नम्र लोगों में बुद्धि होती है।"));
hinsuperobject.biblemap.put("b20_11_3", new hinsuperobject(20,11,3,"सीधे लोग अपनी खराई से अगुवाई पाते हैं, परन्तु विश्वासघाती अपने कपट से विनाश होते हैं।"));
hinsuperobject.biblemap.put("b20_11_4", new hinsuperobject(20,11,4,"कोप के दिन धन से तो कुछ लाभ नहीं होता, परन्तु धर्म मृत्यु से भी बचाता है।"));
hinsuperobject.biblemap.put("b20_11_5", new hinsuperobject(20,11,5,"खरे मनुष्य का मार्ग धर्म के कारण सीधा होता है, परन्तु दुष्ट अपनी दुष्टता के कारण गिर जाता है।"));
hinsuperobject.biblemap.put("b20_11_6", new hinsuperobject(20,11,6,"सीधे लोगों को बचाव उनके धर्म के कारण होता है, परन्तु विश्वासघाती लोग अपनी ही दुष्टता में फंसते हैं।"));
hinsuperobject.biblemap.put("b20_11_7", new hinsuperobject(20,11,7,"जब दुष्ट मरता, तब उसकी आशा टूट जाती है, और अधर्मी की आशा व्यर्थ होती है।"));
hinsuperobject.biblemap.put("b20_11_8", new hinsuperobject(20,11,8,"धर्मी विपत्ति से छूट जाता है, परन्तु दुष्ट उसी विपत्ति में पड़ जाता है।"));
hinsuperobject.biblemap.put("b20_11_9", new hinsuperobject(20,11,9,"भक्तिहीन जन अपने पड़ोसी को अपने मुंह की बात से बिगाड़ता है, परन्तु धर्मी लोग ज्ञान के द्वारा बचते हैं।"));
hinsuperobject.biblemap.put("b20_11_10", new hinsuperobject(20,11,10,"जब धर्मियों का कल्याण होता है, तब नगर के लोग प्रसन्न होते हैं, परन्तु जब दुष्ट नाश होते, तब जय- जयकार होता है।"));
hinsuperobject.biblemap.put("b20_11_11", new hinsuperobject(20,11,11,"सीधे लोगों के आशीर्वाद से नगर की बढ़ती होती है, परन्तु दुष्टों के मुंह की बात से वह ढाया जाता है।"));
hinsuperobject.biblemap.put("b20_11_12", new hinsuperobject(20,11,12,"जो अपने पड़ोसी को तुच्छ जानता है, वह निर्बुद्धि है, परन्तु समझदार पुरूष चुपचाप रहता है।"));
hinsuperobject.biblemap.put("b20_11_13", new hinsuperobject(20,11,13,"जो लुतराई करता फिरता वह भेद प्रगट करता है, परन्तु विश्वासयोग्य मनुष्य बात को छिपा रखता है।"));
hinsuperobject.biblemap.put("b20_11_14", new hinsuperobject(20,11,14,"जहां बुद्धि की युक्ति नहीं, वहां प्रजा विपत्ति में पड़ती है; परन्तु सम्मति देनेवालों की बहुतायत के कारण बचाव होता है।"));
hinsuperobject.biblemap.put("b20_11_15", new hinsuperobject(20,11,15,"जो परदेशी का उत्तरदायी होता है, वह बड़ा दु:ख उठाता है, परन्तु जो उत्तरदायित्व से घृणा करता, वह निडर रहता है।"));
hinsuperobject.biblemap.put("b20_11_16", new hinsuperobject(20,11,16,"अनुग्रह करनेवाली स्त्री प्रतिष्ठा नहीं खोती है, और बलात्कारी लाग धन को नहीं खोते।"));
hinsuperobject.biblemap.put("b20_11_17", new hinsuperobject(20,11,17,"कृपालु मनुष्य अपना ही भला करता है, परन्तु जो क्रूर है, वह अपनी ही देह को दु:ख देता है।"));
hinsuperobject.biblemap.put("b20_11_18", new hinsuperobject(20,11,18,"दुष्ट मिथ्या कमाई कमाता है, परन्तु जो धर्म का बीज बोता, उसको निश्चय फल मिलता है।"));
hinsuperobject.biblemap.put("b20_11_19", new hinsuperobject(20,11,19,"जो धर्म में दृढ़ रहता, वह जीवन पाता है, परन्तु जो बुराई का पीछा करता, वह मृत्यु का कौर हो जाता है।"));
hinsuperobject.biblemap.put("b20_11_20", new hinsuperobject(20,11,20,"जो मन के टेढ़े है, उन से यहोवा को घृणा आती है, परन्तु वह खरी चालवालों से प्रसन्न रहता है।"));
hinsuperobject.biblemap.put("b20_11_21", new hinsuperobject(20,11,21,"मैं दृढ़ता के साथ कहता हूं, बुरा मनुष्य निर्दोष न ठहरेगा, परन्तु धर्मी का वंश बचाया जाएगा।"));
hinsuperobject.biblemap.put("b20_11_22", new hinsuperobject(20,11,22,"जो सुन्दर स्त्री विवेक नहीं रखती, वह थूथन में सोने की नत्थ पहिने हुए सूअर के समान है।"));
hinsuperobject.biblemap.put("b20_11_23", new hinsuperobject(20,11,23,"धर्मियों की लालसा तो केवल भलाई की होती है; परन्तु दुष्टों की आशा का फल क्रोध ही होता है।"));
hinsuperobject.biblemap.put("b20_11_24", new hinsuperobject(20,11,24,"ऐसे हैं, जो छितरा देते हैं, तौभी उनकी बढ़ती ही होती है; और ऐसे भी हैं जो यर्थाथ से कम देते हैं, और इस से उनकी घटती ही होती है।"));
hinsuperobject.biblemap.put("b20_11_25", new hinsuperobject(20,11,25,"उदार प्राणी हृष्ट पुष्ट हो जाता है, और जो औरों की खेती सींचता है, उसकी भी सींची जाएगी।"));
hinsuperobject.biblemap.put("b20_11_26", new hinsuperobject(20,11,26,"जो अपना अनाज रख छोड़ता है, उसकी लोग शाप देते हैं, परन्तु जो उसे बेच देता है, उसको आशीर्वाद दिया जाता है।"));
hinsuperobject.biblemap.put("b20_11_27", new hinsuperobject(20,11,27,"जो यत्न से भलाई करता है वह औरों की प्रसन्नता खोजता है, परन्तु जो दूसरे की बुराई का खोजी होता है, उसी पर बुराई आ पड़ती है।"));
hinsuperobject.biblemap.put("b20_11_28", new hinsuperobject(20,11,28,"जो अपने धन पर भरोसा रखता है वह गिर जाता है, परन्तु धर्मी लोग नये पत्ते की नाई लहलहाते हैं।"));
hinsuperobject.biblemap.put("b20_11_29", new hinsuperobject(20,11,29,"जो अपने घराने को दु:ख देता, उसका भाग वायु ही होगा, और मूढ़ बुद्धिमान का दास हो जाता है।"));
hinsuperobject.biblemap.put("b20_11_30", new hinsuperobject(20,11,30,"धर्मी का प्रतिफल जीवन का वृक्ष होता है, और बुद्धिमान मनुष्य लोगों के मन को मोह लेता है।"));
hinsuperobject.biblemap.put("b20_11_31", new hinsuperobject(20,11,31,"देख, धर्मी को पृथ्वी पर फल मिलेगा, तो निश्चय है कि दुष्ट और पापी को भी मिलेगा।।"));
hinsuperobject.biblemap.put("b20_12_1", new hinsuperobject(20,12,1,"जो शिक्षा पाने में प्रीति रखता है वह ज्ञान से प्रीति रखता है, परन्तु जो डांट से बैर रखता, वह पशु सरीखा है।"));
hinsuperobject.biblemap.put("b20_12_2", new hinsuperobject(20,12,2,"भले मनुष्य से तो यहोवा प्रसन्न होता है, परन्तु बुरी युक्ति करनेवाले को वह दोषी ठहराता है।"));
hinsuperobject.biblemap.put("b20_12_3", new hinsuperobject(20,12,3,"कोई मनुष्य दुष्टता के कारण स्थिर नहीं होता, परन्तु धर्मियों की जड़ उखड़ने की नहीं।"));
hinsuperobject.biblemap.put("b20_12_4", new hinsuperobject(20,12,4,"भली स्त्री अपने पति का मुकुट है, परन्तु जो लज्जा के काम करती वह मानो उसकी हडि्डयों के सड़ने का कारण होती है।"));
hinsuperobject.biblemap.put("b20_12_5", new hinsuperobject(20,12,5,"धर्मियों की कल्पनाएं न्याय ही की होती हैं, परन्तु दुष्टों की युक्तियां छल की हैं।"));
hinsuperobject.biblemap.put("b20_12_6", new hinsuperobject(20,12,6,"दुष्टों की बातचीत हत्या करने के लिये घात लगाने के विषय में होती है, परन्तु सीधे लोग अपने मुंह की बात के द्वारा छुड़ानेवाले होते हैं।"));
hinsuperobject.biblemap.put("b20_12_7", new hinsuperobject(20,12,7,"जब दुष्ट लोग उलटे जाते हैं तब वे रहते ही नहीं, परन्तु धर्मियों का घर स्थिर रहता है।"));
hinsuperobject.biblemap.put("b20_12_8", new hinsuperobject(20,12,8,"मनुष्य कि बुद्धि के अनुसार उसकी प्रशंसा होती है, परन्तु कुटिल तुच्छ जाना जाता है।"));
hinsuperobject.biblemap.put("b20_12_9", new hinsuperobject(20,12,9,"जो रोटी की आस लगाए रहता है, और बड़ाई मारता है, उस से दास रखनेवाला तुच्छ मनुष्य भी उत्तम है।"));
hinsuperobject.biblemap.put("b20_12_10", new hinsuperobject(20,12,10,"धर्मी अपने पशु के भी प्राण की सुधि रखता है, परन्तु दुष्टों की दया भी निर्दयता है।"));
hinsuperobject.biblemap.put("b20_12_11", new hinsuperobject(20,12,11,"जो अपनी भूमि को जोतता, वह पेट भर खाता है, परन्तु जो निकम्मों की संगति करता, वह निर्बुद्धि ठहरता है।"));
hinsuperobject.biblemap.put("b20_12_12", new hinsuperobject(20,12,12,"दुष्ट जन बुरे लोगों के जाल की अभिलाषा करते हैं, परन्तु धर्मियों की जड़ हरी भरी रहती है।"));
hinsuperobject.biblemap.put("b20_12_13", new hinsuperobject(20,12,13,"बुरा मनुष्य अपने दुर्वचनों के कारण फन्दे में फंसता है, परन्तु धर्मी संकट से निकास पाता है।"));
hinsuperobject.biblemap.put("b20_12_14", new hinsuperobject(20,12,14,"सज्जन अपने वचनों के फल के द्वारा भलाई से तृप्त होता है, और जैसी जिसकी करनी वैसी उसकी भरनी होती है।"));
hinsuperobject.biblemap.put("b20_12_15", new hinsuperobject(20,12,15,"मूढ़ को अपनी ही चाल सीधी जान पड़ती है, परन्तु जो सम्मति मानता, वह बुद्धिमान है।"));
hinsuperobject.biblemap.put("b20_12_16", new hinsuperobject(20,12,16,"मूढ़ की रिस उसी दिन प्रगट हो जाती है, परन्तु चतुर अपमान को छिपा रखता है।"));
hinsuperobject.biblemap.put("b20_12_17", new hinsuperobject(20,12,17,"जो सच बोलता है, वह धर्म प्रगट करता है, परन्तु जो झूठी साक्षी देता, वह छल प्रगट करता है।"));
hinsuperobject.biblemap.put("b20_12_18", new hinsuperobject(20,12,18,"ऐसे लोग हैं जिनका बिना सोचविचार का बोलना तलवार की नाई चुभता है, परन्तु बुद्धिमान के बोलने से लोग चंगे होते हैं।"));
hinsuperobject.biblemap.put("b20_12_19", new hinsuperobject(20,12,19,"सच्चाई सदा बनी रहेगी, परन्तु झूढ पल ही भर का होता है।"));
hinsuperobject.biblemap.put("b20_12_20", new hinsuperobject(20,12,20,"पुरी युक्ति करनेवालों के मन में छल रहता है, परन्तु मेल की युक्ति करनेवालों को आनन्द होता है।"));
hinsuperobject.biblemap.put("b20_12_21", new hinsuperobject(20,12,21,"धर्मी को हानि नहीं होती है, परन्तु दुष्ट लोग सारी विपत्ति में डूब जाते हैं।"));
hinsuperobject.biblemap.put("b20_12_22", new hinsuperobject(20,12,22,"झूठों से यहोवा को घृणा आती है परन्तु जो विश्वास से काम करते हैं, उन से वह प्रसन्न होता है।"));
hinsuperobject.biblemap.put("b20_12_23", new hinsuperobject(20,12,23,"चतुर मनुष्य ज्ञान को प्रगट नहीं करता है, परन्तु मूढ़ अपने मन की मूढ़ता ऊंचे शब्द से प्रचार करता है।"));
hinsuperobject.biblemap.put("b20_12_24", new hinsuperobject(20,12,24,"कामकाजी लोग प्रभुता करते हैं, परन्तु आलसी बगारी में पकड़े जाते हैं।"));
hinsuperobject.biblemap.put("b20_12_25", new hinsuperobject(20,12,25,"उदास मन दब जाता है, परन्तु भली बात से वह आनन्दित होता है।"));
hinsuperobject.biblemap.put("b20_12_26", new hinsuperobject(20,12,26,"धर्मी अपने पड़ोसी की अगुवाई करता है, परन्तु दुष्ट लोग अपनी ही चाल के कारण भटक जाते हैं।"));
hinsuperobject.biblemap.put("b20_12_27", new hinsuperobject(20,12,27,"आलसी अहेर का पीछा नहीं करता, परन्तु कामकाजी को अनमोल वस्तु मिलती है।"));
hinsuperobject.biblemap.put("b20_12_28", new hinsuperobject(20,12,28,"धर्म की बाट में जीवन मिलता है, और उसके पथ में मृत्यु का पता भी नहीं।।"));
hinsuperobject.biblemap.put("b20_13_1", new hinsuperobject(20,13,1,"बुद्धिमान पुत्रा पिता की शिक्षा सुनता है, परन्तु ठट्ठा करनेवाला घुड़की को भी नहीं सुनता।"));
hinsuperobject.biblemap.put("b20_13_2", new hinsuperobject(20,13,2,"सज्जन अपनी बातों के कारण उत्तम वस्तु खाने पाता है, परन्तु विश्वासघाती लोगों का पेट उपद्रव से भरता है।"));
hinsuperobject.biblemap.put("b20_13_3", new hinsuperobject(20,13,3,"जो अपने मुंह की चौकसी करता है, वह अपने प्राण की रक्षा करता है, परन्तु जो गाल बजाता उसका विनाश जो जाता है।"));
hinsuperobject.biblemap.put("b20_13_4", new hinsuperobject(20,13,4,"आलसी का प्राण लालसा तो करता है, और उसको कुछ नहीं मिलता, परन्तु कामकाजी हृष्ट पुष्ट हो जाते हैं।"));
hinsuperobject.biblemap.put("b20_13_5", new hinsuperobject(20,13,5,"धर्मी झूठे वचन से बैर रखता है, परन्तु दुष्ट लज्जा का कारण और लज्जित हो जाता है।"));
hinsuperobject.biblemap.put("b20_13_6", new hinsuperobject(20,13,6,"धर्म खरी चाल चलनेवाली की रक्षा करता है, परन्तु पापी अपनी दुष्टता के कारण उलट जाता है।"));
hinsuperobject.biblemap.put("b20_13_7", new hinsuperobject(20,13,7,"कोई तो धन बटोरता, परन्तु उसके पास कुछ नहीं रहता, और कोई धन उड़ा देता, तौभी उसके पास बहुत रहता है।"));
hinsuperobject.biblemap.put("b20_13_8", new hinsuperobject(20,13,8,"प्राण की छुड़ौती मनुष्य का धन है, परन्तु निर्धन घुड़की को सुनता भी नहीं।"));
hinsuperobject.biblemap.put("b20_13_9", new hinsuperobject(20,13,9,"धर्मियों की ज्योति आनन्द के साथ रहती है, परन्तु दुष्टों का दिया बुझ जाता है।"));
hinsuperobject.biblemap.put("b20_13_10", new hinsuperobject(20,13,10,"झगड़े रगड़े केवल अंहकार ही से होते हैं, परन्तु जो लोग सम्मति मानते हैं, उनके पास बुद्धि रहती है।"));
hinsuperobject.biblemap.put("b20_13_11", new hinsuperobject(20,13,11,"निर्धन के पास माल नहीं रहता, परन्तु जो अपने परिश्रम से बटोरता, उसकी बढ़ती होती है।"));
hinsuperobject.biblemap.put("b20_13_12", new hinsuperobject(20,13,12,"जब आशा पूरी होने से विलम्ब होता है, तो मन शिथिल होता है, परन्तु जब लालसा पूरी होती है, तब जीवन का वृक्ष लगता है।"));
hinsuperobject.biblemap.put("b20_13_13", new hinsuperobject(20,13,13,"जो वचन को तुच्छ जानता, वह नाश हो जाता है, परन्तु आज्ञा के डरवैये को अच्छा फल मिलता है।"));
hinsuperobject.biblemap.put("b20_13_14", new hinsuperobject(20,13,14,"बुद्धिमान की शिक्षा का जीवन का सोता है, और उसके द्वारा लोग मृत्यु के फन्दों से बच सकते हैं।"));
hinsuperobject.biblemap.put("b20_13_15", new hinsuperobject(20,13,15,"सुबुद्धि के कारण अनुग्रह होता है, परन्तु विश्वासघातियों का मार्ग कड़ा होता है।"));
hinsuperobject.biblemap.put("b20_13_16", new hinsuperobject(20,13,16,"सब चतुर तो ज्ञान से काम करते हैं, परन्तु मूर्ख अपनी मूढ़ता फैलाता है।"));
hinsuperobject.biblemap.put("b20_13_17", new hinsuperobject(20,13,17,"दुष्ट दूत बुराई में फंसता है, परन्तु विश्वासयोग्य दूत से कुशलक्षेम होता है।"));
hinsuperobject.biblemap.put("b20_13_18", new hinsuperobject(20,13,18,"जो शिक्षा को सुनी- अनसुनी करता वह निर्धन होता और अपमान पाता है, परन्तु जो डांट को मानता, उसकी महिमा होती है।"));
hinsuperobject.biblemap.put("b20_13_19", new hinsuperobject(20,13,19,"लालसा का पूरा होना तो प्राण को मीठा लगता है, परन्तु बुराई से हटना, मूर्खों के प्राण को बुरा लगता है।"));
hinsuperobject.biblemap.put("b20_13_20", new hinsuperobject(20,13,20,"बुद्धिमानों की संगति कर, तब तू भी बुद्धिमान हो जाएगा, परन्तु मूर्खों का साथी नाश हो जाएगा।"));
hinsuperobject.biblemap.put("b20_13_21", new hinsuperobject(20,13,21,"बुराई पापियों के पीछे पड़ती है, परन्तु धर्मियों को अच्छा फल मिलता है।"));
hinsuperobject.biblemap.put("b20_13_22", new hinsuperobject(20,13,22,"भला मनुष्य अपने नाती- पोतों के लिये भाग छोड़ जाता है, परन्तु पापी की सम्पत्ति धर्मी के लिये रखी जाती है।"));
hinsuperobject.biblemap.put("b20_13_23", new hinsuperobject(20,13,23,"निर्बल लोगों को खेती बारी से बहुत भोजनवस्तु मिलती है, परन्तु ऐसे लोग भी हैं जो अन्याय के कारण मिट जाते हैं।"));
hinsuperobject.biblemap.put("b20_13_24", new hinsuperobject(20,13,24,"जो बेटे पर छड़ी नहीं चलाता वह उसका बैरी है, परन्तु जो उस से प्रेम रखता, वह यत्न से उसको शिक्षा देता है।"));
hinsuperobject.biblemap.put("b20_13_25", new hinsuperobject(20,13,25,"धर्मी पेट भर खाते पाता है, परन्तु दुष्ट भूखे ही रहते हैं।।"));
hinsuperobject.biblemap.put("b20_14_1", new hinsuperobject(20,14,1,"हर बुद्धिमान स्त्री अपने घर को बनाती है, पर मूढ़ स्त्री उसको अपने ही हाथों से ढा देती है।"));
hinsuperobject.biblemap.put("b20_14_2", new hinsuperobject(20,14,2,"जो सीधाई से चलता वह यहोवा का भय माननेवाला है, परन्तु जो टेढ़ी चाल चलता वह उसको तुच्छ जाननेवाला ठहरता है।"));
hinsuperobject.biblemap.put("b20_14_3", new hinsuperobject(20,14,3,"मूढ़ के मुंह में गर्व का अंकुर है, परन्तु बुद्धिमान लोग अपने वचनों के द्वारा रक्षा पाते हैं।"));
hinsuperobject.biblemap.put("b20_14_4", new hinsuperobject(20,14,4,"जहां बैल नहीं, वहां गौशाला निर्मल तो रहती है, परन्तु बैल के बल से अनाज की बढ़ती हाती है।"));
hinsuperobject.biblemap.put("b20_14_5", new hinsuperobject(20,14,5,"सच्चा साक्षी झूठ नहीं बोलता, परन्तु झूठा साक्षी झूठी बातें उड़ाता है।"));
hinsuperobject.biblemap.put("b20_14_6", new hinsuperobject(20,14,6,"ठट्ठा करनेवाला बुद्धि को ढूंढ़ता, परन्तु नहीं पाता, परन्तु समझवाले को ज्ञान सहज से मिलता है।"));
hinsuperobject.biblemap.put("b20_14_7", new hinsuperobject(20,14,7,"मूर्ख से अलग हो जा, तू उस से ज्ञान की बात न पाएगा।"));
hinsuperobject.biblemap.put("b20_14_8", new hinsuperobject(20,14,8,"चतुर की बुद्धि अपनी चाल का जानना है, परन्तु मूर्खों की मूढ़ता छल करना है।"));
hinsuperobject.biblemap.put("b20_14_9", new hinsuperobject(20,14,9,"मूढ़ लोग दोषी होने को ठट्ठा जानते हैं, परन्तु सीधे लोगों के बीच अनुग्रह होता है।"));
hinsuperobject.biblemap.put("b20_14_10", new hinsuperobject(20,14,10,"मन अपना ही दु:ख जानता है, और परदेशी उसके आनन्द में हाथ नहीं डाल सकता।"));
hinsuperobject.biblemap.put("b20_14_11", new hinsuperobject(20,14,11,"दुष्टों को घर विनाश हो जाता है, परन्तु सीधे लोगों के तम्बू में आबादी होती है।"));
hinsuperobject.biblemap.put("b20_14_12", new hinsuperobject(20,14,12,"ऐसा मार्ग है, जो मनुष्य को ठीक देख पड़ता है, परन्तु उसके अन्त में मृत्यु ही मिलती है।"));
hinsuperobject.biblemap.put("b20_14_13", new hinsuperobject(20,14,13,"हंसी के समय भी मन उदास होता है, और आनन्द के अन्त में शोक होता है।"));
hinsuperobject.biblemap.put("b20_14_14", new hinsuperobject(20,14,14,"जिसका मन ईश्वर की ओर से हट जाता है, वह अपनी चालचलन का फल भोगता है, परन्तु भला मनुष्य आप ही आप सन्तुष्ट होता है।"));
hinsuperobject.biblemap.put("b20_14_15", new hinsuperobject(20,14,15,"भोला तो हर एक बात को सच मानता है, परन्तु चतुर मनुष्य समझ बूझकर चलता है।"));
hinsuperobject.biblemap.put("b20_14_16", new hinsuperobject(20,14,16,"बुद्धिमान डरकर बुराई से हटता है, परन्तु मूर्ख ढीठ होकर निडर रहता है।"));
hinsuperobject.biblemap.put("b20_14_17", new hinsuperobject(20,14,17,"जो झट क्रोध करे, वह मूढ़ता का काम भी करेगा, और जो बुरी युक्तियां निकालता है, उस से लोग बैर रखते हैं।"));
hinsuperobject.biblemap.put("b20_14_18", new hinsuperobject(20,14,18,"भोलों का भाग मूढ़ता ही होता है, परन्तु चतुरों को ज्ञानरूपी मुकुट बान्धा जाता है।"));
hinsuperobject.biblemap.put("b20_14_19", new hinsuperobject(20,14,19,"बुरे लोग भलों के सम्मुख, और दुष्ट लोग धर्मी के फाटक पर दण्डवत् करते हैं।"));
hinsuperobject.biblemap.put("b20_14_20", new hinsuperobject(20,14,20,"निर्धन का पड़ोसी भी उस से घृणा करता है, परन्तु धनी के बहुतेरे प्रेमी होते हैं।"));
hinsuperobject.biblemap.put("b20_14_21", new hinsuperobject(20,14,21,"जो अपने पड़ोसी को तुच्छ जानता, वह पाप करता है, परन्तु जो दीन लोगों पर अनुग्रह करता, वह धन्य होता है।"));
hinsuperobject.biblemap.put("b20_14_22", new hinsuperobject(20,14,22,"जो बुरी युक्ति निकालते हैं, क्या वे भ्रम में नहीं पड़ते? परन्तु भली युक्ति निकालनेवालों से करूणा और सच्चाई का व्यवहार किया जाता है।"));
hinsuperobject.biblemap.put("b20_14_23", new hinsuperobject(20,14,23,"परिश्रम से सदा लाभ होता है, परन्तु बकवाद करने से केवल घटती होती है।"));
hinsuperobject.biblemap.put("b20_14_24", new hinsuperobject(20,14,24,"बुद्धिमानों का धन उनका मुकुट ठहरता है, परन्तु मूर्खों की मूढ़ता निरी मूढ़ता है।"));
hinsuperobject.biblemap.put("b20_14_25", new hinsuperobject(20,14,25,"सच्चा साक्षी बहुतों के प्राण बचाता है, परन्तु जो झूठी बातें उड़ाया करता है उस से धोखा ही होता है।"));
hinsuperobject.biblemap.put("b20_14_26", new hinsuperobject(20,14,26,"यहोवा के भय मानने से दृढ़ भरोसा होता है, और उसके पुत्रों केा शरणस्थान मिलता है।"));
hinsuperobject.biblemap.put("b20_14_27", new hinsuperobject(20,14,27,"यहोवा का भय मानना, जीवन का सोता है, और उसके द्वारा लोग मृत्यु के फन्दों से बच जाते हैं।"));
hinsuperobject.biblemap.put("b20_14_28", new hinsuperobject(20,14,28,"राजा की महिमा प्रजा की बहुतायत से होती है, परन्तु जहां प्रजा नहीं, वहां हाकिम नाश हो जाता है।"));
hinsuperobject.biblemap.put("b20_14_29", new hinsuperobject(20,14,29,"जो विलम्ब से क्रोध करनेवाला है वह बड़ा समझवाला है, परन्तु जो अधीर है, वह मूढ़ता की बढ़ती करता है।"));
hinsuperobject.biblemap.put("b20_14_30", new hinsuperobject(20,14,30,"शान्त मन, तन का जीवन है, परन्तु मन के जलने से हडि्डयां भी जल जाती हैं।"));
hinsuperobject.biblemap.put("b20_14_31", new hinsuperobject(20,14,31,"जो कंगाल पर अंधेर करता, वह उसके कर्ता की निन्द करता है, परन्तु जो दरिद्र पर अनुग्रह करता, वह उसकी महिमा करता है।"));
hinsuperobject.biblemap.put("b20_14_32", new hinsuperobject(20,14,32,"दुष्ट मनुष्य बुराई करता हुआ नाश हो जाता है, परन्तु धर्मी को मृत्यु के समय भी शरण मिलती है।"));
hinsuperobject.biblemap.put("b20_14_33", new hinsuperobject(20,14,33,"समझवाले के मन में बुद्धि वास किए रहती है, परन्तु मूर्खों के अन्त:काल में जो कुछ है वह प्रगट हो जाता है।"));
hinsuperobject.biblemap.put("b20_14_34", new hinsuperobject(20,14,34,"जाति की बढ़ती धर्म ही से होती है, परन्तु पाप से देश के लोगों का अपमान होता है।"));
hinsuperobject.biblemap.put("b20_14_35", new hinsuperobject(20,14,35,"जो कर्मचारी बुद्धि से काम करता है उस पर राजा प्रसन्न होता है, परन्तु जो लज्जा के काम करता, उस पर वह रोष करता है।।"));
hinsuperobject.biblemap.put("b20_15_1", new hinsuperobject(20,15,1,"कोमल उत्तर सुनने से जलजलाहट ठण्डी होती है, परन्तु कटुवचन से क्रोध धधक उठता है।"));
hinsuperobject.biblemap.put("b20_15_2", new hinsuperobject(20,15,2,"बुद्धिमान ज्ञान का ठीक बखान करते हैं, परन्तु मूर्खों के मुंह से मूढ़ता उबल आती है।"));
hinsuperobject.biblemap.put("b20_15_3", new hinsuperobject(20,15,3,"यहोवा की आंखें सब स्थानों में लगी रहती हैं, वह बुरे भले दोनों को देखती रहती हैं।"));
hinsuperobject.biblemap.put("b20_15_4", new hinsuperobject(20,15,4,"शान्ति देनेवाली बात जीवन- वृक्ष है, परन्तु उलट फेर की बात से आत्मा दु:खित होती है।"));
hinsuperobject.biblemap.put("b20_15_5", new hinsuperobject(20,15,5,"मूढ़ अपने पिता की शिक्षा का तिरस्कार करता है, परन्तु जो डांट को मानता, वह चतुर हो जाता है।"));
hinsuperobject.biblemap.put("b20_15_6", new hinsuperobject(20,15,6,"धर्मी के घर में बहुत धन रहता है, परन्तु दुष्ट के उपार्जन में दु:ख रहता है।"));
hinsuperobject.biblemap.put("b20_15_7", new hinsuperobject(20,15,7,"बुद्धिमान लोग बातें करने से ज्ञान को फैलाते हैं, परन्तु मूर्खों का मन ठीक नहीं रहता।"));
hinsuperobject.biblemap.put("b20_15_8", new hinsuperobject(20,15,8,"दुष्ट लोगों के बलिदान से यहोवा धृणा करता है, परन्तु वह सीधे लोगों की प्रार्थना से प्रसन्न होता है।"));
hinsuperobject.biblemap.put("b20_15_9", new hinsuperobject(20,15,9,"दुष्ट के चालचलन से यहोवा को घृणा आती है, परन्तु जो धर्म का पीछा करता उस से वह प्रेम रखता है।"));
hinsuperobject.biblemap.put("b20_15_10", new hinsuperobject(20,15,10,"जो मार्ग को छोड़ देता, उसको बड़ी ताड़ना मिलती है, और जो डांट से बैर रखता, वह अवश्य मर जाता है।"));
hinsuperobject.biblemap.put("b20_15_11", new hinsuperobject(20,15,11,"जब कि अधोलोक और विनाशलोक यहोवा के साम्हने खुले रहते हैं, तो निश्चय मनुष्यों के मन भी।"));
hinsuperobject.biblemap.put("b20_15_12", new hinsuperobject(20,15,12,"ठट्ठा करनेवाला डांटे जाने से प्रसन्न नहीं होता, और न वह बुद्धिमानों के पास जाता है।"));
hinsuperobject.biblemap.put("b20_15_13", new hinsuperobject(20,15,13,"मन आनन्दित होने से मुख पर भी प्रसन्नता छा जाती है, परन्तु मन के दु:ख से आत्मा निराश होती है।"));
hinsuperobject.biblemap.put("b20_15_14", new hinsuperobject(20,15,14,"समझनेवाले का मन ज्ञान की खोज में रहता है, परन्तु मूर्ख लोग मूढ़ता से पेट भरते हैं।"));
hinsuperobject.biblemap.put("b20_15_15", new hinsuperobject(20,15,15,"दुखिया के सब दिन दु:ख भरे रहते हैं, परन्तु जिसका मन प्रसन्न रहता है, वह मानो नित्य भोज में जाता है।"));
hinsuperobject.biblemap.put("b20_15_16", new hinsuperobject(20,15,16,"घबराहट के साथ बहुत रखे हुए धन से, यहोवा के भय के साथ थोड़ा ही धन उत्तम है,"));
hinsuperobject.biblemap.put("b20_15_17", new hinsuperobject(20,15,17,"प्रेम वाले घर में सागपात का भोजन, बैर वाले घर में पाले हुए बैल का मांस खाने से उत्तम है।"));
hinsuperobject.biblemap.put("b20_15_18", new hinsuperobject(20,15,18,"क्रोधी पुरूष झगड़ा मचाता है, परन्तु जो विलम्ब से क्रोध करनेवाला है, वह मुक मों को दबा देता है।"));
hinsuperobject.biblemap.put("b20_15_19", new hinsuperobject(20,15,19,"आलसी का मार्ग कांटों से रून्धा हुआ होता है, परन्तु सीधे लोगों का मार्ग राजमार्ग ठहरता है।"));
hinsuperobject.biblemap.put("b20_15_20", new hinsuperobject(20,15,20,"बुद्धिमान पुत्रा से पिता आनन्दित होता है, परन्तु मूर्ख अपनी माता को तुच्छ जानता है।"));
hinsuperobject.biblemap.put("b20_15_21", new hinsuperobject(20,15,21,"निर्बुद्धि को मूढ़ता से आनन्द होता है, परन्तु समझवाला मनुष्य सीधी चाल चलता है।"));
hinsuperobject.biblemap.put("b20_15_22", new hinsuperobject(20,15,22,"बिना सम्मति की कल्पनाएं निष्फल हुआ करती हैं, परन्तु बहुत से मंत्रियों की सम्मत्ति से बात ठहरती है।"));
hinsuperobject.biblemap.put("b20_15_23", new hinsuperobject(20,15,23,"सज्जन उत्तर देने से आनन्दित होता है, और अवसर पर कहा हुआ वचन क्या ही भला होता है!"));
hinsuperobject.biblemap.put("b20_15_24", new hinsuperobject(20,15,24,"बुद्धिमान के लिये जीवन का मार्ग ऊपर की ओर जाता है, इस रीति से वह अधोलोक में पड़ने से बच जाता है।"));
hinsuperobject.biblemap.put("b20_15_25", new hinsuperobject(20,15,25,"यहोवा अहंकारियों के घर को ढा देता है, परन्तु विधवा के सिवाने को अटल रखता है।"));
hinsuperobject.biblemap.put("b20_15_26", new hinsuperobject(20,15,26,"बुरी कल्पनाएं यहोवा को घिनौनी लगती हैं, परन्तु शुद्ध जन के वचन मनभावने हैं।"));
hinsuperobject.biblemap.put("b20_15_27", new hinsuperobject(20,15,27,"लालची अपने घराने को दु:ख देता है, परन्तु घूस से घृणा करनेवाला जीवित रहता है।"));
hinsuperobject.biblemap.put("b20_15_28", new hinsuperobject(20,15,28,"धर्मी मन में सोचता है कि क्या उत्तर दूं, परन्तु दुष्टों के मुंह से बुरी बातें उबल आती हैं।"));
hinsuperobject.biblemap.put("b20_15_29", new hinsuperobject(20,15,29,"यहोवा दुष्टों से दूर रहता है, परन्तु धर्मियों की प्रार्थना सुनता है।"));
hinsuperobject.biblemap.put("b20_15_30", new hinsuperobject(20,15,30,"आंखों की चमक से मन को आनन्द होता है, और अच्छे समाचार से हडि्डयां पुष्ट होती हैं।"));
hinsuperobject.biblemap.put("b20_15_31", new hinsuperobject(20,15,31,"जो जीवनदायी डांट कान लगाकर सुनता है, वह बुद्धिमानों के संग ठिकाना पाता है।"));
hinsuperobject.biblemap.put("b20_15_32", new hinsuperobject(20,15,32,"जो शिक्षा को सुनी- अनसुनी करता, वह अपने प्राण को तुच्छ जानता है, परन्तु जो डांट को सुनता, वह बुद्धि प्राप्त करता है।"));
hinsuperobject.biblemap.put("b20_15_33", new hinsuperobject(20,15,33,"यहोवा के भय मानने से शिक्षा प्राप्त होती है, और महिमा से पहिले नम्रता होती है।।"));
hinsuperobject.biblemap.put("b20_16_1", new hinsuperobject(20,16,1,"मन की युक्ति मनुष्य के वश में रहती है, परन्तु मुंह से कहना यहोवा की ओर से होता है।"));
hinsuperobject.biblemap.put("b20_16_2", new hinsuperobject(20,16,2,"मनुष्य का सारा चालचलन अपनी दृष्टि में पवित्रा ठहरता है, परन्तु यहोवा मन को तौलता है।"));
hinsuperobject.biblemap.put("b20_16_3", new hinsuperobject(20,16,3,"अपने कामों को यहोवा पर डाल दे, इस से तेरी कल्पनाएं सिद्ध होंगी।"));
hinsuperobject.biblemap.put("b20_16_4", new hinsuperobject(20,16,4,"यहोवा ने सब वस्तुएं विशेष उ :श्य के लिये बनाई हैं, वरन दुष्ट को भी विपत्ति भोगने के लिये बनाया है।"));
hinsuperobject.biblemap.put("b20_16_5", new hinsuperobject(20,16,5,"सब मन के घमण्डियों से यहोवा घृणा करता है करता है; मैं दृढ़ता से कहता हूं, ऐसे लोग निर्दोष न ठहरेंगे।"));
hinsuperobject.biblemap.put("b20_16_6", new hinsuperobject(20,16,6,"अधर्म का प्रायश्चित कृपा, और सच्चाई से होता है, और यहोवा के भय मानने के द्वारा मनुष्य बुराई करने से बच जाते हैं।"));
hinsuperobject.biblemap.put("b20_16_7", new hinsuperobject(20,16,7,"जब किसी का चालचलन यहोवा को भवता है, तब वह उसके शत्रुओं का भी उस से मेल कराता है।"));
hinsuperobject.biblemap.put("b20_16_8", new hinsuperobject(20,16,8,"अन्याय के बड़े लाभ से, न्याय से थोड़ा ही प्राप्त करना उत्तम है।"));
hinsuperobject.biblemap.put("b20_16_9", new hinsuperobject(20,16,9,"मनुष्य मन में अपने मार्ग पर विचार करता है, परन्तु यहोवा ही उसके पैरों को स्थिर करता है।"));
hinsuperobject.biblemap.put("b20_16_10", new hinsuperobject(20,16,10,"राजा के मुंह से दैवीवाणी निकलती है, न्याय करने में उस से चूक नहीं होती।"));
hinsuperobject.biblemap.put("b20_16_11", new hinsuperobject(20,16,11,"सच्चा तराजू और पलड़े यहोवा की ओर से होते हैं, थैली में जितने बटखरे हैं, सब उसी के बनवाए हुए हैं।"));
hinsuperobject.biblemap.put("b20_16_12", new hinsuperobject(20,16,12,"दुष्टता करना राजाओं के लिये घृणित काम है, क्योंकि उनकी गद्दी धर्म ही से स्थिर रहती है।"));
hinsuperobject.biblemap.put("b20_16_13", new hinsuperobject(20,16,13,"धर्म की बात बोलनेवालों से राजा प्रसन्न होता है, और जो सीधी बातें बोलता है, उस से वह प्रेम रखता है।"));
hinsuperobject.biblemap.put("b20_16_14", new hinsuperobject(20,16,14,"राजा का क्रोध मृत्यु के दूत के समान है, परन्तु बुद्धिमान मनुष्य उसको ठण्डा करता है।"));
hinsuperobject.biblemap.put("b20_16_15", new hinsuperobject(20,16,15,"राजा के मुख की चमक में जीवन रहता है, और उसकी प्रसन्नता बरसात के अन्त की घटा के समान होती है।"));
hinsuperobject.biblemap.put("b20_16_16", new hinsuperobject(20,16,16,"बुद्धि की प्राप्ति चोखे सोने से क्या ही उत्तम है! और समझ की प्राप्ति चान्दी से अति योग्य है।"));
hinsuperobject.biblemap.put("b20_16_17", new hinsuperobject(20,16,17,"बुराई से हटना सीधे लोगों के लिये राजमार्ग है, जो अपने चालचलन की चौकसी करता, वह अपने प्राण की भी रक्षा करता है।"));
hinsuperobject.biblemap.put("b20_16_18", new hinsuperobject(20,16,18,"विनाश से पहिले गर्व, और ठोकर खाने से पहिले घमण्ड होता है।"));
