package hin.bible;

public class hinbible33 {

public hinbible33() {

putmapbible33();

}

private void putmapbible33() {

hinsuperobject.biblemap.put("b33_1_1", new hinsuperobject(33,1,1,"यहोवा का वचन, जो यहूदा के राजा योताम, आहाज और हिजकिरयाह के दिनों में मीका मोरेशेती को पहुंचा, जिस को उस ने शोमरोन और यरूशलेम के विषय में पाया।।"));
hinsuperobject.biblemap.put("b33_1_2", new hinsuperobject(33,1,2,"हे जाति- जाति के सब लोगों, सुनो! हे पृथ्वी तू उस सब समेत जो तुझ में है, ध्यान दे! और प्रभु यहोवा तुम्हारे विरूद्ध, वरन परमेश्वर अपने पवित्रा मन्दिर में से तुम पर साक्षी दे।"));
hinsuperobject.biblemap.put("b33_1_3", new hinsuperobject(33,1,3,"कयोंकि देख, यहोवा अपने पवित्रास्थान से बाहर निकल रहा है, और वह उतरकर पृथ्वी के ऊंचे स्थानों पर चलेगा।"));
hinsuperobject.biblemap.put("b33_1_4", new hinsuperobject(33,1,4,"और पहाड़ उसके नीचे गल जाएंगे, और तराई ऐसे फटेंगी, जैसे मोम आग की आंच से, और पानी जो घाट से नीचे बहता है।"));
hinsuperobject.biblemap.put("b33_1_5", new hinsuperobject(33,1,5,"यह सब याकूब के अपराध, और इस्राएल के घराने के पाप के कारण से होता है। याकूब का अपराध क्या है? क्या सामरिया नहीं? और यहूदा के ऊंचे स्थान क्या हैं? क्या यरूशलेम नहीं?"));
hinsuperobject.biblemap.put("b33_1_6", new hinsuperobject(33,1,6,"इस कारण मैं सामारिया को मैदान के खेत का ढेर कर दूंगा, और दाख का बगीचा बनाऊंगा; और मैं उसके पत्थरों को खड्ड में लुढ़का दूंगा, और उसकी नेव उखाड़ दूंगा।"));
hinsuperobject.biblemap.put("b33_1_7", new hinsuperobject(33,1,7,"उसकी सब खुदी हुई मूरतें टुकड़े टुकड़े की जाएंगी; और जो कुछ उस ने छिनाला करके कमाया है वह आग से भस्म किया जाएगा, और उसकी सब प्रतिमाओं को मैं चकनाचूर करूंगा; क्योंकि छिनाले ही की कमाई से उसने उसको संचय किया है, और वह फिर छिनाले की सी कमाई हो जाएगी।।"));
hinsuperobject.biblemap.put("b33_1_8", new hinsuperobject(33,1,8,"इस कारण मैं छाती पीटकर हाय, हाय, करूंगा; मैं लुटा हुआ सा और नंगा चला फिरा करूंगा; मैं गीदड़ों की नाई चिल्लाऊंगा, और शतुर्मुगों की नाईं रोऊंगा।"));
hinsuperobject.biblemap.put("b33_1_9", new hinsuperobject(33,1,9,"क्योंकि उसका घाव असाध्य है; और विपत्ति यहूदा पर भी आ पड़ी, वरन वह मेरे जातिभाइयों पर पड़कर यरूशलेम के फाटक तक पहुंच गई है।।"));
hinsuperobject.biblemap.put("b33_1_10", new hinsuperobject(33,1,10,"गात नगर में इसकी चर्चा मत करो, और मत रोओ; बेतआप्रा में धूति में लोटपोट करो।"));
hinsuperobject.biblemap.put("b33_1_11", new hinsuperobject(33,1,11,"हे शापीर की रहनेवाली नंगी होकर निर्लज चली जा; सानान की रहनेवाली नहीं निकल सकती; बेतसेल के रोने पीटने के कारण उसका शरणस्थान तुम से ले लिया जाएगा।"));
hinsuperobject.biblemap.put("b33_1_12", new hinsuperobject(33,1,12,"क्योंकि मारोत की रहनेवाली तो कुशल की बाट जोहते- जोहते तड़प गई है, क्योंकि यहोवा की ओर से यरूशलेम के फाटक तक विपत्ति आ पहुंची है।"));
hinsuperobject.biblemap.put("b33_1_13", new hinsuperobject(33,1,13,"हे लाकीश की रहनेवाली अपने रथों में वेग चलनेवाले घोड़े जोत; तुझी से सिरयोन की प्रजा के पाप का आरम्भ हुआ, क्योंकि इस्राएल के अपराध तुझी में पाए गए।"));
hinsuperobject.biblemap.put("b33_1_14", new hinsuperobject(33,1,14,"इस कारण तू गात के मोरेशेत को दान देकर दूर कर देगा; अकजीब के घर से इस्राएल के राजा धोशा ही खाएंगे।"));
hinsuperobject.biblemap.put("b33_1_15", new hinsuperobject(33,1,15,"हे मारेशा की रहनेवाली मैं फिर तुझ पर एक अधिकारी ठहराऊंगा, और इस्राएल के प्रतिष्ठित लोगों को अदुल्लाम में आना पड़ेगा।"));
hinsuperobject.biblemap.put("b33_1_16", new hinsuperobject(33,1,16,"अपने दुलारे लड़कों के लिये अपना केश कटवाकर सिर मुंड़ा, वरन अपना पूरा सिर गिद्ध के समान गंजा कर दे, क्योंकि वे बंधुए होकर तेरे पास से चले गए हैं।"));
hinsuperobject.biblemap.put("b33_2_1", new hinsuperobject(33,2,1,"हाय उन पर, जो बिछौनों पर पड़े हुए बुराइयों की कल्पना करते और दुष्ट कर्म की इच्छा करते हैं, और बलवन्त होने के कारण भोर को दिन निकलते ही वे उसको पूरा करते हैं।"));
hinsuperobject.biblemap.put("b33_2_2", new hinsuperobject(33,2,2,"वे खेतों का लालच करके उन्हें छली लेते हैं, और घरों का लालच करके उन्हें भी ले लेते हैं; और उसके घराने समेत पुरूष पर, और उसके निज भाग समेत किसी पुरूष पर अन्धेर और अत्याचार कहते हैं।"));
hinsuperobject.biblemap.put("b33_2_3", new hinsuperobject(33,2,3,"इस कारण, यहोवा यों कहता है, मैं इस कुल पर ऐसी विपत्ति डालने पर हूं, जिस के नीचे से तुम अपनी गर्दन हटा न सकोगे; क्योंकि वह विपत्ति का समय होगा।"));
hinsuperobject.biblemap.put("b33_2_4", new hinsuperobject(33,2,4,"उस समय यह अत्यन्त शोक का गीत दृष्टान्त की रीति पर गाया जाएगा: हम तो सर्वनाश हो गए; वह मेरे लोगों के भाग को बिगाड़ता है; हाय, वह उसे मुझ से कितनी दूर कर देता है! वह हमारे खेत बलवा करनेवाले को दे देता है।"));
hinsuperobject.biblemap.put("b33_2_5", new hinsuperobject(33,2,5,"इस कारण तेरा ऐसा कोई न हो, जो यहोवा की मण्डली में चिट्ठी डालकर नापने की डोरी डाले।।"));
hinsuperobject.biblemap.put("b33_2_6", new hinsuperobject(33,2,6,"बकवासी कहा करते हैं, कि बकवास न करो। इन बातें के लिये न कहा करो; ऐसे लोगों में से अपमान न मिटेगा।"));
hinsuperobject.biblemap.put("b33_2_7", new hinsuperobject(33,2,7,"हे याकूब के घराने, क्या यह कहा जाए कि यहोवा का आत्मा अधीर हो गया है? क्या ये काम उसी के किए हुए हैं? क्या मेरे वचनों से उसका भला नहीं होता जो सीधाई से चलता है?"));
hinsuperobject.biblemap.put("b33_2_8", new hinsuperobject(33,2,8,"परन्तु कल की बात है कि मेरी प्रजा शत्रु बनकर मेरे विरूद्ध उठी है; तुम शान्त और भोले- भाले राहियों के तन पर से चादर छीन लेते हो जो लड़ाई का विचार न करके निधड़क चले जाते हैं।"));
hinsuperobject.biblemap.put("b33_2_9", new hinsuperobject(33,2,9,"मेरी प्रजा की स्त्रियों को तुम उनके सुखधामों से निकाल देते हो; और उनके नन्हें बच्चों से तुम मेरी दी हुई उत्तम वस्तुएं सर्वदा के लिये छीन लेते हो।"));
hinsuperobject.biblemap.put("b33_2_10", new hinsuperobject(33,2,10,"उठो, चले जाओ! क्योंकि यह तुम्हारा विश्रामस्थान नहीं है; इसका कारण वह अशुद्धता है जो कठिन दु:ख के साथ तुम्हारा नाश करेगी।"));
hinsuperobject.biblemap.put("b33_2_11", new hinsuperobject(33,2,11,"यदि कोई झूठी आत्मा में चलता हुए झूठी और व्यर्थ बातें कहे और कहे कि मैं तुम्हें नित्य दाखमधु और मदिरा के लिये प्रचार सुनाता रहूंगा, तो वही इन लोगों का भविष्यद्वक्ता ठहरेगा।।"));
hinsuperobject.biblemap.put("b33_2_12", new hinsuperobject(33,2,12,"हे याकूब, मैं निश्चय तुम सभों को इकट्ठा करूंगा; मैं इस्राएल के बचे हुओं को निश्चय इकट्ठा करूंगा; और बोस्रा की भेड़बकरियों की नाईं एक संग रखूंगा। उस झुण्ड की नाईं जो अच्छी चराई में हो, वे मनुष्यों की बहुतायत के मारे कोलाहल मचाएंगे।"));
hinsuperobject.biblemap.put("b33_2_13", new hinsuperobject(33,2,13,"उनके आगे आगे बाड़े का तोड़नेवाला गया है, इसलिये वे भी उसे तोड़ रहे हैं, और फाटक से होकर निकल जा रहे हैं; उनका राजा उनके आगे आगे गया अर्थात् यहोवा उनका सरदार और अगुवा है।।"));
hinsuperobject.biblemap.put("b33_3_1", new hinsuperobject(33,3,1,"और मैं ने कहा, हे याकूब के प्रधानों, हे इस्राएल के घराने के न्याइयों, सुनो! क्या न्याय का भेद जानना तुम्हारा काम नहीं?"));
hinsuperobject.biblemap.put("b33_3_2", new hinsuperobject(33,3,2,"तुम तो भलाई से बैर, और बुराई से प्रीति रखते हो, मानो, तुम, लोगों पर से उनकी खाल, और उनकी हडि्डयों पर से उनका मांस उधेड़ लेते हो;"));
hinsuperobject.biblemap.put("b33_3_3", new hinsuperobject(33,3,3,"वरन तुम मेरे लोगों का मांस खा भी लेते, और उनकी खाल उधेड़ते हो; तुम उनकी हडि्डयों को हांड़ी में पकाने के लिये टुकड़े टुकड़े करते हो।"));
hinsuperobject.biblemap.put("b33_3_4", new hinsuperobject(33,3,4,"वे उस समय यहोवा की दोहाई देंगे, परन्तु वह उनकी न सुनेगा, वरन उस समय वह उनके बुरे कामों के कारण उन से मुंह फेल लेगा।।"));
hinsuperobject.biblemap.put("b33_3_5", new hinsuperobject(33,3,5,"यहोवा का यह वचन है कि जो भविष्यद्वक्ता मेरी प्रजा को भटका देते हैं, और जब उन्हें खाने को मिलता है तब शान्ति, शान्ति, पुकारते हैं, और यदि कोई उनके मुंह में कुछ न दे, तो उसके विरूद्ध युद्ध करते को तैयार हो जाते हैं।"));
hinsuperobject.biblemap.put("b33_3_6", new hinsuperobject(33,3,6,"इस कारण तुम पर ऐसी रात आएगी, कि तुम को दर्शन न मिलेगा, और तुम ऐसे अन्धकार में पड़ोगे कि भावी न कह सकोगे। भविष्यद्वक्ताओं के लिये सूर्य अस्त होगा, और दिन रहते उन पर अन्धियारा छा जाएगा।"));
hinsuperobject.biblemap.put("b33_3_7", new hinsuperobject(33,3,7,"दर्शी लज्जित होंगे, और भावी कहनेवालों के मुंह काले होंगे; और वे सब के सब अपने ओठों को इसलिये ढांपेंगे कि परमेश्वर की ओर से उत्तर नहीं मिलता।"));
hinsuperobject.biblemap.put("b33_3_8", new hinsuperobject(33,3,8,"परन्तु मैं तो यहोवा की आत्मा से शक्ति, न्याय और पराक्रम पाकर परिपूर्ण हूं कि मैं याकूब को उसका अपराध और इस्राएल को उसका पाप जता सकूं।"));
hinsuperobject.biblemap.put("b33_3_9", new hinsuperobject(33,3,9,"हे याकूब के घराने के प्रधानों, हे इस्राएल के घराने के न्यायियो, हे न्याय से धृणा करनेवालो और सब सीधी बातों की टेढ़ी- मेढ़ी करनेवालो, यह बात सुनो।"));
hinsuperobject.biblemap.put("b33_3_10", new hinsuperobject(33,3,10,"तुम सिरयोन को हत्या करके और यरूशलेम को कुटिलता करके दृढ़ करते हो।"));
hinsuperobject.biblemap.put("b33_3_11", new hinsuperobject(33,3,11,"उसके प्रधान घूस ले लेकर विचार करते, और याजक दाम ले लेकर व्यवस्था देते हैं, और भविष्यद्वक्ता रूपये के लिये भावी कहते हैं; तौीाी वे यह कहकर यहोवा पर भरोसा रखते हैं, यहोवा हमारे बीच में है, इसलिये कोई विपत्ति हम पर न आएगी।"));
hinsuperobject.biblemap.put("b33_3_12", new hinsuperobject(33,3,12,"इसलिये तुम्हारे कारण सिरयोन जोतकर खेत बनाया जाएगा, और यरूशलेम डींह ही डींह हो जाएगा, और जिस पर्वत पर भवन बना है, वह वन के ऊंचे स्थान सा हो जाएगा।।"));
hinsuperobject.biblemap.put("b33_4_1", new hinsuperobject(33,4,1,"अन्त के दिनों में ऐसा होगा कि यहोवा के भवन के भवन का पर्वत सब पहाड़ों पर दृढ़ किया जाएगा, और सब पहाड़ियों से अधिक ऊंचा किया जाएगा; और हर जाति के लोग धारा की नाईं उसकी ओर चलेंगे।"));
hinsuperobject.biblemap.put("b33_4_2", new hinsuperobject(33,4,2,"और बहुत जातियों के लाग जाएंगे, और आपस में कहेंगे, आओ, हम यहोवा के पर्वत पर चढ़कर, याकूब के परमेश्वर के भवन में जाएं; तब वह हम को अपने मार्ग सिखाएगा, और हम उसके पथों पर चलेंगे। क्योंकि यहोवा की व्यवस्था सिरयोन से, और उसका वचन यरूशलेम से निकलेगा।"));
hinsuperobject.biblemap.put("b33_4_3", new hinsuperobject(33,4,3,"वह बहुत देखों के लोगों का न्याय करेगा, और दूर दूर तक की सामर्थी जातयों के झगड़ों को मिटाएगा; सो वे अपनी तलवारें पीटकर हल के फाल, और अपने भालों से हंसिया बनाएंगे; तब एक जाति दूसरी जाति के विरूद्ध तलवार फिर न चलाएगी;"));
hinsuperobject.biblemap.put("b33_4_4", new hinsuperobject(33,4,4,"और लोग आगे को युद्ध विद्या न सीखेंगे। परन्तु वे अपनी अपनी दाखलता और अंजीर के वृक्ष तले बैठा करेंगे, और कोई उनको न डराएगा; सेनाओं के यहोवा ने यही वचन दिया है।।"));
hinsuperobject.biblemap.put("b33_4_5", new hinsuperobject(33,4,5,"सब राज्यों के लोग तो अपने अपने देवता का नाम लेकर चलते हैं, परन्तु हम लोग अपने परमेश्वर यहोवा का नाम लेकर सदा सर्वदा चलते रहेंगे।।"));
hinsuperobject.biblemap.put("b33_4_6", new hinsuperobject(33,4,6,"यहोवा की यह वाणी है, उस समय मैं प्रजा के लंगड़ों को, और बरबस निकाले हुओं को, और जिन को मैं ने दु:ख दिया है उन सब को इकट्ठे करूंगा।"));
hinsuperobject.biblemap.put("b33_4_7", new hinsuperobject(33,4,7,"और लंगड़ों को मैं बचा रचाूंगा, और दूर किए हुओं को एक सामर्थी जाति कर दूंगा; और यहोवा उन पर सिरयोन पर्वत के ऊपर से सदा राज्य करता रहेगा।।"));
hinsuperobject.biblemap.put("b33_4_8", new hinsuperobject(33,4,8,"और हे एदेर के गुम्मट, हे सिरयोन की पहाड़ी, पहिली प्रभुता अर्थात् यरूशलेम का राज्य तुझे मिलेगा।।"));
hinsuperobject.biblemap.put("b33_4_9", new hinsuperobject(33,4,9,"अब तू क्यों चिल्लाती है? क्या तुझ में कोई राजा नहीं रहा? क्या तेरा युक्ति करनेवाला नाश हो गया, जिस से जच्चा स्त्री की नाईं तुझे पीड़ा उठती है?"));
hinsuperobject.biblemap.put("b33_4_10", new hinsuperobject(33,4,10,"हे सिरयोन की बेटी, जच्चा स्त्री की नाईं पीड़ा उठाकर उत्पन्न कर; क्योंकि अब तू गढ़ी में से निकलकर मैदान में बसेगी, वरन बाबुल तक जाएगी; वहीं तू छुड़ाई जाएगी, अर्थात् वहीं यहोवा तुझे तेरे शत्रुओं के वश में से छुड़ा लेगा।।"));
hinsuperobject.biblemap.put("b33_4_11", new hinsuperobject(33,4,11,"और अब बहुत सी जातियां तेरे विरूद्ध इकट्ठी होकर तेरे विषय में कहेंगी सिरयोन अपवित्रा की जाए, और हम अपनी आंखों से उसको निहारें।"));
hinsuperobject.biblemap.put("b33_4_12", new hinsuperobject(33,4,12,"परन्तु वे यहोवा की कल्पनाएं नहीं जानते, न उसकी युक्ति समझते हैं, कि वह उन्हें ऐसा बटोर लेगा जैसे खलिहान में पूले बटोरे जाते हैं।"));
hinsuperobject.biblemap.put("b33_4_13", new hinsuperobject(33,4,13,"हे सिरयोन, उठ और दांव कर, मैं तेरे सींगों को लोहे के, और तेरे खुओं कों पीतल के बना दूंगा; ओर तू बहुत सी जातियों को चूरचूर करेगी, ओर उनकी कमाई यहोवा को और उनकी धन- सम्पित्ति पृथ्वी के प्रभु के लिये अर्पण करेगी।।"));
hinsuperobject.biblemap.put("b33_5_1", new hinsuperobject(33,5,1,"अब हे बहुत दलों की स्वामिनी, दल बान्ध- बान्धकर इकट्ठी हो, क्योंकि उस ने हम लोगों को घेर लिया है; वे इस्राएल के न्यायी के गाल पर सोंटा मारेंगे।"));
hinsuperobject.biblemap.put("b33_5_2", new hinsuperobject(33,5,2,"हे बेतलेहेम एप्राता, यदि तू ऐसा छोटा है कि यहूदा के हजारों में गिना नहीं जाता, तौभी तुझ में से मेरे लिये एक पुरूष निकलेगा, जो इस्राएलियों में प्रभुता करनेवाला होगा; और उसका निकलना प्राचीनकाल से, वरन अनादि काल से होता आया है।"));
hinsuperobject.biblemap.put("b33_5_3", new hinsuperobject(33,5,3,"इस कारण वह उनको उस समय तक त्यागे रहेगा, जब तक जच्चा उत्पन्न न करे; तब इस्राएलियों के पास उसके बचे हुए भाई लौटकर उन से मिल जाएंगे।"));
hinsuperobject.biblemap.put("b33_5_4", new hinsuperobject(33,5,4,"और वह खड़ा होकर यहोवा की दी हुई शक्ति से, और अपने परमेश्वर यहोवा के नाम के प्रताप से, उनकी चरवाही करेगा। और वे सुरक्षित रहेंगे, क्योंकि अब वह पृथ्वी की छोर तक महान् ठहरेगा।।"));
hinsuperobject.biblemap.put("b33_5_5", new hinsuperobject(33,5,5,"और वह शान्ति का मूल होगा, जब अश्शूरी हमारे देश पर चढ़ाई करें, और हमारे राजभवनों में पांव धरें, तब हम उनके विरूद्ध सात चरवाहे वरन आठ प्रधान मनुष्य खड़ें करेंगे।"));
hinsuperobject.biblemap.put("b33_5_6", new hinsuperobject(33,5,6,"और वे अश्शूर के देश को वरन पैठाव के स्थानों तक निम्रोद के देश केा तलवार चलाकर मार लेंगे; और जब अश्शूरी लोग हमारे देश में आएं, और उसके सिवाने के भीतर पांव धरें, तब वही पुरूष हम को उन से बचाएगा।"));
hinsuperobject.biblemap.put("b33_5_7", new hinsuperobject(33,5,7,"और याकूब के बचे हुए लोग बहुत राज्यों के बीच ऐसा काम देंगे, जैसा यहोवा की ओर से पड़नेवाली ओस, और घास पर की वर्षा, जो किसी के लिये नहीं ठहरती और मनुष्यों की बाट नहीं जोहती।"));
hinsuperobject.biblemap.put("b33_5_8", new hinsuperobject(33,5,8,"और याकूब के बचे हुए लोग जातियों में और देश देश के लोगों के बीच ऐसे होंगे जैसे वनपशुओं में सिंह, वा भेड़- बकरियों के झुण्डों में जवान सिंह होता है, क्योंकि जब वह उनके बीच में से जाए, तो लताड़ता और फाड़ता जाएगा, और कोई बचा न सकेगा।"));
hinsuperobject.biblemap.put("b33_5_9", new hinsuperobject(33,5,9,"तेरा हाथ तेरे द्रोहियों पर पड़े, और तेरे सब शत्रु नाश हो जाएं।।"));
hinsuperobject.biblemap.put("b33_5_10", new hinsuperobject(33,5,10,"यहोवा की यही वाणी है, उस समय मैं तेरे घोड़ों को तेरे बीच में से नाश करूंगा; और तेरे रथों का विनाश करूंगा।"));
hinsuperobject.biblemap.put("b33_5_11", new hinsuperobject(33,5,11,"ओर मैं तेरे देश के नगरों को भी नाश करूंगा, और तेरे किलों को ढा दूंगा।"));
hinsuperobject.biblemap.put("b33_5_12", new hinsuperobject(33,5,12,"और मैं तेरे तन्त्रा- मन्त्रा नाश करूंगा, और तुझ में टोन्हे आगे को न रहेंगे।"));
hinsuperobject.biblemap.put("b33_5_13", new hinsuperobject(33,5,13,"ओर मैं तेरी खुदी हुई मूरतें, और तेरी लाठें, तेरे बीच में से नाश करूंगा; और तू आगे को अपने हाथ की बनाई हुई वस्तुओं को दण्डवत् न करेगा।"));
hinsuperobject.biblemap.put("b33_5_14", new hinsuperobject(33,5,14,"और मैं तेरी अशेरा नाम मूरतों को तेरी भूमि में से उखाड़ डालूंगा, और तेरे नगरों का विनाश करूंगा।"));
hinsuperobject.biblemap.put("b33_5_15", new hinsuperobject(33,5,15,"और मैं अन्यजातियों से जो मेरा कहा नहीं मानतीं, क्रोध और जल जलाहट के साथ पलटा लूंगा।।"));
hinsuperobject.biblemap.put("b33_6_1", new hinsuperobject(33,6,1,"जो बात यहोवा कहता है, उसे सुनो: उठकर, पहाड़ों के साम्हने वादविवाद कर, और टीले भी तेरी सुनने पाएं।"));
hinsuperobject.biblemap.put("b33_6_2", new hinsuperobject(33,6,2,"हे पहाड़ों, और हे पृथ्वी की अटल नेव, यहोवा का वादविवाद सुनो, क्योंकि यहोवा का अपनी प्रजा के साथ मुक मा है, और वह इस्राएल से वादविवाद करता है।।"));
hinsuperobject.biblemap.put("b33_6_3", new hinsuperobject(33,6,3,"हे मेरी प्रजा, मैं ने तेरा क्या किया, और क्या करके मैं ने तुझे उकता दिया है?"));
hinsuperobject.biblemap.put("b33_6_4", new hinsuperobject(33,6,4,"मेरे विरूद्ध साक्षी दे! मैं तो तुझे मि  देश से निकाल ले आया, और दासत्व के घर में से तुझे छुड़ा लाया; और तेरी अगुवाई करने को मूसा, हारून और मरियम को भेज दिया।"));
hinsuperobject.biblemap.put("b33_6_5", new hinsuperobject(33,6,5,"हे मेरी प्रजा, स्मरण कर, कि मोआब के राजा बालक ने तेरे विरूद्ध कौन सी युक्ति की? और बोर के पुत्रा बिलाम ने उसको क्या सम्मत्ति दी? और शित्तिम से गिल्गाल तक की बातों का स्मरण कर, जिस से तू यहोवा के धर्म के काम समझ सके।।"));
hinsuperobject.biblemap.put("b33_6_6", new hinsuperobject(33,6,6,"मैं क्या लेकर यहोवा के सम्मुख आऊं, और ऊपर रहनेवाले परमेश्वर के साम्हने झुकूं? क्या मैं होमबलि के लिये एक एक वर्ष के बछड़े लेकर उसके सम्मुख आऊं?"));
hinsuperobject.biblemap.put("b33_6_7", new hinsuperobject(33,6,7,"क्या यहोवा हजारों मेढ़ों से, वा तेल की लाखों नदियों से प्रसन्न होगा? क्या मैं अपने अपराध के प्रायश्चित्त में अपने पहिलौठे को वा अपने पाप के बदले में अपने जन्माए हुए किसी को दूं?"));
hinsuperobject.biblemap.put("b33_6_8", new hinsuperobject(33,6,8,"हे मनुष्य, वह तुझे बता चुका है कि अच्छा क्या है; और यहोवा तुझ से इसे छोड़ और क्या चाहता है, कि तू न्याय से काम करे, और कृपा से प्रीति रखे, और अपने परमेश्वर के साथ नम्रता से चले?"));
hinsuperobject.biblemap.put("b33_6_9", new hinsuperobject(33,6,9,"यहोवा इस नगर को पुकार रहा है, और सम्पूर्ण ज्ञान, तेरे नाम का भय मानना है: राजदण्ड की, और जो उसे देनेवाला है उसकी बात सुनो!"));
hinsuperobject.biblemap.put("b33_6_10", new hinsuperobject(33,6,10,"क्या अब तक दुष्ट के घर में दुष्टता से पाया हुआ धन और छोटा एपा घृणित नहीं है?"));
hinsuperobject.biblemap.put("b33_6_11", new hinsuperobject(33,6,11,"क्या मैं कपट का तराजू और घटबढ़ के बटखरों की थैली लेकर पवित्रा ठहर सकता हूं?"));
hinsuperobject.biblemap.put("b33_6_12", new hinsuperobject(33,6,12,"यहां के धनवान् लोग उपद्रव का काम देखा करते हैं; और यहां के सब रहनेवाले झूठ बोलते हैं और उनके मुंह से छल की बातें निकलती हैं।"));
hinsuperobject.biblemap.put("b33_6_13", new hinsuperobject(33,6,13,"इस कारण मैं तुझे मारते मारते बहुत ही घायल करता हूं, और तेरे पापों के कारण तुझ को उजाड़ डालता हूं।"));
hinsuperobject.biblemap.put("b33_6_14", new hinsuperobject(33,6,14,"तू खाएगा, परन्तु तृप्त न होगा, तेरा पट जलता ही रहेगा; और तू अपनी सम्पत्ति लेकर चलेगा, परन्तु न बचा सकेगा, और जो कुछ तू बचा भी ले, उसको मैं तलवार चलाकर लुटवा दूंगा।"));
hinsuperobject.biblemap.put("b33_6_15", new hinsuperobject(33,6,15,"तू बोएगा, परन्तु लवेगा नहीं; तू जलपाई का तेल निकालेगा, परन्तु लगाने न पाएगा; और दाख रौंदेगा, परन्तु दाखमधु पीने न पाएगा।"));
hinsuperobject.biblemap.put("b33_6_16", new hinsuperobject(33,6,16,"क्योंकि वे ओम्री की विधियों पर, और अहाब के घराने के सब कामों पर चलते हैं; और तुम उनकी युक्तियों के अनुसार चलते हो; इसलिये मैं तुझे उजाड़ दूंगा, और इस नगर के रहनेवालों पर ताली बजवाऊंगा, और तुम मेरी प्रजा की नामधराई सहोगे।।"));
hinsuperobject.biblemap.put("b33_7_1", new hinsuperobject(33,7,1,"हाय मुझ पर ! क्योंकि मैं उस जल के समान हो गया हूं जो धूपकाल के फल तोड़ने पर, वा रही हुठ दाख बीनने के समय के अन्त में आ जाए, मुझे तो पक्की अंजीरों की लालसा थी, परन्तु खाने के लिये कोई गुच्छा नही रहा।"));
hinsuperobject.biblemap.put("b33_7_2", new hinsuperobject(33,7,2,"भक्त लोग पृथ्वी पर से नाश हो गए हैं, और मनुष्यों में एक भी सीधा नहीं जन नहीं रहा; वे सब के सब हत्या के लिये घात लगाते, और जाल लगाकर अपने अपने भाई का आहेर करते हैं।"));
hinsuperobject.biblemap.put("b33_7_3", new hinsuperobject(33,7,3,"वे अपने दोनों हाथों से मन लगाकर बुराई करते हैं; हाकिम घूस मांगता, और न्यायी घूस लेने को तैयार रहता है, और रईस अपने मन की दुष्टता वर्णन करता है; इसी प्रकार से वे सब मिलकर जालसाजी करते हैं।"));
hinsuperobject.biblemap.put("b33_7_4", new hinsuperobject(33,7,4,"उन में से जो सब से उत्तम है, जो सब से सीधा है, वह कांटेवाले बाड़े से भी बुरा है। तेरे पहरूओं का कहा हुआ दिन, अर्थात् तेरे दण्ड का दिन आ गया है। अब वे शीघ्र चौंधिया जाएंगे।"));
hinsuperobject.biblemap.put("b33_7_5", new hinsuperobject(33,7,5,"मित्रा पर विश्वास मत करो, परममित्रा पर भी भरोसा मत रखो; वरन अपनी अर्द्धागिन से भी संभलकर बोलना।"));
hinsuperobject.biblemap.put("b33_7_6", new hinsuperobject(33,7,6,"क्योंकि पुत्रा पिता का अपमान करता, और बेटी माता के, और पतोह सास के विरूद्ध उठती है; मनुष्य के शत्रु उसके घर ही के लोग होते हैं।"));
hinsuperobject.biblemap.put("b33_7_7", new hinsuperobject(33,7,7,"परन्तु मैं यहोवा की ओर ताकता रहूंगा, मैं अपने उद्धारकर्ता परमेश्वर की बाट जोहता रहूंगा; मेरा परमेश्वर मेरी सुनेगा।।"));
hinsuperobject.biblemap.put("b33_7_8", new hinsuperobject(33,7,8,"हे मेरी बैरिन, मुझ पर आनन्द मत कर; क्योंकि ज्योंही मैं गिरूंगा त्योंही उठूंगा; और ज्योंही मैं अन्धकार में पडूंगा त्योंहि यहोवा मेरे लिये ज्योति का काम देगा।"));
hinsuperobject.biblemap.put("b33_7_9", new hinsuperobject(33,7,9,"मैं ने यहोवा के विरूद्ध पाप किया है, इस कारण मैं उस समय तक उसके क्रोध को सहता रहूंगा जब तक कि वह मेरा मुक मा लड़कर मेरा न्याय न चुकाएगा। उस समय वह मुझे उजियाले में निकाल ले आएगा, और मैं उसका धर्म देखूंगा।"));
hinsuperobject.biblemap.put("b33_7_10", new hinsuperobject(33,7,10,"तब मेरी बैरिन जो मुझ से यह कहती है कि तेरा परमेश्वर यहोवा कहां रहा, वह भी उसे देखेगी और लज्जा से मुंह ढांपेगी। मैं अपनी आंखों से उसे देखूंगा; तब वह सड़कों की कीच की नाईं लताड़ी जाएगी।।"));
hinsuperobject.biblemap.put("b33_7_11", new hinsuperobject(33,7,11,"तेरे बाड़ों के बान्धने के दिन उसकी सीमा बढ़ाई जाएगी।"));
hinsuperobject.biblemap.put("b33_7_12", new hinsuperobject(33,7,12,"उस दिन अश्शूर से, और मि  और महानद के बीच के, और समुद्र- समुद्र और पहाड़- पहाड़ के बीच में देशों से लोग तेरे पास आंएगे।"));
hinsuperobject.biblemap.put("b33_7_13", new hinsuperobject(33,7,13,"तौभी यह देश अपने रहनेवालों के कामों के कारण उजाड़ ही रहेगा।।"));
hinsuperobject.biblemap.put("b33_7_14", new hinsuperobject(33,7,14,"तू लाठी लिये हुए अपनी प्रजा की चरवाही कर, अर्थात् अपने निज भाग की भेड़- बकरियों की, जो कर्म्मेल के वन में अलग बैठती है; वे पूर्वकाल की नाईं बाशान और गिलाद में चरा करें।।"));
hinsuperobject.biblemap.put("b33_7_15", new hinsuperobject(33,7,15,"जैसे कि मि  देश से तेरे निकल आने के दिनों में, वैसी ही अब मैं उसको अद्भुत काम दिखाऊंगा।"));
hinsuperobject.biblemap.put("b33_7_16", new hinsuperobject(33,7,16,"अन्यजातियां देखकर अपने सारे पराक्रम के विषय में लजाएंगी; वे अपने मुंह को हाथ से छिपाएंगी, और उनके कान बहिरे हो जाएंगे।"));
hinsuperobject.biblemap.put("b33_7_17", new hinsuperobject(33,7,17,"वे सर्प की नाईं मिट्टी चाटेंगी, और भूमि पर रेंगनेवाले जन्तुओं की भांति अपने बिलों में से कांपती हुई निकलेंगी; हे हमारे परमेश्वर यहोवा के पास थरथराती हुई आएंगी, और वे तुझ से डरेंगी।।"));
hinsuperobject.biblemap.put("b33_7_18", new hinsuperobject(33,7,18,"तेरे समान ऐसा परमेश्वर कहां है जो अधर्म को क्षमा करे और अपने निज भाग के बचे हुओं के अपराध को ढांप दे? वह अपने क्रोध को सदा बनाए नहीं रहता, क्योंकि वह करूणा से प्रीति रखता है।"));
hinsuperobject.biblemap.put("b33_7_19", new hinsuperobject(33,7,19,"वह फिर हम पर दया करेगा, और हमारे अधर्म के कामों को लताड़ डालेगा। तू उनके सब पापों को गहिरे समुद्र में डाल देगा।"));
hinsuperobject.biblemap.put("b33_7_20", new hinsuperobject(33,7,20,"तू याकूब के विषय में वह सच्चई, और इब्राहीम के विषय में वह करूणा पूरी करेगा, जिस की शपथ तू प्राचीनकाल के दिनों से लेकर अब तक हमारे पितरों से खाता आया है।।"));

}

}