hinsuperobject.biblemap.put("b4_3_21", new hinsuperobject(4,3,21,"गेर्शोन से लिब्नियों और शिमियों के कुल चले; गेर्शोनवंशियों के कुल ये ही हैं।"));
hinsuperobject.biblemap.put("b4_3_22", new hinsuperobject(4,3,22,"इन में से जितने पुरूषों की अवस्था एक महीने की वा उस से अधिक थी, उन सभों की गिनती साढ़े सात हजार थी।"));
hinsuperobject.biblemap.put("b4_3_23", new hinsuperobject(4,3,23,"गेर्शोनवाले कुल निवास के पीछे पच्छिम की ओर अपने डेरे डाला करें;"));
hinsuperobject.biblemap.put("b4_3_24", new hinsuperobject(4,3,24,"और गेर्शोनियों के मूलपुरूष से घराने का प्रधान लाएल का पुत्रा एल्यासाप हो।"));
hinsuperobject.biblemap.put("b4_3_25", new hinsuperobject(4,3,25,"और मिलापवाले तम्बू की जो वस्तुएं गेर्शोनवंशियों को सौंपी जाएं वे ये हों, अर्थात् निवास और तम्बू, और उसका ओहार, और मिलापवाले तम्बू से द्वार का पर्दा,"));
hinsuperobject.biblemap.put("b4_3_26", new hinsuperobject(4,3,26,"और जो आंगन निवास और वेदी की चारों ओर है उसके पर्दे, और उसके द्वार का पर्दा, और सब डोरियां जो उस में काम आती हैं।।"));
hinsuperobject.biblemap.put("b4_3_27", new hinsuperobject(4,3,27,"फिर कहात से अम्रामियों, यिसहारियों, हेब्रोनियों, और उज्जीएलियों के कुल चले; कहातियों के कुल ये ही हैं।"));
hinsuperobject.biblemap.put("b4_3_28", new hinsuperobject(4,3,28,"उन में से जितने पुरूषों की अवस्था एक महीने की वा उस से अधिक थी उनकी गिनती आठ हजार छ: सौ थी। वे पवित्रा स्थान की रक्षा के उत्तरदायी थे।"));
hinsuperobject.biblemap.put("b4_3_29", new hinsuperobject(4,3,29,"कहातियों के कुल निवास की उस अलंग पर अपने डेरे डाला करें जो दक्खिन की ओर है;"));
hinsuperobject.biblemap.put("b4_3_30", new hinsuperobject(4,3,30,"और कहातवाले कुलों से मूलपुरूष के घराने का प्रधान उज्जीएल का पुत्रा एलीसापान हो।"));
hinsuperobject.biblemap.put("b4_3_31", new hinsuperobject(4,3,31,"और जो वस्तुएं उनको सौंपी जाएं वे सन्दूक, मेज़, दीवट, वेदियां, और पवित्रास्थान का वह सामान जिस से सेवा टहल होती है, और पर्दा; निदान पवित्रास्थान में काम में आनेवाला सारा सामान हो।"));
hinsuperobject.biblemap.put("b4_3_32", new hinsuperobject(4,3,32,"और लेवियों के प्रधानों का प्रधान हारून याजक का पुत्रा एलीआजार हो, और जो लोग पवित्रास्थान की सौंपी हुई वस्तुओं की रक्षा करेंगे उन पर वही मुखिया ठहरे।।"));
hinsuperobject.biblemap.put("b4_3_33", new hinsuperobject(4,3,33,"फिर मरारी से महलियों और मूशियों के कुल चले; मरारी के कुल ये ही हैं।"));
hinsuperobject.biblemap.put("b4_3_34", new hinsuperobject(4,3,34,"इन में से जितने पुरूषों की अवस्था एक महीने की वा उस से अधिक थी उन सभों की गिनती छ: हजार दो सौ थी।"));
hinsuperobject.biblemap.put("b4_3_35", new hinsuperobject(4,3,35,"और मरारी के कुलों के मूलपुरूष के घराने का प्रधान अबीहैल का पुत्रा सूरीएल हो; ये लोग निवास के उत्तर की ओर अपने डेरे खड़े करें।"));
hinsuperobject.biblemap.put("b4_3_36", new hinsuperobject(4,3,36,"और जो वस्तुएं मरारीवंशियों को सौंपी जाएं कि वे उनकी रक्षा करें, वे निवास के तख्ते, बेंड़े, खम्भे, कुर्सियां, और सारा सामान; निदान जो कुछ उसके बरतने में काम आए;"));
hinsuperobject.biblemap.put("b4_3_37", new hinsuperobject(4,3,37,"और चारों ओर के आंगन के खम्भे, और उनकी कुर्सियां, खूंटे और डोरियां हों।"));
hinsuperobject.biblemap.put("b4_3_38", new hinsuperobject(4,3,38,"और जो मिलापवाले तम्बू के साम्हने, अर्थात् निवास के साम्हने, पूरब की ओर जहां से सूर्योदय होता है, अपने डेरे डाला करें, वे मूसा और हारून और उसके पुत्रों के डेरे हों, और पवित्रास्थान की रखवाली इस्त्राएलियों के बदले वे ही किया करें, और दूसरा जो कोई उसके समीप आए वह मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_3_39", new hinsuperobject(4,3,39,"यहोवा की इस आज्ञा को पाकर एक महीने की वा उस से अधिक अवस्थावाले जितने लेवीय पुरूषों को मूसा और हारून ने उनके कुलों के अनुसार गिन लिया, वे सब के सब बाईस हजार थे।।"));
hinsuperobject.biblemap.put("b4_3_40", new hinsuperobject(4,3,40,"फिर यहोवा ने मूसा से कहा, इस्त्राएलियों के जितने पहिलौठे पुरूषों की अवस्था एक महीने की वा उस से अधिक है, उन सभों को नाम ले लेकर गिन ले।"));
hinsuperobject.biblemap.put("b4_3_41", new hinsuperobject(4,3,41,"और मेरे लिये इस्त्राएलियों के सब पहिलौठों की सन्ती लेवियों को, और इस्त्राएलियों के पशुओं के सब पहिलौठों की सन्ती लेवियों के पशुओं को ले; मैं यहोवा हूं।"));
hinsuperobject.biblemap.put("b4_3_42", new hinsuperobject(4,3,42,"यहोवा की इस आज्ञा के अनुसार मूसा ने इस्त्राएलियों के सब पहिलौठों को गिन लिया।"));
hinsuperobject.biblemap.put("b4_3_43", new hinsuperobject(4,3,43,"और सब पहिलौठे पुरूष जिनकी अवस्था एक महीने की वा उस से अधिक थी, उनके नामों की गिनती बाईस हजार दो सौ तिहत्तर थी।।"));
hinsuperobject.biblemap.put("b4_3_44", new hinsuperobject(4,3,44,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_3_45", new hinsuperobject(4,3,45,"इस्त्राएलियों के सब पहिलौठों की सन्ती लेवियों को, और उनके पशुओं की सन्ती लेवियों के पशुओं को ले; और लेवीय मेरे ही हों; मैं यहोवा हूं।"));
hinsuperobject.biblemap.put("b4_3_46", new hinsuperobject(4,3,46,"और इस्त्राएलियों के पहिलौठों मे से जो दो सौ तिहत्तर गिनती में लेवियों से अधिक हैं, उनके छुड़ाने के लिये,"));
hinsuperobject.biblemap.put("b4_3_47", new hinsuperobject(4,3,47,"पुरूष पीछे पांच शेकेल ले; वे पवित्रास्थान के शेकेल के हिसाब से हों, अर्थात् बीस गेरा का शेकेल हो।"));
hinsuperobject.biblemap.put("b4_3_48", new hinsuperobject(4,3,48,"और जो रूपया उन अधिक पहिलौठों की छुडौती का होगा उसे हारून और उसके पुत्रों को दे देना।"));
hinsuperobject.biblemap.put("b4_3_49", new hinsuperobject(4,3,49,"और जो इस्त्राएली पहिलौठे लेवियों के द्वारा छुड़ाए हुओं से अधिक थे उनके हाथ से मूसा ने छुड़ौती का रूपया लिया।"));
hinsuperobject.biblemap.put("b4_3_50", new hinsuperobject(4,3,50,"और एक हजार तीन सौ पैंसठ शैकेल रूपया पवित्रास्थान के शेकेल के हिसाब से वसूल हुआ।"));
hinsuperobject.biblemap.put("b4_3_51", new hinsuperobject(4,3,51,"और यहोवा की आज्ञा के अनुसार मूसा ने छुड़ाए हुओं का रूपया हारून और उसके पुत्रों को दे दिया।।"));
hinsuperobject.biblemap.put("b4_4_1", new hinsuperobject(4,4,1,"फिर यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_4_2", new hinsuperobject(4,4,2,"लेवियों में से कहातियों की, उनके कुलों और पितरों के घरानों के अनुसार, गिनती करो,"));
hinsuperobject.biblemap.put("b4_4_3", new hinsuperobject(4,4,3,"अर्थात् तीस वर्ष से लेकर पचास वर्ष तक की अवस्थावालों की सेना में, जितने मिलापवाले तम्बू में कामकाज करने को भरती हैं।"));
hinsuperobject.biblemap.put("b4_4_4", new hinsuperobject(4,4,4,"और मिलापवाले तम्बू में परमपवित्रा वस्तुओं के विषय कहातियों का यह काम होगा,"));
hinsuperobject.biblemap.put("b4_4_5", new hinsuperobject(4,4,5,"अर्थात् जब जब छावनी का कूच हो तब तब हारून और उसके पुत्रा भीतर आकर, बीचवाले पर्दे को उतार के उस से साक्षीपत्रा के सन्दूक को ढंाप दें;"));
hinsuperobject.biblemap.put("b4_4_6", new hinsuperobject(4,4,6,"तब वे उस पर सूइसों की खालों का ओहार डालें, और उसके ऊपर सम्पूर्ण नीले रंग का कपड़ा डालें, और सन्दूक में डण्डों को लगाएं।"));
hinsuperobject.biblemap.put("b4_4_7", new hinsuperobject(4,4,7,"फिर भेंटवाली रोटी की मेज़ पर नीला कपड़ा बिछाकर उस पर परातों, धूपदानों, करवों, और उंडेलने के कटोरों को रखें; और नित्य की रोटी भी उस पर हो;"));
hinsuperobject.biblemap.put("b4_4_8", new hinsuperobject(4,4,8,"तब वे उन पर लाल रंग का कपड़ा बिछाकर उसको सुइसों की खालों के ओहार से ढ़ापे, और मेज़ के डण्डों को लगा दें।"));
hinsuperobject.biblemap.put("b4_4_9", new hinsuperobject(4,4,9,"फिर वे नीले रंग का कपड़ा लेकर दीपकों, गलतराशों, और गुलदानों समेत उजियाला देनेवाले दीवट को, और उसके सब तेल के पात्रों को जिन से उसकी सेवा टहल होती है ढांपे;"));
hinsuperobject.biblemap.put("b4_4_10", new hinsuperobject(4,4,10,"तब वे सारे सामान समेत दीवट को सूइसों की खालों के ओहार के भीतर रखकर डण्डे पर धर दें।"));
hinsuperobject.biblemap.put("b4_4_11", new hinsuperobject(4,4,11,"फिर वे सोने की वेदी पर एक नीला कपड़ा बिछाकर उसको सूइसों की खालों के ओहार के भीतर रखकर डण्डे पर धर दें।"));
hinsuperobject.biblemap.put("b4_4_12", new hinsuperobject(4,4,12,"तब वे सेवा टहल के सारे सामान को लेकर, जिस से पवित्रास्थान में सेवा टहल होली है, नीले कपड़े के भीतर रखकर सूइसों की खालों के ओहार से ढांपे, और डण्डे पर धर दें।"));
hinsuperobject.biblemap.put("b4_4_13", new hinsuperobject(4,4,13,"फिर वे वेदी पर से सब राख उठाकर वेदी पर बैंजनी रंग का कपड़ा बिछाएं;"));
hinsuperobject.biblemap.put("b4_4_14", new hinsuperobject(4,4,14,"तब जिस सामान से वेदी पर की सेवा टहल होती है वह सब, अर्थात् उसके करछे, कांटे, फावड़ियां, और कटोरे आदि, वेदी का सारा सामान उस पर रखें; और उसके ऊपर सूइसों की खालों का ओहार बिछाकर वेदी में डण्डों को लगाएं।"));
hinsuperobject.biblemap.put("b4_4_15", new hinsuperobject(4,4,15,"और जब हारून और उसके पुत्रा छावनी के कूच के समय पवित्रास्थान और उसके सारे सामान को ढंाप चुकें, तब उसके बाद कहाती उसके उठाने के लिये आएं, पर किसी पवित्रा वस्तु को न छुएं, कहीं ऐसा न हो कि मर जाएं। कहातियों के उठाने के लिये मिलापवाले तम्बू की ये ही वस्तुएं हैं।"));
hinsuperobject.biblemap.put("b4_4_16", new hinsuperobject(4,4,16,"और जो वस्तुएं हारून याजक के पुत्रा एलीजार को रक्षा के लिये सौंपी जाएं वे ये हैं, अर्थात् उजियाला देने के लिये तेल, और सुगन्धित धूप, और नित्य अन्नबलि, और अभिषेक का तेल, और सारे निवास, और उस में की सब वस्तुएं, और पवित्रास्थान और उसके कुल समान।।"));
hinsuperobject.biblemap.put("b4_4_17", new hinsuperobject(4,4,17,"फिर यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_4_18", new hinsuperobject(4,4,18,"कहातियों के कुलों के गोत्रियों को लेवियों में से नाश न होने देना;"));
hinsuperobject.biblemap.put("b4_4_19", new hinsuperobject(4,4,19,"उसके साथ ऐसा करो, कि जब वे परमपवित्रा वस्तुओं के समीप आएं तब न मरें परन्तु जीवित रहें; अर्थात् हारून और उसके पुत्रा भीतर आकर एक एक के लिये उसकी सेवकाई और उसका भार ठहरा दें,"));
hinsuperobject.biblemap.put("b4_4_20", new hinsuperobject(4,4,20,"और वे पवित्रा वस्तुओं के देखने को क्षण भर के लिये भी भीतर आने न पाएं, कहीं ऐसा न हो कि मर जाएं।।"));
hinsuperobject.biblemap.put("b4_4_21", new hinsuperobject(4,4,21,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_4_22", new hinsuperobject(4,4,22,"गेर्शोनियों की भी गिनती उनके पितरों के घरानों और कुलों के अनुसार कर;"));
hinsuperobject.biblemap.put("b4_4_23", new hinsuperobject(4,4,23,"तीस वर्ष से लेकर पचास वर्ष तक की अवस्थावाले, जितने मिलापवाले तम्बू में सेवा करने को सेना में भरती हों उन सभों को गिन ले।"));
hinsuperobject.biblemap.put("b4_4_24", new hinsuperobject(4,4,24,"सेवा करने और भार उठाने में गेर्शोनियों के कुलवालों की यह सेवकाई हो;"));
hinsuperobject.biblemap.put("b4_4_25", new hinsuperobject(4,4,25,"अर्थात् वे निवास के पटों, और मिलापवाले तम्बू और उसके ओहार, और इसके ऊपरवाले सूइसों की खालों के ओहार, और मिलापवाले तम्बू के द्वार के पर्दे,"));
hinsuperobject.biblemap.put("b4_4_26", new hinsuperobject(4,4,26,"और निवास, और वेदी की चारों ओर के आंगन के पर्दों, और आंगन के द्वार के पर्दे, और उनकी डोरियों, और उन में बरतने के सारे सामान, इन सभों को वे उठाया करें; और इन वस्तुओं से जितना काम होता है वह सब भी उनकी सेवकाई में आए।"));
hinsuperobject.biblemap.put("b4_4_27", new hinsuperobject(4,4,27,"और गेर्शोनियों के वंश की सारी सेवकाई हारून और उसके पुत्रों के कहने से हुआ करे, अर्थात् जो कुछ उनको उठाना, और जो जो सेवकाई उनको करनी हो, उनका सारा भार तुम ही उन्हें सौपा करो।"));
hinsuperobject.biblemap.put("b4_4_28", new hinsuperobject(4,4,28,"मिलापवाले तम्बू में गेर्शोनियों के कुलों की यही सेवकाई ठहरे; और उन पर हारून याजक का पुत्रा ईतामार अधिकार रखे।।"));
hinsuperobject.biblemap.put("b4_4_29", new hinsuperobject(4,4,29,"फिर मरारियों को भी तू उनके कुलों और पितरों के घरानों के अनुसार गिन लें;"));
hinsuperobject.biblemap.put("b4_4_30", new hinsuperobject(4,4,30,"तीस वर्ष से लेकर पचास वर्ष तक की अवस्थावाले, जितने मिलापवाले तम्बू की सेवा करने को सेना में भरती हों, उन सभों को गिन ले।"));
hinsuperobject.biblemap.put("b4_4_31", new hinsuperobject(4,4,31,"और मिलापवाले तम्बू में की जिन वस्तुओं के उठाने की सेवकाई उनको मिले वे ये हों, अर्थात् निवास के तख्ते, बेड़े, खम्भे, और कुर्सियां,"));
hinsuperobject.biblemap.put("b4_4_32", new hinsuperobject(4,4,32,"और चारों ओर आंगन के खम्भे, और इनकी कुर्सियां, खूंटे, डोरियां, और भांति भांति के बरतने का सारा सामान; और जो जो सामान ढ़ोने के लिये उनको सौपा जाए उस में से एक एक वस्तु का नाम लेकर तुम गिन दो।"));
hinsuperobject.biblemap.put("b4_4_33", new hinsuperobject(4,4,33,"मरारियों के कुलों की सारी सेवकाई जो उन्हें मिलापवाले तम्बू के विषय करनी होगी वह यही है; वह हारून याजक के पुत्रा ईतामार के अधिकार में रहे।।"));
hinsuperobject.biblemap.put("b4_4_34", new hinsuperobject(4,4,34,"तब मूसा और हारून और मण्डली के प्रधानों ने कहातियों के वंश को, उनके कुलों और पितरों के घरानों के अनुसार,"));
hinsuperobject.biblemap.put("b4_4_35", new hinsuperobject(4,4,35,"तीस वर्ष से लेकर पचास वर्ष की अवस्था के, जितने मिलापवाले तम्बू की सेवकाई करने को सेना में भरती हुए थे, उन सभों को गिन लिया;"));
hinsuperobject.biblemap.put("b4_4_36", new hinsuperobject(4,4,36,"और जो अपने अपने कुल के अनुसार गिने गए वे दो हजार साढ़े सात सौ थे।"));
hinsuperobject.biblemap.put("b4_4_37", new hinsuperobject(4,4,37,"कहातियों के कुलों में से जितने मिलापवाले तम्बू में सेवा करने वाले गिने गए वे इतने ही थे; जो आज्ञा यहोवा ने मूसा के द्वारा दी थी उसी के अनुसार मूसा और हारून ने इनको गिन लिया।।"));
hinsuperobject.biblemap.put("b4_4_38", new hinsuperobject(4,4,38,"और गेर्शोनियों में से जो अपने कुलों और पितरों के घरानों के अनुसार गिने गए,"));
hinsuperobject.biblemap.put("b4_4_39", new hinsuperobject(4,4,39,"अर्थात् तीस वर्ष से लेकर पचास वर्ष तक की अवस्था के, जो मिलापवाले तम्बू की सेवकाई करने को सेना में भरती हुए थे,"));
hinsuperobject.biblemap.put("b4_4_40", new hinsuperobject(4,4,40,"उनकी गिनती उनके कुलों और पितरों के घरानों के अनुसार दो हजार छ: सौ तीस थी।"));
hinsuperobject.biblemap.put("b4_4_41", new hinsuperobject(4,4,41,"गेर्शोनियों के कुलों में से जितने मिलापवाले तम्बू में सेवा करनेवाले गिने गए वे इतने ही थे; यहोवा की आज्ञा के अनुसार मूसा और हारून ने इनको गिन लिया।।"));
hinsuperobject.biblemap.put("b4_4_42", new hinsuperobject(4,4,42,"फिर मरारियों के कुलों में से जो अपने कुलों और पितरों के घरानों के अनुसार गिने गए,"));
hinsuperobject.biblemap.put("b4_4_43", new hinsuperobject(4,4,43,"अर्थात् तीस वर्ष से लेकर पचास वर्ष तक की अवस्था के, जो मिलापवाले तम्बू की सेवकाई करने को सेना में भरती हुए थे,"));
hinsuperobject.biblemap.put("b4_4_44", new hinsuperobject(4,4,44,"उनकी गिनती उनके कुलों के अनुसार तीन हजार दो सौ थी।"));
hinsuperobject.biblemap.put("b4_4_45", new hinsuperobject(4,4,45,"मरारियों के कुलों में से जिनको मूसा और हारून ने, यहोवा की उस आज्ञा के अनुसार जो मूसा के द्वारा मिली थी, गिन लिया वे इतने ही थे।।"));
hinsuperobject.biblemap.put("b4_4_46", new hinsuperobject(4,4,46,"लेवियों में से जिनको मूसा और हारून और इस्त्राएली प्रधानों ने उनके कुलों और पितरों के घरानों के अनुसार गिन लिया,"));
hinsuperobject.biblemap.put("b4_4_47", new hinsuperobject(4,4,47,"अर्थात् तीस वर्ष से लेकर पचास वर्ष तक की अवस्थावाले, जितने मिलापवाले तम्बू की सेवकाई करने का बोझ उठाने का काम करने को हाजिर होने वाले थे,"));
hinsuperobject.biblemap.put("b4_4_48", new hinsuperobject(4,4,48,"उन सभों की गिनती आठ हजार पांच सौ अस्सी थी।"));
hinsuperobject.biblemap.put("b4_4_49", new hinsuperobject(4,4,49,"ये अपनी अपनी सेवा और बोझ ढ़ोने के अनुसार यहोवा के कहने पर गए। जो आज्ञा यहोवा ने मूसा को दी थी उसी के अनुसार वे गिने गए।।"));
hinsuperobject.biblemap.put("b4_5_1", new hinsuperobject(4,5,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_5_2", new hinsuperobject(4,5,2,"इस्त्राएलियों को आज्ञा दे, कि वे सब कोढ़ियों को, और जितनों के प्रमेह हो, और जितने लोथ के कारण अशुद्ध हों, उन सभों को छावनी से निकाल दें;"));
hinsuperobject.biblemap.put("b4_5_3", new hinsuperobject(4,5,3,"ऐसों को चाहे पुरूष हों चाहे स्त्री छावनी से निकालकर बाहर कर दें; कहीं ऐसा न हो कि तुम्हारी छावनी, जिसके बीच मैं निवास करता हूं, उनके कारण अशुद्ध हो जाए।"));
hinsuperobject.biblemap.put("b4_5_4", new hinsuperobject(4,5,4,"और इस्त्राएलियों ने वैसा ही किया, अर्थात् ऐसे लोगों को छावनी से निकालकर बाहर कर दिया; जैसा यहोवा ने मूसा से कहा था इस्त्राएलियों ने वैसा ही किया।।"));
hinsuperobject.biblemap.put("b4_5_5", new hinsuperobject(4,5,5,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_5_6", new hinsuperobject(4,5,6,"इस्त्राएलियों से कह, कि जब कोई पुरूष वा स्त्री ऐसा कोई पाप करके जो लोग किया करते हैं यहोवा को विश्वासघात करे, और वह प्राणी दोषी हो,"));
hinsuperobject.biblemap.put("b4_5_7", new hinsuperobject(4,5,7,"तब वह अपना किया हुआ पाप मान ले; और पूरे मूल में पांचवां अंश बढ़ाकर अपने दोष के बदले में उसी को दे, जिसके विषय दोषी हुआ हो।"));
hinsuperobject.biblemap.put("b4_5_8", new hinsuperobject(4,5,8,"परन्तु यदि उस मनुष्य का कोई कुटुम्बी न हो जिसे दोष का बदला भर दिया जाए, तो उस दोष का जो बदला यहोवा को भर दिया जाए वह याजक का हो, और वह उस प्रायश्चित्तवाले मेढ़े से अधिक हो जिस से उसके लिये प्रायश्चित्त किया जाए।"));
hinsuperobject.biblemap.put("b4_5_9", new hinsuperobject(4,5,9,"और जितनी पवित्रा की हुई वस्तुएं इस्त्राएली उठाई हुई भेंट करके याजक के पास लाएं, वे उसी की हों;"));
hinsuperobject.biblemap.put("b4_5_10", new hinsuperobject(4,5,10,"सब मनुष्यों की पवित्रा की हुई वस्तुएं उसी की ठहरें; कोई जो कुछ याजक को दे वह उसका ठहरे।।"));
hinsuperobject.biblemap.put("b4_5_11", new hinsuperobject(4,5,11,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_5_12", new hinsuperobject(4,5,12,"इस्त्राएलियों से कह, कि यदि किसी मनुष्य की स्त्री कुचाल चलकर उसका विश्वासघात करे,"));
hinsuperobject.biblemap.put("b4_5_13", new hinsuperobject(4,5,13,"और कोई पुरूष उसके साथ कुकर्म करे, परन्तु यह बात उसके पति से छिपी हो और खुली न हो, और वह अशुद्ध हो गई, परन्तु न तो उसके विरूद्ध कोई साक्षी हो, और न कुकर्म करते पकड़ी गई हो;"));
hinsuperobject.biblemap.put("b4_5_14", new hinsuperobject(4,5,14,"और उसके पति के मन में जलन उत्पन्न हो, अर्थात् वह अपने स्त्री पर जलने लगे और वह अशुद्ध हुई हो; वा उसके मन में जलन उत्पन्न हो, अर्थात् वह अपनी स्त्री पर जलने लगे परन्तु वह अशुद्ध न हुई हो;"));
hinsuperobject.biblemap.put("b4_5_15", new hinsuperobject(4,5,15,"तो वह पुरूष अपनी स्त्री को याजक के पास ले जाए, और उसके लिये एपा का दसवां अंश जव का मैदा चढ़ावा करके ले आए; परन्तु उस पर तेल न डाले, न लोबान रखे, क्योंकि वह जलनवाला और स्मरण दिलानेवाला, अर्थात् अधर्म का स्मरण करानेवाला अन्नबलि होगा।"));
hinsuperobject.biblemap.put("b4_5_16", new hinsuperobject(4,5,16,"तब याजक उस स्त्री को समीप ले जाकर यहोवा के साम्हने खड़ी करे;"));
hinsuperobject.biblemap.put("b4_5_17", new hinsuperobject(4,5,17,"और याजक मिट्टी के पात्रा में पवित्रा जल ले, और निवासस्थान की भूमि पर की धूलि में से कुछ लेकर उस जल में डाल दे।"));
hinsuperobject.biblemap.put("b4_5_18", new hinsuperobject(4,5,18,"तब याजक उस स्त्री को यहोवा के साम्हने खड़ी करके उसके सिर के बाल बिखराए, और स्मरण दिलानेवाले अन्नबलि को जो जलनवाला है उसके हाथों पर धर दे। और अपने हाथ में याजक कडुवा जल लिये रहे जो शाप लगाने का कारण होगा।"));
hinsuperobject.biblemap.put("b4_5_19", new hinsuperobject(4,5,19,"तब याजक स्त्री को शपथ धरवाकर कहे, कि यदि किसी पुरूष ने तुझ से कुकर्म न किया हो, और तू पति को छोड़ दूसरे की ओर फिरके अशुद्ध न हो गई हो, तो तू इस कडुवे जल के गुण से जो शाप का कारण होता है बची रहे।"));
hinsuperobject.biblemap.put("b4_5_20", new hinsuperobject(4,5,20,"पर यदि तू अपने पति को छोड़ दूसरे की ओर फिरके अशुद्ध हुई हो, और तेरे पति को छोड़ किसी दूसरे पुरूष ने तुझ से प्रसंग किया हो,"));
hinsuperobject.biblemap.put("b4_5_21", new hinsuperobject(4,5,21,"(और याजक उसे शाप देनेवाली शपथ धराकर कहे,) यहोवा तेरी जांघ सड़ाए और तेरा पेट फुलाए, और लोग तेरा नाम लेकर शाप और धिक्कार दिया करें;"));
hinsuperobject.biblemap.put("b4_5_22", new hinsuperobject(4,5,22,"अर्थात् वह जल जो शाप का कारण होता है तेरी अंतड़ियों में जाकर तेरे पेट को फुलाए, और तेरी जांघ को सड़ा दे। तब वह स्त्री कहे, आमीन, आमीन।"));
hinsuperobject.biblemap.put("b4_5_23", new hinsuperobject(4,5,23,"तब याजक शाप के ये शब्द पुस्तक में लिखकर उस कडुवे जल से मिटाके,"));
hinsuperobject.biblemap.put("b4_5_24", new hinsuperobject(4,5,24,"उस स्त्री को वह कडुवा जल पिलाए जो शाप का कारण होगा उस स्त्री के पेट में जाकर कडुवा हो जाएगा।"));
hinsuperobject.biblemap.put("b4_5_25", new hinsuperobject(4,5,25,"और याजक स्त्री के हाथ में से जलनवाले अन्नबलि को लेकर यहोवा के आगे हिलाकर वेदी के समीप पहुंचाए;"));
hinsuperobject.biblemap.put("b4_5_26", new hinsuperobject(4,5,26,"और याजक उस अन्नबलि में से उसका स्मरण दिलानेवाला भाग, अर्थात् मुट्ठी भर लेकर वेदी पर जलाए, और उसके बाद स्त्री को वह जल पिलाए।"));
hinsuperobject.biblemap.put("b4_5_27", new hinsuperobject(4,5,27,"और जब वह उसे वह जल पिला चुके, तब यदि वह अशुद्ध हुई हो और अपने पति का विश्वासघात किया हो, तो वह जल जो शाप का कारण होता है उस स्त्री के पेट में जाकर कडुवा हो जाएगा, और उसका पेट फूलेगा, और उसकी जांघ सड़ जाएगी, और उस स्त्री का नाम उसके लोगों के बीच स्रापित होगा।"));
hinsuperobject.biblemap.put("b4_5_28", new hinsuperobject(4,5,28,"पर यदि वह स्त्री अशुद्ध न हुई हो और शुद्ध ही हो, तो वह निर्दोष ठहरेगी और गर्भिणी हो सकेगी।"));
hinsuperobject.biblemap.put("b4_5_29", new hinsuperobject(4,5,29,"जलन की व्यवस्था यही है, चाहे कोई स्त्री अपने पति को छोड़ दूसरे की ओर फिरके अशुद्ध हो,"));
hinsuperobject.biblemap.put("b4_5_30", new hinsuperobject(4,5,30,"चाहे पुरूष के मन में जलन उत्पन्न हो और वह अपनी स्त्री पर जलने लगे; तो वह उसको यहोवा के सम्मुख खड़ी कर दे, और याजक उस पर यह सारी व्यवस्था पूरी करे।"));
hinsuperobject.biblemap.put("b4_5_31", new hinsuperobject(4,5,31,"तब पुरूष अधर्म से बचा रहेगा, और स्त्री अपने अधर्म का बोझ आप उठाएगी।।"));
hinsuperobject.biblemap.put("b4_6_1", new hinsuperobject(4,6,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_6_2", new hinsuperobject(4,6,2,"इस्त्राएलियों से कह, कि जब कोई पुरूष वा स्त्री नाज़ीर की मन्नत, अर्थात् अपने को यहोवा के लिये न्यारा करने की विशेष मन्नत माने,"));
hinsuperobject.biblemap.put("b4_6_3", new hinsuperobject(4,6,3,"तब वह दाखमधु आदि मदिरा से न्यारा रहे; वह न दाखमधु का, न और मदिरा का सिरका पीए, और न दाख का कुछ रस भी पीए, वरन दाख न खाए, चाहे हरी हो चाहे सूखी।"));
hinsuperobject.biblemap.put("b4_6_4", new hinsuperobject(4,6,4,"जितने दिन यह न्यारा रहे उतने दिन तक वह बीज से ले छिलके तक, जो कुछ दाखलता से उत्पन्न होता है, उस में से कुछ न खाए।"));
hinsuperobject.biblemap.put("b4_6_5", new hinsuperobject(4,6,5,"फिर जितने दिन उस ने न्यारे रहने की मन्नत मानी हो उतने दिन तक वह अपने सिर पर छुरा न फिराए; और जब तक वे दिन पूरे न हों जिन में वह यहोवा के लिये न्यारा रहे तब तक वह पवित्रा ठहरेगा, और अपने सिर के बालों को बढ़ाए रहे।"));
hinsuperobject.biblemap.put("b4_6_6", new hinsuperobject(4,6,6,"जितने दिन वह यहोवा के लिये न्यारा रहे उतने दिन तक किसी लोथ के पास न जाए।"));
hinsuperobject.biblemap.put("b4_6_7", new hinsuperobject(4,6,7,"चाहे उसका पिता, वा माता, वा भाई, वा बहिन भी मरे, तौभी वह उनके कारण अशुद्ध न हो; क्योंकि अपने परमेश्वर के लिये न्यारा रहने का चिन्ह उसके सिर पर होगा।"));
hinsuperobject.biblemap.put("b4_6_8", new hinsuperobject(4,6,8,"अपने न्यारे रहने के सारे दिनों में वह यहोवा के लिये पवित्रा ठहरा रहे।"));
hinsuperobject.biblemap.put("b4_6_9", new hinsuperobject(4,6,9,"और यदि कोई उसके पास अचानक मर जाए, और उसके न्यारे रहने का जो चिन्ह उसके सिर पर होगा वह अशुद्ध हो जाए, तो वह शुद्ध होने के दिन, अर्थात् सातवें दिन अपने सिर मुंड़ाए।"));
hinsuperobject.biblemap.put("b4_6_10", new hinsuperobject(4,6,10,"और आठवें दिन वह दो पंडुक वा कबूतरी के दो बच्चे मिलापवाले तम्बू के द्वार पर याजक के पास ले जाए,"));
hinsuperobject.biblemap.put("b4_6_11", new hinsuperobject(4,6,11,"और याजक एक को पापबलि, और दूसरे को होमबलि करके उसके लिये प्रायश्चित्त करे, क्योंकि वह लोथ के कारण पापी ठहरा है। और याजक उसी दिन उसका सिर फिर पवित्रा करे,"));
hinsuperobject.biblemap.put("b4_6_12", new hinsuperobject(4,6,12,"और वह अपने न्यारे रहने के दिनों को फिर यहोवा के लिये न्यारे ठहराए, और एक वर्ष का एक भेड़ का बच्चा दोषबलि करके ले आए; और जो दिन इस से पहिले बीत गए हों वे व्यर्थ गिने जाए, क्योंकि उसके न्यारे रहने का चिन्ह अशुद्ध हो गया।।"));
hinsuperobject.biblemap.put("b4_6_13", new hinsuperobject(4,6,13,"फिर जब नाज़ीर के न्यारे रहने के दिन पूरे हों, उस समय के लिये उसकी यह व्यवस्था है; अर्थात् वह मिलापवाले तम्बू के द्वार पर पहुंचाया जाए,"));
hinsuperobject.biblemap.put("b4_6_14", new hinsuperobject(4,6,14,"और वह यहोवा के लिये होमबलि करके एक वर्ष का एक निर्दोष भेड़ का बच्चा पापबलि करके, और एक वर्ष की एक निर्दोष भेड़ की बच्ची, और मेलबलि के लिये एक निर्दोष मेढ़ा,"));
hinsuperobject.biblemap.put("b4_6_15", new hinsuperobject(4,6,15,"और अखमीरी रोटियों की एक टोकरी, अर्थात् तेल से सने हुए मैदे के फुलके, और तेल से चुपड़ी हुई अखमीरी पपड़ियां, और उन बलियों के अन्नबलि और अर्घ; ये सब चढ़ावे समीप ले जाए।"));
hinsuperobject.biblemap.put("b4_6_16", new hinsuperobject(4,6,16,"इन सब को याजक यहोवा के साम्हने पहुंचाकर उसके पापबलि और होमबलि को चढ़ाए,"));
hinsuperobject.biblemap.put("b4_6_17", new hinsuperobject(4,6,17,"और अखमीरी रोटी की टोकरी समेत मेढ़े को यहोवा के लिये मेलबलि करके, और उस मेलबलि के अन्नबलि और अर्घ को भी चढ़ाए।"));
hinsuperobject.biblemap.put("b4_6_18", new hinsuperobject(4,6,18,"तब नाज़ीर अपने न्यारे रहने के चिन्हवाले सिर को मिलापवाले तम्बू के द्वार पर मुण्डाकर अपने बालों को उस आग पर डाल दे जो मेलबलि के नीचे होगी।"));
hinsuperobject.biblemap.put("b4_6_19", new hinsuperobject(4,6,19,"फिर जब नाज़ीर अपने न्यारे रहने के चिन्हवाले सिर को मुण्डा चुके तब याजक मेढ़े को पकाया हुआ कन्धा, और टोकरी में से एक अखमीरी रोटी, और एक अखमीरी पपड़ी लेकर नाज़ीर के हाथों पर धर दे,"));
hinsuperobject.biblemap.put("b4_6_20", new hinsuperobject(4,6,20,"और याजक इनको हिलाने की भेंट करके यहोवा के साम्हने हिलाए; हिलाई हुई छाती और उठाई हुई जांघ समेत ये भी याजक के लिये पवित्रा ठहरें; इसके बाद वह नाज़ीर दाखमधु पी सकेगा।"));
hinsuperobject.biblemap.put("b4_6_21", new hinsuperobject(4,6,21,"नाज़ीर की मन्नत की, और जो चढ़ावा उसको अपने न्यारे होने के कारण यहोवा के लिये चढ़ाना होगा उसकी भी यही व्यवस्था है। जो चढ़ावा वह अपनी पूंजी के अनुसार चढ़ा सके, उस से अधिक जैसी मन्नत उस ने मानी हो, वैसे ही अपने न्यारे रहने की व्यवस्था के अनुसार उसे करना होगा।।"));
hinsuperobject.biblemap.put("b4_6_22", new hinsuperobject(4,6,22,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_6_23", new hinsuperobject(4,6,23,"हारून और उसके पुत्रों से कह, कि तुम इस्त्राएलियों को इन वचनों से आशीर्वाद दिया करना कि,"));
hinsuperobject.biblemap.put("b4_6_24", new hinsuperobject(4,6,24,"यहोवा तुझे आशीष दे और तेरी रक्षा करे:"));
hinsuperobject.biblemap.put("b4_6_25", new hinsuperobject(4,6,25,"यहोवा तुझ पर अपने मुख का प्रकाश चमकाए, और तुझ पर अनुग्रह करे:"));
hinsuperobject.biblemap.put("b4_6_26", new hinsuperobject(4,6,26,"यहोवा अपना मुख तेरी ओर करे, और तुझे शांति दे।"));
hinsuperobject.biblemap.put("b4_6_27", new hinsuperobject(4,6,27,"इस रीति से मेरे नाम को इस्त्राएलियों पर रखें, और मैं उन्हें आशीष दिया करूंगा।।"));
hinsuperobject.biblemap.put("b4_7_1", new hinsuperobject(4,7,1,"फिर जब मूसा ने निवास को खड़ा किया, और सारे सामान समेत उसका अभिषेक करके उसको पवित्रा किया, और सारे सामान समेत वेदी का भी अभिषेक करके उसे पवित्रा किया,"));
hinsuperobject.biblemap.put("b4_7_2", new hinsuperobject(4,7,2,"तब इस्त्राएल के प्रधान जो अपने अपने पितरों के घरानों के मुख्य पुरूष, और गोत्रों के भी प्रधान होकर गिनती लेने के काम पर नियुक्त थे,"));
hinsuperobject.biblemap.put("b4_7_3", new hinsuperobject(4,7,3,"वे यहोवा के साम्हने भेंट ले आए, और उनकी भेंट छ: छाई हुई गाड़ियां और बारह बैल थे, अर्थात् दो दो प्रधान की ओर से एक एक गाड़ी, और एक एक प्रधान की ओर से एक एक बैल; इन्हें वे निवास के साम्हने यहोवा के समीप ले गए।"));
hinsuperobject.biblemap.put("b4_7_4", new hinsuperobject(4,7,4,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_7_5", new hinsuperobject(4,7,5,"उन वस्तुओं को तू उन से ले ले, कि मिलापवाले तम्बू के बरतन में काम आएं, सो तू उन्हें लेवियों के एक एक कुल की विशेष सेवकाई के अनुसार उनको बांट दे।"));
hinsuperobject.biblemap.put("b4_7_6", new hinsuperobject(4,7,6,"सो मूसा ने वे सब गाड़ियां और बैल लेकर लेवियों को दे दिये।"));
hinsuperobject.biblemap.put("b4_7_7", new hinsuperobject(4,7,7,"गेर्शोनियों को उनकी सेवकाई के अनुसार उस ने दो गाड़ियां और चार बैल दिए;"));
hinsuperobject.biblemap.put("b4_7_8", new hinsuperobject(4,7,8,"और मरारियों को उनकी सेवकाई के अनुसार उस ने चार गाड़ियां और आठ बैल दिए; ये सब हारून याजक के पुत्रा ईतामार के अधिकार में किए गए।"));
hinsuperobject.biblemap.put("b4_7_9", new hinsuperobject(4,7,9,"और कहातियों को उस ने कुछ न दिया, क्योंकि उनके लिये पवित्रा वस्तुओं की यह सेवकाई थी कि वह उसे अपने कन्धों पर उठा लिया करें।।"));
hinsuperobject.biblemap.put("b4_7_10", new hinsuperobject(4,7,10,"फिर जब वेदी का अभिषेक हुआ तब प्रधान उसके संस्कार की भेंट वेदी के आगे समीप ले जाने लगे।"));
hinsuperobject.biblemap.put("b4_7_11", new hinsuperobject(4,7,11,"तब यहोवा ने मूसा से कहा, वेदी के संस्कार के लिये प्रधान लोग अपनी अपनी भेंट अपने अपने नियत दिन पर चढ़ाएं।।"));
hinsuperobject.biblemap.put("b4_7_12", new hinsuperobject(4,7,12,"सो जो पुरूष पहिले दिन अपनी भेंट ले गया वह यहूदा गोत्रावाले अम्मीनादाब का पुत्रा महशोन था;"));
hinsuperobject.biblemap.put("b4_7_13", new hinsuperobject(4,7,13,"उसकी भेंट यह थी, अर्थात् पवित्रास्थानवाले शेकेल के हिसाब से एक सौ तीस शेकेल चांदी का एक परात, और सत्तर शेकेल चांदी का एक कटोरा, ये दोनों अन्नबलि के लिये तेल से सने हुए और मैदे से भरे हुए थे;"));
hinsuperobject.biblemap.put("b4_7_14", new hinsuperobject(4,7,14,"फिर धूप से भरा हुआ दस शेकेल सोने का एक धूपदान;"));
hinsuperobject.biblemap.put("b4_7_15", new hinsuperobject(4,7,15,"होमबलि के लिये एक बछड़ा, एक मेढ़ा, और एक वर्ष का एक भेड़ी का बच्चा;"));
hinsuperobject.biblemap.put("b4_7_16", new hinsuperobject(4,7,16,"पापबलि के लिये एक बकरा;"));
