hinsuperobject.biblemap.put("b4_7_27", new hinsuperobject(4,7,27,"होमबलि के लिये एक बछड़ा, एक मेढ़ा, और एक वर्ष का एक भेड़ी का बच्चा;"));
hinsuperobject.biblemap.put("b4_7_28", new hinsuperobject(4,7,28,"पापबलि के लिये एक बकरा;"));
hinsuperobject.biblemap.put("b4_7_29", new hinsuperobject(4,7,29,"और मेलबलि के लिये दो बैल, और पांच मेढ़े, और पांच बकरे, और एक एक वर्ष के पांच भेड़ी के बच्चे। हेलोन के पुत्रा एलीआब की यहीं भेंट थी।।"));
