hinsuperobject.biblemap.put("b4_8_7", new hinsuperobject(4,8,7,"उन्हें शुद्ध करने के लिये तू ऐसा कर, कि पावन करने वाला जल उन पर छिड़क दे, फिर वे सर्वांग मुण्डन कराएं, और वस्त्रा धोएं, और वे अपने को स्वचछ करें।"));
hinsuperobject.biblemap.put("b4_8_8", new hinsuperobject(4,8,8,"तब वे तेल से सने हुए मैदे के अन्नबलि समेत एक बछड़ा ले लें, और तू पापबलि के लिये एक दूसरा बछड़ा लेना।"));
hinsuperobject.biblemap.put("b4_8_9", new hinsuperobject(4,8,9,"और तू लेवियों को मिलापवाले तम्बू के साम्हने समीप पहुंचाना, और इस्त्राएलियों की सारी मण्डली को इकट्ठा करना।"));
hinsuperobject.biblemap.put("b4_8_10", new hinsuperobject(4,8,10,"तब तू लेवियों को यहोवा के आगे समीप ले आना, और इस्त्राएली अपने अपने हाथ उन पर रखें,"));
hinsuperobject.biblemap.put("b4_8_11", new hinsuperobject(4,8,11,"तब हारून लेवियों को यहोवा के साम्हने इस्त्राएलियों की ओर से हिलाई हुई भेंट करके अर्पण करे, कि वे यहोवा की सेवा करनेवाले ठहरें।"));
hinsuperobject.biblemap.put("b4_8_12", new hinsuperobject(4,8,12,"और लेवीय अपने अपने हाथ उन बछड़ों के सिरों पर रखें; तब तू लेवियों के लिये प्रायश्चित्त करने को एक बछड़ा पापबलि और दूसरा होमबलि करके यहोवा के लिये चढ़ाना।"));
hinsuperobject.biblemap.put("b4_8_13", new hinsuperobject(4,8,13,"और लेवियों को हारून और उसके पुत्रों के सम्मुख खड़ा करना, और उनको हिलाने की भेंट के लिये यहोवा को अपर्ण करना।"));
hinsuperobject.biblemap.put("b4_8_14", new hinsuperobject(4,8,14,"और उन्हें इस्त्राएलियों में से अलग करना, सो वे मेरे ही ठहरेंगे।"));
hinsuperobject.biblemap.put("b4_8_15", new hinsuperobject(4,8,15,"और जब तू लेवियों को शुद्ध करके हिलाई हुई भेंट के लिये अर्पण कर चुके, उसके बाद वे मिलापवाले तम्बू सम्बन्धी सेवा टहल करने के लिये अन्दर आया करें।"));
hinsuperobject.biblemap.put("b4_8_16", new hinsuperobject(4,8,16,"क्योंकि वे इस्त्राएलियों मे से मुझे पूरी रीति से अर्पण किए हुए हैं; मै ने उनको सब इस्त्राएलियों में से एक एक स्त्री के पहिलौठे की सन्ती अपना कर लिया है।"));
hinsuperobject.biblemap.put("b4_8_17", new hinsuperobject(4,8,17,"इस्त्राएलियों के पहिलौठे, चाहे मनुष्य के हों चाहे पशु के, सब मेरे हैं; क्योंकि मैं ने उन्हें उस समय अपने लिये पवित्रा ठहराया जब मैं ने मि  देश के सब पहिलौठों को मार डाला।"));
hinsuperobject.biblemap.put("b4_8_18", new hinsuperobject(4,8,18,"और मैं ने इस्त्राएलियों के सब पहिलौठों के बदले लेवियों को लिया है।"));
hinsuperobject.biblemap.put("b4_8_19", new hinsuperobject(4,8,19,"उन्हे लेकर मै ने हारून और उसके पुत्रों को इस्त्राएलियों में से दान करके दे दिया है, कि वे मिलापवाले तम्बू में इस्त्राएलियों के निमित्त सेवकाई और प्रायश्चित्त किया करें, कहीं ऐसा न हो कि जब इस्त्राएली पवित्रास्थान के समीप आएं तब उन पर कोई महाविपत्ति आ पड़े।"));
hinsuperobject.biblemap.put("b4_8_20", new hinsuperobject(4,8,20,"लेवियों के विषय यहोवा की यह आज्ञा पाकर मूसा और हारून और इस्त्राएलियों की सारी मण्डली ने उनके साथ ठीक वैसा ही किया।"));
hinsuperobject.biblemap.put("b4_8_21", new hinsuperobject(4,8,21,"लेवियों ने तो अपने को पाप से पावन किया, और अपने वस्त्रों को धो डाला; और हारून ने उन्हें यहोवा के साम्हने हिलाई हुई भेंट के निमित्त अर्पण किया, और उन्हें शुद्ध करने को उनके लिये प्राशिचाय्त्त भी किया।"));
hinsuperobject.biblemap.put("b4_8_22", new hinsuperobject(4,8,22,"और उसके बाद लेवीय हारून और उसके पुत्रों के साम्हने मिलापवाले तम्बू में अपनी अपनी सेवकाई करने को गए; और जो आज्ञा यहोवा ने मूसा को लेवियों के विषय में दी थी उसी के अनुसार वे उन से व्यवहार करने लगे।।"));
hinsuperobject.biblemap.put("b4_8_23", new hinsuperobject(4,8,23,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_8_24", new hinsuperobject(4,8,24,"जो लेवियों को करना है वह यह है, कि पच्चाीस वर्ष की अवस्था से लेकर उस से अधिक आयु में वे मिलापवाले तम्बू सम्बन्धी काम करने के लिये भीतर उपस्थित हुआ करें;"));
hinsuperobject.biblemap.put("b4_8_25", new hinsuperobject(4,8,25,"और जब पचास वर्ष के हों तो फिर उस सेवा के लिये न आए और न काम करें;"));
hinsuperobject.biblemap.put("b4_8_26", new hinsuperobject(4,8,26,"परन्तु वे अपने भाई बन्धुओं के साथ मिलापवाले तम्बू के पास रक्षा का काम किया करें, और किसी प्रकार की सेवकाई न करें। लेवियों को जो जो काम सौंपे जाएं उनके विषय तू उन से ऐसा ही करना।।"));
hinsuperobject.biblemap.put("b4_9_1", new hinsuperobject(4,9,1,"इस्त्राएलियों के मि  देश से निकलने के दूसरे वर्ष के पहिले महीने में यहोवा ने सीनै के जंगल में मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_9_2", new hinsuperobject(4,9,2,"इस्त्राएली फसह नाम पर्ब्ब को उसके नियत समय पर माना करें।"));
hinsuperobject.biblemap.put("b4_9_3", new hinsuperobject(4,9,3,"अर्थात् इसी महीने के चौदहवें दिन को गोधूलि के समय तुम लोग उसे सब विधियों और नियमों के अनुसार मानना।"));
hinsuperobject.biblemap.put("b4_9_4", new hinsuperobject(4,9,4,"तब मूसा ने इस्त्राएलियों से फसह मानने के लिये कह दिया।"));
hinsuperobject.biblemap.put("b4_9_5", new hinsuperobject(4,9,5,"और उन्हों ने पहले महीने के चौदहवें दिन को गोधूलि के समय सीनै के जंगल में फसह को माना; और जो जो आज्ञाएं यहोवा ने मूसा को दी थीं उन्हीं के अनुसार इस्त्राएलियों ने किया।"));
hinsuperobject.biblemap.put("b4_9_6", new hinsuperobject(4,9,6,"परन्तु कितने लोग किसी मनुष्य की लोथ के द्वारा अशुद्ध होने के कारण उस दिन फसह को न मान सके; वे उसी दिन मूसा और हारून के समीप जाकर मूसा से कहने लगे,"));
hinsuperobject.biblemap.put("b4_9_7", new hinsuperobject(4,9,7,"हम लोग एक मनुष्य की लोथ के कारण अशुद्ध हैं; परन्तु हम क्यों रूके रहें, और इस्त्राएलियों के संग यहोवा का चढ़ावा नियत समय पर क्यों न चढ़ाएं?"));
hinsuperobject.biblemap.put("b4_9_8", new hinsuperobject(4,9,8,"मूसा ने उन से कहा, ठहरे रहो, मै सुन लूं कि यहोवा तुम्हारे विषय में क्या आज्ञा देता है।"));
hinsuperobject.biblemap.put("b4_9_9", new hinsuperobject(4,9,9,"यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_9_10", new hinsuperobject(4,9,10,"इस्त्राएलियों से कह, कि चाहे तुम लोग चाहे तुम्हारे वंश में से कोई भी किसी लोथ के कारण अशुद्ध हो, वा दूर की यात्रा पर हो, तौभी वह यहोवा के लिये फसह को माने।"));
hinsuperobject.biblemap.put("b4_9_11", new hinsuperobject(4,9,11,"वे उसे दूसरे महीने के चौदहवें दिन को गोधूलि के समय मानें; और फसह के बलिपशु के मांस को अखमीरी रोटी और कडुए सागपात के साथ खाएं।"));
hinsuperobject.biblemap.put("b4_9_12", new hinsuperobject(4,9,12,"और उस में से कुछ भी बिहान तक न रख छोड़े, और न उसकी कोई हड्डी तोड़े; वे उस पर्ब्ब को फसह की सारी विधियों के अनुसार मानें।"));
hinsuperobject.biblemap.put("b4_9_13", new hinsuperobject(4,9,13,"परन्तु जो मनुष्य शुद्ध हो और यात्रा पर न हो, परन्तु फसह के पर्ब्ब को न माने, वह प्राणी अपने लोगों में से नाश किया जाए, उस मनुष्य को यहोवा का चढ़ावा नियत समय पर न ले आने के कारण अपने पाप का बोझ उठाना पड़ेगा।"));
hinsuperobject.biblemap.put("b4_9_14", new hinsuperobject(4,9,14,"और यदि कोई परदेशी तुम्हारे साथ रहकर चाहे कि यहोवा के लिये फसह माने, तो वह उसी विधि और नियम के अनुसार उसको माने; देशी और परदेशी दोनों के लिये तुम्हारी एक ही विधि हो।।"));
hinsuperobject.biblemap.put("b4_9_15", new hinsuperobject(4,9,15,"जिस दिन निवास जो साक्षी का तम्बू भी कहलाता है खड़ा किया गया, उस दिन बादल उस पर छा गया; और सन्ध्या को वह निवास पर आग सा दिखाई दिया और भोर तक दिखाई देता रहा।"));
hinsuperobject.biblemap.put("b4_9_16", new hinsuperobject(4,9,16,"और नित्य ऐसा ही हुआ करता था; अर्थात् दिन को बादल छाया रहता, और रात को आग दिखाई देती थी।"));
hinsuperobject.biblemap.put("b4_9_17", new hinsuperobject(4,9,17,"और जब जब वह बादल तम्बू पर से उठ जाता तब इस्त्राएली प्रस्थान करते थे; और जिस स्थान पर बादल ठहर जाता वहीं इस्त्राएली अपने डेरे खड़े करते थे।"));
hinsuperobject.biblemap.put("b4_9_18", new hinsuperobject(4,9,18,"यहोवा की आज्ञा से इस्त्राएली कूच करते थे, और यहोवा ही की आज्ञा से वे डेरे खड़े भी करते थे; और जितने दिन तक वह बादल निवास पर ठहरा रहता उतने दिन तक वे डेरे डाले पड़े रहते थे।"));
hinsuperobject.biblemap.put("b4_9_19", new hinsuperobject(4,9,19,"और जब जब बादल बहुत दिन निवास पर छाया रहता तब इस्त्राएली यहोवा की आज्ञा मानते, और प्रस्थान नहीं करते थे।"));
hinsuperobject.biblemap.put("b4_9_20", new hinsuperobject(4,9,20,"और कभी कभी वह बादल थोड़े ही दिन तक निवास पर रहता, और तब भी वे यहोवा की आज्ञा से डेरे डाले पड़े रहते थे और फिर यहोवा की आज्ञा ही से प्रस्थान करते थे।"));
hinsuperobject.biblemap.put("b4_9_21", new hinsuperobject(4,9,21,"और कभी कभी बादल केवल सन्ध्या से भोर तक रहता; और जब वह भोर को उठ जाता था तब वे प्रस्थान करते थे, और यदि वह रात दिन बराबर रहता तो जब बादल उठ जाता तब ही वे प्रस्थान करते थे।"));
hinsuperobject.biblemap.put("b4_9_22", new hinsuperobject(4,9,22,"वह बादल चाहे दो दिन, चाहे एक महीना, चाहे वर्ष भर, जब तक निवास पर ठहरा रहता तब तक इस्त्राएली अपने डेरों में रहते और प्रस्थान नहीं करते थे; परन्तु जब वह उठ जाता तब वे प्रस्थान करते थे।"));
hinsuperobject.biblemap.put("b4_9_23", new hinsuperobject(4,9,23,"यहोवा की आज्ञा से वे अपने डेरें खड़े करते, और यहोवा ही की आज्ञा से वे प्रस्थान करते थे; जो आज्ञा यहोवा मूसा के द्वारा देता था उसको वे माना करते थे।।"));
hinsuperobject.biblemap.put("b4_10_1", new hinsuperobject(4,10,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_10_2", new hinsuperobject(4,10,2,"चांदी की दो तुरहियां गढ़के बनाई जाएं; तू उनको मण्डली के बुलाने, और छावनियों के प्रस्थान करने में काम में लाना।"));
hinsuperobject.biblemap.put("b4_10_3", new hinsuperobject(4,10,3,"और जब वे दोनों फंूकी जाएं, तब सारी मण्डली मिलापवाले तम्बू के द्वार पर तेरे पास इकट्ठी हो जाए।"));
hinsuperobject.biblemap.put("b4_10_4", new hinsuperobject(4,10,4,"और यदि एक ही तुरही फूंकी जाए, तो प्रधान लोग जो इस्त्राएल के हजारों के मुख्य पुरूष हैं तेरे पास इकट्ठे हो जाएं।"));
hinsuperobject.biblemap.put("b4_10_5", new hinsuperobject(4,10,5,"जब तुम लोग सांस बान्धकर फूंको, तो पूरब दिशा की छावनियों का प्रस्थान हो।"));
hinsuperobject.biblemap.put("b4_10_6", new hinsuperobject(4,10,6,"और जब तुम दूसरी बेर सांस बान्धकर फूंको, तब दक्खिन दिशा की छावनियों का प्रस्थान हो। उनके प्रस्थान करने के लिये वे सांस बान्धकर फूंकें।"));
hinsuperobject.biblemap.put("b4_10_7", new hinsuperobject(4,10,7,"और जब लोगों को इकट्ठा करके सभा करनी हो तब भी फूंकना परन्तु सांस बान्धकर नहीं।"));
hinsuperobject.biblemap.put("b4_10_8", new hinsuperobject(4,10,8,"और हारून के पुत्रा जो याजक हैं वे उन तुरहियों को फूंका करें। यह बात तुम्हारी पीढ़ी- पीढ़ी के लिये सर्वदा की विधि रहे।"));
hinsuperobject.biblemap.put("b4_10_9", new hinsuperobject(4,10,9,"और जब तुम अपने देश में किसी सतानेवाले बैरी से लड़ने को निकलो, तब तुरहियों को सांस बान्धकर फूंकना, तब तुम्हारे परमेश्वर यहोवा को तुम्हारा स्मरण आएगा, और तुम अपने शत्रुओं से बचाए जाओगे।"));
hinsuperobject.biblemap.put("b4_10_10", new hinsuperobject(4,10,10,"और अपने आनन्द के दिन में, और अपने नियत पर्ब्बों में, और महीनों के आदि में, अपने होमबलियों और मेलबलियों के साथ उन तुरहियों को फूंकना; इस से तुम्हारे परमेश्वर को तुम्हारा स्मरण आएगा; मैं तुम्हारा परमेश्वर यहोवा हूं।।"));
hinsuperobject.biblemap.put("b4_10_11", new hinsuperobject(4,10,11,"और दूसरे वर्ष के दूसरे महीने के बीसवें दिन को बादल साक्षी के निवास पर से उठ गया,"));
hinsuperobject.biblemap.put("b4_10_12", new hinsuperobject(4,10,12,"तब इस्त्राएली सीनै के जंगल में से निकलकर प्रस्थान करके निकले; और बादल पारान नाम जंगल में ठहर गया।"));
hinsuperobject.biblemap.put("b4_10_13", new hinsuperobject(4,10,13,"उनका प्रस्थान यहोवा की उस आज्ञा के अनुसार जो उस ने मूसा को दी थी आरम्भ हुआ।"));
hinsuperobject.biblemap.put("b4_10_14", new hinsuperobject(4,10,14,"और सब से पहले तो यहूदियों की छावनी के झंडे का प्रस्थान हुआ, और वे दल बान्धकर चले; और उन का सेनापति अम्मीनादाब का पुत्रा नहशोन था।"));
hinsuperobject.biblemap.put("b4_10_15", new hinsuperobject(4,10,15,"और इस्साकारियों के गोत्रा का सेनापति सूआर का पुत्रा नतनेल था।"));
hinsuperobject.biblemap.put("b4_10_16", new hinsuperobject(4,10,16,"और जबूलूनियों के गोत्रा का सेनापति हेलोन का पुत्रा एलीआब था।"));
hinsuperobject.biblemap.put("b4_10_17", new hinsuperobject(4,10,17,"तब निवास उतारा गया, और गेर्शोनियों और मरारियों ने जो निवास को उठाते थे प्रस्थान किया।"));
hinsuperobject.biblemap.put("b4_10_18", new hinsuperobject(4,10,18,"फिर रूबेन की छावनी झंडे का कूच हुआ, और वे भी दल बनाकर चले; और उनका सेनापति शदेऊर का पुत्रा एलीशूर था।"));
hinsuperobject.biblemap.put("b4_10_19", new hinsuperobject(4,10,19,"और शिमोनियों के गोत्रा का सेनापति सूरीश ै का पुत्रा शलूमीएल था।"));
hinsuperobject.biblemap.put("b4_10_20", new hinsuperobject(4,10,20,"और गादियों के गोत्रा का सेनापति दूएल का पुत्रा एल्यासाप था।"));
hinsuperobject.biblemap.put("b4_10_21", new hinsuperobject(4,10,21,"तब कहातियों ने पवित्रा वस्तुओं को उठाए हुए प्रस्थान किया, और उनके पहुंचने तक गेर्शोनियों और मरारियों ने निवास को खड़ा कर दिया।"));
hinsuperobject.biblemap.put("b4_10_22", new hinsuperobject(4,10,22,"फिर एप्रैमियों की छावनी के झंडे का कूच हुआ, और वे भी दल बनाकर चले; और उनका सेनापति अम्मीहूद का पुत्रा एलीशामा था।"));
hinsuperobject.biblemap.put("b4_10_23", new hinsuperobject(4,10,23,"और मनश्शेइयों के गोत्रा को सेनापति पदासूर का पुत्रा गम्लीएल था।"));
hinsuperobject.biblemap.put("b4_10_24", new hinsuperobject(4,10,24,"और बिन्यामीनियों के गोत्रा का सेनापति गिदोनी का पुत्रा अबीदान था।"));
hinsuperobject.biblemap.put("b4_10_25", new hinsuperobject(4,10,25,"फिर दानियों की छावनी जो सब छावनियों के पीछे थी, उसके झंडे का प्रस्थान हुआ, और वे भी दल बना कर चले; और उनका सेनापति अम्मीश ै का पुत्रा अखीआज़र था।"));
hinsuperobject.biblemap.put("b4_10_26", new hinsuperobject(4,10,26,"और आशेरियों के गोत्रा का सेनापति ओक्रान का पुत्रा पजीएल था।"));
hinsuperobject.biblemap.put("b4_10_27", new hinsuperobject(4,10,27,"और नप्तालियों के गोत्रा का सेनापति एनान का पुत्रा अहीरा था।"));
hinsuperobject.biblemap.put("b4_10_28", new hinsuperobject(4,10,28,"इस्त्राएली इसी प्रकार अपने अपने दलों के अनुसार प्रस्थान करते, और आगे बढ़ा करते थे।"));
hinsuperobject.biblemap.put("b4_10_29", new hinsuperobject(4,10,29,"और मूसा ने अपने ससुर रूएल मिद्यानी के पुत्रा होबाब से कहा, हम लोग उस स्थान की यात्रा करते हैं जिसके विषय में यहोवा ने कहा है, कि मैं उसे तुम को दूंगा; सो तू भी हमारे संग चल, और हम तेरी भलाई करेंगे; क्योंकि यहोवा ने इस्त्राएल के विषय में भला ही कहा है।"));
hinsuperobject.biblemap.put("b4_10_30", new hinsuperobject(4,10,30,"होबाब ने उसे उत्तर दिया, कि मैं नहीं जाऊंगा; मैं अपने देश और कुटुम्बियों में लौट जाऊंगा।"));
hinsuperobject.biblemap.put("b4_10_31", new hinsuperobject(4,10,31,"फिर मूसा ने कहा, हम को न छोड़, क्योंकि जंगल में कहां कहां डेरा खड़ा करना चाहिये, यह तुझे ही मालूम है, तू हमारे लिये आंखों का काम देना।"));
hinsuperobject.biblemap.put("b4_10_32", new hinsuperobject(4,10,32,"और यदि तू हमारे संग चले, तो निश्चय जो भलाई यहोवा हम से करेगा उसी के अनुसार हम भी तुझ से वैसा ही करेंगे।।"));
hinsuperobject.biblemap.put("b4_10_33", new hinsuperobject(4,10,33,"फिर इस्त्राएलियों ने यहोवा के पर्वत से प्रस्थान करके तीन दिन की यात्रा की; और उन तीनों दिनों के मार्ग में यहोवा की वाचा का सन्दूक उनके लिये विश्राम का स्थान ढूंढ़ता हुआ उनके आगे आगे चलता रहा।"));
hinsuperobject.biblemap.put("b4_10_34", new hinsuperobject(4,10,34,"और जब वे छावनी के स्थान से प्रस्थान करते थे तब दिन भर यहोवा का बादल उनके ऊपर छाया रहता था।"));
hinsuperobject.biblemap.put("b4_10_35", new hinsuperobject(4,10,35,"और जब जब सन्दूक का प्रस्थान होता था तब तब मूसा यह कहा करता था, कि हे यहोवा, उठ, और तेरे शत्रु तित्तर बित्तर हो जाएं, और तेरे बैरी तेरे साम्हने से भाग जाएं।"));
hinsuperobject.biblemap.put("b4_10_36", new hinsuperobject(4,10,36,"और जब जब वह ठहर जाता था तब तब मूसा कहा करता था, कि हे यहोवा, हजारोंझार इस्त्राएलियों में लौटकर आ जा।।"));
hinsuperobject.biblemap.put("b4_11_1", new hinsuperobject(4,11,1,"फिर वे लोग बुड़बुड़ाने और यहोवा के सुनते बुरा कहने लगे; निदान यहोवा ने सुना, और उसका कोप भड़क उठा, और यहोवा की आग उनके मध्य जल उठी, और छावनी के एक किनारे से भस्म करने लगी।"));
hinsuperobject.biblemap.put("b4_11_2", new hinsuperobject(4,11,2,"तब मूसा के पास आकर चिल्लाए; और मूसा ने यहोवा से प्रार्थना की, तब वह आग बुझ गई,"));
hinsuperobject.biblemap.put("b4_11_3", new hinsuperobject(4,11,3,"और उस स्थान का नाम तबेरा पड़ा, क्योंकि यहोवा की आग उन में जल उठी थी।।"));
hinsuperobject.biblemap.put("b4_11_4", new hinsuperobject(4,11,4,"फिर जो मिली- जुली भीड़ उनके साथ थी वह कामुकता करने लगी; और इस्त्राएली भी फिर रोने और कहने लगे, कि हमें मांस खाने को कौन देगा।"));
hinsuperobject.biblemap.put("b4_11_5", new hinsuperobject(4,11,5,"हमें वे मछलियां स्मरण हैं जो हम मि  में सेंतमेंत खाया करते थे, और वे खीरे, और खरबूजे, और गन्दने, और प्याज, और लहसुन भी;"));
hinsuperobject.biblemap.put("b4_11_6", new hinsuperobject(4,11,6,"परन्तु अब हमारा जी घबरा गया है, यहां पर इस मन्ना को छोड़ और कुछ भी देख नहीं पड़ता।"));
hinsuperobject.biblemap.put("b4_11_7", new hinsuperobject(4,11,7,"मन्ना तो धनिये के समान था, और उसका रंग रूप मोती का सा था।"));
hinsuperobject.biblemap.put("b4_11_8", new hinsuperobject(4,11,8,"लोग इधर उधर जाकर उसे बटोरते, और चक्की में पीसते वा ओखली में कूटते थे, फिर तसले में पकाते, और उसके फुलके बनाते थे; और उसका स्वाद तेल में बने हुए पुए का सा था।"));
hinsuperobject.biblemap.put("b4_11_9", new hinsuperobject(4,11,9,"और रात को छावनी में ओस पड़ती थी तब उसके साथ मन्ना भी गिरता था।"));
hinsuperobject.biblemap.put("b4_11_10", new hinsuperobject(4,11,10,"और मूसा ने सब घरानों के आदमियों को अपने अपने डेरे के द्वार पर रोते सुना; और यहोवा का कोप अत्यन्त भड़का, और मूसा को भी बुरा मालूम हुआ।"));
hinsuperobject.biblemap.put("b4_11_11", new hinsuperobject(4,11,11,"तब मूसा ने यहोवा से कहा, तू अपने दास से यह बुरा व्यवहार क्यों करता है? और क्या कारण है कि मैं ने तेरी दृष्टि में अनुग्रह नहीं पाया, कि तू ने इन सब लोगों का भार मुझ पर डाला है?"));
hinsuperobject.biblemap.put("b4_11_12", new hinsuperobject(4,11,12,"क्या ये सब लोग मेरे ही कोख में पड़े थे? क्या मैं ही ने उनको उत्पन्न किया, जो तू मुझ से कहता है, कि जैसे पिता दूध पीते बालक को अपनी गोद में उठाए उठाए फिरता है, वैसे ही मैं इन लोगों को अपनी गोद में उठाकर उस देश में ले जाऊं, जिसके देने की शपथ तू ने उनके पूर्वजों से खाई है?"));
hinsuperobject.biblemap.put("b4_11_13", new hinsuperobject(4,11,13,"मुझे इतना मांस कहां से मिले कि इन सब लोगों को दूं? ये तो यह कह कहकर मेरे पास रो रहे हैं, कि तू हमे मांस खाने को दे।"));
hinsuperobject.biblemap.put("b4_11_14", new hinsuperobject(4,11,14,"मैं अकेला इन सब लोगों का भार नहीं सम्भाल सकता, कयोंकि यह मेरी शक्ति के बाहर है।"));
hinsuperobject.biblemap.put("b4_11_15", new hinsuperobject(4,11,15,"और जो तुझे मेरे साथ यही व्यवहार करना है, तो मुझ पर तेरा इतना अनुग्रह हो, कि तू मेरे प्राण एकदम ले ले, जिस से मैं अपनी दुर्दशा न देखने पाऊं।।"));
hinsuperobject.biblemap.put("b4_11_16", new hinsuperobject(4,11,16,"यहोवा ने मूसा से कहा, इस्त्राएली पुरनियों में से सत्तर ऐसे पुरूष मेरे पास इकट्ठे कर, जिनको तू जानता है कि वे प्रजा के पुरनिये और उनके सरदार है कि वे प्रजा के पुरनिये और उनके सरदार हैं; और मिलापवाले तम्बू के पास ले आ, कि वे तेरे साथ यहां खड़े हों।"));
hinsuperobject.biblemap.put("b4_11_17", new hinsuperobject(4,11,17,"तब मैं उतरकर तुझ से वहां बातें करूंगा; और जो आत्मा तुझ में है उस में से कुछ लेकर उन में समवाऊंगा; और वे इन लोगों का भार तेरे संग उठाए रहेंगे, और तुझे उसको अकेले उठाना न पड़ेगा।"));
hinsuperobject.biblemap.put("b4_11_18", new hinsuperobject(4,11,18,"और लोगों से कह, कल के लिये अपने को पवित्रा करो, तब तुम्हें मांस खाने को मिलेगा; क्योंकि तुम यहोवा के सुनते हुए यह कह कहकर रोए हो, कि हमें मांस खाने को कौन देगा? हम मि  ही में भले थे। सो यहोवा तुम को मांस खाने को देगा, और तुम खाना।"));
hinsuperobject.biblemap.put("b4_11_19", new hinsuperobject(4,11,19,"फिर तुम एक दिन, वा दो, वा पांच, वा दस, वा बीस दिन ही नहीं,"));
hinsuperobject.biblemap.put("b4_11_20", new hinsuperobject(4,11,20,"परन्तु महीने भर उसे खाते रहोगे, जब तक वह तुम्हारे नथनों से निकलने न लगे और तुम को उस से घृणा न हो जाए, कयोंकि तुम लोगों ने यहोवा को जो तुम्हारे मध्य में है तुच्छ जाना है, और उसके साम्हने यह कहकर रोए हो, कि हम मि  से क्यों निकल आए?"));
hinsuperobject.biblemap.put("b4_11_21", new hinsuperobject(4,11,21,"फिर मूसा ने कहा, जिन लोगों के बीच मैं हूं उन में से छ: लाख तो प्यादे ही हैं; और तू ने कहा है, कि मैं उन्हें इतना मांस दूंगा, कि वे महीने भर उसे खाते ही रहेंगे।"));
hinsuperobject.biblemap.put("b4_11_22", new hinsuperobject(4,11,22,"क्या वे सब भेड़- बकरी गाय- बैल उनके लिये मारे जाए, कि उनको मांस मिले? वा क्या समुद्र की सब मछलियां उनके लिये इकट्ठी की जाएं, कि उनको मांस मिले?"));
hinsuperobject.biblemap.put("b4_11_23", new hinsuperobject(4,11,23,"यहोवा ने मूसा से कहा, क्या यहोवा का हाथ छोटा हो गया है? अब तू देखेगा, कि मेरा वचन जो मैं ने तुझ से कहा है वह पूरा होता है कि नहीं।"));
hinsuperobject.biblemap.put("b4_11_24", new hinsuperobject(4,11,24,"तब मूसा ने बाहर जाकर प्रजा के लोगों को यहोवा की बातें कह सुनाई; और उनके पुरनियों में से सत्तर पुरूष इकट्ठे करके तम्बू के चारों ओर खड़े किए।"));
hinsuperobject.biblemap.put("b4_11_25", new hinsuperobject(4,11,25,"तब यहोवा बादल में होकर उतरा और उस ने मूसा से बातें की, और जो आत्मा उस में थी उस में से लेकर उन सत्तर पुरनियों में समवा दिया; और जब वह आत्मा उन में आई तब वे नबूवत करने लगे। परन्तु फिर और कभी न की।"));
hinsuperobject.biblemap.put("b4_11_26", new hinsuperobject(4,11,26,"परन्तु दो मनुष्य छावनी में रह गए थे, जिस में से एक का नाम एलदाद और दूसरे का मेदाद था, उन में भी आत्मा आई; ये भी उन्हीं में से थे जिनके नाम लिख लिये गये थे, पर तम्बू के पास न गए थे, और वे छावनी ही में नबूवत करने लगे।"));
hinsuperobject.biblemap.put("b4_11_27", new hinsuperobject(4,11,27,"तब किसी जवान ने दौड़ कर मूसा को बतलाया, कि एलदाद और मेदाद छावनी में नबूवत कर रहे हैं।"));
hinsuperobject.biblemap.put("b4_11_28", new hinsuperobject(4,11,28,"तब नून का पुत्रा यहोशू, जो मूसा का टहलुआ और उसके चुने हुए वीरों में से था, उस ने मूसा से कहा, हे मेरे स्वामी मूसा, उनको रोक दे।"));
hinsuperobject.biblemap.put("b4_11_29", new hinsuperobject(4,11,29,"मूसा ने उन से कहा, क्या तू मेरे कारण जलता है? भला होता कि यहोवा की सारी प्रजा के लोग नबी होते, और यहोवा अपना आत्मा उन सभों में समवा देता!"));
hinsuperobject.biblemap.put("b4_11_30", new hinsuperobject(4,11,30,"तब फिर मूसा इस्त्राएल के पुरनियों समेत छावनी में चला गया।"));
hinsuperobject.biblemap.put("b4_11_31", new hinsuperobject(4,11,31,"तब यहोवा की ओर से एक बड़ी आंधी आई, और वह समुद्र से बटेरें उड़ाके छावनी पर और उसके चारों ओर इतनी ले आईं, कि वे इधर उधर एक दिन के मार्ग तक भूमि पर दो हाथ के लगभग ऊंचे तक छा गए।"));
hinsuperobject.biblemap.put("b4_11_32", new hinsuperobject(4,11,32,"और लोगों ने उठकर उस दिन भर और रात भर, और दूसरे दिन भी दिन भर बटेरों को बटोरते रहे; जिस ने कम से कम बटोरा उस ने दस होमेर बटोरा; और उन्हों ने उन्हें छावनी के चारों ओर फैला दिया।"));
hinsuperobject.biblemap.put("b4_11_33", new hinsuperobject(4,11,33,"मांस उनके मुंह ही में था, और वे उसे खाने न पाए थे, कि यहोवा का कोप उन पर भड़क उठा, और उस ने उनको बहुत बड़ी मार से मारा।"));
hinsuperobject.biblemap.put("b4_11_34", new hinsuperobject(4,11,34,"और उस स्थान का नाम किब्रोथत्तावा पड़ा, क्योंकि जिन लोगों ने कामुकता की थी उनको वहां मिट्टी दी गई।"));
hinsuperobject.biblemap.put("b4_11_35", new hinsuperobject(4,11,35,"फिर इस्त्राएली किब्रोथत्तावा से प्रस्थान करके हसेरोत में पहुंचे, और वहीं रहे।।"));
hinsuperobject.biblemap.put("b4_12_1", new hinsuperobject(4,12,1,"मूसा ने तो एक कूशी स्त्री के साथ ब्याह कर लिया था। सो मरियम और हारून उसकी उस ब्याहिता कूशी स्त्री के कारण उसकी निन्दा करने लगे;"));
hinsuperobject.biblemap.put("b4_12_2", new hinsuperobject(4,12,2,"उन्हों ने कहा, क्या यहोवा ने केवल मूसा ही के साथ बातें की हैं? क्या उस ने हम से भी बातें नहीं कीं? उनकी यह बात यहोवा ने सुनी।"));
hinsuperobject.biblemap.put("b4_12_3", new hinsuperobject(4,12,3,"मूसा तो पृथ्वी भर के रहने वाले मनुष्यों से बहुत अधिक नम्र स्वभाव का था।"));
hinsuperobject.biblemap.put("b4_12_4", new hinsuperobject(4,12,4,"सो यहोवा ने एकाएक मूसा और हारून और मरियम से कहा, तुम तीनों मिलापवाले तम्बू के पास निकल आओ। तब वे तीनों निकल आए।"));
hinsuperobject.biblemap.put("b4_12_5", new hinsuperobject(4,12,5,"तब यहोवा ने बादल के खम्भे में उतरकर तम्बू के द्वार पर खड़ा होकर हारून और मरियम को बुलाया; सो वे दोनों उसके पास निकल आए।"));
hinsuperobject.biblemap.put("b4_12_6", new hinsuperobject(4,12,6,"तब यहोवा ने कहा, मेरी बातें सुनो: यदि तुम में कोई नबी हो, तो उस पर मैं यहोवा दर्शन के द्वारा अपने आप को प्रगट करूंगा, वा स्वप्न में उस से बातें करूंगा।"));
hinsuperobject.biblemap.put("b4_12_7", new hinsuperobject(4,12,7,"परन्तु मेरा दास मूसा ऐसा नहीं है; वह तो मेरे सब घरानों मे विश्वास योग्य है।"));
hinsuperobject.biblemap.put("b4_12_8", new hinsuperobject(4,12,8,"उस से मैं गुप्त रीति से नहीं, परन्तु आम्हने साम्हने और प्रत्यक्ष होकर बातें करता हूं; और वह यहोवा का स्वरूप निहारने पाता है। सो तुम मेरे दास मूसा की निन्दा करते हुए क्यों नहीं डरे?"));
hinsuperobject.biblemap.put("b4_12_9", new hinsuperobject(4,12,9,"तब यहोवा का कोप उन पर भड़का, और वह चला गया;"));
hinsuperobject.biblemap.put("b4_12_10", new hinsuperobject(4,12,10,"तब वह बादल तम्बू के ऊपर से उठ गया, और मरियम कोढ़ से हिम के समान श्वेत हो गई। और हारून ने मरियम की ओर दृष्टि की, और देखा, कि वह कोढ़िन हो गई है।"));
hinsuperobject.biblemap.put("b4_12_11", new hinsuperobject(4,12,11,"तब हारून मूसा से कहने लगा, हे मेरे प्रभु, हम दोनों ने जो मूर्खता की वरन पाप भी किया, यह पाप हम पर न लगने दे।"));
hinsuperobject.biblemap.put("b4_12_12", new hinsuperobject(4,12,12,"और मरियम को उस मरे हुए के समान न रहने दे, जिसकी देह अपनी मां के पेट से निकलते ही अधगली हो।"));
hinsuperobject.biblemap.put("b4_12_13", new hinsuperobject(4,12,13,"सो मूसा ने यह कहकर यहोवा की दोहाई दी, हे ईश्वर, कृपा कर, और उसको चंगा कर।"));
hinsuperobject.biblemap.put("b4_12_14", new hinsuperobject(4,12,14,"यहोवा ने मूसा से कहा, यदि उसका पिता उसके मुंह पर थूका ही होता, तो क्या सात दिन तक वह लज्जित न रहती? सो वह सात दिन तक छावनी से बाहर बन्द रहे, उसके बाद वह फिर भीतर आने पाए।"));
hinsuperobject.biblemap.put("b4_12_15", new hinsuperobject(4,12,15,"सो मरियम सात दिन तक छावनी से बाहर बन्द रही, और जब तक मरियम फिर आने न पाई तब तक लोगों ने प्रस्थान न किया।"));
hinsuperobject.biblemap.put("b4_12_16", new hinsuperobject(4,12,16,"उसके बाद उन्हों ने हसेरोत से प्रस्थान करके पारान नाम जंगल में अपने डेरे खड़े किए।।"));
hinsuperobject.biblemap.put("b4_13_1", new hinsuperobject(4,13,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_13_2", new hinsuperobject(4,13,2,"कनान देश जिसे मैं इस्त्राएलियों को देता हूं उसका भेद लेने के लिये पुरूषों को भेज; वे उनके पितरों के प्रति गोत्रा का एक प्रधान पुरूष हों।"));
hinsuperobject.biblemap.put("b4_13_3", new hinsuperobject(4,13,3,"यहोवा से यह आज्ञा पाकर मूसा ने ऐसे पुरूषों को पारान जंगल से भेज दिया, जो सब के सब इस्त्राएलियों के प्रधान थे।"));
hinsuperobject.biblemap.put("b4_13_4", new hinsuperobject(4,13,4,"उनके नाम ये हैं, अर्थात् रूबेन के गोत्रा में से जककूर का पुत्रा शम्मू;"));
hinsuperobject.biblemap.put("b4_13_5", new hinsuperobject(4,13,5,"शिमोन के गोत्रा में से होरी का पुत्रा शापात;"));
hinsuperobject.biblemap.put("b4_13_6", new hinsuperobject(4,13,6,"यहूदा के गोत्रा में से यपुन्ने का पुत्रा कालेब;"));
hinsuperobject.biblemap.put("b4_13_7", new hinsuperobject(4,13,7,"इस्साकार के गोत्रा में से योसेप का पुत्रा यिगाल;"));
hinsuperobject.biblemap.put("b4_13_8", new hinsuperobject(4,13,8,"एप्रैम के गोत्रा में से नून का पुत्रा होशे;"));
hinsuperobject.biblemap.put("b4_13_9", new hinsuperobject(4,13,9,"बिन्यामीन के गोत्रा में से रापू का पुत्रा पलती;"));
hinsuperobject.biblemap.put("b4_13_10", new hinsuperobject(4,13,10,"जबूलून के गोत्रा में से सोदी का पुत्रा गद्दीएल;"));
hinsuperobject.biblemap.put("b4_13_11", new hinsuperobject(4,13,11,"यूसुफ वंशियों में, मनश्शे के गोत्रा में से सूसी का पुत्रा गद्दी;"));
hinsuperobject.biblemap.put("b4_13_12", new hinsuperobject(4,13,12,"दान के गोत्रा में से गमल्ली का पुत्रा अम्मीएल;"));
hinsuperobject.biblemap.put("b4_13_13", new hinsuperobject(4,13,13,"आशेर के गोत्रा में से मीकाएल का पुत्रा सतूर;"));
hinsuperobject.biblemap.put("b4_13_14", new hinsuperobject(4,13,14,"नप्ताली के गोत्रा में से वोप्सी का पुत्रा नहूबी;"));
hinsuperobject.biblemap.put("b4_13_15", new hinsuperobject(4,13,15,"गाद के गोत्रा में से माकी का पुत्रा गूएल।"));
hinsuperobject.biblemap.put("b4_13_16", new hinsuperobject(4,13,16,"जिन पुरूषों को मूसा ने देश का भेद लेने के लिये भेजा था उनके नाम ये ही हैं। और नून के पुत्रा होशे का नाम उस ने यहोशू रखा।"));
hinsuperobject.biblemap.put("b4_13_17", new hinsuperobject(4,13,17,"उन को कनान देश के भेद लेने को भेजते समय मूसा ने कहा, इधर से, अर्थात् दक्षिण देश होकर जाओ,"));
hinsuperobject.biblemap.put("b4_13_18", new hinsuperobject(4,13,18,"और पहाड़ी देश में जाकर उस देश को देख लो कि कैसा है, और उस में बसे हुए लोगों को भी देखो कि वे बलवान् हैं वा निर्बल, थोड़े हैं वा बहुत,"));
hinsuperobject.biblemap.put("b4_13_19", new hinsuperobject(4,13,19,"और जिस देश में वे बसे हुए हैं सो कैसा है, अच्छा वा बुरा, और वे कैसी कैसी बस्तियों में बसे हुए हैं, और तम्बुओं में रहते हैं वा गढ़ वा किलों में रहते हैं,"));
hinsuperobject.biblemap.put("b4_13_20", new hinsuperobject(4,13,20,"और वह देश कैसा है, उपजाऊ है वा बंजर है, और उस में वृक्ष हैं वा नहीं। और तुम हियाव बान्धे चलो, और उस देश की उपज में से कुछ लेते भी आना। वह समय पहली पक्की दाखों का था।"));
hinsuperobject.biblemap.put("b4_13_21", new hinsuperobject(4,13,21,"सो वे चल दिए, और सीन नाम जंगल से ले रहोब तक, जो हमात के मार्ग में है, सारे देश को देखभालकर उसका भेद लिया।"));
hinsuperobject.biblemap.put("b4_13_22", new hinsuperobject(4,13,22,"सो वे दक्षिण देश होकर चले, और हेब्रोन तक गए; वहां अहीमन, शेशै, और तल्मै नाम अनाकवंशी रहते थे। हेब्रोन तो मि  के सोअन से सात वर्ष पहिले बसाया गया था।"));
hinsuperobject.biblemap.put("b4_13_23", new hinsuperobject(4,13,23,"तब वे एशकोल नाम नाले तक गए, और वहां से एक डाली दाखों के गुच्छे समेत तोड़ ली, और दो मनुष्य उस एक लाठी पर लटकाए हुए उठा ले चले गए; और वे अनारों और अंजीरों में से भी कुछ कुछ ले आए।"));
hinsuperobject.biblemap.put("b4_13_24", new hinsuperobject(4,13,24,"इस्त्राएली वहां से जो दाखों का गुच्छा तोड़ ले आए थे, इस कारण उस स्थान का नाम एशकोल नाला रखा गया।"));
hinsuperobject.biblemap.put("b4_13_25", new hinsuperobject(4,13,25,"चालीस दिन के बाद वे उस देश का भेद लेकर लौट आए।"));
hinsuperobject.biblemap.put("b4_13_26", new hinsuperobject(4,13,26,"और पारान जंगल के कादेश नाम स्थान में मूसा और हारून और इस्त्राएलियों की सारी मण्डली के पास पहुंचे; और उनको और सारी मण्डली को संदेशा दिया, और उस देश के फल उनको दिखाए।"));
hinsuperobject.biblemap.put("b4_13_27", new hinsuperobject(4,13,27,"उन्हों ने मूसा से यह कहकर वर्णन किया, कि जिस देश में तू ने हम को भेजा था उस में हम गए; उस में सचमुच दूध और मधु की धाराएं बहती हैं, और उसकी उपज में से यही है।"));
hinsuperobject.biblemap.put("b4_13_28", new hinsuperobject(4,13,28,"परन्तु उस देश के निवासी बलवान् हैं, और उसके नगर गढ़वाले हैं और बहुत बड़े हैं; और फिर हम ने वहां अनाकवंशियों को भी देखा।"));
hinsuperobject.biblemap.put("b4_13_29", new hinsuperobject(4,13,29,"दक्षिण देश में तो अमालेकी बसे हुए हैं; और पहाड़ी देश में हित्ती, यबूसी, और एमोरी रहते हैं; और समुद्र के किनारे किनारे और यरदन नदी के तट पर कनानी बसे हुए हैं।"));
hinsuperobject.biblemap.put("b4_13_30", new hinsuperobject(4,13,30,"पर कालेब ने मूसा के साम्हने प्रजा के लोगों को चुप कराने की मनसा से कहा, हम अभी चढ़के उस देश को अपना कर लें; क्योंकि नि:सन्देह हम में ऐसा करने की शक्ति है।"));
hinsuperobject.biblemap.put("b4_13_31", new hinsuperobject(4,13,31,"पर जो पुरूष उसके संग गए थे उन्हों ने कहा, उन लोगों पर चढ़ने की शक्ति हम में नहीं है; क्योंकि वे हम से बलवान् हैं।"));
hinsuperobject.biblemap.put("b4_13_32", new hinsuperobject(4,13,32,"और उन्हों ने इस्त्राएलियों के साम्हने उस देश की जिसका भेद उन्हों ने लिया था यह कहकर निन्दा भी की, कि वह देश जिसका भेद लेने को हम गये थे ऐसा है, जो अपने निवासियों को निगल जाता है; और जितने पुरूष हम ने उस में देखे वे सब के सब बड़े डील डौल के हैं।"));
hinsuperobject.biblemap.put("b4_13_33", new hinsuperobject(4,13,33,"फिर हम ने वहां नपीलों को, अर्थात् नपीली जातिवाले अनाकवंशियों को देखा; और हम अपनी दृष्टि में तो उनके साम्हने टिड्डे के सामान दिखाई पड़ते थे, और ऐसे ही उनकी दृष्टि में मालूम पड़ते थे।।"));
hinsuperobject.biblemap.put("b4_14_1", new hinsuperobject(4,14,1,"तब सारी मण्डली चिल्ला उठी; और रात भर वे लोग रोते ही रहे।"));
hinsuperobject.biblemap.put("b4_14_2", new hinsuperobject(4,14,2,"और सब इस्त्राएली मूसा और हारून पर बुड़बुड़ाने लगे; और सारी मण्डली उस ने कहने लगी, कि भला होता कि हम मि  ही में मर जाते! वा इस जंगल ही में मर जाते!"));
hinsuperobject.biblemap.put("b4_14_3", new hinsuperobject(4,14,3,"और यहोवा हम को उस देश में ले जाकर क्यों तलवार से मरवाना चाहता है? हमारी स्त्रियां और बालबच्चे तो लूट में चलें जाएंगे; क्या हमारे लिये अच्छा नहीं कि हम मि  देश को लौट जाएं?"));
hinsuperobject.biblemap.put("b4_14_4", new hinsuperobject(4,14,4,"फिर वे आपस में कहने लगे, आओ, हम किसी को अपना प्रधान बना लें, और मि  को लौट चलें।"));
hinsuperobject.biblemap.put("b4_14_5", new hinsuperobject(4,14,5,"तब मूसा और हारून इस्त्राएलियों की सारी मण्डली के साम्हने मुंह के बल गिरे।"));
hinsuperobject.biblemap.put("b4_14_6", new hinsuperobject(4,14,6,"और नून का पुत्रा यहोशू और यपुन्ने का पुत्रा कालिब, जो देश के भेद लेनेवालों में से थे, अपने अपने वस्त्रा फाड़कर,"));
hinsuperobject.biblemap.put("b4_14_7", new hinsuperobject(4,14,7,"इस्त्राएलियों की सारी मण्डली से कहने लगे, कि जिस देश का भेद लेने को हम इधर उधर घूम कर आए हैं, वह अत्यन्त उत्तम देश है।"));
hinsuperobject.biblemap.put("b4_14_8", new hinsuperobject(4,14,8,"यदि यहोवा हम से प्रसन्न हो, तो हम को उस देश में, जिस में दूध और मधु की धाराएं बहती हैं, पहुंचाकर उसे हमे दे देगा।"));
hinsuperobject.biblemap.put("b4_14_9", new hinsuperobject(4,14,9,"केवल इतना करो कि तुम यहोवा के विरूद्ध बलवा न करो; और न तो उस देश के लोगों से डरो, क्योंकि वे हमारी रोटी ठहरेंगे; छाया उनके ऊपर से हट गई है, और यहोवा हमारे संग है; उन से न डरो।"));
hinsuperobject.biblemap.put("b4_14_10", new hinsuperobject(4,14,10,"तब सारी मण्डली चिल्ला उठी, कि इनको पत्थरवाह करो। तब यहोवा का तेज सब इस्त्राएलियों पर प्रकाशमान हुआ।।"));
hinsuperobject.biblemap.put("b4_14_11", new hinsuperobject(4,14,11,"तब यहोवा ने मूसा से कहा, वे लोग कब तक मेरा तिरस्कार करते रहेंगे? और मेरे सब आश्चर्यकर्म देखने पर भी कब तक मुझ पर विश्वास न करेंगे?"));
hinsuperobject.biblemap.put("b4_14_12", new hinsuperobject(4,14,12,"मैं उन्हें मरी से मारूंगा, और उनके निज भाग से उन्हें निकाल दूंगा, और तुझ से एक जाति उपजाऊंगा जो उन से बड़ी और बलवन्त होगी।"));
hinsuperobject.biblemap.put("b4_14_13", new hinsuperobject(4,14,13,"मूसा ने यहोवा से कहा, तब तो मिद्दी जिनके मध्य में से तू अपनी सामर्थ्य दिखाकर उन लोगों को निकाल ले आया है यह सुनेंगे,"));
hinsuperobject.biblemap.put("b4_14_14", new hinsuperobject(4,14,14,"और इस देश के निवासियों से कहेंगे। उन्हों ने तो यह सुना है, कि तू जो यहोवा है इन लोगों के मध्य में रहता है; और प्रत्यक्ष दिखाई देता है, और तेरा बादल उनके ऊपर ठहरा रहता है, और तू दिन को बादल के खम्भे में, और रात को अग्नि के खम्भे में होकर इनके आगे आगे चला करता है।"));
hinsuperobject.biblemap.put("b4_14_15", new hinsuperobject(4,14,15,"इसलिये यदि तू इन लोगों को एक ही बार मे मार डाले, तो जिन जातियों ने तेरी कीर्त्ति सुनी है वे कहेंगी,"));
hinsuperobject.biblemap.put("b4_14_16", new hinsuperobject(4,14,16,"कि यहोवा उन लोगों को उस देश में जिसे उस ने उन्हें देने की शपथ खाई थी पहुंचा न सका, इस कारण उस ने उन्हें जंगल में घात कर डाला है।"));
hinsuperobject.biblemap.put("b4_14_17", new hinsuperobject(4,14,17,"सो अब प्रभु की सामर्थ्य की महिमा तेरे इस कहने के अनुसार हो,"));
hinsuperobject.biblemap.put("b4_14_18", new hinsuperobject(4,14,18,"कि यहोवा कोप करने में धीरजवन्त और अति करूणामय है, और अधर्म और अपराध का क्षमा करनेवाला है, परन्तु वह दोषी को किसी प्रकार से निर्दोष न ठहराएगा, और पूर्वजों के अधर्म का दण्ड उनके बेटों, और पोतों, और परपोतों को देता है।"));
hinsuperobject.biblemap.put("b4_14_19", new hinsuperobject(4,14,19,"अब इन लोगों के अधर्म को अपनी बड़ी करूणा के अनुसार, और जैसे तू मि  से लेकर यहां तक क्षमा करता रहा है वैसे ही अब भी क्षमा कर दे।"));
hinsuperobject.biblemap.put("b4_14_20", new hinsuperobject(4,14,20,"यहोवा ने कहा, तेरी बिनती के अनुसार मैं क्षमा करता हूं;"));
hinsuperobject.biblemap.put("b4_14_21", new hinsuperobject(4,14,21,"परन्तु मेरे जीवन की शपथ सचमुच सारी पृथ्वी यहोवा की महिमा से परिपूर्ण हो जाएगी;"));
hinsuperobject.biblemap.put("b4_14_22", new hinsuperobject(4,14,22,"उन सब लोगों ने जिन्हों ने मेरी महिमा मि  देश में और जंगल में देखी, और मेरे किए हुए आश्चर्यकर्मों को देखने पर भी दस बार मेरी परीक्षा की, और मेरी बातें नहीं मानी,"));
hinsuperobject.biblemap.put("b4_14_23", new hinsuperobject(4,14,23,"इसलिये जिस देश के विषय मैं ने उनके पूर्वजों से शपथ खाई, उसको वे कभी देखने न पाएंगे; अर्थात् जितनों ने मेरा अपमान किया है उन में से कोई भी उसे देखने न पाएगा।"));
hinsuperobject.biblemap.put("b4_14_24", new hinsuperobject(4,14,24,"परन्तु इस कारण से कि मेरे दास कालिब के साथ और ही आत्मा है, और उस ने पूरी रीति से मेरा अनुकरण किया है, मैं उसको उस देश में जिस में वह हो आया है पहुंचाऊंगा, और उसका वंश उस देश का अधिकारी होगा।"));
hinsuperobject.biblemap.put("b4_14_25", new hinsuperobject(4,14,25,"अमालेकी और कनानी लोग तराई में रहते हैं, सो कल तुम घूमकर प्रस्थान करो, और लाल समुद्र के मार्ग से जंगल में जाओ।।"));
hinsuperobject.biblemap.put("b4_14_26", new hinsuperobject(4,14,26,"फिर यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_14_27", new hinsuperobject(4,14,27,"यह बुरी मण्डली मुझ पर बुड़बुड़ाती रहती है, उसको मैं कब तक सहता रहूं? इस्त्राएली जो मुझ पर बुड़बुड़ाते रहते हैं, उनका यह बुड़बुड़ाना मैं ने तो सुना है।"));
hinsuperobject.biblemap.put("b4_14_28", new hinsuperobject(4,14,28,"सो उन से कह, कि यहोवा की यह वाणी है, कि मेरे जीवन की शपथ जो बातें तुम ने मेरे सुनते कही हैं, नि:सन्देह मैं उसी के अनुसार तुम्हारे साथ व्यवहार करूंगा।"));
hinsuperobject.biblemap.put("b4_14_29", new hinsuperobject(4,14,29,"तुम्हारी लोथें इसी जंगल में पड़ी रहेंगी; और तुम सब में से बीस वर्ष की वा उस से अधिक अवस्था के जितने गिने गए थे, और मुझ पर बुड़बुड़ाते थे,"));
hinsuperobject.biblemap.put("b4_14_30", new hinsuperobject(4,14,30,"उस में से यपुन्ने के पुत्रा कालिब और नून के पुत्रा यहोशू को छोड़ कोई भी उस देश में न जाने पाएगा, जिसके विषय मैं ने शपथ खाई है कि तुम को उस में बसाऊंगा।"));
hinsuperobject.biblemap.put("b4_14_31", new hinsuperobject(4,14,31,"परन्तु तुम्हारे बालबच्चे जिनके विषय तुम ने कहा है, कि ये लूट में चले जाएंगे, उनको मैं उस देश में पहुंचा दूंगा; और वे उस देश को जान लेंगे जिस को तुम ने तुच्छ जाना है।"));
hinsuperobject.biblemap.put("b4_14_32", new hinsuperobject(4,14,32,"परन्तु तुम लोगों की लोथें इसी जंगल में पड़ी रहेंगी।"));
hinsuperobject.biblemap.put("b4_14_33", new hinsuperobject(4,14,33,"और जब तक तुम्हारी लोथें जंगल में न गल जाएं तक तक, अर्थात् चालीस वर्ष तक, तुम्हारे बालबच्चे जंगल में तुम्हारे व्यभिचार का फल भोगते हुए चरवाही करते रहेंगे।"));
hinsuperobject.biblemap.put("b4_14_34", new hinsuperobject(4,14,34,"जितने दिन तुम उस देश का भेद लेते रहे, अर्थात् चालीस दिन उनकी गिनती के अनुसार, दिन पीछे उस वर्ष, अर्थात् चालीस वर्ष तक तुम अपने अधर्म का दण्ड उठाए रहोगे, तब तुम जान लोगे कि मेरा विरोध क्या है।"));
hinsuperobject.biblemap.put("b4_14_35", new hinsuperobject(4,14,35,"मैं यहोवा यह कह चुका हूं, कि इस बुरी मण्डली के लोग जो मेरे विरूद्ध इकट्ठे हुए हैं उसी जंगल में मर मिटेंगे; और नि:सन्देह ऐसा ही करूंगा भी।"));
hinsuperobject.biblemap.put("b4_14_36", new hinsuperobject(4,14,36,"तब जिन पुरूषों को मूसा ने उस देश के भेद लेने के लिये भेजा था, और उन्हों ने लौटकर उस देश की नामधराई करके सारी मण्डली को कुड़कुड़ाने के लिये उभारा था,"));
hinsuperobject.biblemap.put("b4_14_37", new hinsuperobject(4,14,37,"उस देश की वे नामधराई करनेवाले पुरूष यहोवा के मारने से उसके साम्हने मर गये।"));
hinsuperobject.biblemap.put("b4_14_38", new hinsuperobject(4,14,38,"परन्तु देश के भेद लेनेवाले पुरूषों में से नून का पुत्रा यहोशू और यपुन्ने का पुत्रा कालिब दोनों जीवित रहे।"));
hinsuperobject.biblemap.put("b4_14_39", new hinsuperobject(4,14,39,"तब मूसा ने ये बातें सब इस्त्राएलियों को कह सुनाई और वे बहुत विलाप करने लगे।"));
hinsuperobject.biblemap.put("b4_14_40", new hinsuperobject(4,14,40,"और वे बिहान को सवेरे उठकर यह कहते हुए पहाड़ की चोटी पर चढ़ने लगे, कि हम ने पाप किया है; परन्तु अब तैयार हैं, और उस स्थान को जाएंगे जिसके विषय यहोवा ने वचन दिया था।"));
hinsuperobject.biblemap.put("b4_14_41", new hinsuperobject(4,14,41,"तब मूसा ने कहा, तुम यहोवा की आज्ञा का उल्लंघन क्यों करते हो? यह सुफल न होगा।"));
hinsuperobject.biblemap.put("b4_14_42", new hinsuperobject(4,14,42,"यहोवा तुम्हारे मध्य में नहीं है, मत चढ़ो, नहीं तो शत्रुओं से हार जाओगे।"));
hinsuperobject.biblemap.put("b4_14_43", new hinsuperobject(4,14,43,"वहां तुम्हारे आगे अमालेकी और कनानी लोग हैं, सो तुम तलवार से मारे जाओगे; तुम यहोवा को छोड़कर फिर गए हो, इसलिये वह तुम्हारे संग नहीं रहेगा।"));
hinsuperobject.biblemap.put("b4_14_44", new hinsuperobject(4,14,44,"परन्तु वे ढिठाई करके पहाड़ की चोटी पर चढ़ गए, परन्तु यहोवा की वाचा का सन्दूक, और मूसा, छावनी से न हटे।"));
hinsuperobject.biblemap.put("b4_14_45", new hinsuperobject(4,14,45,"अब अमालेकी और कनानी जो उस पहाड़ पर रहते थे उन पर चढ़ आए, और होर्मा तक उनको मारते चले आए।।"));
hinsuperobject.biblemap.put("b4_15_1", new hinsuperobject(4,15,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_15_2", new hinsuperobject(4,15,2,"इस्त्राएलियों से कह, कि जब तुम अपने निवास के देश में पहुंचों, जो मैं तुम्हे देता हूं,"));
hinsuperobject.biblemap.put("b4_15_3", new hinsuperobject(4,15,3,"और यहोवा के लिये क्या होमबलि, क्या मेलबलि, कोई हव्य चढ़ावो, चाहे वह विशेष मन्नत पूरी करने का हो चाहे स्वेच्छाबलि का हो, चाहे तुम्हारे नियत समयों में का हो, या वह चाहे गाय- बैल चाहे भेड़- बकरियों में का हो, जिस से यहोवा के लिये सुखदायक सुगन्ध हो;"));
hinsuperobject.biblemap.put("b4_15_4", new hinsuperobject(4,15,4,"तब उस होमबलि वा मेलबलि के संग भेड़ के बच्चे पीछे यहोवा के लिये चौथाई हिन तेल से सना हुआ एपा का दसवां अंश मैदा अन्नबलि करके चढ़ाना,"));
hinsuperobject.biblemap.put("b4_15_5", new hinsuperobject(4,15,5,"और चौथाई हिन दाखमधु अर्घ करके देना।"));
hinsuperobject.biblemap.put("b4_15_6", new hinsuperobject(4,15,6,"और मेढ़े पीछे तिहाई हिन तेल से सना हुआ एपा का दो दसवां अंश मैदा अन्नबलि करके चढ़ाना;"));
hinsuperobject.biblemap.put("b4_15_7", new hinsuperobject(4,15,7,"और उसका अर्घ यहोवा को सुखदायक सुगन्ध देनेवाला तिहाई दिन दाखमधु देना।"));
hinsuperobject.biblemap.put("b4_15_8", new hinsuperobject(4,15,8,"और जब तू यहोवा को होमबलि वा किसी विशेष मन्नत पूरी करने के लिये बलि वा मेलबलि करके बछड़ा चढ़ाए,"));
hinsuperobject.biblemap.put("b4_15_9", new hinsuperobject(4,15,9,"तब बछड़े का चढ़ानेवाला उसके संग आध हिन तेल से सना हुआ एपा का तीन दसवां अंश मैदा अन्नबलि करके चढ़ाए।"));
hinsuperobject.biblemap.put("b4_15_10", new hinsuperobject(4,15,10,"और उसका अर्घ आध हिन दाखमधु चढ़ाए, वह यहोवा को सुखदायक सुगन्ध देनेवाला हव्य होगा।"));
hinsuperobject.biblemap.put("b4_15_11", new hinsuperobject(4,15,11,"एक एक बछड़े, वा मेढ़े, वा भेड़ के बच्चे, वा बकरी के बच्चे के साथ इसी रीति चढ़ावा चढ़ाया जाए।"));
hinsuperobject.biblemap.put("b4_15_12", new hinsuperobject(4,15,12,"तुम्हारे बलिपशुओं की जितनी गिनती हो, उसी गिनती के अनुसार एक एक के साथ ऐसा ही किया करना।"));
hinsuperobject.biblemap.put("b4_15_13", new hinsuperobject(4,15,13,"जितने देशी हों वे यहोवा को सुखदायक सुगन्ध देनेवाला हव्य चढ़ाते समय ये काम इसी रीति से किया करें।"));
hinsuperobject.biblemap.put("b4_15_14", new hinsuperobject(4,15,14,"और यदि कोई परदेशी तुम्हारे संग रहता हो, वा तुम्हारी किसी पीढ़ी में तुम्हारे बीच कोई रहनेवाला हो, और वह यहोवा को सुखदायक सुगन्ध देनेवाला हव्य चढ़ाना चाहे, तो जिस प्रकार तुम करोगे उसी प्रकार वह भी करे।"));
hinsuperobject.biblemap.put("b4_15_15", new hinsuperobject(4,15,15,"मण्डली के लिये, अर्थात् तुम्हारे और तुम्हारे संग रहनेवाले परदेशी दोनों के लिये एक ही विधि हो; तुम्हारी पीढ़ी पीढ़ी में यह सदा की विधि ठहरे, कि जैसे तुम हो वैसे ही परदेशी भी यहोवा के लिये ठहरता है।"));
hinsuperobject.biblemap.put("b4_15_16", new hinsuperobject(4,15,16,"तुम्हारे और तुम्हारे संग रहनेवाले परदेशियों के लिये एक ही व्यवस्था और एक ही नियम है।।"));
hinsuperobject.biblemap.put("b4_15_17", new hinsuperobject(4,15,17,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_15_18", new hinsuperobject(4,15,18,"इस्त्राएलियों को मेरा यह वचन सुना, कि जब तुम उस देश में पहुंचो जहां मैं तुम को लिये जाता हूं,"));
hinsuperobject.biblemap.put("b4_15_19", new hinsuperobject(4,15,19,"और उस देश की उपज का अन्न खाओ, तब यहोवा के लिये उठाई हुई भेंट चढ़ाया करो।"));
hinsuperobject.biblemap.put("b4_15_20", new hinsuperobject(4,15,20,"अपने पहिले गूंधे हुए आटे की एक पपड़ी उठाई हुई भेंट करके यहोवा के लिये चढ़ाना; जैसे तुम खलिहान में से उठाई हुई भेंट चढ़ाओगे वैसे ही उसको भी उठाया करना।"));
hinsuperobject.biblemap.put("b4_15_21", new hinsuperobject(4,15,21,"अपनी पीढ़ी पीढ़ी में अपने पहिले गूंधे हुए आटे में से यहोवा को उठाई हुई भेंट दिया करना।।"));
hinsuperobject.biblemap.put("b4_15_22", new hinsuperobject(4,15,22,"फिर जब तुम इन सब आज्ञाओं में से जिन्हें यहोवा ने मूसा को दिया है किसी का उल्लंघन भूल से करो,"));
hinsuperobject.biblemap.put("b4_15_23", new hinsuperobject(4,15,23,"अर्थात् जिस दिन से यहोवा आज्ञा देने लगा, और आगे की तुम्हारी पीढ़ी पीढ़ी में उस दिन से उस ने जितनी आज्ञाएं मूसा के द्वारा दी हैं,"));
hinsuperobject.biblemap.put("b4_15_24", new hinsuperobject(4,15,24,"उस में यदि भूल से किया हुआ पाप मण्डली के बिना जाने हुआ हो, तो सारी मण्डली यहोवा को सुखदायक सुगन्ध देनेवाला होमबलि करके एक बछड़ा, और उसके संग नियम के अनुसार उसका अन्नबलि और अर्घ चढ़ाए, और पापबलि करके एक बकरा चढ़ाए।"));
hinsuperobject.biblemap.put("b4_15_25", new hinsuperobject(4,15,25,"जब याजक इस्त्राएलियों की सारी मण्डली के लिये प्रायश्चित्त करे, और उनकी क्षमा की जाएगी; क्योंकि उनका पाप भूल से हुआ, और उन्हों ने अपनी भूल के लिये अपना चढ़ावा, अर्थात् यहोवा के लिये हव्य और अपना पापबलि उसके साम्हने चढ़ाया।"));
hinsuperobject.biblemap.put("b4_15_26", new hinsuperobject(4,15,26,"सो इस्त्राएलियों की सारी मण्डली का, और उसके बीच रहनेवाले परदेशी का भी, वह पाप क्षमा किया जाएगा, क्योंकि वह सब लोगों के अनजान में हुआ।"));
hinsuperobject.biblemap.put("b4_15_27", new hinsuperobject(4,15,27,"फिर यदि कोई प्राणी भूल से पाप करे, तो वह एक वर्ष की एक बकरी पापबलि करके चढ़ाए।"));
hinsuperobject.biblemap.put("b4_15_28", new hinsuperobject(4,15,28,"और याजक भूल से पाप करनेवाले प्राणी के लिये यहोवा के साम्हने प्रायश्चित्त करे; सो इस प्रायश्चित्त के कारण उसका वह पाप क्षमा किया जाएगा।"));
hinsuperobject.biblemap.put("b4_15_29", new hinsuperobject(4,15,29,"जो कोई भूल से कुछ करे, चाहे वह परदेशी होकर रहता हो, सब के लिये तुम्हारी एक ही व्यवस्था हो।"));
hinsuperobject.biblemap.put("b4_15_30", new hinsuperobject(4,15,30,"परन्तु क्या देशी क्या परदेशी, जो प्राणी ढिठाई से कुछ करे, वह यहोवा का अनादर करनेवाला ठहरेगा, और वह प्राणी अपने लोगों में से नाश किया जाए।"));
hinsuperobject.biblemap.put("b4_15_31", new hinsuperobject(4,15,31,"वह जो यहोवा का वचन तुच्छ जानता है, और उसकी आज्ञा का टालनेवाला है, इसलिये वह प्राणी निश्चय नाश किया जाए; उसका अधर्म उसी के सिर पड़ेगा।।"));
hinsuperobject.biblemap.put("b4_15_32", new hinsuperobject(4,15,32,"जब इस्त्राएली जंगल में रहते थे, उन दिनों एक मनुष्य विश्राम के दिन लकड़ी बीनता हुआ मिला।"));
hinsuperobject.biblemap.put("b4_15_33", new hinsuperobject(4,15,33,"और जिनको वह लकड़ी बीनता हुआ मिला, वे उसको मूसा और हारून, और सारी मण्डली के पास ले गए।"));
hinsuperobject.biblemap.put("b4_15_34", new hinsuperobject(4,15,34,"उन्हों ने उसको हवालात में रखा, क्योंकि ऐसे मनुष्य से क्या करना चाहिये वह प्रकट नहीं किया गया था।"));
hinsuperobject.biblemap.put("b4_15_35", new hinsuperobject(4,15,35,"तब यहोवा ने मूसा से कहा, वह मनुष्य निश्चय मार डाला जाए; सारी मण्डली के लोग छावनी के बाहर उस पर पत्थरवाह करें।"));
hinsuperobject.biblemap.put("b4_15_36", new hinsuperobject(4,15,36,"सो जैसा यहोवा ने मूसा को आज्ञा दी थी उसी के अनुसार सारी मण्डली के लोगों ने उसको छावनी से बाहर ले जाकर पत्थरवाह किया, और वह मर गया।"));
hinsuperobject.biblemap.put("b4_15_37", new hinsuperobject(4,15,37,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_15_38", new hinsuperobject(4,15,38,"इस्त्राएलियों से कह, कि अपनी पीढ़ी पीढ़ी में अपने वस्त्रों के कोर पर झालर लगाया करना, और एक एक कोर की झालर पर एक नीला फीता लगाया करना;"));
hinsuperobject.biblemap.put("b4_15_39", new hinsuperobject(4,15,39,"और वह तुम्हारे लिये ऐसी झालर ठहरे, जिस से जब जब तुम उसे देखो तब तब यहोवा की सारी आज्ञाएं तुम हो स्मरण आ जाएं; और तुम उनका पालन करो, और तुम अपने अपने मन और अपनी अपनी दृष्टि के वश में होकर व्यभिचार न करते फिरो जैसे करते आए हो।"));
hinsuperobject.biblemap.put("b4_15_40", new hinsuperobject(4,15,40,"परन्तु तुम यहोवा की सब आज्ञाओं को स्मरण करके उनका पालन करो, और अपने परमेश्वर के लिये पवित्रा बनो।"));
hinsuperobject.biblemap.put("b4_15_41", new hinsuperobject(4,15,41,"मैं यहोवा तुम्हारा परमेश्वर हूं, जो तुम्हे मि  देश से निकाल ले आया कि तुम्हारा परमेश्वर ठहरूं; मैं तुम्हारा परमेश्वर यहोवा हूं।।"));
hinsuperobject.biblemap.put("b4_16_1", new hinsuperobject(4,16,1,"कोरह जो लेवी का परपोता, कहात का पोता, और यिसहार का पुत्रा था, वह एलीआब के पुत्रा दातान और अबीराम, और पेलेत के पुत्रा ओन,"));
hinsuperobject.biblemap.put("b4_16_2", new hinsuperobject(4,16,2,"इन तीनों रूबेनियों से मिलकर मण्डली के अढ़ाई सौ प्रधान, जो सभासद और नामी थे, उनको संग लिया;"));
hinsuperobject.biblemap.put("b4_16_3", new hinsuperobject(4,16,3,"और वे मूसा और हारून के विरूद्ध उठ खड़े हुए, और उन से कहने लगे, तुम ने बहुत किया, अब बस करो; क्योंकि सारी मण्डली का एक एक मनुष्य पवित्रा है, और यहोवा उनके मध्य में रहता है; इसलिये तुम यहोवा की मण्डली में ऊंचे पदवाले क्यों बन बैठे हो?"));
hinsuperobject.biblemap.put("b4_16_4", new hinsuperobject(4,16,4,"यह सुनकर मूसा अपने मुंह के बल गिरा;"));
hinsuperobject.biblemap.put("b4_16_5", new hinsuperobject(4,16,5,"फिर उस ने कोरह और उसकी सारी मण्डली से कहा, कि बिहान को यहोवा दिखला देगा कि उसका कौन है, और पवित्रा कौन है, और उसको अपने समीप बुला लेगा; जिसको वह आप चुन लेगा उसी को अपने समीप बुला भी लेगा।"));
hinsuperobject.biblemap.put("b4_16_6", new hinsuperobject(4,16,6,"इसलिये, हे कोरह, तुम अपनी सारी मण्डली समेत यह करो, अर्थात् अपना अपना धूपदान ठीक करो;"));
hinsuperobject.biblemap.put("b4_16_7", new hinsuperobject(4,16,7,"और कल उन में आग रखकर यहोवा के साम्हने धूप देना, तब जिसको यहोवा चुन ले वही पवित्रा ठहरेगा। हे लेवियों, तुम भी बड़ी बड़ी बातें करते हो, अब बस करो।"));
hinsuperobject.biblemap.put("b4_16_8", new hinsuperobject(4,16,8,"फिर मूसा ने कोरह से कहा, हे लेवियों, सुनो,"));
hinsuperobject.biblemap.put("b4_16_9", new hinsuperobject(4,16,9,"क्या यह तुम्हें छोटी बात जान पड़ती है, कि इस्त्राएल के परमेश्वर ने तुम को इस्त्राएल की मण्डली से अलग करके अपने निवास की सेवकाई करने, और मण्डली के साम्हने खड़े होकर उसकी भी सेवा टहल करने के लिये अपने समीप बुला लिया है;"));
hinsuperobject.biblemap.put("b4_16_10", new hinsuperobject(4,16,10,"और तुझे और तेरे सब लेवी भाइयों को भी अपने समीप बुला लिया है? फिर भी तुम याजक पद के भी खोजी हो?"));
hinsuperobject.biblemap.put("b4_16_11", new hinsuperobject(4,16,11,"और इसी कारण तू ने अपनी सारी मण्डली को यहोवा के विरूद्ध इकट्ठी किया है; हारून कौन है कि तुम उस पर बुड़बुड़ाते हो?"));
hinsuperobject.biblemap.put("b4_16_12", new hinsuperobject(4,16,12,"फिर मूसा ने एलीआब के पुत्रा दातान और अबीराम को बुलवा भेजा; और उन्हों ने कहा, हम तेरे पास नहीं आएंगे।"));
hinsuperobject.biblemap.put("b4_16_13", new hinsuperobject(4,16,13,"क्या यह एक छोटी बात है, कि तू हम को ऐसे देश से जिस में दूध और मधु की धाराएं बहती है इसलिये निकाल लाया है, कि हमें जंगल में मार डाले, फिर क्या तू हमारे ऊपर प्रधान भी बनकर अधिकार जताता है?"));
hinsuperobject.biblemap.put("b4_16_14", new hinsuperobject(4,16,14,"फिर तू हमें ऐसे देश में जहां दूध और मधु की धाराएं बहती हैं नहीं पहुंचाया, और न हमें खेतों और दाख की बारियों के अधिकारी किया। क्या तू इन लोगों की आंखों में धूलि डालेगा? हम तो नहीं आएंगे।"));
hinsuperobject.biblemap.put("b4_16_15", new hinsuperobject(4,16,15,"तब मूसा का कोप बहुत भड़क उठा, और उस ने यहोवा से कहा, उन लोगों की भेंट की ओर दृष्टि न कर। मैं ने तो उन से एक गदहा भी नहीं लिया, और न उन में से किसी की हानि की है।"));
hinsuperobject.biblemap.put("b4_16_16", new hinsuperobject(4,16,16,"तब मूसा ने कोरह से कहा, कल तू अपनी सारी मण्डली को साथ लेकर हारून के साथ यहोवा के साम्हने हाजिर होना;"));
hinsuperobject.biblemap.put("b4_16_17", new hinsuperobject(4,16,17,"और तुम सब अपना अपना धूपदान लेकर उन में धूप देना, फिर अपना अपना धूपदान जो सब समेत अढ़ाई सौ होंगे यहोवा के साम्हने ले जाना; विशेष करके तू और हारून अपना अपना धूपदान ले जाना।"));
hinsuperobject.biblemap.put("b4_16_18", new hinsuperobject(4,16,18,"सो उन्हों ने अपना अपना धूपदान लेकर और उन में आग रखकर उन पर धूप डाला; और मूसा और हारून के साथ मिलापवाले तम्बू के द्वार पर खड़े हुए।"));
hinsuperobject.biblemap.put("b4_16_19", new hinsuperobject(4,16,19,"और कोरह ने सारी मण्डली को उनके विरूद्ध मिलापवाले तम्बू के द्वार पर इकट्ठा कर लिया। तब यहोवा का तेज सारी मण्डली को दिखाई दिया।।"));
hinsuperobject.biblemap.put("b4_16_20", new hinsuperobject(4,16,20,"तब यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_16_21", new hinsuperobject(4,16,21,"उस मण्डली के बीच में से अलग हो जाओ। कि मैं उन्हें पल भर में भस्म कर डालूं।"));
hinsuperobject.biblemap.put("b4_16_22", new hinsuperobject(4,16,22,"तब वे मुंह के बल गिरके कहने लगे, हे ईश्वर, हे सब प्राणियों के आत्माओं के परमेश्वर, क्या एक पुरूष के पाप के कारण तेरा क्रोध सारी मण्डली पर होगा?"));
hinsuperobject.biblemap.put("b4_16_23", new hinsuperobject(4,16,23,"यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_16_24", new hinsuperobject(4,16,24,"मण्डली के लोगों से कह, कि कोरह, दातान, और अबीराम के तम्बुओं के आसपास से हट जाओ।"));
hinsuperobject.biblemap.put("b4_16_25", new hinsuperobject(4,16,25,"तब मूसा उठकर दातान और अबीराम के पास गया; और इस्त्राएलियों के वृद्ध लोग उसके पीछे पीछे गए।"));
hinsuperobject.biblemap.put("b4_16_26", new hinsuperobject(4,16,26,"और उस ने मण्डली के लोगों से कहा, तुम उन दुष्ट मनुष्यों के डेरों के पास से हट जाओ, और उनकी कोई वस्तु न छूओ, कहीं ऐसा न हो कि तुम भी उनके सब पापों में फंसकर मिट जाओ।"));
hinsuperobject.biblemap.put("b4_16_27", new hinsuperobject(4,16,27,"यह सुन वे कोरह, दातान, और अबीराम के तम्बुओं के आसपास से हट गए; परन्तु दातान और अबीराम निकलकर अपनी पत्नियों, बेंटों, और बालबच्चों समेत अपने अपने डेरे के द्वार पर खड़े हुए।"));
hinsuperobject.biblemap.put("b4_16_28", new hinsuperobject(4,16,28,"तब मूसा ने कहा, इस से तुम जान लोगे कि यहोवा ने मुझे भेजा है कि यह सब काम करूं, क्योंकि मैं ने अपनी इच्छा से कुछ नहीं किया।"));
hinsuperobject.biblemap.put("b4_16_29", new hinsuperobject(4,16,29,"यदि उन मनुष्यों की मृत्यु और सब मनुष्यों के समान हो, और उनका दण्ड सब मनुष्यों के समान हो, तब जानों कि मैं यहोवा का भेजा हुआ नहीं हूं।"));
hinsuperobject.biblemap.put("b4_16_30", new hinsuperobject(4,16,30,"परन्तु यदि यहोवा अपनी अनोखी शक्ति प्रकट करे, और पृथ्वी अपना मुंह पसारकर उनको, और उनका सब कुछ निगल जाए, और वे जीते जी अधोलोक में जा पड़ें, तो तुम समझ लो कि इन मनुष्यों ने यहोवा का अपमान किया है।"));
hinsuperobject.biblemap.put("b4_16_31", new hinsuperobject(4,16,31,"वह ये सब बातें कह ही चुका था, कि भूमि उन लोगों के पांव के नीचे फट गई;"));
hinsuperobject.biblemap.put("b4_16_32", new hinsuperobject(4,16,32,"और पृथ्वी ने अपना मुंह खोल दिया और उनका और उनका घरद्वार का सामान, और कोरह के सब मनुष्यों और उनकी सारी सम्पत्ति को भी निगल लिया।"));
hinsuperobject.biblemap.put("b4_16_33", new hinsuperobject(4,16,33,"और वे और उनका सारा घरबार जीवित ही अधोलोक में जा पड़े; और पृथ्वी ने उनको ढंाप लिया, और वे मण्डली के बीच में से नष्ट हो गए।"));
hinsuperobject.biblemap.put("b4_16_34", new hinsuperobject(4,16,34,"और जितने इस्त्राएली उनके चारों ओर थे वे उनका चिल्लाना सुन यह कहते हुए भागे, कि कहीं पृथ्वी हम को भी निगल न ले!"));
hinsuperobject.biblemap.put("b4_16_35", new hinsuperobject(4,16,35,"तब यहोवा के पास से आग निकली, और उन अढ़ाई सौ धूप चढ़ानेवालों को भस्म कर डाला।।"));
hinsuperobject.biblemap.put("b4_16_36", new hinsuperobject(4,16,36,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_16_37", new hinsuperobject(4,16,37,"हारून याजक के पुत्रा एलीआजार से कह, कि उन धूपदानों को आग में से उठा ले; और आग के अंगारों को उधर ही छितरा दे, क्योंकि वे पवित्रा हैं।"));
hinsuperobject.biblemap.put("b4_16_38", new hinsuperobject(4,16,38,"जिन्हों ने पाप करके अपने ही प्राणों की हानि की है, उनके धूपदानों के पत्तर पीट पीटकर बनाए जाएं जिस से कि वह वेदी के मढ़ने के काम आवे; क्योंकि उन्हों ने यहोवा के साम्हने रखा था; इस से वे पवित्रा हैं। इस प्रकार वह इस्त्राएलियों के लिये एक निशान ठहरेगा।"));
hinsuperobject.biblemap.put("b4_16_39", new hinsuperobject(4,16,39,"सो एलीआजर याजक ने उन पीतल के धूपदानों को, जिन में उन जले हुए मनुष्यों ने धूप चढ़ाया था, लेकर उनके पत्तर पीटकर वेदी के मढ़ने के लिये बनवा दिए,"));
hinsuperobject.biblemap.put("b4_16_40", new hinsuperobject(4,16,40,"कि इस्त्राएलियों को इस बात का स्मरण रहे कि कोई दूसरा, जो हारून के वंश का न हो, यहोवा के साम्हने धूप चढ़ाने को समीप न जाए, ऐसा न हो कि वह भी कोरह और उसकी मण्डली के समान नष्ट हो जाए, जैसे कि यहोवा ने मूसा के द्वारा उसको आज्ञा दी थी।।"));
hinsuperobject.biblemap.put("b4_16_41", new hinsuperobject(4,16,41,"दूसरे दिन इस्त्राएलियों की सारी मण्डली यह कहकर मूसा और हारून पर बुड़बुड़ाने लगी, कि यहोवा की प्रजा को तुम ने मार डाला है।"));
hinsuperobject.biblemap.put("b4_16_42", new hinsuperobject(4,16,42,"और जब मण्डली के लोग मूसा और हारून के विरूद्ध इकट्ठे हो रहे थे, तब उन्हों ने मिलापवाले तम्बू की ओर दृष्टि की; और देखा, कि बादल ने उसे छा लिया है, और यहोवा का तेज दिखाई दे रहा है।"));
hinsuperobject.biblemap.put("b4_16_43", new hinsuperobject(4,16,43,"तब मूसा और हारून मिलापवाले तम्बू के साम्हने आए,"));
hinsuperobject.biblemap.put("b4_16_44", new hinsuperobject(4,16,44,"तब यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_16_45", new hinsuperobject(4,16,45,"तुम उस मण्डली के लोगों के बीच से हट जाओ, कि मैं उन्हें पल भर में भस्म कर डालूं। तब वे मुंह के बल गिरे।"));
hinsuperobject.biblemap.put("b4_16_46", new hinsuperobject(4,16,46,"और मूसा ने हारून से कहा, धूपदान को लेकर उस में वेदी पर से आग रखकर उस पर धूप डाल, मण्डली के पास फुरती से जाकर उसके लिये प्रायश्चित्त कर; क्योंकि यहोवा का कोप अत्यन्त भड़का है, और मरी फैलने लगी है।"));
hinsuperobject.biblemap.put("b4_16_47", new hinsuperobject(4,16,47,"मूसा की आज्ञा के अनुसार हारून धूपदान लेकर मण्डली के बीच में दौड़ा गया; और यह देखकर कि लोगों में मरी फैलने लगी है, उस ने धूप जलाकर लोगों के लिये प्रायश्चित्त किया।"));
hinsuperobject.biblemap.put("b4_16_48", new hinsuperobject(4,16,48,"और वह मुर्दों और जीवित के मध्य में खड़ा हुआ; तब मरी थम गई।"));
hinsuperobject.biblemap.put("b4_16_49", new hinsuperobject(4,16,49,"और जो कोरह के संग भागी होकर मर गए थे, उन्हें छोड़ जो लोग इस मरी से मर गए वे चौदह हजार सात सौ थे।"));
hinsuperobject.biblemap.put("b4_16_50", new hinsuperobject(4,16,50,"तब हारून मिलापवाले तम्बू के द्वार पर मूसा के पास लौट गया, और मरी थम गई।।"));
hinsuperobject.biblemap.put("b4_17_1", new hinsuperobject(4,17,1,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_17_2", new hinsuperobject(4,17,2,"इस्त्राएलियों से बातें करके उन के पूर्वजों के घरानों के अनुसार, उनके सब प्रधानों के पास से एक एक छड़ी ले; और उन बारह छड़ियों में से एक एक पर एक एक के मूल पुरूष का नाम लिख,"));
hinsuperobject.biblemap.put("b4_17_3", new hinsuperobject(4,17,3,"और लेवियों की छड़ी पर हारून का नाम लिख। क्योंकि इस्त्राएलियों के पूर्वजों के घरानों के एक एक मुख्य पुरूष की एक एक छड़ी होगी।"));
hinsuperobject.biblemap.put("b4_17_4", new hinsuperobject(4,17,4,"और उन छड़ियों को मिलापवाले तम्बू में साक्षीपत्रा के आगे, जहां मैं तुम लोगों से मिला करता हूं, रख दे।"));
hinsuperobject.biblemap.put("b4_17_5", new hinsuperobject(4,17,5,"और जिस पुरूष को मैं चुनूंगा उसकी छड़ी में कलियां फूट निकलेंगी; और इस्त्राएली जो तुम पर बुड़बुड़ाते रहते हैं, वह बुड़बुड़ाना मैं अपने ऊपर से दूर करूंगा।"));
hinsuperobject.biblemap.put("b4_17_6", new hinsuperobject(4,17,6,"सो मूसा ने इस्त्राएलियों से यह बात कही; और उनके सब प्रधानों ने अपने अपने लिये, अपने अपने पूर्वजों के घरानों के अनुसार, एक एक छड़ी उसे दी, सो बारह छड़ियां हुई; और उन की छड़ियों में हारून की भी छड़ी थी।"));
hinsuperobject.biblemap.put("b4_17_7", new hinsuperobject(4,17,7,"उन छड़ियों को मूसा ने साक्षीपत्रा के तम्बू में यहोवा के साम्हने रख दिया।"));
hinsuperobject.biblemap.put("b4_17_9", new hinsuperobject(4,17,9,"सो मूसा उन सब छड़ियों को यहोवा के साम्हने से निकालकर सब इस्त्राएलियों के पास ले गया; और उन्हों ने अपनी अपनी छड़ी पहिचानकर ले ली।"));
hinsuperobject.biblemap.put("b4_17_10", new hinsuperobject(4,17,10,"फिर यहोवा ने मूसा से कहा, हारून की छड़ी को साक्षीपत्रा के साम्हने फिर धर दे, कि यह उन दंगा करनेवालो के लिये एक निशान बनकर रखी रहे, कि तू उनका बुड़बुड़ाना जो मेरे विरूद्ध होता रहता है भविष्य में रोक रखे, ऐसा न हो कि वे मर जाएं।"));
hinsuperobject.biblemap.put("b4_17_11", new hinsuperobject(4,17,11,"और मूसा ने यहोवा की इस आज्ञा के अनुसार ही किया।।"));
hinsuperobject.biblemap.put("b4_17_12", new hinsuperobject(4,17,12,"तब इस्त्राएली मूसा से कहने लगे, देख, हमारे प्राण निकला चाहते हैं, हम नष्ट हुए, हम सब के सब नष्ट हुए जाते हैं।"));
hinsuperobject.biblemap.put("b4_17_13", new hinsuperobject(4,17,13,"जो कोई यहोवा के निवास के समीप जाता है वह मारा जाता है। तो क्या हम सब के सब मर ही जाएंगे।।"));
hinsuperobject.biblemap.put("b4_18_1", new hinsuperobject(4,18,1,"फिर यहोवा ने हारून से कहा, कि पवित्रास्थान के अधर्म का भार तुझ पर, और तेरे पुत्रों और तेरे पिता के घराने पर होगा; और तुम्हारा याजक कर्म के अधर्म का भार भी तेरे पुत्रों पर होगा।"));
hinsuperobject.biblemap.put("b4_18_2", new hinsuperobject(4,18,2,"और लेवी का गोत्रा, अर्थात् तेरे मूलपुरूष के गोत्रावाले जो तेरे भाई हैं, उनको भी अपने साथ ले आया कर, और वे तुझ से मिल जाएं, और तेरी सेवा टहल किया करें, परन्तु साक्षीपत्रा के तम्बू के साम्हने तू और तेरे पुत्रा ही आया करें।"));
hinsuperobject.biblemap.put("b4_18_3", new hinsuperobject(4,18,3,"जो तुझे सौंपा गया है उसकी और सारे तम्बू की भी वे रक्षा किया करें; परन्तु पवित्रास्थान के पात्रों के और वेदी के समीप न आएं, ऐसा न हो कि वे और तुम लोग भी मर जाओ।"));
hinsuperobject.biblemap.put("b4_18_4", new hinsuperobject(4,18,4,"सो वे तुझ से मिल जाएं, और मिलापवाले तम्बू की सारी सेवकाई की वस्तुओं की रक्षा किया करें; परन्तु जो तेरे कुल का न हो वह तुम लोगों के समीप न आने पाए।"));
hinsuperobject.biblemap.put("b4_18_5", new hinsuperobject(4,18,5,"और पवित्रास्थान और वेदी की रखवाली तुम ही किया करो, जिस से इस्त्राएलियों पर फिर कोप न भड़के।"));
hinsuperobject.biblemap.put("b4_18_6", new hinsuperobject(4,18,6,"परन्तु मैं ने आप तुम्हारे लेवी भाइयों को इस्त्राएलियों के बीच से अलग कर लिया है, और वे मिलापवाले तम्बू की सेवा करने के लिये तुम को और यहोवा को सौंप दिये गए हैं।"));
hinsuperobject.biblemap.put("b4_18_7", new hinsuperobject(4,18,7,"पर वेदी की और बीचवाले पर्दे के भीतर की बातों की सेवकाई के लिये तू और तेरे पुत्रा अपने याजकपद की रक्षा करना, और तुम ही सेवा किया करना; क्योंकि मैं तुम्हें याजकपद की सेवकाई दान करता हूं; और जो तेरे कुल का न हो वह यदि समीप आए तो मार डाला जाए।।"));
hinsuperobject.biblemap.put("b4_18_8", new hinsuperobject(4,18,8,"फिर यहोवा ने हारून से कहा, सुन, मै आप तुझ को उठाई हुई भेंट सौंप देता हूं, अर्थात् इस्त्राएलियों की पवित्रा की हुई वस्तुएं; जितनी हों उन्हें मैं तेरा अभिषेक वाला भाग ठहराकर तुझे और तेरे पुत्रों को सदा का हक करके दे देता हूं।"));
hinsuperobject.biblemap.put("b4_18_9", new hinsuperobject(4,18,9,"जो परमपवित्रा वस्तुएं आग में होम न ही जाएंगी वे तेरी ही ठहरें, अर्थात् इस्त्राएलियों के सब चढ़ावों में से उनके सब अन्नबलि, सब पापबलि, और सब दोषबलि, जो वे मुझ को दें, वह तेरे और तेरे पुत्रों के लिये परमपवित्रा ठहरें।"));
hinsuperobject.biblemap.put("b4_18_10", new hinsuperobject(4,18,10,"उनको परमपवित्रा वस्तु जानकर खाया करना; उनको हर एक पुरूष खा सकता है; वे तेरे लिये पवित्रा हैं।"));
hinsuperobject.biblemap.put("b4_18_11", new hinsuperobject(4,18,11,"फिर ये वस्तुएं भी तेरी ठहरें, अर्थात् जितनी भेंट इस्त्राएली हिलाने के लिये दें, उनको मैं तुझे और तेरे बेटे- बेटियों को सदा का हक करके दे देता हूं; तेरे घराने में जितने शुद्ध हों वह उन्हें खा सकेंगे।"));
hinsuperobject.biblemap.put("b4_18_12", new hinsuperobject(4,18,12,"फिर उत्तम से उत्तम नया दाखमधु, और गेहूं, अर्थात् इनकी पहली उपज जो वे यहोवा को दें, वह मैं तुझ को देता हूं।"));
hinsuperobject.biblemap.put("b4_18_13", new hinsuperobject(4,18,13,"उनके देश के सब प्रकार की पहली उपज, जो वे यहोवा के लिये ले आएं, वह तेरी ही ठहरे; तेरे घराने में जितने शुद्ध हों वे उन्हें खा सकेंगें।"));
hinsuperobject.biblemap.put("b4_18_14", new hinsuperobject(4,18,14,"इस्त्राएलियों में जो कुछ अर्पण किया जाए वह भी तेरा ही ठहरे।"));
hinsuperobject.biblemap.put("b4_18_15", new hinsuperobject(4,18,15,"सब प्राणियों में से जितने अपनी अपनी मां के पहिलौठे हों, जिन्हें लोग यहोवा के लिये चढ़ाएं, चाहे मनुष्य के चाहे पशु के पहिलौठे हों, वे सब तेरे ही ठहरें; परन्तु मनुष्यों और अशुद्ध पशुओं के पहिलौठों को दाम लेकर छोड़ देना।"));
hinsuperobject.biblemap.put("b4_18_16", new hinsuperobject(4,18,16,"और जिन्हें छुड़ाना हो, जब वे महीने भर के हों तब उनके लिये अपने ठहराए हुए मोल के अनुसार, अर्थात् पवित्रास्थान के बीस गेरा के शेकेल के हिसाब से पांच शेकेल लेके उन्हें छोड़ना।"));
hinsuperobject.biblemap.put("b4_18_17", new hinsuperobject(4,18,17,"पर गाय, वा भेड़ी, वा बकरी के पहिलौठे को न छोड़ना; वे तो पवित्रा हैं। उनके लोहू को वेदी पर छिड़क देना, और उनकी चरबी को हव्य करके जलाना, जिस से यहोवा के लिये सुखदायक सुगन्ध हो;"));
hinsuperobject.biblemap.put("b4_18_18", new hinsuperobject(4,18,18,"परन्तु उनका मांस तेरा ठहरे, और हिलाई हुई छाती, और दहिनी जांघ भी तेरा ही ठहरे।"));
hinsuperobject.biblemap.put("b4_18_19", new hinsuperobject(4,18,19,"पवित्रा वस्तुओं की जितनी भेंटें इस्त्राएली यहोवा को दें, उन सभों को मैं तुझे और तेरे बेटे- बेटियों को सदा का हक करके दे देता हूं: यह तो तेरे और तेरे वंश के लिये यहोवा की सदा के लिये नमक की अटल वाचा है।"));
hinsuperobject.biblemap.put("b4_18_20", new hinsuperobject(4,18,20,"फिर यहोवा ने हारून से कहा, इस्त्राएलियों के देश में तेरा कोई भाग न होगा, और न उनके बीच तेरा कोई अंश होगा; उनके बीच तेरा भाग और तेरा अंश मैं ही हूं।।"));
hinsuperobject.biblemap.put("b4_18_21", new hinsuperobject(4,18,21,"फिर मिलापवाले तम्बू की जो सेवा लेवी करते हैं उसके बदले मैं उनको इस्त्राएलियों का सब दशमांश उनका निज भाग कर देता हूं।"));
hinsuperobject.biblemap.put("b4_18_22", new hinsuperobject(4,18,22,"और भविष्य में इस्त्राएली मिलापवाले तम्बू के समीप न आएं, ऐसा न हो कि उनके सिर पर पाप लगे, और वे मर जाएं।"));
hinsuperobject.biblemap.put("b4_18_23", new hinsuperobject(4,18,23,"परन्तु लेवी मिलापवाले तम्बू की सेवा किया करें, और उनके अधर्म का भार वे ही उठाया करें; यह तुम्हारी पीढ़ीयों में सदा की विधि ठहरे; और इस्त्राएलियों के बीच उनका कोई निज भाग न होगा।"));
hinsuperobject.biblemap.put("b4_18_24", new hinsuperobject(4,18,24,"क्योंकि इस्त्राएली जो दशमांश यहोवा को उठाई हुई भेंट करके देंगे, उसे मैं लेवियों को निज भाग करके देता हूं, इसीलिये मैं ने उनके विषय में कहा है, कि इस्त्राएलियों के बीच कोई भाग उनको न मिले।"));
hinsuperobject.biblemap.put("b4_18_25", new hinsuperobject(4,18,25,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_18_26", new hinsuperobject(4,18,26,"तू लेवियों से कह, कि जब जब तुम इस्त्राएलियों के हाथ से वह दशमांश लो जिसे यहोवा तुम को तुम्हारा निज भाग करके उन से दिलाता है, तब तब उस में से यहोवा के लिये एक उठाई हुई भेंट करके दशमांश का दशमांश देना।"));
hinsuperobject.biblemap.put("b4_18_27", new hinsuperobject(4,18,27,"और तुम्हारी उठाई हुई भेंट तुम्हारे हित के लिये ऐसी गिनी जाएगी जैसा खलिहान में का अन्न, वा रसकुण्ड में का दाखरस गिना जाता है।"));
hinsuperobject.biblemap.put("b4_18_28", new hinsuperobject(4,18,28,"इस रीति तुम भी अपने सब दशमांशों में से, जो इस्त्राएलियों की ओर से पाओगे, यहोवा को एक उठाई हुई भेंट देना; और यहोवा की यह उठाई हुई भेंट हारून याजक को दिया करना।"));
hinsuperobject.biblemap.put("b4_18_29", new hinsuperobject(4,18,29,"जितने दान तुम पाओ उन में से हर एक का उत्तम से उत्तम भाग, जो पवित्रा ठहरा है, सो उसे यहोवा के लिये उठाई हुई भेंट करके पूरी पूरी देना।"));
hinsuperobject.biblemap.put("b4_18_30", new hinsuperobject(4,18,30,"इसलिये तू लेवियों से कह, कि जब तुम उस में का उत्तम से उत्तम भाग उठाकर दो, तब यह तुम्हारे लिये खलिहान में के अन्न, और रसकुण्ड के रस के तुल्य गिना जाएगा;"));
hinsuperobject.biblemap.put("b4_18_31", new hinsuperobject(4,18,31,"और उसको तुम अपने घरानों समेत सब स्थानों में खा सकते हो, क्योंकि मिलापवाले तम्बू की जो सेवा तुम करोगे उसका बदला यही ठहरा है।"));
hinsuperobject.biblemap.put("b4_18_32", new hinsuperobject(4,18,32,"और जब तुम उसका उत्तम से उत्तम भाग उठाकर दो तब उसके कारण तुम को पाप न लगेगा। परन्तु इस्त्राएलियों की पवित्रा की हुई वस्तुओं को अपवित्रा न करना, ऐसा न हो कि तुम मर जाओ।।"));
hinsuperobject.biblemap.put("b4_19_1", new hinsuperobject(4,19,1,"फिर यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_19_2", new hinsuperobject(4,19,2,"व्यवस्था की जिस विधि की आज्ञा यहोवा देता है वह यह है; कि तू इस्त्राएलियों से कह, कि मेरे पास एक लाल निर्दोष बछिया ले आओ, जिस में कोई भी दोष न हो, और जिस पर जूआ कभी न रखा गया हो।"));
hinsuperobject.biblemap.put("b4_19_3", new hinsuperobject(4,19,3,"तब एलीआजर याजक को दो, और वह उसे छावनी से बाहर ले जाए, और कोई उसको उसके सम्हने बलिदान करे;"));
hinsuperobject.biblemap.put("b4_19_4", new hinsuperobject(4,19,4,"तब एलीआजर याजक अपनी उंगली से उसका कुछ लोहू लेकर मिलापवाले तम्बू के साम्हने की ओर सात बार छिड़क दे।"));
hinsuperobject.biblemap.put("b4_19_5", new hinsuperobject(4,19,5,"तब कोई उस बछिया को खाल, मांस, लोहू, और गोबर समेत उसके साम्हने जलाए;"));
hinsuperobject.biblemap.put("b4_19_6", new hinsuperobject(4,19,6,"और याजक देवदारू की लकड़ी, जूफा, और लाल रंग का कपड़ा लेकर उस आग में जिस में बछिया जलती हो डाल दे।"));
hinsuperobject.biblemap.put("b4_19_7", new hinsuperobject(4,19,7,"तब वह अपने वस्त्रा धोए और स्नान करे, इसके बाद छावनी में तो आए, परन्तु सांझ तक अशुद्ध रहे।"));
hinsuperobject.biblemap.put("b4_19_8", new hinsuperobject(4,19,8,"और जो मनुष्य उसको जलाए वह भी जल से अपने वस्त्रा धोए और स्नान करे, और सांझ तक अशुद्ध रहे।"));
hinsuperobject.biblemap.put("b4_19_9", new hinsuperobject(4,19,9,"फिर कोई शुद्ध पुरूष उस बछिया की राख बटोरकर छावनी के बाहर किसी शुद्ध स्थान में रख छोड़े; और वह राख इस्त्राएलियों की मण्डली के लिये अशुद्धता से छुड़ानेवाले जल के लिये रखी रहे; वह तो पापबलि है।"));
hinsuperobject.biblemap.put("b4_19_10", new hinsuperobject(4,19,10,"और जो मनुष्य बछिया की राख बटोरे वह अपने वस्त्रा धोए, और सांझ तक अशुद्ध रहे। और यह इस्त्राएलियों के लिये, और उनके बीच रहनेवाले परदेशियों के लिये भी सदा की विधि ठहरे।"));
hinsuperobject.biblemap.put("b4_19_11", new hinsuperobject(4,19,11,"जो किसी मनुष्य की लोथ छूए वह सात दिन तक अशुद्ध रहे;"));
hinsuperobject.biblemap.put("b4_19_12", new hinsuperobject(4,19,12,"ऐसा मनुष्य तीसरे दिन उस जल से पाप छुड़ाकर अपने को पावन करे, और सातवें दिन शुद्ध ठहरे; परन्तु यदि वह तीसरे दिन आप को पाप छुड़ाकर पावन न करे, तो सातवें दिन शुद्ध ठहरेगा।"));
hinsuperobject.biblemap.put("b4_19_13", new hinsuperobject(4,19,13,"जो कोई किसी मनुष्य की लोथ छूकर पाप छुड़ाकर अपने को पावन न करे, वह यहोवा के निवासस्थान का अशुद्ध करनेवाला ठहरेगा, और वह प्राणी इस्त्राएल में से नाश किया जाए; अशुद्धता से छुड़ानेवाला जल उस पर न छिड़का गया, इस कारण वह अशुद्ध ठहरेगा, उसकी अशुद्धता उस में बची रहेगी।"));
hinsuperobject.biblemap.put("b4_19_14", new hinsuperobject(4,19,14,"यदि कोई मनुष्य डेरे में मर जाए तो व्यवस्था यह यह है, कि जितने उस डेरे में रहें, वा उस में जाएं, वे सब सात दिन तक अशुद्ध रहें।"));
hinsuperobject.biblemap.put("b4_19_15", new hinsuperobject(4,19,15,"और हर एक खुला हुआ पात्रा, जिस पर कोई ढकना लगा न हो, वह अशुद्ध ठहरे।"));
hinsuperobject.biblemap.put("b4_19_16", new hinsuperobject(4,19,16,"और जो कोई मैदान में तलवार से मारे हुए को, वा अपनी मृत्यु से मरे हुए को, वा मनुष्य की हड्डी को, वा किसी कब्र को छूए, तो सात दिन तक अशुद्ध रहे।"));
hinsuperobject.biblemap.put("b4_19_17", new hinsuperobject(4,19,17,"अशुद्ध मनुष्य के लिये जलाए हुए पापबलि की राख में से कुछ लेकर पात्रा में डालकर उस पर सोते का जल डाला जाए;"));
hinsuperobject.biblemap.put("b4_19_18", new hinsuperobject(4,19,18,"तब कोई शुद्ध मनुष्य जूफा लेकर उस जल में डुबाकर जल को उस डेरे पर, और जितने पात्रा और मनुष्य उस में हों, उन पर छिड़के, और हड्डी के, वा मारे हुए के, वा अपनी मृत्यु से मरे हुए के, वा कब्र के छूनेवाले पर छिड़क दे;"));
hinsuperobject.biblemap.put("b4_19_19", new hinsuperobject(4,19,19,"वह शुद्ध पुरूष तीसरे दिन और सातवें दिन उस अशुद्ध मनुष्य पर छिड़के; और सातवें दिन वह उसके पाप छुड़ाकर उसको पावन करे, तब वह अपने वस्त्रों को धोकर और जल से स्नान करके सांझ को शुद्ध ठहरे।"));
hinsuperobject.biblemap.put("b4_19_20", new hinsuperobject(4,19,20,"और जो कोई अशुद्ध होकर अपने पाप छुड़ाकर अपने को पावन न कराए, वह प्राणी यहोवा के पवित्रा स्थान का अशुद्ध करनेवाला ठहरेगा, इस कारण वह मण्डली के बीच में से नाश किया जाए; अशुद्धता से छुडानेवाला जल उस पर न छिड़का गया, इस कारण से वह अशुद्ध ठहरेगा।"));
hinsuperobject.biblemap.put("b4_19_21", new hinsuperobject(4,19,21,"और यह उनके लिये सदा की विधि ठहरे। जो अशुद्धता से छुड़ानेवाला जल छिड़के वह अपने वस्त्रों को धोए; और जिस जन से अशुद्धता से छुड़ानेवाला जल छू जाए वह भी सांझ तक अशुद्ध रहे।"));
hinsuperobject.biblemap.put("b4_19_22", new hinsuperobject(4,19,22,"और जो कुछ वह अशुद्ध मनुष्य छूए वह भी अशुद्ध ठहरे; और जो प्राणी उस वस्तु को छूए वह भी सांझ तक अशुद्ध रहे।।"));
hinsuperobject.biblemap.put("b4_20_1", new hinsuperobject(4,20,1,"पहिले महीने में सारी इस्त्राएली मण्डली के लोग सीनै नाम जंगल में आ गए, और कादेश में रहने लगे; और वहां मरियम मर गई, और वहीं उसको मिट्टी दी गई।"));
hinsuperobject.biblemap.put("b4_20_2", new hinsuperobject(4,20,2,"वहां मण्डली के लोगों के लिये पानी न मिला; सो वे मूसा और हारून के विरूद्ध इकट्ठे हुए।"));
hinsuperobject.biblemap.put("b4_20_3", new hinsuperobject(4,20,3,"और लोग यह कहकर मूसा से झगड़ने लगे, कि भला होता कि हम उस समय ही मर गए होते जब हमारे भाई यहोवा के साम्हने मर गए!"));
hinsuperobject.biblemap.put("b4_20_4", new hinsuperobject(4,20,4,"और तुम यहोवा की मण्डली को इस जंगल में क्यों ले आए हो, कि हम अपने पशुओं समेत यहां मर जाए?"));
hinsuperobject.biblemap.put("b4_20_5", new hinsuperobject(4,20,5,"और तुम ने हम को मि  से क्यों निकालकर इस बुरे स्थान में पहुंचाया है? यहां तो बीच, वा अंजीर, वा दाखलता, वा अनार, कुछ नहीं है, यहां तक कि पीने को कुछ पानी भी नहीं है।"));
hinsuperobject.biblemap.put("b4_20_6", new hinsuperobject(4,20,6,"तब मूसा और हारून मण्डली के साम्हने से मिलापवाले तम्बू के द्वार पर जाकर अपने मुंह के बल गिरे। और यहोवा का तेज उनको दिखाई दिया।"));
hinsuperobject.biblemap.put("b4_20_7", new hinsuperobject(4,20,7,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_20_8", new hinsuperobject(4,20,8,"उस लाठी को ले, और तू अपने भाई हारून समेत मण्डली को इकट्ठा करके उनके देखते उस चट्टान से बातें कर, तब वह अपना जल देगी; इस प्रकार से तू चट्टान में से उनके लिये जल निकाल कर मण्डली के लोगों और उनके पशुओं को पिला।"));
hinsuperobject.biblemap.put("b4_20_9", new hinsuperobject(4,20,9,"यहोवा की इस आज्ञा के अनुसार मूसा ने उसके साम्हने से लाठी को ले लिया।"));
hinsuperobject.biblemap.put("b4_20_10", new hinsuperobject(4,20,10,"और मूसा और हारून ने मण्डली को उस चट्टान के साम्हने इकट्ठा किया, तब मूसा ने उस से कह, हे दंगा करनेवालो, सुनो; क्या हम को इस चट्टान में से तुम्हारे लिये जल निकालना होगा?"));
hinsuperobject.biblemap.put("b4_20_11", new hinsuperobject(4,20,11,"तब मूसा ने हाथ उठाकर लाठी चट्टान पर दो बार मारी; और उस में से बहुत पानी फूट निकला, और मण्डली के लोग अपने पशुओं समेत पीने लगे।"));
hinsuperobject.biblemap.put("b4_20_12", new hinsuperobject(4,20,12,"परन्तु मूसा और हारून से यहोवा ने कहा, तुम ने जो मुझ पर विश्वास नहीं किया, और मुझे इस्त्राएलियों की दृष्टि में पवित्रा नहीं ठहराया, इसलिये तुम इस मण्डली को उस देश में पहुंचाने न पाओगे जिसे मैं ने उन्हें दिया है।"));
hinsuperobject.biblemap.put("b4_20_13", new hinsuperobject(4,20,13,"उस सोते का नाम मरीबा पड़ा, क्योंकि इस्त्राएलियों ने यहोवा से झगड़ा किया था, और वह उनके बीच पवित्रा ठहराया गया।।"));
hinsuperobject.biblemap.put("b4_20_14", new hinsuperobject(4,20,14,"फिर मूसा ने कादेश से एदोम के राजा के पास दूत भेजे, कि तेरा भाई इस्त्राएल यों कहता है, कि हम पर जो जो क्लेश पड़े हैं वह तू जानता होगा;"));
hinsuperobject.biblemap.put("b4_20_15", new hinsuperobject(4,20,15,"अर्थात् यह कि हमारे पुरखा मि  में गए थे, और हम मि  में बहुत दिन रहे; और मिस्त्रियों ने हमारे पुरखाओं के साथ और हमारे साथ भी बुरा बर्ताव किया;"));
hinsuperobject.biblemap.put("b4_20_16", new hinsuperobject(4,20,16,"परन्तु जब हम ने यहोवा की दोहाई दी तब उस ने हमारी सुनी, और एक दूत को भेजकर हमें मि  से निकाल ले आया है; सो अब हम कादेश नगर में हैं जो तेरे सिवाने ही पर है।"));
hinsuperobject.biblemap.put("b4_20_17", new hinsuperobject(4,20,17,"सो हमें अपने देश में से होकर जाने दे। हम किसी खेत वा दाख की बारी से होकर न चलेंगे, और कूओं का पानी न पीएंगे; सड़क- सड़क होकर चले जाएंगे, और जब तक तेरे देश से बाहर न हो जाएं, तब तक न दहिने न बाएं मुड़ेंगे।"));
hinsuperobject.biblemap.put("b4_20_18", new hinsuperobject(4,20,18,"परन्तु एदोमियों ने उसके पास कहला भेजा, कि तू मेरे देश में से होकर मत जा, नहीं तो मैं तलवार लिये हुए तेरा साम्हना करने को निकलूंगा।"));
hinsuperobject.biblemap.put("b4_20_19", new hinsuperobject(4,20,19,"इस्त्राएलियों ने उसके पास फिर कहला भेजा, कि हम सड़क ही सड़क चलेंगे, और यदि हम और हमारे पशु तेरा पानी पीएं, तो उसका दाम देंगे, हम को और कुछ नहीं, केवल पांव पांव चलकर निकल जाने दे।"));
hinsuperobject.biblemap.put("b4_20_20", new hinsuperobject(4,20,20,"परन्तु उस ने कहा, तू आने न पाएगा। और एदोम बड़ी सेना लेकर भुजबल से उसका साम्हना करने को निकल आया।"));
hinsuperobject.biblemap.put("b4_20_21", new hinsuperobject(4,20,21,"इस प्रकार एदोम ने इस्त्राएल को अपने देश के भीतर से होकर जाने देने से इन्कार किया; इसलिये इस्त्राएल उसकी ओर से मुड़ गए।।"));
hinsuperobject.biblemap.put("b4_20_22", new hinsuperobject(4,20,22,"तब इस्त्राएलियों की सारी मण्डली कादेश से कूच करके होर नाम पहाड़ के पास आ गई।"));
hinsuperobject.biblemap.put("b4_20_23", new hinsuperobject(4,20,23,"और एदोम देश के सिवाने पर होर पहाड़ में यहोवा ने मूसा और हारून से कहा,"));
hinsuperobject.biblemap.put("b4_20_24", new hinsuperobject(4,20,24,"हारून अपने लोगों मे जा मिलेगा; क्योंकि तुम दोनो ने जो मरीबा नाम सोते पर मेरा कहना छोड़कर मुझ से बलवा किया है, इस कारण वह उस देश में जाने न पाएगा जिसे मैं ने इस्त्राएलियों को दिया है।"));
hinsuperobject.biblemap.put("b4_20_25", new hinsuperobject(4,20,25,"सो तू हारून और उसके पुत्रा एलीआजर को होर पहाड़ पर ले चल;"));
hinsuperobject.biblemap.put("b4_20_26", new hinsuperobject(4,20,26,"और हारून के वस्त्रा उतारके उसके पुत्रा एलीआजर को पहिना; तब हारून वहीं मरकर अपने लोगों मे जा मिलेगा।"));
hinsuperobject.biblemap.put("b4_20_27", new hinsuperobject(4,20,27,"यहोवा की इस आज्ञा के अनुसार मूसा ने किया; वे सारी मण्डली के देखते होर पहाड़ पर चढ़ गए।"));
hinsuperobject.biblemap.put("b4_20_28", new hinsuperobject(4,20,28,"तब मूसा ने हारून के वस्त्रा उतारके उसके पुत्रा एलीआजर को पहिनाए और हारून वहीं पहाड़ की चोटी पर मर गया। तब मूसा और एलीआजर पहाड़ पर से उतर आए।"));
hinsuperobject.biblemap.put("b4_20_29", new hinsuperobject(4,20,29,"और जब इस्त्राएल की सारी मण्डली ने देखा कि हारून का प्राण छूट गया है, तब इस्त्राएल के सब घराने के लोग उसके लिये तीस दिन तक रोते रहे।।"));
hinsuperobject.biblemap.put("b4_21_1", new hinsuperobject(4,21,1,"तब अराद का कनानी राजा, जो दक्खिन देश में रहता था, यह सुनकर, कि जिस मार्ग से वे भेदिये आए थे उसी मार्ग से अब इस्त्राएली आ रहे हैं, इस्त्राएल से लड़ा, और उन में से कितनों को बन्धुआ कर लिया।"));
hinsuperobject.biblemap.put("b4_21_2", new hinsuperobject(4,21,2,"तब इस्त्राएलियों ने यहोवा से यह कहकर मन्नत मानी, कि यदि तू सचमुच उन लोगों को हमारे वश में कर दे, तो हम उनके नगरों को सत्यनाश कर देंगे।"));
hinsuperobject.biblemap.put("b4_21_3", new hinsuperobject(4,21,3,"इस्त्राएल की यह बात सुनकर यहोवा ने कनानियों को उनके वश में कर दिया; सो उन्हों ने उनके नगरों समेत उनको भी सत्यानाश किया; इस से उस स्थान का नाम होर्मा रखा गया।।"));
hinsuperobject.biblemap.put("b4_21_4", new hinsuperobject(4,21,4,"फिर उन्हों ने होर पहाड़ से कूच करके लाल समुद्र का मार्ग लिया, कि एदोम देश से बाहर बाहर घूमकर जाएं; और लोगों का मन मार्ग के कारण बहुत व्याकुल हो गया।"));
hinsuperobject.biblemap.put("b4_21_5", new hinsuperobject(4,21,5,"सो वे परमेश्वर के विरूद्ध बात करने लगे, और मूसा से कहा, तुम लोग हम को मि  से जंगल में मरने के लिये क्यों ले आए हो? यहां न तो रोटी है, और न पानी, और हमारे प्राण इस निकम्मी रोटी से दुखित हैं।"));
hinsuperobject.biblemap.put("b4_21_6", new hinsuperobject(4,21,6,"सो यहोवा ने उन लोगों में तेज विषवाले सांप भेजे, जो उनको डसने लगे, और बहुत से इस्त्राएली मर गए।"));
hinsuperobject.biblemap.put("b4_21_7", new hinsuperobject(4,21,7,"तब लोग मूसा के पास जाकर कहने लगे, हम ने पाप किया है, कि हम ने यहोवा के और तेरे विरूद्ध बातें की हैं; यहोवा से प्रार्थना कर, कि वह सांपों को हम से दूर करे। तब मूसा ने उनके लिये प्रार्थना की।"));
hinsuperobject.biblemap.put("b4_21_8", new hinsuperobject(4,21,8,"यहोवा ने मूसा से कहा एक तेज विषवाले सांप की प्रतिमा बनवाकर खम्भे पर लटका; तब जो सांप से डसा हुआ उसको देख ले वह जीवित बचेगा।"));
hinsuperobject.biblemap.put("b4_21_9", new hinsuperobject(4,21,9,"सो मूसा ने पीतल को एक सांप बनवाकर खम्भे पर लटकाया; तब सांप के डसे हुओं में से जिस जिस ने उस पीतल के सांप को देखा वह जीवित बच गया।"));
hinsuperobject.biblemap.put("b4_21_10", new hinsuperobject(4,21,10,"फिर इस्त्राएलियों ने कूच करके ओबोत में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_21_11", new hinsuperobject(4,21,11,"और ओबोत से कूच करके अबारीम नाम डीहों में डेरे डाले, जो पूरब की ओर मोआब के साम्हने के जंगल में है।"));
hinsuperobject.biblemap.put("b4_21_12", new hinsuperobject(4,21,12,"वंहा से कूच करके उन्हों ने जेरेद नाम नाले में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_21_13", new hinsuperobject(4,21,13,"वहां से कूच करके उन्हों ने अर्नोन नदी, जो जंगल में बहती और एमोरियों के देश से निकलती है, उसकी परली ओर डेरे खड़े किए; क्योंकि अर्नोन मोआबियों और एमोरियों के बीच होकर मोआब देश का सिवाना ठहरा है।"));
hinsuperobject.biblemap.put("b4_21_14", new hinsuperobject(4,21,14,"इस कारण यहोवा के संग्राम नाम पुस्तक में इस प्रकार लिखा है, कि सूपा में बाहेब, और अर्नोन के नाले,"));
hinsuperobject.biblemap.put("b4_21_15", new hinsuperobject(4,21,15,"और उन नालों की ढलान जो आर नाम नगर की ओर है, और जो मोआब के सिवाने पर है।"));
hinsuperobject.biblemap.put("b4_21_16", new hinsuperobject(4,21,16,"फिर वहां से कूच करके वे बैर तक गए; वहां वही कूआं है जिसके विषय में यहोवा ने मूसा से कहा था, कि उन लोगों को इकट्ठा कर, और मैं उन्हे पानी दूंगा।।"));
hinsuperobject.biblemap.put("b4_21_17", new hinsuperobject(4,21,17,"उस समय इस्त्राएल ने यह गीत गया, कि हे कूएं, उबल आ, उस कूएं के विषय में गाओ!"));
hinsuperobject.biblemap.put("b4_21_18", new hinsuperobject(4,21,18,"जिसको हाकिमों ने खोदा, और इस्त्राएल के रईसों ने अपने सोंटों और लाठियों से खोद लिया।।"));
hinsuperobject.biblemap.put("b4_21_19", new hinsuperobject(4,21,19,"फिर वे जंगल से मत्ताना को, और मत्ताना से नहलीएल को, और नहलीएल से बामोत को,"));
hinsuperobject.biblemap.put("b4_21_20", new hinsuperobject(4,21,20,"और बामोत से कूच करके उस तराई तक जो मोआब के मैदान में है, और पिसगा के उस सिरे तक भी जो यशीमोन की ओर झुका है पहुंच गए।।"));
hinsuperobject.biblemap.put("b4_21_21", new hinsuperobject(4,21,21,"तब इस्त्राएल ने एमोरियों के राजा सीहोन के पास दूतों से यह कहला भेजा,"));
hinsuperobject.biblemap.put("b4_21_22", new hinsuperobject(4,21,22,"कि हमें अपने देश में होकर जाने दे; हम मुड़कर किसी खेत वा दाख की बारी में तो न जाएंगे; न किसी कूएं का पानी पीएंगे; और जब तक तेरे देश से बाहर न हो जाएं तब तक सड़क ही से चले जाएंगे।"));
hinsuperobject.biblemap.put("b4_21_23", new hinsuperobject(4,21,23,"तौभी सीहोन ने इस्त्राएल को अपने देश से होकर जाने न दिया; वरन अपनी सारी सेना को इकट्ठा करके इस्त्राएल का साम्हना करने को जंगल में निकल आया, और यहस को आकर उन से लड़ा।"));
hinsuperobject.biblemap.put("b4_21_24", new hinsuperobject(4,21,24,"तब इस्त्राएलियों ने उस को तलवार से मार लिया, और अर्नोन से यब्बोक नदी तक, जो अम्मोनियों का सिवाना था, उसके देश के अधिकारी हो गए; अम्मोनियों का सिवाना तो दृढ़ था।"));
hinsuperobject.biblemap.put("b4_21_25", new hinsuperobject(4,21,25,"सो इस्त्राएल ने एमोरियों के सब नगरों को ले लिया, और उन में, अर्थात् हेशबोन और उसके आस पास के नगरों में रहने लगे।"));
hinsuperobject.biblemap.put("b4_21_26", new hinsuperobject(4,21,26,"हेशबोन एमोरियों के राजा सीहोन का नगर था; उस ने मोआब के अगले राजा से लड़के उसका सारा देश अर्नोन तक उसके हाथ से छीन लिया था।"));
hinsuperobject.biblemap.put("b4_21_27", new hinsuperobject(4,21,27,"इस कारण गूढ़ बात के कहनेवाले कहते हैं, कि हेशबोन में आओ, सीहोन का नगर बसे, और दृढ़ किया जाए।"));
hinsuperobject.biblemap.put("b4_21_28", new hinsuperobject(4,21,28,"क्योंकि हेशबोन से आग, अर्थात् सीहोन के नगर से लौ निकली; जिस से मोआब देश का आर नगर, और अर्नोन के ऊंचे स्थानों के स्वामी भस्म हुए।"));
hinsuperobject.biblemap.put("b4_21_29", new hinsuperobject(4,21,29,"हे मोआब, तुझ पर हाय! कमोश देवता की प्रजा नाश हुई, उस ने अपने बेटों को भगेडू, और अपनी बेटियों को एमोरी राजा सीहोन की दासी कर दिया।"));
hinsuperobject.biblemap.put("b4_21_30", new hinsuperobject(4,21,30,"हम ने उन्हें गिरा दिया है, हेशबोन दीबोन तक नष्ट हो गया है, और हम ने नोपह और मेदबा तक भी उजाड़ दिया है।।"));
hinsuperobject.biblemap.put("b4_21_31", new hinsuperobject(4,21,31,"सो इस्त्राएल एमोरियों के देश में रहने लगा।"));
hinsuperobject.biblemap.put("b4_21_32", new hinsuperobject(4,21,32,"तब मूसा ने याजेर नगर का भेद लेने को भेजा; और उन्हों ने उसके गांवों को लिया, और वहां के एमोरियों को उस देश से निकाल दिया।"));
hinsuperobject.biblemap.put("b4_21_33", new hinsuperobject(4,21,33,"तब वे मुड़के बाशान के मार्ग से जाने लगे; और बाशान के राजा ओग न उनका साम्हना किया, अर्थात् लड़ने को अपनी सारी सेना समेत एद्रेई में निकल आया।"));
hinsuperobject.biblemap.put("b4_21_34", new hinsuperobject(4,21,34,"तब यहोवा ने मूसा से कहा, उस से मत डर; क्योंकि मैं उसको सारी सेना और देश समेत तेरे हाथ में कर देता हूं; और जैसा तू ने एमोरियों के राजा हेशबोनवासी सीहोन के साथ किया है, वैसा ही उसके साथ भी करना।"));
hinsuperobject.biblemap.put("b4_21_35", new hinsuperobject(4,21,35,"तब उन्हों ने उसको, और उसके पुत्रों और सारी प्रजा को यहां तक मारा कि उसका कोई भी न बचा; और वे उसके देश के अधिकारी को गए।"));
hinsuperobject.biblemap.put("b4_22_1", new hinsuperobject(4,22,1,"तब इस्त्राएलियों ने कूच करके यरीहो के पास यरदन नदी के इस पार मोआब के अराबा में डेरे खड़े किए।।"));
hinsuperobject.biblemap.put("b4_22_2", new hinsuperobject(4,22,2,"और सिप्पोर के पुत्रा बालाक ने देखा कि इस्त्राएल ने एमोरियों से क्या क्या किया है।"));
hinsuperobject.biblemap.put("b4_22_3", new hinsuperobject(4,22,3,"इसलिये मोआब यह जानकर, कि इस्त्राएली बहुत हैं, उन लोगों से अत्यन्त डर गया; यहां तक कि मोआब इस्त्राएलियों के कारण अत्यन्त व्याकुल हुआ।"));
hinsuperobject.biblemap.put("b4_22_4", new hinsuperobject(4,22,4,"तब मोआबियों ने मिद्यानी पुरनियों से कहा, अब वह दल हमारे चारों ओर के सब लोगों को चट कर जाएगा, जिस तरह बैल खेत की हरी घास को चट कर जाता है। उस समय सिप्पोर का पुत्रा बालाक मोआब का राजा था;"));
hinsuperobject.biblemap.put("b4_22_5", new hinsuperobject(4,22,5,"और इस ने पतोर नगर को, जो महानद के तट पर बोर के पुत्रा बिलाम के जातिभाइयों की भूमि थी, वहां बिलाम के पास दूत भेजे, कि वे यह कहकर उसे बुला लाएं, कि सुन एक दल मि  से निकल आया है, और भूमि उन से ढक गई है, और अब वे मेरे साम्हने ही आकर बस गए हैं।"));
hinsuperobject.biblemap.put("b4_22_6", new hinsuperobject(4,22,6,"इसलिये आ, और उन लोगों को मेरे निमित्त शाप दे, क्योंकि वे मुझ से अधिक बलवन्त हैं, तब सम्भव है कि हम उन पर जयवन्त हों, और हम सब इनको अपने देश से मारकर निकाल दें; क्योंकि यह तो मैं जानता हूं कि जिसको तू आशीर्वाद देता है वह धन्य होता है, और जिसको तू शाप देता है वह स्रापित होता है।"));
hinsuperobject.biblemap.put("b4_22_7", new hinsuperobject(4,22,7,"तब मोआबी और मिद्यानी पुरनिये भावी कहने की दक्षिणा लेकर चले, और बिलाम के पास पहुंचकर बालाक की बातें कह सुनाईं।"));
hinsuperobject.biblemap.put("b4_22_8", new hinsuperobject(4,22,8,"उस ने उन से कहा, आज रात को यहां टिको, और जो बात यहोवा मुझ से कहेगा, उसी के अनुसार मैं तुम को उत्तर दूंगा; तब मोआब के हाकिम बिलाम के यहां ठहर गए।"));
hinsuperobject.biblemap.put("b4_22_9", new hinsuperobject(4,22,9,"तब परमेश्वर ने बिलाम के पास आकर पूछा, कि तेरे यहां ये पुरूष कौन हैं?"));
hinsuperobject.biblemap.put("b4_22_10", new hinsuperobject(4,22,10,"बिलाम ने परमेश्वर से कहा सिप्पोर के पुत्रा मोआब के राजा बालाक ने मेरे पास यह कहला भेजा है,"));
hinsuperobject.biblemap.put("b4_22_11", new hinsuperobject(4,22,11,"कि सुन, जो दल मि  से निकल आया है उस से भूमि ढंप गई है; इसलिये आकर मेरे लिये उन्हें शाप दे; सम्भव है कि मैं उनसे लड़कर उनको बरबस निकाल सकूंगा।"));
hinsuperobject.biblemap.put("b4_22_12", new hinsuperobject(4,22,12,"परमेश्वर ने बिलाम से कहा, तू इनके संग मत जा; उन लोगों को शाप मत दे, क्योंकि वे आशीष के भागी हो चुके हैं।"));
hinsuperobject.biblemap.put("b4_22_13", new hinsuperobject(4,22,13,"भोर को बिलाम ने उठकर बालाक के हाकिमों से कहा, तुम अपने देश को चले जाओ; क्योंकि यहोवा मुझे तुम्हारे साथ जाने की आज्ञा नहीं देता।"));
hinsuperobject.biblemap.put("b4_22_14", new hinsuperobject(4,22,14,"तब मोआबी हाकिम चले गए और बालाक के पास जाकर कहा, कि बिलाम ने हमारे साथ आने से नाह किया है।"));
hinsuperobject.biblemap.put("b4_22_15", new hinsuperobject(4,22,15,"इस पर बालाक ने फिर और हाकिम भेजे, जो पहिलों से प्रतिष्ठित और गिनती में भी अधिक थे।"));
hinsuperobject.biblemap.put("b4_22_16", new hinsuperobject(4,22,16,"उन्हों ने बिलाम के पास आकर कहा, कि सिप्पोर का पुत्रा बालाक यों कहता है, कि मेरे पास आने से किसी कारण नाह न कर;"));
hinsuperobject.biblemap.put("b4_22_17", new hinsuperobject(4,22,17,"क्योंकि मैं निश्चय तेरी बड़ी प्रतिष्ठा करूंगा, और जो कुछ तू मुझ से कहे वही मैं करूंगा; इसलिये आ, और उन लोगों को मेरे निमित्त शाप दे।"));
hinsuperobject.biblemap.put("b4_22_18", new hinsuperobject(4,22,18,"बिलाम ने बालाक के कर्मचारियों को उत्तर दिया, कि चाहे बालाक अपने घर को सोने चांदी से भरकर मुझे दे दे, तौभी मैं अपने परमेश्वर यहोवा की आज्ञा को पलट नहीं सकता, कि उसे घटाकर वा बढ़ाकर मानूं।"));
hinsuperobject.biblemap.put("b4_22_19", new hinsuperobject(4,22,19,"इसलिये अब तुम लोग आज रात को यहीं टिके रहो, ताकि मैं जान लूं, कि यहोवा मुझ से और क्या कहता है।"));
hinsuperobject.biblemap.put("b4_22_20", new hinsuperobject(4,22,20,"और परमेश्वर ने रात को बिलाम के पास आकर कहा, यदि वे पुरूष तुझे बुलाने आए हैं, तो तू उठकर उनके संग जा; परन्तु जो बात मैं तुझ से कहूं उसी के अनुसार करना।"));
hinsuperobject.biblemap.put("b4_22_21", new hinsuperobject(4,22,21,"तब बिलाम भोर को उठा, और अपनी गदही पर काठी बान्धकर मोआबी हाकिमों के संग चल पड़ा।"));
hinsuperobject.biblemap.put("b4_22_22", new hinsuperobject(4,22,22,"और उसके जाने के कारण परमेश्वर का कोप भड़क उठा, और यहोवा का दूत उसका विरोध करने के लिये मार्ग रोककर खड़ा हो गया। वह तो अपनी गदही पर सवार होकर जा रहा था, और उसके संग उसके दो सेवक भी थे।"));
hinsuperobject.biblemap.put("b4_22_23", new hinsuperobject(4,22,23,"और उस गदही को यहोवा का दूत हाथ में नंगी तलवार लिये हुए मार्ग में खड़ा दिखाई पड़ा; तब गदही मार्ग छोड़कर खेत में चली गई; तब बिलाम ने गदही को मारा, कि वह मार्ग पर फिर आ जाए।"));
hinsuperobject.biblemap.put("b4_22_24", new hinsuperobject(4,22,24,"तब यहोवा का दूत दाख की बारियों के बीच की गली में, जिसके दोनों ओर बारी की दीवार थी, खड़ा हुआ।"));
hinsuperobject.biblemap.put("b4_22_25", new hinsuperobject(4,22,25,"यहोवा के दूत को देखकर गदही दीवार से ऐसी सट गई, कि बिलाम का पांव दीवार से दब गया; तब उस ने उसको फिर मारा।"));
hinsuperobject.biblemap.put("b4_22_26", new hinsuperobject(4,22,26,"तब यहोवा का दूत आगे बढ़कर एक सकेत स्थान पर खड़ा हुआ, जहां न तो दहिनी ओर हटने की जगह थी और न बाईं ओर।"));
hinsuperobject.biblemap.put("b4_22_27", new hinsuperobject(4,22,27,"वहां यहोवा के दूत को देखकर गदही बिलाम को लिये दिये बैठ गई; फिर तो बिलाम का कोप भड़क उठा, और उस ने गदही को लाठी से मारा।"));
hinsuperobject.biblemap.put("b4_22_28", new hinsuperobject(4,22,28,"तब यहोवा ने गदही का मुंह खोल दिया, और वह बिलाम से कहने लगी, मैं ने तेरा क्या किया है, कि तू ने मुझे तीन बार मारा?"));
hinsuperobject.biblemap.put("b4_22_29", new hinsuperobject(4,22,29,"बिलाम ने गदही से कहा, यह कि तू ने मुझ से नटखटी की। यदि मेरे हाथ में तलवार होती तो मैं तुझे अभी मार डालता।"));
hinsuperobject.biblemap.put("b4_22_30", new hinsuperobject(4,22,30,"गदही ने बिलाम से कहा क्या मैं तेरी वही गदही नहीं जिस पर तू जन्म से आज तक चढ़ता आया है? क्या मैं तुझ से कभी ऐसा करती थी? वह बोला, नहीं।"));
hinsuperobject.biblemap.put("b4_22_31", new hinsuperobject(4,22,31,"तब यहोवा ने बिलाम की आंखे खोलीं, और उसको यहोवा का दूत हाथ में नंगी तलवार लिये हुए मार्ग में खड़ा दिखाई पड़ा; तब वह झुक गया, और मुंह के बल गिरके दण्डवत की।"));
hinsuperobject.biblemap.put("b4_22_32", new hinsuperobject(4,22,32,"यहोवा के दूत ने उस से कहा, तू ने अपनी गदही को तीन बार क्यों मारा? सुन, तेरा विरोध करने को मैं ही आया हूं, इसलिये कि तू मेरे साम्हने उलटी चाल चलता है;"));
hinsuperobject.biblemap.put("b4_22_33", new hinsuperobject(4,22,33,"और यह गदही मुझे देखकर मेरे साम्हने से तीन बार हट गई। जो वह मेरे साम्हने से हट न जाती, तो नि:सन्देह मैं अब तक तुझ को मार ही डालता, परन्तु उसको जीवित छोड़ देता।"));
hinsuperobject.biblemap.put("b4_22_34", new hinsuperobject(4,22,34,"तब बिलाम ने यहोवा के दूत से कहा, मैं ने पाप किया है; मैं नहीं जानता था कि तू मेरा साम्हना करने को मार्ग में खड़ा है। इसलिये अब यदि तुझे बुरा लगता है, तो मैं लौट जाता हूं।"));
hinsuperobject.biblemap.put("b4_22_35", new hinsuperobject(4,22,35,"यहोवा के दूत ने बिलाम से कहा, इन पुरूषों के संग तू चला जा; परन्तु केवल वही बात कहना जो मैं तुझ से कहूंगा। तब बिलाम बालाक के हाकिमों के संग चला गया।"));
hinsuperobject.biblemap.put("b4_22_36", new hinsuperobject(4,22,36,"यह सुनकर, कि बिलाम आ रहा है, बालाक उस से भेंट करने के लिये मोआब के उस नगर तक जो उस देश के अर्नोनवाले सिवाने पर है गया।"));
hinsuperobject.biblemap.put("b4_22_37", new hinsuperobject(4,22,37,"बालाक ने बिलाम से कहा, क्या मैं ने बड़ी आशा से तुझे नहीं बुलवा भेजा था? फिर तू मेरे पास क्यों नहीं चला आया? क्या मैं इस योग्य नहीं कि सचमुच तेरी उचित प्रतिष्ठा कर सकता?"));
hinsuperobject.biblemap.put("b4_22_38", new hinsuperobject(4,22,38,"बिलाम ने बालाक से कहा, देख मैं तेरे पास आया तो हूं! परन्तु अब क्या मैं कुछ कर सकता हूं? जो बात परमेश्वर मेरे मुंह में डालेगा वही बात मैं कहूंगा।"));
hinsuperobject.biblemap.put("b4_22_39", new hinsuperobject(4,22,39,"तब बिलाम बालाक के संग संग चला, और वे किर्यथूसोत तक आए।"));
hinsuperobject.biblemap.put("b4_22_40", new hinsuperobject(4,22,40,"और बालाक ने बैल और भेड़- बकरियों को बलि किया, और बिलाम और उसके साथ के हाकिमों के पास भेजा।"));
hinsuperobject.biblemap.put("b4_22_41", new hinsuperobject(4,22,41,"बिहान को बालाक बिलाम को बालू के ऊंचे स्थानों पर चढ़ा ले गया, और वहां से उसको सब इस्त्राएली लोग दिखाई पड़े।।"));
hinsuperobject.biblemap.put("b4_23_1", new hinsuperobject(4,23,1,"तब बिलाम ने बालाक से कहा, यहां पर मेरे लिये सात वेदियां बनवा, और इसी स्थान पर सात बछड़े और सात मेढ़े तैयार कर।"));
hinsuperobject.biblemap.put("b4_23_2", new hinsuperobject(4,23,2,"तब बालाक ने बिलाम के कहने के अनुसार किया; और बालाक और बिलाम ने मिलकर प्रत्येक वेदी पर एक बछड़ा और एक मेढ़ा चढ़ाया।"));
hinsuperobject.biblemap.put("b4_23_3", new hinsuperobject(4,23,3,"फिर बिलाम ने बालाक से कहा, तू अपने होमबलि के पास खड़ा रह, और मैं जाता हूं; सम्भव है कि यहोवा मुझ से भेंट करने को आए; और जो कुछ वह मुझ पर प्रकाश करेगा वही मैं तुझ को बताऊंगा। तब वह एक मुण्डे पहाड़ पर गया।"));
hinsuperobject.biblemap.put("b4_23_4", new hinsuperobject(4,23,4,"और परमेश्वर बिलाम से मिला; और बिलाम ने उस से कहा, मैं ने सात वेदियां तैयार की हैं, और प्रत्येक वेदी पर एक बछड़ा और एक मेढ़ा चढ़ाया है।"));
hinsuperobject.biblemap.put("b4_23_5", new hinsuperobject(4,23,5,"यहोवा ने बिलाम के मुंह में एक बाल डालीं, और कहा, बालाक के पास लौट जो, और यों कहना।"));
hinsuperobject.biblemap.put("b4_23_6", new hinsuperobject(4,23,6,"और वह उसके पास लौटकर आ गया, और क्या देखता है, कि वह सारे मोआबी हाकिमों समेत अपने होमबलि के पास खड़ा है।"));
hinsuperobject.biblemap.put("b4_23_7", new hinsuperobject(4,23,7,"तब बिलाम ने अपनी गूढ़ बात आरम्भ की, और कहने लगा, बालाक ने मुझे आराम से, अर्थात् मोआब के राजा ने मुझे पूरब के पहाड़ों से बुलवा भेजा: आ, मेरे लिये याकूब को शाप दे, आ, इस्त्राएल को धमकी दे!"));
hinsuperobject.biblemap.put("b4_23_8", new hinsuperobject(4,23,8,"परन्तु जिन्हें ईश्वर ने नहीं शाप दिया उन्हें मैं क्यों शाप दूं? और जिन्हें यहोवा ने धमकी नहीं दी उन्हें मैं कैसे धमकी दूं?"));
hinsuperobject.biblemap.put("b4_23_9", new hinsuperobject(4,23,9,"चट्टानों की चोटी पर से वे मुझे दिखाई पड़ते हैं, पहाड़ियों पर से मैं उनको देखता हूं; वह ऐसी जाति है जो अकेली बसी रहेगी, और अन्यजातियों से अलग गिनी जाएगी!"));
hinsuperobject.biblemap.put("b4_23_10", new hinsuperobject(4,23,10,"याकूब के धूलि के किनके को कौन गिन सकता है, वा इस्त्राएल की चौथाई की गिनती कौन ले सकता है? सौभाग्य यदि मेरी मृत्यु धर्मियों की सी, और मेरा अन्त भी उन्हीं के समान हो!"));
hinsuperobject.biblemap.put("b4_23_11", new hinsuperobject(4,23,11,"तब बालाक ने बिलाम से कहा, तू ने मुझ से क्या किया है?"));
hinsuperobject.biblemap.put("b4_23_12", new hinsuperobject(4,23,12,"उस ने कहा, जो बात यहोवा ने मुझे सिखलाई क्या मुझे उसी को सावधानी से बोलना न चाहिये?"));
hinsuperobject.biblemap.put("b4_23_13", new hinsuperobject(4,23,13,"बालाक ने उस से कहा, मेरे संग दूसरे स्थान पर चल, जहां से वे तुझे दिखाई देंगे; तू उन सभों को तो नहीं, केवल बाहरवालों को देख सकेगा; वहां से उन्हें मेरे लिये शाप दे।"));
hinsuperobject.biblemap.put("b4_23_14", new hinsuperobject(4,23,14,"तब वह उसको सोपीम नाम मैदान में पिसगा के सिरे पर ले गया, और वहां सात वेदियां बनवाकर प्रत्येक पर एक बछड़ा और एक मेढ़ा चढ़ाया।"));
hinsuperobject.biblemap.put("b4_23_15", new hinsuperobject(4,23,15,"तब बिलाम ने बालाक से कहा, अपने होमबलि के पास यहीं खड़ा रह, और मैं उधर जाकर यहोवा से भेंट करूं।"));
hinsuperobject.biblemap.put("b4_23_16", new hinsuperobject(4,23,16,"और यहोवा ने बिलाम से भेंट की, और उस ने उसके मुंह में एक बात डाली, और कहा, कि बालाक के पास लौट जा, और यों कहना।"));
hinsuperobject.biblemap.put("b4_23_17", new hinsuperobject(4,23,17,"और वह उसके पास गया, और क्या देखता है, कि वह मोआबी हाकिमों समेत अपने होमबलि के पास खड़ा है। और बालाक ने पूछा, कि यहोवा ने क्या कहा है?"));
hinsuperobject.biblemap.put("b4_23_18", new hinsuperobject(4,23,18,"तब बिलाम ने अपनी गूढ़ बात आरम्भ की, और कहने लगा, हे बालाक, मन लगाकर सुन, हे सिप्पोर के पुत्रा, मेरी बात पर कान लगा:"));
hinsuperobject.biblemap.put("b4_23_19", new hinsuperobject(4,23,19,"ईश्वर मनुष्य नहीं, कि झूठ बोले, और न वह आदमी है, कि अपनी इच्छा बदले। क्या जो कुछ उस ने कहा उसे न करे? क्या वह वचन देकर उस पूरा न करे?"));
hinsuperobject.biblemap.put("b4_23_20", new hinsuperobject(4,23,20,"देख, आशीर्वाद ही देने की आज्ञा मैं ने पाई है: वह आशीष दे चुका है, और मैं उसे नहीं पलट सकता।"));
hinsuperobject.biblemap.put("b4_23_21", new hinsuperobject(4,23,21,"उस ने याकूब में अनर्थ नहीं पाया; और न इस्त्राएल में अन्याय देखा है। उसका परमेश्वर यहोवा उसके संग है, और उन में राजा की सी ललकार होती है।"));
hinsuperobject.biblemap.put("b4_23_22", new hinsuperobject(4,23,22,"उनको मि  में से ईश्वर ही निकाले लिये आ रहा है, वह तो बैनेले सांड के समान बल रखता है।"));
hinsuperobject.biblemap.put("b4_23_23", new hinsuperobject(4,23,23,"निश्चय कोई मंत्रा याकूब पर नहीं चल सकता, और इस्त्राएल पर भावी कहना कोई अर्थ नहीं रखता; परन्तु याकूब और इस्त्राएल के विषय अब यह कहा जाएगा, कि ईश्वर ने क्या ही विचित्रा काम किया है!"));
hinsuperobject.biblemap.put("b4_23_24", new hinsuperobject(4,23,24,"सुन, वह दल सिंहनी की नाई उठेगा, और सिंह की नाई खड़ा होगा; वह जब तक अहेर को न खा ले, और मरे हुओं के लोहू को न पी ले, तब तक न लेटेगा।।"));
hinsuperobject.biblemap.put("b4_23_25", new hinsuperobject(4,23,25,"तब बालाक ने बिलाम से कहा, उनको न तो शाप देना, और न आशीष देना।"));
hinsuperobject.biblemap.put("b4_23_26", new hinsuperobject(4,23,26,"बिलाम ने बालाक से कहा, क्या मैं ने तुझ से नहीं कहा, कि जो कुछ यहोवा मुझ से कहेगा, वही मुझे करना पड़ेगा?"));
hinsuperobject.biblemap.put("b4_23_27", new hinsuperobject(4,23,27,"बालाक ने बिलाम से कहा चल, मैं तुझ को एक और स्थान पर ले चलता हूं; सम्भव है कि परमेश्वर की इच्छा हो कि तू वहां से उन्हें मेरे लिये शाप दे।"));
hinsuperobject.biblemap.put("b4_23_28", new hinsuperobject(4,23,28,"तब बालाक बिलाम को पोर के सिरे पर, जहां से यशीमोन देश दिखाई देता है, ले गया।"));
hinsuperobject.biblemap.put("b4_23_29", new hinsuperobject(4,23,29,"और बिलाम ने बालाक से कहा, यहां पर मेरे लिये सात वेदियां बनवा, और यहां सात बछड़े और सात मेढ़े तैयार कर।"));
hinsuperobject.biblemap.put("b4_23_30", new hinsuperobject(4,23,30,"बिलाम के कहने के अनुसार बालाक ने प्रत्येक वेदी पर एक बछड़ा और एक मेढ़ा चढ़ाया।।"));
hinsuperobject.biblemap.put("b4_24_1", new hinsuperobject(4,24,1,"यह देखकर, कि यहोवा इस्त्राएल को आशीष ही दिलाना चाहता है, बिलाम पहिले की नाई शकुन देखने को न गया, परन्तु अपना मुंह जंगल की ओर कर लिया।"));
hinsuperobject.biblemap.put("b4_24_2", new hinsuperobject(4,24,2,"और बिलाम ने आंखे उठाई, और इस्त्राएलियों को अपने गोत्रा गोत्रा के अनुसार बसे हुए देखा। और परमेश्वर का आत्मा उस पर उतरा।"));
hinsuperobject.biblemap.put("b4_24_3", new hinsuperobject(4,24,3,"तब उसने अपनी गूढ़ बात आरम्भ की, और कहने लगा, कि बोर के पुत्रा बिलाम की यह वाणी है, जिस पुरूष की आंखें बन्द थीं उसी की यह वाणी है,"));
hinsuperobject.biblemap.put("b4_24_4", new hinsuperobject(4,24,4,"ईश्वर के वचनों का सुननेवाला, जो दण्डवत में पड़ा हुआ खुली हुई आंखों से सर्वशक्तिमान का दर्शन पाता है, उसी की यह वाणी है: कि"));
hinsuperobject.biblemap.put("b4_24_5", new hinsuperobject(4,24,5,"हे याकूब, तेरे डेरे, और हे इस्त्राएल, तेरे निवासस्थान क्या ही मनभावने हैं!"));
hinsuperobject.biblemap.put("b4_24_6", new hinsuperobject(4,24,6,"वे तो नालों वा घाटियों की नाई, और नदी के तट की वाटिकाओं के समान ऐसे फैले हुए हैं, जैसे कि यहोवा के लगाए हुए अगर के वृक्ष, और जल के निकट के देवदारू।"));
hinsuperobject.biblemap.put("b4_24_7", new hinsuperobject(4,24,7,"और उसके डोलों से जल उमण्डा करेगा, और उसका बीच बहुतेरे जलभरे खेतों में पडेग़ा, और उसका राजा अगाग से भी महान होगा, और उसका राज्य बढ़ता ही जाएगा।"));
hinsuperobject.biblemap.put("b4_24_8", new hinsuperobject(4,24,8,"उसको मि  में से ईश्वर की निकाले लिये आ रहा है; वह तो बनैले सांड़ के सामान बल रखता है, जाति जाति के लोग जो उसके द्रोही है उनको वह खा जायेगा, और उनकी हडि्डयों को टुकडे  टुकडे  करेगा, और अपने तीरों से उनको बेधेगा।"));
hinsuperobject.biblemap.put("b4_24_9", new hinsuperobject(4,24,9,"वह दबका बैठा है, वह सिंह वा सिंहनी की नाई लेट गया है; अब उसको कौन छेड़े? जो कोई तुझे आशीर्वाद दे सो आशीष पाए, और जो कोई तुझे शाप दे वह स्रापित हो।।"));
hinsuperobject.biblemap.put("b4_24_10", new hinsuperobject(4,24,10,"तब बालाक का कोप बिलाम पर भड़क उठा; और उस ने हाथ पर हाथ पटककर बिलाम से कहा, मैं ने तुझे अपने शत्रुओं के शाप देने के लिये बुलवाया, परन्तु तू ने तीन बार उन्हें आशीर्वाद ही आशीर्वाद दिया है।"));
hinsuperobject.biblemap.put("b4_24_11", new hinsuperobject(4,24,11,"इसलिये अब तू अपने स्थान पर भाग जा; मैं ने तो सोचा था कि तेरी बड़ी प्रतिष्ठा करूंगा, परन्तु अब यहोवा ने तुझे प्रतिष्ठा पाने से रोक रखा है।"));
hinsuperobject.biblemap.put("b4_24_12", new hinsuperobject(4,24,12,"बिलाम ने बालाक से कहा, जो दूत तू ने मेरे पास भेजे थे, क्या मैं ने उन से भी न कहा था,"));
hinsuperobject.biblemap.put("b4_24_13", new hinsuperobject(4,24,13,"कि चाहे बालाक अपने घर को सोने चांदी से भरकर मुझे दे, तौभी मैं यहोवा की आज्ञा तोड़कर अपने मन से न तो भला कर सकता हूं और न बुरा; जो कुछ यहोवा कहेगा वही मैं कहूंगा?"));
hinsuperobject.biblemap.put("b4_24_14", new hinsuperobject(4,24,14,"अब सुन, मैं अपने लोगों के पास लौट कर जाता हूं; परन्तु पहिले मैं तुझे चिता देता हूं कि अन्त के दिनों में वे लोग तेरी प्रजा से क्या क्या करेंगे।"));
hinsuperobject.biblemap.put("b4_24_15", new hinsuperobject(4,24,15,"फिर वह अपनी गूढ़ बात आरम्भ करके कहने लगा, कि बोर के पुत्रा बिलाम की यह वाणी है, जिस पुरूष की आंखे बन्द थी उसी की यह वाणी है,"));
hinsuperobject.biblemap.put("b4_24_16", new hinsuperobject(4,24,16,"ईश्वर के वचनों का सुननेवाला, और परमप्रधान के ज्ञान का जाननेवाला, जो दण्डवत् में पड़ा हुआ खुली हुई आंखों से सर्वशक्तिमान का दर्शन पाता है, उसी की यह वाणी है: कि"));
hinsuperobject.biblemap.put("b4_24_17", new hinsuperobject(4,24,17,"मै उसको देखूंगा तो सही, परन्तु अभी नहीं; मैं उसको निहारूंगा तो सही, परन्तु समीप होके नहीं: याकूब में से एक तारा उदय होगा, और इस्त्राएल में से एक राज दण्ड उठेगा; जो मोआब की अलंगों को चूर कर देगा, जो सब दंगा करनेवालों को गिरा देगा।"));
hinsuperobject.biblemap.put("b4_24_18", new hinsuperobject(4,24,18,"तब एदोम और सेईर भी, जो उसके शत्रु हैं, दोनों उसके वश में पड़ेंगे, और इस्त्राएल वीरता दिखाता जाएगा।"));
hinsuperobject.biblemap.put("b4_24_19", new hinsuperobject(4,24,19,"और याकूब ही में से एक अधिपति आवेगा जो प्रभुता करेगा, और नगर में से बचे हुओं को भी सत्यानाश करेगा।।"));
hinsuperobject.biblemap.put("b4_24_20", new hinsuperobject(4,24,20,"फिर उस ने अमालेक पर दृष्टि करके अपनी गूढ़ बात आरम्भ की, और कहने लगा, अमालेक अन्यजातियों में श्रेष्ठ तो था, परन्तु उसका अन्त विनाश ही है।।"));
hinsuperobject.biblemap.put("b4_24_21", new hinsuperobject(4,24,21,"फिर उस ने केनियों पर दृष्टि करके अपनी गूढ़ बात आरम्भ की, और कहने लगा, तेरा निवासस्थान अति दृढ़ तो है, और तेरा बसेरा चट्टान पर तो है;"));
hinsuperobject.biblemap.put("b4_24_22", new hinsuperobject(4,24,22,"तौभी केन उजड़ जाएगा। और अन्त में अश्शूर् तुझे बन्धुआई में ले आएगा।।"));
hinsuperobject.biblemap.put("b4_24_23", new hinsuperobject(4,24,23,"फिर उस ने अपनी गूढ़ बात आरम्भ की, और कहने लगा, हाय जब ईश्वर यह करेगा तब कौन जीवित बचेगा?"));
hinsuperobject.biblemap.put("b4_24_24", new hinsuperobject(4,24,24,"तौभी कित्तियों के पास से जहाजवाले आकर अश्शूर् को और एबेर को भी दु:ख देंगे; और अन्त में उसका भी विनाश हो जाएगा।।"));
hinsuperobject.biblemap.put("b4_24_25", new hinsuperobject(4,24,25,"तब बिलाम चल दिया, और अपने स्थान पर लौट गया; और बालाक ने भी अपना मार्ग लिया।।"));
hinsuperobject.biblemap.put("b4_25_1", new hinsuperobject(4,25,1,"इस्त्राएली शित्तीम में रहते थे, और लोग मोआबी लड़कियों के संग कुकर्म करने लगे।"));
hinsuperobject.biblemap.put("b4_25_2", new hinsuperobject(4,25,2,"और जब उन स्त्रीयों ने उन लोगों को अपने देवताओं के यज्ञों में नेवता दिया, तब वे लोग खाकर उनके देवताओं को दण्डवत् करने लगे।"));
hinsuperobject.biblemap.put("b4_25_3", new hinsuperobject(4,25,3,"यों इस्त्राएली बालपोर देवता को पूजने लगे। तब यहोवा का कोप इस्त्राएल पर भड़क उठा;"));
hinsuperobject.biblemap.put("b4_25_4", new hinsuperobject(4,25,4,"और यहोवा ने मूसा से कहा, प्रजा के सब प्रधानों को पकड़कर यहोवा के लिये धूप में लटका दे, जिस से मेरा भड़का हुआ कोप इस्त्राएल के ऊपर से दूर हो जाए।"));
hinsuperobject.biblemap.put("b4_25_5", new hinsuperobject(4,25,5,"तब मूसा ने इस्त्राएली न्यायियों से कहा, तुम्हारे जो जो आदमी बालपोर के संग मिल गए हैं उन्हें घात करो।।"));
hinsuperobject.biblemap.put("b4_25_6", new hinsuperobject(4,25,6,"और जब इस्त्राएलियों की सारी मण्डली मिलापवाले तम्बू के द्वार पर रो रही थी, तो एक इस्त्राएली पुरूष मूसा और सब लोगों की आंखों के सामने एक मिद्यानी स्त्री को अपने साथ अपने भाइयों के पास ले आया।"));
hinsuperobject.biblemap.put("b4_25_7", new hinsuperobject(4,25,7,"इसे देखकर एलीआजर का पुत्रा पीनहास, जो हारून याजक का पोता था, उस ने मण्डली में से उठकर हाथ में एक बरछी ली,"));
hinsuperobject.biblemap.put("b4_25_8", new hinsuperobject(4,25,8,"और उस इस्त्राएली पुरूष के डेरे में जाने के बाद वह भी भीतर गया, और उस पुरूष और उस स्त्री दोनों के पेट में बरछी बेध दी। इस पर इस्त्राएलियों में जो मरी फैल गई थी वह थम गई।"));
hinsuperobject.biblemap.put("b4_25_9", new hinsuperobject(4,25,9,"और मरी से चौबीस हजार मनुष्य मर गए।।"));
hinsuperobject.biblemap.put("b4_25_10", new hinsuperobject(4,25,10,"तब यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_25_11", new hinsuperobject(4,25,11,"हारून याजक का पोता एलीआजर का पुत्रा पीनहास, जिसे इस्त्राएलियों के बीच मेरी सी जलन उठी, उस ने मेरी जलजलाहट को उन पर से यहां तक दूर किया है, कि मैं ने जलकर उनका अन्त नहीं कर डाला।"));
hinsuperobject.biblemap.put("b4_25_12", new hinsuperobject(4,25,12,"इसलिये तू कह दे, कि मैं उस से शांति की वाचा बान्धता हूं;"));
hinsuperobject.biblemap.put("b4_25_13", new hinsuperobject(4,25,13,"और वह उसके लिये, और उसके बाद उसके वंश के लिये, सदा के याजकपद की वाचा होगी, क्योंकि उसे अपने परमेश्वर के लिये जलन उठी, और उस ने इस्त्राएलियों के लिये प्रायश्चित्त किया।"));
hinsuperobject.biblemap.put("b4_25_14", new hinsuperobject(4,25,14,"जो इस्त्राएली पुरूष मिद्यानी स्त्री के संग मारा गया, उसका नाम जिम्री था, वह साल का पुत्रा और शिमोनियों में से अपने पितरों के घराने का प्रधान था।"));
hinsuperobject.biblemap.put("b4_25_15", new hinsuperobject(4,25,15,"और जो मिद्यानी स्त्री मारी गई उसका नाम कोजबी था, वह सूर की बेटी थी, जो मिद्यानी पितरों के एक घराने के लोगों का प्रधान था।।"));
hinsuperobject.biblemap.put("b4_25_16", new hinsuperobject(4,25,16,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_25_17", new hinsuperobject(4,25,17,"मिद्यानियों को सता, और उन्हें मार;"));
hinsuperobject.biblemap.put("b4_25_18", new hinsuperobject(4,25,18,"क्योंकि पोर के विषय और कोजबी के विषय वे तुम को छल करके सताते हैं। कोजबी तो एक मिद्यानी प्रधान की बेटी और मिद्यानियों की जाति बहिन थी, और मरी के दिन में पोर के मामले में मारी गई।।"));
hinsuperobject.biblemap.put("b4_26_1", new hinsuperobject(4,26,1,"फिर यहोवा ने मूसा और एलीआजर नाम हारून याजक के पुत्रा से कहा,"));
hinsuperobject.biblemap.put("b4_26_2", new hinsuperobject(4,26,2,"इस्त्राएलियों की सारी मण्डली में जितने बीस वर्ष के, वा उस से अधिक अवस्था के होने से इस्त्राएलियों के बीच युद्ध करने के योग्य हैं, उनके पितरों के घरानों के अनुसार उन सभों की गिनती करो।"));
hinsuperobject.biblemap.put("b4_26_3", new hinsuperobject(4,26,3,"सो मूसा और एलीआजर याजक ने यरीहो के पास यरदन नदी के तीर पर मोआब के अराबा में उन से समझाके कहा,"));
hinsuperobject.biblemap.put("b4_26_4", new hinsuperobject(4,26,4,"बीस वर्ष के और उस से अधिक अवस्था के लोगों की गिनती लो, जैसे कि यहोवा ने मूसा और इस्त्राएलियों को मि  देश से निकले आने के समय आज्ञा दी थी।।"));
hinsuperobject.biblemap.put("b4_26_5", new hinsuperobject(4,26,5,"रूबेन जो इस्त्राएल का जेठा था; उसके ये पुत्रा थे; अर्थात् हनोक, जिस से हनोकियों का कुल चला; और पल्लू, जिस से पल्लूइयों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_6", new hinsuperobject(4,26,6,"हेद्दॊन, जिस से हेद्दॊनियों का कुल चला; और कर्मी, जिस से कर्मियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_7", new hinsuperobject(4,26,7,"रूबेनवाले कुल ये ही थे; और इन में से जो गिने गए वे तैतालीस हजार सात सौ तीस पुरूष थे।"));
hinsuperobject.biblemap.put("b4_26_8", new hinsuperobject(4,26,8,"और पल्लू का पुत्रा एलीआब था।"));
hinsuperobject.biblemap.put("b4_26_9", new hinsuperobject(4,26,9,"और पल्लू का पुत्रा नमूएल, दातान, और अबीराम थे। ये वही दातान और अबीराम हैं जो सभासद थे; और जिस समय कोरह की मण्डली ने यहोवा से झगड़ा किया था, उस समय उस मण्डली में मिलकर वे भी मूसा और हारून से झगड़े थे;"));
hinsuperobject.biblemap.put("b4_26_10", new hinsuperobject(4,26,10,"और जब उन अढ़ाई सौ मनुष्यों के आग में भस्म हो जाने से वह मण्डली मिट गई, उसी समय पृथ्वी ने मुंह खोलकर कोरह समेत इनको भी निगल लिया; और वे एक दृष्टान्त ठहरे।"));
hinsuperobject.biblemap.put("b4_26_11", new hinsuperobject(4,26,11,"परन्तु कोरह के पुत्रा तो नहीं मरे थे।"));
hinsuperobject.biblemap.put("b4_26_12", new hinsuperobject(4,26,12,"शिमोन के पुत्रा जिन से उनके कुल निकले वे ये थे; अर्थात् नमूएल, जिस से नमूएलियों का कुल चला; और यामीन, जिस से यामीनियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_13", new hinsuperobject(4,26,13,"और जेरह, जिस से जेरहियों का कुल चला; और शाऊल, जिस से शाऊलियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_14", new hinsuperobject(4,26,14,"शिमोनवाले कुल ये ही थे; इन में से बाईस हजार दो सौ पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_15", new hinsuperobject(4,26,15,"और गाद के पुत्रा जिस से उनके कुल निकले वे ये थे; अर्थात् सपोन, जिस से सपोनियों का कुल चला; और हाग्गी, जिस से हाग्गियों का कुल चला; और शूनी, जिस से शूनियों का कुल चला; और ओजनी, जिस से ओजनियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_16", new hinsuperobject(4,26,16,"और एरी, जिस से एरियों का कुल चला; और अरोद, जिस से अरोदियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_17", new hinsuperobject(4,26,17,"और अरेली, जिस से अरेलियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_18", new hinsuperobject(4,26,18,"गाद के वंश के कुल ये ही थे; इन में से साढ़े चालीस हजार पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_19", new hinsuperobject(4,26,19,"और यहूदा के एक और ओनान नाम पुत्रा तो हुए, परन्तु वे कनान देश में मर गए।"));
hinsuperobject.biblemap.put("b4_26_20", new hinsuperobject(4,26,20,"और यहूदा के जिन पुत्रों से उनके कुल निकले वे ये थे; अर्थात् शेला, जिस से शेलियों का कुल चला; और पेरेस जिस से पेरेसियों का कुल चला; और जेरह, जिस से जेरहियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_21", new hinsuperobject(4,26,21,"और पेरेस के पुत्रा ये थे; अर्थात् हेद्दॊन, जिस से हेद्दॊनियों का कुल चला; और हामूल, जिस से हामूलियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_22", new hinsuperobject(4,26,22,"यहूदियों के कुल ये ही थे; इन में से साढ़े छिहत्तर हजार पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_23", new hinsuperobject(4,26,23,"और इस्साकार के पुत्रा जिन से उनके कुल निकले वे ये थे; अर्थात् तोला, जिस से तोलियों का कुल चला; और पुव्वा, जिस से पुव्वियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_24", new hinsuperobject(4,26,24,"और याशूब, जिस से याशूबियों का कुल चला; और शिम्रोन, जिस से शिम्रोनियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_25", new hinsuperobject(4,26,25,"इस्साकारियों के कुल ये ही थे; इन में से चौसठ हजार तीन सौ पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_26", new hinsuperobject(4,26,26,"और जबूलून के पुत्रा जिन से उनके कुल निकले वे ये थे; अर्थात् सेरेद जिस से सेरेदियों का कुल चला; और एलोन, जिस से एलोनियों का कुल चला; और यहलेल, जिस से यहलेलियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_27", new hinsuperobject(4,26,27,"जबूलूनियों के कुल ये ही थे; इन में से साढ़े साठ हजार पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_28", new hinsuperobject(4,26,28,"और यूसुफ के पुत्रा जिस से उनके कुल निकले वे मनश्शे और एप्रैम थे।"));
hinsuperobject.biblemap.put("b4_26_29", new hinsuperobject(4,26,29,"मनश्शे के पुत्रा ये थे; अर्थात् माकीर, जिस से माकीरियों का कुल चला; और माकीर से गिलाद उत्पन्न हुआ; और गिलाद से गिलादियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_30", new hinsuperobject(4,26,30,"गिलाद के तो पुत्रा ये थे; अर्थात् ईएजेर, जिस से ईएजेरियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_31", new hinsuperobject(4,26,31,"और हेलेक, जिस से हेलेकियों का कुल चला और अस्त्रीएल, जिस से अस्त्रीएलियों का कुल चला; और शेकेम, जिस से शेकेमियों का कुल चला; और शमीदा, जिस से शमीदियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_32", new hinsuperobject(4,26,32,"और हेपेर, जिस से हेपेरियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_33", new hinsuperobject(4,26,33,"और हेपेर के पुत्रा सलोफाद के बेटे नहीं, केवल बेटियां हुई; इन बेटियों के नाम महला, नोआ, होग्ला, मिल्का, और तिर्सा हैं।"));
hinsuperobject.biblemap.put("b4_26_34", new hinsuperobject(4,26,34,"मनश्शेवाले कुल ये ही थे; और इन में से जो गिने गए वे बावन हजार सात सौ पुरूष थे।।"));
hinsuperobject.biblemap.put("b4_26_35", new hinsuperobject(4,26,35,"और एप्रैम के पुत्रा जिन से उनके कुल निकले वे ये थे; अर्थात् शूतेलह, जिस से शूतेलहियों का कुल चला; और बेकेर, जिस से बेकेरियों का कुल चला; और तहन जिस से तहनियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_36", new hinsuperobject(4,26,36,"और शूतेलह के यह पुत्रा हुआ; अर्थात् एरान, जिस से एरानियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_37", new hinsuperobject(4,26,37,"एप्रैमियों के कुल ये ही थे; इन में से साढ़े बत्तीस हजार पुरूष गिने गए। अपने कुलों के अनुसार यूसुफ के वंश के लोग ये ही थे।।"));
hinsuperobject.biblemap.put("b4_26_38", new hinsuperobject(4,26,38,"और बिन्यामीन के पुत्रा जिन से उनके कुल निकले वे ये थे; अर्थात् बेला जिस से लेवियों का कुल चला; और अशबेल, जिस से अशबेलियों का कुल चला; और अहीराम, जिस से अहीरामियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_39", new hinsuperobject(4,26,39,"और शपूपास, जिस से शपूपामियों का कुल चला; और हूपाम, जिस से हूपामियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_40", new hinsuperobject(4,26,40,"और बेला के पुत्रा अर्द और नामान थे; और अर्द से तो अर्दियों को कुल, और नामान से नामानियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_41", new hinsuperobject(4,26,41,"अपने कुलों के अनुसार बिन्यामीनी ये ही थे; और इन में से जो गिने गए वे पैंतालीस हजार छ: सौ पुरूष थे।।"));
hinsuperobject.biblemap.put("b4_26_42", new hinsuperobject(4,26,42,"और दान का पुत्रा जिस से उनका कुल निकला यह था; अर्थात् शूहाम, जिस से शूहामियों का कुल चला। और दान का कुल यही था।"));
hinsuperobject.biblemap.put("b4_26_43", new hinsuperobject(4,26,43,"और शूहामियों में से जो गिने गए उनके कुल में चौसठ हजार चार सौ पुरूष थे।।"));
hinsuperobject.biblemap.put("b4_26_44", new hinsuperobject(4,26,44,"और आशेर के पुत्रा जिस से उनके कुल निकले वे ये थे; अर्थात् यिम्ना, जिस से यिम्नियों का कुल चला; यिश्री, जिस से यिश्रियों का कुल चला; और बरीआ, जिस से बरीइयों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_45", new hinsuperobject(4,26,45,"फिर बरीआ के ये पुत्रा हुए; अर्थात् हेबेर, जिस से हेबेरियों का कुल चला; और मल्कीएल, जिस से मल्कीएलियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_46", new hinsuperobject(4,26,46,"और आशेर की बेटी का नाम सेरह है।"));
hinsuperobject.biblemap.put("b4_26_47", new hinsuperobject(4,26,47,"आशेरियों के कुल ये ही थे; इन में से तिर्पन हजार चार सौ पुरूष गिने गए।।"));
hinsuperobject.biblemap.put("b4_26_48", new hinsuperobject(4,26,48,"और नप्ताली के पुत्रा जिस से उनके कुल निकले वे ये थे; अर्थात् यहसेल, जिस से यहसेलियों का कुल चला; और गूनी, जिस से गूनियों का कुल चला;"));
hinsuperobject.biblemap.put("b4_26_49", new hinsuperobject(4,26,49,"येसेर, जिस से येसेरियों का कुल चला; और शिल्लेम, जिस से शिल्लेमियों का कुल चला।"));
hinsuperobject.biblemap.put("b4_26_50", new hinsuperobject(4,26,50,"अपने कुलों के अनुसार नप्ताली के कुल ये ही थे; और इन में से जो गिने गए वे पैंतालीस हजार चार सौ पुरूष थे।।"));
hinsuperobject.biblemap.put("b4_26_51", new hinsuperobject(4,26,51,"सब इस्त्राएलियों में से जो गिने गए थे वे ये ही थे; अर्थात् छ: लाख एक हजार सात सौ तीस पुरूष थे।।"));
hinsuperobject.biblemap.put("b4_26_52", new hinsuperobject(4,26,52,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_26_53", new hinsuperobject(4,26,53,"इनको, इनकी गिनती के अनुसार, वह भूमि इनका भाग होने के लिये बांट दी जाए।"));
hinsuperobject.biblemap.put("b4_26_54", new hinsuperobject(4,26,54,"अर्थात् जिस कुल से अधिक हों उनको अधिक भाग, और जिस में कम हों उनको कम भाग देना; प्रत्येक गोत्रा को उसका भाग उसके गिने हुए लोगों के अनुसार दिया जाए।"));
hinsuperobject.biblemap.put("b4_26_55", new hinsuperobject(4,26,55,"तौभी देश चिट्ठी डालकर बांटा जाए; इस्त्राएलियों के पितरों के एक एक गोत्रा का नाम, जैसे जैसे निकले वैसे वैसे वे अपना अपना भाग पाएं।"));
hinsuperobject.biblemap.put("b4_26_56", new hinsuperobject(4,26,56,"चाहे बहुतों का भाग हो चाहे थोड़ों का हो, जो जो भाग बांटे जाएं वह चिट्ठी डालकर बांटे जाए।।"));
hinsuperobject.biblemap.put("b4_26_57", new hinsuperobject(4,26,57,"फिर लेवियों में से जो अपने कुलों के अनुसार गिने गए वे ये हैं; अर्थात् गेर्शोनियों से निकला हुआ गेर्शोनियों का कुल; कहात से निकला हुआ कहातियों का कुल; और मरारी से निकला हुआ मरारियों का कुल।"));
hinsuperobject.biblemap.put("b4_26_58", new hinsuperobject(4,26,58,"लेवियों के कुल ये हैं; अर्थात् लिब्नियों का, हेब्रानियों का, महलियों का, मूशियों का, और कोरहियों का कुल। और कहात से अम्राम उत्पन्न हुआ।"));
hinsuperobject.biblemap.put("b4_26_59", new hinsuperobject(4,26,59,"और अम्राम की पत्नी का नाम योकेबेद है, वह लेवी के वंश की थी जो लेवी के वंश में मि  देश में उत्पन्न हुई थी; और वह अम्राम से हारून और मूसा और उनकी बहिन मरियम को भी जनी।"));
hinsuperobject.biblemap.put("b4_26_60", new hinsuperobject(4,26,60,"और हारून से नादाब, अबीहू, एलीआजर, और ईतामार उत्पन्न हुए।"));
hinsuperobject.biblemap.put("b4_26_61", new hinsuperobject(4,26,61,"नादाब और अबीहू तो उस समय मर गए थे, जब वे यहोवा के साम्हने ऊपरी आग ले गए थे।"));
hinsuperobject.biblemap.put("b4_26_62", new hinsuperobject(4,26,62,"सब लेवियों में से जो गिने गये, अर्थात् जितने पुरूष एक महीने के वा उस से अधिक अवस्था के थे, वे तेईस हजार थे; वे इस्त्राएलियों के बीच इसलिये नहीं गिने गए, क्योंकि उनको देश का कोई भाग नहीं दिया गया था।।"));
hinsuperobject.biblemap.put("b4_26_63", new hinsuperobject(4,26,63,"मूसा और एलीआजर याजक जिन्हों ने मोआब के अराबा में यरीहो के पास की यरदन नदी के तट पर इस्त्राएलियों को गिन लिया, उनके गिने हुए लोग इतने ही थे।"));
hinsuperobject.biblemap.put("b4_26_64", new hinsuperobject(4,26,64,"परन्तु जिन इस्त्राएलियों को मूसा और हारून याजक ने सीनै के जंगल में गिना था, उन में से एक पुरूष इस समय के गिने हुओं में न था।"));
hinsuperobject.biblemap.put("b4_26_65", new hinsuperobject(4,26,65,"क्योंकि यहोवा ने उनके विषय कहा था, कि वे निश्चय जंगल में मर जाएंगे, इसलिये यपुन्ने के पुत्रा कालेब और नून के पुत्रा यहोशू को छोड़, उन में से एक भी पुरूष नहीं बचा।।"));
hinsuperobject.biblemap.put("b4_27_1", new hinsuperobject(4,27,1,"तब यूसुफ के पुत्रा मनश्शे के वंश के कुलों में से सलोफाद, जो हेपेर का पुत्रा, और गिलाद का पोता, और मनश्शे के पुत्रा माकीर का परपोता था, उसकी बेटियां जिनके नाम महला, नोवा, होग्ला, मिलका, और तिर्सा हैं वे पास आईं।"));
hinsuperobject.biblemap.put("b4_27_2", new hinsuperobject(4,27,2,"और वे मूसा और एलीआजर याजक और प्रधानों और सारी मण्डली के साम्हने मिलापवाले तम्बू के द्वार पर खड़ी होकर कहने लगीं,"));
hinsuperobject.biblemap.put("b4_27_3", new hinsuperobject(4,27,3,"हमारा पिता जंगल में मर गया; परन्तु वह उस मण्डली में का न था जो कोरह की मण्डली के संग होकर यहोवा के विरूद्ध इकट्ठी हुई थी, वह अपने ही पाप के कारण मरा; और उसके कोई पुत्रा न था।"));
hinsuperobject.biblemap.put("b4_27_4", new hinsuperobject(4,27,4,"तो हमारे पिता का नाम उसके कुल में से पुत्रा न होने के कारण क्यों मिट जाए? हमारे चाचाओं के बीच हमें भी कुछ भूमि निज भाग करके दे।"));
hinsuperobject.biblemap.put("b4_27_5", new hinsuperobject(4,27,5,"उनकी यह बिनती मूसा ने यहोवा को सुनाई।"));
hinsuperobject.biblemap.put("b4_27_6", new hinsuperobject(4,27,6,"यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_27_7", new hinsuperobject(4,27,7,"सलोफाद की बेटियां ठीक कहती हैं; इसलिये तू उनके चाचाओं के बीच उनको भी अवश्य ही कुछ भूमि निज भाग करके दे, अर्थात् उनके पिता का भाग उनके हाथ सौंप दे।"));
hinsuperobject.biblemap.put("b4_27_8", new hinsuperobject(4,27,8,"और इस्त्राएलियों से यह कह, कि यदि कोई मनुष्य निपुत्रा मर जाए, तो उसका भाग उसकी बेटी के हाथ सौंपना।"));
hinsuperobject.biblemap.put("b4_27_9", new hinsuperobject(4,27,9,"और यदि उसके कोई बेटी भी न हो, तो उसका भाग उसके भाइयों को देना।"));
hinsuperobject.biblemap.put("b4_27_10", new hinsuperobject(4,27,10,"और यदि उसके भाई भी न हों, तो उसका भाग चाचाओं को देना।"));
hinsuperobject.biblemap.put("b4_27_11", new hinsuperobject(4,27,11,"और यदि उसके चाचा भी न हों, तो उसके कुल में से उसका जो कुटुम्बी सब से समीप हो उसको उसका भाग देना, कि वह उसका अधिकारी हो। इस्त्राएलियों के लिये यह न्याय की विधि ठहरेगी, जैसे कि यहोवा ने मूसा को आज्ञा दी।।"));
hinsuperobject.biblemap.put("b4_27_12", new hinsuperobject(4,27,12,"फिर यहोवा ने मूसा से कहा, इस अबारीम नाम पर्वत के ऊपर चढ़के उस देश को देख ले जिसे मैं ने इस्त्राएलियों को दिया है।"));
hinsuperobject.biblemap.put("b4_27_13", new hinsuperobject(4,27,13,"और जब तू उसको देख लेगा, तब अपने भाई हारून की नाई तू भी अपने लोगों मे जा मिलेगा,"));
hinsuperobject.biblemap.put("b4_27_14", new hinsuperobject(4,27,14,"क्योंकि सीन नाम जंगल में तुम दोनों ने मण्डली के झगड़ने के समय मेरी आज्ञा को तोड़कर मुझ से बलवा किया, और मुझे सोते के पास उनकी दृष्टि में पवित्रा नहीं ठहराया। ( यह मरीबा नाम सोता है जो सीन नाम जंगल के कादेश में है )"));
hinsuperobject.biblemap.put("b4_27_15", new hinsuperobject(4,27,15,"मूसा ने यहोवा से कहा,"));
hinsuperobject.biblemap.put("b4_27_16", new hinsuperobject(4,27,16,"यहोवा, जो सारे प्राणियों की आत्माओं का परमेश्वर है, वह इस मण्डली के लोगों के ऊपर किसी पुरूष को नियुक्त कर दे,"));
hinsuperobject.biblemap.put("b4_27_17", new hinsuperobject(4,27,17,"जो उसके साम्हने आया जाया करे, और उनका निकालने और पैठानेवाला हो; जिस से यहोवा की मण्डली बिना चरवाहे की भेड़ बकरियों के समान न रहे।"));
hinsuperobject.biblemap.put("b4_27_18", new hinsuperobject(4,27,18,"यहोवा ने मूसा से कहा, तू नून के पुत्रा यहोशू को लेकर उस पर हाथ रख; वह तो ऐसा पुरूष है जिस में मेरा आत्मा बसा है;"));
hinsuperobject.biblemap.put("b4_27_19", new hinsuperobject(4,27,19,"और उसको एलीआजर याजक के और सारी मण्डली के साम्हने खड़ा करके उनके साम्हने उसे आज्ञा दे।"));
hinsuperobject.biblemap.put("b4_27_20", new hinsuperobject(4,27,20,"और अपनी महिमा में से कुछ उसे दे, जिस से इस्त्राएलियों की सारी मण्डली उसकी माना करे।"));
hinsuperobject.biblemap.put("b4_27_21", new hinsuperobject(4,27,21,"और वह एलीआजर याजक के साम्हने खड़ा हुआ करे, और एलीआजर उसके लिये यहोवा से ऊरीम की आज्ञा पूछा करे; और वह इस्त्राएलियों की सारी मण्डली समेत उसके कहने से जाया करे, और उसी के कहने से लौट भी आया करे।"));
hinsuperobject.biblemap.put("b4_27_22", new hinsuperobject(4,27,22,"यहोवा की इस आज्ञा के अनुसार मूसा ने यहोशू को लेकर, एलीआजर याजक और सारी मण्डली के साम्हने खड़ा करके,"));
hinsuperobject.biblemap.put("b4_27_23", new hinsuperobject(4,27,23,"उस पर हाथ रखे, और उसको आज्ञा दी जैसे कि यहोवा ने मूसा के द्वारा कहा था।।"));
hinsuperobject.biblemap.put("b4_28_1", new hinsuperobject(4,28,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_28_2", new hinsuperobject(4,28,2,"इस्त्राएलियों को यह आज्ञा सुना कि मेरा चढ़ावा, अर्थात् मुझे सुखदायक सुगन्ध देनेवाला मेरा हव्यरूपी भोजन, तुम लोग मेरे लिये उनके नियत समयों पर चढ़ाने के लिये स्मरण रखना।"));
hinsuperobject.biblemap.put("b4_28_3", new hinsuperobject(4,28,3,"और तू उन से कह, कि जो जो तुम्हें यहोवा के लिये चढ़ाना होगा वे ये हैं; अर्थात् नित्य होमबलि के लिये एक एक वर्ष के दो निर्दोष भेड़ी के बच्चे प्रतिदिन चढ़ाया करे।"));
hinsuperobject.biblemap.put("b4_28_4", new hinsuperobject(4,28,4,"एक बच्चे को भोर को और दूसरे को गोधूलि के समय चढ़ाना;"));
hinsuperobject.biblemap.put("b4_28_5", new hinsuperobject(4,28,5,"और भेड़ के बच्चे के पीछे एक चौथाई हीन कूटके निकाले हुए तेल से सने हुए एपा के दसवें अंश मैदे का अन्नबलि चढ़ाना।"));
hinsuperobject.biblemap.put("b4_28_6", new hinsuperobject(4,28,6,"यह नित्य होमबलि है, जो सीनै पर्वत पर यहोवा का सुखदायक सुगन्धवाला हव्य होने के लिये ठहराया गया।"));
hinsuperobject.biblemap.put("b4_28_7", new hinsuperobject(4,28,7,"और उसका अर्घ प्रति एक भेड़ के बच्चे के संग एक चौथाई हीन हो; मदिरा का यह अर्घ यहोवा के लिये पवित्रास्थान में देना।"));
hinsuperobject.biblemap.put("b4_28_8", new hinsuperobject(4,28,8,"और दूसरे बच्चे को गोधूलि के समय चढ़ाना; अन्नबलि और अर्घ समेत भोर के होमबलि की नाई उसे यहोवा को सुखदायक सुगन्ध देनेवाला हव्य करके चढ़ाना।।"));
hinsuperobject.biblemap.put("b4_28_9", new hinsuperobject(4,28,9,"फिर विश्रामदिन को दो निर्दोष भेड़ के एक साल के नर बच्चे, और अन्नबलि के लिये तेल से सना हुआ एपा का दो दसवां अंश मैदा अर्घ समेत चढ़ाना।"));
hinsuperobject.biblemap.put("b4_28_10", new hinsuperobject(4,28,10,"नित्य होमबलि और उसके अर्घ के अलावा प्रत्येक विश्रामदिन का यही होमबलि ठहरा है।।"));
hinsuperobject.biblemap.put("b4_28_11", new hinsuperobject(4,28,11,"फिर अपने महीनों के आरम्भ में प्रतिमास यहोवा के लिये होमबलि चढ़ाना; अर्थात् दो बछड़े, एक मेढ़ा, और एक एक वर्ष के निर्दोष भेड़ के सात बच्चे;"));
hinsuperobject.biblemap.put("b4_28_12", new hinsuperobject(4,28,12,"और बछड़े पीछे तेल से सना हुआ एपा का तीन दसवां अंश मैदा, और उस एक मेढ़े के साथ तेल से सना हुआ एपा का दो दसवां अंश मैदा;"));
hinsuperobject.biblemap.put("b4_28_13", new hinsuperobject(4,28,13,"और प्रत्येक भेड़ के बच्चे के पीछे तेल से सना हुआ एपा का दसवां अंश मैदा, उन सभों को अन्नबलि करके चढ़ाना; वह सुखदायक सुगन्ध देने के लिये होमबलि और यहोवा के लिये हव्य ठहरेगा।"));
hinsuperobject.biblemap.put("b4_28_14", new hinsuperobject(4,28,14,"और उनके साथ ये अर्घ हों; अर्थात् बछड़े पीछे आध हीन, मेढ़े के साथ तिहाई हीन, और भेड़ के बच्चे पीछे चौथाई हीन दाखमधु दिया जाए; वर्ष के सब महीनों में से प्रति एक महीने का यही होमबलि ठहरे।"));
hinsuperobject.biblemap.put("b4_28_15", new hinsuperobject(4,28,15,"और एक बकरा पापबलि करके यहोवा के लिये चढ़ाया जाए; यह नित्य होमबलि और उसके अर्घ के अलावा चढ़ाया जाए।।"));
hinsuperobject.biblemap.put("b4_28_16", new hinsuperobject(4,28,16,"फिर पहिले महीने के चौदहवें दिन को यहोवा का फसह हुआ करे।"));
hinsuperobject.biblemap.put("b4_28_17", new hinsuperobject(4,28,17,"और उसी महीने के पन्द्रहवें दिन को पर्ब्ब लगा करे; सात दिन तक अखमीरी रोटी खाई जाए।"));
hinsuperobject.biblemap.put("b4_28_18", new hinsuperobject(4,28,18,"पहिले दिन पवित्रा सभा हो; और उस दिन परिश्रम का कोई काम न किया जाए;"));
hinsuperobject.biblemap.put("b4_28_19", new hinsuperobject(4,28,19,"उस में तुम यहोवा के लिये हव्य, अर्थात् होमबलि चढ़ाना; सो दो बछड़े, एक मेढ़ा, और एक एक वर्ष के सात भेड़ के बच्चे हों; ये सब निर्दोष हों;"));
hinsuperobject.biblemap.put("b4_28_20", new hinsuperobject(4,28,20,"और उनका अन्नबलि तेल से सने हुए मैदे का हो; बछड़े पीछे एपा का तीन दसवां अंश और मेढ़े के सात एपा का दो दसवां अंश मैदा हो।"));
hinsuperobject.biblemap.put("b4_28_21", new hinsuperobject(4,28,21,"और सातों भेड़ के बच्चों में से प्रति एक बच्चे पीछे एपा का दसवां अंश चढ़ाना।"));
hinsuperobject.biblemap.put("b4_28_22", new hinsuperobject(4,28,22,"और एक बकरा भी पापबलि करके चढ़ाना, जिस से तुम्हारे लिये प्रायश्चित्त हो।"));
hinsuperobject.biblemap.put("b4_28_23", new hinsuperobject(4,28,23,"भोर का होमबलि जो नित्य होमबलि ठहरा है, उसके अलावा इनको चढ़ाना।"));
hinsuperobject.biblemap.put("b4_28_24", new hinsuperobject(4,28,24,"इस रीति से तुम उन सातों दिनों में भी हव्य का भोजन चढ़ाना, जो यहोवा को सुखदायक सुगन्ध देने के लिये हो; यह नित्य होमबलि और उसके अर्घ के अलावा चढ़ाया जाए।"));
hinsuperobject.biblemap.put("b4_28_25", new hinsuperobject(4,28,25,"और सातवें दिन भी तुम्हारी पवित्रा सभा हो; और उस दिन परिश्रम का कोई काम न करना।।"));
hinsuperobject.biblemap.put("b4_28_26", new hinsuperobject(4,28,26,"फिर पहिली उपज के दिन में, जब तुम अपने अठवारे नाम पर्ब्ब में यहोवा के लिये नया अन्नबलि चढ़ाओगे, तब भी तुम्हारी पवित्रा सभा हो; और परिश्रम का कोई काम न करना।"));
hinsuperobject.biblemap.put("b4_28_27", new hinsuperobject(4,28,27,"और एक होमबलि चढ़ाना, जिस से यहोवा के लिये सुखदायक सुगन्ध हो; अर्थात् दो बछड़े, एक मेढ़ा, और एक एक वर्ष के सात भेड़ के बच्चे;"));
hinsuperobject.biblemap.put("b4_28_28", new hinsuperobject(4,28,28,"और उनका अन्नबलि तेल से सने हुए मैदे का हो; अर्थात् बछड़े पीछे एपा का तीन दसवां अंश, और मेढ़े के संग एपा का दो दसवां अंश,"));
hinsuperobject.biblemap.put("b4_28_29", new hinsuperobject(4,28,29,"और सातों भेड़ के बच्चों में से एक एक बच्चे के पीछे एपा का दसवां अंश मैदा चढ़ाना।"));
hinsuperobject.biblemap.put("b4_28_30", new hinsuperobject(4,28,30,"और एक बकरा भी चढ़ाना, जिस से तुम्हारे लिये प्रायश्चित्त हो।"));
hinsuperobject.biblemap.put("b4_28_31", new hinsuperobject(4,28,31,"ये सब निर्दोष हों; और नित्य होमबलि और उसके अन्नबलि और अर्घ के अलावा इसको भी चढ़ाना।।"));
hinsuperobject.biblemap.put("b4_29_1", new hinsuperobject(4,29,1,"फिर सातवें महीने के पहिले दिन को तुम्हारी पवित्रा सभा हो; उस में परिश्रम का कोई काम न करना। वह तुम्हारे लिये जयजयकार का नरसिंगा फूंकने का दिन ठहरा है;"));
hinsuperobject.biblemap.put("b4_29_2", new hinsuperobject(4,29,2,"तुम होमबलि चढ़ाना, जिस से यहोवा के लिये सुखदायक सुगन्ध हो; अर्थात् बछड़ा, एक मेढ़ा, और एक एक वर्ष के सात निर्दोष भेड़ के बच्चे;"));
hinsuperobject.biblemap.put("b4_29_3", new hinsuperobject(4,29,3,"और उनका अन्नबलि तेल से सने हुए मैदे का हो; अर्थात् बछड़े के साथ एपा का दो दसवां अंश,"));
hinsuperobject.biblemap.put("b4_29_4", new hinsuperobject(4,29,4,"और सातों भेड़ के बच्चों में से एक एक बच्चे पीछे एपा का दसवां अंश मैदा चढ़ाना।"));
hinsuperobject.biblemap.put("b4_29_5", new hinsuperobject(4,29,5,"और एक बकरा भी पापबलि करके चढ़ाना, जिस से तुम्हारे लिये प्रायश्चित्त हो।"));
hinsuperobject.biblemap.put("b4_29_6", new hinsuperobject(4,29,6,"इन सभों से अधिक नए चांद का होमबलि और उसका अन्नबलि, और नित्य होमबलि और उसका अन्नबलि, और उन सभों के अर्घ भी उनके नियम के अनुसार सुखदायक सुगन्ध देने के लिये यहोवा के हव्य करके चढ़ाना।।"));
