hinsuperobject.biblemap.put("b4_31_1", new hinsuperobject(4,31,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_31_2", new hinsuperobject(4,31,2,"मिद्यानियों से इस्त्राएलियों का पलटा ले; बाद को तू अपने लोगों में जा मिलेगा।"));
hinsuperobject.biblemap.put("b4_31_3", new hinsuperobject(4,31,3,"तब मूसा ने लोगों से कहा, अपने में से पुरूषों को युद्ध के लिये हथियार बन्धाओ, कि वे मिद्यानियों पर चढ़के उन से यहोवा का पलटा ले।"));
hinsuperobject.biblemap.put("b4_31_4", new hinsuperobject(4,31,4,"इस्त्राएल के सब गोत्रों में से प्रत्येक गोत्रा के एक एक हजार पुरूषों को युद्ध करने के लिये भेजो।"));
hinsuperobject.biblemap.put("b4_31_5", new hinsuperobject(4,31,5,"तब इस्त्राएल के सब गोत्रों में से प्रत्येक गोत्रा के एक एक हजार पुरूष चुने गये, अर्थात् युद्ध के लिये हथियार- बन्द बारह हजार पुरूष।"));
hinsuperobject.biblemap.put("b4_31_6", new hinsuperobject(4,31,6,"प्रत्येक गोत्रा में से उन हजार हजार पुरूषों को, और एलीआजर याजक के पुत्रा पीनहास को, मूसा ने युद्ध करने के लिये भेजा, और उसके हाथ में पवित्रास्थान के पात्रा और वे तुरहियां थीं जो सांस बान्ध बान्ध कर फूंकी जाती थीं।"));
hinsuperobject.biblemap.put("b4_31_7", new hinsuperobject(4,31,7,"और जो आज्ञा यहोवा ने मूसा को दी थी, उसके अनुसार उन्हों ने मिद्यानियों से युद्ध करके सब पुरूषों को घात किया।"));
hinsuperobject.biblemap.put("b4_31_8", new hinsuperobject(4,31,8,"और दूसरे जूझे हुओं को छोड़ उन्हों ने एवी, रेकेम, सूर, हूर, और रेबा नाम मिद्यान के पांचों राजाओं को घात किया; और बोर के पुत्रा बिलाम को भी उन्हों ने तलवार से घात किया।"));
hinsuperobject.biblemap.put("b4_31_9", new hinsuperobject(4,31,9,"और इस्त्राएलियों ने मिद्यानी स्त्रियों को बालबच्चों समेत बन्धुआई में कर लिया; और उनके गाय- बैल, भेड़- बकरी, और उनकी सारी सम्पत्ति को लूट लिया।"));
hinsuperobject.biblemap.put("b4_31_10", new hinsuperobject(4,31,10,"और उनके निवास के सब नगरों, और सब छावनियों को फूंक दिया;"));
hinsuperobject.biblemap.put("b4_31_11", new hinsuperobject(4,31,11,"तब वे, क्या मनुष्य क्या पशु, सब बन्धुओं और सारी लूट- पाट को लेकर"));
hinsuperobject.biblemap.put("b4_31_12", new hinsuperobject(4,31,12,"यरीहो के पास की यरदन नदी के तीर पर, मोआब के अराबा में, छावनी के निकट, मूसा और एलीआजर याजक और इस्त्राएलियों की मण्डली के पास आए।।"));
hinsuperobject.biblemap.put("b4_31_13", new hinsuperobject(4,31,13,"तब मूसा और एलीआजर याजक और मण्डली के सब प्रधान छावनी के बाहर उनका स्वागत करने को निकले।"));
hinsuperobject.biblemap.put("b4_31_14", new hinsuperobject(4,31,14,"और मूसा सहस्त्रापति- शतपति आदि, सेनापतियों से, जो युद्ध करके लौटे आते थे क्रोधित होकर कहने लगा,"));
hinsuperobject.biblemap.put("b4_31_15", new hinsuperobject(4,31,15,"क्या तुम ने सब स्त्रियों को जीवित छोड़ दिया?"));
hinsuperobject.biblemap.put("b4_31_16", new hinsuperobject(4,31,16,"देखे, बिलाम की सम्मति से, पोर के विषय में इस्त्राएलियों से यहोवा का विश्वासघात इन्हीं ने कराया, और यहोवा की मण्डली में मरी फैली।"));
hinsuperobject.biblemap.put("b4_31_17", new hinsuperobject(4,31,17,"सो अब बालबच्चों में से हर एक लड़के को, और जितनी स्त्रियों ने पुरूष का मुंह देखा हो उन सभों को घात करो।"));
hinsuperobject.biblemap.put("b4_31_18", new hinsuperobject(4,31,18,"परन्तु जितनी लड़कियों ने पुरूष का मुंह न देखा हो उन सभों को तुम अपने लिये जीवित रखो।"));
hinsuperobject.biblemap.put("b4_31_19", new hinsuperobject(4,31,19,"और तुम लोग सात दिन तक छावनी के बाहर रहो, और तुम में से जितनों ने किसी प्राणी को घात किया, और जितनों ने किसी मरे हुए को छूआ हो, वे सब अपने अपने बन्धुओं समेत तीसरे और सातवें दिनों में अपने अपने को पाप छुड़ाकर पावन करें।"));
hinsuperobject.biblemap.put("b4_31_20", new hinsuperobject(4,31,20,"और सब वस्त्रों, और चमड़े की बनी हुई सब वस्तुओं, और बकरी के बालों की और लकड़ी की बनी हुई सब वस्तुओं को पावन कर लो।"));
hinsuperobject.biblemap.put("b4_31_21", new hinsuperobject(4,31,21,"तब एलीआजर याजक ने सेना के उन पुरूषों से जो युद्ध करने गए थे कहा, व्यवस्था की जिस विधि की आज्ञा यहोवा ने मूसा को दी है वह यह है,"));
hinsuperobject.biblemap.put("b4_31_22", new hinsuperobject(4,31,22,"कि सोना, चांदी, पीतल, लोहा, रांगा, और सीसा,"));
hinsuperobject.biblemap.put("b4_31_23", new hinsuperobject(4,31,23,"जो कुछ आग में ठहर सके उसको आग में डालो, तब वह शुद्ध ठहरेगा; तौभी वह अशुद्धता छुड़ानेवाले जल के द्वारा पावन किया जाए; परन्तु जो कुछ आग में न ठहर सके उसे जल में डुबाओ।"));
hinsuperobject.biblemap.put("b4_31_24", new hinsuperobject(4,31,24,"और सातवें दिन अपने वस्त्रों को धोना, तब तुम शुद्ध ठहरोगे; और तब छावनी में आना।।"));
hinsuperobject.biblemap.put("b4_31_25", new hinsuperobject(4,31,25,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_31_26", new hinsuperobject(4,31,26,"एलीआजर याजक और मण्डली के पितरों के घरानों के मुख्य मुख्य पुरूषों को साथ लेकर तू लूट के मनुष्यों और पशुओं की गिनती कर;"));
hinsuperobject.biblemap.put("b4_31_27", new hinsuperobject(4,31,27,"तब उनको आधा आधा करके एक भाग उन सिपाहियों को जो युद्ध करने को गए थे, और दूसरा भाग मण्डली को दे।"));
hinsuperobject.biblemap.put("b4_31_28", new hinsuperobject(4,31,28,"फिर जो सिपाही युद्ध करने को गए थे, उनके आधे में से यहोवा के लिये, क्या मनुष्य, क्या गाय- बैल, क्या गदहे, क्या भेड़- बकरियां"));
hinsuperobject.biblemap.put("b4_31_29", new hinsuperobject(4,31,29,"पांच सौ के पीछे एक को मानकर ले ले; और यहोवा की भेंट करके एलीआजर याजक को दे दे।"));
hinsuperobject.biblemap.put("b4_31_30", new hinsuperobject(4,31,30,"फिर इस्त्राएलियों के आधे में से, क्या मनुष्य, क्या गाय- बैल, क्या गदहे, क्या भेड़- बकरियां, क्या किसी प्रकार का पशु हो, पचास के पीछे एक लेकर यहोवा के निवास की रखवाली करनेवाले लेवियों को दे।"));
hinsuperobject.biblemap.put("b4_31_31", new hinsuperobject(4,31,31,"यहोवा की इस आज्ञा के अनुसार जो उस ने मूसा को दी मूसा और एलीआजर याजक ने किया।"));
hinsuperobject.biblemap.put("b4_31_32", new hinsuperobject(4,31,32,"और जो वस्तुएं सेना के पुरूषों ने अपने अपने लिये लूट ली थीं उन से अधिक की लूट यह थी; अर्थात् छ: लाख पचहत्तर हजार भेड़- बकरियां,"));
hinsuperobject.biblemap.put("b4_31_33", new hinsuperobject(4,31,33,"बहत्तर हजार गाय बैल,"));
hinsuperobject.biblemap.put("b4_31_34", new hinsuperobject(4,31,34,"इकसठ हजार गदहे,"));
hinsuperobject.biblemap.put("b4_31_35", new hinsuperobject(4,31,35,"और मनुष्यों में से जिन स्त्रियों ने पुरूष का मुंह नहीं देखा था वह सब बत्तीस हजार थीं।"));
hinsuperobject.biblemap.put("b4_31_36", new hinsuperobject(4,31,36,"और इसका आधा, अर्थात् उनका भाग जो युद्ध करने को गए थे, उस में भेड़बकरियां तीन लाख साढ़े सैंतीस हजार,"));
hinsuperobject.biblemap.put("b4_31_37", new hinsuperobject(4,31,37,"जिस में से पौने सात सौ भेड़- बकरियां यहोवा का कर ठहरीं।"));
hinsuperobject.biblemap.put("b4_31_38", new hinsuperobject(4,31,38,"और गाय- बैल छत्तीस हजार, जिन में से बहत्तर यहोवा का कर ठहरे।"));
hinsuperobject.biblemap.put("b4_31_39", new hinsuperobject(4,31,39,"और गदहे साढ़े तीस हजार, जिन में से इकसठ यहोवा का कर ठहरे।"));
hinsuperobject.biblemap.put("b4_31_40", new hinsuperobject(4,31,40,"और मनुष्य सोलह हजार जिन में से बत्तीस प्राणी यहोवा का कर ठहरे।"));
hinsuperobject.biblemap.put("b4_31_41", new hinsuperobject(4,31,41,"इस कर को जो यहोवा की भेंट थी मूसा ने यहोवा की आज्ञा के अनुसार एलीआजर याजक को दिया।"));
hinsuperobject.biblemap.put("b4_31_42", new hinsuperobject(4,31,42,"और इस्त्राएलियों की मण्डली का आधा"));
hinsuperobject.biblemap.put("b4_31_43", new hinsuperobject(4,31,43,"तीन लाख साढ़े सैंतिस हजार भेड़- बकरियां"));
hinsuperobject.biblemap.put("b4_31_44", new hinsuperobject(4,31,44,"छत्तीस हजार गाय- बैल,"));
hinsuperobject.biblemap.put("b4_31_45", new hinsuperobject(4,31,45,"साढ़े तीस हजार गदहे,"));
hinsuperobject.biblemap.put("b4_31_46", new hinsuperobject(4,31,46,"और सोलह हजार मनुष्य हुए।"));
hinsuperobject.biblemap.put("b4_31_47", new hinsuperobject(4,31,47,"इस आधे में से, जिसे मूसा ने युद्ध करनेवाले पुरूषों के पास से अलग किया था, यहोवा की आज्ञा के अनुसार मूसा ने, क्या मनुष्य क्या पशु, पचास पीछे एक लेकर यहोवा के निवास की रखवाली करनेवाले लेवियों को दिया।"));
hinsuperobject.biblemap.put("b4_31_48", new hinsuperobject(4,31,48,"तब सहस्त्रापति- शतपति आदि, जो सरदार सेना के हजारों के ऊपर नियुक्त थे, वे मूसा के पास आकर कहने लगे,"));
hinsuperobject.biblemap.put("b4_31_49", new hinsuperobject(4,31,49,"जो सिपाही हमारे अधीन थे उनकी तेरे दासों ने गिनती ली, और उन में से एक भी नहीं घटा।"));
hinsuperobject.biblemap.put("b4_31_50", new hinsuperobject(4,31,50,"इसलिये पायजेब, कड़े, मुंदरियां, बालियां, बाजूबन्द, सोने के जो गहने, जिस ने पाया है, उनको हम यहोवा के साम्हने अपने प्राणों के निमित्त प्रायश्चित्त करने को यहोवा की भेंट करके ले आए हैं।"));
hinsuperobject.biblemap.put("b4_31_51", new hinsuperobject(4,31,51,"तब मूसा और एलीआजर याजक ने उन से वे सब सोने के नक्काशीदार गहने ले लिए।"));
hinsuperobject.biblemap.put("b4_31_52", new hinsuperobject(4,31,52,"और सहस्त्रापतियों और शतपतियों ने जो भेंट का सोना यहोवा की भेंट करके दिया वह सब का सब सोलह हजार साढ़े सात सौ शेकेल का था।"));
hinsuperobject.biblemap.put("b4_31_53", new hinsuperobject(4,31,53,"( योद्धाओं ने तो अपने अपने लिये लूट ले ली थी। )"));
hinsuperobject.biblemap.put("b4_31_54", new hinsuperobject(4,31,54,"यह सोना मूसा और एलीआजर याजक ने सहस्त्रापतियों और शतपतियों से लेकर मिलापवाले तम्बू में पहुंचा दिया, कि इस्त्राएलियों के लिये यहोवा के साम्हने स्म्रण दिलानेवाली वस्तु ठहरे।।"));
hinsuperobject.biblemap.put("b4_32_1", new hinsuperobject(4,32,1,"रूबेनियों और गादियों के पास बहुत जानवर थे। जब उन्हों ने याजेर और गिलाद देशों को देखकर विचार किया, कि वह ढ़ोरों के योग्य देश है,"));
hinsuperobject.biblemap.put("b4_32_2", new hinsuperobject(4,32,2,"तब मूसा और एलीआजर याजक और मण्डली के प्रधानों के पास जाकर कहने लगे,"));
hinsuperobject.biblemap.put("b4_32_3", new hinsuperobject(4,32,3,"अतारोत, दीबोन, याजेर, निम्रा, हेशबोन, एलाले, सबाम, नबो, और बोन नगरों का देश"));
hinsuperobject.biblemap.put("b4_32_4", new hinsuperobject(4,32,4,"जिस पर यहोवा ने इस्त्राएल की मण्डली को विजय दिलवाई है, वह ढोरों के योग्य है; और तेरे दासों के पास ढोर हैं।"));
hinsuperobject.biblemap.put("b4_32_5", new hinsuperobject(4,32,5,"फिर उन्हों ने कहा, यदि तेरा अनुग्रह तेरे दासों पर हो, तो यह देश तेरे दासों को मिले कि उनकी निज भूमि हो; हमें यरदन पार न ले चल।"));
hinsuperobject.biblemap.put("b4_32_6", new hinsuperobject(4,32,6,"मूसा ने गादियों और रूबेनियों से कहा, जब तुम्हारे भाई युद्ध करने को जाएंगे तब क्या तुम यहां बैठे रहोगे?"));
hinsuperobject.biblemap.put("b4_32_7", new hinsuperobject(4,32,7,"और इस्त्राएलियों से भी उस पार के देश जाने के विषय जो यहोवा ने उन्हें दिया है तुम क्यों अस्वीकार करवाते हो?"));
hinsuperobject.biblemap.put("b4_32_8", new hinsuperobject(4,32,8,"जब मैं ने तुम्हारे बापदादों को कादेशबर्ने से कनान देश देखने के लिये भेजा, तब उन्हों ने भी ऐसा ही किया था।"));
hinsuperobject.biblemap.put("b4_32_9", new hinsuperobject(4,32,9,"अर्थात् जब उन्हों ने एशकोल नाम नाले तक पहुंचकर देश को देखा, तब इस्त्राएलियों से उस देश के विषय जो यहोवा ने उन्हें दिया था अस्वीकार करा दिया।"));
hinsuperobject.biblemap.put("b4_32_10", new hinsuperobject(4,32,10,"इसलिये उस समय यहोवा ने कोप करके यह शपथ खाई कि,"));
hinsuperobject.biblemap.put("b4_32_11", new hinsuperobject(4,32,11,"नि:सन्देह जो मनुष्य मि  से निकल आए हैं उन में से, जितने बीस वर्ष के वा उस से अधिक अवस्था के हैं, वे उस देश को देखने न पाएंगे, जिसके देने की शपथ मैं ने इब्राहीम, इसहाक, और याकूब से खाई है, क्योंकि वे मेरे पीछे पूरी रीति से नहीं हो लिये;"));
hinsuperobject.biblemap.put("b4_32_12", new hinsuperobject(4,32,12,"परन्तु यपुन्ने कनजी का पुत्रा कालेब, और नून का पुत्रा यहोशू, ये दोनों जो मेरे पीछे पूरी रीति से हो लिये हैं ये तो उसे देखने पाएंगे।"));
hinsuperobject.biblemap.put("b4_32_13", new hinsuperobject(4,32,13,"सो यहोवा का कोप इस्त्राएलियों पर भड़का, और जब तक उस पीढ़ी के सब लोगों का अन्त न हुआ, जिन्हों ने यहोवा के प्रति बुरा किया था, तब तक अर्थात् चालीस वर्ष तक वह जंगल में मारे मारे फिराता रहा।"));
hinsuperobject.biblemap.put("b4_32_14", new hinsuperobject(4,32,14,"और सुनो, तुम लोग उन पापियों के बच्चे होकर इसी लिये अपने बाप- दादों के स्थान पर प्रकट हुए हो, कि इस्त्राएल के विरूद्ध यहोवा से भड़के हुए कोप को और भड़काओ!"));
hinsuperobject.biblemap.put("b4_32_15", new hinsuperobject(4,32,15,"यदि तुम उसके पीछे चलने से फिर जाओ, तो वह फिर हम सभों को जंगल में छोड़ देगा; इस प्रकार तुम इन सारे लोगों का नाश कराओगे।"));
hinsuperobject.biblemap.put("b4_32_16", new hinsuperobject(4,32,16,"तब उन्हों ने मूसा के और निकट आकर कहा, हम अपने ढ़ोरों के लिये यहीं भेड़शाले बनाएंगे, और अपने बालबच्चों के लिये यहीं नगर बसाएंगे,"));
hinsuperobject.biblemap.put("b4_32_17", new hinsuperobject(4,32,17,"परन्तु आप इस्त्राएलियों के आगे आगे हथियार बन्द तब तक चलेंगे, जब तक उनको उनके स्थान में न पहुंचा दे; परन्तु हमारे बालबच्चे इस देश के निवासियों के डर से गढ़वाले नगरों में रहेंगे।"));
hinsuperobject.biblemap.put("b4_32_18", new hinsuperobject(4,32,18,"परन्तु जब तक इस्त्राएली अपने अपने भाग के अधिकारी न हों तब तक हम अपने घरों को न लौटेंगे।"));
hinsuperobject.biblemap.put("b4_32_19", new hinsuperobject(4,32,19,"हम उनके साथ यरदन पार वा कहीं आगे अपना भाग न लेंगे, क्योंकि हमारा भाग यरदन के इसी पार पूरब की ओर मिला है।"));
hinsuperobject.biblemap.put("b4_32_20", new hinsuperobject(4,32,20,"तब मूसा ने उन से कहा, यदि तुम ऐसा करो, अर्थात् यदि तुम यहोवा के आगे आगे युद्ध करने को हथियार बान्धो।"));
hinsuperobject.biblemap.put("b4_32_21", new hinsuperobject(4,32,21,"और हर एक हथियार- बन्द यरदन के पार तब तक चले, जब तक यहोवा अपने आगे से अपने शत्रुओं को न निकाले"));
hinsuperobject.biblemap.put("b4_32_22", new hinsuperobject(4,32,22,"और देश यहोवा के वश में न आए; तो उसके पीछे तुम यहां लौटोगे, और यहोवा के और इस्त्राएल के विषय निर्दोष ठहरोगे; और यह देश यहोवा के प्रति तुम्हारी निज भूमि ठहरेगा।"));
hinsuperobject.biblemap.put("b4_32_23", new hinsuperobject(4,32,23,"और यदि तुम ऐसा न करो, तो यहोवा के विरूद्ध पापी ठहरोगे; और जान रखो कि तुम को तुम्हारा पाप लगेगा।"));
hinsuperobject.biblemap.put("b4_32_24", new hinsuperobject(4,32,24,"तुम अपने बालबच्चों के लिये नगर बसाओ, और अपनी भेड़- बकरियों के लिये भेड़शाले बनाओ; और जो तुम्हारे मुंह से निकला है वही करो।"));
hinsuperobject.biblemap.put("b4_32_25", new hinsuperobject(4,32,25,"तब गादियों और रूबेनियों ने मूसा से कहा, अपने प्रभु की आज्ञा के अनुसार तेरे दास करेंगे।"));
hinsuperobject.biblemap.put("b4_32_26", new hinsuperobject(4,32,26,"हमारे बालबच्चे, स्त्रियां, भेड़- बकरी आदि, सब पशु तो यहीं गिलाद के नगरों में रहेंगे;"));
hinsuperobject.biblemap.put("b4_32_27", new hinsuperobject(4,32,27,"परन्तु अपने प्रभु के कहे के अनुसार तेरे दास सब के सब युद्ध के लिये हथियार- बन्द यहोवा के आगे आगे लड़ने को पार जाएंगे।"));
hinsuperobject.biblemap.put("b4_32_28", new hinsuperobject(4,32,28,"तब मूसा ने उनके विषय में एलीआजर याजक, और नून के पुत्रा यहोशू, और इस्त्राएलियों के गोत्रों के पितरों के घरानों के मुख्य मुख्य पुरूषों को यह आज्ञा दी,"));
hinsuperobject.biblemap.put("b4_32_29", new hinsuperobject(4,32,29,"कि यदि सब गादी और रूबेनी पुरूष युद्ध के लिये हथियार- बन्द तुम्हारे संग यरदन पार जाएं, और देश तुम्हारे वश में आ जाए, तो गिलाद देश उनकी निज भूमि होने को उन्हें देना।"));
hinsuperobject.biblemap.put("b4_32_30", new hinsuperobject(4,32,30,"परन्तु यदि वे तुम्हारे संग हथियार- बन्द पार न जाएं, तो उनकी निज भूमि तुम्हारे बीच कनान देश में ठहरे।"));
hinsuperobject.biblemap.put("b4_32_31", new hinsuperobject(4,32,31,"तब गादी और रूबेनी बोल उठे, यहोवा ने जैसा तेरे दासों से कहलाया है वैसा ही हम करेंगे।"));
hinsuperobject.biblemap.put("b4_32_32", new hinsuperobject(4,32,32,"हम हथियार- बन्द यहोवा के आगे आगे उस पार कनान देश में जाएंगे, परन्तु हमारी निज भूमि यरदन के इसी पार रहे।।"));
hinsuperobject.biblemap.put("b4_32_33", new hinsuperobject(4,32,33,"तब मूसा ने गादियों और रूबेनियों को, और यूसुफ के पुत्रा मनश्शे के आधे गोत्रियों को एमोरियों के राजा सीहोन और बाशान के राजा ओग, दोनों के राज्यों का देश, नगरों, और उनके आसपास की भूमि समेत दे दिया।"));
hinsuperobject.biblemap.put("b4_32_34", new hinsuperobject(4,32,34,"तब गादियों ने दीबोन, अतारोत, अरोएर,"));
hinsuperobject.biblemap.put("b4_32_35", new hinsuperobject(4,32,35,"अत्राौत, शोपान, याजेर, योगबहा,"));
hinsuperobject.biblemap.put("b4_32_36", new hinsuperobject(4,32,36,"बेतनिम्रा, और बेथारान नाम नगरों को दृढ़ किया, और उन में भेड़- बकरियों के लिये भेड़शाले बनाए।"));
hinsuperobject.biblemap.put("b4_32_37", new hinsuperobject(4,32,37,"और रूबेनियों ने हेशबोन, एलाले, और किर्यातैम को,"));
hinsuperobject.biblemap.put("b4_32_38", new hinsuperobject(4,32,38,"फिर नबो और बालमोन के नाम बदलकर उनको, और सिबमा को दृढ़ किया; और उन्हों ने अपने दृढ़ किए हुए नगरों के और और नाम रखे।"));
hinsuperobject.biblemap.put("b4_32_39", new hinsuperobject(4,32,39,"और मनश्शे के पुत्रा माकीर के वंशवालों ने गिलाद देश में जाकर उसे ले लिया, और जो एमोरी उस में रहते थे उनको निकाल दिया।"));
hinsuperobject.biblemap.put("b4_32_40", new hinsuperobject(4,32,40,"तब मूसा ने मनश्शे के पुत्रा माकीर के वंश को गिलाद दे दिया, और वे उस में रहने लगे।"));
hinsuperobject.biblemap.put("b4_32_41", new hinsuperobject(4,32,41,"और मनश्शेई याईर ने जाकर गिलाद की कितनी बस्तियां ले लीं, और उनके नाम हव्वोत्याईर रखे।"));
hinsuperobject.biblemap.put("b4_32_42", new hinsuperobject(4,32,42,"और नोबह ने जाकर गांवों समेत कनात को ले लिया, और उसका नाम अपने नाम पर नोबह रखा।।"));
hinsuperobject.biblemap.put("b4_33_1", new hinsuperobject(4,33,1,"जब से इस्त्राएली मूसा और हारून की अगुवाई से दल बान्धकर मि  देश से निकले, तब से उनके ये पड़ाव हुए।"));
hinsuperobject.biblemap.put("b4_33_2", new hinsuperobject(4,33,2,"मूसा ने यहोवा से आज्ञा पाकर उनके कूच उनके पड़ावों के अनुसार लिख दिए; और वे ये हैं।"));
hinsuperobject.biblemap.put("b4_33_3", new hinsuperobject(4,33,3,"पहिले महीने के पन्द्रहवें दिन को उन्हों ने रामसेस से कूच किया; फसह के दूसरे दिन इस्त्राएली सब मिस्त्रियों के देखते बेखटके निकल गए,"));
hinsuperobject.biblemap.put("b4_33_4", new hinsuperobject(4,33,4,"जब कि मिद्दी अपने सब पहिलौठों को मिट्टी दे रहे थे जिन्हें यहोवा ने मारा था; और उस ने उनके देवताओं को भी दण्ड दिया था।"));
hinsuperobject.biblemap.put("b4_33_5", new hinsuperobject(4,33,5,"इस्त्राएलियों ने रामसेस से कूच करे सुक्कोत में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_6", new hinsuperobject(4,33,6,"और सुक्कोत से कूच करके एताम में, जो जंगल के छोर पर हैं, डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_7", new hinsuperobject(4,33,7,"और एताम से कूच करके वे पीहहीरोत को मुड़ गए, जो बालसपोन के साम्हने है; और मिगदोल के साम्हने डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_8", new hinsuperobject(4,33,8,"तब वे पीहहीरोत के साम्हने से कूच कर समुद्र के बीच होकर जंगल में गए, और एताम नाम जंगल में तीन दिन का मार्ग चलकर मारा में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_9", new hinsuperobject(4,33,9,"फिर मारा से कूच करके वे एलीम को गए, और एलीम में जल के बारह सोते और सत्तर खजूर के वृक्ष मिले, और उन्हों ने वहां डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_10", new hinsuperobject(4,33,10,"तब उन्हों ने एलीम से कूच करे लाल समुद्र के तीर पर डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_11", new hinsuperobject(4,33,11,"और लाल समुद्र से कूच करके सीन नाम जंगल में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_12", new hinsuperobject(4,33,12,"फिर सीन नाम जंगल से कूच करके उन्हों ने दोपका में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_13", new hinsuperobject(4,33,13,"और दोपका से कूच करके आलूश में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_14", new hinsuperobject(4,33,14,"और आलूश से कूच करके रपीदीम में डेरा किया, और वहां उन लोगों को पीने का पानी न मिला।"));
hinsuperobject.biblemap.put("b4_33_15", new hinsuperobject(4,33,15,"फिर उन्हों ने रपीदीम से कूच करके सीनै के जंगल में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_16", new hinsuperobject(4,33,16,"और सीनै के जंगल से कूच करके किब्रोथत्तावा में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_17", new hinsuperobject(4,33,17,"और किब्रोथत्तावा से कूच करे हसेरोत में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_18", new hinsuperobject(4,33,18,"और हसेरोत से कूच करके रित्मा में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_19", new hinsuperobject(4,33,19,"फिर उन्हों ने रित्मा से कूच करके रिम्मोनपेरेस में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_20", new hinsuperobject(4,33,20,"और रिम्मोनपेरेस से कूच करके लिब्ना में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_21", new hinsuperobject(4,33,21,"और लिब्ना से कूच करके रिस्सा में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_22", new hinsuperobject(4,33,22,"और रिस्सा से कूच करके कहेलाता में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_23", new hinsuperobject(4,33,23,"और कहेलाता से कूच करके शेपेर पर्वत के पास डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_24", new hinsuperobject(4,33,24,"फिर उन्हों ने शेपेर पर्वत से कूच करके हरादा में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_25", new hinsuperobject(4,33,25,"और हरादा से कूच करके मखेलोत में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_26", new hinsuperobject(4,33,26,"और मखेलोत से कूच करके तहत में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_27", new hinsuperobject(4,33,27,"और तहत से कूच करके तेरह में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_28", new hinsuperobject(4,33,28,"और तेरह से कूच करके मित्का में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_29", new hinsuperobject(4,33,29,"फिर मित्का से कूच करके उन्हों ने हशमोना में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_30", new hinsuperobject(4,33,30,"और हशमोना से कूच करके मोसेरोत मे डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_31", new hinsuperobject(4,33,31,"और मोसेरोत से कूच करके याकानियों के बीच डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_32", new hinsuperobject(4,33,32,"और याकानियों के बीच से कूच करके होर्हग्गिदगाद में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_33", new hinsuperobject(4,33,33,"और होर्हग्गिदगाद से कूच करके योतबाता में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_34", new hinsuperobject(4,33,34,"और योतबाता से कूच करके अब्रोना में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_35", new hinsuperobject(4,33,35,"और अब्रोना से कूच करके एस्योनगेबेर में डेरे खड़े किए।"));
hinsuperobject.biblemap.put("b4_33_36", new hinsuperobject(4,33,36,"और एस्योनगेबेर के कूच करके उन्हों ने सीन नाम जंगल के कादेश में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_37", new hinsuperobject(4,33,37,"फिर कादेश से कूच करके होर पर्वत के पास, जो एदोम देश के सिवाने पर है, डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_38", new hinsuperobject(4,33,38,"वहां इस्त्राएलियों के मि  देश से निकलने के चालीसवें वर्ष के पांचवें महीने के पहिले दिन को हारून याजक यहोवा की आज्ञा पाकर होर पर्वत पर चढ़ा, और वहां मर गया।"));
hinsuperobject.biblemap.put("b4_33_39", new hinsuperobject(4,33,39,"और जब हारून होर पर्वत पर मर गया तब वह एक सौ तेईस वर्ष का था।"));
hinsuperobject.biblemap.put("b4_33_40", new hinsuperobject(4,33,40,"और अरात का कनानी राजा, जो कनान देश के दक्खिन भाग में रहता था, उस ने इस्त्राएलियों के आने का समाचार पाया।"));
hinsuperobject.biblemap.put("b4_33_41", new hinsuperobject(4,33,41,"तब इस्त्राएलियों ने होर पर्वत से कूच करके सलमोना में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_42", new hinsuperobject(4,33,42,"और सलमोना से कूच करके पूनोन में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_43", new hinsuperobject(4,33,43,"और पूनोन से कूच करके ओबोस में डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_44", new hinsuperobject(4,33,44,"और ओबोस से कूच करके अबारीम नाम डीहों में जो मोआब के सिवाने पर हैं, डेरे डाले।"));
hinsuperobject.biblemap.put("b4_33_45", new hinsuperobject(4,33,45,"तब उन डीहों से कूच करके उन्हों ने दीबोनगाद में डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_46", new hinsuperobject(4,33,46,"और दीबोनगाद से कूच करके अल्मोनदिबलातैम से कूच करके उन्हों ने अबारीम नाम पहाड़ों मे नबो के साम्हने डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_47", new hinsuperobject(4,33,47,"और अल्मोनदिबलातैम से कूच करके उन्हों ने अबारीम नाम पहाड़ों में नबो के साम्हने डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_48", new hinsuperobject(4,33,48,"फिर अबारीम पहाड़ों से कूच करके मोआब के अराबा में, यरीहो के पास यरदन नदी के तट पर डेरा किया।"));
hinsuperobject.biblemap.put("b4_33_49", new hinsuperobject(4,33,49,"और वे मोआब के अराबा में वेत्यशीमोत से लेकर आबेलशित्तीम तक यरदन के तीर तीर डेरे डाले।।"));
hinsuperobject.biblemap.put("b4_33_50", new hinsuperobject(4,33,50,"फिर मोआब के अराबा में, यरीहो के पास की यरदन नदी के तट पर, यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_33_51", new hinsuperobject(4,33,51,"इस्त्राएलियों को समझाकर कह, जब तुम यरदन पार होकर कनान देश में पहुंचो"));
hinsuperobject.biblemap.put("b4_33_52", new hinsuperobject(4,33,52,"तब उस देश के निवासियों को उनके देश से निकाल देना; और उनके सब नक्काशे पत्थरों को और ढली हुई मूर्तियों को नाश करना, और उनके सब पूजा के ऊंचे स्थानों को ढा देना।"));
hinsuperobject.biblemap.put("b4_33_53", new hinsuperobject(4,33,53,"और उस देश को अपने अधिकार में लेकर उस में निवास करना, क्योंकि मैं ने वह देश तुम्हीं को दिया है कि तुम उसके अधिकारी हो।"));
hinsuperobject.biblemap.put("b4_33_54", new hinsuperobject(4,33,54,"और तुम उस देश को चिट्ठी डालकर अपने कुलों के अनुसार बांट लेना; अर्थात् जो कुल अधिकवाले हैं उन्हें अधिक, और जो थोड़ेवाले हैं उनको थोड़ा भाग देना; जिस कुल की चिट्ठी जिस स्थान के लिये निकले वही उसका भाग ठहरे; अपने पितरों के गोत्रों के अनुसार अपना अपना भाग लेना।"));
hinsuperobject.biblemap.put("b4_33_55", new hinsuperobject(4,33,55,"परन्तु यदि तुम उस देश के निवासियों को अपने आगे से न निकालोगे, तो उन में से जिनको तुम उस में रहने दोगे वे मानो तुम्हारी आंखों में कांटे और तुम्हारे पांजरों में कीलें ठहरेंगे, और वे उस देश में जहां तुम बसोगे तुम्हें संकट में डालेंगे।"));
hinsuperobject.biblemap.put("b4_33_56", new hinsuperobject(4,33,56,"और उन से जैसा बर्ताव करने की मनसा मैं ने की है वैसा ही तुम से करूंगा।"));
hinsuperobject.biblemap.put("b4_34_1", new hinsuperobject(4,34,1,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_34_2", new hinsuperobject(4,34,2,"इस्त्राएलियों को यह आज्ञा दे, कि जो देश तुम्हारा भाग होगा वह तो चारों ओर से सिवाने तक का कनान देश है, इसलिये जब तुम कनान देश मे पहुंचों,"));
hinsuperobject.biblemap.put("b4_34_3", new hinsuperobject(4,34,3,"तब तुम्हारा दक्खिनी प्रान्त सीन नाम जंगल से ले एदोम देश के किनारे किनारे होता हुआ चला जाए, और तुम्हारा दक्खिनी सिवाना खारे ताल के सिरे पर आरम्भ होकर पश्चिम की ओर चले;"));
hinsuperobject.biblemap.put("b4_34_4", new hinsuperobject(4,34,4,"वहां से तुम्हारा सिवाना अक्रब्बीम नाम चढ़ाई की दक्खिन की ओर पहुंचकर मुड़े, और सीन तक आए, और कादेशबर्ने की दक्खिन की ओर निकले, और हसरस्रार तक बढ़के अस्मोन तक पहुंचे;"));
hinsuperobject.biblemap.put("b4_34_5", new hinsuperobject(4,34,5,"फिर वह सिवाना अस्मोन से घूमकर मि  के नाले तक पहुंचे, और उसका अन्त समुद्र का तट ठहरे।"));
hinsuperobject.biblemap.put("b4_34_6", new hinsuperobject(4,34,6,"फिर पच्छिमी सिवाना महासमुद्र हो; तुम्हारा पच्छिमी सिवाना यही ठहरे।"));
hinsuperobject.biblemap.put("b4_34_7", new hinsuperobject(4,34,7,"और तुम्हारा उत्तरीय सिवाना यह हो, अर्थात् तुम महासमुद्र से ले होर पर्वत तक सिवाना बन्धाना;"));
hinsuperobject.biblemap.put("b4_34_8", new hinsuperobject(4,34,8,"और होर पर्वत से हामात की घाटी तक सिवाना बान्धना, और वह सदाद पर निकले;"));
hinsuperobject.biblemap.put("b4_34_9", new hinsuperobject(4,34,9,"फिर वह सिवाना जिप्रोन तक पहुंचे, और हसरेनान पर निकले; तुम्हारा उत्तरीय सिवाना यही ठहरे।"));
hinsuperobject.biblemap.put("b4_34_10", new hinsuperobject(4,34,10,"फिर अपना पूरबी सिवाना हसरेनान से शपाम तक बान्धना;"));
hinsuperobject.biblemap.put("b4_34_11", new hinsuperobject(4,34,11,"और वह सिवाना शपाम से रिबला तक, जो ऐन की पूर्व की ओर है, नीचे को उतरते उतरते किन्नेरेत नाम ताल के पूर्व से लग जाए;"));
hinsuperobject.biblemap.put("b4_34_12", new hinsuperobject(4,34,12,"और वह सिवाना यरदन तक उतरके खारे ताल के तट पर निकले। तुम्हारे देश के चारों सिवाने ये ही ठहरें।"));
hinsuperobject.biblemap.put("b4_34_13", new hinsuperobject(4,34,13,"तब मूसा ने इस्त्राएलियों से फिर कहा, जिस देश के तुम चिट्ठी डालकर अधिकारी होगे, और यहोवा ने उसे साढ़े नौ गोत्रा के लोगों को देने की आज्ञा दी है, वह यही है;"));
hinsuperobject.biblemap.put("b4_34_14", new hinsuperobject(4,34,14,"परन्तु रूबेनियों और गादियों के गोत्रा तो अपने अपने पितरों के कुलों के अनुसार अपना अपना भाग पा चुके हैं, और मनश्शे के आधे गोत्रा के लोग भी अपना भाग पा चुके हैं;"));
hinsuperobject.biblemap.put("b4_34_15", new hinsuperobject(4,34,15,"अर्थात् उन अढ़ाई गोत्रों के लोग यरीहो के पास की यरदन के पार पूर्व दिशा में, जहां सूर्योदय होता है, अपना अपना भाग पा चुके हैं।।"));
hinsuperobject.biblemap.put("b4_34_16", new hinsuperobject(4,34,16,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_34_17", new hinsuperobject(4,34,17,"कि जो पुरूष तुम लोगों के लिये उस देश को बांटेंगे उनके नाम ये हैं; अर्थात् एलीआजर याजक और नून का पुत्रा यहोशू।"));
hinsuperobject.biblemap.put("b4_34_18", new hinsuperobject(4,34,18,"और देश को बांटने के लिये एक एक गोत्रा का एक एक प्रधान ठहराना।"));
hinsuperobject.biblemap.put("b4_34_19", new hinsuperobject(4,34,19,"और इन पुरूषों के नाम ये हैं; अर्थात् यहूदागोत्री यपुन्ने का पुत्रा कालेब,"));
hinsuperobject.biblemap.put("b4_34_20", new hinsuperobject(4,34,20,"शिमोनगोत्री अम्मीहूद का पुत्रा शमुएल,"));
hinsuperobject.biblemap.put("b4_34_21", new hinsuperobject(4,34,21,"बिन्यामीनगोत्री किसलोन का पुत्रा एलीदाद,"));
hinsuperobject.biblemap.put("b4_34_22", new hinsuperobject(4,34,22,"दानियों के गोत्रा का प्रधान योग्ली का पुत्रा बुक्की,"));
hinsuperobject.biblemap.put("b4_34_23", new hinsuperobject(4,34,23,"यूसुफियों में से मनश्शेइयों के गोत्रा का प्रधान एपोद का पुत्रा हन्नीएल,"));
hinsuperobject.biblemap.put("b4_34_24", new hinsuperobject(4,34,24,"और एप्रैमियों के गोत्रा का प्रधान शिम्तान का पुत्रा कमूएल,"));
hinsuperobject.biblemap.put("b4_34_25", new hinsuperobject(4,34,25,"जबूलूनियों के गोत्रा का प्रधान पर्नाक का पुत्रा एलीसापान,"));
hinsuperobject.biblemap.put("b4_34_26", new hinsuperobject(4,34,26,"इस्साकारियों के गोत्रा का प्रधान अज्जान का पुत्रा पलतीएल,"));
hinsuperobject.biblemap.put("b4_34_27", new hinsuperobject(4,34,27,"आशेरियों के गोत्रा का प्रधान शलोमी का पुत्रा अहीहूद,"));
hinsuperobject.biblemap.put("b4_34_28", new hinsuperobject(4,34,28,"और नप्तालियों के गोत्रा का प्रधान अम्मीहूद का पुत्रा पदहेल।"));
hinsuperobject.biblemap.put("b4_34_29", new hinsuperobject(4,34,29,"जिन पुरूषों को यहोवा ने कनान देश को इस्त्राएलियों के लिये बांटने की आज्ञा दी वे ये ही हैं।।"));
hinsuperobject.biblemap.put("b4_35_1", new hinsuperobject(4,35,1,"फिर यहोवा ने, मोआब के अराबा में, यरीहो के पास की यरदन नदी के तट पर मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_35_2", new hinsuperobject(4,35,2,"इस्त्राएलियों को आज्ञा दे, कि तुम अपने अपने निज भाग की भूमि में से लेवियों को रहने के लिये नगर देना; और नगरों के चारों ओर की चराइयां भी उनको देना।"));
hinsuperobject.biblemap.put("b4_35_3", new hinsuperobject(4,35,3,"नगर तो उनके रहने के लिये, और चराइयां उनके गाय- बैल और भेड़- बकरी आदि, उनके सब पशुओं के लिये होंगी।"));
hinsuperobject.biblemap.put("b4_35_4", new hinsuperobject(4,35,4,"और नगरों की चराइयां, जिन्हें तुम लेवियों को दोगे, वह एक एक नगर की शहरपनाह से बाहर चारों ओर एक एक हजार हाथ तक की हों।"));
hinsuperobject.biblemap.put("b4_35_5", new hinsuperobject(4,35,5,"और नगर के बाहर पूर्व, दक्खिन, पच्छिम, और उत्तर अलंग, दो दो हजार हाथ इस रीति से नापना कि नगर बीचोंबीच हो; लेवियों के एक एक नगर की चराई इतनी ही भूमि की हो।"));
hinsuperobject.biblemap.put("b4_35_6", new hinsuperobject(4,35,6,"और जो नगर तुम लेवियों को दोगे उन में से छ: शरणनगर हों, जिन्हें तुम को खूनी के भागने के लिये ठहराना होगा, और उन से अधिक बयालीस नगर और भी देना।"));
hinsuperobject.biblemap.put("b4_35_7", new hinsuperobject(4,35,7,"जितने नगर तुम लेवियों को दोगे वे सब अड़तालीस हों, और उनके साथ चराइयां देना।"));
hinsuperobject.biblemap.put("b4_35_8", new hinsuperobject(4,35,8,"और जो नगर तुम इस्त्राएलियों की निज भूमि में से दो, वे जिनके बहुत नगर हों उन से थोड़े लेकर देना; सब अपने अपने नगरों में से लेवियों को अपने ही अपने भाग के अनुसार दें।।"));
hinsuperobject.biblemap.put("b4_35_9", new hinsuperobject(4,35,9,"फिर यहोवा ने मूसा से कहा,"));
hinsuperobject.biblemap.put("b4_35_10", new hinsuperobject(4,35,10,"इस्त्राएलियों से कह, कि जब तुम यरदन पार होकर कनान देश में पहुंचो,"));
hinsuperobject.biblemap.put("b4_35_11", new hinsuperobject(4,35,11,"तक ऐसे नगर ठहराना जो तुम्हारे लिये शरणनगर हों, कि जो कोई किसी को भूल से मारके खूनी ठहरा हो वह वहां भाग जाए।"));
hinsuperobject.biblemap.put("b4_35_12", new hinsuperobject(4,35,12,"वे नगर तुम्हारे निमित्त पलटा लेनेवाले से शरण लेने के काम आएंगे, कि जब तक खूनी न्याय के लिये मण्डली के साम्हने खड़ा न हो तब तक वह न मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_13", new hinsuperobject(4,35,13,"और शरण के जो नगर तुम दोगे वे छ: हों।"));
hinsuperobject.biblemap.put("b4_35_14", new hinsuperobject(4,35,14,"तीन नगर तो यरदन के इस पार, और तीन कनान देश में देना; शरणनगर इतने ही रहें।"));
hinsuperobject.biblemap.put("b4_35_15", new hinsuperobject(4,35,15,"ये छहों नगर इस्त्राएलियों के और उनके बीच रहनेवाले परदेशियों के लिये भी शरणस्थान ठहरें, कि जो कोई किसी को भूल से मार डाले वह वहीं भाग जाए।"));
hinsuperobject.biblemap.put("b4_35_16", new hinsuperobject(4,35,16,"परन्तु यदि कोई किसी को लोहे के किसी हथियार से ऐसा मारे कि वह मर जाए, तो वह खूनी ठहरेगा; और वह खूनी अवश्य मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_17", new hinsuperobject(4,35,17,"और यदि कोई ऐसा पत्थर हाथ में लेकर, जिस से कोई मर सकता है, किसी को मारे, और वह मर जाए, तो वह भी खूनी ठहरेगा; और वह खूनी अवश्य मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_18", new hinsuperobject(4,35,18,"वा कोई हाथ में ऐसी लकड़ी लेकर, जिस से कोई मर सकता है, किसी को मारे, और वह मर जाए, तो वह भी खूनी ठहरेगा; और वह खूनी अवश्य मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_19", new hinsuperobject(4,35,19,"लोहू का पलटा लेनेवाला आप की उस खूनी को मार डाले; जब भी वह मिले तब ही वह उसे मार डाले।"));
hinsuperobject.biblemap.put("b4_35_20", new hinsuperobject(4,35,20,"और यदि कोई किसी को बैर से ढकेल दे, वा घात लगाकर कुछ उस पर ऐसे फेंक दे कि वह मर जाए,"));
hinsuperobject.biblemap.put("b4_35_21", new hinsuperobject(4,35,21,"वा शत्रुता से उसको अपने हाथ से ऐसा मारे कि वह मर जाए, तो जिस ने मारा हो वह अवश्य मार डाला जाए; वह खूनी ठहरेगा; लोहू का पलटा लेनेवाला जब भी वह खूनी उसे मिल जाए तब ही उसको मार डाले।"));
hinsuperobject.biblemap.put("b4_35_22", new hinsuperobject(4,35,22,"परन्तु यदि कोई किसी को बिना सोचे, और बिना शत्रुता रखे ढकेल दे, वा बिना घात लगाए उस पर कुछ फेंक दे,"));
hinsuperobject.biblemap.put("b4_35_23", new hinsuperobject(4,35,23,"वा ऐसा कोई पत्थर लेकर, जिस से कोई मर सकता है, दूसरे को बिना देखे उस पर फेंक दे, और वह मर जाए, परन्तु वह न उसका शत्रु हो, और न उसकी हानि का खोजी रहा हो;"));
hinsuperobject.biblemap.put("b4_35_24", new hinsuperobject(4,35,24,"तो मण्डली मारनेवाले और लोहू का पलटा लेनेवाले के बीच इन नियमों के अनुसार न्याय करे;"));
hinsuperobject.biblemap.put("b4_35_25", new hinsuperobject(4,35,25,"और मण्डली उस खूनी को लोहू के पलटा लेनेवाले के हाथ से बचाकर उस शरणनगर में जहां वह पहिले भाग गया हो लौटा दे, और जब तक पवित्रा तेल से अभिषेक किया हुआ महायाजक न मर जाए तब तक वह वहीं रहे।"));
hinsuperobject.biblemap.put("b4_35_26", new hinsuperobject(4,35,26,"परन्तु यदि वह खूनी उस शरणस्थान के सिवाने से जिस में वह भाग गया हो बाहर निकलकर और कहीं जाए,"));
hinsuperobject.biblemap.put("b4_35_27", new hinsuperobject(4,35,27,"और लोहू का पलटा लेनेवाला उसको शरणस्थान के सिवाने के बाहर कहीं पाकर मार डाले, तो वह लोहू बहाने का दोषी न ठहरे।"));
hinsuperobject.biblemap.put("b4_35_28", new hinsuperobject(4,35,28,"क्योंकि खूनी को महायाजक की मृत्यु तक शरणस्थान में रहना चाहिये; और महायाजक के मरने के पश्चात् वह अपनी निज भूमि को लौट सकेगा।"));
hinsuperobject.biblemap.put("b4_35_29", new hinsuperobject(4,35,29,"तुम्हारी पीढ़ी पीढ़ी में तुम्हारे सब रहने के स्थानों में न्याय की यह विधि होगी।"));
hinsuperobject.biblemap.put("b4_35_30", new hinsuperobject(4,35,30,"और जो कोई किसी मनुष्य को मार डाले वह साक्षियों के कहने पर मार डाला जाए, परन्तु एक ही साक्षी की साक्षी से कोई न मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_31", new hinsuperobject(4,35,31,"और जो खूनी प्राणदण्ड के योग्य ठहरे उस से प्राणदण्ड के बदले में जुरमाना न लेना; वह अवश्य मार डाला जाए।"));
hinsuperobject.biblemap.put("b4_35_32", new hinsuperobject(4,35,32,"और जो किसी शरणस्थान में भागा हो उसके लिये भी इस मतलब से जुरमाना न लेना, कि वह याजक के मरने से पहिले फिर अपने देश मे रहने को लौटने पाएं।"));
hinsuperobject.biblemap.put("b4_35_33", new hinsuperobject(4,35,33,"इसलिये जिस देश में तुम रहोगे उसको अशुद्ध न करना; खून से तो देश अशुद्ध हो जाता है, और जिस देश में जब खून किया जाए तब केवल खूनी के लोहू बहाने ही से उस देश का प्रायश्चित्त हो सकता है।"));
hinsuperobject.biblemap.put("b4_35_34", new hinsuperobject(4,35,34,"जिस देश में तुम निवास करोगे उसके बीच मै रहूंगा, उसको अशुद्ध न करना; मैं यहोवा तो इस्त्राएलियों के बीच रहता हूं।।"));
hinsuperobject.biblemap.put("b4_36_1", new hinsuperobject(4,36,1,"फिर यूसुफियों के कुलों में से गिलाद, जो माकीर का पुत्रा और मनश्शे का पोता था, उसके वंश के कुल के पितरों के घरानों के मुख्य मुख्य पुरूष मूसा के समीप जाकर उस प्रधानों के साम्हने, जो इस्त्राएलियों के पितरों के घरानों के मुख्य पुरूष थे, कहने लगे,"));
