private void putmapbible41() {

hinsuperobject.biblemap.put("b41_1_1", new hinsuperobject(41,1,1,"परमेश्वर के पुत्रा यीशु मसीह के सुसमाचार का आरम्भ।"));
hinsuperobject.biblemap.put("b41_1_2", new hinsuperobject(41,1,2,"जैसे यशायाह भविष्यद्वकता की पुस्तक में लिखा है कि देख; मैं अपने दूत को तेरे आगे भेजता हूं, जो तेरे लिये मार्ग सुधारेगा।"));
hinsuperobject.biblemap.put("b41_1_3", new hinsuperobject(41,1,3,"जंगल में एक पुकारनेवाले का शब्द सुनाई दे रहा है कि प्रभु का मार्ग तैयार करो, और उस की सड़कें सीधी करो।"));
hinsuperobject.biblemap.put("b41_1_4", new hinsuperobject(41,1,4,"यूहन्ना आया, जो जंगल में बपतिस्मा देता, और पापों की क्षमा के लिये मनफिराव के बपतिस्मा का प्रचार करता था।"));
hinsuperobject.biblemap.put("b41_1_5", new hinsuperobject(41,1,5,"और सारे यहूदिया देश के, और यरूशलेम के सब रहनेवाले निकलकर उसके पास गए, और अपने पापों को मानकर यरदन नदी में उस से बपतिस्मा लिया।"));
hinsuperobject.biblemap.put("b41_1_6", new hinsuperobject(41,1,6,"यूहन्ना ऊंट के रोम का वस्त्रा पहिने और अपनी कमर में चमड़ें का पटुका बान्धे रहता था ओर टिडि्डयां और वन मधु खाया करता था।"));
hinsuperobject.biblemap.put("b41_1_7", new hinsuperobject(41,1,7,"और यह प्रचार करता था, कि मेरे बाद वह आने वाला है, जो मुझ से शक्तिमान है; मैं इस योग्य नहीं कि झुककर उसके जूतों का बन्ध खोलूं।"));
hinsuperobject.biblemap.put("b41_1_8", new hinsuperobject(41,1,8,"मैं ने तो तुम्हें पानी से बपतिस्मा दिया है पर वह तुम्हें पवित्रा आत्मा से बपतिस्मा देगा।।"));
hinsuperobject.biblemap.put("b41_1_9", new hinsuperobject(41,1,9,"उन दिनों में यीशु ने गलील के नासरत से आकर, यरदन में यूहन्ना से बपतिस्मा लिया।"));
hinsuperobject.biblemap.put("b41_1_10", new hinsuperobject(41,1,10,"और जब वह पानी से निकलकर ऊपर आया, तो तुरन्त उस ने आकाश को खुलते और आत्मा को कबूतर की नाईं अपने ऊपर उतरते देखा।"));
hinsuperobject.biblemap.put("b41_1_11", new hinsuperobject(41,1,11,"और यह आकाशवाणी हई, कि तू मेरा प्रिय पुत्रा है, तुझ से मैं प्रसन्न हूं।।"));
hinsuperobject.biblemap.put("b41_1_12", new hinsuperobject(41,1,12,"तब आत्मा ने तुरन्त उस को जंगल की ओर भेजा।"));
hinsuperobject.biblemap.put("b41_1_13", new hinsuperobject(41,1,13,"और जंगल में चालीस दिन तक शैतान ने उस की परीक्षा की; और वह वन पशुओं के साथ रहा; और स्वर्गदूत उन की सेवा करते रहे।।"));
hinsuperobject.biblemap.put("b41_1_14", new hinsuperobject(41,1,14,"यूहन्ना के पकड़वाए जाने के बाद यीशु ने गलील में आकर परमेश्वर के राज्य का सुसमाचार प्रचार किया।"));
hinsuperobject.biblemap.put("b41_1_15", new hinsuperobject(41,1,15,"और कहा, समय पूरा हुआ है, और परमेश्वर का राज्य निकट आ गया है; मन फिराओ और सुसमाचार पर विश्वास करो।।"));
hinsuperobject.biblemap.put("b41_1_16", new hinsuperobject(41,1,16,"गलील की झील के किनारे किनारे जाते हुए, उस ने शमौन और उसके भाई अन्द्रियास को झील में जाल डालते देखा; क्योंकि वे मछुवे थे।"));
hinsuperobject.biblemap.put("b41_1_17", new hinsuperobject(41,1,17,"और यीशु ने उन से कहा; मेरे पीछे चले आओ; मैं तुम को मनुष्यों के मछुवे बनाऊंगा।"));
hinsuperobject.biblemap.put("b41_1_18", new hinsuperobject(41,1,18,"वे तुरन्त जालों को छोड़कर उसके पीछे हो लिए।"));
hinsuperobject.biblemap.put("b41_1_19", new hinsuperobject(41,1,19,"और कुछ आगे बढ़कर, उस ने जब्दी के पुत्रा याकूब, और उसके भाई यहून्ना को, नाव पर जालों को सुधारते देखा।"));
hinsuperobject.biblemap.put("b41_1_20", new hinsuperobject(41,1,20,"उस ने तुरन्त उन्हें बुलाया; और वे अपने पिता जब्दी को मजदूरी के साथ नाव पर छोड़कर, उसके पीछे चले गए।।"));
hinsuperobject.biblemap.put("b41_1_21", new hinsuperobject(41,1,21,"और वे कफरनहूम में आए, और वह तुरन्त सब्त के दिन सभा के घर में जाकर उपदेश करने लगा।"));
hinsuperobject.biblemap.put("b41_1_22", new hinsuperobject(41,1,22,"और लोग उसके उपदेश से चकित हुए; क्योंकि वह उन्हें शास्त्रियों की नाईं नहीं, परन्तु अधिकारी की नाई उपदेश देता था।"));
hinsuperobject.biblemap.put("b41_1_23", new hinsuperobject(41,1,23,"और उसी समय, उन की सभा के घर में एक मनुष्य था, जिस में एक अशुद्ध आत्मा थी।"));
hinsuperobject.biblemap.put("b41_1_24", new hinsuperobject(41,1,24,"उस ने चिल्लाकर कहा, हे यीशु नासरी, हमें तुझ से क्या काम? क्या तू हमें नाश करने आया है? मैं तुझे जानता हूं, तू कौन है? परमेश्वर का पवित्रा जन!"));
hinsuperobject.biblemap.put("b41_1_25", new hinsuperobject(41,1,25,"यीशु ने उसे डांटकर कहा, चुप रह; और उस में से निकल जा।"));
hinsuperobject.biblemap.put("b41_1_26", new hinsuperobject(41,1,26,"तब अशुद्ध आत्मा उस को मरोड़कर, और बड़े शब्द से चिल्लाकर उस में से निकल गई।"));
hinsuperobject.biblemap.put("b41_1_27", new hinsuperobject(41,1,27,"इस पर सब लोग आश्चर्य करते हुए आपस में वाद- विवाद करने लगे कि यह क्या बात है? यह तो कोई नया उपदेश है! वह अधिकार के साथ अशुद्ध आत्माओं को भी आज्ञा देता है, और वे उस की आज्ञा मानती हैं।"));
hinsuperobject.biblemap.put("b41_1_28", new hinsuperobject(41,1,28,"सो उसका नाम तुरन्त गलील के आस पास के सारे देश में हर जगह फैल गया।।"));
hinsuperobject.biblemap.put("b41_1_29", new hinsuperobject(41,1,29,"और वह तुरन्त आराधनालय में से निकलकर, याकूब और यूहन्ना के साथ शमौन और अन्द्रियास के घर आया।"));
hinsuperobject.biblemap.put("b41_1_30", new hinsuperobject(41,1,30,"और शमौन की सास ज्वर से पीड़ित थी, और उन्हों ने तुरन्त उसके विषय में उस से कहा।"));
hinsuperobject.biblemap.put("b41_1_31", new hinsuperobject(41,1,31,"तब उस ने पास जाकर उसका हाथ पकड़ के उसे उठाया; और उसका ज्वर उस पर से उतर गया, और वह उन की सेवा- टहल करने लगी।।"));
hinsuperobject.biblemap.put("b41_1_32", new hinsuperobject(41,1,32,"सन्ध्या के समय जब सूर्य डूब गया तो लोग सब बीमारों को और उन्हें जिन में दुष्टात्मा भीं उसके पास लाए।"));
hinsuperobject.biblemap.put("b41_1_33", new hinsuperobject(41,1,33,"और सारा नगर द्वार पर इकट्ठा हुआ।"));
hinsuperobject.biblemap.put("b41_1_34", new hinsuperobject(41,1,34,"और उस ने बहुतों को जो नाना प्रकार की बीमारियों से दुखी थे, चंगा किया; और बहुत से दुष्टात्माओं को निकाला; और दुष्टात्माओं को बोलने न दिया, क्योंकि वे उसे पहचानती थीं।।"));
hinsuperobject.biblemap.put("b41_1_35", new hinsuperobject(41,1,35,"और भोर को दिन निकलने से बहुत पहिले, वह उठकर निकला, और एक जंगली स्थान में गया और वहां प्रार्थना करने लगा।"));
hinsuperobject.biblemap.put("b41_1_36", new hinsuperobject(41,1,36,"तब शमौन और उसके साथी उस की खोज में गए।"));
hinsuperobject.biblemap.put("b41_1_37", new hinsuperobject(41,1,37,"जब वह मिला, तो उस से कहा; कि सब लोग तुझे ढूंढ रहे हैं।"));
hinsuperobject.biblemap.put("b41_1_38", new hinsuperobject(41,1,38,"उस न उन से कहा, आओ; हम ओर कहीं आस पास की बस्तियों में जाएं, कि मैं वहां भी प्रचार करूं, क्योंकि मै। इसी लिये निकला हूं।"));
hinsuperobject.biblemap.put("b41_1_39", new hinsuperobject(41,1,39,"सो वह सारे गलील में उन की सभाओं में जा जाकर प्रचार करता और दुष्टात्माओं को निकालता रहा।।"));
hinsuperobject.biblemap.put("b41_1_40", new hinsuperobject(41,1,40,"और एक कोढ़ी ने उसके पास आकर, उस से बिनती की, और उसके साम्हने घुटने टेककर, उस से कहा; यदि तू चाहे तो मुझे शुद्ध कर सकता है।"));
hinsuperobject.biblemap.put("b41_1_41", new hinsuperobject(41,1,41,"उस ने उस पर तरस खाकर हाथ बढ़ाया, और उसे छूकर कहा; मैं चाहता हूं तू शुद्ध हो जा।"));
hinsuperobject.biblemap.put("b41_1_42", new hinsuperobject(41,1,42,"और तुरन्त उसका कोढ़ जाता रहा, और वह शुद्ध हो गया।"));
hinsuperobject.biblemap.put("b41_1_43", new hinsuperobject(41,1,43,"तब उस ने उसे चिताकर तुरन्त विदा किया।"));
hinsuperobject.biblemap.put("b41_1_44", new hinsuperobject(41,1,44,"और उस से कहा, देख, किसी से कुछ मत कहना, परन्तु जाकर अपने आप को याजक को दिखा, और अपने शुद्ध होने के विषय में जो कुछ मूसा ने ठहराया है उसे भेंट चढ़ा, कि उन पर गवाही हो।"));
hinsuperobject.biblemap.put("b41_1_45", new hinsuperobject(41,1,45,"परन्तु वह बाहर जाकर इस बात को बहुत प्रचार करने और यहां तक फैलाने लगा, कि यीशु फिर खुल्लमखुल्ला नगर में न जा सका, परन्तु बाहर जंगली स्थानों में रहा; औश्र चहुंओर से लागे उसके पास आते रहे।।"));
hinsuperobject.biblemap.put("b41_2_1", new hinsuperobject(41,2,1,"कई दिन के बाद वह फिर कफरनहूम में आया और सुना गया, कि वह घर में है।"));
hinsuperobject.biblemap.put("b41_2_2", new hinsuperobject(41,2,2,"फिर इतने लोग इकट्ठे हुए, कि द्वार के पास भी जगह नहीं मिली; और वह उन्हें वचन सुना रहा था।"));
hinsuperobject.biblemap.put("b41_2_3", new hinsuperobject(41,2,3,"और लोग एक झोले के मारे हुए को चार मनुष्यों से उठवाकर उसके पास ले आए।"));
hinsuperobject.biblemap.put("b41_2_4", new hinsuperobject(41,2,4,"परन्तु जब वे भीड़ के कारण उसके निकट न पंहुच सके, तो उन्हों ने उस छत को जिस के नीचे वह था, खोल दिया और जब उसे उधेड़ चुके, तो उस खाट को जिस पर झोले का मारा हुआ पड़ा था, लटका दिया।"));
hinsuperobject.biblemap.put("b41_2_5", new hinsuperobject(41,2,5,"यीशु न, उन का विश्वास देखकर, उस झोले के मारे हुए से कहा; हे पुत्रा, तेरे पाप क्षमा हुए।"));
hinsuperobject.biblemap.put("b41_2_6", new hinsuperobject(41,2,6,"तब कई एक शास्त्री जो वहां बैठे थे, अपने अपने मन में विचार करने लगे।"));
hinsuperobject.biblemap.put("b41_2_7", new hinsuperobject(41,2,7,"कि यह मनुष्य क्यों ऐसा कहता है? यह तो परमेश्वर की निन्दा करता है, परमेश्वर को छोड़ और कोन पाप क्षमा कर सकता है?"));
hinsuperobject.biblemap.put("b41_2_8", new hinsuperobject(41,2,8,"यीशु ने तुरन्त अपनी आत्मा में जान लिया, कि वे अपने अपने मन में ऐसा विचार कर रहे हैं, और उन से कहा, तुम अपने अपने मन में यह विचार क्यों कर रहे हो?"));
hinsuperobject.biblemap.put("b41_2_9", new hinsuperobject(41,2,9,"सहज क्या है? क्या झोले के मारे से यह कहता कि तेरे पाप क्षमा हुए, या यह कहना, कि उठ अपनी खाट उठा कर चल फिर?"));
hinsuperobject.biblemap.put("b41_2_10", new hinsuperobject(41,2,10,"परन्तु जिस से तुम जान लो कि मनुष्य के पुत्रा को पृथ्वी पर पाप क्षमा करने का भी अधिकार है (उस ने उस झोले के मारे हुए से कहा)।"));
hinsuperobject.biblemap.put("b41_2_11", new hinsuperobject(41,2,11,"मैं तुझ से कहता हूं; उठ, अपनी खाट उठाकर अपने घर चला जा।"));
hinsuperobject.biblemap.put("b41_2_12", new hinsuperobject(41,2,12,"और वह उठा, और तुरन्त खाट उठाकर और सब के साम्हने से निकलकर चला गया, इस पर सब चकित हुए, और परमेश्वर की बड़ाई करके कहने लगे, कि हम ने ऐसा कभी नहीं देखा।।"));
hinsuperobject.biblemap.put("b41_2_13", new hinsuperobject(41,2,13,"वह फिर निकलकर झील के किनारे गया, और सारी भीड़ उसके पास आई, और वह उन्हें उपदेश देने लगा।"));
hinsuperobject.biblemap.put("b41_2_14", new hinsuperobject(41,2,14,"जाते हुए उस ने हलफई के पुत्रा लेवी को चुंगी की चौकी पर बैठे देखा, और उस से कहा; मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b41_2_15", new hinsuperobject(41,2,15,"और वह उठकर, उसके पीछे हो लिया: और वह उसके घर में भोजन करने बैठे; क्योंकि वे बहुत से थे, और उसके पीछे हो लिये थे।"));
hinsuperobject.biblemap.put("b41_2_16", new hinsuperobject(41,2,16,"और शास्त्रियों और फरीसियों ने यह देखकर, कि वह तो पापियों और चुंगी लेनेवालों के साथ भोजन कर रहा है, उसक चेलों से कहा; वह तो चुंगी लेनेवालों और पापियों के साथ खाता पिता है!!"));
hinsuperobject.biblemap.put("b41_2_17", new hinsuperobject(41,2,17,"यीशु ने यह सुनकर, उन से कहा, भले चंगों को वैद्य की आवश्यकता नहीं, परन्तु बीमारों को है: मैं धर्मियों को नहीं, परन्तु पापियों को बुलाने आया हूं।।"));
hinsuperobject.biblemap.put("b41_2_18", new hinsuperobject(41,2,18,"यूहन्ना के चेले, और फरीसी उपवास करते थे; सो उन्हों ने आकर उस से यह कहा; कि यूहन्ना के चेले और फरीसियों के चेले क्यों उपवास रखते हैं? परन्तु तेरे चेले उपवास नहीं रखते।"));
hinsuperobject.biblemap.put("b41_2_19", new hinsuperobject(41,2,19,"यीशु ने उन से कहा, जब तक दुल्हा बरातियों के साथ दहता है क्या वे उपवास कर सकते हैं? सो जब तक दूल्हा उन के साथ है, तब तक वे उपवास नहीं कर सकते।"));
hinsuperobject.biblemap.put("b41_2_20", new hinsuperobject(41,2,20,"परन्तु वे दिन आएंगे, कि दूल्हा उन से अलग किया जाएगा; उस समय वे उपवास करेंगे।"));
hinsuperobject.biblemap.put("b41_2_21", new hinsuperobject(41,2,21,"कोरे कपड़े का पैबन्द पुराने पहिरावन पर कोई नहीं लगाता; नहीं तो वह पैबन्द उस में से कुछ खींच लेगा, अर्थात् नया, पुराने से, और वह और फट जाएगा।"));
hinsuperobject.biblemap.put("b41_2_22", new hinsuperobject(41,2,22,"नये दाखरस को पुरानी मशकों में कोई नहीं रखता, नहीं तो दाखरस मश्कों को फाड़ देगा, और दाखरस और मश्कें दोनों नष्ट हो जाएंगी; परन्तु दाख का नया रस नई मश्कों में भरा जाता है।।"));
hinsuperobject.biblemap.put("b41_2_23", new hinsuperobject(41,2,23,"और ऐसा हुआ कि वह सब्त के दिन खेतों में से होकर जा रहा था; और उसके चेले चलते हुए बालें तोड़ने लगे।"));
hinsuperobject.biblemap.put("b41_2_24", new hinsuperobject(41,2,24,"तब फरीसियों ने उस से कहा, देख; ये सब्त के दिन वह काम क्यों करते हैं जो उचित नहीं?"));
hinsuperobject.biblemap.put("b41_2_25", new hinsuperobject(41,2,25,"उस ने उन से कहा, क्या तुम ने कभी नहीं पढ़ा, कि जब दाऊद को आवश्यकता हुई और जब वह और उसके साथी भूखे हुए, तब उस ने क्या किया था?"));
hinsuperobject.biblemap.put("b41_2_26", new hinsuperobject(41,2,26,"उस ने क्योंकर अबियातार महायाजक के समय, परमेश्वर के भवन में जाकर, भेंट की रोटियां खाईं, जिसका खाना याजकों को छोड़ और किसी को भी उचित नहीं, और अपने साथियों को भी दीं?"));
hinsuperobject.biblemap.put("b41_2_27", new hinsuperobject(41,2,27,"और उस ने उन से कहा; सब्त का दिन मनुष्य के लिये बनाया गया है, न कि मनुष्य सब्त के दिन के लिये।"));
hinsuperobject.biblemap.put("b41_2_28", new hinsuperobject(41,2,28,"इसलिये मनुष्य का पुत्रा सब्त के दिन का भी स्वामी है।।"));
hinsuperobject.biblemap.put("b41_3_1", new hinsuperobject(41,3,1,"और वह आराधनालय में फिर गया; और वहां एक मनुष्य था, जस का हाथ सूख गया था।"));
hinsuperobject.biblemap.put("b41_3_2", new hinsuperobject(41,3,2,"और वे उस पर दोष लगाने के लिये उस की घात में लगे हुए थे, कि देखें, वह सब्त के दिन में उसे चंगा करता है कि नहीं।"));
hinsuperobject.biblemap.put("b41_3_3", new hinsuperobject(41,3,3,"उस ने सूखे हाथवाले मनुष्य से कहा; बीच में खड़ा हो।"));
hinsuperobject.biblemap.put("b41_3_4", new hinsuperobject(41,3,4,"और उन से कहा; क्या सब्त के दिन भला करना उचित है या बुरा करता, प्राण को बचाना या मारना? पर वे चुप रहे।"));
hinsuperobject.biblemap.put("b41_3_5", new hinsuperobject(41,3,5,"और उस ने उन के मन की कठोरता से उदास होकर, उन को क्रोध से चारों ओर देखा, और उस मनुष्य से कहा, अपना हाथ बढ़ा उस ने बढ़ाया, और उसका हाथ अच्छा हो गया।"));
hinsuperobject.biblemap.put("b41_3_6", new hinsuperobject(41,3,6,"तब फरीसी बाहर जाकर तुरन्त हेरोदियों के साथ उसके विरोध में सम्मति करने लगे, कि उसे किस प्रकार नाश करें।।"));
hinsuperobject.biblemap.put("b41_3_7", new hinsuperobject(41,3,7,"और यीशु अपने चेलों के साथ झील की ओर चला गया: और गलील से एक बड़ी भीड़ उसके पीछे हो ली।"));
hinsuperobject.biblemap.put("b41_3_8", new hinsuperobject(41,3,8,"और यहूदिया, और यरूशलेम और इदूमिया से, और यरदन के पार, और सूर और सैदा के आसपास से एक बड़ी भीड़ यह सुनकर, कि वह कैसे अचम्भे के काम करता है, उसके पास आई।"));
hinsuperobject.biblemap.put("b41_3_9", new hinsuperobject(41,3,9,"और उस ने अपने चेलों से कहा, भीड़ के कारण एक छोटी नाव मेरे लिये तैयार रहे ताकि वे मुझे दबा न सकें।"));
hinsuperobject.biblemap.put("b41_3_10", new hinsuperobject(41,3,10,"क्योंकि उस ने बहुतों को चंगा किया था; इसलिये जितने लोगे रोग से ग्रसित थे, उसे छूने के लिये उस पर गिरे पड़ते थे।"));
hinsuperobject.biblemap.put("b41_3_11", new hinsuperobject(41,3,11,"और अशुद्ध आत्मांए भी, जब उसे देखती थीं, तो उसके आगे गिर पड़ती थीं, और चिल्लाकर कहती थीं कि तू परमेश्वर का पुत्रा है।"));
hinsuperobject.biblemap.put("b41_3_12", new hinsuperobject(41,3,12,"और उस ने उन्हें बहुत चिताया, कि मुझे प्रगट न करना।।"));
hinsuperobject.biblemap.put("b41_3_13", new hinsuperobject(41,3,13,"फिर वह पहाड़ पर चढ़ गया, और जिन्हें वह चाहता था उन्हें अपने पास बुलाया; और वे उसके पास चले आए।"));
hinsuperobject.biblemap.put("b41_3_14", new hinsuperobject(41,3,14,"तब उस ने बारह पुरूषों को नियुक्त किया, कि वे उसके साथ साथ रहें, और वह उन्हें भेजे, कि प्रचार करें।"));
hinsuperobject.biblemap.put("b41_3_15", new hinsuperobject(41,3,15,"और दुष्टात्माओं के निकलने का अधिकार रखें।"));
hinsuperobject.biblemap.put("b41_3_16", new hinsuperobject(41,3,16,"और वे ये हैं: शमौन जिस का नाम उस ने पतरस रखा।"));
hinsuperobject.biblemap.put("b41_3_17", new hinsuperobject(41,3,17,"और जब्दी का पुत्रा याकूब, और याकूब का भाई यूहन्ना, जिनका नाम उस ने बूअनरगिस, अर्थात् गर्जन के पुत्रा रखा।"));
hinsuperobject.biblemap.put("b41_3_18", new hinsuperobject(41,3,18,"और अन्द्रियास, और फिलिप्पुस, और बरतुल्मै, और मत्ती, और थोमा, और हलफई का पुत्रा याकूब; और तद्दी, और शमौन कनानी।"));
hinsuperobject.biblemap.put("b41_3_19", new hinsuperobject(41,3,19,"और यहूदा इस्करियोती, जिस ने उसे पकड़वा भी दिया।।"));
hinsuperobject.biblemap.put("b41_3_20", new hinsuperobject(41,3,20,"और वह घर में आया: और ऐसी भीड़ इकट्ठी हो गई, कि वे रोटी भी न खा सके।"));
hinsuperobject.biblemap.put("b41_3_21", new hinsuperobject(41,3,21,"जब उसके कुटुम्बियों ने यह सुना, तो उसे पकड़ने के लिये निकले; क्योंकि कहते थे, कि उसका चित्त ठिकाने नहीं है।"));
hinsuperobject.biblemap.put("b41_3_22", new hinsuperobject(41,3,22,"और शास्त्री जो यरूशलेम से आए थे, यह कहते थे, कि उस में शैतान है, और यह भी, कि वह दुष्टात्माओं के सरदार की सहायता से दुष्टात्माओं को निकालता है।"));
hinsuperobject.biblemap.put("b41_3_23", new hinsuperobject(41,3,23,"और वह उन्हें पास बुलाकर, उन से दुष्टान्तों में कहने लगा; शैतान क्योंकर शैतान को निकाल सकता है?"));
hinsuperobject.biblemap.put("b41_3_24", new hinsuperobject(41,3,24,"और यदि किसी राज्य में फूट पड़े, तो वह राज्य क्योंकर स्थिर रह सकता है?"));
hinsuperobject.biblemap.put("b41_3_25", new hinsuperobject(41,3,25,"और यदि किसी घर में फूट पड़े, तो वह घर क्योंकर स्थिर रह सकेगा?"));
hinsuperobject.biblemap.put("b41_3_26", new hinsuperobject(41,3,26,"और यदि शैतान अपना ही विरोधी होकर अपने में फूट डाले, तो वह क्योंकर बना रह सकता है? उसका तो अन्त ही हो जाता है।"));
hinsuperobject.biblemap.put("b41_3_27", new hinsuperobject(41,3,27,"किन्तु कोई मनुष्य किसी बलवन्त के घर में घुसकर उसका माल लूट नहीं सकता, जब तक कि वह पहिले उस बलवन्त को न बान्ध ले; और तब उसके घर को लूट लेगा।"));
hinsuperobject.biblemap.put("b41_3_28", new hinsuperobject(41,3,28,"मैं तुम से सच कहता हूं, कि मनुष्यों की सन्तान के सब पाप और निन्दा जो वे करते हैं, क्षमा की जाएगी।"));
hinsuperobject.biblemap.put("b41_3_29", new hinsuperobject(41,3,29,"परन्तु जो कोई पवित्रात्मा के विरूद्ध निन्दा करे, वह कभी भी क्षमा न किया जाएगा: वरन वह अनन्त पाप का अपराधी ठहरता है।"));
hinsuperobject.biblemap.put("b41_3_30", new hinsuperobject(41,3,30,"क्योंकि वे यह कहते थे, कि उस में अशुद्ध आत्मा है।।"));
hinsuperobject.biblemap.put("b41_3_31", new hinsuperobject(41,3,31,"और उस की माता और उसके भाई आए, और बाहर खड़े होकर उसे बुलवा भेजा।"));
hinsuperobject.biblemap.put("b41_3_32", new hinsuperobject(41,3,32,"और भीड़ उसके आसपास बैठी थी, और उन्हों ने उस से कहा; देख, तेरी माता और तेरे भाई बाहर तुझे ढूंढते हैं।"));
hinsuperobject.biblemap.put("b41_3_33", new hinsuperobject(41,3,33,"उस ने उन्हें उत्तर दिया, कि मेरी माता और मेरे भाई कौन हैं?"));
hinsuperobject.biblemap.put("b41_3_34", new hinsuperobject(41,3,34,"और उन पर जो उसके आस पास बैठे थे, दृष्टि करके कहा, देखो, मेरी माता और मेरे भाई यह हैं।"));
hinsuperobject.biblemap.put("b41_3_35", new hinsuperobject(41,3,35,"क्योंकि जो कोई परमेश्वर की इच्छा पर चले, वही मेरे भाई, और बहिन और माता है।।"));
hinsuperobject.biblemap.put("b41_4_1", new hinsuperobject(41,4,1,"वह फिर झील के किनारे उपदेश देने लगा: और ऐसी बड़ी भीड़ उसके पास इकट्ठी हो गई, कि वह झील में एक नाव पर चढ़कर बैठ गया और सारी भीड़ भूमि पर झील के किनारे खड़ी रही।"));
hinsuperobject.biblemap.put("b41_4_2", new hinsuperobject(41,4,2,"और वह उन्हें दृष्टान्तों में बहुत सी बातें सिखाने लगेा, और अपने उपदेश में उन से कहा।"));
hinsuperobject.biblemap.put("b41_4_3", new hinsuperobject(41,4,3,"सुनो: देखो, एक बोनेवाला, बीज बाने के लिये निकला!"));
hinsuperobject.biblemap.put("b41_4_4", new hinsuperobject(41,4,4,"और बोते समय कुछ तो मार्ग के किनारे गिरा और पक्षियों ने आकर उसे चुग लिया।"));
hinsuperobject.biblemap.put("b41_4_5", new hinsuperobject(41,4,5,"और कुछ पत्थरीली भूमि पर गिरा जहां उस की बहुत मिट्टी न मिली, और गहरी मिट्टी न मिलने के कारण जल्द उग आया।"));
hinsuperobject.biblemap.put("b41_4_6", new hinsuperobject(41,4,6,"और जब सूर्य निकला, तो जल गया, और जड़ न पकड़ने के कारण सूख गया।"));
hinsuperobject.biblemap.put("b41_4_7", new hinsuperobject(41,4,7,"और कुछ तो झाड़ियों में गिरा, और झाड़ियों ने बढ़कर उसे दबा लिया, और वह फल न लाया।"));
hinsuperobject.biblemap.put("b41_4_8", new hinsuperobject(41,4,8,"परन्तु कुछ अच्छी भूमि पर गिरा; और वह उगा, और बढ़कर फलवन्त हुआ; और कोई तीस गुणा, कोई साठ गुणा और कोई सौ गुणा फल लाया।"));
hinsuperobject.biblemap.put("b41_4_9", new hinsuperobject(41,4,9,"और उस ने कहा; जिस के पास सुनने के लिये कान हों वह सुन ले।।"));
hinsuperobject.biblemap.put("b41_4_10", new hinsuperobject(41,4,10,"जब वह अकेला रह गया, तो उसके साथियों ने उन बारह समेत उस से इन दृष्टान्तों के विषय में पूछा।"));
hinsuperobject.biblemap.put("b41_4_11", new hinsuperobject(41,4,11,"उस ने उन से कहा, तुम को तो परमेश्वर के राज्य के भेद की समझ दी गई है, परन्तु बाहरवालों के लिये सब बातें दृष्टान्तों में होती हैं।"));
hinsuperobject.biblemap.put("b41_4_12", new hinsuperobject(41,4,12,"इसलिये कि वे देखते हुए देखें और उन्हें सुझाई न पड़े और सुनते हुए सुनें भी और न समझें; ऐसा न हो कि वे फिरें, और क्षमा किए जाएं।"));
hinsuperobject.biblemap.put("b41_4_13", new hinsuperobject(41,4,13,"फिर उस ने उन से कहा; क्या तुम यह दृष्टान्त नहीं समझते? तो फिर और सब दृष्टान्तों को क्योंकर समझोगे?"));
hinsuperobject.biblemap.put("b41_4_14", new hinsuperobject(41,4,14,"बानेवाला वचन बोता है।"));
hinsuperobject.biblemap.put("b41_4_15", new hinsuperobject(41,4,15,"जो मार्ग के किनारे के हैं जहां वचन बोया जाता है, ये वे हैं, कि जब उन्हों ने सुना, तो शैतान तुरन्त आकर वचन को जो उन में बोया गया था, उठा ले जाता है।"));
hinsuperobject.biblemap.put("b41_4_16", new hinsuperobject(41,4,16,"और वैसे ही जो पत्थरीली भूमि पर बोए जाते हैं, ये वे हैं, कि जो वचन को सुनकर तुरन्त आनन्द से ग्रहण कर लेते हैं।"));
hinsuperobject.biblemap.put("b41_4_17", new hinsuperobject(41,4,17,"परन्तु अपने भीतर जड़ न रखते के कारण वे थोड़े भी दिनों के लिये रहते हैं; इस के बाद जब वचन के कारण उन पर क्लेश या उपद्रव होता है, तो वे तुरन्त ठोकर खाते हैं।"));
hinsuperobject.biblemap.put("b41_4_18", new hinsuperobject(41,4,18,"और जो झाडियों में बोए गए ये वे हैं जिन्हों ने वचन सुना।"));
hinsuperobject.biblemap.put("b41_4_19", new hinsuperobject(41,4,19,"और संसार की चिन्ता, और धन का धोखा, और और वस्तुओं का लोभ उन में समाकर वचन को दबा देता है। और वह निष्फल रह जाता है।"));
hinsuperobject.biblemap.put("b41_4_20", new hinsuperobject(41,4,20,"और जो अच्छी भूमि में बोए गए, ये वे हैं, जो वचन सुनकर ग्रहण करते और फल लाते हैं, कोई तीस गुणा, कोई साठ गुणा, और कोई सौ गुणा।।"));
hinsuperobject.biblemap.put("b41_4_21", new hinsuperobject(41,4,21,"और उस ने उन से कहा; क्या दिये को इसलिये लाते हैं कि पैमाने या खाट के निचे रखा जाए? क्या इसलिये नहीं, कि दीवट पर रखा जाए?"));
hinsuperobject.biblemap.put("b41_4_22", new hinsuperobject(41,4,22,"क्योंकि कोई वस्तु छिपी नहीं, परन्तु इसलिये कि प्रगट हो जाए;"));
hinsuperobject.biblemap.put("b41_4_23", new hinsuperobject(41,4,23,"और न कुछ गुप्त है पर इसलिये कि प्रगट हो जाए। यदि किसी के सुनने के कान हों, तो सुन ले।"));
hinsuperobject.biblemap.put("b41_4_24", new hinsuperobject(41,4,24,"फिर उस ने उन से कहा; चौकस रहो, कि क्या सुनते हो? जिस नाप से तुम नापते हो उसी से तुम्हारे लिये भी नापा जाएगा, और तुम को अधिक दिय जाएगा।"));
hinsuperobject.biblemap.put("b41_4_25", new hinsuperobject(41,4,25,"क्योंकि जिस के पास है, उस को दिया जाएगा; परन्तु जिस के पास नहीं है उस से वह भी जो उसके पास है; ले लिया जाएगा।।"));
hinsuperobject.biblemap.put("b41_4_26", new hinsuperobject(41,4,26,"फिर उस ने कहा; परमेश्वर का राजय ऐसा है, जैसे कोई मनुष्य भूमि पर बीज छींटे।"));
hinsuperobject.biblemap.put("b41_4_27", new hinsuperobject(41,4,27,"और रात को सोए, और दिन को जागे और वह बीच ऐसे उगे और बढ़े कि वह न जाने।"));
hinsuperobject.biblemap.put("b41_4_28", new hinsuperobject(41,4,28,"पृथ्वी आप से आप फल लाती है पलिे अंकुर, तब बाल, और तब बालों में तैयार दाना।"));
hinsuperobject.biblemap.put("b41_4_29", new hinsuperobject(41,4,29,"परन्तु जब दाना पक जाता है, तब वह तुरन्त हंसिया लगाता है, क्योंकि कटनी आ पहुंची है।।"));
hinsuperobject.biblemap.put("b41_4_30", new hinsuperobject(41,4,30,"फिर उस ने कहा, हम परमेश्वर के राज्य की उपमा किस से दें, और किस दृष्टान्त से उसका वर्णन करें?"));
hinsuperobject.biblemap.put("b41_4_31", new hinsuperobject(41,4,31,"वह राई के दाने के समान हैं; कि जब भूमि में बोया जाता है तो भूमि के सब बीजों से छोटा होता है।"));
hinsuperobject.biblemap.put("b41_4_32", new hinsuperobject(41,4,32,"परन्तु जब बोया गया, तो उगकर सब साग पात से बड़ा हो जाता है, और उसकी ऐसी बड़ी डालियां निकलती हैं, कि आकाश के पक्षी उसकी छाया में बसेरा कर सकते हैं।।"));
hinsuperobject.biblemap.put("b41_4_33", new hinsuperobject(41,4,33,"और वह उन्हें इस प्रकार के बहुत से दृष्टान्त दे देकर उन की समझ के अनुसार वचन सुनाता था।"));
hinsuperobject.biblemap.put("b41_4_34", new hinsuperobject(41,4,34,"और बिना दृष्टान्त कहे उन से कुछ भी नहीं कहता था; परन्तु एकान्त में वह अपने निज चेलों को सब बातों का अर्थ बताता था।।"));
hinsuperobject.biblemap.put("b41_4_35", new hinsuperobject(41,4,35,"उसी दिन जब सांझ हुई, तो उस ने उन से कहा; आओ, हम पार चलें,।"));
hinsuperobject.biblemap.put("b41_4_36", new hinsuperobject(41,4,36,"और वे भीड़ को छोड़कर जैसा वह था, वैसा की उसे नाव पर साथ ले चले; और उसके साथ, और भी नावें थीं।"));
hinsuperobject.biblemap.put("b41_4_37", new hinsuperobject(41,4,37,"तब बड़ी आन्धी आई, और लहरें नाव पर यहां तक लगीं, कि वह अब पानी से भरी जाती थी।"));
hinsuperobject.biblemap.put("b41_4_38", new hinsuperobject(41,4,38,"और वह आप पिछले भाग में गद्दी पर सो रहा था; तब उन्हों ने उसे जगाकर उस से कहा; हे गुरू, क्या तुझे चिन्ता नहीं, कि हम नाश हुए जाते हैं?"));
hinsuperobject.biblemap.put("b41_4_39", new hinsuperobject(41,4,39,"तब उस ने उठकर आन्धी को डांटा, और पानी से कहा; \"शान्त रह, थम जा\" : और आन्धी थम गई और बड़ा चैन हो गया।"));
hinsuperobject.biblemap.put("b41_4_40", new hinsuperobject(41,4,40,"और उन से कहा; तुम क्यों डरते हो? क्या तुम्हें अब तक विश्वास नहीं?"));
hinsuperobject.biblemap.put("b41_4_41", new hinsuperobject(41,4,41,"और वे बहुत ही डर गए और आपस में बोले; यह कौन है, कि आन्धी और पानी भी उस की आज्ञा मानते हैं?"));
hinsuperobject.biblemap.put("b41_5_1", new hinsuperobject(41,5,1,"और वे झील के पार गिरासेनियों के देश में पहुंचे।"));
hinsuperobject.biblemap.put("b41_5_2", new hinsuperobject(41,5,2,"और जब वह नाव पर से उतरा तो तुरन्त एक मनुष्य जिस में अशुद्ध आत्मा थी कब्रों से निकलकर उसे मिला।"));
hinsuperobject.biblemap.put("b41_5_3", new hinsuperobject(41,5,3,"वह कब्रों में रहा करता था। और कोई उसे सांकलों से भी न बान्ध सकता था।"));
hinsuperobject.biblemap.put("b41_5_4", new hinsuperobject(41,5,4,"क्योंकि वह बार बार बेड़ियों और सांकलों से बान्धा गया था, पर उस ने साकलों को तोड़ दिया, और बेड़ियों के टुकड़े टुकड़े कर दिए थे, और कोई उसे वश में नहीं कर सकता था।"));
hinsuperobject.biblemap.put("b41_5_5", new hinsuperobject(41,5,5,"वह लगातार रात- दिन कब्रों और पहाड़ो में चिल्लाता, और अपने को पत्थरों से घायल करता था।"));
hinsuperobject.biblemap.put("b41_5_6", new hinsuperobject(41,5,6,"वह यीशु को दूर ही से देखकर दौड़ा, और उसे प्रणाम किया।"));
hinsuperobject.biblemap.put("b41_5_7", new hinsuperobject(41,5,7,"और ऊंचे शब्द से चिल्लाकर कहा; हे यीशु, परमप्रधान परमेश्वर के पुत्रा, मुझे तुझ से क्या काम? मैं तुण्े परमेश्वर की शपथ देता हूं, कि मुझे पीड़ा न दे।"));
hinsuperobject.biblemap.put("b41_5_8", new hinsuperobject(41,5,8,"क्योंकि उस ने उस से कहा था, हे अशुद्ध आत्मा, इस मनुष्य में से निकल आ।"));
hinsuperobject.biblemap.put("b41_5_9", new hinsuperobject(41,5,9,"उस ने उस से पूछा; तेरा कया नाम है? उस ने उस से कहा; मेरा नाम सेना है; क्योंकि हम बहुत हैं।"));
hinsuperobject.biblemap.put("b41_5_10", new hinsuperobject(41,5,10,"और उस ने उस से बहुत बिनती की, हमें इस देश से बाहर न भेज।"));
hinsuperobject.biblemap.put("b41_5_11", new hinsuperobject(41,5,11,"वहां पहाड़ पर सूअरों का एक बड़ा झुण्ड चर रहा था।"));
hinsuperobject.biblemap.put("b41_5_12", new hinsuperobject(41,5,12,"और उन्हों ने उस से बिनती करके कहा, कि हमें उन सूअरों में भेज दे, कि हम उन के भीतर जाएं।"));
hinsuperobject.biblemap.put("b41_5_13", new hinsuperobject(41,5,13,"सो उस ने उन्हें आज्ञा दी और अशुद्ध आत्मा निकलकर सूअरों के भीतर पैठ गई और झुण्ड, जो कोई दो हजार का था, कड़ाडे पर से झपटकर झील में जा पड़ा, और डूब मरा।"));
hinsuperobject.biblemap.put("b41_5_14", new hinsuperobject(41,5,14,"और उन के चरवाहों ने भागकर नगर और गांवों में समाचार सुनाया।"));
hinsuperobject.biblemap.put("b41_5_15", new hinsuperobject(41,5,15,"और जो हुआ था, लोग उसे देखने आए। और यीशु के पास आकर, वे उस में सेना समाई थी, कपड़े पहिने और सचेत बैठे देखकर, डर गए।"));
hinsuperobject.biblemap.put("b41_5_16", new hinsuperobject(41,5,16,"और देखनेवालों ने उसका जिस में दुष्टात्माएं थीं, और सूअरों का पूरा हाल, उन को कह सुनाया।"));
hinsuperobject.biblemap.put("b41_5_17", new hinsuperobject(41,5,17,"और वे उस से बिनती कर के कहने लगे, कि हमारे सिवानों से चला जा।"));
hinsuperobject.biblemap.put("b41_5_18", new hinsuperobject(41,5,18,"और जब वह नाव पर चढ़ने लगा, तो वह जिस में पहिले दुष्टात्माएं थीं, उस से बिनती करने लगा, कि मुझे अपने साथ रहने दे।"));
hinsuperobject.biblemap.put("b41_5_19", new hinsuperobject(41,5,19,"परन्तु उस ने उसे आज्ञा न दी, और उस से कहा, अपने घर जाकर अपने लोगों को बता, कि तुझ पर दया करके प्रभु ने तेरे लिये कैसे बड़े काम किए हैं।"));
hinsuperobject.biblemap.put("b41_5_20", new hinsuperobject(41,5,20,"वह जाकर दिकपुलिस में इस बात का प्रचार करने लगा, कि यीशु ने मेरे लिये कैसे बड़े काम किए; और सब अचम्भा करते थे।।"));
hinsuperobject.biblemap.put("b41_5_21", new hinsuperobject(41,5,21,"जब यीशु फिर नाव से पार गया, तो एक बड़ी भीड़ उसके पास इकट्ठी हो गई; और वह झील के किनारे था।"));
hinsuperobject.biblemap.put("b41_5_22", new hinsuperobject(41,5,22,"और याईर नाम आराधनालय के सरदारों में से एक आया, और उसे देखकर, उसके पांवों पर गिरा।"));
hinsuperobject.biblemap.put("b41_5_23", new hinsuperobject(41,5,23,"और उस ने यह कहकर बहुत बिनती की, कि मेरी छोटी बेटी मरने पर है: तू आकर उस पर हाथ रख, कि वह चंगी होकर जीवित रहे।"));
hinsuperobject.biblemap.put("b41_5_24", new hinsuperobject(41,5,24,"तब वह उसके साथ चला; और बड़ी भीड़ उसके पीदे हो ली, यहां तक कि लोग उस पर गिरे पड़ते थे।।"));
hinsuperobject.biblemap.put("b41_5_25", new hinsuperobject(41,5,25,"और एक स्त्री, जिस को बारह वर्ष से लोहू बहने का रोग था।"));
hinsuperobject.biblemap.put("b41_5_26", new hinsuperobject(41,5,26,"और जिस ने बहुत वैद्यों से बड़ा दुख उठाया और अपना सब माल व्यय करने पर भी कुछ लाभ न उठाया था, परन्तु और भी रोगी हो गई थी।"));
hinsuperobject.biblemap.put("b41_5_27", new hinsuperobject(41,5,27,"यीशु की चर्चा सुनकर, भीड़ में उसके पीछे से आई, और उसके वस्त्रा को छू लिया।"));
hinsuperobject.biblemap.put("b41_5_28", new hinsuperobject(41,5,28,"क्योंकि वह कहती थी, यदि मैं उसके वस्त्रा ही को छू लूंगी, तो चंगी हो जाऊंगह।"));
hinsuperobject.biblemap.put("b41_5_29", new hinsuperobject(41,5,29,"और तुरन्त उसका लोहू बहना बन्द हो गया; और उस ने अपनी देह में जान लिया, कि मैं उस बीमारी से अच्छी हो गई।"));
hinsuperobject.biblemap.put("b41_5_30", new hinsuperobject(41,5,30,"यीशु ने तुरन्त अपने में जान लिया, कि मुझ से सामर्थ निकली है, और भीड़ में पीछे फिरकर पूछा; मेरा वस्त्रा किस ने छूआ?"));
hinsuperobject.biblemap.put("b41_5_31", new hinsuperobject(41,5,31,"उसके चेलों ने उस से कहा; तू देखता है, कि भीड़ तुझ पर गिरी पड़ती है, और तू कहता है; कि किस ने मुझे छुआ?"));
hinsuperobject.biblemap.put("b41_5_32", new hinsuperobject(41,5,32,"तब उस ने उसे देखने के लिये जिस ने यह काम किया था, चारों ओर दृष्टि की।"));
hinsuperobject.biblemap.put("b41_5_33", new hinsuperobject(41,5,33,"तब वह स्त्री यह जानकर, कि मेरी कैसी भलाई हुई है, डरती और कांपती हुई आई, और उसके पांवों पर गिरकर, उस से सब हाल सच सच कह दिया।"));
hinsuperobject.biblemap.put("b41_5_34", new hinsuperobject(41,5,34,"उस ने उस से कहा; पुत्री तेरे विश्वास ने तुझे चंगा किया है: कुशल से जा, और अपनी इस बीमारी से बची रह।।"));
hinsuperobject.biblemap.put("b41_5_35", new hinsuperobject(41,5,35,"वह यह कह ही रहा था, कि आराधनालय के सरदार के घर से लोगों ने आकर कहा, कि तेरी बेटी तो मर गई; अब गुरू को क्यों दुख देता है?"));
hinsuperobject.biblemap.put("b41_5_36", new hinsuperobject(41,5,36,"जो बात वे कह रहे थे, उस को यीशु ने अनसुनी करके, आराधनालय के सरदार से कहा; मत डर; केवल विश्वास रख।"));
hinsuperobject.biblemap.put("b41_5_37", new hinsuperobject(41,5,37,"और उस ने पतरस और याकूब और याकूब के भाई यूहन्ना को छोड़, और किसी को अपने साथ आने न दिया।"));
hinsuperobject.biblemap.put("b41_5_38", new hinsuperobject(41,5,38,"और अराधनालय के सरदार के घर में पहुंचकर, उस ने लोगों को बहुत रोते और चिल्लाते देखा।"));
hinsuperobject.biblemap.put("b41_5_39", new hinsuperobject(41,5,39,"तब उस ने भीतर जाकर उस से कहा, तुम क्यों हल्ला मचाते और रोते हो? लड़की मरी नहीं, परन्तु सो रही है।"));
hinsuperobject.biblemap.put("b41_5_40", new hinsuperobject(41,5,40,"वे उस की हंसी करने लगे, परन्तु उस ने सब को निकालकर लड़की के मातापिता और अपने साथियों को लेकर, भीतर जंहा लड़की पड़ी थी, गया।"));
hinsuperobject.biblemap.put("b41_5_41", new hinsuperobject(41,5,41,"और लड़की का हाथ पकड़कर उस से कहा, `तलीता कूमी'; जिस का अर्थ यह है कि `हे लड़की, मैं तुझ से कहता हूं, उठ'।"));
hinsuperobject.biblemap.put("b41_5_42", new hinsuperobject(41,5,42,"और लड़की तुरन्त उठकर चलने फिरने लगी; क्योंकि वह बारह वर्ष की थी। और इस पर लोग बहुत चकित हो गए।"));
hinsuperobject.biblemap.put("b41_5_43", new hinsuperobject(41,5,43,"फिर उस ने उन्हें चिताकर आज्ञा दी कि यह बात कोई जानने न पाए और कहा; कि उसे कुछ खाने को दिया जाए।।"));
hinsuperobject.biblemap.put("b41_6_1", new hinsuperobject(41,6,1,"वहां से निकलकर वह अपने देश में आया, और उसके चेले उसके पीछे हो लिए।"));
hinsuperobject.biblemap.put("b41_6_2", new hinsuperobject(41,6,2,"सब्त के दिन वह आराधनालय में उपदेश करने लगा; और बहुत लोग सुनकर चकित हुए और कहने लगे, इस को ये बातें कहां से आ गई? और यह कौन सा ज्ञान है जो उस को दिया गया है? और कैसे सामर्थ के काम इसके हाथों से प्रगट होते हैं?"));
hinsuperobject.biblemap.put("b41_6_3", new hinsuperobject(41,6,3,"क्या यह वही बढ़ई नहीं, जो मरियम का पुत्रा, और याकूब और योसेस और यहूदा और शमौन का भाई है? और क्या उस की बहिनें यहां हमारे बीच में नहीं रहतीं? इसलिये उन्हों ने उसके विषय मे ठोकर खाई।"));
hinsuperobject.biblemap.put("b41_6_4", new hinsuperobject(41,6,4,"यीशु ने उन से कहा, कि भविष्यद्वक्ता अपने देश और अपने कुटुम्ब और अपने घर को छोड़ और कहीं भी निरादर नहीं होता।"));
hinsuperobject.biblemap.put("b41_6_5", new hinsuperobject(41,6,5,"और वह वहां कोई सामर्थ का काम न कर सका, केवल थोड़े बीमारों पर हाथ रखकर उन्हें चंगा किया।।"));
hinsuperobject.biblemap.put("b41_6_6", new hinsuperobject(41,6,6,"और उस ने उन के अविश्वास पर आश्चर्य किया और चारों ओर से गावों में उपदेश करता फिरा।।"));
hinsuperobject.biblemap.put("b41_6_7", new hinsuperobject(41,6,7,"और वह बारहों को अपने पास बुलाकर उन्हें दो दो करके भेजने लगा; और उन्हें अशुद्ध आत्माओं पर अधिकार दिया।"));
hinsuperobject.biblemap.put("b41_6_8", new hinsuperobject(41,6,8,"और उस ने उन्हें आज्ञा दी, कि मार्ग के लिये लाठी छोड़ और कुछ न लो; न तो रोटी, न झोली, न पटुके में पैसे।"));
hinsuperobject.biblemap.put("b41_6_9", new hinsuperobject(41,6,9,"परन्तु जूतियां पहिनो और दो दो कुरते न पहिनो।"));
hinsuperobject.biblemap.put("b41_6_10", new hinsuperobject(41,6,10,"और उस ने उन से कहा; जहां कहीं तुम किसी घर में उतरो तो जब तक वहां से विदा न हो, तब तक उसी में ठहरे रहो।"));
hinsuperobject.biblemap.put("b41_6_11", new hinsuperobject(41,6,11,"जिस स्थान के लोग तुम्हें ग्रहण न करें, और तुम्हारी न सुनें, वहां से चलते ही अपने तलवों की धूल झाड़ डालो, कि उन पर गवाही हो।"));
hinsuperobject.biblemap.put("b41_6_12", new hinsuperobject(41,6,12,"और उन्हों ने जाकर प्रचार किया, कि मन फिराओ।"));
hinsuperobject.biblemap.put("b41_6_13", new hinsuperobject(41,6,13,"और बहुतेरे दुष्टात्माओं को निकाला, और बहुत बीमारों पर तेल मलकर उन्हें चंगा किया।।"));
hinsuperobject.biblemap.put("b41_6_14", new hinsuperobject(41,6,14,"और हेरोदेस राजा ने उस की चर्चा सुनी, क्योंकि उसका नाम फैल गया था, और उस ने कहा, कि यूहन्ना बपतिस्मा देनेवाला मरे हुओं में से जी उठा है, इसी लिये उस से ये सामर्थ के काम प्रगट होते हैं।"));
hinsuperobject.biblemap.put("b41_6_15", new hinsuperobject(41,6,15,"और औरों ने कहा, यह एलिरयाह है, परन्तु औरों ने कहा, भविष्यद्वक्ता या भविष्यद्वक्ताओं में से किसी एक के समान है।"));
hinsuperobject.biblemap.put("b41_6_16", new hinsuperobject(41,6,16,"हेरोदेस ने यह सुन कर कहा, जिस यूहन्ना का सिर मैं ने कटवाया था, वही जी उठा है।"));
hinsuperobject.biblemap.put("b41_6_17", new hinsuperobject(41,6,17,"क्योंकि हेरोदेस ने आप अपने भाई फिलिप्पुस की पत्नी हेरोदियास के कारण, जिस से उस ने ब्याह किया था, लोगों को भेजकर यूहन्ना को पकड़वाकर बन्दीगृह में डाल दिया था।"));
hinsuperobject.biblemap.put("b41_6_18", new hinsuperobject(41,6,18,"क्योंकि यूहन्ना ने हेरोदेस से कहा था, कि अपने भाई की पत्नी को रखना तुझे उचित नहीं।"));
hinsuperobject.biblemap.put("b41_6_19", new hinsuperobject(41,6,19,"इसलिये हेरोदियास उस से बैर रखती थी और यह चाहती थी, कि उसे मरवा डाले, परन्तु ऐसा न हो सका।"));
hinsuperobject.biblemap.put("b41_6_20", new hinsuperobject(41,6,20,"क्योंकि हेरोदेस यूहन्ना को धर्मी और पवित्रा पुरूष जानकर उस से डरता था, और उसे बचाए रखता था, और उस की सुनकर बहुत घबराता था, पर आनन्द से सुनता था।"));
hinsuperobject.biblemap.put("b41_6_21", new hinsuperobject(41,6,21,"और ठीक अवसर पर जब हेरोदेस ने अपने जन्म दिन में अपने प्रधानों और सेनापतियों, और गलील के बड़े लोगों के लिये जेवनार की।"));
hinsuperobject.biblemap.put("b41_6_22", new hinsuperobject(41,6,22,"और उसी हेरोदियास की बेटी भीतर आई, और नाचकर हेरोदेस को और उसके साथ बैठनेवालों को प्रसन्न किया; तब राजा ने लड़की से कहा, तू जो चाहे मुझ से मांग मैं तुझे दूंगा।"));
hinsuperobject.biblemap.put("b41_6_23", new hinsuperobject(41,6,23,"और उस ने शपथ खाई, कि मैं अपने आधे राज्य तक जो कुछ तू मुझ से मांगेगी मैं तुझे दूंगा।"));
hinsuperobject.biblemap.put("b41_6_24", new hinsuperobject(41,6,24,"उस ने बाहर जाकर अपनी माता से पूछा, कि मैं क्या मांगूं? वह बोली; यूहन्ना बपतिस्मा देनेवाले का सिर।"));
hinsuperobject.biblemap.put("b41_6_25", new hinsuperobject(41,6,25,"वह तुरन्त राजा के पास भीतर आई, और उस से बिनती की; मैं चाहती हूं, कि तू अभी यूहन्ना बपतिस्मा देनेवाले का सिर एक थाल में मुझे मंगवा दे।"));
hinsuperobject.biblemap.put("b41_6_26", new hinsuperobject(41,6,26,"तब राजा बहुत उदास हुआ, परन्तु अपनी शपथ के कारण और साथ बैठनेवालों के कारण उसे टालना न चाहा।"));
hinsuperobject.biblemap.put("b41_6_27", new hinsuperobject(41,6,27,"और राजा ने तुरन्त एक सिपाही को आज्ञा देकर भेजा, कि उसका सिर काट लाए।"));
hinsuperobject.biblemap.put("b41_6_28", new hinsuperobject(41,6,28,"उस ने जेलखाने में जाकर उसका सिर काटा, और एक थाल में रखकर लाया और लड़की को दिया, और लड़की ने अपनी मां को दिया।"));
hinsuperobject.biblemap.put("b41_6_29", new hinsuperobject(41,6,29,"यह सुनकर उसके चेले आए, और उस की लोथ को उठाकर कब्र में रखा।"));
hinsuperobject.biblemap.put("b41_6_30", new hinsuperobject(41,6,30,"प्रेरितों ने यीशु के पास इकट्ठे होकर, जो कुछ उन्हों ने किया, और सिखाया था, सब उस को बता दिया।"));
hinsuperobject.biblemap.put("b41_6_31", new hinsuperobject(41,6,31,"उस ने उन से कहा; तुम आप अलग किसी जंगली स्थान में आकर थोड़ा विश्राम करो; क्योंकि बहुत लोग आते जाते थे, और उन्हें खाने का अवसर भी नहीं मिलता था।"));
hinsuperobject.biblemap.put("b41_6_32", new hinsuperobject(41,6,32,"इसलिये वे नाव पर चढ़कर, सुनसान जगह में अलग चले गए।"));
hinsuperobject.biblemap.put("b41_6_33", new hinsuperobject(41,6,33,"और बहुतों ने उन्हें जाते देखकर पचिान लिया, और सब नगरों से इकट्ठे होकर वहां पैदल दौड़े और उन से पहिले जा पहुंचे।"));
hinsuperobject.biblemap.put("b41_6_34", new hinsuperobject(41,6,34,"उस ने निकलकर बड़ी भीड़ देखी, और उन पर तरस खाया, क्योंकि वे उन भेड़ों के समान थे, जिन का कोई रखवाला न हो; और वह उन्हें बहुत सी बातें सिखाने लगा।"));
hinsuperobject.biblemap.put("b41_6_35", new hinsuperobject(41,6,35,"जब दिन बहुत ढल गया, तो उसके चेले उसके पास आकर कहने लगे; यह सुनसान जगह है, और दिन बहुत ढल गया है।"));
hinsuperobject.biblemap.put("b41_6_36", new hinsuperobject(41,6,36,"उन्हें विदा कर, कि चारों ओर के गांवों और बस्तियों में जाकर, अपने लिये कुछ खाने को मोल लें।"));
hinsuperobject.biblemap.put("b41_6_37", new hinsuperobject(41,6,37,"उस ने उन्हें उत्तर दिया; कि तुम ही उन्हें खाने को दो : उनहों ने उस से कहा; क्या हम सौ दीनार की रोटियां मोल लें, और उन्हें खिलाएं?"));
hinsuperobject.biblemap.put("b41_6_38", new hinsuperobject(41,6,38,"उस ने उन से कहा; जाकर देखो तुम्हारे पास कितनी रोटियां हैं? उन्हों ने मालूम करके कहा; पांच और दो मछली भी।"));
hinsuperobject.biblemap.put("b41_6_39", new hinsuperobject(41,6,39,"तब उस ने उन्हें आज्ञा दी, कि सब को हरी घास पर पांति पांति से बैठा दो।"));
hinsuperobject.biblemap.put("b41_6_40", new hinsuperobject(41,6,40,"वे सौ सौ और पचास पचास करके पांति पांति बैठ गए।"));
hinsuperobject.biblemap.put("b41_6_41", new hinsuperobject(41,6,41,"और उस ने उन पांच रोटियों को और दो मछलियों को लिया, और स्वर्ग की ओर देखकर धन्यवाद किया और रोटियां तोड़ तोड़ कर चेलों को देता गया, कि वे लोगों को परोसें, और वे दो मछलियां भी उन सब में बांट दीं।"));
hinsuperobject.biblemap.put("b41_6_42", new hinsuperobject(41,6,42,"और सब खाकर तृप्त हो गए।"));
hinsuperobject.biblemap.put("b41_6_43", new hinsuperobject(41,6,43,"और उन्हों ने टुकडों से बारह टोकरियां भर कर उठाई, और कुछ मछलियों से भी।"));
hinsuperobject.biblemap.put("b41_6_44", new hinsuperobject(41,6,44,"जिन्हों ने रोटियां खाई, वे पांच हजार पुरूष थे।।"));
hinsuperobject.biblemap.put("b41_6_45", new hinsuperobject(41,6,45,"तब उस ने तुरन्त अपने चेलों को बरबस नाव पर चढाया, कि वे उस से पहिले उस पार बैतसैदा को चले जांए, जब तक कि वह लोगों को विदा करे।"));
hinsuperobject.biblemap.put("b41_6_46", new hinsuperobject(41,6,46,"और उन्हें विदा करके पहाड़ पर प्रार्थना करने को गया।"));
hinsuperobject.biblemap.put("b41_6_47", new hinsuperobject(41,6,47,"और जब सांझ हुई, तो नाव झील के बीच में थी, और वह अकेला भूमि पर था।"));
hinsuperobject.biblemap.put("b41_6_48", new hinsuperobject(41,6,48,"और जब उस ने देखा, कि वे खेते खेते घबरा गए हैं, क्योंकि हवा उन के विरूद्ध थी, तो रात के चौथे पहर के निकट वह झील पर चलते हुए उन के पास आया; और उन से आगे निकल जाना चाहता था।"));
hinsuperobject.biblemap.put("b41_6_49", new hinsuperobject(41,6,49,"परन्तु उन्हों ने उसे झील पर चलते देखकर समझा, कि भूत है, और चिल्ला उठे, क्योंकि सब उसे देखकर घबरा गए थे।"));
hinsuperobject.biblemap.put("b41_6_50", new hinsuperobject(41,6,50,"पर उस ने तुरन्त उन से बातें कीं और कहा; ढाढ़स बान्धों : मैं हूं; डरो मत।"));
hinsuperobject.biblemap.put("b41_6_51", new hinsuperobject(41,6,51,"तब वह उन के पास नाव पर आया, और हवा थम गई: वे बहुत ही आश्चर्य करने लगे।"));
hinsuperobject.biblemap.put("b41_6_52", new hinsuperobject(41,6,52,"क्योंकि वे उन रोटियों के विषय में ने समझे थे परन्तु उन के मन कठोर हो गए थे।।"));
hinsuperobject.biblemap.put("b41_6_53", new hinsuperobject(41,6,53,"और वे पार उतरकर गन्नेसरत में पहुंचे, और नाव घाअ पर लगाई।"));
hinsuperobject.biblemap.put("b41_6_54", new hinsuperobject(41,6,54,"और जब वे नाव पर से उतरे, तो लोग तुरन्त उस को पहचान कर।"));
hinsuperobject.biblemap.put("b41_6_55", new hinsuperobject(41,6,55,"आसपास के सारे देश में दोड़े, और बीमारों को खांटों पर डालकर, जहां जहां समाचार पाया कि वह है, वहां वहां लिए फिरे।"));
hinsuperobject.biblemap.put("b41_6_56", new hinsuperobject(41,6,56,"और जहां कहीं वह गांवों, नगरों, या बस्तियों में जाता था, तो लोग बीमारों को बाजारों में रखकर उस से बिनती करते थे, कि वह उन्हें अपने वस्त्रा के आंचल ही हो छू लेने दे: और जितने उसे छूते थे, सब चंगे हो जाते थे।।"));
hinsuperobject.biblemap.put("b41_7_1", new hinsuperobject(41,7,1,"तब फरीसी और कई एक शास्त्री जो यरूशलेम से आए थे, उसके पास इकट्ठे हुए।"));
hinsuperobject.biblemap.put("b41_7_2", new hinsuperobject(41,7,2,"और उन्हों ने उसके कई चेलों को अशुद्ध अर्थात् बिना हाथ धोए रोटी खाते देखा।"));
hinsuperobject.biblemap.put("b41_7_3", new hinsuperobject(41,7,3,"क्योंकि फरीसी और सब यहूदी, पुरनियों की रीति पर चलते हैं और जब तक भली भांति हाथ नहीं धो लेते तब तक नहीं खाते।"));
hinsuperobject.biblemap.put("b41_7_4", new hinsuperobject(41,7,4,"और बाजार से आकर, जब तक स्नान नहीं कर लेते, तब तक नहीं खाते; और बहुत सी और बातें हैं, जो उन के पास मानने के लिये पहुंचाई गई हैं, जैसे कटोरों, और लोटों, और तांबे के बरतनों को धोना- मंाजना।"));
hinsuperobject.biblemap.put("b41_7_5", new hinsuperobject(41,7,5,"इसलिये उन फरीसियों और शास्त्रियों ने उस से पूछा, कि तेरे चेले क्यों पुरनियों की रीतों पर नहीं चलते, और बिना हाथ धोए रोटी खाते हैं?"));
hinsuperobject.biblemap.put("b41_7_6", new hinsuperobject(41,7,6,"उस ने उन से कहा; कि यशायाह ने तुम कपटियों के विषय में बहुत ठीक भविष्यद्ववाणी की; जैसा लिखा है; कि ये लोग होठों से तो मेरा आदर करते हैं, पर उन का मन मुझ से दूर रहता है।"));
hinsuperobject.biblemap.put("b41_7_7", new hinsuperobject(41,7,7,"और ये व्यर्थ मेरी उपासना करते हैं, क्योंकि मनुष्यों की आज्ञाओं को धर्मोंपादेश करके सिखाते हैं।"));
hinsuperobject.biblemap.put("b41_7_8", new hinsuperobject(41,7,8,"क्योंकि तुम परमेश्वर की आज्ञा को टालकर मनुष्यों की रीतियों को मानते हो।"));
hinsuperobject.biblemap.put("b41_7_9", new hinsuperobject(41,7,9,"और उस ने उन से कहा; तुम अपनी रीतियों को मानने के लिये परमेश्वर आज्ञा कैसी अच्छी तरह टाल देते हो!"));
hinsuperobject.biblemap.put("b41_7_10", new hinsuperobject(41,7,10,"क्योंकि मूसा ने कहा है कि अपने पिता और अपनी माता का आदर कर; ओर जो कोई पिता वा माता को बुरा कहे, वह अवश्य मार डाला जाए।"));
hinsuperobject.biblemap.put("b41_7_11", new hinsuperobject(41,7,11,"परन्तु तुम कहते हो कि यदि कोई अपने पिता वा माता से कहे, कि जो कुछ तुझे मुझ से लाभ पहुंच सकता था, वह कुरबान अर्थात् संकल्प हो चुका।"));
hinsuperobject.biblemap.put("b41_7_12", new hinsuperobject(41,7,12,"तो तुम उस को उसके पिता वा उस की माता की कुछ सेवा करने नहीं देते।"));
hinsuperobject.biblemap.put("b41_7_13", new hinsuperobject(41,7,13,"इस प्रकार तुम अपनी रीतियों से, जिन्हें तुम ने ठहराया है, परमेश्वर का वचन टाल देते हो; और ऐसे ऐसे बहुत से काम करते हो।"));
hinsuperobject.biblemap.put("b41_7_14", new hinsuperobject(41,7,14,"और उस ने लोगों को अपने पास बुलाकर उन से कहा, तुम सब मेरी सुनो, और समझो।"));
hinsuperobject.biblemap.put("b41_7_15", new hinsuperobject(41,7,15,"ऐसी तो कोई वस्तु नहीं जो मनुष्य को बाहर से समाकर अशुद्ध करे; परन्तु जो वस्तुएं मनुष्य के भीतर से निकलती हैं, वे ही उसे अशुद्ध करती हैं।"));
hinsuperobject.biblemap.put("b41_7_16", new hinsuperobject(41,7,16,"यदि किसी के सुनने के कान हों तो सुन ले।"));
hinsuperobject.biblemap.put("b41_7_17", new hinsuperobject(41,7,17,"जब वह भीड़ के पास से घर में गया, तो उसके चेलों ने इस दृष्टान्त के विषय में उस से पूछा।"));
hinsuperobject.biblemap.put("b41_7_18", new hinsuperobject(41,7,18,"उस ने उन से कहा; क्या तुम भी ऐसे ना समझ हो? क्या तुम नहीं समझते, कि जो वस्तु बाहर से मनुष्य के भीतर जाती है, वह उसे अशुद्ध नहीं कर सकती?"));
hinsuperobject.biblemap.put("b41_7_19", new hinsuperobject(41,7,19,"क्योंकि वह उसके मन में नहीं, परन्तु पेट में जाती है, और संडास में निकल जाती है? यह कहकर उस ने सब भोजन वस्तुओं को शुद्ध ठहराया।"));
hinsuperobject.biblemap.put("b41_7_20", new hinsuperobject(41,7,20,"फिर उस ने कहा; जो मनुष्य में से निकलता है, वही मनुष्य को अशुद्ध करता है।"));
hinsuperobject.biblemap.put("b41_7_21", new hinsuperobject(41,7,21,"क्योंकि भीतर से अर्थात् मनुष्य मे मन से, बुरी बुरी चिन्ता व्यभिचार।"));
hinsuperobject.biblemap.put("b41_7_22", new hinsuperobject(41,7,22,"चोरी, हत्या, परस्त्रीगमन, लोभ, दुष्टता, छल, लुचपन, कुदृष्टि, निन्दा, अभिमान, और मूर्खता निकलती हैं।"));
hinsuperobject.biblemap.put("b41_7_23", new hinsuperobject(41,7,23,"ये सब बुरी बातें भीतर ही से निकलती हैं और मनुष्य को अशुद्ध करती हैं।।"));
hinsuperobject.biblemap.put("b41_7_24", new hinsuperobject(41,7,24,"फिर वह वहां से उठकर सूर और सैदा के देशों में आया; और एक घर में गया, और चाहता था, कि कोई न जाने; परन्तु वह छिप न सका।"));
hinsuperobject.biblemap.put("b41_7_25", new hinsuperobject(41,7,25,"और तुरन्त एक स्त्री जिस की छोटी बेटी में अशुद्ध आत्मा थी, उस की चर्चा सुन कर आई, और उसके पांवों पर गिरी।"));
hinsuperobject.biblemap.put("b41_7_26", new hinsuperobject(41,7,26,"यह यूनानी और सूरूफिनीकी जाति की थी; और उस ने उस से बिनती की, कि मेरी बेटी में से दुष्टात्मा निकाल दे।"));
hinsuperobject.biblemap.put("b41_7_27", new hinsuperobject(41,7,27,"उस ने उस से कहा, पहिले लड़कों को तृप्त होने दे, क्योंकि लड़कों को रोटी लेकर कुत्तों के आगे डालना उचित नहीं है।"));
hinsuperobject.biblemap.put("b41_7_28", new hinsuperobject(41,7,28,"उस ने उस को उत्तर दिया; कि सच है प्रभु; तौभी कुत्ते भी तो मेज के नीचे बालकों की रोटी का चूर चार खा लेते हैं।"));
hinsuperobject.biblemap.put("b41_7_29", new hinsuperobject(41,7,29,"उस ने उस सके कहा; इस बात के कारण चली जा; दुष्टात्मा तेरी बेटी में से निकल गई है।"));
hinsuperobject.biblemap.put("b41_7_30", new hinsuperobject(41,7,30,"और उस ने अपने घर आकर देखा कि लड़की खाट पर पड़ी है, और दुष्टात्मा निकल गई है।।"));
hinsuperobject.biblemap.put("b41_7_31", new hinsuperobject(41,7,31,"फिर वह सूर और सैदा के देशों से निकलकर दिकपुलिस देश से होता हुआ गलील की झील पर पहुंचा।"));
hinsuperobject.biblemap.put("b41_7_32", new hinsuperobject(41,7,32,"और लोगों ने एक बहिरे को जो हक्ला भी था, उसके पास लाकर उस से बिनती की, कि अपना हाथ उस पर रखे।"));
hinsuperobject.biblemap.put("b41_7_33", new hinsuperobject(41,7,33,"तब वह उस को भीड़ से अलग ले गया, और अपनी उंगलियां उसके कानों में डालीं, और थूक कर उस की जीभ को छूआ।"));
hinsuperobject.biblemap.put("b41_7_34", new hinsuperobject(41,7,34,"और स्वर्ग की ओर देखकर आह भरी, और उस से कहा; इप्फत्तह, अर्थात् खुल जा।"));
hinsuperobject.biblemap.put("b41_7_35", new hinsuperobject(41,7,35,"और उसके कान खुल गए, और उस की जीभ की गांठ भी खुल गई, और वह साफ साफ बोलने लगा।"));
hinsuperobject.biblemap.put("b41_7_36", new hinsuperobject(41,7,36,"तब उस ने उन्हें चिताया कि किसी से न कहना; परन्तु जितना उस ने उन्हें चिताया उतना ही वे और प्रचार करने लगे।"));
hinsuperobject.biblemap.put("b41_7_37", new hinsuperobject(41,7,37,"और वे बहुत ही आश्चर्य में होकर कहने लगे, उस ने जो कुछ किया सब अच्छा किया है; वह बहिरों को सुनने, की, और गूंगों को बोलने की शक्ति देता है।।"));
hinsuperobject.biblemap.put("b41_8_1", new hinsuperobject(41,8,1,"उन दिनों में, जब फिर बड़ी भीड़ इकट्ठी हुई, और उन के पास कुछ खाने को न था, तो उस ने अपने चेलों को पास बुलाकर उन से कहा।"));
hinsuperobject.biblemap.put("b41_8_2", new hinsuperobject(41,8,2,"मुझे इस भीड़ पर तरस आता है, क्योंकि यह तीन दिन से बराबर मेरे साथ हैं, और उन के पास कुछ भी खाने को नहीं।"));
hinsuperobject.biblemap.put("b41_8_3", new hinsuperobject(41,8,3,"यदि मैं उन्हें भूखा घर भेज दूं, तो मार्ग में थक कर रह जाएंगे; क्योकि इन में से कोई कोई दूर से आए हैं।"));
hinsuperobject.biblemap.put("b41_8_4", new hinsuperobject(41,8,4,"उसके चेलों ने उस को उत्तर दिया, कि यहां जंगल में इतनी रोटी कोई कहां से लाए कि ये तृप्त हों?"));
hinsuperobject.biblemap.put("b41_8_5", new hinsuperobject(41,8,5,"उस ने उन से पूछा; तुम्हारे पास कितनी रोटियां हैं? उन्हों ने कहा, सात।"));
hinsuperobject.biblemap.put("b41_8_6", new hinsuperobject(41,8,6,"तब उस ने लोगों को भूमि पर बैठने की आज्ञा दी, और वे सात रोटियां लीं, और धन्यवाद करके तोड़ी, और अपने चेलों को देता गया कि उन के आगे रखें, और उन्हों ने लोगों के आगे परोस दिया"));
hinsuperobject.biblemap.put("b41_8_7", new hinsuperobject(41,8,7,"उन के पास थोड़ी सी छोटी मछलियों भी थीं; और उसने धन्यवाद करके उन्हें भी लोगों के आगे रखने की आज्ञा दी।"));
hinsuperobject.biblemap.put("b41_8_8", new hinsuperobject(41,8,8,"सो वे खाकर तृप्त हो गए और शेष टृकड़ों के सात टोकरे भरकर उठाए।"));
hinsuperobject.biblemap.put("b41_8_9", new hinsuperobject(41,8,9,"और लोग चार हजार के लगभग थे; और उस ने उन को विदा किया।"));
hinsuperobject.biblemap.put("b41_8_10", new hinsuperobject(41,8,10,"और वह तुरन्त अपने चेलों के साथ नाव पर चढ़कर दलमनूता देश को चला गया।।"));
hinsuperobject.biblemap.put("b41_8_11", new hinsuperobject(41,8,11,"फिर फरीसी निकलकर उस से वाद- विवाद करने लगे, और उसे जांचने के लिये उस से कोई स्वर्गीय चिन्ह मांगा।"));
hinsuperobject.biblemap.put("b41_8_12", new hinsuperobject(41,8,12,"उस ने अपनी आत्मा में आह मार कर कहा, इस समय के लोग क्यों चिन्ह ढूंढ़ते हैं? मैं तुम से सच कहता हूं, कि इस समय के लोगों को कोई चिन्ह नहीं दिया जाएगा।"));
hinsuperobject.biblemap.put("b41_8_13", new hinsuperobject(41,8,13,"और वह उन्हें छोड़कर फिर नाव पर चढ़ गया और पार चला गया।।"));
hinsuperobject.biblemap.put("b41_8_14", new hinsuperobject(41,8,14,"और वे रोटी लेना भूल गए थे, और नाव में उन के पास एक ही रोटी थी।"));
hinsuperobject.biblemap.put("b41_8_15", new hinsuperobject(41,8,15,"और उस ने उन्हें चिताया, कि देखो, फरीसियों के खमीर और हेरोदेस के खमीर से चौकस रहो।"));
hinsuperobject.biblemap.put("b41_8_16", new hinsuperobject(41,8,16,"वे आपस में विचार करके कहने लगे, कि हमारे पास तो रोटी नहीं है।"));
hinsuperobject.biblemap.put("b41_8_17", new hinsuperobject(41,8,17,"यह जानकर यीशु ने उन से कहा; तुम क्यों आपस में विचार कर रहे हो कि हमारे पास रोटी नहीं? क्या अब तक नहीं जानते और नहीं समझते?"));
hinsuperobject.biblemap.put("b41_8_18", new hinsuperobject(41,8,18,"क्या तुम्हारा मन कठोर हो गया है? क्या आंखे रखते हुए भी नहीं देखते, और कान रखते हुए भी नहीं सुनते? और तुम्हें स्मरण नहीं।"));
hinsuperobject.biblemap.put("b41_8_19", new hinsuperobject(41,8,19,"कि जब मैं ने पांच हजार के लिये पांच रोटी तोड़ी थीं तो तुम ने टुकड़ों की कितनी टोकरियां भरकर उठाईं? उन्हों ने उस से कहा, सात टोकरे।"));
hinsuperobject.biblemap.put("b41_8_20", new hinsuperobject(41,8,20,"उस ने उन से कहा, सात टोकरे।"));
hinsuperobject.biblemap.put("b41_8_21", new hinsuperobject(41,8,21,"उस ने उन से कहा, क्या तुम अब तक नहीं समझते?"));
hinsuperobject.biblemap.put("b41_8_22", new hinsuperobject(41,8,22,"और वे बैतसैदा में आए; और लोग एक अन्धे को उसके पास ले आए और उस से बिनती की, कि उस को छूए।"));
hinsuperobject.biblemap.put("b41_8_23", new hinsuperobject(41,8,23,"वह उस अन्धे का हाथ पकड़कर उसे गांव के बाहर ले गया, और उस की आंखों में थूककर उस पर हाथ रखे, और उस से पूछा; क्या तू कुछ देखता है?"));
hinsuperobject.biblemap.put("b41_8_24", new hinsuperobject(41,8,24,"उस ने आंख उठा कर कहा; मैं मनुष्यों को देखता हूं; क्योंकि वे मुझे चलते हुए दिखाई देते हैं, जैसे पेड़।"));
hinsuperobject.biblemap.put("b41_8_25", new hinsuperobject(41,8,25,"तब उस ने फिर दोबारा उस की आंखों पर हाथ रखे, और उस ने ध्यान से देखा, और चंगा हो गया, और सब कुछ साफ साफ देखने लगा।"));
hinsuperobject.biblemap.put("b41_8_26", new hinsuperobject(41,8,26,"और उस ने उस से यह कहकर घर भेजा, कि इस गांव के भीतर पांव भी न रखना।।"));
hinsuperobject.biblemap.put("b41_8_27", new hinsuperobject(41,8,27,"यीशु और उसके चेले कैसरिया फिलिप्पी के गावों में चले गए: और मार्ग में उस ने अपने चेलों से पूछा कि लोग मुझे क्या कहते हैं?"));
hinsuperobject.biblemap.put("b41_8_28", new hinsuperobject(41,8,28,"उन्हों ने उत्तर दिया, कि यूहन्ना बपतिस्मा देनेवाला; पर कोई कोई; एलिरयाह; और कोई कोई भविष्यद्वक्ताओं में से एक भी कहते हैं।"));
hinsuperobject.biblemap.put("b41_8_29", new hinsuperobject(41,8,29,"उस ने उन से पूछा; परन्तु तुम मुझे क्या कहते हो? पतरस ने उस को उत्तर दिया; तू मसीह है।"));
hinsuperobject.biblemap.put("b41_8_30", new hinsuperobject(41,8,30,"तब उस ने उन्हें चिताकर कहा, कि मेरे विषय में यह किसी से न कहना।"));
hinsuperobject.biblemap.put("b41_8_31", new hinsuperobject(41,8,31,"और वह उन्हें सिखाने लगा, कि मनुष्य के पुत्रा के लिये अवश्य है, कि वह बहुत दुख उठाए, और पुरनिए और महायाजक और शास्त्री उसे तुच्छ समझकर मार डालें और वह तीन दिन के बाद जी उठे।"));
hinsuperobject.biblemap.put("b41_8_32", new hinsuperobject(41,8,32,"उस ने यह बात उन से साफ साफ कह दी: इस पर पतरस उसे अलग ले जाकर झिड़कने लगा।"));
hinsuperobject.biblemap.put("b41_8_33", new hinsuperobject(41,8,33,"परन्तु उस ने फिरकर, और अपने चेलों की ओर देखकर पतरस को झिड़कर कर कहा; कि हे शैतान, मेरे साम्हने से दूर हो; क्योंकि तू परमेश्वर की बातों पर नहीं, परन्तु मनुष्य की बातों पर मन लगाता है।"));
hinsuperobject.biblemap.put("b41_8_34", new hinsuperobject(41,8,34,"उस ने भीड़ को अपने चेलों समेत पास बुलाकर उन से कहा, जो कोई मेरे पीछे आना चाहे, वह अपने आपे से इन्कार करे और अपना क्रूस उठाकर, मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b41_8_35", new hinsuperobject(41,8,35,"क्योंकि जो कोई अपना प्राण बचाना चाहे वह उसे खोएगा, पर जो कोई मेरे और सुसमाचार के लिये अपना प्राण खोएगा, वह उसे बचाएगा।"));
hinsuperobject.biblemap.put("b41_8_36", new hinsuperobject(41,8,36,"यदि मनुष्य सारे जगत को प्राप्त करे और अपने प्राण की हानि उठाए, तो उसे क्या लाभ होगा?"));
hinsuperobject.biblemap.put("b41_8_37", new hinsuperobject(41,8,37,"और मनुष्य अपने प्राण के बदले क्या देगा?"));
hinsuperobject.biblemap.put("b41_8_38", new hinsuperobject(41,8,38,"जो कोई इस व्यभिचारी और पापी जाति के बीच मुझ से और मेरी बातों से लजाएगा, मनुष्य का पुत्रा भी जब वह पवित्रा दूतों के साथ अपने पिता की महिमा सहित आएगा, तब उस से भी लजाएगा।"));
hinsuperobject.biblemap.put("b41_9_1", new hinsuperobject(41,9,1,"और उस ने उन से कहा; मैं तुम से सच कहता हूं, कि जो यहां खड़े हैं, उन में से कोई ऐसे हैं, कि जब तक परमेश्वर के राज्य को सामर्थ सहित आता हुआ न देख लें, तब तक मृत्यु का स्वाद कदापि न चखेंगे।।"));
hinsuperobject.biblemap.put("b41_9_2", new hinsuperobject(41,9,2,"छ: दिन के बाद यीशु ने पतरस और याकूब और यूहन्ना को साथ लिया, और एकान्त में किसी ऊंचे पहाड़ पर ले गया; और उन के साम्हने उसका रूप बदल गया।"));
hinsuperobject.biblemap.put("b41_9_3", new hinsuperobject(41,9,3,"और उसका वस्त्रा ऐसा चमकने लगा और यहां तक अति उज्जवल हुआ, कि पृथ्वी पर कोई धोबी भी वैसा उज्जवल नहीं कर सकता।"));
hinsuperobject.biblemap.put("b41_9_4", new hinsuperobject(41,9,4,"और उन्हें मूसा के साथ एलिरयाह दिखाई दिया; और वे यीशु के साथ बातें करते थे।"));
hinsuperobject.biblemap.put("b41_9_5", new hinsuperobject(41,9,5,"इस पर पतरस ने यीशु से कहा; हे रब्बी, हमारा यहां रहना अच्छा है: इसलिये हम तीन मण्डप बनाएं; एक तेरे लिये, एक मूसा के लिये, और एक एलिरयाह के लिये।"));
hinsuperobject.biblemap.put("b41_9_6", new hinsuperobject(41,9,6,"क्योंकि वह न जानता था, कि क्या उत्तर दे; इसलिये कि वे बहुत डर गए थे।"));
hinsuperobject.biblemap.put("b41_9_7", new hinsuperobject(41,9,7,"तब एक बादल ने उन्हें छा लिया, और उस बादल में से यह शब्द निकला, कि यह मेरा प्रिय पुत्रा है; उस की सुनो।"));
hinsuperobject.biblemap.put("b41_9_8", new hinsuperobject(41,9,8,"तब उन्हों ने एकाएक चारों और दृष्टि की, और यीशु को छोड़ अपने साथ और किसी को न देखा।।"));
hinsuperobject.biblemap.put("b41_9_9", new hinsuperobject(41,9,9,"पहाड़ से उतरते हुए, उस ने उन्हें आज्ञा दी, कि जब तक मनुष्य का पुत्रा मरे हुओं में से जी न उठे, तब तक जो कुछ तुम ने देखा है वह किसी से न कहना।"));
hinsuperobject.biblemap.put("b41_9_10", new hinsuperobject(41,9,10,"उन्हों ने इस बात को स्मरण रखा; और आपस में वाद- विवाद करने लगे, कि मरे हुओं में से जी उठने का क्या अर्थ है?"));
hinsuperobject.biblemap.put("b41_9_11", new hinsuperobject(41,9,11,"और उन्हों ने उस से पूछा, शास्त्री क्यों कहते हैं, कि एलिरयाह का पहिले आना अवश्य है?"));
hinsuperobject.biblemap.put("b41_9_12", new hinsuperobject(41,9,12,"उस ने उन्हें उत्तर दिया कि एलिरयाह सचमुच पहिले आकर सब कुछ सुधारेगा, परन्तु मनुष्य के पुत्रा के विषय में यह क्यों लिखा है, कि वह बहुत दुख उठाएगा, और तुच्छ गिना जाएगा?"));
hinsuperobject.biblemap.put("b41_9_13", new hinsuperobject(41,9,13,"परन्तु मैं तुम से कहता हूं, कि एलिरयाह तो आ चुका, और जैसा उसके विषय में लिखा है, उन्हों ने जो कुछ चाहा उसके साथ किया।।"));
hinsuperobject.biblemap.put("b41_9_14", new hinsuperobject(41,9,14,"और जब वह चेलों के पास आया, तो देखा कि उन के चारों और बड़ी भीड़ लगी है और शास्त्री उन के साथ विवाद कर रहें हैं।"));
hinsuperobject.biblemap.put("b41_9_15", new hinsuperobject(41,9,15,"और उसे देखते ही सब बहुत ही आश्चर्य करने लगे, और उस की ओर दौड़कर उसे नमस्कार किया।"));
hinsuperobject.biblemap.put("b41_9_16", new hinsuperobject(41,9,16,"उस ने उन से पूछा; तुम इन से क्या विवाद कर रहे हो?"));
hinsuperobject.biblemap.put("b41_9_17", new hinsuperobject(41,9,17,"भीड़ में से एक ने उसे उत्तर दिया, कि हे गुरू, मैं अपने पुत्रा को, जिस में गूंगी आत्मा समाई है, तेरे पास लाया था।"));
hinsuperobject.biblemap.put("b41_9_18", new hinsuperobject(41,9,18,"जहां कहीं वह उसे पकड़ती है, वहीं पटकर देती है: और वह मुंह में फेन भर लाता, और दांत पीसता, और सूखता जाता है: और मैं ने चेलों से कहा िाा कि वे उसे निकाल दें परन्तु वह निकाल न सके।"));
hinsuperobject.biblemap.put("b41_9_19", new hinsuperobject(41,9,19,"यह सुनकर उस ने उन से उत्तर देके कहा: कि हे अविश्वासी लोगों, मैं कब तक तुम्हारे साथ रहूंगा? और कब तक तुम्हारी सहूंगा? उसे मेरे पास लाओ।"));
hinsuperobject.biblemap.put("b41_9_20", new hinsuperobject(41,9,20,"तब वे उसे उसके पास ले आए: और जब उस ने उसे देखा, तो उस आत्मा ने तुरन्त उसे मरोड़ा; और वह भूमि पर गिरा, और मुंह से फेन बहाते हुए लोटने लगा।"));
hinsuperobject.biblemap.put("b41_9_21", new hinsuperobject(41,9,21,"उस ने उसके पिता से पूछा; इस की यह दशा कब से है?"));
hinsuperobject.biblemap.put("b41_9_22", new hinsuperobject(41,9,22,"उस ने कहा, बचपन से : उस ने इसे नाश करने के लिये कभी आग और कभी पानी में गिराया; परन्तु यदि तू कुछ कर सके, तो हम पर तरस खाकर हमारा उपकार कर।"));
hinsuperobject.biblemap.put("b41_9_23", new hinsuperobject(41,9,23,"यीशु ने उस से कहा; यदि तू कर सकता है; यह क्या बता है? विश्वास करनेवाले के लिये सब कुछ हो सकता है।"));
hinsuperobject.biblemap.put("b41_9_24", new hinsuperobject(41,9,24,"बालक के पिता ने तुरन्त गिड़गिड़ाकर कहा; हे प्रभु, मैं विश्वास करता हूं, मेरे अविश्वास का उपाय कर।"));
hinsuperobject.biblemap.put("b41_9_25", new hinsuperobject(41,9,25,"जब यीशु ने देखा, कि लोग दौड़कर भीड़ लगा रहे हैं, तो उस ने अशुद़्ध आत्म को यह कहकर डांटा, कि हे गूंगी और बहिरी आत्मा, मैं तुझे आज्ञा देता हूं, उस में से निकल आ, और उस में फिर कभी प्रवेश न कर।"));
hinsuperobject.biblemap.put("b41_9_26", new hinsuperobject(41,9,26,"तब वह चिल्लाकर, और उसे बहुत मरोड़ कर, निकल आई; और बालक मरा हुआ सा हो गया, यहां तक कि बहुत लागे कहने लगे, कि वह मर गया।"));
hinsuperobject.biblemap.put("b41_9_27", new hinsuperobject(41,9,27,"परन्तु यीशु ने उसका हाथ पकड़ के उसे उठाया, और वह खड़ा हो गया।"));
hinsuperobject.biblemap.put("b41_9_28", new hinsuperobject(41,9,28,"जब वह घर में आया, तो उसके चेलों ने एकान्त में उस से पूछा, हम उसे क्या न निकाल सके?"));
hinsuperobject.biblemap.put("b41_9_29", new hinsuperobject(41,9,29,"उस ने उन से कहा, कि यह जाति बिना प्रार्थना किसी और उपाय से निकल नहीं सकती।।"));
hinsuperobject.biblemap.put("b41_9_30", new hinsuperobject(41,9,30,"फिर वे वहां से चले, और गलील में होकर जा रहे थे, और वह अपने चेलों को उपदेश देता और उन से कहता था, कि मनुष्य का पुत्रा मनुष्यों के हाथ में पकड़वाया जाएगा, और वे उसे मार डालेंगे, और वह मरने के तीन दिन बाद जी उठेगा।"));
hinsuperobject.biblemap.put("b41_9_31", new hinsuperobject(41,9,31,"पर यह बात उन की समझ में नहीं आई, और वे उस से पूछने से डरते थे।।"));
hinsuperobject.biblemap.put("b41_9_32", new hinsuperobject(41,9,32,"फिर वे कफरनहूम में आए; और घर में आकर उस ने उन से पूछा कि रास्ते में तुम किस बात पर विवाद करते थे?"));
hinsuperobject.biblemap.put("b41_9_33", new hinsuperobject(41,9,33,"वे चुप रहे, क्योंकि मार्ग में उन्हों ने आपस में यह वाद- विवाद किया था, कि हम में से बड़ा कौन है?"));
hinsuperobject.biblemap.put("b41_9_34", new hinsuperobject(41,9,34,"वे चुप रहे, क्योंकि मार्ग में उन्हों ने आपस में यह वाद- विवाद किया था, कि हम में से बड़ा कौन है?"));
hinsuperobject.biblemap.put("b41_9_35", new hinsuperobject(41,9,35,"तब उस ने बैठकर बारहों को बुलाया, और उन से कहा, यदि कोई बड़ा होना चाहे, तो सब से छोटा और सब का सेवक बने।"));
hinsuperobject.biblemap.put("b41_9_36", new hinsuperobject(41,9,36,"और उस ने एक बालक को लेकर उन के बीच में खड़ा किया, और उसके गोद में लेकर उन से कहा।"));
hinsuperobject.biblemap.put("b41_9_37", new hinsuperobject(41,9,37,"जो कोई मेरे नाम से ऐसे बालकों में से किसी एक को भी ग्रहण करता है, वह मुझे ग्रहण करता है; और जो कोई मुझे ग्रहण करता, वह मुझे नहीं, बरन मेरे भेजनेवाले को ग्रहण करता है।।"));
hinsuperobject.biblemap.put("b41_9_38", new hinsuperobject(41,9,38,"तब यूहन्ना ने उस से कहा, हे गुरू हम ने एक मनुष्य को तेरे नाम से दुष्टात्मओं को निकालते देखा और हम उसे मना करने लगे, क्योंकि वह हमारे पीछे नहीं हो लेता था।"));
hinsuperobject.biblemap.put("b41_9_39", new hinsuperobject(41,9,39,"यीशु ने कहा, उस को मत मना करो; क्योंकि ऐसा कोई नहीं जो मेरे नाम से सामर्थ का काम करे, और जल्दी से मुझे बुरा कह सके।"));
hinsuperobject.biblemap.put("b41_9_40", new hinsuperobject(41,9,40,"क्योंकि जो हमारे विरोध में नहीं, वह हमारी ओर है।"));
hinsuperobject.biblemap.put("b41_9_41", new hinsuperobject(41,9,41,"जो कोई एक कटोरा पानी तुम्हें इसलिये पिलाए कि तुम मसीह के हो तो मैं तुम से सच कहता हूं कि वह अपना प्रतिफल किसी रीति से न खोएगा।"));
hinsuperobject.biblemap.put("b41_9_42", new hinsuperobject(41,9,42,"पर जो कोई इन छोटों में से जो मुझ पर विश्वास करते हैं, किसी को ठोकर खिलाए तो उसके लिये भला यह हे कि एक बड़ी चक्की का पाट उसके गले में लटकाया जाए और वह समुद्र में डाल दिया जाए।"));
hinsuperobject.biblemap.put("b41_9_43", new hinsuperobject(41,9,43,"यदि तेरा हाथ तुझे ठोकर खिलाए तो उसे काट डाल टुण्डा होकर जीवन में प्रवेश करना, तेरे लिये इस से भला है कि दो हाथ रहते हुए नरक के बीच उस आग में डाला जाए जो कभी बुझने की नहीं।"));
hinsuperobject.biblemap.put("b41_9_44", new hinsuperobject(41,9,44,"."));
hinsuperobject.biblemap.put("b41_9_45", new hinsuperobject(41,9,45,"और यदि तेरा पांव तुझे ठोकर खिलाए तो उसे काट डाल।"));
hinsuperobject.biblemap.put("b41_9_46", new hinsuperobject(41,9,46,"लंगड़ा होकर जीवन में प्रवेश करना तेरे लिये इस से भला है, कि दो पांव रहते हुए नरक में डाला जाए।"));
hinsuperobject.biblemap.put("b41_9_47", new hinsuperobject(41,9,47,"और यदि तेरी आंख तुझे ठोकर खिलाए तो उसे निकाल डाल, काना होकर परमेश्वर के राज्य में प्रवेश करना तेरे लिये इस से भला है, कि दो आंख रहते हुए तू नरक में डाला जाए।"));
hinsuperobject.biblemap.put("b41_9_48", new hinsuperobject(41,9,48,"जहां उन का कीड़ा नहीं मरता और आग नहीं बुझती।"));
hinsuperobject.biblemap.put("b41_9_49", new hinsuperobject(41,9,49,"क्योंकि हर एक जन आग से नमकीन किया जाएगा।"));
hinsuperobject.biblemap.put("b41_9_50", new hinsuperobject(41,9,50,"नमक अच्छा है, पर यदि नमक की नमकीनी जाती रहे, तो उसे किस से स्वादित करोगे? अपने में नमक रखो, और आपस में मेल मिलाप से रहो।।"));
hinsuperobject.biblemap.put("b41_10_1", new hinsuperobject(41,10,1,"फिर वह वहां से उठकर यहूदिया के सिवानों में और यरदन के पार आया, और भीड़ उसके पास फिर इकट्ठी हो गई, और वह अपनी रीति के अनुसार उन्हें फिर उपदेश देने लगा।"));
hinsuperobject.biblemap.put("b41_10_2", new hinsuperobject(41,10,2,"तब फरीसियों ने उसके पास आकर उस की परीक्षा करने को उस से पूछा, क्या यह उचित है, कि पुरूष अपनी पत्नी को त्यागे?"));
hinsuperobject.biblemap.put("b41_10_3", new hinsuperobject(41,10,3,"उस ने उन को उत्तर दिया, कि मूसा ने तुम्हें क्या आज्ञा दी है?"));
hinsuperobject.biblemap.put("b41_10_4", new hinsuperobject(41,10,4,"उन्हों ने कहा, मूसा ने त्याग पत्रा लिखने और त्यागने की आज्ञा दी है।"));
hinsuperobject.biblemap.put("b41_10_5", new hinsuperobject(41,10,5,"यीशु ने उन से कहा, कि तुम्हारे मन की कठोरता के कारण उस ने तुम्हारे लिये यह आज्ञा लिखी।"));
hinsuperobject.biblemap.put("b41_10_6", new hinsuperobject(41,10,6,"पर सृष्टि के आरम्भ से परमेश्वर ने नर और नारी करके उन को बनाया है।"));
hinsuperobject.biblemap.put("b41_10_7", new hinsuperobject(41,10,7,"इस कारण मनुष्य अपने माता- पिता से अलग होकर अपनी पत्नी के साथ रहेगा, और वे दोनों एक तन होंगे।"));
hinsuperobject.biblemap.put("b41_10_8", new hinsuperobject(41,10,8,"इसलिये वे अब दो नहीं पर एक तन हैं।"));
hinsuperobject.biblemap.put("b41_10_9", new hinsuperobject(41,10,9,"इसलिये जिसे परमेश्वर ने जोड़ा है उसे मनुष्य अलग न करे।"));
hinsuperobject.biblemap.put("b41_10_10", new hinsuperobject(41,10,10,"और घर में चेलों ने इस के विषय में उस से फिर पूछा।"));
hinsuperobject.biblemap.put("b41_10_11", new hinsuperobject(41,10,11,"उस ने उन से कहा, जो कोई अपनी पत्नी को त्यागकर दूसरी से ब्याह करे तो वह उस पहिली के विरोध में व्यभिचार करता है।"));
hinsuperobject.biblemap.put("b41_10_12", new hinsuperobject(41,10,12,"और यदि पत्नी अपने पति को छोड़कर दूसरे से ब्याह करे, तो वह व्यभिचार करता है।"));
hinsuperobject.biblemap.put("b41_10_13", new hinsuperobject(41,10,13,"फिर लोग बालकों को उसके पास लाने लगे, कि वह उन पर हाथ रखे, पर चेलों ने उनको डांटा।"));
hinsuperobject.biblemap.put("b41_10_14", new hinsuperobject(41,10,14,"यीशु ने यह देख क्रुध होकर उन से कहा, बालकों को मेरे पास आने दो और उन्हें मना न करो, क्योंकि परमेश्वर का राज्य ऐसों ही का है।"));
hinsuperobject.biblemap.put("b41_10_15", new hinsuperobject(41,10,15,"मैं तुम से सच कहता हूं, कि जो कोई परमेश्वर के राज्य को बालक की नाई ग्रहण न करे, वह उस में कभी प्रवेश करने न पाएगा।"));
hinsuperobject.biblemap.put("b41_10_16", new hinsuperobject(41,10,16,"और उस ने उन्हें गोद में लिया, और उन पर हाथ रखकर उन्हें आशीष दी।।"));
hinsuperobject.biblemap.put("b41_10_17", new hinsuperobject(41,10,17,"और जब वह निकलकर मार्ग में जाता था, तो एक मनुष्य उसके पास दौड़ता हुआ आया, और उसके आगे घुटने टेककर उस से पूछा? हे उत्तम गुरू, अनन्त जीवन का अधिकारी होने के लिये मैं क्यां करूं?"));
hinsuperobject.biblemap.put("b41_10_18", new hinsuperobject(41,10,18,"यीशु ने उस से कहा, तू मुझे उत्तम क्यों कहता है? कोई उत्तम नहीं, केवल एक अर्थात् परमेश्वर।"));
hinsuperobject.biblemap.put("b41_10_19", new hinsuperobject(41,10,19,"तू आज्ञाओं को तो जानता है; हत्या न करना, व्यभिचार न करना, चोरी न करना, झूठी गवाही न देना, छल न करना, अपने पिता और अपनी माता का आदर करना।"));
hinsuperobject.biblemap.put("b41_10_20", new hinsuperobject(41,10,20,"उस ने उस से कहा, हे गुरू, इन सब को मैं लड़कपन से मानता आया हूं।"));
hinsuperobject.biblemap.put("b41_10_21", new hinsuperobject(41,10,21,"यीशु ने उस पर दृष्टि करके उस से प्रेम किया, और उस से कहा, तुझ में एक बात की घटी है; जा, जो कुछ तेरा है, उसे बेच कर कंगालों को दे, और तुझे स्वर्ग में धन मिलेगा, और आकर मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b41_10_22", new hinsuperobject(41,10,22,"इस बात से उसके चिहरे पर उदासी छा गई, और वह शोक करता हुआ चला गया, क्योंकि वह बहुत धनी था।"));
hinsuperobject.biblemap.put("b41_10_23", new hinsuperobject(41,10,23,"यीशु ने चारों ओर देखकर अपने चेलों से कहा, धनवानों को परमेश्वरके राज्य में प्रवेश करना कैसा कठिन है!"));
hinsuperobject.biblemap.put("b41_10_24", new hinsuperobject(41,10,24,"यीशु ने चारों और देखकर अपने चेलों से कहा, धनवानों को परमेश्वर के राज्य में प्रवेश करना कैसा कठिन है!"));
hinsuperobject.biblemap.put("b41_10_25", new hinsuperobject(41,10,25,"चेले उस की बातों से अचम्भित हुए, इस पर यीशु ने फिर उन को उत्तर दिया, हे बालको, जो धन पर भरोसा रखते हैं, उन के लिये परमेश्वर के राज्य में प्रवेश करना कैसा कठिन है!"));
hinsuperobject.biblemap.put("b41_10_26", new hinsuperobject(41,10,26,"वे बहुत ही चकित होकर आपस में कहने लगे तो फिर किस का उद्धार हो सकता है।"));
hinsuperobject.biblemap.put("b41_10_27", new hinsuperobject(41,10,27,"यीशु ने उन की ओर देखकर कहा, मनुष्यों से तो यह नहीं हो सकता, परन्तु परमेश्वर से हो सकता है; क्योंकि परमेश्वर से सब कुछ हो सकता है।"));
hinsuperobject.biblemap.put("b41_10_28", new hinsuperobject(41,10,28,"पतरस उस से कहने लगा, कि देख, हम तो सब कुछ छोड़कर तेरे पीछे हो लिये हैं।"));
hinsuperobject.biblemap.put("b41_10_29", new hinsuperobject(41,10,29,"यीशु ने कहा, मैं तुम से सच कहता हूं, कि ऐसा कोई नहीं, जिस ने मेरे और सुसमाचार के लिये घर या भाइयों या बहिनों या माता या पिता या लड़के- बालों या खेतों को छोड़ दिया हो।"));
hinsuperobject.biblemap.put("b41_10_30", new hinsuperobject(41,10,30,"और अब इस समय सौ गुणा न पाए, घरों और भाइयों और बहिनों और माताओं और लड़के- बालों और खेतों को पर उपद्रव के साथ और परलोक में अनन्त जीवन।"));
hinsuperobject.biblemap.put("b41_10_31", new hinsuperobject(41,10,31,"पर बहुतेरे जो पहिले हैं, पिछले होंगे; और जो पिछले हैं, वे पहिले होंगे।"));
hinsuperobject.biblemap.put("b41_10_32", new hinsuperobject(41,10,32,"और वे यरूशलेम को जाते हुए मार्ग में थे, और यीशु उन के आगे आगे जा रहा था: और वे अचम्भा करने लगे और जो उसके पीछे पीछे चलते थे डरने लगे, तब वह फिर उन बारहों को लेकर उन से वे बातें कहने लगा, जो उस पर आनेवाली थीं।"));
hinsuperobject.biblemap.put("b41_10_33", new hinsuperobject(41,10,33,"कि देखो, हम यरूशलेम को जाते हैं, और मनुष्य का पुत्रा महायाजकों और शास्त्रियों के हाथ पकड़वाया जाएगा, और वे उस को घात के योग्य ठहराएंगे, और अन्यजातियों के हाथ में सौंपेंगे।"));
hinsuperobject.biblemap.put("b41_10_34", new hinsuperobject(41,10,34,"और वे उस को ठट्ठों में उड़ाएंगे, और उस पर थूकेंगे, और उसे कोड़े मारेंगे, और उसे घात करेंगे, और तीन दिन के बाद वह जी उठेगा।।"));
hinsuperobject.biblemap.put("b41_10_35", new hinsuperobject(41,10,35,"तब जब्दी के पुत्रा याकूब और यूहन्ना ने उसके पास आकर कहा, हे गुरू, हम चाहते हैं, कि जो कुछ हम तुझ से मांगे, वही तू हमारे लिये करे।"));
hinsuperobject.biblemap.put("b41_10_36", new hinsuperobject(41,10,36,"उस ने उन से कहा, तुम क्या चाहते हो कि मैं तुम्हारे लिये करूं?"));
hinsuperobject.biblemap.put("b41_10_37", new hinsuperobject(41,10,37,"उन्हों ने उस से कहा, कि हमें यह दे, कि तेरी महिमा में हम में से एक तेरे दहिने और दूसरा तेरे बांए बैठे।"));
hinsuperobject.biblemap.put("b41_10_38", new hinsuperobject(41,10,38,"यीशु न उन से कहा, तुम नहीं जानते, कि क्या मांगते हो? जो कटोरा मैं पीने पर हूं, क्या पी सकते हो? और जो बपतिस्मा मैं लेने पर हूं, क्या ले सकते हो?"));
hinsuperobject.biblemap.put("b41_10_39", new hinsuperobject(41,10,39,"उन्हों ने उस से कहा, हम से हो सकता है: यीशु ने उन से कहा: जो कटोरा मैं पीने पर हूं, तुम पीओंगे; और जो बपतिस्मा मैं लेने पर हूं, उसे लोगे।"));
hinsuperobject.biblemap.put("b41_10_40", new hinsuperobject(41,10,40,"पर जिन के लिये तैयार किया गया है, उन्हें छोड़ और किसी को अपने दहिने और अपने बाएं बिठाना मेरा काम नहीं।"));
hinsuperobject.biblemap.put("b41_10_41", new hinsuperobject(41,10,41,"यह सुनकर दसों याकूब और यूहन्ना पर रिसियाने लगे।"));
hinsuperobject.biblemap.put("b41_10_42", new hinsuperobject(41,10,42,"और यीशु ने उन को पास बुला कर उन से कहा, तुम जानते हो, कि जो अन्यजातियों के हाकिम समझे जाते हैं, वे उन पर प्रभुता करते हैं; और उन में जो बड़ें हैं, उन पर अधिकार जताते हैं।"));
hinsuperobject.biblemap.put("b41_10_43", new hinsuperobject(41,10,43,"पर तुम में ऐसा नहीं है, बरन जो कोई तुम में बड़ा होना चाहे वह तुम्हारा सेवक बने।"));
hinsuperobject.biblemap.put("b41_10_44", new hinsuperobject(41,10,44,"और जो कोई तुम में प्रधान होना चाहे, वह सब का दास बने।"));
hinsuperobject.biblemap.put("b41_10_45", new hinsuperobject(41,10,45,"क्योंकि मनुष्य का पुत्रा इसलिये नहीं आया, कि उस की सेवा अहल की जाए, पर इसलिये आया, कि आप सेवा टहल करे, और बहुतों की छुड़ौती के लिये अपना प्राण दे।।"));
hinsuperobject.biblemap.put("b41_10_46", new hinsuperobject(41,10,46,"और वे यरीहो में आए, और जब वह और उसके चेले, और एक बड़ी भीड़ यरीहो से निकलती थी, तो तिमाई का पुत्रा बरतिमाई एक अन्धा भिखारी सड़क के किनारे बैठा था।"));
hinsuperobject.biblemap.put("b41_10_47", new hinsuperobject(41,10,47,"वह यह सुनकर कि यीशु नासरी है, पुकार पुकार कर कहने लगा; कि हे दाऊद की सन्तान, यीशु मुझ पर दया कर।"));
hinsuperobject.biblemap.put("b41_10_48", new hinsuperobject(41,10,48,"बहुतों ने उसे डांटा कि चुप रहे, पर वह और भी पुकारने लगा, कि हे दाऊद की सन्तान, मुझ पर दया कर।"));
hinsuperobject.biblemap.put("b41_10_49", new hinsuperobject(41,10,49,"तब यीशु ने ठहरकर कहा, उसे बुलाओ; और लोगों ने उस अन्धे को बुलाकर उस से कहा, ढाढ़स बान्ध, उठ, वह तुझे बुलाता है।"));
hinsuperobject.biblemap.put("b41_10_50", new hinsuperobject(41,10,50,"वह अपना कपड़ा फेंककर शीघ्र उठा, और यीशु के पास आया।"));
hinsuperobject.biblemap.put("b41_10_51", new hinsuperobject(41,10,51,"इस पर यीशु ने उस से कहा; तू क्या चाहता है कि मैं तेरे लिये करूं? अन्धे ने उस से कहा, हे रब्बी, यह कि मैं देखने लगूं।"));
hinsuperobject.biblemap.put("b41_10_52", new hinsuperobject(41,10,52,"यीशु ने उस से कहा; चला जा, तेरे विश्वास ने तुझे चंगा कर दिया है: और वह तुरन्त देखने लगा, और मार्ग में उसके पीछे हो लिया।।"));
hinsuperobject.biblemap.put("b41_11_1", new hinsuperobject(41,11,1,"जब वे यरूशलेम के निकट जैतून पहाड़ पर बैतफगे और बैतनिरयाह के पास आए, तो उस ने अपने चेलों में से दो को यह कहकर भेजा।"));
hinsuperobject.biblemap.put("b41_11_2", new hinsuperobject(41,11,2,"कि अपने साम्हने के गांव में जाओ, और उस में पंहुचते ही एक गदही का बच्चा जिस पर कभी कोई नहीं चढ़ा, बन्धाहुआ तुम्हें मिलेगा, उसे खोल लाओ।"));
hinsuperobject.biblemap.put("b41_11_3", new hinsuperobject(41,11,3,"यदि तुम से कोई पूछे, यह क्यों करते हो? तो कहना, कि प्रभु को इस का प्रयोजन है; और वह शीघ्र उसे यहां भेज देगा।"));
hinsuperobject.biblemap.put("b41_11_4", new hinsuperobject(41,11,4,"उन्हों ने जाकर उस बच्चे को बाहर द्वार के पास चौक में बन्धा हुआ पाया, और खोलते लगे।"));
hinsuperobject.biblemap.put("b41_11_5", new hinsuperobject(41,11,5,"और उन में से जो वहां खड़े थे, कोई कोई कहने लगे कि यह क्या करते हो, गदही के बच्चे को क्यों खेलते हो?"));
hinsuperobject.biblemap.put("b41_11_6", new hinsuperobject(41,11,6,"उन्हों ने जैसा यीशु ने कहा था, वैसा ही उन से कह दिया; तब उन्हों ने उन्हें जाने दिया।"));
hinsuperobject.biblemap.put("b41_11_7", new hinsuperobject(41,11,7,"और उन्हों ने बच्चे को यीशु के पास लाकर उस पर अपने कपड़े डाले और वह उस पर बैठ गया।"));
hinsuperobject.biblemap.put("b41_11_8", new hinsuperobject(41,11,8,"और बहुतों ने अपने कपड़े मार्ग में बिछाए और औरों ने खेतों में से डालियां काट काट कर फैला दीं।"));
hinsuperobject.biblemap.put("b41_11_9", new hinsuperobject(41,11,9,"और जो उसके आगे आगे जाते और पीछे पीछे चले आते थे, पुकार पुकार कर कहते जाते थे, कि होशाना; धन्य है वह जो प्रभु के नाम से आता है।"));
hinsuperobject.biblemap.put("b41_11_10", new hinsuperobject(41,11,10,"हमारे पिता दाऊद का राज्य जो आ रहा है; धन्य है: आकाश में होशाना।।"));
hinsuperobject.biblemap.put("b41_11_11", new hinsuperobject(41,11,11,"और वह यरूशलेम पहुंचकर मन्दिर में आया, और चारों ओर सब वस्तुओं को देखकर बारहों के साथ बैतनिरयाह गया क्योंकि सांझ हो गई थी।।"));
hinsuperobject.biblemap.put("b41_11_12", new hinsuperobject(41,11,12,"दूसरे दिन जब वे बैतनिरयाह से निकले तो उस को भूख लगी।"));
hinsuperobject.biblemap.put("b41_11_13", new hinsuperobject(41,11,13,"और वह दूर से अंजीर का एक हरा पेड़ देखकर निकट गया, कि क्या जाने उस में कुछ पाए: पर पत्तों को छोड़ कुछ न पाया; क्योंकि फल का समय न था।"));
hinsuperobject.biblemap.put("b41_11_14", new hinsuperobject(41,11,14,"इस पर उस ने उस से कहा अब से कोई तेरा फल कभी न खाए। और उसके चेले सुन रहे थे।"));
hinsuperobject.biblemap.put("b41_11_15", new hinsuperobject(41,11,15,"फिर वे यरूशलेम में आए, और वह मन्दिर में गया; और वहां जो लेन- देन कर रहे थे उन्हें बाहर निकालने लगा, और सर्राफों के पीढ़े और कबूतर के बेचनेवालों की चौकियां उलट दीं।"));
hinsuperobject.biblemap.put("b41_11_16", new hinsuperobject(41,11,16,"और मन्दिर में से होकर किसी को बरतन लेकर आने जाने न दिया।"));
hinsuperobject.biblemap.put("b41_11_17", new hinsuperobject(41,11,17,"और उपदेश करके उन से कहा, क्या यह नहीं लिखा है, कि मेरा घर सब जातियों के लिये प्रार्थना का घर कहलाएगा? पर तुम ने इसे डाकुओं की खोह बना दी है।"));
hinsuperobject.biblemap.put("b41_11_18", new hinsuperobject(41,11,18,"यह सुनकर महायाजक और शास्त्री उसके नाश करने का अवसर ढूंढ़ने लगे; क्योंकि उस से डरते थे, इसलिये कि सब लोग उसके उपदेश से चकित होते थे।।"));
hinsuperobject.biblemap.put("b41_11_19", new hinsuperobject(41,11,19,"और प्रति दिन सांझ होते ही वह नगर से बाहर जाया करता था।"));
hinsuperobject.biblemap.put("b41_11_20", new hinsuperobject(41,11,20,"फिर भोर को जब वे उधर से जाते थे तो उन्हों ने उस अंजीर के पेड़ को जड़ तक सूखा हुआ देखा।"));
hinsuperobject.biblemap.put("b41_11_21", new hinsuperobject(41,11,21,"पतरस को वह बात स्मरण आई, और उस ने उस से कहा, हे रब्बी, देख, यह अंजीर का पेड़ जिसे तू ने स्राप दिया था सूख गया है।"));
hinsuperobject.biblemap.put("b41_11_22", new hinsuperobject(41,11,22,"यीशु ने उस को उत्तर दिया, कि परमेश्वर पर विश्वास रखो।"));
hinsuperobject.biblemap.put("b41_11_23", new hinsuperobject(41,11,23,"मैं तुम से सच कहता हूं कि जो कोई इस पहाड़ से कहे; कि तू उखड़ जा, और समुद्र में जा पड़, और अपने मन में सन्देह न करे, वरन प्रतीति करे, कि जो कहता हूं वह हो जाएगा, तो उसके लिये वही होगा।"));
hinsuperobject.biblemap.put("b41_11_24", new hinsuperobject(41,11,24,"इसलिये मैं तुम से कहता हूं, कि जो कुछ तुम प्रार्थना करके मांगों तो प्रतीति कर लो कि तुम्हें मिल गया, और तुम्हारे लिये हो जाएगा।"));
hinsuperobject.biblemap.put("b41_11_25", new hinsuperobject(41,11,25,"और जब कभी तुम खड़े हुए प्रार्थना करते हो, तो यदि तुम्हारे मन में किसी की और से कुछ विरोध, हो तो क्षमा करो: इसलिये कि तुम्हारा स्वर्गीय पिता भी तुम्हारे अपराध क्षमा करे।।"));
hinsuperobject.biblemap.put("b41_11_26", new hinsuperobject(41,11,26,"और यदि तुम क्षमा न करो तो तुम्हारा पिता भी जो स्वर्ग में है, तुम्हारा अपराध क्षमा न करेगा।"));
hinsuperobject.biblemap.put("b41_11_27", new hinsuperobject(41,11,27,"वे फिर यरूशलेम में आए, और जब वह मन्दिर में टहल रहा था तो महायाजक और शास्त्री और पुरनिए उसके पास आकर पूछने लगे।"));
hinsuperobject.biblemap.put("b41_11_28", new hinsuperobject(41,11,28,"कि तू ये काम किस अधिकार से करता है? और यह अधिकार तुझे किस ने दिया है कि तू ये काम करे?"));
hinsuperobject.biblemap.put("b41_11_29", new hinsuperobject(41,11,29,"यीशु ने उस से कहा: मैं भी तुम से एक बात पूछता हूं; मुझे उत्तर दो: तो मैं तुम्हें बताऊंगा कि ये काम किस अधिकार से करता हूं।"));
hinsuperobject.biblemap.put("b41_11_30", new hinsuperobject(41,11,30,"यूहन्ना का बपतिस्मा क्या स्वर्ग की ओर से था वा मनुष्यों की ओर से था? मुझे उत्तर दो।"));
hinsuperobject.biblemap.put("b41_11_31", new hinsuperobject(41,11,31,"तब वे आपस में विवाद करने लगे कि यदि हम कहें, स्वर्ग की ओर से, तो वह कहेगा; फिर तुम ने उस की प्रतीति क्यों नहीं की?"));
hinsuperobject.biblemap.put("b41_11_32", new hinsuperobject(41,11,32,"और यदि हम कहें, मनुष्यों की ओर से तो लोगों का डर है, क्योंकि सब जानते हैं कि यूहन्ना सचमुच भविष्यद्वक्ता है।"));
hinsuperobject.biblemap.put("b41_11_33", new hinsuperobject(41,11,33,"सो उन्हों ने यीशु को उत्तर दिया, कि हम नहीं जानते : यीशु ने उन से कहा, मैं भी तुम को नहीं बताता, कि ये काम किस अधिकार से करता हूं।।"));
hinsuperobject.biblemap.put("b41_12_1", new hinsuperobject(41,12,1,"फिर वह दृष्टान्त में उन से बातें करने लगा: कि किसी मनुष्य ने दाख की बारी लगाई, और उसके चारों ओर बाड़ा बान्धा, और रस का कुंड खोदा, और गुम्मट बनाया; और किसानों को उसका ठीका देकर परदेश चला गया।"));
hinsuperobject.biblemap.put("b41_12_2", new hinsuperobject(41,12,2,"फिर फल के मौसम में उस ने किसानों के पास एक दास को भेजा कि किसान से दाख की बारी के फलों का भाग ले।"));
hinsuperobject.biblemap.put("b41_12_3", new hinsuperobject(41,12,3,"पर उन्हों ने उसे पकड़कर पीटा और छूछे हाथ लौटा दिया।"));
hinsuperobject.biblemap.put("b41_12_4", new hinsuperobject(41,12,4,"फिर उस ने एक और दास को उन के पास भेजा और उन्हों ने उसका सिर फोड़ डाला और उसका अपमान किया।"));
hinsuperobject.biblemap.put("b41_12_5", new hinsuperobject(41,12,5,"फिर उस ने एक और को भेजा, और उन्हों ने उसे मार डाला: तब उस ने और बहुतों को भेजा: उन में से उन्हों ने कितनो को पीटा, और कितनों को मार डाला।"));
hinsuperobject.biblemap.put("b41_12_6", new hinsuperobject(41,12,6,"अब एक ही रह गया था, जो उसका प्रिय पुत्रा था; अन्त में उस ने उसे भी उन के पास यह सोचकर भेजा कि वे मेरे पुत्रा का आदर करेंगे।"));
hinsuperobject.biblemap.put("b41_12_7", new hinsuperobject(41,12,7,"पर उन किसानों ने आपस में कहा; यही तो वारिस है; आओ, हम उसे मार डालें, तब मीरास हमारी हो जाएगी।"));
hinsuperobject.biblemap.put("b41_12_8", new hinsuperobject(41,12,8,"और उन्हों ने उसे पकड़कर मार डाला, और दाख की बारी के बाहर फेंक दिया।"));
hinsuperobject.biblemap.put("b41_12_9", new hinsuperobject(41,12,9,"इसलिये दाख की बारी का स्वामी क्या करेगा? वह आकर उन किसानों को नाश करेगा, और दाख की बारी औरों को दे देगा।"));
hinsuperobject.biblemap.put("b41_12_10", new hinsuperobject(41,12,10,"क्या तुम ने पवित्रा शास्त्रा में यह वचन नहीं पढ़ा, कि जिस पत्थर को राजमिस्त्रायों ने निकम्मा ठहराया था, वही कोने का सिरा हो गया?"));
hinsuperobject.biblemap.put("b41_12_11", new hinsuperobject(41,12,11,"यह प्रभु की ओर से हुआ, और हमारी दुष्टि मे अद्भुत है।"));
hinsuperobject.biblemap.put("b41_12_12", new hinsuperobject(41,12,12,"तब उन्हों ने उसे पकड़ना चाहा; क्योंकि समझ गए थे, कि उस ने हमारे विरोध में यह दृष्टान्त कहा है: पर वे लोगों से डरे; और उसे छोड़ कर चले गए।।"));
hinsuperobject.biblemap.put("b41_12_13", new hinsuperobject(41,12,13,"तब उन्हों ने उसे बातों में फंसाने के लिये कई एक फरीसियों और हेरोदियों को उसके पास भेजा।"));
hinsuperobject.biblemap.put("b41_12_14", new hinsuperobject(41,12,14,"और उन्हों ने आकर उस से कहा; हे गुरू, हम जानते हैं, कि तू सच्चा है, और किसी की परवा नहीं करता; क्योंकि तू मनुष्यों का मुंह देख कर बातें नहीं करता, परन्तु परमेश्वर का मार्ग सच्चाई से बताता है।"));
hinsuperobject.biblemap.put("b41_12_15", new hinsuperobject(41,12,15,"तो क्या कैसा को कर देना उचित है, कि नहीं? हम दें, या न दें? उस ने उन का कपट जानकर उन से कहा; मुझे क्यों परखते हो? एक दीनार मेरे पास लाओ, कि मैं देखूं।"));
hinsuperobject.biblemap.put("b41_12_16", new hinsuperobject(41,12,16,"वे ले आए, और उस ने उन से कहा; यह मूर्ति और नाम किस का है? उन्हों ने कहा, कैसर का।"));
hinsuperobject.biblemap.put("b41_12_17", new hinsuperobject(41,12,17,"यीशु ने उन से कहा; जो कैसर का है वह कैसर को, और जो परमेश्वर का है परमेश्वर को दो: तब वे उस पर बहुत अचम्भा करने लगे।।"));
hinsuperobject.biblemap.put("b41_12_18", new hinsuperobject(41,12,18,"फिर सदूकियों ने भी, जो कहते हैं कि मरे हुओं का जी उठना है ही नहीं, उसके पास आकर उसे पूछा।"));
hinsuperobject.biblemap.put("b41_12_19", new hinsuperobject(41,12,19,"कि हे गुरू, मूसा ने हमारे लिये लिखा है, कि यदि किसी का भाई बिना सन्तान मर जाए, और उस की पत्नी रह जाए, तो उसका भाई उस की पत्नी को ब्याह ले और अपने भाई के लिये वंश उत्पन्न करे : सात भाई थे।"));
hinsuperobject.biblemap.put("b41_12_20", new hinsuperobject(41,12,20,"पहिला भाई ब्याह करके बिना सन्तान मर गया।"));
hinsuperobject.biblemap.put("b41_12_21", new hinsuperobject(41,12,21,"तब दूसरे भाई ने उस स्त्री को ब्याह लिया और बिना सन्तान मर गया; और वैसे ही तीसरे ने भी।"));
hinsuperobject.biblemap.put("b41_12_22", new hinsuperobject(41,12,22,"और सातों से सन्तान न हुई: सब के पीछे वह स्त्री भी मर गई।"));
hinsuperobject.biblemap.put("b41_12_23", new hinsuperobject(41,12,23,"सो जी उठने पर वह उन में से किस की पत्नी होगी? क्योंकि वह सातों की पत्नी हो चुकी थी।"));
hinsuperobject.biblemap.put("b41_12_24", new hinsuperobject(41,12,24,"यीशु ने उन से कहा; क्या तुम इस कारण से भूल में नहीं पड़े हो, कि तुम न तो पवित्रा शास्त्रा ही को जानते हो, और न परमेश्वर की सामर्थ को।"));
hinsuperobject.biblemap.put("b41_12_25", new hinsuperobject(41,12,25,"क्योंकि जब वे मरे हुओं में से जी उठेंगे, तो उन में ब्याह शादी न होगी; पर स्वर्ग में दूतों की नाई होंगे।"));
hinsuperobject.biblemap.put("b41_12_26", new hinsuperobject(41,12,26,"मरे हुओं के जी उठने के विषय में क्या तुम ने मूसा की पुस्तक में झाड़ी की कथा में नही पढ़ा, कि परमेश्वर ने उस से कहा, मैं इब्राहीम का परमेश्वर, और इसहाक का परमेश्वर, और याकूब का परमेश्वर हूं?"));
hinsuperobject.biblemap.put("b41_12_27", new hinsuperobject(41,12,27,"परमेश्वर मरे हुओं का नहीं, वरन जीवतों का परमेश्वर है: सो तुम बड़ी भूल में पड़े हो।।"));
hinsuperobject.biblemap.put("b41_12_28", new hinsuperobject(41,12,28,"और शास्त्रियों में से एक ने आकर उन्हें विवाद करते सुना, और यह जानकर कि उस ने उन्हें अच्छी रीति से उत्तर दिया; उस से पूछा, सब से मुख्य आज्ञा कौन सी है?"));
hinsuperobject.biblemap.put("b41_12_29", new hinsuperobject(41,12,29,"यीशु ने उसे उत्तर दिया, सब आज्ञाओं में से यह मुख्य है; हे इस्राएल सुन; प्रभु हमारा परमेश्वर एक ही प्रभु है।"));
hinsuperobject.biblemap.put("b41_12_30", new hinsuperobject(41,12,30,"और तू प्रभु अपने परमेश्वर से अपने सारे मन से और अपने सारे प्राण से, और अपनी सारी बुद्धि से, और अपनी सारी शक्ति से प्रेम रखना।"));
hinsuperobject.biblemap.put("b41_12_31", new hinsuperobject(41,12,31,"और दूसरी यह है, कि तू अपने पड़ोसी से अपने समान प्रेम रखना: इस से बड़ी और कोई आज्ञा नहीं।"));
hinsuperobject.biblemap.put("b41_12_32", new hinsuperobject(41,12,32,"शास्त्री ने उस से कहा; हे गुरू, बहुत ठीक! तू ने सच कहा, कि वह एक ही है, और उसे छोड़ और कोई नहीं।"));
hinsuperobject.biblemap.put("b41_12_33", new hinsuperobject(41,12,33,"और उस से सारे मन और सारी बुद्धि और सारे प्राण और सारी शक्ति के साथ प्रेम रखना और पड़ोसी से अपने समान प्रेम रखना, सारे होमों और बलिदानों से बढ़कर है।"));
hinsuperobject.biblemap.put("b41_12_34", new hinsuperobject(41,12,34,"जब यीशु ने देखा कि उस ने समझ से उत्तर दिया, तो उस से कहा; तू परमेश्वर के राज्य से दूर नहीं: और किसी को फिर उस से कुछ पूछने का साहस न हुआ।।"));
hinsuperobject.biblemap.put("b41_12_35", new hinsuperobject(41,12,35,"फिर यीशु ने मन्दिर में उपदेश करते हुए यह कहा, कि शास्त्री क्योंकर कहते हैं, कि मसीह दाऊद का पुत्रा है?"));
hinsuperobject.biblemap.put("b41_12_36", new hinsuperobject(41,12,36,"दाऊद ने आपही पवित्रा आत्मा में होकर कहा है, कि प्रभु ने मेरे प्रभु से कहा; मेरे दाहिने बैठ, जब तक कि मैं तेरे बैरियों को तेरे पांवों की पीढ़ी न कर दूं।"));
hinsuperobject.biblemap.put("b41_12_37", new hinsuperobject(41,12,37,"दाऊद तो आप ही उसे प्रभु कहता है, फिर वह उसका पुत्रा कहां से ठहरा? और भीड़ के लोग उस की आनन्द से सुनते थे।।"));
hinsuperobject.biblemap.put("b41_12_38", new hinsuperobject(41,12,38,"उस ने अपने उपदेश में उन से कहा, शस्त्रियों से चौकस रहो, जो लम्बे वस्त्रा पहिने हुए फिरना।"));
hinsuperobject.biblemap.put("b41_12_39", new hinsuperobject(41,12,39,"और बाजारों में नमस्कार, और आराधनालयों में मुख्य मुख्य आसन और जेवनारों में मुख्य मुख्य स्थान भी चाहते हैं।"));
hinsuperobject.biblemap.put("b41_12_40", new hinsuperobject(41,12,40,"वे विधवाओं के घरों को खा जाते हैं, और दिखाने के लिये बड़ी देर तक प्रार्थना करते रहते हैं, ये अधिक दण्ड पाएंगे।।"));
hinsuperobject.biblemap.put("b41_12_41", new hinsuperobject(41,12,41,"और वह मन्दिर के भण्डार के साम्हने बैठकर देख रहा था, कि लोग मन्दिर के भण्डार में किस प्रकार पैसे डालते हैं, और बहुत धनवानों ने बहुत कुछ डाला।"));
hinsuperobject.biblemap.put("b41_12_42", new hinsuperobject(41,12,42,"इतने में एक कंगाल विधवा ने आकर दो दमड़ियां, जो एक अधेले के बराबर होती है, डाली।"));
hinsuperobject.biblemap.put("b41_12_43", new hinsuperobject(41,12,43,"तब उस ने अपने चेलों को पास बुलाकर उन से कहा; मैं तुम से सच कहता हूं, कि मन्दिर के भण्डार में डालने वालों में से इस कंगाल विधवा ने सब से बढ़कर डाला है।"));
hinsuperobject.biblemap.put("b41_12_44", new hinsuperobject(41,12,44,"क्योंकि सब ने अपने धन की बढ़ती में से डाला है, परन्तु इस ने अपनी घटी में से जो कुछ उसका था, अर्थात् अपनी सारी जीविका डाल दी है।"));
hinsuperobject.biblemap.put("b41_13_1", new hinsuperobject(41,13,1,"जब वह मन्दिर से निकल रहा था, तो उसके चलों में से एक ने उस से कहा; हे गुरू, देख, कैसे कैसे पत्थर और कैसे कैसे भवन हैं!"));
hinsuperobject.biblemap.put("b41_13_2", new hinsuperobject(41,13,2,"यीशु ने उस से कहा; क्या तुम ये बड़े बड़े भवन देखते हो: यहां पत्थर पर पत्थर भी बचा न रहेगा जो ढाया न जाएगा।।"));
hinsuperobject.biblemap.put("b41_13_3", new hinsuperobject(41,13,3,"जब वह जैतून के पहाड़ पर मन्दिर के साम्हने बैठा था, तो पतरस और याकूब और यूहन्ना और अन्द्रियास ने अलग जाकर उस से पूछा।"));
hinsuperobject.biblemap.put("b41_13_4", new hinsuperobject(41,13,4,"कि हमें बता कि ये बातें कब होंगी? और जब ये सब बातें पूरी होने पर होंगी उस समय का क्या चिन्ह होगा?"));
hinsuperobject.biblemap.put("b41_13_5", new hinsuperobject(41,13,5,"यीशु उन से कहने लगा; चौकस रहो कि कोई तुम्हें न भरमाए।"));
hinsuperobject.biblemap.put("b41_13_6", new hinsuperobject(41,13,6,"बहुतेरे मेरे नाम से आकर कहेंगे, कि मैं वही हूं और बहुतों को भरमाएंगे।"));
hinsuperobject.biblemap.put("b41_13_7", new hinsuperobject(41,13,7,"और जब तुम लड़ाइयां, और लड़ाइयों की चर्चा सुनो; तो न घबराना: क्योंकि इन का होना अवश्य है; परन्तु उस समय अन्त न होगा।"));
hinsuperobject.biblemap.put("b41_13_8", new hinsuperobject(41,13,8,"क्योंकि जाति पर जाति, और राज्य पर राज्य चढ़ाई करेगा, और हर कहीं भूईंडोल होंगे, और अकाल पडेंगे; यह तो पीड़ाओं का आरम्भ ही होगा।।"));
hinsuperobject.biblemap.put("b41_13_9", new hinsuperobject(41,13,9,"परन्तु तुम अपने विषय में चौकस रहो; क्योंकि लोग तुम्हें महासभाओं में सौंपेंगे और तुम पंचायतों में पीटे जाओगे; और मेरे कारण हाकिमों और राजाओं के आगे खड़े किए जाओगे, ताकि उन के लिये गवाही हो।"));
hinsuperobject.biblemap.put("b41_13_10", new hinsuperobject(41,13,10,"पर अवश्य है कि पहिले सुसमाचार सब जातियों में प्रचार किया जाए।"));
hinsuperobject.biblemap.put("b41_13_11", new hinsuperobject(41,13,11,"जब वे तुम्हें ले जाकर सौंपेंगे, तो पहिले से चिन्ता न करना, कि हम क्या कहेंगे; पर जो कुछ तुम्हें उसी घड़ी बताया जाए, वही कहना; क्योंकि बोलनेवाले तुम नहीं हो, परन्तु पवित्रा आत्मा है।"));
hinsuperobject.biblemap.put("b41_13_12", new hinsuperobject(41,13,12,"और भाई को भाई, और पिता को पुत्रा घात के लिये सौंपेंगे, और लड़केबाले माता- पिता के विरोध में उठकर उन्हें मरवा डालेंगे।"));
hinsuperobject.biblemap.put("b41_13_13", new hinsuperobject(41,13,13,"और मेरे नाम के कारण सब लोग तुम से बैर करेंगे; पर जो अन्त तक धीरज धरे रहेगा, उसी का उद्धार होगा।।"));
hinsuperobject.biblemap.put("b41_13_14", new hinsuperobject(41,13,14,"सो जब तुम उस उजाड़नेवाली घृणित वस्तु को जहां उचित नहीं वहां खड़ी देखो, (पढ़नेवाला समझ ले) तब जो यहूदिया में हों, वे पहाड़ों पर भाग जाएं।"));
hinsuperobject.biblemap.put("b41_13_15", new hinsuperobject(41,13,15,"सो कोठे पर हो, वह अपने घर से कुछ लेने को नीचे न उतरे और न भीतर जाए।"));
hinsuperobject.biblemap.put("b41_13_16", new hinsuperobject(41,13,16,"और जो खेत में हो, वह अपना कपड़ा लेने के लिये पीछे न लौटे।"));
hinsuperobject.biblemap.put("b41_13_17", new hinsuperobject(41,13,17,"उन दिनों में जो गर्भवती और दूध पिलाती होंगी, उन के लिये हाय हाय!"));
hinsuperobject.biblemap.put("b41_13_18", new hinsuperobject(41,13,18,"और प्रार्थना किया करो कि यह जाड़े में न हो।"));
hinsuperobject.biblemap.put("b41_13_19", new hinsuperobject(41,13,19,"क्योंकि वे दिन ऐसे क्लेश के होंगे, कि सृष्टि के आरम्भ से जो परमेश्वर ने सृजी है अब तक न तो हुए, और न कभी फिर होंगे।"));
hinsuperobject.biblemap.put("b41_13_20", new hinsuperobject(41,13,20,"और यदि प्रभु उन दिनों को न घटाता, तो कोई प्राणी भी न बचता; परन्तु उन चुने हुओं के कारण जिन को उस ने चुना है, उन दिनों को घटाया।"));
hinsuperobject.biblemap.put("b41_13_21", new hinsuperobject(41,13,21,"उस समय यदि कोई तुम से कहे; देखो, मसीह यहां है, या देखो, वहां है, तो प्रतीति न करना।"));
hinsuperobject.biblemap.put("b41_13_22", new hinsuperobject(41,13,22,"क्योंकि झूठे मसीह और झूठे भविष्यद्वक्ता उठ खड़े होंगे, और चिन्ह और अद्भुत काम दिखाएंगे कि यदि हो सके तो चुने हुओं को भी भरमा दें।"));
hinsuperobject.biblemap.put("b41_13_23", new hinsuperobject(41,13,23,"पर तुम चौकस रहो: देखो, मैं ने तुम्हें सब बातें पहिले ही से कह दी हैं।"));
hinsuperobject.biblemap.put("b41_13_24", new hinsuperobject(41,13,24,"उन दिनों में, उस क्लेश के बाद सूरज अन्धेरा हो जाएगा, और चान्द प्रकाश न देगा।"));
hinsuperobject.biblemap.put("b41_13_25", new hinsuperobject(41,13,25,"और आकाश से तारागण गिरने लगेंगे: और आकाश की शक्तियां हिलाई जाएंगेी।"));
hinsuperobject.biblemap.put("b41_13_26", new hinsuperobject(41,13,26,"तब लोग मनुष्य के पुत्रा को बड़ी सामर्थ और महिमा के साथ बादलों में आते देखंेगे।"));
hinsuperobject.biblemap.put("b41_13_27", new hinsuperobject(41,13,27,"उस समय वह अपने दूतों को भेजकर, पृथ्वी के इस छोर से आकाश की उस छोर तक चारों दिशा से अपने चुने हुए लोगों को इकट्ठे करेगा।"));
hinsuperobject.biblemap.put("b41_13_28", new hinsuperobject(41,13,28,"अंजीर के पेड़ से यह दृष्टान्त सीखो: जब उस की डाली कोमल हो जाती; और पत्ते निकलने लगते हैं; तो तुम जान लेते हो, कि ग्रीष्मकाल निकट है।"));
hinsuperobject.biblemap.put("b41_13_29", new hinsuperobject(41,13,29,"इसी रीति से जब तुम इन बातों को होते देखो, तो जान लो, कि वह निकट है वरन द्वार ही पर है।"));
hinsuperobject.biblemap.put("b41_13_30", new hinsuperobject(41,13,30,"मैं तुम से सच कहता हूं, कि जब तक ये सब बातें न हो लेंगी, तब तक यह लोग जाते न रहेंगे।"));
hinsuperobject.biblemap.put("b41_13_31", new hinsuperobject(41,13,31,"आकाश और पृथ्वी अल जाएंगे, परन्तु मेरी बातें कभी न टलेंगी।"));
hinsuperobject.biblemap.put("b41_13_32", new hinsuperobject(41,13,32,"उस दिन या उस घड़ी के विषय में कोई नहीं जानता, न स्वर्ग के दूत और न पुत्रा; परन्तु केवल पिता।"));
hinsuperobject.biblemap.put("b41_13_33", new hinsuperobject(41,13,33,"देखो, जागते और प्रार्थना करते रहो; क्योंकि तुम नहीं जानते कि वह समय कब आएगा।"));
hinsuperobject.biblemap.put("b41_13_34", new hinsuperobject(41,13,34,"यह उस मनुष्य की सी दशा है, जो परदेश जाते समय अपना घर छोड़ जाए, और अपने दासों को अधिकार दे: और हर एक को उसका काम जता दे, और द्वारपाल को जागते रहने की आज्ञा दे।"));
hinsuperobject.biblemap.put("b41_13_35", new hinsuperobject(41,13,35,"इसलिये जागते रहो; क्योंकि तुम नहीं जानते कि घर का स्वामी कब आएगा, सांझ को या आधी रात को, या मुर्ग के बांग देने के समय या भोर को।"));
hinsuperobject.biblemap.put("b41_13_36", new hinsuperobject(41,13,36,"ऐसा न हो कि वह अचानक आकर तुम्हें सोते पाए।"));
hinsuperobject.biblemap.put("b41_13_37", new hinsuperobject(41,13,37,"और जो मैं तुम से कहता हूं, वही सब से कहता हूं, जागते रहो।।"));
hinsuperobject.biblemap.put("b41_14_1", new hinsuperobject(41,14,1,"दो दिन के बाद फसह और अखमीरी रोटी का पर्व्व होनेवाला था: और महायाजक और शास्त्री इस बात की खोज में थे कि उसे क्योंकर छल से पकड़ कर मार डालें।"));
hinsuperobject.biblemap.put("b41_14_2", new hinsuperobject(41,14,2,"परन्तु कहते थे, कि पर्व्व के दिन नहीं, कहीं ऐसा न हो कि लोगों मे बलवा मचे।।"));
hinsuperobject.biblemap.put("b41_14_3", new hinsuperobject(41,14,3,"जब वह बैतनिरयाह में शमौन कोढ़ी के घर भोजन करने बैठा हुआ था जब एक स्त्री संगमरमर के पात्रा में जटामांसी का बहुमूल्य शुद्ध इत़्रा लेकर आई; और पात्रा तोड़ कर इत्रा को उसके सिर पर उण्डेला।"));
hinsuperobject.biblemap.put("b41_14_4", new hinsuperobject(41,14,4,"परन्तु कोई कोई अपने मन में रिसियाकर कहने लगे, इस इत्रा को क्यों सत्यनाश किया गया?"));
hinsuperobject.biblemap.put("b41_14_5", new hinsuperobject(41,14,5,"क्योकि यह इत्रा तो ती सौ दीनार से अधिक मूल्य में बेचकर कंगालों को बांटा जा सकता था, ओर वे उस को झिड़कने लगे।"));
hinsuperobject.biblemap.put("b41_14_6", new hinsuperobject(41,14,6,"यीशु ने कहा; उसे छोड़ दो; उसे क्यों सताते हो? उस ने तो मेरे साथ भालाई की है।"));
hinsuperobject.biblemap.put("b41_14_7", new hinsuperobject(41,14,7,"कंगाल तुम्हारे साथ सदा रहते हैं: और तुम जब चाहो तब उन से भलाई कर सकते हो; पर मैं तुम्हारे साथ सदा न रहूंगा।"));
hinsuperobject.biblemap.put("b41_14_8", new hinsuperobject(41,14,8,"जो कुछ वह कर सकी, उस ने किया; उस ने मेरे गाड़े जाने की तैयारी में पहिले से मेरी देह पर इत्रा मला है।"));
hinsuperobject.biblemap.put("b41_14_9", new hinsuperobject(41,14,9,"मैं तुम से सच कहता हूं, कि सारे जगत में जहां कहीं सुसमाचार प्रचर किया जाएगा, वहां उसके इस काम की चर्चा भी उसके स्मरण में की जाएगी।।"));
hinsuperobject.biblemap.put("b41_14_10", new hinsuperobject(41,14,10,"तब यहूदा इसकरियोती जो बारह में से एक था, महायाजकों के पास गया, कि उसे उन के हाथ पकड़वा दे।"));
hinsuperobject.biblemap.put("b41_14_11", new hinsuperobject(41,14,11,"वे यह सुनकर आनन्दित हुए, और उस को रूपये देना स्वीकार किया, और यह अवसर ढूंढ़ने लगा कि उसे किसी प्रकार पकड़वा दे।।"));
hinsuperobject.biblemap.put("b41_14_12", new hinsuperobject(41,14,12,"अखमीरी रोटी के पर्व्व के पहिले दिन, जिस में से फसह का बलिदान करते थे, उसके चेलों ने उस से पूछा, तू कहां चाहता है, कि हम जाकर तेरे लिये फसह खाने की तैयारी करे?"));
hinsuperobject.biblemap.put("b41_14_13", new hinsuperobject(41,14,13,"उस ने अपने चेलों में से दो को यह कहकर भेजा, कि नगर में जाओ, और एक मनुष्य जल का घड़ा उठाए, हुए तुम्हें मिलेगा, उसके पीछे हो लेना।"));
hinsuperobject.biblemap.put("b41_14_14", new hinsuperobject(41,14,14,"और वह जिस घर में जाए उस घर के स्वामी से कहना; गुरू कहता है, कि मेरी पाहुनशाला जिस में मैं अपने चेलों के साथ फसह खाऊं कहां है?"));
hinsuperobject.biblemap.put("b41_14_15", new hinsuperobject(41,14,15,"वह तुम्हें एक सजी सजाई, और तैयार की हुई बड़ी अटारी दिखा देगा, वहां हमारे लिये तैयारी करो।"));
hinsuperobject.biblemap.put("b41_14_16", new hinsuperobject(41,14,16,"सो चेले निकलकर नगर में आये और जैसा उस ने उन से कहा था, वैसा की पाया, और फसह तैयार किया।।"));
hinsuperobject.biblemap.put("b41_14_17", new hinsuperobject(41,14,17,"जब सांझ हुई, तो वह बारहों के साथ आया।"));
hinsuperobject.biblemap.put("b41_14_18", new hinsuperobject(41,14,18,"और जब वे बैठे भोजन कर रहे थे, तो यीशु ने कहा; मैं तुम से सच कहता हूं, कि तुम में से एक, जो मेरे साथ भोजन कर रहा है, मुझे पकड़वाएगा।"));
hinsuperobject.biblemap.put("b41_14_19", new hinsuperobject(41,14,19,"उन पर उदासी छा गई और वे एक एक करके उस से कहने लगे; क्या वह मैं हूं?"));
hinsuperobject.biblemap.put("b41_14_20", new hinsuperobject(41,14,20,"उस ने उन से कहा, वह बारहों में से एक है, जो मेरे साथ थाली में हाथ डालता है।"));
hinsuperobject.biblemap.put("b41_14_21", new hinsuperobject(41,14,21,"क्योंकि मनुष्य का पुत्रा तो, जैसा उसके विषय में लिखा है, जाता ही है; परन्तु उस मनुष्य पर हाय जिस के द्वारा मनुष्य का पुत्रा पकड़वाया जाता है! यदि उस मनुष्य का जन्म ही न होताख् तो उसके लिये भला होता।।"));
hinsuperobject.biblemap.put("b41_14_22", new hinsuperobject(41,14,22,"और जब वे खा ही रहे थे तो उस ने रोटी ली, और आशीष मांगकर तोड़ी, और उनहें दी, और कहा, लो, यह मेरी देह है।"));
hinsuperobject.biblemap.put("b41_14_23", new hinsuperobject(41,14,23,"फिर उस ने कटोरा लेकर धन्यवाद किया, और उन्हें दिया; और उन सब ने उस में से पीया।"));
hinsuperobject.biblemap.put("b41_14_24", new hinsuperobject(41,14,24,"और उस ने उन से कहा, यह वाचा का मेरा वह लोहू है, जो बहुतों के लिये बहाया जाता है।"));
hinsuperobject.biblemap.put("b41_14_25", new hinsuperobject(41,14,25,"मैं तुम से सच कहता हूं, कि दाख का रस उस दिन तक फिर कभी न पीऊंगा, जब तक परमेश्वर के राज्य में नया न पीऊं।।"));
hinsuperobject.biblemap.put("b41_14_26", new hinsuperobject(41,14,26,"फिर वे भजन गाकर बाहर जैतून के पहाड़ पर गए।।"));
hinsuperobject.biblemap.put("b41_14_27", new hinsuperobject(41,14,27,"तब यीशु ने उन से कहा; तुम सब ठोकर खाओगे, क्योंकि लिखा है, कि मैं रखवाले को मारूंगा, और भेड़ तित्तर बित्तर हो जाएंगी।"));
hinsuperobject.biblemap.put("b41_14_28", new hinsuperobject(41,14,28,"परन्तु मैं अपने जी उठने के बाद तुम से पहिले गलील को जाऊंगा।"));
hinsuperobject.biblemap.put("b41_14_29", new hinsuperobject(41,14,29,"पतरस ने उस से कहा; यदि एक ठोकर खाएं तो खांए, पर मैं ठोकर नहीं खाऊंगा।"));
hinsuperobject.biblemap.put("b41_14_30", new hinsuperobject(41,14,30,"यीशु ने उस से कहा; मैं तुझ से सच कहता हूं, कि आज ही इसी रात को मुर्गे के दो बार बांग देने से पहिले, तू तीन बार मुझ से मुकर जाएगा।"));
hinsuperobject.biblemap.put("b41_14_31", new hinsuperobject(41,14,31,"पर उस ने और भी जोर देकर कहा, यदि मुझे तेरे साथ मरना भी पड़े तौभी तेरा इन्कार कभी न करूंगा: इसी प्रकार और सब ने भी कहा।।"));
hinsuperobject.biblemap.put("b41_14_32", new hinsuperobject(41,14,32,"फिर वे गतसमने नाम एक जगह में आए, और उस ने अपने चेलों से कहा, यहां बैठे रहो, जब तक मैं प्रार्थना करूं।"));
hinsuperobject.biblemap.put("b41_14_33", new hinsuperobject(41,14,33,"और वह पतरस और याकूब और यूहन्ना को अपने साथ ले गया: और बहुत ही अधीर, और व्याकुल होने लगा।"));
hinsuperobject.biblemap.put("b41_14_34", new hinsuperobject(41,14,34,"और उन से कहा; मेरा मन बहुत उदास है, यहां तक कि मैं मरने पर हूं: तुम यहां ठहरो, और जागते रहो।"));
hinsuperobject.biblemap.put("b41_14_35", new hinsuperobject(41,14,35,"और वह थोड़ा आगे बढ़ा, और भूमि पर गिरकर प्रार्थना करने लगा, कि यदि हो सके तो यह घड़ी मुझ पर से टल जाए।"));
hinsuperobject.biblemap.put("b41_14_36", new hinsuperobject(41,14,36,"और कहा, हे अब्बा, हे पिता, तुझ से सब कुछ हो सकता है; इस कटोरे को मेरे पास से हटा ले: तौभी जैसा मैं चाहता हूं वैसा नहीं, पर जो तू चाहता है वही हो।"));
hinsuperobject.biblemap.put("b41_14_37", new hinsuperobject(41,14,37,"फिर वह आया, और उन्हें सोते पाकर पतरस से कहा; हे शमौन तू सो रहा है? क्या तू एक घड़ी भी न जाग सका?"));
hinsuperobject.biblemap.put("b41_14_38", new hinsuperobject(41,14,38,"जागते और प्रार्थना करते रहो कि तुम परीक्षा में न पड़ो: आत्मा तो तैयार है, पर शरीर दुर्बल है।"));
hinsuperobject.biblemap.put("b41_14_39", new hinsuperobject(41,14,39,"और वह फिर चला गया, और वही बात कहकर प्रार्थना की।"));
hinsuperobject.biblemap.put("b41_14_40", new hinsuperobject(41,14,40,"और फिर आकर उन्हें सोते पाया, क्योंकि उन की आंखे नींद से भरी थीं; और नहीं जानते थे कि उसे क्या उत्तर दें।"));
hinsuperobject.biblemap.put("b41_14_41", new hinsuperobject(41,14,41,"फिर तीसरी बार आकर उन से कहा; अब सोते रहो और विश्राम करो, बस, घड़ी आ पहुंची; देखो मनुष्य का पुत्रा पापियों के हाथ पकड़वाया जाता है।"));
hinsuperobject.biblemap.put("b41_14_42", new hinsuperobject(41,14,42,"उठो, चलें: देखो, मेरा पकड़वानेवाला निकट आ पहुंचा है।।"));
hinsuperobject.biblemap.put("b41_14_43", new hinsuperobject(41,14,43,"वह यह कह ही रहा था, कि यहूदा जो बारहों में से था, अपने साथ महायाजकों और शास्त्रियों और पुरनियों की ओर से एक बड़ी भीड़ तलवारें और लाठियां लिए हुए तुरन्त आ पहुंची।"));
hinsuperobject.biblemap.put("b41_14_44", new hinsuperobject(41,14,44,"और उसके पकड़नेवाले ने उन्हें यह पता दिया था, कि जिस को मैं चूमूं वही है, उसे पकड़कर यतन से ले जाना।"));
hinsuperobject.biblemap.put("b41_14_45", new hinsuperobject(41,14,45,"और वह आया, और तुरन्त उसके पास जाकर कहा; हे रब्बी और उस को बहुत चूमा।"));
hinsuperobject.biblemap.put("b41_14_46", new hinsuperobject(41,14,46,"तब उन्हों ने उस पर हाथ डालकर उसे पकड़ लिया।"));
hinsuperobject.biblemap.put("b41_14_47", new hinsuperobject(41,14,47,"उन में से जो पास खड़े थे, एक ने तलवार खींच कर महायाजक के दास पर चलाई, और उसका कान उड़ा दिया।"));
hinsuperobject.biblemap.put("b41_14_48", new hinsuperobject(41,14,48,"यीशु ने उन से कहा; क्या तुम डाकू जानकर मेरे पकड़ने के लिये तलवारें और लाठियां लेकर निकले हो?"));
hinsuperobject.biblemap.put("b41_14_49", new hinsuperobject(41,14,49,"मैं तो हर दिन मन्दिर में तुम्हारे साथ रहकर उपदेश दिया करता था, और तब तुम ने मुझे न पकड़ा: परन्तु यह इसलिये हुआ है कि पवित्रा शास्त्रा की बातें पूरी हों।"));
hinsuperobject.biblemap.put("b41_14_50", new hinsuperobject(41,14,50,"इस पर सब चेले उसे छोड़कर भाग गए।।"));
hinsuperobject.biblemap.put("b41_14_51", new hinsuperobject(41,14,51,"और एक जवान अपनी नंगी देह पर चादर ओढ़े हुए उसके पीदे हो लिया; और लोगों ने उसे पकड़ा।"));
hinsuperobject.biblemap.put("b41_14_52", new hinsuperobject(41,14,52,"पर वह चादर छोड़कर नंगा भाग गया।।"));
hinsuperobject.biblemap.put("b41_14_53", new hinsuperobject(41,14,53,"फिर वे यीशु को महायाजक के पास ले गए; और सब महायाजक और पुरनिए और शास्त्री उसके यहां इकट्ठे हो गए।"));
hinsuperobject.biblemap.put("b41_14_54", new hinsuperobject(41,14,54,"पतरस दूर ही दूर से उसके पीछे पीछे महायाजक के आंगन के भीतर तक गया, और प्यादों के साथ बैठ कर आग तापने लगा।"));
hinsuperobject.biblemap.put("b41_14_55", new hinsuperobject(41,14,55,"महायाजक और सारी महासभा यीशु के मार डालने के लिये उसके विरोध में गवाही की खोज में थे, पर न मिली।"));
hinsuperobject.biblemap.put("b41_14_56", new hinsuperobject(41,14,56,"क्योंकि बहुतेरे उसके विरोध में झूठी गवाही दे रहे थे, पर उन की गवाही एक सी न थी।"));
hinsuperobject.biblemap.put("b41_14_57", new hinsuperobject(41,14,57,"तब कितनों ने उठकर उस पर यह झूठी गवाही दी।"));
hinsuperobject.biblemap.put("b41_14_58", new hinsuperobject(41,14,58,"कि हम ने इसे यह कहते सुना है कि मैं इस हाथ के बनाए हुए मन्दिर को ढ़ा दूंगा, और तीन दिन में दूसरा बनाऊंगा, जो हाथ से न बना हो।"));
hinsuperobject.biblemap.put("b41_14_59", new hinsuperobject(41,14,59,"इस पर भी उन की गवाही एक सी न निकली।"));
hinsuperobject.biblemap.put("b41_14_60", new hinsuperobject(41,14,60,"तब महायाजक ने बीच में खड़े होकर यीशु से पूछा; कि तू कोई उत्तर नहीं देता? ये लोग तेरे विरोध में क्या गवाही देते हैं?"));
hinsuperobject.biblemap.put("b41_14_61", new hinsuperobject(41,14,61,"परन्तु वह मौन साधे रहा, और कुछ उत्तर न दिया: महायाजक ने उस से फिर पूछा, क्या तू उस परम धन्य का पुत्रा मसीह है?"));
hinsuperobject.biblemap.put("b41_14_62", new hinsuperobject(41,14,62,"यीशु ने कहा; हां मैं हूं: और तुम मनुष्य के पुत्रा को सर्वशक्तिमान की दहिनी और बैठे, और आकाश के बादलों के साथ आते देखोगे।"));
hinsuperobject.biblemap.put("b41_14_63", new hinsuperobject(41,14,63,"तब महायाजक ने अपने वस्त्रा फाड़कर कहा; अब हमें गवाहों का क्या प्रयोजन है?"));
hinsuperobject.biblemap.put("b41_14_64", new hinsuperobject(41,14,64,"तुम ने यह निन्दा सुनी: तुम्हारी क्या राय है उन सब ने कहा, वह वध के योग्य है।"));
hinsuperobject.biblemap.put("b41_14_65", new hinsuperobject(41,14,65,"तब कोई तो उस पर थूकने, और कोई उसका मुंह ढांपने और उसे घूसे मारने, और उस से कहने लगे, कि भविष्यद्वाणी कर: और प्यादों ने उसे लेकर थप्पड़ मारे।।"));
hinsuperobject.biblemap.put("b41_14_66", new hinsuperobject(41,14,66,"जब पतरस नीचे आंगन में था, तो महायाजक की लौंडियों में से एक वहां आई।"));
hinsuperobject.biblemap.put("b41_14_67", new hinsuperobject(41,14,67,"और पतरस को आग तापते देखकर उस पर टकटकी लगाकर देखा और कहने लगी, तू भी तो उस नासरी यीशु के साथ था।"));
hinsuperobject.biblemap.put("b41_14_68", new hinsuperobject(41,14,68,"वह मुकर गया, और कहा, कि मैं तो नहीं जानता औश्र नहीं समझता कि तू क्या कह रही है: फिर वह बाहर डेवढ़ी में गया; और मुर्गे ने बांग दी।"));
hinsuperobject.biblemap.put("b41_14_69", new hinsuperobject(41,14,69,"वह लौंडी उसे देखकर उन से जो पास खड़े थे, फिर कहने लगी, कि उन में से एक है।"));
hinsuperobject.biblemap.put("b41_14_70", new hinsuperobject(41,14,70,"परन्तु वह फिर मुकर गया और थोड़ी देर बाद उन्हों ने जो पास खड़े थे फिर पतरस से कहा; निश्चय तू उन में से एक है; क्योंकि तू गलीली भी है।"));
hinsuperobject.biblemap.put("b41_14_71", new hinsuperobject(41,14,71,"तब वह धिक्कार देन और शपथ खाने लगा, कि मैं उस मनुष्य को, जिस की तुम चर्चा करते हो, नहीं जानता।"));
hinsuperobject.biblemap.put("b41_14_72", new hinsuperobject(41,14,72,"तब तुरन्त दूसरी बार मुर्ग ने बांग दी: पतरस को यह बात जो यीशु ने उस से कही थी स्मरण आई, कि मुर्ग के दो बार बांग देने से पहिले तू तीन बार मेरा इन्कार करेगा: वह इस बात को सोचकर रोने लगा।।"));
hinsuperobject.biblemap.put("b41_15_1", new hinsuperobject(41,15,1,"और भोर होते ही तुरन्त महायाकों, पुरनियों, और शास्त्रियों ने वरन सारी महासभा ने सलाह करके यीशु को बन्धवाया, और उसे ले जाकर पीलातुस के हाथ सौंप दिया।"));
hinsuperobject.biblemap.put("b41_15_2", new hinsuperobject(41,15,2,"और पीलातुस ने उस से पूछा, क्या तू यहूदियों का राजा है? उस ने उस को उत्तर दिया; कि तू आप ही कह रहा है।"));
hinsuperobject.biblemap.put("b41_15_3", new hinsuperobject(41,15,3,"और महायाजक उस पर बहुत बातों का दोष लगा रहे थे।"));
hinsuperobject.biblemap.put("b41_15_4", new hinsuperobject(41,15,4,"पीलातुस ने उस से फिर पूछा, क्या तू कुछ उत्तर नहीं देता, देख ये तुझ पर कितनी बातों का दोष लगाते हैं?"));
hinsuperobject.biblemap.put("b41_15_5", new hinsuperobject(41,15,5,"यीशु ने फिर कुछ उत्तर नहीं दिया; यहां तक कि पीलातुस को बड़ा आश्चर्य हुआ।।"));
hinsuperobject.biblemap.put("b41_15_6", new hinsuperobject(41,15,6,"और वह उस पर्व्व में किसी एक बन्धुए को जिसे वे चाहते थे, उन के लिये छोड़ दिया करता था।"));
hinsuperobject.biblemap.put("b41_15_7", new hinsuperobject(41,15,7,"और बरअब्बा नाम एक मनुष्य उन बलवाइयों के साथ बन्धुआ था, जिन्हों ने बलवे में हत्या की थी।"));
hinsuperobject.biblemap.put("b41_15_8", new hinsuperobject(41,15,8,"और भीड़ ऊपर जाकर उस से बिनती करने लगी, कि जैसा तू हमारे लिये करता आया है वैसा ही कर।"));
hinsuperobject.biblemap.put("b41_15_9", new hinsuperobject(41,15,9,"पीलातुस ने उन को यह उत्तर दिया, क्या तुम चाहते हो, कि मैं तुम्हारे लिये यहूदियों के राजा को छोड़ दूं?"));
hinsuperobject.biblemap.put("b41_15_10", new hinsuperobject(41,15,10,"क्योंकि वह जानता था, कि महायाजकों ने उसे डाह से पकड़वाया था।"));
hinsuperobject.biblemap.put("b41_15_11", new hinsuperobject(41,15,11,"परन्तु महायाजकों ने लोगों को उभारा, कि वह बरअब्बा ही को उन के लिये छोड़ दे।"));
hinsuperobject.biblemap.put("b41_15_12", new hinsuperobject(41,15,12,"यह सून पीलातुस ने उन से फिर पूछा; तो जिसे तुम यहूदियों का राजा कहते हो, उस को मैं क्या करूं? वे फिर चिल्लाए, कि उसे क्रूस पर चढ़ा दे।"));
hinsuperobject.biblemap.put("b41_15_13", new hinsuperobject(41,15,13,"पीलातुस ने उन से कहा; क्यों, इस ने क्या बुराई की है?"));
hinsuperobject.biblemap.put("b41_15_14", new hinsuperobject(41,15,14,"परन्तु वे और भी चिल्लाए, कि उसे क्रूस पर चढ़ा दे।"));
hinsuperobject.biblemap.put("b41_15_15", new hinsuperobject(41,15,15,"तक पीलातुस ने भीड़ को प्रसन्न करने की इच्छा से, बरअब्बा को उन के लिये छोड़ दिया, और यीशु को कोड़े लगवाकर सौंप दिया, कि क्रूस पर चढ़ाया जाए।"));
hinsuperobject.biblemap.put("b41_15_16", new hinsuperobject(41,15,16,"और सिपाही उसे किले के भीतर आंगत में ले गए जो प्रीटोरियुन कहलाता है, और सारी पलटन को बुला लाए।"));
hinsuperobject.biblemap.put("b41_15_17", new hinsuperobject(41,15,17,"और उन्हों ने उसे बैंजनी वस्त्रा पहिनाया और कांटों का मुकुट गूंथकर उसके सिर पर रखा।"));
hinsuperobject.biblemap.put("b41_15_18", new hinsuperobject(41,15,18,"और यह कहकर उसे नमस्कार करने लगे, कि हे यहूदियों के राजा, नमस्कार!"));
hinsuperobject.biblemap.put("b41_15_19", new hinsuperobject(41,15,19,"और वे उसके सिर पर सरकण्डे मारते, और उस पर थूकते, और घुटने टेककर उसे प्रणाम करते रहे।"));
hinsuperobject.biblemap.put("b41_15_20", new hinsuperobject(41,15,20,"और जब वे उसका ठट्ठा कर चुके, तो उस पर बैंजनी वस्त्रा उतारकर उसी के कपड़े पहिनाए; और तब उसे क्रूस पर चढ़ाने के लिये बाहर ले गए।"));
hinsuperobject.biblemap.put("b41_15_21", new hinsuperobject(41,15,21,"और सिकन्दर और रूफुस का पिता, शमौन नाम एक कुरेनी मनुष्य, जो गांव से आ रहा था उधर से निकला; उन्हों ने उसे बेगार में पकड़ा, कि उसका क्रूस उठा ले चले।"));
hinsuperobject.biblemap.put("b41_15_22", new hinsuperobject(41,15,22,"और वे उसे गुलगुता नाम जगह पर जिस का अर्थ खोपड़ी की जगह है लाए।"));
hinsuperobject.biblemap.put("b41_15_23", new hinsuperobject(41,15,23,"और उसे मुर्र मिला हुआ दाखरस देने लगे, परन्तु उस ने नहीं लिया।"));
hinsuperobject.biblemap.put("b41_15_24", new hinsuperobject(41,15,24,"तब उन्हों ने उस को क्रूस पर चढ़ाया, और उसके कपड़ों पर चिटि्ठयां डालकर, कि किस को क्या मिले, उन्हें बांट लिया।"));
hinsuperobject.biblemap.put("b41_15_25", new hinsuperobject(41,15,25,"और पहर दिन चढ़ा था, जब उन्हों ने उस को क्रूस पर चढ़ाया।"));
hinsuperobject.biblemap.put("b41_15_26", new hinsuperobject(41,15,26,"और उसका दोषपत्रा लिखकर उसके ऊपर लगा दिया गया कि \"यहूदियों का राजा\"।"));
hinsuperobject.biblemap.put("b41_15_27", new hinsuperobject(41,15,27,"और उन्हों ने उसके साथ दो डाकू, एक उस की दहिनी और एक उस की बाईं ओर क्रूस पर चढ़ाए।"));
hinsuperobject.biblemap.put("b41_15_28", new hinsuperobject(41,15,28,"तब धर्मशास्त्रा का वह वचन कि वह अपराधियों के संग गिना गया पूरा हुआ।"));
hinsuperobject.biblemap.put("b41_15_29", new hinsuperobject(41,15,29,"और मार्ग में जानेवाले सिर हिला हिलाकर और यह कहकर उस की निन्दा करते थे, कि वाह! मन्दिर के ढानेवाले, और तीन दिन में बनानेवाले! क्रूस पर से उतर कर अपने आप को बचा ले।"));
hinsuperobject.biblemap.put("b41_15_30", new hinsuperobject(41,15,30,"इसी रीति से महायाजक भी, शास्त्रियों समेत,"));
hinsuperobject.biblemap.put("b41_15_31", new hinsuperobject(41,15,31,"आपस में ठट्ठे से कहते थे; कि इस ने औरों को बचाया, और अपने को नहीं बचा सकता।"));
hinsuperobject.biblemap.put("b41_15_32", new hinsuperobject(41,15,32,"इस्राएल का राजा मसीह अब क्रूस पर से उतर आए कि हम देखकर विश्वास करें: और जो उसके साथ क्रूसों पर चढ़ाए गए थे, वे भी उस की निन्दा करते थे।।"));
hinsuperobject.biblemap.put("b41_15_33", new hinsuperobject(41,15,33,"और दोपहर होने पर, सारे देश में अन्धियारा छा गया; और तीसरे पहर तक रहा।"));
hinsuperobject.biblemap.put("b41_15_34", new hinsuperobject(41,15,34,"तीसरे पहर यीशु ने बड़े शब्द से पुकार कर कहा, इलोई, इलोई, लमा शबक्तनी? जिस का अर्थ है; हे मेरे परमेश्वर, हे मेरे परमेश्वर, तू ने मुझे क्यों छोड़ दिया?"));
hinsuperobject.biblemap.put("b41_15_35", new hinsuperobject(41,15,35,"जो पास खड़े थे, उन में से कितनों ने यह सुनकर कहा: देखो यह एलिरयाह को पुकारता है।"));
hinsuperobject.biblemap.put("b41_15_36", new hinsuperobject(41,15,36,"और एक ने दौड़कर इस्पंज को सिरके से डुबोया, और सरकण्डे पर रखकर उसे चुसाया; और कहा, ठहर जाओ, देखें, कि एलिरयाह उसे उतारने कि लिये आता है कि नहीं।"));
hinsuperobject.biblemap.put("b41_15_37", new hinsuperobject(41,15,37,"तब यीशु ने बड़े शब्द से चिल्लाकर प्राण छोड़ दिये।"));
hinsuperobject.biblemap.put("b41_15_38", new hinsuperobject(41,15,38,"और मन्दिर का पर्दा ऊपर से नीचे तक फटकर दो टुकड़े हो गया।"));
hinsuperobject.biblemap.put("b41_15_39", new hinsuperobject(41,15,39,"जो सूबेदार उसके सम्हने खड़ा था, जब उसे यूं चिल्लाकर प्राण छोड़ते हुए देखा, तो उस ने कहा, सचमुच यह मनुष्य, परमेश्वर का पुत्रा था।"));
hinsuperobject.biblemap.put("b41_15_40", new hinsuperobject(41,15,40,"कई स्त्रियां भी दूर से देख रही थीं: उन में मरियम मगदलीनी और छोटे याकूब की और योसेस की माता मरियम और शलोमी थीं।"));
hinsuperobject.biblemap.put("b41_15_41", new hinsuperobject(41,15,41,"जब वह गलील में थ, तो ये उसके पीछे हो लेती थीं और उस की सेवाटहल किया करती थीं; और और भी बहुत सी स्त्रियां थीं, जो उसके साथ यरूशलेम में आई थीं।।"));
hinsuperobject.biblemap.put("b41_15_42", new hinsuperobject(41,15,42,"जब संध्या हो गई, तो इसलिये कि तैयारी का दिन था, जो सब्त के एक दिन पहिले होता है।"));
hinsuperobject.biblemap.put("b41_15_43", new hinsuperobject(41,15,43,"अरिमितिया का रहेनवाला यूसुुफ आया, जो प्रतिष्ठित मंत्री और आप भी परमेश्वर के राज्य की बाट जोहता था; वह हियाव करके पीलातुस के पास गया और यीशु की लोथ मांगी।"));
hinsuperobject.biblemap.put("b41_15_44", new hinsuperobject(41,15,44,"पीलातुस ने आश्चर्य किया, कि वह इतना शीघ्र मर गया; और सूबेदार को बुलाकर पूछा, कि क्या उस को मरे हुए देर हुई?"));
hinsuperobject.biblemap.put("b41_15_45", new hinsuperobject(41,15,45,"सो जब सूबेदार के द्वारा हाल जान लिया, तो लोथ यूसुफ को दिला दी।"));
hinsuperobject.biblemap.put("b41_15_46", new hinsuperobject(41,15,46,"तब उस ने एक पतली चादर मोल ली, और लोथ को उतारकर चादर में लपेटा, और एक कब्र मे जो चट्टान में खोदी गई थी रखा, और कब्र के द्वार पर एक पत्थर लुढ़कार दिया।"));
hinsuperobject.biblemap.put("b41_15_47", new hinsuperobject(41,15,47,"और मरियम मगदलीनी और योसेस की माता मरियम देख रही थीं, कि वह कहां रखा गया है।।"));
hinsuperobject.biblemap.put("b41_16_1", new hinsuperobject(41,16,1,"जब सब्त का दिन बीत गया, तो मरियम मगदलीनी और याकूब की माता मरियम और शलोमी ने सुगन्धित वस्तुएं मोल ली, कि आकर उस पर मलें।"));
hinsuperobject.biblemap.put("b41_16_2", new hinsuperobject(41,16,2,"और सप्ताह के पहिले दिन बड़ी भोर, जब सूरज निकला ही था, वे कब्र पर आईं।"));
hinsuperobject.biblemap.put("b41_16_3", new hinsuperobject(41,16,3,"और आपस में कहती थीं, कि हमारे लिये कब्र के द्वार पर से पत्थर कौन लुढ़ाएगा?"));
hinsuperobject.biblemap.put("b41_16_4", new hinsuperobject(41,16,4,"जब उन्हों ने आंख उठाई, तो देखा कि पत्थर लुढ़का हुआ है! क्योंकि वह बहुत ही बड़ा था।"));
hinsuperobject.biblemap.put("b41_16_5", new hinsuperobject(41,16,5,"और कब्र के भीतर जाकर, उन्हों ने एक जवान को श्वेत वस्त्रा पहिने हुए दहिनी ओर बैठे देखा, और बहुत चकित हुई।"));
hinsuperobject.biblemap.put("b41_16_6", new hinsuperobject(41,16,6,"उस ने उन से कहा, चकित मत हो, तुम यीशु नासरी को, जो क्रूस पर चढ़ाया गया था, ढूंढ़ती हो: वह जी उठा है; यहां नहीं है; देखो, यही वह स्थान है, जहां उन्हों ने उसे रखा था।"));
hinsuperobject.biblemap.put("b41_16_7", new hinsuperobject(41,16,7,"परन्तु तुम जाओ, और उसके चेलों और पतरस से कहो, कि वह तुम से पहिले गलील को जाएगा; जैसा उस ने तुम से कहा था, तुम वही उसे देखोगे।"));
hinsuperobject.biblemap.put("b41_16_8", new hinsuperobject(41,16,8,"और वे निकलकर कब्र से भाग गईं; क्योंकि कपकपी और घबराहट उन पर छा गई थीं और उन्हों ने किसी से कुछ न कहा, क्योंकि डरती थीं।।"));
hinsuperobject.biblemap.put("b41_16_9", new hinsuperobject(41,16,9,"सप्ताह के पहिले दिन भोर होते ही वह जी उठ कर पहिले पहिल मरियम मगदलीनी को जिस में से उस ने सात दुष्टात्माएं निकाली थीं, दिखाई दिया।"));
hinsuperobject.biblemap.put("b41_16_10", new hinsuperobject(41,16,10,"उस ने जाकर उसके साथियों को जो शोक में डूबे हुए थे और रो रहे थे, समाचार दिया।"));
hinsuperobject.biblemap.put("b41_16_11", new hinsuperobject(41,16,11,"और उन्हों ने यह सुनकर की वह जीवित है, और उस ने उसे देखा है प्रतीति न की।।"));
hinsuperobject.biblemap.put("b41_16_12", new hinsuperobject(41,16,12,"इस के बाद वह दूसरे रूप में उन में से दो को जब वे गांव की ओर जा रहे थे, दिखाई दिया।"));
hinsuperobject.biblemap.put("b41_16_13", new hinsuperobject(41,16,13,"उन्हों ने भी जाकर औरों को समाचार दिया, परन्तु उन्हों ने उन की भी प्रतीति न की।।"));
hinsuperobject.biblemap.put("b41_16_15", new hinsuperobject(41,16,15,"और उस ने उन से कहा, तुम सारे जगत में जाकर सारी सृष्टि के लोगों को सुसमाचार प्रचार करो।"));
hinsuperobject.biblemap.put("b41_16_16", new hinsuperobject(41,16,16,"जो विश्वास करे और बपतिस्मा ले उसी का उद्धार होगा, परन्तु जो विश्वास ने करेगा वह दोषी ठहराया जाएगा।"));
hinsuperobject.biblemap.put("b41_16_17", new hinsuperobject(41,16,17,"और विश्वास करनेवालों में ये चिन्ह होंगे कि वे मेरे नाम से दुष्टात्माओं को निकालेंगे।"));
hinsuperobject.biblemap.put("b41_16_18", new hinsuperobject(41,16,18,"नई नई भाषा बोलेंगे, सांपों को उठा लेंगे, और यदि वे नाशक वस्तु भी पी जांए तौभी उन की कुछ हानि न होगी, वे बीमारों पर हाथ रखेंगे, और वे चंगे हो जाएंगे।"));
hinsuperobject.biblemap.put("b41_16_19", new hinsuperobject(41,16,19,"निदान प्रभु यीशु उन से बातें करने के बाद स्वर्ग पर उठा लिया गया, और परमेश्वर की दहिनी ओर बैठ गया।"));
hinsuperobject.biblemap.put("b41_16_20", new hinsuperobject(41,16,20,"और उन्हों ने निकलकर हर जगह प्रचार किया, और प्रभु उन के साथ काम करता रहा, और उन चिन्हों के द्वारा जो साथ साथ होते थे वचन को, दृढ़ करता रहा। आमीन।।"));

}

