hinsuperobject.biblemap.put("b43_1_1", new hinsuperobject(43,1,1,"आदि में वचन था, और वचन परमेश्वर के साथ था, और वचन परमेश्वर था।"));
hinsuperobject.biblemap.put("b43_1_2", new hinsuperobject(43,1,2,"यही आदि में परमेश्वर के साथ था।"));
hinsuperobject.biblemap.put("b43_1_3", new hinsuperobject(43,1,3,"सब कुछ उसी के द्वारा उत्पन्न हुआ और जो कुछ उत्पन्न हुआ है, उस में से कोई भी वस्तु उसके बिना उत्पन्न न हुई।"));
hinsuperobject.biblemap.put("b43_1_4", new hinsuperobject(43,1,4,"उस में जीवन था; और वह जीवन मुनष्यों की ज्योति थी।"));
hinsuperobject.biblemap.put("b43_1_5", new hinsuperobject(43,1,5,"और ज्योति अन्धकार में चमकती है; और अन्धकार ने उसे ग्रहण न किया।"));
hinsuperobject.biblemap.put("b43_1_6", new hinsuperobject(43,1,6,"एक मनुष्य परमेश्वर की ओर से आ उपस्थित हुआ जिस का नाम यूहन्ना था।"));
hinsuperobject.biblemap.put("b43_1_7", new hinsuperobject(43,1,7,"यह गवाही देने आया, कि ज्योति की गवाही दे, ताकि सब उसके द्वारा विश्वास लाएं।"));
hinsuperobject.biblemap.put("b43_1_8", new hinsuperobject(43,1,8,"वह आप तो वह ज्योति न था, परन्तु उस ज्योति की गवाही देने के लिये आया था।"));
hinsuperobject.biblemap.put("b43_1_9", new hinsuperobject(43,1,9,"सच्ची ज्योति जो हर एक मनुष्य को प्रकाशित करती है, जगत में आनेवाली थी।"));
hinsuperobject.biblemap.put("b43_1_10", new hinsuperobject(43,1,10,"वह जगत में था, और जगत उसके द्वारा उत्पन्न हुआ, और जगत ने उसे नहीं पहिचाना।"));
hinsuperobject.biblemap.put("b43_1_11", new hinsuperobject(43,1,11,"वह अपने घर में आया और उसके अपनों ने उसे ग्रहण नहीं किया।"));
hinsuperobject.biblemap.put("b43_1_12", new hinsuperobject(43,1,12,"परन्तु जितनों ने उसे ग्रहण किया, उस ने उन्हें परमेश्वर के सन्तान होने का अधिकार दिया, अर्थात् उन्हें जो उसके नाम पर विश्वास रखते हैं।"));
hinsuperobject.biblemap.put("b43_1_13", new hinsuperobject(43,1,13,"वे न तो लोहू से, न शरीर की इच्छा से, न मनुष्य की इच्छा से, परन्तु परमेश्वर से उत्पन्न हुए हैं।"));
hinsuperobject.biblemap.put("b43_1_14", new hinsuperobject(43,1,14,"और वचन देहधारी हुआ; और अनुग्रह और सच्चाई से परिपूर्ण होकर हमारे बीच में डेरा किया, और हम ने उस की ऐसी महिमा देखी, जैसी पिता के एकलौते की महिमा।"));
hinsuperobject.biblemap.put("b43_1_15", new hinsuperobject(43,1,15,"यूहन्ना ने उसके विषय में गवाही दी, और पुकारकर कहा, कि यह वही है, जिस का मैं ने वर्णन किया, कि जो मेरे बाद आ रहा है, वह मुझ से बढ़कर है क्योंकि वह मुझ से पहिले था।"));
hinsuperobject.biblemap.put("b43_1_16", new hinsuperobject(43,1,16,"क्योंकि उस की परिपूर्णता से हम सब ने प्राप्त किया अर्थात् अनुग्रह पर अनुग्रह।"));
hinsuperobject.biblemap.put("b43_1_17", new hinsuperobject(43,1,17,"इसलिये कि व्यवस्था तो मूसा के द्वारा दी गई; परन्तु अनुग्रह, और सच्चाई यीशु मसीह के द्वारा पहुंची।"));
hinsuperobject.biblemap.put("b43_1_18", new hinsuperobject(43,1,18,"परमेश्वर को किसी ने कभी नहीं देखा, एकलौता पुत्रा जो पिता की गोद में हैं, उसी ने उसे प्रगट किया।।"));
hinsuperobject.biblemap.put("b43_1_19", new hinsuperobject(43,1,19,"यूहन्ना की गवाही यह है, कि जब यहूदियों ने यरूशलेम से याजकों और लेवीयों को उस से यह पूछने के लिये भेजा, कि तू कौन है?"));
hinsuperobject.biblemap.put("b43_1_20", new hinsuperobject(43,1,20,"तो उस ने यह मान लिया, और इन्कार नहीं किया परन्तु मान लिया कि मैं मसीह नहीं हूं।"));
hinsuperobject.biblemap.put("b43_1_21", new hinsuperobject(43,1,21,"तब उन्हों ने उस से पूछा, तो फिर कौन है? क्या तू एलिरयाह है? उस ने कहा, मैं नहीं हूं: तो क्या तू वह भविष्यद्वक्ता है? उस ने उत्तर दिया, कि नहीं।"));
hinsuperobject.biblemap.put("b43_1_22", new hinsuperobject(43,1,22,"तब उन्हों ने उस से पूछा, फिर तू है कौन? ताकि हम अपने भेजनेवालों को उत्तर दें; तू अपने विषय में क्या कहता है?"));
hinsuperobject.biblemap.put("b43_1_23", new hinsuperobject(43,1,23,"उस ने कहा, मैं जैसा यशायाह भविष्यद्वक्ता ने कहा है, जंगल में एक पुकारनेवाले का शब्द हूं कि तुम प्रभु का मार्ग सीधा करो।"));
hinsuperobject.biblemap.put("b43_1_24", new hinsuperobject(43,1,24,"ये फरीसियों की ओर से भेजे गए थे।"));
hinsuperobject.biblemap.put("b43_1_25", new hinsuperobject(43,1,25,"उन्हों ने उस से यह प्रश्न पूछा, कि यदि तू न मसीह है, और न एलिरयाह, और न वह भविष्यद्वक्ता है, तो फिर बपतिस्मा क्यों देता है?"));
hinsuperobject.biblemap.put("b43_1_26", new hinsuperobject(43,1,26,"यूहन्ना ने उन को उत्तर दिया, कि मैं तो जल से बपतिस्मा देता हूं; परन्तु तुम्हारे बीच में एक व्यक्ति खड़ा है, जिसे तुम नहीं जानते।"));
hinsuperobject.biblemap.put("b43_1_27", new hinsuperobject(43,1,27,"अर्थात् मेरे बाद आनेवाला है, जिस की जूती का बन्ध मैं खोलने के योग्य नहीं।"));
hinsuperobject.biblemap.put("b43_1_28", new hinsuperobject(43,1,28,"ये बातें यरदन के पार बैतनिरयाह में हुई, जहां यूहन्ना बपतिस्मा देता था।"));
hinsuperobject.biblemap.put("b43_1_29", new hinsuperobject(43,1,29,"दूसरे दिन उस ने यीशु को अपनी ओर आते देखकर कहा, देखो, यह परमेश्वर का मेम्ना है, जो जगत के पाप उठा ले जाता है।"));
hinsuperobject.biblemap.put("b43_1_30", new hinsuperobject(43,1,30,"यह वही है, जिस के विषय में मैं ने कहा था, कि एक पुरूष मेरे पीछे आता है, जो मुझ से श्रेष्ठ है, क्योंकि वह मुझ से पहिले था।"));
hinsuperobject.biblemap.put("b43_1_31", new hinsuperobject(43,1,31,"और मैं तो उसे पहिचानता न था, परन्तु इसलिये मैं जल से बपतिस्मा देता हुआ आया, कि वह इस्त्राएल पर प्रगट हो जाए।"));
hinsuperobject.biblemap.put("b43_1_32", new hinsuperobject(43,1,32,"और यूहन्ना ने यह गवाही दी, कि मैं ने आत्मा को कबूतर की नाईं आकाश से उतरते देखा है, और वह उस पर ठहर गया।"));
hinsuperobject.biblemap.put("b43_1_33", new hinsuperobject(43,1,33,"और मैं तो उसे पहिचानता न था, परन्तु जिस ने मुझे जल से बपतिस्मा देने को भेजा, उसी ने मुझ से कहा, कि जिस पर तू आत्मा को उतरते और ठहरते देखे; वही पवित्रा आत्मा से बपतिस्मा देनेवाला है।"));
hinsuperobject.biblemap.put("b43_1_34", new hinsuperobject(43,1,34,"और मैं ने देखा, और गवाही दी है, कि यही परमेश्वर का पुत्रा है।।"));
hinsuperobject.biblemap.put("b43_1_35", new hinsuperobject(43,1,35,"दूसरे दिन फिर यूहन्ना और उसके चेलों में से दो जन खड़े हुए थे।"));
hinsuperobject.biblemap.put("b43_1_36", new hinsuperobject(43,1,36,"और उस ने यीशु पर जो जा रहा था दृष्टि करके कहा, देखो, यह परमेश्वर का मेम्ना है।"));
hinsuperobject.biblemap.put("b43_1_37", new hinsuperobject(43,1,37,"तब वे दोनों चेले उस की सुनकर यीशु के पीछे हो लिए।"));
hinsuperobject.biblemap.put("b43_1_38", new hinsuperobject(43,1,38,"यीशु ने फिरकर और उन को पीछे आते देखकर उन से कहा, तुम किस की खोज में हो? उन्हों ने उस से कहा, हे रब्बी, अर्थात् (हे गुरू) तू कहां रहता है? उस ने उन से कहा, चलो, तो देख लोगे।"));
hinsuperobject.biblemap.put("b43_1_39", new hinsuperobject(43,1,39,"तब उन्हों ने आकर उसके रहने का स्थान देखा, और उस दिन उसी के साथ रहे; और यह दसवें घंटे के लगभग था।"));
hinsuperobject.biblemap.put("b43_1_40", new hinsuperobject(43,1,40,"उन दोनों में से जो यूहन्ना की बात सुनकर यीशु के पीछे हो लिए थे, एक तो शमौन पतरस का भाई अन्द्रियास था।"));
hinsuperobject.biblemap.put("b43_1_41", new hinsuperobject(43,1,41,"उस ने पहिले अपने सगे भाईं शमौन से मिलकर उस से कहा, कि हम को ख्रिस्तस अर्थात् मसीह मिल गया।"));
hinsuperobject.biblemap.put("b43_1_42", new hinsuperobject(43,1,42,"वह उसे यीशु के पास लाया: यीशु ने उस पर दृष्टि करके कहा, कि तू यूहन्ना का पुत्रा शमौन है, तू केफा, अर्थात् पतरस कहलाएगा।।"));
hinsuperobject.biblemap.put("b43_1_43", new hinsuperobject(43,1,43,"ूदूसरे दिन यीशु ने गलील को जाना चाहा; और फिलिप्पुस से मिलकर कहा, मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b43_1_44", new hinsuperobject(43,1,44,"फिलिप्पुस तो अन्द्रियास और पतरस के नगर बैतसैदा का निवासी था।"));
hinsuperobject.biblemap.put("b43_1_45", new hinsuperobject(43,1,45,"फिलिप्पुस ने नतनएल से मिलकर उस से कहा, कि जिस का वर्णन मूसा ने व्यवस्था में और भविष्यद्वक्ताओं ने किया है, वह हम को मिल गया; वह यूसुफ का पुत्रा, यीशु नासरी है।"));
hinsuperobject.biblemap.put("b43_1_46", new hinsuperobject(43,1,46,"नतनएल ने उस से कहा, क्या कोई अच्छी वस्तु भी नासरत से निकल सकती है? फिलिप्पुस ने उस से कहा, चलकर देख ले।"));
hinsuperobject.biblemap.put("b43_1_47", new hinsuperobject(43,1,47,"यीशु ने नतनएल को अपनी ओर आते देखकर उसके विषय में कहा, देखो, यह सचमुच इस्त्राएली है: इस में कपट नहीं।"));
hinsuperobject.biblemap.put("b43_1_48", new hinsuperobject(43,1,48,"नतनएल ने उस से कहा, तू मुझे कहां से जानता है? यीशु ने उस को उत्तर दिया; उस से पहिले कि फिलिप्पुस ने तुझे बुलाया, जब तू अंजीर के पेड़ के तले था, तब मैं ने तुझे देखा था।"));
hinsuperobject.biblemap.put("b43_1_49", new hinsuperobject(43,1,49,"नतनएल ने उस से कहा, तू मुझे कहां से जानता है? यीशु ने उस को उत्तर दिया; उस से पहिले कि फिलिप्पुस ने तुझे बुलाया, जब तू अंजीर के पेड़ के तले था, तब मैं ने तुझे देखा था।"));
hinsuperobject.biblemap.put("b43_1_50", new hinsuperobject(43,1,50,"नतनएल ने उस को उत्तर दिया, कि हे रब्बी, तू परमेश्वर का पुत्रा है; तू इस्त्राएल का महाराजा है।"));
hinsuperobject.biblemap.put("b43_1_51", new hinsuperobject(43,1,51,"यीशु ने उस को उत्तर दिया; मैं ने जो तुझ से कहा, कि में ने तुझे अंजीर के पेड़ के तले देखा, क्या तू इसी लिये विश्वास करता है? तू इस से बड़े बड़े काम देखेगा।"));
hinsuperobject.biblemap.put("b43_2_1", new hinsuperobject(43,2,1,"फिर उस से कहा, मैं तुम से सच सच कहता हूं कि तुम स्वर्ग को खुला हुआ, और परमेश्वर के स्वर्गदूतों को ऊपर जाते और मनुष्य के पुत्रा के ऊपर उतरते देखोगे।।"));
hinsuperobject.biblemap.put("b43_2_2", new hinsuperobject(43,2,2,"फिर तीसरे दिन गलील के काना में किसी का ब्याह था, और यीशु की माता भी वहां थी।"));
hinsuperobject.biblemap.put("b43_2_3", new hinsuperobject(43,2,3,"और यीशु और उसके चेले भी उस ब्याह में नेवते गए थे।"));
hinsuperobject.biblemap.put("b43_2_4", new hinsuperobject(43,2,4,"जब दाखरस घट गया, तो यीशु की माता ने उस से कहा, कि उन के पास दाखरस नहीं रहा।"));
hinsuperobject.biblemap.put("b43_2_5", new hinsuperobject(43,2,5,"यीशु ने उस से कहा, हे महिला मुझे तुझ से क्या काम? अभी मेरा समय नहीं आया।"));
hinsuperobject.biblemap.put("b43_2_6", new hinsuperobject(43,2,6,"उस की माता ने सेवकों से कहा, जो कुद वह तुम से कहे, वही करना।"));
hinsuperobject.biblemap.put("b43_2_7", new hinsuperobject(43,2,7,"वहां यहूदियों के शुद्ध करने की रीति के अनुसार पत्थर के छ: मटके धरे थे, जि में दो दो, तीन तीन मन समाता था।"));
hinsuperobject.biblemap.put("b43_2_8", new hinsuperobject(43,2,8,"यीशु ने उन से कहा, अब निकालकर भोज के प्रधान के पास ले जाओ।"));
hinsuperobject.biblemap.put("b43_2_9", new hinsuperobject(43,2,9,"वे ले गए, जब भोज के प्रधान ने वह पानी चखा, जो दाखरस बन गया था, और नहीं जानता था, कि वह कहां से आया हे, (परन्तु जिन सेवकों ने पानी निकाला था, वे जानते थे) तो भोज के प्रधान ने दूल्हे को बुलाकर, उस से कहा।"));
hinsuperobject.biblemap.put("b43_2_10", new hinsuperobject(43,2,10,"हर एक मनुष्य पहिले अच्छा दाखरस देता है और जब लोग पीकर छक जाते हैं, तब मध्यम देता है; परन्तु तू ने अच्छा दाखरस अब तक रख छोड़ा है।"));
hinsuperobject.biblemap.put("b43_2_11", new hinsuperobject(43,2,11,"यीशु ने गलील के काना में अपना यह पहिला चिन्ह दिखाकर अपनी महिमा प्रगट की और उसके चेलों ने उस पर विश्वास किया।।"));
hinsuperobject.biblemap.put("b43_2_12", new hinsuperobject(43,2,12,"इस के बाद वह और उस की माता और उसके भाई और उसके चेले कफरनहूम को गए और वहां कुछ दिन रहे।।"));
hinsuperobject.biblemap.put("b43_2_13", new hinsuperobject(43,2,13,"यहूदियों का फसह का पर्ब्ब निकट था और यीशु यरूशलेम को गया।"));
hinsuperobject.biblemap.put("b43_2_14", new hinsuperobject(43,2,14,"और उस ने मन्दिर में बैल और भेड़ और कबूतर के बेचनेवालों ओर सर्राफों को बैठे हुए पाया।"));
hinsuperobject.biblemap.put("b43_2_15", new hinsuperobject(43,2,15,"और रस्सियों का कोड़ा बनाकर, सब भेड़ों और बैलों को मन्दिर से निकाल दिया, और सर्राफों के पैसे बिथरा दिए, और पीढ़ों को उलट दिया।"));
hinsuperobject.biblemap.put("b43_2_16", new hinsuperobject(43,2,16,"और कबूतर बेचनेवालों से कहा; इन्हें यहां से ले जाओ: मेरे पिता के भवन को व्योपार का घर मत बनाओ।"));
hinsuperobject.biblemap.put("b43_2_17", new hinsuperobject(43,2,17,"तब उसके चेलों को स्मरण आया कि लिखा है, `तेरे घर की धुन मुझे खा जाएगी'।"));
hinsuperobject.biblemap.put("b43_2_18", new hinsuperobject(43,2,18,"इस पर यहूदियों ने उस से कहा, तू जो यह करता है तो हमें कौन सा चिन्ह दिखाता हे?"));
hinsuperobject.biblemap.put("b43_2_19", new hinsuperobject(43,2,19,"यीशु ने उन को उत्तर दिया; कि इस मन्दिर को ढा दो, और मैं उसे तीन दिन में खड़ा कर दूंगा।"));
hinsuperobject.biblemap.put("b43_2_20", new hinsuperobject(43,2,20,"यहूदियों ने कहा; इस मन्दिर के बनाने में छियालीस वर्ष लगे हें, और क्या तू उसे तीन दिन में खड़ा कर देगा?"));
hinsuperobject.biblemap.put("b43_2_21", new hinsuperobject(43,2,21,"परन्तु उस ने अपनी देह के मन्दिर के विषय में कहा था।"));
hinsuperobject.biblemap.put("b43_2_22", new hinsuperobject(43,2,22,"सो जब वह मुर्दों में से जी उठा तो उसके चेलों को स्मरण आया, कि उस ने यह कहा था; और उन्हों ने पवित्रा शास्त्रा और उस वचन की जो यीशु ने कहा था, प्रतीति की।।"));
hinsuperobject.biblemap.put("b43_2_23", new hinsuperobject(43,2,23,"जब वह यरूशलेम में फसह के समय पर्ब्ब में था, तो बहुतों ने उन चिन्हों को जो वह दिखाता था देखकर उसके नाम पर विश्वास किया।"));
hinsuperobject.biblemap.put("b43_2_24", new hinsuperobject(43,2,24,"परन्तु यीशु ने अपने आप को उन के भरोसे पर नहीं छोड़ा, क्योंकि वह सब को जानता था।"));
hinsuperobject.biblemap.put("b43_2_25", new hinsuperobject(43,2,25,"और उसे प्रयोजन न था, कि मनुष्य के विषय में कोई गवाही दे, क्योंकि वह आप जानता था, कि मनुष्य के मन में क्या है?"));
hinsuperobject.biblemap.put("b43_3_1", new hinsuperobject(43,3,1,"फरीसियों में से नीकुदेमुस नाम एक मनुष्य था, जो यहूदियों का सरदार था।"));
hinsuperobject.biblemap.put("b43_3_2", new hinsuperobject(43,3,2,"उस ने रात को यीशु के पास आकर उस से कहा, हे रब्बी, हम जानते हैं, कि तू परमेश्वर की आरे से गुरू हो कर अया है; क्योंकि कोई इन चिन्हों को जो तू दिखाता है, यदि परमेश्वर उसके साथ न हो, तो नहीं दिखा सकता।"));
hinsuperobject.biblemap.put("b43_3_3", new hinsuperobject(43,3,3,"यीशु ने उस को उत्तर दिया; कि मैं तुझ से सच सच कहता हूं, यदि कोई नये सिरे से न जन्में तो परमेश्वर का राज्य देख नहीं सकता।"));
hinsuperobject.biblemap.put("b43_3_4", new hinsuperobject(43,3,4,"नीकुदेमुस ने उस से कहा, मनुष्य जब बूढ़ा हो गया, तो क्योंकर जन्म ले सकता है?"));
hinsuperobject.biblemap.put("b43_3_5", new hinsuperobject(43,3,5,"यीशु ने उत्तर दिया, कि मैं तुझ से सच सच कहता हूं; जब तक कोई मनुष्य जल और आत्मा से न जन्मे तो वह परमेश्वर के राज्य में प्रवेश नहीं कर सकता।"));
hinsuperobject.biblemap.put("b43_3_6", new hinsuperobject(43,3,6,"क्योंकि जो शरीर से जन्मा है, वह शरीर है; और जो आत्मा से जन्मा है, वह आत्मा है।"));
hinsuperobject.biblemap.put("b43_3_7", new hinsuperobject(43,3,7,"अचम्भा न कर, कि मैं ने तुझ से कहा; कि तुम्हें नये सिरे से जन्म लेना अवश्य है।"));
hinsuperobject.biblemap.put("b43_3_8", new hinsuperobject(43,3,8,"हवा जिधर चाहती है उधर चलती है, और तू उसका शब्द सुनता है, परन्तु नहीं जानता, कि वह कहां से आती और किधर को जाती है? जो कोई आत्मा से जन्मा है वह ऐसा ही है।"));
hinsuperobject.biblemap.put("b43_3_9", new hinsuperobject(43,3,9,"नीकुदेमुस ने उस को उत्तर दिया; कि ये बातें क्योंकर हो सकती हैं?"));
hinsuperobject.biblemap.put("b43_3_10", new hinsuperobject(43,3,10,"यह सुनकर यीशु ने उस से कहा; तू इस्त्राएलियों का गुरू हो कर भी क्या इन बातों को नहीं समझता।"));
hinsuperobject.biblemap.put("b43_3_11", new hinsuperobject(43,3,11,"मैं तुझ से सच सच कहता हूं कि हम जो जानते हैं, वह कहते हैं, और जिसे हम ने देखा है उस की गवाही देते हैं, और तुम हमारी गवाही ग्रहण नहीं करते।"));
hinsuperobject.biblemap.put("b43_3_12", new hinsuperobject(43,3,12,"जब मैं ने तुम से पृथ्वी की बातें कहीं, और तुम प्रतीति नहीं करते, तो यदि मैं तुम से स्वर्ग की बातें कहूं, तो फिर क्योंकर प्रतीति करोगे?"));
hinsuperobject.biblemap.put("b43_3_13", new hinsuperobject(43,3,13,"और कोई स्वर्ग पर नहीं चढ़ा, केवल वहीं जो स्वर्ग से उतरा, अर्थात् मनुष्य का पुत्रा जो स्वर्ग में है।"));
hinsuperobject.biblemap.put("b43_3_14", new hinsuperobject(43,3,14,"और जिस रीति से मूसा ने जंगल में सांप को ऊंचे पर चढ़ाया, उसी रीति से अवश्य है कि मनुष्य का पुत्रा भी ऊंचे पर चढ़ाया जाए।"));
hinsuperobject.biblemap.put("b43_3_15", new hinsuperobject(43,3,15,"ताकि जो कोई विश्वास करे उस में अनन्त जीवन पाए।।"));
hinsuperobject.biblemap.put("b43_3_16", new hinsuperobject(43,3,16,"क्योंकि परमेश्वर ने जगत से ऐसा प्रेम रखा कि उस ने अपना एकलौता पुत्रा दे दिया, ताकि जो कोई उस पर विश्वास करे, वह नाश न हो, परन्तु अनन्त जीवन पाए।"));
hinsuperobject.biblemap.put("b43_3_17", new hinsuperobject(43,3,17,"परमेश्वर ने अपने पुत्रा को जगत में इसलिये नहीं भेजा, कि जगत पर दंड की आज्ञा दे परन्तु इसलिये कि जगत उसके द्वारा उद्धार पाए।"));
hinsuperobject.biblemap.put("b43_3_18", new hinsuperobject(43,3,18,"जो उस पर विश्वास करता है, उस पर दंड की आज्ञा नहीं होती, परन्तु जो उस पर विश्वास नहीं करता, वह दोषी ठहरा चुका; इसलिये कि उस ने परमेश्वर के एकलौते पुत्रा के नाम पर विश्वास नहीं किया।"));
hinsuperobject.biblemap.put("b43_3_19", new hinsuperobject(43,3,19,"और दंड की आज्ञा का कारण यह है कि ज्योति जगत में आई है, और मनुष्यों ने अन्धकार को ज्योति से अधिक प्रिय जाना क्योंकि उन के काम बुरे थे।"));
hinsuperobject.biblemap.put("b43_3_20", new hinsuperobject(43,3,20,"क्योंकि जो कोई बुराई करता है, वह ज्योति से बैर रखता है, और ज्योति के निकट नहीं आता, ऐसा न हो कि उसके कामों पर दोष लगाया जाए।"));
hinsuperobject.biblemap.put("b43_3_21", new hinsuperobject(43,3,21,"परन्तु जो सच्चाई पर चलता हैख् वह ज्योति के निकट आता है, ताकि उसके काम प्रगट हों, कि वह परमेश्वर की ओर से किए गए हैं।"));
hinsuperobject.biblemap.put("b43_3_22", new hinsuperobject(43,3,22,"इस के बाद यीशु और उसके चेले यहूदिया देश में आए; और वह वहां उन के साथर रहकर बपतिस्मा देने लगा।"));
hinsuperobject.biblemap.put("b43_3_23", new hinsuperobject(43,3,23,"और यूहन्ना भी शालेम् के निकट ऐनोन में बपतिस्मा देता था। क्योंकि वहां बहुत जल था और लोग आकर बपतिस्मा लेते थे।"));
hinsuperobject.biblemap.put("b43_3_24", new hinsuperobject(43,3,24,"क्योंकि यूहन्ना उस समय तक जेलखाने में नहीं डाला गया था।"));
hinsuperobject.biblemap.put("b43_3_25", new hinsuperobject(43,3,25,"वहां यूहन्ना के चेलों का किसी यहूदी के साथ शुद्धि के विषय में वाद- विवाद हुआ।"));
hinsuperobject.biblemap.put("b43_3_26", new hinsuperobject(43,3,26,"और उन्हों ने यूहन्ना के पास आकर उस से कहा, हे रब्बी, जो व्यक्ति यरदन के पार तेरे साथ था, और जिस की तू ने गवाही दी है देख, वह बपतिस्मा देता है, और सब उसके पास आते हैं।"));
hinsuperobject.biblemap.put("b43_3_27", new hinsuperobject(43,3,27,"यूहन्ना ने उत्तर दिया, जब तक मनुष्य को स्वर्ग से न दिया जाए तब तक वह कुछ नहीं पा सकता।"));
hinsuperobject.biblemap.put("b43_3_28", new hinsuperobject(43,3,28,"तुम तो आप ही मेरे गवाह हो, कि मैं ने कहा, मैं मसीह नहीं, परन्तु उसके आगे भेजा गया हूं।"));
hinsuperobject.biblemap.put("b43_3_29", new hinsuperobject(43,3,29,"जिस की दुलहिन है, वही दूल्हा है: परन्तु दूल्हे का मित्रा जो खड़ा हुआ उस की सुनता है, दूल्हे के शब्द से बहुत हर्षित होता है; अब मेरा यह हर्ष पूरा हुआ है।"));
hinsuperobject.biblemap.put("b43_3_30", new hinsuperobject(43,3,30,"अवश्य है कि वह बढ़े और मैं घटूं।।"));
hinsuperobject.biblemap.put("b43_3_31", new hinsuperobject(43,3,31,"जो ऊपर से आता है, वह सर्वोत्तम है, जो पृथ्वी से आता है वह पृथ्वी का है; और पृथ्वी की ही बातें कहता है: जो स्वर्ग से आता है, वह सब के ऊपर है।"));
hinsuperobject.biblemap.put("b43_3_32", new hinsuperobject(43,3,32,"जो कुछ उस ने देखा, और सुना है, उसी की गवाही देता है; और कोई उस की गवाही ग्रहण नहीं करता।"));
hinsuperobject.biblemap.put("b43_3_33", new hinsuperobject(43,3,33,"जिस ने उस की गवाही ग्रहण कर ली उस ने इस बात पर छाप दे दी कि परमेश्वर सच्चा है।"));
hinsuperobject.biblemap.put("b43_3_34", new hinsuperobject(43,3,34,"क्योंकि जिसे परमेश्वर ने भेजा है, वह परमेश्वर की बातें कहता है: क्योंकि वह आत्मा नाप नापकर नहीं देता।"));
hinsuperobject.biblemap.put("b43_3_35", new hinsuperobject(43,3,35,"पिता पुत्रा से प्रेम रखता है, और उस ने सब वस्तुएं उसके हाथ में दे दी हैं।"));
hinsuperobject.biblemap.put("b43_3_36", new hinsuperobject(43,3,36,"जो पुत्रा पर विश्वास करता है, अनन्त जीवन उसका है; परन्तु जो पुत्रा की नहीं मानता, वह जीवन को नहीं देखेगा, परन्तु परमेश्वर का क्रोध उस पर रहता है।।"));
hinsuperobject.biblemap.put("b43_4_1", new hinsuperobject(43,4,1,"फिर जब प्रभु को मालूम हुआ, कि फरीसियों ने सुना है, कि यीशु यूहन्ना से अधिक चेले बनाता, और उन्हें बपतिस्मा देता है।"));
hinsuperobject.biblemap.put("b43_4_2", new hinsuperobject(43,4,2,"(यद्यपि यीशु आप नहीं बरन उसके चेले बपतिस्मा देते थे)।"));
hinsuperobject.biblemap.put("b43_4_3", new hinsuperobject(43,4,3,"तब यहूदिया को छोड़कर फिर गलील को चला गया।"));
hinsuperobject.biblemap.put("b43_4_4", new hinsuperobject(43,4,4,"और उस को सामरिया से होकर जाना अवश्य था।"));
hinsuperobject.biblemap.put("b43_4_5", new hinsuperobject(43,4,5,"सो वह सूखार नाम सामरिया के एक नगर तक आया, जो उस भूमि के पास है, जिस याकूब ने अपने पुत्रा यूसुफ को दिया था।"));
hinsuperobject.biblemap.put("b43_4_6", new hinsuperobject(43,4,6,"और याकूब का कूआं भी वहीं था; सो यीशु मार्ग का थका हुआ उस कूएं पर योंही बैठ गया, और यह बात छठे घण्टे के लगभग हुई।"));
hinsuperobject.biblemap.put("b43_4_7", new hinsuperobject(43,4,7,"इतने में एक सामरी स्त्री जल भरने को आई: यीशु ने उस से कहा, मुझे पानी पिला।"));
hinsuperobject.biblemap.put("b43_4_8", new hinsuperobject(43,4,8,"क्योंकि उसके चेले तो नगर में भोजन मोल लेने को गए थे।"));
hinsuperobject.biblemap.put("b43_4_9", new hinsuperobject(43,4,9,"उस सामरी स्त्री ने उस से कहा, तू यहूदी होकर मुझ सामरी स्त्री से पानी क्यों मांगता है? (क्योंकि यहूदी सामरियों के साथ किसी प्रकार का व्यवहार नहीं रखते)।"));
hinsuperobject.biblemap.put("b43_4_10", new hinsuperobject(43,4,10,"यीशु ने उत्तर दिया, यदि तू परमेश्वर के बरदान को जानती, और यह भी जानती कि वह कौन है जो तुझ से कहता है; मुझे पानी पिला तो तू उस से मांगती, और वह तुझे जीवन का जल देता।"));
hinsuperobject.biblemap.put("b43_4_11", new hinsuperobject(43,4,11,"स्त्री ने उस से कहा, हे प्रभु, तेरे पास जल भरने को तो कुछ है भी नहीं, और कूआं गहिरा है: तो फिर वह जीवन का जल तेरे पास कहा से आया?"));
hinsuperobject.biblemap.put("b43_4_12", new hinsuperobject(43,4,12,"क्या तू हमारे पिता याकूब से बड़ा है, जिस ने हमें यह कूआं दिया; और आपही अपने सन्तान, और अपने ढारों समेत उस में से पीया?"));
hinsuperobject.biblemap.put("b43_4_13", new hinsuperobject(43,4,13,"यीशु ने उस को उत्तर दिया, कि जो कोई यह जल पीएगा वह फिर पियासा होगा।"));
hinsuperobject.biblemap.put("b43_4_14", new hinsuperobject(43,4,14,"परन्तु जो कोई उस जल में से पीएगा जो मैं उसे दूंगा, वह फिर अनन्तकाल तक पियासा न होगा: बरन जो जल मैं उसे दूंगा, वह उस में एक सोता बन जाएगा जो अनन्त जीवन के लिये उमड़ता रहेगा।"));
hinsuperobject.biblemap.put("b43_4_15", new hinsuperobject(43,4,15,"स्.ाी ने उस से कहा, हे प्रभु, वह जल मुझे दे ताकि मैं प्यासी न होऊं और न जल भरने को इतनी दूर आऊं।"));
hinsuperobject.biblemap.put("b43_4_16", new hinsuperobject(43,4,16,"यीशु ने उस से कहा, जा, अपने पति को यहां बुला ला।"));
hinsuperobject.biblemap.put("b43_4_17", new hinsuperobject(43,4,17,"स्त्री ने उत्तर दिया, कि मैं बिना पति की हूं: यीशु ने उस से कहा, तू ठीक कहती है कि मैं बिना पति की हूं।"));
hinsuperobject.biblemap.put("b43_4_18", new hinsuperobject(43,4,18,"क्योंकि तू पांच पति कर चुकी है, और जिस के पास तू अब है वह भी तेरा पति नहीं; यह तू ने सच कहा है।"));
hinsuperobject.biblemap.put("b43_4_19", new hinsuperobject(43,4,19,"स्त्री ने उस से कहा, हे प्रभु, मुझे ज्ञात होता है कि तू भविष्यद्वक्ता है।"));
hinsuperobject.biblemap.put("b43_4_20", new hinsuperobject(43,4,20,"हमारे बापदादों ने उसी पहाड़ पर भजन किया: और तुम कहते हो कि वह जगह जहां भजन करना चाहिए यरूशलेम में है।"));
hinsuperobject.biblemap.put("b43_4_21", new hinsuperobject(43,4,21,"यीशु ने उस से कहा, हे नारी, मेरी बात की प्रतीति कर कि वह समय आता है कि तुम न तो इस पहाड़ पर पिता का भजन करोगे न यरूशलेम में।"));
hinsuperobject.biblemap.put("b43_4_22", new hinsuperobject(43,4,22,"तुम जिसे नहीं जानते, उसका भजन करते हो; और हम जिसे जानते हैं उसका भजन करते हैं; क्योंकि उद्धार यहूदियों में से है।"));
hinsuperobject.biblemap.put("b43_4_23", new hinsuperobject(43,4,23,"परन्तु वह समय आता है, बरन अब भी है जिस में सच्चे भक्त पिता का भजन आत्मा और सच्चाई से करेंगे, क्योंकि पिता अपने लिये ऐसे ही भजन करनेवालों को ढूंढ़ता है।"));
hinsuperobject.biblemap.put("b43_4_24", new hinsuperobject(43,4,24,"परमेश्वर आत्मा है, और अवश्य है कि उसके भजन करनेवाले आत्मा और सच्चाई से भजन करें।"));
hinsuperobject.biblemap.put("b43_4_25", new hinsuperobject(43,4,25,"स्त्री ने उस से कहा, मैं जानती हूं कि मसीह जो ख्रीस्तुस कहलाता है, आनेवाला है; जब वह आएगा, तो हमें सब बातें बता देगा।"));
hinsuperobject.biblemap.put("b43_4_26", new hinsuperobject(43,4,26,"यीशु ने उस से कहा, मैं जो तुझ से बोल रहा हूं, वही हूं।।"));
hinsuperobject.biblemap.put("b43_4_27", new hinsuperobject(43,4,27,"इतने में उसके चेले आ गए, और अचम्भा करने लगे, कि वह स्त्री से बातें कर रहा है; तौभी किसी ने न कहा, कि तू क्या चाहता है? या किस लिये उस से बातें करता है।"));
hinsuperobject.biblemap.put("b43_4_28", new hinsuperobject(43,4,28,"तब स्त्री अपना घड़ा छोड़कर नगर में चली गई, और लोगों से कहने लगी।"));
hinsuperobject.biblemap.put("b43_4_29", new hinsuperobject(43,4,29,"आओ, एक मनुष्य को देखो, जिस ने सब कुछ जो मैं ने किया मुझे बता दिया: कहीं यह तो मसीह नहीं है?"));
hinsuperobject.biblemap.put("b43_4_30", new hinsuperobject(43,4,30,"सो वे नगर से निकलकर उसके पास आने लगे।"));
hinsuperobject.biblemap.put("b43_4_31", new hinsuperobject(43,4,31,"इतने में उसके चेले यीशु से यह बिनती करने लगे, कि हे रब्बी, कुछ खा ले।"));
hinsuperobject.biblemap.put("b43_4_32", new hinsuperobject(43,4,32,"परन्तु उस ने उन से कहा, मेरे पास खाने के लिये ऐसा भोजन है जिसे तुम नहीं जानते।"));
hinsuperobject.biblemap.put("b43_4_33", new hinsuperobject(43,4,33,"तब चेलों ने आपस में कहा, क्या कोई उसके लिये कुछ खाने को लाया है?"));
hinsuperobject.biblemap.put("b43_4_34", new hinsuperobject(43,4,34,"यीशु ने उन से कहा, मेरा भोजन यह है, कि अपने भेजनेवाले की इच्छा के अनुसार चलूं और उसका काम पूरा करूं।"));
hinsuperobject.biblemap.put("b43_4_35", new hinsuperobject(43,4,35,"क्या तुम नहीं कहते, कि कटनी होने में अब भी चार महीने पड़े हैं? देखो, मैं तुम से कहता हूं, अपनी आंखे उठाकर खेतों पर दृष्टि डालो, कि वे कटनी के लिये पक चुके हैं।"));
hinsuperobject.biblemap.put("b43_4_36", new hinsuperobject(43,4,36,"और काटनेवाला मजदूरी पाता, और अनन्त जीवन के लिये फल बटोरता है; ताकि बोनेवाला और काटनेवाला दोनों मिलकर आनन्द करें।"));
hinsuperobject.biblemap.put("b43_4_37", new hinsuperobject(43,4,37,"क्योंकि इस पर यह कहावत ठीक बैठती है कि बानेवाला और है और काटनेवाला और।"));
hinsuperobject.biblemap.put("b43_4_38", new hinsuperobject(43,4,38,"मैं ने तुम्हें वह खेत काटने के लिये भेजा, जिस में तुम ने परिश्रम नहीं किया: औरों ने परिश्रम किया और तुम उन के परिश्रम के फल में भागी हुए।।"));
hinsuperobject.biblemap.put("b43_4_39", new hinsuperobject(43,4,39,"और उस नगर के बहुत सामरियों ने उस स्त्री के कहने से, जिस ने यह गवाही दी थी, कि उस ने सब कुछ जो मैं ने किया है, मुझे बता दिया, विश्वास किया।"));
hinsuperobject.biblemap.put("b43_4_40", new hinsuperobject(43,4,40,"तब जब ये सामरी उसके पास आए, तो उस से बिनती करने लगे, कि हमारे यहां रह: सो वह वहां दो दिन तक रहा।"));
hinsuperobject.biblemap.put("b43_4_41", new hinsuperobject(43,4,41,"और उसके वचन के कारण और भी बहुतेरों ने विश्वास किया।"));
hinsuperobject.biblemap.put("b43_4_42", new hinsuperobject(43,4,42,"और उस स्त्री से कहा, अब हम तेरे कहने की से विश्वास नहीं करते; क्योंकि हम ने आप ही सुन लिया, और जानते हैं कि यही सचमुच में जगत का उद्धारकर्ता है।।"));
hinsuperobject.biblemap.put("b43_4_43", new hinsuperobject(43,4,43,"फिर उन दो दिनों के बाद वह वहां से कूच करके गलील को गया।"));
hinsuperobject.biblemap.put("b43_4_44", new hinsuperobject(43,4,44,"क्योंकि यीशु ने आप ही साक्षी दी, कि भविष्यद्वक्ता अपने देश में आदर नहीं पाता।"));
hinsuperobject.biblemap.put("b43_4_45", new hinsuperobject(43,4,45,"जब वह गलील में आया, तो गलीली आनन्द के साथ उस से मिले; क्योंकि जितने काम उस ने यरूशलेम में पर्ब्ब के समय किए थे, उन्हों ने उन सब को देखा था, क्योंकि वे भी पर्ब्ब में गए थे।।"));
hinsuperobject.biblemap.put("b43_4_46", new hinsuperobject(43,4,46,"तब वह फिर गलील के काना में आया, जहां उस ने पानी को दाख रस बनाया था: और राजा का कर्मचारी था जिस का पुत्रा कफरनहूम में बीमार था।"));
hinsuperobject.biblemap.put("b43_4_47", new hinsuperobject(43,4,47,"वह यह सुनकर कि यीशु यहूदिया से गलील में आ गया है, उसके पास गया और उस से बिनती करने लगा कि चलकर मेरे पुत्रा को चंगा कर दे: क्योंकि वह मरने पर था।"));
hinsuperobject.biblemap.put("b43_4_48", new hinsuperobject(43,4,48,"यीशु ने उस से कहा, जब तक तुम चिन्ह और अद्भुत काम न देखोगे तब तक कदापि विश्वास न करोगे।"));
hinsuperobject.biblemap.put("b43_4_49", new hinsuperobject(43,4,49,"राजा के कर्मचारी ने उस से कहा; हे प्रभु, मेरे बालक की मृत्यु होने के पहिले चल।"));
hinsuperobject.biblemap.put("b43_4_50", new hinsuperobject(43,4,50,"यीशु ने उस से कहा, जा, तेरा पुत्रा जीवित है: उस मनुष्य ने यीशु की कही हुई बात की प्रतीति की, और चला गया।"));
hinsuperobject.biblemap.put("b43_4_51", new hinsuperobject(43,4,51,"वह मार्ग में जा रहा था, कि उसके दास उस से आ मिले और कहने लगे कि तेरा लड़का जीवित है।"));
hinsuperobject.biblemap.put("b43_4_52", new hinsuperobject(43,4,52,"उस न उन से पूछा कि किस घड़ी वह अच्दा होने लगा? उन्हों ने उस से कहा, कल सातवें घण्टे में उसका ज्वर उतर गया।"));
hinsuperobject.biblemap.put("b43_4_53", new hinsuperobject(43,4,53,"तब पिता जान गया, कि यह उसी घड़ी हुआ जिस घड़ी यीशु ने उस से कहा, तेरा पुत्रा जीवित है, और उस ने और उसके सारे घराने ने विश्वास किया।"));
hinsuperobject.biblemap.put("b43_4_54", new hinsuperobject(43,4,54,"यह दूसरा आश्चर्यकर्म था, जो यीशु ने यहूदिया से गलील में आकर दिखाया।।"));
hinsuperobject.biblemap.put("b43_5_1", new hinsuperobject(43,5,1,"इन बातों के पीछे यहूदियों का एक पर्ब्ब हुआ और यीशु यरूशलेम को गया।।"));
hinsuperobject.biblemap.put("b43_5_2", new hinsuperobject(43,5,2,"यरूशलेम में भेड़- फाटक के पास एक कुण्ड है जो इब्रानी भाषा में बेतहसदा कहलाता है, और उसके पांच ओसारे हैंं।"));
hinsuperobject.biblemap.put("b43_5_3", new hinsuperobject(43,5,3,"इन में बहुत से बीमार, अन्धे, लंगड़े और सूखे अंगवाले (पानी के हिलने की आशा में) पड़े रहते थे।"));
hinsuperobject.biblemap.put("b43_5_4", new hinsuperobject(43,5,4,"(क्योंकि नियुक्ति समय पर परमेश्वर के स्वर्गदूत कुण्ड में उतरकर पानी को हिलाया करते थे: पानी हिलते ही जो कोई पहिले उतरता वह चंगा हो जाता था चाहे उसकी कोई बीमारी क्यों न हो।)"));
hinsuperobject.biblemap.put("b43_5_5", new hinsuperobject(43,5,5,"वहां एक मनुष्य था, जो अड़तीस वर्ष से बीमारी में पड़ा था।"));
hinsuperobject.biblemap.put("b43_5_6", new hinsuperobject(43,5,6,"यीशु ने उसे पड़ा हुआ देखकर और जानकर कि वह बहुत दिनों से इस दशा में पड़ा है, उस से पूछा, क्या तू चंगा होना चाहता है?"));
hinsuperobject.biblemap.put("b43_5_7", new hinsuperobject(43,5,7,"उस बीमार ने उस को उत्तर दिया, कि हे प्रभु, मेरे पास कोई मनुष्य नहीं, कि जब पानी हिलाया जाए, तो मुझे कुण्ड में उतारे; परन्तु मेरे पहुंचते पहुंचते दूसरा मुझ से पहिले उतर पड़ता है।"));
hinsuperobject.biblemap.put("b43_5_8", new hinsuperobject(43,5,8,"यीशु ने उस से कहा, उठ, अपनी खाट उठाकर चल फिर।"));
hinsuperobject.biblemap.put("b43_5_9", new hinsuperobject(43,5,9,"वह मनुष्य तुरन्त चंगा हो गया, और अपनी खाट उठाकर चलने फिरने लगा।"));
hinsuperobject.biblemap.put("b43_5_10", new hinsuperobject(43,5,10,"वह सब्त का दिन था। इसलिये यहूदी उस से, जो चंगा हुआ था, कहने लगे, कि आज तो सब्त का दिन है, तुझे खाट उठानी उचित्त नहीं।"));
hinsuperobject.biblemap.put("b43_5_11", new hinsuperobject(43,5,11,"उस ने उन्हें उत्तर दिया, कि जिस ने मुझे चंगा किया, उसी ने मुझ से कहा, अपनी खाट उठाकर चल फिर।"));
hinsuperobject.biblemap.put("b43_5_12", new hinsuperobject(43,5,12,"उन्हों ने उस से पूछा वह कौन मनुष्य है जिस ने तुझ से कहा, खाट उठाकर चल फिर?"));
hinsuperobject.biblemap.put("b43_5_13", new hinsuperobject(43,5,13,"परन्तु जो चंगा हो गया था, वह नहीं जानता था वह कौन है; क्योंकि उस जगह में भीड़ होने के कारण यीशु वहां से हट गया था।"));
hinsuperobject.biblemap.put("b43_5_14", new hinsuperobject(43,5,14,"इन बातों के बाद वह यीशु को मन्दिर में मिला, तब उस न उस से कहा, देख, तू तो चंगा हो गया है; फिर से पाप मत करना, ऐसा न हो कि इस से कोई भारी विपत्ति तुझ पर आ पड़े।"));
hinsuperobject.biblemap.put("b43_5_15", new hinsuperobject(43,5,15,"उस मनुष्य ने जाकर यहूदियों से कह दिया, कि जिस ने मुझे चंगा किया, वह यीशु है।"));
hinsuperobject.biblemap.put("b43_5_16", new hinsuperobject(43,5,16,"इस कारण यहूदी यीशु को सताने लगे, क्योंकि वह ऐसे ऐसे काम सब्त के दिन करता था।"));
hinsuperobject.biblemap.put("b43_5_17", new hinsuperobject(43,5,17,"इस पर यीशु ने उन से कहा, कि मेरा पिता अब तक काम करता है, और मैं भी काम करता हूं।"));
hinsuperobject.biblemap.put("b43_5_18", new hinsuperobject(43,5,18,"इस कारण यहूदी और भी अधिक उसके मार डालने का प्रयत्न करने लगे, कि वह न केवल सब्त के दिन की विधि को तोड़ता, परन्तु परमेश्वर को अपना पिता कह कर, अपने आप को परमेश्वर के तुल्य ठहराता था।।"));
hinsuperobject.biblemap.put("b43_5_19", new hinsuperobject(43,5,19,"इस पर यीशु ने उन से कहा, मैं तुम से सच सच कहता हूं, पुत्रा आप से कुछ नहीं कर सकता, केवल वह जो पिता को करते देखता है, क्योंकि जिन जिन कामों को वह करता है उन्हें पुत्रा भी उसी रीति से करता है।"));
hinsuperobject.biblemap.put("b43_5_20", new hinsuperobject(43,5,20,"क्योंकि पिता पुत्रा से प्रीति रखता है और जो जो काम वह आप करता है, वह सब उसे दिखाता है; और वह इन से भी बड़े काम उसे दिखाएगा, ताकि तुम अचम्भा करो।"));
hinsuperobject.biblemap.put("b43_5_21", new hinsuperobject(43,5,21,"क्योकि जैसा पिता मरे हुओं को उठाता और जिलाता है, वैसा ही पुत्रा भी जिन्हें चाहता है उन्हें जिलाता है।"));
hinsuperobject.biblemap.put("b43_5_22", new hinsuperobject(43,5,22,"और पिता किसी का न्याय भी नहीं करता, परन्तु न्याय करने का सब काम पुत्रा को सौंप दिया है।"));
hinsuperobject.biblemap.put("b43_5_23", new hinsuperobject(43,5,23,"इसलिये कि सब लोग जैसे पिता का आदर करते हैं वैसे ही पुत्रा का भी आदर करें: जो पुत्रा का आदर नहीं करता, वह पिता का जिस ने उसे भेजा है, आदर नहीं करता।"));
hinsuperobject.biblemap.put("b43_5_24", new hinsuperobject(43,5,24,"मैं तुम से सच सच कहता हूं, जो मेरा वचन सुनकर मेरे भेजनेवाले की प्रतीति करता है, अनन्त जीवन उसका है, और उस पर दंड की आज्ञा नहीं होती परन्तु वह मृत्यु से पार होकर जीवन में प्रवेश कर चुका है।"));
hinsuperobject.biblemap.put("b43_5_25", new hinsuperobject(43,5,25,"मैं तुम से सच सच कहता हूं, वह समय आता है, और अब है, जिस में मृतक परमेश्वर के पुत्रा का शब्द सुनेंगे, और जो सुनेंगे वे जीएंगे।"));
hinsuperobject.biblemap.put("b43_5_26", new hinsuperobject(43,5,26,"क्योंकि जिस रीति से पिता अपने आप में जीवन रखता है, उसी रीति से उस ने पुत्रा को भी यह अधिकार दिया है कि अपने आप में जीवन रखे।"));
hinsuperobject.biblemap.put("b43_5_27", new hinsuperobject(43,5,27,"बरन उसे न्याय करने का भी अधिकार दिया है, इसलिये कि वह मनुष्य का पुत्रा है।"));
hinsuperobject.biblemap.put("b43_5_28", new hinsuperobject(43,5,28,"इस से अचम्भा मत करो, क्योंकि वह समय आता है, कि जितने कब्रों में हैं, उसका शब्द सुनकर निकलेंगे।"));
hinsuperobject.biblemap.put("b43_5_29", new hinsuperobject(43,5,29,"जिन्हों ने भलाई की है वे जीवन के पुनरूत्थान के लिये जी उठेंगे और जिन्हों ने बुराई की है वे दंड के पुनरूत्थान के लिये जी उठेंगे।"));
hinsuperobject.biblemap.put("b43_5_30", new hinsuperobject(43,5,30,"मैं अपने आप से कुछ नहीं कर सकता; जैसा सुनता हूं, वैसा न्याय करता हूं, और मेरा न्याय सच्चा है; क्योंकि मैं अपनी इच्छा नहीं, परन्तु अपने भेजनेवाले की इच्छा चाहता हूं।"));
hinsuperobject.biblemap.put("b43_5_31", new hinsuperobject(43,5,31,"यदि मैं आप ही अपनी गवाही दूं; तो मेरी गवाही सच्ची नहीं।"));
hinsuperobject.biblemap.put("b43_5_32", new hinsuperobject(43,5,32,"एक और है जो मेरी गवाही देता है वह सच्ची है।"));
hinsuperobject.biblemap.put("b43_5_33", new hinsuperobject(43,5,33,"तुम ने यूहन्ना से पुछवाया और उस ने सच्चाई की गवाही दी है।"));
hinsuperobject.biblemap.put("b43_5_34", new hinsuperobject(43,5,34,"परन्तु मैं अपने विषय में मनुष्य की गवाही नहीं चाहता; तौभी मैं ये बातें इसलिये कहता हूं, कि तुम्हें उद्धार मिले।"));
hinsuperobject.biblemap.put("b43_5_35", new hinsuperobject(43,5,35,"वह जो जलता और चमकता हुआ दीपक था; और तुम्हें कुछ देर तक उस की ज्योति में, मगन होना अच्छा लगा।"));
hinsuperobject.biblemap.put("b43_5_36", new hinsuperobject(43,5,36,"परन्तु मेरे पास जो गवाही है वह यूहन्ना की गवाही से बड़ी है: क्योंकि जो काम पिता ने मुझे पूरा करने को सौंपा है अर्थात् यही काम जो मैं करता हूं, वे मेरे गवाह हैं, कि पिता ने मुझे भेजा है।"));
hinsuperobject.biblemap.put("b43_5_37", new hinsuperobject(43,5,37,"और पिता जिस ने मुझे भेजा है, उसी ने मेरी गवाही दी है: तुम ने न कभी उसका शब्द सुना, और न उसका रूप देखा है।"));
hinsuperobject.biblemap.put("b43_5_38", new hinsuperobject(43,5,38,"और उसके वचन को मन में स्थिर नहीं रखते क्योंकि जिसे उस ने भेजा उस की प्रतीति नहीं करते।"));
hinsuperobject.biblemap.put("b43_5_39", new hinsuperobject(43,5,39,"तुम पवित्रा शास्त्रा में ढूंढ़ते हो, क्योंकि समझते हो कि उस में अनन्त जीवन तुम्हें मिलता है, और यह वही है, जो मेरी गवाही देता है।"));
hinsuperobject.biblemap.put("b43_5_40", new hinsuperobject(43,5,40,"फिर भी तुम जीवन पाने के लिये मेरे पास आना नहीं चाहते।"));
hinsuperobject.biblemap.put("b43_5_41", new hinsuperobject(43,5,41,"मैं मनुष्यों से आदर नहीं चाहता।"));
hinsuperobject.biblemap.put("b43_5_42", new hinsuperobject(43,5,42,"परन्तु मैं तुम्हें जानता हूं, कि तुम में परमेश्वर का प्रेम नहीं।"));
hinsuperobject.biblemap.put("b43_5_43", new hinsuperobject(43,5,43,"मैं अपने पिता के नाम से आया हूं, और तुम मुझे ग्रहण नहीं करते; यदि कोई और अपने ही नाम से आए, तो उसे ग्रहण कर लोगे।"));
hinsuperobject.biblemap.put("b43_5_44", new hinsuperobject(43,5,44,"तुम जो एक दूसरे से आदर चाहते हो और वह आदर जो अद्वैत परमेश्वर की ओर से है, नहीं चाहते, किसी प्रकार विश्वास कर सकते हो?"));
hinsuperobject.biblemap.put("b43_5_45", new hinsuperobject(43,5,45,"यह न समझो, कि मैं पिता के साम्हने तुम पर दोष लगाऊंगा: तुम पर दोष लगानेवाला तो है, अर्थात् मूसा जिस पर तुम ने भरोसा रखा है।"));
hinsuperobject.biblemap.put("b43_5_46", new hinsuperobject(43,5,46,"क्योंकि यदि तुम मूसा की प्रतीति करते, तो मेरी भी प्रतीति करते, इसलिये कि उस ने मेरे विषय में लिखा है।"));
hinsuperobject.biblemap.put("b43_5_47", new hinsuperobject(43,5,47,"परन्तु यदि तुम उस की लिखी हुई बातों की प्रतीति नहीं करते, तो मेरी बातों की क्योंकर प्रतीति करोगे।।"));
hinsuperobject.biblemap.put("b43_6_1", new hinsuperobject(43,6,1,"इन बातों के बाद यीशु गलील की झील अर्थात् तिबिरियास की झील के पास गया।"));
hinsuperobject.biblemap.put("b43_6_2", new hinsuperobject(43,6,2,"और एक बड़ी भीड़ उसके पीछे हो ली कयोंकि जो आश्चर्य कर्म वह बीमारों पर दिखाता था वे उन को देखते थे।"));
hinsuperobject.biblemap.put("b43_6_3", new hinsuperobject(43,6,3,"तब यीशु पहाड़ पर चढ़कर अपने चेलों के साथ वहां बैठा।"));
hinsuperobject.biblemap.put("b43_6_4", new hinsuperobject(43,6,4,"और यहूदियों के फसह के पर्ब्ब निकट था।"));
hinsuperobject.biblemap.put("b43_6_5", new hinsuperobject(43,6,5,"तब यीशु ने अपनी आंखे उठाकर एक बड़ी भीड़ को अपने पास आते देखा, और फिलिप्पुस से कहा, कि हम इन के भोजन के लिये कहां से रोटी मोल लाएं?"));
hinsuperobject.biblemap.put("b43_6_6", new hinsuperobject(43,6,6,"परन्तु उस ने यह बात उसे परखने के लिये कही; क्योंकि वह आप जानता था कि मैं क्या करूंगा।"));
hinsuperobject.biblemap.put("b43_6_7", new hinsuperobject(43,6,7,"फिलिप्पुस ने उस को उत्तर दिया, कि दो सौ दीनार की रोटी उन के लिये पूरी भी न होंगी कि उन में से हर एक को थोड़ी थोड़ी मिल जाए।"));
hinsuperobject.biblemap.put("b43_6_8", new hinsuperobject(43,6,8,"उसके चेलों में से शमौन पतरस के भाई अन्द्रियास ने उस से कहा।"));
hinsuperobject.biblemap.put("b43_6_9", new hinsuperobject(43,6,9,"यहां एक लड़का है जिस के पास जव की पांच रोटी और दो मछलियां हैं परन्तु इतने लोगों के लिये वे क्या हैं।"));
hinsuperobject.biblemap.put("b43_6_10", new hinsuperobject(43,6,10,"यीशु ने कहा, कि लोगों को बैठा दो। उस जगह बहुत घास थी: तब वे लोग जो गिनती में लगभग पांच हजार के थे, बैठ गए:"));
hinsuperobject.biblemap.put("b43_6_11", new hinsuperobject(43,6,11,"तब यीशु ने रोटियां लीं, और धन्यवाद करके बैठनेवालों को बांट दी: और वैसे ही मछलियों में से जितनी वे चाहते थे बांट दिया।"));
hinsuperobject.biblemap.put("b43_6_12", new hinsuperobject(43,6,12,"जब वे खाकर तृप्त हो गए तो उस ने अपने चेलों से कहा, कि बचे हुए टुकड़े बटोर लो, कि कुछ फेंका न जाए।"));
hinsuperobject.biblemap.put("b43_6_13", new hinsuperobject(43,6,13,"सो उन्हों ने बटोरा, और जव की पांच रोटियों के टुकड़े जो खानेवालों से बच रहे थे उन की बारह टोकरियां भरीं।"));
hinsuperobject.biblemap.put("b43_6_14", new hinsuperobject(43,6,14,"तब जो आश्चर्य कर्म उस ने कर दिखाया उसे वे लोग देखकर कहने लगे; कि वह भविष्यद्वक्ता जो जगत में आनेवाला था निश्चय यही है।"));
hinsuperobject.biblemap.put("b43_6_15", new hinsuperobject(43,6,15,"यीशु यह जानकर कि वे मुझे राजा बनाने के लिये आकर पकड़ना चाहते हैं, फिर पहाड़ पर अकेला चला गया।"));
hinsuperobject.biblemap.put("b43_6_16", new hinsuperobject(43,6,16,"फिर जब संध्या हुई, तो उसके चेले झील के किनारे गए।"));
hinsuperobject.biblemap.put("b43_6_17", new hinsuperobject(43,6,17,"और नाव पर चढ़कर झील के पार कफरनहूम को जाने लगे: उस समय अन्धेरा हो गया था, और यीशु अभी तक उन के पास नहीं आया था।"));
hinsuperobject.biblemap.put("b43_6_18", new hinsuperobject(43,6,18,"और आन्धी के कारण झील में लहरे उठने लगीं।"));
hinsuperobject.biblemap.put("b43_6_19", new hinsuperobject(43,6,19,"सो जब वे खेते खेते तीन चार मील के लगभग निकल गए, तो उन्हों ने यीशु को झील पर चलते, और नाव के निकट आते देखा, और डर गए।"));
hinsuperobject.biblemap.put("b43_6_20", new hinsuperobject(43,6,20,"परन्तु उस ने उन से कहा, कि मैं हूं; डरो मत।"));
hinsuperobject.biblemap.put("b43_6_21", new hinsuperobject(43,6,21,"सो वे उसे नाव पर चढ़ा लेने के लिये तैयार हुए और तुरन्त वह नाव के स्थान पर जा पहुंची जहां वह जाते थे।"));
hinsuperobject.biblemap.put("b43_6_22", new hinsuperobject(43,6,22,"दूसरे दिन उस भीड़ ने, जो झील के पार खड़ी थी, यह देखा, कि यहां एक को छोड़कर और कोई छोटी नाव न थी, और यीशु अपने चेलों के साथ उस नाव पर न चढ़ा, परन्तु केवल उसके चेले चले गए थे।"));
hinsuperobject.biblemap.put("b43_6_23", new hinsuperobject(43,6,23,"(तौभी और छोटी नावें तिबिरियास से उस जगह के निकट आई, जहां उन्हों ने प्रभु के धन्यवाद करने के बाद रोटी खाई थी।)"));
hinsuperobject.biblemap.put("b43_6_24", new hinsuperobject(43,6,24,"सो जब भीड़ ने देखा, कि यहां न यीशु है, और न उसके चेले, तो वे भी छोटी छोटी नावों पर चढ़ के यीशु को ढूंढ़ते हुए कफरनहूम को पहुंचे।"));
hinsuperobject.biblemap.put("b43_6_25", new hinsuperobject(43,6,25,"और झील के पार उस से मिलकर कहा, हे रब्बी, तू यहां कब आया?"));
hinsuperobject.biblemap.put("b43_6_26", new hinsuperobject(43,6,26,"यीशु ने उन्हें उत्तर दिया, कि मैं तुम से सच सच कहता हूं, तुम मुझे इसलिये नहीं ढूंढ़ते हो कि तुम ने अचम्भित काम देखे, परन्तु इसलिये कि तुम रोटियां खाकर तृप्त हुए।"));
hinsuperobject.biblemap.put("b43_6_27", new hinsuperobject(43,6,27,"नाशमान भोजन के लिये परिश्रम न करो, परन्तु उस भोजन के लिये जो अनन्त जीवन तक ठहरता है, जिसे मनुष्य का पुत्रा तुम्हें देगा, क्योंकि पिता, अर्थात् परमेश्वर ने उसी पर छाप कर दी है।"));
hinsuperobject.biblemap.put("b43_6_28", new hinsuperobject(43,6,28,"उन्हों ने उस से कहा, परमेश्वर के कारर्य करने के लिये हम क्या करें?"));
hinsuperobject.biblemap.put("b43_6_29", new hinsuperobject(43,6,29,"यीशु ने उन्हें उत्तर दिया; परमेश्वर का कारर्य यह है, कि तुम उस पर, जिसे उस ने भेजा है, विश्वास करो।"));
hinsuperobject.biblemap.put("b43_6_30", new hinsuperobject(43,6,30,"तब उन्हों ने उस से कहा, फिर तू कौन का चिन्ह दिखाता है कि हम उसे देखकर तेरी प्रतीति करें, तू कौन सा काम दिखाता है?"));
hinsuperobject.biblemap.put("b43_6_31", new hinsuperobject(43,6,31,"हमारे बापदादों ने जंगल में मन्ना खाया; जैसा लिखा है; कि उस ने उन्हें खाने के लिये स्वर्ग से रोटी दी।"));
hinsuperobject.biblemap.put("b43_6_32", new hinsuperobject(43,6,32,"यीशु ने उन से कहा, मैं तुम से सच सच कहता हूं कि मूसा ने तुम्हें वह रोटी स्वर्ग से न दी, परन्तु मेरा पिता तुम्हें सच्ची रोटी स्वर्ग से देता है।"));
hinsuperobject.biblemap.put("b43_6_33", new hinsuperobject(43,6,33,"क्योकि परमेश्वर की रोटी वही है, जो स्वर्ग से उतरकर जगत को जीवन देती है।"));
hinsuperobject.biblemap.put("b43_6_34", new hinsuperobject(43,6,34,"तब उन्हों ने उस से कहा, हे प्रभु, यह रोटी हमें सर्वदा दिया कर।"));
hinsuperobject.biblemap.put("b43_6_35", new hinsuperobject(43,6,35,"यीशु ने उन से कहा, जीवन की रोटी मैं हूं: जो मेरे पास आएगा वह कभी भूखा न होगा और जो मुझ पर विश्वास करेगा, वह कभी पियासा न होगा।"));
hinsuperobject.biblemap.put("b43_6_36", new hinsuperobject(43,6,36,"परन्तु मैं ने तुम से कहा, कि तुम ने मुझे देख भी लिया है, तोभी विश्वास नहीं करते।"));
hinsuperobject.biblemap.put("b43_6_37", new hinsuperobject(43,6,37,"जो कुछ पिता मुझे देता है वह सब मेरे पास आएगा, उसे मैं कभी न निकालूंगा।"));
hinsuperobject.biblemap.put("b43_6_38", new hinsuperobject(43,6,38,"क्योंकि मैं अपनी इच्छा नहीं, बरन अपने भेजनेवाले की इच्छा पूरी करने के लिये स्वर्ग से उतरा हूं।"));
hinsuperobject.biblemap.put("b43_6_39", new hinsuperobject(43,6,39,"और मेरे भेजनेवाले की इच्छा यह है कि जो कुछ उस ने मुझे दिया है, उस में से मैं कुछ न खोऊं परन्तु उसे अंतिम दिन फिर जिला उठाऊं।"));
hinsuperobject.biblemap.put("b43_6_40", new hinsuperobject(43,6,40,"क्योंकि मेरे पिता की इच्छा यह है, कि जो कोई पुत्रा को देखे, और उस पर विश्वास करे, वह अनन्त जीवन पाए; और मैं उसे अंतिम दिन फिर जिला उठाऊंगा।"));
hinsuperobject.biblemap.put("b43_6_41", new hinsuperobject(43,6,41,"सो यहूदी उस पर कुड़कुड़ाने लगे, इसलिये कि उस ने कहा था; कि जो रोटी स्वर्ग से उतरी, वह मैं हूं।"));
hinsuperobject.biblemap.put("b43_6_42", new hinsuperobject(43,6,42,"और उन्हों ने कहा; क्या यह यूसुफ का पुत्रा यीशु नहीं, जिस के माता पिता को हम जानते हैं? तो वह क्योंकर कहता है कि मैं स्वर्ग से उतरा हूं।"));
hinsuperobject.biblemap.put("b43_6_43", new hinsuperobject(43,6,43,"यीशु ने उन को उत्तर दिया, कि आपस में मत कुड़कुड़ाओ।"));
hinsuperobject.biblemap.put("b43_6_44", new hinsuperobject(43,6,44,"कोई मेरे पास नहीं आ सकता, जब तक पिता, जिस ने मुझे भेजा है, उसे खींच न ले; और मैं उस को अंतिम दिन फिर जिला उठाऊंगा।"));
hinsuperobject.biblemap.put("b43_6_45", new hinsuperobject(43,6,45,"भविष्यद्वक्ताओं के लेखों में यह लिखा है, कि वे सब परमेश्वर की ओर से सिखाए हुए होंगे। जिस किसी ने पिता से सुना और सीखा है, वह मेरे पास आता है।"));
hinsuperobject.biblemap.put("b43_6_46", new hinsuperobject(43,6,46,"यह नहीं, कि किसी ने पिता को देखा परन्तु जो परमेश्वर की ओर से है, केवल उसी ने पिता को देखा है।"));
hinsuperobject.biblemap.put("b43_6_47", new hinsuperobject(43,6,47,"मैं तुम से सच सच कहता हूं, कि जो कोई विश्वास करता है, अनन्त जीवन उसी का है।"));
hinsuperobject.biblemap.put("b43_6_48", new hinsuperobject(43,6,48,"जीवन की रोटी मैं हूं।"));
hinsuperobject.biblemap.put("b43_6_49", new hinsuperobject(43,6,49,"तुम्हारे बापदादों ने जंगल में मन्ना खाया और मर गए।"));
hinsuperobject.biblemap.put("b43_6_50", new hinsuperobject(43,6,50,"यह वह रोटी है जो स्वर्ग से उतरती है ताकि मनुष्य उस में से खाए और न मरे।"));
hinsuperobject.biblemap.put("b43_6_51", new hinsuperobject(43,6,51,"जीवन की रोटी जो स्वर्ग से उतरी मैं हूं। यदि कोई इस रोटी में से खाए, तो सर्वदा जीवित रहेगा और जो रोटी मैं जगत के जीवन के लिये दूंगा, वह मेरा मांस है।"));
hinsuperobject.biblemap.put("b43_6_52", new hinsuperobject(43,6,52,"इस पर यहूदी यह कहकर आपस में झगड़ने लगे, कि यह मनुष्य क्योंकर हमें अपना मांस खाने को दे सकता है?"));
hinsuperobject.biblemap.put("b43_6_53", new hinsuperobject(43,6,53,"यीशु ने उन से कहा; मैं तुम से सच सच कहता हूं जब तक मनुष्य के पुत्रा का मांस न खाओ, और उसका लोहू न पीओ, तुम में जीवन नहीं।"));
hinsuperobject.biblemap.put("b43_6_54", new hinsuperobject(43,6,54,"जो मेरा मांस खाता, और मेरा लोहू पीता हे, अनन्त जीवन उसी का है, और मैं अंतिम दिन फिर उसे जिला उठाऊंगा।"));
hinsuperobject.biblemap.put("b43_6_55", new hinsuperobject(43,6,55,"क्योंकि मेरा मांस वास्तव में खाने की वस्तु है और मेरा लोहू वास्तव में पीन की वस्तु है।"));
hinsuperobject.biblemap.put("b43_6_56", new hinsuperobject(43,6,56,"जो मेरा मांस खाता और मेरा लोहू पीता है, वह मुझ में स्थिर बना रहता है, और मैं उस में।"));
hinsuperobject.biblemap.put("b43_6_57", new hinsuperobject(43,6,57,"जैसा जीवते पिता ने मुझे भेजा और मैं पिता के कारण जीवित हूं वैसा ही वह भी जो मुझे खाएगा मेरे कारण जीवित रहेगा।"));
hinsuperobject.biblemap.put("b43_6_58", new hinsuperobject(43,6,58,"जो रोटी स्वर्ग से उतरी यही है, बापदादों के समान नहीं कि खाया, और मर गए: जो कोई यह रोटी खाएगा, वह सर्वदा जीवित रहेगा।"));
hinsuperobject.biblemap.put("b43_6_59", new hinsuperobject(43,6,59,"ये बातें उस ने कफरनहूम के एक आराधनालय में उपदेश देते समय कहीं।"));
hinsuperobject.biblemap.put("b43_6_60", new hinsuperobject(43,6,60,"इसलिये उसके चेलों में से बहुतों ने यह सुनकर कहा, कि यह बात नागवार है; इसे कौन सुन सकता है?"));
hinsuperobject.biblemap.put("b43_6_61", new hinsuperobject(43,6,61,"यीशु ने अपने मन में यह जान कर कि मेरे चेले आपस में इस बात पर कुड़कुड़ाते हैं, उन से पूछा, क्या इस बात से तुम्हें ठोकर लगती है?"));
hinsuperobject.biblemap.put("b43_6_62", new hinsuperobject(43,6,62,"और यदि तुम मनुष्य के पुत्रा को जहां वह पहिले था, वहां ऊपर जाते देखोगे, तो क्या होगा?"));
hinsuperobject.biblemap.put("b43_6_63", new hinsuperobject(43,6,63,"आत्मा तो जीवनदायक है, शरीर से कुछ लाभ नहीं: जो बातें मैं ने तुम से कहीं हैं वे आत्मा है, और जीवन भी हैं।"));
hinsuperobject.biblemap.put("b43_6_64", new hinsuperobject(43,6,64,"परन्तु तुम में से कितने ऐसे हैं जो विश्वास नहीं करते: क्योंकि यीशु तो पहिले ही से जानता था कि जो विश्वास नहीं करते, वे कौन हैं? और कौन मुझे पकड़वाएगा।"));
hinsuperobject.biblemap.put("b43_6_65", new hinsuperobject(43,6,65,"और उस ने कहा, इसी लिये मैं ने तुम से कहा था कि जब तक किसी को पिता की ओर यह बरदान न दिया जाए तक तक वह मेरे पास नहीं आ सकता।"));
hinsuperobject.biblemap.put("b43_6_66", new hinsuperobject(43,6,66,"इस पर उसके चेलों में से बहुतेरे उल्टे फिर गए और उसके बाद उसके साथ न चले।"));
hinsuperobject.biblemap.put("b43_6_67", new hinsuperobject(43,6,67,"तब यीशु ने उन बारहों से कहा, क्या तुम भी चले जाना चाहते हो?"));
hinsuperobject.biblemap.put("b43_6_68", new hinsuperobject(43,6,68,"शमौन पतरस ने उस को उत्तर दिया, कि हे प्रभु हम किस के पास जाएं? अनन्त जीवन की बातें तो तेरे ही पास हैं।"));
hinsuperobject.biblemap.put("b43_6_69", new hinsuperobject(43,6,69,"और हम ने विश्वास किया, और जान गए हैं, कि परमेश्वर का पवित्रा जन तू ही है।"));
hinsuperobject.biblemap.put("b43_6_70", new hinsuperobject(43,6,70,"यीशु ने उन्हें उत्तर दिया, क्या मैं ने तुम बारहों को नहीं चुन लिया? तौभी तुम में से एक व्यक्ति शैतान है।"));
hinsuperobject.biblemap.put("b43_6_71", new hinsuperobject(43,6,71,"यह उस ने शमौन इस्करियोती के पुत्रा यहूदाह के विषय में कहा, क्योंकि यही जो उन बारहों में से था, उसे पकड़वाने को था।।"));
hinsuperobject.biblemap.put("b43_7_1", new hinsuperobject(43,7,1,"इन बातों के बाद यीशु गलील में फिरता रहा, क्योंकि यहूदी उसे मार डालने का यत्न कर रहे थे, इसलिये वह यहूदिया में फिरना न चाहता था।"));
hinsuperobject.biblemap.put("b43_7_2", new hinsuperobject(43,7,2,"और यहूदियों का मण्डपों का पर्ब्ब निकट था।"));
hinsuperobject.biblemap.put("b43_7_3", new hinsuperobject(43,7,3,"इसलिये उसके भाइयों ने उस से कहा, यहां से कूच करके यहूदिया में चला जा, कि जो काम तू करता है, उन्हें तेरे चेले भी देखें।"));
hinsuperobject.biblemap.put("b43_7_4", new hinsuperobject(43,7,4,"क्योंकि ऐसा कोई न होगा जो प्रसिद्ध होना चाहे, और छिपकर काम करे: यदि तू यह काम करता है, तो अपने तई जगत पर प्रगट कर।"));
hinsuperobject.biblemap.put("b43_7_5", new hinsuperobject(43,7,5,"क्योंकि उसके भाई भी उस पर विश्वास नहीं करते थे।"));
hinsuperobject.biblemap.put("b43_7_6", new hinsuperobject(43,7,6,"तब यीशु ने उन से कहा, मेरा समय अभी नहीं आया; परन्तु तुम्हारे लिये सब समय है।"));
hinsuperobject.biblemap.put("b43_7_7", new hinsuperobject(43,7,7,"जगत तुम से बैर नहीं कर सकता, परन्तु वह मुझ से बैर करता है, क्योंकि मैं उसके विरोध में यह गवाही देता हूं, कि उसके काम बुरे हैं।"));
hinsuperobject.biblemap.put("b43_7_8", new hinsuperobject(43,7,8,"तुम पर्ब्ब में जाओ: मैं अभी इस पर्ब्ब में नहीं जाता; क्योंकि अभी तक मेरा समय पूरा नहीं हुआ।"));
hinsuperobject.biblemap.put("b43_7_9", new hinsuperobject(43,7,9,"वह उन से ये बातें कहकर गलील ही में रह गया।।"));
hinsuperobject.biblemap.put("b43_7_10", new hinsuperobject(43,7,10,"परन्तु जब उसके भाई पर्ब्ब में चले गए, तो वह आप ही प्रगट में नहीं, परन्तु मानों गुप्त होकर गया।"));
hinsuperobject.biblemap.put("b43_7_11", new hinsuperobject(43,7,11,"तो यहूदी पर्ब्ब में उसे यह कहकर ढूंढ़ने लगे कि वह कहां है?"));
hinsuperobject.biblemap.put("b43_7_12", new hinsuperobject(43,7,12,"और लोगों में उसके विषय चुपके चुपके बहुत सी बातें हुईं: कितने कहते थे; वह भला मनुष्य है: और कितने कहते थे; नहीं, वह लोगों को भरमाता है।"));
hinsuperobject.biblemap.put("b43_7_13", new hinsuperobject(43,7,13,"तौभी यहूदियों के भय के मारे कोई व्यक्ति उसके विषय में खुलकर नहीं बोलता था।"));
hinsuperobject.biblemap.put("b43_7_14", new hinsuperobject(43,7,14,"और जब पर्ब्ब के आधे दिन बीत गए; तो यीशु मन्दिर में जाकर उपदेश करने लगा।"));
hinsuperobject.biblemap.put("b43_7_15", new hinsuperobject(43,7,15,"तब यहूदियों ने अचम्भा करके कहा, कि इसे बिन पढ़े विद्या कैसे आ गई?"));
hinsuperobject.biblemap.put("b43_7_16", new hinsuperobject(43,7,16,"यीशु ने उन्हें उत्तर दिया, कि मेरा उपदेश मेरा नहीं, परन्तु मेरे भेजनेवाले का है।"));
hinsuperobject.biblemap.put("b43_7_17", new hinsuperobject(43,7,17,"यदि कोई उस की इच्छा पर चलना चाहे, तो वह इस उपदेश के विषय में जान जाएगा कि वह परमेश्वर की ओर से है, या मैं अपनी ओर से कहता हूं।"));
hinsuperobject.biblemap.put("b43_7_18", new hinsuperobject(43,7,18,"जो अपनी ओर से कुछ कहता है, वह अपनी ही बढ़ाई चाहता है; परन्तु जो अपने भेजनेवाले की बड़ाई चाहता है वही सच्चा है, और उस में अधर्म नहीं।"));
hinsuperobject.biblemap.put("b43_7_19", new hinsuperobject(43,7,19,"क्या मूसा ने तुम्हें व्यवस्था नहीं दी? तौभी तुम में से काई व्यवस्था पर नहीं चलता। तुम क्यों मुझे मार डालना चाहते हो?"));
hinsuperobject.biblemap.put("b43_7_20", new hinsuperobject(43,7,20,"लोगों ने उत्तर दिया; कि तुझ में दुष्टात्मा है; कौन तुझे मार डालना चाहता है?"));
hinsuperobject.biblemap.put("b43_7_21", new hinsuperobject(43,7,21,"यीशु ने उन को उत्तर दिया, कि मैं ने एक काम किया, और तुम सब अचम्भा करते हो।"));
hinsuperobject.biblemap.put("b43_7_22", new hinsuperobject(43,7,22,"इसी कारण मूसा ने तुम्हें खतने की आज्ञा दी है (यह नहीं कि वह मूसा की ओर से है परन्तु बाप- दादों से चली आई है), और तुम सब्त के दिन को मनुष्य का खतना करते हो।"));
hinsuperobject.biblemap.put("b43_7_23", new hinsuperobject(43,7,23,"जब सब्त के दिन मनुष्य का खतना किया जाता है ताकि मूसा की व्यवस्था की आज्ञा टल न जाए, तो तुम मुझ पर क्यों इसलिये क्रोध करते हो, कि मैं ने सब्त के दिन एक मनुष्य को पूरी रीति से चंगा किया।"));
hinsuperobject.biblemap.put("b43_7_24", new hinsuperobject(43,7,24,"मुंह देखकर न्याय न चुकाओ, परन्तु ठीक ठीक न्याय चुकाओ।।"));
hinsuperobject.biblemap.put("b43_7_25", new hinsuperobject(43,7,25,"तब कितने यरूशलेमी कहने लगे; क्या यह वह नहीं, जिस के मार डालने का प्रयत्न किया जा रहा है।"));
hinsuperobject.biblemap.put("b43_7_26", new hinsuperobject(43,7,26,"परन्तु देखो, वह तो खुल्लमखुल्ला बातें करता है और कोई उस से कुछ नहीं कहता; क्या सम्भव है कि सरदारों ने सच सच जान लिया है; कि यही मसीह है।"));
hinsuperobject.biblemap.put("b43_7_27", new hinsuperobject(43,7,27,"इस को तो हम जानते हैं, कि यह कहां का है; परन्तु मसीह जब आएगा, तो कोई न जानेगा कि वह कहां का है।"));
hinsuperobject.biblemap.put("b43_7_28", new hinsuperobject(43,7,28,"तब यीशु ने मन्दिर में उपदेश देते हुए पुकार के कहा, तुम मुझे जानते हो और यह भी जानते हो कि मैं कहां का हूं: मैं तो आप से नहीं आया परन्तु मेरा भेजनेवाला सच्चा है, उस को तुम नहीं जानते।"));
hinsuperobject.biblemap.put("b43_7_29", new hinsuperobject(43,7,29,"मैं उसे जानता हूं; क्योंकि मैं उस की ओर से हूं और उसी ने मुझे भेजा है।"));
hinsuperobject.biblemap.put("b43_7_30", new hinsuperobject(43,7,30,"इस पर उन्हों ने उसे पकड़ना चाहा तौभी किसी ने उस पर हाथ न डाला, क्योंकि उसका समय अब तक न आया था।"));
hinsuperobject.biblemap.put("b43_7_31", new hinsuperobject(43,7,31,"और भीड़ में से बहुतेरों ने उस पर विश्वास किया, और कहने लगे, कि मसीह जब आएगा, तो क्या इस से अधिक आश्चर्यकर्म दिखाएगा जो इस ने दिखाए?"));
hinsuperobject.biblemap.put("b43_7_32", new hinsuperobject(43,7,32,"फरीसियों ने लोगों को उसके विषय में ये बातें चुपके चुपके करते सुना; और महायाजकों और फरीसियों ने उसके पकड़ने को सिपाही भेजे।"));
hinsuperobject.biblemap.put("b43_7_33", new hinsuperobject(43,7,33,"इस पर यीशु ने कहा, मैं थोड़ी देर तक और तुम्हारे साथ हूं; तब अपने भेजनेवाले के पास चला जाऊंगा।"));
hinsuperobject.biblemap.put("b43_7_34", new hinsuperobject(43,7,34,"तुम मुझे ढूंढ़ोगे, परन्तु नहीं पाओगे और जहां मैं हूं, वहां तुम नहीं आ सकते।"));
hinsuperobject.biblemap.put("b43_7_35", new hinsuperobject(43,7,35,"यहूदियों ने आपस में कहा, यह कहां जाएगा, कि हम इसे न पाएंगे: क्या वह उन के पास जाएगा, जो यूनानियों में तित्तर बित्तर होकर रहते हैं, और यूनानियों को भी उपदेश देगा?"));
hinsuperobject.biblemap.put("b43_7_36", new hinsuperobject(43,7,36,"यह क्या बात है जो उस ने कही, कि तुम मुझे ढूंढ़ोगे, परन्तु न पाओगे: और जहां मैं हूं, वहां तुम नहीं आ सकते?"));
hinsuperobject.biblemap.put("b43_7_37", new hinsuperobject(43,7,37,"फिर पर्ब्ब के अंतिम दिन, जो मुख्य दिन है, यीशु खड़ा हुआ और पुकार कर कहा, यदि कोई पियासा हो तो मेरे पास आकर पीए।"));
hinsuperobject.biblemap.put("b43_7_38", new hinsuperobject(43,7,38,"जो मुझ पर विश्वास करेगा, जैसा पवित्रा शास्त्रा में आया है उसके हृदय में से जीवन के जल की नदियां बह निकलेगी।"));
hinsuperobject.biblemap.put("b43_7_39", new hinsuperobject(43,7,39,"उस ने यह वचन उस आत्मा के विषय में कहा, जिसे उस पर विश्वास करनेवाले पाने पर थे; क्योंकि आत्मा अब तक न उतरा था; क्योंकि यीशु अब तक अपनी महिमा को न पहुंचा था।"));
hinsuperobject.biblemap.put("b43_7_40", new hinsuperobject(43,7,40,"तब भीड़ में से किसी किसी ने ये बातें सुन कर कहा, सचमुच यही वह भविष्यद्वक्ता है।"));
hinsuperobject.biblemap.put("b43_7_41", new hinsuperobject(43,7,41,"औरों ने कहा; यह मसीह है, परन्तु किसी ने कहा; क्यों? क्या मसीह गलील से आएगा?"));
hinsuperobject.biblemap.put("b43_7_42", new hinsuperobject(43,7,42,"क्या पवित्रा शास्त्रा में नहीं आया, कि मसीह दाऊद के वंश से और बैतलहम गांव से आएगा जहां दाऊद रहता था?"));
hinsuperobject.biblemap.put("b43_7_43", new hinsuperobject(43,7,43,"सो उसके कारण लोगों में फूट पड़ी।"));
hinsuperobject.biblemap.put("b43_7_44", new hinsuperobject(43,7,44,"उन में से कितने उसे पकड़ना चाहते थे, परन्तु किसी ने उस पर हाथ न डाला।।"));
hinsuperobject.biblemap.put("b43_7_45", new hinsuperobject(43,7,45,"तब सिपाही महायाजकों और फरीसियों के पास आए, और उन्हों ने उन से कहा, तुम उसे क्यों नहीं लाए?"));
hinsuperobject.biblemap.put("b43_7_46", new hinsuperobject(43,7,46,"सिपाहियों ने उत्तर दिया, कि किसी मनुष्य ने कभी ऐसी बातें न की।"));
hinsuperobject.biblemap.put("b43_7_47", new hinsuperobject(43,7,47,"फरीसियों ने उन को उत्तर दिया, क्या तुम भी भरमाए गए हो?"));
hinsuperobject.biblemap.put("b43_7_48", new hinsuperobject(43,7,48,"क्या सरदारों या फरीसियों में से किसी ने भी उस पर विश्वास किया है?"));
hinsuperobject.biblemap.put("b43_7_49", new hinsuperobject(43,7,49,"परन्तु ये लोग जो व्यवस्था नहीं जानते, स्त्रापित हैं।"));
hinsuperobject.biblemap.put("b43_7_50", new hinsuperobject(43,7,50,"नीकुदेमुस ने, (जो पहिले उसके पास आया था और उन में से एक था), उन से कहा।"));
hinsuperobject.biblemap.put("b43_7_51", new hinsuperobject(43,7,51,"क्या हमारी व्यवस्था किसी व्यक्ति को जब तक पहिले उस की सुनकर जान न ले, कि वह क्या करता है; दोषी ठहराती है?"));
hinsuperobject.biblemap.put("b43_7_52", new hinsuperobject(43,7,52,"उन्हों ने उसे उत्तर दिया; क्या तू भी गलील का है ढूंढ़ और देख, कि गलील से कोई भविष्यद्वक्ता प्रगट नहीं होने का।"));
hinsuperobject.biblemap.put("b43_7_53", new hinsuperobject(43,7,53,"तब सब कोई अपने अपने घर को गए।।"));
hinsuperobject.biblemap.put("b43_8_1", new hinsuperobject(43,8,1,"परन्तु यीशु जैतून के पहाड़ पर गया।"));
hinsuperobject.biblemap.put("b43_8_2", new hinsuperobject(43,8,2,"और भोर को फिर मन्दिर में आया, और सब लोग उसके पास आए; और वह बैठकर उन्हें उपदेश देने लगा।"));
hinsuperobject.biblemap.put("b43_8_3", new hinsuperobject(43,8,3,"तब शास्त्री और फरीसी एक स्त्री को लाए, जो व्यभिचार में पकड़ी गई थी, और उस को बीच में खड़ी करके यीशु से कहा।"));
hinsuperobject.biblemap.put("b43_8_4", new hinsuperobject(43,8,4,"हे गुरू, यह स्त्री व्यभिचार करते ही पकड़ी गई है।"));
hinsuperobject.biblemap.put("b43_8_5", new hinsuperobject(43,8,5,"व्यवस्था में मूसा ने हमें आज्ञा दी है कि ऐसी स्त्रियों कों पत्थरवाह करें: सो तू इस स्त्री के विषय में क्या कहता है?"));
hinsuperobject.biblemap.put("b43_8_6", new hinsuperobject(43,8,6,"उन्हों ने उस को परखने के लिये यह बात कही ताकि उस पर दोष लगाने के लिये कोई बात पाएं, परन्तु यीशु झुककर उंगली से भूमि पर लिखने लगा।"));
hinsuperobject.biblemap.put("b43_8_7", new hinsuperobject(43,8,7,"जब वे उस से पूछते रहे, तो उस ने सीधे होकर उन से कहा, कि तूुम में जो निष्पाप हो, वही पहिले उसको पत्थर मारे।"));
hinsuperobject.biblemap.put("b43_8_8", new hinsuperobject(43,8,8,"और फिर झुककर भूमि पर उंगली से लिखने लगा।"));
hinsuperobject.biblemap.put("b43_8_9", new hinsuperobject(43,8,9,"परन्तु वे यह सुनकर बड़ों से लेकर छोटों तक एक एक करके निकल गए, और यीशु अकेला रह गया, और स्त्री वहीं बीच में खड़ी रह गई।"));
hinsuperobject.biblemap.put("b43_8_10", new hinsuperobject(43,8,10,"यीशु ने सीधे होकर उस से कहा, हे नारी, वे कहां गए? क्या किसी ने तुझ पर दंड की आज्ञा न दी।"));
hinsuperobject.biblemap.put("b43_8_11", new hinsuperobject(43,8,11,"उसा ने कहा, हे प्रभु, किसी ने नहीं: यीशु ने कहा, मैं भी तुझ पर दंड की आज्ञा नहीं देता; जा, और फिर पाप न करना।।"));
hinsuperobject.biblemap.put("b43_8_12", new hinsuperobject(43,8,12,"तब यीशु ने फिर लोगों से कहा, जगत की ज्योति मैं हूं; जो मेरे पीछे हो लेगा, वह अन्धकार में न चलेगा, परन्तु जीवन की ज्योति पाएगा।"));
hinsuperobject.biblemap.put("b43_8_13", new hinsuperobject(43,8,13,"फरीसियों ने उस से कहा; तू अपनी गवाही आप देता है; तेरी गवाही ठीक नहीं।"));
hinsuperobject.biblemap.put("b43_8_14", new hinsuperobject(43,8,14,"यीशु ने उन को उत्तर दिया; कि यदि मैं अपनी गवाही आप देता हूं, तौभी मेरी गवाही ठीक है, क्योंकि मैं जानता हूं, कि मैं कहां से आया हूं और कहां को जाता हूं? परन्तु तुम नहीं जानते कि मैं कहां से आता हूं या कहां को जाता हूं।"));
hinsuperobject.biblemap.put("b43_8_15", new hinsuperobject(43,8,15,"तुम शरीर के अनुसार न्याय करते हो; मैं किसी का न्याय नहीं करता।"));
hinsuperobject.biblemap.put("b43_8_16", new hinsuperobject(43,8,16,"और यदि मैं न्याय करूं भी, तो मेरा न्याय सच्चा है; क्योंकि मैं अकेला नहीं, परन्तु मैं हूं, और पिता है जिस ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_8_17", new hinsuperobject(43,8,17,"और तुम्हारी व्यवस्था में भी लिखा है; कि दो जनों की गवाही मिलकर ठीक होती है।"));
hinsuperobject.biblemap.put("b43_8_18", new hinsuperobject(43,8,18,"एक तो मैं आप अपनी गवाही देता हूं, और दूसरा पिता मेरी गवाही देता है जिस ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_8_19", new hinsuperobject(43,8,19,"उन्हों ने उस से कहा, तेरा पिता कहां है? यीशु ने उत्तर दिया, कि न तुम मुझे जानते, तो मेरे पिता को भी जानते।"));
hinsuperobject.biblemap.put("b43_8_20", new hinsuperobject(43,8,20,"ये बातें उस ने मन्दिर में उपदेश देते हुए भण्डार घर में कहीं, और किसी ने उसे न पकड़ा; क्योंकि उसका समय अब तक नहीं आया था।।"));
hinsuperobject.biblemap.put("b43_8_21", new hinsuperobject(43,8,21,"उस ने फिर उन से कहा, मैं जाता हूं और तुम मुझे ढूंढ़ोगे और अपने आप में मरोगे: जहां मैं जाता हूं, वहां तुम नहीं आ सकते।"));
hinsuperobject.biblemap.put("b43_8_22", new hinsuperobject(43,8,22,"इस पर यहूदियों ने कहा, क्या वह अपने आप को मार डालेगा, जो कहता है; कि जहां मैं जाता हूं वहां तुम नहीं आ सकते?"));
hinsuperobject.biblemap.put("b43_8_23", new hinsuperobject(43,8,23,"उस ने उन से कहा, तुम नीचे के हो, मैं ऊपर का हूं; तुम संसार के हो, मैं संसार का नहीं।"));
hinsuperobject.biblemap.put("b43_8_24", new hinsuperobject(43,8,24,"इसलिये मैं ने तुम से कहा, कि तुम अपने पापों में मरोगे; क्योंकि यदि तुम विश्वास न करोगे कि मैं वहीं हूं, तो अपने पापों में मरोगे।"));
hinsuperobject.biblemap.put("b43_8_25", new hinsuperobject(43,8,25,"उन्हों ने उस से कहा, तू कौन है? यीशु ने उन से कहा, वही हूं जो प्रारम्भ से तुम से कहता आया हूं।"));
hinsuperobject.biblemap.put("b43_8_26", new hinsuperobject(43,8,26,"तुम्हारे विषय में मुझे बहुत कुछ कहना और निर्णय करना है परन्तु मेरा भेजनेवाला सच्चा है; और जो मैं ने उस से सुना हे, वही जगत से कहहा हूं।"));
hinsuperobject.biblemap.put("b43_8_27", new hinsuperobject(43,8,27,"वे न समझे कि हम से पिता के विषय में कहता है।"));
hinsuperobject.biblemap.put("b43_8_28", new hinsuperobject(43,8,28,"तब यीशु ने कहा, कि जब तुम मनुष्य के पुत्रा को ऊंचे पर चढ़ाओगे, तो जानोगे कि मैं वही हूं, और अपने आप से कुछ नहीं करता, परन्तु जैसे पिता ने मुझे सिखाया, वैसे ही ये बातें कहता हूं।"));
hinsuperobject.biblemap.put("b43_8_29", new hinsuperobject(43,8,29,"और मेरा भेजनेवाला मेरे साथ है; उस ने मुझे अकेला नहीं छोड़ा; क्योंकि मैं सर्वदा वही काम करता हूं, जिस से वह प्रसन्न होता है।"));
hinsuperobject.biblemap.put("b43_8_30", new hinsuperobject(43,8,30,"वह ये बातें कह ही रहा था, कि बहुतेरों ने उस पर विश्वास किया।।"));
hinsuperobject.biblemap.put("b43_8_31", new hinsuperobject(43,8,31,"तब यीशु ने उन यहूदियों से जिन्हों ने उन की प्रतीति की थी, कहा, यदि तुम मेरे वचन में बने रहोगे, तो सचमुच मेरे चेले ठहरोगे।"));
hinsuperobject.biblemap.put("b43_8_32", new hinsuperobject(43,8,32,"और सत्य को जानोगे, और सत्य तुम्हें स्वतंत्रा करेगा।"));
hinsuperobject.biblemap.put("b43_8_33", new hinsuperobject(43,8,33,"उन्हों ने उस को उत्तर दिया; कि हम तो इब्राहीम के वंश से हैं और कभी किसी के दास नहीं हुए; फिर तू क्योंकर कहता है, कि तुम स्वतंत्रा हो जाओगे?"));
hinsuperobject.biblemap.put("b43_8_34", new hinsuperobject(43,8,34,"यीशु ने उन को उत्तर दिया; मैं तुम से सच सच कहता हूं कि जो कोई पाप करता है, वह पाप का दास है।"));
hinsuperobject.biblemap.put("b43_8_35", new hinsuperobject(43,8,35,"और दास सदा घर में नहीं रहता; पुत्रा सदा रहता है।"));
hinsuperobject.biblemap.put("b43_8_36", new hinsuperobject(43,8,36,"सो यदि पुत्रा तुम्हें स्वतंत्रा करेगा, तो सचमुच तुम स्वतंत्रा हो जाओगे।"));
hinsuperobject.biblemap.put("b43_8_37", new hinsuperobject(43,8,37,"मैं जानता हूं कि तुम इब्राहीम के वंश से हो; तौभी मेरा वचन तुम्हारे हृदय में जगह नहीं पाता, इसलिये तुम मुझे मार डालना चाहते हो।"));
hinsuperobject.biblemap.put("b43_8_38", new hinsuperobject(43,8,38,"मैं वही कहता हूं, जो अपने पिता के यहां देखा है; और तुम वही करते रहते हो जो तुमने अपने पिता से सुना है।"));
hinsuperobject.biblemap.put("b43_8_39", new hinsuperobject(43,8,39,"उन्हों ने उन को उत्तर दिया, कि हमारा पिता तो इब्राहीम है: यीशु ने उन से कहा; यदि तुम इब्राहीम के सन्तान होते, तो इब्राहीम के समान काम करते।"));
hinsuperobject.biblemap.put("b43_8_40", new hinsuperobject(43,8,40,"परन्तु अब तुम मुझ ऐसे मनुष्य को मार डालना चाहते हो, जिस ने तुम्हें वह सत्य वचन बताया जो परमेश्वर से सुना, यह तो इब्राहीम ने नहीं किया था।"));
hinsuperobject.biblemap.put("b43_8_41", new hinsuperobject(43,8,41,"तुम अपने पिता के समान काम करते हो: उन्हों ने उस से कहा, हम व्यभिचार से नहीं जन्मे; हमारा एक पिता है अर्थात् परमेश्वर।"));
hinsuperobject.biblemap.put("b43_8_42", new hinsuperobject(43,8,42,"यीशु ने उन से कहा; यदि परमेश्वर तुम्हारा पिता होता, तो तुम मुझ से प्रेम रखते; क्योंकि मैं परमेश्वर में से निकल कर आया हूं; मैं आप से नहीं आया, परन्तु उसी ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_8_43", new hinsuperobject(43,8,43,"तुम मेरी बात क्यों नहीं समझते? इसलिये कि मेरा वचन सुन नहीं सकते।"));
hinsuperobject.biblemap.put("b43_8_44", new hinsuperobject(43,8,44,"तुम अपने पिता शैतान से हो, और अपने पिता की लालसाओं को पूरा करना चाहते हो। वह तो आरम्भ से हत्यारा है, और सत्य पर स्थिर न रहा, क्योंकि सत्य उस में है ही नहीं: जब वह झूठ बोलता, तो अपने स्वभाव ही से बोलता है; क्योंकि वह झूठा है, बरन झूठ का पिता है।"));
hinsuperobject.biblemap.put("b43_8_45", new hinsuperobject(43,8,45,"परन्तु मैं जो सच बोलता हूं, इसीलिये तुम मेरी प्रतीति नहीं करते।"));
hinsuperobject.biblemap.put("b43_8_46", new hinsuperobject(43,8,46,"तुम में से कौन मुझे पापी ठहराता है? और यदि मैं सच बोलता हूं, तो तुम मेरी प्रतीति क्यों नहीं करते?"));
hinsuperobject.biblemap.put("b43_8_47", new hinsuperobject(43,8,47,"जो परमेश्वर से होता हे, वह परमेश्वर की बातें सुनता है; और तुम इसलिये नहीं सुनते कि परमेश्वर की ओर से नहीं हो।"));
hinsuperobject.biblemap.put("b43_8_48", new hinsuperobject(43,8,48,"यह सुन यहूदियों ने उस से कहा; क्या हम ठीक नहीं कहते, कि तू सामरी है, और तुझ में दुष्टात्मा है?"));
hinsuperobject.biblemap.put("b43_8_49", new hinsuperobject(43,8,49,"यीशु ने उत्तर दिया, कि मुझ में दुष्टात्मा नहीं; परन्तु मैं अपने पिता का आदर करता हूं, और तुम मेरा निरादर करते हो।"));
hinsuperobject.biblemap.put("b43_8_50", new hinsuperobject(43,8,50,"परन्तु मैं अपनी प्रतिष्ठा नहीं चाहता, हां, एक तो है जो चाहता है, और न्याय करता है।"));
hinsuperobject.biblemap.put("b43_8_51", new hinsuperobject(43,8,51,"मैं तुम से सच सच कहता हूं, कि यदि कोई व्यक्ति मेरे वचन पर चलेगा, तो वह अनन्त काल तक मृत्यु को न देखेगा।"));
hinsuperobject.biblemap.put("b43_8_52", new hinsuperobject(43,8,52,"यहूदियों ने उस से कहा, कि अब हम ने जान लिया कि तुझ में दुष्टात्मा है: इब्राहीम मर गया, और भविष्यद्वक्ता भी मर गए हैं और तू कहता है, कि यदि कोई मेरे वचन पर चलेगा तो वह अनन्त काल तक मृत्यु का स्वाद न चखेगा।"));
hinsuperobject.biblemap.put("b43_8_53", new hinsuperobject(43,8,53,"हमारा पिता इब्राहीम तो मर गया, क्या तू उस से बड़ा है? और भविष्यद्वक्ता भी मर गए, तू अपने आप को क्या ठहराता है।"));
hinsuperobject.biblemap.put("b43_8_54", new hinsuperobject(43,8,54,"यीशु ने उत्तर दिया; यदि मैं आप अपनी महिमा करूं, तो मेरी महिमा कुछ नहीं, परन्तु मेरी महिमा करनेवाला मेरा पिता है, जिसे तुम कहते हो, कि वह हमारा परमेश्वर है।"));
hinsuperobject.biblemap.put("b43_8_55", new hinsuperobject(43,8,55,"और तुम ने तो उसे नहीं जाना: परन्तु मैं उसे जानता हूं; और यदि कहूं कि मैं उसे नहीं जानता, तो मैं तुम्हारी नाईं झूठा ठहरूंगा: परन्तु मैं उसे जानता, और उसके वचन पर चलता हूं।"));
hinsuperobject.biblemap.put("b43_8_56", new hinsuperobject(43,8,56,"तुम्हारा पिता इब्राहीम मेरा दिन देखने की आशा से बहुत मगन था; और उस ने देखा, और आनन्द किया।"));
hinsuperobject.biblemap.put("b43_8_57", new hinsuperobject(43,8,57,"यहूदियों ने उस से कहा, अब तक तू पचास वर्ष का नहीं; फिर भी तू ने इब्राहीम को देखा है?"));
hinsuperobject.biblemap.put("b43_8_58", new hinsuperobject(43,8,58,"यीशु ने उन से कहा; मैं तुम से सच सच कहता हूं; कि पहिले इसके कि इब्राहीम उत्पन्न हुआ मैं हूं।"));
hinsuperobject.biblemap.put("b43_8_59", new hinsuperobject(43,8,59,"तब उन्हों ने उसे मारने के लिये पत्थर उठाए, परन्तु यीशु छिपकर मन्दिर से निकल गया।।"));
hinsuperobject.biblemap.put("b43_9_1", new hinsuperobject(43,9,1,"फिर जाते हुए उस ने एक मनुष्य को देखा, जो जन्म का अन्धा था।"));
hinsuperobject.biblemap.put("b43_9_2", new hinsuperobject(43,9,2,"और उसके चेलों ने उस से पूछा, हे रब्बी, किस ने पाप किया था कि यह अन्धा जन्मा, इस मनुष्य ने, या उसके माता पिता ने?"));
hinsuperobject.biblemap.put("b43_9_3", new hinsuperobject(43,9,3,"यीशु ने उत्तर दिया, कि न तो इस ने पाप किया था, न इस के माता पिता ने: परन्तु यह इसलिये हुआ, कि परमेश्वर के काम उस में प्रगट हों।"));
hinsuperobject.biblemap.put("b43_9_4", new hinsuperobject(43,9,4,"जिस ने मुझे भेजा है; हमें उसके काम दिन ही दिन में करना अवश्य है: वह रात आनेवाली है जिस में कोई काम नहीं कर सकता।"));
hinsuperobject.biblemap.put("b43_9_5", new hinsuperobject(43,9,5,"जब तक मैं जंगल में हूं, तब तक जगत की ज्योति हूं।"));
hinsuperobject.biblemap.put("b43_9_6", new hinsuperobject(43,9,6,"यह कहकर उस ने भूमि पर थूका और उस थूक से मिट्टी सानी, और वह मिट्टी उस अन्धे की आंखों पर लगाकर।"));
hinsuperobject.biblemap.put("b43_9_7", new hinsuperobject(43,9,7,"उस से कहा; जा शीलोह के कुण्ड में धो ले, (जिस का अर्थ भेजा हुआ है) सो उस ने जाकर धोया, और देखता हुआ लौट आया।"));
hinsuperobject.biblemap.put("b43_9_8", new hinsuperobject(43,9,8,"तब पड़ोसी और जिन्हों ने पिहले उसे भीख मांगते देखा था, कहने लगे; क्या यह वही नहीं, जो बैठा भीख मांगा करता था?"));
hinsuperobject.biblemap.put("b43_9_9", new hinsuperobject(43,9,9,"कितनों ने कहा, यह वही है: औरों ने कहा, नहीं; परन्तु उसके समान है: उस ने कहा, मैं वही हूं।"));
hinsuperobject.biblemap.put("b43_9_10", new hinsuperobject(43,9,10,"तब वे उस से पूछने लगे, तेरी आंखें क्योंकर खुल गई?"));
hinsuperobject.biblemap.put("b43_9_11", new hinsuperobject(43,9,11,"उस ने उत्तर दिया, कि यीशु नाम एक व्यक्ति ने मिट्टी सानी, और मेरी आंखों पर लगाकर मुझ से कहा, कि शीलोह में जाकर धो ले; सो मैं गया, और धोकर देखने लगा।"));
hinsuperobject.biblemap.put("b43_9_12", new hinsuperobject(43,9,12,"उन्हों ने उस से पूछा; वह कहां है? उस ने कहा; मैं नहीं जानता।।"));
hinsuperobject.biblemap.put("b43_9_13", new hinsuperobject(43,9,13,"लोग उसे जो पहिले अन्धा था फरीसियों के पास ले गए।"));
hinsuperobject.biblemap.put("b43_9_14", new hinsuperobject(43,9,14,"जिस दिन यीशु ने मिट्टी सानकर उस की आंखे खोली थी वह सब्त का दिन था।"));
hinsuperobject.biblemap.put("b43_9_15", new hinsuperobject(43,9,15,"फिर फरीसियों ने भी उस से पूछा; तेरी आंखें किस रीति से खुल गई? उस न :उन से कहा; उस ने मेरी आंखो पर मिट्टी लगाई, फिर मैं ने धो लिया, और अब देखता हूं।"));
hinsuperobject.biblemap.put("b43_9_16", new hinsuperobject(43,9,16,"इस पर कई फरीसी कहने लगे; यह मनुष्य परमेश्वर की ओर से नहीं, क्योंकि वह सब्त का दिन नहीं मानता। औरों ने कहा, पापी मनुष्य क्योंकर ऐसे चिन्ह दिखा सकता है? सो उन में फूट पड़ी।"));
hinsuperobject.biblemap.put("b43_9_17", new hinsuperobject(43,9,17,"उन्हों ने उस अन्धे से फिर कहा, उस ने जो तेरी आंखे खोली, तू उसके विषय में क्या कहता है? उस ने कहा, यह भविष्यद्वक्ता है।"));
hinsuperobject.biblemap.put("b43_9_18", new hinsuperobject(43,9,18,"परन्तु यहूदियों को विश्वास न हुआ कि यह अन्धा था और अब देखता है जब तक उन्हों ने उसके माता- पिता को जिस की आंखे खुल गई थी, बुलाकर।"));
hinsuperobject.biblemap.put("b43_9_19", new hinsuperobject(43,9,19,"उन से न पूछा, कि क्या यह तुम्हारा पुत्रा है, जिसे तुम कहते हो कि अन्धा जन्मा था? फिर अब क्योंकर देखता है?"));
hinsuperobject.biblemap.put("b43_9_20", new hinsuperobject(43,9,20,"उसके माता- पिता ने उत्तर दिया; हम तो जानते हैं कि यह हमारा पुत्रा है, और अन्धा जन्मा था।"));
hinsuperobject.biblemap.put("b43_9_21", new hinsuperobject(43,9,21,"परन्तु हम यह नहीं जानते हैं कि अब क्योंकर देखता है; और न यह जानते हैं, कि किस ने उस की आंखे खोलीं; वह सयाना है; उसी से पूछ लो; वह अपने विषय में आप कह देगा।"));
hinsuperobject.biblemap.put("b43_9_22", new hinsuperobject(43,9,22,"ये बातें उसके माता- पिता ने इसलिये कहीं क्योंकि वे यहूदियों से डरते थे; क्योकि यहूदी एका कर चुके थे, कि यदि कोई कहे कि वह मसीह है, तो आराधनालय से निकाला जाए।"));
hinsuperobject.biblemap.put("b43_9_23", new hinsuperobject(43,9,23,"इसी कारण उसके माता- पिता ने कहा, कि वह सयाना है; उसी से पूछ लो।"));
hinsuperobject.biblemap.put("b43_9_24", new hinsuperobject(43,9,24,"तब उन्हों ने उस मनुष्य को जो अन्धा था दूसरी बार बुलाकर उस से कहा, परमेश्वर की स्तुति कर; हम तो जानते हैं कि वह मनुष्य पापी है।"));
hinsuperobject.biblemap.put("b43_9_25", new hinsuperobject(43,9,25,"उस ने उत्तर दिया: मैं नहीं जानता कि वह पापी है या नहीं: मैं एक बात जानता हूं कि मैं अन्धा था और अब देखता हूं।"));
hinsuperobject.biblemap.put("b43_9_26", new hinsuperobject(43,9,26,"उन्हों ने उस से फिर कहा, कि उस ने तेरे साथ क्या किया? और किस तेरह तेरी आंखें खोली?"));
hinsuperobject.biblemap.put("b43_9_27", new hinsuperobject(43,9,27,"उस ने उन से कहा; मैं तो तुम से कह चुका, और तुम ने ने सुना; अब दूसरी बार क्यों सुनना चाहते हो? क्या तुम भी उसके चेले होना चाहते हो?"));
hinsuperobject.biblemap.put("b43_9_28", new hinsuperobject(43,9,28,"तब वे उसे बुरा- भला कहकर बोले, तू ही उसका चेला है; हम तो मूसा के चेले हैं।"));
hinsuperobject.biblemap.put("b43_9_29", new hinsuperobject(43,9,29,"हम जानते हैं कि परमेश्वर ने मूसा से बातें कीं; परन्तु इस मनुष्य को नहीं जानते की कहां का है।"));
hinsuperobject.biblemap.put("b43_9_30", new hinsuperobject(43,9,30,"उस ने उन को उत्तर दिया; यह तो अचम्भे की बात है कि तुम नहीं जानते की कहां का है तौभी उस ने मेरी आंखें खोल दीं।"));
hinsuperobject.biblemap.put("b43_9_31", new hinsuperobject(43,9,31,"हम जानते हैं कि परमेश्वर पापियों की नहीं सुनता परन्तु यदि कोई परमेश्वर का भक्त हो, और उस की इच्छा पर चलता है, तो वह उस की सुनता है।"));
hinsuperobject.biblemap.put("b43_9_32", new hinsuperobject(43,9,32,"जगत के आरम्भ से यह कभी सुनने में नहीं आया, कि किसी ने भी जन्म के अन्धे की आंखे खोली हों।"));
hinsuperobject.biblemap.put("b43_9_33", new hinsuperobject(43,9,33,"यदि यह व्यक्ति परमेश्वर की ओर से न होता, तो कुछ भी नहीं कर सकता।"));
hinsuperobject.biblemap.put("b43_9_34", new hinsuperobject(43,9,34,"उन्हों ने उस को उत्तर दिया, कि तू तो बिलकुल पापों में जन्मा है, तू हमें क्या सिखाता है? और उन्हों ने उसे बाहर निकाल दिया।।"));
hinsuperobject.biblemap.put("b43_9_35", new hinsuperobject(43,9,35,"यीशु ने सुना, कि उन्हों ने उसे बाहर निकाल दिया है; और जब उसे भेंट हुई तो कहा, कि क्या तू परमेश्वर के पुत्रा पर विश्वास करता है?"));
hinsuperobject.biblemap.put("b43_9_36", new hinsuperobject(43,9,36,"उस ने उत्तर दिया, कि हे प्रभु; वह कौन है कि मैं उस पर विश्वास करूं?"));
hinsuperobject.biblemap.put("b43_9_37", new hinsuperobject(43,9,37,"यीशु ने उस से कहा, तू ने उसे देखा भी है; और जो तेरे साथ बातें कर रहा है वही है।"));
hinsuperobject.biblemap.put("b43_9_38", new hinsuperobject(43,9,38,"उस ने कहा, हे प्रभु, मैं विश्वास करता हूं: और उसे दंडवत किया।"));
hinsuperobject.biblemap.put("b43_9_39", new hinsuperobject(43,9,39,"तब यीशु ने कहा, मैं इस जगत में न्याय के लिये आया हूं, ताकि जो नहीं देखते वे देखें, और जो देखते हैं वे अन्धे हो जाएं।"));
hinsuperobject.biblemap.put("b43_9_40", new hinsuperobject(43,9,40,"जो फरीसी उसके साथ थे, उन्हों ने ये बातें सुन कर उस से कहा, क्या हम भी अन्धे हैं?"));
hinsuperobject.biblemap.put("b43_9_41", new hinsuperobject(43,9,41,"यीशु ने उन से कहा, यदि तुम अन्धे होते तो पापी न ठहरते परन्तु अब कहते हो, कि हम देखते हैं, इसलिये तुम्हारा पाप बना रहता है।।"));
hinsuperobject.biblemap.put("b43_10_1", new hinsuperobject(43,10,1,"मैं तुम से सच सच कहता हूं, कि जो कोई द्वार से भेड़शाला में प्रवेश नहीं करता, परन्तु और किसी ओर से चढ़ जाता है, वह चोर और डाकू है।"));
hinsuperobject.biblemap.put("b43_10_2", new hinsuperobject(43,10,2,"परन्तु जो द्वार से भीतर प्रवेश करता है वह भेड़ों का चरवाहा है।"));
hinsuperobject.biblemap.put("b43_10_3", new hinsuperobject(43,10,3,"उसके लिये द्वारपाल द्वार खोल देता है, और भेंड़ें उसका शब्द सुनती हैं, और वह अपनी भेड़ों को नाम ले लेकर बुलाता है और बाहर ले जाता है।"));
hinsuperobject.biblemap.put("b43_10_4", new hinsuperobject(43,10,4,"और जब वह अपनी सब भेड़ों को बाहर निकाल चुकता है, तो उन के आगे आगे चलता है, और भेड़ें उसके पीछे पीछे हो लेती हैं; क्योंकि वे उसक शब्द पहचानती हैं।"));
hinsuperobject.biblemap.put("b43_10_5", new hinsuperobject(43,10,5,"परन्तु वे पराये के पीछे नहीं जाएंगी, परन्तु उस से भागेंगी, क्योंकि वे परायों का शब्द नहीं पहचानती।"));
hinsuperobject.biblemap.put("b43_10_6", new hinsuperobject(43,10,6,"यीशु ने उन से यह दृष्टान्त कहा, परन्तु वे न समझे कि ये क्या बातें हैं जो वह हम से कहता है।।"));
hinsuperobject.biblemap.put("b43_10_7", new hinsuperobject(43,10,7,"तब यीशु ने उन से फिर कहा, मैं तुम से सच सच कहता हूं, कि भेड़ों का द्वार मैं हूं।"));
hinsuperobject.biblemap.put("b43_10_8", new hinsuperobject(43,10,8,"जितने मुझ से पहिले आए; वे सब चोर और डाकू हैं परन्तु भेड़ों ने उन की न सुनी।"));
hinsuperobject.biblemap.put("b43_10_9", new hinsuperobject(43,10,9,"द्वार मैं हूं: यदि कोई मेरे द्वारा भीतर प्रवेश करे तो उद्धार पाएगा औश्र भीतर बाहर आया जाया करेगा और चारा पाएगा।"));
hinsuperobject.biblemap.put("b43_10_10", new hinsuperobject(43,10,10,"चोर किसी और काम के लिये नहीं परन्तु केवल चोरी करने और घात करने और नष्ट करने को आता है। मैं इसलिये आया कि वे जीवन पाएं, और बहुतायत से पाएं।"));
hinsuperobject.biblemap.put("b43_10_11", new hinsuperobject(43,10,11,"अच्छा चरवाहा मैं हूं; अच्छा चरवाहा भेड़ों के लिये अपना प्राण देता है।"));
hinsuperobject.biblemap.put("b43_10_12", new hinsuperobject(43,10,12,"मजदूर जो न चरवाहा है, और न भेड़ों का मालिक है, भेड़िए को आते हुए देख, भेड़ों को छोड़कर भाग जाता है, और भेड़िया उन्हें पकड़ता और तित्तर बित्तर कर देता है।"));
hinsuperobject.biblemap.put("b43_10_13", new hinsuperobject(43,10,13,"वह इसलिये भाग जाता है कि वह मजदूर है, और उस को भेड़ों की चिन्ता नहीं।"));
hinsuperobject.biblemap.put("b43_10_14", new hinsuperobject(43,10,14,"अच्छा चरवाहा मैं हूं; जिस तरह पिता मुझे जानता है, और मैं पिता को जानता हूं।"));
hinsuperobject.biblemap.put("b43_10_15", new hinsuperobject(43,10,15,"इसी तरह मैं अपी भेड़ों को जानता हूं, और मेरी भेड़ें मुझे जानती हैं, और मैं भेड़ों के लिये अपना प्राण देता हूं।"));
hinsuperobject.biblemap.put("b43_10_16", new hinsuperobject(43,10,16,"और मेरी और भी भेड़ें हैं, जो इस भेड़शाला की नहीं; मुझे उन का भी लाना अवश्य है, वे मेरा शब्द सुनेंगी; तब एक ही झुण्ड और एक ही चरवाहा होगा।"));
hinsuperobject.biblemap.put("b43_10_17", new hinsuperobject(43,10,17,"पिता इसलिये मुझ से प्रेम रखता है, कि मैं अपना प्राण देता हूं, कि उसे फिर ले लूं।"));
hinsuperobject.biblemap.put("b43_10_18", new hinsuperobject(43,10,18,"कोई उसे मुझ से छीनता नहीं, बरन मैं उसे आप ही देता हूं: मुझे उसके देने का अधिकार है, और उसे फिर लेने का भी अधिकार है: यह आज्ञा मेरे पिता से मुझे मिली है।।"));
hinsuperobject.biblemap.put("b43_10_19", new hinsuperobject(43,10,19,"इन बातों के कारण यहूदियों में फिर फूट पड़ी।"));
hinsuperobject.biblemap.put("b43_10_20", new hinsuperobject(43,10,20,"उन में से बहुतेरे कहने लगे, कि उस में दुष्टात्मा है, और वह पागल है; उस की क्यों सुनते हो?"));
hinsuperobject.biblemap.put("b43_10_21", new hinsuperobject(43,10,21,"औरों ने कहा, ये बात ऐसे मनुष्य की नहीं जिस में दुष्टात्मा हो: क्या दुष्टात्मा अन्धों की आंखे खोल सकती है?"));
hinsuperobject.biblemap.put("b43_10_22", new hinsuperobject(43,10,22,"यरूशलेम में स्थापन पर्ब्ब हुआ, और जाड़े की ऋतु थी।"));
hinsuperobject.biblemap.put("b43_10_23", new hinsuperobject(43,10,23,"और यीशु मन्दिर में सुलैमान के ओसारे में टहल रहा था।"));
hinsuperobject.biblemap.put("b43_10_24", new hinsuperobject(43,10,24,"तब यहूदियों ने उसे आ घेरा और पूछा, तू हमारे मन को कब तक दुविधा में रखेगा? यदि तू मसीह है, तो हम से साफ कह दे।"));
hinsuperobject.biblemap.put("b43_10_25", new hinsuperobject(43,10,25,"यीशु ने उन्हें उत्तर दिया, कि मैं ने तुम से कह दिया, और तुम प्रतीति करते ही नहीं, जो काम मैं अपने पिता के नाम से करता हूं वे ही मेरे गवाह हैं।"));
hinsuperobject.biblemap.put("b43_10_26", new hinsuperobject(43,10,26,"परन्तु तुम इसलिये प्रतीति नहीं करते, कि मेरी भेड़ों में से नहीं हो।"));
hinsuperobject.biblemap.put("b43_10_27", new hinsuperobject(43,10,27,"मेरी भेड़ें मेरा शब्द सुनती हैं, और मैं उन्हें जानता हूं, और वे मेरे पीछे पीछे चलती हैं।"));
hinsuperobject.biblemap.put("b43_10_28", new hinsuperobject(43,10,28,"और मैं उन्हें अनन्त जीवन देता हूं, और वे कभी नाश नहीं होंगी, और कोई उन्हें मेरे हाथ से छीन न लेगा।"));
hinsuperobject.biblemap.put("b43_10_29", new hinsuperobject(43,10,29,"मेरा पिता, जिस ने उन्हें मुझ को दिया है, सब से बड़ा है, और कोई उन्हें पिता के हाथ से छीन नहीं सकता।"));
hinsuperobject.biblemap.put("b43_10_30", new hinsuperobject(43,10,30,"मैं और पिता एक हैं।"));
hinsuperobject.biblemap.put("b43_10_31", new hinsuperobject(43,10,31,"यहूदियों ने उसे पत्थरवाह करते को फिर पत्थर उठाए।"));
hinsuperobject.biblemap.put("b43_10_32", new hinsuperobject(43,10,32,"इस पर यीशु ने उन से कहा, कि मैं ने तुम्हें अपने पिता की ओर से बहुत से भले काम दिखाए हैं, उन में से किस काम के लिये तुम मुझे पत्थरवाह करते हो?"));
hinsuperobject.biblemap.put("b43_10_33", new hinsuperobject(43,10,33,"यहूदियों ने उस को उत्तर दिया, कि भले काम के लिये हम तुझे पत्थरवाह नहीं करते, परन्तु परमेश्वर की निन्दा के कारण और इसलिये कि तू मनुष्य होकर अपने आप को परमेश्वर बनाता है।"));
hinsuperobject.biblemap.put("b43_10_34", new hinsuperobject(43,10,34,"यीशु ने उन्हें उत्तर दिया, क्या तुम्हारी व्यवस्था में नहीं लिखा है कि मैं ने कहा, तुम ईश्वर हो?"));
hinsuperobject.biblemap.put("b43_10_35", new hinsuperobject(43,10,35,"यदि उस ने उन्हें ईश्वर कहा जिन के पास परमेश्वर का वचन पहुंचा (और पवित्रा शास्त्रा की बात लोप नहीं हो सकती।)"));
hinsuperobject.biblemap.put("b43_10_36", new hinsuperobject(43,10,36,"तो जिसे पिता ने पवित्रा ठहराकर जगत में भेजा है, तुम उस से कहते हो कि तू निन्दा करता है, इसलिये कि मैं ने कहा, मैं परमेश्वर का पुत्रा हूं।"));
hinsuperobject.biblemap.put("b43_10_37", new hinsuperobject(43,10,37,"यदि मैं अपने पिता के काम नहीं करता, तो मेरी प्रतीति न करो।"));
hinsuperobject.biblemap.put("b43_10_38", new hinsuperobject(43,10,38,"परन्तु यदि मैं करता हूं, तो चाहे मेरी प्रतीति न भी करो, परन्तु उन कामों की तो प्रतीति करो, ताकि तुम जानो, और समझो, कि पिता मुझ में है, और मैं पिता में हूं।"));
hinsuperobject.biblemap.put("b43_10_39", new hinsuperobject(43,10,39,"तब उन्हों ने फिर उसे पकड़ने का प्रयत्न किया परन्तु वह उन के हाथ से निकल गया।।"));
hinsuperobject.biblemap.put("b43_10_40", new hinsuperobject(43,10,40,"फिर वह यरदन के पार उस स्थान पर चला गया, जहां यूहन्ना पहिले बपतिस्मा दिया करता था, और वहीं रहा।"));
hinsuperobject.biblemap.put("b43_10_41", new hinsuperobject(43,10,41,"और बहुतेरे उसके पास आकर कहते थे, कि युहन्ना ने तो कोई चिन्ह नहीं दिखाया, परन्तु जो कुछ यूहन्ना ने इस के विषय में कहा था वह सब सच था।"));
hinsuperobject.biblemap.put("b43_10_42", new hinsuperobject(43,10,42,"और वहां बहुतेरों ने उस पर विश्वास किया।।"));
hinsuperobject.biblemap.put("b43_11_1", new hinsuperobject(43,11,1,"मरियम और उस की बहिन मरथा के गांव बैतनिरयाह का लाजर नाम एक मनुष्य बीमार था।"));
hinsuperobject.biblemap.put("b43_11_2", new hinsuperobject(43,11,2,"यह वही मरियम थी जिस ने प्रभु पर इत्रा डालकर उसके पांवों को अपने बालों से पोंछा था, इसी का भाई लाजर बीमार था।"));
hinsuperobject.biblemap.put("b43_11_3", new hinsuperobject(43,11,3,"सो उस की बहिनों ने उसे कहला भेजा, कि हे प्रभु, देख, जिस से तू प्रीति रखता है, वह बीमार है।"));
hinsuperobject.biblemap.put("b43_11_4", new hinsuperobject(43,11,4,"यह सुनकर यीशु ने कहा, यह बीमारी मृत्यु की नहीं, परन्तु परमेश्वर की महिमा के लिये है, कि उसके द्वारा परमेश्वर के पुत्रा की महिमा हो।"));
hinsuperobject.biblemap.put("b43_11_5", new hinsuperobject(43,11,5,"और यीशु मरथा और उस की बहन और लाजर से प्रेम रखता था।"));
hinsuperobject.biblemap.put("b43_11_6", new hinsuperobject(43,11,6,"सो जब उस ने सुना, कि वह बीमार है, तो जिस स्थान पर वह था, वहां दो दिन और ठहर गया।"));
hinsuperobject.biblemap.put("b43_11_7", new hinsuperobject(43,11,7,"फिर इस के बाद उस ने चेलों से कहा, कि आओ, हम फिर यहूदिया को चलें।"));
hinsuperobject.biblemap.put("b43_11_8", new hinsuperobject(43,11,8,"चेलों ने उस से कहा, हे रब्बी, अभी तो यहूदी तुझे पत्थरवाह करना चाहते थे, और क्या तू फिर भी वहीं जाता है?"));
hinsuperobject.biblemap.put("b43_11_9", new hinsuperobject(43,11,9,"यीशु ने उत्तर दिया, क्या दिन के बारह घंटे नहीं होते? यदि कोई दिन को चले, तो ठोकर नहीं खाता है, क्योंकि इस जगत का उजाला देखता है।"));
hinsuperobject.biblemap.put("b43_11_10", new hinsuperobject(43,11,10,"परन्तु यदि कोई रात को चले, तो ठोकर खाता है, क्योंकि उस में प्रकाश नहीं।"));
hinsuperobject.biblemap.put("b43_11_11", new hinsuperobject(43,11,11,"उस ने ये बातें कहीं, और इस के बाद उन से कहने लगा, कि हमारा मित्रा लाजर सो गया है, परन्तु मैं उसे जगाने जाता हूं।"));
hinsuperobject.biblemap.put("b43_11_12", new hinsuperobject(43,11,12,"तब चेलों ने उस से कहा, हे प्रभु, यदि वह सो गया है, तो बच जाएगा।"));
hinsuperobject.biblemap.put("b43_11_13", new hinsuperobject(43,11,13,"यीशु ने तो उस की मृत्यु के विषय में कहा था: परन्तु वे समझे कि उस ने नींद से सो जाने के विषय में कहा।"));
hinsuperobject.biblemap.put("b43_11_14", new hinsuperobject(43,11,14,"तब यीशु ने उन से साफ कह दिया, कि लाजर मर गया है।"));
hinsuperobject.biblemap.put("b43_11_15", new hinsuperobject(43,11,15,"और मैं तुम्हारे कारण आनन्दित हूं कि मैं वहां न था जिस से तुम विश्वास करो, परन्तु अब आओ, हम उसके पास चलें।"));
hinsuperobject.biblemap.put("b43_11_16", new hinsuperobject(43,11,16,"तब थोमा ने जो दिदुमुस कहलाता है, अपने साथ के चेलों से कहा, आओ, हम भी उसके साथ मरने को चलें।"));
hinsuperobject.biblemap.put("b43_11_17", new hinsuperobject(43,11,17,"सो यीशु को आकर यह मालूम हुआ कि उसे कब्र में रखे चार दिन हो चुके हैं।"));
hinsuperobject.biblemap.put("b43_11_18", new hinsuperobject(43,11,18,"बैतनिरयाह यरूशलेम के समीप कोई दो मील की दूरी पर था।"));
hinsuperobject.biblemap.put("b43_11_19", new hinsuperobject(43,11,19,"और बहुत से यहूदी मरथा और मरियम के पास उन के भाई के विषय में शान्ति देने के लिये आए थे।"));
hinsuperobject.biblemap.put("b43_11_20", new hinsuperobject(43,11,20,"सो मरथा यीशु के आने का समचार सुनकर उस से भेंट करने को गई, परन्तु मरियम घर में बैठी रही।"));
hinsuperobject.biblemap.put("b43_11_21", new hinsuperobject(43,11,21,"मरथा ने यीशु से कहा, हे प्रभु, यदि तू यहां होता, तो मेरा भाई कदापि न मरता।"));
hinsuperobject.biblemap.put("b43_11_22", new hinsuperobject(43,11,22,"और अब भी मैं जानती हूं, कि जो कुछ तू परमेश्वर से मांगेगा, परमेश्वर तुझे देगा।"));
hinsuperobject.biblemap.put("b43_11_23", new hinsuperobject(43,11,23,"यीशु ने उस से कहा, तेरा भाई जी उठेगा।"));
hinsuperobject.biblemap.put("b43_11_24", new hinsuperobject(43,11,24,"मरथा ने उस से कहा, मैं जानती हूं, कि अन्तिम दिन में पुनरूत्थान के समय वह जी उठेगा।"));
hinsuperobject.biblemap.put("b43_11_25", new hinsuperobject(43,11,25,"यीशु ने उस से कहा, पुनरूत्थान और जीवन मैं ही हूं, जा कोई मुझ पर विश्वास करता है वह यदि मर भी जाए, तौभी जीएगा।"));
hinsuperobject.biblemap.put("b43_11_26", new hinsuperobject(43,11,26,"और जो कोई जीवता है, और मुझ पर विश्वास करता है, वह अनन्तकाल तक न मरेगा, क्या तू इस बात पर विश्वास करती है?"));
hinsuperobject.biblemap.put("b43_11_27", new hinsuperobject(43,11,27,"उस ने उस से कहा, हां हे प्रभु, मैं विश्वास कर चुकी हूं, कि परमेश्वर का पुत्रा मसीह जो जगत में आनेवाला था, वह तू ही है।"));
hinsuperobject.biblemap.put("b43_11_28", new hinsuperobject(43,11,28,"यह कहकर वह चली गई, और अपनी बहिन मरियम को चुपके से बुलाकर कहा, गुरू यहीं है, और तुझे बुलाता है।"));
hinsuperobject.biblemap.put("b43_11_29", new hinsuperobject(43,11,29,"वह सुनते ही तुरन्त उठकर उसके पास आई।"));
hinsuperobject.biblemap.put("b43_11_30", new hinsuperobject(43,11,30,"(यीशु अभी गांव में नहीं पहुंचा था, परन्तु उसी स्थान में था जहां मरथा ने उस से भेंट की थी।)"));
hinsuperobject.biblemap.put("b43_11_31", new hinsuperobject(43,11,31,"तब जो यहूदी उसके साथ घर में थे, और उसे शान्ति दे रहे थे, यह देखकर कि मरियम तुरन्त उठके बाहर गई है और यह समझकर कि वह कब्र पर रोने को जाती है, उसके पीछे हो लिये।"));
hinsuperobject.biblemap.put("b43_11_32", new hinsuperobject(43,11,32,"जब मरियम वहां पहुंची जहां यीशु था, तो उसे देखते ही उसके पांवों पर गिर के कहा, हे प्रभु, यदि तू यहां होता तो मेरा भाई न मरता।"));
hinsuperobject.biblemap.put("b43_11_33", new hinsuperobject(43,11,33,"जब यीशु न उस को और उन यहूदियों को जो उसके साथ आए थे रोते हुए देखा, तो आत्मा में बहुत ही उदास हुआ, और घबरा कर कहा, तुम ने उसे कहां रखा है?"));
hinsuperobject.biblemap.put("b43_11_34", new hinsuperobject(43,11,34,"उन्हों ने उस से कहा, हे प्रभु, चलकर देख ले।"));
hinsuperobject.biblemap.put("b43_11_35", new hinsuperobject(43,11,35,"यीशु के आंसू बहने लगे।"));
hinsuperobject.biblemap.put("b43_11_36", new hinsuperobject(43,11,36,"तब यहूदी कहने लगे, देखो, वह उस से कैसी प्रीति रखता था।"));
hinsuperobject.biblemap.put("b43_11_37", new hinsuperobject(43,11,37,"परन्तु उन में से कितनों ने कहा, क्या यह जिस ने अन्धे की आंखें खोली, यह भी न कर सका कि यह मनुष्य न मरता?"));
hinsuperobject.biblemap.put("b43_11_38", new hinsuperobject(43,11,38,"यीशु मन में फिर बहुत ही उदास होकर कब्र पर आया, वह एक गुफा थी, और एक पत्थर उस पर धरा था।"));
hinsuperobject.biblemap.put("b43_11_39", new hinsuperobject(43,11,39,"यीशु ने कहा; पत्थर को उठाओ: उस मरे हुए की बहिन मरथा उस से कहने लगी, हे प्रभु, उस में से अब तो दुर्गंध आती है क्योंकि उसे मरे चार दिन हो गए।"));
hinsuperobject.biblemap.put("b43_11_40", new hinsuperobject(43,11,40,"यीशु ने उस से कहा, क्या मैं ने तुझ से न कहा िाा कि यदि तू विश्वास करेगी, तो परमेश्वर की महिमा को देखेगी।"));
hinsuperobject.biblemap.put("b43_11_41", new hinsuperobject(43,11,41,"तब उन्हों ने उस पत्थर को हटाया, फिर यीशु ने आंखें उठाकर कहा, हे पिता, मैं तेरा धन्यवाद करता हूं कि तू ने मेरी सुन ली है।"));
hinsuperobject.biblemap.put("b43_11_42", new hinsuperobject(43,11,42,"और मै जानता था, कि तू सदा मेरी सुनता है, परन्तु जो भीड़ आस पास खड़ी है, उन के कारण मैं ने यह कहा, जिस से कि वे विश्वास करें, कि तू ने मुझे भेजा है।"));
hinsuperobject.biblemap.put("b43_11_43", new hinsuperobject(43,11,43,"यह कहकर उस ने बड़े शब्द से पुकारा, कि हे लाजर, निकल आ।"));
hinsuperobject.biblemap.put("b43_11_44", new hinsuperobject(43,11,44,"जो मर गया था, वह कफन से हाथ पांव बन्धे हुए निकल आया और उसका मुंह अंगोछे से लिपटा हुआ थारू यीशु ने उन से कहा, उसे खोलकर जाने दो।।"));
hinsuperobject.biblemap.put("b43_11_45", new hinsuperobject(43,11,45,"तब जो यहूदी मरियम के पास आए थे, और उसका यह काम देखा था, उन में से बहुतों ने उस पर विश्वास किया।"));
hinsuperobject.biblemap.put("b43_11_46", new hinsuperobject(43,11,46,"परन्तु उन में से कितनों ने फरीसियों के पास जाकर यीशु के कामों का समाचार दिया।।"));
hinsuperobject.biblemap.put("b43_11_47", new hinsuperobject(43,11,47,"इस पर महायाजकों और फरीसियों ने मुख्य सभा के लोगों को इकट्ठा करके कहा, हम करते क्या हैं? यह मनुष्य तो बहुत चिन्ह दिखाता है।"));
hinsuperobject.biblemap.put("b43_11_48", new hinsuperobject(43,11,48,"यदि हम उसे योंही छोड़ दे, तो सब उस पर विश्वास ले आएंगे और रोमी आकर हमारी जगह और जाति दोनों पर अधिकार कर लेंगे।"));
hinsuperobject.biblemap.put("b43_11_49", new hinsuperobject(43,11,49,"तब उन में से काइफा नाम एक व्यक्ति ने जो उस वर्ष का महायाजक था, उन से कहा, तुम कुछ नहीं जानते।"));
hinsuperobject.biblemap.put("b43_11_50", new hinsuperobject(43,11,50,"और न यह सोचते हो, कि तुम्हारे लिये यह भला है, कि हमारे लोगों के लिये एक मनुष्य मरे, और न यह, कि सारी जाति नाश हो।"));
hinsuperobject.biblemap.put("b43_11_51", new hinsuperobject(43,11,51,"यह बात उस ने अपनी ओर से न कही, परन्तु उस वर्ष का महायाजक होकर भविष्यद्वणी की, कि यीशु उस जाति के लिये मरेगा।"));
hinsuperobject.biblemap.put("b43_11_52", new hinsuperobject(43,11,52,"और न केवल उस जाति के लिये, बरन इसलिये भी, कि परमेश्वर की तित्तर बित्तर सन्तानों को एक कर दे।"));
hinsuperobject.biblemap.put("b43_11_53", new hinsuperobject(43,11,53,"सो उसी दिन से वे उसके मार डालने की सम्मति करने लगे।।"));
hinsuperobject.biblemap.put("b43_11_54", new hinsuperobject(43,11,54,"इसलिये यीशु उस समय से यहूदियों में प्रगट होकर न फिरा; परन्तु वहां से जंगल के निकट के देख में इफ्राईम नाम, एक नगर को चला गया; और अपने चेलों के साथ वहीं रहने लगा।"));
hinsuperobject.biblemap.put("b43_11_55", new hinsuperobject(43,11,55,"और यहूदियों का फसह निकट था, और बहुतेरे लोग फसह से पहिले दिहात से यरूशलेम को गए कि अपने आप को शुद्ध करें।"));
hinsuperobject.biblemap.put("b43_11_56", new hinsuperobject(43,11,56,"सो वे यीशु को ढूंढ़ने और मन्दिर में खड़े होकर आपस में कहने लगे, तुम क्या समझते हो?"));
hinsuperobject.biblemap.put("b43_11_57", new hinsuperobject(43,11,57,"क्या वह पर्ब्ब में नहीं आएगा? और महायाजकों और फरीसियों ने भी आज्ञा दे रखी थी, कि यदि कोई यह जाने कि यीशु कहां है तो बताए, कि उसे पकड़ लें।।"));
hinsuperobject.biblemap.put("b43_12_1", new hinsuperobject(43,12,1,"फिर यीशु फतह से छ: दिन पहिले बैतनिरयाह में आया, जंहा लाजर था: जिसे यीशु ने मरे हुओं में से जिलाया था।"));
hinsuperobject.biblemap.put("b43_12_2", new hinsuperobject(43,12,2,"वहां उन्हों ने उसके लिये भोजन तैयार किया, और मरथा सेवा कर रही थी, और लाजर उन में से एक था, जो उसके साथ भोजन करने के लिये बैठे थे।"));
hinsuperobject.biblemap.put("b43_12_3", new hinsuperobject(43,12,3,"तब मरियम ने जटामासी का आध सेर बहुमोल इत्रा लेकर यीशु के पावों पर डाला, और अपने बालों से उसके पांव पोंछे, और इत्रा की सुगंध से घर सुगन्धित हो गया।"));
hinsuperobject.biblemap.put("b43_12_4", new hinsuperobject(43,12,4,"परन्तु उसके चेलों में से यहूदा इस्करियोती नाम एक चेला जो उसे पकड़वाने पर था, कहने लगा।"));
hinsuperobject.biblemap.put("b43_12_5", new hinsuperobject(43,12,5,"यह इत्रा तीन सौ दीनार में बेचकर कंगालों को कयों न दिया गया?"));
hinsuperobject.biblemap.put("b43_12_6", new hinsuperobject(43,12,6,"उस ने यह बात इसलिये न कही, कि उसे कंगालों की चिन्ता थी, परन्तु इसलिये कि वह चोर था और उसके पास उन की थैली रहती थी, और उस में जो कुछ डाला जाता था, वह निकाल लेता था।"));
hinsuperobject.biblemap.put("b43_12_7", new hinsuperobject(43,12,7,"यीशु ने कहा, उसे मेरे गाड़े जाने के दिन के लिये रहने दे।"));
hinsuperobject.biblemap.put("b43_12_8", new hinsuperobject(43,12,8,"क्योंकि कंगाल तो तुम्हारे साथ सदा रहते हैं, परन्तु मैं तुम्हारे साथ सदा न रहूंगा।।"));
hinsuperobject.biblemap.put("b43_12_9", new hinsuperobject(43,12,9,"यहूदियों में से साधारण लोग जान गए, कि वह वहां है, और वे न केवल यीशु के कारण आए परन्तु इसलिये भी कि लाजर को देंखें, जिसे उस ने मरे हुओं में से जिलाया था।"));
hinsuperobject.biblemap.put("b43_12_10", new hinsuperobject(43,12,10,"तब महायाजकों ने लाजर को भी मार डालने की सम्मति की।"));
hinsuperobject.biblemap.put("b43_12_11", new hinsuperobject(43,12,11,"क्योंकि उसके कारण बहुत से यहूदी चले गए, और यीशु पर विश्वास किया।।"));
hinsuperobject.biblemap.put("b43_12_12", new hinsuperobject(43,12,12,"दूसरे दिन बहुत से लोगों ने जो पर्ब्ब में आए थे, यह सुनकर, कि यीशु यरूशलेम में आता है।"));
hinsuperobject.biblemap.put("b43_12_13", new hinsuperobject(43,12,13,"खजूर की, डालियां लीें, और उस से भेंट करने को निकले, और पुकारने लगे, कि होशाना, धन्य इस्त्राएल का राजा, जो प्रभु के नाम से आता है।"));
hinsuperobject.biblemap.put("b43_12_14", new hinsuperobject(43,12,14,"जब यीशु को एक गदहे का बच्चा मिला, तो उस पर बैठा।"));
hinsuperobject.biblemap.put("b43_12_15", new hinsuperobject(43,12,15,"जैसा लिखा है, कि हे सिरयोन की बेटी, मत डर, देख, तेरा राजा गदहे के बच्चे पर चढ़ा हुआ चला आता है।"));
hinsuperobject.biblemap.put("b43_12_16", new hinsuperobject(43,12,16,"उसके चेले, ये बातें पहिले न समझे थे; परन्तु जब यीशु की महिमा प्रगट हुई, तो उन को स्मरण आया, कि ये बातें उसके विषय में लिखी हुई थीं; और लोगों ने उस से इस प्रकार का व्यवहार किया था।"));
hinsuperobject.biblemap.put("b43_12_17", new hinsuperobject(43,12,17,"तब भीड़ के लोगों ने जो उस समय उसके साथ थे यह गवाही दी कि उस ने लाजर को कब्र में से बुलाकर, मरे हुओं में से जिलाया था।"));
hinsuperobject.biblemap.put("b43_12_18", new hinsuperobject(43,12,18,"इसी कारण लोग उस से भेंट करने को आए थे क्योंकि उन्हों ने सुना था, कि उस ने यह आश्चर्यकर्म दिखाया है।"));
hinsuperobject.biblemap.put("b43_12_19", new hinsuperobject(43,12,19,"तब फरीसियों ने आपस में कहा, सोचो तो सही कि तुम से कुछ नहीं बन पड़ता: देखो, संसार उसके पीछे हो चला है।।"));
hinsuperobject.biblemap.put("b43_12_20", new hinsuperobject(43,12,20,"जो लोग उस पर्ब्ब में भजन करने आए थे उन में से कई यूनानी थे।"));
hinsuperobject.biblemap.put("b43_12_21", new hinsuperobject(43,12,21,"उन्हों ने गलील के बैतसैदा के रहनेवाले फिलिप्पुस के पास आकर उस से बिनती की, कि श्रीमान् हम यीशु से भेंट करना चाहते हैं।"));
hinsuperobject.biblemap.put("b43_12_22", new hinsuperobject(43,12,22,"फिलिप्पुस ने आकर अद्रियास से कहा; तब अन्द्रियास और फिलिप्पुस ने यीशु से कहा।"));
hinsuperobject.biblemap.put("b43_12_23", new hinsuperobject(43,12,23,"इस पर यीशु ने उन से कहा, वह समय आ गया है, कि मनुष्य के पुत्रा कि महिमा हो।"));
hinsuperobject.biblemap.put("b43_12_24", new hinsuperobject(43,12,24,"मैं तुम से सच सच कहता हूं, कि जब तक गेहूं का दाना भूमि में पड़कर मर नहीं जाता, वह अकेला रहता है परन्तु जब मर जाता है, तो बहुत फल लाता है।"));
hinsuperobject.biblemap.put("b43_12_25", new hinsuperobject(43,12,25,"जो अपने प्राण को प्रिय जानता है, वह उसे खो देता है; और जो इस जगत में अपने प्राण को अप्रिय जानता हे, वह उसे खो देता है; और जो इस जगत में अपने प्राण को अप्रिय जानता है; वह अनन्त जीवन के लिये उस की रक्षा करता करेगा।"));
hinsuperobject.biblemap.put("b43_12_26", new hinsuperobject(43,12,26,"यदि कोई मेरी सेवा करे, तो मेरे पीछे हो ले; और जहां मैं हूं वहां मेरा सेवक भी होगा; यदि कोई मेरी सेवा करे, तो पिता उसका आदर करेगा।"));
hinsuperobject.biblemap.put("b43_12_27", new hinsuperobject(43,12,27,"जब मेरा जी व्याकुल हो रहा है। इसलिये अब मैं क्या कहूं? हे पिता, मुझे इस घड़ी से बचा? परन्तु मैं इसी कारण इस घड़ी को पहुंचा हूं।"));
hinsuperobject.biblemap.put("b43_12_28", new hinsuperobject(43,12,28,"हे पिता अपने नाम की महिमा कर: तब यह आकाशवाणी हुई, कि मैं ने उस की महिमा की है, और फिर भी करूंगा।"));
hinsuperobject.biblemap.put("b43_12_29", new hinsuperobject(43,12,29,"तब जो लोग खड़े हुए सुन रहे थे, उन्हों ने कहा; कि बादल गरजा, औरों ने कहा, कोई स्वर्गदूत उस से बोला।"));
hinsuperobject.biblemap.put("b43_12_30", new hinsuperobject(43,12,30,"इस पर यीशु ने कहा, यह शब्द मेरे लिये नहीं परन्तु तुम्हारे लिये आया है।"));
hinsuperobject.biblemap.put("b43_12_31", new hinsuperobject(43,12,31,"अब इस जगत का न्याय होता है, अब इस जगत का सरदार निकाल दिया जाएगा।"));
hinsuperobject.biblemap.put("b43_12_32", new hinsuperobject(43,12,32,"और मैं यदि पृथ्वी पर से ऊंचे पर चढ़ाया जाऊंगा, तो सब को अपने पास खीचंूगा।"));
hinsuperobject.biblemap.put("b43_12_33", new hinsuperobject(43,12,33,"ऐसा कहकर उस ने यह प्रगट कर दिया, कि वह कैसी मृत्यु से मरेगा।"));
hinsuperobject.biblemap.put("b43_12_34", new hinsuperobject(43,12,34,"इस पर लोगों ने उस से कहा, कि हम ने व्यवस्था की यह बात सुनी है, कि मसीह सर्वदा रहेगा, फिर तू क्यों कहता है, कि मनुष्य के पुत्रा को ऊंचे पर चढ़ाया जाना अवश्य है?"));
hinsuperobject.biblemap.put("b43_12_35", new hinsuperobject(43,12,35,"यह मनुष्य का पुत्रा कौन है? यीशु ने उन से कहा, ज्योति अब थोड़ी देन तक तुम्हारे बीच में है, जब तक ज्योति तुम्हारे साथ है तब तक चले चलो; ऐसा न हो कि अन्धकार तुम्हें आ घेरे; जो अन्धकार में चलता है वह नहीं जानता कि किधर जाता है।"));
hinsuperobject.biblemap.put("b43_12_36", new hinsuperobject(43,12,36,"जब तक ज्योति तुम्हारे साथ है, ज्योति पर विश्वास करो कि तुम ज्योति के सन्तान होओ।। ये बातें कहकर यीशु चला गया और उन से छिपा रहा।"));
hinsuperobject.biblemap.put("b43_12_37", new hinsuperobject(43,12,37,"और उस ने उन के साम्हने इतने चिन्ह दिखाए, तौभी उन्हों ने उस पर विश्वास न किया।"));
hinsuperobject.biblemap.put("b43_12_38", new hinsuperobject(43,12,38,"ताकि यशायाह भविष्यद्वक्ता का वचन पूरा हो जो उस ने कहा कि हे प्रभु हमारे समाचार की किस ने प्रतीति की है? और प्रभु का भुजबल किस पर प्रगट हुआ?"));
hinsuperobject.biblemap.put("b43_12_39", new hinsuperobject(43,12,39,"इस कारण वे विश्वास न कर सके, क्योंकि यशायाह ने फिर भी कहा।"));
hinsuperobject.biblemap.put("b43_12_40", new hinsuperobject(43,12,40,"कि उस ने उन की आंखें अन्धी, और उन का मन कठोर किया है; कहीं ऐसा न हो, कि आंखों से देखें, और मन से समझें, और फिरें, और मैं उन्हें चंगा करूं।"));
hinsuperobject.biblemap.put("b43_12_41", new hinsuperobject(43,12,41,"यशायाह ने ये बातें इसलिये कहीं, कि उस ने उस की महिमा देखी; और उस ने उसके विषय में बातें की।"));
hinsuperobject.biblemap.put("b43_12_42", new hinsuperobject(43,12,42,"तौभी सरदारों में से भी बहुतों ने उस पर विश्वास किया, परन्तु फरीसियों के कारण प्रगट में नहीं मानते थे, ऐसा न हो कि आराधनालय में से निकाले जाएं।"));
hinsuperobject.biblemap.put("b43_12_43", new hinsuperobject(43,12,43,"क्योंकि मनुष्यों की प्रशंसा उन को परमेश्वर की प्रशंसा से अधिक प्रिय लगती थी।।"));
hinsuperobject.biblemap.put("b43_12_44", new hinsuperobject(43,12,44,"यीशु ने पुकारकर कहा, जो मुझ पर विश्वास करता है, वह मुझ पर नहीं, बरन मेरे भेजनेवाले पर विश्वास करता है।"));
hinsuperobject.biblemap.put("b43_12_45", new hinsuperobject(43,12,45,"और जो मुझे देखता है, वह मेरे भेजनेवाले को देखता है।"));
hinsuperobject.biblemap.put("b43_12_46", new hinsuperobject(43,12,46,"मैं जगत में ज्योति होकर आया हूं ताकि जो कोई मुझ पर विश्वास करे, वह अन्धकार में ने रहे।"));
hinsuperobject.biblemap.put("b43_12_47", new hinsuperobject(43,12,47,"यदि कोई मेरी बातें सुनकर न माने, तो मैं उसे दोषी नहीं ठहराता, क्योंकि मैं जगत को दोषी ठहराने के लिये नहीं, परन्तु जगत का उद्धार करने के लिये आया हूं।"));
hinsuperobject.biblemap.put("b43_12_48", new hinsuperobject(43,12,48,"जो मुझे तुच्छ जानता है और मेरी बातें ग्रहण नहीं करता है उस को दोषी ठहरानेवाला तो एक है: अर्थात् जो वचन मैं ने कहा है, वह पिछले दिन में उसे दोषी ठहराएगा।"));
hinsuperobject.biblemap.put("b43_12_49", new hinsuperobject(43,12,49,"क्योंकि मैं ने अपनी ओर से बातें नहीं कीं, परन्तु पिता जिस ने मुझे भेजा है उसी ने मुझे आज्ञा दी है, कि क्या क्या कहूं? और क्या क्या बोलूं?"));
hinsuperobject.biblemap.put("b43_12_50", new hinsuperobject(43,12,50,"और मैं जानता हूं, कि उस की आज्ञा अनन्त जीवन है इसलिये मैं जो बोलता हूं, वह जैसा पिता ने मुझ से कहा है वैसा ही बोलता हूं।।"));
hinsuperobject.biblemap.put("b43_13_1", new hinsuperobject(43,13,1,"फसह के पर्ब्ब से पहिले जब यीशु ने जान लिया, कि मेरी वह घड़ी आ पहुंची है कि जगत छोड़कर पिता के पास जाऊं, तो अपने लोगों से, जो जगत में थे, जैसा प्रेम वह रखता था, अन्त तक वैसा ही प्रेम रखता रहा।"));
hinsuperobject.biblemap.put("b43_13_2", new hinsuperobject(43,13,2,"और जब शैतान शमौन के पुत्रा यहूदा इस्करियोती के मन में यह डाल चुका था, कि उसे पकड़वाए, तो भोजन के समय।"));
hinsuperobject.biblemap.put("b43_13_3", new hinsuperobject(43,13,3,"यीशु ने यह जानकर कि पिता ने सब कुछ मेरे हाथ में कर दिया है और मैं परमेश्वर के पास से आया हूं, और परमेश्वर के पास जाता हूं।"));
hinsuperobject.biblemap.put("b43_13_4", new hinsuperobject(43,13,4,"भोजन पर से उठकर अपने कपड़े उतार दिए, और अंगोछा लेकर अपनी कमर बान्धी।"));
hinsuperobject.biblemap.put("b43_13_5", new hinsuperobject(43,13,5,"तब बरतन में पानी भरकर चेलों के पांव धोने और जिस अंगोछे से उस की कमर बन्धी थी उसी से पोंछने लगा।"));
hinsuperobject.biblemap.put("b43_13_6", new hinsuperobject(43,13,6,"जब वह शमौन पतरस के पास आया: तब उस ने उस से कहा, हे प्रभु,"));
hinsuperobject.biblemap.put("b43_13_7", new hinsuperobject(43,13,7,"क्या तू मेरे पांव धोता है? यीशु ने उस को उत्तर दिया, कि जो मैं करता हूं, तू अब नहीं जानता, परन्तु इस के बाद समझेगा।"));
hinsuperobject.biblemap.put("b43_13_8", new hinsuperobject(43,13,8,"पतरस ने उस से कहा, तू मेरे पांव कभी न धोने पाएगा: यह सुनकर यीशु ने उस से कहा, यदि मैं तुझे न धोऊं, तो मेरे साथ तेरा कुछ भी साझा नहीं।"));
hinsuperobject.biblemap.put("b43_13_9", new hinsuperobject(43,13,9,"शमौन पतरस ने उस से कहा, हे प्रभु, तो मेरे पांव ही नहीं, बरन हाथ और सिर भी धो दे।"));
hinsuperobject.biblemap.put("b43_13_10", new hinsuperobject(43,13,10,"यीशु ने उस से कहा, जो नहा चुका है, उसे पांव के सिवा और कुछ धोने का प्रयोजन नहीं; परन्तु वह बिलकुल शुद्ध है: और तुम शुद्ध हो; परन्तु सब के सब नहीं।"));
hinsuperobject.biblemap.put("b43_13_11", new hinsuperobject(43,13,11,"वह तो अपने पकड़वानेवाले को जानता था इसी लिये उस ने कहा, तुम सब के सब शुद्ध नहीं।।"));
hinsuperobject.biblemap.put("b43_13_12", new hinsuperobject(43,13,12,"जब वह उन के पांव धो चुका और अपने कपड़े पहिनकर फिर बैठ गया तो उन से कहने लगा, क्या तुम समझे कि मैं ने तुम्हारे साथ क्या किया?"));
hinsuperobject.biblemap.put("b43_13_13", new hinsuperobject(43,13,13,"तुम मुझे गुरू, और प्रभु, कहते हो, और भला कहते हो, क्योंकि मैं वहीं हूं।"));
hinsuperobject.biblemap.put("b43_13_14", new hinsuperobject(43,13,14,"यदि मैं ने प्रभु और गुरू होकर तुम्हारे पांव धोए; तो तुम्हें भी एक दुसरे के पांव धोना चाहिए।"));
hinsuperobject.biblemap.put("b43_13_15", new hinsuperobject(43,13,15,"क्योंकि मैं ने तुम्हें नमूना दिखा दिया है, कि जैसा मैं ने तुम्हारे साथ किया है, तुम भी वैसा ही किया करो।"));
hinsuperobject.biblemap.put("b43_13_16", new hinsuperobject(43,13,16,"मैं तुम से सच सच कहता हूं, दास अपने स्वामी से बड़ा नहीं; और न भेजा हुआ अपने भेजनेवाले से।"));
hinsuperobject.biblemap.put("b43_13_17", new hinsuperobject(43,13,17,"तुम तो ये बातें जानते हो, और यदि उन पर चलो, तो धन्य हो।"));
hinsuperobject.biblemap.put("b43_13_18", new hinsuperobject(43,13,18,"मैं तुम सब के विषय में नहीं कहता: जिन्हें मैं ने चुन लिया है, उन्हें मैं जानता हूं: परन्तु यह इसलिये है, कि पवित्रा शास्त्रा का यह वचन पूरा हो, कि जो मेरी रोटी खाता है, उस ने मुझ पर लात उठाई।"));
hinsuperobject.biblemap.put("b43_13_19", new hinsuperobject(43,13,19,"अब मैं उसके होने से पहिले तुम्हें जताए देता हूं कि जब हो जाए तो तुम विश्वास करो कि मैं वहीं हूं।"));
hinsuperobject.biblemap.put("b43_13_20", new hinsuperobject(43,13,20,"मैं तुम से सच सच कहता हूं, कि जो मेरे भेजे हुए को ग्रहण करता है, वह मुझे ग्रहण करता है, और जो मुझे ग्रहण करता है, वह मेरे भेजनेवाले को ग्रहण करता है।।"));
hinsuperobject.biblemap.put("b43_13_21", new hinsuperobject(43,13,21,"ये बातें कहकर यीशु आत्मा में व्याकुल हुआ और यह गवाही दी, कि मैं तुम से सच सच कहता हूं, कि तुम में से एक मुझे पकड़वाएगा।"));
hinsuperobject.biblemap.put("b43_13_22", new hinsuperobject(43,13,22,"चेले यह संदेह करते हुए, कि वह किस के विषय में कहता है, एक दूसरे की ओर देखने लगे।"));
hinsuperobject.biblemap.put("b43_13_23", new hinsuperobject(43,13,23,"उसके चेलों में से एक जिस से यीशु प्रेम रखता था, यीशु की छाती की ओर झुका हुआ बैठा था।"));
hinsuperobject.biblemap.put("b43_13_24", new hinsuperobject(43,13,24,"तब शमौन पतरस ने उस की ओर सैन करके पूछा, कि बता तो, वह किस के विषय में कहता है?"));
hinsuperobject.biblemap.put("b43_13_25", new hinsuperobject(43,13,25,"तब उस ने उसी तरह यीशु की छाती की ओर झुक कर पूछा, हे प्रभु, वह कौन है? यीशु ने उत्तर दिया, जिसे मैं यह रोटी का टुकड़ा डुबोकर दूंगा, वही है।"));
hinsuperobject.biblemap.put("b43_13_26", new hinsuperobject(43,13,26,"और उस ने टुकड़ा डुबोकर शमौन के पुत्रा यहूदा इस्करियोती को दिया।"));
hinsuperobject.biblemap.put("b43_13_27", new hinsuperobject(43,13,27,"और टुकड़ा लेते ही शैतान उस में समा गया: तब यीशु ने उस से कहा, जो तू करता है, तुरन्त कर।"));
hinsuperobject.biblemap.put("b43_13_28", new hinsuperobject(43,13,28,"परन्तु बैठनेवालों में से किसी ने न जाना कि उस ने यह बात उस से किस लिये कही।"));
hinsuperobject.biblemap.put("b43_13_29", new hinsuperobject(43,13,29,"यहूदा के पास थैली रहती थी, इसलिये किसी किसी ने समझा, कि यीशु उस से कहता है, कि जो कुछ हमें पर्ब्ब के लिये चाहिए वह मोल ले, या यह कि कंगालों को कुछ दे।"));
hinsuperobject.biblemap.put("b43_13_30", new hinsuperobject(43,13,30,"तब वह टुकड़ा लेकर तुरन्त बाहर चला गया, और रात्रि का समय था।।"));
hinsuperobject.biblemap.put("b43_13_31", new hinsuperobject(43,13,31,"जब वह बाहर चला गया तो यीशु ने कहा; अब मनुष्य पुत्रा की महिमा हुई, और परमेश्वर की महिमा उस में हुई।"));
hinsuperobject.biblemap.put("b43_13_32", new hinsuperobject(43,13,32,"और परमेश्वर भी अपने में उस की महिमा करेगा, बरन तुरन्त करेगा।"));
hinsuperobject.biblemap.put("b43_13_33", new hinsuperobject(43,13,33,"हे बालको, मैं और थोड़ी देन तुम्हारे पास हूं: फिर तुम मुझे ढूंढोगे, और जैसा मैं ने यहूदियों से कहा, कि जहां मैं जाता हूं, वहां तुम नहीं आ सकते वैसा ही मैं अब तुम से भी कहता हूं।"));
hinsuperobject.biblemap.put("b43_13_34", new hinsuperobject(43,13,34,"मैं तुम्हें एक नई आज्ञा देता हूं, कि एक दूसरे से प्रेम रखो: जैसा मैं ने तुम से प्रेम रखा है, वैसा ही तुम भी एक दुसरे से प्रेम रखो।"));
hinsuperobject.biblemap.put("b43_13_35", new hinsuperobject(43,13,35,"यदि आपस में प्रेम रखोगे तो इसी से सब जानेंगे, कि तुम मेरे चेले हो।।"));
hinsuperobject.biblemap.put("b43_13_36", new hinsuperobject(43,13,36,"शमौन पतरस ने उस से कहा, हे प्रभु, तू कहां जाता है? यीशु ने उत्तर दिया, कि जहां मैं जाता हूं, वहां तू अब मेरे पीछे आ नहीं सकता! परन्तु इस के बाद मेरे पीछे आएगा।"));
hinsuperobject.biblemap.put("b43_13_37", new hinsuperobject(43,13,37,"पतरस ने उस से कहा, हे प्रभु, अभी मैं तेरे पीछे क्यों नहीं आ सकता? मैं तो तेरे लिये अपना प्राण दूंगा।"));
hinsuperobject.biblemap.put("b43_13_38", new hinsuperobject(43,13,38,"यीशु ने उत्तर दिया, क्या तू मेरे लिये अपना प्राण देगा? मैं तुझ से सच सच कहता हूं कि मुर्ग बांग न देगा जब तक तू तीन बार मेरा इन्कार न कर लेगा।"));
hinsuperobject.biblemap.put("b43_14_1", new hinsuperobject(43,14,1,"तुम्हारा मन व्याकुल न हो, तुम परमेश्वर पर विश्वास रखते हो मुझ पर भी विश्वास रखो।"));
hinsuperobject.biblemap.put("b43_14_2", new hinsuperobject(43,14,2,"मेरे पिता के घर में बहुत से रहने के स्थान हैं, यदि न होते, तो मैं तुम से कह देता क्योंकि मैं तुम्हारे लिये जगह तैयार करने जाता हूं।"));
hinsuperobject.biblemap.put("b43_14_3", new hinsuperobject(43,14,3,"और यदि मैं जाकर तुम्हारे लिये जगह तैयार करूं, तो फिर आकर तुम्हें अपने यहंा ले जाऊंगा, कि जहां मैं रहूं वहां तुम भी रहो।"));
hinsuperobject.biblemap.put("b43_14_4", new hinsuperobject(43,14,4,"और जहां मैं जाता हूं तुम वहां का मार्ग जानते हो।"));
hinsuperobject.biblemap.put("b43_14_5", new hinsuperobject(43,14,5,"थोमा ने उस से कहा, हे प्रभु, हम नहीं जानते कि तू हां जाता है? तो मार्ग कैसे जानें?"));
hinsuperobject.biblemap.put("b43_14_6", new hinsuperobject(43,14,6,"यीशु ने उस से कहा, मार्ग और सच्चाई और जीवन मैं ही हूं; बिना मेरे द्वारा कोई पिता के पास नहीं पहुंच सकता।"));
hinsuperobject.biblemap.put("b43_14_7", new hinsuperobject(43,14,7,"यदि तुम ने मुझे जाना होता, तो मेरे पिता को भी जानते, और अब उसे जानते हो, और उसे देखा भी है।"));
hinsuperobject.biblemap.put("b43_14_8", new hinsuperobject(43,14,8,"फिलिप्पुस ने उस से कहा, हे प्रभु, पिता को हमें दिखा दे: यही हमारे लिये बहुत है।"));
hinsuperobject.biblemap.put("b43_14_9", new hinsuperobject(43,14,9,"यीशु ने उस से कहा; हे फिलिप्पुस, मैं इतने दिन से तुम्हारे साथ हूं, और क्या तू मुझे नहीं जानता? जिस ने मुझे देखा है उस ने पिता को देखा है: तू क्यों कहता है कि पिता को हमें दिखा।"));
hinsuperobject.biblemap.put("b43_14_10", new hinsuperobject(43,14,10,"क्या तू प्रतीति नहीं करता, कि मैं पिता में हूं, और पिता मुझ में हैं? ये बातें जो मैं तुम से कहता हूं, अपनी ओर से नहीं कहता, परन्तु पिता मुझ में रहकर अपने काम करता है।"));
hinsuperobject.biblemap.put("b43_14_11", new hinsuperobject(43,14,11,"मेरी ही प्रतीति करो, कि मैं पिता में हूं; और पिता मुझ में है; नहीं तो कामों ही के कारण मेरी प्रतीति करो।"));
hinsuperobject.biblemap.put("b43_14_12", new hinsuperobject(43,14,12,"मैं तुम से सच सच कहता हूं, कि जो मुझ पर विश्वास रखता है, ये काम जो मैं करता हूं वही भी करेगा, बरन इन से भी बड़े काम करेगा, क्योंकि मैं पिता के पास जाता हूं।"));
hinsuperobject.biblemap.put("b43_14_13", new hinsuperobject(43,14,13,"और जो कुछ तुम मेरे नाम से मांगोगे, वही मैं करूंगा कि पुत्रा के द्वारा पिता की महिमा हो।"));
hinsuperobject.biblemap.put("b43_14_14", new hinsuperobject(43,14,14,"यदि तुम मुझ से मेरे नाम से कुछ मांगोगे, तो मैं उसे करूंगा।"));
hinsuperobject.biblemap.put("b43_14_15", new hinsuperobject(43,14,15,"यदि तुम मुझ से प्रेम रखते हो, तो मेरी आज्ञाओं को मानोगे।"));
hinsuperobject.biblemap.put("b43_14_16", new hinsuperobject(43,14,16,"और मैं पिता से बिनती करूंगा, और वह तुम्हें एक और सहायक देगा, कि वह सर्वदा तुम्हारे साथ रहे।"));
hinsuperobject.biblemap.put("b43_14_17", new hinsuperobject(43,14,17,"अर्थात् सत्य का आत्मा, जिसे संसार ग्रहण नहीं कर सकता, क्योंकि वह न उसे देखता है और न उसे जानता है: तुम उसे जानते हो, क्योंकि वह तुम्हारे साथ रहता है, और वह तुम में होगा।"));
hinsuperobject.biblemap.put("b43_14_18", new hinsuperobject(43,14,18,"मैं तुम्हें अनाथ न छोडूंगा, मैं तुम्हारे पास आता हूं।"));
hinsuperobject.biblemap.put("b43_14_19", new hinsuperobject(43,14,19,"और थोड़ी देर रह गई है कि संसार मुझे न देखेगा, परन्तु तुम मुझे देखोगे, इसलिये कि मैं जीवित हूं, तुम भी जीवित रहोगे।"));
hinsuperobject.biblemap.put("b43_14_20", new hinsuperobject(43,14,20,"उस दिन तुम जानोगे, कि मैं अपने पिता में हूं, और तुम मुझ में, और मैं तुम में।"));
hinsuperobject.biblemap.put("b43_14_21", new hinsuperobject(43,14,21,"जिस के पास मेरी आज्ञा है, और वह उन्हें मानता है, वही मुझ से प्रेम रखता है, और जो मुझ से प्रेम रखता है, उस से मेरा पिता प्रेम रखेगा, और मैं उस से प्रेम रखूंगा, और अपने आप को उस पर प्रगट करूंगा।"));
hinsuperobject.biblemap.put("b43_14_22", new hinsuperobject(43,14,22,"उस यहूदा ने जो इस्करियोती न था, उस से कहा, हे प्रभु, क्या हुआ की तू अपने आप को हम पर प्रगट किया चाहता है, और संसार पर नहीं।"));
hinsuperobject.biblemap.put("b43_14_23", new hinsuperobject(43,14,23,"यीशु ने उस को उत्तर दिया, यदि कोई मुझ से प्रेम रखे, तो वह मेरे वचन को मानेगा, और मेरा पिता उस से प्रेम रखेगा, और हम उसके पास आएंगे, और उसके साथ वास करेंगे।"));
hinsuperobject.biblemap.put("b43_14_24", new hinsuperobject(43,14,24,"जो मुझ से प्रेम नहीं रखता, वह मेरे वचन नहीं मानता, और जो वचन तुम सुनते हो, वह मेरा नहीं वरन पिता का है, जिस ने मुझे भेजा।।"));
hinsuperobject.biblemap.put("b43_14_25", new hinsuperobject(43,14,25,"ये बातें मैं ने तुम्हारे साथ रहते हुए तुम से कही।"));
hinsuperobject.biblemap.put("b43_14_26", new hinsuperobject(43,14,26,"परन्तु सहायक अर्थात् पवित्रा आत्मा जिसे पिता मेरे नाम से भेजेगा, वह तुम्हें सब बातें सिखाएगा, और जो कुछ मैं ने तुम से कहा है, वह सब तुम्हें स्मरण कराएगा।"));
hinsuperobject.biblemap.put("b43_14_27", new hinsuperobject(43,14,27,"मैं तुम्हें शान्ति दिए जाता हूं, अपनी शान्ति तुम्हें देता हूं; जैसे संसार देता है, मैं तुम्हें नहीं देता: तुम्हारा मन न घबराए और न डरे।"));
hinsuperobject.biblemap.put("b43_14_28", new hinsuperobject(43,14,28,"तुम ने सुना, कि मैं ने तुम से कहा, कि मैं जाता हूं, और तुम्हारे पास फिर आता हूं: यदि तुम मुझ से प्रेम रखते, तो इस बात से आनन्दित होते, कि मैं पिता के पास जाता हूं क्योंकि पिता मुझ से बड़ा है।"));
hinsuperobject.biblemap.put("b43_14_29", new hinsuperobject(43,14,29,"और मैं ने अब इस के होने के पहिले तुम से कह दिया है, कि जब वह हो जाए, तो तुम प्रतीति करो।"));
hinsuperobject.biblemap.put("b43_14_30", new hinsuperobject(43,14,30,"मैं अब से तुम्हारे साथ और बहुत बातें न करूंगा, क्योंकि इस संसार का सरदार आता है, और मुझ में उसका कुछ नहीं।"));
hinsuperobject.biblemap.put("b43_14_31", new hinsuperobject(43,14,31,"परन्तु यह इसलिये होता है कि संसार जाने कि मैं पिता से प्रेम रखता हूं, और जिस तरह पिता ने मुझे आज्ञा दी, मैं वैसे ही करता हूं: उठो, यहां से चलें।।"));
hinsuperobject.biblemap.put("b43_15_1", new hinsuperobject(43,15,1,"सच्ची दाखलता मैं हूं; और मेरा पिता किसान है।"));
hinsuperobject.biblemap.put("b43_15_2", new hinsuperobject(43,15,2,"जो डाली मुझ में है, और नहीं फलती, उसे वह काट डालता है, और जो फलती है, उसे वह छांटता है ताकि और फले।"));
hinsuperobject.biblemap.put("b43_15_3", new hinsuperobject(43,15,3,"तुम तो उस वचन के कारण जो मैं ने तुम से कहा है, शुद्ध हो।"));
hinsuperobject.biblemap.put("b43_15_4", new hinsuperobject(43,15,4,"तुम मुझ में बने रहो, और मैं तुम में: जैसे डाली यदि दाखलता में बनी न रहे, तो अपने आप से नहीं फल सकती, वैसे ही तुम भी यदि मुझ में बने न रहो तो नहीं फल सकते।"));
hinsuperobject.biblemap.put("b43_15_5", new hinsuperobject(43,15,5,"में दाखलता हूं: तुम डालियां हो; जो मुझ में बना रहता है, और मैं उस में, वह बहुत फल फलता है, क्योंकि मुझ से अलग होकर तुम कुछ भी नहीं कर सकते।"));
hinsuperobject.biblemap.put("b43_15_6", new hinsuperobject(43,15,6,"यदि कोई मुझ में बना न रहे, तो वह डाली की नाई फेंक दिया जाता, और सूख जाता है; और लोग उन्हें बटोरकर आग में झोंक देते हैं, और वे जल जाती हैं।"));
hinsuperobject.biblemap.put("b43_15_7", new hinsuperobject(43,15,7,"यदि तुम मुझ में बने रहो, और मेरी बातें तुम में बनी रहें तो जो चाहो मांगो और वह तुम्हारे लिये हो जाएगा।"));
hinsuperobject.biblemap.put("b43_15_8", new hinsuperobject(43,15,8,"मेरे पिता की महिमा इसी से होती है, कि तुम बहुत सा फल लाओ, तब ही तुम मेरे चेले ठहरोगे।"));
hinsuperobject.biblemap.put("b43_15_9", new hinsuperobject(43,15,9,"जैसा पिता ने मुझ से प्रेम रखा, मेरे प्रेम में बने रहो।"));
hinsuperobject.biblemap.put("b43_15_10", new hinsuperobject(43,15,10,"यदि तुम मेरी आज्ञाओं को मानोगे, तो मेरे प्रेम में बने रहोगे: जैसा कि मैं ने अपने पिता की आज्ञाओं को माना है, और उसके प्रेम में बना रहता हूं।"));
hinsuperobject.biblemap.put("b43_15_11", new hinsuperobject(43,15,11,"मैं ने ये बातें तुम से इसलिये कही हैं, कि मेरा आनन्द तुम में बना रहे, और तुम्हारा आनन्द पूरा हो जाए।"));
hinsuperobject.biblemap.put("b43_15_12", new hinsuperobject(43,15,12,"मेरी आज्ञा यह है, कि जैसा मैं ने तुम से प्रेम रखा, वैसा ही तुम भी एक दूसरे से प्रेम रखो।"));
hinsuperobject.biblemap.put("b43_15_13", new hinsuperobject(43,15,13,"इस से बड़ा प्रेम किसी का नहीं, कि कोई अपने मित्रों के लिये अपना प्राण दे।"));
hinsuperobject.biblemap.put("b43_15_14", new hinsuperobject(43,15,14,"जो कुछ मैं तुम्हें आज्ञा देता हूं, यदि उसे करो, तो तुम मेरे मित्रा हो।"));
hinsuperobject.biblemap.put("b43_15_15", new hinsuperobject(43,15,15,"अब से मैं तुम्हें दास न कहूंगा, क्योंकि दास नहीं जानता, कि उसका स्वामी क्या करता है: परन्तु मैं ने तुम्हें मित्रा कहा है, क्योंकि मैं ने जो बातें अपने पिता से सुनीं, वे सब तुम्हें बता दीं।"));
hinsuperobject.biblemap.put("b43_15_16", new hinsuperobject(43,15,16,"तुम ने मुझे नहीं चुना परन्तु मैं ने तुम्हें चुना है और तुम्हें ठहराया ताकि तुम जाकर फल लाओ; और तुम्हारा फल बना रहे, कि तुम मेरे नाम से जो कुछ पिता से मांगो, वह तुम्हें दे।"));
hinsuperobject.biblemap.put("b43_15_17", new hinsuperobject(43,15,17,"इन बातें की आज्ञा मैं तुम्हें इसलिये देता हूं, कि तुम एक दूसरे से प्रेम रखो।"));
hinsuperobject.biblemap.put("b43_15_18", new hinsuperobject(43,15,18,"यदि संसार तुम से बैर रखता है, तो तुम जानते हो, कि उस ने तुम से पहिले मुझ से भी बैर रखा।"));
hinsuperobject.biblemap.put("b43_15_19", new hinsuperobject(43,15,19,"यदि तुम संसार के होते, तो संसार अपनों से प्रीति रखता, परन्तु इस कारण कि तुम संसार में से चुन लिया है इसी लिये संसार तुम से बैर रखता है।"));
hinsuperobject.biblemap.put("b43_15_20", new hinsuperobject(43,15,20,"जो बात मै। ने तुम से कही थी, कि दास अपने स्वामी से बड़ा नहीं होता, उसको याद रखो: यदि उन्हों ने मुझे सताया, तो तुम्हें भी सताएंगे; यदि उन्हों ने मेरी बात मानी, तो तुम्हारी भी मानेंगे।"));
hinsuperobject.biblemap.put("b43_15_21", new hinsuperobject(43,15,21,"परन्तु यह सब कुछ वे मेरे नाम के कारण तुम्हारे साथ करेंगे क्योंकि वे मेरे भेजनेवाले को नहीं जानते।"));
hinsuperobject.biblemap.put("b43_15_22", new hinsuperobject(43,15,22,"यदि मैं न आता और उन से बातें न करता, तो वे पापी न ठहरते परन्तु अब उन्हें उन के पाप के लिये कोई बहाना नहीं।"));
hinsuperobject.biblemap.put("b43_15_23", new hinsuperobject(43,15,23,"जो मुझ से बैर रखता है, वह मेरे पिता से भी बैर रखता है।"));
hinsuperobject.biblemap.put("b43_15_24", new hinsuperobject(43,15,24,"यदि मैं उन में वे काम न करता, जो और किसी ने नहीं किए तो वे पापी नहीं ठहरते, परन्तु अब तो उन्हों ने मुझे और मेरे पिता दोनों को देखा, और दोनों से बैर किया।"));
hinsuperobject.biblemap.put("b43_15_25", new hinsuperobject(43,15,25,"और यह इसलिये हुआ, कि वहि वचन पूरा हो, जो उन की व्यवस्था में लिखा है, कि उन्हों ने मुझ से व्यर्थ बैर किया।"));
hinsuperobject.biblemap.put("b43_15_26", new hinsuperobject(43,15,26,"परन्तु जब वह सहायक आएगा, जिसे मैं तुम्हारे पास भेजूंगा, अर्थात् सत्य का आत्मा जो पिता की ओर से निकलता है, तो वह मेरी गवाही देगा।"));
hinsuperobject.biblemap.put("b43_15_27", new hinsuperobject(43,15,27,"और तुम भी गवाह हो क्योंकि तुम आरम्भ से मेरे साथ रहे हो।।"));
hinsuperobject.biblemap.put("b43_16_1", new hinsuperobject(43,16,1,"ये बातें मैं ने तुम से इसलिये कहीं कि तुम ठोकर न खाओ।"));
hinsuperobject.biblemap.put("b43_16_2", new hinsuperobject(43,16,2,"वे तुम्हें आराधनालयों में से निकाल देंगे, वरन वह समय आता है, कि जो कोई तुम्हें मार डालेगा यह समझेगा कि मैं परमेश्वर की सेवा करता हूं।"));
hinsuperobject.biblemap.put("b43_16_3", new hinsuperobject(43,16,3,"और यह वे इसलिये करेंगे कि उन्हों ने न पिता को जाना है और न मुझे जानते हैं।"));
hinsuperobject.biblemap.put("b43_16_4", new hinsuperobject(43,16,4,"परन्तु ये बातें मैं ने इसलिये तुम से कहीं, कि जब उन का समय आए तो तुम्हें स्मरण आ जाए, कि मैं ने तुम से पहिले ही कह दिया था: और मैं ने आरम्भ में तुम से ये बातें इसलिये नहीं कहीं क्योंकि मैं तुम्हारे साथ था।"));
hinsuperobject.biblemap.put("b43_16_5", new hinsuperobject(43,16,5,"अब मैं अपने भेजनेवाले के पास जाता हूं और तुम में से कोई मुझ से नहीं पूछता, कि तू कहां जाता हैं?"));
hinsuperobject.biblemap.put("b43_16_6", new hinsuperobject(43,16,6,"परन्तु मैं ने जो ये बातें तुम से कही हैं, इसलिये तुम्हारा मन शोक से भर गया।"));
hinsuperobject.biblemap.put("b43_16_7", new hinsuperobject(43,16,7,"तौभी मैं तुम से सच कहता हूं, कि मेरा जाना तुम्हारे लिये अच्छा है, क्योंकि यदि मैं न जाऊं, तो वह सहायक तुम्हारे पास न आएगा, परन्तु यदि मैं जाऊंगा, तो उसे तुम्हारे पास भेज दूंगा।"));
hinsuperobject.biblemap.put("b43_16_8", new hinsuperobject(43,16,8,"और वह आकर संसार को पाप और धार्मिकता और न्याय के विषय में निरूत्तर करेगा।"));
hinsuperobject.biblemap.put("b43_16_9", new hinsuperobject(43,16,9,"पाप के विषय में इसलिये कि वे मुझ पर विश्वास नहीं करते।"));
hinsuperobject.biblemap.put("b43_16_10", new hinsuperobject(43,16,10,"और धार्मिकता के विषय में इसलिये कि मैं पिता के पास जाता हूं,"));
hinsuperobject.biblemap.put("b43_16_11", new hinsuperobject(43,16,11,"और तुम मुझे फिर न देखोगे: न्याय के विषय में इसलिये कि संसार का सरदार दोषी ठहराया गया है।"));
hinsuperobject.biblemap.put("b43_16_12", new hinsuperobject(43,16,12,"मुझे तुम से और भी बहुत सी बातें कहनी हैं, परन्तु अभी तुम उन्हें सह नहीं सकते।"));
hinsuperobject.biblemap.put("b43_16_13", new hinsuperobject(43,16,13,"परन्तु जब वह अर्थात् सत्य का आत्मा आएगा, तो तुम्हें सब सत्य का मार्ग बताएगा, क्योंकि वह अपनी ओर से न कहेगा, परन्तु जो कुछ सुनेगा, वही कहेगा, और आनेवाली बातें तुम्हें बताएगा।"));
hinsuperobject.biblemap.put("b43_16_14", new hinsuperobject(43,16,14,"वह मेरी महिमा करेगा, क्योंकि वह मेरी बातों में से लेकर तुम्हें बताएगा।"));
hinsuperobject.biblemap.put("b43_16_15", new hinsuperobject(43,16,15,"जो कुछ पिता का है, वह सब मेरा है; इसलिये मैं ने कहा, कि वह मेरी बातों में से लेकर तुम्हें बताएगा।"));
hinsuperobject.biblemap.put("b43_16_16", new hinsuperobject(43,16,16,"थोड़ी देर तुम मुझे न देखोगे, और फिर थोड़ी देर में मुझे देखोगे।"));
hinsuperobject.biblemap.put("b43_16_17", new hinsuperobject(43,16,17,"तब उसके कितने चेलों ने आपस में कहा, यह क्या है, जो वह हम से कहता है, कि थोड़ी देर में तुम मुझे न देखोगे, और फिर थोड़ी देर में मुझे देखोगे? और यह इसलिये कि मैं कि मैं पिता के मास जाता हूं?"));
hinsuperobject.biblemap.put("b43_16_18", new hinsuperobject(43,16,18,"तब उन्हों ने कहा, यह थोड़ी देर जो वह कहता है, क्या बात है? हम नहीं जानते, कि क्या कहता है।"));
hinsuperobject.biblemap.put("b43_16_19", new hinsuperobject(43,16,19,"यीशु ने यह जानकर, कि वे मुझ से पूछना चाहते हैं, उन से कहा, क्या तुम आपस में मेरी इस बाते के विषय में पूछ पाछ करते हो, कि थोड़ी देर में तुम मुझे न देखोगे, और फिर थोड़ी देर में मुझे देखोगे।"));
hinsuperobject.biblemap.put("b43_16_20", new hinsuperobject(43,16,20,"मैं तुम से सच सच कहता हूं; कि तुम रोओगे और विलाप करोगे, परन्तु संसार आनन्द करेगा: तुम्हें शोक होगा, परन्तु तुम्हारा शोक आनन्द बन जाएगा।"));
hinsuperobject.biblemap.put("b43_16_21", new hinsuperobject(43,16,21,"जब स्त्री जनने लगती है तो उस को शोक होता है, क्योंकि उस की दु:ख की घड़ी आ पहुंची, परन्तु जब वह बालक जन्म चुकी तो इस आनन्द से कि जगत में एक मनुष्य उत्पन्न हुआ, उस संकट को फिर स्मरण नहीं करती।"));
hinsuperobject.biblemap.put("b43_16_22", new hinsuperobject(43,16,22,"और तुम्हें भी अब तो शोक है, परन्तु मैं तुम से फिर मिलूंगा और तुम्हारे मन में आनन्द होगा; और तुम्हारा आनन्द कोई तुम से छीन न लेगा।"));
hinsuperobject.biblemap.put("b43_16_23", new hinsuperobject(43,16,23,"उस दिन तुम मुझ से कुछ न पूछोगे: मैं तुम से सच सच कहता हूं, यदि पिता से कुछ मांगोगे, तो वह मेरे नाम से तुम्हें देगा।"));
hinsuperobject.biblemap.put("b43_16_24", new hinsuperobject(43,16,24,"अब तक तुम ने मेरे नाम से कुछ नहीं मांगा; मांगो तो पाओगे ताकि तुम्हारा आनन्द पूरा हो जाए।।"));
hinsuperobject.biblemap.put("b43_16_25", new hinsuperobject(43,16,25,"मैं ने ये बातें तुम से दृष्टान्तों में कही हैं, परन्तु वह समय आता है, कि मैं तुम से दृष्टान्तों में और फिर नहीं कहूंगा परन्तु खोलकर तुम्हें पिता के विषय में बताऊंगा।"));
hinsuperobject.biblemap.put("b43_16_26", new hinsuperobject(43,16,26,"उस दिन तुम मेरे नाम से मांगोगे, और मैं तुम से यह नहीं कहता, कि मैं तुम्हारे लिये पिता से बिनती करूंगा।"));
hinsuperobject.biblemap.put("b43_16_27", new hinsuperobject(43,16,27,"क्योंकि पिता तो आप ही तुम से प्रीति रखता है, इसलिये कि तुम ने मुझ से प्रीति रखी है, और यह भी प्रतीति की है, कि मैं पिता कि ओर से निकल आया।"));
hinsuperobject.biblemap.put("b43_16_28", new hinsuperobject(43,16,28,"मैं पिता से निकलकर जगत में आया हूं, फिर जगत को छोड़कर पिता के पास जाता हूं।"));
hinsuperobject.biblemap.put("b43_16_29", new hinsuperobject(43,16,29,"उसके चेलों ने कहा, देख, अब तो तू खोलकर कहता है, और कोई दृष्टान्त नहीं कहता।"));
hinsuperobject.biblemap.put("b43_16_30", new hinsuperobject(43,16,30,"अब हम जान गए, कि तू सब कुछ जानता है, और तुझे प्रयोजन नहीं, कि कोई तुझ से पूछे, इस से हम प्रतीति करते हैं, कि तू परमेश्वर से निकला है।"));
hinsuperobject.biblemap.put("b43_16_31", new hinsuperobject(43,16,31,"यह सुन यीशु ने उन से कहा, क्या तुम अब प्रतीति करते हो?"));
hinsuperobject.biblemap.put("b43_16_32", new hinsuperobject(43,16,32,"देखो, वह घड़ी आती है वरन आ पहुंची कि तुम सब तित्तर बित्तर होकर अपना अपना मार्ग लोगे, और मुझे अकेला छोड़ दोगे, तौभी मैं अकेला नहीं क्योंकि पिता मेरे साथ है।"));
hinsuperobject.biblemap.put("b43_16_33", new hinsuperobject(43,16,33,"मैं ने ये बातें तुम से इसलिये कही हैं, कि तुम्हें मुझ में शान्ति मिले; संसार में तुम्हें क्लेश होता है, परन्तु ढाढ़स बांधो, मैं ने संसार को जीन लिया है।।"));
hinsuperobject.biblemap.put("b43_17_1", new hinsuperobject(43,17,1,"यीशु ने ये बातें कहीं और अपनी आंखे आकाश की ओर उठाकर कहा, हे पिता, वह घड़ी आ पहुंची, अपने पुत्रा की महिमा कर, कि पुत्रा भी तेरी महिमा करे।"));
hinsuperobject.biblemap.put("b43_17_2", new hinsuperobject(43,17,2,"क्योंकि तू ने उस का सब प्राणियों पर अधिकार दिया, कि जिन्हें तू ने उस को दिया है, उन सब को वह अनन्त जीवन दे।"));
hinsuperobject.biblemap.put("b43_17_3", new hinsuperobject(43,17,3,"और अनन्त जीवन यह है, कि वे तुझ अद्वैत सच्चे परमेश्वर को और यीशु मसीह को, जिसे तू ने भेजा है, जाने।"));
hinsuperobject.biblemap.put("b43_17_4", new hinsuperobject(43,17,4,"जो काम तू ने मुझे करने को दिया था, उसे पूरा करके मैं ने पृथ्वी पर तेरी महिमा की है।"));
hinsuperobject.biblemap.put("b43_17_5", new hinsuperobject(43,17,5,"और अब, हे पिता, तू अपने साथ मेरी महिमा उस महिमा से कर जो जगत के होने से पहिले, मेरी तेरे साथ थी।"));
hinsuperobject.biblemap.put("b43_17_6", new hinsuperobject(43,17,6,"मैं ने तेरा नाम उन मनुष्यों पर प्रगट किया जिन्हें तू ने जगत में से मुझे दिया: वे तेरे थे और तू ने उन्हें मुझे दिया और उन्हों ने तेरे वचन को मान लिया है।"));
hinsuperobject.biblemap.put("b43_17_7", new hinsuperobject(43,17,7,"अब वे जान गए हैं, कि जो कुछ तू ने मुझे दिया है, सब तेरी ओर से है।"));
hinsuperobject.biblemap.put("b43_17_8", new hinsuperobject(43,17,8,"क्योंकि जो बातें तू ने मुझे पहुंचा दीं, मैं ने उन्हें उनको पहुंचा दिया और उन्हों ने उन को ग्रहण किया: और सच सच जान लिया है, कि मैं तेरी ओर से निकला हूं, और प्रतीति कर ली है कि तू ही ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_17_9", new hinsuperobject(43,17,9,"मैं उन के लिये बिनती करता हूं, संसार के लिये बिनती नहीं करता हूं परन्तु उन्हीं के लिये जिन्हें तू ने मुझे दिया है, क्योंकि वे तेरे हैं।"));
hinsuperobject.biblemap.put("b43_17_10", new hinsuperobject(43,17,10,"और जो कुछ मेरा है वह सब तेरा है; और जो तेरा है वह मेरा है वह सब तेरा है; और जो तेरा है, वह मेरा है; और इन से मेरी महिमा प्रगट हुई है।"));
hinsuperobject.biblemap.put("b43_17_11", new hinsuperobject(43,17,11,"मैं आगे को जगत में न रहूंगा, परन्तु ये जगत में रहेंगे, और मैं तेरे पास आता हूं; हे पवित्रा पिता, अपने उस नाम से जो तू ने मुझे दिया है, उन की रक्षा कर, कि वे हमारी नाई एक हों।"));
hinsuperobject.biblemap.put("b43_17_12", new hinsuperobject(43,17,12,"जब मै। उन के साथ था, तो मैं ने तेरे उस नाम से, जो तू ने मुझे दिया है, उन की रक्षा की, मैं ने उन की चौकसी की और विनाश के पुत्रा को छोड़ उन में से काई नाश न हुआ, इसलिये कि पवित्रा शास्त्रा की बात पूरी हो।"));
hinsuperobject.biblemap.put("b43_17_13", new hinsuperobject(43,17,13,"परन्तु अब मैं तेरे पास आता हूं, और ये बातें जगत में कहता हूं, कि वे मेरा आनन्द अपने में पूरा पाएं।"));
hinsuperobject.biblemap.put("b43_17_14", new hinsuperobject(43,17,14,"मैं ने तेरा वचन उन्हें पहुंचा दिया है, और संसार ने उन से बैर किया, क्योंकि जैसा मैं संसार का नहीं, वैसे ही वे भी संसार के नहीं।"));
hinsuperobject.biblemap.put("b43_17_15", new hinsuperobject(43,17,15,"मैं यह बिनती नहीं करता, कि तू उन्हें जगत से उठा ले, परन्तु यह कि तू उन्हें उस दुष्ट से बचाए रख।"));
hinsuperobject.biblemap.put("b43_17_16", new hinsuperobject(43,17,16,"जैसे मैं संसार का नहीं, वैसे ही वे भी संसार के नहीं।"));
hinsuperobject.biblemap.put("b43_17_17", new hinsuperobject(43,17,17,"सत्य के द्वारा उन्हें पवित्रा कर: तेरा वचन सत्य है।"));
hinsuperobject.biblemap.put("b43_17_18", new hinsuperobject(43,17,18,"जैसे तू ने जगत में मुझे भेजा, वैसे ही मैं ने भी उन्हें जगत में भेजा।"));
hinsuperobject.biblemap.put("b43_17_19", new hinsuperobject(43,17,19,"और उन के लिये मैं अपने आप को पवित्रा करता हूं ताकि वे भी सत्य के द्वारा पवित्रा किए जाएं।"));
hinsuperobject.biblemap.put("b43_17_20", new hinsuperobject(43,17,20,"मैं केवल इन्हीं के लिये बिनती नहीं करता, परन्तु उन के लिये भी जो इन के वचन के द्वारा मुझ पर विश्वास करेंगे, कि वे सब एक हों।"));
hinsuperobject.biblemap.put("b43_17_21", new hinsuperobject(43,17,21,"जैसा तू हे पिता मुझ में हैं, और मैं तुझ में हूं, वैसे ही वे भी हम में हों, इसलिये कि जगत प्रतीति करे, कि तू ही ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_17_22", new hinsuperobject(43,17,22,"और वह महिमा जो तू ने मुझे दी, मैं ने उन्हें दी है कि वे वैसे ही एक हों जैसे की हम एक हैं।"));
hinsuperobject.biblemap.put("b43_17_23", new hinsuperobject(43,17,23,"मैं उन में और तू मुझ में कि वे सिद्ध होकर एक हो जाएं, और जगत जाने कि तू ही ने मुझे भेजा, और जैसा तू ने मुझ से प्रेम रखा, वैसा ही उन से प्रेम रखा।"));
hinsuperobject.biblemap.put("b43_17_24", new hinsuperobject(43,17,24,"हे पिता, मै। चाहता हूं कि जिन्हें तू ने मुझे दिया है, जहां मैं हूं, वहां वे भी मेरे साथ हों कि वे मेरी उस महिमा को देखें जो तू ने मुझे दी है, क्योंकि तू ने जगत की उत्पत्ति से पहिले मुझ से प्रेम रखा।"));
hinsuperobject.biblemap.put("b43_17_25", new hinsuperobject(43,17,25,"हे धार्मिक पिता, संसार ने मुझे नहीं जाना, परन्तु मैं ने तुझे जाना और इन्हों ने भी जाना कि तू ही ने मुझे भेजा।"));
hinsuperobject.biblemap.put("b43_17_26", new hinsuperobject(43,17,26,"और मैं ने तेरा नाम उन को बताया और बताता रहूंगा कि जो प्रेम तुझ को मुझ से था, वह उन में रहे और मैं उन में रहूं।।"));
hinsuperobject.biblemap.put("b43_18_1", new hinsuperobject(43,18,1,"यीशु ये बातें कहकर अपने चेलों के साथ किद्रोन के नाले के पार गया, वहां एक बारी थी, जिस में वह और उसके चेले गए।"));
hinsuperobject.biblemap.put("b43_18_2", new hinsuperobject(43,18,2,"और उसका पकड़वानेवाला यहूदा भी वह जगह जानता था, क्योंकि यीशु अपने चेलों के साथ वहां जाया करता था।"));
hinsuperobject.biblemap.put("b43_18_3", new hinsuperobject(43,18,3,"तब यहूदा पलटन को और महायाजकों और फरीसियों की ओर से प्यादों को लेकर दीपकों और मशालों और हथियारों को लिए हुए वहां आया।"));
hinsuperobject.biblemap.put("b43_18_4", new hinsuperobject(43,18,4,"तब यीशु उन सब बातों को जो उस पर आनेवाली थीं, जानकर निकला, और उन से कहने लगा, किसे ढूंढ़ते हो?"));
hinsuperobject.biblemap.put("b43_18_5", new hinsuperobject(43,18,5,"उन्हों ने उसको उत्तर दिया, यीशु नासरी को: यीशु ने उन से कहा, मैं ही हूं: और उसका पकड़वानेवाला यहूदा भी उन के साथ खड़ा था।"));
hinsuperobject.biblemap.put("b43_18_6", new hinsuperobject(43,18,6,"उसके यह कहते ही, कि मैं हूं, वे पीछे हटकर भूमि पर गिर पड़े।"));
hinsuperobject.biblemap.put("b43_18_7", new hinsuperobject(43,18,7,"तब उस ने फिर उन से पूछा, तुम किस को ढूंढ़ते हो।"));
hinsuperobject.biblemap.put("b43_18_8", new hinsuperobject(43,18,8,"वे बोले, यीशु नासरी को। यीशु ने उत्तर दिया, मैं तो तुम से कह चुका हूं कि मैं ही हूं, यदि मुझे ढूंढ़ते हो तो इन्हें जाने दो।"));
hinsuperobject.biblemap.put("b43_18_9", new hinsuperobject(43,18,9,"यइ इसलिये हुआ, कि वह वचन पूरा हो, जो उस ने कहा था कि जिन्हें तू ने मुझे दिया, उन में से मैं ने एक को भी न खोया।"));
hinsuperobject.biblemap.put("b43_18_10", new hinsuperobject(43,18,10,"शमौन पतरस ने तलवार, जो उसके पास थी, खींची और महायाजक के दास पर चलाकर, उसका दहिना कान उड़ा दिया, उस दास का नाम मलखुस था।"));
hinsuperobject.biblemap.put("b43_18_11", new hinsuperobject(43,18,11,"तब यीशु ने पतरस से कहा, अपनी तलवार काठी में रख: जो कटोरा पिता ने मुझे दिया है क्या मैं उसे न पीऊं?"));
hinsuperobject.biblemap.put("b43_18_12", new hinsuperobject(43,18,12,"तब सिपाहियों और उन के सूबेदार और यहूदियों के प्यादों ने यीशु को पकड़कर बान्ध लिया।"));
hinsuperobject.biblemap.put("b43_18_13", new hinsuperobject(43,18,13,"और पहिले उसे हन्ना के पास ले गए क्योंकि वह उस वर्ष के महायाजक काइफा का ससुर था।"));
hinsuperobject.biblemap.put("b43_18_14", new hinsuperobject(43,18,14,"यह वही काइफा था, जिस ने यहूदियों को सलाह दी थी कि हमारे लोगों के लिये एक पुरूष का मरना अच्छा है।"));
hinsuperobject.biblemap.put("b43_18_15", new hinsuperobject(43,18,15,"शमौन पतरस और एक और चेला भी यीशु के पीछे हो लिए: यह चेला महायाजक का जाना पहचाना था और यीशु के साथ महायाजक के आंगत में गया।"));
hinsuperobject.biblemap.put("b43_18_16", new hinsuperobject(43,18,16,"परन्तु पतरस द्वार पर खड़ा रहा, तब वह दूसरा चेला जो महायाजक का जाना पहचाना था, बाहर निकला, और द्वारपालिन से कहकर, पतरस को भीतर ले आया।"));
hinsuperobject.biblemap.put("b43_18_17", new hinsuperobject(43,18,17,"उस दासी ने जो द्वारपालिन थी, पतरस से कहा, क्या तू भी इस मनुष्य के चेलों में से है? उस ने कहा, मैं नहीं हूं।"));
hinsuperobject.biblemap.put("b43_18_18", new hinsuperobject(43,18,18,"दास और प्यादे जाड़े के कारण कोएले धधकाकर खड़े ताप रहे थे और पतरस भी उन के साथ खड़ा ताप रहा था।।"));
hinsuperobject.biblemap.put("b43_18_19", new hinsuperobject(43,18,19,"तक महायाजक ने यीशु से उसके चेलों के विषय में और उसके उपदेश के विषय में पूछा।"));
hinsuperobject.biblemap.put("b43_18_20", new hinsuperobject(43,18,20,"यीशु ने उस को उत्तर दिया, कि मैं ने जागत से खोलकर बातें की; मैं ने सभाओं और आराधनालय में जहां सब यहूदी इकट्ठे हुआ करते हैं सदा उपदेश किया और गुप्त में कुछ भी नहीं कहा।"));
hinsuperobject.biblemap.put("b43_18_21", new hinsuperobject(43,18,21,"तू मुझ से क्यों पूछता है? सुननेवालों से पूछ: कि मैं ने उन से क्या कहा? देख वे जानते हैं; कि मैं ने क्या क्या कहा?"));
hinsuperobject.biblemap.put("b43_18_22", new hinsuperobject(43,18,22,"तब उस ने यह कहा, तो प्यादों में से एक ने जो पास खड़ा था, यीशु को थप्पड़ मारकर कहा, क्या तू महायाजक को इस प्रकार उत्तर देता है।"));
hinsuperobject.biblemap.put("b43_18_23", new hinsuperobject(43,18,23,"यीशु ने उसे उत्तर दिया, यदि मैं ने बुरा कहा, तो उस बुराई पर गवाही दे; परन्तु यदि भला कहा, तो मुझे क्यों मारता है?"));
hinsuperobject.biblemap.put("b43_18_24", new hinsuperobject(43,18,24,"हन्ना ने उसे बन्धे हुए काइफा महायाजक के पास भेज दिया।।"));
hinsuperobject.biblemap.put("b43_18_25", new hinsuperobject(43,18,25,"शमौन पतरस खड़ा हुआ ताप रहा था। तब उन्हों ने उस से कहा; क्या तू भी उसके चेलों में से है? उस न इन्कार करके कहा, मैं नहीं हूं।"));
hinsuperobject.biblemap.put("b43_18_26", new hinsuperobject(43,18,26,"महायाजक के दासों में से एक जो उसके कुटुम्ब में से था, जिसका कान पतरस ने काट डाला था, बोला, क्या मैं ने तुझे उसके साथ बारी में न देखा था?"));
hinsuperobject.biblemap.put("b43_18_27", new hinsuperobject(43,18,27,"पतरस फिर इन्कार कर गया और तुरन्त मुर्ग ने बांग दी।।"));
hinsuperobject.biblemap.put("b43_18_28", new hinsuperobject(43,18,28,"और वे यीशु को काइफा के पास से किले को ले गए और भोर का समय था, परन्तु वे आप किले के भीतर न गए ताकि अशुद्ध न हों परन्तु फसह खा सकें।"));
hinsuperobject.biblemap.put("b43_18_29", new hinsuperobject(43,18,29,"तब पीलातुस उन के पास बाहर निकल आया और कहा, तुम इस मनुष्य पर किस बात की नालिया करते हो?"));
hinsuperobject.biblemap.put("b43_18_30", new hinsuperobject(43,18,30,"उन्हों ने उस को उत्तर दिया, कि यदि वह कुकर्मी न होता तो हम उसे तेरे हाथ न सौंपते।"));
hinsuperobject.biblemap.put("b43_18_31", new hinsuperobject(43,18,31,"पीलातुस ने उन से कहा, तुम ही इसे ले जाकर अपनी व्यवस्था के अनुसार उसका न्याय करो: यहूदयों ने उस से कहा, हमें अधिकार नहीं कि किसी का प्राण लें।"));
hinsuperobject.biblemap.put("b43_18_32", new hinsuperobject(43,18,32,"यह इसलिये हुआ, कि यीशु की वह बात पूरी हो जो उस ने यह पता देते हुए कही थी, कि उसका मरना कैसा होगा।।"));
hinsuperobject.biblemap.put("b43_18_33", new hinsuperobject(43,18,33,"तब पीलातुस फिर किले के भीतर गया और यीशु को बुलाकर, उस से पूछा, क्या तू यहूदियों का राजा है?"));
hinsuperobject.biblemap.put("b43_18_34", new hinsuperobject(43,18,34,"यीशु ने उत्तर दिया, क्या तू यह बात अपनी ओर से कहता है या औरों ने मेरे विषय में तुझ से कही?"));
hinsuperobject.biblemap.put("b43_18_35", new hinsuperobject(43,18,35,"पीलातुस ने उत्तर दिया, क्या मैं यहूदी हूं? तेरी ही जाति और महायाजकों ने तुझे मेरे हाथ सौंपा, तू ने क्या किया है?"));
hinsuperobject.biblemap.put("b43_18_36", new hinsuperobject(43,18,36,"यीशु ने उत्तर दिया, कि मेरा राज्य इस जगत का नहीं, यदि मेरा राज्य इस जगत का होता, तो मेरे सेवक लड़ते, कि मैं यहूदियों के हाथ सौंपा न जाता: परन्तु अब मेरा राज्य यहां का नहीं।"));
hinsuperobject.biblemap.put("b43_18_37", new hinsuperobject(43,18,37,"पीलातुस ने उस से कहा, कि तू कहता है, क्योंकि मैं राजा हूं; मैं ने इसलिये जन्म लिया, और इसलिये जगत में आया हूं कि सत्य पर गवाही दूं जो कोई सत्य का है, वह मेरा शब्द सुनता है।"));
hinsuperobject.biblemap.put("b43_18_38", new hinsuperobject(43,18,38,"पीलातुस ने उस से कहा, सत्य क्या है? और यह कहकर वह फिर यहूदियों के पास निकल गया और उन से कहा, मैं तो उस में कुछ दोष नहीं पाता।"));
hinsuperobject.biblemap.put("b43_18_39", new hinsuperobject(43,18,39,"पर तुम्हारी यह रीति है कि मैं फसह में तुम्हारे लिये एक व्यक्ति को छोड़ दूं सो क्या तुम चाहते हो, कि मैं तुम्हारे लिये यहूदियों के राजा को छोड़ दूं?"));
hinsuperobject.biblemap.put("b43_18_40", new hinsuperobject(43,18,40,"तब उन्हों ने फिर चिल्लाकर कहा, इसे नहीं परन्तु हमारे लिये बरअब्बा को छोड़ दे; और बरअब्बा डाकू था।।"));
hinsuperobject.biblemap.put("b43_19_1", new hinsuperobject(43,19,1,"इस पर पीलातुस ने यीशु को लेकर कोड़े लगवाए।"));
hinsuperobject.biblemap.put("b43_19_2", new hinsuperobject(43,19,2,"और सिपाहियों ने कांटों का मुकुट गूंथकर उसके सिर पर रखा, और उसे बैंजनी वस्त्रा पहिनाया।"));
hinsuperobject.biblemap.put("b43_19_3", new hinsuperobject(43,19,3,"और उसके पास आ आकर कहने लगे, हे यहूदियों के राजा, प्रणाम! और उसे थप्पड़ मारे।"));
hinsuperobject.biblemap.put("b43_19_4", new hinsuperobject(43,19,4,"तक पीलातुस ने फिर बाहर निकलकर लोगों से कहा, देखो, मैं उसे तुम्हारे पास फिर बाहर लाता हूं; ताकि तुम जानो कि मैं कुछ भी दोष नहीं पाता।"));
hinsuperobject.biblemap.put("b43_19_5", new hinsuperobject(43,19,5,"तक यीशु कांटों का मुकुट और बैंजनी वस्त्रा पहिने हुए बाहर निकला और पीलातुस ने उन से कहा, देखो, यह पुरूष।"));
hinsuperobject.biblemap.put("b43_19_6", new hinsuperobject(43,19,6,"जब महायाजकों और प्यादों ने उसे देखा, तो चिल्लाकर कहा, कि उसे क्रूस पर चढ़ा, क्रूस पर : पीलातुस ने उन से कहा, तुम ही उसे लेकर क्रूस पर चढ़ाओ; क्योंकि मैं उस में दोष नहीं पाता।"));
hinsuperobject.biblemap.put("b43_19_7", new hinsuperobject(43,19,7,"यहूदियों ने उस को उत्तर दिया, कि हमारी भी व्यवस्था है और उस व्यवस्था के अनुसार वह मारे जाने के योग्य है क्योंकि उस ने अपने आप को परमेश्वर का पुत्रा बनाया।"));
hinsuperobject.biblemap.put("b43_19_8", new hinsuperobject(43,19,8,"जब पीलातुस ने यह बात सुनी तो और भी डर गया।"));
hinsuperobject.biblemap.put("b43_19_9", new hinsuperobject(43,19,9,"और फिर किले के भीतर गया और यीशु से कहा, तू कहां का है? परन्तु यीशु ने उसे कुछ भी उत्तर न दिया।"));
hinsuperobject.biblemap.put("b43_19_10", new hinsuperobject(43,19,10,"पीलातुस ने उस से कहा, मुझ से क्यों नहीं बोलता? क्या तू नहीं जानता कि तुझे छोड़ देने का अधिकार मुझे है और तुझे क्रूस पर चढ़ाने का भी मुझे अधिकार है।"));
hinsuperobject.biblemap.put("b43_19_11", new hinsuperobject(43,19,11,"यीशु ने उत्तर दिया, कि यदि तुझे ऊपर से न दिया जाता, तो तेरा मुझ पर कुछ अधिकार न होता; इसलिये जिस ने मुझे तेरे हाध पकड़वाया है, उसका पाप अधिक है।"));
hinsuperobject.biblemap.put("b43_19_12", new hinsuperobject(43,19,12,"इस से पीलातुस ने उसे छोड़ देना चाहा, परन्तु यहूदयों ने चिल्ला चिल्लाकर कहा, यदि तू इस को छोड़ देगा तो तेरी भक्ति कैसर की ओर नहीं; जो कोई अपने आप को राजा बनाता है वह कैसर का साम्हना करता है।"));
hinsuperobject.biblemap.put("b43_19_13", new hinsuperobject(43,19,13,"ये बातें सुनकर पीलातुस यीशु को बाहर लाया और उस जगह एक चबूतरा था जो इब्रानी में गब्बता कहलाता है, और न्याय आसन पर बैठा।"));
hinsuperobject.biblemap.put("b43_19_14", new hinsuperobject(43,19,14,"यह फसह की तैयारी का दिन था और छठे घंटे के लगभग था: तब उस ने यहूदियों से कहा, देखो, यही है, तुम्हारा राजा!"));
hinsuperobject.biblemap.put("b43_19_15", new hinsuperobject(43,19,15,"परन्तु वे चिल्लाए कि ले जा! ले जा! उसे क्रूस पर चढ़ा: पीलातुस ने उन से कहा, क्या मैं तुम्हारे राजा को क्रूस पर चढ़ाऊं? महायाजकों ने उत्तर दिया, कि कैसर को छोड़ हमारा और कोई राजा नहीं।"));
hinsuperobject.biblemap.put("b43_19_16", new hinsuperobject(43,19,16,"तब उस ने उसे उन के हाथ सौंप दिया ताकि वह क्रूस पर चढ़ाया जाए।।"));
hinsuperobject.biblemap.put("b43_19_17", new hinsuperobject(43,19,17,"तब वे यीशु को ले गए। और वह अपना क्रूस उठाए हुए उस स्थान तक बाहर गया, जो खोपड़ी का स्थान कहलाता है और इब्रानी में गुलगुता।"));
hinsuperobject.biblemap.put("b43_19_18", new hinsuperobject(43,19,18,"वहां उन्हों ने उसे और उसके साथ और दो मनुष्यों को क्रूस पर चढ़ाया, एक को इधर और एक को उधर, और बीच में यीशु को।"));
hinsuperobject.biblemap.put("b43_19_19", new hinsuperobject(43,19,19,"और पीलातुस ने एक दोष- पत्रा लिखकर क्रूस पर लगा दिया और उस में यह लिखा हुआ था, यीशु नासरी यहूदियों का राजा।"));
hinsuperobject.biblemap.put("b43_19_20", new hinsuperobject(43,19,20,"यह दोष- पत्रा बहुत यहूदियों ने पढ़ा क्योंकि वह स्थान जहां यीशु क्रूस पर चढ़ाया गया था नगर के पास था और पत्रा इब्रानी और लतीनी और यूनानी में लिखा हुआ था।"));
hinsuperobject.biblemap.put("b43_19_21", new hinsuperobject(43,19,21,"तब यहूदियों के महायाजकों ने पीलातुस से कहा, यहूदियों का राजा मत लिख परन्तु यह कि \"उस ने कहा, मैं यहूदियों का राजा हूं\"।"));
hinsuperobject.biblemap.put("b43_19_22", new hinsuperobject(43,19,22,"पीलातुस ने उत्तर दिया, कि मैं ने जो लिख दिया, वह लिख दिया।।"));
hinsuperobject.biblemap.put("b43_19_23", new hinsuperobject(43,19,23,"जब सिपाही यीशु को क्रूस पर चढ़ा चुके, तो उसके कपड़े लेकर चार भाग किए, हर सिपाही के लिये एक भाग और कुरता भी लिया, परन्तु कुरता बिन सीअन ऊपर से नीचे तक बुना हुआ था: इसलिये उन्हों ने आपस में कहा, हम इस को न फाडें, परन्तु इस पर चिट्ठी डालें कि वह किस का होगा।"));
hinsuperobject.biblemap.put("b43_19_24", new hinsuperobject(43,19,24,"यह इसलिये हुआ, कि पवित्रा शास्त्रा की बात पूरी हो कि उन्हों ने मेरे कपड़े आपस में बांट लिये और मेरे वस्त्रा पर चिट्ठी डाली: सो सिपाहियों ने ऐसा ही किया।"));
hinsuperobject.biblemap.put("b43_19_25", new hinsuperobject(43,19,25,"परन्तु यीशु के क्रूस के पास उस की माता और उस की माता की बहिन मरियम, क्लोपास की पत्नी और मरियम मगदलीनी खड़ी थी।"));
hinsuperobject.biblemap.put("b43_19_26", new hinsuperobject(43,19,26,"यीशु ने अपनी माता से कहा; हे नारी, देख, यह तेरा पुत्रा है।"));
hinsuperobject.biblemap.put("b43_19_27", new hinsuperobject(43,19,27,"तब उस चेले से कहा, यह तेरी माता है, और उसी समय से वह चेला, उसे अपने घर ले गया।।"));
hinsuperobject.biblemap.put("b43_19_28", new hinsuperobject(43,19,28,"इस के बाद यीशु ने यह जानकर कि अब सब कुछ हो चुका; इसलिये कि पवित्रा शास्त्रा की बात पूरी हो कहा, मैं प्यासा हूं।"));
hinsuperobject.biblemap.put("b43_19_29", new hinsuperobject(43,19,29,"वहां एक सिरके से भरा हुआ बर्तन धरा था, सो उन्हों ने सिरके के भिगोए हुए इस्पंज को जूफे पर रखकर उसके मुंह से लगाया।"));
hinsuperobject.biblemap.put("b43_19_30", new hinsuperobject(43,19,30,"जब यीशु ने वह सिरका लिया, तो कहा पूरा हुआ और सिर झुकाकर प्राण त्याग दिए।।"));
hinsuperobject.biblemap.put("b43_19_31", new hinsuperobject(43,19,31,"और इसलिये कि वह तैयारी का दिन था, यहूदियों ने पीलातुस से बिनती की कि उन की टांगे तोड़ दी जाएं और वे उतारे जाएं ताकि सब्त के दिन वे क्रूसों पर न रहें, क्योंकि वह सब्त का दिन बड़ा दिन था।"));
hinsuperobject.biblemap.put("b43_19_32", new hinsuperobject(43,19,32,"सो सिपाहियों ने आकर पहिले की टांगें तोड़ीं तब दूसरे की भी, जो उसके साथ क्रूसों पर चढ़ाए गए थे।"));
hinsuperobject.biblemap.put("b43_19_33", new hinsuperobject(43,19,33,"परन्तु जब यीशु के पास आकर देखा कि वह मर चुका है, तो उस की टांगें न तोड़ीं।"));
hinsuperobject.biblemap.put("b43_19_34", new hinsuperobject(43,19,34,"परन्तु सिपाहियों में से एक ने बरछे से उसका पंजर बेधा और उस में से तुरन्त लोहू और पानी निकला।"));
hinsuperobject.biblemap.put("b43_19_35", new hinsuperobject(43,19,35,"जिस ने यह देखा, उसी ने गवाही दी है, और उस की गवाही सच्ची है; और वह जानता है, कि सच कहता है कि तुम भी विश्वास करो।"));
hinsuperobject.biblemap.put("b43_19_36", new hinsuperobject(43,19,36,"ये बातें इसलिये हुईं कि पवित्रा शास्त्रा की यह बात पूरी हो कि उस की कोई हड्डी तोड़ी न जाएगी।"));
hinsuperobject.biblemap.put("b43_19_37", new hinsuperobject(43,19,37,"फिर एक और स्थान पर यह लिखा है, कि जिसे उन्हों ने बेधा है, उस पर दृष्टि करेंगे।।"));
hinsuperobject.biblemap.put("b43_19_38", new hinsuperobject(43,19,38,"इन बातों के बाद अरमतियाह के यूसुफ ने, जो यीशु का चेला था, (परन्तु यहूदियों के डर से इस बात को छिपाए रखता था), पीलातुस से बिनती की, कि मैं यीशु की लोथ को ले जाऊं, और पीलातुस ने उस की बिनती सुनी, और वह आकर उस की लोथ ले गया।"));
hinsuperobject.biblemap.put("b43_19_39", new hinsuperobject(43,19,39,"निकुदेमुस भी जो पहिले यीशु के पास रात को गया था पचास सेर के लगभग मिला हुआ गन्धरस और एलवा ले आया।"));
hinsuperobject.biblemap.put("b43_19_40", new hinsuperobject(43,19,40,"तब उन्हों ने यीशु की लोथ को लिया और यहूदियों के गाड़ने की रीति के अनुसार उसे सुगन्ध द्रव्य के साथ कफन में लपेटा।"));
hinsuperobject.biblemap.put("b43_19_41", new hinsuperobject(43,19,41,"उस स्थान पर जहां यीशु क्रूस पर चढ़ाया गया था, एक बारी थी; और उस बारी में एक नई कब्र थी; जिस में कभी कोई न रखा गया था।"));
hinsuperobject.biblemap.put("b43_19_42", new hinsuperobject(43,19,42,"सो यहूदियों की तैयारी के दिन के कारण, उन्हों ने यीशु को उसी में रखा, क्योंकि वह कब्र निकट थी।।"));
hinsuperobject.biblemap.put("b43_20_1", new hinsuperobject(43,20,1,"सप्ताह के पहिले दिन मरियम मगदलीनी भोर को अंधेरा रहते ही कब्र पर आई, और पत्थर को कब्र से हटा हुआ देखा।"));
hinsuperobject.biblemap.put("b43_20_2", new hinsuperobject(43,20,2,"तब वह दौड़ी और शमौन पतरस और उस दूसरे चेले के पास जिस से यीशु प्रेम रखता था आकर कहा, वे प्रभु को कब्र में से निकाल ले गए हैं; और हम नहीं जानतीं, कि उसे कहां रख दिया है।"));
hinsuperobject.biblemap.put("b43_20_3", new hinsuperobject(43,20,3,"तब पतरस और वह दूसरा चेला निकलकर कब्र की ओर चले।"));
hinsuperobject.biblemap.put("b43_20_4", new hinsuperobject(43,20,4,"और दोनों साथ साथ दौड़ रहे थे, परन्तु दूसरा चेला पतरस से आगे बढ़कर कब्र पर पहिले पहुंचा।"));
hinsuperobject.biblemap.put("b43_20_5", new hinsuperobject(43,20,5,"और झुककर कपड़े पड़े देखे: तौभी वह भीतर न गया।"));
hinsuperobject.biblemap.put("b43_20_6", new hinsuperobject(43,20,6,"तब शमौन पतरस उसके पीछे पीछे पहुंचा और कब्र के भीतर गया और कपड़े पड़े देखे।"));
hinsuperobject.biblemap.put("b43_20_7", new hinsuperobject(43,20,7,"और वह अंगोछा जो उसके सिर से बन्धा हुआ था, कपड़ों के साथ पड़ा हुआ नहीं परन्तु अलग एक जगह लपेटा हुआ देखा।"));
hinsuperobject.biblemap.put("b43_20_8", new hinsuperobject(43,20,8,"तब दूसरा चेला भी जो कब्र पर पहिले पहुंचा था, भीतर गया और देखकर विश्वास किया।"));
hinsuperobject.biblemap.put("b43_20_9", new hinsuperobject(43,20,9,"वे तो अब तक पवित्रा शास्त्रा की वह बात न समझते थे, कि उसे मरे हुओं में से जी उठना होगा।"));
hinsuperobject.biblemap.put("b43_20_10", new hinsuperobject(43,20,10,"तब ये चेले अपने घर लौट गए।"));
hinsuperobject.biblemap.put("b43_20_11", new hinsuperobject(43,20,11,"परन्तु मरियम रोती हुई कब्र के पास ही बाहर खड़ी रही और रोते रोते कब्र की ओर झुककर,"));
hinsuperobject.biblemap.put("b43_20_12", new hinsuperobject(43,20,12,"दो स्वर्गदूतों को उज्जवत कपड़े पहिने हुए एक को सिरहाने और दूसरे को पैताने बैठे देखा, जहां यीशु की लोथ पड़ी थी।"));
hinsuperobject.biblemap.put("b43_20_13", new hinsuperobject(43,20,13,"उन्हों ने उस से कहा, हे नारी, तू क्यों रोती है? उस ने उन से कहा, वे मरे प्रभु को उठा ले गए और मैं नहीं जानती कि उसे कहां रखा है।"));
hinsuperobject.biblemap.put("b43_20_14", new hinsuperobject(43,20,14,"यह कहकर वह पीछे फिरी और यीशु को खड़े देखा और न पहचाना कि यह यीशु है।"));
hinsuperobject.biblemap.put("b43_20_15", new hinsuperobject(43,20,15,"यीशु ने उस से कहा, हे नारी तू क्यों रोती है? जिस को ढूंढ़ती है? उस ने माली समझकर उस से कहा, हे महाराज, यदि तू ने उसे उठा लिया है तो मुझ से कह कि उसे कहां रखा है और मैं उसे ले जाऊंगी।"));
hinsuperobject.biblemap.put("b43_20_16", new hinsuperobject(43,20,16,"यीशु ने उस से कहा, मरियम! उस ने पीछे फिरकर उस से इब्रानी में कहा, रब्बूनी अर्थात् हे गुरू।"));
hinsuperobject.biblemap.put("b43_20_17", new hinsuperobject(43,20,17,"यीशु ने उस से कहा, मुझे मत छू क्योंकि मैं अब तक पिता के पास ऊपर नहीं गया, परन्तु मेरे भाइयों के पास जाकर उन से कह दे, कि मैं अपने पिता, और तुम्हारे परमेश्वर के पास ऊपर जाता हूं।"));
hinsuperobject.biblemap.put("b43_20_18", new hinsuperobject(43,20,18,"मरियम मगदलीनी ने जाकर चेलों को बताया, कि मैं ने प्रभु को देखा और उस ने मुझ से बातें कहीं।।"));
hinsuperobject.biblemap.put("b43_20_19", new hinsuperobject(43,20,19,"उसी दिन जो सप्ताह का पहिला दिन था, सन्ध्या के समय जब वहां के द्वार जहां चेले थे, यहूदियों के डर के मारे बन्द थे, तब यीशु आया और बीच में खड़ा होकर उन से कहा, तुम्हें शान्ति मिले।"));
hinsuperobject.biblemap.put("b43_20_20", new hinsuperobject(43,20,20,"और यह कहकर उस ने अपना हाथ और अपना पंजर उन को दिखाए: तब चेले प्रभु को देखकर आनन्दित हुए।"));
hinsuperobject.biblemap.put("b43_20_21", new hinsuperobject(43,20,21,"यीशु ने फिर उन से कहा, तुम्हें शान्ति मिले; जैसे पिता ने मुझे भेजा है, वैसे ही मैं भी तुम्हें भेजता हूं।"));
hinsuperobject.biblemap.put("b43_20_22", new hinsuperobject(43,20,22,"यह कहकर उस ने उन पर फूंका और उन से कहा, पवित्रा आत्मा लो।"));
hinsuperobject.biblemap.put("b43_20_23", new hinsuperobject(43,20,23,"जिन के पाप तुम क्षमा करो वे उन के लिये क्षमा किए गए हैं जिन के तुम रखो, वे रखे गए हैं।।"));
hinsuperobject.biblemap.put("b43_20_24", new hinsuperobject(43,20,24,"परन्तु बारहों में से एक व्यक्ति अर्थात् थोमा जो दिदुमुस कहलाता है, जब यीशु आया तो उन के साथ न था।"));
hinsuperobject.biblemap.put("b43_20_25", new hinsuperobject(43,20,25,"जब और चेले उस से कहने लगे कि हम ने प्रभु को देखा है: तब उस ने उन से कहा, जब तक मैं उस के हाथों में कीलों के छेद न देख लूं, और कीलों के छेदों में अपनी उंगली न डाल लूं, तब तक मैं प्रतीति नहीं करूंगा।।"));
hinsuperobject.biblemap.put("b43_20_26", new hinsuperobject(43,20,26,"आठ दिन के बाद उस के चेले फिर घर के भीतर थे, और थोमा उन के साथ था, और द्वार बन्द थे, तब यीशु ने आकर और बीच में खड़ा होकर कहा, तुम्हें शान्ति मिले।"));
hinsuperobject.biblemap.put("b43_20_27", new hinsuperobject(43,20,27,"तब उस ने थोमा से कहा, अपनी उंगली यहां लाकर मेरे पंजर में डाल और अविश्वासी नहीं परन्तु विश्वासी हो।"));
hinsuperobject.biblemap.put("b43_20_28", new hinsuperobject(43,20,28,"यह सुन थोमा ने उत्तर दिया, हे मेरे प्रभु, हे मेरे परमेश्वर!"));
hinsuperobject.biblemap.put("b43_20_29", new hinsuperobject(43,20,29,"यीशु ने उस से कहा, तू ने तो मुझे देखकर विश्वास किया है, धन्य हैं वे जिन्हों ने बिना देखे विश्वास किया।।"));
hinsuperobject.biblemap.put("b43_20_30", new hinsuperobject(43,20,30,"यीशु ने और भी बहुत चिन्ह चेलों के साम्हने दिखाए, जो इस पुस्तक में लिखे नहीं गए।"));
hinsuperobject.biblemap.put("b43_20_31", new hinsuperobject(43,20,31,"परन्तु ये इसलिये लिखे गए हैं, कि तुम विश्वास करो, कि यीशु ही परमेश्वर का पुत्रा मसीह है: और विश्वास करके उसके नाम से जीवन पाओ।।"));
hinsuperobject.biblemap.put("b43_21_1", new hinsuperobject(43,21,1,"इन बातों के बाद यीशु ने अपने आप को तिबिरियास झील के किनारे चेलों पर प्रगट किया और इस रीति से प्रगट किया।"));
hinsuperobject.biblemap.put("b43_21_2", new hinsuperobject(43,21,2,"शमौन पतरस और थोमा जो दिदुमुस कहलाता है, और गलील के काना नगर का नतनएल और जब्दी के पुत्रा, और उसके चेलों में से दो और जन इकट्ठे थे।"));
hinsuperobject.biblemap.put("b43_21_3", new hinsuperobject(43,21,3,"शमौन पतरस ने उन से कहा, मैं मछली पकड़ने जाता हूं: उन्हों ने उस से कहा, हम भी तेरे साथ चलते हैं: सो वे निकलकर नाव पर चढ़े, परन्तु उस रात कुछ न पकड़ा।"));
hinsuperobject.biblemap.put("b43_21_4", new hinsuperobject(43,21,4,"भोर होते ही यीशु किनारे पर खड़ा हुआ; तौभी चेलों ने न पहचाना कि यह यीशु है।"));
hinsuperobject.biblemap.put("b43_21_5", new hinsuperobject(43,21,5,"तब यीशु ने उन से कहा, हे बालको, क्या तुम्हारे पास कुछ खाने को है? उन्हों ने उत्तर दिया कि नहीं।"));
hinsuperobject.biblemap.put("b43_21_6", new hinsuperobject(43,21,6,"उस ने उन से कहा, नाव की दहिनी ओर जाल डालो, तो पाओगे, तब उन्हों ने जाल डाला, और अब मछलियों की बहुतायत के कारण उसे खींच न सके।"));
hinsuperobject.biblemap.put("b43_21_7", new hinsuperobject(43,21,7,"इसलिये उस चेले ने जिस से यीशु प्रेम रखता था पतरस से कहा, यह तो प्रभु है: शमौन पतरस ने यह सुनकर कि प्रभु है, कमर में अंगरखा कस लिया, क्योंकि वह नंगा था, और झील में कूद पड़ा।"));
hinsuperobject.biblemap.put("b43_21_8", new hinsuperobject(43,21,8,"परन्तु और चेले डोंगी पर मछलियों से भरा हुआ जाल खींचते हुए आए, क्योंकि वे किनारे से अधिक दूर नहीं, कोई दो सौ हाथ पर थे।"));
hinsuperobject.biblemap.put("b43_21_9", new hinsuperobject(43,21,9,"जब किनारे पर उतरे, तो उन्हों ने कोएले की आग, और उस र मछली रखी हुई, और रोटी देखी।"));
hinsuperobject.biblemap.put("b43_21_10", new hinsuperobject(43,21,10,"यीशु ने उन से कहा, जो मछलियां तुम ने अभी पकड़ी हैं, उन में से कुछ लाओ।"));
hinsuperobject.biblemap.put("b43_21_11", new hinsuperobject(43,21,11,"शमौन पतरस ने डोंगी पर चढ़कर एक सौ तिर्मन बड़ी मछलियों से भरा हुआ जाल किनारे पर खींचा, और इतनी मछलियां होने पर भी जान न फटा।"));
hinsuperobject.biblemap.put("b43_21_12", new hinsuperobject(43,21,12,"यीशु ने उन से कहा, कि आओ, भोजन करो और चेलों में से किसी को हियाव न हुआ, कि उस से पूछे, कि तू कौन है?"));
hinsuperobject.biblemap.put("b43_21_13", new hinsuperobject(43,21,13,"यीशु आया, और रोटी लेकर उन्हें दी, और वैसे ही मछली भी।"));
hinsuperobject.biblemap.put("b43_21_14", new hinsuperobject(43,21,14,"यह तीसरी बार है, कि यीशु ने मरे हुओं में से जी उठने के बाद चेलों को दर्शन दिए।।"));
hinsuperobject.biblemap.put("b43_21_15", new hinsuperobject(43,21,15,"भोजन करने के बाद यीशु ने शमौन पतरस से कहा, हे शमौन, यूहन्ना के पुत्रा, क्या तू इन से बढ़कर मुझ से प्रेम रखता है? उस ने उस से कहा, हां प्रभु तू तो जानता है, कि मैं तुझ से प्रीति रखता हूं: उस ने उस से कहा, मेरे मेमनों को चरा।"));
hinsuperobject.biblemap.put("b43_21_16", new hinsuperobject(43,21,16,"उस ने फिर दूसरी बार उस से कहा, हे शमौन यूहन्ना के पुत्रा, क्या तू मुझ से प्रेम रखता है? उस ने उन से कहा, हां, प्रभु तू जानता है, कि मैं तुझ से प्रीति रखता हूं: उस ने उस से कहा, मेरी भेड़ों की रखवाली कर।"));
hinsuperobject.biblemap.put("b43_21_17", new hinsuperobject(43,21,17,"उस ने तीसरी बार उस से कहा, हे शमौन, यूहन्ना के पुत्रा, क्या तू मुझ से प्रीति रखता है? पतरस उदास हुआ, कि उस ने उसे तीसरी बार ऐसा कहा; कि क्या तू मुझ से प्रीति रखता है? और उस से कहा, हे प्रभु, तू तो सब कुछ जानता है: तू यह जानता है कि मैं तुझ से प्रीति रखता हूं: यीशु ने उस से कहा, मेरी भेड़ों को चरा।"));
hinsuperobject.biblemap.put("b43_21_18", new hinsuperobject(43,21,18,"मैं तुझ से सच सच कहता हूं, जब तू जवान था, तो अपनी कमर बान्धकर जहां चाहता था, वहां फिरता था; परन्तु जब तू बूढ़ा होगा, तो अपने हाथ लम्बे करेगा, और दूसरा तेरी कमर बान्धकर जहां तू न चाहेगा वहां तुझे ले जाएगा।"));
hinsuperobject.biblemap.put("b43_21_19", new hinsuperobject(43,21,19,"उस ने इन बातों से पता दिया कि पतरस कैसी मृत्यु से परमेश्वर की महिमा करेगा; और यह कहकर, उस से कहा, मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b43_21_20", new hinsuperobject(43,21,20,"पतरस ने फिरकर उस चेले को पीछे आते देखा, जिस से यीशु प्रेम रखता था, और जिस ने भोजन के समय उस की छाती की और झुककर पूछा हे प्रभु, तेरा पकड़वानेवाला कौन है?"));
hinsuperobject.biblemap.put("b43_21_21", new hinsuperobject(43,21,21,"उसे देखकर पतरस ने यीशु से कहा, हे प्रभु, इस का क्या हाल होगा?"));
hinsuperobject.biblemap.put("b43_21_22", new hinsuperobject(43,21,22,"यीशु ने उस से कहा, यदि मैं चाहूं कि वह मेरे आने तक ठहरा रहे, तो तुझे क्या? तू मेरे पीछे हो ले।"));
hinsuperobject.biblemap.put("b43_21_23", new hinsuperobject(43,21,23,"इसलिये भाइयों में यह बात फैल गई, कि वह चेला न मरेगा; तौभी यीशु ने उस से यह नहीं कहा, कि यह न मरेगा, परन्तु यह कि यदि मैं चाहूं कि यह मेरे आने तक ठहरा रहे, तो तुझे इस से क्या?"));
hinsuperobject.biblemap.put("b43_21_24", new hinsuperobject(43,21,24,"यह वही चेला है, जो इन बातों की गवाही देता है और जिस ने इन बातों को लिखा है और हम जानते हैं, कि उस की गवाही सच्ची है।"));
