package hin.bible;

public class hinbible47 {

public hinbible47() {

putmapbible47();

}

private void putmapbible47() {

hinsuperobject.biblemap.put("b47_1_1", new hinsuperobject(47,1,1,"पौलुस की ओर से जो परमेश्वर की इच्छा से मसीह यीशु का प्रेरित है, और भाई तीमुथियुस की ओर से परमेश्वर की उस कलीसिया के नाम जो कुरिन्थुस में है; और सारे अखमा के सब पवित्रा लागों के नाम।।"));
hinsuperobject.biblemap.put("b47_1_2", new hinsuperobject(47,1,2,"हमारे पिता परमेश्वर और प्रभु यीशु मसीह की ओर से अनुग्रह और शान्ति मिलती रहे।।"));
hinsuperobject.biblemap.put("b47_1_3", new hinsuperobject(47,1,3,"हमारे प्रभु यीशु मसीह के परमेश्वर, और पिता का धन्यवाद हो, जो दया का पिता, और सब प्रकार की शान्ति का परमेश्वर है।"));
hinsuperobject.biblemap.put("b47_1_4", new hinsuperobject(47,1,4,"वह हमारे सब क्लेशों में शान्ति देता है; ताकि हम उस शान्ति के कारण जो परमेश्वर हमें देता है, उन्हें भी शान्ति दे सकें, जो किसी प्रकार के क्लेश में हों।"));
hinsuperobject.biblemap.put("b47_1_5", new hinsuperobject(47,1,5,"क्योंकि जैसे मसीह के दुख हम को अधिक होते हैं, वैसे ही हमारी शान्ति भी मसीह के द्वारा अधिक हाती है।"));
hinsuperobject.biblemap.put("b47_1_6", new hinsuperobject(47,1,6,"यदि हम क्लेश पाते हैं, तो यह तुम्हारी शान्ति और उद्धार के लिये है और यदि शान्ति पाते हैं, तो यह तुम्हारी शान्ति के लिये है; जिस के प्रभाव से तुम धीरज के साथ उन क्लेशों को सह लेते हो, जिन्हें हम भी सहते हैं।"));
hinsuperobject.biblemap.put("b47_1_7", new hinsuperobject(47,1,7,"और हमारी आशा तुम्हारे विषय में दृढ़ है; क्योंकि हम जानते हैं, कि तुम जैसे दुखों के वैसे ही शान्ति के भी सहभागी हो।"));
hinsuperobject.biblemap.put("b47_1_8", new hinsuperobject(47,1,8,"हे भाइयों, हम नहीं चाहते कि तुम हमारे उस क्लेश से अनजान रहो, जो आसिया में हम पर पड़ा, कि ऐसे भारी बोझ से दब गए थे, जो हमारी समर्थ से बाहर था, यहां तक कि हम जीवन से भी हाथ धो बैठे थे।"));
hinsuperobject.biblemap.put("b47_1_9", new hinsuperobject(47,1,9,"बरन हम ने अपने मन में समझ लिया था, कि हम पर मृत्यु की आज्ञा हो चुकी है कि हम अपना भरोसा न रखें, बरन परमेश्वर का जो मरे हुओं को जिलाता है।"));
hinsuperobject.biblemap.put("b47_1_10", new hinsuperobject(47,1,10,"उसी ने हमें ऐसी बड़ी मृत्यु से बचाया, और बचाएगा; और उस से हमारी यह आशा है, कि वह आगे को भी बचाता रहेगा।"));
hinsuperobject.biblemap.put("b47_1_11", new hinsuperobject(47,1,11,"और तुम भी मिलकर प्रार्थना के द्वारा हमारी सहायता करोगे, कि जो बरदान बहुतों के द्वारा हमें मिला, उसके कारण बहुत लोग हमारी ओर से धन्यवाद करें।।"));
hinsuperobject.biblemap.put("b47_1_12", new hinsuperobject(47,1,12,"क्योंकि हम अपने विवेक की इस गवाही पर घमण्ड करते हैं, कि जगत में और विशेष करके तुम्हारे बीच हमारा चरित्रा परमेश्वर के योग्य ऐसी पवित्राता और सच्चाई सहित था, जो शारीरिक ज्ञान से नहीं, परन्तु परमेश्वर के अनुग्रह के साथ था।"));
hinsuperobject.biblemap.put("b47_1_13", new hinsuperobject(47,1,13,"हम तुम्हें और कुछ नहीं लिखते, केवल वह जो तुम पढ़ते या मानते भी हो, और मुझे आशा है, कि अन्त तक भी मानते रहोगे।"));
hinsuperobject.biblemap.put("b47_1_14", new hinsuperobject(47,1,14,"जैसा तुम में से कितनों ने मान लिया है, कि हम तुम्हारे घमण्ड का कारण है; वैसे तुम भी प्रभु यीशु के दिन हमारे लिये घमण्ड का कारण ठहरोगे।।"));
hinsuperobject.biblemap.put("b47_1_15", new hinsuperobject(47,1,15,"और इस भरोसे से मैं चाहता था कि पहिले तुम्हारे पास आऊं; कि तुम्हें एक और दान मिले।"));
hinsuperobject.biblemap.put("b47_1_16", new hinsuperobject(47,1,16,"और तुम्हारे पास से होकर मकिदुनिया को जाऊं, और तुम मुछे यहूदिया की ओर कुद दूर तक पहुंचाओ।"));
hinsuperobject.biblemap.put("b47_1_17", new hinsuperobject(47,1,17,"इसलिये मैं ने जो यह इच्छा की थी तो क्या मैं ने चंचलता दिखाई? या जो करना चाहता हूं क्या शरीर के अनुसार करना चाहता हूं, कि मैं बात में हां, हां भी करूं;"));
hinsuperobject.biblemap.put("b47_1_18", new hinsuperobject(47,1,18,"और नहीं नहीं भी करूं? परमेश्वर सच्चा गवाह है, कि हमारे उस वचन में जो तुम से कहा हां और नहीं दानों पाई नहीं जातीं।"));
hinsuperobject.biblemap.put("b47_1_19", new hinsuperobject(47,1,19,"क्योंकि परमेश्वर का पुत्रा यीशु मसीह जिसका हमारे द्वारा अर्थात् मेरे और सिलवानुस और तीमुथियुस के द्वारा तुम्हारे बीच मे प्रचार हुआ; उस में हां और नहीं दोनों न थी; परन्तु, उस में हां ही हां हुई।"));
hinsuperobject.biblemap.put("b47_1_20", new hinsuperobject(47,1,20,"क्यांकि परमेश्वर की जितनी प्रतिज्ञाएं हैं, वे सब उसी में हां के साथ हैं: इसलिये उसके द्वारा आमीन भी हुई, कि हमारे द्वारा परमेश्वर की महिमा हो।"));
hinsuperobject.biblemap.put("b47_1_21", new hinsuperobject(47,1,21,"और जो हमें तुम्हारे साथ मसीह में दृढ़ करता है, और जिस ने हमें अभिषेक किया वही परमेश्वर है।"));
hinsuperobject.biblemap.put("b47_1_22", new hinsuperobject(47,1,22,"जिस ने हम पर छाप भी कर दी है और बयान में आत्मा को हमारे मनों में दिया।।"));
hinsuperobject.biblemap.put("b47_1_23", new hinsuperobject(47,1,23,"मैं परमेश्वर को गवाह करता हूं, कि मै अब तक कुरिन्थुस में इसलिये नहीं आया, कि मुझे तुम पर तरस आता था।"));
hinsuperobject.biblemap.put("b47_1_24", new hinsuperobject(47,1,24,"यह नहीं, कि हम विश्वास के विषय में तुम पर प्रभुता जताना चाहते हैं; परन्तु तुम्हारे आनन्द में सहायक हैं क्योंकि तुम विश्वास ही से स्थिर रहते हो।"));
hinsuperobject.biblemap.put("b47_2_1", new hinsuperobject(47,2,1,"मैंने अपने मन में यही ठान लिया था कि फिर तुम्हारे पास उदास होकर न आऊं।"));
hinsuperobject.biblemap.put("b47_2_2", new hinsuperobject(47,2,2,"क्योंकि यदि मैं तुम्हें उदास करूं, तो मुझे आनन्द देनेवाला कौन होगा, केवल वही जिस को मैं ने उदास किया?"));
hinsuperobject.biblemap.put("b47_2_3", new hinsuperobject(47,2,3,"और मैं ने यही बात तुम्हें इसलिये लिखी, कि कहीं ऐसा न हो, कि मेरे आने पर जिन से आनन्द मिलना चाहिए, मैं उन से उदास होऊं; क्योंकि मुझे तुम सब पर इस बात का भरोसा है, कि जो मेरा आनन्द है, वही तुम सब का भी है।"));
hinsuperobject.biblemap.put("b47_2_4", new hinsuperobject(47,2,4,"बड़े क्लेश, और मन के कष्ट से, मैं ने बहुत से आंसु बहा बहाकर तुम्हें लिखा, इसलिये नहीं, कि तुम उदास हो, परन्तु इसलिये कि तुम उस बड़े प्रेम को जान लो, जो मुझे तुम से है।।"));
hinsuperobject.biblemap.put("b47_2_5", new hinsuperobject(47,2,5,"और यदि किसी ने उदास किया है, तो मुझे ही नहीं बरन (कि उसके साथ बहुत कड़ाई न करूं) कुछ कुछ तुम सब को भी उदास किया है।"));
hinsuperobject.biblemap.put("b47_2_6", new hinsuperobject(47,2,6,"ऐसे जन के लिये यह दण्ड जो भाइयों में से बहुतों ने दिया, बहुत है।"));
hinsuperobject.biblemap.put("b47_2_7", new hinsuperobject(47,2,7,"इसलिये इस से यह भला है कि उसका अपराध क्षमा करो; और शान्ति दो, न हो कि ऐसा मनुष्य उदासी में डूब जाए।"));
hinsuperobject.biblemap.put("b47_2_8", new hinsuperobject(47,2,8,"इस कारण मैं तुम से बिनती करता हूं, कि उस को अपने प्रेम का प्रमाण दो।"));
hinsuperobject.biblemap.put("b47_2_9", new hinsuperobject(47,2,9,"क्योंकि मैं ने इसलिये भी लिखा था, कि तुम्हें परख लूं, कि सब बातों के मानने के लिये तैयार हो, कि नहीं।"));
hinsuperobject.biblemap.put("b47_2_10", new hinsuperobject(47,2,10,"जिस का तुम कुछ क्षमा करते हो उस मैं भी क्षमा करता हूं, क्योंकि मैं ने भी जो कुछ क्षमा किया है, यदि किया हो, तो तुम्हारे कारण मसीह की जगह में होकर क्षमा किया है।"));
hinsuperobject.biblemap.put("b47_2_11", new hinsuperobject(47,2,11,"कि शैतान का हम पर दांव न चले, क्योंकि हम उस की युक्तियों से अनजान नहीं।"));
hinsuperobject.biblemap.put("b47_2_12", new hinsuperobject(47,2,12,"और जब मैं मसीह का सुसमाचार, सुनाने को त्रोआस में आया, और प्रभु ने मेरे लिये एक द्वार खोल दिया।"));
hinsuperobject.biblemap.put("b47_2_13", new hinsuperobject(47,2,13,"तो मेरे मन में चैन ने मिला, इसलिये कि मैं ने अपने भाई तितुस को नहीं पाया; सो उन से विदा होकर मैं मकिदुनिया को चला गया।"));
hinsuperobject.biblemap.put("b47_2_14", new hinsuperobject(47,2,14,"परन्तु परमेश्वर का धन्यवाद हो, जो मसीह में सदा हम को जय के उत्सव में लिये फिरता है, और अपने ज्ञान का सुगन्ध हमारे द्वारा हर जगह फैलाता है।"));
hinsuperobject.biblemap.put("b47_2_15", new hinsuperobject(47,2,15,"क्योंकि हम परमेश्वर के निकट उद्धार पानेवालों, और नाश होनेवालों, दोनो के लिये मसीह के सुगन्ध हैं।"));
hinsuperobject.biblemap.put("b47_2_16", new hinsuperobject(47,2,16,"कितनो के लिये तो मरने के निमित्त मृन्यु की गन्ध, और कितनो के लिये जीवन के निमित्त जीवन की सुगन्ध, और इन बातों के योग्य कौन है?"));
hinsuperobject.biblemap.put("b47_2_17", new hinsuperobject(47,2,17,"क्योंकि हम उन बहुतों के समान नहीं, जो परमेश्वर के वचन में मिलावट करते हैं; परन्तु मन की सच्चाई से, और परमेश्वर की ओर से परमेश्वर को उपस्थित जानकर मसीह में बोलते हैं।।"));
hinsuperobject.biblemap.put("b47_3_1", new hinsuperobject(47,3,1,"क्या हम फिर अपनी बड़ाई करने लगे? या हमें कितनों कि नाई सिफारिश की पत्रियां तुम्हारे पास लानी या तुम से लेनी हैं?"));
hinsuperobject.biblemap.put("b47_3_2", new hinsuperobject(47,3,2,"हमारी पत्री तुम ही हो, जो हमारे हृदयों पर लिखी हुई है, और उसे सब मनुष्य पहिचानते और पढ़ते है।"));
hinsuperobject.biblemap.put("b47_3_3", new hinsuperobject(47,3,3,"यह प्रगट है, कि तुम मसीह की पत्री हो, जिस को हम ने सेवकों की नाई लिखा; और जो सियाही से नहीं, परन्तु जीवते परमेश्वर के आत्मा से पत्थर की पटियों पर नहीं, परन्तु हृदय की मांस रूपी पटियों पर लिखी है।"));
hinsuperobject.biblemap.put("b47_3_4", new hinsuperobject(47,3,4,"हम मसीह के द्वारा परमेश्वर पर ऐसा ही भरोसा रखते हैं।"));
hinsuperobject.biblemap.put("b47_3_5", new hinsuperobject(47,3,5,"यह नहीं, कि हम अपने आप से इस योग्य हैं, कि अपनी ओर से किसी बात का विचार कर सकें; पर हमारी योग्यता परमेश्वर की ओर से है।"));
hinsuperobject.biblemap.put("b47_3_6", new hinsuperobject(47,3,6,"जिस ने हमें नई वाचा के सेवक होने के योग्य भी किया, शब्द के सेवक नहीं बरन आत्मा के; क्योंकि शब्द मारता है, पर आत्मा जिलाता है।"));
hinsuperobject.biblemap.put("b47_3_7", new hinsuperobject(47,3,7,"और यहद मृत्यु की यह वाचा जिस के अक्षर पत्थरों पर खोद गए थे, यहां तक तेजोमय हुई, कि मूसा के मुंह पर के तेज के कराण जो घटता भी जाता था, इस्त्राएल उसके मुंह पर दृष्टि नहीं कर सकते थे।"));
hinsuperobject.biblemap.put("b47_3_8", new hinsuperobject(47,3,8,"तो आत्मा की वाचा और भी तेजोमय क्यों न होगी?"));
hinsuperobject.biblemap.put("b47_3_9", new hinsuperobject(47,3,9,"क्योंकि जब दोषी ठहरानेवाली वाचा तेजोमय थी, तो धर्मी ठहरानेवाली वाचा और भी तेजोमय क्यों न होगी?"));
hinsuperobject.biblemap.put("b47_3_10", new hinsuperobject(47,3,10,"और जो तेजोमय था, वह भी उस तेज के कारण जो उस से बढ़कर तेजामय था, कुछ तेजोमय न ठहरा।"));
hinsuperobject.biblemap.put("b47_3_11", new hinsuperobject(47,3,11,"क्योंकि जब वह जो घटता जाता था तेजोमय था, तो वह जो स्थिर रहेगा, और भी तेजोमय क्यों न होगा?"));
hinsuperobject.biblemap.put("b47_3_12", new hinsuperobject(47,3,12,"सो ऐसी आशा रखकर हम हियाव के साथ बोलते हैं।"));
hinsuperobject.biblemap.put("b47_3_13", new hinsuperobject(47,3,13,"और मूसर की नाईं नहीं, जिस ने अपने मुंह पर परदा डाला था ताकि इस्त्राएली उस घटनेवाली वस्तु के अन्त को न देखें।"));
hinsuperobject.biblemap.put("b47_3_14", new hinsuperobject(47,3,14,"परन्तु वे मतिमन्द हो गए, क्योंकि आज तक पुराने नियम के पढ़ते समय उन के हृदयों पर वही परदा पड़ा रहता है; पर वह मसीह में उठ जाता है।"));
hinsuperobject.biblemap.put("b47_3_15", new hinsuperobject(47,3,15,"और आज तक जब कभी मूसा की पुस्तक पढ़ी जाती है, तो उन के हृदय पर परदा पड़ा रहता है।"));
hinsuperobject.biblemap.put("b47_3_16", new hinsuperobject(47,3,16,"परन्तु जब कभी उन का हृदय प्रभु की ओर फिरेगा, तब वह परदा उठ जाएगा।"));
hinsuperobject.biblemap.put("b47_3_17", new hinsuperobject(47,3,17,"प्रभु तो आत्मा है: और जहां कहीं प्रभु का आत्मा है वहां स्वतंत्राता है।"));
hinsuperobject.biblemap.put("b47_3_18", new hinsuperobject(47,3,18,"परन्तु जब हम सब के उघाड़े चेहरे से प्रभु का प्रताप इस प्रकार प्रगट होता है, जिस प्रकार दर्पण में, तो प्रभु के द्वारा जो आत्मा है, हम उसी तेजस्वी रूप में अंश अंश कर के बदलते जाते हैं।।"));
hinsuperobject.biblemap.put("b47_4_1", new hinsuperobject(47,4,1,"इसलिये जब हम पर ऐसी दया हुई, कि हमें यह सेवा मिली, तो हम हियाव नहीं छोड़ते।"));
hinsuperobject.biblemap.put("b47_4_2", new hinsuperobject(47,4,2,"परन्तु हम ने लज्जा के गुप्त कामों को त्याग दिया, और न चतुराई से चलते, और न परमेश्वर के वचन में मिलावट करते हैं, परन्तु सत्य को प्रगट करके, परमेश्वर के साम्हने हर एक मनुष्य के विवेक में अपनी भलाई बैठाते हैं।"));
hinsuperobject.biblemap.put("b47_4_3", new hinsuperobject(47,4,3,"परन्तु यदि हमारे सुसमाचार पर परदा पड़ा है, तो यह नाश होनेवालों ही के लिये पड़ा है।"));
hinsuperobject.biblemap.put("b47_4_4", new hinsuperobject(47,4,4,"और उन अविश्वासियों के लिये, जिन की बुद्धि को इस संसार के ईश्वर ने अन्धी कर दी है, ताकि मसीह जो परमेश्वर का प्रतिरूप है, उसके तेजोमय सुसमाचार का प्रकाश उन पर न चमके।"));
hinsuperobject.biblemap.put("b47_4_5", new hinsuperobject(47,4,5,"क्योंकि हम अपने को नहीं, परन्तु मसीह यीशु को प्रचार करते हैं, कि वह प्रभु है; और उसके विषय में यह कहते हैं, कि हम यीशु के कारण तुम्हारे सेवक हैं।"));
hinsuperobject.biblemap.put("b47_4_6", new hinsuperobject(47,4,6,"इसलिये कि परमेश्वर ही है, जिस ने कहा, कि अन्धकार में से ज्योति चमके; और वही हमारे हृदयों में चमका, कि परमेश्वर की महिमा की पहिचान की ज्योति यीशु मसीह के चेहरे से प्रकाशमान हो।।"));
hinsuperobject.biblemap.put("b47_4_7", new hinsuperobject(47,4,7,"परन्तु महारे पास यह धन मिट्ठी के बरतनों में रखा है, कि यह असीम सामर्थ हमारी ओर से नहीं, बरन परमेश्वर ही की ओर से ठहरे।"));
hinsuperobject.biblemap.put("b47_4_8", new hinsuperobject(47,4,8,"हम चारों ओर से क्लेश तो भोगते हैं, पर संकट में नहीं पड़ते; निरूपाय तो हैं, पर निराश नहीं होते।"));
hinsuperobject.biblemap.put("b47_4_9", new hinsuperobject(47,4,9,"सताए तो जाते हैं; पर त्यागे नहीं जाते; गिराए तो जाते हैं, पर नाश नहीं होते।"));
hinsuperobject.biblemap.put("b47_4_10", new hinsuperobject(47,4,10,"हम यीशु की मृत्यु को अपनी देह में हर समय लिये फिरते हैं; कि यीशु का जीवन भी हमारी देह में प्रगट हो।"));
hinsuperobject.biblemap.put("b47_4_11", new hinsuperobject(47,4,11,"क्योंकि हम जीते जी सर्वदा यीशु के कारण मृत्यु के हाथ में सौंपे जाते हैं कि यीशु का जीवन भी हमारे मरनहार शरीर में प्रगट हो।"));
hinsuperobject.biblemap.put("b47_4_12", new hinsuperobject(47,4,12,"सो मृत्यु तो हम पर प्रभाव डालती है और जीवन तुम पर।"));
hinsuperobject.biblemap.put("b47_4_13", new hinsuperobject(47,4,13,"और इसलिये कि हम में वही विश्वास की आत्मा है, (जिस के विषय मे लिखा है, कि मैं ने विश्वास किया, इसलिये मैं बोला) सो हम भी विश्वास करते हैं, इसी लिये बोलते हैं।"));
hinsuperobject.biblemap.put("b47_4_14", new hinsuperobject(47,4,14,"क्योंकि हम जातने हैं, जिस ने प्रभु यीशु को जिलाया, वही हमें भी यीशु में भागी जानकर जिलाएगा, और तुम्हारे साथ अपने साम्हने उपस्थित करेगा।"));
hinsuperobject.biblemap.put("b47_4_15", new hinsuperobject(47,4,15,"क्योंकि सब वस्तुएं तुम्हारे लिये हैं, ताकि अनुग्रह बहुतों के द्वारा अधिक होकर परमेश्वर की महिमा के लिये धन्यवाद भी बढ़ाए।।"));
hinsuperobject.biblemap.put("b47_4_16", new hinsuperobject(47,4,16,"इसलिये हम हियाव नहीं छोड़ते; यद्यपि हमारा बाहरी मनुष्यत्व नाश भी होता जाता है, तौभी हमारा भीतरी मनुष्यत्व दिन प्रतिदिन नया होता जाता है।"));
hinsuperobject.biblemap.put("b47_4_17", new hinsuperobject(47,4,17,"क्योंकि हमारा पल भर का हल्का सा क्लेश हमारे लिये बहुत ही महत्वपूर्ण और अनन्त जीवन महिमा उत्पन्न करता जाता है।"));
hinsuperobject.biblemap.put("b47_4_18", new hinsuperobject(47,4,18,"और हम तो देखी हुई वस्तुओं को नहीं परन्तु अनदेखी वस्तुओं को देखते रहते हैं, क्योंकि देखी हुई वस्तुएं थोड़े ही दिन की हैं, परन्तु अनदेखी वस्तुएं सदा बनी रहती हैं।"));
hinsuperobject.biblemap.put("b47_5_1", new hinsuperobject(47,5,1,"क्योंकि हम जानते हैं, कि जब हमारा पृथ्वी पर का डेरा सरीखा घर गिराया जाएगा तो हमें परमेश्वर की ओर से स्वर्ग पर एक ऐसा भवन मिलेगा, जो हाथों से बना हुआ घर नहीं परन्तु चिरस्थाई है।"));
hinsuperobject.biblemap.put("b47_5_2", new hinsuperobject(47,5,2,"इस में तो हम कहरते, और बड़ी लालसा रखते हैं; कि अपने स्वर्गीय घर को पहिन लें।"));
hinsuperobject.biblemap.put("b47_5_3", new hinsuperobject(47,5,3,"कि इस के पहिनने से हम नंगे न पाए जाएं।"));
hinsuperobject.biblemap.put("b47_5_4", new hinsuperobject(47,5,4,"और हम इस डेरे में रहते हुए बोझ से दबे कहरते रहते हैं; क्योंकि हम उतारना नहीं, बरन और पहिनना चाहते हैं, ताकि वह जो मरनहार है जीवन में डूब जाए।"));
hinsuperobject.biblemap.put("b47_5_5", new hinsuperobject(47,5,5,"और जिस ने हमें इसी बात के लिये तैयार किया है वह परमेश्वर है, जिस ने हमें बयाने में आत्मा भी दिया है।"));
hinsuperobject.biblemap.put("b47_5_6", new hinsuperobject(47,5,6,"सो हम सदा ढाढ़स बान्धे रहते हैं और यह जानते हैं; कि जब तक हम देह में रहते हैं, तब तक प्रभु से अलग हैं।"));
hinsuperobject.biblemap.put("b47_5_7", new hinsuperobject(47,5,7,"क्योंकि हम रूप को देखकर नहीं, पर विश्वास से चलते हैं।"));
hinsuperobject.biblemap.put("b47_5_8", new hinsuperobject(47,5,8,"इसलिये हम ढाढ़स बान्धे रहते हैं, और देह से अलग होकर प्रभु के साथ रहना और भी उत्तम समझते हैं।"));
hinsuperobject.biblemap.put("b47_5_9", new hinsuperobject(47,5,9,"इस कारण हमारे मन की उमंग यह है, कि चाहे साथ रहें, चाहे अलग रहें पर हम उसे भाते रहें।"));
hinsuperobject.biblemap.put("b47_5_10", new hinsuperobject(47,5,10,"क्योंकि अवश्य है, कि हम सब का हाल मसीह के न्याय आसन के साम्हने खुल जाए, कि हर एक व्यक्ति अपने अपने भले बुरे कामों का बदला जो उस ने देह के द्वारा किए हों पाए।।"));
hinsuperobject.biblemap.put("b47_5_11", new hinsuperobject(47,5,11,"सो प्रभु का भय मानकर हम लोगों को समझाते हैं और परमेश्वर पर हमारा हाल प्रगट है; और मेरी आशा यह है, कि तुम्हारे विवेक पर भी प्रगट हुआ होगा।"));
hinsuperobject.biblemap.put("b47_5_12", new hinsuperobject(47,5,12,"हम फिर भी अपनी बड़ाई तुम्हारे साम्हने नहीं करते बरन हम अपने विषय में तुम्हें घमण्ड करने का अवसर देते हैं, कि तुम उन्हें उत्तर दे सको, जो मन पर नहीं, बरन दिखवटी बातों पर घमण्ड करते हैं।"));
hinsuperobject.biblemap.put("b47_5_13", new hinsuperobject(47,5,13,"यदि हम बेसुध हैं, तो परमेश्वर के लिये; और यदि चैतन्य हैं, तो तुम्हारे लिये हैं।"));
hinsuperobject.biblemap.put("b47_5_14", new hinsuperobject(47,5,14,"क्योंकि मसीह का प्रेम हमें विवश कर देता है; इसलिये कि हम यह समझते हैं, कि जब एक सब के लिये मरा तो सब मर गए।"));
hinsuperobject.biblemap.put("b47_5_15", new hinsuperobject(47,5,15,"और वह इस निमित्त सब के लिये मरा, कि जो जीवित हैं, वे आगे को अपने लिये न जीएं परन्तु उसके लिये जो उन के लिये मरा और फिर जी उठा।"));
hinsuperobject.biblemap.put("b47_5_16", new hinsuperobject(47,5,16,"सो अब से हम किसी को शरीर के अनुसार न समझेंगे, और यदि हम ने मसीह को भी शरीर के अनुसार जाना था, तौभी अब से उस को ऐसा नहीं जानेंगे।"));
hinsuperobject.biblemap.put("b47_5_17", new hinsuperobject(47,5,17,"सो यदि कोई मसीह में है तो वह नई सृष्टि है: पुरानी बातें बीत गई हैं; देखो, वे सब नई हो गईं।"));
hinsuperobject.biblemap.put("b47_5_18", new hinsuperobject(47,5,18,"और सब बातें परमेश्वर की ओर से हैं, जिस ने मसीह के द्वारा अपने साथ हमारा मेल मिलाप की सेवा हमें सौंप दी है।"));
hinsuperobject.biblemap.put("b47_5_19", new hinsuperobject(47,5,19,"अर्थात् परमेश्वर ने मसीह में होकर अपने साथ संसार का मेल मिलाप कर लिया, और उन के अपराधों का दोष उन पर नहीं लगाया और उस ने मेल मिलाप का वचन हमें सौंप दिया है।।"));
hinsuperobject.biblemap.put("b47_5_20", new hinsuperobject(47,5,20,"सो हम मसीह के राजदूत हैं; मानो परमेश्वर हमारे द्वारा समझाता है: हम मसीह की ओर से निवेदन करते हैं, कि परमेश्वर के साथ मेल मिलाप कर लो।"));
hinsuperobject.biblemap.put("b47_5_21", new hinsuperobject(47,5,21,"जो पाप से अज्ञात था, उसी को उस ने हमारे लिये पाप ठहराया, कि हम उस में होकर परमेश्वर की धार्मिकता बन जाएं।।"));
hinsuperobject.biblemap.put("b47_6_1", new hinsuperobject(47,6,1,"और हम जो उसके सहकर्मी हैं यह भी समझाते हैं, कि परमेश्वर का अनुगंह जो तुम पर हुआ, व्यर्थ न रहने दो।"));
hinsuperobject.biblemap.put("b47_6_2", new hinsuperobject(47,6,2,"क्योंकि वह तो कहता है, कि अपनी प्रसन्नता के समय मैं ने तेरी सहायता की: देखो, अभी उद्धार का दिन है।"));
hinsuperobject.biblemap.put("b47_6_3", new hinsuperobject(47,6,3,"हम किसी बात में ठोकर खाने का कोई भी अवसर नहीं देते, कि हमारी सेवा पर कोई दोष न आए।"));
hinsuperobject.biblemap.put("b47_6_4", new hinsuperobject(47,6,4,"परन्तु हर बात में परमेश्वर के सेवकों की नाई अपने सद्गुणों को प्रगट करते हैं, बड़े धैर्य से, क्लेशों से, दरिद्रता से, संकटो से।"));
hinsuperobject.biblemap.put("b47_6_5", new hinsuperobject(47,6,5,"कोड़े खाने से, कैद होने से, हुल्लड़ों से, परिश्रम से, जागते रहने से, उपवास करने से।"));
hinsuperobject.biblemap.put("b47_6_6", new hinsuperobject(47,6,6,"पवित्राता से, ज्ञान से, धीरज से, कृपालुता से, पवित्रा आत्मा से।"));
hinsuperobject.biblemap.put("b47_6_7", new hinsuperobject(47,6,7,"सच्चे प्रेम से, सत्य के वचन से, परमेश्वर की सामर्थ से; धार्मिकता के हथियारों से जो दहिने, बाएं हैं।"));
hinsuperobject.biblemap.put("b47_6_8", new hinsuperobject(47,6,8,"आदर और निरादर से, दुरनाम और सुनाम से, यद्यपि भरमानेवालों के ऐसे मालूम होते हैं तौभी सच्चे हैं।"));
hinsuperobject.biblemap.put("b47_6_9", new hinsuperobject(47,6,9,"अनजानों के सदृश्य हैं; तौभी प्रसिद्ध हैं; मरते हुओं के ऐसे हैं और देखों जीवित हैं; मारखानेवालों के सदृश हैं परन्तु प्राण से मारे नहीं जाते।"));
hinsuperobject.biblemap.put("b47_6_10", new hinsuperobject(47,6,10,"शोक करनेवाले के समान हैं, परन्तु सर्वदा आनन्द करते हैं, कंगालों के ऐसे हैं, परन्तु बहुतों को धनवान बना देते हैं; ऐसे हैं जैसे हमारे पास कुछ नहीं तौभी सब कुछ रखते हैं।"));
hinsuperobject.biblemap.put("b47_6_11", new hinsuperobject(47,6,11,"हे कुरिन्थियों, हम ने खुलकर तुम से बातें की हैं, हमारा हृदय तुम्हारी ओर खुला हुआ है।"));
hinsuperobject.biblemap.put("b47_6_12", new hinsuperobject(47,6,12,"तुम्हारे लिये हमारे मन में कुछ सकेती नहीं, पर तुम्हारे ही मनों में सकेती है।"));
hinsuperobject.biblemap.put("b47_6_13", new hinsuperobject(47,6,13,"पर अपने लड़के- बाले जानकर तुम से कहता हूं, कि तुम भी उसके बदले में अपना हृदय खोल दो।।"));
hinsuperobject.biblemap.put("b47_6_14", new hinsuperobject(47,6,14,"अविश्वासियों के साथ असमान जूए में न जुतो, क्योंकि धार्मिकता और अधर्म का क्या मेल जोल? या ज्योति और अन्धकार की क्या संगति?"));
hinsuperobject.biblemap.put("b47_6_15", new hinsuperobject(47,6,15,"और मसीह का बलियाल के साथ क्या लगाव? या विश्वासी के साथ अविश्वासी का क्या नाता?"));
hinsuperobject.biblemap.put("b47_6_16", new hinsuperobject(47,6,16,"और मूरतों के साथ परमेश्वर के मन्दिर का क्या सम्बन्ध? क्योंकि हम तो जीवते परमेश्वर का मन्दिर हैं; जैसा परमेश्वर ने कहा है कि मैं उन में बसूंगा और उन में चला फिरा करूंगा; और मैं उन का परमेश्वर हूंगा, और वे मेरे लोग होंगे।"));
hinsuperobject.biblemap.put("b47_6_17", new hinsuperobject(47,6,17,"इसलिये प्रभु कहता है, कि उन के बीच में से निकलो और अलग रहो; और अशुद्ध वस्तु को मत छूओ, तो मैं तुम्हें ग्रहण करूंगा।"));
hinsuperobject.biblemap.put("b47_6_18", new hinsuperobject(47,6,18,"और तुम्हारा पिता हूंगा, और तुम मेरे बेटे और बेटियां होगे: यह सर्वशक्तिमान प्रभु परमेश्वर का वचन है।।"));
hinsuperobject.biblemap.put("b47_7_1", new hinsuperobject(47,7,1,"सो हे प्यारो जब कि ये प्रतिज्ञाएं हमें मिली हैं, तो आओ, हम अपने आप को शरीर और आत्मा की सब मलिनता शुद्ध करें, और परमेश्वर का भय रखते हुए पवित्राता को सिद्ध करें।।"));
hinsuperobject.biblemap.put("b47_7_2", new hinsuperobject(47,7,2,"हमें अपने हृदय में जगह दो: हम ने न किसी से अन्याय किया, न किसी को बिगाड़ा, और न किसी को ठगा।"));
hinsuperobject.biblemap.put("b47_7_3", new hinsuperobject(47,7,3,"मैं तुम्हें दोषी ठहराने के लिये यह नहीं कहता: क्योंकि मैं पहिले ही कह चूका हूं, कि तुम हमारे हृदय में ऐसे बस गए हो कि हम तुम्हारे साथ मरने जीने के लिये तैयार हैं।"));
hinsuperobject.biblemap.put("b47_7_4", new hinsuperobject(47,7,4,"मैं तुम से बहुत हियाव के साथ बोल रहा हूं, मुझे तुम पर बड़ा घमण्ड है: मैं शान्ति से भर गया हूं; अपने सारे क्लेश में मैं आनन्द से अति भरपूर रहता हूं।।"));
hinsuperobject.biblemap.put("b47_7_5", new hinsuperobject(47,7,5,"क्योंकि जब हम मकिदुनिया में आए, तब भी हमारे शरीर को चैन नहीं मिला, परन्तु हम चारों ओर से क्लेश पाते थे; बाहर लड़ाइयां थीं, भीतर भयंकर बातें थी।"));
hinsuperobject.biblemap.put("b47_7_6", new hinsuperobject(47,7,6,"तौभी दानों को शान्ति देनेवाले परमेश्वर ने तितुस के आने से हम को शान्ति दी।"));
hinsuperobject.biblemap.put("b47_7_7", new hinsuperobject(47,7,7,"और न केवल उसके आने से परन्तु उस की उस शान्ति से भी, जो उस को तुम्हारी ओर से मिली थी; और उस ने तुम्हारी लालसा, और तुम्हारे दुख ओर मेरे लिये तुम्हारी धुन का समाचार हमें सुनाया, जिस से मुझे और भी आनन्द हुआ।"));
hinsuperobject.biblemap.put("b47_7_8", new hinsuperobject(47,7,8,"क्योकि यद्यपि मैं ने अपनी पत्री से तुम्हें शोकित किया, परन्तु उस से पछताता नहीं जैसा कि पहिले पछताता था क्योंकि मैं देखता हूं, कि उस पत्री से तुम्हें शोक तो हुआ परन्तु वह थोड़ी देर के लिये था।"));
hinsuperobject.biblemap.put("b47_7_9", new hinsuperobject(47,7,9,"अब मैं आनन्दित हूं पर इसलिये नहीं कि तुम को शोक पहुंचा बरन इसलिये कि तुम ने उस शोक के कारण मन फिराया, क्योंकि तुम्हारा शोक परमेश्वर की इच्छा के अनुसार था, कि हमारी ओर से तुम्हें किसी बात में हानि न पहुंचे।"));
hinsuperobject.biblemap.put("b47_7_10", new hinsuperobject(47,7,10,"क्योंकि परमेश्वर- भक्ति का शोक ऐसा पश्चाताप उत्पन्न करता है जिस का परिणाम उद्धार है और फिर उस से पछताना नहीं पड़ता: परन्तु संसारी शोक मृत्यु उत्पन्न करता है।"));
hinsuperobject.biblemap.put("b47_7_11", new hinsuperobject(47,7,11,"सो देखो, इसी बात से कि तुम्हेें परमेश्वर- भक्ति का शोक हुआ तुम में कितनी उत्तेजना और प्रत्यत्तर और रिस, और भय, और लालसा, और धुन और पलआ लेने का विचार उत्पन्न हुआ? तुम ने सब प्रकार से यह सिद्ध कर दिखाया, कि तुम इस बात में निर्दोष हो।"));
hinsuperobject.biblemap.put("b47_7_12", new hinsuperobject(47,7,12,"फिर मैं ने जो तुम्हारे पास लिखा था, वह न तो उसके कारण लिखा, जिस ने अन्याय किया, और न उसके कारण जिस पर अन्याय किया गया, परन्तु इसलिये कि तुम्हारी उत्तेजना जो हमारे लिये है, वह परमेश्वर के साम्हने तुम पर प्रगट हो जाए।"));
hinsuperobject.biblemap.put("b47_7_13", new hinsuperobject(47,7,13,"इसलिये हमें शान्ति हुई; और हमारी इस शान्ति के साथ तितुस के आनन्द के कारण और भी आनन्द हुआ कयोंकि उसका जी तुम सब के कारण हरा भरा हो गया है।"));
hinsuperobject.biblemap.put("b47_7_14", new hinsuperobject(47,7,14,"क्योंकि यदि मैं ने उसके साम्हने तुम्हारे विषय में कुछ घमण्ड दिखाया, तो लज्जित नहीं हुआ, परन्तु जैसे हम ने तुम से सब बातें सच सच कह दी थीं, वैसे ही हमारा धमण्ड दिखाना तितुस के साम्हने भी सच निकला।"));
hinsuperobject.biblemap.put("b47_7_15", new hinsuperobject(47,7,15,"और जब उस को तुम सब के आज्ञाकारी होने का स्मरण आता है, कि क्योंकर तुम ने डरते और कांपते हुए उस से भेंट की; तो उसका प्रेम तुम्हारी ओर और भी बढ़ता जाता है।"));
hinsuperobject.biblemap.put("b47_7_16", new hinsuperobject(47,7,16,"मैं आनन्द करता हूं, कि तुम्हारी ओर से मुझे हर बात में ढाढ़स होता है।।"));
hinsuperobject.biblemap.put("b47_8_1", new hinsuperobject(47,8,1,"अब हे भाइयों, हम तुम्हें परमेश्वर के उस अनुग्रह का समाचार देते हैं, जो मकिदुनिया की कलीसियाओं पर हुआ है।"));
hinsuperobject.biblemap.put("b47_8_2", new hinsuperobject(47,8,2,"कि क्लेश की बड़ी परीक्षा में उन के बड़े आनन्द और भारी कंगालपन के बढ़ जाने से उन की उदारता बहुत बढ़ गई।"));
hinsuperobject.biblemap.put("b47_8_3", new hinsuperobject(47,8,3,"और उनके विषय में मेरी यह गवाही है, कि उन्हों ने अपनी सामर्थ भर बरन सामर्थ से भी बाहर मन से दिया।"));
hinsuperobject.biblemap.put("b47_8_4", new hinsuperobject(47,8,4,"और इस दान में और पवित्रा लोगों की सेवा में भागी होने के अनुग्रह के विषय में हम से बार बार बहुत बिनती की।"));
hinsuperobject.biblemap.put("b47_8_5", new hinsuperobject(47,8,5,"और जैसी हम ने आज्ञा की थी, वैसी ही नहीं, बरन उन्हों ने प्रभु को, फिर परमेश्वर की इच्छा से हम को भी अपने तई दे दिया।"));
hinsuperobject.biblemap.put("b47_8_6", new hinsuperobject(47,8,6,"इसलिये हम ने तितुस को समझाया, कि जेसा उस ने पहिले आरम्भ किया था, वैसा ही तुम्हारे बीच में इस दान के काम को पूरा भी कर ले।"));
hinsuperobject.biblemap.put("b47_8_7", new hinsuperobject(47,8,7,"सो जैसे हर बात में अर्थात् विश्वास, वचन, ज्ञान और सब प्रकार के यत्न में, और उस प्रेम में, जो हम से रखते हो, बढ़ते जाते हो, वैसे ही इस दान के काम में भी बढ़ते जाओ।"));
hinsuperobject.biblemap.put("b47_8_8", new hinsuperobject(47,8,8,"मैं आज्ञा की रीति पर तो नहीं, परन्तु और के उत्साह से तुम्हारे प्रेम की सच्चाई को परखने के लिये कहता हूं।"));
hinsuperobject.biblemap.put("b47_8_9", new hinsuperobject(47,8,9,"तुम हमारे प्रभु यीशु मसीह का अनुग्रह जानते हो, कि वह धनी होकर भी तुम्हारे लिये कंगाल बन गया ताकि उसके कंगाल हो जाने से तुम धनी हो जाओ।"));
hinsuperobject.biblemap.put("b47_8_10", new hinsuperobject(47,8,10,"और इस बात में मेरा विचार यही है, क्योंकि यह तुम्हारे लिये अच्छा है; जो एक वर्ष से न तो केवल इस काम को करने ही में, परन्तु इस बात के चाहने में भी प्रथम हुए थे।"));
hinsuperobject.biblemap.put("b47_8_11", new hinsuperobject(47,8,11,"इसलिये अब यह काम पूरा करो; कि इच्छा करने में तुम तैयार थे, वैसा ही अपनी अपनी पूंजी के अनुसार पूरा भी करो।"));
hinsuperobject.biblemap.put("b47_8_12", new hinsuperobject(47,8,12,"कयोंकि यदि मन की तैयारी हो तो दान उसके अनुसार ग्रहण भी होता है जो उसके पास है न कि उसके अनुसार जो उसके पास नहीं।"));
hinsuperobject.biblemap.put("b47_8_13", new hinsuperobject(47,8,13,"यह नहींख् कि औरो को चैन और तुम को क्लेश मिले।"));
hinsuperobject.biblemap.put("b47_8_14", new hinsuperobject(47,8,14,"परनतु बराबरी के विचार से इस समय तुम्हारी बढ़ती उनकी घटी में काम आए, ताकि उन की बढ़ती भी तुम्हारी घटी में काम आए, कि बराबरी हो जाए।"));
hinsuperobject.biblemap.put("b47_8_15", new hinsuperobject(47,8,15,"जेसा लिखा है, कि जिस ने बहुत बटोरा उसका कुछ अधिक न निकला और जिस ने थोड़ा बटोरा उसका कुछ कम न निकला।।"));
hinsuperobject.biblemap.put("b47_8_16", new hinsuperobject(47,8,16,"और परमेश्वर का धन्यवाद हो, जिस ने तुम्हारे लिये वही उत्साह तितुस के हृदय में डाल दिया है।"));
hinsuperobject.biblemap.put("b47_8_17", new hinsuperobject(47,8,17,"कि उस ने हमारा समझाना मान लिया बरन बहुत उत्साही होकर वह अपनी इच्छा से तुम्हारे पास गया है।"));
hinsuperobject.biblemap.put("b47_8_18", new hinsuperobject(47,8,18,"और हम ने उसके साथ उस भाई को भेजा है जिस का नाम सुसमाचार के विषय में सब कलीसिया में फैला हुआ है।"));
hinsuperobject.biblemap.put("b47_8_19", new hinsuperobject(47,8,19,"और इतना ही नहीं, परन्तु वह कलीसिया से ठहराया भी गया कि इस दान के काम के लिये हमारे साथ जाए और हम यह सेवा इसलिये करते हैं, कि प्रभु की महिमा और हमारे मन की तैयारी प्रगट हो जाए।"));
hinsuperobject.biblemap.put("b47_8_20", new hinsuperobject(47,8,20,"हम इस बात में चौकस रहते हैं, कि इस उदारता के काम के विषय में जिस की सेवा हम करते हैं, कोई हम पर दोष न लगाते पाए।"));
hinsuperobject.biblemap.put("b47_8_21", new hinsuperobject(47,8,21,"क्योंकि जो बातें केवल प्रभु ही के निकट नहीं, परन्तु मनुष्यों के निकट भी भली हैं हम उन की चिन्ता करते हैं।"));
hinsuperobject.biblemap.put("b47_8_22", new hinsuperobject(47,8,22,"और हम ने उसके साथ अपने भाई को भेजा है, जिस को हम ने बार बार परख के बहुत बातों में उत्साही पाया है; परन्तु अब तुम पर उस को बड़ा भरोसा है, इस कारण वह और भी अधिक उत्साही है।"));
hinsuperobject.biblemap.put("b47_8_23", new hinsuperobject(47,8,23,"यदि कोई तितुस के विषय में पूछे, तो वह मेरा साथी, और तुम्हारे लिये मेरा सहकर्मी है, और यदि हमारे भाइयों के विषय में पूछे, तो वे कलीसियाओं के भेजे हुए और मसीह की महिमा हैं।"));
hinsuperobject.biblemap.put("b47_8_24", new hinsuperobject(47,8,24,"सो अपना प्रेम और हमारा वह घमण्ड जो तुम्हारे विषय में है कलीसियाओं के साम्हने उन्हें सिद्ध करके दिखाओ।।"));
hinsuperobject.biblemap.put("b47_9_1", new hinsuperobject(47,9,1,"अब उस सेवा के विषय में जो पवित्रा लोगों के लिये की जाती है, मुझे तुम को लिखना अवश्य नहीं।"));
hinsuperobject.biblemap.put("b47_9_2", new hinsuperobject(47,9,2,"क्योंकि मैं तुम्हारे मन की तैयारी को जानता हूं, जिस के कारण मैं तुम्हारे विषय में मकिदुनियों के साम्हने घमण्ड दिखाता हूं, कि अखया के लोग एक वर्ष से तैयार हुए हैं, और तुमहारे उत्साह ने और बहुतों को भी उभारा है।"));
hinsuperobject.biblemap.put("b47_9_3", new hinsuperobject(47,9,3,"परन्तु मैं ने भाइयों को इसलिये भेजा है, कि हम ने जो घमण्ड तुम्हारे विषय में दिखाया, वह इस बात में व्यर्थ न ठहरे; परन्तु जैसा मैं ने कहा; वैसे ही तुम तैयार हो रहो।"));
hinsuperobject.biblemap.put("b47_9_4", new hinsuperobject(47,9,4,"ऐसा न हो, कि यदि कोई मकिदुनी मेरे साथ आए, और तुम्हें तैयार न पाए, तो क्या जानें, इस भरोसे के कारण हम (यह नहीं कहते कि तुम) लज्जित हों।"));
hinsuperobject.biblemap.put("b47_9_5", new hinsuperobject(47,9,5,"इसलिये मैं ने भाइयों से यह बिनती करना अवश्य समझा कि वे पहिले से तुम्हारे पास जाएं, और तुम्हारी उदारता का फल जिस के विषय में पहिले से वचन दिया गया था, तैयार कर रखें, कि यह दबाव से नहीं परन्तु उदारता के फल की नाई तैयार हो।।"));
hinsuperobject.biblemap.put("b47_9_6", new hinsuperobject(47,9,6,"परन्तु बात तो यह है, कि जो थोड़ा बोता है वह थोड़ा काटेगा भी; और जो बहुत बोता है, वह बहुत काटेगा।"));
hinsuperobject.biblemap.put("b47_9_7", new hinsuperobject(47,9,7,"हर एक जन जैसा मन में ठाने वैसा ही दान करेद्ध न कुढ़ कुढ़ के, और न दबाव से, क्योंकि परमेश्वर हर्ष से देनेवाले से प्रेम रखता है।"));
hinsuperobject.biblemap.put("b47_9_8", new hinsuperobject(47,9,8,"और परमेश्वर सच प्रकार का अनुग्रह तुम्हें बहुतायत से दे सकता है जिस से हर बात में और हर समय, सब कुछ, जो तुम्हें आवश्यक हो, तुम्हारे पास रहे, और हर एक भले काम के लिये तुम्हारे पास बहुत कुछ हो।"));
hinsuperobject.biblemap.put("b47_9_9", new hinsuperobject(47,9,9,"जेसा लिखा है, उस ने बिथराया, उस ने कंगालों को दान दिया, उसका धर्म सदा बना रहेगा।"));
hinsuperobject.biblemap.put("b47_9_10", new hinsuperobject(47,9,10,"सो जो बोनेवाले को बीज, और भोजन के लिये रोटी देता है वह तुम्हें बीज देगा, और उसे फलवन्त करेगा; और तुम्हारे धर्म के फलों को बढ़ाएगा।"));
hinsuperobject.biblemap.put("b47_9_11", new hinsuperobject(47,9,11,"कि तुम हर बात में सब प्रकार की उदारता के लिये जो हमारे द्वारा परमेश्वर का धन्यवाद करवाती है, धनवान किए जाओ।"));
hinsuperobject.biblemap.put("b47_9_12", new hinsuperobject(47,9,12,"क्योंकि इस सेवा के पूरा करने से, न केवल पवित्रा लोगों की घटियां पूरी होती हैं, परन्तु लोगों की ओर से परमेश्वर का बहुत धन्यवाद होता है।"));
hinsuperobject.biblemap.put("b47_9_13", new hinsuperobject(47,9,13,"क्योंकि इस सेवा से प्रमाण लेकर परमेश्वर की महिमा प्रगट करते हैं, कि तुम मसीह के सुसमाचार को मान कर उसके आधीन रहते हो, और उन की, और सब की सहायता करने में उदारता प्रगट करते रहते हो।"));
hinsuperobject.biblemap.put("b47_9_14", new hinsuperobject(47,9,14,"ओर वे तुम्हारे लिये प्रार्थना करते हैं; और इसलिये कि तुम पर परमेश्वर का बड़ा ही अनुग्रह है, तुम्हारी लालसा करते रहते हैं।"));
hinsuperobject.biblemap.put("b47_9_15", new hinsuperobject(47,9,15,"परमेश्वर को उसके उस दान के लिये जो वर्णन से बाहर है, धन्यवाद हो।।"));
hinsuperobject.biblemap.put("b47_10_1", new hinsuperobject(47,10,1,"मैं वही पौलुस जो तुम्हारे साम्हने दीन हूं, परन्तु पीठ पीछे तुम्हारी ओर साहस करता हूं; तुम को मसीह की नम्रता, और कोमलता के कारण समझाता हूं।"));
hinsuperobject.biblemap.put("b47_10_2", new hinsuperobject(47,10,2,"मैं यह बिनती करता हूं, कि तुम्हारे साम्हने मुझे निर्भय होकर साहस करना न पड़े; जैसा मैं कितनों पर जो हम को शरीर के अनुसार चलनेवाले समझते हैं, वीरता दिखाने का विचार करता हूं।"));
hinsuperobject.biblemap.put("b47_10_3", new hinsuperobject(47,10,3,"क्योंकि यद्यपि हम शरीर में चलते फिरते हैं, तौभी शरीर के अनुसार नहीं लड़ते।"));
hinsuperobject.biblemap.put("b47_10_4", new hinsuperobject(47,10,4,"क्योंकि हमारी लड़ाई के हथियार शारीरिक नहीं, पर गढ़ों को ढा देने के लिये परमेश्वर के द्वारा सामर्थी हैं।"));
hinsuperobject.biblemap.put("b47_10_5", new hinsuperobject(47,10,5,"सो हम कल्पनाओं को, और हर एक ऊंची बात को, जो परमेश्वर की पहिचान के विरोध में उठती है, खण्डन करते हैं; और हर एक भावना को कैद करके मसीह का आज्ञाकारी बना देते हैं।"));
hinsuperobject.biblemap.put("b47_10_6", new hinsuperobject(47,10,6,"और तैयार रहते हैं कि जब तुम्हारा आज्ञा मानना पूरा हो जाए, तो हर एक प्रकार के आज्ञा न मानने का पलटा लें।"));
hinsuperobject.biblemap.put("b47_10_7", new hinsuperobject(47,10,7,"तुम इन्हीं बातों को दखते हो, जो आंखों के साम्हने हैं, यदि किसी का अपने पर यह भरोसा हो, कि मैं मसीह का हूं, तो वह यह भी जान ले, कि जैसा वह मसीह का है, वैसे ही हम भी हैं।"));
hinsuperobject.biblemap.put("b47_10_8", new hinsuperobject(47,10,8,"क्योंकि यदि मैं उस अधिकार के विषय में और भी घमण्ड दिखाऊं, जो प्रभु ने तुम्हारे बिगाड़ने के लिये नहीं पर बनाने के लिये हमें दिया है, तो लज्जित न हूंगा।"));
hinsuperobject.biblemap.put("b47_10_9", new hinsuperobject(47,10,9,"यह मैं इसलिये कहता हूं, कि पत्रियों के द्वारा तुम्हें डरानेवाला न ठहरूं।"));
hinsuperobject.biblemap.put("b47_10_10", new hinsuperobject(47,10,10,"क्योंकि कहते हें, कि उस की पत्रियां तो गम्भीर और प्रभावशाली हैं; परन्तु जब देखते हैं, तो वह देह का निर्बल और वक्तव्य में हल्का जान पड़ता है।"));
hinsuperobject.biblemap.put("b47_10_11", new hinsuperobject(47,10,11,"सो जो ऐसा कहता है, कि समझ रखे, कि जैसे पीठ पीछे पत्रियों में हमारे वचन हैं, वैसे ही तुम्हारे साम्हने हमारे काम भी होंगे।"));
hinsuperobject.biblemap.put("b47_10_12", new hinsuperobject(47,10,12,"क्योंकि हमें यह हियाव नहीं कि हम अपने आप को उन में से ऐसे कितनों के साथ गिनें, या उन से अपने को मिलाएं, जो अपनी प्रशंसा करते हैं, और अपने आप को आपस में नाप तौलकर एक दूसरे से मिलान करके मूर्ख ठहरते हैं।"));
hinsuperobject.biblemap.put("b47_10_13", new hinsuperobject(47,10,13,"हम तो सीमा से बाहर घमण्ड कदापि न करेंगे, परन्तु उसी सीमा तक जो परमेश्वर ने हमारे लिये ठहरा दी है, और उस में तुम भी आ गए हो और उसी के अनुसार घमण्ड भी करेंगे।"));
hinsuperobject.biblemap.put("b47_10_14", new hinsuperobject(47,10,14,"क्योंकि हम अपनी सीमा से बाहर अपने आप को बढ़ाना नहीं चाहते, जैसे कि तुम तक न पहुंचने की दशा में होता, बरन मसीह का सुसमाचार सुनाते हुए तुम तक पहुंच चुके हैं।"));
hinsuperobject.biblemap.put("b47_10_15", new hinsuperobject(47,10,15,"और हम सीमा से बाहर औरों के परिश्रम पर घमण्उ नहीं करते; परन्तु हमें आशा है, कि ज्यों ज्यों तुम्हारा विश्वास बढ़ता जाएगा त्यों त्यों हम अपनी सीमा के अनुसार तुम्हारे कारण और भी बढ़ते जाएंगे।"));
hinsuperobject.biblemap.put("b47_10_16", new hinsuperobject(47,10,16,"कि हम तुम्हारे सिवानों से आगे बढ़कर सुसमाचार सुनाएं, और यह नहीं, कि हम औरों की सीमा के भीतर बने बनाए कामों पर घमण्ड करें।"));
hinsuperobject.biblemap.put("b47_10_17", new hinsuperobject(47,10,17,"परनतु जो घमण्ड करे, वह प्रभु पर घमण्ड करें।"));
hinsuperobject.biblemap.put("b47_10_18", new hinsuperobject(47,10,18,"क्योंकि जो अपनी बड़ाई करता है, वह नहीं, परन्तु जिस की बड़ाई प्रभु करता है, वही ग्रहण किया जाता है।।"));
hinsuperobject.biblemap.put("b47_11_1", new hinsuperobject(47,11,1,"यदि तुम मेरी थोड़ी मूर्खता सह लेते तो क्या ही भला होता; हां, मेरी सह भी लेते हो।"));
hinsuperobject.biblemap.put("b47_11_2", new hinsuperobject(47,11,2,"क्योंकि मैं तुम्हारे विषय मे ईश्वरीय धुन लगाए रहता हूं, इसलिये कि मैं ने एक ही पुरूष से तुम्हारी बात लगाई है, कि तुम्हें पवित्रा कुंवारी की नाई मसीह को सौंप दूं।"));
hinsuperobject.biblemap.put("b47_11_3", new hinsuperobject(47,11,3,"परन्तु मैं डरता हूं कि जैसे सांप ने अपनी चतुराई से हव्वा को बहकाया, वैसे ही तुम्हारे मन उस सीधाई और पवित्राता से जो मसीह के साथ होनी चाहिए कहीं भ्रष्ट न किए जाएं।"));
hinsuperobject.biblemap.put("b47_11_4", new hinsuperobject(47,11,4,"यदि कोई तुम्हारे पास आकर, किसी दूसरे यीशु को प्रचार करे, जिस का प्रचार हम ने नहीं किया: या कोई और आत्मा तुम्हें मिले; जो पहिले न मिला था; या और कोई सुसमाचार जिसे तुम ने पहिले न माना था, तो तुम्हारा सहना ठीक होता।"));
hinsuperobject.biblemap.put("b47_11_5", new hinsuperobject(47,11,5,"मैं तो समझता हमं, कि मैं किसी बात में बड़े से बड़े प्रेरितों से कम नहीं हूं।"));
hinsuperobject.biblemap.put("b47_11_6", new hinsuperobject(47,11,6,"यदि मैं व्क्तवय में अनाड़ी हूं, तौभी ज्ञान में नहीं; बरन हम ने इस को हर बात में सब पर तुम्हारे लिये प्रगट किया है।"));
hinsuperobject.biblemap.put("b47_11_7", new hinsuperobject(47,11,7,"क्या इस में मैं ने कुछ पाप किया; कि मैं ने तुम्हें परमेश्वर का सुसमाचार सेंत मेंत सुनाया; और अपने आप को नीचा किया, कि तुम ऊंचे हो जाओ?"));
hinsuperobject.biblemap.put("b47_11_8", new hinsuperobject(47,11,8,"मैं ने और कलीसियाओं को लूटा अर्थात् मैं ने उन से मजदूरी ली, ताकि तुम्हारी सेवा करूं।"));
hinsuperobject.biblemap.put("b47_11_9", new hinsuperobject(47,11,9,"ओर जब तुम्हारे साथ था, और मुझे घटी हुई, तो मैं ने किसी पर भार नहीं दिया, क्योंकि भाइयों ने, मकिदुनिया से आकर मेरी घटी को परी की: और मैं ने हर बात में अपने आप को तुम पर भार होने से रोका, और रोके रहूंगा।"));
hinsuperobject.biblemap.put("b47_11_10", new hinsuperobject(47,11,10,"यदि मसीह की सच्चाई मुझ में है, तो अखया देश में कोई मुझे इस घमण्ड से न रोकेगा।"));
hinsuperobject.biblemap.put("b47_11_11", new hinsuperobject(47,11,11,"किस लिये? क्या इसलिये कि मैं तुम से प्रेम नहीं रखता? परमेश्वर यह जानता है।"));
hinsuperobject.biblemap.put("b47_11_12", new hinsuperobject(47,11,12,"परन्तु जो मैं करता हूं, वही करता रहूंगा; कि जो लोग दांव ढूंढ़ते हैं, उन्हें मैं दांव पाने दूं, ताकि जिस बात में वे घमण्ड करते हैं, उस में वे हमारे ही समान ठहरें।"));
hinsuperobject.biblemap.put("b47_11_13", new hinsuperobject(47,11,13,"क्योंकि ऐसे लोग झूठे प्रेरित, और छल से काम करनेवाले, और मसीह के प्रेरितों का रूप धरनेवाले हैं।"));
hinsuperobject.biblemap.put("b47_11_14", new hinsuperobject(47,11,14,"और यह कुछ अचम्भे की बात नहीं क्योंकि शैतान आप भी ज्योतिमर्य स्वर्गदूत का रूप धारण करता है।"));
hinsuperobject.biblemap.put("b47_11_15", new hinsuperobject(47,11,15,"सो यदि उसके सेवक भी धर्म के सेवकों का सा रूप धरें, तो कुछ बड़ी बात नहीं परन्तु उन का अन्त उन के कामों के अनुसार होगा।"));
hinsuperobject.biblemap.put("b47_11_16", new hinsuperobject(47,11,16,"मैं फिर कहता हूं, कोई मुझे मूर्ख न समझे; नहीं तो मूर्ख ही समझकर मेरी सह लो, ताकि थोड़ा सा मैं भी घमण्ड करूं।"));
hinsuperobject.biblemap.put("b47_11_17", new hinsuperobject(47,11,17,"इस बेधड़क घमण्ड से बोलने में जो कुछ मैं कहता हूं वह प्रभू की आज्ञा के अनुसार नहीं पर मानों मूर्खता से ही कहता हूं।"));
hinsuperobject.biblemap.put("b47_11_18", new hinsuperobject(47,11,18,"जब कि बहुत लोग शरीर के अनुसार घमण्ड करते हैं, तो मैं भी घमण्ड करूंगा।"));
hinsuperobject.biblemap.put("b47_11_19", new hinsuperobject(47,11,19,"तुम तो समझदार होकर आनन्द से मूर्खों की सह लेते हो।"));
hinsuperobject.biblemap.put("b47_11_20", new hinsuperobject(47,11,20,"क्योंकि जब तुम्हें कोई दास बना लेता है, या खा जाता है, या फसा लेता है, या अपने आप को बड़ा बनाता है, या तुम्हारे मुंह पर थप्पड़ मारता है, तो तुम सह लेते हो।"));
hinsuperobject.biblemap.put("b47_11_21", new hinsuperobject(47,11,21,"मेरा कहता अनादर की रीति पर है, मानो कि हम निर्बल से थे; परन्तु जिस किसी बात में कोई हियाव करता है (मैं मूर्खता से कहता हूं) तो मैं भी हियाव करता हूं।"));
hinsuperobject.biblemap.put("b47_11_22", new hinsuperobject(47,11,22,"क्या वे ही इब्रानी हैं? मैं भी हूं: क्या वे ही इब्राहीम के वंश के हैं? मैं भी हूं: क्या वे ही मसीह के सेवक हैं?"));
hinsuperobject.biblemap.put("b47_11_23", new hinsuperobject(47,11,23,"(मैं पागल की नाई कहता हूं) मैं उन से बढ़कर हूं! अधिक परिश्रम करने में; बार बार कैद होने में; कोड़े खाते में; बार बार मृत्यु के जोखिमों में।"));
hinsuperobject.biblemap.put("b47_11_24", new hinsuperobject(47,11,24,"पांच बार मैं ने यहूदियों के हाथ से उन्तालीस उन्तालीस कोड़े खाए।"));
hinsuperobject.biblemap.put("b47_11_25", new hinsuperobject(47,11,25,"तीन बार मैं ने बेंतेें खाई; एक बार पत्थरवाह किया गया; तीन बार जहाज जिन पर मैं चढ़ा था, टूट गए; एक राज दिन मैं ने समुद्र में काटा।"));
hinsuperobject.biblemap.put("b47_11_26", new hinsuperobject(47,11,26,"मैं बार बार यात्राओं में; नदियों के जोखिमों में; डाकुओं के जोखिमों में; अपने जातिवालों से जोखिमों में; अन्यजातियों से जोखिमों में; नगरों में के जाखिमों में; जंगल के जोखिमों में; समुद्र के जाखिमों में; झूठे भाइयों के बीच जोखिमों में;"));
hinsuperobject.biblemap.put("b47_11_27", new hinsuperobject(47,11,27,"परिश्रम और कष्ट में; बार बार जागते रहने में; भूख- पियास में; बार बार उपवास करते में; जाड़े में; उघाड़े रहने में।"));
hinsuperobject.biblemap.put("b47_11_28", new hinsuperobject(47,11,28,"और और बातों को छोड़कर जिन का वर्णन मैं नहीं करता सब कलीसियाओं की चिन्ता प्रतिदिन मुझे दबाती है।"));
hinsuperobject.biblemap.put("b47_11_29", new hinsuperobject(47,11,29,"किस की निर्बलता से मैं निर्बल नहीं होता? किस के ठोकर खाने से मेरा जी नहीं दुखता?"));
hinsuperobject.biblemap.put("b47_11_30", new hinsuperobject(47,11,30,"यदि घमण्उ करना अवश्य है, तो मैं अपनी निर्बलता की बातों पर करूंगा।"));
hinsuperobject.biblemap.put("b47_11_31", new hinsuperobject(47,11,31,"प्रभु यीशु का परमेश्वर और पिता जो सदा धन्य है, जानता है, कि मैं झूठ नहीं बोलता।"));
hinsuperobject.biblemap.put("b47_11_32", new hinsuperobject(47,11,32,"दमिश्क में अरितास राजा की ओर से जो हाकिम था, उस ने मेरे पकड़ने को दमिस्त्रििकशयों के नगर पर पहरा बैठा रखा था।"));
hinsuperobject.biblemap.put("b47_11_33", new hinsuperobject(47,11,33,"और मैं टोकरे में खिड़की से होकर भीत पर से उतारा गया, और उसके हाथ से बच निकला।।"));
hinsuperobject.biblemap.put("b47_12_1", new hinsuperobject(47,12,1,"यद्यपि घमण्ड करना तो मेरे लिये ठीक नहीं तौभी करना पड़ता है; सो मैं प्रभु के दिए हुए दर्शनों और प्रकाशों की चर्चा करूंगा।"));
hinsuperobject.biblemap.put("b47_12_2", new hinsuperobject(47,12,2,"मैं मसीह में एक मनुष्य को जानता हूं, चौदह वर्ष हुए कि न जाने देहसहित, न जाने देहरहित, परमेश्वर जानता है, ऐसा मनुष्य तीसरे स्वर्ग तक उठा लिया गया।"));
hinsuperobject.biblemap.put("b47_12_3", new hinsuperobject(47,12,3,"मैं ऐसे मनुष्य को जानता हूं न जाने देहसहित, न जाने देहरहित परमेश्वर ही जानता है।"));
hinsuperobject.biblemap.put("b47_12_4", new hinsuperobject(47,12,4,"कि स्वर्ग लोक पर उठा लिया गया, और एसी बातें सुनीं जो कहने की नहीं; और जिन का मुंह में लाना मनुष्य को उचित नहींं।"));
hinsuperobject.biblemap.put("b47_12_5", new hinsuperobject(47,12,5,"ऐसे मनुष्य पर तो मैं घमण्ड करूंगा, परन्तु अपने पर अपनी निर्बलताओं को छोड़, अपने विषय में घमण्ड न करूंगा।"));
hinsuperobject.biblemap.put("b47_12_6", new hinsuperobject(47,12,6,"क्योंकि यदि मैं घमण्ड करना चाहूं भी तो मूंर्ख न हूंगा, क्योंकि सच बोलूंगा; तोभी रूक जाता हूं, ऐसा न हो, कि जैसा कोई मुझे देखता है, या मुझ से सुनता है, मुझे उस से बढ़कर समझे।"));
hinsuperobject.biblemap.put("b47_12_7", new hinsuperobject(47,12,7,"और इसलिये कि मैं प्रकाशों की बहुतायत से फूल न जाऊं, मेरे शरीर में एक कांटा चुभाया गया अर्थात् शैतान का एक दूत कि मुझे घूसे मारे ताकि मैं फूल न जाऊं।"));
hinsuperobject.biblemap.put("b47_12_8", new hinsuperobject(47,12,8,"इस के विषय में मैं ने प्रभु से तीन बार बिनती की, कि मुझ से यह दूर हो जाए।"));
hinsuperobject.biblemap.put("b47_12_9", new hinsuperobject(47,12,9,"और उस ने मुझ से कहा, मेरा अनुग्रह तेरे लिये बहुत है; क्योंकि मेरी सामर्थ निर्बलता में सिद्ध होती है; इसलिये मैं बड़े आनन्द से अपनी निर्बलताओं पर घमण्ड करूंगा, कि मसीह की सामर्थ मुझ पर छाया करती रहे।"));
hinsuperobject.biblemap.put("b47_12_10", new hinsuperobject(47,12,10,"इस कारण मैं मसीह के लिये निर्बलताओं, और निन्दाओं में, और दरिद्रता में, और उपद्रवों में, और संकटों में, प्रसन्न हूं; क्योंकि जब मैं निर्बल होता हूं, तभी बलवन्त होता हूं।।"));
hinsuperobject.biblemap.put("b47_12_11", new hinsuperobject(47,12,11,"मैं मूर्ख तो बना, परन्तु तुम ही ने मुझ से यह बरबस करवाया: तुम्हें तो मेरी प्रशंसा करनी चाहिए थी, क्योंकि यद्यपि मैं कुछ भी नहीं, तौभी उन बड़े से बड़े प्ररितों से किसी बात में कम नहीं हूं।"));
hinsuperobject.biblemap.put("b47_12_12", new hinsuperobject(47,12,12,"प्ररित के लक्षण भी तुम्हारे बीच सब प्रकार के धीरज सहित चिन्हों, और अद्भुत कामों, और सामर्थ के कामों से दिखाए गए।"));
hinsuperobject.biblemap.put("b47_12_13", new hinsuperobject(47,12,13,"तुम कोैन सी बात में और कलीसियों से कम थे, केवल इस में कि मैं ने तुम पर अपना भार न रखा: मेरा यह अन्याय क्षमा करो।"));
hinsuperobject.biblemap.put("b47_12_14", new hinsuperobject(47,12,14,"देखो, मैं तीसरी बार तुम्हारे पास आने को तैयार हूं, और मैं तुम पर कोई भार न रखूंगा; क्योंकि मैं तुम्हारी सम्पत्ति नहीं, बरन तुम ही को चाहता हूं: क्योंकि लड़के- बालों को माता- पिता के लिये धन बटोरना न चाहिए, पर माता- पिता को लड़के- बालों के लिये।"));
hinsuperobject.biblemap.put("b47_12_15", new hinsuperobject(47,12,15,"मैं तुम्हारी आत्माओं के लिये बहुत आनन्द से खर्च करूंगा, बरन आप भी खर्च हो जाऊंगा: क्या जितना बढ़कर मैं तुम से प्रेम रखता हूं, उतना ही घटकर तुम मुझ से प्रेम रखोगे?"));
hinsuperobject.biblemap.put("b47_12_16", new hinsuperobject(47,12,16,"ऐसा हो सकता है, कि मैं ने तुम पर बोझ नहीं डाला, परन्तु चतुराई से तुम्हें धोखा देकर फंसा लिया।"));
hinsuperobject.biblemap.put("b47_12_17", new hinsuperobject(47,12,17,"भला, जिन्हें मैं ने तुम्हारे पास भेजा, क्या उन में से किसी के द्वारा मैं ने छल करके तुम से कुछ ले लिया?"));
hinsuperobject.biblemap.put("b47_12_18", new hinsuperobject(47,12,18,"मै ने तितुस को समझाकर उसके साथ उस भाई को भेजा, तो क्या तीतुस ने छल करके तुम से कुछ लिया? क्या हम एक ही आत्मा के चलाए न चले? क्या एक ही लीक पर न चले?"));
hinsuperobject.biblemap.put("b47_12_19", new hinsuperobject(47,12,19,"तुम अभी तक समझ रहे होगे कि हम तुम्हारे सामने प्रत्युत्तर दे रहे हैं, हम तो परमेश्वर को उपस्थित जानकर मसीह में बोलते हैं, और हे प्रियों, सब बातें तुम्हारी उन्नति ही के लिये कहते हैं।"));
hinsuperobject.biblemap.put("b47_12_20", new hinsuperobject(47,12,20,"क्योंकि मुझे डर है, कहीं ऐसा न हो, कि मैं आकर जैसे चाहता हूं, वैसे तुम्हें न पाऊं; और मुझे भी जैसा तुम नहीं चाहते वैसा ही पाओ, कि तुम में झगड़ा, डाह, क्रोध, विराध, ईर्ष्या, चुगली, अभिमान और बखेड़े हों।"));
hinsuperobject.biblemap.put("b47_12_21", new hinsuperobject(47,12,21,"और मेरा परमेश्वर कहीं मेरे फिर से तुम्हारे यहां आने पर मुझ पर दबाव डाले और मुझे बहुतों के लिये फिर शोक करना पड़े, जिन्हों ने पहिले पाप किया था, और उस गन्दे काम, और व्यभिचार, और लुचपन से, जो उन्हों ने किया, मन नहीं फिराया।।"));
hinsuperobject.biblemap.put("b47_13_1", new hinsuperobject(47,13,1,"अब तीसरी बार तुम्हारे पास आता हूं: दो या तीन गवाहों के मुंह से हर एक बात ठहराई जाएगी।"));
hinsuperobject.biblemap.put("b47_13_2", new hinsuperobject(47,13,2,"जैसे जब दूसरी बार तुम्हारे साथ था, सो वैसे ही अब दूर रहते हुए उन लोगों से जिन्हों ने पहिले पाप किया, और और सब लोगों से अब पहिले से कहे देता हूं, कि यदि मैं फिर आऊंगा, तो नहीं छोडूंगा।"));
hinsuperobject.biblemap.put("b47_13_3", new hinsuperobject(47,13,3,"तुम तो इस का प्रमाण चाहते हो, कि मसीह मुझ में बोलता है, जो तुम्हारे लिये निर्बल नहीं; परन्तु तुम में सामर्थी है।"));
hinsuperobject.biblemap.put("b47_13_4", new hinsuperobject(47,13,4,"वह निर्बलता के कारण क्रूस पर चढ़ाया तो गया, तौभी परमेश्वर की सामर्थ से जीवित है, हम भी तो उस में निर्बल हैं; परन्तु परमेश्वर की सामर्थ से जो तुम्हारे लिये है, उसके साथ जीएंगे।"));
hinsuperobject.biblemap.put("b47_13_5", new hinsuperobject(47,13,5,"अपने प्राण को परखो, कि विश्वास में हो कि नहीं; अपने आप को जांचो, क्या तुम अपने विषय में यह नहीं जानते, कि यीशु मसीह तुम में है? नहीं तो तुम निकम्मे निकले हो।"));
hinsuperobject.biblemap.put("b47_13_6", new hinsuperobject(47,13,6,"पर मेरी आशा है, कि तुम जान लोगे, कि हम निकम्मे नहीं।"));
hinsuperobject.biblemap.put("b47_13_7", new hinsuperobject(47,13,7,"और हम अपने परमेश्वर से प्रार्थना करते हैं, कि तुम कोई बुराई न करो; इसलिये हनीं, कि हम खरे देख पड़ें, पर इसलिये कि तुम भलाई करो, चाहे हम निकम्मे ही ठहरें।"));
hinsuperobject.biblemap.put("b47_13_8", new hinsuperobject(47,13,8,"क्योंकि हम सत्य के विरोध में कुछ नहीं कर सकते, पर सत्य के लिये कर सकते हैं।"));
hinsuperobject.biblemap.put("b47_13_9", new hinsuperobject(47,13,9,"जब हम निर्बल हैं, और तुम बलवन्त हो, तो हम आनन्दित होते हैं, और यह प्रार्थना भी करते हैं, कि तुम सिद्ध हो जाओ।"));
hinsuperobject.biblemap.put("b47_13_10", new hinsuperobject(47,13,10,"इस कारण मैं तुम्हारे पीठ पीछे ये बातें लिखता हूं, कि उपस्थित होकर मुझे उस अधिकार के अनुसार जिसे प्रभु ने बिगाड़ने के लिये नहीं पर बनाने के लिये मुझे दिया है, कढ़ाई से कुछ करना न पडे।।"));
hinsuperobject.biblemap.put("b47_13_11", new hinsuperobject(47,13,11,"निदान, हे भाइयो, आनन्दित रहो; सिद्ध बनते जाओ; ढाढ़स रखो; एक ही मन रखो; मेल से रहो, और प्रेम और शान्ति का दाता परमेश्वर तुम्हारे साथ होगा।"));
hinsuperobject.biblemap.put("b47_13_12", new hinsuperobject(47,13,12,"एक दूसरे को पवित्रा चुम्बन से नमस्कार करो।"));
hinsuperobject.biblemap.put("b47_13_13", new hinsuperobject(47,13,13,"सब पवित्रा लोग तुम्हें नमस्कार करते हैं।"));
hinsuperobject.biblemap.put("b47_13_14", new hinsuperobject(47,13,14,"प्रभु यीशु मसीह का अनुग्रह और परमेश्वर का प्रेम और पवित्रा आत्मा की सहभागिता तुम सब के साथ होती रहे।।"));

}

}