package hin.bible;

public class hinbible48 {

public hinbible48() {

putmapbible48();

}

private void putmapbible48() {

hinsuperobject.biblemap.put("b48_1_1", new hinsuperobject(48,1,1,"पौलुस की, जो न मनुष्यों की ओर से, और न मनुष्य के द्वारा, बरन यीशु मसीह और परमेश्वर पिता के द्वारा, जिस ने मरे हुओं में से जिलाया, प्रेरित है।"));
hinsuperobject.biblemap.put("b48_1_2", new hinsuperobject(48,1,2,"और सारे भाइयों की आरे से, जो मेरे साथ हैं; गलतिया की कलीसियाओं के नाम।"));
hinsuperobject.biblemap.put("b48_1_3", new hinsuperobject(48,1,3,"परमेश्वर पिता, और हमारे प्रभु यीशु मसीह की आरे से तुम्हें अनुगंह और शान्ति मिलती रहे।"));
hinsuperobject.biblemap.put("b48_1_4", new hinsuperobject(48,1,4,"उसी ने अपने आप को हमारे पापों के लिये दे दिया, ताकि हमारे परमेश्वर और पिता की इच्छा के अनुसार हमें इस वर्तमान बुरे संसार से छुड़ाए।"));
hinsuperobject.biblemap.put("b48_1_5", new hinsuperobject(48,1,5,"उस की स्तुति और बड़ाइ। युगानुयुग होती रहे। आमीन।।"));
hinsuperobject.biblemap.put("b48_1_6", new hinsuperobject(48,1,6,"मुझे आश्चर्य होता है, कि जिस ने तुम्हें मसीह के अनुग्रह से बुलाया उस से तुम इतनी जल्दी फिर कर और ही प्रकार के सुसमाचार की ओर झुकने लगे।"));
hinsuperobject.biblemap.put("b48_1_7", new hinsuperobject(48,1,7,"परन्तु वह दूसरा सुसमाचार है ही नहीं: पर बात यह है, कि कितने ऐसे हैं, जो तुम्हें घबरा देते, और मसीह के सुसमाचार को बिगाड़ना चाहते हैं।"));
hinsuperobject.biblemap.put("b48_1_8", new hinsuperobject(48,1,8,"परन्तु यदि हम या स्वर्ग से कोई दूत भी उस सुसमाचार को छोड़ जो हम ने तुम को सुनाया है, कोई और सुसमाचार तुम्हें सुनाए, तो स्त्रामित हो।"));
hinsuperobject.biblemap.put("b48_1_9", new hinsuperobject(48,1,9,"जैसा हम पहिले कह चुके हैं, वैसा ही मैं अब फिर कहता हूं, कि उस सुसमाचार को छोड़ जिसे तुम ने ग्रहण किया है, यदि कोई और सुसमाचार सुनाता है, तो स्त्रापित हो। अब मैं क्या मनुष्यों को मानता हूं या परमेश्वर को? क्या मैं मनुष्यों को प्रसन्न करना चाहता हूं?"));
hinsuperobject.biblemap.put("b48_1_10", new hinsuperobject(48,1,10,"यदि मैं अब तक मनुष्यों को प्रसन्न करता रहता, तो मसीह का दास न होता।।"));
hinsuperobject.biblemap.put("b48_1_11", new hinsuperobject(48,1,11,"हे भाइयो, मैं तुम्हें जताए देता हूं, कि जो सुसमाचार मैं ने सुनाया है, वह मनुष्य का सा नहीं।"));
hinsuperobject.biblemap.put("b48_1_12", new hinsuperobject(48,1,12,"क्योंकि वह मुझै मनुष्य की ओर से नहीं पहुंचा, और न मुझे सिखाया गया, पर यीशु मसीह के प्रकाश से मिला।"));
hinsuperobject.biblemap.put("b48_1_13", new hinsuperobject(48,1,13,"यहूदी मत में जो पहिले मेरा चाल चलन था, तुम सुन चुके हो; कि मैं परमेश्वर की कलीसिया को बहुत ही सताता और नाश करता था।"));
hinsuperobject.biblemap.put("b48_1_14", new hinsuperobject(48,1,14,"और अपने बहुत से जातिवालों से जो मेरी अवस्था के थे यहूदी मत में बढ़ता जाता था और अपने बापदादों के व्यवहारों में बहुत ही उत्तेजित था।"));
hinsuperobject.biblemap.put("b48_1_15", new hinsuperobject(48,1,15,"परन्तु परमेश्वर की, जिस ने मेरी माता के गर्भ ही से मुझे ठहराया और अपने अनुग्रह से बुला लिया,"));
hinsuperobject.biblemap.put("b48_1_16", new hinsuperobject(48,1,16,"जब इच्छा हुई, कि मुझ में अपने पुत्रा को प्रगट करे कि मैं अन्यजातियों में उसका सुसमाचार सुनाऊं; तो न मैं ने मांस और लोहू से सलाह ली;"));
hinsuperobject.biblemap.put("b48_1_17", new hinsuperobject(48,1,17,"और न यरूशलेम को उन के पास गया जो मुझ से पहिले प्रेरित थे, पर तुरन्त अरब को चला गया: और फिर वहां से दमिश्क को लौट आया।।"));
hinsuperobject.biblemap.put("b48_1_18", new hinsuperobject(48,1,18,"फिर तीन बरस के बाद मैं कैफा से भेंट करने के लिये यरूशलेम को गया, और उसके पास पन्द्रह दिन तक रहा।"));
hinsuperobject.biblemap.put("b48_1_19", new hinsuperobject(48,1,19,"परन्तु प्रभु के भाई याकूब को छोड़ और प्रेरितों में से किसी से न मिला।"));
hinsuperobject.biblemap.put("b48_1_20", new hinsuperobject(48,1,20,"जो बातें मैं तुम्हें लिखता हूं, देखो परमेश्वर को उपस्थित जानकर कहता हूं, कि वे झूठी नहीं।"));
hinsuperobject.biblemap.put("b48_1_21", new hinsuperobject(48,1,21,"इस के बाद मैं सूरिया और किलकिया के देशों में आया।"));
hinsuperobject.biblemap.put("b48_1_22", new hinsuperobject(48,1,22,"परन्तु यहूदिया की कलीसियाओं ने जो मसीह में थी, मेरा मुह तो कभी नहीं देखा था।"));
hinsuperobject.biblemap.put("b48_1_23", new hinsuperobject(48,1,23,"परन्तु यही सुना करती थीं, कि जो हमें पहिले सताता था, वह अब उसी धर्म का सुसमाचार सुनाता है, जिसे पहिले नाश करता था।"));
hinsuperobject.biblemap.put("b48_1_24", new hinsuperobject(48,1,24,"और मेरे विषय में परमेश्वर की महिमा करती थीं।।"));
hinsuperobject.biblemap.put("b48_2_1", new hinsuperobject(48,2,1,"चौदह वर्ष के बाद मैं बरनबास के साथ यरूशलेम को गया और तितुस को भी साथ ले गया।"));
hinsuperobject.biblemap.put("b48_2_2", new hinsuperobject(48,2,2,"और मेरा जाना ईश्वरीय प्रकाश के अनुसार हुआ: और जो सुसमाचार मैं अन्यजातियों में प्रचार करता हूं, उस को मैं ने उन्हें बता दिया, पर एकान्त में उन्हीं को जो बड़े समझे जाते थे, ताकि ऐसा न हो, कि मेरी इस समय की, या अगली दौड़ धूप व्यर्थ ठहरे।"));
hinsuperobject.biblemap.put("b48_2_3", new hinsuperobject(48,2,3,"परन्तु तितुस भी जो मेरे साथ था और जो यूनानी है; खतना कराने के लिये विवश नहीं किया गया।"));
hinsuperobject.biblemap.put("b48_2_4", new hinsuperobject(48,2,4,"और यह उन झूठे भाइयों के कारण हुआ, जो चोरी से घुस आए थे, कि उस स्वतंत्राता का जो मसीह यीशु में हमें मिली है, भेद लेकर हमें दास बनाएं।"));
hinsuperobject.biblemap.put("b48_2_5", new hinsuperobject(48,2,5,"उन के आधीन होना हम ने एक घड़ी भर न माना, इसलिये कि सुसमाचार की सच्चाई तुम में बनी रहे।"));
hinsuperobject.biblemap.put("b48_2_6", new hinsuperobject(48,2,6,"फिर जो लोग कुछ समझे जाते थे (वे चाहे कैसे ही थे, मुझे इस से कुछ काम नहीं, परमेश्वर किसी का पक्षपात नहीं करता) उन से जो कुछ भी समझे जाते थे, मुझे कुछ भी नहीं प्राप्त हुआ।"));
hinsuperobject.biblemap.put("b48_2_7", new hinsuperobject(48,2,7,"परन्तु इसके विपरीत जब उन्हों ने देखा, कि जैसा खतना किए हुए लोगों के लिये सुसमाचार का काम पतरस को सौंपा गया वैसा ही खतनारहितों के लिये मुझे सुसमाचार सुनाना सौंपा गया।"));
hinsuperobject.biblemap.put("b48_2_8", new hinsuperobject(48,2,8,"(क्योंकि जिस ने पतरस से खतना किए हुओं में प्रेरिताई का कारर्य बड़े प्रभाव सहित करवाया, उसी ने मुझ से भी अन्यजातियों में प्रभावशाली कारर्य करवाया)"));
hinsuperobject.biblemap.put("b48_2_9", new hinsuperobject(48,2,9,"और जब उन्हों ने उस अनुग्रह को जो मुझे मिला था जान लिया, तो याकूब, और कैफा, और यूहन्ना ने जो कलीसिया के खम्भे समझे जाते थे, मुझ को और बरनबास को दहिना हाथ देकर संग कर लिया, कि हम अन्यजातियों के पास जाएं, और वे खतना किए हुओं के पास।"));
hinsuperobject.biblemap.put("b48_2_10", new hinsuperobject(48,2,10,"केवल यह कहा, कि हम कंगालों की सुधि लें, और इसी काम के करने का मैं आप भी यत्न कर रहा था।"));
hinsuperobject.biblemap.put("b48_2_11", new hinsuperobject(48,2,11,"पर जब कैफा अन्ताकिया में आया तो मैं ने उसके मुंह पर उसका साम्हना किया, क्योंकि वह दोषी ठहरा था।"));
hinsuperobject.biblemap.put("b48_2_12", new hinsuperobject(48,2,12,"इसलिये कि याकूब की ओर से कितने लोगों के आने से पहिले वह अन्यजातियों के साथ खाया करता था, परन्तु जब वे आए, तो खतना किए हुए लोगों के डर के मारे उन से हट गया और किनारा करने लगा।"));
hinsuperobject.biblemap.put("b48_2_13", new hinsuperobject(48,2,13,"और उसके साथ शेष यहूदियों ने भी कपट किया, यहां तक कि बरनबास भी उन के कपट में पड़ गया।"));
hinsuperobject.biblemap.put("b48_2_14", new hinsuperobject(48,2,14,"पर जब मैं ने देखा, कि वे सुसमाचार की सच्चाई पर सीधी चाल नहीं चलते, तो मैं ने सब के साम्हने कैफा से कहा; कि जब तू यहूदी होकर अन्यजातियों की नाई चलता है, और यहूदियों की नाईं नहीं तो तू अन्यजातियों को यहूदियों की नाईं चलने को क्यों कहता है?"));
hinsuperobject.biblemap.put("b48_2_15", new hinsuperobject(48,2,15,"हम जो जन्म के यहूदी हैं, और पापी अन्यजातियों में से नहीं।"));
hinsuperobject.biblemap.put("b48_2_16", new hinsuperobject(48,2,16,"तौभी यह जानकर कि मनुष्य व्यवस्था के कामों से नहीं, पर केवल यीशु मसीह पर विश्वास करने के द्वारा धर्मी ठहरता है, हम ने आप भी मसीह यीशु पर विश्वास किया, कि हम व्यवस्था के कामों से नहीं पर मसीह पर विश्वास करने से धर्मी ठहरें; इसलिये कि व्यवस्था के कामों से कोई प्राणी धर्मी न ठहरेगा।"));
hinsuperobject.biblemap.put("b48_2_17", new hinsuperobject(48,2,17,"हम जो मसीह में धर्मी ठहरना चाहते हैं, यदि आप ही पापी निकलें, तो क्या मसीह पाप का सेवक है? कदापि नहीं।"));
hinsuperobject.biblemap.put("b48_2_18", new hinsuperobject(48,2,18,"क्योंकि जो कुछ मैं ने गिरा दिया, यदि उसी को फिर बनाता हूं, तो अपने आप को अपराधी ठहराता हूं।"));
hinsuperobject.biblemap.put("b48_2_19", new hinsuperobject(48,2,19,"मैं जो व्यवसिाा के द्वारा व्यवस्था के लिये मर गया, कि परमेश्वर के लिये जीऊं।"));
hinsuperobject.biblemap.put("b48_2_20", new hinsuperobject(48,2,20,"मैं मसीह के साथ क्रूस पर चढ़ाया गया हूं, और अब मैं जीवित न रहा, पर मसीह मुझ में जीवित है: और मैं शरीर में अब जो जीवित हूं तो केवल उस विश्वास से जीवित हूं, जो परमेश्वर के पुत्रा पर है, जिस ने मुझ से प्रेम किया, और मेरे लिये अपने आप को दे दिया।"));
hinsuperobject.biblemap.put("b48_2_21", new hinsuperobject(48,2,21,"मैं परमेश्वर के अनुग्रह को व्यर्थ नहीं ठहराता, क्योंकि यदि व्यवस्था के द्वारा धार्मिकता होती, तो मसीह का मरना व्यर्थ होता।।"));
hinsuperobject.biblemap.put("b48_3_1", new hinsuperobject(48,3,1,"हे निर्बुद्धि गलतियों, किस ने तुम्हें मोह लिया? तुम्हारी तो मानों आंखों के साम्हने यीशु मसीह क्रूस पर दिखाया गया!"));
hinsuperobject.biblemap.put("b48_3_2", new hinsuperobject(48,3,2,"मैं तुम से केवल यह जानना चाहता हूं, कि तुम ने आत्मा को, क्या व्यवस्था के कामों से, या विश्वास के समाचार से पाया?"));
hinsuperobject.biblemap.put("b48_3_3", new hinsuperobject(48,3,3,"क्या तुम ऐसे निर्बुद्धि हो, कि आत्मा की रीति पर आरम्भ करके अब शरीर की रीति पर अन्त करोगे?"));
hinsuperobject.biblemap.put("b48_3_4", new hinsuperobject(48,3,4,"क्या तुम ने इतना दुख योंही उठाया? परन्तु कदाचित व्यर्थ नहीं।"));
hinsuperobject.biblemap.put("b48_3_5", new hinsuperobject(48,3,5,"सो जो तुम्हें आत्मा दान करता और तुम में सामर्थ के काम करता है, वह क्या व्यवस्था के कामों से या विश्वास के सुसमाचार से ऐसा करता है?"));
hinsuperobject.biblemap.put("b48_3_6", new hinsuperobject(48,3,6,"इब्राहीम ने तो परमेश्वर पर विश्वास किया और यह उसके लिये धार्मिकता गिनी गई।"));
hinsuperobject.biblemap.put("b48_3_7", new hinsuperobject(48,3,7,"तो यह जान लो, कि जो विश्वास करनेवाले हैं, वे ही इब्राहीम की सन्तान हैं।"));
hinsuperobject.biblemap.put("b48_3_8", new hinsuperobject(48,3,8,"और पवित्राशास्त्रा ने पहिले ही से यह जानकर, कि परमेश्वर अन्यजातियों को विश्वास से धर्मी ठहराएगा, पहिले हीे से इब्राहीम को यह सुसमाचार सुना दिया, कि तुझ में सब जातियां आशीष पाएंगी।"));
hinsuperobject.biblemap.put("b48_3_9", new hinsuperobject(48,3,9,"तो जो विश्वास करनेवाले हैं, वे विश्वासी इब्राहीम के साथ आशीष पाते हैं।"));
hinsuperobject.biblemap.put("b48_3_10", new hinsuperobject(48,3,10,"सो जितने लोग व्यवस्था के कामों पर भरोसा रखते हैं, वे सब स्त्राप के आधीन हैं, क्योंकि लिखा है, कि जो कोई व्यवस्था की पुस्तक में लिखी हुई सब बातों के करने में स्थिर नहीं रहता, वह स्त्रापित है।"));
hinsuperobject.biblemap.put("b48_3_11", new hinsuperobject(48,3,11,"पर यह बात प्रगट है, कि व्यवस्था के द्वारा परमेश्वर के यहां कोई धर्मी नहीं ठहरता क्योंकि धर्मी जन विश्वास से जीवित रहेगा।"));
hinsuperobject.biblemap.put("b48_3_12", new hinsuperobject(48,3,12,"पर व्यवस्था का विश्वास से कुछ सम्बन्ध नहीं; पर जो उन को मानेगा, वह उन के कारण जीवित रहेगा।"));
hinsuperobject.biblemap.put("b48_3_13", new hinsuperobject(48,3,13,"मसीह ने जो हमारे लिये स्त्रापित बना, हमें मोल लेकर व्यवस्था के स्त्राप से छुड़ाया क्योंकि लिखा है, जो कोई काठ पर लटकाया जाता है वह स्त्रापित है।"));
hinsuperobject.biblemap.put("b48_3_14", new hinsuperobject(48,3,14,"यह इसलिये हुआ, कि इब्राहिम की आशीष मसीह यीशु में अन्यजातियों तक पंहुचे, और हम विश्वास के द्वारा उस आत्मा को प्राप्त करें, जिस की प्रतिज्ञा हुई है।।"));
hinsuperobject.biblemap.put("b48_3_15", new hinsuperobject(48,3,15,"हे भाइयों, मैं मनुष्य की रीति पर कहता हूं, कि मनुष्य की वाचा भी जो पक्की हो जाती है, तो न कोई उसे टालता है और न उस में कुछ बढ़ाता है।"));
hinsuperobject.biblemap.put("b48_3_16", new hinsuperobject(48,3,16,"निदान, प्रतिज्ञाएं इब्राहीम को, और उसके वंश को दी गईं; वह यह नहीं कहता, कि वशों को; जेसे बहुतों के विषय में कहा, पर जैसे एक के विषय में कि तेरे वंश को: और वह मसीह है।"));
hinsuperobject.biblemap.put("b48_3_17", new hinsuperobject(48,3,17,"पर मैं यह कहता हूं की जो वाचा परमेश्वर ने पहिले से पक्की की थी, उस को व्यवस्था चार सौ तीस बरस के बाद आकर नहीं टाल देती, कि प्रतिज्ञा व्यर्थ ठहरे।"));
hinsuperobject.biblemap.put("b48_3_18", new hinsuperobject(48,3,18,"क्योंकि यदि मीरास व्यवस्था से मिली है, तो फिर प्रतिज्ञा से नहीं, परन्तु परमेश्वर ने इब्राहीम को प्रतिज्ञा के द्वारा दे दी है।"));
hinsuperobject.biblemap.put("b48_3_19", new hinsuperobject(48,3,19,"तब फिर व्यवस्था क्या रही? वह तो अपराधों के कारण बाद में दी गई, कि उस वंश के आने तक रहे, जिस को प्रतिज्ञा दी गई थी, और वह स्वर्गदूतों के द्वारा एक मध्यस्थ के हाथ ठहराई गई।"));
hinsuperobject.biblemap.put("b48_3_20", new hinsuperobject(48,3,20,"मध्यस्थ तो एक का नहीं होता, परन्तु परमेश्वर एक ही है।"));
hinsuperobject.biblemap.put("b48_3_21", new hinsuperobject(48,3,21,"तो क्या व्यवस्था परमेश्वर की प्रतिज्ञाओं के विरोध में है? कदापि न हो? क्योंकि यदि ऐसी व्यवस्था दी जाती जो जीवन दे सकती, तो सचमुच धार्मिकता व्यवस्था से होती।"));
hinsuperobject.biblemap.put("b48_3_22", new hinsuperobject(48,3,22,"परन्तु पवित्रा शास्त्रा ने सब को पाप के आधीन कर दिया, ताकि वह प्रतिज्ञा जिस का आधार यीशु मसीह पर विश्वास करना है, विश्वास करनेवालों के लिये पूरी हो जाए।।"));
hinsuperobject.biblemap.put("b48_3_23", new hinsuperobject(48,3,23,"पर विश्वास के आने से पहिले व्यवस्था की अधीनता में हमारी रखवाली होती थी, और उस विश्वास के आने तक जो प्रगट होनेवाला था, हम उसी के बन्धन में रहे।"));
hinsuperobject.biblemap.put("b48_3_24", new hinsuperobject(48,3,24,"इसलिये व्यवस्था मसीह तक पहुंचाने को हमारा शिक्षक हुई है, कि हम विश्वास से धर्मी ठहरें।"));
hinsuperobject.biblemap.put("b48_3_25", new hinsuperobject(48,3,25,"परन्तु जब विश्वास आ चुका, तो हम अब शिक्षक के आधीन न रहे।"));
hinsuperobject.biblemap.put("b48_3_26", new hinsuperobject(48,3,26,"क्योंकि तुम सब उस विश्वास करने के द्वारा जो मसीह यीशु पर है, परमेश्वर की सन्तान हो।"));
hinsuperobject.biblemap.put("b48_3_27", new hinsuperobject(48,3,27,"और तुम में से जितनों ने मसीह में बपतिस्मा लिया है उन्हों ने मसीह को पहिन लिया है।"));
hinsuperobject.biblemap.put("b48_3_28", new hinsuperobject(48,3,28,"अब न कोई यहूदी रहा और न यूनानी; न कोई दास, न स्वतंत्रा; न कोई नर, न नारी; क्योंकि तुम सब मसीह यीशु में एक हो।"));
hinsuperobject.biblemap.put("b48_3_29", new hinsuperobject(48,3,29,"और यदि तुम मसीह के हो, तो इब्राहीम के वंश और प्रतिज्ञा के अनुसार वारिस भी हो।।"));
hinsuperobject.biblemap.put("b48_4_1", new hinsuperobject(48,4,1,"मैं यह कहता हूं, कि वारिस जब तक बालक है, यद्यपि सब वस्तुओं का स्वामी है, तौभी उस में और दास में कुछ भेद नहीं।"));
hinsuperobject.biblemap.put("b48_4_2", new hinsuperobject(48,4,2,"परन्तु पिता के ठहराए हुए समय तक रक्षकों और भण्डारियों के वश में रहता है।"));
hinsuperobject.biblemap.put("b48_4_3", new hinsuperobject(48,4,3,"वैसे ही हम भी, जब बालक थे, तो संसार की आदि शिक्षा के वश में होकर दास बने हुए थे।"));
hinsuperobject.biblemap.put("b48_4_4", new hinsuperobject(48,4,4,"परन्तु जब समय पूरा हुआ, तो परमेश्वर ने अपने पुत्रा को भेजा, जो स्त्री से जन्मा, और व्यवस्था के आधीन उत्पन्न हुआ।"));
hinsuperobject.biblemap.put("b48_4_5", new hinsuperobject(48,4,5,"ताकि व्यवस्था के आधीनों को मोल लेकर छुड़ा ले, और हम को लेपालक होने का पद मिले।"));
hinsuperobject.biblemap.put("b48_4_6", new hinsuperobject(48,4,6,"और तुम जो पुत्रा हो, इसलिये परमेश्वर ने अपने पुत्रा के आत्मा को, जो हे अब्बा, हे पिता कहकर पुकारता है, हमारे हृदय में भेजा है।"));
hinsuperobject.biblemap.put("b48_4_7", new hinsuperobject(48,4,7,"इसलिये तू अब दास नहीं, परन्तु पुत्रा है; और जब पुत्रा हुआ, तो परमेश्वर के द्वारा वारिस भी हुआ।"));
hinsuperobject.biblemap.put("b48_4_8", new hinsuperobject(48,4,8,"भला, तक तो तुम परमेश्वर को न जानकर उनके दास थे जो स्वभाव से परमेश्वर नहीं।"));
hinsuperobject.biblemap.put("b48_4_9", new hinsuperobject(48,4,9,"पर अब जो तुम ने परमेश्वर को पहचान लिया बरन परमेश्वर ने तुम केा पहचाना, तो उन निर्बल और निकम्मी आदि- शिक्षा की बातों की ओर क्यों फिरते हो, जिन के तुम दोबारा दास होना चाहते हो?"));
hinsuperobject.biblemap.put("b48_4_10", new hinsuperobject(48,4,10,"तुम दिनों और महीनों और नियत समयों और वर्षों को मानते हो।"));
hinsuperobject.biblemap.put("b48_4_11", new hinsuperobject(48,4,11,"मैं तुम्हारे विषय में डरता हूं, कहीं ऐसा न हो, कि जो परिश्रम मैं नं तुम्हारे लिये किया है व्यर्थ ठहरे।।"));
hinsuperobject.biblemap.put("b48_4_12", new hinsuperobject(48,4,12,"हे भाइयों, मैं तुम से बिनती करता हूं, तुम मेरे समान हो जाओ: क्योंकि मैं भी तुम्हारे समान हुआ हूं; तुम ने मेरा कुछ बिगाड़ा नहीं।"));
hinsuperobject.biblemap.put("b48_4_13", new hinsuperobject(48,4,13,"पर तुम जानते हो, कि पहिले पहिल मैं ने शरीर की निर्बलता के कारण तुम्हें सुसमाचार सुनाया।"));
hinsuperobject.biblemap.put("b48_4_14", new hinsuperobject(48,4,14,"और तुम ने मेरी शारीरिक दशा को जो तुम्हारी परीक्षा का कारण थी, तुच्छ न जाना; न उस ने घृणा की; और परमेश्वर के दूत बरन मसीह के समान मुझे ग्रहण किया।"));
hinsuperobject.biblemap.put("b48_4_15", new hinsuperobject(48,4,15,"तो वह तुम्हारा आनन्द मनाना कहां गया? मैं तुम्हारा गवाह हूं, कि यदि हो सकता, तो तुम अपनी आंखें भी निकालकर मुझे दे देते।"));
hinsuperobject.biblemap.put("b48_4_16", new hinsuperobject(48,4,16,"तो क्या तुम से सच बोलने के कारण मैं तुम्हारा बैरी हो गया हूं।"));
hinsuperobject.biblemap.put("b48_4_17", new hinsuperobject(48,4,17,"वे तुम्हें मित्रा बनाना तो चाहते हैं, पर भली मनसा से नहीं; बरन तुम्हें अलग करना चाहते हैं, कि तुम उन्हीं को मित्रा बना लो।"));
hinsuperobject.biblemap.put("b48_4_18", new hinsuperobject(48,4,18,"पर यह भी अच्छा है, कि भली बात में हर समय मित्रा बनाने का यत्न किया जाए, न केवल उसी समय, कि जब मैं तुम्हारे साथ रहता हूं।"));
hinsuperobject.biblemap.put("b48_4_19", new hinsuperobject(48,4,19,"हे मेरे बालकों, जब तक तुम में मसीह का रूप न बन जाए, तब तक मैं तुम्हारे लिये फिर जच्चा की सी पीड़ाएं सहता हूं।"));
hinsuperobject.biblemap.put("b48_4_20", new hinsuperobject(48,4,20,"इच्छा तो यह होती है, कि अब तुम्हारे पास आकर और ही प्रकार से बोलू, क्योंकि तुम्हारे विषय में मुझे सन्देह है।।"));
hinsuperobject.biblemap.put("b48_4_21", new hinsuperobject(48,4,21,"तुम जो व्यवस्था के आधीन होना चाहते हो, मुझ से कहो, क्या तुम व्यवस्था की नहीं सुनते?"));
hinsuperobject.biblemap.put("b48_4_22", new hinsuperobject(48,4,22,"यह लिखा है, कि इब्राहीम के दो पुत्रा हुए; एक दासी से, और एक स्वतंत्रा स्त्री से।"));
hinsuperobject.biblemap.put("b48_4_23", new hinsuperobject(48,4,23,"परन्तु जो दासी से हुआ, वह शारीरिक रीति से जन्मा, और जो स्वतंत्रा स्त्री से हुआ, वह प्रतिज्ञा के अनुसार जन्मा।"));
hinsuperobject.biblemap.put("b48_4_24", new hinsuperobject(48,4,24,"इन बातों में दृष्टान्त है, ये स्त्रियां मानों दो वाचाएं हैं, एक तो सीना पहाड़ की जिस से दास ही उत्पन्न होते हैं; और वह हाजिरा है।"));
hinsuperobject.biblemap.put("b48_4_25", new hinsuperobject(48,4,25,"और हाजिरा मानो अरब का सीना पहाड़ है, और आधुनिक यरूशलेम उसे तुल्य है, क्योंकि वह अपने बालकों समेत दासत्व में है।"));
hinsuperobject.biblemap.put("b48_4_26", new hinsuperobject(48,4,26,"पर ऊपर की यरूशलेम स्वतंत्रा है, और वह हमारी माता है।"));
hinsuperobject.biblemap.put("b48_4_27", new hinsuperobject(48,4,27,"क्योंकि लिखा है, कि हे बांझ, तू जो नहीं जनती आनन्द कर, तु जिस को पीड़ाएं नहीं उठतीं गला खोलकर जय जयकार कर, क्योंकि त्यागी हुई की सन्तान सुहागिन की सन्तान से भी अधिक है।"));
hinsuperobject.biblemap.put("b48_4_28", new hinsuperobject(48,4,28,"हे भाइयो, हम इसहाक की नाईं प्रतिज्ञा की सन्तान हैं।"));
hinsuperobject.biblemap.put("b48_4_29", new hinsuperobject(48,4,29,"और जैसा उस समय शरीर के अनुसार जन्मा हुआ आत्मा के अनुसार जन्मे हुए को सताता था, वैसा ही अब भी होता है।"));
hinsuperobject.biblemap.put("b48_4_30", new hinsuperobject(48,4,30,"परन्तु पवित्रा शास्त्रा क्या कहता है? दासी और उसके पुत्रा को निकाल दे, क्योंकि दासी का पुत्रा स्वतंत्रा स्त्री के पुत्रा के साथ उत्तराधिकारी नहीं होगा।"));
hinsuperobject.biblemap.put("b48_4_31", new hinsuperobject(48,4,31,"इसलिये हे भाइयों, हम दासी के नहीं परन्तु स्वतंत्रा स्त्री के सन्तान हैं।"));
hinsuperobject.biblemap.put("b48_5_1", new hinsuperobject(48,5,1,"मसीह ने स्वतंत्राता के लिये हमें स्वतंत्रा किया है; सो इसी में स्थिर रहो, और दासत्व के जूए में फिर से न जुतो।।"));
hinsuperobject.biblemap.put("b48_5_2", new hinsuperobject(48,5,2,"देखो, मैं पौलुस तुम से कहता हूं, कि यदि खतना कराओगे, तो मसीह से तुम्हें कुछ लाभ न होगा।"));
hinsuperobject.biblemap.put("b48_5_3", new hinsuperobject(48,5,3,"फिर भी मैं हर एक खतना करानेवाले को जताए देता हूं, कि उसे सारी व्यवस्था माननी पड़ेगी।"));
hinsuperobject.biblemap.put("b48_5_4", new hinsuperobject(48,5,4,"तुम जो व्यवस्था के द्वारा धर्मी ठहरना चाहते हो, मसीह से अलग और अनुग्रह से गिर गए हो।"));
hinsuperobject.biblemap.put("b48_5_5", new hinsuperobject(48,5,5,"क्योंकि आत्मा के कारण, हम विश्वास से, आशा की हुई धार्मिकता की बाट जोहते हैं।"));
hinsuperobject.biblemap.put("b48_5_6", new hinsuperobject(48,5,6,"और मसीह यीशु में न खतना, न खतनारहित कुछ काम का है, परन्तु केवल विश्वास का जो प्रेम के द्वारा प्रभाव करता है।"));
hinsuperobject.biblemap.put("b48_5_7", new hinsuperobject(48,5,7,"तुम तो भली भांति दौड रहे थे, अब किस ने तुम्हें रोक दिया, कि सत्य को न मानो।"));
hinsuperobject.biblemap.put("b48_5_8", new hinsuperobject(48,5,8,"ऐसी सीख तुम्हारे बुलानेवाले की ओर से नहीं।"));
hinsuperobject.biblemap.put("b48_5_9", new hinsuperobject(48,5,9,"थोड़ा सा खमीर सारे गूंधे हुए आटे को खमीर कर डालता है।"));
hinsuperobject.biblemap.put("b48_5_10", new hinsuperobject(48,5,10,"मैं प्रभु पर तुम्हारे विषय में भरोसा रखताह हूं, कि तुम्हारा कोई दूसरा विचार न होगा; परन्तु जो तुम्हें घबरा देता है, वह कोई क्यों न हो दण्ड पाएगा।"));
hinsuperobject.biblemap.put("b48_5_11", new hinsuperobject(48,5,11,"परन्तु हे भाइयो, यदि मैं अब तक खतना का प्रचार करता हूं, तो क्यों अब तक सताया जाता हूं; फिर तो क्रूस की ठोकर जाती रही।"));
hinsuperobject.biblemap.put("b48_5_12", new hinsuperobject(48,5,12,"भला होता, कि जो तुम्हें डांवाडोल करते हैं, वे काट डाले जाते!"));
hinsuperobject.biblemap.put("b48_5_13", new hinsuperobject(48,5,13,"हे भाइयों, तुम स्वतंत्रा होने के लिये बुलाए गए हो परन्तु ऐसा न हो, कि यह स्वतंत्राता शारीरिक कामों के लिये अवसर बने, बरन प्र्रेम से एक दूसरे के दास बनो।"));
hinsuperobject.biblemap.put("b48_5_14", new hinsuperobject(48,5,14,"क्योंकि सारी व्यवस्था इस एक ही बात में पूरी हो जाती है, कि तू अपने पड़ोसी से अपने समान प्रेम रख।"));
hinsuperobject.biblemap.put("b48_5_15", new hinsuperobject(48,5,15,"पर यदि तुम एक दूसरे को दांत से काटते और फाड़ खाते हो, तो चौकस रहो, कि एक दूसरे का सत्यानाश न कर दो।।"));
hinsuperobject.biblemap.put("b48_5_16", new hinsuperobject(48,5,16,"पर मैं कहता हूं, आत्मा के अनुसार चलो, तो तुम शरीर की लालसा किसी रीति से पूरी न करोगे।"));
hinsuperobject.biblemap.put("b48_5_17", new hinsuperobject(48,5,17,"क्योंकि शरीर आत्मा के विरोध में लालसा करती है, और ये एक दूसरे के विरोधी हैं; इसलिये कि जो तुम करना चाहते हो वह न करने पाओ।"));
hinsuperobject.biblemap.put("b48_5_18", new hinsuperobject(48,5,18,"और यदि तुम आत्मा के चलाए चलते हो तो व्यवस्था के आधीन न रहे।"));
hinsuperobject.biblemap.put("b48_5_19", new hinsuperobject(48,5,19,"शरीर के काम तो प्रगट हैं, अर्थात् व्यभिचार, गन्दे काम, लुचपन।"));
hinsuperobject.biblemap.put("b48_5_20", new hinsuperobject(48,5,20,"मूत्ति पूजा, टोना, बैर, झगड़ा, ईर्ष्या, क्रोध, विरोध, फूट, विधर्म।"));
hinsuperobject.biblemap.put("b48_5_21", new hinsuperobject(48,5,21,"डाह, मलवालापन, लीलाक्रीड़ा, और इन के ऐसे और और काम हैं, इन के विषय में मैं तुम को पहिले से कह देता हूं जैसा पहिले कह भी चुका हूं, कि ऐसे ऐसे काम करनेवाले परमेश्वर के राज्य के वारिस न होंगे।"));
hinsuperobject.biblemap.put("b48_5_22", new hinsuperobject(48,5,22,"पर आत्मा का फल प्रेम, आनन्द, मेल, धीरज,"));
hinsuperobject.biblemap.put("b48_5_23", new hinsuperobject(48,5,23,"और कृपा, भालाई, विश्वास, नम्रता, और संयम हैं; ऐसे ऐसे कामों के विरोध में कोई व्यवस्था नहीं।"));
hinsuperobject.biblemap.put("b48_5_24", new hinsuperobject(48,5,24,"और जो मसीह यीशु के हैं, उन्हों ने शरीर को उस की लालसाओं और अभिलाषों समेत क्रूस पर चढ़ा दिया है।।"));
hinsuperobject.biblemap.put("b48_5_25", new hinsuperobject(48,5,25,"यदि हम आत्मा के द्वारा जीवित हैं, तो आत्मा के अनुसार चलें भी।"));
hinsuperobject.biblemap.put("b48_5_26", new hinsuperobject(48,5,26,"हम घमण्डी होकर न एक दूसरे को छेड़ें, और न ऐ दूसरे से डाह करें।"));
hinsuperobject.biblemap.put("b48_6_1", new hinsuperobject(48,6,1,"हे भाइयों, यदि कोई मनुष्य किसी अपराध में पकड़ा जाए, तो तुम जो आत्मिक जो, नम्रता के साथ ऐसे को संभालो, और अपनी भी चौकसी रखो, कि तुम भी परीक्षा में न पड़ो।"));
hinsuperobject.biblemap.put("b48_6_2", new hinsuperobject(48,6,2,"तुम एक दूसरे के भार उठाओ, और इस प्रकार मसीह की व्यवस्था को पूरी करो।"));
hinsuperobject.biblemap.put("b48_6_3", new hinsuperobject(48,6,3,"क्योंकि यदि कोई कुछ न होने पर भी अपने आप को कुछ समझता है, तो अपने आप को धोखा देता है।"));
hinsuperobject.biblemap.put("b48_6_4", new hinsuperobject(48,6,4,"पर हर एक अपने ही काम को जांच ले, और तक दूसरे के विषय में नहीं परन्तु अपने ही विषय में उसको घमण्ड करने का अवसर होगा।"));
hinsuperobject.biblemap.put("b48_6_5", new hinsuperobject(48,6,5,"क्योंकि हर एक व्यक्ति अपना ही बोझ उठाएगा।।"));
hinsuperobject.biblemap.put("b48_6_6", new hinsuperobject(48,6,6,"जो वचन की शिक्षा पाता है, वह सब अच्छी वस्तुओं में सिखानेवाले को भागी करे।"));
hinsuperobject.biblemap.put("b48_6_7", new hinsuperobject(48,6,7,"धोखा न खाओ, परमेश्वर ठट्ठों में नहीं उड़ाया जाता, क्योंकि मनुष्य जो कुछ बोता है, वही काटेगा।"));
hinsuperobject.biblemap.put("b48_6_8", new hinsuperobject(48,6,8,"क्योंकि जो अपने शरीर के लिये बोता है, वह शरीर के द्वारा विनाश की कटनी काटेगा; और जो आत्मा के लिये बोता है, वह आत्मा के द्वारा अनन्त जीवन की कटनी काटेगा।"));
hinsuperobject.biblemap.put("b48_6_9", new hinsuperobject(48,6,9,"हम भले काम करने में हियाव न छोड़े, क्योंकि यदि हम ढीले न हांे, तो ठीक समय पर कटनी काटेंगे।"));
hinsuperobject.biblemap.put("b48_6_10", new hinsuperobject(48,6,10,"इसलिये जहां तक अवसर मिले हम सब के साथ भलाई करें; विशेष करके विश्वासी भाइयों के साथ।।"));
hinsuperobject.biblemap.put("b48_6_11", new hinsuperobject(48,6,11,"देखो, मैं ने कैसे बड़े बड़े अक्षरों में तुम को अपने हाथ से लिखा है।"));
hinsuperobject.biblemap.put("b48_6_12", new hinsuperobject(48,6,12,"जितने लोग शरीरिक दिखव चाहते हैं वे तुम्हारे खतना करवाने के लिये दबाव देते हैं, केवल इसलिये कि वे मसीह के क्रूस के कारण सताए न जाएं।"));
hinsuperobject.biblemap.put("b48_6_13", new hinsuperobject(48,6,13,"क्योंकि खतना करानेवाले आप तो, व्यवस्था पर नहीं चलते, पर तुम्हारा खतना कराना इसलिये चाहते हैं, कि तुम्हारी शारीरिक दशा पर घमण्ड करें।"));
hinsuperobject.biblemap.put("b48_6_14", new hinsuperobject(48,6,14,"पर ऐसा न हो, कि मैं और किसी बात का घमण्ड करूं, केवल हमारे प्रभु यीशु मसीह के क्रूस का जिस के द्वारा संसार मेरी दृष्टि में और मैं संसार की दृष्टि में क्रूस पर चढ़ाया गया हूं।"));
hinsuperobject.biblemap.put("b48_6_15", new hinsuperobject(48,6,15,"क्योंकि न खतना, और न खतनारहित कुछ है, परन्तु नई सृष्टि।"));
hinsuperobject.biblemap.put("b48_6_16", new hinsuperobject(48,6,16,"और जितने इस नियम पर चलेंगे उन पर, और परमेश्वर के इस्त्राएल पर, शान्ति और दया होती रहे।।"));
hinsuperobject.biblemap.put("b48_6_17", new hinsuperobject(48,6,17,"आगे को कोई मुझे दुख न दे, क्योंकि मैं यीशु के दागों को अपनी देह में लिये फिरता हूं।।"));
hinsuperobject.biblemap.put("b48_6_18", new hinsuperobject(48,6,18,"हे भाइयो, हमारे प्रभु यीशु मसीह का अनुग्रह तुम्हारी आत्मा के साथ रहे। आमीन।।"));

}

}