package hin.bible;

public class hinbible59 {

public hinbible59() {

putmapbible59();

}

private void putmapbible59() {

hinsuperobject.biblemap.put("b59_1_1", new hinsuperobject(59,1,1,"परमेश्वर के और प्रभु यीशु मसीह के दास याकूब की ओर से उन बारहों गोत्रों को जो तित्तर बित्तर होकर रहते हैं नमस्कार पहुंचे।।"));
hinsuperobject.biblemap.put("b59_1_2", new hinsuperobject(59,1,2,"हे मेरे भाइयों, जब तुम नाना प्रकार की परीक्षाओं में पड़ो"));
hinsuperobject.biblemap.put("b59_1_3", new hinsuperobject(59,1,3,"तो इसे पूरे आनन्द की बात समझो, यह जानकर, कि तुम्हारे विश्वास के परखे जाने से धीरज उत्पन्न होता है।"));
hinsuperobject.biblemap.put("b59_1_4", new hinsuperobject(59,1,4,"पर धीरज को अपना पूरा काम करने दो, कि तुम पूरे और सिद्ध हो जाओ और तुम में किसी बात की घटी न रहे।।"));
hinsuperobject.biblemap.put("b59_1_5", new hinsuperobject(59,1,5,"पर यदि तुम में से किसी को बुद्धि की घटी हो, तो परमेश्वर से मांगे, जो बिना उलाहना दिए सब को उदारता से देता है; और उस को दी जाएगी।"));
hinsuperobject.biblemap.put("b59_1_6", new hinsuperobject(59,1,6,"पर विश्वास से मांगे, और कुछ सन्देह न करे; क्योंकि सन्देह करनेवाला समुद्र की लहर के समान है जो हवा से बहती और उछलती है।"));
hinsuperobject.biblemap.put("b59_1_7", new hinsuperobject(59,1,7,"ऐसा मनुष्य यह न समझे, कि मुझे प्रभु से कुछ मिलेगा।"));
hinsuperobject.biblemap.put("b59_1_8", new hinsuperobject(59,1,8,"वह व्यक्ति दुचित्ता है, और अपनी सारी बातों में चंचल है।।"));
hinsuperobject.biblemap.put("b59_1_9", new hinsuperobject(59,1,9,"दीन भाई अपने ऊंचे पद पर घमण्ड करे।"));
hinsuperobject.biblemap.put("b59_1_10", new hinsuperobject(59,1,10,"और धनवान अपनी नीच दशा पर: क्योंकि वह घास के फूल की नाई जाता रहेगा।"));
hinsuperobject.biblemap.put("b59_1_11", new hinsuperobject(59,1,11,"क्योंकि सूर्य उदय होते ही कड़ी धूप पड़ती है और घास को सुखा देती है, और उसका फूल झड़ जाता है, और उस की शोभा जाती रहती है; उसी प्रकार धनवान भी अपने मार्ग पर चलते चलते धूल में मिल जाएगा।"));
hinsuperobject.biblemap.put("b59_1_12", new hinsuperobject(59,1,12,"धन्य है वह मनुष्य, जो परीक्षा में स्थिर रहता है; क्योंकि वह खरा निकलकर जीवन का वह मुकुट पाएगा, जिस की प्रतिज्ञा प्रभु ने अपने प्रेम करनेवालों को दी है।"));
hinsuperobject.biblemap.put("b59_1_13", new hinsuperobject(59,1,13,"जब किसी ही परीक्षा हो, तो वह यह न कहे, कि मेरी परीक्षा परमेश्वर की ओर से होती है; क्योंकि न तो बुरी बातों से परमेश्वर की परीक्षा हो सकती है, और न वही किसी की परीक्षा आप करता है।"));
hinsuperobject.biblemap.put("b59_1_14", new hinsuperobject(59,1,14,"परन्तु प्रत्येक व्यक्ति अपनी ही अभिलाषा में खिंचकर, और फंसकर परीक्षा में पड़ता है।"));
hinsuperobject.biblemap.put("b59_1_15", new hinsuperobject(59,1,15,"फिर अभिलाषा गर्भवती होकर पाप को जनता है और पाप बढ़ जाता है तो मृत्यु को उत्पन्न करता है।"));
hinsuperobject.biblemap.put("b59_1_16", new hinsuperobject(59,1,16,"हे मेरे प्रिय भाइयों, धोखा न खाओ।"));
hinsuperobject.biblemap.put("b59_1_17", new hinsuperobject(59,1,17,"क्योंकि हर एक अच्छा वरदान और हर एक उत्तम दान ऊपर ही से है, और ज्योतियों के पिता की ओर से मिलता है, जिस में न तो कोई परिवर्तन हो सकता है, ओर न अदल बदल के कारण उस पर छाया पड़ती है।"));
hinsuperobject.biblemap.put("b59_1_18", new hinsuperobject(59,1,18,"उस ने अपनी ही इच्छा से हमें सत्य के वचन के द्वारा उत्पन्न किया, ताकि हम उस की सृष्टि की हुई वस्तुओं में से एक प्रकार के प्रथम फल हों।।"));
hinsuperobject.biblemap.put("b59_1_19", new hinsuperobject(59,1,19,"हे मेरे प्रिय भाइयो, यह बात तुम जानते हो: इसलिये हर एक मनुष्य सुनने के लिये तत्पर और बोलने में धीरा और क्रोध में धीमा हो।"));
hinsuperobject.biblemap.put("b59_1_20", new hinsuperobject(59,1,20,"क्योंकि मनुष्य का क्रोध परमेश्वर के धर्म का निर्वाह नहीं कर सकता है।"));
hinsuperobject.biblemap.put("b59_1_21", new hinsuperobject(59,1,21,"इसलिये सारी मलिनता और बैर भाव की बढ़ती को दूर करके, उस वचन को नम्रता से ग्रहण कर लो, जो हृदय में बोया गया और जो तुम्हारे प्राणों का उद्धार कर सकता है।"));
hinsuperobject.biblemap.put("b59_1_22", new hinsuperobject(59,1,22,"परन्तु वचन पर चलनेवाले बनो, और केवल सुननेवाले ही नहीं जो अपने आप को धोखा देते हैं।"));
hinsuperobject.biblemap.put("b59_1_23", new hinsuperobject(59,1,23,"क्योंकि जो कोई वचन का सुननेवाला हो, और उस पर चलनेवाला न हो, तो वह उस मनुष्य के समान है जो अपना स्वाभाविक मुंह दर्पण में देखता है।"));
hinsuperobject.biblemap.put("b59_1_24", new hinsuperobject(59,1,24,"इसलिये कि वह अपने आप को देखकर चला जाता, और तुरन्त भूल जाता है कि मैं कैसा था।"));
hinsuperobject.biblemap.put("b59_1_25", new hinsuperobject(59,1,25,"पर जो व्यक्ति स्वतंत्राता की सिद्ध व्यवस्था पर ध्यान करता रहता है, वह अपने काम में इसलिये आशीष पाएगा कि सुनकर नहीं, पर वैसा ही काम करता है।"));
hinsuperobject.biblemap.put("b59_1_26", new hinsuperobject(59,1,26,"यदि कोई अपने आप को भक्त समझे, और अपनी जीभ पर लगाम न दे, पर अपने हृदय को धोखा दे, तो उस की भक्ति व्यर्थ है।"));
hinsuperobject.biblemap.put("b59_1_27", new hinsuperobject(59,1,27,"हमारे परमेश्वर और पिता के निकट शुद्ध और निर्मल भक्ति यह है, कि अनाथों ओर विधवाओं के क्लेश में उन की सुधि लें, और अपने आप को संसार से निष्कलंक रखें।।"));
hinsuperobject.biblemap.put("b59_2_1", new hinsuperobject(59,2,1,"हे मेरे भाइयों, हमारे महिमायुक्त प्रभु यीशु मसीह का विश्वास तुम में पक्षपात के साथ न हो।"));
hinsuperobject.biblemap.put("b59_2_2", new hinsuperobject(59,2,2,"क्योंकि यदि एक पुरूष सोने के छल्ले और सुन्दर वस्त्रा पहिने हुए तुम्हारी सभा में आए और एक कंगाल भी मैले कुचैले कपड़े पहिने हुए आए।"));
hinsuperobject.biblemap.put("b59_2_3", new hinsuperobject(59,2,3,"और तुम उस सुन्दर वस्त्रावाले का मुंह देखकर कहो कि तू वहां अच्छी जगह बैठ; और उस कंगाल से कहो, कि तू यहां खड़ा रह, या मेरे पांव की पीढ़ी के पास बैठ।"));
hinsuperobject.biblemap.put("b59_2_4", new hinsuperobject(59,2,4,"तो क्या तुम ने आपस में भेद भाव न किया और कुविचार से न्याय करनेवाले न ठहरे?"));
hinsuperobject.biblemap.put("b59_2_5", new hinsuperobject(59,2,5,"हे मेरे प्रिय भाइयों सुनो; क्या परमेश्वर ने इस जगत के कंगालों को नहीं चुना कि विश्वास में धर्मी, और उस राज्य के अधिकारी हों, जिस की प्रतिज्ञा उस ने उन से की है जो उस से प्रेम रखते हैं?"));
hinsuperobject.biblemap.put("b59_2_6", new hinsuperobject(59,2,6,"पर तुम ने उस कंगाल का अपमान किया: क्या धनी लोग तुम पर अत्याचार नहीं करते और क्या वे ही तुम्हें कचहरियों में घसीट घसीट कर नहीं ले जाते?"));
hinsuperobject.biblemap.put("b59_2_7", new hinsuperobject(59,2,7,"क्या वे उस उत्तम नाम की निन्दा नहीं करते जिस के तुम कहलाए जाते हो?"));
hinsuperobject.biblemap.put("b59_2_8", new hinsuperobject(59,2,8,"तौभी यदि तुम पवित्रा शास्त्रा के इस वचन के अनुसार, कि तू अपने पड़ोसी से अपने समान प्रेम रख, सचमुच उस राज्य व्यवस्था को पूरी करते हो, तो अच्छा करते हो।"));
hinsuperobject.biblemap.put("b59_2_9", new hinsuperobject(59,2,9,"पर यदि तुम पक्षपात करते हो, तो पाप करते हो; और व्यवस्था तुम्हें अपराधी ठहराती है।"));
hinsuperobject.biblemap.put("b59_2_10", new hinsuperobject(59,2,10,"क्योंकि जो कोई सारी व्यवस्था का पालन करता है परन्तु एक ही बात में चूक जाए तो वह सब बातों मे दोषी ठहरा।"));
hinsuperobject.biblemap.put("b59_2_11", new hinsuperobject(59,2,11,"इसलिये कि जिस ने यह कहा, कि तू व्यभिचार न करना उसी ने यह भी कहा, कि तू हत्या न करना इसलिये यदि तू ने व्यभिचार तो नहीं किया, पर हत्या की तौभी तू व्यवस्था का उलंघन करने वाला ठहरा।"));
hinsuperobject.biblemap.put("b59_2_12", new hinsuperobject(59,2,12,"तुम उन लोगों की नाई वचन बोलो, और काम भी करो, जिन का न्याय स्वतंत्राता की व्यवस्था के अनुसार होगा।"));
hinsuperobject.biblemap.put("b59_2_13", new hinsuperobject(59,2,13,"क्योंकि जिस ने दया नहीं की, उसका न्याय बिना दया के होगा: दया न्याय पर जयवन्त होती है।।"));
hinsuperobject.biblemap.put("b59_2_14", new hinsuperobject(59,2,14,"हे मेरे भाइयों, यदि कोई कहे कि मुझे विश्वास है पर वह कर्म न करता हो, तो उस से क्या लाभ? क्या ऐसा विश्वास कभी उसका उद्धार कर सकता है?"));
hinsuperobject.biblemap.put("b59_2_15", new hinsuperobject(59,2,15,"यदि कोई भाई या बहिन नगें उघाड़े हों, और उन्हें प्रति दिन भोजन की घटी हो।"));
hinsuperobject.biblemap.put("b59_2_16", new hinsuperobject(59,2,16,"और तुम में से कोई उन से कहे, कुशल से जाओ, तुम गरम रहो और तृप्त रहो; पर जो वस्तुएं देह के लिये आवश्यक हैं वह उन्हें न दे, तो क्या लाभ?"));
hinsuperobject.biblemap.put("b59_2_17", new hinsuperobject(59,2,17,"वैसे ही विश्वास भी, यदि कर्म सहित न हो तो अपने स्वभाव में मरा हुआ है।"));
hinsuperobject.biblemap.put("b59_2_18", new hinsuperobject(59,2,18,"बरन कोई कह सकता है कि तुझे विश्वास है, और मैं कर्म करता हूं: तू अपना विश्वास मुझे कर्म बिना तो दिखा; और मैं अपना विश्वास अपने कर्मों के द्वारा तुझे दिखाऊंगा।"));
hinsuperobject.biblemap.put("b59_2_19", new hinsuperobject(59,2,19,"तुझे विश्वास है कि एक ही परमेश्वर है: तू अच्छा करता है: दुष्टात्मा भी विश्वास रखते, और थरथराते हैं।"));
hinsuperobject.biblemap.put("b59_2_20", new hinsuperobject(59,2,20,"पर हे निकम्मे मनुष्य क्या तू यह भी नहीं जानता, कि कर्म बिना विश्वास व्यर्थ है?"));
hinsuperobject.biblemap.put("b59_2_21", new hinsuperobject(59,2,21,"जब हमारे पिता इब्राहीम ने अपने पुत्रा इसहाक को वेदी पर चढ़ाया, तो क्या वह कर्मो से धार्मिक न ठहरा था।"));
hinsuperobject.biblemap.put("b59_2_22", new hinsuperobject(59,2,22,"सो तू ने देख लिया कि विश्वास ने उस के कामों के साथ मिलकर प्रभाव डाला है और कर्मो से विश्वास सिद्ध हुआ।"));
hinsuperobject.biblemap.put("b59_2_23", new hinsuperobject(59,2,23,"और पवित्रा शास्त्रा का यह वचन पूरा हुआ, कि इब्राहीम ने परमेश्वर की प्रतीति की, और यह उसके लिये धर्म गिना गया, और वह परमेश्वर का मित्रा कहलाया।"));
hinsuperobject.biblemap.put("b59_2_24", new hinsuperobject(59,2,24,"सो तुम ने देख लिया कि मनुष्य केवल विश्वास से ही नहीं, बरन कर्मों से भी धर्मी ठहरता है।"));
hinsuperobject.biblemap.put("b59_2_25", new hinsuperobject(59,2,25,"वैसे ही राहाब वेश्या भी जब उस ने दूतों को अपने घर में उतारा, और दूसरे मार्ग से विदा किया, तो क्या कर्मों से धार्मिक न ठहरी?"));
hinsuperobject.biblemap.put("b59_2_26", new hinsuperobject(59,2,26,"निदान, जैसे देह आत्मा बिना मरी हुई है वैसा ही विश्वास भी कर्म बिना मरा हुआ है।।"));
hinsuperobject.biblemap.put("b59_3_1", new hinsuperobject(59,3,1,"हे मेरे भाइयों, तुम में से बहुत उपदेशक न बनें, क्योंकि जानते हो, कि हम उपदेशक और भी दोषी ठहरेंगे।"));
hinsuperobject.biblemap.put("b59_3_2", new hinsuperobject(59,3,2,"इसलिये कि हम सब बहुत बार चूक जाते हैं: जो कोई वचन में नहीं चूकता, वही तो सिद्ध मनुष्य है; और सारी देह पर भी लगाम लगा सकता है।"));
hinsuperobject.biblemap.put("b59_3_3", new hinsuperobject(59,3,3,"जब हम अपने वश में करने के लिये घोड़ों के मुंह में लगाम लगाते हैं, तो हम उन की सारी देह को भी फेर सकते हैं।"));
hinsuperobject.biblemap.put("b59_3_4", new hinsuperobject(59,3,4,"देखो, जहाज भी, यद्यपि ऐसे बड़े होते हैं, और प्रचण्ड वायु से चलाए जाते हैं, तौभी एक छोटी सी पतवार के द्वारा मांझी की इच्छा के अनुसार घुमाए जाते हैं।"));
hinsuperobject.biblemap.put("b59_3_5", new hinsuperobject(59,3,5,"वैसे ही जीभ भी एक छोटा सा अंग है और बड़ी बड़ी डींगे मारती है: देखो, थोड़ी सी आग से कितने बड़े बन में आग लग जाती है।"));
hinsuperobject.biblemap.put("b59_3_6", new hinsuperobject(59,3,6,"जीभ भी एक आग है: भी हमारे अंगों में अधर्म का एक लोक है और सारी देह पर कलंक लगाती है, और भवचक्र में आग लगा देती है और नरक कुण्ड की आग से जलती रहती है।"));
hinsuperobject.biblemap.put("b59_3_7", new hinsuperobject(59,3,7,"क्योंकि हर प्रकार के बन- पशु, पक्षी, और रेंगनेवाले जन्तु और जलचर तो मनुष्य जाति के वश में हो सकते हैं और हो भी गए हैं।"));
hinsuperobject.biblemap.put("b59_3_8", new hinsuperobject(59,3,8,"पर जीभ को मनुष्यों में से कोई वश में नहीं कर सकता; वह एक ऐसी बला है जो कभी रूकती ही नहीं; वह प्राण नाशक विष से भरी हुई है।"));
hinsuperobject.biblemap.put("b59_3_9", new hinsuperobject(59,3,9,"इसी से हम प्रभु और पिता की स्तुति करते हैं; और इसी से मनुष्यों को जो परमेश्वर के स्वरूप में उत्पन्न हुए हैं स्राप देते हैं।"));
hinsuperobject.biblemap.put("b59_3_10", new hinsuperobject(59,3,10,"एक ही मुंह से धन्यवाद और स्राप दोनों निकलते हैं।"));
hinsuperobject.biblemap.put("b59_3_11", new hinsuperobject(59,3,11,"हे मेरे भाइयों, ऐसा नही होना चाहिए।"));
hinsuperobject.biblemap.put("b59_3_12", new hinsuperobject(59,3,12,"क्या सोते के एक ही मुंह से मीठा और खारा जल दोनों निकलता है? हे मेरे भाइयों, क्या अंजीर के पेड़ में जैतून, या दाख की लता में अंजीर लग सकते हैं? वैसे ही खारे सोते से मीठा पानी नहीं निकल सकता।।"));
hinsuperobject.biblemap.put("b59_3_13", new hinsuperobject(59,3,13,"तुम में ज्ञानवान और समझदार कौन है? जो ऐसा हो वह अपने कामों को अच्छे चालचलन से उस नम्रता सहित प्रगट करे जो ज्ञान से उत्पन्न होती है।"));
hinsuperobject.biblemap.put("b59_3_14", new hinsuperobject(59,3,14,"पर यदि तुम अपने अपने मन में कड़वी डाह और विरोध रखते हो, तो सत्य के विरोध में घमण्ड न करना, और न तो झूठ बोलना।"));
hinsuperobject.biblemap.put("b59_3_15", new hinsuperobject(59,3,15,"यह ज्ञान वह नहीं, जो ऊपर से उतरता है बरन सांसारिक, और शारीरिक, और शैतानी है।"));
hinsuperobject.biblemap.put("b59_3_16", new hinsuperobject(59,3,16,"इसलिये कि जहां डाह और विरोध होता है, वहां बखेड़ा और हर प्रकार का दुष्कर्म भी होता है।"));
hinsuperobject.biblemap.put("b59_3_17", new hinsuperobject(59,3,17,"पर जो ज्ञान ऊपर से आता है वह पहिले तो पवित्रा होता है फिर मिलनसार, कोमल और मृदुभाव और दया, और अच्छे फलों से लदा हुआ और पक्षपात और कपट रहित होता है।"));
hinsuperobject.biblemap.put("b59_3_18", new hinsuperobject(59,3,18,"और मिलाप करानेवालों के लिये धार्मिकता का फल मेल- मिलाप के साथ बोया जाता है।।"));
hinsuperobject.biblemap.put("b59_4_1", new hinsuperobject(59,4,1,"तुम में लड़ाइयां और झगड़े कहां से आ गए? क्या उन सुख- विलासों से नहीं जो तुम्हारे अंगों में लड़ते- भिड़ते हैं?"));
hinsuperobject.biblemap.put("b59_4_2", new hinsuperobject(59,4,2,"तुम लालसा रखते हो, और तुम्हें मिलता नहीं; तुम हत्या और डाह करते हो, ओर कुछ प्राप्त नहीं कर सकते; तुम झगड़ते और लड़ते हो; तुम्हें इसलिये नहीं मिलता, कि मांगते नहीं।"));
hinsuperobject.biblemap.put("b59_4_3", new hinsuperobject(59,4,3,"तुम मांगते हो और पाते नहीं, इसलिये कि बुरी इच्छा से मांगते हो, ताकि अपने भोग विलास में उड़ा दो।"));
hinsuperobject.biblemap.put("b59_4_4", new hinsuperobject(59,4,4,"हे व्यभिचारिणियों, क्या तुम नहीं जानतीं, कि संसार से मित्राता करनी परमेश्वर से बैर करना है? सो जो कोई संसार का मित्रा होना चाहता है, वह अपने आप को परमेश्वर का बैरी बनाता है।"));
hinsuperobject.biblemap.put("b59_4_5", new hinsuperobject(59,4,5,"क्या तुम यह समझते हो, कि पवित्रा शास्त्रा व्यर्थ कहता है? जिस आत्मा को उस ने हमारे भीतर बसाया है, क्या वह ऐसी लालसा करता है, जिस का प्रतिफल डाह हो?"));
hinsuperobject.biblemap.put("b59_4_6", new hinsuperobject(59,4,6,"वह तो और भी अनुग्रह देता है; इस कारण यह लिखा है, कि परमेश्वर अभिमानियों से विरोध करता है, पर दीनों पर अनुग्रह करता है।"));
hinsuperobject.biblemap.put("b59_4_7", new hinsuperobject(59,4,7,"इसलिये परमेश्वर के आधीन हो जाओ; और शैतान का साम्हना करो, तो वह तुम्हारे पास से भाग निकलेगा।"));
hinsuperobject.biblemap.put("b59_4_8", new hinsuperobject(59,4,8,"परमेश्वर के निकट आओ, तो वह भी तुम्हारे निकट आएगा: हे पापियों, अपने हाथ शुद्ध करो; और हे दुचित्ते लोगों अपने हृदय को पवित्रा करो।"));
hinsuperobject.biblemap.put("b59_4_9", new hinsuperobject(59,4,9,"दुखी होओ, और शोक करा, और रोओ: तुम्हारी हंसी शोक से और तुम्हारा आनन्द उदासी से बदल जाए।"));
hinsuperobject.biblemap.put("b59_4_10", new hinsuperobject(59,4,10,"प्रभु के साम्हने दीन बनो, तो वह तुम्हें शिरोमणि बनाएगा।"));
hinsuperobject.biblemap.put("b59_4_11", new hinsuperobject(59,4,11,"हे भाइयों, एक दूसरे की बदनामी न करो, जो अपने भाई की बदनामी करता है, या भाई पर दोष लगाता है, वह व्यवस्था की बदनामी करता है, और व्यवस्था पर दोष लगाता है, तो तू व्यवस्था पर चलनेवाला नहीं, पर उस पर हाकिम ठहरा।"));
hinsuperobject.biblemap.put("b59_4_12", new hinsuperobject(59,4,12,"व्यवस्था देनेवाला और हाकिम तो एक ही है, जिसे बचाने और नाश करने की सामर्थ है; तू कौन है, जो अपने पड़ोसी पर दोष लगाता है?"));
hinsuperobject.biblemap.put("b59_4_13", new hinsuperobject(59,4,13,"तुम जो यह कहते हो, कि आज या कल हम किसी और नगर में जाकर वहां एक वर्ष बिताएंगे, और व्यापार करके लाभ उठाएंगे।"));
hinsuperobject.biblemap.put("b59_4_14", new hinsuperobject(59,4,14,"और यह नहीं जानते कि कल क्या होगा: सुन तो लो, तुम्हारा जीवन है ही क्या? तुम तो मानो भाप समान हो, जो थोड़ी देर दिखाई देती है, फिर लोप हो जाती है।"));
hinsuperobject.biblemap.put("b59_4_15", new hinsuperobject(59,4,15,"इस के विपरीत तुम्हें यह कहना चाहिए, कि यदि प्रभु चाहे तो हम जीवित रहेंगे, और यह या वह काम भी करेंगे।"));
hinsuperobject.biblemap.put("b59_4_16", new hinsuperobject(59,4,16,"पर अब तुम अपनी ड़ींग पर घमण्ड करते हो; ऐसा सब घमण्ड बुरा होता है।"));
hinsuperobject.biblemap.put("b59_4_17", new hinsuperobject(59,4,17,"इसलिये जो कोई भलाई करना जानता है और नहीं करता, उसके लिये यह पाप है।।"));
hinsuperobject.biblemap.put("b59_5_1", new hinsuperobject(59,5,1,"हे धनवानों सुन तो लो; तुम अपने आनेवाले क्लेशों पर चिल्लाकर रोओ।"));
hinsuperobject.biblemap.put("b59_5_2", new hinsuperobject(59,5,2,"तुम्हारा धन बिगड़ गया और तुम्हारे वस्त्रों को कीड़े खा गए।"));
hinsuperobject.biblemap.put("b59_5_3", new hinsuperobject(59,5,3,"तुम्हारे सोने- चान्दी में काई लग गई है; और वह काई तुम पर गवाही देगी, और आग की नाई तुम्हारा मांस खा जाएगी: तुम ने अन्तिम युग में धन बटोरा है।"));
hinsuperobject.biblemap.put("b59_5_4", new hinsuperobject(59,5,4,"देखो, जिन मजदूरों ने तुम्हारे खेत काटे, उन की वह मजदूरी जो तुम ने धोखा देकर रख ली है चिल्ला रही है, और लवनेवालों की दोहाई, सेनाओं के प्रभु के कानों तक पहुंच गई है।"));
hinsuperobject.biblemap.put("b59_5_5", new hinsuperobject(59,5,5,"तुम पृथ्वी पर भोग- विलास में लगे रहे और बड़ा ही सुख भोगा; तुम ने इस वध के दिन के लिये अपने हृदय का पालन- पोषण करके मोटा ताजा किया।"));
hinsuperobject.biblemap.put("b59_5_6", new hinsuperobject(59,5,6,"तुम ने धर्मी को दोषी ठहराकर मार डाला; वह तुम्हारा साम्हना नहीं करता।।"));
hinsuperobject.biblemap.put("b59_5_7", new hinsuperobject(59,5,7,"सो हे भाइयों, प्रभु के आगमन तक धीरज धरो, देखो, गृहस्था पृथ्वी के बहुमूल्य फल की आशा रखता हुआ प्रथम और अन्तिम वर्षा होने तक धीरज धरता है।"));
hinsuperobject.biblemap.put("b59_5_8", new hinsuperobject(59,5,8,"तुम भी धीरज धरो, और अपने हृदय को दृढ़ करो, क्योंकि प्रभु का शुभागमन निकट है।"));
hinsuperobject.biblemap.put("b59_5_9", new hinsuperobject(59,5,9,"हे भाइयों, एक दूसरे पर दोष न लगाओ ताकि तुम दोषी न ठहरो, देखो, हाकिम द्वार पर खड़ा है।"));
hinsuperobject.biblemap.put("b59_5_10", new hinsuperobject(59,5,10,"हे भाइयो, जिन भविष्यद्वक्ताओं ने प्रभु के नाम से बातें की, उन्हें दुख उठाने और धीरज धरने का एक आदर्श समझो।"));
hinsuperobject.biblemap.put("b59_5_12", new hinsuperobject(59,5,12,"पर हे मेरे भाइयों, सब से श्रेष्ठ बात यह है, कि शपथ न खाना; न स्वर्ग की न पृथ्वी की, न किसी और वस्तु की, पर तुम्हारी बातचीत हां की हां, और नहीं की नहीं हो, कि तुम दण्ड के योग्य न ठहरो।।"));
hinsuperobject.biblemap.put("b59_5_13", new hinsuperobject(59,5,13,"यदि तुम में कोई दुखी हो तो वह प्रार्थना करे: यदि आनन्दित हो, तो वह स्तुति के भजन गाए।"));
hinsuperobject.biblemap.put("b59_5_14", new hinsuperobject(59,5,14,"यदि तुम में कोई रोगी हो, तो कलीसिया के प्राचीनों को बुलाए, और वे प्रभु के नाम से उस पर तेल मल कर उसके लिये प्रार्थना करें।"));
hinsuperobject.biblemap.put("b59_5_15", new hinsuperobject(59,5,15,"और विश्वास की प्रार्थना के द्वारा रोगी बच जाएगा और प्रभु उस को उठाकर खड़ा करेगा; और यदि उस ने पाप भी किए हों, तो उन की भी क्षमा हो जाएगी।"));
hinsuperobject.biblemap.put("b59_5_16", new hinsuperobject(59,5,16,"इसलिये तुम आपस में एक दूसरे के साम्हने अपने अपने पापों को मान लो; और एक दूसरे के लिये प्रार्थना करो, जिस के चंगे हो जाओ; धर्मी जन की प्रार्थना के प्रभाव से बहुत कुछ हो सकता है।"));
hinsuperobject.biblemap.put("b59_5_17", new hinsuperobject(59,5,17,"एलिरयाह भी तो हमारे समान दुख- सुख भोगी मनुष्य था; और उस ने गिड़गिड़ा कर प्रार्थना की; कि मेंह न बरसे; और साढ़े तीन वर्ष तक भूमि पर मेंह नहीं बरसा।"));
hinsuperobject.biblemap.put("b59_5_18", new hinsuperobject(59,5,18,"फिर उस ने प्रार्थना की, तो आकाश से वर्षा हुई, और भूमि फलवन्त हुई।।"));
hinsuperobject.biblemap.put("b59_5_19", new hinsuperobject(59,5,19,"हे मेरे भाइयों, यदि तुम में कोई सत्य के मार्ग से भटक जाए, और कोई उस को फेर लाए।"));
hinsuperobject.biblemap.put("b59_5_20", new hinsuperobject(59,5,20,"तो वह यह जान ले, कि जो कोई किसी भटके हुए पापी को फेर लाएगा, वह एक प्राण को मृत्यु से बचाएगा, और अनेक पापों पर परदा डालेगा।।"));

}

}