package hin.bible;

public class hinbible62 {

public hinbible62() {

putmapbible62();

}

private void putmapbible62() {

hinsuperobject.biblemap.put("b62_1_1", new hinsuperobject(62,1,1,"उस जीवन के वचन के विषय में जो आदि से था, जिसे हम ने सुना, और जिसे अपनी आंखों से देखा, वरन जिसे हम ने ध्यान से देखा; और हाथों से छूआ।"));
hinsuperobject.biblemap.put("b62_1_2", new hinsuperobject(62,1,2,"(यह जीवन प्रगट हुआ, और हम ने उसे देखा, और उस की गवाही देते हैं, और तुम्हें उस अनन्त जीवन का समाचार देते हैं, जो पिता के साथ था, और हम पर प्रगट हुआ)।"));
hinsuperobject.biblemap.put("b62_1_3", new hinsuperobject(62,1,3,"जो कुछ हम ने देखा और सुना है उसका समाचार तुम्हें भी देते हैं, इसलिये कि तुम भी हमारे साथ सहभागी हो; और हमारी यह सहभागिता पिता के साथ, और उसके पुत्रा यीशु मसीह के साथ है।"));
hinsuperobject.biblemap.put("b62_1_4", new hinsuperobject(62,1,4,"और ये बातें हम इसलिये लिखते हैं, कि हमारा आनन्द पूरा हो जाए।।"));
hinsuperobject.biblemap.put("b62_1_5", new hinsuperobject(62,1,5,"जो समाचार हम ने उस से सुना, और तुम्हें सुनाते हैं, वह यह है; कि परमेश्वर ज्योति हैं: और उस में कुछ भी अन्धकार नहीं।"));
hinsuperobject.biblemap.put("b62_1_6", new hinsuperobject(62,1,6,"यदि हम कहें, कि उसके साथ हमारी सहभागिता है, और फिर अन्धकार में चलें, तो हम झूठे हैं: और सत्य पर नहीं चलते।"));
hinsuperobject.biblemap.put("b62_1_7", new hinsuperobject(62,1,7,"पर यदि जैसा वह ज्योति में है, वैसे ही हम भी ज्योति में चलें, तो एक दूसरे से सहभागिता रखते हैं; और उसके पुत्रा यीशु मसीह का लोहू हमें सब पापों से शुद्ध करता है।"));
hinsuperobject.biblemap.put("b62_1_8", new hinsuperobject(62,1,8,"यदि हम कहें, कि हम में कुछ भी पाप नहीं, तो अपने आप को धोखा देते हैं: और हम में सत्य नहीं।"));
hinsuperobject.biblemap.put("b62_1_9", new hinsuperobject(62,1,9,"यदि हम अपने पापों को मान लें, तो वह हमारे पापों को क्षमा करने, और हमें सब अधर्म से शुद्ध करने में विश्वासयोग्य और धर्मी है।"));
hinsuperobject.biblemap.put("b62_1_10", new hinsuperobject(62,1,10,"यदि कहें कि हम ने पाप नहीं किया, तो उसे झूठा ठहराते हैं, और उसका वचन हम में नहीं है।।"));
hinsuperobject.biblemap.put("b62_2_1", new hinsuperobject(62,2,1,"हे मेरे बालको, मैं ये बातें तुम्हें इसलिये लिखता हूं, कि तुम पाप न करो; और यदि कोई पाप करे, तो पिता के पास हमारा एक सहायक है, अर्थात् धार्मिक यीशु मसीह।"));
hinsuperobject.biblemap.put("b62_2_2", new hinsuperobject(62,2,2,"और वही हमारे पापों का प्रायश्चित्त है: और केवल हमारे ही नहीं, बरन सारे जगत के पापों का भी।"));
hinsuperobject.biblemap.put("b62_2_3", new hinsuperobject(62,2,3,"यदि हम उस की आज्ञाओं को मानेंगे, तो उस से हम जान लेंगे कि हम उसे जान गए हैं।"));
hinsuperobject.biblemap.put("b62_2_4", new hinsuperobject(62,2,4,"जो कोई यह कहता है, कि मैं उसे जान गया हूं, और उस की आज्ञाओं को नहीं मानता, वह झूठा है; और उस में सत्य नहीं।"));
hinsuperobject.biblemap.put("b62_2_5", new hinsuperobject(62,2,5,"पर जो कोई उसके वचन पर चले, उस में सचमुच परमेश्वर का प्रेम सिद्ध हुआ है: हमें इसी से मालूम होता है, कि हम उस में हैं।"));
hinsuperobject.biblemap.put("b62_2_6", new hinsuperobject(62,2,6,"सो कोई यह कहता है, कि मैं उस में बना रहता हूं, उसे चाहिए कि आप भी वैसा ही चले जैसा वह चलता था।"));
hinsuperobject.biblemap.put("b62_2_7", new hinsuperobject(62,2,7,"हे प्रियों, मैं तुम्हें कोई नई आज्ञा नहीं लिखता, पर वही पुरानी आज्ञा जो आरम्भ से तुम्हें मिली है; यह पुरानी आज्ञा वह वचन है, जिसे तुम ने सुना है।"));
hinsuperobject.biblemap.put("b62_2_8", new hinsuperobject(62,2,8,"फिर मैं तुम्हें नई आज्ञा लिखता हूं; और यह तो उस में और तुम में सच्ची ठहरती है; कयोंकि अन्धकार मिटता जाता है और सत्य की ज्योति अभी चमकने लगी है।"));
hinsuperobject.biblemap.put("b62_2_9", new hinsuperobject(62,2,9,"जो कोई यह कहता है, कि मैं ज्योति में हूं; और अपने भाई से बैर रखता है, वह अब तक अन्धकार ही में है।"));
hinsuperobject.biblemap.put("b62_2_10", new hinsuperobject(62,2,10,"जो कोई अपने भाई से प्रेम रखता है, वह ज्योति में रहता है, और ठोकर नहीं खा सकता।"));
hinsuperobject.biblemap.put("b62_2_11", new hinsuperobject(62,2,11,"पर जो कोई अपने भाई से बैर रखता है, वह अन्धकार में है, और अन्धकार में चलता है; और नहीं जानता, कि कहां जाता है, क्योंकि अन्धकार ने उस की आंखे अन्धी कर दी हैं।।"));
hinsuperobject.biblemap.put("b62_2_12", new hinsuperobject(62,2,12,"हे बालको, मैं तुम्हें इसलिये लिखता हूं, कि उसके नाम से तुम्हारे पाप क्षमा हुए।"));
hinsuperobject.biblemap.put("b62_2_13", new hinsuperobject(62,2,13,"हे पितरों, मैं तुम्हें इसलिये लिखता हूं, कि जो आदि से है, तुम उसे जानते हो: हे जवानों, मैं तुम्हें इसलिये लिखता हूं, कि तुम ने उस दुष्ट पर जय पाई है: हे लड़कों मैं ने तुम्हें इसलिये लिखा है, कि तुम पिता को जान गए हो।"));
hinsuperobject.biblemap.put("b62_2_14", new hinsuperobject(62,2,14,"हे पितरों, मैं ने तुम्हें इसलिये लिखा है, कि जो आदि से है तुम उसे जान गए हो: हे जवानो, मैं ने तुम्हें इसलिये लिखा है, कि बलवन्त हो, और परमेश्वर का वचन तुम में बना रहता है, और तुम ने उस दुष्ट पर जय पाई है।"));
hinsuperobject.biblemap.put("b62_2_15", new hinsuperobject(62,2,15,"तुम न तो संसार से और न संसार में की वस्तुओं से प्रेम रखो: यदि कोई संसार से प्रेम रखता है, तो उस में पिता का प्रेम नहीं है।"));
hinsuperobject.biblemap.put("b62_2_16", new hinsuperobject(62,2,16,"क्योंकि जो कुछ संसार में है, अर्थात् शरीर की अभिलाषा, और आंखों की अभिलाषा और जीविका का घमण्ड, वह पिता की ओर से नहीं, परन्तु संसार ही की ओर से है।"));
hinsuperobject.biblemap.put("b62_2_17", new hinsuperobject(62,2,17,"और संसार और उस की अभिलाषाएं दोनों मिटते जाते हैं, पर जो परमेश्वर की इच्छा पर चलता है, वह सर्वदा बना रहेगा।।"));
hinsuperobject.biblemap.put("b62_2_18", new hinsuperobject(62,2,18,"हे लड़कों, यह अन्तिम समय है, और जैसा तुम ने सुना है, कि मसीह का विरोधी आनेवाला है, उसके अनुसार अब भी बहुत से मसीह के विरोधी उठे हैं; इस से हम जानते हैं, कि यह अन्तिम समय है।"));
hinsuperobject.biblemap.put("b62_2_19", new hinsuperobject(62,2,19,"वे निकले तो हम ही में से, पर हम में के थे नहीं; क्योंकि यदि हम में के होते, तो हमारे साथ रहते, पर निकल इसलिये गए कि यह प्रगट हो कि वे सब हम में के नहीं हैं।"));
hinsuperobject.biblemap.put("b62_2_20", new hinsuperobject(62,2,20,"और तुम्हारा तो उस पवित्रा से अभिषेक हुआ है, और तुम सब कुछ जानते हो।"));
hinsuperobject.biblemap.put("b62_2_21", new hinsuperobject(62,2,21,"मैं ने तुम्हें इसलिये नहीं लिखा, कि तुम सत्य को नहीं जानते, पर इसलिये, कि उसे जानते हो, और इसलिये कि कोई झूठ, सत्य की ओर से नहीं।"));
hinsuperobject.biblemap.put("b62_2_22", new hinsuperobject(62,2,22,"झूठा कौन है? केवल वह, जो यीशु के मसीह होने का इन्कार करता है; और मसीह का विरोधी वही है, जो पिता का और पुत्रा का इन्कार करता है।"));
hinsuperobject.biblemap.put("b62_2_23", new hinsuperobject(62,2,23,"जो कोई पुत्रा का इन्कार करता है उसके पास पिता भी नहीं: जो पुत्रा को मान लेता है, उसके पास पिता भी है।"));
hinsuperobject.biblemap.put("b62_2_24", new hinsuperobject(62,2,24,"जो कुछ तुम ने आरम्भ से सुना है वही तुम में बना रहे: जो तुम ने आरम्भ से सुना है, यदि वह तुम में बना रहे, तो तुम भी पुत्रा में, और पिता में बने रहोगे।"));
hinsuperobject.biblemap.put("b62_2_25", new hinsuperobject(62,2,25,"और जिस की उस ने हम से प्रतिज्ञा की वह अनन्त जीवन है।"));
hinsuperobject.biblemap.put("b62_2_26", new hinsuperobject(62,2,26,"मैं ने ये बातें तुम्हें उस के विषय में लिखी हैं, जो तुम्हें भरमाते हैं।"));
hinsuperobject.biblemap.put("b62_2_27", new hinsuperobject(62,2,27,"और तुम्हारा वह अभिषेक, जो उस की ओर से किया गया, तुम में बना रहता है; और तुम्हें इस का प्रयोजन नही, कि कोई तुम्हें सिखाए, बरन जैसे वह अभिषेक जो उस की ओर से किया गया तुम्हें सब बातें सिखाता है, और यह सच्चा है, और झूठा नहीं: और जैसा उस ने तुम्हें सिखाया है वैसे ही तुम उस में बने रहते हो।"));
hinsuperobject.biblemap.put("b62_2_28", new hinsuperobject(62,2,28,"निदान, हे बालको, उस में बने रहो; कि जब वह प्रगट हो, तो हमें हियाव हो, और हम उसके आने पर उसके साम्हने लज्जित न हों।"));
hinsuperobject.biblemap.put("b62_2_29", new hinsuperobject(62,2,29,"यदि तुम जानते हो, कि वह धार्मिक है, तो यह भी जानते हो, कि जो कोई धर्म का काम करता है, वह उस से जन्मा है।"));
hinsuperobject.biblemap.put("b62_3_1", new hinsuperobject(62,3,1,"देखो पिता ने हम से कैसा प्रेम किया है, कि हम परमेश्वर की सन्तान कहलाएं, और हम हैं भी: इस कारण संसार हमें नहीं जानता, क्योंकि उस ने उसे भी नहीं जाना।"));
hinsuperobject.biblemap.put("b62_3_2", new hinsuperobject(62,3,2,"हे प्रियों, अभी हम परमेश्वर की सन्तान हैं, और अब तक यह प्रगट नहीं हुआ, कि हम क्या कुछ होंगे! इतना जानते हैं, कि जब वह प्रगट होगा तो हम भी उसके समान होंगे, क्योंकि उस को वैसा ही देखेंगे जैसा वह है।"));
hinsuperobject.biblemap.put("b62_3_3", new hinsuperobject(62,3,3,"और जो कोई उस पर यह आशा रखता है, वह अपने आप को वैसा ही पवित्रा करता है, जैसा वह पवित्रा है।"));
hinsuperobject.biblemap.put("b62_3_4", new hinsuperobject(62,3,4,"जो कोई पाप करता है, वह व्यवस्था का विरोध करता है; ओर पाप तो व्यवस्था का विरोध है।"));
hinsuperobject.biblemap.put("b62_3_5", new hinsuperobject(62,3,5,"और तुम जानते हो, कि वह इसलिये प्रगट हुआ, कि पापों को हर ले जाए; और उसके स्वभाव में पाप नहीं।"));
hinsuperobject.biblemap.put("b62_3_6", new hinsuperobject(62,3,6,"जो कोई उस में बना रहता है, वह पाप नहीं करता: जो कोई पाप करता है, उस ने न तो उसे देखा है, और न उस को जाना है।"));
hinsuperobject.biblemap.put("b62_3_7", new hinsuperobject(62,3,7,"हे बालको, किसी के भरमाने में न आना; जो धर्म के काम करता है, वही उस की नाईं धर्मी है।"));
hinsuperobject.biblemap.put("b62_3_8", new hinsuperobject(62,3,8,"जो कोई पाप करता है, वह शैतान की ओर से है, क्योंकि शैतान आरम्भ ही से पाप करता आया है: परमेश्वर का पुत्रा इसलिये प्रगट हुआ, कि शैतान के कामों को नाश करे।"));
hinsuperobject.biblemap.put("b62_3_9", new hinsuperobject(62,3,9,"जो कोई परमेश्वर से जन्मा है वह पाप नहीं करता; क्योंकि उसका बीज उस में बना रहता है: और वह पाप कर ही नहीं सकता, क्योंकि परमेश्वर से जन्मा है।"));
hinsuperobject.biblemap.put("b62_3_10", new hinsuperobject(62,3,10,"इसी से परमेश्वर की सन्तान, और शैतान की सन्तान जाने जाते हैं; जो कोई धर्म के काम नहीं करता, वह परमेश्वर से नहीं, और न वह, जो अपने भाई से प्रेम नहीं रखता।"));
hinsuperobject.biblemap.put("b62_3_11", new hinsuperobject(62,3,11,"क्योंकि जो समाचार तुम ने आरम्भ से सुना, वह यह है, कि हम एक दूसरे से बैर रखे।"));
hinsuperobject.biblemap.put("b62_3_12", new hinsuperobject(62,3,12,"और कैन के समान न बनें, जो उस दुष्ट से था, और जिस ने अपने भाई को घात किया: और उसे किस कारण घात किया? इस कारण कि उसके काम बुरे थे, और उसके भाई के काम धर्म के थे।।"));
hinsuperobject.biblemap.put("b62_3_13", new hinsuperobject(62,3,13,"हे भाइयों, यदि संसार तुम से बैर करता है तो अचम्भा न करना।"));
hinsuperobject.biblemap.put("b62_3_14", new hinsuperobject(62,3,14,"हम जानते हैं, कि हम मृत्यु से पार होकर जीवन में पहुंचे हैं; क्योंकि हम भाइयों से प्रेम रखते हैं: जो प्रेम नहीं रखता, वह मृत्यु की दशा में रहता है।"));
hinsuperobject.biblemap.put("b62_3_15", new hinsuperobject(62,3,15,"जो कोई अपने भाई से बैर रखता है, वह हत्यारा है; और तुम जानते हो, कि किसी हत्यारे में अनन्त जीवन नहीं रहता।"));
hinsuperobject.biblemap.put("b62_3_16", new hinsuperobject(62,3,16,"हम ने प्रेम इसी से जाना, कि उस ने हमारे अपने प्राण दे दिए; और हमें भी भाइयों के लिये प्राण देना चाहिए।"));
hinsuperobject.biblemap.put("b62_3_17", new hinsuperobject(62,3,17,"पर जिस किसी के पास संसार की संपत्ति हो और वह अपने भाई को कंगाल देखकर उस पर तरस न खाना चाहे, तो उस में परमेश्वर का प्रेम क्योंकर बना रह सकता है?"));
hinsuperobject.biblemap.put("b62_3_18", new hinsuperobject(62,3,18,"हे बालको, हम वचन और जीभ ही से नहीं, पर काम और सत्य के द्वारा भी प्रेम करें।"));
hinsuperobject.biblemap.put("b62_3_19", new hinsuperobject(62,3,19,"इसी से हम जानेंगे, कि हम सत्य के हैं; और जिस बात में हमारा मन हमें दोष देगा, उसे विषय में हम उसके साम्हने अपने अपने मन को ढाढ़स दे सकेंगे।"));
hinsuperobject.biblemap.put("b62_3_20", new hinsuperobject(62,3,20,"क्योंकि परमेश्वर हमारे मन से बड़ा है; और सब कुछ जानता है।"));
hinsuperobject.biblemap.put("b62_3_21", new hinsuperobject(62,3,21,"हे प्रियो, यदि हमारा मन हमें दोष न दे, तो हमें परमेश्वर के साम्हने हियाव होता है।"));
hinsuperobject.biblemap.put("b62_3_22", new hinsuperobject(62,3,22,"और जो कुछ हम मांगते हैं, वह हमें उस से मिलता है; क्योंकि हम उस की आज्ञाओं को मानते हैं; और जो उसे भाता है वही करते हैं।"));
hinsuperobject.biblemap.put("b62_3_23", new hinsuperobject(62,3,23,"और उस की आज्ञा यह है कि हम उसके पुत्रा यीशु मसीह के नाम पर विश्वास करें और जैसा उस ने हमें आज्ञा दी है उसी के अनुसार आपस में प्रेम रखें।"));
hinsuperobject.biblemap.put("b62_3_24", new hinsuperobject(62,3,24,"और जो उस की आज्ञाओं को मानता है, वह इस में, और यह उस में बना रहता है: और इसी से, अर्थात् उस आत्मा से जो उस ने हमें दिया है, हम जानते हैं, कि वह हम में बना रहता है।।"));
hinsuperobject.biblemap.put("b62_4_1", new hinsuperobject(62,4,1,"हे प्रियों, हर एक आत्मा की प्रतीति न करो: बरन आत्माओं को परखो, कि वे परमेश्वर की ओर से हैं कि नहीं; क्योंकि बहुत से झूठे भविष्यद्वक्ता जगत में निकल खड़े हुए हैं।"));
hinsuperobject.biblemap.put("b62_4_2", new hinsuperobject(62,4,2,"परमेश्वर का आत्मा तुम इसी रीति से पहचान सकते हो, कि जो कोई आत्मा मान लेती है, कि यीशु मसीह शरीर में होकर आया है वह परमेश्वर की ओर से है।"));
hinsuperobject.biblemap.put("b62_4_3", new hinsuperobject(62,4,3,"और जो कोई आत्मा यीशु को नहीं मानती, वह परमेश्वर की ओर से नहीं; और वही तो मसीह के विरोधी की आत्मा है; जिस की चर्चा तुम सुन चुके हो, कि वह आनेवाला है: और अब भी जगत में है।"));
hinsuperobject.biblemap.put("b62_4_4", new hinsuperobject(62,4,4,"हे बालको, तुम परमेश्वर के हो: और तुम ने उन पर जय पाई है; क्योंकि जो तुम में है, वह उस से जो संसार में है, बड़ा है।"));
hinsuperobject.biblemap.put("b62_4_5", new hinsuperobject(62,4,5,"वे संसार के हैं, इस कारण वे संसार की बातें बोलते हैं, और संसार उन की सुनता है।"));
hinsuperobject.biblemap.put("b62_4_6", new hinsuperobject(62,4,6,"हम परमेश्वर के हैं: जो परमेश्वर को जानता है, वह हमारी सुनता है; जो परमेश्वर को नहीं जानता वह हमारी नही सुनता; इसी प्रकार हम सत्य की आत्मा और भ्रम की आत्मा को पहचान लेते हैं।"));
hinsuperobject.biblemap.put("b62_4_7", new hinsuperobject(62,4,7,"हे प्रियों, हम आपस में प्रेम रखें; क्योंकि प्रेम परमेश्वर से है: और जो कोई प्रेम करता है, वह परमेश्वर से जन्मा है; और परमेश्वर को जानता है।"));
hinsuperobject.biblemap.put("b62_4_8", new hinsuperobject(62,4,8,"जो प्रेम नहीं रखता, वह परमेश्वर को नहीं जानता है, क्योंकि परमेश्वर प्रेम है।"));
hinsuperobject.biblemap.put("b62_4_9", new hinsuperobject(62,4,9,"जो प्रेम परमेश्वर हम से रखता है, वह इस से प्रगट हुआ, कि परमेश्वर ने अपने एकलौते पुत्रा को जगत में भेजा है, कि हम उसे द्वारा जीवन पाएं।"));
hinsuperobject.biblemap.put("b62_4_10", new hinsuperobject(62,4,10,"प्रेम इस में नहीं कि हम ने परमेश्वर ने प्रेम किया; पर इस में है, कि उस ने हम से प्रेम किया; और हमारे पापों के प्रायश्चित्त के लिये अपने पुत्रा को भेजा।"));
hinsuperobject.biblemap.put("b62_4_11", new hinsuperobject(62,4,11,"हे प्रियों, जब परमेश्वर ने हम से ऐसा प्रेम किया, तो हम को भी आपस में प्रेम रखना चाहिए।"));
hinsuperobject.biblemap.put("b62_4_12", new hinsuperobject(62,4,12,"परमेश्वर को कभी किसी ने नहीं देखा; यदि हम आपस में प्रेम रखें, तो परमेश्वर हम में बना रहता है; और उसका प्रेम हम में सिद्ध हो गया है।"));
hinsuperobject.biblemap.put("b62_4_13", new hinsuperobject(62,4,13,"इसी से हम जानते हैं, कि हम उस में बने रहते हैं, और वह हम में; क्योंकि उस ने अपने आत्मा में से हमें दिया है।"));
hinsuperobject.biblemap.put("b62_4_14", new hinsuperobject(62,4,14,"और हम ने देख भी लिया और गवाही देते हैं, कि पिता ने पुत्रा को जगत का उद्धारकर्ता करके भेजा है।"));
hinsuperobject.biblemap.put("b62_4_15", new hinsuperobject(62,4,15,"जो कोई यह मान लेता है, कि यीशु परमेश्वर का पुत्रा है: परमेश्वर उस में बना रहता है, और वह परमेश्वर में।"));
hinsuperobject.biblemap.put("b62_4_16", new hinsuperobject(62,4,16,"और जो प्रेम परमेश्वर हम से रखता है, उस को हम जान गए, और हमें उस की प्रतीति है; परमेश्वर प्रेम है: जो प्रेम में बना रहता है, वह परमेश्वर में बना रहता है; और परमेश्वर उस में बना रहता है।"));
hinsuperobject.biblemap.put("b62_4_17", new hinsuperobject(62,4,17,"इसी से प्रेम हम में सिद्ध हुआ, कि हमें न्याय के दिन हियाव हो; क्योंकि जैसा वह है, वैसे ही संसार में हम भी है।"));
hinsuperobject.biblemap.put("b62_4_18", new hinsuperobject(62,4,18,"प्रेम में भय नहीं होता, बरन सिद्ध प्रेम भय को दूर कर देता है, क्योंकि भय के कष्ट होता है, और जो भय करता है, वह प्रेम में सिद्ध नहीं हुआ।"));
hinsuperobject.biblemap.put("b62_4_19", new hinsuperobject(62,4,19,"हम इसलिये प्रेम करते हैं, कि पहिले उस ने हम से प्रेम किया।"));
hinsuperobject.biblemap.put("b62_4_20", new hinsuperobject(62,4,20,"यदि कोई कहे, कि मैं परमेश्वर से प्रेम रखता हूं; और अपने भाई से बैर रखे; तो वह झूठा है: क्योंकि जो अपने भाई से, जिस उस ने देखा है, प्रेम नहीं रखता, तो वह परमेश्वर से भी जिसे उस ने नहीं देखा, प्रेम नहीं रख सकता।"));
hinsuperobject.biblemap.put("b62_4_21", new hinsuperobject(62,4,21,"और उस से हमें यह आज्ञा मिली है, कि जो कोई अपने परमेश्वर से प्रेम रखता है, वह अपने भाई से भी प्रेम रखे।।"));
hinsuperobject.biblemap.put("b62_5_1", new hinsuperobject(62,5,1,"जिसका यह विश्वास है कि यीशु ही मसीह है, वह परमेश्वर से उत्पन्न हुआ है और जो कोई उत्पन्न करनेवाले से प्रेम रखता है, वह उस से भी प्रेम रखता है, जो उस से उत्पन्न हुआ है।"));
hinsuperobject.biblemap.put("b62_5_2", new hinsuperobject(62,5,2,"जब हम परमेश्वर से प्रेम रखते हैं, और उस की आज्ञाओं को मानते हैं, तो इसी से हम जानते हैं, कि परमेश्वर की सन्तानों से प्रेम रखते हैं।"));
hinsuperobject.biblemap.put("b62_5_3", new hinsuperobject(62,5,3,"और परमेश्वर का प्रेम यह है, कि हम उस की आज्ञाओं को मानें; और उस की आज्ञाएं कठिन नहीं।"));
hinsuperobject.biblemap.put("b62_5_4", new hinsuperobject(62,5,4,"क्योंकि जो कुछ परमेश्वर से उत्पन्न हुआ है, वह संसार पर जय प्राप्त करता है, और वह विजय जिस से संसार पर जय प्राप्त होती है हमारा विश्वास है।"));
hinsuperobject.biblemap.put("b62_5_5", new hinsuperobject(62,5,5,"संसार पर जय पानेवाला कौन है? केवल वह जिस का विश्वास है, कि यीशु, परमेश्वर का पुत्रा है।"));
hinsuperobject.biblemap.put("b62_5_6", new hinsuperobject(62,5,6,"यही है वह, जो पानी और लोहू के द्वारा आया था; अर्थात् यीशु मसीह: वह न केवल पानी के द्वारा, बरन पानी और लोहू दोनों के द्वारा आया था।"));
hinsuperobject.biblemap.put("b62_5_7", new hinsuperobject(62,5,7,"और जो गवाही देता है, वह आत्मा है; क्योंकि आत्मा सत्य है।"));
hinsuperobject.biblemap.put("b62_5_8", new hinsuperobject(62,5,8,"और गवाही देनेवाले तीन हैं; आत्मा, और पानी, और लोहू; और तीनों एक ही बात पर सम्मत हैं।"));
hinsuperobject.biblemap.put("b62_5_9", new hinsuperobject(62,5,9,"जब हम मनुष्यों की गवाही मान लेते हैं, तो परमेश्वर की गवाही तो उस से बढ़कर है; और परमेश्वर की गवाही यह है, कि उस ने अपने पुत्रा के विषय में गवाही दी है।"));
hinsuperobject.biblemap.put("b62_5_10", new hinsuperobject(62,5,10,"जो परमेश्वर के पुत्रा पर विश्वास करता है, वह अपने ही में गवाही रखता है; जिस ने परमेश्वर को प्रतीति नहीं की, उस ने उसे झूठा ठहराया; क्योंकि उस ने उस गवाही पर विश्वास नहीं किया, जो परमेश्वर ने अपने पुत्रा के विषय में दी है।"));
hinsuperobject.biblemap.put("b62_5_11", new hinsuperobject(62,5,11,"और वह गवाही यह है, कि परमेश्वर ने हमें अनन्त जीवन दिया है: और यह जीवन उसके पुत्रा में है।"));
hinsuperobject.biblemap.put("b62_5_12", new hinsuperobject(62,5,12,"जिस के पास पुत्रा है, उसके पास जीवन है; और जिस के पास परमेश्वर का पुत्रा नहीं, उसके पास जीवन भी नहीं है।।"));
hinsuperobject.biblemap.put("b62_5_13", new hinsuperobject(62,5,13,"मैं ने तुम्हें, जो परमेश्वर के पुत्रा के नाम पर विश्वास करते हो, इसलिये लिखा है; कि तुम जानो, कि अनन्त जीवन तुम्हारा है।"));
hinsuperobject.biblemap.put("b62_5_14", new hinsuperobject(62,5,14,"और हमें उसके साम्हने जो हियाव होता है, वह यह है; कि यदि हम उस की इच्छा के अनुसार कुछ मांगते हैं, तो हमारी सुनता है।"));
hinsuperobject.biblemap.put("b62_5_15", new hinsuperobject(62,5,15,"और जब हम जानते हैं, कि जो कुछ हम मांगते हैं वह हमारी सुनता है, तो यह भी जानते हैं, कि जो कुछ हम ने उस से मांगा, वह पाया है।"));
hinsuperobject.biblemap.put("b62_5_16", new hinsuperobject(62,5,16,"यदि कोई अपने भाई को ऐसा पाप करते देखे, जिस का फल मृत्यु न हो, तो बिनती करे, और परमेश्वर, उसे, उन के लिये, जिन्हों ने ऐसा पाप किया है जिस का फल मृत्यु है: इस के विषय में मै बिनती करने के लिये नहीं कहता।"));
hinsuperobject.biblemap.put("b62_5_17", new hinsuperobject(62,5,17,"सब प्रकार का अधर्म तो पाप है, परन्तु ऐसा पाप भी है, जिस का फल मृत्यु नहीं।।"));
hinsuperobject.biblemap.put("b62_5_18", new hinsuperobject(62,5,18,"हम जानते हैं, कि जो कोई परमेश्वर से उत्पन्न हुआ है, वह पाप नहीं करता; जो परमेश्वर से उत्पन्न हुआ, उसे वह बचाए रखता है: और वह दुष्ट उसे छूने नहीं पाता।"));
hinsuperobject.biblemap.put("b62_5_19", new hinsuperobject(62,5,19,"हम जानते हैं, कि हम परमेश्वर से हैं, और सारा संसार उस दुष्ट के वंश में पड़ा है।"));
hinsuperobject.biblemap.put("b62_5_20", new hinsuperobject(62,5,20,"और यह भी जानते हैं, कि परमेश्वर का पुत्रा आ गया है और उस ने हमें समझ दी है, कि हम उस सच्चे को पहचानें, और हम उस में जो सत्य है, अर्थात् उसके पुत्रा यीशु मसीह में रहते हैं: सच्चा परमेश्वर और अनन्त जीवन यही है।"));
hinsuperobject.biblemap.put("b62_5_21", new hinsuperobject(62,5,21,"हे बालको, अपने आप को मुरतों से बचाए रखो।।"));

}

}