package hu.bible;

public class hubible11 {

public hubible11() {

putmapbible11();

}

private void putmapbible11() {

husuperobject.biblemap.put("b11_1_1", new husuperobject(11,1,1,"Mikor pedig megvénhedt és megöregedett Dávid király, bár leplekkel takargatták be, mégsem bírt felmelegedni."));
husuperobject.biblemap.put("b11_1_2", new husuperobject(11,1,2,"És mondának néki az õ szolgái: Keressenek az én uramnak, a királynak egy szûz leányt, a ki a király körül legyen, és õt ápolja, aludjék karjai között, és melegítse fel az én uramat, a királyt."));
husuperobject.biblemap.put("b11_1_3", new husuperobject(11,1,3,"Keresének annakokáért egy szép leányt Izráelnek minden határiban; és találák a Súnem városából való Abiságot, a kit el is hozának a királyhoz."));
husuperobject.biblemap.put("b11_1_4", new husuperobject(11,1,4,"És a leány igen szép volt, és a királyt ápolta és szolgált néki. De a király nem ismeré õt."));
husuperobject.biblemap.put("b11_1_5", new husuperobject(11,1,5,"Adónia pedig, Haggitnak fia, felfuvalkodék, ezt mondván: Én fogok uralkodni! És szerze magának szekereket, lovagokat és ötven elõtte járó férfiakat."));
husuperobject.biblemap.put("b11_1_6", new husuperobject(11,1,6,"Kit az õ atyja soha meg nem szomoríta, ezt mondván: Miért cselekszel így?! Ez is pedig igen szép férfi volt, és õt [Haggit] szülte [volt Dávidnak] Absolon után."));
husuperobject.biblemap.put("b11_1_7", new husuperobject(11,1,7,"És tanácskozék Joábbal, Séruja fiával és Abjátár pappal, kik az Adónia pártján voltak."));
husuperobject.biblemap.put("b11_1_8", new husuperobject(11,1,8,"De Sádók pap, meg Benája, a Jójada fia, és Nátán próféta, és Sémei, és Réhi, és a Dávid erõs vitézei nem állottak Adónia mellé."));
husuperobject.biblemap.put("b11_1_9", new husuperobject(11,1,9,"Mikor pedig Adónia áldozatot mutatott be juhokból, ökrökbõl és egyéb kövér barmokból a Zohélet kõsziklánál, a mely a Rógel forrása mellett volt: meghívá egész rokonságát, a király fiait, Júda minden férfiait, a király szolgáit;"));
husuperobject.biblemap.put("b11_1_10", new husuperobject(11,1,10,"De Nátán prófétát és Benáját és amaz erõs vitézeket és Salamont, az õ atyjafiát nem hívá el."));
husuperobject.biblemap.put("b11_1_11", new husuperobject(11,1,11,"Szóla akkor Nátán Bethsabénak, a Salamon anyjának, mondván: Nem hallottad-é, hogy Adónia, a Haggit fia uralkodik, és a mi urunk Dávid nem tud róla semmit?"));
husuperobject.biblemap.put("b11_1_12", new husuperobject(11,1,12,"Jövel azért, hadd adjak néked tanácsot, hogy megmentsd a te életedet, és a te fiadnak, Salamonnak életét."));
husuperobject.biblemap.put("b11_1_13", new husuperobject(11,1,13,"Eredj, menj be Dávid királyhoz, és mondd ezt néki: Uram király, nemde nem esküdtél-é meg a te szolgálóleányodnak ilyenképen: Salamon, a te fiad uralkodik én utánam, és õ ül az én királyi székembe? Miért uralkodik hát Adónia?"));
husuperobject.biblemap.put("b11_1_14", new husuperobject(11,1,14,"És ímé mialatt még te ott a királylyal beszélsz, én is bemegyek utánad, és kiegészítem a te beszédidet."));
husuperobject.biblemap.put("b11_1_15", new husuperobject(11,1,15,"És beméne Bethsabé a királyhoz a kamarába. És a király igen megvénhedett vala, és a Súnembõl való Abiság szolgál vala a királynak."));
husuperobject.biblemap.put("b11_1_16", new husuperobject(11,1,16,"És fejet hajta Bethsabé, és meghajtá magát a királynak. És monda a király: Mit kivánsz?"));
husuperobject.biblemap.put("b11_1_17", new husuperobject(11,1,17,"Felele néki [Bethsabé: Édes] uram, te megesküdtél az Úrra, a te Istenedre a te szolgálóleányodnak ilyenképen: Salamon, a te fiad uralkodik én utánam, és õ ül az én királyi székembe."));
husuperobject.biblemap.put("b11_1_18", new husuperobject(11,1,18,"És ímé mégis Adónia lett királylyá; és ímé, uram király, te nem tudsz errõl semmit."));
husuperobject.biblemap.put("b11_1_19", new husuperobject(11,1,19,"Mert áldozott ökrökkel és nagy sok kövér barmokkal bõségesen, és vendégekké hívta a királynak minden fiait, és Abjátár papot és Joábot, a seregnek hadnagyát, csak Salamont, a te szolgádat nem hívta meg."));
husuperobject.biblemap.put("b11_1_20", new husuperobject(11,1,20,"Még most te vagy, Uram, a király; az egész Izráel népének szemei reád néznek, hogy megjelentsed nékik, kicsoda fog ülni az én uramnak, a királynak székében, õ utána."));
husuperobject.biblemap.put("b11_1_21", new husuperobject(11,1,21,"De ha az én uram, a király, az õ atyáival elaluszik: akkor én és az én fiam, Salamon leszünk bûnösök."));
husuperobject.biblemap.put("b11_1_22", new husuperobject(11,1,22,"És ímé, mikor még a királylyal szólana, Nátán próféta megérkezék."));
husuperobject.biblemap.put("b11_1_23", new husuperobject(11,1,23,"És bejelenték a királynak, mondván: Itt van Nátán próféta. És bemenvén a király eleibe, meghajtá magát a király elõtt, arczczal a földre leborulván."));
husuperobject.biblemap.put("b11_1_24", new husuperobject(11,1,24,"És monda Nátán: Uram király, te mondottad-é: Adónia legyen én utánam a király, és õ üljön az én királyi székembe?"));
husuperobject.biblemap.put("b11_1_25", new husuperobject(11,1,25,"Mert ma aláment, és áldozott ökrökkel és kövér barmokkal bõségesen, és vendégekké hívta a királynak minden fiait és a seregnek hadnagyait és Abjátár papot: és ímé õk esznek és isznak õ elõtte, és [immár] azt kiáltották: Éljen Adónia király!"));
husuperobject.biblemap.put("b11_1_26", new husuperobject(11,1,26,"Engem pedig, a ki a te szolgád vagyok, és Sádók papot és Benáját, a Jójada fiát, és Salamont, a te szolgádat nem hívta meg."));
husuperobject.biblemap.put("b11_1_27", new husuperobject(11,1,27,"Avagy az én uramtól, a királytól lett-é ez a dolog, hogy nem adtad tudtára a te szolgádnak, kicsoda fogna ülni az én uramnak, a királynak székiben [az õ holta] után."));
husuperobject.biblemap.put("b11_1_28", new husuperobject(11,1,28,"És felelvén Dávid király, monda: Hívjátok hozzám Bethsabét, a ki beméne a király eleibe, és megálla a király elõtt."));
husuperobject.biblemap.put("b11_1_29", new husuperobject(11,1,29,"És megesküvék a király, mondván: Él az Úr, a ki megszabadította az én lelkemet minden nyomorúságból,"));
husuperobject.biblemap.put("b11_1_30", new husuperobject(11,1,30,"Hogy a miképen megesküdtem néked az Úrra, Izráel Istenére, ezt mondván: A te fiad Salamon uralkodik én utánam, és õ ül az én királyi székembe én helyettem: ezt ma így meg is teszem."));
husuperobject.biblemap.put("b11_1_31", new husuperobject(11,1,31,"És fejet hajta Bethsabé, arczczal a földre [leborulván,] és magát meghajtván a király elõtt, monda: Éljen az én uram, Dávid király, mindörökké!"));
husuperobject.biblemap.put("b11_1_32", new husuperobject(11,1,32,"Azután monda Dávid király: Hívjátok hozzám Sádók papot és Nátán prófétát és Benáját, Jójadának fiát. És ezek bemenének a király eleibe."));
husuperobject.biblemap.put("b11_1_33", new husuperobject(11,1,33,"És monda nékik a király: Vegyétek mellétek a ti uratoknak szolgáit, és ültessétek Salamont, az én fiamat az én öszvéremre, és vigyétek alá õt Gihonba;"));
husuperobject.biblemap.put("b11_1_34", new husuperobject(11,1,34,"És kenje õt ott Sádók pap és Nátán próféta Izráelnek királyává; és fújjátok meg a harsonákat, és kiáltsátok: Éljen Salamon király!"));
husuperobject.biblemap.put("b11_1_35", new husuperobject(11,1,35,"És jõjjetek fel [onnét] õ utána; és eljövén, üljön az én királyi székembe, és õ uralkodjék én helyettem; mert [immár] meghagytam néki, hogy õ legyen fejedelme mind Izráelnek, mind Júdának."));
husuperobject.biblemap.put("b11_1_36", new husuperobject(11,1,36,"Felele akkor Benája, a Jójada fia a királynak, és monda: Ámen! Így szóljon az Úr, az én uramnak, a királynak Istene [is.]"));
husuperobject.biblemap.put("b11_1_37", new husuperobject(11,1,37,"A miképen vele volt az Úr az én urammal, a királylyal: azonképen legyen vele Salamonnal is, és magasztalja feljebb az õ királyi székét az én uramnak, Dávid királynak királyi székénél."));
husuperobject.biblemap.put("b11_1_38", new husuperobject(11,1,38,"Aláméne azért Sádók pap és Nátán próféta és Benája, a Jójada fia, a Kereteusok is és a Peleteusok, és felülteték Salamont a Dávid király öszvérére, és alávivék õt Gihonba."));
husuperobject.biblemap.put("b11_1_39", new husuperobject(11,1,39,"És vevé Sádók pap az olajos szarut az Úr sátorából, és megkené Salamont; azután kürtölének, és az egész nép ezt kiáltá: Éljen Salamon király!"));
husuperobject.biblemap.put("b11_1_40", new husuperobject(11,1,40,"És felvonult utána az egész nép, és a nép sípolt és felette ujjongott, úgy hogy a föld is megrepedne kiáltásuk zajától."));
husuperobject.biblemap.put("b11_1_41", new husuperobject(11,1,41,"És Adónia is meghallotta és vendégei is mind, a kik nála valának, miután a lakomát már elvégezték; és meghallá Joáb is a kürtölés szavát, és monda: Miért e zaj [és] mozgás a városban?"));
husuperobject.biblemap.put("b11_1_42", new husuperobject(11,1,42,"És a mikor õ még szólana, ímé megérkezék Jonathán, az Abjátár pap fia. És monda Adónia: Jõjj be, mert megbízható férfiú vagy, és jó hírt mondasz."));
husuperobject.biblemap.put("b11_1_43", new husuperobject(11,1,43,"Jonathán pedig felelvén, monda Adóniának: Igen, a mi urunk, Dávid király, Salamont tette királylyá."));
husuperobject.biblemap.put("b11_1_44", new husuperobject(11,1,44,"És elküldötte õ vele Sádók papot a király és Nátán prófétát, és Benáját, a Jójada fiát, a Kereteusokat is és Peleteusokat, és õt a király öszvérére ülteték,"));
husuperobject.biblemap.put("b11_1_45", new husuperobject(11,1,45,"És Sádók pap Nátán prófétával együtt királylyá kente fel õt Gihonnál, és onnét vonulnak fel örömmel; ettõl zendült meg a város. Ez az a zaj, a melyet hallottatok."));
husuperobject.biblemap.put("b11_1_46", new husuperobject(11,1,46,"És [immár] be is ült Salamon az országnak királyi székibe;"));
husuperobject.biblemap.put("b11_1_47", new husuperobject(11,1,47,"És a király szolgái is bemenének, hogy áldják a mi urunkat, Dávid királyt, mondván: Tegye az Isten a Salamon nevét híresebbé a te nevednél, és magasztalja feljebb az õ székit a te székednél. És meghajtá magát a király az õ ágyán."));
husuperobject.biblemap.put("b11_1_48", new husuperobject(11,1,48,"És ekképen is szóla a király: Áldott az Úr, Izráel Istene, a ki adott e mai napon olyat, a ki szemeim láttára [helyettem] üljön az én királyi székemben."));
husuperobject.biblemap.put("b11_1_49", new husuperobject(11,1,49,"Akkor megrettenének, és felkelének mindnyájan a hivatalosok, a kik Adóniával valának, és kiki mind dolgára méne."));
husuperobject.biblemap.put("b11_1_50", new husuperobject(11,1,50,"De Adónia félt Salamontól, és felkészülve elfutott, és megragadta az oltárnak szarvait."));
husuperobject.biblemap.put("b11_1_51", new husuperobject(11,1,51,"Hírül adák pedig Salamonnak ilyen szókkal: Ímé Adónia Salamon királytól való féltében megfogá az oltárnak szarvait, ezt mondván: Esküdjék meg ma nékem Salamon király, hogy meg nem öli az õ szolgáját fegyverrel."));
husuperobject.biblemap.put("b11_1_52", new husuperobject(11,1,52,"És monda Salamon: Ha jámbor lészen, [egy] hajszál fejérõl le nem esik a földre; de ha gonoszság találtatik õ benne, meg kell halnia."));
husuperobject.biblemap.put("b11_1_53", new husuperobject(11,1,53,"Elkülde azért Salamon király, és elhozák õt az oltártól; és eljövén meghajtá magát Salamon király elõtt; és monda Salamon király: Menj el a te házadhoz."));
husuperobject.biblemap.put("b11_2_1", new husuperobject(11,2,1,"Mikor pedig elközelgett Dávidnak ideje, hogy meghaljon, parancsot ada Salamonnak az õ fiának, ezt mondván:"));
husuperobject.biblemap.put("b11_2_2", new husuperobject(11,2,2,"Én elmegyek az egész földnek útján; erõsítsd meg magad és légy férfiú."));
husuperobject.biblemap.put("b11_2_3", new husuperobject(11,2,3,"És õrízd meg az Úrnak a te Istenednek õrizetit, hogy az õ útain járj, és megõrizzed az õ rendeléseit, parancsolatit és ítéleteit, és bizonyságtételeit, a mint meg van írva a Mózes törvényében: hogy elõmented legyen mindenekben, a melyeket cselekedéndesz, és mindenütt, valamerre fordulándasz;"));
husuperobject.biblemap.put("b11_2_4", new husuperobject(11,2,4,"Hogy megteljesítse az Úr az õ beszédét, melyet szólott nékem, mondván: Ha megõrizéndik a te fiaid az õ útjokat, járván én elõttem tökéletességgel, teljes szívök és teljes lelkök szerint; ezt mondván, [mondom]: Soha el nem fogy a férfiú te közüled az Izráelnek királyi székibõl."));
husuperobject.biblemap.put("b11_2_5", new husuperobject(11,2,5,"Azt is jól tudod, mit cselekedett én velem Joáb, a Séruja fia, mit cselekedett az Izráel seregeinek két fõvezérével, Abnerrel, a Nér fiával, és Amasával, a Jéter fiával, a kiket megölt, harczi vért ontván békességnek idején, és hintett harczi vért az õ derekának övére és az õ lábának saruira."));
husuperobject.biblemap.put("b11_2_6", new husuperobject(11,2,6,"Cselekedjél a te bölcseséged szerint, és ne engedd, hogy megõszülvén, békességgel menjen a koporsóba."));
husuperobject.biblemap.put("b11_2_7", new husuperobject(11,2,7,"De a gileádbeli Barzillainak fiaival cselekedjél irgalmasságot, és legyenek a te asztalod vendégei, mert így közeledtek õk is hozzám, mikor Absolon, a te testvéred elõl menekültem."));
husuperobject.biblemap.put("b11_2_8", new husuperobject(11,2,8,"És ímé veled van Sémei, Gérának fia, a Bahurimbeli Benjáminita, a ki gyalázatosan szidalmazott akkor, mikor Mahanáimba mentem; de aztán, mikor elém alájött a Jordánhoz, megesküdtem néki az Úrra, és mondék: Nem öllek meg téged fegyverrel;"));
husuperobject.biblemap.put("b11_2_9", new husuperobject(11,2,9,"Te azonban ne hagyd õt büntetés nélkül, és mivel eszes férfiú vagy, tudod, mit kelljen cselekedned vele, hogy az õ vénségét vérrel bocsássad a koporsóba."));
husuperobject.biblemap.put("b11_2_10", new husuperobject(11,2,10,"Azután elaludt Dávid az õ atyáival, és eltemetteték a Dávid városában."));
husuperobject.biblemap.put("b11_2_11", new husuperobject(11,2,11,"Az idõ pedig, a melyben uralkodék Dávid Izráelen, negyven esztendõ. Hebronban uralkodék hét esztendeig, Jeruzsálemben pedig uralkodék harminchárom esztendeig."));
husuperobject.biblemap.put("b11_2_12", new husuperobject(11,2,12,"Azután Salamon ült Dávidnak, az õ atyjának királyi székibe, és megerõsödék az õ királyi birodalma felette igen."));
husuperobject.biblemap.put("b11_2_13", new husuperobject(11,2,13,"De Adónia, a Haggit fia beméne Bethsabéhoz, a Salamon anyjához, és az monda: Békességes-é a te jöveteled? Ki felele: Békességes."));
husuperobject.biblemap.put("b11_2_14", new husuperobject(11,2,14,"És monda: Beszédem volna veled. Monda az: Szólj."));
husuperobject.biblemap.put("b11_2_15", new husuperobject(11,2,15,"Akkor monda [Adónia:] Te tudod, hogy az ország az enyém vala, és az egész Izráel reám néz vala, hogy én uralkodjam; de elvéteték az ország tõlem, és lõn az én atyámfiáé, mert az Úrtól adattaték néki."));
husuperobject.biblemap.put("b11_2_16", new husuperobject(11,2,16,"Most egy kérést kérek tõled, ne szégyenítsd meg orczámat. Az pedig monda: Beszélj!"));
husuperobject.biblemap.put("b11_2_17", new husuperobject(11,2,17,"És monda: Beszélj, kérlek Salamon királylyal; mert õ a te kérésedet meg nem veti, hogy adja nékem a Súnembõl való Abiságot feleségül."));
husuperobject.biblemap.put("b11_2_18", new husuperobject(11,2,18,"Felele Bethsabé: Jól van, majd szólok melletted a királynak."));
husuperobject.biblemap.put("b11_2_19", new husuperobject(11,2,19,"És beméne Bethsabé Salamon királyhoz, hogy beszéljen vele Adónia érdekében; és felkele a király, és elébe menvén meghajtá magát elõtte, és leüle királyi székibe; és széket tétete a király anyjának, hogy üljön az õ jobbkeze felõl."));
husuperobject.biblemap.put("b11_2_20", new husuperobject(11,2,20,"És monda [Bethsabé]: Egy kis kérést kérek tõled, ne szégyenítsd meg orczámat. És monda néki a király: Kérj édes anyám; mert nem szégyenítem meg orczádat."));
husuperobject.biblemap.put("b11_2_21", new husuperobject(11,2,21,"Monda õ: Adassék a Súnembõl való Abiság Adóniának, a te testvérednek feleségül."));
husuperobject.biblemap.put("b11_2_22", new husuperobject(11,2,22,"Akkor felele Salamon király, és monda az õ anyjának: De miért kéred te a Súnembeli Abiságot Adóniának? Kérjed néki az országot is; mert õ az én bátyám, és vele [egyetért] Abjátár pap, és Joáb, a Séruja fia."));
husuperobject.biblemap.put("b11_2_23", new husuperobject(11,2,23,"És megesküvék Salamon király az Úrra, mondván: Úgy cselekedjék velem az Isten, és úgy segéljen, hogy Adónia a saját élete ellen szólotta ezt a beszédet!"));
husuperobject.biblemap.put("b11_2_24", new husuperobject(11,2,24,"Most azért él az Úr, a ki megerõsített engem és ültetett engem az én atyámnak, Dávidnak királyi székibe, és a ki házat szerzett nékem, a mint megmondotta volt: ma Adóniának meg kell halnia!"));
husuperobject.biblemap.put("b11_2_25", new husuperobject(11,2,25,"Elküldé azért Salamon király Benáját, a Jójada fiát, a ki levágá õt, és meghala."));
husuperobject.biblemap.put("b11_2_26", new husuperobject(11,2,26,"Abjátár papnak pedig monda a király: Menj el Anathótba, a te jószágodba, mert halálnak fia vagy; de ma meg nem öletlek, mivel te hordoztad az Úr Istennek ládáját Dávid, az én atyám elõtt, és mivel az én atyámnak minden nyomorúságaiban részes voltál."));
husuperobject.biblemap.put("b11_2_27", new husuperobject(11,2,27,"És kiûzé Salamon Abjátárt, hogy ne legyen az Úrnak papja, hogy beteljesedjék az Úrnak beszéde, a melyet szólott vala az Éli háza felõl Silóban."));
husuperobject.biblemap.put("b11_2_28", new husuperobject(11,2,28,"És eljutott ez a hír Joábhoz, mert Joáb Adóniához hajlott vala, noha azelõtt nem hajlott vala Absolonhoz, és elfuta Joáb az Úrnak sátorába, és megfogá az oltárnak szarvait."));
husuperobject.biblemap.put("b11_2_29", new husuperobject(11,2,29,"Hírül adák pedig Salamon királynak, hogy Joáb az Úrnak sátorához futott, és ímé az oltár mellett áll. Ekkor elküldé Salamon Benáját, a Jójada fiát, mondván: Menj el, vágd le õt."));
husuperobject.biblemap.put("b11_2_30", new husuperobject(11,2,30,"Mikor pedig Benája az Úrnak sátorához ért, monda néki: Ezt mondja a király: Jõjj ki. Kinek felele [Joáb]: Nem, itt akarok meghalni. És megvivé Benája a királynak e dolgot, mondván: Így szólott Joáb és így felelt nékem."));
husuperobject.biblemap.put("b11_2_31", new husuperobject(11,2,31,"És monda néki a király: Cselekedjél úgy, a mint szólott; vágd le õt és temesd el, hogy elvedd az ártatlan vért, a melyet kiontott Joáb, én rólam és az én atyámnak házáról."));
husuperobject.biblemap.put("b11_2_32", new husuperobject(11,2,32,"És fordítsa az Úr az õ fejére az õ vérét, a miért nálánál igazabb és jobb két férfira támadott, és megölé õket fegyverrel az én atyámnak, Dávidnak tudta nélkül, [tudniillik] Abnert, Nérnek fiát, az Izráel seregének fõvezérét és Amasát, Jéternek fiát, Júda vitézeinek fõvezérét."));
husuperobject.biblemap.put("b11_2_33", new husuperobject(11,2,33,"Ezeknek a vére térjen Joáb fejére és az õ magvának fejére mindörökké: Dávidnak pedig és az õ magvának és az õ házának és királyi székének békessége legyen az Úrtól mindörökké."));
husuperobject.biblemap.put("b11_2_34", new husuperobject(11,2,34,"És elméne Benája, Jójada fia, és reá rohanván megölé õt; és eltemetteték az õ házában, a pusztában."));
husuperobject.biblemap.put("b11_2_35", new husuperobject(11,2,35,"Rendelé pedig a király a Jójada fiát õ helyette a sereg fölé, és Sádók papot rendelé a király Abjátár helyett."));
husuperobject.biblemap.put("b11_2_36", new husuperobject(11,2,36,"És elkülde a király, és magához hivatá Sémeit, és monda néki: Építs házat magadnak Jeruzsálemben és lakjál ott; és onnét ne menj ki se ide, se tova."));
husuperobject.biblemap.put("b11_2_37", new husuperobject(11,2,37,"Mert valamely nap kimenéndesz, és általmenéndesz a Kidron patakján, tudd meg, hogy meg kell halnod, a te véred lészen tennen fejeden."));
husuperobject.biblemap.put("b11_2_38", new husuperobject(11,2,38,"És monda Sémei a királynak: Tetszik nékem e beszéd; a miképen szólott az én uram, a király, a képen cselekeszik a te szolgád; és sok ideig lakék Sémei Jeruzsálemben."));
husuperobject.biblemap.put("b11_2_39", new husuperobject(11,2,39,"Lõn azonban három esztendõ mulva, hogy Sémeinek két szolgája elszökött Ákishoz, Maaka fiához, a Gáthbeli királyhoz; és hírül adák Sémeinek, mondván: Ímé a te szolgáid Gáthban [vannak]."));
husuperobject.biblemap.put("b11_2_40", new husuperobject(11,2,40,"Ekkor felkelt Sémei, és megnyergelé szamarát, és elméne Gáthba Ákishoz, hogy megkeresse az õ szolgáit. Oda érvén Sémei, meghozá szolgáit Gáthból."));
husuperobject.biblemap.put("b11_2_41", new husuperobject(11,2,41,"Hírül adák pedig Salamonnak, hogy elment Sémei Jeruzsálembõl Gáthba, és haza jött."));
husuperobject.biblemap.put("b11_2_42", new husuperobject(11,2,42,"Akkor elkülde a király, és magához hivatá Sémeit, és monda néki: Nemde esküvéssel kényszerítettelek-é téged az Úrra, és bizonyságot tettem néked, ezt mondván: Valamely napon kimenéndesz, s ide s tova menéndesz, bizonynyal tudjad, hogy meghalsz; és azt mondád nékem: Tetszik e beszéd, megértettem."));
husuperobject.biblemap.put("b11_2_43", new husuperobject(11,2,43,"Miért nem tartottad hát meg az Úr elõtt való esküvést, és a parancsolatot, a melyet néked parancsoltam?"));
husuperobject.biblemap.put("b11_2_44", new husuperobject(11,2,44,"Monda annakfelette a király Sémeinek: Te tudod mindazt a gonoszságot, a melyrõl a te szíved bizonyság, és a melyet atyámmal Dáviddal cselekedtél: az Úr most mindazt a gonoszságot a saját fejedre fordította."));
husuperobject.biblemap.put("b11_2_45", new husuperobject(11,2,45,"Salamon király pedig áldott lészen, és Dávidnak királyi széke lészen állandó az Úr elõtt mindörökké."));
husuperobject.biblemap.put("b11_2_46", new husuperobject(11,2,46,"És parancsola a király Benájának, a Jójada fiának, a ki elméne, és levágá [Sémeit], és meghala. És az ország megerõsödék Salamon kezében."));
husuperobject.biblemap.put("b11_3_1", new husuperobject(11,3,1,"Sógorságot szerze azután Salamon a Faraóval, az Égyiptombeli királylyal; és elvevé a Faraó leányát, és hozá Dávidnak városába, míg elvégezé az õ házának és az Úr házának építését és Jeruzsálemnek kõfalát köröskörül."));
husuperobject.biblemap.put("b11_3_2", new husuperobject(11,3,2,"De a nép áldozik vala a magas helyeken; mert nem építtetett vala ház az Úr nevének mind ez ideig."));
husuperobject.biblemap.put("b11_3_3", new husuperobject(11,3,3,"Szereté pedig Salamon az Urat, járván Dávidnak, az õ atyjának parancsolatiban, kivéve, hogy a magas helyeken áldozott, és ott tömjénezett."));
husuperobject.biblemap.put("b11_3_4", new husuperobject(11,3,4,"És mikor Gibeonba ment a király, hogy ott áldozzék, mert ott volt a nagy magaslat, és Salamon azon az oltáron áldozott égõáldozatul ezer barmot:"));
husuperobject.biblemap.put("b11_3_5", new husuperobject(11,3,5,"Megjelenék Gibeonban az Úr Salamonnak azon éjjel álmában, és monda az Isten: Kérj, a mit [akarsz,] hogy adjak néked."));
husuperobject.biblemap.put("b11_3_6", new husuperobject(11,3,6,"És monda Salamon: Te a te szolgáddal, az én atyámmal, Dáviddal nagy irgalmasságot cselekedtél, a miképen [õ is] járt elõtted híven, igazán és hozzád egyenes szívvel, és megtartottad néki ezt a nagy irgalmasságot, hogy néki fiat adtál, a ki az õ királyi székében ül, a mint e mai napon megtetszik."));
husuperobject.biblemap.put("b11_3_7", new husuperobject(11,3,7,"És most, óh én Uram Istenem, te tetted a te szolgádat királylyá, Dávid, az én atyám helyett. Én pedig kicsiny gyermek vagyok, nem tudok kimenni és bejönni."));
husuperobject.biblemap.put("b11_3_8", new husuperobject(11,3,8,"És a te szolgád a te néped között van, a melyet te [magadnak] választottál, nagy nép ez, a mely meg nem számláltathatik, meg sem írattathatik a sokaság miatt."));
husuperobject.biblemap.put("b11_3_9", new husuperobject(11,3,9,"Adj azért a te szolgádnak értelmes szívet, hogy tudja ítélni a te népedet, és tudjon választást tenni a jó és gonosz között; mert kicsoda kormányozhatja ezt a te nagy népedet?"));
husuperobject.biblemap.put("b11_3_10", new husuperobject(11,3,10,"És tetszék e beszéd az Úrnak, hogy Salamon ilyen dolgot kért."));
husuperobject.biblemap.put("b11_3_11", new husuperobject(11,3,11,"Monda azért az Isten néki: Mivelhogy ezt kérted tõlem, és nem kértél magadnak hosszú életet, sem nem kértél gazdagságot, sem pedig nem kérted a te ellenségidnek lelkét; hanem bölcseséget kértél az ítélettételre:"));
husuperobject.biblemap.put("b11_3_12", new husuperobject(11,3,12,"Ímé a te beszéded szerint cselekszem, ímé adok néked bölcs és értelmes szívet, úgy hogy hozzád hasonló nem volt te elõtted, és utánad sem támad olyan, mint te."));
husuperobject.biblemap.put("b11_3_13", new husuperobject(11,3,13,"Sõt még a mit nem kértél, azt is megadom néked, gazdagságot és dicsõséget: úgy hogy a királyok között nem lesz hozzád hasonló senki minden te idõdben."));
husuperobject.biblemap.put("b11_3_14", new husuperobject(11,3,14,"És ha az én útaimon járándasz, megõrizvén az én végzéseimet és parancsolatimat, a miképen járt a te atyád, Dávid: meghosszabbítom életed idejét."));
husuperobject.biblemap.put("b11_3_15", new husuperobject(11,3,15,"És mikor felserkent Salamon, ímé álom volt. És méne Jeruzsálembe, és álla az Úr szövetségének ládája elé, és áldozék egészen égõáldozatokat, és készíte hálaáldozatokat, és szerze nagy lakomát minden szolgáinak."));
husuperobject.biblemap.put("b11_3_16", new husuperobject(11,3,16,"Abban az idõben jött a királyhoz két parázna asszony, és megálla õ elõtte."));
husuperobject.biblemap.put("b11_3_17", new husuperobject(11,3,17,"És monda az egyik asszony: Kérlek uram, én és ez az asszony egy házban laktunk, és szültem õ nála abban a házban."));
husuperobject.biblemap.put("b11_3_18", new husuperobject(11,3,18,"És harmadnappal az én szülésem után, ez az asszony is szült, és együtt valánk, senki idegen nem volt velünk a házban, hanem csak mi ketten valánk abban a házban."));
husuperobject.biblemap.put("b11_3_19", new husuperobject(11,3,19,"És ennek az asszonynak éjszaka meghalt a fia: mert ráfeküdt."));
husuperobject.biblemap.put("b11_3_20", new husuperobject(11,3,20,"És felkelt éjfélkor, és elvitte az én fiamat mellõlem, mert a te szolgálóleányod aludt, és azt maga mellé fekteté, míg az õ meghalt fiát én mellém fektette."));
husuperobject.biblemap.put("b11_3_21", new husuperobject(11,3,21,"Mikor pedig hajnalban felkeltem, hogy megszoptassam az én fiamat: ímé, megholt; de reggel jól megnézegetvén, látám, hogy az nem az én fiam, a kit én szültem."));
husuperobject.biblemap.put("b11_3_22", new husuperobject(11,3,22,"Monda pedig a másik asszony: Nem úgy van, az én fiam az, a ki él, a te fiad pedig az, a ki meghalt. Amaz viszont monda: Nem, hanem a te fiad az, a ki meghalt, és az én fiam az, a ki él. És ekképen versengettek a király elõtt."));
husuperobject.biblemap.put("b11_3_23", new husuperobject(11,3,23,"Akkor monda a király: Ez azt mondja: Ez az én fiam, a ki él, és a te fiad az, a ki meghalt; amaz meg ezt mondja: Semmiképen nem, hanem a te fiad az, a ki meghalt, és az én fiam az, a ki él."));
husuperobject.biblemap.put("b11_3_24", new husuperobject(11,3,24,"És monda a király: Hozzatok nékem kardot! És mikor oda hozák a kardot a király elé,"));
husuperobject.biblemap.put("b11_3_25", new husuperobject(11,3,25,"Monda a király: Vágjátok két részre az eleven gyermeket, és adjátok az egyik részt egyiknek, a másikat pedig a másiknak."));
husuperobject.biblemap.put("b11_3_26", new husuperobject(11,3,26,"Ekkor monda az az asszony, a kié vala az élõ gyermek, a királynak, mert megindult szíve gyermekén: Kérlek, uram, adjátok néki az élõ gyermeket, és ne öljétek meg õt. A másik pedig azt mondja vala. Se enyim, se tied ne legyen; vágjátok ketté."));
husuperobject.biblemap.put("b11_3_27", new husuperobject(11,3,27,"Akkor felele a király, és monda: Adjátok amannak az élõ gyermeket, és meg ne öljétek azt, [mert] az az õ anyja."));
husuperobject.biblemap.put("b11_3_28", new husuperobject(11,3,28,"És mikor hallotta az egész Izráel ezt az ítéletet, a melyet tett vala a király, félék a királynak orczáját, mert látták, hogy Isten bölcsesége van az õ szívében az ítélettételre."));
husuperobject.biblemap.put("b11_4_1", new husuperobject(11,4,1,"És lõn Salamon király az egész Izráel felett királylyá."));
husuperobject.biblemap.put("b11_4_2", new husuperobject(11,4,2,"Ezek [valának] pedig az õ fõemberei: Azária, Sádók papnak fia."));
husuperobject.biblemap.put("b11_4_3", new husuperobject(11,4,3,"Elihóref és Ahija, Sisának fiai íródeákok, Jósafát, az Ahilud fia, emlékíró vala."));
husuperobject.biblemap.put("b11_4_4", new husuperobject(11,4,4,"És Benája, a Jójada fia, a sereg hadnagya; Sádók pedig és Abjátár papok."));
husuperobject.biblemap.put("b11_4_5", new husuperobject(11,4,5,"És Azáriás, a Nátán fia, a tiszttartók elõljárója; és Zábud, a Nátán fia, fõtanácsos és a király barátja."));
husuperobject.biblemap.put("b11_4_6", new husuperobject(11,4,6,"És Ahisár udvarbíró; Adónirám pedig, az Abda fia, kincstartó."));
husuperobject.biblemap.put("b11_4_7", new husuperobject(11,4,7,"Vala pedig Salamonnak tizenkét tiszttartója az egész Izráelen, a kik ellátták a királyt és az õ háznépét: esztendõnként mindeniknek egy hónapig kellett az ellátásról gondot viselnie."));
husuperobject.biblemap.put("b11_4_8", new husuperobject(11,4,8,"És ezek azoknak neveik: Húrnak fia az Efraim hegyén."));
husuperobject.biblemap.put("b11_4_9", new husuperobject(11,4,9,"Dékernek fia Mákásban, Sahálbimban, Béth-Semesben és Elonban és Béth- Hanánban."));
husuperobject.biblemap.put("b11_4_10", new husuperobject(11,4,10,"Hésednek fia Arúbotban, õ hozzá tartozott Szókó és Héfer egész földe."));
husuperobject.biblemap.put("b11_4_11", new husuperobject(11,4,11,"Abinádáb fiaié volt Dór egész határa. A Salamon leánya, Táfát, vala néki felesége."));
husuperobject.biblemap.put("b11_4_12", new husuperobject(11,4,12,"Bahana, az Ahilud fia, [bírja vala] Taanákot és Megiddót és egész Béth- Seánt, mely Sartána mellett vala Jezréel alatt, Béth-Seántól fogva mind Abela Méholáig és mind Jokméámon túl."));
husuperobject.biblemap.put("b11_4_13", new husuperobject(11,4,13,"Gébernek fia, Rámóth Gileádban: övé valának Jáirnak, a Manasse fiának falui, melyek Gileádban [valának;] az övé volt az Argób tartománya, mely Básánban vala, hatvan nagy város kõfallal és érczzárakkal [megerõsítve."));
husuperobject.biblemap.put("b11_4_14", new husuperobject(11,4,14,"Ahinádáb, Iddó fia Mahanáimban."));
husuperobject.biblemap.put("b11_4_15", new husuperobject(11,4,15,"Ahimaás Naftaliban; a Salamon leányát, Bosmátát vevé magának feleségül õ is."));
husuperobject.biblemap.put("b11_4_16", new husuperobject(11,4,16,"Bahana, Khúsai fia, Áserben és Alóthban."));
husuperobject.biblemap.put("b11_4_17", new husuperobject(11,4,17,"Jósafát, Paruákh fia, Issakhárban."));
husuperobject.biblemap.put("b11_4_18", new husuperobject(11,4,18,"Simei, Éla fia, Benjáminban."));
husuperobject.biblemap.put("b11_4_19", new husuperobject(11,4,19,"Géber, Uri fia, a Gileád földében, Sihonnak, az Emoreus királyának földében, és Ógnak, a Básánbeli királynak [földében;] és [ez] egy tiszttartó [bírja vala] azt a földet."));
husuperobject.biblemap.put("b11_4_20", new husuperobject(11,4,20,"És Júda és Izráel megsokasodott, olyan sok volt, mint a tenger mellett való föveny, és ettek, ittak és vigadtak."));
husuperobject.biblemap.put("b11_4_21", new husuperobject(11,4,21,"Salamon pedig uralkodék minden országokon a folyóvíztõl fogva egész a Filiszteusok földéig és Égyiptomnak határáig, és ajándékokat hoznak vala, és szolgálnak vala Salamonnak, életének minden idejében."));
husuperobject.biblemap.put("b11_4_22", new husuperobject(11,4,22,"És Salamon eledele naponként [ez] vala: harmincz véka zsemlyeliszt és hatvan véka közönséges liszt;"));
husuperobject.biblemap.put("b11_4_23", new husuperobject(11,4,23,"Tíz hízlalt ökör, húsz füvön járt ökör és száz juh; a szarvasokon, õzeken, bivalokon és hízlalt madarakon kivül."));
husuperobject.biblemap.put("b11_4_24", new husuperobject(11,4,24,"Mert õ uralkodék minden helyeken a folyóvizen túl Thifsától fogva egész Gázáig; minden királyokon, a kik a folyóvizen túl valának; és békessége volt néki minden alattvalóitól köröskörül."));
husuperobject.biblemap.put("b11_4_25", new husuperobject(11,4,25,"És lakozék Júda és Izráel bátorsággal, kiki mind az õ szõlõtõje és fügefája alatt. Dántól fogva Bersebáig; Salamonnak minden idejében."));
husuperobject.biblemap.put("b11_4_26", new husuperobject(11,4,26,"És Salamonnak volt negyvenezer szekérbe való lova az istállókban, és tizenkétezer lovagja."));
husuperobject.biblemap.put("b11_4_27", new husuperobject(11,4,27,"És ezek a tiszttartók ellátták Salamon királyt és mindazokat, a kik a Salamon király asztalánál valának, kiki az õ hónapján, minden fogyatkozás nélkül."));
husuperobject.biblemap.put("b11_4_28", new husuperobject(11,4,28,"Azután árpát és szalmát is hoztak a lovaknak és a paripáknak arra a helyre, a hol a [király] volt, kiki az õ rendelete szerint."));
husuperobject.biblemap.put("b11_4_29", new husuperobject(11,4,29,"És az Isten adott bölcseséget Salamonnak és igen nagy értelmet és mély szívet, mint a fövény, mely a tenger partján van."));
husuperobject.biblemap.put("b11_4_30", new husuperobject(11,4,30,"Úgy hogy a Salamon bölcsesége nagyobb volt, mint a napkelet minden fiainak bölcsesége és Égyiptomnak egész bölcsesége."));
husuperobject.biblemap.put("b11_4_31", new husuperobject(11,4,31,"Sõt bölcsebb volt minden embereknél, [még] az Ezráhita Ethánnál is és Hémánnál, Kálkólnál és Dardánál, a Máhol fiainál; és híre neve vala minden nemzetségek között köröskörül."));
husuperobject.biblemap.put("b11_4_32", new husuperobject(11,4,32,"És szerze háromezer példabeszédet, és az õ énekeinek [száma] ezer és öt volt."));
husuperobject.biblemap.put("b11_4_33", new husuperobject(11,4,33,"Szólott a fákról is, a Libánon czédrusfájától az izsópig, a mely a falból nevekedik ki; és szólott a barmokról, a madarakról, a csúszó- mászó állatokról és a halakról is."));
husuperobject.biblemap.put("b11_4_34", new husuperobject(11,4,34,"És jõnek vala minden népek közül, hogy hallgassák a Salamon bölcseségét; a földnek minden királyaitól, a kik hallották vala az õ bölcseségét."));
husuperobject.biblemap.put("b11_5_1", new husuperobject(11,5,1,"És Hírám, Tírus királya elküldé az õ szolgáit Salamonhoz, mikor meghallotta, hogy õt kenték királylyá az õ atyja helyett; mert Hírám szerette Dávidot teljes életében."));
husuperobject.biblemap.put("b11_5_2", new husuperobject(11,5,2,"És külde Salamon Hírámhoz, ezt izenvén néki:"));
husuperobject.biblemap.put("b11_5_3", new husuperobject(11,5,3,"Te tudod, hogy Dávid, az én atyám nem építhete házat az Úrnak, az õ Istenének nevének a háborúk miatt, a melyekkel õt körülvették vala, mígnem az Úr az õ lábainak talpa alá vetette azokat;"));
husuperobject.biblemap.put("b11_5_4", new husuperobject(11,5,4,"De most az Úr, az én Istenem nékem nyugodalmat adott mindenfelõl, [úgy hogy] semmi ellenségem és senkitõl semmi bántásom nincs."));
husuperobject.biblemap.put("b11_5_5", new husuperobject(11,5,5,"Ímé azt gondoltam magamban, hogy házat építek az Úrnak, az én Istenemnek nevének, a miképen szólott az Úr Dávidnak, az én atyámnak, ezt mondván: A te fiad, a kit helyetted ültetek a te királyi székedbe, õ építi meg azt a házat az én nevemnek."));
husuperobject.biblemap.put("b11_5_6", new husuperobject(11,5,6,"Most azért parancsold meg, hogy vágjanak nékem czédrusfákat a Libánonon. Az én szolgáim is együtt lesznek a te szolgáiddal; a te szolgáidnak pedig jutalmát megadom néked mind a szerint, a mit mondándasz; mert tudod, hogy nincsen mi közöttünk olyan ember, a ki a favágáshoz úgy értene, mint a Sidonbeliek."));
husuperobject.biblemap.put("b11_5_7", new husuperobject(11,5,7,"Mikor azért meghallotta Hírám a Salamon izenetét, igen megörült, és monda: Áldott legyen e mai napon az Úr, a ki Dávidnak bölcs fiat adott e nagy népen."));
husuperobject.biblemap.put("b11_5_8", new husuperobject(11,5,8,"És elkülde Hírám Salamonhoz, ezt izenvén: Megértettem a mi felõl küldöttél hozzám; én megteszem minden kivánságodat mind a czédrusfákra, mind a fenyõfákra [nézve]."));
husuperobject.biblemap.put("b11_5_9", new husuperobject(11,5,9,"Az én szolgáim a Libánonról aláviszik a tengerre [a fákat:] én pedig azokat tutajokra rakatván, a tengeren addig a helyig vitetem, a melyet te megizentetsz nékem, és azokat ott kihányatom, és te vitesd el. Te pedig abban teljesítsd kivánságomat, hogy adj eledelt az én háznépemnek."));
husuperobject.biblemap.put("b11_5_10", new husuperobject(11,5,10,"Ada azért Hírám Salamonnak czédrusfákat és fenyõfákat, minden kivánsága szerint."));
husuperobject.biblemap.put("b11_5_11", new husuperobject(11,5,11,"Salamon pedig ada Hírámnak húszezer véka búzát az õ háznépének táplálására, és húszezer kórus sajtolt olajat. Ezt adja vala Salamon Hírámnak esztendõrõl-esztendõre."));
husuperobject.biblemap.put("b11_5_12", new husuperobject(11,5,12,"Az Úr azért bölcsességet ada Salamonnak, a mint megmondotta vala néki; és békesség lõn Hírám és Salamon között, és õk szövetséget tõnek egymással."));
husuperobject.biblemap.put("b11_5_13", new husuperobject(11,5,13,"Salamon király pedig robotosokat szedete az egész Izráelbõl; és harminczezer ember lõn robotossá."));
husuperobject.biblemap.put("b11_5_14", new husuperobject(11,5,14,"A kiket aztán elkülde a Libánonra, minden hónapra tíz-tízezer ember egymás után. Egy hónapig a Libánon [hegyén] valának, két hónapig az õ házoknál. Adónirám vala pedig a robotosok feje."));
husuperobject.biblemap.put("b11_5_15", new husuperobject(11,5,15,"Ezenkivül Salamonnak hetvenezer teherhordója, és nyolczvanezer kõvágója volt a hegyen."));
husuperobject.biblemap.put("b11_5_16", new husuperobject(11,5,16,"A pallérok fejedelmein kivül, a kikre Salamon a munkának igazgatását bízta volt, a kik háromezeren és háromszázan [valának], a kik a munkálkodó népet szorgalmaztatták."));
husuperobject.biblemap.put("b11_5_17", new husuperobject(11,5,17,"És megparancsolá a király, hogy nagy és drága köveket vágjanak ki, nevezetesen faragott köveket a ház fundamentomául;"));
husuperobject.biblemap.put("b11_5_18", new husuperobject(11,5,18,"Melyeket kifaragának a Salamon kõmívesei és a Hírám ácsai és a Gibleusok; és elkészíték a fákat és a köveket a ház építéséhez."));
husuperobject.biblemap.put("b11_6_1", new husuperobject(11,6,1,"És megépítteték az Úrnak háza az Izráel fiainak Égyiptom földébõl való kijövetele után a négyszáznyolczvanadik esztendõben, Salamon Izráel felett való uralkodásának negyedik esztendejében, a Zif hónapban, mely a második hónap."));
husuperobject.biblemap.put("b11_6_2", new husuperobject(11,6,2,"És a ház, a melyet Salamon király az Úrnak építe, hatvan sing hosszú, húsz sing széles és harmincz sing magas volt."));
husuperobject.biblemap.put("b11_6_3", new husuperobject(11,6,3,"És egy tornácz vala a ház temploma elõtt, a melynek a hossza húsz sing volt, a háznak szélessége szerint; a szélessége pedig tíz sing volt a ház hosszában."));
husuperobject.biblemap.put("b11_6_4", new husuperobject(11,6,4,"És építe a házon ablakokat is lezárt rostélyzattal."));
husuperobject.biblemap.put("b11_6_5", new husuperobject(11,6,5,"És építe a ház falaira emeleteket köröskörül, a ház falai körül a szent helyen és a szentek szentjén, és készíte mellék helyiségeket körül."));
husuperobject.biblemap.put("b11_6_6", new husuperobject(11,6,6,"Az alsó emelet [belsõ] szélessége öt sing, a középsõ szélessége hat sing és a harmadik szélessége hét sing volt, és bemélyedéseket építe a ház körül kivülrõl, hogy az [emeletek gerendái] ne nyúljanak be a ház falaiba."));
husuperobject.biblemap.put("b11_6_7", new husuperobject(11,6,7,"Mikor pedig a ház építteték, a kõbányának egészen kifaragott köveibõl építtetett, úgy hogy sem kalapácsnak, sem fejszének, sem valami egyéb vasszerszámnak pengése nem hallattatott a háznak felépítésénél."));
husuperobject.biblemap.put("b11_6_8", new husuperobject(11,6,8,"[Az alsó emelet] középsõ mellékhelyiségéhez egy ajtó vezetett a ház jobb oldalán, és egy csiga-grádics vitt fel a [középsõ] emeletbe, és a középsõbõl a harmadikba."));
husuperobject.biblemap.put("b11_6_9", new husuperobject(11,6,9,"Megépíté ekként azt a házat és elvégezé, és befedé a házat gerendákkal és czédrusfadeszkákkal."));
husuperobject.biblemap.put("b11_6_10", new husuperobject(11,6,10,"És megépíté az emeleteket az egész ház körül, a melyeknek magasságok öt- [öt] sing volt, és a házhoz czédrusfagerendákkal ragasztattak."));
husuperobject.biblemap.put("b11_6_11", new husuperobject(11,6,11,"És lõn az Úrnak beszéde Salamonhoz, ezt mondván:"));
husuperobject.biblemap.put("b11_6_12", new husuperobject(11,6,12,"Ez ama ház, a melyet te építesz: Ha az én rendelésimben jársz, és az én ítéletim szerint cselekszel, és megtartod minden én parancsolatimat, azokban járván: [Én is] bizonyára megerõsítem veled az én beszédemet, a melyet szólottam Dávidnak, a te atyádnak;"));
husuperobject.biblemap.put("b11_6_13", new husuperobject(11,6,13,"És az Izráel fiai között lakozom, és nem hagyom el az én népemet, az Izráelt."));
husuperobject.biblemap.put("b11_6_14", new husuperobject(11,6,14,"Megépíté azért Salamon azt a házat, és elvégezé azt."));
husuperobject.biblemap.put("b11_6_15", new husuperobject(11,6,15,"És megbéllelé a ház falait belõl czédrusfával, a ház padlózatától egészen a padlásig beborítá belõl fával; a ház padlózatát pedig beborítá fenyõdeszkákkal."));
husuperobject.biblemap.put("b11_6_16", new husuperobject(11,6,16,"És építe a ház hátulján egy húsz sing hosszú czédrusfa falat a padlózattól egész a padlásig, és építé azt [a ház] hátulsó részének: szentek-szentjének."));
husuperobject.biblemap.put("b11_6_17", new husuperobject(11,6,17,"Az elõtte való szenthely hossza negyven sing volt."));
husuperobject.biblemap.put("b11_6_18", new husuperobject(11,6,18,"Belülrõl az egész ház merõ czédrus volt, kivésett sártökökkel és kinyilt virágbimbókkal, úgy hogy semmi kõ ki nem látszott."));
husuperobject.biblemap.put("b11_6_19", new husuperobject(11,6,19,"És a szentek-szentjét építé a ház belsõ részében, hogy abba helyheztesse az Úr szövetségének ládáját."));
husuperobject.biblemap.put("b11_6_20", new husuperobject(11,6,20,"A szentek-szentje belsõ részének a hossza vala húsz sing, a szélessége is húsz sing, a magassága is húsz sing, és beborítá azt finom aranynyal; az oltárt is beborítá czédrusdeszkákkal."));
husuperobject.biblemap.put("b11_6_21", new husuperobject(11,6,21,"És Salamon beborította a házat belõl finom aranynyal, és arany lánczot vont a belsõ rész elõtt, a melyet szintén bevont aranynyal."));
husuperobject.biblemap.put("b11_6_22", new husuperobject(11,6,22,"Úgy, hogy az egész ház be volt vonva merõ aranynyal, sõt az oltárt is, a mely a szentek-szentje elõtt volt, egészen beborítá aranynyal."));
husuperobject.biblemap.put("b11_6_23", new husuperobject(11,6,23,"És csinált a szentek-szentjébe két tíz sing magas Kérubot olajfából."));
husuperobject.biblemap.put("b11_6_24", new husuperobject(11,6,24,"És öt sing volt az egyik Kérub szárnya, és öt sing a másik Kérub szárnya is, úgy, hogy az egyik szárnya végétõl, a [másik] szárnya végéig tíz sing vala."));
husuperobject.biblemap.put("b11_6_25", new husuperobject(11,6,25,"A másik Kérub is tíz sing volt; és mind a két Kérubnak mind a mértéke, mind a faragása egy vala;"));
husuperobject.biblemap.put("b11_6_26", new husuperobject(11,6,26,"Úgy, hogy az egyik Kérub magassága tíz sing, és ugyanannyi a másik Kérubé is."));
husuperobject.biblemap.put("b11_6_27", new husuperobject(11,6,27,"És helyezteté a Kérubokat a ház belsejébe, és a mint a Kérubok kiterjeszték szárnyukat, az egyiknek szárnya a [ház] egyik falát, a másik Kérub szárnya pedig a másik falát érte; de a ház közepén összeért egyik szárny a másikkal."));
husuperobject.biblemap.put("b11_6_28", new husuperobject(11,6,28,"És beborítá a Kérubokat aranynyal."));
husuperobject.biblemap.put("b11_6_29", new husuperobject(11,6,29,"És a ház összes falain köröskörül kivül és belõl Kérubokat, pálmafákat és kinyilt virágokat metszetett ki."));
husuperobject.biblemap.put("b11_6_30", new husuperobject(11,6,30,"És beborítá még a ház padlóját is aranynyal kivül és belõl."));
husuperobject.biblemap.put("b11_6_31", new husuperobject(11,6,31,"És a szentek-szentjének bemenetelén csinála ajtót olajfából, az ajtófélfák kiszögellése egy ötödrész volt;"));
husuperobject.biblemap.put("b11_6_32", new husuperobject(11,6,32,"És két ajtószárnyat olajfából, és metszete reájok Kérubokat, pálmafákat és kinyilt virágokat, és beborítá azokat aranynyal; a Kérubokat is és a pálmafákat is megaranyoztatá."));
husuperobject.biblemap.put("b11_6_33", new husuperobject(11,6,33,"E képen csinált a templom szenthelyénél is négyszegletû ajtófélfákat olajfából."));
husuperobject.biblemap.put("b11_6_34", new husuperobject(11,6,34,"És csinált két ajtót cziprusfából, és az egyik ajtón is két forgó ajtószárny volt, a másik ajtón is két forgó ajtószárny."));
husuperobject.biblemap.put("b11_6_35", new husuperobject(11,6,35,"És metszete [azokra] Kérubokat és pálmafákat és kinyilt virágokat, és beborítá aranynyal, rá alkalmaztatva azt a metszésre."));
husuperobject.biblemap.put("b11_6_36", new husuperobject(11,6,36,"Azután felépíté a belsõ pitvart három rend faragott kõbõl és egy rend czédrusgerendából."));
husuperobject.biblemap.put("b11_6_37", new husuperobject(11,6,37,"Az õ uralkodásának negyedik esztendejében fundáltaték az Úrnak háza Zif hónapban."));
husuperobject.biblemap.put("b11_6_38", new husuperobject(11,6,38,"És a tizenegyedik esztendõben, Búl havában, (mely a nyolczadik hónap) végezteték el a ház, minden dolga és rendje szerint. És így építé azt hét esztendeig."));
husuperobject.biblemap.put("b11_7_1", new husuperobject(11,7,1,"Azután a maga házát építé Salamon tizenhárom esztendeig, a mely alatt elvégezé az õ házát egészen."));
husuperobject.biblemap.put("b11_7_2", new husuperobject(11,7,2,"Megépíté a Libánon erdõ házát is, melynek hossza száz sing [vala,] szélessége ötven sing, magassága harmincz sing; [építé azt] négy rend czédrusoszlopon és az oszlopokon czédrusgerendák valának."));
husuperobject.biblemap.put("b11_7_3", new husuperobject(11,7,3,"És bepadlá czédrusdeszkákkal felül a gerendák felett, melyek valának negyvenöt oszlopon, mindenik renden tizenöt."));
husuperobject.biblemap.put("b11_7_4", new husuperobject(11,7,4,"És három rend ablak rajta egymással átellenben, három-három ellenében."));
husuperobject.biblemap.put("b11_7_5", new husuperobject(11,7,5,"És mind az ajtók és azoknak oldalfái négyszögûek [valának] az ablakokkal együtt, és egyik ablak a másiknak átellenébe volt mind a három renden."));
husuperobject.biblemap.put("b11_7_6", new husuperobject(11,7,6,"És építé az oszlopcsarnokot, a melynek hossza ötven sing és szélessége harmincz sing volt; és egy tornáczot ez elé, és oszlopokat és vastag gerendákat ezek elé."));
husuperobject.biblemap.put("b11_7_7", new husuperobject(11,7,7,"És építé a trón-termet, a hol ítélt, a törvényházat, a melyet czédrusfával bélelt meg a padlózattól fogva fel a padlásig."));
husuperobject.biblemap.put("b11_7_8", new husuperobject(11,7,8,"Azután a saját házát [építé,] a melyben õ maga lakott, a másik udvarba befelé a teremtõl, hasonlóan a másikhoz, és építe egy házat a Faraó leányának is, a kit feleségül vett Salamon, hasonlót e teremhez."));
husuperobject.biblemap.put("b11_7_9", new husuperobject(11,7,9,"Mindezek drágakövekbõl voltak, mérték szerint kifaragva, fûrészszel metszve minden oldalról, a fundamentomtól a tetõzetig, kivül is mind a nagy pitvarig."));
husuperobject.biblemap.put("b11_7_10", new husuperobject(11,7,10,"Még a fundamentom is drága és nagy kövekbõl volt: tíz singnyi kövekbõl és nyolcz singnyi kövekbõl."));
husuperobject.biblemap.put("b11_7_11", new husuperobject(11,7,11,"És ezeken felül [voltak] a mérték szerint faragott drágakövek és czédrusfák."));
husuperobject.biblemap.put("b11_7_12", new husuperobject(11,7,12,"És a nagy pitvarban köröskörül három sor faragott kõ és egy sor faragott czédrusgerenda volt, épen mint az Úr házának belsõ pitvara és a ház tornácza."));
husuperobject.biblemap.put("b11_7_13", new husuperobject(11,7,13,"És elkülde Salamon király, és elhozatá Hírámot Tírusból."));
husuperobject.biblemap.put("b11_7_14", new husuperobject(11,7,14,"Ez egy özvegy asszonynak volt a fia a Nafthali nemzetségébõl; az õ atyja pedig Tírusbeli rézmíves ember vala; és ez teljes vala bölcseséggel, értelemmel és tudománynyal, hogy tudna csinálni mindenféle mívet rézbõl. Ki mikor Salamon királyhoz jött, minden mívet megcsinála néki."));
husuperobject.biblemap.put("b11_7_15", new husuperobject(11,7,15,"És formála két réz oszlopot, az egyik oszlop magassága tizennyolcz sing volt, és tizenkét sing zsinór éri vala át mind a két oszlopot."));
husuperobject.biblemap.put("b11_7_16", new husuperobject(11,7,16,"És készíte két gömböt érczbõl öntve, hogy azokat az oszlopok tetejére tegye, és öt sing magas volt az egyik gömb és öt sing magas volt a másik gömb."));
husuperobject.biblemap.put("b11_7_17", new husuperobject(11,7,17,"Reczés mívû hálók, lánczmívû zsinórok voltak a gömbökön, a melyek az oszlopok tetején valának; hét volt az egyik gömbön, és hét volt a másik gömbön is."));
husuperobject.biblemap.put("b11_7_18", new husuperobject(11,7,18,"És megkészíté az oszlopokat, és két sor [díszítést tett] köröskörül az egyik hálón, hogy befedje a gömböket, a melyek az oszlopfõkön voltak; és így csinálá a másik gömböt is."));
husuperobject.biblemap.put("b11_7_19", new husuperobject(11,7,19,"És a gömbök, a melyek a tornáczban levõ oszlopok tetején voltak, liliom formájúak voltak, négy singnyiek."));
husuperobject.biblemap.put("b11_7_20", new husuperobject(11,7,20,"Gömbök voltak a két oszlopon, felül, közel a kidomborodáshoz, a mely a háló mellett volt. És kétszáz gránátalma volt sorban köröskörül a második gömbön."));
husuperobject.biblemap.put("b11_7_21", new husuperobject(11,7,21,"És felállítá az oszlopokat a templom tornáczában; és felállítá a jobb oszlopot, és nevezé annak nevét Jákinnak, és felállítá a bal oszlopot, és nevezé annak nevét Boáznak."));
husuperobject.biblemap.put("b11_7_22", new husuperobject(11,7,22,"És az oszlopok tetején liliomok formáltattak vala. És ilyen módon végezteték el az oszlopok míve."));
husuperobject.biblemap.put("b11_7_23", new husuperobject(11,7,23,"És csinála egy öntött tengert, mely egyik szélétõl fogva a másik széléig tíz sing volt, köröskörül kerek, és öt sing magas, és a kerületit harmincz sing zsinór érte vala körül."));
husuperobject.biblemap.put("b11_7_24", new husuperobject(11,7,24,"Valának pedig a peremén alól köröskörül formáltatva apró sártökök; tíz- tíz mindenik singben az egész tenger körül, az ilyen sártököcskék két renddel valának öntve köröskörül a maga öntésében."));
husuperobject.biblemap.put("b11_7_25", new husuperobject(11,7,25,"És tizenkét ökrön állott, három északra fordulva, három nyugotra, három délre és három naptámadatra, és a tenger fölül rajtok, hátok pedig mind befelé."));
husuperobject.biblemap.put("b11_7_26", new husuperobject(11,7,26,"És a vastagsága egy tenyérnyi volt, és a pereme olyan, mint a pohár ajaka, [vagy] a liliom virága, és kétezer báth fért bele."));
husuperobject.biblemap.put("b11_7_27", new husuperobject(11,7,27,"És készíte tíz ércz-talpat, mindegyik talpat négy sing hosszúra, és négy sing szélesre, és három sing magasra."));
husuperobject.biblemap.put("b11_7_28", new husuperobject(11,7,28,"És e talpak így voltak csinálva: oldalaik voltak, és az oldalak a szélpártázatok között voltak."));
husuperobject.biblemap.put("b11_7_29", new husuperobject(11,7,29,"És az oldalakon, a melyek a pártázatok között voltak, oroszlánok, ökrök és Kérubok voltak, és a pártázatokon felül is ekként; az oroszlánok és ökrök alatt pedig czifrázatok voltak bevésett munkával."));
husuperobject.biblemap.put("b11_7_30", new husuperobject(11,7,30,"És mindenik talpnak négy-négy réz kereke és réz tengelye volt, és a négy szegleten támaszok voltak; a mosdómedenczén alul voltak e támaszok öntve, és mindegyiknek oldalán czifrázatok."));
husuperobject.biblemap.put("b11_7_31", new husuperobject(11,7,31,"És a szája az õ kerekded fészkének belsõ részétõl fogva oda felfelé egy singnyi volt, és a [fészeknek] szája kerekded vala, oszlopformára csinálva, másfél singnyi széles, és szájánál is szép metszések valának, és azoknak pártázatai négyszögûek valának, nem gömbölyûek."));
husuperobject.biblemap.put("b11_7_32", new husuperobject(11,7,32,"És négy kerék volt a pártázatok alatt, és a kerekek tengelyei a talphoz voltak erõsítve, és mindenik keréknek magassága másfél sing vala."));
husuperobject.biblemap.put("b11_7_33", new husuperobject(11,7,33,"És e kerekek hasonlóak valának a szekérnek kerekeihez, csakhogy a tengelyeik, kerékagyaik, küllõik, talpaik mind öntve valának."));
husuperobject.biblemap.put("b11_7_34", new husuperobject(11,7,34,"És négy vállacskát csinált mindenik talp négy szegletén; magából a talpból jöttek ki a vállacskák."));
husuperobject.biblemap.put("b11_7_35", new husuperobject(11,7,35,"És e talp tetején fél singnyi kerekded magasság volt köröskörül, és a talp tetején voltak annak tartókezei és pártázatai a maga [öntésébõl.]"));
husuperobject.biblemap.put("b11_7_36", new husuperobject(11,7,36,"Metsze pedig annak tábláira, tartókezeire, pártázataira Kérubokat, oroszlánokat és pálmafákat: mindeniknek az üres helye szerint, és koszorút köröskörül."));
husuperobject.biblemap.put("b11_7_37", new husuperobject(11,7,37,"Így készítette a tíz talpat egy öntésbõl, egy mérték és forma szerint."));
husuperobject.biblemap.put("b11_7_38", new husuperobject(11,7,38,"És csinála tíz mosdómedenczét is rézbõl, és mindenik mosdómedenczébe negyven báth fér vala; és mindenik mosdómedencze négy singnyi vala, és a tíz talp mindenikén egy-egy mosdómedencze vala."));
husuperobject.biblemap.put("b11_7_39", new husuperobject(11,7,39,"És helyhezteté a talpak ötét a ház jobbfelõl való részére, és ötét a ház balfelõl való részére; a tengert pedig helyhezteté a ház jobbrésze felõl naptámadatra dél ellenébe."));
husuperobject.biblemap.put("b11_7_40", new husuperobject(11,7,40,"És készített Hírám még üstöket, lapátokat és medenczéket, és elvégezé az egész munkát, a melyet Salamon királynak csinált az Úr házához;"));
husuperobject.biblemap.put("b11_7_41", new husuperobject(11,7,41,"[Tudniillik] a két oszlopot és a kerek gömböket, a melyek a két oszlop tetejére tétettek, és a két hálót a két kerek gömb befedezésére, a melyek az oszlopok tetejére [tétettek.]"));
husuperobject.biblemap.put("b11_7_42", new husuperobject(11,7,42,"És a négyszáz gránátalmát a két hálóra; két rend gránátalmát minden hálóba, a két kerekded gömb befejezésére, a melyek valának az oszlopok tetején;"));
husuperobject.biblemap.put("b11_7_43", new husuperobject(11,7,43,"A tíz talpat és a talpakra való tíz mosdómedenczét;"));
husuperobject.biblemap.put("b11_7_44", new husuperobject(11,7,44,"Az egy tengert és a tizenkét ökröt a tenger alá;"));
husuperobject.biblemap.put("b11_7_45", new husuperobject(11,7,45,"Fazekakat, lapátokat és medenczéket. És mindezek az edények, a melyeket Hírám Salamon királynak az Úr háza számára készített, csiszolt rézbõl voltak."));
husuperobject.biblemap.put("b11_7_46", new husuperobject(11,7,46,"A Jordán völgyében önteté ezeket a király az agyagos földben, Sukhót és Sártán között."));
husuperobject.biblemap.put("b11_7_47", new husuperobject(11,7,47,"És mindezeket az edényeket Salamon méretlen hagyá, a réznek felettébb való sokasága miatt."));
husuperobject.biblemap.put("b11_7_48", new husuperobject(11,7,48,"És megcsináltata Salamon minden egyéb felszerelést is, mely az Úr házához [szükséges volt]: az arany oltárt, az arany asztalt, melyen a szent kenyerek állottak."));
husuperobject.biblemap.put("b11_7_49", new husuperobject(11,7,49,"És a gyertyatartókat színaranyból, ötöt jobbfelõl és ötöt balfelõl a szentek-szentje elé, és arany virágokat, lámpákat, és hamvvevõket."));
husuperobject.biblemap.put("b11_7_50", new husuperobject(11,7,50,"Azután csészéket, késeket, medenczéket, tömjénezõket és serpenyõket színaranyból, sõt a belsõ ház, a szentek-szentje és a szenthely ajtainak sarkait is mind aranyból."));
husuperobject.biblemap.put("b11_7_51", new husuperobject(11,7,51,"És ilyenképen elvégezteték az egész mû, a melyet Salamon király csinála az Úrnak házához. És bevivé Salamon az õ atyjától, Dávidtól az [Istennek] szenteltetett jószágot, az ezüstöt, aranyat és az edényeket és azokat is az Úr házának kincsei közé tevé."));
husuperobject.biblemap.put("b11_8_1", new husuperobject(11,8,1,"Akkor összegyûjté Salamon az Izráel véneit és a nemzetségeknek minden fejeit, az Izráel fiai atyjaiknak fejedelmeit Salamon királyhoz Jeruzsálembe, hogy az Úr szövetségének ládáját felvigyék a Dávid városából, mely a Sion."));
husuperobject.biblemap.put("b11_8_2", new husuperobject(11,8,2,"És felgyûlének Salamon királyhoz az Izráel minden férfiai az Ethánim havában, az ünnepen; ez a hetedik hónap."));
husuperobject.biblemap.put("b11_8_3", new husuperobject(11,8,3,"Mikor pedig eljöttek mindnyájan az Izráel vénei: felvevék a papok a ládát,"));
husuperobject.biblemap.put("b11_8_4", new husuperobject(11,8,4,"És felvivék az Úr ládáját, a gyülekezetnek sátorát, és mind a szent edényeket, melyek valának a sátorban, és felvivék azokat a papok és a Léviták."));
husuperobject.biblemap.put("b11_8_5", new husuperobject(11,8,5,"És Salamon király és az Izráel egész gyülekezete, a mely õ hozzá gyûlt, [megyen vala] õ vele a láda elõtt, áldozván juhokkal és ökrökkel, melyek meg sem számláltathatnának, sem pedig meg nem irattathatnának a sokaság miatt."));
husuperobject.biblemap.put("b11_8_6", new husuperobject(11,8,6,"És bevivék a papok az Úr szövetségének ládáját az õ helyére a ház belsõ részébe, a szentek-szentjébe, a Kérubok szárnyai alá."));
husuperobject.biblemap.put("b11_8_7", new husuperobject(11,8,7,"Mert a Kérubok kiterjesztik vala szárnyaikat a láda helye felett, és befedik vala a Kérubok a ládát és annak rúdjait felülrõl."));
husuperobject.biblemap.put("b11_8_8", new husuperobject(11,8,8,"És a rudak olyan hosszúak voltak, hogy azok vége látható volt a szenthelyen a szentek-szentjének elsõ része felõl; azonban kivül nem voltak láthatók; és ott vannak mind e mai napig."));
husuperobject.biblemap.put("b11_8_9", new husuperobject(11,8,9,"És nem volt egyéb a ládában, mint csak a két kõtábla, a melyeket Mózes a Hórebnél helyezett bele, mikor az Úr szövetséget kötött az Izráel fiaival, mikor kijövének Égyiptom földébõl."));
husuperobject.biblemap.put("b11_8_10", new husuperobject(11,8,10,"Mikor pedig kijöttek a papok a szenthelybõl: köd tölté be az Úrnak házát,"));
husuperobject.biblemap.put("b11_8_11", new husuperobject(11,8,11,"Úgy, hogy meg sem állhattak a papok az õ szolgálatjokban a köd miatt; mert az Úr dicsõsége töltötte vala be az Úrnak házát."));
husuperobject.biblemap.put("b11_8_12", new husuperobject(11,8,12,"Akkor monda Salamon: Az Úr mondotta, hogy õ lakoznék ködben."));
husuperobject.biblemap.put("b11_8_13", new husuperobject(11,8,13,"Építve építettem házat néked lakásul; helyet, a hol örökké lakjál."));
husuperobject.biblemap.put("b11_8_14", new husuperobject(11,8,14,"Azután megfordult a király, és megáldá Izráel egész gyülekezetét és az Izráel egész gyülekezete felállott."));
husuperobject.biblemap.put("b11_8_15", new husuperobject(11,8,15,"És monda: Áldott legyen az Úr, Izráelnek Istene, a ki szólott az õ szája által Dávidnak, az én atyámnak; és azt az õ hatalmasságával beteljesítette, mondván:"));
husuperobject.biblemap.put("b11_8_16", new husuperobject(11,8,16,"Attól a naptól fogva, a melyen kihoztam az én népemet, az Izráelt Égyiptomból, soha nem választottam egyetlen várost sem az Izráel minden nemzetségei közül, hogy ott nékem házat építenének, a melyben lenne az én nevem; hanem csak Dávidot választottam, hogy õ legyen az én népem, Izráel felett."));
husuperobject.biblemap.put("b11_8_17", new husuperobject(11,8,17,"És ámbár az én atyám, Dávid már elvégezte volt, hogy õ épít házat az Úrnak, Izráel Istene nevének;"));
husuperobject.biblemap.put("b11_8_18", new husuperobject(11,8,18,"De az Úr azt mondá Dávidnak, az én atyámnak: Azt, hogy arra gondoltál, hogy az én nevemnek házat építs, jól cselekedted, hogy szívedben ezt végezted;"));
husuperobject.biblemap.put("b11_8_19", new husuperobject(11,8,19,"Mégis nem te építesz házat nékem, hanem a te fiad, a ki a te ágyékodból származik, az épít házat az én nevemnek."));
husuperobject.biblemap.put("b11_8_20", new husuperobject(11,8,20,"És beteljesíté az Úr az õ beszédét, a melyet szólott; mert felkelék az én atyám, Dávid helyett, és ülék az Izráel királyi székibe, a miképen megmondotta vala az Úr, és megépítém a házat az Úr, Izráel Istene nevének."));
husuperobject.biblemap.put("b11_8_21", new husuperobject(11,8,21,"És helyet szerzettem ott a ládának, a melyben az Úrnak szövetsége vagyon, a melyet szerzett a mi atyáinkkal, mikor kihozta õket Égyiptom földébõl."));
husuperobject.biblemap.put("b11_8_22", new husuperobject(11,8,22,"És oda állott Salamon az Úr oltára elé, az Izráel egész gyülekezetével szembe, és felemelé kezeit az ég felé,"));
husuperobject.biblemap.put("b11_8_23", new husuperobject(11,8,23,"És monda: Uram, Izráel Istene! nincsen hozzád hasonló Isten, sem az égben ott fenn, sem a földön itt alant, a ki megtartod a szövetséget és az irgalmasságot a te szolgáidnak, a kik te elõtted teljes szívvel járnak;"));
husuperobject.biblemap.put("b11_8_24", new husuperobject(11,8,24,"A ki megtartottad azt, a mit a te szolgádnak, Dávidnak, az én atyámnak szólottál; mert magad szólottál néki, és a te hatalmaddal beteljesítetted, a mint e mai napon [megtetszik."));
husuperobject.biblemap.put("b11_8_25", new husuperobject(11,8,25,"Most azért Uram, Izráel Istene, tartsd meg, a mit a te szolgádnak, Dávidnak, az én atyámnak igértél, ezt mondván: A te [magodból] [való] férfiú el nem fogy én elõttem, a ki az Izráel királyi székibe üljön; csakhogy a te fiaid õrizzék meg az õ útjokat, hogy én elõttem járjanak, a mint te én elõttem jártál."));
husuperobject.biblemap.put("b11_8_26", new husuperobject(11,8,26,"Most teljesítsd be, Izráel Istene, a te szavaidat, a melyeket szólottál a te szolgádnak, Dávidnak, az én atyámnak."));
husuperobject.biblemap.put("b11_8_27", new husuperobject(11,8,27,"Vajjon gondolható-é, hogy lakozhatnék az Isten a földön? Ímé az ég, és az egeknek egei be nem foghatnak téged; mennyivel kevésbbé e ház, a melyet én építettem."));
husuperobject.biblemap.put("b11_8_28", new husuperobject(11,8,28,"De tekints a te szolgád imádságára és könyörgésére, óh Uram, én Istenem, hogy meghalljad a dicséretet és az imádságot, a melylyel a te szolgád könyörög elõtted e mai napon;"));
husuperobject.biblemap.put("b11_8_29", new husuperobject(11,8,29,"Hogy a te szemeid e házra nézzenek éjjel és nappal, e helyre, a mely felõl azt mondottad: Ott lészen az én nevem; hallgasd meg ez imádságot, a melylyel könyörög a te szolgád e helyen."));
husuperobject.biblemap.put("b11_8_30", new husuperobject(11,8,30,"És hallgasd meg a te szolgádnak és a te népednek, az Izráelnek könyörgését, a kik imádkozándanak e helyen; hallgasd meg lakhelyedbõl, a mennyekbõl, és meghallgatván légy kegyelmes!"));
husuperobject.biblemap.put("b11_8_31", new husuperobject(11,8,31,"Mikor vétkezéndik valaki felebarátja ellen, és esküre köteleztetik, hogy megesküdjék és õ ide jõ, megesküszik az oltár elõtt ebben a házban:"));
husuperobject.biblemap.put("b11_8_32", new husuperobject(11,8,32,"Te hallgasd meg a mennyekbõl, és vidd véghez, és tégy igazat a te szolgáid között, kárhoztatván az istentelent, hogy fején teljék a mit keresett; és megigazítván az igazat, megfizetvén néki az õ igazsága szerint."));
husuperobject.biblemap.put("b11_8_33", new husuperobject(11,8,33,"Mikor megverettetik a te néped, az Izráel, az õ ellenségeitõl, mivel ellened vétkeztek, és hozzád megtéréndenek, és vallást teéndenek a te nevedrõl, és néked imádkozándanak és könyörgéndenek e házban:"));
husuperobject.biblemap.put("b11_8_34", new husuperobject(11,8,34,"Te hallgasd meg a mennyekbõl, és bocsásd meg az Izráelnek, a te népednek vétkét; és hozd vissza õket arra a földre, a melyet adtál az õ atyáiknak."));
husuperobject.biblemap.put("b11_8_35", new husuperobject(11,8,35,"Mikor berekesztetik az ég, és nem lészen esõ, mert ellened vétkeztek; és imádkozándanak e helyen, és vallást teéndenek a te nevedrõl, és megtéréndenek az õ bûnökbõl, mert te szorongatod õket:"));
husuperobject.biblemap.put("b11_8_36", new husuperobject(11,8,36,"Te hallgasd meg õket a mennyekbõl, és légy kegyelmes a te szolgáidnak és az Izráelnek, a te népednek vétke iránt, tanítsd meg õket a jó útra, a melyen járjanak; és adj esõt a te földedre, a melyet örökségül adtál a te népednek."));
husuperobject.biblemap.put("b11_8_37", new husuperobject(11,8,37,"Éhség ha lesz e földön, ha döghalál, aszály, ragya, sáska, cserebogár; ha ellenség szállja meg kapuit; vagy más csapás és nyavalya jövénd reájok:"));
husuperobject.biblemap.put("b11_8_38", new husuperobject(11,8,38,"A ki akkor könyörög és imádkozik, legyen az bárki; vagy a te egész néped, az Izráel, ha elismeri kiki az õ szívére [mért] csapást, és kiterjeszténdi kezeit e ház felé:"));
husuperobject.biblemap.put("b11_8_39", new husuperobject(11,8,39,"Te hallgasd meg a mennyekbõl, a te lakhelyedbõl és légy kegyelmes, és cselekedd azt, hogy kinek-kinek fizess az õ útai szerint, a mint megismerted az õ szívét, mert egyedül csak te ismered minden embernek szívét."));
husuperobject.biblemap.put("b11_8_40", new husuperobject(11,8,40,"Hogy féljenek téged mind éltig, míg e földnek színén lakoznak, a melyet adtál a mi atyáinknak."));
husuperobject.biblemap.put("b11_8_41", new husuperobject(11,8,41,"Sõt még az idegen is, a ki nem a te néped, az Izráel közül való, ha eljövénd messze földrõl a te nevedért;"));
husuperobject.biblemap.put("b11_8_42", new husuperobject(11,8,42,"(Mert meghallják a te nagyságos nevedet és a te hatalmas kezedet és kinyújtott karodat), és eljövén imádkozánd e házban:"));
husuperobject.biblemap.put("b11_8_43", new husuperobject(11,8,43,"Te hallgasd meg a mennyekbõl, a te lakhelyedbõl, és add meg az idegennek mindazt, a miért könyörög néked, hogy mind az egész földön való népek megismerjék a te nevedet, és féljenek úgy téged, miképen a te néped az Izráel; és ismerjék meg, hogy a te nevedrõl neveztetik ez a ház, a melyet én építettem."));
husuperobject.biblemap.put("b11_8_44", new husuperobject(11,8,44,"Ha a te néped hadba megy ki az õ ellensége ellen, az úton, a melyen te küldöd el, és imádkozándik az Úrhoz, [fordulván] az úton e város felé, a melyet te magadnak választottál és e ház felé, a melyet építettem a te nevednek:"));
husuperobject.biblemap.put("b11_8_45", new husuperobject(11,8,45,"Hallgasd meg a mennyekbõl az õ imádságokat és könyörgésöket, és szerezz nékik igazságot."));
husuperobject.biblemap.put("b11_8_46", new husuperobject(11,8,46,"Ha te ellened vétkezéndenek, - mert nincsen ember, a ki ne vétkeznék - és megharagudván reájok, ellenség kezébe adándod, és fogva elviéndik õket azok, a kiktõl megfogattak, az ellenségnek földére, messze vagy közel;"));
husuperobject.biblemap.put("b11_8_47", new husuperobject(11,8,47,"És eszökre térnek a földön, melyre fogva vitettek, és megtérvén könyörgenek néked azoknak földökön, a kiktõl fogva elvitettek, mondván: Vétkeztünk, hamisan és istentelenül cselekedtünk!"));
husuperobject.biblemap.put("b11_8_48", new husuperobject(11,8,48,"És megtéréndenek te hozzád teljes szívökbõl és lelkökbõl, az õ ellenségöknek földében, a kik õket fogva elvitték, és imádkozándanak hozzád az õ földöknek útja felé [fordulva,] a melyet adtál az õ atyáiknak, [és] e város felé, a melyet magadnak választottál, és e ház felé, a melyet a te nevednek építettem:"));
husuperobject.biblemap.put("b11_8_49", new husuperobject(11,8,49,"Hallgasd meg a mennyekbõl, a te lakhelyedbõl az õ imádságokat és könyörgésöket, és szerezz nékik igazságot;"));
husuperobject.biblemap.put("b11_8_50", new husuperobject(11,8,50,"És légy kegyelmes a te néped iránt, a kik ellened vétkezéndenek, és minden bûneik iránt, a melyekkel ellened vétkeztek; és szerezz kedvességet nékik azok elõtt, a kik õket fogva tartják, hogy könyörüljenek rajtok;"));
husuperobject.biblemap.put("b11_8_51", new husuperobject(11,8,51,"Mert õk a te néped és örökséged, a kiket kihoztál Égyiptomból, a vaskemencze közepébõl,"));
husuperobject.biblemap.put("b11_8_52", new husuperobject(11,8,52,"Hogy a te szemeid nézzenek a te szolgádnak imádságára és a te népednek az Izráelnek könyörgésére; meghallgatván õket mindenkor, mikor téged segítségül hívnak."));
husuperobject.biblemap.put("b11_8_53", new husuperobject(11,8,53,"Mert te különválasztottad õket magadnak örökségül a földnek minden népei közül; a miképen megmondottad volt Mózes, a te szolgád által, mikor kihoztad a mi atyáinkat Égyiptomból, Uram Isten!"));
husuperobject.biblemap.put("b11_8_54", new husuperobject(11,8,54,"És mikor elvégezte Salamon az Úr elõtt való minden imádságát és könyörgését, felkele az Úr oltára elõl, és megszünék az õ térdein való állástól, és kezeinek az égbe felemelésétõl."));
husuperobject.biblemap.put("b11_8_55", new husuperobject(11,8,55,"És felállván megáldá az Izráel egész gyülekezetét, felszóval ezt mondván:"));
husuperobject.biblemap.put("b11_8_56", new husuperobject(11,8,56,"Áldott legyen az Úr, a ki nyugodalmat adott az õ népének, az Izráelnek, minden õ beszéde szerint, csak egy beszéde is hiábavaló nem volt minden õ jó beszédei közül, a melyeket szólott Mózes, az õ szolgája által."));
husuperobject.biblemap.put("b11_8_57", new husuperobject(11,8,57,"Az Úr, a mi Istenünk, legyen velünk, a miképen volt a mi atyáinkkal, ne hagyjon el minket, el se távozzék tõlünk,"));
husuperobject.biblemap.put("b11_8_58", new husuperobject(11,8,58,"Hanem hajtsa magához a mi szívünket, hogy járjunk minden õ útaiban, és õrizzük meg az õ parancsolatit, rendeléseit és végzésit, a melyeket a mi atyáinknak parancsolt."));
husuperobject.biblemap.put("b11_8_59", new husuperobject(11,8,59,"És ezek a szavak, a melyekkel imádkoztam az Úr elõtt, legyenek jelen az Úr elõtt a mi Istenünk elõtt éjjel és nappal, hogy ítéletet tegyen az õ szolgájának és az õ népének, az Izráelnek, minden idõben,"));
husuperobject.biblemap.put("b11_8_60", new husuperobject(11,8,60,"Hogy megismerjék a földön minden népek, hogy [csak] az Úr az Isten, és hogy õ kívülötte nincsen [más."));
husuperobject.biblemap.put("b11_8_61", new husuperobject(11,8,61,"És a ti szívetek legyen tökéletes az Úrhoz, a mi Istenünkhöz, hogy járjatok az õ rendeléseiben, és õrizzétek meg az õ parancsolatit, miképen e mai napon."));
husuperobject.biblemap.put("b11_8_62", new husuperobject(11,8,62,"És a király és az egész Izráel õ vele, áldozatokat áldozának az Úr elõtt."));
husuperobject.biblemap.put("b11_8_63", new husuperobject(11,8,63,"És Salamon áldozék hálaadó áldozatul, a melyet áldozék az Úrnak: huszonkétezer ökröt, százhúszezer juhot. És e képen szentelék fel az Úr házát a király és az Izráel minden fiai."));
husuperobject.biblemap.put("b11_8_64", new husuperobject(11,8,64,"Ugyanazon napon szentelé fel a király a középsõ pitvart, mely az Úrnak háza elõtt vala; mert ott szerze egészen égõáldozatokat, ételáldozatokat és hálaáldozatok kövéreit. Mert a rézoltár, mely az Úr elõtt állott, kisebb volt, mintsem reá fért volna az égõáldozat, az ételáldozat és a hálaáldozatok kövére."));
husuperobject.biblemap.put("b11_8_65", new husuperobject(11,8,65,"És Salamon ünnepet szerze ebben az idõben, és vele együtt az egész Izráel; egy nagy gyûlést Hámát határától fogva Égyiptom határáig, az Úr elõtt, a mi Istenünk elõtt, hét napig és újra hét napig, [azaz] tizennégy napig."));
husuperobject.biblemap.put("b11_8_66", new husuperobject(11,8,66,"És a nyolczadik napon elbocsátá a népet. És áldák a királyt, és elmenének az õ hajlékaikba örömmel és víg szívvel, mindama jók felett, a melyeket cselekedett az Úr az õ szolgájával, Dáviddal, és az Izráellel, az õ népével."));
husuperobject.biblemap.put("b11_9_1", new husuperobject(11,9,1,"És lõn, mikor elvégezte Salamon az Úr házának és a király házának építését, és mindent a mit kívánt és a mit akart építeni Salamon:"));
husuperobject.biblemap.put("b11_9_2", new husuperobject(11,9,2,"Megjelenék az Úr Salamonnak másodszor is, a miként megjelent volt néki Gibeonban."));
husuperobject.biblemap.put("b11_9_3", new husuperobject(11,9,3,"És monda néki az Úr: Meghallgattam a te imádságodat és könyörgésedet, a melylyel könyörgöttél elõttem: Megszenteltem e házat, a melyet építettél, abba helyheztetvén az én nevemet mindörökké, és ott lesznek az én szemeim, és az én szívem mindenkor."));
husuperobject.biblemap.put("b11_9_4", new husuperobject(11,9,4,"És ha te elõttem járándasz, a mint járt Dávid, a te atyád, egyenes és tökéletes szívvel, úgy cselekedvén mindenekben, a mint néked megparancsoltam, az én rendelésimet és végzésimet megtartándod;"));
husuperobject.biblemap.put("b11_9_5", new husuperobject(11,9,5,"Megerõsítem a te birodalmadnak trónját az Izráelen mindörökké, a mint megígértem volt Dávidnak a te atyádnak, mondván: Nem fogy el a te [nemzetségedbõl] [való ]férfiú az Izráel királyi székébõl."));
husuperobject.biblemap.put("b11_9_6", new husuperobject(11,9,6,"De hogyha elszakadtok ti és a ti fiaitok én tõlem, és meg nem õrizénditek az én parancsolatimat és végzéseimet, melyeket elõtökbe adtam; hanem elmentek, és idegen isteneknek szolgáltok, és meghajoltok azok elõtt:"));
husuperobject.biblemap.put("b11_9_7", new husuperobject(11,9,7,"Kigyomlálom az Izráelt e föld színérõl, a melyet nékik adtam; e házat, melyet az én nevemnek szenteltem, elvetem szemeim elõl, és az Izráel példabeszédül és meséül lészen minden nép elõtt."));
husuperobject.biblemap.put("b11_9_8", new husuperobject(11,9,8,"És bár e ház felséges, mégis a kik elmennek mellette, elcsodálkoznak, felkiáltanak, és azt mondják: Miért cselekedett így az Úr ezzel a földdel és ezzel a házzal?"));
husuperobject.biblemap.put("b11_9_9", new husuperobject(11,9,9,"És azt felelik: Azért, mert elhagyták az Urat, az õ Istenöket, a ki az õ atyáikat kihozta volt Égyiptom földébõl, és idegen istenekhez ragaszkodtak, és azokat imádták, és azoknak szolgáltak: ezért bocsátá õ reájok az Úr mind ezt a nyomorúságot."));
husuperobject.biblemap.put("b11_9_10", new husuperobject(11,9,10,"És lõn a húsz esztendõ végén, a mialatt Salamon a két házat, az Úr házát és a király házát megépíté,"));
husuperobject.biblemap.put("b11_9_11", new husuperobject(11,9,11,"A melyekhez Hírám, Tírus királya adott volt ajándékban Salamonnak czédrusfákat, fenyõfákat, aranyat egész kivánsága szerint: ada Salamon király Hírámnak húsz várost Galileának földén."));
husuperobject.biblemap.put("b11_9_12", new husuperobject(11,9,12,"És kiméne Hírám Tírusból, hogy megnézze azokat a városokat, a melyeket Salamon néki ada, de nem tetszettek azok néki."));
husuperobject.biblemap.put("b11_9_13", new husuperobject(11,9,13,"És monda: Miféle városok ezek, atyámfia, a melyeket nékem adtál? És Kábul földnek nevezé azokat mind e mai napig."));
husuperobject.biblemap.put("b11_9_14", new husuperobject(11,9,14,"Küldött vala pedig Hírám a királynak százhúsz tálentom aranyat."));
husuperobject.biblemap.put("b11_9_15", new husuperobject(11,9,15,"És ez az összege annak az adónak is, a melyet kivetett volt Salamon király, hogy megépíthesse az Úr házát, és a maga házát, és Millót, és Jeruzsálem kõfalait, és Kháczort, Megiddót és Gézert."));
husuperobject.biblemap.put("b11_9_16", new husuperobject(11,9,16,"Mert a Faraó, Égyiptom királya, feljött volt, és meghódítá Gézert, és felégette tûzzel, és a Kananeusokat, a kik a városban laktak, megölte, és adá azt ajándékban az õ leányának, a Salamon feleségének."));
husuperobject.biblemap.put("b11_9_17", new husuperobject(11,9,17,"És megépíté Salamon Gézert és az alsó Bethoront;"));
husuperobject.biblemap.put("b11_9_18", new husuperobject(11,9,18,"Bahalátot és Thadmort a pusztában, azon a földön;"));
husuperobject.biblemap.put("b11_9_19", new husuperobject(11,9,19,"És a tárházak minden városait, a melyek a Salamonéi valának, a szekerek városait, és a lovagok városait, és mindeneket, a melyeknek építéséhez Salamonnak kedve volt Jeruzsálemben és a Libánonon, és az õ birodalmának egész földén."));
husuperobject.biblemap.put("b11_9_20", new husuperobject(11,9,20,"És mindazt a népet, a mely megmaradott volt az Emoreusoktól, Hitteusoktól, Perizeusoktól, Hivveusoktól, Jebuzeusoktól, a kik nem valának az Izráel fiai közül."));
husuperobject.biblemap.put("b11_9_21", new husuperobject(11,9,21,"Azoknak fiait, a kik õ utánok azon a földön maradtak volt, a kiket az Izráeliták ki nem irthattak, Salamon jobbágyokká tette mind e mai napig."));
husuperobject.biblemap.put("b11_9_22", new husuperobject(11,9,22,"De az Izráel fiai közül senkit nem vetett Salamon szolgálat alá, hanem ezek hadakozó férfiak voltak és õ szolgái és fõemberei és hadnagyai és az õ szekereinek és lovagjainak fejei."));
husuperobject.biblemap.put("b11_9_23", new husuperobject(11,9,23,"És a hivatalnokoknak, a kik Salamon munkáinak élén állottak, száma ötszázötven volt, a kik igazgatták a népet, a mely dolgozott a munkán."));
husuperobject.biblemap.put("b11_9_24", new husuperobject(11,9,24,"És a Faraó leánya felméne a Dávid városából a maga házába, a melyet [Salamon] épített néki. Akkor építé meg Millót is."));
husuperobject.biblemap.put("b11_9_25", new husuperobject(11,9,25,"És áldozék Salamon minden esztendõben háromszor, égõ és hálaáldozatot azon az oltáron, a melyet épített vala az Úrnak, és áldozik vala jóillattal azon, a mely az Úr elõtt vala. És elvégezé a házat."));
husuperobject.biblemap.put("b11_9_26", new husuperobject(11,9,26,"És hajókat is csináltata Salamon király Esiongáberben, a mely Elót mellett van a Veres tenger partján, az Edom földén."));
husuperobject.biblemap.put("b11_9_27", new husuperobject(11,9,27,"És elküldé Hírám az õ szolgáit a hajókon, a kik jó hajósok és a tengeren jártasak [valának], a Salamon szolgáival."));
husuperobject.biblemap.put("b11_9_28", new husuperobject(11,9,28,"És egész Ofirig menének, és hozának onnét négyszázhúsz tálentom aranyat, és vivék azt Salamon királyhoz."));
husuperobject.biblemap.put("b11_10_1", new husuperobject(11,10,1,"A Séba királynéasszonya pedig hallván Salamonnak hírét és az Úr nevét, eljöve, hogy megkisértgesse õt nehéz kérdésekkel."));
husuperobject.biblemap.put("b11_10_2", new husuperobject(11,10,2,"És bejöve Jeruzsálembe igen nagy sereggel és tevékkel, a melyek hoznak vala fûszerszámokat, igen sok aranyat és drágaköveket, és Salamonhoz méne, és szóla vele mindenekrõl, a melyek szívén voltak."));
husuperobject.biblemap.put("b11_10_3", new husuperobject(11,10,3,"És Salamon megfelelt néki mindenre, semmi sem volt a király elõtt elrejtve, a mire ne tudott volna néki felelni."));
husuperobject.biblemap.put("b11_10_4", new husuperobject(11,10,4,"És a mikor látta Séba királynéasszonya Salamonnak minden bölcseségét, és a házat, a melyet épített vala;"));
husuperobject.biblemap.put("b11_10_5", new husuperobject(11,10,5,"És az õ asztalának étkeit, és szolgáinak lakásait, és szolgái udvarlásának módját, és azok öltözeteit, és pohárszékeit, és az õ áldozatját, a melylyel az Úrnak házában áldozott: a lélekzete is elállott;"));
husuperobject.biblemap.put("b11_10_6", new husuperobject(11,10,6,"És monda a királynak: Mind igaz, a mit az én földemben hallottam volt a te dolgaid felõl és a te bölcseségedrõl,"));
husuperobject.biblemap.put("b11_10_7", new husuperobject(11,10,7,"De én hinni sem akartam azokat a beszédeket, míg én magam el nem jöttem, és szemeimmel nem láttam. És ímé nékem a felét sem beszélték el: te meghaladtad bölcseséggel és jósággal a hírt, a melyet hallottam felõled."));
husuperobject.biblemap.put("b11_10_8", new husuperobject(11,10,8,"Boldogok a te embereid, boldogok ezek a te szolgáid, a kik udvarlanak néked mindenkor, és hallhatják a te bölcseségedet:"));
husuperobject.biblemap.put("b11_10_9", new husuperobject(11,10,9,"Legyen az Úr, a te Istened áldott, a ki kedvelt téged, hogy az Izráel királyi székibe ültetett, mert szerette az Úr az Izráelt mindörökké, és királylyá tett téged, hogy ítéletet és igazságot szolgáltass."));
husuperobject.biblemap.put("b11_10_10", new husuperobject(11,10,10,"És ada a királynak száz és húsz tálentom aranyat, és igen sok fûszerszámot és drágakövet. Nem hoztak azután ilyen és ennyi sokaságú fûszerszámot, a mennyit Séba királynéasszonya ada Salamon királynak."));
husuperobject.biblemap.put("b11_10_11", new husuperobject(11,10,11,"És a Hírám hajója is, mely aranyat hozott Ofirból, ébenfát is hozott Ofirból nagy bõséggel és drágaköveket."));
husuperobject.biblemap.put("b11_10_12", new husuperobject(11,10,12,"És csinála a király az ébenfából oszlopokat az Úr házába és a király házába, és az éneklõknek hegedûket és lantokat; nem hoztak soha többé olyan ébenfákat, és nem is láttak olyanokat mind e mai napig."));
husuperobject.biblemap.put("b11_10_13", new husuperobject(11,10,13,"És Salamon király ada a Séba királynéasszonyának mindent, a mit csak kívánt és kért tõle, azokon kivül, a melyeket gazdagságához képest ada Salamon király néki. Annakutána megtére, és méne az õ földébe szolgáival egyetemben."));
husuperobject.biblemap.put("b11_10_14", new husuperobject(11,10,14,"Vala pedig mértéke az aranynak, a mely kezéhez jõ vala Salamonnak minden esztendõben, hatszáz és hatvanhat tálentom arany."));
husuperobject.biblemap.put("b11_10_15", new husuperobject(11,10,15,"Azonkivül, [a mi jõ vala] az árus emberektõl és a fûszerszámokkal kereskedõ kalmároktól, és mind az Arábiabeli királyoktól, és annak a földnek tiszttartóitól."));
husuperobject.biblemap.put("b11_10_16", new husuperobject(11,10,16,"És csináltata Salamon király kétszáz paizst tiszta vert aranyból; mindenik paizsra hatszáz aranyat adott."));
husuperobject.biblemap.put("b11_10_17", new husuperobject(11,10,17,"És háromszáz kerek paizst vert aranyból, három font aranyat ada mindenik paizsra: és azokat a király helyhezteté a Libánon erdõ házába."));
husuperobject.biblemap.put("b11_10_18", new husuperobject(11,10,18,"És készíte a király elefántcsontból egy nagy királyi széket és beborítá azt finom aranynyal."));
husuperobject.biblemap.put("b11_10_19", new husuperobject(11,10,19,"Hat grádicsa volt e királyi széknek és e szék teteje kerekded vala hátul, és karjai valának mindkétfelõl az ülés mellett, és két oroszlán álla ott a karoknál."));
husuperobject.biblemap.put("b11_10_20", new husuperobject(11,10,20,"És tizenkét oroszlán álla ott kétfelõl a grádics hat lépésén. Senki soha olyant nem csinált egyetlen országban sem."));
husuperobject.biblemap.put("b11_10_21", new husuperobject(11,10,21,"És Salamon királynak összes ivóedényei is aranyból voltak, és a Libánon erdõ házának összes edényei tiszta aranyból; nem volt azok között semmi ezüst, mert [annak] semmi becse nem vala Salamon idejében."));
husuperobject.biblemap.put("b11_10_22", new husuperobject(11,10,22,"Mert a király Társis hajója, a mely a tengeren Hírám hajójával járt, minden három esztendõben egyszer fordult meg, s hozott a Társis hajó aranyat, ezüstöt, elefántcsontokat, majmokat és pávákat."));
husuperobject.biblemap.put("b11_10_23", new husuperobject(11,10,23,"És feljebb magasztaltaték Salamon király gazdagsággal és bölcseséggel a földön való minden királyoknál."));
husuperobject.biblemap.put("b11_10_24", new husuperobject(11,10,24,"És mind az egész föld kivánja vala látni Salamont, hogy hallhatnák az õ bölcseségét, melyet Isten az õ szívébe adott volt."));
husuperobject.biblemap.put("b11_10_25", new husuperobject(11,10,25,"És azok néki ajándékot hoznak vala, ezüst és arany edényeket, öltözeteket, hadi szerszámokat, fûszerszámokat, lovakat, öszvéreket, esztendõnként."));
husuperobject.biblemap.put("b11_10_26", new husuperobject(11,10,26,"És gyûjte Salamon szekereket és lovagokat, úgy hogy ezer és négyszáz szekere, és tizenkétezer lovagja volt néki, a kiket helyheztete a szekerek városaiba, és a király mellé Jeruzsálemben."));
husuperobject.biblemap.put("b11_10_27", new husuperobject(11,10,27,"És felhalmozá a király Jeruzsálemben az ezüstöt mint a követ, és a czédrust, mint a vad fügefákat, a melyek nagy tömegben vannak a mezõn."));
husuperobject.biblemap.put("b11_10_28", new husuperobject(11,10,28,"És Salamonnak Égyiptomból hozának lovakat, és a király kereskedõi sereggel vették volt a [lovakat] megszabott áron."));
husuperobject.biblemap.put("b11_10_29", new husuperobject(11,10,29,"És egy-egy szekér hatszáz ezüst siklusért és egy-egy ló százötven siklusért ment fel és jött ki Égyiptomból, és ugyancsak õk szállították [ezeket] a Hitteusok királyainak és Siria királyainak."));
husuperobject.biblemap.put("b11_11_1", new husuperobject(11,11,1,"Salamon király pedig megszerete sok idegen asszonyt, még pedig a Faraó leányán kivül a Moábiták, Ammoniták, Edomiták, Sídonbeliek és Hitteusok leányait,"));
husuperobject.biblemap.put("b11_11_2", new husuperobject(11,11,2,"Olyan népek közül, a kik felõl azt mondotta volt az Úr az Izráel fiainak: Ne menjetek hozzájok, és õket se engedjétek magatokhoz jõni, bizonyára az õ isteneik után hajtják a ti szíveteket. Ezekhez ragaszkodék Salamon szeretettel."));
husuperobject.biblemap.put("b11_11_3", new husuperobject(11,11,3,"És valának néki feleségei, hétszáz királynéasszony és háromszáz ágyas; és az õ feleségei elhajták az õ szívét."));
husuperobject.biblemap.put("b11_11_4", new husuperobject(11,11,4,"És mikor megvénült Salamon, az õ feleségei elhajták az õ szívét az idegen istenek után, úgy hogy nem volt már az õ szíve tökéletes az Úrhoz, az õ Istenéhez, a mint az õ atyjának, Dávidnak szíve."));
husuperobject.biblemap.put("b11_11_5", new husuperobject(11,11,5,"Mert Salamon követi vala Astoretet, a Sídonbeliek istenét, és Milkómot, az Ammoniták útálatos bálványát."));
husuperobject.biblemap.put("b11_11_6", new husuperobject(11,11,6,"És gonosz dolgot cselekedék Salamon az Úr szemei elõtt, és nem követé olyan tökéletességgel az Urat, mint Dávid, az õ atyja."));
husuperobject.biblemap.put("b11_11_7", new husuperobject(11,11,7,"Akkor építe Salamon templomot Kámosnak, a Moábiták útálatos bálványának a hegyen, a mely Jeruzsálem átellenében van, és Moloknak, az Ammon fiai útálatos bálványának."));
husuperobject.biblemap.put("b11_11_8", new husuperobject(11,11,8,"És ekképen cselekedék [Salamon] mind az õ idegen feleségeivel, a kik az õ isteneiknek tömjéneztek és áldoztak."));
husuperobject.biblemap.put("b11_11_9", new husuperobject(11,11,9,"Megharaguvék azért az Úr Salamonra, hogy elhajlott az õ szíve az Úrtól, Izráel Istenétõl, a ki megjelent volt néki kétszer is,"));
husuperobject.biblemap.put("b11_11_10", new husuperobject(11,11,10,"És azt parancsolta volt néki, hogy ne kövessen idegen isteneket, és mégsem õrizte meg az Úr parancsolatját."));
husuperobject.biblemap.put("b11_11_11", new husuperobject(11,11,11,"Monda azért az Úr Salamonnak: Miután ez történt veled, és nem õrizted meg az én szövetségemet és az én rendelésimet, a melyeket parancsoltam néked: elszakasztván elszakasztom tõled az országot, és adom a te szolgádnak."));
husuperobject.biblemap.put("b11_11_12", new husuperobject(11,11,12,"Mindazáltal míg élsz, nem cselekeszem ezt Dávidért, a te atyádért; hanem a te fiadnak kezétõl szakasztom el azt."));
husuperobject.biblemap.put("b11_11_13", new husuperobject(11,11,13,"De nem szakasztom el az egész birodalmat; hanem egy nemzetséget adok a te fiadnak Dávidért, az én szolgámért és Jeruzsálemért, a melyet magamnak választottam."));
husuperobject.biblemap.put("b11_11_14", new husuperobject(11,11,14,"És ellenséget támaszta az Úr Salamonra, az Edombeli Hadádot, a ki az Edombeli királyi nembõl való vala."));
husuperobject.biblemap.put("b11_11_15", new husuperobject(11,11,15,"Mert mikor Dávid az Edomiták ellen ment volt, és Joáb, a sereg fõvezére elment volt a megöletteknek temetésére, és levágott minden férfiú nemet Edomban, -"));
husuperobject.biblemap.put("b11_11_16", new husuperobject(11,11,16,"Mert hat hónapig volt ott Joáb az egész Izráellel, míg minden férfiúi nemet ki nem vesztett Edomban, -"));
husuperobject.biblemap.put("b11_11_17", new husuperobject(11,11,17,"Akkor szaladott vala el Hadád és vele együtt valami Edomiták az õ atyjának szolgái közül õ vele, bemenvén Égyiptomba. Hadád pedig [akkor még] kis gyermek volt."));
husuperobject.biblemap.put("b11_11_18", new husuperobject(11,11,18,"Kik felkelvén Midiánból, menének Páránba, és melléjök vévén a Páránbeli férfiak közül, bemenének Égyiptomba a Faraóhoz, az Égyiptombeli királyhoz, a ki házat ada néki, és ételt, [italt] szolgáltata néki, és jószágot is ada néki."));
husuperobject.biblemap.put("b11_11_19", new husuperobject(11,11,19,"Igen kedvében lõn azért Hadád a Faraónak, úgyannyira, hogy feleségül adá néki az õ feleségének hugát, Táfnes királyasszonynak hugát."));
husuperobject.biblemap.put("b11_11_20", new husuperobject(11,11,20,"És a Táfnes huga szülé néki Génubátot, az õ fiát, és elválasztá azt Táfnes a Faraó házában, és Génubát ott volt a Faraó házában, a Faraó fiai között."));
husuperobject.biblemap.put("b11_11_21", new husuperobject(11,11,21,"Mikor pedig Hadád meghallotta Égyiptomban, hogy Dávid elaludt az õ atyáival, és hogy Joáb is, a seregnek fõvezére, meghalt, monda Hadád a Faraónak: Bocsáss el engem, hadd menjek el az én földembe."));
husuperobject.biblemap.put("b11_11_22", new husuperobject(11,11,22,"És felele néki a Faraó: Mi nélkül szûkölködöl én nálam, hogy a te földedbe igyekezel menni? Felele az: Semmi nélkül [nem szûkölködöm,] de [kérlek] bocsáss el engem."));
husuperobject.biblemap.put("b11_11_23", new husuperobject(11,11,23,"És támaszta az Isten néki [más] ellenséget [is,] Rézont, az Eljada fiát, a ki elfutott vala Hadadézertõl, a Sóbabeli királytól, az õ urától."));
husuperobject.biblemap.put("b11_11_24", new husuperobject(11,11,24,"És [hadakozó] férfiakat gyûjtött maga mellé, és õ vala a sereg hadnagya, mikor megölé õket Dávid; azután Damaskusba menvén ott lakának, és uralkodának Damaskusban."));
husuperobject.biblemap.put("b11_11_25", new husuperobject(11,11,25,"És ellensége volt Izráelnek Salamonnak egész életében, a nyomorúságon kivül, a melyet Hadád szerze, és gyûlölte Izráelt, és uralkodott Siriában."));
husuperobject.biblemap.put("b11_11_26", new husuperobject(11,11,26,"Azután Jeroboám, a Nébát fia, Seredából való Efrateus, - a kinek anyja Sérua, egy özvegy asszony volt - a Salamon szolgája emelte fel kezét a király ellen."));
husuperobject.biblemap.put("b11_11_27", new husuperobject(11,11,27,"Annak pedig, a miért felemelte kezét a király ellen, ez volt az oka: Mikor Salamon megépítette Millót, és berakatta az õ atyjának, a Dávid városának romlását;"));
husuperobject.biblemap.put("b11_11_28", new husuperobject(11,11,28,"Jeroboám erõs férfiú vala; és látván Salamon, hogy az õ szolgája az õ dolgában szorgalmatos, reá bízá a József háza gondviselésének egész terhét."));
husuperobject.biblemap.put("b11_11_29", new husuperobject(11,11,29,"És történt ebben az idõben, hogy mikor kiment [egysze]r Jeroboám Jeruzsálembõl, találkozék az úton Ahijával, a Silóbeli prófétával, és rajta új köpönyeg volt, és csak ketten valának a mezõn együtt."));
husuperobject.biblemap.put("b11_11_30", new husuperobject(11,11,30,"És megragadván Ahija az új ruhát, a mely azon volt, hasítá azt tizenkét részre."));
husuperobject.biblemap.put("b11_11_31", new husuperobject(11,11,31,"És monda Jeroboámnak: Vedd el magadnak a tíz részt; mert ezt mondja az Úr, Izráel Istene: Ímé elszakasztom ez országot Salamon kezétõl, és néked adom a tíz nemzetséget;"));
husuperobject.biblemap.put("b11_11_32", new husuperobject(11,11,32,"Egy nemzetséget hagyok pedig õ nála az én szolgámért, Dávidért, és Jeruzsálem városáért, a melyet magamnak választottam az Izráel minden nemzetségei közül,"));
husuperobject.biblemap.put("b11_11_33", new husuperobject(11,11,33,"Még pedig azért, mert elhagytak engem, és imádták Astoretet, a Sídonbeliek istenét, és Kámost, a Moábiták istenét, és Milkomot, az Ammon fiainak istenét, és nem jártak az én utaimban, hogy azt cselekedték volna, a mi tetszett volna az én szemeimnek: az én rendelésimet és végzéseimet, a mint Dávid, az õ atyja."));
husuperobject.biblemap.put("b11_11_34", new husuperobject(11,11,34,"De nem veszem el az egész birodalmat az õ kezétõl, hanem akarom, hogy fejedelem legyen életének minden idejében, Dávidért az én szolgámért, a kit választottam; mivelhogy megõrizte az én parancsolatimat és rendeléseimet;"));
husuperobject.biblemap.put("b11_11_35", new husuperobject(11,11,35,"Hanem az õ fiának kezétõl [már] elveszem a királyságot, és néked adom azt, tudniillik a tíz nemzetséget."));
husuperobject.biblemap.put("b11_11_36", new husuperobject(11,11,36,"Az õ fiának pedig egy nemzetséget adok, hogy Dávidnak, az én szolgámnak legyen elõttem szövétneke mindenkor Jeruzsálemben, a városban, a melyet magamnak választottam, hogy ott helyheztessem az én nevemet."));
husuperobject.biblemap.put("b11_11_37", new husuperobject(11,11,37,"Téged pedig felveszlek, és uralkodol mindenekben a te lelkednek kívánsága szerint, és király lész az Izráelen."));
husuperobject.biblemap.put("b11_11_38", new husuperobject(11,11,38,"És ha te minden parancsolatimnak engedéndesz, és járándasz az én utaimban, és azt cselekedénded, a mi tetszik nékem, megõrizvén az én rendelésimet és parancsolatimat, a mint Dávid, az én szolgám cselekedett: én veled leszek, és építek néked állandó házat, a mint Dávidnak építettem, és néked adom az Izráelt."));
husuperobject.biblemap.put("b11_11_39", new husuperobject(11,11,39,"És megsanyargatom ezért a Dávid magvát: de még sem örökre."));
husuperobject.biblemap.put("b11_11_40", new husuperobject(11,11,40,"Igyekezik vala pedig Salamon megölni Jeroboámot; ezért felkelvén Jeroboám, futa Égyiptomba, Sésákhoz, az Égyiptombeli királyhoz, és [ott] volt Égyiptomban, Salamon haláláig."));
husuperobject.biblemap.put("b11_11_41", new husuperobject(11,11,41,"Salamonnak egyéb dolgai pedig és minden cselekedetei, a melyeket cselekedett, és bölcsesége avagy nem írattattak-é meg a Salamon cselekedeteirõl írott könyvben?"));
husuperobject.biblemap.put("b11_11_42", new husuperobject(11,11,42,"Az az idõ pedig, a melyben uralkodott Salamon Jeruzsálemben az egész Izráelen: negyven esztendõ."));
husuperobject.biblemap.put("b11_11_43", new husuperobject(11,11,43,"És elaluvék Salamon az õ atyáival, és eltemetteték az õ atyjának, Dávidnak városában. És Roboám, az õ fia uralkodék helyette."));
husuperobject.biblemap.put("b11_12_1", new husuperobject(11,12,1,"És elméne Roboám Síkembe; mert Síkembe gyûlt fel az egész Izráel, hogy királylyá tegyék õt."));
husuperobject.biblemap.put("b11_12_2", new husuperobject(11,12,2,"Mikor pedig meghallotta [ezt] Jeroboám, a Nébát fia, (a ki még Égyiptomban volt, a hová futott volt Salamon király elõl, és Égyiptomban tartózkodék Jeroboám,"));
husuperobject.biblemap.put("b11_12_3", new husuperobject(11,12,3,"És hozzá küldvén, elhivaták õt); elmenének Jeroboám és az Izráel egész gyülekezete, és szólának Roboámnak, mondván:"));
husuperobject.biblemap.put("b11_12_4", new husuperobject(11,12,4,"A te atyád igen megnehezítette a mi igánkat, de te most könnyebbítsd meg atyádnak kemény szolgálatát, és a nehéz igát, a melyet mi reánk vetett, és szolgálunk néked."));
husuperobject.biblemap.put("b11_12_5", new husuperobject(11,12,5,"És monda nékik: Menjetek el, és harmadnap mulva jõjjetek vissza hozzám. És a nép elméne."));
husuperobject.biblemap.put("b11_12_6", new husuperobject(11,12,6,"És tanácsot tarta Roboám király a vénekkel, a kik Salamon, az õ atyja elõtt állottak vala életében, mondván: Micsoda tanácsot adtok ti, hogy milyen választ adjak e népnek?"));
husuperobject.biblemap.put("b11_12_7", new husuperobject(11,12,7,"És szólának azok, mondván: Ha e mai napon szolgája lész e népnek, és nékik szolgálsz, és választ adsz nékik, és jó szót adsz nékik: mind éltig szolgálnak néked."));
husuperobject.biblemap.put("b11_12_8", new husuperobject(11,12,8,"De õ megveté a vének tanácsát, a melyet néki adtak, és tanácsot tarta az ifjakkal, a kik õ vele együtt nevekedtek volt fel, és a kik õ elõtte udvarlottak."));
husuperobject.biblemap.put("b11_12_9", new husuperobject(11,12,9,"És monda azoknak: Micsoda tanácsot adtok ti, hogy választ adjunk e népnek, a mely nékem szólván, azt mondja: Könnyebbítsd meg az igát, a melyet reánk vetett a te atyád?"));
husuperobject.biblemap.put("b11_12_10", new husuperobject(11,12,10,"És mondának néki az ifjak, a kik együtt nevekedtek volt fel õ vele: Így szólj ennek a népnek, a mely szólván néked, ezt mondja: A te atyád megnehezítette a mi igánkat, te pedig könnyebbítsd meg nékünk; e képen szólj nékik: Az én kis ujjam vastagabb az én atyám derekánál."));
husuperobject.biblemap.put("b11_12_11", new husuperobject(11,12,11,"Most azért, ha az én atyám reátok nehéz igát vetett, én még nehezebbé teszem a ti igátokat: ha az én atyám ostorral fékezett titeket, én skorpiókkal ostorozlak benneteket."));
husuperobject.biblemap.put("b11_12_12", new husuperobject(11,12,12,"És elméne Jeroboám és mind az egész nép Roboámhoz harmadnap, a mint meghagyta volt a király, ezt mondván: Jõjjetek hozzám harmadnapon."));
husuperobject.biblemap.put("b11_12_13", new husuperobject(11,12,13,"És a király kemény választ adott a népnek, megvetve a vének tanácsát, a melyet adtak vala néki;"));
husuperobject.biblemap.put("b11_12_14", new husuperobject(11,12,14,"És szóla nékik az ifjak tanácsa szerint, mondván: Ha az én atyám megnehezítette a ti igátokat, én még nehezebbé teszem azt; ha az én atyám ostorral fékezett titeket, én skorpiókkal ostorozlak benneteket."));
husuperobject.biblemap.put("b11_12_15", new husuperobject(11,12,15,"És nem hallgatá meg a király a népet; mert ezt az Úr fordította ekként, hogy megerõsítse az õ beszédét, a melyet szólott volt az Úr a Silóbeli Ahija által Jeroboámnak, a Nébát fiának."));
husuperobject.biblemap.put("b11_12_16", new husuperobject(11,12,16,"Mikor pedig látta az egész Izráel, hogy meg nem hallgatta õket a király, felele az [egész] nép a királynak ekképen: Micsoda részünk van nékünk Dávidban? Nincsen nékünk örökségünk az Isai fiában: [menj el] a te hajlékidba, óh Izráel! Most viseld gondját immár a te házadnak, óh Dávid! Elméne azért az Izráel az õ hajlékiba;"));
husuperobject.biblemap.put("b11_12_17", new husuperobject(11,12,17,"Úgy hogy Roboám csak azokon az Izráel fiain uralkodék, a kik Júda városaiban laktak."));
husuperobject.biblemap.put("b11_12_18", new husuperobject(11,12,18,"És a mikor elküldé Roboám király Adorámot, az adószedõt, megkövezé õt az egész Izráel, és meghala, és [maga] Roboám király is hamarsággal szekerébe üle, hogy elmeneküljön Jeruzsálembe."));
husuperobject.biblemap.put("b11_12_19", new husuperobject(11,12,19,"Így szakada el az Izráel népe Dávidnak házától mind e mai napig."));
husuperobject.biblemap.put("b11_12_20", new husuperobject(11,12,20,"És lõn, mikor meghallotta az egész Izráel, hogy megjött Jeroboám, érette küldvén, hivaták õt a gyülekezetbe, és királylyá tevék õt az egész Izráelen; senki pedig nem követé Dávidnak házát, hanem csak egyedül a Júda nemzetsége. (Vers 21-24: v. ö. 2 Krón. 11,1-4.)"));
husuperobject.biblemap.put("b11_12_21", new husuperobject(11,12,21,"És mikor megérkezett Roboám Jeruzsálembe, összegyûjté Júda egész házát és Benjámin nemzetségét, száznyolczvanezer válogatott hadra való [férfiút,] hogy hadakozzanak az Izráel házával, és visszanyerjék az országot Roboámnak, a Salamon fiának."));
husuperobject.biblemap.put("b11_12_22", new husuperobject(11,12,22,"De az Isten beszéde lõn Sémajához, az Isten emberéhez, mondván:"));
husuperobject.biblemap.put("b11_12_23", new husuperobject(11,12,23,"Ezt mondjad Roboámnak, a Salamon fiának, a Júda királyának, és az egész Júda és Benjámin házának, és a többi népnek, mondván:"));
husuperobject.biblemap.put("b11_12_24", new husuperobject(11,12,24,"Azt mondja az Úr: Fel ne menjetek, és ne hadakozzatok a ti atyátokfiai ellen, az Izráel ellen; térjetek meg kiki a maga házába, mert én tõlem lett e dolog. És õk engedének az Úr beszédének, és visszatérvén, elmenének az Úr beszéde szerint."));
husuperobject.biblemap.put("b11_12_25", new husuperobject(11,12,25,"Jeroboám pedig megépíté Síkemet az Efraim hegyén, és abban lakék; és onnét kimenvén, építé Pénuelt."));
husuperobject.biblemap.put("b11_12_26", new husuperobject(11,12,26,"És monda Jeroboám az õ szívében: Majd visszatér ez ország a Dávid házához;"));
husuperobject.biblemap.put("b11_12_27", new husuperobject(11,12,27,"Ha felmegy a nép, hogy áldozatot tegyen Jeruzsálemben az Úrnak házában; e népnek szíve az õ urához, Roboámhoz, a Júda királyához hajol, és engem megölnek, és visszatérnek Roboámhoz, a Júda királyához."));
husuperobject.biblemap.put("b11_12_28", new husuperobject(11,12,28,"Tanácsot tartván azért a király, csináltata két arany borjút, és monda nékik: Sok néktek Jeruzsálembe felmennetek: Ímhol vannak a te isteneid, óh Izráel, a kik téged kihoztak Égyiptomnak földébõl."));
husuperobject.biblemap.put("b11_12_29", new husuperobject(11,12,29,"És az egyiket helyhezteté Béthelbe, a másikat pedig Dánba."));
husuperobject.biblemap.put("b11_12_30", new husuperobject(11,12,30,"És e dolog nagy bûnnek lett az okozója, mert a nép felment az egyik elé egészen Dánig."));
husuperobject.biblemap.put("b11_12_31", new husuperobject(11,12,31,"Azután felállítá a magas helyek templomát, és papokat szerze a nép aljából, a kik nem voltak a Lévi fiai közül."));
husuperobject.biblemap.put("b11_12_32", new husuperobject(11,12,32,"És szerze Jeroboám egy ünnepet is a nyolczadik hónapban, a hónap tizenötödik napján, a Júdabeli ünnep módja szerint, és áldozék az oltáron. Hasonlóképen cselekedék Béthelben is, áldozván a borjúknak, a melyeket csinált vala, és szerze Béthelben papokat a magaslatokhoz, a melyeket csinált vala."));
husuperobject.biblemap.put("b11_12_33", new husuperobject(11,12,33,"És áldozék azon az oltáron is, a melyet Béthelben állított fel, a nyolczadik hónap tizenötödik napján, abban a hónapban, a melyet az õ szívében gondolt vala; és ünnepet szerze az Izráel fiainak, és felméne az oltárra, hogy jóillatot szerezzen."));
husuperobject.biblemap.put("b11_13_1", new husuperobject(11,13,1,"És ímé Isten embere jöve Júdából Béthelbe, az Úrnak intésére, és Jeroboám ott állott az oltár mellett, hogy tömjént gyújtson."));
husuperobject.biblemap.put("b11_13_2", new husuperobject(11,13,2,"És kiálta az oltár ellen az Úr intése szerint, és monda: Oltár, oltár! ezt mondja az Úr: Ímé egy fiú születik a Dávid házából, a kinek neve Józsiás lészen, a ki megáldozza rajtad a magaslatok papjait, a kik most te rajtad tömjéneznek, és emberek csontjait égetik meg rajtad,"));
husuperobject.biblemap.put("b11_13_3", new husuperobject(11,13,3,"És ugyanazon napon csudát tõn, mondván: E lészen jegye, hogy az Úr mondotta légyen [ezt]: Ímé az oltár meghasad, és kiomol a hamu, mely rajta van."));
husuperobject.biblemap.put("b11_13_4", new husuperobject(11,13,4,"És a mikor meghallotta a király az Isten emberének beszédét, a melyet kiáltott vala az oltár ellen Béthelben, kinyújtá Jeroboám az õ kezét az oltártól, mondván: Fogjátok meg õt. És megszárada az õ keze, a melyet kinyújtott volt ellene, és nem tudta azt magához visszavonni."));
husuperobject.biblemap.put("b11_13_5", new husuperobject(11,13,5,"És meghasadt az oltár, és kiomlott a hamu az oltárról a jel szerint, a melyet tett vala az Isten embere az Úrnak beszéde által."));
husuperobject.biblemap.put("b11_13_6", new husuperobject(11,13,6,"És szóla a király, és monda az Isten emberének: Könyörögj az Úrnak a te Istenednek, és imádkozz érettem, hogy ismét hozzám hajoljon az én kezem. És mikor könyörgött az Isten embere az Úrnak, visszahajla a király keze, és olyan lõn, mint azelõtt."));
husuperobject.biblemap.put("b11_13_7", new husuperobject(11,13,7,"És monda a király az Isten emberének: Jere haza velem és egyél ebédet, meg akarlak ajándékozni."));
husuperobject.biblemap.put("b11_13_8", new husuperobject(11,13,8,"És monda az Isten embere a királynak: Ha a te házadnak felét nékem adnád is, nem mennék el veled, és nem enném kenyeret, sem vizet nem innám e helyen;"));
husuperobject.biblemap.put("b11_13_9", new husuperobject(11,13,9,"Mert azt parancsolta az Úr nékem az õ beszéde által, mondván: Ne egyél [ott] kenyeret, vizet se igyál; vissza se térj az úton, a melyen elmenéndesz."));
husuperobject.biblemap.put("b11_13_10", new husuperobject(11,13,10,"És elméne más úton, és nem tére meg azon az úton, a melyen Béthelbe ment."));
husuperobject.biblemap.put("b11_13_11", new husuperobject(11,13,11,"És lakozék Béthelben egy vén próféta, a kihez eljövén az õ fia, elbeszélé az õ atyjának mindazt a dolgot, a melyet aznap az Isten embere cselekedett volt Béthelben, és a beszédeket, a melyeket szólott vala a királynak; és elbeszélék azokat az õ atyjoknak."));
husuperobject.biblemap.put("b11_13_12", new husuperobject(11,13,12,"Akkor monda nékik az õ atyjok: Mely úton ment el? És megmutaták az õ fiai az útat, a melyen elment volt az Isten embere, a ki Júdából jött."));
husuperobject.biblemap.put("b11_13_13", new husuperobject(11,13,13,"És monda az õ fiainak: Nyergeljétek meg nékem a szamarat; és mikor megnyergelék néki a szamarat, felüle reá,"));
husuperobject.biblemap.put("b11_13_14", new husuperobject(11,13,14,"És elméne az Isten embere után, és megtalálá õt egy cserfa alatt ülve, és monda néki: Te vagy-é amaz Isten embere, a ki Júdából jöttél? És monda: Én vagyok."));
husuperobject.biblemap.put("b11_13_15", new husuperobject(11,13,15,"Akkor monda néki: Jere haza velem és egyél kenyeret."));
husuperobject.biblemap.put("b11_13_16", new husuperobject(11,13,16,"De az felele: Nem mehetek vissza veled, be sem mehetek veled; nem eszem kenyeret, vizet sem iszom veled e helyen;"));
husuperobject.biblemap.put("b11_13_17", new husuperobject(11,13,17,"Mert meg van nékem az Úrnak beszédével parancsolva: Ne egyél kenyeret, vizet se igyál ott, és ne térj azon az úton vissza, a melyen oda menéndesz."));
husuperobject.biblemap.put("b11_13_18", new husuperobject(11,13,18,"És felele az néki: Én is olyan próféta [vagyok,] mint te, és nékem angyal szólott az Úrnak beszédével, mondván: Hozd vissza õt veled a te házadba, hogy kenyeret egyék és vizet igyék. És e képen hazuda néki."));
husuperobject.biblemap.put("b11_13_19", new husuperobject(11,13,19,"Megtére azért vele, és kenyeret evék az õ házában, és vizet ivék."));
husuperobject.biblemap.put("b11_13_20", new husuperobject(11,13,20,"Mikor pedig az asztalnál ülének: lõn az Úr beszéde a prófétához, a ki õt visszahozta vala,"));
husuperobject.biblemap.put("b11_13_21", new husuperobject(11,13,21,"És kiálta az Isten emberének, a ki Júdából jött vala, ezt mondván: Ezt mondja az Úr: Mivelhogy engedetlen voltál az Úr szájának, és meg nem tartottad a parancsolatot, a melyet néked az Úr, a te Istened parancsolt volt;"));
husuperobject.biblemap.put("b11_13_22", new husuperobject(11,13,22,"Hanem visszatértél, és kenyeret ettél és vizet ittál azon a helyen, a mely felõl azt mondotta vala néked: Ne egyél [ott] kenyeret, vizet se igyál: Nem temettetik a te tested a te atyáid sírjába."));
husuperobject.biblemap.put("b11_13_23", new husuperobject(11,13,23,"És miután evett kenyeret és ivott, megnyergelék a szamarat a prófétának, a kit visszahozott vala."));
husuperobject.biblemap.put("b11_13_24", new husuperobject(11,13,24,"És mikor elment, egy oroszlán találá õt az úton, a mely megölé õt; és az õ teste az úton fekszik vala, és mind a szamár, mind az oroszlán a holttest mellett állanak vala."));
husuperobject.biblemap.put("b11_13_25", new husuperobject(11,13,25,"És ímé az arra menõ emberek láták az úton heverõ testet, és az oroszlánt a holttest mellett állani; és elmenvén, elbeszélék a városban, a melyben a vén próféta lakott."));
husuperobject.biblemap.put("b11_13_26", new husuperobject(11,13,26,"A mit mikor meghallott a próféta, a ki visszahozta vala õt az útról, monda: Az Isten embere az, a ki engedetlen volt az Úr szájának; ezért adta az Úr õt az oroszlánnak, és az törte össze és ölte meg õt, az Úrnak beszéde szerint, a melyet szólott volt néki."));
husuperobject.biblemap.put("b11_13_27", new husuperobject(11,13,27,"És szóla az õ fiainak, mondván: Nyergeljétek meg nékem a szamarat. És felnyergelték."));
husuperobject.biblemap.put("b11_13_28", new husuperobject(11,13,28,"És õ elment és megtalálá a holttestet az útfélre vetve, és a szamarat és az oroszlánt a holttest mellett állva. Az oroszlán nem evett a holtból, és a szamarat sem tépte szét."));
husuperobject.biblemap.put("b11_13_29", new husuperobject(11,13,29,"És felvevé a próféta az Isten emberének holttestét, és feltevé azt a szamárra, és visszahozá azt, és beméne a városba a vén próféta, hogy [ott] sirassa és eltemesse õt."));
husuperobject.biblemap.put("b11_13_30", new husuperobject(11,13,30,"És eltemeté a holtat a maga sírboltjába, és siraták õt: Ah szerelmes atyámfia!"));
husuperobject.biblemap.put("b11_13_31", new husuperobject(11,13,31,"És miután eltemette õt, szóla az õ fiainak, mondván: Ha meghalok, ebbe a sírba temessetek engem is, a melybe az Isten embere temettetett, tetemimet tegyétek az õ tetemei mellé;"));
husuperobject.biblemap.put("b11_13_32", new husuperobject(11,13,32,"Mert beteljesedik az, a mit kiáltott az Úrnak beszédével az oltár ellen, a mely Béthelben van, és a magas helyeken való házak ellen, a melyek Samaria városaiban vannak."));
husuperobject.biblemap.put("b11_13_33", new husuperobject(11,13,33,"De még e történet után sem tért meg Jeroboám az õ gonosz útjáról, hanem ismét papokat rendele a nép aljából a magaslatokra, és a ki akarja vala, azt szentelé fel, hogy legyen a magaslatok papja."));
husuperobject.biblemap.put("b11_13_34", new husuperobject(11,13,34,"És ez a dolog lett az [oka] a Jeroboám háza vétkének, és a föld színérõl való kiirtatásának és megsemmisíttetésének."));
husuperobject.biblemap.put("b11_14_1", new husuperobject(11,14,1,"Ebben az idõben megbetegedék Abija, a Jeroboám fia."));
husuperobject.biblemap.put("b11_14_2", new husuperobject(11,14,2,"És monda Jeroboám az õ feleségének: Kelj fel most, és változtasd meg öltözetedet, hogy meg ne ismerjék, hogy te vagy Jeroboám felesége, és menj el Silóba: Ímé ott van Ahija próféta, a ki nékem megmondotta volt, hogy királylyá leszek e népen."));
husuperobject.biblemap.put("b11_14_3", new husuperobject(11,14,3,"És végy magadhoz tíz kenyeret és pogácsát, és egy edényben mézet, és menj el hozzá; õ majd megmondja néked, mi történik a gyermekkel."));
husuperobject.biblemap.put("b11_14_4", new husuperobject(11,14,4,"És e képen cselekedék a Jeroboám felesége; és felkészülvén, elméne Silóba, és beméne az Ahija házába. Ahija azonban már nem látott; mert meghomályosodtak az õ szemei a vénség miatt."));
husuperobject.biblemap.put("b11_14_5", new husuperobject(11,14,5,"És az Úr monda Ahijának: Ímé a Jeroboám felesége jött [hozzád,] hogy valamit kérdjen tõled az õ fia felõl, mert beteg; te azért így s így szólj néki. És mikor bement, másnak tetteté magát."));
husuperobject.biblemap.put("b11_14_6", new husuperobject(11,14,6,"De mikor meghallotta Ahija az õ lábainak zörejét, a mint az ajtóhoz közelgete, monda: Jõjj be Jeroboám felesége; miért tetteted magadat másnak? Én te hozzád kemény [követséggel] küldettem."));
husuperobject.biblemap.put("b11_14_7", new husuperobject(11,14,7,"Menj el, mondd meg Jeroboámnak: Ezt mondja az Úr, Izráelnek Istene: Mivelhogy én téged e nép közül felmagasztaltalak, és téged fejedelemmé tettelek az én népemen, az Izráelen;"));
husuperobject.biblemap.put("b11_14_8", new husuperobject(11,14,8,"És elszakasztottam az országot a Dávid házától, és azt néked adtam; te azonban nem voltál olyan, mint az én szolgám, Dávid, a ki megõrizte az én parancsolatimat, és a ki engem követett teljes szívébõl, csak azt cselekedvén, a mi kedves az én szemeim elõtt;"));
husuperobject.biblemap.put("b11_14_9", new husuperobject(11,14,9,"Hanem gonoszabbul cselekedtél mindazoknál, a kik te elõtted voltak; mert elmentél és idegen isteneket csináltál magadnak, és öntött bálványokat, hogy engem haragra ingerelj, és engem hátad mögé vetettél:"));
husuperobject.biblemap.put("b11_14_10", new husuperobject(11,14,10,"Azért ímé én veszedelmet hozok a Jeroboám házára, és kiirtom Jeroboámnak még az ebét is, és mind a berekesztettet, mind az elhagyottat Izráelben, és kihányom a Jeroboám háza maradékait, miképen a ganéjt kihányják, mígnem vége lesz."));
husuperobject.biblemap.put("b11_14_11", new husuperobject(11,14,11,"A ki meghal a Jeroboám [maradékai] közül a városban, azt az ebek eszik meg; a ki pedig a mezõn hal meg, az égi madarak eszik meg; mert az Úr szólott."));
husuperobject.biblemap.put("b11_14_12", new husuperobject(11,14,12,"Te pedig kelj fel, és menj haza, mert a mint belépsz a városba, meghal a gyermek;"));
husuperobject.biblemap.put("b11_14_13", new husuperobject(11,14,13,"És az egész Izráel siratja õt, és eltemeti õt, mert a Jeroboám magvából csak egyedül õ temettetik el sírba, mivel a Jeroboám háznépe közül az Úr iránt, Izráel Istene iránt csak õ benne találtatott valami jó."));
husuperobject.biblemap.put("b11_14_14", new husuperobject(11,14,14,"És támaszt az Úr magának királyt Izráelben, a ki kigyomlálja a Jeroboám házát egy napon. De mit [mondok?] Már [is támasztott!"));
husuperobject.biblemap.put("b11_14_15", new husuperobject(11,14,15,"És megveri az Úr az Izráelt, mint a hogy a nád a vízben ide s tova hányattatik, és elszakasztja az Izráelt errõl a jó földrõl, a melyet adott az õ atyáiknak, és szétszórja õket a folyóvizen túl, mivelhogy Aserákat csináltak magoknak, hogy az Urat ingereljék."));
husuperobject.biblemap.put("b11_14_16", new husuperobject(11,14,16,"És kézbe adja az Izráelt a Jeroboám bûneiért, a ki [maga is] vétkezett és az Izráelt is bûnbe ejtette."));
husuperobject.biblemap.put("b11_14_17", new husuperobject(11,14,17,"És felkészült a Jeroboám felesége, elment és juta Thirsába. És mikor a ház küszöbén belépett, meghalt a gyermek."));
husuperobject.biblemap.put("b11_14_18", new husuperobject(11,14,18,"És eltemeték õt, és siratá õt az egész Izráel, az Úrnak beszéde szerint, a melyet szólott az õ szolgája, Ahija próféta által."));
husuperobject.biblemap.put("b11_14_19", new husuperobject(11,14,19,"Jeroboámnak pedig egyéb cselekedetei, mimódon hadakozott és uralkodott, ímé meg vannak írva az Izráel királyainak krónika-könyvében."));
husuperobject.biblemap.put("b11_14_20", new husuperobject(11,14,20,"És az idõ, a melyben uralkodott Jeroboám, huszonkét esztendõ, és elaluvék az õ atyáival, és uralkodék Nádáb, az õ fia, õ helyette."));
husuperobject.biblemap.put("b11_14_21", new husuperobject(11,14,21,"Roboám pedig, a Salamon fia Júdában uralkodék: Negyvenegy esztendõs volt Roboám, mikor uralkodni kezdett, és tizenhét esztendeig uralkodott Jeruzsálemben, abban a városban, a melyet az Úr választott volt [magának] az Izráelnek minden nemzetségei közül, hogy ott helyheztesse az õ nevét; és az õ anyjának Naama volt a neve, a ki az Ammon nemzetségébõl való volt."));
husuperobject.biblemap.put("b11_14_22", new husuperobject(11,14,22,"Júda is gonoszul cselekedék az Úr szemei elõtt, és sokkal nagyobb haragra indíták õt az õ vétkeikkel, a melyekkel vétkeztek, mint atyáik azokkal, a melyeket õk cselekedtek volt."));
husuperobject.biblemap.put("b11_14_23", new husuperobject(11,14,23,"Mert õk is építének magoknak magaslatokat, és faragott képeket és Aserákat minden magas halmon, és minden zöldellõ fa alatt."));
husuperobject.biblemap.put("b11_14_24", new husuperobject(11,14,24,"És valának férfi paráznák is az országban, és cselekedének a pogányok minden útálatos vétkei szerint, a kiket az Úr kiûzött volt az Izráel fiai elõtt."));
husuperobject.biblemap.put("b11_14_25", new husuperobject(11,14,25,"És lõn Roboám király ötödik esztendejében, feljött Sisák, az Égyiptombeli király Jeruzsálem ellen."));
husuperobject.biblemap.put("b11_14_26", new husuperobject(11,14,26,"És elvivé az Úr házának kincseit, és a király házának kincseit, és mindent, a mi csak elvihetõ volt; elvitte mind az arany paizsokat is, a melyeket Salamon csináltatott,"));
husuperobject.biblemap.put("b11_14_27", new husuperobject(11,14,27,"A melyek helyett Roboám király azután rézpaizsokat csináltatott, és adá azokat a testõrök fejedelmeinek kezébe, a kik a király házának kapunállói valának."));
husuperobject.biblemap.put("b11_14_28", new husuperobject(11,14,28,"És valahányszor a király bement az Úr házába, bevitték azokat a testõrök, és ismét visszahozták a testõrök házába."));
husuperobject.biblemap.put("b11_14_29", new husuperobject(11,14,29,"Roboámnak pedig több dolgai és minden cselekedetei, nemde nem meg vannak-é írva a Júda királyainak krónika-könyvében?"));
husuperobject.biblemap.put("b11_14_30", new husuperobject(11,14,30,"És folyton hadakozás volt Roboám és Jeroboám között."));
husuperobject.biblemap.put("b11_14_31", new husuperobject(11,14,31,"És elaluvék Roboám az õ atyáival, és eltemetteték az õ atyáival a Dávid városában; és az õ anyjának Naama volt a neve, az Ammoniták nemzetségébõl való; és Abija, az õ fia, lett a király õ helyette."));
husuperobject.biblemap.put("b11_15_1", new husuperobject(11,15,1,"Jeroboám királynak, a Nébát fiának tizennyolczadik esztendejében Abija lett a király Júdában."));
husuperobject.biblemap.put("b11_15_2", new husuperobject(11,15,2,"És három esztendeig uralkodott Jeruzsálemben. Az õ anyjának neve Maaka, az Abisálom leánya."));
husuperobject.biblemap.put("b11_15_3", new husuperobject(11,15,3,"És járt az õ atyjának minden bûneiben, a melyeket õ elõtte cselekedett, és nem volt az õ szíve [olyan] tökéletes az õ Urához Istenéhez, mint Dávidnak, az õ atyjának szíve."));
husuperobject.biblemap.put("b11_15_4", new husuperobject(11,15,4,"De mindazáltal Dávidért adott néki az Úr az õ Istene szövétneket Jeruzsálemben: felmagasztalván az õ fiát õ utána és Jeruzsálemet megerõsítvén;"));
husuperobject.biblemap.put("b11_15_5", new husuperobject(11,15,5,"Mert Dávid azt cselekedte, a mi kedves volt az Úr szemei elõtt, és el nem távozott azoktól, a melyeket parancsolt néki az õ életének minden idejében, kivéve a Hitteus Uriás dolgát."));
husuperobject.biblemap.put("b11_15_6", new husuperobject(11,15,6,"És hadakozás volt Roboám és Jeroboám között mind éltig."));
husuperobject.biblemap.put("b11_15_7", new husuperobject(11,15,7,"Abija egyéb dolgai és minden cselekedetei pedig vajjon nincsenek-é megírva a Júda királyainak krónika-könyvében? És hadakozás volt Abija és Jeroboám között."));
husuperobject.biblemap.put("b11_15_8", new husuperobject(11,15,8,"És elaluvék Abija az õ atyáival, és eltemeték õt a Dávid városában; és uralkodék helyette Asa, az õ fia."));
husuperobject.biblemap.put("b11_15_9", new husuperobject(11,15,9,"Jeroboámnak az Izráel királyának a huszadik esztendejében Asa lett a király Júdában,"));
husuperobject.biblemap.put("b11_15_10", new husuperobject(11,15,10,"És uralkodék negyvenegy esztendeig Jeruzsálemben; és az õ anyjának Maaka vala neve, az Abisálom leánya."));
husuperobject.biblemap.put("b11_15_11", new husuperobject(11,15,11,"És Asa azt cselekedé, a mi kedves volt az Úr szemei elõtt, mint Dávid, az õ atyja."));
husuperobject.biblemap.put("b11_15_12", new husuperobject(11,15,12,"Mert kiveszté a férfiú paráznákat az országból; és lerontá mind a bálványokat, a melyeket csináltak volt az õ atyái."));
husuperobject.biblemap.put("b11_15_13", new husuperobject(11,15,13,"És Maakát az õ anyját is megfosztá a királynéságtól, mivelhogy egy iszonyú bálványt csináltatott a berekben; és elrontá Asa az õ bálványát, és megégeté azt a Kidron pataknál."));
husuperobject.biblemap.put("b11_15_14", new husuperobject(11,15,14,"De ha a magaslatokat nem rontották is le, mégis Asának szíve tökéletes volt az Úrhoz életének minden napjaiban."));
husuperobject.biblemap.put("b11_15_15", new husuperobject(11,15,15,"És bevitte az Úrnak házába az ezüstöt és az aranyat, és az edényeket, a melyeket az õ atyja és õ maga arra szenteltek volt."));
husuperobject.biblemap.put("b11_15_16", new husuperobject(11,15,16,"És hadakozás volt Asa és Baása, az Izráel királya között mind éltig."));
husuperobject.biblemap.put("b11_15_17", new husuperobject(11,15,17,"Mert feljöve Baása, az Izráel királya Júda ellen, és megépíté Rámát, hogy [senkit] ki és be ne bocsásson Asához a Júda királyához."));
husuperobject.biblemap.put("b11_15_18", new husuperobject(11,15,18,"És vévén Asa mind az ezüstöt és aranyat, a mely megmaradott volt mind az Úr, mind a király házának kincseibõl; adá azt az õ szolgáinak kezébe, és elküldé azt Asa király Benhadádnak, Tabrimmon fiának, a ki Héczion fia volt, Siria királyának, a ki Damaskusban lakott, ezt izenvén [néki:]"));
husuperobject.biblemap.put("b11_15_19", new husuperobject(11,15,19,"Szövetség van köztem és te közötted, az én atyám és a te atyád között. Ímé ajándékot küldök néked, ezüstöt és aranyat, bontsd fel a te szövetségedet Baásával, az Izráel királyával, hogy távozzék el tõlem."));
husuperobject.biblemap.put("b11_15_20", new husuperobject(11,15,20,"És Benhadád engedelmeskedett Asa királynak és elküldé az õ seregeinek vezéreit az Izráel városai ellen; és bevevé Hijont és Dánt, és Abel Beth-Maakát, és az egész Kinneróthot a Nafthali egész földével."));
husuperobject.biblemap.put("b11_15_21", new husuperobject(11,15,21,"Mikor pedig Baása ezt meghallotta, abbahagyta Ráma építését, és lakék Thirsában."));
husuperobject.biblemap.put("b11_15_22", new husuperobject(11,15,22,"Akkor Asa király egybegyûjté az egész Júdát, senkit ki nem hagyván, és elhordák Rámának mind köveit, mind fáját, a melyekkel Baása épített volt, és építé azokból Asa király Gébát, a Benjámin nemzetségében és Mispát."));
husuperobject.biblemap.put("b11_15_23", new husuperobject(11,15,23,"Asának minden egyéb dolgai pedig és egész uralkodása és valamit cselekedett, és a városok, a melyeket épített, vajjon nincsenek-é megírva a Júda királyainak krónika-könyvében? kivéve azt, hogy vénségében megbetegedett a lábaira."));
husuperobject.biblemap.put("b11_15_24", new husuperobject(11,15,24,"És elaluvék Asa az õ atyáival, és eltemetteték az õ atyáival Dávidnak, az õ atyjának városában. És Jósafát, az õ fia, uralkodék helyette."));
husuperobject.biblemap.put("b11_15_25", new husuperobject(11,15,25,"Izráelben pedig Nádáb, a Jeroboám fia lett a király, Asának, a Júdabeli királynak második esztendejében; és két esztendeig uralkodék Izráelen;"));
husuperobject.biblemap.put("b11_15_26", new husuperobject(11,15,26,"És gonoszul cselekedék az Úrnak szemei elõtt és jára az õ atyjának útján és annak vétkében, a melylyel bûnbe ejtette volt az Izráelt."));
husuperobject.biblemap.put("b11_15_27", new husuperobject(11,15,27,"De Baása, az Ahija fia, az Issakhár nemzetségébõl, pártot ütött ellene, és megveré õt Baása Gibbethonnál a Filiszteusok [városá]nál; mert Nádáb és az egész Izráel Gibbethonnál táboroztak."));
husuperobject.biblemap.put("b11_15_28", new husuperobject(11,15,28,"És megölé õt Baása, Asának, a júdabeli királynak harmadik esztendejében, és uralkodék helyette."));
husuperobject.biblemap.put("b11_15_29", new husuperobject(11,15,29,"És lõn, mikor uralkodni kezdett, levágá Jeroboámnak egész háznépét; egy lelket sem hagyott Jeroboám háznépébõl, mígnem mind elveszté õket az Úrnak beszéde szerint, a melyet megmondott volt az õ szolgája, a Silóbeli Ahija által,"));
husuperobject.biblemap.put("b11_15_30", new husuperobject(11,15,30,"A Jeroboám vétkeiért, a melyekkel vétkezék és az Izráelt is vétekbe ejté; és a bosszantásért, a melylyel felbosszantotta az Urat, Izráel Istenét."));
husuperobject.biblemap.put("b11_15_31", new husuperobject(11,15,31,"Nádáb egyéb dolgai pedig és minden cselekedetei, vajjon nincsenek-é megírva az Izráel királyainak krónika-könyvében?"));
husuperobject.biblemap.put("b11_15_32", new husuperobject(11,15,32,"És hadakozás volt Asa és Baása, az Izráel királya között mind éltig."));
husuperobject.biblemap.put("b11_15_33", new husuperobject(11,15,33,"Asának, a Júdabeli királynak harmadik esztendejében lett Baása, az Ahija fia királylyá az egész Izráelen Thirsában, huszonnégy esztendeig."));
husuperobject.biblemap.put("b11_15_34", new husuperobject(11,15,34,"És gonoszul cselekedék az Úrnak szemei elõtt, és jára a Jeroboám útján, és az õ vétkében, a melylyel bûnbe ejtette az Izráelt."));
husuperobject.biblemap.put("b11_16_1", new husuperobject(11,16,1,"Lõn azonban az Úrnak beszéde Jéhuhoz, a Hanáni fiához, Baása ellen, mondván:"));
husuperobject.biblemap.put("b11_16_2", new husuperobject(11,16,2,"Mivelhogy téged felemeltelek a porból, és fejedelemmé tettelek téged az én népemen, az Izráelen, te pedig a Jeroboám útján jársz, és bûnbe ejted az én népemet, az Izráelt, hogy engem haragra indítsanak az õ vétkeikkel:"));
husuperobject.biblemap.put("b11_16_3", new husuperobject(11,16,3,"Íme én elvetem Baása maradékait, és az õ háza maradékait, és olyanná teszem a te házadat, mint Jeroboámnak, a Nébát fiának házát."));
husuperobject.biblemap.put("b11_16_4", new husuperobject(11,16,4,"A ki Baása maradékai közül a városban hal meg, azt az ebek eszik meg; a ki pedig a mezõn halánd meg, azt az égi madarak eszik meg."));
husuperobject.biblemap.put("b11_16_5", new husuperobject(11,16,5,"Baása több dolgai pedig, és a mit cselekedett, és az õ uralkodása vajjon nincsenek-é megírva az Izráel királyainak krónika- könyvében?"));
husuperobject.biblemap.put("b11_16_6", new husuperobject(11,16,6,"És elaluvék Baása az õ atyáival, és eltemetteték Thirsában, és az õ fia, Ela uralkodék õ utána."));
husuperobject.biblemap.put("b11_16_7", new husuperobject(11,16,7,"És nemcsak azért lõn az Úrnak beszéde Jéhu próféta, a Hanáni fia által Baása és az õ háza ellen és minden õ gonoszsága ellen, a melyet cselekedett volt az Úr szemei elõtt, õt haragra indítván az õ kezeinek cselekedetei által, hogy olyan lesz mint a Jeroboám háza, hanem azért is, mert azt megölte."));
husuperobject.biblemap.put("b11_16_8", new husuperobject(11,16,8,"És Asának, a Júda királyának huszonhatodik esztendejében lett királylyá Ela, a Baása fia, az Izráelen Thirsában, két esztendeig."));
husuperobject.biblemap.put("b11_16_9", new husuperobject(11,16,9,"Azonban az õ szolgája Zimri, a szekerek fél részének feje, pártot ütött ellene. És Ela Thirsában volt, és az ital miatt igen megrészegedett az Arsa házában, a ki az õ házának Thirsában gondviselõje volt;"));
husuperobject.biblemap.put("b11_16_10", new husuperobject(11,16,10,"És eljövén Zimri, megveré és megölé õt, Asának, a Júdabeli királynak huszonhetedik esztendejében, és õ uralkodék helyette."));
husuperobject.biblemap.put("b11_16_11", new husuperobject(11,16,11,"Mikor pedig királylyá lett és trónját elfoglalta, kiirtá Baása egész házát minden rokonságával és barátaival együtt és nem hagyott belõle még csak egy ebet sem."));
husuperobject.biblemap.put("b11_16_12", new husuperobject(11,16,12,"Így veszté ki Zimri a Baása egész házát, az Úrnak beszéde szerint, a melyet Baása ellen szólott Jéhu próféta által:"));
husuperobject.biblemap.put("b11_16_13", new husuperobject(11,16,13,"Baása minden vétkeiért, és Elának, az õ fiának vétkeiért, a kik vétkeztek és vétekbe ejtették az Izráelt, haragra indítván az Urat, Izráelnek Istenét az õ bálványozásukkal."));
husuperobject.biblemap.put("b11_16_14", new husuperobject(11,16,14,"Elának egyéb dolgai pedig és minden cselekedetei, vajjon nincsenek-é megírva az Izráel királyainak krónika-könyvében?"));
husuperobject.biblemap.put("b11_16_15", new husuperobject(11,16,15,"Asának, a Júdabeli királynak huszonhetedik esztendejében, uralkodék Zimri hét napig Thirsában; a nép pedig Gibbethon elõtt táborozott, a mely a Filiszteusoké."));
husuperobject.biblemap.put("b11_16_16", new husuperobject(11,16,16,"Mikor pedig hallotta a nép a táborban beszélni, hogy pártot ütött Zimri és a királyt meg is ölte: az egész Izráel azon a napon Omrit, az Izráel seregeinek fõvezérét választá királylyá a táborban."));
husuperobject.biblemap.put("b11_16_17", new husuperobject(11,16,17,"És felvonult Omri és az egész Izráel õ vele Gibbethonból, és megszállák Thirsát."));
husuperobject.biblemap.put("b11_16_18", new husuperobject(11,16,18,"De Zimri, a mikor látta, hogy a várost bevették, bemenvén a királyi ház palotájába, tûzzel magára gyújtá a királyi házat, és meghala."));
husuperobject.biblemap.put("b11_16_19", new husuperobject(11,16,19,"Az õ bûneiért, a melyekkel vétkezék, gonoszul cselekedvén az Úr szemei elõtt; járván a Jeroboám útjában és az õ bûnében, a melyet cselekedett volt, vétekbe ejtvén az Izráelt."));
husuperobject.biblemap.put("b11_16_20", new husuperobject(11,16,20,"Zimrinek egyéb dolgai pedig és az õ pártütése, a melyet cselekedett, vajjon nincsenek-é megírva az Izráelbeli királyok krónika-könyvében?"));
husuperobject.biblemap.put("b11_16_21", new husuperobject(11,16,21,"Akkor kétfelé hasonlék az Izráel népe: a nép egyik része Tibnit, a Ginát fiát követte és azt tette királylyá, a másik része pedig Omrihoz ragaszkodott."));
husuperobject.biblemap.put("b11_16_22", new husuperobject(11,16,22,"De a népnek az a része, a mely Omrihoz ragaszkodott, erõsebb volt, mint az a nép, a mely Tibnit, a Ginát fiát követte, és meghalván Tibni, uralkodék Omri."));
husuperobject.biblemap.put("b11_16_23", new husuperobject(11,16,23,"Asának, a Júdabeli királynak harminczegyedik esztendejében uralkodék Omri Izráelben tizenkét esztendeig; Thirsában uralkodék hat esztendeig."));
husuperobject.biblemap.put("b11_16_24", new husuperobject(11,16,24,"És megvevé áron a Samaria hegyét Sémertõl két tálentom ezüstön; és építe a hegyre, és nevezé a várost, a melyet építe, Sémernek, a hegy urának nevérõl, Samariának."));
husuperobject.biblemap.put("b11_16_25", new husuperobject(11,16,25,"És gonoszul cselekedék Omri az Úr szemei elõtt, és gonoszságával meghaladá mind az õ elõtte valókat."));
husuperobject.biblemap.put("b11_16_26", new husuperobject(11,16,26,"És jára Jeroboámnak, a Nébát fiának minden útján és az õ bûnében, a melylyel vétekbe ejté az Izráelt, haragra indítván az Urat, Izráel Istenét az õ bálványozásai által."));
husuperobject.biblemap.put("b11_16_27", new husuperobject(11,16,27,"Omrinak egyéb dolgai pedig és minden cselekedetei és az õ erõssége, a melylyel cselekedett, vajjon nincsenek-é megírva az Izráelbeli királyok krónika-könyvében?"));
husuperobject.biblemap.put("b11_16_28", new husuperobject(11,16,28,"És elaluvék Omri az õ atyáival, és eltemetteték Samariában, és uralkodék õ utána az õ fia, Akháb."));
husuperobject.biblemap.put("b11_16_29", new husuperobject(11,16,29,"Akháb pedig, az Omri fia uralkodni kezde Izráelen, Asának, a Júdabeli királynak harmincznyolczadik esztendejében; és uralkodék Akháb, az Omri fia, Izráelen Samariában huszonkét esztendeig."));
husuperobject.biblemap.put("b11_16_30", new husuperobject(11,16,30,"És gonoszabbul cselekedék Akháb, az Omri fia az Úr szemei elõtt mindazoknál, a kik õ elõtte voltak."));
husuperobject.biblemap.put("b11_16_31", new husuperobject(11,16,31,"Mert nem elégedék meg azzal, hogy Jeroboámnak, a Nébát fiának bûneiben járjon, hanem elméne és feleségül vevé magának Jézabelt, Ethbaálnak, a Sídonbeli királynak leányát, és elmenvén, a Baálnak szolgála, és meghajtá magát annak."));
husuperobject.biblemap.put("b11_16_32", new husuperobject(11,16,32,"És oltárt emele a Baálnak a Baál házában, a melyet Samariában épített."));
husuperobject.biblemap.put("b11_16_33", new husuperobject(11,16,33,"És csinált Akháb Aserát is, és jobban haragra indítá Akháb az Urat, Izráel Istenét, mint az Izráel valamennyi királya, a kik õ elõtte voltak."));
husuperobject.biblemap.put("b11_16_34", new husuperobject(11,16,34,"Ennek idejében építé meg a Béthelbeli Hiel Jérikhót. Az õ elsõszülött fiának, Abirámnak [élete] árán veté meg annak fundamentomát, és az õ kisebbik fiának, Ségubnak [élete] árán állítá fel annak kapuit, az Úr beszéde szerint, a melyet szólott volt Józsué, a Nún fia által."));
husuperobject.biblemap.put("b11_17_1", new husuperobject(11,17,1,"És szóla Thesbites Illés, a Gileád lakói közül, Akhábnak: Él az Úr, az Izráel Istene, a ki elõtt állok, hogy ez esztendõkben sem harmat, sem esõ nem lészen; hanem csak az én beszédem szerint."));
husuperobject.biblemap.put("b11_17_2", new husuperobject(11,17,2,"És lõn az Úrnak beszéde õ hozzá, mondván:"));
husuperobject.biblemap.put("b11_17_3", new husuperobject(11,17,3,"Menj el innét, és menj napkelet felé, és rejtezzél el a Kérith patakja mellett, mely a Jordán felé [folyik.]"));
husuperobject.biblemap.put("b11_17_4", new husuperobject(11,17,4,"És a patakból lesz néked italod; a hollóknak pedig megparancsoltam, hogy tápláljanak téged ott."));
husuperobject.biblemap.put("b11_17_5", new husuperobject(11,17,5,"Elméne azért, és az Úrnak beszéde szerint cselekedék; és elméne és leüle a Kérith patakja mellett, a mely a Jordán felé [folyik.]"));
husuperobject.biblemap.put("b11_17_6", new husuperobject(11,17,6,"És a hollók hoztak néki kenyeret és húst reggel és este, és a patakból ivott."));
husuperobject.biblemap.put("b11_17_7", new husuperobject(11,17,7,"És lõn néhány nap múlva, hogy kiszáradt a patak; mert nem volt esõ a földre."));
husuperobject.biblemap.put("b11_17_8", new husuperobject(11,17,8,"És lõn az Úrnak beszéde õ hozzá, mondván:"));
husuperobject.biblemap.put("b11_17_9", new husuperobject(11,17,9,"Kelj fel, és menj el Sareptába, a mely Sídonhoz tartozik, és légy ott; ímé megparancsoltam ott egy özvegyasszonynak, hogy gondoskodjék rólad."));
husuperobject.biblemap.put("b11_17_10", new husuperobject(11,17,10,"És felkelvén, elméne Sareptába, és mikor a város kapujához érkezett, ímé egy özvegyasszony volt ott, a ki fát szedegetett, és megszólítván azt, monda néki: Hozz, kérlek, egy kevés vizet nékem valami edényben, hogy igyam."));
husuperobject.biblemap.put("b11_17_11", new husuperobject(11,17,11,"De mikor az elment, hogy [vizet] hozzon, utána kiáltott, és monda néki: Hozz, kérlek, egy falat kenyeret is kezedben."));
husuperobject.biblemap.put("b11_17_12", new husuperobject(11,17,12,"Az pedig monda: Él az Úr, a te Istened, hogy nincs semmi [sült] kenyerem, csak egy marok lisztecském van a vékában, és egy kevés olajom a korsóban, és most egy kis fát szedegetek, és haza megyek, és megkészítem azt magamnak és az én fiamnak, hogy megegyük és azután meghaljunk."));
husuperobject.biblemap.put("b11_17_13", new husuperobject(11,17,13,"Monda pedig néki Illés: Ne félj, menj el, cselekedjél a te beszéded szerint; de mindazáltal nékem süss abból elõször egy kis pogácsát, és hozd ide; magadnak és a te fiadnak pedig azután süss;"));
husuperobject.biblemap.put("b11_17_14", new husuperobject(11,17,14,"Mert azt mondja az Úr, Izráel Istene, hogy sem a vékabeli liszt el nem fogy, sem a korsóbeli olaj meg nem kevesül addig, míg az Úr esõt ád a földnek színére."));
husuperobject.biblemap.put("b11_17_15", new husuperobject(11,17,15,"És õ elméne, és az Illés beszéde szerint cselekedék, és evék mind õ, mind amaz, mind annak háznépe, naponként."));
husuperobject.biblemap.put("b11_17_16", new husuperobject(11,17,16,"A vékabeli liszt nem fogyott el, sem pedig a korsóbeli olaj nem kevesült meg, az Úrnak beszéde szerint, a melyet szólott Illés által."));
husuperobject.biblemap.put("b11_17_17", new husuperobject(11,17,17,"És lõn ezek után, hogy megbetegedék a ház gazdasszonyának fia, és az õ betegsége felette nagy vala, annyira, hogy már a lélekzete is elállott."));
husuperobject.biblemap.put("b11_17_18", new husuperobject(11,17,18,"Monda azért Illésnek: Mit vétettem ellened, Istennek embere? Azért jöttél hozzám, hogy eszembe juttassad álnokságomat, és megöljed az én fiamat?"));
husuperobject.biblemap.put("b11_17_19", new husuperobject(11,17,19,"És monda néki: Add ide a te fiadat. És õ elvevé azt az õ kebelérõl, és felvivé a felházba, a melyben õ lakik vala, és az õ ágyára fekteté."));
husuperobject.biblemap.put("b11_17_20", new husuperobject(11,17,20,"Akkor kiálta az Úrhoz, és monda: Én Uram, Istenem, nyomorúságot hozol erre az özvegyre is, a kinél én lakom, hogy az õ fiát megölöd?"));
husuperobject.biblemap.put("b11_17_21", new husuperobject(11,17,21,"És ráborult háromszor a gyermekre, és felkiáltott az Úrhoz, mondván: Én Uram, Istenem, térítsd vissza e gyermek lelkét õ belé!"));
husuperobject.biblemap.put("b11_17_22", new husuperobject(11,17,22,"És meghallgatta az Úr Illés szavát, és megtért a gyermekbe a lélek, és megélede."));
husuperobject.biblemap.put("b11_17_23", new husuperobject(11,17,23,"És felvévén Illés a gyermeket, alávivé õt a felházból a házba, és adá õt az õ anyjának, és monda Illés: Lássad, él a te fiad!"));
husuperobject.biblemap.put("b11_17_24", new husuperobject(11,17,24,"És monda az asszony Illésnek: Most tudtam meg, hogy te Isten embere vagy, és hogy az Úrnak beszéde a te ajkadon: igazság."));
husuperobject.biblemap.put("b11_18_1", new husuperobject(11,18,1,"És lõn sok idõ multán, a harmadik esztendõben, az Úrnak beszéde Illéshez, mondván: Menj el, mutasd meg magadat Akhábnak, és esõt adok a föld színére."));
husuperobject.biblemap.put("b11_18_2", new husuperobject(11,18,2,"És elméne Illés, hogy megmutassa magát Akhábnak. Nagy éhség volt pedig Samariában."));
husuperobject.biblemap.put("b11_18_3", new husuperobject(11,18,3,"És Akháb hivatá Abdiást, a ki az õ házának gondviselõje volt. (Abdiás pedig felette igen féli vala az Urat;"));
husuperobject.biblemap.put("b11_18_4", new husuperobject(11,18,4,"Mert mikor Jézabel megölette az Úr prófétáit, Abdiás száz prófétát vett [oltalmába,] és rejtette el ötvenenként [egy-egy] barlangba, és ott táplálta õket kenyérrel és vízzel)."));
husuperobject.biblemap.put("b11_18_5", new husuperobject(11,18,5,"És monda Akháb Abdiásnak: Menj el az országba szerén-szerte a vizek forrásaihoz és a patakokhoz, hogyha valami füvet találhatnánk, hogy a lovakat és öszvéreket megtarthatnánk életben, és ne hagynánk a barmokat mindenestõl elpusztulni."));
husuperobject.biblemap.put("b11_18_6", new husuperobject(11,18,6,"Eloszták azért magok közt a földet, a melyen kiki elmenjen. Akháb egyedül ment az egyik úton, és Abdiás [is] egyedül ment a másik úton."));
husuperobject.biblemap.put("b11_18_7", new husuperobject(11,18,7,"És mikor Abdiás az úton ment, ímé Illés elõtalálá õt, és mikor felismerte õt, arczra borula és monda: Nem te vagy-é az, uram, Illés?"));
husuperobject.biblemap.put("b11_18_8", new husuperobject(11,18,8,"Felele néki: Én vagyok; menj el, mondd meg a te uradnak: Ímé itt van Illés."));
husuperobject.biblemap.put("b11_18_9", new husuperobject(11,18,9,"Õ pedig monda: Mit vétettem ellened, hogy a te szolgádat Akháb kezébe akarod adni, hogy megöljön engem?"));
husuperobject.biblemap.put("b11_18_10", new husuperobject(11,18,10,"Él az Úr a te Istened: nincs sem nemzetség, sem ország, a hova el nem küldött volna az én uram, hogy megkeressen téged. És ha azt mondották: Nincs itt! az országot és a népet megesküdtette, hogy téged csakugyan nem találtak meg."));
husuperobject.biblemap.put("b11_18_11", new husuperobject(11,18,11,"És most te azt mondod: Menj el, mondd meg a te uradnak: Ímé itt van Illés."));
husuperobject.biblemap.put("b11_18_12", new husuperobject(11,18,12,"Ha most elmegyek tõled, téged pedig olyan helyre ragad el az Úrnak Lelke, a melyet én nem tudok, és én elmegyek, hogy megmondjam Akhábnak, és ha õ téged nem talál meg, engem öl meg; pedig a te szolgád féli az Urat gyermekségétõl fogva."));
husuperobject.biblemap.put("b11_18_13", new husuperobject(11,18,13,"Nem mondották-é meg az én uramnak, mit cselekedtem, mikor Jézabel megölette az Úr prófétáit? hogy hogyan rejtettem el az Úr prófétái közül száz férfiút ötvenenként egy-egy barlangba és tápláltam õket kenyérrel és vízzel."));
husuperobject.biblemap.put("b11_18_14", new husuperobject(11,18,14,"És te mégis azt mondod: Menj el és mondd meg a te uradnak: Ímé itt van Illés; hogy megöljön engemet."));
husuperobject.biblemap.put("b11_18_15", new husuperobject(11,18,15,"És felele Illés: Él a Seregeknek Ura, a ki elõtt állok: e mai napon megmutatom magamat néki."));
husuperobject.biblemap.put("b11_18_16", new husuperobject(11,18,16,"Elméne azért Abdiás Akháb eleibe, és megjelenté ezt néki, és eleibe méne Akháb Illésnek."));
husuperobject.biblemap.put("b11_18_17", new husuperobject(11,18,17,"És mikor meglátta Akháb Illést, monda Akháb néki: Te vagy-é az Izráel megháborítója?"));
husuperobject.biblemap.put("b11_18_18", new husuperobject(11,18,18,"Õ pedig monda: Nem én háborítottam meg az Izráelt, hanem te és a te atyád háza, azzal, hogy elhagytátok az Úrnak parancsolatait, és a Baál után jártatok."));
husuperobject.biblemap.put("b11_18_19", new husuperobject(11,18,19,"Most azért küldj el, gyûjtsd hozzám az egész Izráelt a Kármel hegyre, és a Baál négyszázötven prófétáját, és az Aserának négyszáz prófétáját, a kik a Jézabel asztaláról élnek."));
husuperobject.biblemap.put("b11_18_20", new husuperobject(11,18,20,"És elkülde Akháb mind az egész Izráel fiaihoz, és egybegyûjté a prófétákat a Kármel hegyre."));
husuperobject.biblemap.put("b11_18_21", new husuperobject(11,18,21,"És odamenvén Illés az egész sokasághoz, monda: Meddig sántikáltok kétfelé? Ha az Úr az Isten, kövessétek õt; ha pedig Baál, kövessétek azt. És nem felelt néki a nép csak egy szót sem."));
husuperobject.biblemap.put("b11_18_22", new husuperobject(11,18,22,"Akkor monda Illés a népnek: Én maradtam meg csak egyedül az Úr prófétái közül; míg a Baál prófétái négyszázötvenen vannak;"));
husuperobject.biblemap.put("b11_18_23", new husuperobject(11,18,23,"Adjatok azért nékünk két tulkot, és [õk] válaszszák magoknak az egyik tulkot, a melyet vagdaljanak darabokra, és rakják a fákra; de tüzet ne tegyenek alája; én pedig a másikat készítem el, a melyet a fákra rakok, de tüzet én sem teszek alája."));
husuperobject.biblemap.put("b11_18_24", new husuperobject(11,18,24,"Akkor hívjátok segítségül a ti istenteknek nevét, és én is segítségül hívom az Úrnak nevét; és a mely isten tûz által felel, az az Isten. És felelvén az egész sokaság, monda: Jó lesz!"));
husuperobject.biblemap.put("b11_18_25", new husuperobject(11,18,25,"És monda Illés a Baál prófétáinak: Válaszszátok el magatoknak az egyik tulkot, és készítsétek el ti elõször; mert ti többen [vagytok,] és hívjátok segítségül a ti istenteknek nevét, de tüzet ne tegyetek alája."));
husuperobject.biblemap.put("b11_18_26", new husuperobject(11,18,26,"És vevék a tulkot, a melyet nékik adott, és azt elkészíték, és segítségül hívák a Baálnak nevét reggeltõl fogva délig, mondván: Baál! hallgass meg minket! De nem jött szó, sem felelet. És ott sántikáltak az oltár körül, a melyet készítettek."));
husuperobject.biblemap.put("b11_18_27", new husuperobject(11,18,27,"Mikor pedig már dél lett, elkezdte õket gúnyolni Illés, azt mondván: Kiáltsatok hangosabban, hiszen isten! Talán elmélkedik, vagy félrement, vagy úton van, vagy talán aluszik, és felserken."));
husuperobject.biblemap.put("b11_18_28", new husuperobject(11,18,28,"És [elkezdtek] hangosan kiabálni és az õ szokásuk szerint késekkel és borotvákkal metélték magokat, míg csak ki nem csordult a vérök."));
husuperobject.biblemap.put("b11_18_29", new husuperobject(11,18,29,"Mikor pedig a dél elmúlt, prófétálni [kezdtek] egész az esteli áldozatig; de akkor sem lett se szó, se felelet, se meghallgattatás."));
husuperobject.biblemap.put("b11_18_30", new husuperobject(11,18,30,"Akkor monda Illés az egész sokaságnak: Jõjjetek én hozzám. És hozzá méne az egész sokaság, és megépíté az Úr oltárát, a mely leromboltatott volt."));
husuperobject.biblemap.put("b11_18_31", new husuperobject(11,18,31,"És võn Illés tizenkét követ, a Jákób fiai nemzetségeinek száma szerint, a kiknek az Isten azt mondotta volt: Izráel legyen a te neved;"));
husuperobject.biblemap.put("b11_18_32", new husuperobject(11,18,32,"És oltárt építe a kövekbõl az Úr nevében, és egy árkot húzott az oltár körül, a melybe két véka vetni való mag férne."));
husuperobject.biblemap.put("b11_18_33", new husuperobject(11,18,33,"És oda készíté a fát, és felvagdalá a tulkot, és felraká azt a fára;"));
husuperobject.biblemap.put("b11_18_34", new husuperobject(11,18,34,"És monda: Töltsetek meg négy vedret vízzel, és öntsétek az égõáldozatra és a fára. Monda ismét: Tegyétek ezt még egyszer! És másodszor is azt tevék. Monda még is: Harmadszor is tegyétek meg! És harmadszor is azt mívelék;"));
husuperobject.biblemap.put("b11_18_35", new husuperobject(11,18,35,"Úgy, hogy a víz lecsurgott az oltárról, és még az árok is tele lett vízzel."));
husuperobject.biblemap.put("b11_18_36", new husuperobject(11,18,36,"És a mikor eljött az esteli áldozás ideje, oda lépett Illés próféta, és monda: Óh Uram, Ábrahámnak, Izsáknak és Izráelnek Istene, hadd ismerjék meg e mai napon, hogy te vagy az Isten az Izráelben, és hogy én a te szolgád vagyok, és hogy mindezeket a te parancsolatodból cselekedtem."));
husuperobject.biblemap.put("b11_18_37", new husuperobject(11,18,37,"Hallgass meg engem, Uram, hallgass meg engem, hogy tudja meg e nép, hogy te, az Úr vagy az Isten, és te fordítod vissza az õ szívöket!"));
husuperobject.biblemap.put("b11_18_38", new husuperobject(11,18,38,"Akkor alászálla az Úr tüze, és megemészté az égõáldozatot, a fát, a köveket és a port, és felnyalta a vizet, a mely az árokban volt."));
husuperobject.biblemap.put("b11_18_39", new husuperobject(11,18,39,"Mikor ezt látta az egész sokaság, arczra borult, és monda: Az Úr az Isten! az Úr az Isten!"));
husuperobject.biblemap.put("b11_18_40", new husuperobject(11,18,40,"És monda Illés nékik: Fogjátok meg a Baál prófétáit; senki el ne szaladjon közülök! És megfogák õket, és alávivé õket Illés a Kison patakja mellé, és megölé ott õket."));
husuperobject.biblemap.put("b11_18_41", new husuperobject(11,18,41,"Akkor monda Illés Akhábnak: Eredj fel, egyél és igyál, mert nagy esõnek zúgása [hallszik.]"));
husuperobject.biblemap.put("b11_18_42", new husuperobject(11,18,42,"És felment Akháb, hogy egyék és igyék. Illés pedig felment a Kármel hegy tetejére, és leborula a földre, és az õ orczáját az õ két térde közé tevé;"));
husuperobject.biblemap.put("b11_18_43", new husuperobject(11,18,43,"És monda az õ szolgájának: Menj fel, és nézz a tenger felé. És felment, és [arrafelé] nézett, és monda: Nincsen semmi. És monda [Illés:] Menj vissza hétszer."));
husuperobject.biblemap.put("b11_18_44", new husuperobject(11,18,44,"És lõn hetedúttal, monda [a szolga:] Ímé egy kis felhõcske, mint egy embernek a tenyere, jõ fel a tengerbõl. Akkor monda: Menj fel, mondd meg Akhábnak: Fogj be és menj le, hogy meg ne késleljen az esõ."));
husuperobject.biblemap.put("b11_18_45", new husuperobject(11,18,45,"És lõn azonközben, hogy besötétedett az ég a fellegektõl és a széltõl, és nagy esõ lett. Akháb pedig szekérre ült és elment Jezréelbe."));
husuperobject.biblemap.put("b11_18_46", new husuperobject(11,18,46,"És lõn az Úr keze Illésen, és felövezvén az õ derekát, [még] Akháb elõtt futott el Jezréel felé."));
husuperobject.biblemap.put("b11_19_1", new husuperobject(11,19,1,"És Akháb elbeszélé Jézabelnek mindazokat, a melyeket Illés cselekedett és a többek között, hogy hogyan ölte meg mind a prófétákat fegyverrel."));
husuperobject.biblemap.put("b11_19_2", new husuperobject(11,19,2,"És követet külde Jézabel Illéshez, mondván: Ezt cselekedjék velem az istenek és úgy segéljenek, ha holnap ilyenkor úgy nem cselekszem a te életeddel, mint a hogy [te cselekedtél] azoknak életekkel mind egyig."));
husuperobject.biblemap.put("b11_19_3", new husuperobject(11,19,3,"A mit mikor megértett, felkelvén elméne, [vigyázván] az õ életére. És méne Beersebába, a mely Júdában volt; és ott hagyá az õ szolgáját."));
husuperobject.biblemap.put("b11_19_4", new husuperobject(11,19,4,"Õ pedig elméne a pusztába egynapi járó földre, és elmenvén leüle egy fenyõfa alá, és könyörgött, hogy [hadd] haljon meg, és monda: Elég! Most óh Uram, vedd el az én lelkemet; mert nem vagyok jobb az én atyáimnál!"));
husuperobject.biblemap.put("b11_19_5", new husuperobject(11,19,5,"És lefeküvék és elaluvék a fenyõfa alatt. És ímé angyal illeté õt, és monda néki: Kelj fel, egyél."));
husuperobject.biblemap.put("b11_19_6", new husuperobject(11,19,6,"És mikor körülnézett, ímé fejénél vala egy szén között [sült] pogácsa és egy pohár víz. És evék és ivék, és ismét lefeküvék."));
husuperobject.biblemap.put("b11_19_7", new husuperobject(11,19,7,"És az Úr angyala eljött másodszor is és megilleté õt, és monda: Kelj fel, egyél; mert erõd felett való utad van."));
husuperobject.biblemap.put("b11_19_8", new husuperobject(11,19,8,"És õ felkelt, és evett és ivott; és méne annak az ételnek erejével negyven nap és negyven éjjel egész az Isten hegyéig, Hórebig."));
husuperobject.biblemap.put("b11_19_9", new husuperobject(11,19,9,"És beméne ott egy barlangba, és ott hála. És ímé lõn az Úrnak beszéde õ hozzá, és monda néki: Mit csinálsz itt Illés?"));
husuperobject.biblemap.put("b11_19_10", new husuperobject(11,19,10,"Õ pedig monda: Nagy búsulásom van az Úrért, a Seregek Istenéért; mert elhagyták a te szövetségedet az Izráel fiai, a te oltáraidat lerontották, és a te prófétáidat fegyverrel megölték, és csak én egyedül maradtam, és engem is halálra keresnek."));
husuperobject.biblemap.put("b11_19_11", new husuperobject(11,19,11,"És monda: Jõjj ki és állj meg [ezen] a hegyen, az Úr elõtt. És ímé ott az Úr volt elmenendõ. És az Úr elõtt megyen vala nagy erõs szél, a mely a hegyeket megszaggatta és meghasogatta a kõsziklákat az Úr elõtt; [de] az Úr nem [vala] abban a szélben. És a szél után földindulás lett; de az Úr nem [volt] a földindulásban sem."));
husuperobject.biblemap.put("b11_19_12", new husuperobject(11,19,12,"És a földindulás után tûz jöve, de nem [volt] az Úr a tûzben sem. És a tûz után [egy] halk és szelíd hang [hallatszék.]"));
husuperobject.biblemap.put("b11_19_13", new husuperobject(11,19,13,"És mikor Illés ezt hallotta, befedé az õ arczát palástjával, és kimenvén, megálla a barlang ajtajában, és ímé szózat [lõn] õ hozzá, a mely ezt mondá: Mit csinálsz itt Illés?"));
husuperobject.biblemap.put("b11_19_14", new husuperobject(11,19,14,"És õ felele: Nagy búsulásom van az Úrért, a Seregeknek Istenéért, mert az Izráel fiai elhagyták a te szövetségedet, lerontották a te oltáraidat, és a te prófétáidat megölték fegyverrel, és én egyedül maradtam, és engem is halálra keresnek."));
husuperobject.biblemap.put("b11_19_15", new husuperobject(11,19,15,"És monda az Úr néki: Menj el, térj vissza a te utadon a pusztán át Damaskusba, és mikor oda jutándasz, kenjed királylyá Hazáelt Siriában;"));
husuperobject.biblemap.put("b11_19_16", new husuperobject(11,19,16,"És Jéhut, a Nimsi fiát kenjed királylyá Izráelben, és Elizeust, az Abelméholabeli Sáfát fiát pedig kenjed prófétává a te helyedbe."));
husuperobject.biblemap.put("b11_19_17", new husuperobject(11,19,17,"És lészen, hogy a ki megmenekedik Hazáel fegyverétõl, azt Jéhu öli meg, és a ki megmenekedik a Jéhu fegyverétõl, azt Elizeus öli meg."));
husuperobject.biblemap.put("b11_19_18", new husuperobject(11,19,18,"De meghagyok Izráelben hétezer embert: minden térdet, mely meg nem hajolt a Baálnak, és minden ajkat, mely meg nem csókolta azt."));
husuperobject.biblemap.put("b11_19_19", new husuperobject(11,19,19,"És õ elmenvén onnét, megtalálá Elizeust, a Sáfát fiát, a mint szántott tizenkét járom ökörrel, és õ maga a tizenkettedikkel volt; és Illés hozzá méne, és az õ palástját reá veté."));
husuperobject.biblemap.put("b11_19_20", new husuperobject(11,19,20,"És õ elhagyván az ökröket, Illés után futott, és monda: Kérlek hadd csókoljam meg az én atyámat és az én anyámat, és azután követlek. És monda: Menj, térj vissza; mert mit cselekedtem tenéked?"));
husuperobject.biblemap.put("b11_19_21", new husuperobject(11,19,21,"És elmenvén õ tõle, võn azután egy pár ökröt, és levágá azt, és az ekéhez való szerszámokból [tüzet rakván,] megfõzé azok húsát, és a népnek adá, és evének; és felkelvén, elméne Illés után, és szolgála néki."));
husuperobject.biblemap.put("b11_20_1", new husuperobject(11,20,1,"És Benhadád, Siria királya összegyûjté egész seregét, és harminczkét király volt õ vele és [nagyon sok] ló és szekér, és felméne és megszállá Samariát, és ostromolni [kezdte] azt."));
husuperobject.biblemap.put("b11_20_2", new husuperobject(11,20,2,"És követeket küldött Akhábhoz, az Izráel királyához a városba;"));
husuperobject.biblemap.put("b11_20_3", new husuperobject(11,20,3,"És azt izené néki: Azt mondja Benhadád: A te ezüstöd és aranyad az enyém, a te feleségeid is és a te szép fiaid is az enyémek."));
husuperobject.biblemap.put("b11_20_4", new husuperobject(11,20,4,"És felele az Izráel királya, és monda: A mint megmondottad uram, király, tiéd vagyok mindenekkel, a melyeket bírok."));
husuperobject.biblemap.put("b11_20_5", new husuperobject(11,20,5,"Megtérvén pedig a követek, mondának: Azt mondja Benhadád: Miután hozzád küldöttem, és azt izentem, hogy a te ezüstödet és aranyadat és a te feleségeidet és fiaidat add nékem;"));
husuperobject.biblemap.put("b11_20_6", new husuperobject(11,20,6,"Azért holnap ilyenkor elküldöm az én szolgáimat hozzád, és felkutatják a te házadat, és a te szolgáid házait; és kezekhez veszik, a mi csak kedves elõtted, és elhozzák."));
husuperobject.biblemap.put("b11_20_7", new husuperobject(11,20,7,"Akkor egybehívá az Izráel királya mind az ország véneit, és monda: Vegyétek eszetekbe, és lássátok meg, minemû gonosz szándékkal van ez; mert hozzám küldött az én feleségeimért és gyermekeimért, ezüstömért és aranyomért, és meg nem tagadtam tõle."));
husuperobject.biblemap.put("b11_20_8", new husuperobject(11,20,8,"És mondának néki a vének mindnyájan, és az egész nép: Ne engedj néki, és az õ akaratját be ne teljesítsd."));
husuperobject.biblemap.put("b11_20_9", new husuperobject(11,20,9,"És õ monda a Benhadád követeinek: Mondjátok meg az én uramnak, a királynak: Mindazokat, a melyek felõl elõször izentél a te szolgádnak, megcselekszem, de ezt a dolgot nem tehetem meg. Így elmenvén a követek, megmondák néki a választ."));
husuperobject.biblemap.put("b11_20_10", new husuperobject(11,20,10,"Akkor hozzá külde Benhadád, és monda: Úgy cselekedjenek velem az istenek és úgy segéljenek, hogy Samariának minden pora sem elég, hogy a velem való nép közül mindeniknek csak egy-egy marokkal is jusson!"));
husuperobject.biblemap.put("b11_20_11", new husuperobject(11,20,11,"És felele az Izráel királya, mondván: Mondjátok meg [néki]: Ne kérkedjék úgy, a ki fegyverbe öltözik, mint a ki már leveti a fegyvert!"));
husuperobject.biblemap.put("b11_20_12", new husuperobject(11,20,12,"Meghallván pedig ezt a választ, mikor õ ivott a királyokkal a sátorokban, monda az õ szolgáinak: Vegyétek körül a várost! És azok körülvevék azt."));
husuperobject.biblemap.put("b11_20_13", new husuperobject(11,20,13,"És ímé egy próféta méne Akhábhoz, az Izráel királyához, a ki ezt mondá: Azt mondja az Úr: Avagy nem láttad-é mindezt a nagy sokaságot: ímé e mai napon kezedbe adom azt, hogy megtudjad, hogy én vagyok az Úr!"));
husuperobject.biblemap.put("b11_20_14", new husuperobject(11,20,14,"Monda pedig Akháb: Ki által? És felele: Azt mondja az Úr: A tartományok fejedelmeinek ifjai által. Akkor monda [Akháb:] Ki kezdje meg a harczot? És felele: Te!"));
husuperobject.biblemap.put("b11_20_15", new husuperobject(11,20,15,"Megszámlálá azért a tartományok fejedelmeinek ifjait, a kik kétszázharminczketten voltak; ezekután megszámlálá mind az Izráel fiainak is minden népét, hétezer embert."));
husuperobject.biblemap.put("b11_20_16", new husuperobject(11,20,16,"És elindulának délben. Benhadád pedig ott ivott a királyokkal a sátorokban, és lerészegedett õ és a harminczkét király, a ki segítségére jött vele."));
husuperobject.biblemap.put("b11_20_17", new husuperobject(11,20,17,"És a tartományok fejedelmeinek ifjai vonultak ki legelõször. Benhadád pedig elkülde, és megmondották néki, ezt mondván: Valami férfiak jöttek ki Samariából!"));
husuperobject.biblemap.put("b11_20_18", new husuperobject(11,20,18,"És monda: Akár békességért jöttek ki, fogjátok meg õket elevenen; akár viadalért jöttek ki, fogjátok el õket elevenen."));
husuperobject.biblemap.put("b11_20_19", new husuperobject(11,20,19,"De mikor kivonultak a városból a tartományok fejedelmeinek ifjai és a sereg, a mely õket követte:"));
husuperobject.biblemap.put("b11_20_20", new husuperobject(11,20,20,"Mindenik vágni kezdte azt, a ki eléje került, és elfutottak a Siriabeliek, az Izráel pedig kergette õket, és megfutott maga Benhadád, Siria királya [is] lovon a lovagokkal együtt."));
husuperobject.biblemap.put("b11_20_21", new husuperobject(11,20,21,"És azután kivonult az Izráel királya, és megveré mind a lovagokat, mind a szekereket, és megveré a Siriabelieket nagy csapással."));
husuperobject.biblemap.put("b11_20_22", new husuperobject(11,20,22,"És méne az Izráel királyához [egy] próféta, és ezt mondá néki: Menj el, erõsítsd meg magad, és vedd eszedbe és lásd meg, mit kelljen cselekedned, mert esztendõ mulva [ismét] feljõ Siria királya ellened."));
husuperobject.biblemap.put("b11_20_23", new husuperobject(11,20,23,"A siriabeli király szolgái pedig mondának néki: A hegyeknek istenei az õ isteneik, azért gyõztek le bennünket; de vívjunk csak meg velök a síkon, és meglátod, ha le nem gyõzzük-é õket?"));
husuperobject.biblemap.put("b11_20_24", new husuperobject(11,20,24,"És tedd ezt: Küldd el a királyokat, mindeniket a maga helyérõl; és állíts hadnagyokat helyettök,"));
husuperobject.biblemap.put("b11_20_25", new husuperobject(11,20,25,"És szervezz magadnak olyan sereget, mint az volt, a melyet elvesztettél, és olyan lovakat és szekereket, mint amazok voltak; és vívjunk meg velök a síkon, és meglátod, ha le nem gyõzzük-é õket? És engede az õ szavoknak, és akképen cselekedék."));
husuperobject.biblemap.put("b11_20_26", new husuperobject(11,20,26,"Mikor azért az esztendõ elmult, rendbeszedte Benhadád a Siriabelieket, és feljöve Afekbe, hogy hadakozzék az Izráel ellen."));
husuperobject.biblemap.put("b11_20_27", new husuperobject(11,20,27,"De az Izráel fiai összeszámláltattak és elláttattak élelemmel, és eleikbe menének azoknak. Táborba szállván pedig az Izráel fiai, amazokhoz képest olyanok [voltak,] mint két kicsiny kecskenyájacska; míg a Siriabeliek ellepték a földet."));
husuperobject.biblemap.put("b11_20_28", new husuperobject(11,20,28,"Jött vala pedig egy Isten embere, és szóla az Izráel királyának, mondván: Azt mondja az Úr: Azért mert a Siriabeliek azt mondották, hogy csak a hegyeknek Istene az Úr, és nem a völgyeknek Istene is: mindezt a nagy sokaságot a te kezedbe adom, hogy megismerjétek, hogy én vagyok az Úr."));
husuperobject.biblemap.put("b11_20_29", new husuperobject(11,20,29,"És ott táboroztak egészen velök szemben hetednapig. A hetedik napon azután megütköztek, és az Izráel fiai levágtak a Siriabeliek közül egy nap százezer gyalogost."));
husuperobject.biblemap.put("b11_20_30", new husuperobject(11,20,30,"És a többiek elmenekültek Afek városába; de a falak rászakadtak a megmaradott huszonkétezer emberre, és Benhadád is elfutott és ott bolyongott a városban kamaráról-kamarára."));
husuperobject.biblemap.put("b11_20_31", new husuperobject(11,20,31,"És mondának néki az õ szolgái: Ímé hallottuk, hogy az Izráel házának királyai kegyelmes királyok, azért hadd öltözzünk zsákokba, és vessünk köteleket a mi nyakunkba, és menjünk ki az Izráel királyához, talán életben hagyja a te lelkedet."));
husuperobject.biblemap.put("b11_20_32", new husuperobject(11,20,32,"És zsákokba öltözének, és köteleket vetének nyakokba, és elmenének az Izráel királyához, és mondának: A te szolgád, Benhadád, ezt mondja: Hagyd életben kérlek, az én lelkemet! És monda: Él-e még? én atyámfia õ!"));
husuperobject.biblemap.put("b11_20_33", new husuperobject(11,20,33,"És a férfiak jó jelnek vették azt, és gyorsan megragadták a szót, és mondának: A te atyádfia Benhadád [él]. És monda: Menjetek, és hozzátok ide õt. Kijöve azért õ hozzá Benhadád, és felülteté õt az õ szekerébe,"));
husuperobject.biblemap.put("b11_20_34", new husuperobject(11,20,34,"És monda néki: A városokat, a melyeket elvett volt az én atyám a te atyádtól, azokat visszaadom, és csinálj magadnak utczákat Damaskusban, mint az én atyám csinált volt Samariában; én ezzel a kötéssel bocsátlak el téged. És szövetséget kötött vele, és elbocsátá õt."));
husuperobject.biblemap.put("b11_20_35", new husuperobject(11,20,35,"Egy férfiú pedig a próféták fiai közül monda az õ felebarátjának az Úr beszéde szerint: Verj meg engem kérlek; de ez nem akará õt megverni."));
husuperobject.biblemap.put("b11_20_36", new husuperobject(11,20,36,"Akkor monda néki: Azért, mert nem engedtél az Úr szavának: ímé mihelyt én tõlem elmégy, megöl téged az oroszlán. És a mikor elment õ tõle, találá õt egy oroszlán, és megölé."));
husuperobject.biblemap.put("b11_20_37", new husuperobject(11,20,37,"Talála azután más férfiat, a kinek monda: Kérlek, verj meg engem. És megveré az annyira, hogy megsebesítette."));
husuperobject.biblemap.put("b11_20_38", new husuperobject(11,20,38,"És elméne a próféta, és az útfélen a király elé álla és elváltoztatá magát szemeit bekötözvén."));
husuperobject.biblemap.put("b11_20_39", new husuperobject(11,20,39,"Mikor pedig arra ment el a király, kiálta a királyhoz, és monda: A te szolgád kiment volt a hadba, és ímé egy férfiú eljövén, hoza én hozzám egy férfiút, és monda: Õrizd meg ezt a férfiút; ha elszaladánd, meg kell halnod érette, vagy egy tálentom ezüstöt fizetsz."));
husuperobject.biblemap.put("b11_20_40", new husuperobject(11,20,40,"És mialatt a te szolgádnak itt és amott dolga volt, az [az ember] már nem volt. És monda néki az Izráel királya: Az a te ítéleted; magad akartad."));
husuperobject.biblemap.put("b11_20_41", new husuperobject(11,20,41,"És mindjárt elvevé a kötést az õ szemérõl, és megismeré õt az Izráel királya, hogy a próféták közül való."));
husuperobject.biblemap.put("b11_20_42", new husuperobject(11,20,42,"És monda néki: Ezt mondja az Úr: Mert elbocsátottad kezedbõl a férfiút, a kit én halálra szántam, azért lelked lészen lelkéért, és néped népéért."));
husuperobject.biblemap.put("b11_20_43", new husuperobject(11,20,43,"És házához méne az Izráel királya szomorú és megbúsult szívvel, és méne Samariába."));
husuperobject.biblemap.put("b11_21_1", new husuperobject(11,21,1,"És történt ezek után, hogy a Jezréelbeli Nábótnak egy szõlõje volt Jezréelben, Akhábnak, Samaria királyának a háza mellett."));
husuperobject.biblemap.put("b11_21_2", new husuperobject(11,21,2,"És szóla Akháb Nábótnak, mondván: Add nékem a te szõlõdet, hogy legyen veteményes kertem; mert közel van az én házamhoz, és néked érette jobb szõlõt adok annál, vagy ha néked tetszik, pénzül adom meg az árát."));
husuperobject.biblemap.put("b11_21_3", new husuperobject(11,21,3,"És felele Nábót Akhábnak: Isten õrizzen, hogy néked adjam az én atyáimtól maradt örökséget."));
husuperobject.biblemap.put("b11_21_4", new husuperobject(11,21,4,"Akkor haza méne Akháb nagy bánattal és haraggal a beszéd miatt, a melyet szólott néki a Jezréelbeli Nábót, mondván: Nem adom néked az én atyáimtól maradt örökséget; és lefeküvék az õ ágyára, és arczát [a fal felé] fordítá, és nem evék kenyeret."));
husuperobject.biblemap.put("b11_21_5", new husuperobject(11,21,5,"Hozzá menvén az õ felesége Jézabel, monda néki: Miért háborodott meg a te szíved, és kenyeret nem eszel?"));
husuperobject.biblemap.put("b11_21_6", new husuperobject(11,21,6,"És õ monda néki: Mert ama Jezréelbeli Nábóttal beszéltem, és azt mondám néki: Add nékem a te szõlõdet pénzért, vagy ha inkább tetszik, más szõlõt adok néked érette, õ [pedig] azt mondta: Nem adom néked az én szõlõmet."));
husuperobject.biblemap.put("b11_21_7", new husuperobject(11,21,7,"Akkor monda néki Jézabel, az õ felesége: Te bírod-é most az Izráel királyságát? Kelj fel, egyél kenyeret, és a te szíved örvendezzen, én majd néked adom a Jezréelbeli Nábót szõlõjét."));
husuperobject.biblemap.put("b11_21_8", new husuperobject(11,21,8,"És levelet írt Akháb nevével, a melyet megpecsételt az õ gyûrûjével, és elküldé azt a levelet a véneknek és a fõembereknek, a kik Nábóttal egy városban laktak;"));
husuperobject.biblemap.put("b11_21_9", new husuperobject(11,21,9,"És a levélben ezt írta, mondván: Hirdessetek bõjtöt, és ültessétek Nábótot a nép élére;"));
husuperobject.biblemap.put("b11_21_10", new husuperobject(11,21,10,"És ültessetek vele szembe két istentelen embert, a kik tanúbizonyságot tegyenek õ ellene, mondván: Megszidalmaztad az Istent és a királyt. Azután vigyétek ki, és kövezzétek meg õt, hogy meghaljon."));
husuperobject.biblemap.put("b11_21_11", new husuperobject(11,21,11,"És ekképen cselekedének a vének és a fõemberek, a kik az õ városában laktak, a mint Jézabel nékik megparancsolta, és a mint a levélben megírta, a melyet nékik küldött;"));
husuperobject.biblemap.put("b11_21_12", new husuperobject(11,21,12,"Bõjtöt hirdetének, és ülteték Nábótot a nép élére."));
husuperobject.biblemap.put("b11_21_13", new husuperobject(11,21,13,"Elõjöve azután két istentelen ember, és leült vele szemben, és tanúbizonyságot tettek ez istentelen emberek Nábót ellen a nép elõtt, mondván: Megszidalmazta Nábót az Istent és a királyt. Kivivék azért õt a városból, és megkövezék, és meghala."));
husuperobject.biblemap.put("b11_21_14", new husuperobject(11,21,14,"Azután megizenék Jézabelnek, mondván: Megköveztetett Nábót, és meghalt."));
husuperobject.biblemap.put("b11_21_15", new husuperobject(11,21,15,"Mikor pedig meghallotta Jézabel, hogy megköveztetett Nábót és meghalt, monda Jézabel Akhábnak: Kelj fel és foglald el a Jezréelbeli Nábót szõlõjét, a melyet nem akart néked pénzért oda adni; mert nem él Nábót, hanem meghalt."));
husuperobject.biblemap.put("b11_21_16", new husuperobject(11,21,16,"És mikor meghallotta Akháb, hogy Nábót meghalt, felkelt Akháb, hogy lemenjen a Jezréelbeli Nábót szõlõjébe, és azt elfoglalja."));
husuperobject.biblemap.put("b11_21_17", new husuperobject(11,21,17,"Akkor szóla az Úr Thesbites Illésnek, mondván:"));
husuperobject.biblemap.put("b11_21_18", new husuperobject(11,21,18,"Kelj fel, és menj Akhábnak, az Izráelbeli királynak eleibe, a ki Samariában lakik, ímé ott van a Nábót szõlõjében, a melybe lement, hogy azt elfoglalja;"));
husuperobject.biblemap.put("b11_21_19", new husuperobject(11,21,19,"És szólj néki, ezt mondván: Így szól az Úr: Nemde megölted-é és nemde el is foglaltad-é? És szólj néki, mondván: Ezt mondja az Úr: Ugyanazon a helyen, a hol felnyalták az ebek Nábót vérét, ebek nyalják fel a te véredet is!"));
husuperobject.biblemap.put("b11_21_20", new husuperobject(11,21,20,"És monda Akháb Illésnek: [Megint] rám találtál ellenségem? És õ monda: Rád találtam; mert te magadat mindenestõl arra adtad, hogy gonoszságot cselekedjél az Úr szemei elõtt."));
husuperobject.biblemap.put("b11_21_21", new husuperobject(11,21,21,"Ímé, [azt mondja az Úr:] Veszedelmet hozok reád, és elvesztem a te maradékaidat, és kigyomlálom Akhábnak még az ebét is, és a berekesztettet és az elhagyatottat Izráelben;"));
husuperobject.biblemap.put("b11_21_22", new husuperobject(11,21,22,"És olyanná teszem a te házadat, mint Jeroboámnak, a Nébát fiának házát, és mint Baásának, az Ahija fiának házát azért, a miért haragra ingerlettél engem, és a miért bûnbe ejtetted az Izráelt."));
husuperobject.biblemap.put("b11_21_23", new husuperobject(11,21,23,"És Jézabel felõl is szóla az Úr, mondván: Az ebek eszik meg Jézabelt Jezréel kõfala elõtt;"));
husuperobject.biblemap.put("b11_21_24", new husuperobject(11,21,24,"Azt, a ki Akháb házából a városban hal meg, az ebek eszik meg; azt pedig, a ki a mezõben hal meg, az égi madarak eszik meg."));
husuperobject.biblemap.put("b11_21_25", new husuperobject(11,21,25,"[Mert] bizonyára nem volt Akhábnak mássa, a ki magát arra adta volna, hogy csak gonoszságot cselekedjék az Úr szemei elõtt, a melyre az õ felesége, Jézabel ösztökélé õt."));
husuperobject.biblemap.put("b11_21_26", new husuperobject(11,21,26,"Mert igen útálatos dolgot cselekedék, követvén a bálványokat mind a szerint, a mint cselekedének az Emoreusok, a kiket az Úr kiûzött volt az Izráel fiai elõtt."));
husuperobject.biblemap.put("b11_21_27", new husuperobject(11,21,27,"Lõn pedig, mikor meghallotta Akháb e beszédeket, megszaggatá az õ ruháit, és zsákba öltözék és bõjtöle, és a zsákban hála, és nagy alázatossággal jár vala."));
husuperobject.biblemap.put("b11_21_28", new husuperobject(11,21,28,"És szóla az Úr Thesbites Illésnek, mondván:"));
husuperobject.biblemap.put("b11_21_29", new husuperobject(11,21,29,"Nem láttad-é, hogy Akháb hogyan megalázta magát elõttem? Mivelhogy pedig megalázta magát elõttem, nem hozom reá a veszedelmet az õ életében, [hanem] [csak] az õ fia idejében hozom el a veszedelmet az õ házára."));
husuperobject.biblemap.put("b11_22_1", new husuperobject(11,22,1,"És három esztendõ lefolyt úgy, hogy nem volt hadakozás a Siriabeliek és az Izráel között."));
husuperobject.biblemap.put("b11_22_2", new husuperobject(11,22,2,"De a harmadik esztendõben aláméne Josafát, Júda királya az Izráel királyához,"));
husuperobject.biblemap.put("b11_22_3", new husuperobject(11,22,3,"Akkor monda az Izráel királya az õ szolgáinak: Nem tudjátok-é, hogy Rámoth Gileád a miénk? És mi hallgatunk és nem veszszük vissza azt Siria királyától?"));
husuperobject.biblemap.put("b11_22_4", new husuperobject(11,22,4,"És monda Josafátnak: Feljössz-é velem e hadba Rámoth Gileád ellen? Felele Josafát az Izráel királyának: Úgy én, mint te, úgy az én népem, mint a te néped; úgy az én lovaim, mint a te lovaid."));
husuperobject.biblemap.put("b11_22_5", new husuperobject(11,22,5,"És monda Josafát az Izráel királyának: Kérdezõsködjél még ma az Úr beszéde után."));
husuperobject.biblemap.put("b11_22_6", new husuperobject(11,22,6,"És összegyûjté az Izráel királya a prófétákat, közel négyszáz férfiút, és monda nékik: Elmenjek-é Rámoth Gileád ellen hadba, vagy elhagyjam? És mondának: Menj fel; mert az Úr kezébe adja azt a királynak."));
husuperobject.biblemap.put("b11_22_7", new husuperobject(11,22,7,"És monda Josafát: Nincs itt már több prófétája az Úrnak, hogy attól [is] tudakozódhatnánk?"));
husuperobject.biblemap.put("b11_22_8", new husuperobject(11,22,8,"És monda az Izráel királya Josafátnak: Még van egy férfiú, a ki által megkérdhetjük az Urat, Mikeás, a Jemla fia; de én gyûlölöm õt, mert [soha] nem jövendöl nékem jót, hanem mindig csak rosszat. És monda Josafát: Ne beszéljen így a király!"));
husuperobject.biblemap.put("b11_22_9", new husuperobject(11,22,9,"És elõszólíta az Izráel királya egy udvariszolgát, és monda: Hívd ide hamar Mikeást, a Jemla fiát."));
husuperobject.biblemap.put("b11_22_10", new husuperobject(11,22,10,"És ott ült az Izráel királya és Josafát, Júda királya, ruhákba öltözötten, mindenik a maga trónusán Samaria kapuja elõtt a térségen, és a próféták mind ott jövendöltek elõttük."));
husuperobject.biblemap.put("b11_22_11", new husuperobject(11,22,11,"És Sédékiás, a Kénaána fia, vasszarvakat készített, és monda: Ezt mondja az Úr: Ezekkel ökleled a Siriabelieket, míg meg nem emészted õket."));
husuperobject.biblemap.put("b11_22_12", new husuperobject(11,22,12,"És a próféták is mindnyájan ekképen jövendöltek, mondván: Menj fel Rámoth Gileád ellen, és jó szerencséd lesz; mert azt az Úr a király kezébe adja."));
husuperobject.biblemap.put("b11_22_13", new husuperobject(11,22,13,"A követ pedig, a ki elment volt, hogy elhívja Mikeást, szóla néki, mondván: Ímé a próféták egyenlõ akarattal jót jövendölnek a királynak: szólj, kérlek, te is úgy, mint azok közül egy, és jövendölj jót."));
husuperobject.biblemap.put("b11_22_14", new husuperobject(11,22,14,"Mikeás pedig monda: Él az Úr, hogy [csak] azt fogom mondani, a mit az Úr mondánd nékem."));
husuperobject.biblemap.put("b11_22_15", new husuperobject(11,22,15,"És mikor a királyhoz ment, monda néki a király: Mikeás! elmenjünk-é Rámoth Gileád ellen hadba, vagy elhagyjuk? Õ pedig monda néki: Menj fel és járj szerencsével; az Úr kezébe adja [azt] a királynak."));
husuperobject.biblemap.put("b11_22_16", new husuperobject(11,22,16,"És monda néki a király: Még hányszor kényszerítselek téged, hogy az igaznál egyebet ne mondj nékem az Úr nevében?"));
husuperobject.biblemap.put("b11_22_17", new husuperobject(11,22,17,"És monda: Látám az egész Izráelt szétszéledve a hegyeken, mint a juhokat, a melyeknek nincsen pásztoruk. És monda az Úr: Nincsen ezeknek urok? Térjen vissza kiki az õ házához békességben."));
husuperobject.biblemap.put("b11_22_18", new husuperobject(11,22,18,"És monda az Izráel királya Josafátnak: Nemde nem megmondottam-é néked, hogy [soha] jót nékem nem jövendöl, hanem [csak ]rosszat."));
husuperobject.biblemap.put("b11_22_19", new husuperobject(11,22,19,"És monda [Mikeás:] Azért halld meg most az Úr beszédét: Látám az Urat az õ székiben ülni, és az egész mennyei sereget az õ jobb- és balkeze felõl mellette állani."));
husuperobject.biblemap.put("b11_22_20", new husuperobject(11,22,20,"És monda az Úr: Kicsoda csalja meg Akhábot, hogy felmenjen, és elvesszen Rámoth Gileádnál? És ki egyet, ki mást szól vala hozzá."));
husuperobject.biblemap.put("b11_22_21", new husuperobject(11,22,21,"Akkor elõjõve egy lélek, a ki az Úr eleibe álla, és monda: Én akarom megcsalni õt. Az Úr pedig monda néki: Miképen?"));
husuperobject.biblemap.put("b11_22_22", new husuperobject(11,22,22,"És felele: Kimegyek és hazug lélek leszek minden õ prófétáinak szájában. Akkor monda [az Úr:] Csald meg és gyõzd meg; menj ki, és cselekedjél úgy."));
husuperobject.biblemap.put("b11_22_23", new husuperobject(11,22,23,"Ímé az Úr a hazugságnak lelkét adta mindezeknek a te prófétáidnak szájába; és az Úr szólott veszedelmes dolgot ellened."));
husuperobject.biblemap.put("b11_22_24", new husuperobject(11,22,24,"Akkor odalépett Sédékiás, a Kénaána fia, és arczul csapván Mikeást, monda: Hogyan? Eltávozott volna én tõlem az Úrnak lelke, hogy csak néked szólana?"));
husuperobject.biblemap.put("b11_22_25", new husuperobject(11,22,25,"És monda Mikeás: Ímé meglátod azon a napon, a mikor az egyik kamarából a másik kamarába mégy be, hogy elrejtõzhess."));
husuperobject.biblemap.put("b11_22_26", new husuperobject(11,22,26,"Az Izráel királya pedig monda: Fogjad Mikeást, és vidd vissza õt Ammonhoz, a város fejedelméhez, és Joáshoz, a király fiához;"));
husuperobject.biblemap.put("b11_22_27", new husuperobject(11,22,27,"És mondjad: Ezt mondja a király: Vessétek ezt a tömlöczbe, és tápláljátok õt a nyomorúság kenyerével és a nyomorúság vizével, míg békességgel megjövök."));
husuperobject.biblemap.put("b11_22_28", new husuperobject(11,22,28,"Monda pedig Mikeás: Ha békességgel térsz vissza, nem az Úr szólott én általam. Azután monda: Halljátok ezt meg minden népek!"));
husuperobject.biblemap.put("b11_22_29", new husuperobject(11,22,29,"És felvonult az Izráel királya, és Josafát, a Júda királya Rámoth Gileád [ellen."));
husuperobject.biblemap.put("b11_22_30", new husuperobject(11,22,30,"És monda az Izráel királya Josafátnak: Megváltoztatom ruhámat, és úgy megyek a viadalra, te pedig öltözzél fel ruhádba. És elváltoztatá ruháját az Izráel királya, és úgy méne a viadalra."));
husuperobject.biblemap.put("b11_22_31", new husuperobject(11,22,31,"Siria királya pedig megparancsolá az õ szekerei harminczkét fejedelmeinek, mondván: Ne vívjatok se kicsinynyel, se nagygyal, hanem egyedül csak az Izráel királya ellen vívjatok."));
husuperobject.biblemap.put("b11_22_32", new husuperobject(11,22,32,"És a mikor meglátták a szekerek fejedelmei Josafátot, mondának: Nyilván ez az Izráel királya; és reá rohanván vívának ellene. De Josafát [elkezdett] kiáltani."));
husuperobject.biblemap.put("b11_22_33", new husuperobject(11,22,33,"Mikor pedig látták a szekerek fejedelmei, hogy nem az Izráel királya, ott hagyták."));
husuperobject.biblemap.put("b11_22_34", new husuperobject(11,22,34,"Egy ember pedig [csak úgy] találomra kilövé az õ kézívét, és találá az Izráel királyát a pánczél és a kapocs között. És õ monda a kocsisának: Fordulj meg és vigy ki engem a táborból, mert megsebesültem!"));
husuperobject.biblemap.put("b11_22_35", new husuperobject(11,22,35,"És az ütközet mind erõsebb lett azon a napon, és a király az õ szekerében állott a Siriabeliek ellen, és meghalt este [felé,] és a vér a sebbõl a szekérbe csorgott."));
husuperobject.biblemap.put("b11_22_36", new husuperobject(11,22,36,"És kikiálták napnyugotkor a táborban, mondván: Minden ember [menjen haza] a maga városába és földjébe!"));
husuperobject.biblemap.put("b11_22_37", new husuperobject(11,22,37,"És meghalt a király és visszavitetvén Samariába, eltemeték a királyt Samariában."));
husuperobject.biblemap.put("b11_22_38", new husuperobject(11,22,38,"És mikor mosták az õ szekerét a Samaria mellett lévõ tóban: az ebek nyalták az õ vérét, és paráznák fürödtek ott, az Úrnak beszéde szerint, a melyet szólott volt."));
husuperobject.biblemap.put("b11_22_39", new husuperobject(11,22,39,"Akhábnak egyéb dolgai pedig és minden cselekedetei, az elefántcsontból építtetett ház, és mind a városok, a melyeket épített, vajjon nincsenek- é megírva az Izráel királyainak krónika-könyvében?"));
husuperobject.biblemap.put("b11_22_40", new husuperobject(11,22,40,"És elaluvék Akháb az õ atyáival; és uralkodék õ utána fia, Akházia."));
husuperobject.biblemap.put("b11_22_41", new husuperobject(11,22,41,"És Josafát, az Asa fia lett királylyá Júdában, Akhábnak, az Izráel királyának negyedik esztendejében."));
husuperobject.biblemap.put("b11_22_42", new husuperobject(11,22,42,"És Josafát harminczöt esztendõs volt, mikor uralkodni kezdett, és huszonöt esztendeig uralkodott Jeruzsálemben. Az õ anyjának Azuba volt a neve, Silhi leánya."));
husuperobject.biblemap.put("b11_22_43", new husuperobject(11,22,43,"És jára Asának, az õ atyjának minden útjában, és abból ki nem tére, azt cselekedvén, a mi az Úr szemei elõtt kedves."));
husuperobject.biblemap.put("b11_22_44", new husuperobject(11,22,44,"Csakhogy a magaslatokat nem rombolták le, és a nép még áldozott és tömjénezett a magaslatokon."));
husuperobject.biblemap.put("b11_22_45", new husuperobject(11,22,45,"És békességben élt Josafát az Izráel királyával."));
husuperobject.biblemap.put("b11_22_46", new husuperobject(11,22,46,"Josafátnak egyéb dolgai pedig és az õ ereje, a melylyel cselekedett, és a melylyel hadakozott, vajjon nincsenek-é megírva a Júda királyainak krónika-könyvében?"));
husuperobject.biblemap.put("b11_22_47", new husuperobject(11,22,47,"A férfi paráznákat is, a kik még megmaradtak volt az õ atyjának, Asának idejébõl, kiûzte az országból."));
husuperobject.biblemap.put("b11_22_48", new husuperobject(11,22,48,"Akkor nem volt király Edomban, hanem csak helyettes király."));
husuperobject.biblemap.put("b11_22_49", new husuperobject(11,22,49,"És Josafát Társis hajókat csináltatott, hogy aranyért mennének Ofirba, de nem mehettek el; mert a hajók összetörtek Esiongáberben."));
husuperobject.biblemap.put("b11_22_50", new husuperobject(11,22,50,"Akkor mondá Akházia, az Akháb fia, Josafátnak: Hadd menjenek el az én szolgáim a te szolgáiddal e hajókon; de Josafát nem akará."));
husuperobject.biblemap.put("b11_22_51", new husuperobject(11,22,51,"És elaluvék Josafát az õ atyáival; és eltemetteték az õ atyáival az õ atyjának, Dávidnak városában; és az õ fia, Jórám, uralkodék helyette."));
husuperobject.biblemap.put("b11_22_52", new husuperobject(11,22,52,"Akházia pedig, az Akháb fia kezde uralkodni Izráelen Samariában, Josafátnak, a Júda királyának tizenhetedik esztendejében, és uralkodék Izráelben két esztendeig."));
husuperobject.biblemap.put("b11_22_53", new husuperobject(11,22,53,"És gonoszul cselekedék az Úrnak szemei elõtt, járván az õ atyjának és anyjának útján, és Jeroboámnak, a Nébát fiának útján, a ki bûnbe ejté az Izráelt; [ (I Kings 22:54) És szolgála a Baálnak, és azt imádá és haragra indítá az Urat, Izráel Istenét, mint a hogy az õ atyja cselekedett. ]"));

}

}