husuperobject.biblemap.put("b13_1_24", new husuperobject(13,1,24,"Sém, Arpaksád, Séláh."));
husuperobject.biblemap.put("b13_1_25", new husuperobject(13,1,25,"Héber, Péleg, Réu."));
husuperobject.biblemap.put("b13_1_26", new husuperobject(13,1,26,"Sérug, Nákhor, Tháré."));
husuperobject.biblemap.put("b13_1_27", new husuperobject(13,1,27,"Abrám, ez az Ábrahám."));
husuperobject.biblemap.put("b13_1_28", new husuperobject(13,1,28,"Ábrahám fiai: Izsák és Ismáel."));
husuperobject.biblemap.put("b13_1_29", new husuperobject(13,1,29,"Ezeknek nemzetségei pedig ezek: Ismáel elsõszülötte Nebájót és Kédar, Adbeél és Mibszám."));
husuperobject.biblemap.put("b13_1_30", new husuperobject(13,1,30,"Misma és Dúmah, Massza, Hadad és Théma."));
