husuperobject.biblemap.put("b13_1_32", new husuperobject(13,1,32,"Keturának pedig, az Ábrahám ágyasának fiai, kiket szüle, ezek: Zimrán, Joksán, Médán, Midián, Isbák és Suakh. És a Joksán fiai: Séba és Dédán."));
husuperobject.biblemap.put("b13_1_33", new husuperobject(13,1,33,"És a Midián fiai: Éfah, Héfer, Hánok, Abida és Eldáh. Mindezek a Keturáh fiai."));
husuperobject.biblemap.put("b13_1_34", new husuperobject(13,1,34,"Ábrahám pedig nemzé Izsákot; Izsák fiai pedig ezek: Ézsau és Izráel."));
husuperobject.biblemap.put("b13_1_35", new husuperobject(13,1,35,"Ézsaunak fiai: Elifáz, Réhuél, Jéhus, Jahlám és Korakh."));
