husuperobject.biblemap.put("b13_1_40", new husuperobject(13,1,40,"Sóbál fiai: Alján és Mánakháth, Hébál, Sefi és Onám; Czibhón fiai pedig: Aja és Haná."));
husuperobject.biblemap.put("b13_1_41", new husuperobject(13,1,41,"Haná fia: Disón, Disón fiai pedig: Hamrán és Esbán, Ithrán és Kherán."));
husuperobject.biblemap.put("b13_1_42", new husuperobject(13,1,42,"Eczer fiai: Bilhán és Zahaván, Jakán. Disán fiai: Húcz és Arán."));
husuperobject.biblemap.put("b13_1_43", new husuperobject(13,1,43,"Ezek pedig a királyok, a kik uralkodának Edom földén, mielõtt az Izráel fiai között király uralkodott volna: Bela, Behor fia, az õ városának neve Dinhába vala."));
husuperobject.biblemap.put("b13_1_44", new husuperobject(13,1,44,"[Bela] meghalván, uralkodék helyette Jóbáb, a Boczrából való Zerakh fia."));
husuperobject.biblemap.put("b13_1_45", new husuperobject(13,1,45,"És hogy Jóbáb meghala, uralkodék helyette a Témán földébõl való Khusám."));
husuperobject.biblemap.put("b13_1_46", new husuperobject(13,1,46,"Meghala Khusám is, és uralkodék helyette Hadád, a Bédád fia, ki megveré a Midiánitákat a Moáb mezején; és az õ városának neve Hávit vala."));
husuperobject.biblemap.put("b13_1_47", new husuperobject(13,1,47,"Hadád is, hogy meghala, uralkodék helyette a Masrekából való Szamlá."));
husuperobject.biblemap.put("b13_1_48", new husuperobject(13,1,48,"Szamlá holta után uralkodék helyette Saul, a folyóvíz mellett való Rékhobóthból."));
husuperobject.biblemap.put("b13_1_49", new husuperobject(13,1,49,"Saul is meghala, és uralkodék helyette Báhál-Khanán, az Akhbór fia."));
husuperobject.biblemap.put("b13_1_50", new husuperobject(13,1,50,"Báhál-Khanán holta után uralkodék helyette Hadád; és az õ városának neve Páhi, feleségének pedig neve Mehetábéel, ki Mézaháb leányának Matrédnak volt a leánya."));
husuperobject.biblemap.put("b13_1_51", new husuperobject(13,1,51,"Hadád halála után Edom fejedelmei valának: Timná fejedelem, Halvá fejedelem, Jetéth fejedelem,"));
husuperobject.biblemap.put("b13_1_52", new husuperobject(13,1,52,"Ohólibámá fejedelem, Éla fejedelem és Pinon fejedelem,"));
husuperobject.biblemap.put("b13_1_53", new husuperobject(13,1,53,"Kenáz fejedelem, Témán fejedelem és Mibczár fejedelem,"));
