husuperobject.biblemap.put("b13_2_3", new husuperobject(13,2,3,"Júdának fiai: Hér, Ónán és Séla; e három születék néki a Kanaánita Súahnak leányától. De Hér, Júdának elsõszülött [fia] gonosz vala az Úr szemei elõtt, és megölé õt az [Úr]."));
husuperobject.biblemap.put("b13_2_4", new husuperobject(13,2,4,"Thámár pedig, az õ menye szülé néki Péreczet és Zerákhot. Júdának fiai mindnyájan öten [valának.]"));
husuperobject.biblemap.put("b13_2_5", new husuperobject(13,2,5,"Pérecz fiai: Kheczrón és Khámul."));
husuperobject.biblemap.put("b13_2_6", new husuperobject(13,2,6,"Zerákh fiai pedig: Zimri és Ethán, Hémán, Kálkól és Dára; mindenestõl öten."));
husuperobject.biblemap.put("b13_2_7", new husuperobject(13,2,7,"Kármi fiai: Ákán, Izráelnek megrontója, mivel lopott a zsákmányból."));
husuperobject.biblemap.put("b13_2_8", new husuperobject(13,2,8,"Ethán fia: Azária."));
husuperobject.biblemap.put("b13_2_9", new husuperobject(13,2,9,"Kheczrón fiai, kik születtenek néki: Jérakhméel, Rám és Kélubai."));
husuperobject.biblemap.put("b13_2_10", new husuperobject(13,2,10,"Rám nemzé Amminádábot, Amminádáb pedig nemzé Nakhsont, a Júda fiainak fejedelmét."));
husuperobject.biblemap.put("b13_2_11", new husuperobject(13,2,11,"Nakhson nemzé Szálmát, Szálma pedig nemzé Boázt;"));
husuperobject.biblemap.put("b13_2_12", new husuperobject(13,2,12,"Boáz nemzé Obedet; Obed nemzé Isait;"));
husuperobject.biblemap.put("b13_2_13", new husuperobject(13,2,13,"Isai pedig nemzé Eliábot, az õ elsõszülöttét, és Abinádábot, másodikat, Simeát, harmadikat."));
husuperobject.biblemap.put("b13_2_14", new husuperobject(13,2,14,"Netanéelt, negyediket, és Raddait, ötödiket."));
