husuperobject.biblemap.put("b13_2_28", new husuperobject(13,2,28,"Onám fiai voltak: Sammai és Jáda; és Sammai fiai: Nádáb és Abisúr."));
husuperobject.biblemap.put("b13_2_29", new husuperobject(13,2,29,"Abisúr feleségének neve Abihail, a ki szülé néki Akhbánt és Mólidot."));
husuperobject.biblemap.put("b13_2_30", new husuperobject(13,2,30,"Nádáb fiai: Széled és Appaim: Széled magtalanul halt meg."));
husuperobject.biblemap.put("b13_2_31", new husuperobject(13,2,31,"Appaim fia: Isi; Isi fia: Sésán; Sésán fia: Ahálai."));
