husuperobject.biblemap.put("b13_2_35", new husuperobject(13,2,35,"És adá Sésán e Járha nevû szolgájának az õ leányát feleségül, a ki szülé néki Athait."));
husuperobject.biblemap.put("b13_2_36", new husuperobject(13,2,36,"Athai pedig nemzé Nátánt; Nátán nemzé Zabádot;"));
