husuperobject.biblemap.put("b13_2_42", new husuperobject(13,2,42,"A Káleb fiai pedig, a ki Jérakhméel testvére vala: elsõszülötte Mésa; ez volt Zifnek és Marésa fiainak atyjok, Hebronnak atyja."));
husuperobject.biblemap.put("b13_2_43", new husuperobject(13,2,43,"Hebron fiai: Kórah, Tappuah, Rékem és Séma."));
