husuperobject.biblemap.put("b13_2_45", new husuperobject(13,2,45,"A Sammai fia pedig: Máon; ez a Máon [volt] a Bethsúr atyja."));
husuperobject.biblemap.put("b13_2_46", new husuperobject(13,2,46,"Efa pedig, a Káleb ágyastársa, szülé Háránt és Mósát és Gázezt; és Hárán nemzé Gázezt."));
husuperobject.biblemap.put("b13_2_47", new husuperobject(13,2,47,"Jaddai fiai pedig: Régem, Jotám, Gésán, Pelet, Héfa és Saáf."));
husuperobject.biblemap.put("b13_2_48", new husuperobject(13,2,48,"A Káleb ágyasa Maaka szülé Sébert és Tirhánát."));
husuperobject.biblemap.put("b13_2_49", new husuperobject(13,2,49,"És szülé Saáfot, Madmanna atyját, Sevát, a Makbéna atyját és Gibea atyját; és Aksza a Káleb leánya vala."));
husuperobject.biblemap.put("b13_2_50", new husuperobject(13,2,50,"Ezek voltak Káleb fiai, a ki Húrnak, az Efrata elsõszülöttének fia volt: Sobál, Kirját-Jeárim atyja."));
husuperobject.biblemap.put("b13_2_51", new husuperobject(13,2,51,"Szálma, Bethlehem atyja; Háref, Bethgáder atyja."));
husuperobject.biblemap.put("b13_2_52", new husuperobject(13,2,52,"Voltak pedig Sobálnak is, a Kirját-Jeárim atyjának fiai: Haroé, a fél Menuhót [ura]."));
husuperobject.biblemap.put("b13_2_53", new husuperobject(13,2,53,"A Kirját-Jeárim háznépei: Jitreusok, Puteusok, Sumateusok, Misraiteusok; ezektõl származának a Sorateusok és az Estaoliteusok."));
husuperobject.biblemap.put("b13_2_54", new husuperobject(13,2,54,"Szálma fiai, Bethlehem és a Netofátbeliek, Atróth, Beth-Joáb, a Czórabeli Manahateusok fele."));
husuperobject.biblemap.put("b13_2_55", new husuperobject(13,2,55,"És a Jábesben lakozó tudós emberek háznépei: a Tirateusok, Simateusok, Sukateusok. Ezek a Kineusok, a kik Hámáttól, a Rékáb házának atyjától származtak."));
husuperobject.biblemap.put("b13_3_1", new husuperobject(13,3,1,"Ezek a Dávid fiai, a kik Hebronban születének néki: elsõszülötte vala Amnon, a Jezréelbõl való Ahinoámtól; második vala Dániel, a Kármelbõl való Abigailtól."));
husuperobject.biblemap.put("b13_3_2", new husuperobject(13,3,2,"Harmadik Absolon, a ki Maakának, a Gessurbeli Talmai király leányának volt a fia; negyedik Adónia, Haggittól való."));
