package hu.bible;

public class hubible17 {

public hubible17() {

putmapbible17();

}

private void putmapbible17() {

husuperobject.biblemap.put("b17_1_1", new husuperobject(17,1,1,"Ahasvérus idejében történt (ez az Ahasvérus az, a ki uralkodott Indiától fogva Szerecsenországig százhuszonhét tartományon),"));
husuperobject.biblemap.put("b17_1_2", new husuperobject(17,1,2,"Azokban a napokban, a mikor Ahasvérus király üle királyi székében, a mely Susán várában volt,"));
husuperobject.biblemap.put("b17_1_3", new husuperobject(17,1,3,"Uralkodása harmadik évében lakomát szerze minden fejedelmének és szolgáinak: Persia és Média hatalmasai, fõemberei és a tartományok fejedelmei elõtte valának,"));
husuperobject.biblemap.put("b17_1_4", new husuperobject(17,1,4,"Midõn mutogatá országa dicsõségének gazdagságát és az õ méltóságának fényes díszét sok napon át, száznyolczvan napig."));
husuperobject.biblemap.put("b17_1_5", new husuperobject(17,1,5,"És mikor elmultak ezek a napok, tõn a király az egész népnek, mely Susán várában találtaték, kicsinytõl nagyig, hét napig tartó lakomát, a király palotája kertjének udvarán."));
husuperobject.biblemap.put("b17_1_6", new husuperobject(17,1,6,"Fehér gyapjú és kék bíbor szõnyegek valának megerõsítve a fehér gyapjúból és bíborból való köteleken, ezüst karikákon és fehér márványból való oszlopokon. Aranyból és ezüstbõl való kerevetek voltak az alabástromból, fehér márványból, gyöngykõbõl és vörös márványból való padozaton."));
husuperobject.biblemap.put("b17_1_7", new husuperobject(17,1,7,"Ittak pedig arany poharakból és különb-különbféle poharakból, és királyi bor bõven vala, királyi módon."));
husuperobject.biblemap.put("b17_1_8", new husuperobject(17,1,8,"És az ivás, rendelet folytán, kényszer nélkül [történt;] mert így hagyta meg a király háza minden gondviselõjének, hogy kinek-kinek akarata szerint cselekedjenek."));
husuperobject.biblemap.put("b17_1_9", new husuperobject(17,1,9,"Vásti királyné is lakomát szerze a nõknek, a királyi házban, mely Ahasvérus királyé vala."));
husuperobject.biblemap.put("b17_1_10", new husuperobject(17,1,10,"Hetedik napon, mikor megvídámult a király szíve a bortól, mondá Méhumánnak, Biztának, Hárbonának, Bigtának és Abagtának, Zetárnak és Karkásnak, a hét udvarmesternek, a kik szolgálának Ahasvérus király elõtt,"));
husuperobject.biblemap.put("b17_1_11", new husuperobject(17,1,11,"Hogy hozzák el Vástit, a királynét a király elé, királyi koronával, hogy megmutassa a népeknek és fejedelmeknek az õ szépségét; mert szép arczú vala."));
husuperobject.biblemap.put("b17_1_12", new husuperobject(17,1,12,"De Vásti királyné nem akara menni a király szavára, mely vala az udvarmesterek által. Erre igen megharagudott a király, és az õ haragja felgerjede benne."));
husuperobject.biblemap.put("b17_1_13", new husuperobject(17,1,13,"És monda a király az idõket mérõ bölcseknek, (mert a királynak összes dolgai a jog- és törvénytudók elé [tartoznak.]"));
husuperobject.biblemap.put("b17_1_14", new husuperobject(17,1,14,"Legközelebb valának pedig hozzá: Karséna, Sétár, Admata, Társis, Méres, Marséna, Mémukán, Persiának és Médiának hét fejedelme, a kik láták a király arczát, [és] elõl ülének az országban):"));
husuperobject.biblemap.put("b17_1_15", new husuperobject(17,1,15,"Törvény szerint mit kellene cselekedni Vásti királynéval, mivelhogy nem teljesíté Ahasvérus királynak az udvarmesterek által üzent beszédét?"));
husuperobject.biblemap.put("b17_1_16", new husuperobject(17,1,16,"Akkor monda Mémukán a király és a fejedelmek elõtt: Nemcsak a király ellen vétkezett Vásti királyné; de valamennyi fejedelem és minden nép ellen, Ahasvérus király minden tartományában;"));
husuperobject.biblemap.put("b17_1_17", new husuperobject(17,1,17,"Mert a királyné cselekedetének híre fut minden asszonyhoz, úgy, hogy megvetik férjeiket szemeik elõtt, és azt mondják: Ahasvérus király megparancsolá, hogy Vásti királyné hozzá menjen, és nem ment."));
husuperobject.biblemap.put("b17_1_18", new husuperobject(17,1,18,"És e napon [ugyanazt] fogják mondani Persia és Média fejedelemasszonyai, a kik meghallják a királyné cselekedetét, a király minden fejedelmének, és lesz elég megvetés és harag."));
husuperobject.biblemap.put("b17_1_19", new husuperobject(17,1,19,"Ha azért a királynak tetszik, bocsásson ki királyi parancsot a maga részérõl és irattassék a Persa-Méd törvények közé, visszavonhatatlanul, hogy ne jõjjön többé Vásti Ahasvérus király színe elé, és hogy az õ királyságát adja a király másnak, a ki jobb õ nálánál."));
husuperobject.biblemap.put("b17_1_20", new husuperobject(17,1,20,"És hallják meg a király rendeletét, a melyet teend, egész országában, mert igen nagy az, hogy minden asszony adja meg a tiszteletet az õ férjének, a legnagyobbtól a legkisebbig."));
husuperobject.biblemap.put("b17_1_21", new husuperobject(17,1,21,"És tetszett a szó a királynak és a fejedelmeknek, és a király Mémukán beszéde szerint cselekedék."));
husuperobject.biblemap.put("b17_1_22", new husuperobject(17,1,22,"És leveleket külde a királynak minden tartományába, tartományról tartományra, annak saját írása szerint, és néprõl-népre, annak nyelve szerint, hogy legyen minden férfi úr az õ házában és beszéljen az õ népének nyelvén."));
husuperobject.biblemap.put("b17_2_1", new husuperobject(17,2,1,"Ezek után, a mint megszünt Ahasvérus király haragja, megemlékezék Vástiról, és arról a mit cselekedett, és arról a mit végeztek felõle."));
husuperobject.biblemap.put("b17_2_2", new husuperobject(17,2,2,"És mondának a király apródai, a kik néki szolgálának: Keressenek a királynak szép ábrázatú szûz leányokat."));
husuperobject.biblemap.put("b17_2_3", new husuperobject(17,2,3,"És rendeljen a király tiszteket országának minden tartományába, a kik gyûjtsenek össze minden szép ábrázatú szûz leányt Susán várába, az asszonyok házába, Hégainak, a király kamarásának, az asszonyok õrzõjének keze alá, és adjanak nékik szépítõ szereket."));
husuperobject.biblemap.put("b17_2_4", new husuperobject(17,2,4,"És az a leány, a ki tetszik a király szemeinek, uralkodjék Vásti helyett. És tetszett a dolog a királynak, és úgy cselekedék."));
husuperobject.biblemap.put("b17_2_5", new husuperobject(17,2,5,"Vala egy zsidó férfiú Susán várában, a kinek neve Márdokeus, Jáirnak fia, a ki Simei fia, a ki Kis fia, Benjámin nemzetségébõl,"));
husuperobject.biblemap.put("b17_2_6", new husuperobject(17,2,6,"A ki Jeruzsálembõl fogva vitetett el a számûzött néppel, a mely elvitetett Jekóniással, Júda királyával együtt, a kit rabságba vitt el Nabukodonozor, babilóniai király."));
husuperobject.biblemap.put("b17_2_7", new husuperobject(17,2,7,"És ez gondviselõje volt Hadassának, azaz Eszternek az õ nagybátyja leányának; mert sem atyja, sem anyja nem volt, és a leány szép alakú és szép ábrázatú vala, s mikor meghalt az õ atyja és anyja, Márdokeus leánya gyanánt magához fogadá."));
husuperobject.biblemap.put("b17_2_8", new husuperobject(17,2,8,"Lõn pedig, mikor a király parancsa és rendelete kihirdettetett és sok leány gyûjtetett Susán várába, Hégai keze alá, akkor felvétetett Eszter is a király házába, Hégainak, az asszonyok õrének keze alá."));
husuperobject.biblemap.put("b17_2_9", new husuperobject(17,2,9,"És tetszett a leány az õ szemeinek és kegyébe fogadá, és siete néki szépítõ szereket adni, és kiadni az õ részét és hét leányzót, a kit néki a király házából kelle kiszemelni, és vivé õt és az õ szolgálóleányait az asszonyok legjobb házába."));
husuperobject.biblemap.put("b17_2_10", new husuperobject(17,2,10,"Meg nem mondá Eszter az õ nemzetségét és származását; mert Márdokeus meghagyta néki, hogy meg ne mondja."));
husuperobject.biblemap.put("b17_2_11", new husuperobject(17,2,11,"Márdokeus pedig minden nap járt az asszonyok házának pitvara elõtt, hogy tudakozódjék Eszter hogyléte felõl, és hogy mi történik vele."));
husuperobject.biblemap.put("b17_2_12", new husuperobject(17,2,12,"Mikor pedig eljött az ideje minden leánynak, hogy bemenne Ahasvérus királyhoz, miután asszonyok törvénye szerint tizenkét hónapig bántak vele (mert ennyi idõbe telik az õ szépítésök, hat hónapig mirtusolajjal és hat hónapig illatos szerekkel és asszonyi szépítõ szerekkel):"));
husuperobject.biblemap.put("b17_2_13", new husuperobject(17,2,13,"Akkor így ment a leány a királyhoz: Mindent, a mit kivánt, megadtak néki, hogy menjen azzal az asszonyok házából a király házáig;"));
husuperobject.biblemap.put("b17_2_14", new husuperobject(17,2,14,"Este bement és reggel visszatért az asszonyok második házába, Sahásgáznak, a király udvarmesterének, a hálótársak õrzõjének kezei alá; nem jött többé a királyhoz, csak ha kivánta õt a király, és nevén hívták."));
husuperobject.biblemap.put("b17_2_15", new husuperobject(17,2,15,"Mikor azért eljött az ideje Eszternek, a Márdokeus nagybátyja, Abihail leányának, a kit leánya gyanánt fogadott, hogy bemenne a királyhoz, nem kivánt mást, mint a mit Hégai, a király udvarmestere, az asszonyok õrzõje mondott néki, és kedves vala Eszter mindenki szemei elõtt, a ki õt látá."));
husuperobject.biblemap.put("b17_2_16", new husuperobject(17,2,16,"És felviteték Eszter Ahasvérus királyhoz, az õ királyi házába, a tizedik hónapban, ez a Tébet hónapja, országlásának hetedik évében."));
husuperobject.biblemap.put("b17_2_17", new husuperobject(17,2,17,"És a király Esztert minden asszonynál inkább szereté, és minden leánynál nagyobb kedvet és kegyelmet nyert õ elõtte, és tette a királyi koronát az õ fejére, és királynévá tette Vásti helyett."));
husuperobject.biblemap.put("b17_2_18", new husuperobject(17,2,18,"És nagy lakomát adott a király minden õ fejedelmének és szolgáinak, az Eszter lakomáját, és a tartományoknak nyugalmat adott, és ajándékokat osztogata király módjára."));
husuperobject.biblemap.put("b17_2_19", new husuperobject(17,2,19,"És mikor összegyûjték a szûzeket másodszor, Márdokeus üle a király kapujában."));
husuperobject.biblemap.put("b17_2_20", new husuperobject(17,2,20,"És Eszter nem mondá meg sem származását, sem nemzetségét, miképen megparancsolá néki Márdokeus; mert Márdokeus szavát Eszter úgy fogadá, mint mikor gyámja volt."));
husuperobject.biblemap.put("b17_2_21", new husuperobject(17,2,21,"Azokban a napokban pedig, mikor Márdokeus üle a király kapujában, megharagudott Bigtán és Téres, a király két udvarmestere, a kapu õrei, és azon voltak, hogy rávetik kezeiket Ahasvérus királyra."));
husuperobject.biblemap.put("b17_2_22", new husuperobject(17,2,22,"Tudtára esett e dolog Márdokeusnak, és megmondá Eszter királynénak, és Eszter elmondá a királynak Márdokeus nevében."));
husuperobject.biblemap.put("b17_2_23", new husuperobject(17,2,23,"És megvizsgálták a dolgot és úgy találták, és mind a kettõt fára akasztották. És megírattaték a krónikák könyvébe a király elõtt."));
husuperobject.biblemap.put("b17_3_1", new husuperobject(17,3,1,"Ezek után nagy méltóságra emelé Ahasvérus király Hámánt, a Hammedáta fiát, az Agágibelit, és felmagasztalá õt, és feljebb helyezteté székét minden fejedelménél, a kik vele valának."));
husuperobject.biblemap.put("b17_3_2", new husuperobject(17,3,2,"És a király minden szolgái, a kik a király kapujában valának, térdet hajtottak és leborultak Hámán elõtt; mert úgy parancsolta meg nékik a király; de Márdokeus nem hajtott térdet és nem borult le."));
husuperobject.biblemap.put("b17_3_3", new husuperobject(17,3,3,"Mondának azért a király szolgái, a kik a király kapujában valának, Márdokeusnak: Miért szeged meg a király parancsát?"));
husuperobject.biblemap.put("b17_3_4", new husuperobject(17,3,4,"Lõn pedig, mikor így szólnának néki minden nap és nem hallgata rájok, feljelenték Hámánnak, hogy lássák, megállnak-é Márdokeus dolgai, mert azt jelenté nékik, hogy õ zsidó."));
husuperobject.biblemap.put("b17_3_5", new husuperobject(17,3,5,"És látván Hámán, hogy Márdokeus térdet nem hajt és nem borul le elõtte, megtelék Hámán haraggal."));
husuperobject.biblemap.put("b17_3_6", new husuperobject(17,3,6,"De kevés volt elõtte, hogy csakis Márdokeusra magára vesse rá kezét, (mert megmondták néki Márdokeus nemzetségét) azért igyekezett Hámán elveszteni minden zsidót, a ki Ahasvérus egész országában vala, a Márdokeus nemzetét."));
husuperobject.biblemap.put("b17_3_7", new husuperobject(17,3,7,"Az elsõ hónapban, ez Nisán hónapja, Ahasvérus királyságának tizenkettedik évében, Púrt, azaz sorsot vetének Hámán elõtt napról-napra és hónapról-hónapra a tizenkettedikig, s ez Adár hónapja."));
husuperobject.biblemap.put("b17_3_8", new husuperobject(17,3,8,"És monda Hámán Ahasvérus királynak: Van egy nép, elszórva és elkülönítve a népek között, országod minden tartományában, és az õ törvényei különböznek minden nemzetségtõl, és a király törvényeit nem teljesíti; a királynak bizony nem illik úgy hagyni õket."));
husuperobject.biblemap.put("b17_3_9", new husuperobject(17,3,9,"Ha a királynak tetszik, írja meg, hogy õk elvesztessenek, és én tízezer tálentom ezüstöt mérek a hivatalnokok kezeibe, hogy a király kincstárába vigyék."));
husuperobject.biblemap.put("b17_3_10", new husuperobject(17,3,10,"Akkor lehúzá a király az õ gyûrûjét a maga kezérõl, és adá azt az Agágibeli Hámánnak, Hammedáta fiának, a zsidók ellenségének."));
husuperobject.biblemap.put("b17_3_11", new husuperobject(17,3,11,"És monda a király Hámánnak: Az ezüst tied legyen s a nép is, hogy azt cselekedjed vele, a mi néked tetszik."));
husuperobject.biblemap.put("b17_3_12", new husuperobject(17,3,12,"Elõhivatának azért a király irnokai az elsõ hónap tizenharmadik napján, és megiraték minden úgy, a miként Hámán parancsolá, a király fejedelmeinek és a kormányzóknak, a kik az egyes tartományokban valának, és minden egyes nép fejeinek; minden tartománynak annak írása szerint, és minden egyes népnek az õ nyelve szerint, Ahasvérus király nevében iratott és megpecsételtetett a király gyûrûjével."));
husuperobject.biblemap.put("b17_3_13", new husuperobject(17,3,13,"És elküldettek a levelek futárok által a király minden tartományába, hogy kipusztítsák, megöljék és megsemmisítsék mind a zsidókat, ifjútól a vénig, gyermekeket és asszonyokat egy napon, tizenharmadik napján a tizenkettedik hónapnak, (ez Adár hónapja) és hogy javaikat elragadják."));
husuperobject.biblemap.put("b17_3_14", new husuperobject(17,3,14,"Az írásnak mássa, hogy tétessék törvény minden egyes tartományban, meghirdettetett minden népnek, hogy legyenek készen azon a napon."));
husuperobject.biblemap.put("b17_3_15", new husuperobject(17,3,15,"A futárok kimenének gyorsan a király parancsával. És a törvény Susán várában is kiadatott; a király pedig és Hámán leültek, hogy igyanak; de Susán városa felháborodott."));
husuperobject.biblemap.put("b17_4_1", new husuperobject(17,4,1,"Márdokeus pedig megtudta mindazt, a mi történt; és Márdokeus megszaggatá ruháit, zsákba és hamuba öltözék, és a város közepére méne [és ]kiálta nagy és keserves kiáltással."));
husuperobject.biblemap.put("b17_4_2", new husuperobject(17,4,2,"És eljött a királynak kapuja elé; mert nem volt szabad bemenni a király kapuján zsákruhában."));
husuperobject.biblemap.put("b17_4_3", new husuperobject(17,4,3,"És minden egyes tartományban, azon helyen, a hová a király parancsa és törvénye eljutott, nagy gyásza volt a zsidóknak, bõjt és siralom és jajgatás; zsák és hamu vala terítve sokak alá."));
husuperobject.biblemap.put("b17_4_4", new husuperobject(17,4,4,"Eljöttek azért Eszternek leányai és udvarmesterei és megmondák néki, és megszomorodék a királyasszony nagyon, és külde ruhákat, hogy felöltöztetnék Márdokeust, és hogy vesse le a zsákot magáról; de nem fogadá el."));
husuperobject.biblemap.put("b17_4_5", new husuperobject(17,4,5,"Akkor elõhivatá Eszter Hatákot, a király udvarmesterei közül a ki az õ szolgálatára volt rendelve, és kiküldé õt Márdokeushoz, hogy megtudja, mi az és miért van az?"));
husuperobject.biblemap.put("b17_4_6", new husuperobject(17,4,6,"Kiméne tehát Haták Márdokeushoz a város utczájára, mely a király kapuja elõtt vala."));
husuperobject.biblemap.put("b17_4_7", new husuperobject(17,4,7,"És elmondá néki Márdokeus mindazt, a mi érte õt, és az ezüst összegét, a melyet Hámán mondott, hogy juttat a király kincséhez a zsidókért, hogy elvesztessenek."));
husuperobject.biblemap.put("b17_4_8", new husuperobject(17,4,8,"És az írott rendeletnek mássát is, a melyet Susánban adtak ki eltöröltetésökre, átadá néki, hogy mutassa meg Eszternek, és jelentse és hagyja meg néki, hogy menjen a királyhoz, könyörögni néki és esedezni elõtte az õ nemzetségéért."));
husuperobject.biblemap.put("b17_4_9", new husuperobject(17,4,9,"Elméne azért Haták, és elmondá Eszternek Márdokeus szavait."));
husuperobject.biblemap.put("b17_4_10", new husuperobject(17,4,10,"És monda Eszter Hatáknak, és meghagyá néki, hogy [tudassa ]Márdokeussal:"));
husuperobject.biblemap.put("b17_4_11", new husuperobject(17,4,11,"A király minden szolgája és a király tartományainak népe tudja, hogy minden férfinak és asszonynak, a ki bemegy a királyhoz a belsõ udvarba hivatlanul, egy a törvénye, hogy megölettessék, kivévén, a kire a király aranypálczáját kinyujtja, az él; én pedig nem hívattam, hogy a királyhoz bemenjek, már harmincz napja."));
husuperobject.biblemap.put("b17_4_12", new husuperobject(17,4,12,"És megmondák Márdokeusnak Eszter szavait."));
husuperobject.biblemap.put("b17_4_13", new husuperobject(17,4,13,"És monda Márdokeus visszaüzenve Eszternek: Ne gondold magadban, hogy te a király házában megmenekülhetsz a többi zsidó közül."));
husuperobject.biblemap.put("b17_4_14", new husuperobject(17,4,14,"Mert ha e mostani idõben te hallgatsz, másunnan lészen könnyebbségök és szabadulások a zsidóknak; te pedig és atyád háza elvesztek. És ki tudja, talán e mostani idõért jutottál királyságra?"));
husuperobject.biblemap.put("b17_4_15", new husuperobject(17,4,15,"És monda Eszter visszaüzenve Márdokeusnak:"));
husuperobject.biblemap.put("b17_4_16", new husuperobject(17,4,16,"Menj el és gyûjts egybe minden zsidót, a ki Susánban találtatik, és bõjtöljetek érettem és ne egyetek és ne igyatok három napig se éjjel se nappal, én is és leányaim így bõjtölünk és ekképen megyek be a királyhoz, noha törvény ellenére; ha azután elveszek, hát elveszek."));
husuperobject.biblemap.put("b17_4_17", new husuperobject(17,4,17,"Elméne azért Márdokeus, és úgy cselekedett mindent, a mint néki Eszter parancsolá."));
husuperobject.biblemap.put("b17_5_1", new husuperobject(17,5,1,"Történt pedig harmadnapon, hogy Eszter felöltözött királyiasan, és megállt a király házának belsõ udvarában, a király háza ellenében, és a király üle királyiszékében, a királyi házban, a ház ajtajának átellenében."));
husuperobject.biblemap.put("b17_5_2", new husuperobject(17,5,2,"És lõn, a mint meglátá a király Eszter királynét, hogy áll az udvarban, kegyet talála szemei elõtt, és kinyujtá a király Eszterre az arany pálczát, a mely kezében vala, akkor oda méne Eszter, és megilleté az arany pálcza végét."));
husuperobject.biblemap.put("b17_5_3", new husuperobject(17,5,3,"És monda néki a király: Mi [kell] néked Eszter királyné? És mi a kérésed? Ha az ország fele is, megadatik néked."));
husuperobject.biblemap.put("b17_5_4", new husuperobject(17,5,4,"És felele Eszter: Ha a királynak tetszik, jõjjön a király és Hámán ma a lakomára, melyet néki készítettem."));
husuperobject.biblemap.put("b17_5_5", new husuperobject(17,5,5,"És monda a király: Gyorsan elõ Hámánt, hogy teljesítse Eszter kívánságát. És elment a király és Hámán a lakomára, a melyet Eszter készített vala."));
husuperobject.biblemap.put("b17_5_6", new husuperobject(17,5,6,"Monda pedig a király Eszternek borivás közben: Mi a te kivánságod? Megadatik néked. És mi a te kérésed? Ha az ország fele is, meglészen!"));
husuperobject.biblemap.put("b17_5_7", new husuperobject(17,5,7,"És felele Eszter, és monda: Az én kivánságom és kérésem ez:"));
husuperobject.biblemap.put("b17_5_8", new husuperobject(17,5,8,"Ha kegyet találtam a király szemei elõtt, és ha a királynak tetszik megadni kivánságomat és teljesíteni kérésemet: jõjjön el a király és Hámán a lakomára, a melyet készítek nékik, és holnap a király beszéde szerint cselekszem."));
husuperobject.biblemap.put("b17_5_9", new husuperobject(17,5,9,"És kiment Hámán azon a napon vígan és jó kedvvel. De a mint meglátá Hámán Márdokeust a király kapujában, és ez fel nem kelt és nem mozdult meg elõtte, megtelék Hámán haraggal Márdokeus ellen."));
husuperobject.biblemap.put("b17_5_10", new husuperobject(17,5,10,"De megtartóztatá magát Hámán, és haza ment, és elküldvén, magához hivatá barátait és Zérest, az õ feleségét."));
husuperobject.biblemap.put("b17_5_11", new husuperobject(17,5,11,"És elbeszélé nékik Hámán gazdagságának dicsõségét, fiainak sokaságát és mindazt, a mivel felmagasztalta õt a király, és hogy feljebb emelte õt a fejedelmeknél és a király szolgáinál;"));
husuperobject.biblemap.put("b17_5_12", new husuperobject(17,5,12,"És monda Hámán: Nem is hívott Eszter királyné mást a királylyal a lakomára, a melyet készített, hanem csak engem, és még holnapra is meghivattattam hozzá a királylyal."));
husuperobject.biblemap.put("b17_5_13", new husuperobject(17,5,13,"De mindez semmit sem ér nékem, valamíg látom a zsidó Márdokeust ülni a király kapujában."));
husuperobject.biblemap.put("b17_5_14", new husuperobject(17,5,14,"És monda néki Zéres, az õ felesége és minden barátja: Csináljanak ötven könyöknyi magas fát, és reggel mondd meg a királynak, hogy Márdokeust akaszszák reá, és akkor menj a királylyal a lakomára vígan. És tetszett a dolog Hámánnak, és megcsináltatta a fát."));
husuperobject.biblemap.put("b17_6_1", new husuperobject(17,6,1,"Azon éjjel kerülte az álom a királyt, és megparancsolá, hogy hozzák elõ a történetek emlékkönyvét, és ezek olvastattak a király elõtt."));
husuperobject.biblemap.put("b17_6_2", new husuperobject(17,6,2,"És írva találák, a mint Márdokeus feljelenté Bigtánát és Térest, a király két udvarmesterét, a küszöb õrzõit, a kik azon voltak, hogy rávetik kezöket Ahasvérus királyra."));
husuperobject.biblemap.put("b17_6_3", new husuperobject(17,6,3,"És monda a király: Micsoda tisztességet és méltóságot adtak azért Márdokeusnak? És felelének a király apródjai, az õ szolgái: Semmit sem juttattak néki [azért.]"));
husuperobject.biblemap.put("b17_6_4", new husuperobject(17,6,4,"Akkor monda a király: Ki van az udvarban? (Mert Hámán jöve a királyi ház külsõ udvarába, hogy megmondja a királynak, hogy akasztassa fel Márdokeust a fára, a melyet készittetett néki.)"));
husuperobject.biblemap.put("b17_6_5", new husuperobject(17,6,5,"És felelének néki a király apródjai: Ímé Hámán áll az udvarban. És mondja a király: Jõjjön be!"));
husuperobject.biblemap.put("b17_6_6", new husuperobject(17,6,6,"És beméne Hámán, és monda néki a király: Mit kell cselekedni azzal a férfiúval, a kinek a király tisztességet kiván? (Hámán pedig gondolá az õ szívében: Kinek akarna a király nagyobb tisztességet tenni, mint én nékem?)"));
husuperobject.biblemap.put("b17_6_7", new husuperobject(17,6,7,"És monda Hámán a királynak: A férfiúnak, a kinek a király tisztességet kiván,"));
husuperobject.biblemap.put("b17_6_8", new husuperobject(17,6,8,"Hozzanak királyi ruhát, a melyben a király jár, és lovat, a melyen a király szokott ülni és a melynek fejére királyi koronát szoktak tenni."));
husuperobject.biblemap.put("b17_6_9", new husuperobject(17,6,9,"És adják azt a ruhát és lovat a király egyik legelsõ fejedelmének a kezébe, és öltöztessék fel azt a férfiút, a kinek a király tisztességet kiván, és hordozzák õt a lovon a város utczáin, és kiáltsák elõtte: Így cselekesznek a férfiúval, a kinek a király tisztességet kiván."));
husuperobject.biblemap.put("b17_6_10", new husuperobject(17,6,10,"Akkor monda a király Hámánnak: Siess, hozd elõ azt a ruhát és azt a lovat, a mint mondád, és cselekedjél úgy a zsidó Márdokeussal, a ki a király kapujában ül, és semmit el ne hagyj mindabból, a mit szóltál."));
husuperobject.biblemap.put("b17_6_11", new husuperobject(17,6,11,"Elõhozá azért Hámán a ruhát és a lovat, és felöltözteté Márdokeust, és lovon hordozá õt a város utczáján, és kiáltá elõtte: Így cselekesznek a férfiúval, a kinek a király tisztességet kiván."));
husuperobject.biblemap.put("b17_6_12", new husuperobject(17,6,12,"És Márdokeus megtére a király kapujához. Hámán pedig siete házába, búsulva és fejét betakarva."));
husuperobject.biblemap.put("b17_6_13", new husuperobject(17,6,13,"És elbeszélé Hámán Zéresnek, az õ feleségének és minden barátjának mindazt, a mi vele történt. És mondának néki az õ bölcsei és Zéres, az õ felesége: Ha Márdokeus, a ki elõtt kezdtél hanyatlani, a zsidók magvából való: nem bírsz vele, hanem bizony elesel elõtte."));
husuperobject.biblemap.put("b17_6_14", new husuperobject(17,6,14,"És a míg így beszélének vele, eljövének a király udvarmesterei, és siettek Hámánt a lakomára vinni, a melyet készített vala Eszter."));
husuperobject.biblemap.put("b17_7_1", new husuperobject(17,7,1,"És elméne a király és Hámán a lakomára, Eszter királynéhoz."));
husuperobject.biblemap.put("b17_7_2", new husuperobject(17,7,2,"És monda a király Eszternek másodnapon is, borivás közben: Mi a te kivánságod Eszter királyné? Megadatik. És micsoda a te kérésed? Ha az országnak fele is, meglészen."));
husuperobject.biblemap.put("b17_7_3", new husuperobject(17,7,3,"És felele Eszter királyné, és monda: Ha kegyet találtam szemeid elõtt, oh király! és ha a királynak tetszik, add meg nékem életemet kivánságomra, és nemzetségemet kérésemre."));
husuperobject.biblemap.put("b17_7_4", new husuperobject(17,7,4,"Mert eladattunk, én és az én nemzetségem, hogy kipusztítsanak, megöljenek és megsemmisítsenek minket; ha csak szolgákul, vagy szolgálókul adattunk volna el, akkor hallgatnék, jóllehet az ellenség nem adna kárpótlást a király veszteségéért."));
husuperobject.biblemap.put("b17_7_5", new husuperobject(17,7,5,"És szóla Ahasvérus király, és monda Eszternek, a királynénak: Ki az és hol van az, a kit az õ szíve erre vitt, hogy azt cselekedné?"));
husuperobject.biblemap.put("b17_7_6", new husuperobject(17,7,6,"És monda Eszter: Az ellenség és gyûlölõ, ez a gonosz Hámán! Akkor Hámán megrettene a király és királyné elõtt."));
husuperobject.biblemap.put("b17_7_7", new husuperobject(17,7,7,"A király pedig felkele haragjában a borivástól [és méne] a palotakertbe; Hámán pedig ott maradt, hogy életéért könyörögjön Eszter királynénál; mert látta, hogy a király részérõl elvégeztetett az õ veszte."));
husuperobject.biblemap.put("b17_7_8", new husuperobject(17,7,8,"És mikor a király visszatért a palota kertjébõl a borivás házába, Hámán a kerevetre esék, a melyen Eszter vala. Akkor monda a király: Erõszakot is akar elkövetni a királynén én nálam a házban?! A mint e szó kijött a király szájából, Hámán arczát befedék."));
husuperobject.biblemap.put("b17_7_9", new husuperobject(17,7,9,"És monda Harbona, az udvarmesterek egyike a király elõtt: Ímé a fa is, a melyet készített Hámán Márdokeusnak, a ki a király javára szólott vala, ott áll Hámán házában, ötven könyöknyi magas. És monda a király: Akasszátok õt magát reá!"));
husuperobject.biblemap.put("b17_7_10", new husuperobject(17,7,10,"Felakaszták azért Hámánt a fára, a melyet készített Márdokeusnak, és megszünék a király haragja."));
husuperobject.biblemap.put("b17_8_1", new husuperobject(17,8,1,"Azon a napon adá Ahasvérus király Eszter királynénak Hámánnak, a zsidók ellenségének házát, és Márdokeus beméne a király elébe, mert elmondotta Eszter, micsodája õ néki."));
husuperobject.biblemap.put("b17_8_2", new husuperobject(17,8,2,"Lehúzá azért a király az õ gyûrûjét, a melyet Hámántól elvett, és adá azt Márdokeusnak. Eszter pedig Márdokeust tette Hámán házának fejévé."));
husuperobject.biblemap.put("b17_8_3", new husuperobject(17,8,3,"És tovább szóla Eszter a király elõtt, és leborult annak lábaihoz és sírt és könyörgött, hogy semmisítse meg az agági Hámán gonoszságát és tervét, a melyet kigondolt a zsidók ellen."));
husuperobject.biblemap.put("b17_8_4", new husuperobject(17,8,4,"És a király kinyujtá Eszterre az arany pálczát, és Eszter felkelt és megálla a király elõtt,"));
husuperobject.biblemap.put("b17_8_5", new husuperobject(17,8,5,"És monda: Ha a királynak tetszik, és ha kegyet találtam õ elõtte, és ha helyes a dolog a király elõtt, és én kedves vagyok szemei elõtt: írassék meg, hogy vonassanak vissza az agági Hámánnak, Hammedáta fiának tervérõl szóló levelek, a melyeket írt, hogy elveszessék a zsidókat, a kik a király minden tartományában vannak."));
husuperobject.biblemap.put("b17_8_6", new husuperobject(17,8,6,"Mert hogyan tudnám nézni azt a nyomorúságot, mely érné az én nemzetségemet, és hogyan tudnám nézni az én atyámfiainak veszedelmét?"));
husuperobject.biblemap.put("b17_8_7", new husuperobject(17,8,7,"Monda pedig Ahasvérus király Eszter királynénak és a zsidó Márdokeusnak: Ímé Hámán házát Eszternek adtam, és õt felakasztották a fára azért, mert a zsidókra bocsátotta kezét."));
husuperobject.biblemap.put("b17_8_8", new husuperobject(17,8,8,"Ti pedig írjatok a zsidóknak, a mint tetszik néktek a király nevében és pecsételjétek meg a király gyûrûjével; mert az írás, a mely a király nevében van írva és a királynak gyûrûjével van megpecsételve, vissza nem vonható."));
husuperobject.biblemap.put("b17_8_9", new husuperobject(17,8,9,"Hivattatának azért a király irnokai azonnal, a harmadik hónapban (ez a Siván hónap), annak huszonharmadik napján, és megírák úgy, a miként Márdokeus parancsolá a zsidóknak és a fejedelmeknek, a kormányzóknak és a tartományok fejeinek, Indiától fogva Szerecsenországig százhuszonhét tartományba; minden tartománynak annak írása szerint, és minden nemzetségnek az õ nyelvén, és a zsidóknak az õ írások és az õ nyelvök szerint."));
husuperobject.biblemap.put("b17_8_10", new husuperobject(17,8,10,"És megírá Ahasvérus király nevében, és megpecsételé a király gyûrûjével. És külde leveleket lovas futárok által, a kik lovagolnak a királyi ménes gyors paripáin,"));
husuperobject.biblemap.put("b17_8_11", new husuperobject(17,8,11,"Hogy megengedte a király a zsidóknak, a kik bármely városban vannak, hogy egybegyûljenek és keljenek fel életökért, és hogy kipusztítsák, megöljék és megsemmisítsék minden népnek és tartománynak seregét, a mely õket nyomorgatja, kicsinyeket és nõket, és hogy javaikat elragadják."));
husuperobject.biblemap.put("b17_8_12", new husuperobject(17,8,12,"Egy napon, Ahasvérus királynak minden tartományában, tizenharmadik napján a tizenkettedik hónapnak (ez Adár hónapja)."));
husuperobject.biblemap.put("b17_8_13", new husuperobject(17,8,13,"Az írásnak mássa, hogy adassék törvény minden egyes tartományban, meghirdettetett minden népnek, hogy a zsidók készen legyenek azon a napon, hogy megbosszulják magokat ellenségeiken."));
husuperobject.biblemap.put("b17_8_14", new husuperobject(17,8,14,"A futárok a királyi gyors paripákon kimenének sietve és sürgõsen a király parancsolata szerint; és kiadatott a törvény Susán várában is."));
husuperobject.biblemap.put("b17_8_15", new husuperobject(17,8,15,"Márdokeus pedig kiméne a király elõl kék bíbor és fehér királyi ruhában, és nagy arany koronával, és bíborbársony palástban, és Susán városa vígada és örvendeze."));
husuperobject.biblemap.put("b17_8_16", new husuperobject(17,8,16,"A zsidóknak világosság támada, öröm, vigasság és tisztesség."));
husuperobject.biblemap.put("b17_8_17", new husuperobject(17,8,17,"És minden tartományban és minden városban, a hová a király szava és rendelete eljuta, öröme és vigalma lõn a zsidóknak, lakoma és ünnep, és sokan a föld népei közül zsidókká lettek; mert a zsidóktól való félelem szállta meg õket."));
husuperobject.biblemap.put("b17_9_1", new husuperobject(17,9,1,"A tizenkettedik hónapban azért (ez az Adár hónap), annak tizenharmadik napján, a mikor eljött [az ideje] a király szavának és végzésének, hogy akkor teljesíttessék, azon a napon, a melyen a zsidók ellenségei remélték, hogy hatalmat vesznek rajtok, ellenkezõleg fordult; mert magok a zsidók võnek hatalmat azokon, a kik õket gyûlölék."));
husuperobject.biblemap.put("b17_9_2", new husuperobject(17,9,2,"Egybegyûlének a zsidók városaikban, Ahasvérus király minden tartományában, hogy rávessék kezöket azokra, a kik vesztöket keresték, és senki sem állhatott meg elõttük; mert miattok való félelem szállott minden népre."));
husuperobject.biblemap.put("b17_9_3", new husuperobject(17,9,3,"És a tartományok minden feje, fejedelmek, kormányzók és a király hivatalnokai magasztalták a zsidókat; mert a Márdokeustól való félelem szállott rájok."));
husuperobject.biblemap.put("b17_9_4", new husuperobject(17,9,4,"Mert nagy volt Márdokeus a király házában, és híre elment minden tartományba; mert ez a férfiú, Márdokeus, emelkedett és nagyobbodott."));
husuperobject.biblemap.put("b17_9_5", new husuperobject(17,9,5,"És leverték a zsidók minden ellenségeiket fegyverrel, megölvén és megsemmisítvén azokat, és akaratok szerint cselekedének gyûlölõikkel."));
husuperobject.biblemap.put("b17_9_6", new husuperobject(17,9,6,"És Susán várában megölének és megsemmisítének a zsidók ötszáz férfiút."));
husuperobject.biblemap.put("b17_9_7", new husuperobject(17,9,7,"Parsandátát, Dalpont és Aspatát."));
husuperobject.biblemap.put("b17_9_8", new husuperobject(17,9,8,"Porátát, Adáliát és Aridátát."));
husuperobject.biblemap.put("b17_9_9", new husuperobject(17,9,9,"Parmástát, Arisait, Aridait és Vajzátát."));
husuperobject.biblemap.put("b17_9_10", new husuperobject(17,9,10,"Tíz fiát Hámánnak, Hammedáta fiának, a zsidók ellenségének megölték; de zsákmányra nem tették rá kezöket."));
husuperobject.biblemap.put("b17_9_11", new husuperobject(17,9,11,"Azon a napon megtudá a király a Susán várában megöletteknek számát."));
husuperobject.biblemap.put("b17_9_12", new husuperobject(17,9,12,"És monda a király Eszter királynénak: Susán várában megöltek a zsidók és megsemmisítettek ötszáz férfiút és Hámán tíz fiát; a király többi tartományaiban mit cselekesznek? Mi a kivánságod? és megadatik néked. És mi még a te kérésed? és meglészen."));
husuperobject.biblemap.put("b17_9_13", new husuperobject(17,9,13,"És monda Eszter: Ha a királynak tetszik, engedje meg holnap is a zsidóknak, a kik Susánban laknak, hogy cselekedjenek a mai parancsolat szerint, és Hámán tíz fiát akaszszák fel a fára."));
husuperobject.biblemap.put("b17_9_14", new husuperobject(17,9,14,"És szóla a király, hogy úgy tegyenek. És kiadaték a parancs Susánban, és Hámán tíz fiát felakasztották."));
husuperobject.biblemap.put("b17_9_15", new husuperobject(17,9,15,"És összegyûltek a zsidók, a kik Susánban valának, Adár havának tizennegyedik napján is, és megölének Susánban háromszáz férfiút; de zsákmányra nem tették rá kezöket."));
husuperobject.biblemap.put("b17_9_16", new husuperobject(17,9,16,"És a többi zsidók is, a kik a király tartományaiban voltak, összegyûlének és feltámadtak életökért, és békében maradtak ellenségeiktõl; megölének pedig gyûlölõikbõl hetvenötezeret; de zsákmányra nem tették rá kezöket."));
husuperobject.biblemap.put("b17_9_17", new husuperobject(17,9,17,"[Történt ez] az Adár hónapjának tizenharmadik napján, és megnyugovának a tizennegyedik napon, és tették azt vigalom és öröm napjává."));
husuperobject.biblemap.put("b17_9_18", new husuperobject(17,9,18,"De a zsidók, a kik Susánban valának, egybegyûlének azon [hónap] tizenharmadik és tizennegyedik napján, és megnyugovának azon [hónap] tizenötödik napján, és azt tették vigalom és öröm napjává."));
husuperobject.biblemap.put("b17_9_19", new husuperobject(17,9,19,"Azért a vidéki zsidók, a kik kerítetlen falvakban laktak, Adár hónapjának tizennegyedik napját tették öröm és vigalom [napjá]vá és ünneppé, a melyen ajándékokat küldözgetnek egymásnak."));
husuperobject.biblemap.put("b17_9_20", new husuperobject(17,9,20,"És megírá Márdokeus e dolgokat, és leveleket külde minden zsidónak, a ki Ahasvérus király minden tartományában, közel és távol, vala."));
husuperobject.biblemap.put("b17_9_21", new husuperobject(17,9,21,"Meghagyva nékik, hogy tartsák meg az Adár hónapnak tizennegyedik napját és annak tizenötödik napját évrõl-évre,"));
husuperobject.biblemap.put("b17_9_22", new husuperobject(17,9,22,"Mint olyan napokat, a melyeken megnyugovának a zsidók ellenségeiktõl, és mint olyan hónapot, a melyben keserûségök örömre és siralmuk ünnepre fordult; hogy tartsák meg azokat vigalom és öröm napjaiul, és küldjenek adjándékot egymásnak és adományokat a szegényeknek."));
husuperobject.biblemap.put("b17_9_23", new husuperobject(17,9,23,"És felfogadák a zsidók, hogy megcselekszik, a mit kezdettek és a mit Márdokeus íra nékik."));
husuperobject.biblemap.put("b17_9_24", new husuperobject(17,9,24,"Mert az agági Hámán, Hammedátának fia, minden zsidónak ellensége, szándékozott a zsidókat elveszteni, és Púrt, azaz sorsot vetett, hogy õket megrontsa és megsemmisítse;"));
husuperobject.biblemap.put("b17_9_25", new husuperobject(17,9,25,"De mikor a király tudomására jutott, megparancsolá levélben, hogy gonosz szándéka, a melyet gondolt a zsidók ellen, forduljon az õ fejére, és felakaszták õt és fiait a fára."));
husuperobject.biblemap.put("b17_9_26", new husuperobject(17,9,26,"Annakokáért elnevezék e napokat Púrimnak a Púr nevétõl. És azért ezen levél minden szava szerint, és a mit láttak erre nézve, és a mi érte õket:"));
husuperobject.biblemap.put("b17_9_27", new husuperobject(17,9,27,"Elhatározák és elfogadák a zsidók mind magokra, mind ivadékokra és mindazokra, a kik hozzájok csatlakoznak, örök idõkre, hogy megtartják e két napot, írásuk és határozatuk szerint, minden esztendõben."));
husuperobject.biblemap.put("b17_9_28", new husuperobject(17,9,28,"És ezen napok emlékezetben lesznek és megülik azokat nemzedékrõl- nemzedékre, családról-családra, tartományról-tartományra és városról- városra. És ezek a Púrim napjai el nem múlnak a zsidók között, és emlékök ki nem vész ivadékaik közül."));
husuperobject.biblemap.put("b17_9_29", new husuperobject(17,9,29,"Ira pedig Eszter királyné, Abihail leánya és a zsidó Márdokeus egész hatalommal, hogy megerõsítsék e második levelet a Púrimról."));
husuperobject.biblemap.put("b17_9_30", new husuperobject(17,9,30,"És külde leveleket minden zsidónak Ahasvérus országának százhuszonhét tartományába, békességes és igazságos szavakkal;"));
husuperobject.biblemap.put("b17_9_31", new husuperobject(17,9,31,"Hogy megerõsíttessenek ezek a Púrim napjai, meghatározott idejökben, a miképen meghagyta nékik a zsidó Márdokeus és Eszter királyné, és a mint elrendelték magokra és ivadékaikra a bõjtölés és jajkiáltás dolgát."));
husuperobject.biblemap.put("b17_9_32", new husuperobject(17,9,32,"És Eszter beszéde megerõsíté ezt a Púrim történetét, és könyvbe iraték."));
husuperobject.biblemap.put("b17_10_1", new husuperobject(17,10,1,"És Ahasvérus király adót vettete a földre és a tenger szigeteire."));
husuperobject.biblemap.put("b17_10_2", new husuperobject(17,10,2,"Az õ erejének és hatalmának minden cselekedete pedig és Márdokeus nagy méltóságának története, a mellyel felmagasztalá õt a király, avagy nincsenek-é megírva Média és Persia királyainak évkönyveiben,"));
husuperobject.biblemap.put("b17_10_3", new husuperobject(17,10,3,"Mert a zsidó Márdokeus második volt Ahasvérus király után, nagy a zsidók között, és kedves az õ atyjafiai sokasága elõtt, a ki javát keresé népének, és békességet szerze minden ivadékának."));

}

}