package hu.bible;

public class hubible25 {

public hubible25() {

putmapbible25();

}

private void putmapbible25() {

husuperobject.biblemap.put("b25_1_1", new husuperobject(25,1,1,"Jaj! de árván ül a nagy népû város! Olyanná lõn, mint az özvegyasszony! Nagy volt a nemzetek között, a tartományok közt fejedelemasszony: robotossá lõn!"));
husuperobject.biblemap.put("b25_1_2", new husuperobject(25,1,2,"Sírván sír éjjelente, s könny [borítja] az orczáját! Senki sincs, ki vígasztalná, azok közül, kik szerették; mind megcsalták barátai, ellenségeivé lõnek."));
husuperobject.biblemap.put("b25_1_3", new husuperobject(25,1,3,"Számkivetésbe méne Júda a nyomorúság és a szolgálat sokasága miatt! Ott ül õ a pogányok közt; nem talál nyugodalmat; valamennyi üldözõje utólérte a szorultságában."));
husuperobject.biblemap.put("b25_1_4", new husuperobject(25,1,4,"Sionnak útai gyászolnak; nincsen, a ki ünnepnapra járjon; kapuja mind elpusztult; papjai sóhajtoznak, szûzei nyögnek, csak keserûsége van néki."));
husuperobject.biblemap.put("b25_1_5", new husuperobject(25,1,5,"Elnyomói fõkké lettek; ellenségei boldogok! Bizony az Úr verte meg õt az õ sok bûne miatt; gyermekei rabságra mentek az elnyomó elõtt."));
husuperobject.biblemap.put("b25_1_6", new husuperobject(25,1,6,"És elhagyta Sion leányát minden õ ékessége; fejedelmei olyanok lettek, mint a szarvasok, a melyek nem találnak eledelt, és erõtelenül futnak az üldözõ elõtt."));
husuperobject.biblemap.put("b25_1_7", new husuperobject(25,1,7,"Emlékezik Jeruzsálem az õ nyomorúságának és eltiportatásának napjain minden õ gyönyörûségérõl, a melyek voltak eleitõl fogva; mert az õ népe ellenség kezébe esett és nem volt segítsége. Látták õt az ellenségek; nevettek megsemmisülésén."));
husuperobject.biblemap.put("b25_1_8", new husuperobject(25,1,8,"Vétkezvén vétkezett Jeruzsálem, azért lett csúfsággá, minden tisztelõje megvetette, mert látták az õ mezítelenségét, õ maga pedig sóhajtoz és elfordul."));
husuperobject.biblemap.put("b25_1_9", new husuperobject(25,1,9,"Szennye a ruhája szélén; nem gondolt a jövõjére; csudálatosan alásülyedt, nincs vígasztalója. Lásd meg Uram, az én nyomorúságomat, mert ellenség vett erõt [rajtam!]"));
husuperobject.biblemap.put("b25_1_10", new husuperobject(25,1,10,"Szorongató nyújtja kezét minden kincse után, sõt látta, hogy pogányok mentek be az õ szent helyébe, a kikrõl azt parancsoltad, hogy be ne menjenek a te községedbe."));
husuperobject.biblemap.put("b25_1_11", new husuperobject(25,1,11,"Egész népe sóhajtoz, futkosnak a kenyér után, odaadják drágaságaikat az ételért, hogy megéledjenek. Lásd meg Uram és tekintsd meg, mily útálatossá lettem!"));
husuperobject.biblemap.put("b25_1_12", new husuperobject(25,1,12,"Mindnyájatokat kérlek, ti járókelõk: tekintsétek meg és lássátok meg, ha van-é oly bánat, mint az én bánatom, a mely engem ért, a melylyel engem sujtott az Úr az õ búsult haragjának napján!"));
husuperobject.biblemap.put("b25_1_13", new husuperobject(25,1,13,"A magasságból tüzet bocsátott csontjaimba, és az hatalmaskodik bennök; hálót vetett lábaimnak; hátra vetett, pusztává tett engem; egész napon beteg [vagyok.]"));
husuperobject.biblemap.put("b25_1_14", new husuperobject(25,1,14,"Saját kezével rótta össze az én vétkeim igáját; ráfonódtak nyakamra; megrendítette erõmet; oda adott engem az Úr azok kezébe, a kik elõtt meg nem állhatok."));
husuperobject.biblemap.put("b25_1_15", new husuperobject(25,1,15,"Rakásra hányta az Úr minden vitézemet én bennem; gyûlést hívott össze ellenem, hogy összetörje ifjaimat; sajtóba taposta az Úr Júda szûz leányát."));
husuperobject.biblemap.put("b25_1_16", new husuperobject(25,1,16,"Ezekért sírok én; szemembõl, szemembõl víz folyik alá, mert messze távozott tõlem a vígasztaló, a ki megélesszen engem; fiaim elvesztek, mert gyõzött az ellenség."));
husuperobject.biblemap.put("b25_1_17", new husuperobject(25,1,17,"Terjesztgeti kezeit Sion: nincs vígasztalója; szorongatókat rendelt az Úr Jákób ellen köröskörül; Jeruzsálem csúfsággá lett közöttök."));
husuperobject.biblemap.put("b25_1_18", new husuperobject(25,1,18,"Igaz õ, az Úr, mert az õ szava ellen rugódoztam! Halljátok meg kérlek, mind ti népek, és lássátok meg az én bánatomat: szûzeim és ifjaim fogságba menének!"));
husuperobject.biblemap.put("b25_1_19", new husuperobject(25,1,19,"Kiáltottam azoknak, a kik szerettek engem de õk megcsaltak engem; papjaim és véneim a városban multak ki, a mikor étel után futkostak, hogy megéledjenek."));
husuperobject.biblemap.put("b25_1_20", new husuperobject(25,1,20,"Lásd meg Uram, hogy szorongattatom, belsõ részeim megháborodtak; elfordult bennem az én szívem, mert bizony pártot ütöttem; künn fegyver pusztít, benn [minden] olyan, mint a halál!"));
husuperobject.biblemap.put("b25_1_21", new husuperobject(25,1,21,"Hallották, hogy sóhajtozom és nincs vígasztalóm; minden ellenségem hallotta veszedelmemet; örültek, hogy te [ezt] cselekedted. Hozd el a napot, a melyet hirdettél, hogy olyanok legyenek, mint én!"));
husuperobject.biblemap.put("b25_1_22", new husuperobject(25,1,22,"Jusson elõdbe minden gonoszságuk, és úgy bánj velök, a miképen én velem bántál az én minden bûnömért; mert sok az én sóhajtozásom, és beteg a szívem."));
husuperobject.biblemap.put("b25_2_1", new husuperobject(25,2,1,"Jaj; de sûrû felhõt borított haragjában az Úr Sionnak leányára! az égbõl a földre veté Izráel ékességét, és nem emlékezett meg lábainak zsámolyáról az õ haragja napján."));
husuperobject.biblemap.put("b25_2_2", new husuperobject(25,2,2,"Elnyelte az Úr, nem kimélte Jákóbnak minden hajlékát, letörte haragjában Júda leányának erõsségeit, a földre terítette; megfertõzteté az országot és fejedelmeit."));
husuperobject.biblemap.put("b25_2_3", new husuperobject(25,2,3,"Felgerjedt haragjában letördelé Izráelnek minden szarvát; hátravoná jobbkezét az ellenség elõl, Jákób ellen pedig mint lángoló tûz emésztett köröskörül."));
husuperobject.biblemap.put("b25_2_4", new husuperobject(25,2,4,"Feszítette kézívét, mint valami ellenség, kinyújtá jobbkezét, mint támadó, és megölt mindent, a mi a szemnek kivánatos; Sion leányának sátorában, mint a tüzet önté ki búsulását."));
husuperobject.biblemap.put("b25_2_5", new husuperobject(25,2,5,"Olyan volt az Úr, mint valami ellenség; elnyelte Izráelt, elnyelte minden palotáját, elrontá erõsségeit, és megsokasította Júda leányának a búját, baját."));
husuperobject.biblemap.put("b25_2_6", new husuperobject(25,2,6,"És eltapodta sátorát, mint valami kertet, lerombolta gyülekezése helyét; elfeledtete az Úr a Sionon ünnepet és szombatot, és megútált haragja hevében királyt és papot."));
husuperobject.biblemap.put("b25_2_7", new husuperobject(25,2,7,"Megvetette az Úr az õ oltárát, megútálta szent helyét; ellenség kezébe adá palotáinak kõfalait; zajt ütöttek az Úr házában, mint ünnepnapon."));
husuperobject.biblemap.put("b25_2_8", new husuperobject(25,2,8,"Gondolá az Úr, hogy lerontja Sion leányának kõfalát; kiterjeszté a mérõkötelet, nem vonta vissza kezét a pusztítástól, és siralomra jutott a bástya és a kõfal, együtt búslakodnak!"));
husuperobject.biblemap.put("b25_2_9", new husuperobject(25,2,9,"Kapui besülyedtek a földbe, elveszté és összetöré annak zárait; királya és fejedelmei a pogányok közt vannak. Nincsen törvény, sõt prófétái sem nyernek kijelentést az Úrtól."));
husuperobject.biblemap.put("b25_2_10", new husuperobject(25,2,10,"A földön ülnek, elnémultak Sion leányának vénei, port szórtak a fejökre; zsákba öltöztek, földre csüggesztették fejöket Jeruzsálemnek szûzei."));
husuperobject.biblemap.put("b25_2_11", new husuperobject(25,2,11,"Elsenyvedtek szemeim a könyhullatástól, belsõ részeim háborognak, májam a földre omlik az én népem leányának romlása miatt, mikor elalélt a kis gyermek és a csecsszopó a város utczáin."));
husuperobject.biblemap.put("b25_2_12", new husuperobject(25,2,12,"Azt mondták anyjoknak: Hol a kenyér, meg a bor? mikor elaléltak, mint a sebesültek a város utczáin, mikor kilehelték lelköket anyjoknak kebelén."));
husuperobject.biblemap.put("b25_2_13", new husuperobject(25,2,13,"Mivel bizonyítsak melletted, mihez hasonlítsalak, Jeruzsálem leánya, mivel mérjelek össze téged, hogy megvígasztaljalak, Sionnak szûz leánya?! Bizony nagy a te romlásod, mint a tenger: kicsoda gyógyít meg téged?!"));
husuperobject.biblemap.put("b25_2_14", new husuperobject(25,2,14,"A te prófétáid hazugságot és bolondságot hirdettek néked, és nem fedték fel a te álnokságodat, hogy elfordították volna fogságodat; hanem láttak tenéked hazug és megtévelyítõ prófétálásokat."));
husuperobject.biblemap.put("b25_2_15", new husuperobject(25,2,15,"Összecsapják feletted kezöket minden járó-kelõk; süvöltenek és csóválják fejöket Jeruzsálem leánya felett: Ez-é az a város, a melyrõl azt mondták: tökéletes szépség, az egész földnek öröme?"));
husuperobject.biblemap.put("b25_2_16", new husuperobject(25,2,16,"Feltátották ellened szájokat minden ellenségeid; süvöltenek és csikorgatják fogukat, mondván: Nyeljük el õt! Bizony ez a nap az, a melyet vártunk; megértük, látjuk!"));
husuperobject.biblemap.put("b25_2_17", new husuperobject(25,2,17,"Megcselekedte az Úr, a miket gondolt; beváltotta szavát, a melyet szólt eleitõl fogva; rombolt és nem kimélt, és megvidámította rajtad az ellenséget, felemelte szarvát a te szorongatóidnak."));
husuperobject.biblemap.put("b25_2_18", new husuperobject(25,2,18,"Kiáltott az õ szívök az Úrhoz: Oh Sion leányának kõfala! Folyjon alá könnyed mint a patak, éjjel és nappal; ne szakadjon félbe, síró szemed meg se pihenjen."));
husuperobject.biblemap.put("b25_2_19", new husuperobject(25,2,19,"Kelj fel, riadj éjjel, az õrjárások kezdetén; öntsd ki, mint a vizet a te szívedet az Úr szine elõtt; emeld fel hozzá kezeidet a te kisdedeidnek életéért, a kik elaléltak az éhség miatt minden utczának szegletén."));
husuperobject.biblemap.put("b25_2_20", new husuperobject(25,2,20,"Lásd meg Uram és tekintsd meg, kivel cselekedtél így! Avagy megegyék-é az asszonyok az õ méhöknek gyümölcsét, dédelgetett kisdedeiket; avagy megölettessék-é az Úrnak szent helyén pap és próféta?"));
husuperobject.biblemap.put("b25_2_21", new husuperobject(25,2,21,"Az utczákon a földön fekszik gyermek és vén; szûzeim és ifjaim fegyver miatt hullottak el; öldököltél haragod napján, mészároltál, nem kiméltél."));
husuperobject.biblemap.put("b25_2_22", new husuperobject(25,2,22,"Egybehívtad mint valami ünnepnapra az én rettegtetõimet mindenfelõl, és nem volt az Úr haragjának napján, a ki elmenekült és megszabadult volna. A kiket dédelgettem és felneveltem, ellenségem emésztette meg õket!"));
husuperobject.biblemap.put("b25_3_1", new husuperobject(25,3,1,"Én vagyok az az ember, a ki nyomorúságot látott az õ haragjának vesszeje miatt."));
husuperobject.biblemap.put("b25_3_2", new husuperobject(25,3,2,"Engem vezérlett és járatott sötétségben és nem világosságban."));
husuperobject.biblemap.put("b25_3_3", new husuperobject(25,3,3,"Bizony ellenem fordult, [ellenem] fordítja kezét minden nap."));
husuperobject.biblemap.put("b25_3_4", new husuperobject(25,3,4,"Megfonnyasztotta testemet és bõrömet, összeroncsolta csontjaimat."));
husuperobject.biblemap.put("b25_3_5", new husuperobject(25,3,5,"[Erõsséget] épített ellenem és körülvett méreggel és fáradsággal."));
husuperobject.biblemap.put("b25_3_6", new husuperobject(25,3,6,"Sötét helyekre ültetett engem, mint az örökre meghaltakat."));
husuperobject.biblemap.put("b25_3_7", new husuperobject(25,3,7,"Körülkerített, hogy ki ne mehessek, nehézzé tette lánczomat."));
husuperobject.biblemap.put("b25_3_8", new husuperobject(25,3,8,"Sõt ha kiáltok és segítségül hívom is, nem hallja meg imádságomat."));
husuperobject.biblemap.put("b25_3_9", new husuperobject(25,3,9,"Elkerítette az én útaimat terméskõvel, ösvényeimet elforgatta."));
husuperobject.biblemap.put("b25_3_10", new husuperobject(25,3,10,"Ólálkodó medve õ nékem [és] lesben álló oroszlán."));
husuperobject.biblemap.put("b25_3_11", new husuperobject(25,3,11,"Útaimat elterelte, és darabokra vagdalt és elpusztított engem!"));
husuperobject.biblemap.put("b25_3_12", new husuperobject(25,3,12,"Kifeszítette kézívét, és a nyíl elé czélul állított engem!"));
husuperobject.biblemap.put("b25_3_13", new husuperobject(25,3,13,"Veséimbe bocsátotta tegzének fiait."));
husuperobject.biblemap.put("b25_3_14", new husuperobject(25,3,14,"Egész népemnek csúfjává lettem, és gúnydalukká napestig."));
husuperobject.biblemap.put("b25_3_15", new husuperobject(25,3,15,"Eltöltött engem keserûséggel, megrészegített engem ürömmel."));
husuperobject.biblemap.put("b25_3_16", new husuperobject(25,3,16,"És kova-kõvel tördelte ki fogaimat; porba tiprott engem."));
husuperobject.biblemap.put("b25_3_17", new husuperobject(25,3,17,"És kizártad lelkem a békességbõl; elfeledkeztem a jóról."));
husuperobject.biblemap.put("b25_3_18", new husuperobject(25,3,18,"És mondám: Elveszett az én erõm és az én reménységem az Úrban."));
husuperobject.biblemap.put("b25_3_19", new husuperobject(25,3,19,"Emlékezzél meg az én nyomorúságomról és eltapodtatásomról, az ürömrõl és a méregrõl!"));
husuperobject.biblemap.put("b25_3_20", new husuperobject(25,3,20,"Vissza-visszaemlékezik, és megalázódik bennem az én lelkem."));
husuperobject.biblemap.put("b25_3_21", new husuperobject(25,3,21,"Ezt veszem szívemre, azért bízom."));
husuperobject.biblemap.put("b25_3_22", new husuperobject(25,3,22,"Az Úr kegyelmessége az, hogy még nincsen végünk; mivel nem fogyatkozik el az õ irgalmassága!"));
husuperobject.biblemap.put("b25_3_23", new husuperobject(25,3,23,"Minden reggel meg-megújul; nagy a te hûséged!"));
husuperobject.biblemap.put("b25_3_24", new husuperobject(25,3,24,"Az Úr az én örökségem, mondja az én lelkem, azért benne bízom."));
husuperobject.biblemap.put("b25_3_25", new husuperobject(25,3,25,"Jó az Úr azoknak, a kik várják õt; a léleknek, a mely keresi õt."));
husuperobject.biblemap.put("b25_3_26", new husuperobject(25,3,26,"Jó várni és megadással lenni az Úr szabadításáig."));
husuperobject.biblemap.put("b25_3_27", new husuperobject(25,3,27,"Jó a férfiúnak, ha igát visel ifjúságában."));
husuperobject.biblemap.put("b25_3_28", new husuperobject(25,3,28,"Egyedül ül és hallgat, mert felvette magára."));
husuperobject.biblemap.put("b25_3_29", new husuperobject(25,3,29,"Porba teszi száját, [mondván:] Talán van [még] reménység?"));
husuperobject.biblemap.put("b25_3_30", new husuperobject(25,3,30,"Orczáját tartja az õt verõnek, megelégszik gyalázattal."));
husuperobject.biblemap.put("b25_3_31", new husuperobject(25,3,31,"Mert nem zár ki örökre az Úr."));
husuperobject.biblemap.put("b25_3_32", new husuperobject(25,3,32,"Sõt, ha megszomorít, meg is vígasztal az õ kegyelmességének gazdagsága szerint."));
husuperobject.biblemap.put("b25_3_33", new husuperobject(25,3,33,"Mert nem szíve szerint veri és szomorítja meg az embernek fiát."));
husuperobject.biblemap.put("b25_3_34", new husuperobject(25,3,34,"Hogy lábai alá tiporja valaki a föld minden foglyát;"));
husuperobject.biblemap.put("b25_3_35", new husuperobject(25,3,35,"Hogy elfordíttassék az ember ítélete a Magasságosnak színe elõtt;"));
husuperobject.biblemap.put("b25_3_36", new husuperobject(25,3,36,"Hogy elnyomassék az ember az õ peres dolgában: ezt az Úr nem nézi el."));
husuperobject.biblemap.put("b25_3_37", new husuperobject(25,3,37,"Kicsoda az, a ki szól és meglesz, ha nem parancsolja az Úr?"));
husuperobject.biblemap.put("b25_3_38", new husuperobject(25,3,38,"A Magasságosnak szájából nem jõ ki a gonosz és a jó."));
husuperobject.biblemap.put("b25_3_39", new husuperobject(25,3,39,"Mit zúgolódik az élõ ember? Ki-ki a maga bûneiért [bûnhõdik.]"));
husuperobject.biblemap.put("b25_3_40", new husuperobject(25,3,40,"Tudakozzuk a mi útainkat és vizsgáljuk meg, és térjünk az Úrhoz."));
husuperobject.biblemap.put("b25_3_41", new husuperobject(25,3,41,"Emeljük fel szíveinket kezeinkkel egyetemben Istenhez az égben."));
husuperobject.biblemap.put("b25_3_42", new husuperobject(25,3,42,"Mi voltunk gonoszok és pártütõk, azért nem bocsátottál meg."));
husuperobject.biblemap.put("b25_3_43", new husuperobject(25,3,43,"Felöltötted a haragot és üldöztél minket, öldököltél, nem kiméltél."));
husuperobject.biblemap.put("b25_3_44", new husuperobject(25,3,44,"Felöltötted a felhõt, hogy hozzád ne jusson az imádság."));
husuperobject.biblemap.put("b25_3_45", new husuperobject(25,3,45,"Sepredékké és útálattá tettél minket a népek között."));
husuperobject.biblemap.put("b25_3_46", new husuperobject(25,3,46,"Feltátotta száját ellenünk minden ellenségünk."));
husuperobject.biblemap.put("b25_3_47", new husuperobject(25,3,47,"Rettegés és tõr van mi rajtunk, pusztulás és romlás."));
husuperobject.biblemap.put("b25_3_48", new husuperobject(25,3,48,"Víz-patakok folynak alá az én szemembõl népem leányának romlása miatt."));
husuperobject.biblemap.put("b25_3_49", new husuperobject(25,3,49,"Szemem csörgedez és nem szünik meg, nincs pihenése,"));
husuperobject.biblemap.put("b25_3_50", new husuperobject(25,3,50,"Míg ránk nem tekint és meg nem lát az Úr az égbõl."));
husuperobject.biblemap.put("b25_3_51", new husuperobject(25,3,51,"Szemem bánatba ejté lelkemet városomnak minden leányáért."));
husuperobject.biblemap.put("b25_3_52", new husuperobject(25,3,52,"Vadászva vadásztak reám, mint valami madárra, ellenségeim ok nélkül."));
husuperobject.biblemap.put("b25_3_53", new husuperobject(25,3,53,"Veremben fojtották meg életemet, és követ hánytak rám."));
husuperobject.biblemap.put("b25_3_54", new husuperobject(25,3,54,"Felüláradtak a vizek az én fejem felett; mondám: Kivágattam!"));
husuperobject.biblemap.put("b25_3_55", new husuperobject(25,3,55,"Segítségül hívtam a te nevedet, oh Uram, a legalsó verembõl."));
husuperobject.biblemap.put("b25_3_56", new husuperobject(25,3,56,"Hallottad az én szómat; ne rejtsd el füledet sóhajtásom és kiáltásom elõl."));
husuperobject.biblemap.put("b25_3_57", new husuperobject(25,3,57,"Közelegj hozzám, mikor segítségül hívlak téged; mondd: Ne félj!"));
husuperobject.biblemap.put("b25_3_58", new husuperobject(25,3,58,"Pereld meg Uram lelkemnek perét; váltsd meg életemet."));
husuperobject.biblemap.put("b25_3_59", new husuperobject(25,3,59,"Láttad, oh Uram, az én bántalmaztatásomat; ítéld meg ügyemet."));
husuperobject.biblemap.put("b25_3_60", new husuperobject(25,3,60,"Láttad minden bosszúállásukat, minden ellenem való gondolatjokat."));
husuperobject.biblemap.put("b25_3_61", new husuperobject(25,3,61,"Hallottad Uram az õ szidalmazásukat, minden ellenem való gondolatjokat;"));
husuperobject.biblemap.put("b25_3_62", new husuperobject(25,3,62,"Az ellenem támadóknak ajkait, és ellenem való mindennapi szándékukat."));
husuperobject.biblemap.put("b25_3_63", new husuperobject(25,3,63,"Tekintsd meg leülésöket és felkelésöket; én vagyok az õ gúnydaluk."));
husuperobject.biblemap.put("b25_3_64", new husuperobject(25,3,64,"Fizess meg nékik, Uram, az õ kezeiknek munkája szerint."));
husuperobject.biblemap.put("b25_3_65", new husuperobject(25,3,65,"Adj nékik szívbeli konokságot; átkodul reájok."));
husuperobject.biblemap.put("b25_3_66", new husuperobject(25,3,66,"Üldözd haragodban, és veszesd el õket az Úr ege alól!"));
husuperobject.biblemap.put("b25_4_1", new husuperobject(25,4,1,"Jaj! de meghomályosodott az arany, elváltozott a szép színarany, kiszórattak a szent hely kövei minden utcza szegletére."));
husuperobject.biblemap.put("b25_4_2", new husuperobject(25,4,2,"Sionnak drága fiait, a kik becsesebbek valának mint a színarany, cserépedénynek tekintették, a fazekas munkájának."));
husuperobject.biblemap.put("b25_4_3", new husuperobject(25,4,3,"Még a sárkányok is oda nyujtják emlõiket, szoptatják fiaikat; az én népem leánya pedig kegyetlen, mint a struczmadarak a pusztában."));
husuperobject.biblemap.put("b25_4_4", new husuperobject(25,4,4,"A csecsemõ nyelve az ínyéhez tapadt a szomjúság miatt; a kisdedek kenyeret kértek, és nem volt, a ki nyujtott volna nékik."));
husuperobject.biblemap.put("b25_4_5", new husuperobject(25,4,5,"Kik pompás ételeket evének, elpusztulának az utczákon; a kik bíborban neveltetének, a szemétdombot ölelgetik."));
husuperobject.biblemap.put("b25_4_6", new husuperobject(25,4,6,"Bizony nagyobb az én népem leányának bûnhõdése Sodoma bûnhõdésénél, a mely elsülyedt egy pillanat alatt, noha kézzel sem ütöttek felé."));
husuperobject.biblemap.put("b25_4_7", new husuperobject(25,4,7,"Az õ názireusai tisztábbak valának a hónál, fehérebbek a tejnél, testök pirosabb vala, mint a korál, termetök mint a zafir."));
husuperobject.biblemap.put("b25_4_8", new husuperobject(25,4,8,"[De most] feketébb az õ ábrázatjok a koromnál, nem ismerik meg õket az utczákon; bõrük csontjaikhoz ragadt, elszáradt, mint a fa."));
husuperobject.biblemap.put("b25_4_9", new husuperobject(25,4,9,"Jobban jártak, a kik fegyverrel ölettek meg, mint a kik éhen vesztek el; mert azok átveretve multak ki, [ezek pedig] a mezõ termésének hiánya miatt."));
husuperobject.biblemap.put("b25_4_10", new husuperobject(25,4,10,"Irgalmas anyák kezei megfõzték gyermekeiket, hogy azok eledeleik legyenek az én népem leányának romlásakor."));
husuperobject.biblemap.put("b25_4_11", new husuperobject(25,4,11,"Megteljesíté az Úr az õ búsulását, kiöntötte az õ felgerjedt haragját, és tüzet gyújtott a Sionban, és megemésztette annak fundamentomait."));
husuperobject.biblemap.put("b25_4_12", new husuperobject(25,4,12,"Nem hitték a föld királyai, sem a föld kerekségének lakosai, hogy szorongató és ellenség vonuljon be Jeruzsálemnek kapuin."));
husuperobject.biblemap.put("b25_4_13", new husuperobject(25,4,13,"Az õ prófétáinak bûne, az õ papjainak vétke miatt [van ez,] a kik az igazaknak vérét ontották abban."));
husuperobject.biblemap.put("b25_4_14", new husuperobject(25,4,14,"Tántorogtak, mint vakok az utczákon, vérrel bemocskolva, annyira, hogy ruháikat sem érinthették."));
husuperobject.biblemap.put("b25_4_15", new husuperobject(25,4,15,"Távozzatok! tisztátalan! kiáltották azoknak; távozzatok, távozzatok, ne illessetek! Bizony elfutottak, bujdostak is; a pogányok közt ezt mondták: Nem lakhatnak itt sokáig!"));
husuperobject.biblemap.put("b25_4_16", new husuperobject(25,4,16,"Az Úr haragja oszlatta el õket; többé nem tekint reájok, [mivelhogy] a papok orczáját nem tisztelték, a véneken nem könyörültek."));
husuperobject.biblemap.put("b25_4_17", new husuperobject(25,4,17,"Még mikor meg voltunk, elepedve néztek szemeink a hiábavaló segedelem után; esengve várakoztunk olyan népre, a mely nem szabadított meg."));
husuperobject.biblemap.put("b25_4_18", new husuperobject(25,4,18,"Vadásztak lépéseinkre úgy, hogy nem járhattunk a mi utczáinkon; elközelgetett a mi végünk, beteltek a mi napjaink, bizony eljött a mi végünk!"));
husuperobject.biblemap.put("b25_4_19", new husuperobject(25,4,19,"Gyorsabbak valának a mi üldözõink az égnek saskeselyûinél; a hegyeken kergettek minket, a pusztában ólálkodtak utánunk."));
husuperobject.biblemap.put("b25_4_20", new husuperobject(25,4,20,"Orrunk lehellete, az Úr felkentje megfogattaték az õ vermeikben, a kirõl azt mondottuk: az õ árnyékában élünk a pogányok között."));
husuperobject.biblemap.put("b25_4_21", new husuperobject(25,4,21,"Örülj és vígadozz, Edom leánya, a ki Uz földjén lakozol, [mert ]még te rád is rád kerül a pohár, megrészegedel és meztelenkedel."));
husuperobject.biblemap.put("b25_4_22", new husuperobject(25,4,22,"Eltörültetik a te álnokságod, oh Sion leánya, nem fog téged számûzni többé; meglátogatja a te álnokságodat, Edom leánya fölfedi a te bûneidet."));
husuperobject.biblemap.put("b25_5_1", new husuperobject(25,5,1,"Emlékezzél meg Uram, mi esett meg rajtunk; tekintsd meg és lásd meg gyalázatunkat!"));
husuperobject.biblemap.put("b25_5_2", new husuperobject(25,5,2,"A mi örökségünk idegenekre szállt; házaink a jövevényekéi."));
husuperobject.biblemap.put("b25_5_3", new husuperobject(25,5,3,"Apátlan árvák lettünk; anyáink, mint az özvegyek."));
husuperobject.biblemap.put("b25_5_4", new husuperobject(25,5,4,"Vizünket pénzért iszszuk, tûzifánkat áron kapjuk."));
husuperobject.biblemap.put("b25_5_5", new husuperobject(25,5,5,"Nyakunknál fogva hajtatunk; elfáradtunk, nincsen nyugtunk."));
husuperobject.biblemap.put("b25_5_6", new husuperobject(25,5,6,"Égyiptomnak adtunk kezet, az assziroknak, hogy jóllakjunk kenyérrel."));
husuperobject.biblemap.put("b25_5_7", new husuperobject(25,5,7,"Apáink vétkeztek; nincsenek; mi hordozzuk vétkeiket."));
husuperobject.biblemap.put("b25_5_8", new husuperobject(25,5,8,"Szolgák uralkodnak rajtunk; nincs a ki megszabadítson kezökbõl."));
husuperobject.biblemap.put("b25_5_9", new husuperobject(25,5,9,"Életünk veszélyeztetésével szerezzük kenyerünket a pusztában levõ fegyver miatt."));
husuperobject.biblemap.put("b25_5_10", new husuperobject(25,5,10,"Bõrünk, mint a kemencze, megfeketedett az éhség lázától."));
husuperobject.biblemap.put("b25_5_11", new husuperobject(25,5,11,"Az asszonyokat meggyalázták Sionban, a szûzeket Júda városaiban."));
husuperobject.biblemap.put("b25_5_12", new husuperobject(25,5,12,"A fejedelmeket kezökkel akasztották fel; a vének orczáit nem becsülik."));
husuperobject.biblemap.put("b25_5_13", new husuperobject(25,5,13,"Az ifjak a kézi malmot hordozzák, és a gyermekek a fahordásban botlanak el."));
husuperobject.biblemap.put("b25_5_14", new husuperobject(25,5,14,"A vének eltüntek a kapuból, [megszüntek] az ifjak énekelni."));
husuperobject.biblemap.put("b25_5_15", new husuperobject(25,5,15,"Oda van a mi szívünk öröme, gyászra fordult a mi körtánczunk."));
husuperobject.biblemap.put("b25_5_16", new husuperobject(25,5,16,"Elesett a mi fejünknek koronája, jaj most nékünk mert vétkeztünk!"));
husuperobject.biblemap.put("b25_5_17", new husuperobject(25,5,17,"Ezért lett beteg a mi szívünk, ezekért homályosodtak meg a mi szemeink;"));
husuperobject.biblemap.put("b25_5_18", new husuperobject(25,5,18,"A Sion hegyéért, hogy elpusztult; rókák futkosnak azon!"));
husuperobject.biblemap.put("b25_5_19", new husuperobject(25,5,19,"Te Uram örökké megmaradsz; a te királyi széked nemzedékrõl nemzedékre!"));
husuperobject.biblemap.put("b25_5_20", new husuperobject(25,5,20,"Miért feledkezel el örökre mi rólunk? [miért] hagysz el minket hosszú idõre?"));
husuperobject.biblemap.put("b25_5_21", new husuperobject(25,5,21,"Téríts vissza Uram magadhoz és visszatérünk; újítsd meg a mi napjainkat, mint régen."));
husuperobject.biblemap.put("b25_5_22", new husuperobject(25,5,22,"Mert bizony-bizony megvetettél minket; megharagudtál ránk felettébb!"));

}

}