package hu.bible;

public class hubible31 {

public hubible31() {

putmapbible31();

}

private void putmapbible31() {

husuperobject.biblemap.put("b31_1_1", new husuperobject(31,1,1,"Abdiás látása. Így szól az Úr Isten Edomról: Hírt hallottunk az Úrtól! És hírnök küldetett a népekhez: keljetek fel és támadjunk reá haddal!"));
husuperobject.biblemap.put("b31_1_2", new husuperobject(31,1,2,"Ímé, kicsinynyé tettelek a népek között; felettébb útálatos vagy."));
husuperobject.biblemap.put("b31_1_3", new husuperobject(31,1,3,"Szíved kevélysége csalt meg téged, ki szikla-hasadékokban lakozol, kinek lakóhelye magasan van, a ki mondja az õ szívében: Ki vonhatna le engem a síkra?!"));
husuperobject.biblemap.put("b31_1_4", new husuperobject(31,1,4,"Ha oly magasra szállnál is, mint a sas, és ha a csillagok közé raknád is fészkedet: onnan is levonnálak, ezt mondja az Úr."));
husuperobject.biblemap.put("b31_1_5", new husuperobject(31,1,5,"Ha tolvajok törnének reád, avagy éjjeli rablók, (hogy elpusztíttattál!), nem [annyit] lopnának-é, [a mennyi] elegendõ?! Ha szõlõszedõk jönnének reád, nem hagynának-é gerezdeket?!"));
husuperobject.biblemap.put("b31_1_6", new husuperobject(31,1,6,"Mennyire kifosztogatták Ézsaut; felkutatták rejtett kincseit!"));
husuperobject.biblemap.put("b31_1_7", new husuperobject(31,1,7,"A határig ûznek ki összes frigytársaid; megcsalnak, levernek szövetségeseid; kenyeredet tõrül vetik alád. Nincsen benne okosság!"));
husuperobject.biblemap.put("b31_1_8", new husuperobject(31,1,8,"Azon a napon, ezt mondja az Úr, nem vesztem-é ki a bölcseket Edomból, és az értelmet az Ézsau hegyérõl?!"));
husuperobject.biblemap.put("b31_1_9", new husuperobject(31,1,9,"És megrémülnek a te vitézeid, oh Témán! hogy kiirtassék mindenki az Ézsau hegyérõl az öldöklés által."));
husuperobject.biblemap.put("b31_1_10", new husuperobject(31,1,10,"A Jákób öcséd ellen [elkövetett] erõszakért szégyen borul reád, és kivágatol mindörökre!"));
husuperobject.biblemap.put("b31_1_11", new husuperobject(31,1,11,"A mikor vele szembeálltál; a mikor serege idegenek rabjává lett, és idegenek törtek be kapuján és Jeruzsálemre sorsot vetettek: olyan voltál te is, mint bármelyik közülök."));
husuperobject.biblemap.put("b31_1_12", new husuperobject(31,1,12,"De ne gyönyörködjél öcsédnek napján, az õ szerencsétlenségének napján; és ne örvendj a Júda fiain az õ veszedelmök napján, és ne kérkedjél a szorongattatás napján."));
husuperobject.biblemap.put("b31_1_13", new husuperobject(31,1,13,"Ne törj be népem kapuján nyomorúságuk napján; ne gyönyörködjél te is a baján nyomorúsága napján; és ne nyúlj az õ jószágához nyomorúsága napján;"));
husuperobject.biblemap.put("b31_1_14", new husuperobject(31,1,14,"A résre se állj fel menekülõit elveszíteni; és ne áruld el az õ megmaradottait a szorongattatás napján!"));
husuperobject.biblemap.put("b31_1_15", new husuperobject(31,1,15,"Mert közel van az Úrnak napja minden népek ellen. A mint cselekedtél, úgy cselekesznek veled; a mit te fizettél, visszaszáll fejedre."));
husuperobject.biblemap.put("b31_1_16", new husuperobject(31,1,16,"Mert a mint ti ittatok szent hegyemen, úgy isznak szüntelen az összes népek; bizony isznak és hörpengetnek, és olyanok lesznek, mintha nem lettek volna."));
husuperobject.biblemap.put("b31_1_17", new husuperobject(31,1,17,"De a Sion hegyén szabadulás lészen, és szentté lészen az, és a Jákób háza birtokba veszi az õ örökségét."));
husuperobject.biblemap.put("b31_1_18", new husuperobject(31,1,18,"És a Jákób háza tûz lészen, és a József háza láng; az Ézsau háza pedig pozdorja; és meggyújtják és megemésztik õket, és nem marad meg senki Ézsau házából, mert az Úr szólott."));
husuperobject.biblemap.put("b31_1_19", new husuperobject(31,1,19,"A déliek örökség szerint bírják az Ézsau hegyét, a síkon lakók pedig a Filiszteusokat. És örökség szerint bírják az Efraim mezõit és Samaria mezõit; Benjámin pedig a Gileádot."));
husuperobject.biblemap.put("b31_1_20", new husuperobject(31,1,20,"Izráel fiainak ez a számûzött serege azokat, a melyek a Kananeusoké, mind Sarfátig; a jeruzsálemi számûzöttek pedig, a kik Szefarádban vannak, elfoglalják majd a déli városokat."));
husuperobject.biblemap.put("b31_1_21", new husuperobject(31,1,21,"És a Sion hegyére szabadítók mennek fel, hogy megítéljék az Ézsau hegyét; és az Úré lesz a királyság."));

}

}