package hu.bible;

public class hubible32 {

public hubible32() {

putmapbible32();

}

private void putmapbible32() {

husuperobject.biblemap.put("b32_1_1", new husuperobject(32,1,1,"És lõn az Úrnak szava Jónáshoz, az Amittai fiához, mondván:"));
husuperobject.biblemap.put("b32_1_2", new husuperobject(32,1,2,"Kelj fel, menj Ninivébe, a nagy városba, és kiálts ellene, mert gonoszságuk felhatolt elémbe!"));
husuperobject.biblemap.put("b32_1_3", new husuperobject(32,1,3,"És felkele Jónás, hogy Tarsisba szaladna az Úr elõl. Leméne azért Jáfóba, és talála ott egy hajót, a mely méne Tarsisba, és megadván a hajóbért, beszálla abba, hogy Tarsisba menne velök az Úr színe elõl."));
husuperobject.biblemap.put("b32_1_4", new husuperobject(32,1,4,"Az Úr pedig nagy szelet bocsáta a tengerre, és nagy vihar lõn a tengeren, és a hajó már-már töredezik vala."));
husuperobject.biblemap.put("b32_1_5", new husuperobject(32,1,5,"Megfélemlének azért a hajósok és kiáltának, kiki az õ istenéhez, és a hajóban lévõ holmit a tengerbe hányák, hogy könnyítsenek magukon. Jónás pedig leméne a hajó aljába, és lefeküdt és elaludt."));
husuperobject.biblemap.put("b32_1_6", new husuperobject(32,1,6,"De hozzáméne a kormányos mester, és mondá néki: Mi lelt, te nagy alvó? Kelj fel, kiálts a te Istenedhez; hát ha gondol velünk az Isten, és nem veszünk el!"));
husuperobject.biblemap.put("b32_1_7", new husuperobject(32,1,7,"Egymásnak pedig ezt mondák: Jertek, vessünk sorsot, hogy megtudhassuk: mi miatt van rajtunk e veszedelem? És sorsot vetének, és a sors Jónásra esék."));
husuperobject.biblemap.put("b32_1_8", new husuperobject(32,1,8,"Mondák azért néki: Kérünk, beszéld el nékünk: mi miatt van rajtunk e veszedelem? Mi a te foglalkozásod és honnan jösz? Melyik a te hazád és miféle népbõl való vagy te?"));
husuperobject.biblemap.put("b32_1_9", new husuperobject(32,1,9,"És monda nékik: Héber vagyok én, és az Urat, az egek Istenét félem én, a ki a tengert és a szárazt teremtette."));
husuperobject.biblemap.put("b32_1_10", new husuperobject(32,1,10,"És megfélemlének az emberek nagy félelemmel, és mondák néki: Mit cselekedtél? Mert megtudták azok az emberek, hogy az Úr színe elõl fut, mivelhogy elbeszélé nékik."));
husuperobject.biblemap.put("b32_1_11", new husuperobject(32,1,11,"Mondák azután néki: Mit cselekedjünk veled, hogy a tenger megcsendesedjék ellenünk? Mert a tenger háborgása növekedék."));
husuperobject.biblemap.put("b32_1_12", new husuperobject(32,1,12,"Õ pedig monda nékik: Fogjatok meg és vessetek engem a tengerbe, és megcsendesedik a tenger ellenetek; mert tudom én, hogy miattam van ez a nagy vihar rajtatok."));
husuperobject.biblemap.put("b32_1_13", new husuperobject(32,1,13,"És erõlködtek azok az emberek, hogy visszajussanak a szárazra; de nem tudtak, mert a tenger háborgása növekedék ellenök."));
husuperobject.biblemap.put("b32_1_14", new husuperobject(32,1,14,"Kiáltának azért az Úrhoz, és mondák: Kérünk Uram, kérünk, ne veszszünk el ez ember lelkéért, és ne háríts reánk ártatlan vért; mert te, Uram, úgy cselekedtél, a mint akartad!"));
husuperobject.biblemap.put("b32_1_15", new husuperobject(32,1,15,"És felragadák Jónást és beveték õt a tengerbe, és megszûnék a tenger az õ háborgásától."));
husuperobject.biblemap.put("b32_1_16", new husuperobject(32,1,16,"Azok az emberek pedig nagy félelemmel félék az Urat, és áldozattal áldozának az Úrnak, és fogadásokat fogadának."));
husuperobject.biblemap.put("b32_2_1", new husuperobject(32,2,1,"Az Úr pedig egy nagy halat rendelt, hogy benyelje Jónást. És lõn Jónás a halnak gyomrában három nap és három éjjel."));
husuperobject.biblemap.put("b32_2_2", new husuperobject(32,2,2,"És könyörge Jónás az Úrnak, az õ Istenének a halnak gyomrából."));
husuperobject.biblemap.put("b32_2_3", new husuperobject(32,2,3,"És mondá: Nyomorúságomban az Úrhoz kiálték és meghallgata engem; a Seol torkából sikolték [és] meghallád az én szómat."));
husuperobject.biblemap.put("b32_2_4", new husuperobject(32,2,4,"Mert mélységbe vetettél engem, tenger közepébe, és körülfogott engem a víz; örvényeid és habjaid mind átmentek rajtam!"));
husuperobject.biblemap.put("b32_2_5", new husuperobject(32,2,5,"És én mondám: Elvettettem a te szemeid elõl; vajha láthatnám még szentséged templomát!"));
husuperobject.biblemap.put("b32_2_6", new husuperobject(32,2,6,"Körülvettek engem a vizek lelkemig, mély ár kerített be engem, hinár szövõdött fejemre."));
husuperobject.biblemap.put("b32_2_7", new husuperobject(32,2,7,"A hegyek alapjáig sülyedtem alá; [bezáródtak] a föld závárjai felettem örökre! Mindazáltal kiemelted éltemet a mulásból, oh Uram, Istenem!"));
husuperobject.biblemap.put("b32_2_8", new husuperobject(32,2,8,"Mikor elcsüggedt bennem az én lelkem, megemlékeztem az Úrról, és bejutott az én könyörgésem te hozzád, a te szentséged templomába."));
husuperobject.biblemap.put("b32_2_9", new husuperobject(32,2,9,"A kik hiú bálványokra ügyelnek, elhagyják boldogságukat;"));
husuperobject.biblemap.put("b32_2_10", new husuperobject(32,2,10,"De én hálaadó szóval áldozom néked; megadom, a mit fogadtam. Az Úré a szabadítás. [ (Jonah 2:11) És szóla az Úr a halnak, és kiveté Jónást a szárazra. ]"));
husuperobject.biblemap.put("b32_3_1", new husuperobject(32,3,1,"És lõn az Úrnak szava Jónáshoz másodszor [is,] mondván:"));
husuperobject.biblemap.put("b32_3_2", new husuperobject(32,3,2,"Kelj fel, menj Ninivébe, a nagy városba, és hirdesd néki azt a beszédet, a mit én parancsolok néked."));
husuperobject.biblemap.put("b32_3_3", new husuperobject(32,3,3,"És felkele Jónás, és elméne Ninivébe az Úr szava szerint. Ninive pedig nagy városa vala Istennek, három napi járó [föld.]"));
husuperobject.biblemap.put("b32_3_4", new husuperobject(32,3,4,"És kezde Jónás bemenni a városba egy napi járóra, és kiálta és monda: Még negyven nap, és elpusztul Ninive!"));
husuperobject.biblemap.put("b32_3_5", new husuperobject(32,3,5,"A niniveiek pedig hivének Istenben, és bõjtöt hirdetének, és nagyjaiktól fogva kicsinyeikig zsákba öltözének."));
husuperobject.biblemap.put("b32_3_6", new husuperobject(32,3,6,"És eljuta a beszéd Ninive királyához, és felkele királyi székébõl, és leveté magáról az õ királyi ruháját, és zsákba borítkozék, és üle a porba."));
husuperobject.biblemap.put("b32_3_7", new husuperobject(32,3,7,"És kiáltának és szólának Ninivében, a királynak és fõembereinek akaratából, mondván: Emberek és barmok, ökrök és juhok: semmit meg ne kóstoljanak, ne legeljenek és vizet se igyanak!"));
husuperobject.biblemap.put("b32_3_8", new husuperobject(32,3,8,"Hanem öltözzenek zsákba az emberek és barmok, és kiáltsanak az Istenhez erõsen, és térjen meg kiki az õ gonosz útáról és az erõszakosságból, a mely az õ kezökben van!"));
husuperobject.biblemap.put("b32_3_9", new husuperobject(32,3,9,"Ki tudja? talán visszatér és megengesztelõdik az Isten és elfordul haragjának búsulásától, és nem veszünk el!"));
husuperobject.biblemap.put("b32_3_10", new husuperobject(32,3,10,"És látá Isten az õ cselekedeteiket, hogy megtértek az õ gonosz útjokról: és megbáná az Isten azt a gonoszt, a melyrõl mondá, hogy végrehajtja rajtok, és nem hajtá végre."));
husuperobject.biblemap.put("b32_4_1", new husuperobject(32,4,1,"És igen rossznak látszék ez Jónás elõtt, és megharaguvék."));
husuperobject.biblemap.put("b32_4_2", new husuperobject(32,4,2,"Könyörge azért az Úrhoz, és mondá: Kérlek, Uram! Avagy nem ez vala-é az én mondásom, mikor még az én hazámban valék? azért siettem, hogy Tarsisba futnék, mert tudtam, hogy te irgalmas és kegyelmes Isten vagy, nagy türelmû és nagy irgalmasságú és a gonosz miatt [is] bánkódó."));
husuperobject.biblemap.put("b32_4_3", new husuperobject(32,4,3,"Most azért Uram, vedd el, kérlek, az én lelkemet én tõlem, mert jobb meghalnom, mintsem élnem!"));
husuperobject.biblemap.put("b32_4_4", new husuperobject(32,4,4,"Az Úr pedig mondá: Avagy méltán haragszol-é?"));
husuperobject.biblemap.put("b32_4_5", new husuperobject(32,4,5,"Majd kiméne Jónás a városból, és üle a város keleti része felõl, és csinála ott magának hajlékot, és üle az alatt az árnyékban, a míg megláthatná, mi lészen a városból?"));
husuperobject.biblemap.put("b32_4_6", new husuperobject(32,4,6,"Az Úr Isten pedig egy tököt rendele, és felnöve az Jónás fölé, hogy árnyékot tartson feje fölött és megoltalmazza õt [a hévség] bántásától. És nagy örömmel örvendezék Jónás a tök miatt."));
husuperobject.biblemap.put("b32_4_7", new husuperobject(32,4,7,"De másnapra férget rendele az Isten hajnal-költekor, és megszúrá [az] a tököt, és elszárada."));
husuperobject.biblemap.put("b32_4_8", new husuperobject(32,4,8,"És lõn napköltekor, hogy tikkasztó keleti szelet rendele Isten, és a nap rátûzött a Jónás fejére, és õ elbágyada. Kiváná azért magának a halált, és monda: Jobb halnom, mint élnem!"));
husuperobject.biblemap.put("b32_4_9", new husuperobject(32,4,9,"És monda az Isten Jónásnak: Avagy méltán haragszol-é a tök miatt? És monda: Méltán haragszom, mind halálig!"));
husuperobject.biblemap.put("b32_4_10", new husuperobject(32,4,10,"Az Úr pedig monda: Te szánod a tököt, a melyért nem fáradtál és [a melyet] nem neveltél, a mely egy éjjel támadt és más éjjel elveszett:"));
husuperobject.biblemap.put("b32_4_11", new husuperobject(32,4,11,"Én pedig ne szánjam Ninivét, a nagy várost, a melyben több van tizenkétszer tízezer embernél, a kik nem tudnak különbséget tenni jobb- és balkezük között, és barom is sok van?!"));

}

}