package hu.bible;

public class hubible34 {

public hubible34() {

putmapbible34();

}

private void putmapbible34() {

husuperobject.biblemap.put("b34_1_1", new husuperobject(34,1,1,"Ninive terhe; az elkosi Náhum látásának könyve."));
husuperobject.biblemap.put("b34_1_2", new husuperobject(34,1,2,"Buzgón szeretõ és bosszúálló Isten az Úr, bosszúálló az Úr, és telve haraggal; bosszút áll az Úr az õ ellenségein, és [haragot] tartó az õ gyûlölõi ellen."));
husuperobject.biblemap.put("b34_1_3", new husuperobject(34,1,3,"Hosszútûrõ az Úr és nagyhatalmú, és nem hagy büntetlenül. Szélvészben és viharban van az Úrnak útja, és lábainak pora a felhõ."));
husuperobject.biblemap.put("b34_1_4", new husuperobject(34,1,4,"Megfeddi a tengert és kiapasztja azt, és minden folyamot kiszáraszt. Elfonnyad a Básán és a Kármel, és a Libánon virága elfonnyad."));
husuperobject.biblemap.put("b34_1_5", new husuperobject(34,1,5,"A hegyek reszketnek elõtte, és a halmok szétmállanak. Tekintetétõl megrendül a föld, és a világ, és minden, a mi rajta él."));
husuperobject.biblemap.put("b34_1_6", new husuperobject(34,1,6,"Ki állhatna meg haragja elõtt, és ki birhatná ki búsulásának tüzét? Heve szétfoly, mint a láng, és a kõszálak is szétporlanak tõle."));
husuperobject.biblemap.put("b34_1_7", new husuperobject(34,1,7,"Jó az Úr, erõsség a szorongatás idején, és õ ismeri a benne bízókat."));
husuperobject.biblemap.put("b34_1_8", new husuperobject(34,1,8,"De gáttörõ árvízként pusztít annak helyén, és gyûlölõit setétség üldözi."));
husuperobject.biblemap.put("b34_1_9", new husuperobject(34,1,9,"Mit koholtok az Úr ellen? Elpusztít õ, nem lészen kétszer veszedelem."));
husuperobject.biblemap.put("b34_1_10", new husuperobject(34,1,10,"Ha annyira összefonódnak is, mint a tüskebokrok, és olyan ázottak is, mint az italuk: megemésztetnek, mint a teljesen megszáradt tarló."));
husuperobject.biblemap.put("b34_1_11", new husuperobject(34,1,11,"Belõled származott, a ki gonoszt koholt az Úr ellen, a ki álnokságot tanácsolt."));
husuperobject.biblemap.put("b34_1_12", new husuperobject(34,1,12,"Így szól az Úr: Ha teljes erõben és sokan vannak is, mégis levágatnak és elenyésznek. Megaláztalak téged, de nem foglak többé megalázni."));
husuperobject.biblemap.put("b34_1_13", new husuperobject(34,1,13,"Most már leveszem rólad az õ igáját, és bilincseidet leszaggatom."));
husuperobject.biblemap.put("b34_1_14", new husuperobject(34,1,14,"Felõled pedig azt rendeli az Úr: Nevednek ne támadjon többé magva; isteneid házából kivesztem a faragott és öntött képeket; megásom sírodat, mert becstelen vagy."));
husuperobject.biblemap.put("b34_1_15", new husuperobject(34,1,15,"Ímé a hegyeken örömhírhozónak lábai! Békességet hirdet. Ünnepeld Júda ünnepeidet, fizesd le fogadásaidat; mert nem vonul át rajtad többé a semmirekellõ; mindenestõl kiirtatott."));
husuperobject.biblemap.put("b34_2_1", new husuperobject(34,2,1,"Pusztító jön fel ellened; õrizd a várat, nézzed az útat, erõsítsd derekadat, keményítsd meg erõdet igen."));
husuperobject.biblemap.put("b34_2_2", new husuperobject(34,2,2,"Mert helyreállítja az Úr Jákób büszkeségét, mint Izráel büszkeségét; mert rablók rabolták ki õket, csemetéiket pedig kivágták."));
husuperobject.biblemap.put("b34_2_3", new husuperobject(34,2,3,"Vitézeinek pajzsa veres, katonáinak [ruházata] bíborszínû, [hadi]szekere aczéltûzben [ragyog] fegyverkezése napján, és a dárdákat rengetik."));
husuperobject.biblemap.put("b34_2_4", new husuperobject(34,2,4,"Az utczákon robognak a szekerek, összeütköznek a piaczokon; tekintetök mint a fáklyák, futkosnak mint a villámok."));
husuperobject.biblemap.put("b34_2_5", new husuperobject(34,2,5,"Emlegeti [vitézlõ] hõseit; ingadoznak lépéseikben, sietnek a kõfalra, és felállíttatik a védõsáncz."));
husuperobject.biblemap.put("b34_2_6", new husuperobject(34,2,6,"A folyóvizek kapui megnyílnak, és a palota megrendül."));
husuperobject.biblemap.put("b34_2_7", new husuperobject(34,2,7,"De elvégeztetett: felfedik, elvitetik, és szolgálói keseregnek, mint nyögõ galambok, mellöket vervén."));
husuperobject.biblemap.put("b34_2_8", new husuperobject(34,2,8,"Pedig Ninive olyan, mint a [bõ]vizû tó, eleitõl fogva: mégis futnak õk. Álljatok meg, álljatok meg! De vissza se tekint senki."));
husuperobject.biblemap.put("b34_2_9", new husuperobject(34,2,9,"Raboljatok ezüstöt, raboljatok aranyat; száma sincs a rejtett kincseknek, gazdag minden drága edényben."));
husuperobject.biblemap.put("b34_2_10", new husuperobject(34,2,10,"Feldúlva, széthányva, kifosztatva! Szíve megolvadt, a térdek reszketnek, fájdalom van egész derekában, és mindnyájok arcza elvesztette pirosságát."));
husuperobject.biblemap.put("b34_2_11", new husuperobject(34,2,11,"Hol van az oroszlánok tanyája, és az oroszlán-kölyköknek ama legelõje, a hová járt a nõstény- és hímoroszlán, az oroszlán-kölyök, és nem volt a ki elriaszsza?"));
husuperobject.biblemap.put("b34_2_12", new husuperobject(34,2,12,"Az oroszlán torkig valót ragadozott kölykeinek, és fojtogatott nõstényeinek, és megtöltötte barlangjait zsákmánynyal, tanyáit pedig prédával."));
husuperobject.biblemap.put("b34_2_13", new husuperobject(34,2,13,"Ímé rád [török,] azt mondja a Seregek Ura, és füstté égetem szekereit. Oroszlán-kölykeidet kard emészti meg, és kiirtom e földrõl zsákmányodat, és nem hallatszik többé követeidnek szava."));
husuperobject.biblemap.put("b34_3_1", new husuperobject(34,3,1,"Jaj a vérszopó városnak! Mindenestõl hazug [és] erõszakkal telve, [és] nem szûnik rabolni."));
husuperobject.biblemap.put("b34_3_2", new husuperobject(34,3,2,"Ostor-csattogás, kerék-zörgés zaja; dobogó ló, robogó szekér;"));
husuperobject.biblemap.put("b34_3_3", new husuperobject(34,3,3,"Törtetõ lovag, kardok villogása, dárda villanása, sebesült tömegek, holtak sokasága, nincs számok az elesetteknek; megbotlanak hulláikban."));
husuperobject.biblemap.put("b34_3_4", new husuperobject(34,3,4,"A szép parázna sok paráznaságáért, a hitetésnek mesternõje miatt, a ki népeket ejtett meg paráznaságával, és nemzetségeket bûbájaival:"));
husuperobject.biblemap.put("b34_3_5", new husuperobject(34,3,5,"Ímé, rád [török,] azt mondja a Seregek Ura, és orczádra fordítom ruhádnak alját, és népeknek mutatom meg meztelenségedet, és országoknak gyalázatodat."));
husuperobject.biblemap.put("b34_3_6", new husuperobject(34,3,6,"Rútságot hányatok rád, és gyalázattal illetlek téged, és olyanná teszlek, mint a kit csudálnak."));
husuperobject.biblemap.put("b34_3_7", new husuperobject(34,3,7,"És mind, a ki meglát, elmenekül tõled, és ezt mondja: Elpusztult Ninive! Ki bánkódik rajta? Hol keressek néked vígasztalókat?"));
husuperobject.biblemap.put("b34_3_8", new husuperobject(34,3,8,"Avagy jobb vagy-é Nó-Amonnál, a mely a folyamoknál fekszik, vizek veszik körül; a melynek tenger a sáncza, tenger a kõfala?"));
husuperobject.biblemap.put("b34_3_9", new husuperobject(34,3,9,"Kús volt erõssége meg Égyiptom, és száma sem volt [annak.] Puth és Libia is segítõid voltak;"));
husuperobject.biblemap.put("b34_3_10", new husuperobject(34,3,10,"De ez is számkivetésbe, fogságba jutott; kisdedeik is falhoz verettek minden utcza sarkán; fõembereire sorsot vetettek, és nagyjait mind bilincsekbe verték."));
husuperobject.biblemap.put("b34_3_11", new husuperobject(34,3,11,"Te is megrészegedel, elfeledtté leszel; te is keresel majd menedéket a gyûlölködõ elõl."));
husuperobject.biblemap.put("b34_3_12", new husuperobject(34,3,12,"Minden erõsséged olyan, mint a zsenge gyümölcsû fügefa; ha megrázatnak, az evõ szájába hullnak."));
husuperobject.biblemap.put("b34_3_13", new husuperobject(34,3,13,"Ímé, a te néped asszonynép te benned, földednek kapui tárva kitárulnak gyûlölõidnek, tûz emészti meg záraidat!"));
husuperobject.biblemap.put("b34_3_14", new husuperobject(34,3,14,"Meríts magadnak ostromhoz való vizet, javítsd erõsségeidet; menj be a sárba, taposd az agyagot, javítsd a tégla-vetõt!"));
husuperobject.biblemap.put("b34_3_15", new husuperobject(34,3,15,"Legott tûz emészt meg téged, fegyver irt ki téged, megemészt, mint a szöcske; szaporodjál bár, mint a szöcske, szaporodjál bár mint a sáska!"));
husuperobject.biblemap.put("b34_3_16", new husuperobject(34,3,16,"Többen voltak kalmáraid, mint az égnek csillagai: a szöcske csapong és elrepül!"));
husuperobject.biblemap.put("b34_3_17", new husuperobject(34,3,17,"Fejedelmeid mint a sáska, vezéreid mint a tücsök-raj; hideg idõkben gyepûkben tanyáz, napkeletkor pedig elrepül, és helye sem tudható meg, hol volt."));
husuperobject.biblemap.put("b34_3_18", new husuperobject(34,3,18,"Szunnyadoznak pásztoraid, Assiria királya, feküsznek [vitézlõ] hõseid; néped a hegyeken széledez, és nincsen, a ki összegyûjtse."));
husuperobject.biblemap.put("b34_3_19", new husuperobject(34,3,19,"Nincs enyhítés a te sebedre, gyógyíthatatlan a te nyavalyád. A kik híredet hallják, mind tapsolnak feletted, mert kire nem hatott volna ki a te gonoszságod soha?"));

}

}