package hu.bible;

public class hubible35 {

public hubible35() {

putmapbible35();

}

private void putmapbible35() {

husuperobject.biblemap.put("b35_1_1", new husuperobject(35,1,1,"A teher, a melyet Habakuk próféta látott."));
husuperobject.biblemap.put("b35_1_2", new husuperobject(35,1,2,"Meddig kiáltok még oh Uram, és nem hallgatsz meg! Kiáltozom hozzád az erõszak miatt, és nem szabadítasz meg!"));
husuperobject.biblemap.put("b35_1_3", new husuperobject(35,1,3,"Miért láttatsz velem hamisságot, és szemléltetsz nyomorgatást? Pusztítás és erõszak van elõttem, per keletkezik és versengés támad!"));
husuperobject.biblemap.put("b35_1_4", new husuperobject(35,1,4,"Azért inog a törvény, és nem érvényesül az igaz ítélet; mert gonosz hálózza be az igazat, azért származik hamis ítélet!"));
husuperobject.biblemap.put("b35_1_5", new husuperobject(35,1,5,"Nézzetek szét a népek között, vizsgálódjatok és csodálkozással csodálkozzatok, mert oly dolgot cselekszem a ti napjaitokban, mit el sem hinnétek, ha beszélnék!"));
husuperobject.biblemap.put("b35_1_6", new husuperobject(35,1,6,"Mert ímé, feltámasztom a Káldeusokat, a kegyetlen és vakmerõ nemzetet, a mely eljárja a földet széltében, hogy hajlékokat foglaljon el, a melyek nem az övéi."));
husuperobject.biblemap.put("b35_1_7", new husuperobject(35,1,7,"Rettenetes és iszonyatos ez, maga szerzi törvényét és hatalmát."));
husuperobject.biblemap.put("b35_1_8", new husuperobject(35,1,8,"És lovai serényebbek a párduczoknál, és gyorsabbak az estveli farkasoknál, és elõtörtetnek az õ lovasai; és az õ lovasai messzirõl jõnek, repülnek, mint a zsákmányra sietõ keselyû."));
husuperobject.biblemap.put("b35_1_9", new husuperobject(35,1,9,"Mindnyája ragadományért jön, arczuk elõre néz, és annyi foglyot gyûjt, mint a föveny."));
husuperobject.biblemap.put("b35_1_10", new husuperobject(35,1,10,"Kaczag ez a királyokon, és a fejedelmek néki nevetség, minden erõsséget csak nevet, töltést emel és megostromolja azt."));
husuperobject.biblemap.put("b35_1_11", new husuperobject(35,1,11,"Majd tovaszáll viharként és elvonul és bûnbe esik; õ kinek istene az õ hatalma."));
husuperobject.biblemap.put("b35_1_12", new husuperobject(35,1,12,"Avagy nem te vagy-é Uram, öröktõl fogva az én Istenem, Szentem? Nem veszünk el! Ítéletre rendelted õt, oh Uram, fenyítõül választottad õt, én erõsségem!"));
husuperobject.biblemap.put("b35_1_13", new husuperobject(35,1,13,"Tisztábbak szemeid, hogysem nézhetnéd a gonoszt, és a nyomorgatást nem szemlélheted: miért szemléled [hát] a hitszegõket? [és] hallgatsz, mikor a gonosz elnyeli a nálánál igazabbat?!"));
husuperobject.biblemap.put("b35_1_14", new husuperobject(35,1,14,"Olyanokká teszed az embert, mint a tenger halai, és mint a csúszómászó állatok, a melyeknek nincsen vezérök?"));
husuperobject.biblemap.put("b35_1_15", new husuperobject(35,1,15,"Mindnyáját kivonsza horoggal, gyalomjába keríti, és hálójába takarítja be õket; ezért örül és vígad."));
husuperobject.biblemap.put("b35_1_16", new husuperobject(35,1,16,"Ezért áldozik gyalomjának, és füstöl az õ hálójának, mert ezekkel kövér az õ része, és zsíros az õ eledele."));
husuperobject.biblemap.put("b35_1_17", new husuperobject(35,1,17,"Vajjon azért ürítheti-é gyalmát, és szüntelen ölheti-é a nemzeteket kímélet nélkül?!"));
husuperobject.biblemap.put("b35_2_1", new husuperobject(35,2,1,"Õrhelyemre állok, és megállok a bástyán, és vigyázok, hogy lássam, mit szól hozzám, és mit feleljek én panaszom dolgában."));
husuperobject.biblemap.put("b35_2_2", new husuperobject(35,2,2,"És felele nékem az Úr, és mondá: Írd fel [e] látomást, és vésd táblákra, hogy könnyen olvasható legyen."));
husuperobject.biblemap.put("b35_2_3", new husuperobject(35,2,3,"Mert e látomás bizonyos idõre [szól,] de vége felé siet és meg nem csal; ha késik is, bízzál benne; mert eljön, el fog jõni, nem marad el!"));
husuperobject.biblemap.put("b35_2_4", new husuperobject(35,2,4,"Ímé, felfuvalkodott, nem igaz õ benne az õ lelke; az igaz pedig az õ hite által él."));
husuperobject.biblemap.put("b35_2_5", new husuperobject(35,2,5,"És bizony, a bor is megcsal: felgerjed a férfiú és nincsen nyugalma; õ, a ki tátja száját, mint a Seol, és olyan õ, mint a halál: telhetetlen, és magához ragad minden népet és magához csatol minden nemzetet."));
husuperobject.biblemap.put("b35_2_6", new husuperobject(35,2,6,"Avagy nem költenek-é ezek mindnyájan példabeszédet róla, és találós mesét reá, mondván: Jaj annak, a ki rakásra gyûjti, a mi nem övé! De meddig? És a ki adóssággal terheli magát!"));
husuperobject.biblemap.put("b35_2_7", new husuperobject(35,2,7,"Avagy nem támadnak-é hirtelen, a kik téged mardossanak, és nem serkennek-é fel, a kik háborgassanak téged? És zsákmányul esel nékik."));
husuperobject.biblemap.put("b35_2_8", new husuperobject(35,2,8,"Mivelhogy kifosztogattál sok nemzetet, kifosztanak téged mind a többi népek az emberek véréért, és az országok, városok és minden bennök lakozók ellen való erõszaktételért."));
husuperobject.biblemap.put("b35_2_9", new husuperobject(35,2,9,"Jaj annak, a ki bûnös szerzeményt szerez házának, hogy magasra rakhassa fészkét, hogy megszabadulhasson a gonosz hatalma elõl."));
husuperobject.biblemap.put("b35_2_10", new husuperobject(35,2,10,"Házadnak gyalázatára tervezted a sok nép kiirtását, és bûnössé tetted lelkedet."));
husuperobject.biblemap.put("b35_2_11", new husuperobject(35,2,11,"Mert a kõ [is ellened] kiált a falból, és a gerenda a fa-alkotmányból visszhangoz néki."));
husuperobject.biblemap.put("b35_2_12", new husuperobject(35,2,12,"Jaj annak, a ki várost épít vérengzéssel, és a ki várat emel álnoksággal."));
husuperobject.biblemap.put("b35_2_13", new husuperobject(35,2,13,"Avagy ímé, nem a Seregek Urától van-é ez, hogy a népek tûznek építenek, és a nemzetek a hiábavalóságnak fáradoznak?"));
husuperobject.biblemap.put("b35_2_14", new husuperobject(35,2,14,"Mert az Úr dicsõségének ismeretével betelik a föld, a miképen a folyamok megtöltik a tengert."));
husuperobject.biblemap.put("b35_2_15", new husuperobject(35,2,15,"Jaj annak, a ki megitatja felebarátját, epédet keverve belé, hogy megrészegítsed õt, hogy láthassad az õ szemérmöket!"));
husuperobject.biblemap.put("b35_2_16", new husuperobject(35,2,16,"Gyalázattal telsz meg dicsõség helyett; igyál te is és láttassék szemérmed; reád fordul az Úr jobbjának pohara, és gyalázat [borítja] [el] dicsõségedet."));
husuperobject.biblemap.put("b35_2_17", new husuperobject(35,2,17,"Bizony, a libanoni erõszakoskodás [gyászba] borít téged, és a vadak pusztítása, a mely rettegteté õket, az emberek véréért és az országon, a városon és annak minden lakosán ûzött erõszakosságért."));
husuperobject.biblemap.put("b35_2_18", new husuperobject(35,2,18,"Mit használ a faragott kép, hogy a faragója kifaragta azt? vagy az öntött kép és a mely hazugságot tanít, hogy a képnek faragója bízik abban, csinálván néma bálványokat?"));
husuperobject.biblemap.put("b35_2_19", new husuperobject(35,2,19,"Jaj annak, aki fának mondja: Serkenj fel! néma kõnek: Ébredj fel! Taníthat-é ez? Ímé, borítva van aranynyal és ezüsttel, lélek pedig nincs benne semmi!"));
husuperobject.biblemap.put("b35_2_20", new husuperobject(35,2,20,"Ellenkezõleg az Úr az õ szent templomában, hallgasson elõtte az egész föld!"));
husuperobject.biblemap.put("b35_3_1", new husuperobject(35,3,1,"Habakuk próféta könyörgése a sigjónóth szerint."));
husuperobject.biblemap.put("b35_3_2", new husuperobject(35,3,2,"Uram, hallám, a mit hirdettél, [és] megrettenék! Uram! Évek közepette keltsd életre a te munkádat, évek közepette jelentsd meg azt! Haragban emlékezzél meg kegyelmességrõl!"));
husuperobject.biblemap.put("b35_3_3", new husuperobject(35,3,3,"Isten a Témán felõl jön, és a Szent a Párán hegyérõl. Szela. Dicsõsége elborítja az egeket, és dícséretével megtelik a föld."));
husuperobject.biblemap.put("b35_3_4", new husuperobject(35,3,4,"Ragyogása, mint a napé, sugarak [támadnak] mellõle; és ott van az õ hatalmának rejteke."));
husuperobject.biblemap.put("b35_3_5", new husuperobject(35,3,5,"Elõtte döghalál jár, és nyomaiban forró láz támad."));
husuperobject.biblemap.put("b35_3_6", new husuperobject(35,3,6,"Megáll és méregeti a földet, pillant és megrendíti a népeket, az örökkévaló hegyek szétporlanak, elsüllyednek az örökkévaló halmok; az õ ösvényei örökkévalók!"));
husuperobject.biblemap.put("b35_3_7", new husuperobject(35,3,7,"Bomlani látom Khusán sátrait, reszketnek a Midián-föld kárpitjai!"));
husuperobject.biblemap.put("b35_3_8", new husuperobject(35,3,8,"A folyók ellen gerjedt-é fel az Úr? Vajjon a folyókra haragszol-é, vagy a tengerre bõszültél-é fel, hogy lovaidon és diadal-szekereiden robogsz?"));
husuperobject.biblemap.put("b35_3_9", new husuperobject(35,3,9,"Csupasz, meztelen a te kézíved, a törzseknek [esküvéssel tett] igéret szerint! Szela. A föld folyókat ömleszt."));
husuperobject.biblemap.put("b35_3_10", new husuperobject(35,3,10,"Látnak téged [és] megrendülnek a hegyek, gátat tör a víz-ár, harsog a hullám, [és] magasra emeli karjait."));
husuperobject.biblemap.put("b35_3_11", new husuperobject(35,3,11,"A nap [és] hold megállnak helyökön czikázó nyilaid fényétõl [és] ragyogó kopjád villanásától."));
husuperobject.biblemap.put("b35_3_12", new husuperobject(35,3,12,"Haragodban eltaposod a földet, búsultodban szétmorzsolod a nemzeteket."));
husuperobject.biblemap.put("b35_3_13", new husuperobject(35,3,13,"Kiszállsz néped szabadítására, fölkented segítségére; szétzúzod a fõt a gonosznak házában; nyakig feltakarod az alapjait. Szela!"));
husuperobject.biblemap.put("b35_3_14", new husuperobject(35,3,14,"Saját dárdájával vered át az õ vezéreinek fejét, a kik berohannak, hogy szétszórjanak engem; ujjonganak, hogy rejtekében emészthetik meg a szegényt."));
husuperobject.biblemap.put("b35_3_15", new husuperobject(35,3,15,"Lovaiddal megtaposod a tengert, a nagy vizek hullámait."));
husuperobject.biblemap.put("b35_3_16", new husuperobject(35,3,16,"Hallám és reszket a bensõm, a szózatra remegnek ajkaim; porladni kezdenek csontjaim, reszketnek lábaim: hogy nyugton legyek a nyomorúság napján, a mely feljön a népre, mely megsanyargatja azt."));
husuperobject.biblemap.put("b35_3_17", new husuperobject(35,3,17,"Mert a fügefa nem fog virágozni, a szõlõkben nem lészen gyümölcs, megcsal az olajfa termése, a szántóföldek sem teremnek eleséget, kivész a juh az akolból, és nem lesz ökör az istállóban."));
husuperobject.biblemap.put("b35_3_18", new husuperobject(35,3,18,"De én örvendezni fogok az Úrban, [és] vígadok az én szabadító Istenemben."));
husuperobject.biblemap.put("b35_3_19", new husuperobject(35,3,19,"Az Úr Isten az én erõsségem, hasonlókká teszi lábaimat a nõstény szarvasokéihoz, és az én magas helyeimen jártat engemet! Az éneklõmesternek, az én hangszereimmel."));

}

}