package hu.bible;

public class hubible36 {

public hubible36() {

putmapbible36();

}

private void putmapbible36() {

husuperobject.biblemap.put("b36_1_1", new husuperobject(36,1,1,"Az Úr igéje, a melyet szólt Sofóniásnak, a Kusi fiának, a ki Gedáliás fia, a ki Amariás fia, a ki Ezékiás fia, Jósiásnak az Amon fiának idejében."));
husuperobject.biblemap.put("b36_1_2", new husuperobject(36,1,2,"Elvesztek, mindent elvesztek e föld színérõl, azt mondja az Úr."));
husuperobject.biblemap.put("b36_1_3", new husuperobject(36,1,3,"Elvesztek embert és barmot; elvesztem az ég madarait, és a tenger halait és a botránkoztatás eszközeit a hitetlenekkel együtt; az embert is kiirtom a föld színérõl, azt mondja az Úr."));
husuperobject.biblemap.put("b36_1_4", new husuperobject(36,1,4,"És kinyújtom kezemet Júda ellen és Jeruzsálem minden lakója ellen, és kiirtom e helyrõl a Baál maradékát, a bálványpapok nevét a papokkal együtt;"));
husuperobject.biblemap.put("b36_1_5", new husuperobject(36,1,5,"Azokat is, a kik a háztetõkön az ég seregének hajlonganak; és azokat, a kik hajlonganak, esküdvén az Úrra, de esküsznek az õ Molokjokra is;"));
husuperobject.biblemap.put("b36_1_6", new husuperobject(36,1,6,"Azokat is, a kik elfordultak az Úr követésétõl, és a kik nem keresik az Urat és nem tudakoznak felõle."));
husuperobject.biblemap.put("b36_1_7", new husuperobject(36,1,7,"Hallgass az Úr Isten orczája elõtt, mert közel van az Úrnak napja, mert áldozatot készíttetett az Úr, megszentelte az õ hivatalosait."));
husuperobject.biblemap.put("b36_1_8", new husuperobject(36,1,8,"És lészen az Úr áldozatának napján: megfenyítem majd a fejedelmeket és a királyok fiait és mindazokat, a kik idegen öltözetbe öltöztek."));
husuperobject.biblemap.put("b36_1_9", new husuperobject(36,1,9,"És megfenyítem mindazt, a ki a küszöbön ugrál ama napon, a kik erõszakkal és csalárdsággal töltik meg az õ uroknak házát."));
husuperobject.biblemap.put("b36_1_10", new husuperobject(36,1,10,"És lészen azon a napon, azt mondja az Úr, kiáltó szózat a hal-kaputól fogva, és jajgatás az alsó városból, és nagy recsegés a halmok felõl."));
husuperobject.biblemap.put("b36_1_11", new husuperobject(36,1,11,"Jajgassatok, ti, a kik a völgyben laktok, mert elpusztul az egész kalmár nép, kivágattatik minden, a ki ezüstöt mér."));
husuperobject.biblemap.put("b36_1_12", new husuperobject(36,1,12,"És lészen az idõben, megmotozom majd Jeruzsálemet szövétnekkel, és megfenyítem azokat, a kik saját seprejökön hevernek, a kik ezt mondják szívökben: sem jót, sem rosszat nem cselekszik az Úr."));
husuperobject.biblemap.put("b36_1_13", new husuperobject(36,1,13,"És gazdagságuk prédává lesz, házaik pedig pusztává. Építnek házakat, de nem lakják, és plántálnak szõlõket, de nem iszszák azoknak borát."));
husuperobject.biblemap.put("b36_1_14", new husuperobject(36,1,14,"Közel van az Úrnak nagy napja, közel van és igen siet; az Úr napjának szava keserves, kiáltoz azon a hõs [is.]"));
husuperobject.biblemap.put("b36_1_15", new husuperobject(36,1,15,"Haragnak napja az a nap, szorongatásnak és nyomorúságnak napja; pusztításnak és pusztulásnak napja; sötétségnek és homálynak napja; felhõnek és borúnak napja."));
husuperobject.biblemap.put("b36_1_16", new husuperobject(36,1,16,"Kürtnek és tárogatónak napja a megerõsített városok ellen és a büszke tornyok ellen!"));
husuperobject.biblemap.put("b36_1_17", new husuperobject(36,1,17,"És megszorongatom az embereket és járnak, mint a vakok, mert vétkeztek az Úr ellen, és kiontatik vérök, mint a por, és testök, mint szemét."));
husuperobject.biblemap.put("b36_1_18", new husuperobject(36,1,18,"Sem ezüstjök, sem aranyuk nem szabadíthatja meg õket az Úr haragjának napján, és az õ féltõ szeretetének tüze megemészti az egész földet; mert véget vet, bizony hirtelen vet véget e föld minden lakosának."));
husuperobject.biblemap.put("b36_2_1", new husuperobject(36,2,1,"Térjetek eszetekre, s eszméljetek fel, ti, arczátlan nemzet,"));
husuperobject.biblemap.put("b36_2_2", new husuperobject(36,2,2,"Mielõtt szûlne a végzés (mint a polyva száll tova az a nap!); míg rátok nem jön az Úr haragjának tüze, míg rátok nem jön az Úr haragjának napja!"));
husuperobject.biblemap.put("b36_2_3", new husuperobject(36,2,3,"Keressétek az Urat mindnyájan e föld alázatosai, a kik az õ ítélete szerint cselekesztek; keressétek az igazságot, keressétek az alázatosságot: talán megoltalmaztattok az Úr haragjának napján!"));
husuperobject.biblemap.put("b36_2_4", new husuperobject(36,2,4,"Mert elhagyottá lesz Gáza, Askelon pedig pusztasággá; Asdódot délben ûzik el, és Ekron kiirtatik."));
husuperobject.biblemap.put("b36_2_5", new husuperobject(36,2,5,"Jaj a tenger vidékén lakóknak, a Kereteusok nemzetségének! Az Úr igéje ellened van, te Kanaán, Filiszteusok földje; elpusztítalak téged, lakatlanná leszel."));
husuperobject.biblemap.put("b36_2_6", new husuperobject(36,2,6,"És a tenger vidéke legelõkké, pásztorok tanyáivá és juhoknak aklaivá lészen."));
husuperobject.biblemap.put("b36_2_7", new husuperobject(36,2,7,"És [az a] vidék a Júda házának maradékáé lesz, õk legeltetnek azon; estére Askelon házaiban heverésznek, mert meglátogatja õket az Úr, az õ Istenök, és visszahozza az õ foglyaikat."));
husuperobject.biblemap.put("b36_2_8", new husuperobject(36,2,8,"Hallottam Moáb gyalázkodását és Ammon fiainak szidalmait, a melyekkel gyalázták népemet, és felfuvalkodtak az õ határok ellen."));
husuperobject.biblemap.put("b36_2_9", new husuperobject(36,2,9,"Azért élek én, mond a Seregeknek Ura, Izráelnek Istene, hogy Moáb olyanná lészen, mint Sodoma, Ammon fiai pedig, mint Gomora: tövistermõ föld, só-telep és pusztaság örökre; népem maradéka prédálja fel õket, és nemzetségem ivadékai bírják majd õket."));
husuperobject.biblemap.put("b36_2_10", new husuperobject(36,2,10,"Ez [esik meg] rajtok az õ kevélységökért, a miért gyalázkodtak és felfuvalkodtak a Seregek Urának népe ellen."));
husuperobject.biblemap.put("b36_2_11", new husuperobject(36,2,11,"Rettenetes lesz az Úr ellenök, mert elfogyatja a földnek minden istenét, és néki hódol majd kiki a maga lakhelyén, a pogányoknak is minden szigete."));
husuperobject.biblemap.put("b36_2_12", new husuperobject(36,2,12,"Ti is, kúsiak! Fegyverem öli meg õket."));
husuperobject.biblemap.put("b36_2_13", new husuperobject(36,2,13,"És kinyújtja kezét észak felé, és elveszti Assiriát, Ninivét pusztasággá teszi, kietlenné, mint egy sivatag."));
husuperobject.biblemap.put("b36_2_14", new husuperobject(36,2,14,"És nyájak heverésznek bensejében, mindenféle állatok serege: pelikán és sündisznó hálnak párkányain, az ablakban [azoknak] szava hangzik, a küszöbön omladék lészen, mert lefosztatott a czédrus!"));
husuperobject.biblemap.put("b36_2_15", new husuperobject(36,2,15,"Ímé, a víg város, a bátorságban lakozó, a mely ezt mondja vala szívében: Én vagyok és nincs kívülem más! milyen pusztasággá lõn, vadak tanyájává, a ki átmegy rajta, mind süvöltöz és csapkodja kezét."));
husuperobject.biblemap.put("b36_3_1", new husuperobject(36,3,1,"Jaj az ellenszegülõnek és undoknak, az erõszakos városnak!"));
husuperobject.biblemap.put("b36_3_2", new husuperobject(36,3,2,"Nem hallgatott a szóra, nem fogadta a fenyítéket, nem bízott az Úrban, Istenéhez nem közelített!"));
husuperobject.biblemap.put("b36_3_3", new husuperobject(36,3,3,"Fejedelmei [olyanok] benne, [mint] az ordító oroszlánok, birái, [mint] az estve [járó] farkasok, nem hagynak reggelre a csonton."));
husuperobject.biblemap.put("b36_3_4", new husuperobject(36,3,4,"Prófétái hivalkodók, hitetõ férfiak; papjai megfertéztetik a szent helyet, erõszakot tesznek a törvényen."));
husuperobject.biblemap.put("b36_3_5", new husuperobject(36,3,5,"Az Úr igaz õ benne, nem cselekszik hamisságot; reggelrõl reggelre napfényre hozza ítéletét; nem mulasztja el: de nem ismeri a szégyent a gonosz!"));
husuperobject.biblemap.put("b36_3_6", new husuperobject(36,3,6,"Nemzeteket irtottam ki; elpusztultak tornyaik; feldúltam falvaikat, nincs, a ki átmenjen rajtok; elromboltattak városaik, egy ember sincs bennök, nincsen lakosuk!"));
husuperobject.biblemap.put("b36_3_7", new husuperobject(36,3,7,"Mondtam: Csak félj engem, vedd fel a fenyítéket (akkor nem irtatott volna ki lakhelye; mindaz, a mit felõle végeztem): mégis mihelyt felvirradtak, rosszra indították minden cselekedetöket."));
husuperobject.biblemap.put("b36_3_8", new husuperobject(36,3,8,"Azért várjatok rám, azt mondja az Úr, míg prédára kelek; mert elvégeztem, hogy egybegyûjtöm a népeket, hogy összeszedem az országokat, hogy kiöntsem rájok búsulásomat, haragomnak egész hevét, mert gerjedezésem tüzében emésztetik meg az egész föld!"));
husuperobject.biblemap.put("b36_3_9", new husuperobject(36,3,9,"Akkor változtatom majd a népek ajkát tisztává, hogy mind segítségül hívják az Úr nevét, hogy egy akarattal szolgálják õt."));
husuperobject.biblemap.put("b36_3_10", new husuperobject(36,3,10,"Kús folyóvizein túlról hozzák imádóim, szétszórt népemnek leányai ételáldozatomat nékem."));
husuperobject.biblemap.put("b36_3_11", new husuperobject(36,3,11,"Azon a napon nem szégyenülsz meg egyetlen cselekedetért sem, a melyekkel vétkeztél ellenem; mert akkor eltávolítom körödbõl azokat, a kik kérkedve örvendeznek benned, és nem kevélykedel többé az én szent hegyemen."));
husuperobject.biblemap.put("b36_3_12", new husuperobject(36,3,12,"És marasztok közötted nyomorult és szegény népet, a kik bíznak az Úr nevében."));
husuperobject.biblemap.put("b36_3_13", new husuperobject(36,3,13,"Izráel maradéka nem cselekszik hamisságot, nem szól hazugságot, és nem találtatik szájában álnokságnak nyelve, hanem legelésznek és lenyugosznak és nem lesz a ki felrettentse [õket."));
husuperobject.biblemap.put("b36_3_14", new husuperobject(36,3,14,"Énekelj Sionnak leánya, harsogj Izráel, örvendj és teljes szívvel vígadj Jeruzsálem leánya!"));
husuperobject.biblemap.put("b36_3_15", new husuperobject(36,3,15,"Megváltoztatta az Úr a te ítéletedet, elfordította ellenségedet; Izráel királya, az Úr, közötted van, nem látsz többé gonoszt!"));
husuperobject.biblemap.put("b36_3_16", new husuperobject(36,3,16,"Azon a napon ezt mondják Jeruzsálemnek: Ne félj! Ne lankadjanak kezeid Sion!"));
husuperobject.biblemap.put("b36_3_17", new husuperobject(36,3,17,"Az Úr, a te Istened közötted van; erõs õ, megtart; örül te rajtad örömmel, hallgat az õ szerelmében, énekléssel örvendez néked."));
husuperobject.biblemap.put("b36_3_18", new husuperobject(36,3,18,"Az ünnep miatt bánkódókat egybegyûjtöm, a kik közüled valók; gyalázat terhe van rajtok."));
husuperobject.biblemap.put("b36_3_19", new husuperobject(36,3,19,"Ímé, én elbánok minden te nyomorgatóddal abban az idõben, és megtartom a sántát, és összeszedem a szétszórtakat, és híresekké és nevesekké teszem õket az õ gyalázatjoknak egész földén."));
husuperobject.biblemap.put("b36_3_20", new husuperobject(36,3,20,"Abban az idõben elhozlak titeket, és akkor összeszedlek titeket, mert nevesekké és híresekké teszlek titeket a földnek minden népe között, mikor megfordítom a ti fogságotokat a ti szemetek elõtt, azt mondja az Úr."));

}

}