package hu.bible;

public class hubible37 {

public hubible37() {

putmapbible37();

}

private void putmapbible37() {

husuperobject.biblemap.put("b37_1_1", new husuperobject(37,1,1,"Dárius király második esztendejének hatodik hónapjában, a hónapnak elsõ napján szóla az Úr Aggeus próféta által Zorobábelnek, a Sealtiél fiának, Júda fejedelmének, és Jósuának, a Jehosadák fiának, a fõpapnak, mondván:"));
husuperobject.biblemap.put("b37_1_2", new husuperobject(37,1,2,"Így szól a Seregeknek Ura, mondván: Ezt mondja e nép: Nem jött még el az idõ, az Úr háza építésének ideje!"));
husuperobject.biblemap.put("b37_1_3", new husuperobject(37,1,3,"Az Úr pedig így szól Aggeus próféta által, mondván:"));
husuperobject.biblemap.put("b37_1_4", new husuperobject(37,1,4,"Ideje-é néktek, hogy ti mennyezetes házakban lakozzatok, holott ez a ház romban áll?"));
husuperobject.biblemap.put("b37_1_5", new husuperobject(37,1,5,"Most azért ezt mondja a Seregeknek Ura: Gondoljátok meg jól a ti útaitokat!"));
husuperobject.biblemap.put("b37_1_6", new husuperobject(37,1,6,"Sokat vetettetek, de keveset takartok; esztek, de meg nem elégesztek; isztok, de meg nem részegesztek; ruházkodtok, de meg nem melegesztek, a bérlõ is lyukas zacskóra bérel."));
husuperobject.biblemap.put("b37_1_7", new husuperobject(37,1,7,"Ezt mondja a Seregeknek Ura: Gondoljátok meg jól a ti útaitokat!"));
husuperobject.biblemap.put("b37_1_8", new husuperobject(37,1,8,"Menjetek fel a hegyre, és hordjatok fát, és építsétek e házat, hogy gyönyörködjem benne, és dicsõíttessem, azt mondja az Úr."));
husuperobject.biblemap.put("b37_1_9", new husuperobject(37,1,9,"Sokat vártatok, de ímé kevés lett; haza is hordtátok, de én ráfuvallok arra! Mi okért? azt mondja a Seregeknek Ura. Az én házamért, a mely ím romban áll, ti pedig siettek, kiki a maga házához."));
husuperobject.biblemap.put("b37_1_10", new husuperobject(37,1,10,"Azért vonták meg tõletek az egek a harmatot, vonta meg a föld az õ termését."));
husuperobject.biblemap.put("b37_1_11", new husuperobject(37,1,11,"És pusztulást rendeltem a [sík] földre és a hegyekre, a búzára és a borra, az olajra és mindarra, a mit a föld terem; sõt az emberre és a baromra és minden kézimunkára is."));
husuperobject.biblemap.put("b37_1_12", new husuperobject(37,1,12,"És meghallá Zorobábel, a Sealtiél fia, és Jósua, a Jehosadák fia, a fõpap, és a nép minden többi [tag]ja az Úrnak, az õ Istenöknek szavát és Aggeus próféta beszédeit, a miként elküldte õt az Úr, az õ Istenök, és megfélemlék a nép az Úr elõtt."));
husuperobject.biblemap.put("b37_1_13", new husuperobject(37,1,13,"És szóla Aggeus, az Úr követe, az Úr küldetésében, mondván a népnek: Én veletek vagyok, azt mondja az Úr."));
husuperobject.biblemap.put("b37_1_14", new husuperobject(37,1,14,"És felindítá az Úr Zorobábelnek, a Sealtiél fiának, a Júda fejedelmének lelkét, és Jósuának, a Jehosadák fiának, a fõpapnak lelkét, és a nép minden többi [tag]jának lelkét is, és bemenének és munkálkodának a Seregek Urának, az õ Istenöknek házában."));
husuperobject.biblemap.put("b37_1_15", new husuperobject(37,1,15,"A hatodik hónapnak huszonnegyedik napján, Dárius királynak második esztendejében."));
husuperobject.biblemap.put("b37_2_1", new husuperobject(37,2,1,"A hetedik hónap huszonnegyedik napján szóla az Úr Aggeus próféta által, mondván:"));
husuperobject.biblemap.put("b37_2_2", new husuperobject(37,2,2,"Szólj csak Zorobábelnek, a Sealtiél fiának, Júda fejedelmének, és Jósuának, a Jehosadák fiának, a fõpapnak, és a nép többi [tag]jainak, mondván:"));
husuperobject.biblemap.put("b37_2_3", new husuperobject(37,2,3,"Ki van még életben köztetek, a ki látta ezt a házat az õ elsõ dicsõségében? És milyennek látjátok ti most ezt? Avagy olyan-é ez a ti szemetekben, mintha semmi volna?"));
husuperobject.biblemap.put("b37_2_4", new husuperobject(37,2,4,"De most légy bátor, Zorobábel, azt mondja az Úr! Légy bátor te is, Jósua, Jehosadák fia, te fõpap, és légy bátor e földnek minden népe, azt mondja az Úr, és cselekedjétek, mert én veletek vagyok, azt mondja a Seregeknek Ura,"));
husuperobject.biblemap.put("b37_2_5", new husuperobject(37,2,5,"Az igét, a melylyel szövetségre léptem veletek, mikor kijöttetek Égyiptomból, és az én lelkem köztetek marad. Ne féljetek!"));
husuperobject.biblemap.put("b37_2_6", new husuperobject(37,2,6,"Mert ezt mondja a Seregeknek Ura: Egy kevés [idõ] van még, és én megindítom az eget és a földet, a tengert és a szárazt."));
husuperobject.biblemap.put("b37_2_7", new husuperobject(37,2,7,"És megindítok minden népet, és eljön, a kit minden népek óhajtanak, és megtöltöm e házat dicsõséggel, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b37_2_8", new husuperobject(37,2,8,"Enyém az ezüst és enyém az arany, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b37_2_9", new husuperobject(37,2,9,"Nagyobb lészen e második háznak dicsõsége az elsõnél, azt mondja a Seregeknek Ura, és e helyen adok békességet, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b37_2_10", new husuperobject(37,2,10,"A kilenczedik hónap huszonnegyedikén, Dáriusnak második esztendejében szóla az Úr Aggeus próféta által, mondván:"));
husuperobject.biblemap.put("b37_2_11", new husuperobject(37,2,11,"Ezt mondja a Seregeknek Ura: Kérdezd meg csak a papokat a törvény felõl, mondván:"));
husuperobject.biblemap.put("b37_2_12", new husuperobject(37,2,12,"Ha szentelt húst visz valaki az õ köntösének szárnyában, és illeti annak szárnyával a kenyeret vagy a fõzeléket, vagy a bort, vagy az olajat, vagy bármi más eleséget: vajjon szent-e az? És felelének a papok, és mondák: Nem."));
husuperobject.biblemap.put("b37_2_13", new husuperobject(37,2,13,"Aggeus pedig mondá: Ha hulla által fertõzött illeti mindezeket, tisztátalanná lesz-é? És felelének a papok, és mondák: Tisztátalanná!"));
husuperobject.biblemap.put("b37_2_14", new husuperobject(37,2,14,"Aggeus pedig felele, és mondá: Épp így e nép és épp így e nemzet én elõttem, azt mondja az Úr, és épp így kezöknek minden munkája, és a mit ide felhoznak: tisztátalan az."));
husuperobject.biblemap.put("b37_2_15", new husuperobject(37,2,15,"Most azért jól gondoljátok meg e naptól fogva az elmultakat is, mielõtt még követ kõre tettek volna az Úr hajlékában!"));
husuperobject.biblemap.put("b37_2_16", new husuperobject(37,2,16,"Az elõtt elmentek a huszas garmadához, és tíz lett; elmentek a sajtóhoz, hogy ötven vederrel merítsenek, és húsz lett."));
husuperobject.biblemap.put("b37_2_17", new husuperobject(37,2,17,"Megvertelek titeket üszöggel, ragyával, és kezetek minden munkáját kõesõvel, és még sem [hajoltatok] hozzám, azt mondja az Úr. x^2.18 Jól gondoljátok hát meg e naptól fogva az elmultakat is! A kilenczedik [hónap] huszonnegyedik napjától, attól a naptól fogva, a melyen letétetett az Úr hajlékának alapja. Jól meggondoljátok!"));
husuperobject.biblemap.put("b37_2_19", new husuperobject(37,2,19,"Van-é még mag a csûrben? És bizony, a szõlõ, a füge, a gránátalma és az olajfa sem termett! E naptól fogva megáldalak."));
husuperobject.biblemap.put("b37_2_20", new husuperobject(37,2,20,"Másodszor is szóla az Úr Aggeusnak a hónap huszonnegyedik napján, mondván:"));
husuperobject.biblemap.put("b37_2_21", new husuperobject(37,2,21,"Mondd meg Zorobábelnek, Júda fejedelmének: Én megindítom az eget és a földet."));
husuperobject.biblemap.put("b37_2_22", new husuperobject(37,2,22,"És felforgatom az országok királyi székét, és elfogyasztom a pogány országok erejét, és felforgatom a szekeret és a benne ülõket, és lehullanak a lovak és a rajtok ülõk: kiki az õ atyjafiának fegyvere által."));
husuperobject.biblemap.put("b37_2_23", new husuperobject(37,2,23,"Azon a napon, azt mondja a Seregeknek Ura, felveszlek téged Zorobábel, Sealtiélnek fia, én szolgám, azt mondja az Úr, és olyanná teszlek, mint egy pecsétgyürû, mert téged választottalak el, azt mondja a Seregeknek Ura."));

}

}