package hu.bible;

public class hubible39 {

public hubible39() {

putmapbible39();

}

private void putmapbible39() {

husuperobject.biblemap.put("b39_1_1", new husuperobject(39,1,1,"Az Úr igéjének terhe Izráel ellen, Malakiás által."));
husuperobject.biblemap.put("b39_1_2", new husuperobject(39,1,2,"Szerettelek titeket, azt mondja az Úr, és azt mondjátok: Miben szerettél minket? Avagy nem atyjafia vala-é Ézsau Jákóbnak? azt mondja az Úr: Jákóbot pedig szerettem;"));
husuperobject.biblemap.put("b39_1_3", new husuperobject(39,1,3,"Ézsaut ellenben gyûlöltem, és az õ hegyeit pusztává tettem, örökségét pedig pusztai sakálokévá."));
husuperobject.biblemap.put("b39_1_4", new husuperobject(39,1,4,"Ha azt mondja Edom: Elpusztultunk; de térjünk meg és építsük fel a romladékokat: ezt mondja a Seregeknek Ura: Õk építenek, de én elrontom, és elnevezik õket istentelenség határának és oly népnek, a melyre örökké haragszik az Úr."));
husuperobject.biblemap.put("b39_1_5", new husuperobject(39,1,5,"És látják ezt a ti szemeitek, és magatok is mondjátok: Nagy az Úr az Izráel határa felett."));
husuperobject.biblemap.put("b39_1_6", new husuperobject(39,1,6,"A fiú tiszteli atyját, a szolga is az õ urát. És ha én atya vagyok: hol az én tisztességem? És ha én úr vagyok, hol az én félelmem? azt mondja a Seregeknek Ura néktek, ti papok, a kik útáljátok az én nevemet, és [ezt] mondjátok: Mivel útáljuk a te nevedet?"));
husuperobject.biblemap.put("b39_1_7", new husuperobject(39,1,7,"Megfertéztetett kenyeret hoztok oltáromra, és azt mondjátok: Mivel fertéztetünk meg téged? Azzal, mikor azt gondoljátok, hogy az Úrnak asztala megvetni való."));
husuperobject.biblemap.put("b39_1_8", new husuperobject(39,1,8,"Hogyha vakot hoztok áldozatul: Nem bûn-é [az?] vagy ha sántát hoztok és bénát: nem bûn-é [az?] Vidd csak azt a te fejedelmednek: vajjon kedvvel fogad-é, avagy reád tekint-é? azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_1_9", new husuperobject(39,1,9,"Most azért engeszteljétek meg az Istennek orczáját, hogy könyörüljön rajtunk. Általatok van ez, elnézheti-é ezt néktek? azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_1_10", new husuperobject(39,1,10,"Vajha valaki közületek bezárná az ajtót, hiába ne tüzelnétek az én oltáromon! Nem telik kedvem bennetek, azt mondja a Seregeknek Ura; az ételáldozatot sem kedvelem a ti kezetekbõl!"));
husuperobject.biblemap.put("b39_1_11", new husuperobject(39,1,11,"Hiszen napkelettõl fogva napnyugotig nagy az én nevem a pogányok között, és minden helyen tömjénnel áldoznak az én nevemnek és tiszta ételáldozattal! Bizony nagy az én nevem a pogányok között! azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_1_12", new husuperobject(39,1,12,"Ti pedig meggyalázzátok azt, mikor azt mondjátok: Az Úrnak asztala megvetni való, és annak jövedelme, eledele útálatos."));
husuperobject.biblemap.put("b39_1_13", new husuperobject(39,1,13,"És azt mondjátok: Ímé, mily fáradság! és ráfújtok, azt mondja a Seregeknek Ura; pedig a rablottat hozzátok, meg a sántát és betegest; ételáldozatot is hoztok, [de] hát kedves-é az a ti kezetekbõl? azt mondja az Úr."));
husuperobject.biblemap.put("b39_1_14", new husuperobject(39,1,14,"Átkozott pedig az álnok! Van ugyan a nyájában hím, és fogadást is tesz: mégis hitványnyal áldozik az Úrnak. Pedig nagy király vagyok én, azt mondja a Seregeknek Ura, és félelmetes az én nevem a pogányok között!"));
husuperobject.biblemap.put("b39_2_1", new husuperobject(39,2,1,"Most azért néktek szól ez a parancsolat, ti papok!"));
husuperobject.biblemap.put("b39_2_2", new husuperobject(39,2,2,"Ha meg nem hallgatjátok és ha nem veszitek szívetekre, hogy dicsõséget adjatok az én nevemnek, azt mondja a Seregeknek Ura: átkot bocsátok reátok, és elátkozom a ti áldásotokat; bizony elátkozom azt, ha nem veszitek szívetekre!"));
husuperobject.biblemap.put("b39_2_3", new husuperobject(39,2,3,"Ímé, én megrontom a ti vetni való magotokat, és szemetet szórok orczáitokba, a ti ünneplésteknek szemetjét, és ahhoz hordanak ki titeket."));
husuperobject.biblemap.put("b39_2_4", new husuperobject(39,2,4,"És megtudjátok, hogy azért adtam néktek e parancsolatot, hogy szövetségem legyen a Lévivel, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_2_5", new husuperobject(39,2,5,"Szövetségem volt vele életre és békességre, és félelmül adtam azt néki és félt engemet, és megalázta magát az én nevem elõtt."));
husuperobject.biblemap.put("b39_2_6", new husuperobject(39,2,6,"Igazság törvénye volt az õ szájában, és nem találtatott álnokság az õ ajkaiban; békességben és egyenességgel járt velem, és sokakat megtérített a bûnbõl."));
husuperobject.biblemap.put("b39_2_7", new husuperobject(39,2,7,"Mert a papnak ajkai õrzik a tudományt, és az õ szájából törvényt várnak, mivel a Seregek Urának követe õ."));
husuperobject.biblemap.put("b39_2_8", new husuperobject(39,2,8,"De ti elhajlottatok ez útról, sokakat megbotránkoztattatok a törvénnyel, felbontottátok Lévi szövetségét, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_2_9", new husuperobject(39,2,9,"Azért én is szidalmasakká tettelek titeket és útálatosakká az egész nép elõtt, a miatt, hogy meg nem õriztétek az én útamat, hanem személyválogatók voltatok a törvénnyel."));
husuperobject.biblemap.put("b39_2_10", new husuperobject(39,2,10,"Nem egy atyánk van-é mindnyájunknak? Nem egy Isten teremtett-é minket? Miért csalja hát kiki az õ felebarátját, megrontván a mi atyáink szövetségét?"));
husuperobject.biblemap.put("b39_2_11", new husuperobject(39,2,11,"Hûtelenné lett Júda, és útálatosság támadt Izráelben és Jeruzsálemben, mert megfertõztette Júda az Úrnak szentségét, a melyet õ szeret, és idegen istennek leányát vette el."));
husuperobject.biblemap.put("b39_2_12", new husuperobject(39,2,12,"Elveszt az Úr mindenkit, a ki ezt cselekszi, a vigyázót és a felelõt, a Jákób sátraiból, még ha áldozatot visz is a Seregek Urának."));
husuperobject.biblemap.put("b39_2_13", new husuperobject(39,2,13,"És ezt is cselekszitek: betöltitek az Úr oltárát könyhullatással, sírással és kesergéssel, hogy ne tekintsen többé az ételáldozatra, és ne fogadjon el szívességet a ti kezetekbõl."));
husuperobject.biblemap.put("b39_2_14", new husuperobject(39,2,14,"És azt mondjátok: Miért? Azért, mert az Úr tett bizonyságot közted és a te ifjúságod felesége közt, a kit te megcsaltál, holott társad és szövetséges feleséged!"));
husuperobject.biblemap.put("b39_2_15", new husuperobject(39,2,15,"Nem tett ilyet egy sem, a kinek még volt lelke. És mit keresett az az egy? Istentõl való magvat. Õrizzétek meg azért a ti lelketeket, és a ti ifjúságotok feleségét meg ne csaljátok!"));
husuperobject.biblemap.put("b39_2_16", new husuperobject(39,2,16,"Mert gyûlölöm az elbocsátást, ezt mondja az Úr, Izráelnek Istene, és [azt,] a ki ruhájára kegyetlenséget borít, azt mondja a Seregeknek Ura. Õrizzétek meg azért lelketeket és ne csalárdkodjatok!"));
husuperobject.biblemap.put("b39_2_17", new husuperobject(39,2,17,"Elfárasztottátok az Urat beszédeitekkel, és azt mondjátok: Mivel fárasztottuk el? Azzal, hogy azt mondjátok: Minden gonosztevõ jó az Úr szemeiben, és gyönyörködik õ azokban; vagy: Hol van az ítéletnek Istene?"));
husuperobject.biblemap.put("b39_3_1", new husuperobject(39,3,1,"Ímé, elküldöm én az én követemet, és megtisztítja elõttem az útat, és mindjárt eljön az õ templomába az Úr, a kit ti kerestek, és a szövetségnek követe, a kit ti kívántok; ímé, eljön, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_3_2", new husuperobject(39,3,2,"De kicsoda szenvedheti el az õ eljövetelének napját? És kicsoda áll meg az õ megjelenésekor? Hiszen olyan õ, mint az ötvösnek tüze, és a ruhamosóknak lúgja!"));
husuperobject.biblemap.put("b39_3_3", new husuperobject(39,3,3,"És ül mint ötvös vagy ezüsttisztogató és megtisztítja Lévi fiait és fényessé teszi õket, mint az aranyat és ezüstöt; és igazsággal visznek ételáldozatot az Úrnak."));
husuperobject.biblemap.put("b39_3_4", new husuperobject(39,3,4,"És kedves lesz az Úrnak a Júda és Jeruzsálem ételáldozatja, mint a régi napokban és elõbbi esztendõkben."));
husuperobject.biblemap.put("b39_3_5", new husuperobject(39,3,5,"Mert ítéletre indulok hozzátok, és gyors tanú leszek a szemfényvesztõk ellen, a paráznák és hamisan esküvõk ellen, és azok ellen, a kik megrövidítik a munkásnak bérét, az özvegyet és árvát, és a kik nyomorgatják az idegent, és nem félnek engem, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_3_6", new husuperobject(39,3,6,"Mert én, az Úr, meg nem változom, ti pedig, Jákóbnak fiai, nem emésztettek meg!"));
husuperobject.biblemap.put("b39_3_7", new husuperobject(39,3,7,"Atyáitok idejétõl fogva eltértetek rendeléseimtõl és nem tartottátok meg [azokat.] Térjetek hozzám, és én is hozzátok térek, azt mondja a Seregeknek Ura. De azt mondjátok: Miben térjünk meg?"));
husuperobject.biblemap.put("b39_3_8", new husuperobject(39,3,8,"Avagy az ember csalhatja-é az Istent? ti mégis csaltatok engem. És azt mondjátok: Mivel csalunk téged? A tizeddel és az áldozni valóval."));
husuperobject.biblemap.put("b39_3_9", new husuperobject(39,3,9,"Átokkal vagytok elátkozva, mégis csaltok engem: a nép egészben!"));
husuperobject.biblemap.put("b39_3_10", new husuperobject(39,3,10,"Hozzátok be a tizedet mind az én tárházamba, hogy legyen ennivaló az én házamban, és ezzel próbáljatok meg engem, azt mondja a Seregeknek Ura, ha nem nyitom meg néktek az egek csatornáit, és ha nem árasztok reátok áldást bõségesen."));
husuperobject.biblemap.put("b39_3_11", new husuperobject(39,3,11,"És megdorgálom érettetek a kártevõt, és nem veszti el földetek gyümölcsét, és nem lesz a szõlõtök meddõ a mezõn, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_3_12", new husuperobject(39,3,12,"És boldognak mondanak titeket mind a nemzetek; mert kívánatos földdé lesztek ti, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_3_13", new husuperobject(39,3,13,"Keményen szóltatok ellenem, azt mondja az Úr, és azt mondjátok: Mit szóltunk ellened?"));
husuperobject.biblemap.put("b39_3_14", new husuperobject(39,3,14,"Azt mondtátok: Hiábavaló az Isten szolgálata, és mi haszna, hogy megtartjuk törvényeit, és hogy alázatosan járunk a Seregeknek Ura elõtt?"));
husuperobject.biblemap.put("b39_3_15", new husuperobject(39,3,15,"Sõt inkább magunk hirdetjük boldogoknak a kevélyeket; hiszen gyarapodtak, noha gonoszságot ûznek, és megszabadulnak, noha kisértik az Istent!"));
husuperobject.biblemap.put("b39_3_16", new husuperobject(39,3,16,"Akkor tanakodtak egymással az Úrnak tisztelõi, az Úr pedig figyelt és hallgatott, és egy emlékkönyv iraték õ elõtte azoknak, a kik félik az Urat és becsülik az õ nevét."));
husuperobject.biblemap.put("b39_3_17", new husuperobject(39,3,17,"És azon a napon, azt mondja a Seregeknek Ura, a melyet én szerzek, tulajdonommá lesznek és kedvezek nékik, a mint kiki kedvez a maga fiának, a ki szolgálja õt."));
husuperobject.biblemap.put("b39_3_18", new husuperobject(39,3,18,"És megtértek és meglátjátok, [hogy különbség van] az igaz és a gonosz között, az Isten szolgája között és a között, a ki nem szolgálja õt."));
husuperobject.biblemap.put("b39_4_1", new husuperobject(39,4,1,"Mert ímé, eljön a nap, lángoló, mint a sütõ-kemencze, és [olyanná] lesz minden kevély és minden gonosztevõ, [mint] a pozdorja, és megégeti õket az eljövendõ nap, azt mondja a Seregeknek Ura, a mely nem hagy rajtok gyökeret, sem ágat."));
husuperobject.biblemap.put("b39_4_2", new husuperobject(39,4,2,"És feltámad néktek, a kik félitek az én nevemet, az igazságnak napja, és gyógyulás lesz az õ szárnyai alatt, és kimentek és ugrándoztok, mint a hízlalt tulkok."));
husuperobject.biblemap.put("b39_4_3", new husuperobject(39,4,3,"És széttapodjátok a gonoszokat, és porrá lesznek lábaitok nyomása alatt azon a napon, a melyet én szerzek, azt mondja a Seregeknek Ura."));
husuperobject.biblemap.put("b39_4_4", new husuperobject(39,4,4,"Emlékezzetek meg Mózesnek, az én szolgámnak törvényérõl, a melyet rendeltem õ általa a Hóreben, az egész Izráelnek rendelésekül és ítéletekül."));
husuperobject.biblemap.put("b39_4_5", new husuperobject(39,4,5,"Ímé, én elküldöm néktek Illyést, a prófétát, mielõtt eljön az Úrnak nagy és félelmetes napja."));
husuperobject.biblemap.put("b39_4_6", new husuperobject(39,4,6,"És az atyák szívét a fiakhoz fordítja, a fiak szívét pedig az atyákhoz, hogy el ne jõjjek és meg ne verjem e földet átokkal."));

}

}