husuperobject.biblemap.put("b4_3_9", new husuperobject(4,3,9,"És adjad a lévitákat Áronnak és az õ fiainak; mert valóban néki adattak Izráel fiaitól."));
husuperobject.biblemap.put("b4_3_10", new husuperobject(4,3,10,"Áront pedig és az õ fiait rendeld [föléjök,] hogy õrizzék az õ papságukat; és ha idegen járulna oda, haljon meg."));
husuperobject.biblemap.put("b4_3_11", new husuperobject(4,3,11,"Szóla azután az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_3_12", new husuperobject(4,3,12,"Ímé én kiválasztottam a lévitákat Izráel fiai közül, minden elsõszülött helyett, a mely az õ anyjának méhét megnyitja Izráel fiai között: azért legyenek a léviták enyéim."));
husuperobject.biblemap.put("b4_3_13", new husuperobject(4,3,13,"Mert enyém minden elsõszülött; a mikor megöltem minden elsõszülöttet Égyiptom földén, magamnak szenteltem minden elsõszülöttet Izráelben; akár ember, akár barom, enyéim legyenek: én vagyok az Úr."));
husuperobject.biblemap.put("b4_3_14", new husuperobject(4,3,14,"Szóla azután az Úr Mózesnek a Sinai pusztájában, mondván:"));
husuperobject.biblemap.put("b4_3_15", new husuperobject(4,3,15,"Számláld meg Lévi fiait az õ atyáiknak háznépe szerint, az õ nemzetségeik szerint; egy hónapostól fogva, és azon felül minden finemût számlálj meg."));
husuperobject.biblemap.put("b4_3_16", new husuperobject(4,3,16,"Megszámlálá azért Mózes õket az Úr szava szerint, a miképen meghagyatott vala néki."));
husuperobject.biblemap.put("b4_3_17", new husuperobject(4,3,17,"És ezek voltak a Lévi fiai az õ neveik szerint: Gerson, Kéhát és Mérári."));
husuperobject.biblemap.put("b4_3_18", new husuperobject(4,3,18,"Ezek pedig a Gerson fiainak nevei az õ nemzetségök szerint: Libni és Simhi."));
husuperobject.biblemap.put("b4_3_19", new husuperobject(4,3,19,"Továbbá a Kéhát fiai az õ nemzetségök szerint: Amrám és Iczhár, Hebron és Uzziél."));
husuperobject.biblemap.put("b4_3_20", new husuperobject(4,3,20,"A Mérári fiai pedig az õ nemzetségök szerint: Makhli és Músi. Ezek a Lévi nemzetségei, az õ atyáiknak háznépe szerint."));
husuperobject.biblemap.put("b4_3_21", new husuperobject(4,3,21,"Gersontól valók a Libni nemzetsége és a Simhi nemzetsége; ezek a Gersoniták nemzetségei."));
husuperobject.biblemap.put("b4_3_22", new husuperobject(4,3,22,"Az õ megszámláltjaik, az egy hónapostól fogva és feljebb minden finemûnek száma szerint, az õ megszámláltjaik: hétezer és ötszáz."));
husuperobject.biblemap.put("b4_3_23", new husuperobject(4,3,23,"A Gersoniták nemzetségei a hajlék megett járjanak tábort nyugot felõl."));
husuperobject.biblemap.put("b4_3_24", new husuperobject(4,3,24,"És a Gersoniták atyái háznépének fejedelme legyen Eliásáf, a Láél fia."));
husuperobject.biblemap.put("b4_3_25", new husuperobject(4,3,25,"A Gerson fiainak [tiszte] pedig: ügyelni a gyülekezet sátorában, a hajlékra, a sátorra, annak takarójára, és a gyülekezet sátora nyílásának leplére."));
husuperobject.biblemap.put("b4_3_26", new husuperobject(4,3,26,"Továbbá a pitvarnak szõnyegeire, és a pitvar nyílásának leplére, a mely van a hajlékon és az oltáron köröskörül, és annak köteleire, és minden azzal járó szolgálatra."));
husuperobject.biblemap.put("b4_3_27", new husuperobject(4,3,27,"Kéháttól való pedig az Amrám nemzetsége, az Iczhár nemzetsége, a Hebron nemzetsége és az Uzziél nemzetsége: Ezek Kéhátnak nemzetségei."));
husuperobject.biblemap.put("b4_3_28", new husuperobject(4,3,28,"Minden finemûnek száma szerint, egy hónapostól fogva és feljebb, nyolczezeren és hatszázan valának a szenthelynek õrizõi."));
husuperobject.biblemap.put("b4_3_29", new husuperobject(4,3,29,"A Kéhát fiainak nemzetségei a hajlék oldala mellett dél felõl járjanak tábort."));
husuperobject.biblemap.put("b4_3_30", new husuperobject(4,3,30,"És a Kéhátiták nemzetségének, az õ atyái háznépének fejedelme [legyen] Elisáfán, Uzziélnek fia."));
husuperobject.biblemap.put("b4_3_31", new husuperobject(4,3,31,"Az õ [tisztök] pedig, ügyelni a ládára, az asztalra, a gyertyatartóra, az oltárokra és a szenthelynek edényeire, a melyekkel szolgálnak, és a takaróra, és minden azzal járó szolgálatra."));
husuperobject.biblemap.put("b4_3_32", new husuperobject(4,3,32,"Továbbá a léviták fejedelmeinek fejedelme [legyen] Eleázár, Áron pap fia: a szenthelyre ügyelõknek elõljárója."));
husuperobject.biblemap.put("b4_3_33", new husuperobject(4,3,33,"Méráritól való a Makhli és Músi nemzetségei; ezek a Mérári nemzetségei."));
husuperobject.biblemap.put("b4_3_34", new husuperobject(4,3,34,"Az õ megszámláltjaik pedig minden finemûnek száma szerint, egy hónapostól fogva és feljebb: hatezer és kétszáz."));
husuperobject.biblemap.put("b4_3_35", new husuperobject(4,3,35,"És a Mérári nemzetségének, az õ atyái háznépének fejedelme [legyen] Suriel, az Abihail fia; a hajléknak észak felõl való oldala mellett járjanak tábort."));
husuperobject.biblemap.put("b4_3_36", new husuperobject(4,3,36,"A Mérári fiainak pedig tisztök legyen felügyelni: a hajlék deszkáira, annak reteszrúdaira, oszlopaira és annak talpaira, minden edényeire és minden azzal járó szolgálatra;"));
husuperobject.biblemap.put("b4_3_37", new husuperobject(4,3,37,"Továbbá a pitvar körül való oszlopokra és azoknak talpaira, szegeire és köteleire."));
husuperobject.biblemap.put("b4_3_38", new husuperobject(4,3,38,"A hajlék elõtt keletre, a gyülekezet sátora elõtt naptámadat felõl, Mózes, Áron és az õ fiai járjanak tábort, a kik felügyelnek a szenthely szolgálatára, [és] Izráel fiainak ügyeire; ha pedig idegen járulna oda, haljon meg."));
husuperobject.biblemap.put("b4_3_39", new husuperobject(4,3,39,"A léviták minden megszámláltja, a kiket Mózes és Áron az Úr rendeletére nemzetségenként számláltak vala meg, minden finemû, az egy hónapostól fogva és feljebb: huszonkét ezer."));
husuperobject.biblemap.put("b4_3_40", new husuperobject(4,3,40,"És monda az Úr Mózesnek: Számláld meg Izráel fiainak minden finemû elsõszülöttét, egy hónapostól fogva és feljebb, és pedig névszerint számláld meg õket."));
husuperobject.biblemap.put("b4_3_41", new husuperobject(4,3,41,"És válaszd a lévitákat nékem (én vagyok az Úr) az Izráel fiai közül való minden elsõszülött helyett; és a léviták barmait, Izráel fiai barmainak minden elsõ fajzása helyett."));
husuperobject.biblemap.put("b4_3_42", new husuperobject(4,3,42,"Megszámlálá azért Mózes, a mint parancsolta vala néki az Úr, Izráel fiainak minden elsõszülöttét."));
husuperobject.biblemap.put("b4_3_43", new husuperobject(4,3,43,"És lõn minden finemû elsõszülött a nevek száma szerint, egy hónapostól fogva és feljebb, az õ megszámláltjaik: huszonkét ezer kétszáz és hetvenhárom."));
husuperobject.biblemap.put("b4_3_44", new husuperobject(4,3,44,"És szóla az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_3_45", new husuperobject(4,3,45,"Válaszd a lévitákat az Izráel fiai közül való minden elsõszülött helyett; és a léviták barmait az õ barmaik helyett, és legyenek enyéim a léviták. Én vagyok az Úr."));
husuperobject.biblemap.put("b4_3_46", new husuperobject(4,3,46,"A mi pedig a kétszáz és hetvenháromnak megváltását illeti, a kik felül vannak a lévitákon Izráel fiainak elsõszülöttei közül:"));
husuperobject.biblemap.put("b4_3_47", new husuperobject(4,3,47,"Végy öt-öt siklust fejenként; a szent siklus szerint vedd azt, (húsz géra egy siklus)."));
husuperobject.biblemap.put("b4_3_48", new husuperobject(4,3,48,"És add azt a pénzt Áronnak és az õ fiainak, váltságul a köztök lévõ számfelettiekért."));
husuperobject.biblemap.put("b4_3_49", new husuperobject(4,3,49,"Bevevé azért Mózes a váltságpénzt azoktól, a kik felül voltak a lévitáktól megváltottakon."));
husuperobject.biblemap.put("b4_3_50", new husuperobject(4,3,50,"Izráel fiainak elsõszülöttitõl vevé be e pénzt: ezer háromszáz és hatvanöt siklust, a szent siklus szerint."));
husuperobject.biblemap.put("b4_3_51", new husuperobject(4,3,51,"És adá Mózes a megváltottaknak pénzét Áronnak és az õ fiainak, az Úr rendelete szerint, a miképen parancsolta az Úr Mózesnek."));
husuperobject.biblemap.put("b4_4_1", new husuperobject(4,4,1,"És szóla az Úr Mózesnek és Áronnak, mondván:"));
husuperobject.biblemap.put("b4_4_2", new husuperobject(4,4,2,"Vedd számba a Kéhát fiait Lévi fiai közül nemzetségenként, az õ atyjoknak háznépe szerint."));
husuperobject.biblemap.put("b4_4_3", new husuperobject(4,4,3,"Harmincz esztendõstõl és azon felül az ötven esztendõsökig mindenkit, a ki szolgálatra való, hogy munkálkodjék a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_4", new husuperobject(4,4,4,"Ez a tisztök Kéhát fiainak a gyülekezet sátorában: a legszentségesebbekrõl [való gondviselés]."));
husuperobject.biblemap.put("b4_4_5", new husuperobject(4,4,5,"Áron és az õ fiai pedig, mikor indulni akar a tábor, menje[nek] be, és vegyék le a takaró függönyt, és takarják be azzal a bizonyság ládáját."));
husuperobject.biblemap.put("b4_4_6", new husuperobject(4,4,6,"És tegyenek arra borzbõrbõl csinált takarót, és borítsák azt be egészen kékszínû ruhával felülrõl, és dugják belé a rúdjait is."));
husuperobject.biblemap.put("b4_4_7", new husuperobject(4,4,7,"A szent [kenyerek] asztalát is borítsák be kékszínû ruhával, azon felül tegyék rá a tálakat, a csészéket, a kelyheket, és az italáldozathoz való kancsókat, és ama szüntelen való kenyér is rajta legyen."));
husuperobject.biblemap.put("b4_4_8", new husuperobject(4,4,8,"Azután borítsanak azokra karmazsinszínû ruhát, és takarják be azt borzbõrbõl való takaróval, és dugják belé a rúdjait is."));
husuperobject.biblemap.put("b4_4_9", new husuperobject(4,4,9,"Vegyenek azután kékszínû ruhát, és takarják be a világításra való gyertyatartót és annak mécseit, hamvvevõit, hamutartóit és minden olajos edényét, a melyekkel szolgálnak körülte."));
husuperobject.biblemap.put("b4_4_10", new husuperobject(4,4,10,"És tegyék azt és minden edényét borzbõrbõl csinált takaróba, és tegyék saroglyára."));
husuperobject.biblemap.put("b4_4_11", new husuperobject(4,4,11,"Az arany oltárt is borítsák be kékszínû ruhával, és takarják be borzbõrbõl csinált takaróval, és dugják belé a rúdjait [is]."));
husuperobject.biblemap.put("b4_4_12", new husuperobject(4,4,12,"Vegyék elõ azután a szolgálatnak minden eszközét, a melyekkel szolgálni fognak a szenthelyen, és tegyék kékszínû ruhába, és takarják be azokat borzbõrbõl csinált takaróval, és tegyék a saroglyára."));
husuperobject.biblemap.put("b4_4_13", new husuperobject(4,4,13,"Azután takarítsák el a hamvat az oltárról, és borítsanak arra bíborpiros színû ruhát."));
husuperobject.biblemap.put("b4_4_14", new husuperobject(4,4,14,"És tegyék rá arra minden õ eszközét, a melyekkel szolgálnak azon: a szenes serpenyõket, a villákat, a lapátokat és a medenczéket, az oltárnak minden eszközét; és borítsanak arra borzbõrbõl csinált takarót, és dugják belé a rúdjait [is]."));
husuperobject.biblemap.put("b4_4_15", new husuperobject(4,4,15,"Ha pedig elvégezi Áron és az õ fiai a szenthelynek és a szenthely minden edényének betakarását, mikor el akar indulni a tábor: akkor jöjjenek el Kéhát fiai, hogy elvigyék azokat; de ne illessék a szenthelynek [semmi edényét], hogy meg ne haljanak. Ezek Kéhát fiainak terhei a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_16", new husuperobject(4,4,16,"Eleázárnak pedig, az Áron pap fiának tiszte: a világító olajra, a füstölõ szerekre, a szüntelen való ételáldozatra és a kenetnek olajára; az egész hajlékra és mindenre, a mi abban van, mind a szenthelyre, mind annak edényeire való gondviselés."));
husuperobject.biblemap.put("b4_4_17", new husuperobject(4,4,17,"Szóla azután az Úr Mózesnek és Áronnak mondván:"));
husuperobject.biblemap.put("b4_4_18", new husuperobject(4,4,18,"A Kéhátiták nemzetségének törzsét ne [engedjétek] kiirtani a léviták közül;"));
husuperobject.biblemap.put("b4_4_19", new husuperobject(4,4,19,"Hanem ezt míveljétek õ velök, hogy éljenek és meg ne haljanak, mikor járulnak a szentségek szentségéhez: Áron és az õ fiai jöjjenek el, és rendeljék el õket, kit-kit az õ szolgálatára és az õ terhére."));
husuperobject.biblemap.put("b4_4_20", new husuperobject(4,4,20,"Egy pillanatra se menjenek be, hogy meglássák a szenthelyet, hogy meg ne haljanak."));
husuperobject.biblemap.put("b4_4_21", new husuperobject(4,4,21,"Szóla ezután az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_4_22", new husuperobject(4,4,22,"Vedd számba a Gerson fiait is az õ atyjoknak háznépe szerint, az õ nemzetségök szerint."));
husuperobject.biblemap.put("b4_4_23", new husuperobject(4,4,23,"A harmincz esztendõstõl és azon felül az ötven esztendõsig számláld meg õket; mindenkit, a ki szolgálatot teljesíteni való, hogy szolgáljon a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_24", new husuperobject(4,4,24,"Ez legyen munkájok a Gersoniták nemzetségeinek a szolgálatban és a teher[hordozás]ban:"));
husuperobject.biblemap.put("b4_4_25", new husuperobject(4,4,25,"Hordozzák a hajlék kárpitjait és a gyülekezet sátorát, annak takaróját és a borzbõrbõl csinált takarót, a mely rajta van felül, és a gyülekezet sátora nyilásának leplét."));
husuperobject.biblemap.put("b4_4_26", new husuperobject(4,4,26,"És a pitvar szõnyegeit, és a pitvar kapunyílásának leplét, a melyek a hajlékon és az oltáron köröskörül vannak, azoknak köteleit, és az õ szolgálatjokhoz való minden edényt; és mindazt, a mi tenni való azokkal, õk teljesítsék."));
husuperobject.biblemap.put("b4_4_27", new husuperobject(4,4,27,"Áronnak és az õ fiainak beszéde szerint legyen a Gersoniták fiainak minden szolgálatjok, minden terhökre és minden szolgálatjokra nézve; minden terhöket pedig az õ õrizetökre bízzátok."));
husuperobject.biblemap.put("b4_4_28", new husuperobject(4,4,28,"Ez a Gersoniták fiai nemzetségének szolgálata a gyülekezet sátorában; az õ szolgálatuk pedig legyen az Áron pap fiának, Ithamárnak keze alatt."));
husuperobject.biblemap.put("b4_4_29", new husuperobject(4,4,29,"A Mérári fiait is az õ nemzetségeik szerint, az õ atyjoknak háznépei szerint számláld meg."));
husuperobject.biblemap.put("b4_4_30", new husuperobject(4,4,30,"A harmincz esztendõstõl és annál feljebb az ötven esztendõsig számláld meg õket; mindenkit, a ki szolgálatra való, hogy szolgálatot végezzen a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_31", new husuperobject(4,4,31,"Ezek pedig, a mikre ügyelniök kell a továbbvitelnél [és] a gyülekezet sátorában való minden szolgálatuknál: a hajlék deszkái, annak reteszrúdjai, oszlopai és talpai."));
husuperobject.biblemap.put("b4_4_32", new husuperobject(4,4,32,"A pitvarnak is köröskörül való oszlopai és azok talpai, szegei és kötelei, azoknak minden szerszámai, és minden szolgálatjok[hoz való]. Név szerint is megszámláljátok az edényeket, a mikre a továbbvitelnél ügyelniök kell."));
husuperobject.biblemap.put("b4_4_33", new husuperobject(4,4,33,"Ez legyen szolgálatuk a Mérári fiai nemzetségeinek, a melylyel szolgáljanak a gyülekezet sátorában, Ithamárnak, az Áron pap fiának vezetése alatt."));
husuperobject.biblemap.put("b4_4_34", new husuperobject(4,4,34,"És megszámlálá Mózes és Áron és a gyülekezet fejedelmei a Kéhátiták fiait, az õ nemzetségök szerint és az õ atyjoknak háznépe szerint,"));
husuperobject.biblemap.put("b4_4_35", new husuperobject(4,4,35,"A harmincz esztendõstõl és annál feljebb, az ötven esztendõsig mindenkit, a ki szolgálatra való, hogy szolgáljon a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_36", new husuperobject(4,4,36,"Vala pedig azoknak száma az õ nemzetségeik szerint: kétezer hétszáz és ötven."));
husuperobject.biblemap.put("b4_4_37", new husuperobject(4,4,37,"Ezek a Kéhátiták nemzetségeinek megszámláltjai, mindazok, a kik szolgálnak vala a gyülekezet sátorában, a kiket megszámlált Mózes és Áron az Úrnak parancsolatja szerint, a Mózes keze által."));
husuperobject.biblemap.put("b4_4_38", new husuperobject(4,4,38,"A Gerson fiainak száma az õ nemzetségeik szerint, és az õ atyáiknak háznépe szerint."));
husuperobject.biblemap.put("b4_4_39", new husuperobject(4,4,39,"A harmincz esztendõstõl és azon felül az ötven esztendõsig mindazok, a kik szolgálatra valók, hogy szolgáljanak a gyülekezet sátorában."));
husuperobject.biblemap.put("b4_4_40", new husuperobject(4,4,40,"Azoknak száma az õ nemzetségök szerint, az õ atyjoknak háznépe szerint: kétezer hatszáz és harmincz."));
husuperobject.biblemap.put("b4_4_41", new husuperobject(4,4,41,"Ezek a Gerson fiai nemzetségeinek megszámláltjai, a kik mindnyájan szolgálnak a gyülekezet sátorában, a kiket megszámlált Mózes és Áron az Úrnak beszéde szerint."));
husuperobject.biblemap.put("b4_4_42", new husuperobject(4,4,42,"A Mérári fiai nemzetségeinek száma az õ nemzetségei szerint, az õ atyjoknak háznépe szerint;"));
husuperobject.biblemap.put("b4_4_43", new husuperobject(4,4,43,"A harmincz esztendõstõl és azon felül az ötven esztendõsig mindazok, a kik szolgálatra valók, hogy szolgáljanak a gyülekezet sátorában;"));
husuperobject.biblemap.put("b4_4_44", new husuperobject(4,4,44,"Ezeknek száma az õ nemzetségeik szerint: háromezer és kétszáz."));
husuperobject.biblemap.put("b4_4_45", new husuperobject(4,4,45,"Ezek a Mérári fiai nemzetségeinek megszámláltjai, a kiket megszámlált Mózes és Áron az Úrnak beszéde szerint, a Mózes keze által."));
husuperobject.biblemap.put("b4_4_46", new husuperobject(4,4,46,"Mindnyájan a megszámláltatott léviták, a kiket megszámlált Mózes és Áron, és Izráel fejedelmei az õ nemzetségeik és atyjoknak háznépe szerint;"));
husuperobject.biblemap.put("b4_4_47", new husuperobject(4,4,47,"A harmincz esztendõstõl és azon felül az ötven esztendõsig, a kik csak alkalmatosak a szolgálatra, a szolgálatnak gyakorlására, és a továbbvitelnél való szolgálatra a gyülekezetnek sátorában;"));
husuperobject.biblemap.put("b4_4_48", new husuperobject(4,4,48,"Ezeknek száma lõn nyolczezer ötszáz és nyolczvan."));
husuperobject.biblemap.put("b4_4_49", new husuperobject(4,4,49,"Az Úrnak beszéde szerint számlálták meg õket a Mózes keze által kit-kit az õ szolgálata szerint, és az õ [vinni való] terhe szerint. Ez az a számlálás, a melyet az Úr parancsolt vala Mózesnek."));
husuperobject.biblemap.put("b4_5_1", new husuperobject(4,5,1,"És szóla az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_5_2", new husuperobject(4,5,2,"Parancsold meg Izráel fiainak, hogy ûzzenek ki a táborból minden poklost, és minden magfolyóst, és mindenkit, a ki holttest miatt lett tisztátalanná."));
husuperobject.biblemap.put("b4_5_3", new husuperobject(4,5,3,"Ûzzétek azt ki akár férfi, akár asszony; a táboron kivül ûzzétek õket, hogy tisztátalanná ne tegyék az õ táborukat, mivelhogy én közöttök lakozom."));
husuperobject.biblemap.put("b4_5_4", new husuperobject(4,5,4,"És úgy cselekedének Izráel fiai, és kiûzék azokat a táboron kivül; a miképen meghagyta vala az Úr Mózesnek, úgy cselekedtek Izráel fiai."));
husuperobject.biblemap.put("b4_5_5", new husuperobject(4,5,5,"Szóla azután az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_5_6", new husuperobject(4,5,6,"Szólj Izráel fiainak: Ha akár férfi, akár asszony, akármi emberi bûnt követ el, a mely által hûtelenné válik az Úrhoz; az a lélek vétkessé lesz."));
husuperobject.biblemap.put("b4_5_7", new husuperobject(4,5,7,"Vallja meg azért az õ bûnét, a melyet elkövetett, és fizesse meg a kárt, [a mit okozott,] teljes értékében, azután toldja ahhoz annak ötödrészét, és adja annak, a kinek kárt tett."));
husuperobject.biblemap.put("b4_5_8", new husuperobject(4,5,8,"Ha pedig nincs az embernek atyjafia, a kinek megfizetné a kárt: a megtérített kár az Úré legyen a pap számára, az engesztelésre való koson kivül, a melylyel engesztelés tétetik érte."));
husuperobject.biblemap.put("b4_5_9", new husuperobject(4,5,9,"Izráel fiainak minden szent dolgaiból minden felmutatott áldozat azé a papé legyen, a kihez viszik azt."));
husuperobject.biblemap.put("b4_5_10", new husuperobject(4,5,10,"És kinek-kinek az õ szenteltje a magáé legyen; a mit pedig kiki a papnak ád, azé legyen."));
husuperobject.biblemap.put("b4_5_11", new husuperobject(4,5,11,"Szóla azután az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_5_12", new husuperobject(4,5,12,"Szólj Izráel fiainak, és mondd meg nékik: Ha elhajol valakinek a felesége, és hûtelenné válik iránta;"));
husuperobject.biblemap.put("b4_5_13", new husuperobject(4,5,13,"És hál valaki õ vele közösülve, és az õ férje elõtt titok marad, és elrejtetik, hogy õ megfertõztetett; bizonyság pedig nincs ellene, sem a [bûnön] nem kapták;"));
husuperobject.biblemap.put("b4_5_14", new husuperobject(4,5,14,"De felgerjed õ benne a féltékenység lelke, és féltékenykedik a feleségére, mivelhogy az megfertõztetett; vagy felgerjed õ benne a féltékenység lelke, és féltékenykedik a feleségére, jóllehet az meg nem fertõztetett."));
husuperobject.biblemap.put("b4_5_15", new husuperobject(4,5,15,"Akkor vigye a férfiú az õ feleségét a paphoz; és vigye el azzal együtt az érette való áldozatot; egy efa árpalisztnek tizedrészét; [de] ne öntsön arra olajt, és ne tegyen arra temjént, mert féltékenységi ételáldozat ez, emlékeztetõ ételáldozat ez, a mely hamisságra emlékeztet."));
husuperobject.biblemap.put("b4_5_16", new husuperobject(4,5,16,"A pap pedig léptesse elõ [az asszonyt], és állassa õt az Úr elé."));
husuperobject.biblemap.put("b4_5_17", new husuperobject(4,5,17,"És vegyen a pap szent vizet cserépedénybe; a hajlék pádimentomán levõ porból is vegyen a pap, és tegye azt a vízbe."));
husuperobject.biblemap.put("b4_5_18", new husuperobject(4,5,18,"És állassa a pap az asszonyt az Úr elé és fejét meztelenítse meg az asszonynak, és tegye annak kezére az emlékeztetõ ételáldozatot, féltékenységi ételáldozat az; és a papnak kezében legyen átokhozó keserû víz."));
husuperobject.biblemap.put("b4_5_19", new husuperobject(4,5,19,"És eskesse meg õt a pap, és ezt mondja az asszonynak: Ha nem hált veled senki, és ha el nem hajoltál tisztátalanságra a te férjed mellett, ne ártson néked ez az átokhozó, keserû víz."));
husuperobject.biblemap.put("b4_5_20", new husuperobject(4,5,20,"Ha pedig elhajoltál a te férjed mellõl, és megfertõztetted magadat, és valaki közösült veled a te férjeden kívül,"));
husuperobject.biblemap.put("b4_5_21", new husuperobject(4,5,21,"Miután megeskette a pap az asszonyt az átoknak esküjével, ezt mondja az asszonynak: Tegyen tégedet az Úr átokká, és eskü-[példává] a te néped között, megszárasztván az Úr a te tomporodat, és a te méhedet dagadtá tévén."));
husuperobject.biblemap.put("b4_5_22", new husuperobject(4,5,22,"És menjen be az átokhozó víz a te belsõ részeidbe, hogy megdagadjon a te méhed, és megszáradjon a te tomporod. Az asszony pedig mondja: Ámen! Ámen!"));
husuperobject.biblemap.put("b4_5_23", new husuperobject(4,5,23,"És írja fel a pap ez átkokat levélre, azután törölje le a keserû vízzel."));
husuperobject.biblemap.put("b4_5_24", new husuperobject(4,5,24,"És itassa meg az asszonynyal az átokhozó keserû vizet, hogy bemenjen õ belé az átokhozó víz az õ keserûségére."));
husuperobject.biblemap.put("b4_5_25", new husuperobject(4,5,25,"Azután vegye el a pap az asszony kezébõl a féltékenységi ételáldozatot, és lóbálja meg azt az ételáldozatot az Úr elõtt, és áldozzék azzal az oltáron."));
husuperobject.biblemap.put("b4_5_26", new husuperobject(4,5,26,"És vegyen egy marokkal a pap az ételáldozatból emlékeztetõûl, és füstölögtesse el az oltáron, és azután itassa meg az asszonynyal a vizet."));
husuperobject.biblemap.put("b4_5_27", new husuperobject(4,5,27,"És ha megitatta vele a vizet, akkor lészen, [hogy], ha megfertõztette magát, és hûtelenné lett az õ férjéhez, bemegy az az átokhozó víz õ belé az õ keserûségére, és megdagad az õ méhe, és megszárad az õ tompora, és az az asszony átokká lesz az õ népe között."));
husuperobject.biblemap.put("b4_5_28", new husuperobject(4,5,28,"Ha pedig nem fertõztette meg magát az asszony, hanem tiszta: akkor ártatlan, és termékeny lészen."));
husuperobject.biblemap.put("b4_5_29", new husuperobject(4,5,29,"Ez a féltékenységi törvény, mikor elhajol az asszony az õ férje mellõl, és megfertõzteti magát;"));
husuperobject.biblemap.put("b4_5_30", new husuperobject(4,5,30,"Vagy mikor valaki, a kiben felgerjed a féltékenység lelke annyira, hogy féltékenykedik a feleségére; az õ feleségét állatja az Úr elé. És cselekedjék vele a pap mind e törvény szerint."));
husuperobject.biblemap.put("b4_5_31", new husuperobject(4,5,31,"És ártatlan lesz a férfi a bûntõl, az asszony pedig viseli az õ bûnének terhét."));
husuperobject.biblemap.put("b4_6_1", new husuperobject(4,6,1,"És szóla az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_6_2", new husuperobject(4,6,2,"Szólj Izráel fiainak, és mondd meg nékik: Mikor férfi vagy asszony külön fogadást tesz, nazireusi fogadást, hogy így az Úrnak szentelje magát:"));
husuperobject.biblemap.put("b4_6_3", new husuperobject(4,6,3,"Bortól és részegítõ italtól szakassza el magát; boreczetet és részegítõ italból való eczetet ne igyék, és semmi szõlõbõl csinált italt se igyék, se új, se asszú szõlõt ne egyék."));
husuperobject.biblemap.put("b4_6_4", new husuperobject(4,6,4,"Az õ nazireusságának egész idején át semmit a félét ne igyék, a mi a szõlõtõrõl kerül, a szõlõ magvától fogva a szõlõ héjáig."));
husuperobject.biblemap.put("b4_6_5", new husuperobject(4,6,5,"Az õ nazireusi fogadásának egész idején, beretva az õ fejét ne járja; míg be nem teljesednek a napok, a melyekre az Úrnak szentelte magát, szent legyen, hagyja növekedni az õ fejének hajfürteit."));
husuperobject.biblemap.put("b4_6_6", new husuperobject(4,6,6,"Az egész idõn át, a melyre az Úrnak szentelte magát, megholtnak testéhez be ne menjen."));
husuperobject.biblemap.put("b4_6_7", new husuperobject(4,6,7,"Se atyjának, se anyjának, se fiú- se leánytestvéreinek holttestével meg ne fertõztesse magát, mikor meghalnak, mert az õ Istenének nazireussága van az õ fején."));
husuperobject.biblemap.put("b4_6_8", new husuperobject(4,6,8,"Az õ nazireusságának egész idejében szent legyen az Úrnak."));
husuperobject.biblemap.put("b4_6_9", new husuperobject(4,6,9,"Ha pedig meghal valaki õ nála hirtelenséggel, és megfertõzteti az õ nazireus fejét: nyírja meg a fejét az õ tisztulásának napján, a hetedik napon nyírja meg azt."));
husuperobject.biblemap.put("b4_6_10", new husuperobject(4,6,10,"A nyolczadik napon pedig vigyen két gerliczét vagy két galambfiat a papnak a gyülekezet sátorának nyílásához."));
husuperobject.biblemap.put("b4_6_11", new husuperobject(4,6,11,"És készítse el a pap egyiket bûnért való áldozatul, a másikat pedig egészen égõáldozatul, és szerezzen néki engesztelést, a miért vétkezett a holttest miatt; és szentelje meg annak fejét azon napon."));
husuperobject.biblemap.put("b4_6_12", new husuperobject(4,6,12,"És az õ nazireusságának napjait szentelje [újra] az Úrnak, és vigyen az õ vétkéért való áldozatul egy esztendõs bárányt; az elébbi napok pedig essenek el, mert megfertõztette az õ nazireusságát."));
husuperobject.biblemap.put("b4_6_13", new husuperobject(4,6,13,"Ez pedig a nazireus törvénye: A mely napon betelik az õ nazireusságának ideje, vigyék õt a gyülekezet sátorának nyílásához."));
husuperobject.biblemap.put("b4_6_14", new husuperobject(4,6,14,"Õ pedig vigye fel az õ áldozatját az Úrnak: egy esztendõs, ép hím bárányt egészen égõáldozatul, és egy esztendõs, ép nõstény bárányt bûnért való áldozatul, és egy ép kost hálaáldozatul."));
husuperobject.biblemap.put("b4_6_15", new husuperobject(4,6,15,"Továbbá egy kosár kovásztalan kenyeret, olajjal elegyített lánglisztbõl való lepényeket, és olajjal megkent kovásztalan pogácsákat, a hozzájok való étel- és italáldozatokkal."));
husuperobject.biblemap.put("b4_6_16", new husuperobject(4,6,16,"És vigye azokat a pap az Úr elé, és készítse el annak bûnéért való áldozatát és egészen égõáldozatát."));
husuperobject.biblemap.put("b4_6_17", new husuperobject(4,6,17,"A kost is készítse el hálaadó áldozatul az Úrnak, a kosárban lévõ kovásztalan kenyerekkel egybe, és készítse el a pap az ahhoz való étel- és italáldozatot is."));
husuperobject.biblemap.put("b4_6_18", new husuperobject(4,6,18,"A nazireus pedig nyírja meg az õ nazireus fejét a gyülekezet sátorának nyílásánál, és vegye az õ nazireus fejének haját, és tegye azt a tûzre, a mely van a hálaadó áldozat alatt."));
husuperobject.biblemap.put("b4_6_19", new husuperobject(4,6,19,"Vegye azután a pap a kosnak megfõtt lapoczkáját, és egy kovásztalan lepényt a kosárból, és egy kovásztalan pogácsát, és tegye a nazireus tenyerére, minekutána megnyirta az õ nazireus [fejét]."));
husuperobject.biblemap.put("b4_6_20", new husuperobject(4,6,20,"És lóbálja meg a pap azokat áldozatul az Úr elõtt; a papnak szenteltetett ez, a meglóbált szegyen és a felemelt lapoczkán felül. Azután igyék bort a nazireus."));
husuperobject.biblemap.put("b4_6_21", new husuperobject(4,6,21,"Ez a nazireus törvénye, a ki fogadást tett, [és] az õ áldozata az õ nazireusságáért az Úrnak, azonkivül, a mihez módja van. Az õ fogadása szerint, a melyet fogadott, a képen cselekedjék, az õ nazireusságának törvénye szerint."));
husuperobject.biblemap.put("b4_6_22", new husuperobject(4,6,22,"És szóla az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_6_23", new husuperobject(4,6,23,"Szólj Áronnak és az õ fiainak, mondván: Így áldjátok meg Izráel fiait, mondván nékik:"));
husuperobject.biblemap.put("b4_6_24", new husuperobject(4,6,24,"Áldjon meg tégedet az Úr, és õrizzen meg tégedet."));
husuperobject.biblemap.put("b4_6_25", new husuperobject(4,6,25,"Világosítsa meg az Úr az õ orczáját te rajtad, és könyörüljön te rajtad."));
husuperobject.biblemap.put("b4_6_26", new husuperobject(4,6,26,"Fordítsa az Úr az õ orczáját te reád, és adjon békességet néked."));
husuperobject.biblemap.put("b4_6_27", new husuperobject(4,6,27,"Így tegyék az én nevemet Izráel fiaira, hogy én megáldjam õket."));
husuperobject.biblemap.put("b4_7_1", new husuperobject(4,7,1,"És lõn, hogy a mely napon elvégezé Mózes a sátor felállítását, és felkené azt, és megszentelé azt, minden edényével egybe, és az oltárt és annak minden edényét; és felkené és megszentelé azokat:"));
husuperobject.biblemap.put("b4_7_2", new husuperobject(4,7,2,"Akkor elõjövének az Izráel fejedelmei, az õ atyjok házának fejei; ezek a törzsek fejedelmei, és ezek a megszámláltattak felügyelõi:"));
husuperobject.biblemap.put("b4_7_3", new husuperobject(4,7,3,"És vivék az õ áldozatukat az Úr elé: hat borított szekeret, és tizenkét ökröt; egy-egy szekeret két-két fejedelemért, és mindenikért egy-egy ökröt; és odavivék azokat a sátor elébe."));
husuperobject.biblemap.put("b4_7_4", new husuperobject(4,7,4,"És szóla az Úr Mózesnek, mondván:"));
husuperobject.biblemap.put("b4_7_5", new husuperobject(4,7,5,"Vedd el õ tõlök, és legyenek azok felhasználva a gyülekezet sátorának szolgálatában; és add azokat a lévitáknak, mindeniknek az õ szolgálata szerint."));
husuperobject.biblemap.put("b4_7_6", new husuperobject(4,7,6,"Elvevé azért Mózes a szekereket és ökröket, és adá azokat a lévitáknak."));
husuperobject.biblemap.put("b4_7_7", new husuperobject(4,7,7,"Két szekeret és négy ökröt ada a Gerson fiainak, az õ szolgálatuk szerint."));
husuperobject.biblemap.put("b4_7_8", new husuperobject(4,7,8,"Négy szekeret pedig és nyolcz ökröt ada a Mérári fiainak, az õ szolgálatuk szerint, Ithamárnak, Áron pap fiának keze alá."));
husuperobject.biblemap.put("b4_7_9", new husuperobject(4,7,9,"A Kéhát fiainak pedig [semmit] nem ada; mert a szentség szolgálata illette vala õket, a melyet vállon hordoznak vala."));
husuperobject.biblemap.put("b4_7_10", new husuperobject(4,7,10,"Vivének pedig a fejedelmek az oltár felszentelésére [valókat] azon napon, a melyen az felkenetett, és vivék a fejedelmek az õ áldozatukat az oltár elébe."));
husuperobject.biblemap.put("b4_7_11", new husuperobject(4,7,11,"És monda az Úr Mózesnek: Egyik napon egyik fejedelem, másik napon másik fejedelem vigye az õ áldozatát az oltár felszentelésére."));
husuperobject.biblemap.put("b4_7_12", new husuperobject(4,7,12,"És vivé elsõ napon az õ áldozatát Naasson, az Amminádáb fia, Júda nemzetségébõl."));
husuperobject.biblemap.put("b4_7_13", new husuperobject(4,7,13,"Vala pedig az õ áldozata, egy ezüst tál, száz és harmincz siklus súlyú, egy ezüst medencze hetven siklus súlyú, a szent siklus szerint, és mind a kettõ telve vala olajjal elegyített lisztlánggal, ételáldozatul."));
husuperobject.biblemap.put("b4_7_14", new husuperobject(4,7,14,"Egy arany csésze, tíz [siklus súlyú], füstölõ szerekkel telve."));
husuperobject.biblemap.put("b4_7_15", new husuperobject(4,7,15,"Egy fiatal tulok, egy kos, egy esztendõs bárány egészen égõáldozatul."));
husuperobject.biblemap.put("b4_7_16", new husuperobject(4,7,16,"Egy kecskebak bûnért való áldozatul."));
husuperobject.biblemap.put("b4_7_17", new husuperobject(4,7,17,"Hálaadó áldozatul pedig két ökör, öt kos, öt kecskebak, öt bárány, esztendõsök. Ez Naassonnak, Amminádáb fiának áldozata."));
husuperobject.biblemap.put("b4_7_18", new husuperobject(4,7,18,"Másodnapon vivé Néthanéel, Suárnak fia, Izsakhár [nemzetségének] fejedelme."));
husuperobject.biblemap.put("b4_7_19", new husuperobject(4,7,19,"Vive az õ áldozatául egy ezüst tálat, száz és harmincz siklus súlyút, egy ezüst medenczét, hetven siklus súlyút, a szent siklus szerint, mind a kettõ telve vala olajjal elegyített lisztlánggal, ételáldozatul."));
husuperobject.biblemap.put("b4_7_20", new husuperobject(4,7,20,"Egy arany csészét, tíz [siklus súlyút], füstölõ szerekkel telve."));
husuperobject.biblemap.put("b4_7_21", new husuperobject(4,7,21,"Egy fiatal tulkot, egy kost, egy esztendõs bárányt egészen égõáldozatul."));
husuperobject.biblemap.put("b4_7_22", new husuperobject(4,7,22,"Egy kecskebakot bûnért való áldozatul."));
husuperobject.biblemap.put("b4_7_23", new husuperobject(4,7,23,"Hálaadó áldozatul pedig két ökröt, öt kost, öt bakot, öt bárányt, esztendõsöket. Ez Néthanéelnek a Suár fiának áldozata."));
husuperobject.biblemap.put("b4_7_24", new husuperobject(4,7,24,"Harmadik napon a Zebulon fiainak fejedelme: Eliáb, Hélon fia."));
husuperobject.biblemap.put("b4_7_25", new husuperobject(4,7,25,"Az õ áldozata volt egy ezüst tál, száz harmincz siklus súlyú, egy ezüst medencze, hetven siklus súlyú, a szent siklus szerint, mind a kettõ telve vala olajjal elegyített lisztlánggal, ételáldozatul."));
husuperobject.biblemap.put("b4_7_26", new husuperobject(4,7,26,"Egy arany csésze, tíz [siklus súlyú] füstölõ szerekkel telve."));
husuperobject.biblemap.put("b4_7_27", new husuperobject(4,7,27,"Egy fiatal tulok, egy kos, egy esztendõs bárány, egészen égõáldozatul."));
