package hu.bible;

public class hubible43 {

public hubible43() {

putmapbible43();

}

private void putmapbible43() {

husuperobject.biblemap.put("b43_1_1", new husuperobject(43,1,1,"Kezdetben vala az Íge, és az Íge vala az Istennél, és Isten vala az Íge."));
husuperobject.biblemap.put("b43_1_2", new husuperobject(43,1,2,"Ez kezdetben az Istennél vala."));
husuperobject.biblemap.put("b43_1_3", new husuperobject(43,1,3,"Minden õ általa lett és nála nélkül semmi sem lett, a mi lett."));
husuperobject.biblemap.put("b43_1_4", new husuperobject(43,1,4,"Õ benne vala az élet, és az élet vala az emberek világossága;"));
husuperobject.biblemap.put("b43_1_5", new husuperobject(43,1,5,"És a világosság a sötétségben fénylik, de a sötétség nem fogadta be azt."));
husuperobject.biblemap.put("b43_1_6", new husuperobject(43,1,6,"Vala egy Istentõl küldött ember, kinek neve János."));
husuperobject.biblemap.put("b43_1_7", new husuperobject(43,1,7,"Ez jött tanúbizonyságul, hogy bizonyságot tegyen a világosságról, hogy mindenki higyjen õ általa."));
husuperobject.biblemap.put("b43_1_8", new husuperobject(43,1,8,"Nem õ vala a világosság, hanem [jött,] hogy bizonyságot tegyen a világosságról."));
husuperobject.biblemap.put("b43_1_9", new husuperobject(43,1,9,"Az igazi világosság eljött volt [már] a világba, a mely megvilágosít minden embert."));
husuperobject.biblemap.put("b43_1_10", new husuperobject(43,1,10,"A világban volt és a világ általa lett, de a világ nem ismerte meg õt."));
husuperobject.biblemap.put("b43_1_11", new husuperobject(43,1,11,"Az övéi közé jöve, és az övéi nem fogadák be õt."));
husuperobject.biblemap.put("b43_1_12", new husuperobject(43,1,12,"Valakik pedig befogadák õt, hatalmat ada azoknak, hogy Isten fiaivá legyenek, azoknak, a kik az õ nevében hisznek;"));
husuperobject.biblemap.put("b43_1_13", new husuperobject(43,1,13,"A kik nem vérbõl, sem a testnek akaratából, sem a férfiúnak indulatjából, hanem Istentõl születtek."));
husuperobject.biblemap.put("b43_1_14", new husuperobject(43,1,14,"És az Íge testté lett és lakozék mi közöttünk (és láttuk az õ dicsõségét, mint az Atya egyszülöttjének dicsõségét), a ki teljes vala kegyelemmel és igazsággal."));
husuperobject.biblemap.put("b43_1_15", new husuperobject(43,1,15,"János bizonyságot tett õ róla, és kiáltott, mondván: Ez vala, a kirõl mondám: A ki utánam jõ, elõttem lett, mert elõbb volt nálamnál."));
husuperobject.biblemap.put("b43_1_16", new husuperobject(43,1,16,"És az õ teljességébõl vettünk mindnyájan kegyelmet is kegyelemért."));
husuperobject.biblemap.put("b43_1_17", new husuperobject(43,1,17,"Mert a törvény Mózes által adatott, a kegyelem [pedig] és az igazság Jézus Krisztus által lett."));
husuperobject.biblemap.put("b43_1_18", new husuperobject(43,1,18,"Az Istent soha senki nem látta; az egyszülött Fiú, a ki az Atya kebelében van, az jelentette ki [õt.]"));
husuperobject.biblemap.put("b43_1_19", new husuperobject(43,1,19,"És ez a János bizonyságtétele, a mikor a zsidók papokat és Lévitákat küldöttek Jeruzsálembõl, hogy megkérdezzék õt: Kicsoda vagy te?"));
husuperobject.biblemap.put("b43_1_20", new husuperobject(43,1,20,"És megvallá és nem tagadá; és megvallá, hogy: Nem én vagyok a Krisztus."));
husuperobject.biblemap.put("b43_1_21", new husuperobject(43,1,21,"És kérdezék õt: Kicsoda tehát? Illés vagy-é te? És monda: Nem vagyok. A próféta vagy-é te? És õ felele: Nem."));
husuperobject.biblemap.put("b43_1_22", new husuperobject(43,1,22,"Mondának azért néki: Kicsoda vagy? Hogy megfelelhessünk azoknak, a kik minket elküldöttek: Mit mondasz magad felõl?"));
husuperobject.biblemap.put("b43_1_23", new husuperobject(43,1,23,"Monda: Én kiáltó szó vagyok a pusztában. Egyengessétek az Úrnak útját, a mint megmondotta Ésaiás próféta."));
husuperobject.biblemap.put("b43_1_24", new husuperobject(43,1,24,"És a küldöttek a farizeusok közül valók voltak:"));
husuperobject.biblemap.put("b43_1_25", new husuperobject(43,1,25,"És megkérdék õt és mondának néki: Miért keresztelsz tehát, ha te nem vagy a Krisztus, sem Illés, sem a próféta?"));
husuperobject.biblemap.put("b43_1_26", new husuperobject(43,1,26,"Felele nékik János, mondván: Én vízzel keresztelek; de köztetek van, a kit ti nem ismertek."));
husuperobject.biblemap.put("b43_1_27", new husuperobject(43,1,27,"Õ az, a ki utánam jõ, a ki elõttem lett, a kinek én nem vagyok méltó, hogy saruja szíjját megoldjam."));
husuperobject.biblemap.put("b43_1_28", new husuperobject(43,1,28,"Ezek Béthabarában lettek, a Jordánon túl, a hol János keresztel vala."));
husuperobject.biblemap.put("b43_1_29", new husuperobject(43,1,29,"Másnap látá János Jézust õ hozzá menni, és monda: Ímé az Istennek ama báránya, a ki elveszi a világ bûneit!"));
husuperobject.biblemap.put("b43_1_30", new husuperobject(43,1,30,"Ez az, a kirõl én ezt mondám: Én utánam jõ egy férfiú, a ki elõttem lett, mert elõbb volt nálamnál."));
husuperobject.biblemap.put("b43_1_31", new husuperobject(43,1,31,"És én nem ismertem õt; de hogy megjelentessék Izráelnek, azért jöttem én, a ki vízzel keresztelek."));
husuperobject.biblemap.put("b43_1_32", new husuperobject(43,1,32,"És bizonyságot tõn János, mondván: Láttam a Lelket leszállani az égbõl, mint egy galambot; és megnyugovék õ rajta."));
husuperobject.biblemap.put("b43_1_33", new husuperobject(43,1,33,"És én nem ismertem õt; de a ki elkülde engem, hogy vízzel kereszteljek, az mondá nékem: A kire látod a Lelket leszállani és rajta megnyugodni, az az, a ki keresztel Szent Lélekkel."));
husuperobject.biblemap.put("b43_1_34", new husuperobject(43,1,34,"És én láttam, és bizonyságot tettem, hogy ez az Isten Fia."));
husuperobject.biblemap.put("b43_1_35", new husuperobject(43,1,35,"Másnap ismét [ott] állt vala János és kettõ az õ tanítványai közül;"));
husuperobject.biblemap.put("b43_1_36", new husuperobject(43,1,36,"És ránézvén Jézusra, a mint [ott] jár vala, monda: Ímé az Isten Báránya!"));
husuperobject.biblemap.put("b43_1_37", new husuperobject(43,1,37,"És hallá õt a két tanítvány, a mint szól vala, és követék Jézust."));
husuperobject.biblemap.put("b43_1_38", new husuperobject(43,1,38,"Jézus pedig hátrafordulván és látván, hogy követik azok, monda nékik:"));
husuperobject.biblemap.put("b43_1_39", new husuperobject(43,1,39,"Mit kerestek? Azok pedig mondának néki: Rabbi, (a mi megmagyarázva azt teszi: Mester) hol lakol?"));
husuperobject.biblemap.put("b43_1_40", new husuperobject(43,1,40,"Monda nékik: Jõjjetek és lássátok meg. Elmenének és megláták, hol lakik; és nála maradának azon a napon: vala pedig körülbelül tíz óra."));
husuperobject.biblemap.put("b43_1_41", new husuperobject(43,1,41,"A kettõ közül, a kik Jánostól [ezt] hallották és õt követték vala, András volt az egyik, a Simon Péter testvére."));
husuperobject.biblemap.put("b43_1_42", new husuperobject(43,1,42,"Találkozék ez elõször a maga testvérével, Simonnal, és monda néki: Megtaláltuk a Messiást (a mi megmagyarázva azt teszi: Krisztus);"));
husuperobject.biblemap.put("b43_1_43", new husuperobject(43,1,43,"És vezeté õt Jézushoz. Jézus pedig reá tekintvén, monda: Te Simon vagy, a Jóna fia; te Kéfásnak fogsz hivatni (a mi megmagyarázva: Kõszikla)."));
husuperobject.biblemap.put("b43_1_44", new husuperobject(43,1,44,"A következõ napon Galileába akart menni Jézus; és találkozék Fileppel, és monda néki: Kövess engem!"));
husuperobject.biblemap.put("b43_1_45", new husuperobject(43,1,45,"Filep pedig Bethsaidából, az András és Péter városából való volt."));
husuperobject.biblemap.put("b43_1_46", new husuperobject(43,1,46,"Találkozék Filep Nátánaellel, és monda néki: A ki felõl írt Mózes a törvényben, és a próféták, megtaláltuk a názáreti Jézust, Józsefnek fiát."));
husuperobject.biblemap.put("b43_1_47", new husuperobject(43,1,47,"És monda néki Nátánael: Názáretbõl támadhat-é valami jó? Monda néki Filep: Jer és lásd meg!"));
husuperobject.biblemap.put("b43_1_48", new husuperobject(43,1,48,"Látá Jézus Nátánaelt õ hozzá menni, és monda õ felõle: Ímé egy igazán Izráelita, a kiben hamisság nincsen."));
husuperobject.biblemap.put("b43_1_49", new husuperobject(43,1,49,"Monda néki Nátánael: Honnan ismersz engem? Felele Jézus és monda néki: Mielõtt hítt téged Filep, láttalak téged, a mint a fügefa alatt voltál."));
husuperobject.biblemap.put("b43_1_50", new husuperobject(43,1,50,"Felele Nátánael és monda néki: Rabbi, te vagy az Isten Fia, te vagy az Izráel Királya!"));
husuperobject.biblemap.put("b43_1_51", new husuperobject(43,1,51,"Felele Jézus és monda néki: Hogy azt mondám néked: láttalak a fügefa alatt, hiszel? Nagyobbakat látsz majd ezeknél. [ (John 1:52) És monda néki: Bizony, bizony mondom néktek: Mostantól fogva meglátjátok a megnyilt eget, és az Isten angyalait, a mint felszállnak és leszállnak az ember Fiára. ]"));
husuperobject.biblemap.put("b43_2_1", new husuperobject(43,2,1,"És harmadnapon menyegzõ lõn a galileai Kánában; és ott volt a Jézus anyja;"));
husuperobject.biblemap.put("b43_2_2", new husuperobject(43,2,2,"És Jézus is meghivaték az õ tanítványaival együtt a menyegzõbe."));
husuperobject.biblemap.put("b43_2_3", new husuperobject(43,2,3,"És elfogyván a bor, a Jézus anyja monda néki: Nincs boruk."));
husuperobject.biblemap.put("b43_2_4", new husuperobject(43,2,4,"Monda néki Jézus: Mi közöm nékem te hozzád, oh asszony? Nem jött még el az én órám."));
husuperobject.biblemap.put("b43_2_5", new husuperobject(43,2,5,"Mond az õ anyja a szolgáknak: Valamit mond néktek, megtegyétek."));
husuperobject.biblemap.put("b43_2_6", new husuperobject(43,2,6,"Vala pedig ott hat kõveder elhelyezve a zsidók tisztálkodási módja szerint, melyek közül egybe-egybe két-három métréta fér vala."));
husuperobject.biblemap.put("b43_2_7", new husuperobject(43,2,7,"Monda nékik Jézus: Töltsétek meg a vedreket vízzel. És megtölték azokat színig."));
husuperobject.biblemap.put("b43_2_8", new husuperobject(43,2,8,"És monda nékik: Most merítsetek, és vigyetek a násznagynak. És vittek."));
husuperobject.biblemap.put("b43_2_9", new husuperobject(43,2,9,"A mint pedig megízlelé a násznagy a borrá lett vizet, és nem tudja vala, honnét van, (de a szolgák tudták, a kik a vizet merítik vala), szólítá a násznagy a võlegényt,"));
husuperobject.biblemap.put("b43_2_10", new husuperobject(43,2,10,"És monda néki: Minden ember a jó bort adja fel elõször, és mikor megittasodtak, akkor az alábbvalót: te a jó bort ekkorra tartottad."));
husuperobject.biblemap.put("b43_2_11", new husuperobject(43,2,11,"Ezt az elsõ jelt a galileai Kánában tevé Jézus, és megmutatá az õ dicsõségét; és hivének benne az õ tanítványai."));
husuperobject.biblemap.put("b43_2_12", new husuperobject(43,2,12,"Azután leméne Kapernaumba, õ és az õ anyja és a testvérei és tanítványai; és ott maradának néhány napig,"));
husuperobject.biblemap.put("b43_2_13", new husuperobject(43,2,13,"Mert közel vala a zsidók husvétja, és felméne Jézus Jeruzsálembe."));
husuperobject.biblemap.put("b43_2_14", new husuperobject(43,2,14,"És [ott] találá a templomban az ökrök, juhok és galambok árúsait és a pénzváltókat, a mint ülnek vala:"));
husuperobject.biblemap.put("b43_2_15", new husuperobject(43,2,15,"És kötélbõl ostort csinálván, kiûzé mindnyájokat a templomból, az ökröket is a juhokat is; és a pénzváltók pénzét kitölté, az asztalokat pedig feldönté;"));
husuperobject.biblemap.put("b43_2_16", new husuperobject(43,2,16,"És a galambárúsoknak monda: Hordjátok el ezeket innen; ne tegyétek az én Atyámnak házát kalmárság házává."));
husuperobject.biblemap.put("b43_2_17", new husuperobject(43,2,17,"Megemlékezének pedig az õ tanítványai, hogy meg van írva: A te házadhoz való féltõ szeretet emészt engem."));
husuperobject.biblemap.put("b43_2_18", new husuperobject(43,2,18,"Felelének azért a zsidók és mondának néki: Micsoda jelt mutatsz nékünk, hogy ezeket cselekszed?"));
husuperobject.biblemap.put("b43_2_19", new husuperobject(43,2,19,"Felele Jézus és monda nékik: Rontsátok le a templomot, és három nap alatt megépítem azt."));
husuperobject.biblemap.put("b43_2_20", new husuperobject(43,2,20,"Mondának azért a zsidók: Negyvenhat esztendeig épült ez a templom, és te három nap alatt megépíted azt?"));
husuperobject.biblemap.put("b43_2_21", new husuperobject(43,2,21,"Õ pedig az õ testének templomáról szól vala."));
husuperobject.biblemap.put("b43_2_22", new husuperobject(43,2,22,"Mikor azért feltámadt a halálból, megemlékezének az õ tanítványai, hogy ezt mondta; és hivének az írásnak, és a beszédnek, a melyet Jézus mondott vala."));
husuperobject.biblemap.put("b43_2_23", new husuperobject(43,2,23,"A mint pedig Jeruzsálemben vala husvétkor az ünnepen, sokan hivének az õ nevében, látván az õ jeleit, a melyeket cselekszik vala."));
husuperobject.biblemap.put("b43_2_24", new husuperobject(43,2,24,"Maga azonban Jézus nem bízza vala magát reájok, a miatt, hogy õ ismeré mindnyájokat,"));
husuperobject.biblemap.put("b43_2_25", new husuperobject(43,2,25,"És mivelhogy nem szorult rá, hogy valaki bizonyságot tegyen az emberrõl; mert magától [is] tudta, mi volt az emberben."));
husuperobject.biblemap.put("b43_3_1", new husuperobject(43,3,1,"Vala pedig a farizeusok közt egy ember, a neve Nikodémus, a zsidók fõembere:"));
husuperobject.biblemap.put("b43_3_2", new husuperobject(43,3,2,"Ez jöve Jézushoz éjjel, és monda néki: Mester, tudjuk, hogy Istentõl jöttél tanítóul; mert senki sem teheti e jeleket, a melyeket te teszel, hanem ha az Isten van vele."));
husuperobject.biblemap.put("b43_3_3", new husuperobject(43,3,3,"Felele Jézus és monda néki: Bizony, bizony mondom néked: ha valaki újonnan nem születik, nem láthatja az Isten országát."));
husuperobject.biblemap.put("b43_3_4", new husuperobject(43,3,4,"Monda néki Nikodémus: Mimódon születhetik az ember, ha vén? Vajjon bemehet-é az õ anyjának méhébe másodszor, és születhetik-é?"));
husuperobject.biblemap.put("b43_3_5", new husuperobject(43,3,5,"Felele Jézus: Bizony, bizony mondom néked: Ha valaki nem születik víztõl és Lélektõl, nem mehet be az Isten országába."));
husuperobject.biblemap.put("b43_3_6", new husuperobject(43,3,6,"A mi testtõl született, test az; és a mi Lélektõl született, lélek az."));
husuperobject.biblemap.put("b43_3_7", new husuperobject(43,3,7,"Ne csodáld, hogy azt mondám néked: Szükség néktek újonnan születnetek."));
husuperobject.biblemap.put("b43_3_8", new husuperobject(43,3,8,"A szél fú, a hová akar, és annak zúgását hallod, de nem tudod honnan jõ és hová megy: így van mindenki, a ki Lélektõl született."));
husuperobject.biblemap.put("b43_3_9", new husuperobject(43,3,9,"Felele Nikodémus és monda néki: Mimódon lehetnek ezek?"));
husuperobject.biblemap.put("b43_3_10", new husuperobject(43,3,10,"Felele Jézus és monda néki: Te Izráel tanítója vagy, és nem tudod ezeket?"));
husuperobject.biblemap.put("b43_3_11", new husuperobject(43,3,11,"Bizony, bizony mondom néked, a mit tudunk, azt mondjuk, és a mit látunk, arról teszünk bizonyságot; és a mi bizonyságtételünket el nem fogadjátok."));
husuperobject.biblemap.put("b43_3_12", new husuperobject(43,3,12,"Ha a földiekrõl szóltam néktek és nem hisztek, mimódon hisztek, ha a mennyeiekrõl szólok néktek?"));
husuperobject.biblemap.put("b43_3_13", new husuperobject(43,3,13,"És senki sem ment fel a mennybe, hanemha az, a ki a mennybõl szállott alá, az embernek Fia, a ki a mennyben van."));
husuperobject.biblemap.put("b43_3_14", new husuperobject(43,3,14,"És a miképen felemelte Mózes a kígyót a pusztában, akképen kell az ember Fiának felemeltetnie."));
husuperobject.biblemap.put("b43_3_15", new husuperobject(43,3,15,"Hogy valaki hiszen õ benne, el ne veszszen, hanem örök élete legyen."));
husuperobject.biblemap.put("b43_3_16", new husuperobject(43,3,16,"Mert úgy szerette Isten e világot, hogy az õ egyszülött Fiát adta, hogy valaki hiszen õ benne, el ne vesszen, hanem örök élete legyen."));
husuperobject.biblemap.put("b43_3_17", new husuperobject(43,3,17,"Mert nem azért küldte az Isten az õ Fiát a világra, hogy kárhoztassa a világot, hanem hogy megtartassék a világ általa."));
husuperobject.biblemap.put("b43_3_18", new husuperobject(43,3,18,"A ki hiszen õ benne, el nem kárhozik; a ki pedig nem hisz, immár elkárhozott, mivelhogy nem hitt az Isten egyszülött Fiának nevében."));
husuperobject.biblemap.put("b43_3_19", new husuperobject(43,3,19,"Ez pedig a kárhoztatás, hogy a világosság e világra jött, és az emberek inkább szerették a sötétséget, mint a világosságot; mert az õ cselekedeteik gonoszak valának."));
husuperobject.biblemap.put("b43_3_20", new husuperobject(43,3,20,"Mert minden, a ki hamisan cselekszik, gyûlöli a világosságot és nem megy a világosságra, hogy az õ cselekedetei fel ne fedessenek;"));
husuperobject.biblemap.put("b43_3_21", new husuperobject(43,3,21,"A ki pedig az igazságot cselekszi, az a világosságra megy, hogy az õ cselekedetei nyilvánvalókká legyenek, hogy Isten szerint való cselekedetek."));
husuperobject.biblemap.put("b43_3_22", new husuperobject(43,3,22,"Ezután elméne Jézus az õ tanítványaival a Júdea földére; és ott idõzék velök, és keresztele."));
husuperobject.biblemap.put("b43_3_23", new husuperobject(43,3,23,"János pedig szintén keresztel vala Énonban, Sálemhez közel, mert ott sok volt a víz. És oda járulának és megkeresztelkedének."));
husuperobject.biblemap.put("b43_3_24", new husuperobject(43,3,24,"Mert János még nem vetteték a tömlöczbe."));
husuperobject.biblemap.put("b43_3_25", new husuperobject(43,3,25,"Vetekedés támada azért a János tanítványai és a judeaiak között a mosakodás felõl."));
husuperobject.biblemap.put("b43_3_26", new husuperobject(43,3,26,"És menének Jánoshoz és mondának néki: Mester! A ki veled vala a Jordánon túl, a kirõl te bizonyságot tettél, ímé az keresztel, és hozzá megy mindenki."));
husuperobject.biblemap.put("b43_3_27", new husuperobject(43,3,27,"Felele János és monda: Az ember semmit sem vehet, hanem ha a mennybõl adatott néki."));
husuperobject.biblemap.put("b43_3_28", new husuperobject(43,3,28,"Ti magatok vagytok a bizonyságaim, hogy megmondtam: Nem vagyok én a Krisztus, hanem hogy õ elõtte küldettem el."));
husuperobject.biblemap.put("b43_3_29", new husuperobject(43,3,29,"A kinek jegyese van, võlegény az; a võlegény barátja pedig, a ki ott áll és hallja õt, örvendezve örül a võlegény szavának. Ez az én örömem immár betelt."));
husuperobject.biblemap.put("b43_3_30", new husuperobject(43,3,30,"Annak növekednie kell, nékem pedig alább szállanom."));
husuperobject.biblemap.put("b43_3_31", new husuperobject(43,3,31,"A ki felülrõl jött, feljebb való mindenkinél. A ki a földrõl való, földi az és földieket szól; a ki a mennybõl jött, feljebb való mindenkinél."));
husuperobject.biblemap.put("b43_3_32", new husuperobject(43,3,32,"És arról tesz bizonyságot, a mit látott és hallott; és az õ bizonyságtételét senki sem fogadja be."));
husuperobject.biblemap.put("b43_3_33", new husuperobject(43,3,33,"A ki az õ bizonyságtételét befogadja, az megpecsételte, hogy az Isten igaz."));
husuperobject.biblemap.put("b43_3_34", new husuperobject(43,3,34,"Mert a kit az Isten küldött, az Isten beszédeit szólja; mivelhogy az Isten nem mérték szerint adja a Lelket."));
husuperobject.biblemap.put("b43_3_35", new husuperobject(43,3,35,"Az Atya szereti a Fiút, és az õ kezébe adott mindent."));
husuperobject.biblemap.put("b43_3_36", new husuperobject(43,3,36,"A ki hisz a Fiúban, örök élete van; a ki pedig nem enged a Fiúnak, nem lát életet, hanem az Isten haragja marad rajta."));
husuperobject.biblemap.put("b43_4_1", new husuperobject(43,4,1,"Amint azért megtudta az Úr, hogy a farizeusok meghallották, hogy Jézus több tanítványt szerez és keresztel, mint János,"));
husuperobject.biblemap.put("b43_4_2", new husuperobject(43,4,2,"(Jóllehet Jézus maga nem keresztelt, hanem a tanítványai,)"));
husuperobject.biblemap.put("b43_4_3", new husuperobject(43,4,3,"Elhagyá Júdeát és elméne ismét Galileába."));
husuperobject.biblemap.put("b43_4_4", new husuperobject(43,4,4,"Samárián kell vala pedig általmennie."));
husuperobject.biblemap.put("b43_4_5", new husuperobject(43,4,5,"Megy vala azért Samáriának Sikár nevû városába, annak a teleknek szomszédjába, a melyet Jákób adott vala az õ fiának, Józsefnek."));
husuperobject.biblemap.put("b43_4_6", new husuperobject(43,4,6,"Ott vala pedig a Jákób forrása. Jézus azért, az utazástól elfáradva, azonmód leüle a forráshoz. Mintegy hat óra vala."));
husuperobject.biblemap.put("b43_4_7", new husuperobject(43,4,7,"Jöve egy samáriabeli asszony vizet meríteni; monda néki Jézus: Adj innom!"));
husuperobject.biblemap.put("b43_4_8", new husuperobject(43,4,8,"Az õ tanítványai ugyanis elmentek a városba, hogy ennivalót vegyenek."));
husuperobject.biblemap.put("b43_4_9", new husuperobject(43,4,9,"Monda azért néki a samáriai asszony: Hogy kérhetsz inni zsidó létedre én tõlem, a ki samáriai asszony vagyok?! Mert a zsidók nem barátkoznak a samáriaiakkal."));
husuperobject.biblemap.put("b43_4_10", new husuperobject(43,4,10,"Felele Jézus és monda néki: Ha ismernéd az Isten ajándékát, és [hogy] ki az, a ki ezt mondja néked: Adj innom!; te kérted volna õt, és adott volna néked élõ vizet."));
husuperobject.biblemap.put("b43_4_11", new husuperobject(43,4,11,"Monda néki az asszony: Uram, nincs mivel merítened, és a kút mély: hol vennéd tehát az élõ vizet?"));
husuperobject.biblemap.put("b43_4_12", new husuperobject(43,4,12,"Avagy nagyobb vagy-é te a mi atyánknál, Jákóbnál, a ki nékünk adta ezt a kutat, és ebbõl ivott õ is, a fiai is és jószága is?"));
husuperobject.biblemap.put("b43_4_13", new husuperobject(43,4,13,"Felele Jézus és monda néki: Mindaz, a ki ebbõl a vízbõl iszik, ismét megszomjúhozik:"));
husuperobject.biblemap.put("b43_4_14", new husuperobject(43,4,14,"Valaki pedig abból a vízbõl iszik, a melyet én adok néki, soha örökké meg nem szomjúhozik; hanem az a víz, a melyet én adok néki, örök életre buzgó víznek kútfeje lesz õ benne."));
husuperobject.biblemap.put("b43_4_15", new husuperobject(43,4,15,"Monda néki az asszony: Uram, add nékem azt a vizet, hogy meg ne szomjúhozzam, és ne jõjjek ide meríteni!"));
husuperobject.biblemap.put("b43_4_16", new husuperobject(43,4,16,"Monda néki Jézus: Menj el, hívd a férjedet, és jõjj ide!"));
husuperobject.biblemap.put("b43_4_17", new husuperobject(43,4,17,"Felele az asszony és monda: Nincs férjem. Monda néki Jézus: Jól mondád, hogy: Nincs férjem;"));
husuperobject.biblemap.put("b43_4_18", new husuperobject(43,4,18,"Mert öt férjed volt, és a mostani nem férjed: ezt igazán mondtad."));
husuperobject.biblemap.put("b43_4_19", new husuperobject(43,4,19,"Monda néki az asszony: Uram, látom, hogy te próféta vagy."));
husuperobject.biblemap.put("b43_4_20", new husuperobject(43,4,20,"A mi atyáink ezen a hegyen imádkoztak; és ti azt mondjátok, hogy Jeruzsálemben van az a hely, a hol imádkozni kell."));
husuperobject.biblemap.put("b43_4_21", new husuperobject(43,4,21,"Monda néki Jézus: Asszony, hidd el nékem, hogy eljõ az óra, a mikor sem nem ezen a hegyen, sem nem Jeruzsálemben imádjátok az Atyát."));
husuperobject.biblemap.put("b43_4_22", new husuperobject(43,4,22,"Ti azt imádjátok, a mit nem ismertek; mi azt imádjuk, a mit ismerünk: mert az idvesség a zsidók közül támadt."));
husuperobject.biblemap.put("b43_4_23", new husuperobject(43,4,23,"De eljõ az óra, és az most vagyon, amikor az igazi imádók lélekben, és igazságban imádják az Atyát: mert az Atya is ilyeneket keres, az õ imádóiul."));
husuperobject.biblemap.put("b43_4_24", new husuperobject(43,4,24,"Az Isten lélek: és a kik õt imádják, szükség, hogy lélekben és igazságban imádják."));
husuperobject.biblemap.put("b43_4_25", new husuperobject(43,4,25,"Monda néki az asszony: Tudom, hogy Messiás jõ (a ki Krisztusnak mondatik); mikor az eljõ, megjelent nékünk mindent."));
husuperobject.biblemap.put("b43_4_26", new husuperobject(43,4,26,"Monda néki Jézus: Én vagyok az, a ki veled beszélek."));
husuperobject.biblemap.put("b43_4_27", new husuperobject(43,4,27,"Eközben megjövének az õ tanítványai; és csodálkozának, hogy asszonnyal beszélt; mindazáltal egyik sem mondá: Mit keresel? vagy: Mit beszélsz vele?"));
husuperobject.biblemap.put("b43_4_28", new husuperobject(43,4,28,"Ott hagyá azért az asszony a vedrét, és elméne a városba, és monda az embereknek:"));
husuperobject.biblemap.put("b43_4_29", new husuperobject(43,4,29,"Jertek, lássatok egy embert, a ki megmonda nékem mindent, a mit cselekedtem. Nem ez-é a Krisztus?"));
husuperobject.biblemap.put("b43_4_30", new husuperobject(43,4,30,"Kimenének azért a városból, és hozzá menének."));
husuperobject.biblemap.put("b43_4_31", new husuperobject(43,4,31,"Aközben pedig kérék õt a tanítványok, mondván: Mester, egyél!"));
husuperobject.biblemap.put("b43_4_32", new husuperobject(43,4,32,"Õ pedig monda nékik: Van nékem eledelem, a mit egyem, a mit ti nem tudtok."));
husuperobject.biblemap.put("b43_4_33", new husuperobject(43,4,33,"Mondának azért a tanítványok egymásnak: Hozott-é néki valaki enni?"));
husuperobject.biblemap.put("b43_4_34", new husuperobject(43,4,34,"Monda nékik Jézus: Az én eledelem az, hogy annak akaratját cselekedjem, a ki elküldött engem, és az õ dolgát elvégezzem."));
husuperobject.biblemap.put("b43_4_35", new husuperobject(43,4,35,"Ti nem azt mondjátok-é, hogy még négy hónap és eljõ az aratás? Ímé, mondom néktek: Emeljétek fel szemeiteket, és lássátok meg a tájékokat, hogy már fehérek az aratásra."));
husuperobject.biblemap.put("b43_4_36", new husuperobject(43,4,36,"És a ki arat, jutalmat nyer, és az örök életre gyümölcsöt gyûjt; hogy mind a vetõ, mind az arató együtt örvendezzen."));
husuperobject.biblemap.put("b43_4_37", new husuperobject(43,4,37,"Mert ebben az a mondás igaz, hogy más a vetõ, más az arató."));
husuperobject.biblemap.put("b43_4_38", new husuperobject(43,4,38,"Én annak az aratására küldtelek titeket, a mit nem ti munkáltatok; mások munkálták, és ti a mások munkájába állottatok."));
husuperobject.biblemap.put("b43_4_39", new husuperobject(43,4,39,"Abból a városból pedig sokan hivének benne a Samaritánusok közül annak az asszonynak beszédéért, a ki bizonyságot tett vala, hogy: Mindent megmondott nékem, a mit cselekedtem."));
husuperobject.biblemap.put("b43_4_40", new husuperobject(43,4,40,"A mint azért oda mentek hozzá a Samaritánusok, kérék õt, hogy maradjon náluk; és ott marada két napig."));
husuperobject.biblemap.put("b43_4_41", new husuperobject(43,4,41,"És sokkal többen hivének a maga beszédéért,"));
husuperobject.biblemap.put("b43_4_42", new husuperobject(43,4,42,"És azt mondják vala az asszonynak, hogy: Nem a te beszédedért hiszünk immár: mert magunk hallottuk, és tudjuk, hogy bizonnyal ez a világ idvezítõje, a Krisztus."));
husuperobject.biblemap.put("b43_4_43", new husuperobject(43,4,43,"Két nap mulva pedig kiméne onnét, és elméne Galileába."));
husuperobject.biblemap.put("b43_4_44", new husuperobject(43,4,44,"Mert Jézus maga tett bizonyságot arról, hogy a prófétának nincs tisztessége a maga hazájában."));
husuperobject.biblemap.put("b43_4_45", new husuperobject(43,4,45,"Mikor azért beméne Galileába, befogadták õt a Galileabeliek, mivelhogy látták vala mindazt, a mit Jeruzsálemben cselekedett az ünnepen; mert õk is elmentek vala az ünnepre."));
husuperobject.biblemap.put("b43_4_46", new husuperobject(43,4,46,"Ismét a galileai Kánába méne azért Jézus, a hol a vizet borrá változtatta. És volt Kapernaumban egy királyi ember, a kinek a fia beteg vala."));
husuperobject.biblemap.put("b43_4_47", new husuperobject(43,4,47,"Mikor ez meghallá, hogy Jézus Júdeából Galileába érkezett, hozzá méne és kéré õt, hogy menjen el és gyógyítsa meg az õ fiát; mert halálán vala."));
husuperobject.biblemap.put("b43_4_48", new husuperobject(43,4,48,"Monda azért néki Jézus: Ha jeleket és csodákat nem láttok, nem hisztek."));
husuperobject.biblemap.put("b43_4_49", new husuperobject(43,4,49,"Monda néki a királyi ember: Uram, jõjj, mielõtt a gyermekem meghal."));
husuperobject.biblemap.put("b43_4_50", new husuperobject(43,4,50,"Monda néki Jézus: Menj el, a te fiad él. És hitt az ember a szónak, a mit Jézus mondott néki, és elment."));
husuperobject.biblemap.put("b43_4_51", new husuperobject(43,4,51,"A mint pedig már megy vala, elébe jövének az õ szolgái, és hírt hozának néki, mondván, hogy: A te fiad él."));
husuperobject.biblemap.put("b43_4_52", new husuperobject(43,4,52,"Megtudakozá azért tõlük az órát, a melyben megkönnyebbedett vala; és mondának néki: Tegnap hét órakor hagyta el õt a láz;"));
husuperobject.biblemap.put("b43_4_53", new husuperobject(43,4,53,"Megérté azért az atya, hogy abban az órában, a melyben azt mondá néki a Jézus, hogy: a te fiad él. És hitt õ, és az õ egész háza népe."));
husuperobject.biblemap.put("b43_4_54", new husuperobject(43,4,54,"Ezt ismét második jel gyanánt tevé Jézus, mikor Júdeából Galileába ment."));
husuperobject.biblemap.put("b43_5_1", new husuperobject(43,5,1,"Ezek után ünnepök vala a zsidóknak, és felméne Jézus Jeruzsálembe."));
husuperobject.biblemap.put("b43_5_2", new husuperobject(43,5,2,"Van pedig Jeruzsálemben a Juh[kapu]nál egy tó, a melyet héberül Bethesdának neveznek. Öt tornácza van."));
husuperobject.biblemap.put("b43_5_3", new husuperobject(43,5,3,"Ezekben feküvék a betegek, vakok, sánták, aszkórosok nagy sokasága, várva a víznek megmozdulását."));
husuperobject.biblemap.put("b43_5_4", new husuperobject(43,5,4,"Mert idõnként angyal szálla a tóra, és felzavará a vizet: a ki tehát elõször lépett bele a víz felzavarása után, meggyógyult, akárminémû betegségben volt."));
husuperobject.biblemap.put("b43_5_5", new husuperobject(43,5,5,"Vala pedig ott egy ember, a ki harmincnyolcz esztendõt töltött betegségében."));
husuperobject.biblemap.put("b43_5_6", new husuperobject(43,5,6,"Ezt a mint látta Jézus, hogy ott fekszik, és megtudta, hogy már sok idõ óta [úgy] van; monda néki: Akarsz-é meggyógyulni?"));
husuperobject.biblemap.put("b43_5_7", new husuperobject(43,5,7,"Felele néki a beteg: Uram, nincs emberem, hogy a mikor a víz felzavarodik, bevigyen engem a tóba; és mire én oda érek, más lép be elõttem."));
husuperobject.biblemap.put("b43_5_8", new husuperobject(43,5,8,"Monda néki Jézus: Kelj fel, vedd fel a te nyoszolyádat, és járj!"));
husuperobject.biblemap.put("b43_5_9", new husuperobject(43,5,9,"És azonnal meggyógyula az ember, és felvevé nyoszolyáját, és jár vala. Aznap pedig szombat vala."));
husuperobject.biblemap.put("b43_5_10", new husuperobject(43,5,10,"Mondának azért a zsidók a meggyógyultnak: Szombat van, nem szabad néked a nyoszolyádat hordanod!"));
husuperobject.biblemap.put("b43_5_11", new husuperobject(43,5,11,"Felele nékik: A ki meggyógyított engem, az mondá nékem: Vedd fel a nyoszolyádat, és járj."));
husuperobject.biblemap.put("b43_5_12", new husuperobject(43,5,12,"Megkérdék azért õt: Ki az az ember, a ki mondá néked: Vedd fel a nyoszolyádat, és járj?"));
husuperobject.biblemap.put("b43_5_13", new husuperobject(43,5,13,"A meggyógyult pedig nem tudja vala, hogy ki az; mert Jézus félre vonult, sokaság lévén azon a helyen."));
husuperobject.biblemap.put("b43_5_14", new husuperobject(43,5,14,"Ezek után találkozék vele Jézus a templomban, és monda néki: Ímé meggyógyultál; többé ne vétkezzél, hogy rosszabbul ne legyen dolgod!"));
husuperobject.biblemap.put("b43_5_15", new husuperobject(43,5,15,"Elméne az az ember, és hírül adá a zsidóknak, hogy Jézus az, a ki õt meggyógyította."));
husuperobject.biblemap.put("b43_5_16", new husuperobject(43,5,16,"És e miatt üldözõbe vevék a zsidók Jézust, és meg akarák õt ölni, hogy ezeket mûvelte szombaton."));
husuperobject.biblemap.put("b43_5_17", new husuperobject(43,5,17,"Jézus pedig felele nékik: Az én Atyám mind ez ideig munkálkodik, én is munkálkodom."));
husuperobject.biblemap.put("b43_5_18", new husuperobject(43,5,18,"E miatt aztán még inkább meg akarák õt ölni a zsidók, mivel nem csak a szombatot rontotta meg, hanem az Istent is saját Atyjának mondotta, egyenlõvé tévén magát az Istennel."));
husuperobject.biblemap.put("b43_5_19", new husuperobject(43,5,19,"Felele azért Jézus, és monda nékik: Bizony, bizony mondom néktek: a Fiú semmit sem tehet önmagától, hanem ha látja cselekedni az Atyát, mert a miket az cselekszik, ugyanazokat hasonlatosképen a Fiú is cselekszi."));
husuperobject.biblemap.put("b43_5_20", new husuperobject(43,5,20,"Mert az Atya szereti a Fiút, és mindent megmutat néki, a miket õ maga cselekszik; és ezeknél nagyobb dolgokat is mutat majd néki, hogy ti csudálkozzatok."));
husuperobject.biblemap.put("b43_5_21", new husuperobject(43,5,21,"Mert a mint az Atya feltámasztja a halottakat és megeleveníti, úgy a Fiú is a kiket akar, megelevenít."));
husuperobject.biblemap.put("b43_5_22", new husuperobject(43,5,22,"Mert az Atya nem ítél senkit, hanem az ítéletet egészen a Fiúnak adta;"));
husuperobject.biblemap.put("b43_5_23", new husuperobject(43,5,23,"Hogy mindenki úgy tisztelje a Fiút, miként tisztelik az Atyát. A ki nem tiszteli a Fiút, nem tiszteli az Atyát, a ki elküldte õt."));
husuperobject.biblemap.put("b43_5_24", new husuperobject(43,5,24,"Bizony, bizony mondom néktek, hogy a ki az én beszédemet hallja és hisz annak, a ki engem elbocsátott, örök élete van; és nem megy a kárhozatra, hanem általment a halálból az életre."));
husuperobject.biblemap.put("b43_5_25", new husuperobject(43,5,25,"Bizony, bizony mondom néktek, hogy eljõ az idõ, és az most vagyon, mikor a halottak hallják az Isten Fiának szavát, és a kik hallják, élnek."));
husuperobject.biblemap.put("b43_5_26", new husuperobject(43,5,26,"Mert a miként az Atyának élete van önmagában, akként adta a Fiúnak is, hogy élete legyen önmagában:"));
husuperobject.biblemap.put("b43_5_27", new husuperobject(43,5,27,"És hatalmat ada néki az ítélettételre is, mivelhogy embernek fia."));
husuperobject.biblemap.put("b43_5_28", new husuperobject(43,5,28,"Ne csodálkozzatok ezen: mert eljõ az óra, a melyben mindazok, a kik a koporsókban vannak, meghallják az õ szavát,"));
husuperobject.biblemap.put("b43_5_29", new husuperobject(43,5,29,"És kijõnek; a kik a jót cselekedték, az élet feltámadására; a kik pedig a gonoszt mûvelték, a kárhozat feltámadására."));
husuperobject.biblemap.put("b43_5_30", new husuperobject(43,5,30,"Én semmit sem cselekedhetem magamtól; a mint hallok, úgy ítélek, és az én ítéletem igazságos; mert nem a magam akaratát keresem, hanem annak akaratát, a ki elküldött engem, az Atyáét."));
husuperobject.biblemap.put("b43_5_31", new husuperobject(43,5,31,"Ha én teszek bizonyságot magamról, az én bizonyságtételem nem igaz."));
husuperobject.biblemap.put("b43_5_32", new husuperobject(43,5,32,"Más az, a ki bizonyságot tesz rólam; és tudom, hogy igaz az a bizonyságtétel, a melylyel bizonyságot tesz rólam."));
husuperobject.biblemap.put("b43_5_33", new husuperobject(43,5,33,"Ti elküldtetek Jánoshoz, és bizonyságot tett az igazságról."));
husuperobject.biblemap.put("b43_5_34", new husuperobject(43,5,34,"De én nem embertõl nyerem a bizonyságtételt; hanem ezeket azért mondom, hogy ti megtartassatok."));
husuperobject.biblemap.put("b43_5_35", new husuperobject(43,5,35,"Õ az égõ és fénylõ szövétnek vala, ti pedig csak egy ideig akartatok örvendezni az õ világosságában."));
husuperobject.biblemap.put("b43_5_36", new husuperobject(43,5,36,"De nékem nagyobb bizonyságom van a Jánosénál: mert azok a dolgok, a melyeket rám bízott az Atya, hogy elvégezzem azokat, azok a dolgok, a melyeket én cselekszem, tesznek bizonyságot rólam, hogy az Atya küldött engem."));
husuperobject.biblemap.put("b43_5_37", new husuperobject(43,5,37,"A ki elküldött engem, maga az Atya is bizonyságot tett rólam. Sem hangját nem hallottátok soha, sem ábrázatát nem láttátok."));
husuperobject.biblemap.put("b43_5_38", new husuperobject(43,5,38,"Az õ ígéje sincs maradandóan bennetek: mert a kit õ elküldött, ti annak nem hisztek."));
husuperobject.biblemap.put("b43_5_39", new husuperobject(43,5,39,"Tudakozzátok az írásokat, mert azt hiszitek, hogy azokban van a ti örök életetek; és ezek azok, a melyek bizonyságot tesznek rólam;"));
husuperobject.biblemap.put("b43_5_40", new husuperobject(43,5,40,"És nem akartok hozzám jõni, hogy életetek legyen!"));
husuperobject.biblemap.put("b43_5_41", new husuperobject(43,5,41,"Dicsõséget emberektõl nem nyerek."));
husuperobject.biblemap.put("b43_5_42", new husuperobject(43,5,42,"De ismerlek benneteket, hogy az Istennek szeretete nincs meg bennetek:"));
husuperobject.biblemap.put("b43_5_43", new husuperobject(43,5,43,"Én az én Atyám nevében jöttem, és nem fogadtatok be engem; ha más jõne a maga nevében, azt befogadnátok."));
husuperobject.biblemap.put("b43_5_44", new husuperobject(43,5,44,"Mimódon hihettek ti, a kik egymástól nyertek dicsõséget, és azt a dicsõséget, a mely az egy Istentõl van, nem keresitek?"));
husuperobject.biblemap.put("b43_5_45", new husuperobject(43,5,45,"Ne állítsátok, hogy én vádollak majd benneteket az Atyánál; van a ki vádol titeket, Mózes, a kiben ti reménykedtetek."));
husuperobject.biblemap.put("b43_5_46", new husuperobject(43,5,46,"Mert ha hinnétek Mózesnek, nékem is hinnétek; mert én rólam írt õ."));
husuperobject.biblemap.put("b43_5_47", new husuperobject(43,5,47,"Ha pedig az õ írásainak nem hisztek, mimódon hisztek az én beszédeimnek?"));
husuperobject.biblemap.put("b43_6_1", new husuperobject(43,6,1,"Ezek után elméne Jézus a galileai tengeren, a Tiberiáson túl."));
husuperobject.biblemap.put("b43_6_2", new husuperobject(43,6,2,"És nagy sokaság követé õt, mivelhogy látják vala az õ csodatételeit, a melyeket cselekszik vala a betegeken."));
husuperobject.biblemap.put("b43_6_3", new husuperobject(43,6,3,"Felméne pedig Jézus a hegyre, és leüle ott a tanítványaival."));
husuperobject.biblemap.put("b43_6_4", new husuperobject(43,6,4,"Közel vala pedig husvét, a zsidók ünnepe."));
husuperobject.biblemap.put("b43_6_5", new husuperobject(43,6,5,"Mikor azért felemelé Jézus a szemeit, és látá, hogy nagy sokaság jõ hozzá, monda Filepnek: Honnan vegyünk kenyeret, hogy ehessenek ezek?"));
husuperobject.biblemap.put("b43_6_6", new husuperobject(43,6,6,"Ezt pedig azért mondá, hogy próbára tegye õt; mert õ maga tudta, mit akar vala cselekedni."));
husuperobject.biblemap.put("b43_6_7", new husuperobject(43,6,7,"Felele néki Filep: Kétszáz dénár árú kenyér nem elég ezeknek, hogy mindenikök kapjon valami keveset."));
husuperobject.biblemap.put("b43_6_8", new husuperobject(43,6,8,"Monda néki egy az õ tanítványai közül, András, a Simon Péter testvére:"));
husuperobject.biblemap.put("b43_6_9", new husuperobject(43,6,9,"Van itt egy gyermek, a kinek van öt árpa kenyere és két hala; de mi az ennyinek?"));
husuperobject.biblemap.put("b43_6_10", new husuperobject(43,6,10,"Jézus pedig monda: Ültessétek le az embereket. Nagy fû vala pedig azon a helyen. Leülének azért a férfiak, számszerint mintegy ötezeren."));
husuperobject.biblemap.put("b43_6_11", new husuperobject(43,6,11,"Jézus pedig vevé a kenyereket, és hálát adván, adta a tanítványoknak, a tanítványok pedig a leülteknek; hasonlóképen a halakból is, a mennyit akarnak vala."));
husuperobject.biblemap.put("b43_6_12", new husuperobject(43,6,12,"A mint pedig betelének, monda az õ tanítványainak: Szedjétek össze a megmaradt darabokat, hogy semmi el ne veszszen."));
husuperobject.biblemap.put("b43_6_13", new husuperobject(43,6,13,"Összeszedék azért, és megtöltének tizenkét kosarat az öt árpa kenyérbõl való darabokkal, a melyek megmaradtak vala az evõk után."));
husuperobject.biblemap.put("b43_6_14", new husuperobject(43,6,14,"Az emberek azért látva a jelt, a melyet Jézus tõn, mondának: Bizonnyal ez ama próféta, a ki eljövendõ vala a világra."));
husuperobject.biblemap.put("b43_6_15", new husuperobject(43,6,15,"Jézus azért, a mint észrevevé, hogy jõni akarnak és õt elragadni, hogy királylyá tegyék, ismét elvonula egymaga a hegyre."));
husuperobject.biblemap.put("b43_6_16", new husuperobject(43,6,16,"Mikor pedig estveledék, lemenének az õ tanítványai a tengerhez,"));
husuperobject.biblemap.put("b43_6_17", new husuperobject(43,6,17,"És beszállva a hajóba, mennek vala a tengeren túl Kapernaumba. És már sötétség volt, és még nem ment vala hozzájuk Jézus."));
husuperobject.biblemap.put("b43_6_18", new husuperobject(43,6,18,"És a tenger a nagy szél fúvása miatt háborog vala."));
husuperobject.biblemap.put("b43_6_19", new husuperobject(43,6,19,"Mikor azért huszonöt, vagy harmincz futamatnyira beevezének, megláták Jézust, a mint jár vala a tengeren és a hajóhoz közeledik vala: és megrémülének."));
husuperobject.biblemap.put("b43_6_20", new husuperobject(43,6,20,"Õ pedig monda nékik: Én vagyok, ne féljetek!"));
husuperobject.biblemap.put("b43_6_21", new husuperobject(43,6,21,"Be akarák azért õt venni a hajóba: és a hajó azonnal ama földnél vala, a melyre menének."));
husuperobject.biblemap.put("b43_6_22", new husuperobject(43,6,22,"Másnap a sokaság, a mely a tengeren túl állott vala, látva, hogy nem vala ott más hajó, csak az az egy, a melybe a Jézus tanítványai szállottak, és hogy Jézus nem ment be az õ tanítványaival a hajóba, hanem csak az õ tanítványai mentek el,"));
husuperobject.biblemap.put("b43_6_23", new husuperobject(43,6,23,"De jöttek [más] hajók Tiberiásból közel ahhoz a helyhez, a hol a kenyeret ették, miután hálákat adott az Úr:"));
husuperobject.biblemap.put("b43_6_24", new husuperobject(43,6,24,"Mikor azért látta a sokaság, hogy sem Jézus, sem a tanítványai nincsenek ott, beszállának õk is a hajókba, és elmenének Kapernaumba, keresvén Jézust."));
husuperobject.biblemap.put("b43_6_25", new husuperobject(43,6,25,"És megtalálván õt a tengeren túl, mondának néki: Mester, mikor jöttél ide?"));
husuperobject.biblemap.put("b43_6_26", new husuperobject(43,6,26,"Felele nékik Jézus és monda: Bizony, bizony mondom néktek: nem azért kerestek engem, hogy jeleket láttatok, hanem azért, mert ettetek ama kenyerekbõl, és jóllaktatok."));
husuperobject.biblemap.put("b43_6_27", new husuperobject(43,6,27,"Munkálkodjatok ne az eledelért, a mely elvész, hanem az eledelért, a mely megmarad az örök életre, a melyet az embernek Fia ád majd néktek; mert õt az Atya pecsételte el, az Isten."));
husuperobject.biblemap.put("b43_6_28", new husuperobject(43,6,28,"Mondának azért néki: Mit csináljunk, hogy az Isten dolgait cselekedjük?"));
husuperobject.biblemap.put("b43_6_29", new husuperobject(43,6,29,"Felele Jézus és monda nékik: Az az Isten dolga, hogy higyjetek abban, a kit õ küldött."));
husuperobject.biblemap.put("b43_6_30", new husuperobject(43,6,30,"Mondának azért néki: Micsoda jelt mutatsz tehát te, hogy lássuk és higyjünk néked? Mit mûvelsz?"));
husuperobject.biblemap.put("b43_6_31", new husuperobject(43,6,31,"A mi atyáink a mannát ették a pusztában; a mint meg van írva: Mennyei kenyeret adott vala enniök."));
husuperobject.biblemap.put("b43_6_32", new husuperobject(43,6,32,"Monda azért nékik Jézus: Bizony, bizony mondom néktek: nem Mózes adta néktek a mennyei kenyeret, hanem az én Atyám adja majd néktek az igazi mennyei kenyeret."));
husuperobject.biblemap.put("b43_6_33", new husuperobject(43,6,33,"Mert az az Istennek kenyere, a mely mennybõl száll alá, és életet ád a világnak."));
husuperobject.biblemap.put("b43_6_34", new husuperobject(43,6,34,"Mondának azért néki: Uram, mindenkor add nékünk ezt a kenyeret!"));
husuperobject.biblemap.put("b43_6_35", new husuperobject(43,6,35,"Jézus pedig monda nékik: Én vagyok az életnek ama kenyere; a ki hozzám jõ, semmiképen meg nem éhezik, és a ki hisz bennem, meg nem szomjúhozik soha."));
husuperobject.biblemap.put("b43_6_36", new husuperobject(43,6,36,"De mondám néktek, hogy noha láttatok is engem, még sem hisztek."));
husuperobject.biblemap.put("b43_6_37", new husuperobject(43,6,37,"Minden, a mit nékem ád az Atya, én hozzám jõ; és azt, a ki hozzám jõ, semmiképen ki nem vetem."));
husuperobject.biblemap.put("b43_6_38", new husuperobject(43,6,38,"Mert azért szállottam le a mennybõl, hogy ne a magam akaratát cselekedjem, hanem annak akaratát, a ki elküldött engem."));
husuperobject.biblemap.put("b43_6_39", new husuperobject(43,6,39,"Az pedig az Atyának akarata, a ki elküldött engem, hogy a mit nékem adott, abból semmit el ne veszítsek, hanem feltámaszszam azt az utolsó napon."));
husuperobject.biblemap.put("b43_6_40", new husuperobject(43,6,40,"Az pedig annak az akarata, a ki elküldött engem, hogy mindaz, a ki látja a Fiút és hisz õ benne, örök élete legyen; és én feltámaszszam azt azt utolsó napon."));
husuperobject.biblemap.put("b43_6_41", new husuperobject(43,6,41,"Zúgolódának azért a zsidók õ ellene, hogy azt mondá: Én vagyok az a kenyér, a mely a mennybõl szállott alá."));
husuperobject.biblemap.put("b43_6_42", new husuperobject(43,6,42,"És mondának: Nem ez-é Jézus, a József fia, a kinek mi ismerjük atyját és anyját? mimódon mondja hát ez, hogy: A mennybõl szállottam alá?"));
husuperobject.biblemap.put("b43_6_43", new husuperobject(43,6,43,"Felele azért Jézus és monda nékik: Ne zúgolódjatok egymás között!"));
husuperobject.biblemap.put("b43_6_44", new husuperobject(43,6,44,"Senki sem jöhet én hozzám, hanemha az Atya vonja azt, a ki elküldött engem; én pedig feltámasztom azt az utolsó napon."));
husuperobject.biblemap.put("b43_6_45", new husuperobject(43,6,45,"Meg van írva a prófétáknál: És mindnyájan Istentõl tanítottak lesznek. Valaki azért az Atyától hallott, és tanult, én hozzám jõ."));
husuperobject.biblemap.put("b43_6_46", new husuperobject(43,6,46,"Nem hogy az Atyát valaki látta, csak az, a ki Istentõl van, az látta az Atyát."));
husuperobject.biblemap.put("b43_6_47", new husuperobject(43,6,47,"Bizony, bizony mondom néktek: A ki én bennem hisz, örök élete van annak."));
husuperobject.biblemap.put("b43_6_48", new husuperobject(43,6,48,"Én vagyok az életnek kenyere."));
husuperobject.biblemap.put("b43_6_49", new husuperobject(43,6,49,"A ti atyáitok a mannát ették a pusztában, és meghaltak."));
husuperobject.biblemap.put("b43_6_50", new husuperobject(43,6,50,"Ez az a kenyér, a mely a mennybõl szállott alá, hogy kiki egyék belõle és meg ne haljon."));
husuperobject.biblemap.put("b43_6_51", new husuperobject(43,6,51,"Én vagyok amaz élõ kenyér, a mely a mennybõl szállott alá; ha valaki eszik e kenyérbõl, él örökké. És az a kenyér pedig, a melyet én adok, az én testem, a melyet én adok a világ életéért."));
husuperobject.biblemap.put("b43_6_52", new husuperobject(43,6,52,"Tusakodának azért a zsidók egymás között, mondván: Mimódon adhatja ez nékünk a testét, hogy [azt] együk?"));
husuperobject.biblemap.put("b43_6_53", new husuperobject(43,6,53,"Monda azért nékik Jézus: Bizony, bizony mondom néktek: Ha nem eszitek az ember Fiának testét és nem iszszátok az õ vérét, nincs élet bennetek."));
husuperobject.biblemap.put("b43_6_54", new husuperobject(43,6,54,"A ki eszi az én testemet és iszsza az én véremet, örök élete van annak, és én feltámasztom azt az utolsó napon."));
husuperobject.biblemap.put("b43_6_55", new husuperobject(43,6,55,"Mert az én testem bizony étel és az én vérem bizony ital."));
husuperobject.biblemap.put("b43_6_56", new husuperobject(43,6,56,"A ki eszi az én testemet és iszsza az én véremet, az én bennem lakozik és én is abban."));
husuperobject.biblemap.put("b43_6_57", new husuperobject(43,6,57,"A miként elküldött engem amaz élõ Atya, és én az Atya által élek: akként az is, a ki engem eszik, él én általam."));
husuperobject.biblemap.put("b43_6_58", new husuperobject(43,6,58,"Ez az a kenyér, a mely a mennybõl szállott alá; nem úgy, a mint a ti atyáitok evék a mannát és meghalának: a ki ezt a kenyeret eszi, él örökké."));
husuperobject.biblemap.put("b43_6_59", new husuperobject(43,6,59,"Ezeket mondá a zsinagógában, a mikor tanít vala Kapernaumban."));
husuperobject.biblemap.put("b43_6_60", new husuperobject(43,6,60,"Sokan azért, a kik hallák [ezeket] az õ tanítványai közül, mondának: Kemény beszéd ez; ki hallgathatja õt?"));
husuperobject.biblemap.put("b43_6_61", new husuperobject(43,6,61,"Tudván pedig Jézus õ magában, hogy e miatt zúgolódnak az õ tanítványai, monda nékik: Titeket ez megbotránkoztat?"));
husuperobject.biblemap.put("b43_6_62", new husuperobject(43,6,62,"Hát ha meglátjátok az embernek Fiát felszállani oda, a hol elébb vala?!"));
husuperobject.biblemap.put("b43_6_63", new husuperobject(43,6,63,"A lélek az, a mi megelevenít, a test nem használ semmit: a beszédek, a melyeket én szólok néktek, lélek és élet."));
husuperobject.biblemap.put("b43_6_64", new husuperobject(43,6,64,"De vannak némelyek közöttetek, a kik nem hisznek. Mert eleitõl fogva tudta Jézus, kik azok, a kik nem hisznek, és ki az, a ki elárulja õt."));
husuperobject.biblemap.put("b43_6_65", new husuperobject(43,6,65,"És monda: Azért mondtam néktek, hogy senki sem jöhet én hozzám, hanemha az én Atyámtól van megadva néki."));
husuperobject.biblemap.put("b43_6_66", new husuperobject(43,6,66,"Ettõl fogva sokan visszavonulának az õ tanítványai közül és nem járnak vala többé õ vele."));
husuperobject.biblemap.put("b43_6_67", new husuperobject(43,6,67,"Monda azért Jézus a tizenkettõnek: Vajjon ti is el akartok-é menni?"));
husuperobject.biblemap.put("b43_6_68", new husuperobject(43,6,68,"Felele néki Simon Péter: Uram, kihez mehetnénk? Örök életnek beszéde van te nálad."));
husuperobject.biblemap.put("b43_6_69", new husuperobject(43,6,69,"És mi elhittük és megismertük, hogy te vagy a Krisztus, az élõ Istennek Fia."));
husuperobject.biblemap.put("b43_6_70", new husuperobject(43,6,70,"Felele nékik Jézus: Nem én választottalak-é ki titeket, a tizenkettõt? és egy közületek ördög."));
husuperobject.biblemap.put("b43_6_71", new husuperobject(43,6,71,"Értette pedig Júdás Iskáriótest, Simon fiát, mert ez akarta õt elárulni, noha egy volt a tizenkettõ közül."));
husuperobject.biblemap.put("b43_7_1", new husuperobject(43,7,1,"És ezek után Galileában jár vala Jézus; mert nem akar vala Júdeában járni, mivelhogy azon igyekezének a Júdeabeliek, hogy õt megöljék."));
husuperobject.biblemap.put("b43_7_2", new husuperobject(43,7,2,"Közel vala pedig a zsidók ünnepe, a sátoros ünnep."));
husuperobject.biblemap.put("b43_7_3", new husuperobject(43,7,3,"Mondának azért néki az õ atyjafiai: Menj el innen, és térj Júdeába, hogy a te tanítványaid is lássák a te dolgaidat, a melyeket cselekszel."));
husuperobject.biblemap.put("b43_7_4", new husuperobject(43,7,4,"Mert senki sem cselekszik titkon semmit, a ki maga ismeretessé akar lenni. Ha ilyeneket cselekszel, mutasd meg magadat a világnak."));
husuperobject.biblemap.put("b43_7_5", new husuperobject(43,7,5,"Mert az õ atyjafiai sem hivének benne."));
husuperobject.biblemap.put("b43_7_6", new husuperobject(43,7,6,"Monda azért nékik Jézus: Az én idõm még nincs itt; a ti idõtök pedig mindig készen van."));
husuperobject.biblemap.put("b43_7_7", new husuperobject(43,7,7,"Titeket nem gyûlölhet a világ, de engem gyûlöl; mert én bizonyságot teszek felõle, hogy az õ cselekedetei gonoszak."));
husuperobject.biblemap.put("b43_7_8", new husuperobject(43,7,8,"Ti menjetek fel erre az ünnepre: én még nem megyek fel erre az ünnepre; mert az én idõm még nem tölt be."));
husuperobject.biblemap.put("b43_7_9", new husuperobject(43,7,9,"Ezeket mondván pedig nékik, marada Galileában."));
husuperobject.biblemap.put("b43_7_10", new husuperobject(43,7,10,"A mint pedig felmenének az õ atyjafiai, akkor õ is felméne az ünnepre, nem nyilvánosan, hanem mintegy titkon."));
husuperobject.biblemap.put("b43_7_11", new husuperobject(43,7,11,"A zsidók azért keresik vala õt az ünnepen, és mondának: Hol van õ?"));
husuperobject.biblemap.put("b43_7_12", new husuperobject(43,7,12,"És a sokaságban nagy zúgás vala õ miatta. Némelyek azt mondják vala, hogy jó ember; mások pedig azt mondják vala: Nem, hanem a népnek hitetõje."));
husuperobject.biblemap.put("b43_7_13", new husuperobject(43,7,13,"Mindamellett senki sem beszélt vala nyiltan õ felõle a zsidóktól való félelem miatt."));
husuperobject.biblemap.put("b43_7_14", new husuperobject(43,7,14,"Már-már az ünnep közepén azonban felméne Jézus a templomba, és tanít vala."));
husuperobject.biblemap.put("b43_7_15", new husuperobject(43,7,15,"És csodálkozának a zsidók, mondván: Mimódon tudja ez az írásokat, holott nem tanulta?!"));
husuperobject.biblemap.put("b43_7_16", new husuperobject(43,7,16,"Felele nékik Jézus és monda: Az én tudományom nem az enyém, hanem azé, a ki küldött engem."));
husuperobject.biblemap.put("b43_7_17", new husuperobject(43,7,17,"Ha valaki cselekedni akarja az õ akaratát, megismerheti e tudományról, vajjon Istentõl van-é, vagy én magamtól szólok?"));
husuperobject.biblemap.put("b43_7_18", new husuperobject(43,7,18,"A ki magától szól, a maga dicsõségét keresi; a ki pedig annak dicsõségét keresi, a ki küldte õt, igaz az, és nincs abban hamisság."));
husuperobject.biblemap.put("b43_7_19", new husuperobject(43,7,19,"Nem Mózes adta-é néktek a törvényt? és senki sem teljesíti közületek a törvényt. Miért akartok engem megölni?"));
husuperobject.biblemap.put("b43_7_20", new husuperobject(43,7,20,"Felele a sokaság és monda: Ördög van benned. Ki akar téged megölni?"));
husuperobject.biblemap.put("b43_7_21", new husuperobject(43,7,21,"Felele Jézus és monda nékik: Egy dolgot cselekvém, és mindnyájan csodáljátok."));
husuperobject.biblemap.put("b43_7_22", new husuperobject(43,7,22,"Azért Mózes adta néktek a körülmetélkedést (nem mintha Mózestõl való volna, hanem az atyáktól): és szombaton körülmetélitek az embert."));
husuperobject.biblemap.put("b43_7_23", new husuperobject(43,7,23,"Ha körülmetélhetõ az ember szombaton, hogy a Mózes törvénye meg ne romoljon; én rám haragusztok-é, hogy egy embert egészen meggyógyítottam szombaton?"));
husuperobject.biblemap.put("b43_7_24", new husuperobject(43,7,24,"Ne ítéljetek a látszat után, hanem igaz ítélettel ítéljetek!"));
husuperobject.biblemap.put("b43_7_25", new husuperobject(43,7,25,"Mondának azért némelyek a jeruzsálemiek közül: Nem ez-é az, a kit meg akarnak ölni?"));
husuperobject.biblemap.put("b43_7_26", new husuperobject(43,7,26,"És ímé nyiltan szól, és semmit sem szólnak néki. Talán bizony megismerték a fõemberek, hogy bizony ez a Krisztus?"));
husuperobject.biblemap.put("b43_7_27", new husuperobject(43,7,27,"De jól tudjuk, honnan való ez; mikor pedig eljõ a Krisztus, senki sem tudja, honnan való."));
husuperobject.biblemap.put("b43_7_28", new husuperobject(43,7,28,"Kiálta azért Jézus a templomban, tanítván és mondván: Mind engem ismertek, mind azt tudjátok, honnan való vagyok; és én magamtól nem jöttem, de igaz az, a ki engem elküldött, a kit ti nem ismertek."));
husuperobject.biblemap.put("b43_7_29", new husuperobject(43,7,29,"Én azonban ismerem õt, mert õ tõle vagyok, és õ küldött engem."));
husuperobject.biblemap.put("b43_7_30", new husuperobject(43,7,30,"Akarják vala azért õt megfogni; de senki sem veté reá a kezét, mert nem jött még el az õ órája."));
husuperobject.biblemap.put("b43_7_31", new husuperobject(43,7,31,"A sokaság közül pedig sokan hivének õ benne; és azt mondják vala, hogy: A Krisztus mikor eljõ, tehet-é majd több csodát azoknál, a melyeket ez tett?"));
husuperobject.biblemap.put("b43_7_32", new husuperobject(43,7,32,"Meghallák a farizeusok, a mint a sokaság ezeket suttogja vala felõle; és szolgákat küldének a farizeusok és a fõpapok, hogy fogják meg õt."));
husuperobject.biblemap.put("b43_7_33", new husuperobject(43,7,33,"Monda azért nékik Jézus: Egy kevés ideig még veletek vagyok, és majd ahhoz megyek, a ki elküldött engem."));
husuperobject.biblemap.put("b43_7_34", new husuperobject(43,7,34,"Kerestek majd engem, és nem találtok meg, és a hol én vagyok, ti nem jöhettek oda."));
husuperobject.biblemap.put("b43_7_35", new husuperobject(43,7,35,"Mondának azért a zsidók magok között: Hová akar ez menni, hogy mi majd nem találjuk meg õt? Vajjon a görögök közé szóródottakhoz akar-é menni, és a görögöket tanítani?"));
husuperobject.biblemap.put("b43_7_36", new husuperobject(43,7,36,"Micsoda beszéd ez, a melyet monda: Kerestek majd engem, és nem találtok meg; és a hol én vagyok, ti nem jöhettek oda?"));
husuperobject.biblemap.put("b43_7_37", new husuperobject(43,7,37,"Az ünnep utolsó nagy napján pedig felálla Jézus és kiálta, mondván: Ha valaki szomjúhozik, jõjjön én hozzám, és igyék."));
husuperobject.biblemap.put("b43_7_38", new husuperobject(43,7,38,"A ki hisz én bennem, a mint az írás mondotta, élõ víznek folyamai ömlenek annak belsejébõl."));
husuperobject.biblemap.put("b43_7_39", new husuperobject(43,7,39,"Ezt pedig mondja vala a Lélekrõl, a melyet veendõk valának az õ benne hívõk: mert még nem vala Szent Lélek; mivelhogy Jézus még nem dicsõítteték meg."));
husuperobject.biblemap.put("b43_7_40", new husuperobject(43,7,40,"Sokan azért a sokaság közül, a mint hallák e beszédet, ezt mondják vala: Bizonynyal ez ama Próféta."));
husuperobject.biblemap.put("b43_7_41", new husuperobject(43,7,41,"Némelyek mondának: Ez a Krisztus. Mások pedig mondának: Csak nem Galileából jön el a Krisztus?"));
husuperobject.biblemap.put("b43_7_42", new husuperobject(43,7,42,"Nem az írás mondta-é, hogy a Dávid magvából, és Bethlehembõl, ama városból jön el a Krisztus, a hol Dávid vala?"));
husuperobject.biblemap.put("b43_7_43", new husuperobject(43,7,43,"Hasonlás lõn azért õ miatta a sokaságban."));
husuperobject.biblemap.put("b43_7_44", new husuperobject(43,7,44,"Némelyek pedig közûlök akarják vala õt megfogni, de senki sem veté reá a kezét."));
husuperobject.biblemap.put("b43_7_45", new husuperobject(43,7,45,"Elmenének azért a szolgák a fõpapokhoz és farizeusokhoz; és mondának azok õ nékik: Miért nem hoztátok el õt?"));
husuperobject.biblemap.put("b43_7_46", new husuperobject(43,7,46,"Felelének a szolgák: Soha ember úgy nem szólott, mint ez az ember!"));
husuperobject.biblemap.put("b43_7_47", new husuperobject(43,7,47,"Felelének azért nékik a farizeusok: Vajjon ti is el vagytok-é hitetve?"));
husuperobject.biblemap.put("b43_7_48", new husuperobject(43,7,48,"Vajjon a fõemberek vagy a farizeusok közül hitt-é benne valaki?"));
husuperobject.biblemap.put("b43_7_49", new husuperobject(43,7,49,"De ez a sokaság, a mely nem ismeri a törvényt, átkozott!"));
husuperobject.biblemap.put("b43_7_50", new husuperobject(43,7,50,"Monda nékik Nikodémus, a ki éjjel ment vala õ hozzá, a ki egy vala azok közül:"));
husuperobject.biblemap.put("b43_7_51", new husuperobject(43,7,51,"Vajjon a mi törvényünk kárhoztatja-é az embert, ha elõbb ki nem hallgatja és nem tudja, hogy mit cselekszik?"));
husuperobject.biblemap.put("b43_7_52", new husuperobject(43,7,52,"Felelének és mondának néki: Vajjon te is Galileus vagy-é? Tudakozódjál és lásd meg, hogy Galileából nem támadt próféta."));
husuperobject.biblemap.put("b43_7_53", new husuperobject(43,7,53,"És mindnyájan haza menének."));
husuperobject.biblemap.put("b43_8_1", new husuperobject(43,8,1,"Jézus pedig elméne az Olajfák hegyére."));
husuperobject.biblemap.put("b43_8_2", new husuperobject(43,8,2,"Jó reggel azonban ismét ott vala a templomban, és az egész nép hozzá méne; és leülvén, tanítja vala õket."));
husuperobject.biblemap.put("b43_8_3", new husuperobject(43,8,3,"Az írástudók és a farizeusok pedig egy asszonyt vivének hozzá, a kit házasságtörésen kaptak vala, és a középre állítván azt,"));
husuperobject.biblemap.put("b43_8_4", new husuperobject(43,8,4,"Mondának néki: Mester, ez az asszony tetten kapatott, mint házasságtörõ."));
husuperobject.biblemap.put("b43_8_5", new husuperobject(43,8,5,"A törvényben pedig megparancsolta nékünk Mózes, hogy az ilyenek köveztessenek meg: te azért mit mondasz?"));
husuperobject.biblemap.put("b43_8_6", new husuperobject(43,8,6,"Ezt pedig azért mondák, hogy megkísértsék õt, hogy legyen õt mivel vádolniok. Jézus pedig lehajolván, az ujjával ír vala a földre."));
husuperobject.biblemap.put("b43_8_7", new husuperobject(43,8,7,"De mikor szorgalmazva kérdezék õt, felegyenesedve monda nékik: A ki közületek nem bûnös, az vesse rá elõször a követ."));
husuperobject.biblemap.put("b43_8_8", new husuperobject(43,8,8,"És újra lehajolván, írt vala a földre."));
husuperobject.biblemap.put("b43_8_9", new husuperobject(43,8,9,"Azok pedig [ezt] hallván és a lelkiismeret által vádoltatván, egymásután kimenének a vénektõl kezdve mind az utolsóig; és egyedül Jézus maradt vala és az asszony a középen állva."));
husuperobject.biblemap.put("b43_8_10", new husuperobject(43,8,10,"Mikor pedig Jézus felegyenesedék és senkit sem láta az asszonyon kívül, monda néki: Asszony, hol vannak azok a te vádlóid? Senki sem kárhoztatott-é téged?"));
husuperobject.biblemap.put("b43_8_11", new husuperobject(43,8,11,"Az pedig monda: Senki, Uram! Jézus pedig monda néki: Én sem kárhoztatlak: eredj el és többé ne vétkezzél!"));
husuperobject.biblemap.put("b43_8_12", new husuperobject(43,8,12,"Ismét szóla azért hozzájok Jézus, mondván: Én vagyok a világ világossága: a ki engem követ, nem járhat a sötétségben, hanem övé lesz az életnek világossága."));
husuperobject.biblemap.put("b43_8_13", new husuperobject(43,8,13,"Mondának azért néki a farizeusok: Te magadról teszel bizonyságot; a te bizonyságtételed nem igaz."));
husuperobject.biblemap.put("b43_8_14", new husuperobject(43,8,14,"Felele Jézus és monda nékik: Ha magam teszek is bizonyságot magamról, az én bizonyságtételem igaz; mert tudom honnan jöttem és hová megyek; ti pedig nem tudjátok honnan jövök és hová megyek."));
husuperobject.biblemap.put("b43_8_15", new husuperobject(43,8,15,"Ti test szerint ítéltek, én nem ítélek senkit."));
husuperobject.biblemap.put("b43_8_16", new husuperobject(43,8,16,"De ha ítélek is én, az én ítéletem igaz; mert én nem egyedül vagyok, hanem én és az Atya, a ki küldött engem."));
husuperobject.biblemap.put("b43_8_17", new husuperobject(43,8,17,"A ti törvényetekben is meg van pedig írva, hogy két ember bizonyságtétele igaz."));
husuperobject.biblemap.put("b43_8_18", new husuperobject(43,8,18,"Én vagyok a ki bizonyságot teszek magamról, és bizonyságot tesz rólam az Atya, a ki küldött engem."));
husuperobject.biblemap.put("b43_8_19", new husuperobject(43,8,19,"Mondának azért néki: Hol van a te Atyád? Felele Jézus: Sem engem nem ismertek, sem az én Atyámat; ha engem ismernétek, az én Atyámat is ismernétek."));
husuperobject.biblemap.put("b43_8_20", new husuperobject(43,8,20,"Ezeket a beszédeket mondá Jézus a kincstartó helyen, a mikor tanít vala a templomban; és senki sem fogta meg õt, mert még nem jött el az õ órája."));
husuperobject.biblemap.put("b43_8_21", new husuperobject(43,8,21,"Ismét monda azért nékik Jézus: Én elmegyek, és kerestek majd engem, és a ti bûneitekben fogtok meghalni: a hová én megyek, ti nem jöhettek oda."));
husuperobject.biblemap.put("b43_8_22", new husuperobject(43,8,22,"Mondának azért a zsidók: Avagy megöli-é magát, hogy azt mondja: A hová én megyek, ti nem jöhettek oda?"));
husuperobject.biblemap.put("b43_8_23", new husuperobject(43,8,23,"És monda nékik: Ti innét alól valók vagytok, én onnét felül való vagyok; ti e világból valók vagytok, én nem vagyok e világból való."));
husuperobject.biblemap.put("b43_8_24", new husuperobject(43,8,24,"Azért mondám néktek, hogy a ti bûneitekben haltok meg: mert ha nem hiszitek, hogy én vagyok, meghaltok a ti bûneitekben."));
husuperobject.biblemap.put("b43_8_25", new husuperobject(43,8,25,"Mondának azért néki: Ki vagy te? És monda nékik Jézus: A mit eleitõl fogva mondok is néktek."));
husuperobject.biblemap.put("b43_8_26", new husuperobject(43,8,26,"Sok beszélni és ítélni valóm van felõletek: de igaz az, a ki küldött engem; és én azokat beszélem a világnak, a miket tõle hallottam vala."));
husuperobject.biblemap.put("b43_8_27", new husuperobject(43,8,27,"Nem vevék észre, hogy az Atyáról szól vala nékik."));
husuperobject.biblemap.put("b43_8_28", new husuperobject(43,8,28,"Monda azért nékik Jézus: Mikor felemelitek az embernek Fiát, akkor megismeritek, hogy én vagyok és semmit sem cselekszem magamtól, hanem a mint az Atya tanított engem, úgy szólok."));
husuperobject.biblemap.put("b43_8_29", new husuperobject(43,8,29,"És a ki küldött engem, én velem van. Nem hagyott engem az Atya egyedül, mert én mindenkor azokat cselekszem, a melyek néki kedvesek."));
husuperobject.biblemap.put("b43_8_30", new husuperobject(43,8,30,"A mikor ezeket mondá, sokan hivének õ benne."));
husuperobject.biblemap.put("b43_8_31", new husuperobject(43,8,31,"Monda azért Jézus a benne hívõ zsidóknak: Ha ti megmaradtok az én beszédemben, bizonynyal az én tanítványaim vagytok;"));
husuperobject.biblemap.put("b43_8_32", new husuperobject(43,8,32,"És megismeritek az igazságot, és az igazság szabadokká tesz titeket."));
husuperobject.biblemap.put("b43_8_33", new husuperobject(43,8,33,"Felelének néki: Ábrahám magva vagyunk, és nem szolgáltunk soha senkinek: mimódon mondod te, hogy szabadokká lesztek?"));
husuperobject.biblemap.put("b43_8_34", new husuperobject(43,8,34,"Felele nékik Jézus: Bizony, bizony mondom néktek, hogy mindaz, a ki bûnt cselekszik, szolgája a bûnnek."));
husuperobject.biblemap.put("b43_8_35", new husuperobject(43,8,35,"A szolga pedig nem marad mindörökké a házban: a Fiú marad [ott] mindörökké."));
husuperobject.biblemap.put("b43_8_36", new husuperobject(43,8,36,"Azért ha a Fiú megszabadít titeket, valósággal szabadok lesztek."));
husuperobject.biblemap.put("b43_8_37", new husuperobject(43,8,37,"Tudom, hogy Ábrahám magva vagytok; de meg akartok engem ölni, mert az én beszédemnek nincs helye nálatok."));
husuperobject.biblemap.put("b43_8_38", new husuperobject(43,8,38,"Én azt beszélem, a mit az én Atyámnál láttam; ti is azt cselekszitek azért, a mit a ti atyátoknál láttatok."));
husuperobject.biblemap.put("b43_8_39", new husuperobject(43,8,39,"Felelének és mondának néki: A mi atyánk Ábrahám. Monda nékik Jézus: Ha Ábrahám gyermekei volnátok, az Ábrahám dolgait cselekednétek."));
husuperobject.biblemap.put("b43_8_40", new husuperobject(43,8,40,"Ámde meg akartok engem ölni, olyan embert, a ki az igazságot beszéltem néktek, a melyet az Istentõl hallottam. Ábrahám ezt nem cselekedte."));
husuperobject.biblemap.put("b43_8_41", new husuperobject(43,8,41,"Ti a ti atyátok dolgait cselekszitek. Mondának azért néki: Mi nem paráznaságból születtünk; egy atyánk van, az Isten."));
husuperobject.biblemap.put("b43_8_42", new husuperobject(43,8,42,"Monda azért nékik Jézus: Ha az Isten volna a ti atyátok, szeretnétek engem: mert én az Istentõl származtam és jöttem; mert nem is magamtól jöttem, hanem õ küldött engem."));
husuperobject.biblemap.put("b43_8_43", new husuperobject(43,8,43,"Miért nem értitek az én beszédemet? Mert nem hallgatjátok az én szómat."));
husuperobject.biblemap.put("b43_8_44", new husuperobject(43,8,44,"Ti az ördög atyától valók vagytok, és a ti atyátok kívánságait akarjátok teljesíteni. Az emberölõ volt kezdettõl fogva, és nem állott meg az igazságban, mert nincsen õ benne igazság. Mikor hazugságot szól, a sajátjából szól; mert hazug és hazugság atyja."));
husuperobject.biblemap.put("b43_8_45", new husuperobject(43,8,45,"Mivelhogy pedig én igazságot szólok, nem hisztek nékem."));
husuperobject.biblemap.put("b43_8_46", new husuperobject(43,8,46,"Ki vádol engem közületek bûnnel? Ha pedig igazságot szólok: miért nem hisztek ti nékem?"));
husuperobject.biblemap.put("b43_8_47", new husuperobject(43,8,47,"A ki az Istentõl van, hallgatja az Isten beszédeit; azért nem hallgatjátok ti, mert nem vagytok az Istentõl valók."));
husuperobject.biblemap.put("b43_8_48", new husuperobject(43,8,48,"Felelének azért a zsidók és mondának néki: Nem jól mondjuk-é mi, hogy te Samaritánus vagy, és ördög van benned?"));
husuperobject.biblemap.put("b43_8_49", new husuperobject(43,8,49,"Felele Jézus: Nincs én bennem ördög; hanem tisztelem az én Atyámat, és ti gyaláztok engem."));
husuperobject.biblemap.put("b43_8_50", new husuperobject(43,8,50,"Pedig én nem keresem az én dicsõségemet: van a ki keresi és megítéli."));
husuperobject.biblemap.put("b43_8_51", new husuperobject(43,8,51,"Bizony, bizony mondom néktek, ha valaki megtartja az én beszédemet, nem lát halált soha örökké."));
husuperobject.biblemap.put("b43_8_52", new husuperobject(43,8,52,"Mondának azért néki a zsidók: Most értettük meg, hogy ördög van benned. Ábrahám meghalt, a próféták is; és te azt mondod: Ha valaki megtartja az én beszédemet, nem kóstol halált örökké."));
husuperobject.biblemap.put("b43_8_53", new husuperobject(43,8,53,"Avagy nagyobb vagy-é te a mi atyánknál Ábrahámnál, a ki meghalt? A próféták is meghaltak: kinek állítod te magadat?"));
husuperobject.biblemap.put("b43_8_54", new husuperobject(43,8,54,"Felele Jézus: Ha én dicsõítem magamat, az én dicsõségem semmi: az én Atyám az, a ki dicsõít engem, a kirõl ti azt mondjátok, hogy a ti Istenetek,"));
husuperobject.biblemap.put("b43_8_55", new husuperobject(43,8,55,"És nem ismeritek õt: de én ismerem õt; és ha azt mondom, hogy nem ismerem õt, hozzátok hasonlóvá, hazuggá leszek: de ismerem õt, és az õ beszédét megtartom."));
husuperobject.biblemap.put("b43_8_56", new husuperobject(43,8,56,"Ábrahám a ti atyátok örvendezett, hogy meglátja az én napomat; látta is, és örült."));
husuperobject.biblemap.put("b43_8_57", new husuperobject(43,8,57,"Mondának azért néki a zsidók: Még ötven esztendõs nem vagy, és Ábrahámot láttad?"));
husuperobject.biblemap.put("b43_8_58", new husuperobject(43,8,58,"Monda nékik Jézus: Bizony, bizony mondom néktek: Mielõtt Ábrahám lett, én vagyok."));
husuperobject.biblemap.put("b43_8_59", new husuperobject(43,8,59,"Köveket ragadának azért, hogy reá hajigálják; Jézus pedig elrejtõzködék, és kiméne a templomból, átmenvén közöttük; és ilyen módon eltávozék."));
husuperobject.biblemap.put("b43_9_1", new husuperobject(43,9,1,"És a mint eltávozék, láta egy embert, a ki születésétõl fogva vak vala."));
husuperobject.biblemap.put("b43_9_2", new husuperobject(43,9,2,"És kérdezék õt a tanítványai, mondván: Mester, ki vétkezett, ez-é vagy ennek szülei, hogy vakon született?"));
husuperobject.biblemap.put("b43_9_3", new husuperobject(43,9,3,"Felele Jézus: Sem ez nem vétkezett, sem ennek szülei; hanem, hogy nyilvánvalókká legyenek benne az Isten dolgai."));
husuperobject.biblemap.put("b43_9_4", new husuperobject(43,9,4,"Nékem cselekednem kell annak dolgait, a ki elküldött engem, a míg nappal van: eljõ az éjszaka, mikor senki sem munkálkodhatik."));
husuperobject.biblemap.put("b43_9_5", new husuperobject(43,9,5,"Míg e világon vagyok, e világ világossága vagyok."));
husuperobject.biblemap.put("b43_9_6", new husuperobject(43,9,6,"Ezeket mondván, a földre köpe, és az õ nyálából sárt csinála, és rákené a sarat a vak szemeire,"));
husuperobject.biblemap.put("b43_9_7", new husuperobject(43,9,7,"És monda néki: Menj el, mosakodjál meg a Siloám tavában (a mi azt jelenti: Küldött). Elméne azért és megmosakodék, és megjöve látva."));
husuperobject.biblemap.put("b43_9_8", new husuperobject(43,9,8,"A szomszédok azért, és a kik az elõtt látták azt, hogy vak vala, mondának: Nem ez-é az, a ki [itt] szokott ülni és koldulni?"));
husuperobject.biblemap.put("b43_9_9", new husuperobject(43,9,9,"Némelyek azt mondák, hogy: Ez az; mások pedig, hogy: Hasonlít hozzá. Õ azt mondá, hogy: Én vagyok [az]."));
husuperobject.biblemap.put("b43_9_10", new husuperobject(43,9,10,"Mondának azért néki: Mimódon nyiltak meg a te szemeid?"));
husuperobject.biblemap.put("b43_9_11", new husuperobject(43,9,11,"Felele az és monda: Egy ember, a kit Jézusnak mondanak, sarat készíte és rákené a szemeimre, és monda nékem: Menj el a Siloám tavára és mosódjál meg; miután pedig elmenék és megmosakodám, megjöve látásom."));
husuperobject.biblemap.put("b43_9_12", new husuperobject(43,9,12,"Mondának azért néki: Hol van az? Monda: Nem tudom."));
husuperobject.biblemap.put("b43_9_13", new husuperobject(43,9,13,"Vivék õt, a ki elõbb még vak volt, a farizeusokhoz."));
husuperobject.biblemap.put("b43_9_14", new husuperobject(43,9,14,"Mikor pedig Jézus a sarat csinálá és felnyitá ennek szemeit, szombat vala."));
husuperobject.biblemap.put("b43_9_15", new husuperobject(43,9,15,"Szintén a farizeusok is megkérdezék azért õt, mimódon jött meg a látása? Õ pedig monda nékik: Sarat tõn szemeimre, és megmosakodám, és látok."));
husuperobject.biblemap.put("b43_9_16", new husuperobject(43,9,16,"Mondának azért némelyek a farizeusok közül: Ez az ember nincsen Istentõl, mert nem tartja meg a szombatot. Mások mondának: Mimódon tehet bûnös ember ilyen jeleket? És hasonlás lõn közöttük."));
husuperobject.biblemap.put("b43_9_17", new husuperobject(43,9,17,"Újra mondának a vaknak: Te mit szólsz õ róla, hogy megnyitá a szemeidet? Õ pedig monda: Hogy próféta."));
husuperobject.biblemap.put("b43_9_18", new husuperobject(43,9,18,"Nem hivék azért a zsidók róla, hogy vak vala és megjöve a látása, mígnem elõhívák annak szüleit, a kinek megjöve a látása,"));
husuperobject.biblemap.put("b43_9_19", new husuperobject(43,9,19,"És megkérdezék azokat, mondván: Ez a ti fiatok, a kirõl azt mondjátok, hogy vakon született? mimódon lát hát most?"));
husuperobject.biblemap.put("b43_9_20", new husuperobject(43,9,20,"Felelének nékik annak szülei és mondának: Tudjuk, hogy ez a mi fiunk, és hogy vakon született:"));
husuperobject.biblemap.put("b43_9_21", new husuperobject(43,9,21,"De mimódon lát most, nem tudjuk; vagy ki nyitotta meg a szemeit, mi nem tudjuk: elég idõs [már] õ; õt kérdezzétek; õ beszéljen magáról."));
husuperobject.biblemap.put("b43_9_22", new husuperobject(43,9,22,"Ezeket mondák annak szülei, mivelhogy félnek vala a zsidóktól: mert megegyeztek már a zsidók, hogy ha valaki Krisztusnak vallja õt, rekesztessék ki a gyülekezetbõl."));
husuperobject.biblemap.put("b43_9_23", new husuperobject(43,9,23,"Ezért mondák annak szülei, hogy: Elég idõs, õt kérdezzétek."));
husuperobject.biblemap.put("b43_9_24", new husuperobject(43,9,24,"Másodszor is szólíták azért az embert, a ki vak vala, és mondának néki: Adj dicsõséget az Istennek; mi tudjuk, hogy ez az ember bûnös."));
husuperobject.biblemap.put("b43_9_25", new husuperobject(43,9,25,"Felele azért az és monda: Ha bûnös-é, nem tudom: egyet tudok, hogy noha vak voltam, most látok."));
husuperobject.biblemap.put("b43_9_26", new husuperobject(43,9,26,"Újra mondák pedig néki: Mit csinált veled? Mimódon nyitotta meg a szemeidet?"));
husuperobject.biblemap.put("b43_9_27", new husuperobject(43,9,27,"Felele nékik: Már mondám néktek és nem hallátok: miért akarjátok újra hallani? avagy ti is az õ tanítványai akartok lenni?"));
husuperobject.biblemap.put("b43_9_28", new husuperobject(43,9,28,"Szidalmazák azért õt és mondának: Te vagy annak a tanítványa; mi pedig a Mózes tanítványai vagyunk."));
husuperobject.biblemap.put("b43_9_29", new husuperobject(43,9,29,"Mi tudjuk, hogy Mózessel beszélt az Isten: errõl pedig azt sem tudjuk, honnan való."));
husuperobject.biblemap.put("b43_9_30", new husuperobject(43,9,30,"Felele az ember és monda nékik: Bizony csodálatos az, hogy ti nem tudjátok honnan való, és az én szemeimet megnyitotta."));
husuperobject.biblemap.put("b43_9_31", new husuperobject(43,9,31,"Pedig tudjuk, hogy az Isten nem hallgatja meg a bûnösöket; hanem ha valaki istenfélõ, és az õ akaratát cselekszi, azt hallgatja meg."));
husuperobject.biblemap.put("b43_9_32", new husuperobject(43,9,32,"Öröktõl fogva nem hallaték, hogy vakon szülöttnek szemeit valaki megnyitotta volna."));
husuperobject.biblemap.put("b43_9_33", new husuperobject(43,9,33,"Ha ez nem Istentõl volna, semmit sem cselekedhetnék."));
husuperobject.biblemap.put("b43_9_34", new husuperobject(43,9,34,"Felelének és mondának néki: Te mindenestõl bûnben születtél, és te tanítasz minket? És kiveték õt."));
husuperobject.biblemap.put("b43_9_35", new husuperobject(43,9,35,"Meghallá Jézus, hogy kiveték azt; és találkozván vele, monda néki: Hiszel-é te az Isten Fiában?"));
husuperobject.biblemap.put("b43_9_36", new husuperobject(43,9,36,"Felele az és monda: Ki az, Uram, hogy higyjek benne?"));
husuperobject.biblemap.put("b43_9_37", new husuperobject(43,9,37,"Monda pedig néki Jézus: Láttad is õt, és a ki beszél veled, az az."));
husuperobject.biblemap.put("b43_9_38", new husuperobject(43,9,38,"Az pedig monda: Hiszek, Uram. És imádá Õt."));
husuperobject.biblemap.put("b43_9_39", new husuperobject(43,9,39,"És monda Jézus: Ítélet végett jöttem én e világra, hogy a kik nem látnak, lássanak; és a kik látnak, vakok legyenek."));
husuperobject.biblemap.put("b43_9_40", new husuperobject(43,9,40,"És hallák ezeket némely farizeusok, a kik vele valának, és mondának néki: Avagy mi is vakok vagyunk-é?"));
husuperobject.biblemap.put("b43_9_41", new husuperobject(43,9,41,"Monda nékik Jézus: Ha vakok volnátok, nem volna bûnötök; ámde azt mondjátok, hogy látunk: azért a ti bûnötök megmarad."));
husuperobject.biblemap.put("b43_10_1", new husuperobject(43,10,1,"Bizony, bizony mondom néktek: A ki nem az ajtón megy be a juhok aklába, hanem másunnan hág be, tolvaj az és rabló."));
husuperobject.biblemap.put("b43_10_2", new husuperobject(43,10,2,"A ki pedig az ajtón megy be, a juhok pásztora az."));
husuperobject.biblemap.put("b43_10_3", new husuperobject(43,10,3,"Ennek az ajtónálló [ajtót] nyit; és a juhok hallgatnak annak szavára; és a maga juhait nevökön szólítja, és kivezeti õket."));
husuperobject.biblemap.put("b43_10_4", new husuperobject(43,10,4,"És mikor kiereszti az õ juhait, elõttök megy; és a juhok követik õt, mert ismerik az õ hangját."));
husuperobject.biblemap.put("b43_10_5", new husuperobject(43,10,5,"Idegent pedig nem követnek, hanem elfutnak attól: mert nem ismerik az idegenek hangját."));
husuperobject.biblemap.put("b43_10_6", new husuperobject(43,10,6,"Ezt a példázatot mondá nékik Jézus; de õk nem értették, mi az, a mit szól vala nékik."));
husuperobject.biblemap.put("b43_10_7", new husuperobject(43,10,7,"Újra monda azért nékik Jézus: Bizony, bizony mondom néktek, hogy én vagyok a juhoknak ajtaja."));
husuperobject.biblemap.put("b43_10_8", new husuperobject(43,10,8,"Mindazok, a kik elõttem jöttek, tolvajok és rablók: de nem hallgattak rájok a juhok."));
husuperobject.biblemap.put("b43_10_9", new husuperobject(43,10,9,"Én vagyok az ajtó: ha valaki én rajtam megy be, megtartatik és bejár és kijár majd, és legelõt talál."));
husuperobject.biblemap.put("b43_10_10", new husuperobject(43,10,10,"A tolvaj nem egyébért jõ, hanem hogy lopjon és öljön és pusztítson; én azért jöttem, hogy életök legyen, és bõvölködjenek."));
husuperobject.biblemap.put("b43_10_11", new husuperobject(43,10,11,"Én vagyok a jó pásztor: a jó pásztor életét adja a juhokért."));
husuperobject.biblemap.put("b43_10_12", new husuperobject(43,10,12,"A béres pedig és a ki nem pásztor, a kinek a juhok nem tulajdonai, látja a farkast jõni, és elhagyja a juhokat, és elfut: és a farkas elragadozza azokat, és elszéleszti a juhokat."));
husuperobject.biblemap.put("b43_10_13", new husuperobject(43,10,13,"A béres pedig [azért] fut el, mert béres, és nincs gondja a juhokra."));
husuperobject.biblemap.put("b43_10_14", new husuperobject(43,10,14,"Én vagyok a jó pásztor; és ismerem az enyéimet, és engem [is] ismernek az enyéim,"));
husuperobject.biblemap.put("b43_10_15", new husuperobject(43,10,15,"A miként ismer engem az Atya, és én [is] ismerem az Atyát; és életemet adom a juhokért."));
husuperobject.biblemap.put("b43_10_16", new husuperobject(43,10,16,"Más juhaim is vannak nékem, a melyek nem ebbõl az akolból valók: azokat is elõ kell hoznom, és hallgatnak majd az én szómra; és lészen egy akol [és] egy pásztor."));
husuperobject.biblemap.put("b43_10_17", new husuperobject(43,10,17,"Azért szeret engem az Atya, mert én leteszem az én életemet, hogy újra felvegyem azt."));
husuperobject.biblemap.put("b43_10_18", new husuperobject(43,10,18,"Senki sem veszi azt el én tõlem, hanem én teszem le azt én magamtól. Van hatalmam letenni azt, és van hatalmam ismét felvenni azt. Ezt a parancsolatot vettem az én Atyámtól."));
husuperobject.biblemap.put("b43_10_19", new husuperobject(43,10,19,"Újra hasonlás lõn a zsidók között e beszédek miatt."));
husuperobject.biblemap.put("b43_10_20", new husuperobject(43,10,20,"És sokan mondják vala közülök: Ördög van benne és bolondozik, mit hallgattok reá?"));
husuperobject.biblemap.put("b43_10_21", new husuperobject(43,10,21,"Mások mondának: Ezek nem ördöngõsnek beszédei. Vajjon az ördög megnyithatja-é a vakok szemeit?"));
husuperobject.biblemap.put("b43_10_22", new husuperobject(43,10,22,"Lõn pedig Jeruzsálemben a templomszentelés ünnepe: és tél vala;"));
husuperobject.biblemap.put("b43_10_23", new husuperobject(43,10,23,"És Jézus a templomban, a Salamon tornáczában jár vala."));
husuperobject.biblemap.put("b43_10_24", new husuperobject(43,10,24,"Körülvevék azért õt a zsidók, és mondának néki: Meddig tartasz még bizonytalanságban bennünket? Ha te vagy a Krisztus, mondd meg nékünk nyilván!"));
husuperobject.biblemap.put("b43_10_25", new husuperobject(43,10,25,"Felele nékik Jézus: Megmondtam néktek, és nem hiszitek: a cselekedetek, a melyeket én cselekszem az én Atyám nevében, azok tesznek bizonyságot rólam."));
husuperobject.biblemap.put("b43_10_26", new husuperobject(43,10,26,"De ti nem hisztek, mert ti nem az én juhaim közül vagytok. A mint megmondtam néktek:"));
husuperobject.biblemap.put("b43_10_27", new husuperobject(43,10,27,"Az én juhaim hallják az én szómat, és én ismerem õket, és követnek engem:"));
husuperobject.biblemap.put("b43_10_28", new husuperobject(43,10,28,"És én örök életet adok nékik; és soha örökké el nem vesznek, és senki ki nem ragadja õket az én kezembõl."));
husuperobject.biblemap.put("b43_10_29", new husuperobject(43,10,29,"Az én Atyám, a ki [azokat] adta nékem, nagyobb mindeneknél; és senki sem ragadhatja ki [azokat] az én Atyámnak kezébõl."));
husuperobject.biblemap.put("b43_10_30", new husuperobject(43,10,30,"Én és az Atya egy vagyunk."));
husuperobject.biblemap.put("b43_10_31", new husuperobject(43,10,31,"Ismét köveket ragadának azért a zsidók, hogy megkövezzék õt."));
husuperobject.biblemap.put("b43_10_32", new husuperobject(43,10,32,"Felele nékik Jézus: Sok jó dolgot mutattam néktek az én Atyámtól; azok közül melyik dologért köveztek meg engem?"));
husuperobject.biblemap.put("b43_10_33", new husuperobject(43,10,33,"Felelének néki a zsidók, mondván: Jó dologért nem kövezünk meg téged, hanem káromlásért, tudniillik, hogy te ember létedre Istenné teszed magadat."));
husuperobject.biblemap.put("b43_10_34", new husuperobject(43,10,34,"Felele nékik Jézus: Nincs-é megírva a ti törvényetekben: Én mondám: Istenek vagytok?"));
husuperobject.biblemap.put("b43_10_35", new husuperobject(43,10,35,"Ha azokat isteneknek mondá, a kikhez az Isten beszéde lõn (és az írás fel nem bontható),"));
husuperobject.biblemap.put("b43_10_36", new husuperobject(43,10,36,"Arról mondjátok-é ti, a kit az Atya megszentelt és elküldött e világra: Káromlást szólsz; mivelhogy azt mondám: Az Isten Fia vagyok?!"));
husuperobject.biblemap.put("b43_10_37", new husuperobject(43,10,37,"Ha az én Atyám dolgait nem cselekszem, ne higyjetek nékem;"));
husuperobject.biblemap.put("b43_10_38", new husuperobject(43,10,38,"Ha pedig azokat cselekszem, ha nékem nem hisztek is, higyjetek a cselekedeteknek: hogy megtudjátok és elhigyjétek, hogy az Atya én bennem van, és én õ benne vagyok."));
husuperobject.biblemap.put("b43_10_39", new husuperobject(43,10,39,"Ismét meg akarák azért õt fogni; de kiméne az õ kezökbõl."));
husuperobject.biblemap.put("b43_10_40", new husuperobject(43,10,40,"És újra elméne túl a Jordánon, arra a helyre, a hol János elõször keresztelt vala; és ott marada."));
husuperobject.biblemap.put("b43_10_41", new husuperobject(43,10,41,"És sokan menének õ hozzá és mondják vala, hogy: János nem tett ugyan semmi csodát; de mindaz, a mit János e felõl mondott, igaz vala."));
husuperobject.biblemap.put("b43_10_42", new husuperobject(43,10,42,"És sokan hivének ott õ benne."));
husuperobject.biblemap.put("b43_11_1", new husuperobject(43,11,1,"Vala pedig egy beteg, Lázár, Bethániából, Máriának és az õ testvérének, Márthának falujából."));
husuperobject.biblemap.put("b43_11_2", new husuperobject(43,11,2,"Az a Mária volt pedig az, a kinek a testvére Lázár beteg vala, a ki megkente vala az Urat kenettel és a hajával törlé meg annak lábait."));
husuperobject.biblemap.put("b43_11_3", new husuperobject(43,11,3,"Küldének azért a testvérek õ hozzá, mondván: Uram, ímé, a kit szeretsz, beteg."));
husuperobject.biblemap.put("b43_11_4", new husuperobject(43,11,4,"Jézus pedig, a mikor ezt hallotta, monda: Ez a betegség nem halálos, hanem az Isten dicsõségére való, hogy dicsõíttessék általa az Istennek Fia."));
husuperobject.biblemap.put("b43_11_5", new husuperobject(43,11,5,"Szereti vala pedig Jézus Márthát és annak nõtestvérét, és Lázárt."));
husuperobject.biblemap.put("b43_11_6", new husuperobject(43,11,6,"Mikor azért meghallá, hogy beteg, akkor két napig marada azon a helyen, a hol vala."));
husuperobject.biblemap.put("b43_11_7", new husuperobject(43,11,7,"Ez után aztán monda tanítványainak: Menjünk ismét Júdeába."));
husuperobject.biblemap.put("b43_11_8", new husuperobject(43,11,8,"Mondának néki a tanítványok: Mester, most akarnak vala téged megkövezni a Júdabeliek, és újra oda mégy?"));
husuperobject.biblemap.put("b43_11_9", new husuperobject(43,11,9,"Felele Jézus: Avagy nem tizenkét órája van-é a napnak? Ha valaki nappal jár, nem botlik meg, mert látja e világnak világosságát."));
husuperobject.biblemap.put("b43_11_10", new husuperobject(43,11,10,"De a ki éjjel jár, megbotlik, mert nincsen abban világosság."));
husuperobject.biblemap.put("b43_11_11", new husuperobject(43,11,11,"Ezeket mondá; és ezután monda nékik: Lázár, a mi barátunk, elaludt; de elmegyek, hogy felköltsem õt."));
husuperobject.biblemap.put("b43_11_12", new husuperobject(43,11,12,"Mondának azért az õ tanítványai: Uram, ha elaludt, meggyógyul."));
husuperobject.biblemap.put("b43_11_13", new husuperobject(43,11,13,"Pedig Jézus annak haláláról beszélt; de õk azt hitték, hogy álomnak alvásáról szól."));
husuperobject.biblemap.put("b43_11_14", new husuperobject(43,11,14,"Ekkor azért nyilván monda nékik Jézus: Lázár megholt."));
husuperobject.biblemap.put("b43_11_15", new husuperobject(43,11,15,"És örülök, hogy nem voltam ott, ti érettetek, hogy higyjetek. De menjünk el õ hozzá!"));
husuperobject.biblemap.put("b43_11_16", new husuperobject(43,11,16,"Monda azért Tamás, a ki Kettõsnek mondatik, az õ tanítványtársainak: Menjünk el mi is, hogy meghaljunk vele."));
husuperobject.biblemap.put("b43_11_17", new husuperobject(43,11,17,"Elmenvén azért Jézus, úgy találá, hogy az már négy napja vala sírban."));
husuperobject.biblemap.put("b43_11_18", new husuperobject(43,11,18,"Bethánia pedig közel vala Jeruzsálemhez, mintegy tizenöt futamatnyira;"));
husuperobject.biblemap.put("b43_11_19", new husuperobject(43,11,19,"És a zsidók közül sokan mentek vala Márthához és Máriához, hogy vigasztalják õket az õ testvérök felõl."));
husuperobject.biblemap.put("b43_11_20", new husuperobject(43,11,20,"Mártha azért, a mint hallja vala, hogy Jézus jõ, elébe méne; Mária pedig otthon ül vala."));
husuperobject.biblemap.put("b43_11_21", new husuperobject(43,11,21,"Monda azért Mártha Jézusnak: Uram, ha itt lettél volna, nem halt volna meg a testvérem."));
husuperobject.biblemap.put("b43_11_22", new husuperobject(43,11,22,"De most is tudom, hogy a mit csak kérsz az Istentõl, megadja néked az Isten."));
husuperobject.biblemap.put("b43_11_23", new husuperobject(43,11,23,"Monda néki Jézus: Feltámad a te testvéred."));
husuperobject.biblemap.put("b43_11_24", new husuperobject(43,11,24,"Monda néki Mártha: Tudom, hogy feltámad a feltámadáskor az utolsó napon."));
husuperobject.biblemap.put("b43_11_25", new husuperobject(43,11,25,"Monda néki Jézus: Én vagyok a feltámadás és az élet: a ki hisz én bennem, ha meghal is, él;"));
husuperobject.biblemap.put("b43_11_26", new husuperobject(43,11,26,"És a ki csak él és hisz én bennem, soha meg nem hal. Hiszed-é ezt?"));
husuperobject.biblemap.put("b43_11_27", new husuperobject(43,11,27,"Monda néki: Igen Uram, én hiszem, hogy te vagy a Krisztus, az Istennek Fia, a ki e világra jövendõ vala."));
husuperobject.biblemap.put("b43_11_28", new husuperobject(43,11,28,"És a mint ezeket mondotta vala, elméne, és titkon szólítá az õ testvérét Máriát, mondván: A Mester itt van és hív téged."));
husuperobject.biblemap.put("b43_11_29", new husuperobject(43,11,29,"Mihelyt ez hallá, felkele hamar és hozzá méne."));
husuperobject.biblemap.put("b43_11_30", new husuperobject(43,11,30,"Jézus pedig nem ment vala még be a faluba, hanem azon a helyen vala, a hová Mártha elébe ment vala."));
husuperobject.biblemap.put("b43_11_31", new husuperobject(43,11,31,"A zsidók azért, a kik õ vele otthon valának és vigasztalák õt, látván, hogy Mária hamar felkél és kimegy vala, utána menének, ezt mondván: A sírhoz megy, hogy ott sírjon."));
husuperobject.biblemap.put("b43_11_32", new husuperobject(43,11,32,"Mária azért, a mint oda ére, a hol Jézus vala, meglátván õt, az õ lábaihoz esék, mondván néki: Uram, ha itt voltál volna, nem halt volna meg az én testvérem."));
husuperobject.biblemap.put("b43_11_33", new husuperobject(43,11,33,"Jézus azért, a mint látja vala, hogy az sír és sírnak a vele jött zsidók [is,] elbúsula lelkében és igen megrendüle."));
husuperobject.biblemap.put("b43_11_34", new husuperobject(43,11,34,"És monda: Hová helyeztétek õt? Mondának néki: Uram, jer és lásd meg!"));
husuperobject.biblemap.put("b43_11_35", new husuperobject(43,11,35,"Könnyekre fakadt Jézus."));
husuperobject.biblemap.put("b43_11_36", new husuperobject(43,11,36,"Mondának azért a zsidók: Ímé, mennyire szerette õt!"));
husuperobject.biblemap.put("b43_11_37", new husuperobject(43,11,37,"Némelyek pedig mondának közülök: Nem megtehette volna-é ez, a ki a vaknak szemét felnyitotta, hogy ez ne haljon meg?"));
husuperobject.biblemap.put("b43_11_38", new husuperobject(43,11,38,"Jézus pedig újra felindulva magában, oda megy vala a sírhoz. Az pedig egy üreg vala, és kõ feküvék rajta."));
husuperobject.biblemap.put("b43_11_39", new husuperobject(43,11,39,"Monda Jézus: Vegyétek el a követ. Monda néki a megholtnak nõtestvére, Mártha: Uram, immár szaga van, hiszen negyednapos."));
husuperobject.biblemap.put("b43_11_40", new husuperobject(43,11,40,"Monda néki Jézus: Nem mondtam-é néked, hogy ha hiszel, meglátod majd az Istennek dicsõségét?"));
husuperobject.biblemap.put("b43_11_41", new husuperobject(43,11,41,"Elvevék azért a követ onnan, a hol a megholt feküszik vala. Jézus pedig felemelé szemeit az égre, és monda: Atyám, hálát adok néked, hogy meghallgattál engem."));
husuperobject.biblemap.put("b43_11_42", new husuperobject(43,11,42,"Tudtam is én, hogy te mindenkor meghallgatsz engem; csak a körülálló sokaságért mondtam, hogy elhigyjék, hogy te küldtél engem."));
husuperobject.biblemap.put("b43_11_43", new husuperobject(43,11,43,"És mikor ezeket mondá, fenszóval kiálta: Lázár, jõjj ki!"));
husuperobject.biblemap.put("b43_11_44", new husuperobject(43,11,44,"És kijöve a megholt, lábain és kezein kötelékekkel megkötözve, és az orczája kendõvel vala leborítva. Monda nékik Jézus: Oldozzátok meg õt, és hagyjátok menni."));
husuperobject.biblemap.put("b43_11_45", new husuperobject(43,11,45,"Sokan hivének azért õ benne ama zsidók közül, a kik Máriához mentek vala, és láták, a miket cselekedett vala."));
husuperobject.biblemap.put("b43_11_46", new husuperobject(43,11,46,"De némelyek azok közül elmenének a farizeusokhoz, és elbeszélék nékik, a miket Jézus cselekedett vala."));
husuperobject.biblemap.put("b43_11_47", new husuperobject(43,11,47,"Egybegyûjték azért a papifejedelmek és a farizeusok a fõtanácsot, és mondának: Mit cselekedjünk? mert ez az ember sok csodát mível."));
husuperobject.biblemap.put("b43_11_48", new husuperobject(43,11,48,"Ha ekképen hagyjuk õt, mindenki hinni fog õ benne: és eljõnek majd a rómaiak és elveszik tõlünk mind e helyet, mind e népet."));
husuperobject.biblemap.put("b43_11_49", new husuperobject(43,11,49,"Egy pedig õ közülök, Kajafás, a ki fõpap vala abban az esztendõben, monda nékik: Ti semmit sem tudtok."));
husuperobject.biblemap.put("b43_11_50", new husuperobject(43,11,50,"Meg sem gondoljátok, hogy jobb nékünk, hogy egy ember haljon meg a népért, és az egész nép el ne vesszen."));
husuperobject.biblemap.put("b43_11_51", new husuperobject(43,11,51,"Ezt pedig nem magától mondta: hanem mivelhogy abban az esztendõben fõpap vala, jövendõt monda, hogy Jézus meg fog halni a népért;"));
husuperobject.biblemap.put("b43_11_52", new husuperobject(43,11,52,"És nemcsak a népért, hanem azért is, hogy az Istennek elszéledt gyermekeit egybegyûjtse."));
husuperobject.biblemap.put("b43_11_53", new husuperobject(43,11,53,"Ama naptól azért azon tanakodának, hogy õt megöljék."));
husuperobject.biblemap.put("b43_11_54", new husuperobject(43,11,54,"Jézus azért nem jár vala többé nyilvánosan a zsidók között, hanem elméne onnan a vidékre, a pusztához közel, egy Efraim nevû városba; és ott tartózkodék az õ tanítványaival."));
husuperobject.biblemap.put("b43_11_55", new husuperobject(43,11,55,"Közel vala pedig a zsidók husvétja: és sokan menének fel Jeruzsálembe a vidékrõl husvét elõtt, hogy megtisztuljanak."));
husuperobject.biblemap.put("b43_11_56", new husuperobject(43,11,56,"Keresék azért Jézust, és szólnak vala egymással a templomban állva: Mit gondoltok, hogy nem jön-é fel az ünnepre?"));
husuperobject.biblemap.put("b43_11_57", new husuperobject(43,11,57,"A papi fejedelmek pedig és a farizeusok is parancsolatot adának, hogy ha valaki megtudja, hogy hol van, jelentse meg, hogy õt megfogják."));
husuperobject.biblemap.put("b43_12_1", new husuperobject(43,12,1,"Jézus azért hat nappal a husvét elõtt méne Bethániába, a hol a megholt Lázár vala, a kit feltámasztott a halálból."));
husuperobject.biblemap.put("b43_12_2", new husuperobject(43,12,2,"Vacsorát készítének azért ott néki, és Mártha szolgál vala fel; Lázár pedig egy vala azok közül, a kik együtt ülnek vala õ vele."));
husuperobject.biblemap.put("b43_12_3", new husuperobject(43,12,3,"Mária azért elõvévén egy font igazi, drága nárdusból való kenetet, megkené a Jézus lábait, és megtörlé annak lábait a saját hajával; a ház pedig megtelék a kenet illatával."));
husuperobject.biblemap.put("b43_12_4", new husuperobject(43,12,4,"Monda azért egy az õ tanítványai közül, Iskáriótes Júdás, Simonnak fia, a ki õt elárulandó vala:"));
husuperobject.biblemap.put("b43_12_5", new husuperobject(43,12,5,"Miért nem adták el ezt a kenetet háromszáz dénáron, és [miért nem] adták a szegényeknek?"));
husuperobject.biblemap.put("b43_12_6", new husuperobject(43,12,6,"Ezt pedig nem azért mondá, mintha néki a szegényekre volna gondja, hanem mivelhogy tolvaj vala, és nála vala az erszény, és amit abba tesznek vala, elcsené."));
husuperobject.biblemap.put("b43_12_7", new husuperobject(43,12,7,"Monda azért Jézus: Hagyj békét néki; az én temetésem idejére tartogatta õ ezt."));
husuperobject.biblemap.put("b43_12_8", new husuperobject(43,12,8,"Mert szegények mindenkor vannak veletek, én pedig nem mindenkor vagyok."));
husuperobject.biblemap.put("b43_12_9", new husuperobject(43,12,9,"A zsidók közül azért nagy sokaság értesült vala arról, hogy õ ott van: és oda menének nemcsak Jézusért, hanem hogy Lázárt is lássák, a kit feltámasztott a halálból."));
husuperobject.biblemap.put("b43_12_10", new husuperobject(43,12,10,"A papifejedelmek pedig tanácskozának, hogy Lázárt is megöljék;"));
husuperobject.biblemap.put("b43_12_11", new husuperobject(43,12,11,"Mivelhogy a zsidók közül sokan õ miatta menének oda és hivének a Jézusban."));
husuperobject.biblemap.put("b43_12_12", new husuperobject(43,12,12,"Másnap a nagy sokaság, a mely az ünnepre jött vala, hallván, hogy Jézus Jeruzsálembe jõ,"));
husuperobject.biblemap.put("b43_12_13", new husuperobject(43,12,13,"Pálmaágakat võn, és kiméne elébe, és kiált vala: Hozsánna: Áldott, a ki jõ az Úrnak nevében, az Izráelnek ama királya!"));
husuperobject.biblemap.put("b43_12_14", new husuperobject(43,12,14,"Találván pedig Jézus egy szamarat, felüle arra, a mint meg van írva:"));
husuperobject.biblemap.put("b43_12_15", new husuperobject(43,12,15,"Ne félj Sionnak leánya: Ímé a te királyod jõ, szamárnak vemhén ülve."));
husuperobject.biblemap.put("b43_12_16", new husuperobject(43,12,16,"Ezeket pedig nem értették eleinte az õ tanítványai: hanem mikor megdicsõítteték Jézus, akkor emlékezének vissza, hogy ezek õ felõle vannak megírva, és [hogy] ezeket mívelték õ vele."));
husuperobject.biblemap.put("b43_12_17", new husuperobject(43,12,17,"A sokaság azért, a mely õ vele vala, mikor kihívta Lázárt a koporsóból és feltámasztotta õt a halálból, bizonyságot tõn."));
husuperobject.biblemap.put("b43_12_18", new husuperobject(43,12,18,"Azért is méne õ elébe a sokaság, mivel hallá, hogy ezt a csodát mívelte vala."));
husuperobject.biblemap.put("b43_12_19", new husuperobject(43,12,19,"Mondának azért a farizeusok egymás között: Látjátok-é, hogy semmit sem értek? Ímé, [mind] e világ õ utána megy."));
husuperobject.biblemap.put("b43_12_20", new husuperobject(43,12,20,"Néhány görög is vala azok között, a kik felmenének, hogy imádkozzanak az ünnepen:"));
husuperobject.biblemap.put("b43_12_21", new husuperobject(43,12,21,"Ezek azért a galileai Bethsaidából való Filephez menének, és kérék õt, mondván: Uram, látni akarjuk a Jézust."));
husuperobject.biblemap.put("b43_12_22", new husuperobject(43,12,22,"Megy vala Filep és szóla Andrásnak, és viszont András és Filep szóla Jézusnak."));
husuperobject.biblemap.put("b43_12_23", new husuperobject(43,12,23,"Jézus pedig felele nékik, mondván: Eljött az óra, hogy megdicsõíttessék az embernek Fia."));
husuperobject.biblemap.put("b43_12_24", new husuperobject(43,12,24,"Bizony, bizony mondom néktek: Ha a földbe esett gabonamag el nem hal, csak egymaga marad; ha pedig elhal, sok gyümölcsöt terem."));
husuperobject.biblemap.put("b43_12_25", new husuperobject(43,12,25,"A ki szereti a maga életét, elveszti azt; és a ki gyûlöli a maga életét e világon, örök életre tartja meg azt."));
husuperobject.biblemap.put("b43_12_26", new husuperobject(43,12,26,"A ki nékem szolgál, engem kövessen; és a hol én vagyok, ott lesz az én szolgám is: és a ki nékem szolgál, megbecsüli azt az Atya."));
husuperobject.biblemap.put("b43_12_27", new husuperobject(43,12,27,"Most az én lelkem háborog; és mit mondjak? Atyám, ments meg engem ettõl az órától. De azért jutottam ez órára."));
husuperobject.biblemap.put("b43_12_28", new husuperobject(43,12,28,"Atyám, dicsõítsd meg a te nevedet! Szózat jöve azért az égbõl: Meg is dicsõítettem, és újra megdicsõítem."));
husuperobject.biblemap.put("b43_12_29", new husuperobject(43,12,29,"A sokaság azért, a mely [ott] állt és hallotta vala, azt mondá, hogy mennydörgött; mások mondának: Angyal szólt néki."));
husuperobject.biblemap.put("b43_12_30", new husuperobject(43,12,30,"Felele Jézus és monda: Nem én érettem lõn e szó, hanem ti érettetek."));
husuperobject.biblemap.put("b43_12_31", new husuperobject(43,12,31,"Most van e világ kárhoztatása; most vettetik ki e világ fejedelme:"));
husuperobject.biblemap.put("b43_12_32", new husuperobject(43,12,32,"És én, ha felemeltetem e földrõl, mindeneket magamhoz vonszok."));
husuperobject.biblemap.put("b43_12_33", new husuperobject(43,12,33,"Ezt pedig azért mondá, hogy megjelentse, milyen halállal kell meghalnia."));
husuperobject.biblemap.put("b43_12_34", new husuperobject(43,12,34,"Felele néki a sokaság: Mi azt hallottuk a törvénybõl, hogy a Krisztus örökké megmarad: hogyan mondod hát te, hogy az ember Fiának fel kell emeltetnie? Kicsoda ez az ember Fia?"));
husuperobject.biblemap.put("b43_12_35", new husuperobject(43,12,35,"Monda azért nékik Jézus: Még egy kevés ideig veletek van a világosság. Járjatok, a míg világosságotok van, hogy sötétség ne lepjen meg titeket: és a ki a sötétségben jár, nem tudja, hová megy."));
husuperobject.biblemap.put("b43_12_36", new husuperobject(43,12,36,"Míg a világosságotok megvan, higyjetek a világosságban, hogy a világosság fiai legyetek. Ezeket mondá Jézus, és elmenvén, elrejtõzködék elõlük."));
husuperobject.biblemap.put("b43_12_37", new husuperobject(43,12,37,"És noha õ ennyi jelt tett vala elõttük, mégsem hivének õ benne:"));
husuperobject.biblemap.put("b43_12_38", new husuperobject(43,12,38,"Hogy beteljesedjék az Ésaiás próféta beszéde, a melyet monda: Uram, ki hitt a mi tanításunknak? és az Úr karja kinek jelentetett meg?"));
husuperobject.biblemap.put("b43_12_39", new husuperobject(43,12,39,"Azért nem hihetnek vala, mert ismét monda Ésaiás:"));
husuperobject.biblemap.put("b43_12_40", new husuperobject(43,12,40,"Megvakította az õ szemeiket, és megkeményítette az õ szívöket; hogy szemeikkel ne lássanak és szívökkel ne értsenek, és meg ne térjenek, és meg ne gyógyítsam õket."));
husuperobject.biblemap.put("b43_12_41", new husuperobject(43,12,41,"Ezeket mondá Ésaiás, a mikor látá az õ dicsõségét; és beszéle õ felõle."));
husuperobject.biblemap.put("b43_12_42", new husuperobject(43,12,42,"Mindazáltal a fõemberek közül is sokan hivének õ benne: de a farizeusok miatt nem vallák be, hogy ki ne rekesztessenek a gyülekezetbõl:"));
husuperobject.biblemap.put("b43_12_43", new husuperobject(43,12,43,"Mert inkább szerették az emberek dicséretét, mintsem az Istennek dicséretét."));
husuperobject.biblemap.put("b43_12_44", new husuperobject(43,12,44,"Jézus pedig kiálta és monda: A ki hisz én bennem, nem én bennem hisz, hanem abban, a ki elküldött engem."));
husuperobject.biblemap.put("b43_12_45", new husuperobject(43,12,45,"És a ki engem lát, azt látja, a ki küldött engem."));
husuperobject.biblemap.put("b43_12_46", new husuperobject(43,12,46,"Én világosságul jöttem e világra, hogy senki ne maradjon a sötétségben, a ki én bennem hisz."));
husuperobject.biblemap.put("b43_12_47", new husuperobject(43,12,47,"És ha valaki hallja az én beszédeimet és nem hisz, én nem kárhoztatom azt: mert nem azért jöttem, hogy kárhoztassam a világot, hanem hogy megtartsam a világot."));
husuperobject.biblemap.put("b43_12_48", new husuperobject(43,12,48,"A ki megvet engem és nem veszi be az én beszédeimet, van annak, a ki õt kárhoztassa: a beszéd, a melyet szólottam, az kárhoztatja azt az utolsó napon."));
husuperobject.biblemap.put("b43_12_49", new husuperobject(43,12,49,"Mert én nem magamtól szóltam; hanem az Atya, a ki küldött engem, õ parancsolta nékem, hogy mit mondjak és mit beszéljek."));
husuperobject.biblemap.put("b43_12_50", new husuperobject(43,12,50,"És tudom, hogy az õ parancsolata örök élet. A miket azért én beszélek, úgy beszélem, a mint az Atya mondotta vala nékem."));
husuperobject.biblemap.put("b43_13_1", new husuperobject(43,13,1,"A husvét ünnepe elõtt pedig, tudván Jézus, hogy eljött az õ órája, hogy átmenjen e világból az Atyához, mivelhogy szerette az övéit e világon, mindvégig szerette õket."));
husuperobject.biblemap.put("b43_13_2", new husuperobject(43,13,2,"És vacsora közben, a mikor az ördög belesugalta már Iskáriótes Júdásnak, a Simon fiának szívébe, hogy árulja el õt,"));
husuperobject.biblemap.put("b43_13_3", new husuperobject(43,13,3,"Tudván Jézus, hogy az Atya mindent hatalmába adott néki, és hogy õ az Istentõl jött és az Istenhez megy,"));
husuperobject.biblemap.put("b43_13_4", new husuperobject(43,13,4,"Felkele a vacsorától, leveté a felsõ ruháját; és egy kendõt vévén, körülköté magát."));
husuperobject.biblemap.put("b43_13_5", new husuperobject(43,13,5,"Azután vizet tölte a medenczébe, és kezdé mosni a tanítványok lábait, és megtörleni a kendõvel, a melylyel körül van kötve."));
husuperobject.biblemap.put("b43_13_6", new husuperobject(43,13,6,"Méne azért Simon Péterhez; és az monda néki: Uram, te mosod-é meg az én lábaimat?"));
husuperobject.biblemap.put("b43_13_7", new husuperobject(43,13,7,"Felele Jézus és monda néki: A mit én cselekszem, te azt most nem érted, de ezután majd megérted."));
husuperobject.biblemap.put("b43_13_8", new husuperobject(43,13,8,"Monda néki Péter: Az én lábaimat nem mosod meg soha! Felele néki Jézus: Ha meg nem moslak téged, semmi közöd sincs én hozzám."));
husuperobject.biblemap.put("b43_13_9", new husuperobject(43,13,9,"Monda néki Simon Péter: Uram, ne csak lábaimat, hanem kezeimet és fejemet is!"));
husuperobject.biblemap.put("b43_13_10", new husuperobject(43,13,10,"Monda néki Jézus: A ki megfürödött, nincs másra szüksége, mint a lábait megmosni, különben egészen tiszta; ti is tiszták vagytok, de nem mindnyájan."));
husuperobject.biblemap.put("b43_13_11", new husuperobject(43,13,11,"Tudta ugyanis, hogy ki árulja el õt; azért mondá: Nem vagytok mindnyájan tiszták!"));
husuperobject.biblemap.put("b43_13_12", new husuperobject(43,13,12,"Mikor azért megmosta azoknak lábait, és a felsõ ruháját felvette, újra leülvén, monda nékik: Értitek-é, hogy mit cselekedtem veletek?"));
husuperobject.biblemap.put("b43_13_13", new husuperobject(43,13,13,"Ti engem így hívtok: Mester, és Uram . És jól mondjátok, mert az vagyok."));
husuperobject.biblemap.put("b43_13_14", new husuperobject(43,13,14,"Azért, ha én az Úr és a Mester megmostam a ti lábaitokat, néktek is meg kell mosnotok egymás lábait."));
husuperobject.biblemap.put("b43_13_15", new husuperobject(43,13,15,"Mert példát adtam néktek, hogy a miképen én cselekedtem veletek, ti is akképen cselekedjetek."));
husuperobject.biblemap.put("b43_13_16", new husuperobject(43,13,16,"Bizony, bizony mondom néktek: A szolga nem nagyobb az õ Uránál; sem a követ nem nagyobb annál, a ki azt küldte."));
husuperobject.biblemap.put("b43_13_17", new husuperobject(43,13,17,"Ha tudjátok ezeket, boldogok lesztek, ha cselekszitek ezeket."));
husuperobject.biblemap.put("b43_13_18", new husuperobject(43,13,18,"Nem mindnyájatokról szólok; tudom én kiket választottam el; hanem hogy beteljesedjék az írás: A ki velem ette a kenyeret, a sarkát emelte fel ellenem."));
husuperobject.biblemap.put("b43_13_19", new husuperobject(43,13,19,"Most megmondom néktek, mielõtt meglenne, hogy mikor meglesz, higyjétek majd, hogy én vagyok."));
husuperobject.biblemap.put("b43_13_20", new husuperobject(43,13,20,"Bizony, bizony mondom néktek: A ki befogadja, ha valakit elküldök, engem fogad be; a ki pedig engem befogad, azt fogadja be, a ki engem küldött."));
husuperobject.biblemap.put("b43_13_21", new husuperobject(43,13,21,"Mikor ezeket mondja vala Jézus, igen nyugtalankodék lelkében, s bizonyságot tõn, és monda: Bizony, bizony mondom néktek, hogy egy ti közületek elárul engem."));
husuperobject.biblemap.put("b43_13_22", new husuperobject(43,13,22,"A tanítványok ekkor egymásra tekintének bizonytalankodva, [hogy ]kirõl szól."));
husuperobject.biblemap.put("b43_13_23", new husuperobject(43,13,23,"Egy pedig az õ tanítványai közül a Jézus kebelén nyugszik vala, a kit szeret vala Jézus."));
husuperobject.biblemap.put("b43_13_24", new husuperobject(43,13,24,"Int azért ennek Simon Péter, hogy tudakozza meg, ki az, a kirõl szól?"));
husuperobject.biblemap.put("b43_13_25", new husuperobject(43,13,25,"Az pedig a Jézus kebelére hajolván, monda néki: Uram, ki az?"));
husuperobject.biblemap.put("b43_13_26", new husuperobject(43,13,26,"Felele Jézus: Az, a kinek én a bemártott falatot adom. És bemártván a falatot, adá Iskáriótes Júdásnak, a Simon fiának."));
husuperobject.biblemap.put("b43_13_27", new husuperobject(43,13,27,"És a falat után akkor beméne abba a Sátán. Monda azért néki Jézus: A mit cselekszel, hamar cselekedjed."));
husuperobject.biblemap.put("b43_13_28", new husuperobject(43,13,28,"Ezt pedig senki sem érté a leültek közül, miért mondta néki."));
husuperobject.biblemap.put("b43_13_29", new husuperobject(43,13,29,"Némelyek ugyanis állíták, mivelhogy az erszény Júdásnál vala, hogy azt mondá néki Jézus: Vedd meg, a mikre szükségünk van az ünnepre; vagy, hogy adjon valamit a szegényeknek."));
husuperobject.biblemap.put("b43_13_30", new husuperobject(43,13,30,"Az pedig, mihelyt a falatot elvevé, azonnal kiméne: vala pedig éjszaka."));
husuperobject.biblemap.put("b43_13_31", new husuperobject(43,13,31,"Mikor azért kiment vala, monda Jézus: Most dicsõítteték meg az embernek Fia, az Isten is megdicsõítteték õ benne."));
husuperobject.biblemap.put("b43_13_32", new husuperobject(43,13,32,"Ha megdicsõítteték õ benne az Isten, az Isten is megdicsõíti õt õ magában, és ezennel megdicsõíti õt."));
husuperobject.biblemap.put("b43_13_33", new husuperobject(43,13,33,"Fiaim, egy kevés ideig még veletek vagyok. Kerestek majd engem; de a miként a zsidóknak mondám, hogy: A hová én megyek, ti nem jöhettek; most néktek is mondom."));
husuperobject.biblemap.put("b43_13_34", new husuperobject(43,13,34,"Új parancsolatot adok néktek, hogy egymást szeressétek; a mint én szerettelek titeket, úgy szeressétek ti is egymást."));
husuperobject.biblemap.put("b43_13_35", new husuperobject(43,13,35,"Errõl ismeri meg mindenki, hogy az én tanítványaim vagytok, ha egymást szeretni fogjátok."));
husuperobject.biblemap.put("b43_13_36", new husuperobject(43,13,36,"Monda néki Simon Péter: Uram, hová mégy? Felele néki Jézus: A hová én megyek, most én utánam nem jöhetsz; utóbb azonban utánam jössz."));
husuperobject.biblemap.put("b43_13_37", new husuperobject(43,13,37,"Monda néki Péter: Uram, miért nem mehetek most utánad? Az életemet adom éretted!"));
husuperobject.biblemap.put("b43_13_38", new husuperobject(43,13,38,"Felele néki Jézus: Az életedet adod érettem? Bizony, bizony mondom néked, nem szól addig a kakas, mígnem háromszor megtagadsz engem."));
husuperobject.biblemap.put("b43_14_1", new husuperobject(43,14,1,"Ne nyugtalankodjék a ti szívetek: higyjetek Istenben, és higyjetek én bennem."));
husuperobject.biblemap.put("b43_14_2", new husuperobject(43,14,2,"Az én Atyámnak házában sok lakóhely van; ha pedig nem [volna], megmondtam volna néktek. Elmegyek, hogy helyet készítsek néktek."));
husuperobject.biblemap.put("b43_14_3", new husuperobject(43,14,3,"És ha majd elmegyek és helyet készítek néktek, ismét eljövök és magamhoz veszlek titeket; hogy a hol én vagyok, ti is ott legyetek."));
husuperobject.biblemap.put("b43_14_4", new husuperobject(43,14,4,"És hogy hová megyek én, tudjátok; az útat is tudjátok."));
husuperobject.biblemap.put("b43_14_5", new husuperobject(43,14,5,"Monda néki Tamás: Uram, nem tudjuk hová mégy; mimódon tudhatjuk azért az útat?"));
husuperobject.biblemap.put("b43_14_6", new husuperobject(43,14,6,"Monda néki Jézus: Én vagyok az út, az igazság és az élet; senki sem mehet az Atyához, hanemha én általam."));
husuperobject.biblemap.put("b43_14_7", new husuperobject(43,14,7,"Ha megismertetek volna engem, megismertétek volna az én Atyámat is; és mostantól fogva ismeritek õt, és láttátok õt."));
husuperobject.biblemap.put("b43_14_8", new husuperobject(43,14,8,"Monda néki Filep: Uram, mutasd meg nékünk az Atyát, és elég nékünk!"));
husuperobject.biblemap.put("b43_14_9", new husuperobject(43,14,9,"Monda néki Jézus: Annyi idõ óta veletek vagyok, és még sem ismertél meg engem, Filep? a ki engem látott, látta az Atyát; mimódon mondod azért te: Mutasd meg nékünk az Atyát?"));
husuperobject.biblemap.put("b43_14_10", new husuperobject(43,14,10,"Nem hiszed-é, hogy én az Atyában vagyok, és az Atya én bennem van? A beszédeket, a melyeket én mondok néktek, nem magamtól mondom; hanem az Atya, a ki én bennem lakik, õ cselekszi e dolgokat."));
husuperobject.biblemap.put("b43_14_11", new husuperobject(43,14,11,"Higyjetek nékem, hogy én az Atyában vagyok, és az Atya én bennem van; ha pedig nem, magokért a cselekedetekért higyjetek nékem."));
husuperobject.biblemap.put("b43_14_12", new husuperobject(43,14,12,"Bizony, bizony mondom néktek: A ki hisz én bennem, az is cselekszi majd azokat a cselekedeteket, a melyeket én cselekeszem; és nagyobbakat is cselekszik azoknál; mert én az én Atyámhoz megyek."));
husuperobject.biblemap.put("b43_14_13", new husuperobject(43,14,13,"És akármit kértek majd az én nevemben, megcselekszem azt, hogy dicsõíttessék az Atya a Fiúban."));
husuperobject.biblemap.put("b43_14_14", new husuperobject(43,14,14,"Ha valamit kértek az én nevemben, én megcselekszem azt."));
husuperobject.biblemap.put("b43_14_15", new husuperobject(43,14,15,"Ha engem szerettek, az én parancsolataimat megtartsátok."));
husuperobject.biblemap.put("b43_14_16", new husuperobject(43,14,16,"És én kérem az Atyát, és más vígasztalót ád néktek, hogy veletek maradjon mindörökké."));
husuperobject.biblemap.put("b43_14_17", new husuperobject(43,14,17,"Az igazságnak ama Lelkét: a kit a világ be nem fogadhat, mert nem látja õt és nem ismeri õt; de ti ismeritek õt, mert nálatok lakik, és bennetek marad."));
husuperobject.biblemap.put("b43_14_18", new husuperobject(43,14,18,"Nem hagylak titeket árvákul; eljövök ti hozzátok."));
husuperobject.biblemap.put("b43_14_19", new husuperobject(43,14,19,"Még egy kevés idõ és a világ nem lát engem többé; de ti megláttok engem: mert én élek, ti is élni fogtok."));
husuperobject.biblemap.put("b43_14_20", new husuperobject(43,14,20,"Azon a napon megtudjátok majd ti, hogy én az én Atyámban vagyok, és ti én bennem, és én ti bennetek."));
husuperobject.biblemap.put("b43_14_21", new husuperobject(43,14,21,"A ki ismeri az én parancsolataimat és megtartja azokat, az szeret engem; a ki pedig engem szeret, azt szereti az én Atyám, én is szeretem azt, és kijelentem magamat annak."));
husuperobject.biblemap.put("b43_14_22", new husuperobject(43,14,22,"Monda néki Júdás (nem az Iskáriótes): Uram, mi dolog, hogy nékünk jelented ki magadat, és nem a világnak?"));
husuperobject.biblemap.put("b43_14_23", new husuperobject(43,14,23,"Felele Jézus és monda néki: Ha valaki szeret engem, megtartja az én beszédemet: és az én Atyám szereti azt, és ahhoz megyünk, és annál lakozunk."));
husuperobject.biblemap.put("b43_14_24", new husuperobject(43,14,24,"A ki nem szeret engem, nem tartja meg az én beszédeimet: és az a beszéd, a melyet hallotok, nem az enyém, hanem az Atyáé, a ki küldött engem."));
husuperobject.biblemap.put("b43_14_25", new husuperobject(43,14,25,"Ezeket beszéltem néktek, a míg veletek valék."));
husuperobject.biblemap.put("b43_14_26", new husuperobject(43,14,26,"Ama vígasztaló pedig, a Szent Lélek, a kit az én nevemben küld az Atya, az mindenre megtanít majd titeket, és eszetekbe juttatja mindazokat, a miket mondottam néktek."));
husuperobject.biblemap.put("b43_14_27", new husuperobject(43,14,27,"Békességet hagyok néktek; az én békességemet adom néktek: nem úgy adom én néktek, a mint a világ adja. Ne nyugtalankodjék a ti szívetek, se ne féljen!"));
husuperobject.biblemap.put("b43_14_28", new husuperobject(43,14,28,"Hallottátok, hogy én azt mondtam néktek: Elmegyek, és eljövök hozzátok. Ha szeretnétek engem, örvendeznétek, hogy azt mondtam: Elmegyek az Atyához; mert az én Atyám nagyobb nálamnál."));
husuperobject.biblemap.put("b43_14_29", new husuperobject(43,14,29,"És most mondtam meg néktek, mielõtt meglenne: hogy a mikor majd meglesz, higyjetek."));
husuperobject.biblemap.put("b43_14_30", new husuperobject(43,14,30,"Nem sokat beszélek már veletek, mert jön a világ fejedelme: és én bennem nincsen semmije;"));
husuperobject.biblemap.put("b43_14_31", new husuperobject(43,14,31,"De, hogy megtudja a világ, hogy szeretem az Atyát és úgy cselekszem, a mint az én Atyám parancsolta nékem: keljetek fel, menjünk el innen."));
husuperobject.biblemap.put("b43_15_1", new husuperobject(43,15,1,"Én vagyok az igazi szõlõtõ, és az én Atyám a szõlõmûves."));
husuperobject.biblemap.put("b43_15_2", new husuperobject(43,15,2,"Minden szõlõvesszõt, a mely én bennem gyümölcsöt nem terem, lemetsz; mindazt pedig, a mely gyümölcsöt terem, megtisztítja, hogy több gyümölcsöt teremjen."));
husuperobject.biblemap.put("b43_15_3", new husuperobject(43,15,3,"Ti már tiszták vagytok ama beszéd által, a melyet szóltam néktek."));
husuperobject.biblemap.put("b43_15_4", new husuperobject(43,15,4,"Maradjatok én bennem és én is ti bennetek. Miképen a szõlõvesszõ nem teremhet gyümölcsöt magától, hanemha a szõlõtõkén marad; akképen ti sem, hanemha én bennem maradtok."));
husuperobject.biblemap.put("b43_15_5", new husuperobject(43,15,5,"Én vagyok a szõlõtõ, ti a szõlõvesszõk: A ki én bennem marad, én pedig õ benne, az terem sok gyümölcsöt: mert nálam nélkül semmit sem cselekedhettek."));
husuperobject.biblemap.put("b43_15_6", new husuperobject(43,15,6,"Ha valaki nem marad én bennem, kivettetik, mint a szõlõvesszõ, és megszárad; és egybe gyûjtik ezeket és a tûzre vetik, és megégnek."));
husuperobject.biblemap.put("b43_15_7", new husuperobject(43,15,7,"Ha én bennem maradtok, és az én beszédeim bennetek maradnak, kérjetek, a mit csak akartok, és meglesz az néktek."));
husuperobject.biblemap.put("b43_15_8", new husuperobject(43,15,8,"Abban dicsõíttetik meg az én Atyám, hogy sok gyümölcsöt teremjetek; és legyetek nékem tanítványaim."));
husuperobject.biblemap.put("b43_15_9", new husuperobject(43,15,9,"A miképen az Atya szeretett engem, én is úgy szerettelek titeket: maradjatok meg ebben az én szeretetemben."));
husuperobject.biblemap.put("b43_15_10", new husuperobject(43,15,10,"Ha [a]z én parancsolataimat megtartjátok, megmaradtok az én szeretetemben; a miképen én megtartottam az én Atyámnak parancsolatait, és megmaradok az õ szeretetében."));
husuperobject.biblemap.put("b43_15_11", new husuperobject(43,15,11,"Ezeket beszéltem néktek, hogy megmaradjon ti bennetek az én örömem és a ti örömetek beteljék."));
husuperobject.biblemap.put("b43_15_12", new husuperobject(43,15,12,"Ez az én parancsolatom, hogy szeressétek egymást, a miképen én szerettelek titeket."));
husuperobject.biblemap.put("b43_15_13", new husuperobject(43,15,13,"Nincsen senkiben nagyobb szeretet annál, mintha valaki életét adja az õ barátaiért."));
husuperobject.biblemap.put("b43_15_14", new husuperobject(43,15,14,"Ti az én barátaim vagytok, ha azokat cselekszitek, a miket én parancsolok néktek."));
husuperobject.biblemap.put("b43_15_15", new husuperobject(43,15,15,"Nem mondalak többé titeket szolgáknak; mert a szolga nem tudja, mit cselekszik az õ ura; titeket pedig barátaimnak mondottalak; mert mindazt, a mit az én Atyámtól hallottam, tudtul adtam néktek."));
husuperobject.biblemap.put("b43_15_16", new husuperobject(43,15,16,"Nem ti választottatok engem, hanem én választottalak titeket, és én rendeltelek titeket, hogy ti elmenjetek és gyümölcsöt teremjetek, és a ti gyümölcsötök megmaradjon; hogy akármit kértek az Atyától az én nevemben, megadja néktek."));
husuperobject.biblemap.put("b43_15_17", new husuperobject(43,15,17,"Ezeket parancsolom néktek, hogy egymást szeressétek."));
husuperobject.biblemap.put("b43_15_18", new husuperobject(43,15,18,"Ha gyûlöl titeket a világ, tudjátok meg, hogy engem elébb gyûlölt ti nálatoknál."));
husuperobject.biblemap.put("b43_15_19", new husuperobject(43,15,19,"Ha e világból volnátok, a világ szeretné azt, a mi az övé; de mivelhogy nem vagytok e világból, hanem én választottalak ki magamnak titeket e világból, azért gyûlöl titeket a világ."));
husuperobject.biblemap.put("b43_15_20", new husuperobject(43,15,20,"Emlékezzetek meg ama beszédekrõl, a melyeket én mondtam néktek: Nem nagyobb a szolga az õ uránál. Ha engem üldöztek, titeket is üldöznek majd; ha az én beszédemet megtartották, a tiéteket is megtartják majd."));
husuperobject.biblemap.put("b43_15_21", new husuperobject(43,15,21,"De mindezt az én nevemért cselekszik veletek, mivelhogy nem ismerik azt, a ki küldött engem."));
husuperobject.biblemap.put("b43_15_22", new husuperobject(43,15,22,"Ha nem jöttem volna és nem beszéltem volna nékik, nem volna bûnük: de most nincs mivel menteniök az õ bûnöket."));
husuperobject.biblemap.put("b43_15_23", new husuperobject(43,15,23,"A ki engem gyûlöl, gyûlöli az én Atyámat is."));
husuperobject.biblemap.put("b43_15_24", new husuperobject(43,15,24,"Ha ama cselekedeteket nem cselekedtem volna közöttük, a melyeket senki más nem cselekedett, nem volna bûnük; de most láttak is, gyûlöltek is, mind engem, mind az én Atyámat."));
husuperobject.biblemap.put("b43_15_25", new husuperobject(43,15,25,"De [azért lõn így,] hogy beteljesedjék a mondás, a mely megiratott az õ törvényökben: Ok nélkül gyûlöltek engem."));
husuperobject.biblemap.put("b43_15_26", new husuperobject(43,15,26,"Mikor pedig eljõ majd a Vígasztaló, a kit én küldök néktek az Atyától, az igazságnak Lelke, a ki az Atyától származik, az tesz majd én rólam bizonyságot."));
husuperobject.biblemap.put("b43_15_27", new husuperobject(43,15,27,"De ti is bizonyságot tesztek; mert kezdettõl fogva én velem vagytok."));
husuperobject.biblemap.put("b43_16_1", new husuperobject(43,16,1,"Ezeket beszéltem néktek, hogy meg ne botránkozzatok."));
husuperobject.biblemap.put("b43_16_2", new husuperobject(43,16,2,"A gyülekezetekbõl kirekesztenek titeket; sõt jön idõ, hogy a ki öldököl titeket, mind azt hiszi, hogy isteni tiszteletet cselekszik."));
husuperobject.biblemap.put("b43_16_3", new husuperobject(43,16,3,"És ezeket azért cselekszik veletek, mert nem ismerték meg az Atyát, sem engem."));
husuperobject.biblemap.put("b43_16_4", new husuperobject(43,16,4,"Ezeket pedig azért beszéltem néktek, hogy a mikor eljõ az az idõ, megemlékezzetek róluk, hogy én mondtam néktek. De ezeket kezdettõl fogva nem mondottam néktek, mivelhogy veletek valék."));
husuperobject.biblemap.put("b43_16_5", new husuperobject(43,16,5,"Most pedig elmegyek ahhoz, a ki küldött engem; és senki sem kérdezi tõlem közületek: Hová mégy?"));
husuperobject.biblemap.put("b43_16_6", new husuperobject(43,16,6,"Hanem, mivelhogy ezeket beszéltem néktek, a szomorúság eltöltötte a szíveteket."));
husuperobject.biblemap.put("b43_16_7", new husuperobject(43,16,7,"De én az igazat mondom néktek: Jobb néktek, hogy én elmenjek: mert ha el nem megyek, nem jõ el hozzátok a Vígasztaló: ha pedig elmegyek, elküldöm azt ti hozzátok."));
husuperobject.biblemap.put("b43_16_8", new husuperobject(43,16,8,"És az, mikor eljõ, megfeddi a világot bûn, igazság és ítélet tekintetében:"));
husuperobject.biblemap.put("b43_16_9", new husuperobject(43,16,9,"Bûn tekintetében, hogy nem hisznek én bennem;"));
husuperobject.biblemap.put("b43_16_10", new husuperobject(43,16,10,"És igazság tekintetében, hogy én az én Atyámhoz megyek, és többé nem láttok engem;"));
husuperobject.biblemap.put("b43_16_11", new husuperobject(43,16,11,"Ítélet tekintetében pedig, hogy e világnak fejedelme megítéltetett."));
husuperobject.biblemap.put("b43_16_12", new husuperobject(43,16,12,"Még sok mondani valóm van hozzátok, de most el nem hordozhatjátok."));
husuperobject.biblemap.put("b43_16_13", new husuperobject(43,16,13,"De mikor eljõ amaz, az igazságnak Lelke, elvezérel majd titeket minden igazságra. Mert nem õ magától szól, hanem azokat szólja, a miket hall, és a bekövetkezendõket megjelenti néktek."));
husuperobject.biblemap.put("b43_16_14", new husuperobject(43,16,14,"Az engem dicsõít majd, mert az enyémbõl vesz, és megjelenti néktek."));
husuperobject.biblemap.put("b43_16_15", new husuperobject(43,16,15,"Mindaz, a mi az Atyáé, az enyém: azért mondám, hogy az enyémbõl vesz, és megjelenti néktek."));
husuperobject.biblemap.put("b43_16_16", new husuperobject(43,16,16,"Egy kevés [idõ,] és nem láttok engem; és ismét egy kevés [idõ,] és megláttok majd engem: mert én az Atyához megyek."));
husuperobject.biblemap.put("b43_16_17", new husuperobject(43,16,17,"Mondának azért az õ tanítványai közül egymásnak: Mi az, a mit nékünk mond: Egy kevés [idõ,] és nem láttok engem; és ismét egy kevés [idõ,] és megláttok majd engem; és: mert én az Atyához megyek?"));
husuperobject.biblemap.put("b43_16_18", new husuperobject(43,16,18,"Mondának azért: Mi az a kevés [idõ,] a mirõl szól? Nem tudjuk, mit mond."));
husuperobject.biblemap.put("b43_16_19", new husuperobject(43,16,19,"Megérté azért Jézus, hogy õt akarnák megkérdezni, és monda nékik: Arról tudakozzátok-é egymást, hogy azt mondám: Egy kevés [idõ,] és nem láttok engem; és ismét egy kevés [idõ,] és megláttok majd engem?"));
husuperobject.biblemap.put("b43_16_20", new husuperobject(43,16,20,"Bizony, bizony mondom néktek, hogy sírtok és jajgattok ti, a világ pedig örül: ti szomorkodtok, hanem a ti szomorúságtok örömre fordul."));
husuperobject.biblemap.put("b43_16_21", new husuperobject(43,16,21,"Az asszony mikor szûl, szomorúságban van, mert eljött az õ órája: de mikor megszûli az õ gyermekét, nem emlékezik többé a kínra az öröm miatt, hogy ember született e világra."));
husuperobject.biblemap.put("b43_16_22", new husuperobject(43,16,22,"Ti is azért most ugyan szomorúságban vagytok, de ismét meglátlak majd titeket, és örülni fog a ti szívetek, és senki el nem veszi tõletek a ti örömeteket."));
husuperobject.biblemap.put("b43_16_23", new husuperobject(43,16,23,"És azon a napon nem kérdeztek majd engem semmirõl. Bizony, bizony mondom néktek, hogy a mit csak kérni fogtok az Atyától az én nevemben, megadja néktek."));
husuperobject.biblemap.put("b43_16_24", new husuperobject(43,16,24,"Mostanáig semmit sem kértetek az Atyától az én nevemben: kérjetek és megkapjátok, hogy a ti örömetek teljes legyen."));
husuperobject.biblemap.put("b43_16_25", new husuperobject(43,16,25,"Ezeket példázatokban mondottam néktek; de eljõ az idõ, mikor nem példázatokban beszélek majd néktek, hanem nyiltan beszélek néktek az Atyáról."));
husuperobject.biblemap.put("b43_16_26", new husuperobject(43,16,26,"Azon a napon az én nevemben kértek majd: és nem mondom néktek, hogy én kérni fogom az Atyát ti érettetek;"));
husuperobject.biblemap.put("b43_16_27", new husuperobject(43,16,27,"Mert maga az Atya szeret titeket, mivelhogy ti szerettetek engem, és elhittétek, hogy én az Istentõl jöttem ki."));
husuperobject.biblemap.put("b43_16_28", new husuperobject(43,16,28,"Kijöttem az Atyától, és jöttem e világba: ismét elhagyom e világot, és elmegyek az Atyához."));
husuperobject.biblemap.put("b43_16_29", new husuperobject(43,16,29,"Mondának néki az õ tanítványai: Ímé, most nyiltan beszélsz és semmi példázatot nem mondasz."));
husuperobject.biblemap.put("b43_16_30", new husuperobject(43,16,30,"Most tudjuk, hogy te mindent tudsz, és nincs szükséged arra, hogy valaki téged megkérdezzen: errõl hiszszük, hogy az Istentõl jöttél ki."));
husuperobject.biblemap.put("b43_16_31", new husuperobject(43,16,31,"Felele nékik Jézus: Most hiszitek?"));
husuperobject.biblemap.put("b43_16_32", new husuperobject(43,16,32,"Ímé eljõ az óra, és immár eljött, hogy szétoszoljatok kiki az övéihez, és engem egyedül hagyjatok; de nem vagyok egyedül, mert az Atya velem van."));
husuperobject.biblemap.put("b43_16_33", new husuperobject(43,16,33,"Azért beszéltem ezeket néktek, hogy békességetek legyen én bennem. E világon nyomorúságtok lészen; de bízzatok: és meggyõztem a világot."));
husuperobject.biblemap.put("b43_17_1", new husuperobject(43,17,1,"Ezeket beszélte Jézus; és felemelé szemeit az égre, és monda: Atyám, eljött az óra; dicsõítsd meg a te Fiadat, hogy a te Fiad is dicsõítsen téged;"));
husuperobject.biblemap.put("b43_17_2", new husuperobject(43,17,2,"A miként te hatalmat adtál néki minden testen, hogy örök életet adjon mindennek, a mit néki adtál."));
husuperobject.biblemap.put("b43_17_3", new husuperobject(43,17,3,"Az pedig az örök élet, hogy megismerjenek téged, az egyedül igaz Istent, és a kit elküldtél, a Jézus Krisztust."));
husuperobject.biblemap.put("b43_17_4", new husuperobject(43,17,4,"Én dicsõítettelek téged e földön: elvégeztem a munkát, a melyet reám bíztál, hogy végezzem azt."));
husuperobject.biblemap.put("b43_17_5", new husuperobject(43,17,5,"És most te dicsõíts meg engem, Atyám, te magadnál azzal a dicsõséggel, a melylyel bírtam te nálad a világ létele elõtt."));
husuperobject.biblemap.put("b43_17_6", new husuperobject(43,17,6,"Megjelentettem a te nevedet az embereknek, a kiket e világból nékem adtál: tiéid valának, és nékem adtad azokat, és a te beszédedet megtartották."));
husuperobject.biblemap.put("b43_17_7", new husuperobject(43,17,7,"Most tudták meg, hogy mindaz te tõled van, a mit nékem adtál:"));
husuperobject.biblemap.put("b43_17_8", new husuperobject(43,17,8,"Mert ama beszédeket, a melyeket nékem adtál, õ nékik adtam; és õk befogadták, és igazán megismerték, hogy én tõled jöttem ki, és elhitték, hogy te küldtél engem."));
husuperobject.biblemap.put("b43_17_9", new husuperobject(43,17,9,"Én ezekért könyörgök: nem a világért könyörgök, hanem azokért, a kiket nékem adtál, mert a tiéid."));
husuperobject.biblemap.put("b43_17_10", new husuperobject(43,17,10,"És az enyémek mind a tiéid, és a tiéid az enyémek: és megdicsõíttetem õ bennök."));
husuperobject.biblemap.put("b43_17_11", new husuperobject(43,17,11,"És nem vagyok többé e világon, de õk a világon vannak, én pedig te hozzád megyek. Szent Atyám, tartsd meg õket a te nevedben, a kiket nékem adtál, hogy egyek legyenek, mint mi!"));
husuperobject.biblemap.put("b43_17_12", new husuperobject(43,17,12,"Mikor velök valék a világon, én megtartám õket a te nevedben; a kiket nékem adtál, megõrizém, és senki el nem veszett közülök, csak a veszedelemnek fia, hogy az írás beteljesüljön."));
husuperobject.biblemap.put("b43_17_13", new husuperobject(43,17,13,"Most pedig te hozzád megyek; és ezeket beszélem a világon, hogy õk az én örömemet teljesen bírják õ magokban."));
husuperobject.biblemap.put("b43_17_14", new husuperobject(43,17,14,"Én a te ígédet nékik adtam; és a világ gyûlölte õket, mivelhogy nem e világból valók, a mint hogy én sem e világból vagyok."));
husuperobject.biblemap.put("b43_17_15", new husuperobject(43,17,15,"Nem azt kérem, hogy vedd ki õket e világból, hanem hogy õrizd meg õket a gonosztól."));
husuperobject.biblemap.put("b43_17_16", new husuperobject(43,17,16,"Nem e világból valók, a mint hogy én sem e világból vagyok."));
husuperobject.biblemap.put("b43_17_17", new husuperobject(43,17,17,"Szenteld meg õket a te igazságoddal: A te ígéd igazság."));
husuperobject.biblemap.put("b43_17_18", new husuperobject(43,17,18,"A miképen te küldtél engem e világra, úgy küldtem én is õket e világra;"));
husuperobject.biblemap.put("b43_17_19", new husuperobject(43,17,19,"És én õ érettök [oda] szentelem magamat, hogy õk is megszenteltekké legyenek [az] igazságban."));
husuperobject.biblemap.put("b43_17_20", new husuperobject(43,17,20,"De nemcsak õ érettök könyörgök, hanem azokért is, a kik az õ beszédökre hisznek majd én bennem;"));
husuperobject.biblemap.put("b43_17_21", new husuperobject(43,17,21,"Hogy mindnyájan egyek legyenek; a mint te én bennem, Atyám, és én te benned, hogy õk is egyek legyenek mi bennünk: hogy elhigyje a világ, hogy te küldtél engem."));
husuperobject.biblemap.put("b43_17_22", new husuperobject(43,17,22,"És én azt a dicsõséget, a melyet nékem adtál, õ nékik adtam, hogy egyek legyenek, a miképen mi egy vagyunk:"));
husuperobject.biblemap.put("b43_17_23", new husuperobject(43,17,23,"Én õ bennök, és te én bennem: hogy tökéletesen egygyé legyenek, és hogy megismerje a világ, hogy te küldtél engem, és szeretted õket, a miként engem szerettél."));
husuperobject.biblemap.put("b43_17_24", new husuperobject(43,17,24,"Atyám, a kiket nékem adtál, akarom, hogy a hol én vagyok, azok is én velem legyenek; hogy megláthassák az én dicsõségemet, a melyet nékem adtál: mert szerettél engem e világ alapjának felvettetése elõtt."));
husuperobject.biblemap.put("b43_17_25", new husuperobject(43,17,25,"Igazságos Atyám! És e világ nem ismert téged, de én ismertelek téged; és ezek megismerik, hogy te küldtél engem;"));
husuperobject.biblemap.put("b43_17_26", new husuperobject(43,17,26,"És megismertettem õ velök a te nevedet, és megismertetem; hogy az a szeretet legyen õ bennök, a mellyel engem szerettél, és én [is] õ bennök [legyek.]"));
husuperobject.biblemap.put("b43_18_1", new husuperobject(43,18,1,"Mikor ezeket mondta vala Jézus, kiméne az õ tanítványaival együtt túl a Kedron patakán, a hol egy kert vala, a melybe bemenének õ és az õ tanítványai."));
husuperobject.biblemap.put("b43_18_2", new husuperobject(43,18,2,"Ismeré pedig azt a helyet Júdás is, a ki õt elárulja vala; mivelhogy gyakorta ott gyûlt egybe Jézus az õ tanítványaival."));
husuperobject.biblemap.put("b43_18_3", new husuperobject(43,18,3,"Júdás azért magához vevén a [katonai] csapatot, és a papi fejedelmektõl és a farizeusoktól szolgákat, oda méne fáklyákkal, lámpásokkal és fegyverekkel."));
husuperobject.biblemap.put("b43_18_4", new husuperobject(43,18,4,"Jézus azért tudván mindazt, a mi reá következendõ vala, elõre méne, és monda azoknak: Kit kerestek?"));
husuperobject.biblemap.put("b43_18_5", new husuperobject(43,18,5,"Felelének néki: A názáreti Jézust. Monda nékik Jézus: Én vagyok. [Ott] állt pedig õ velök Júdás is, a ki elárulta õt."));
husuperobject.biblemap.put("b43_18_6", new husuperobject(43,18,6,"Mikor azért azt mondá nékik, hogy: Én vagyok; hátra vonulának és földre esének."));
husuperobject.biblemap.put("b43_18_7", new husuperobject(43,18,7,"Ismét megkérdezé azért õket: Kit kerestek? És azok mondának: A názáreti Jézust."));
husuperobject.biblemap.put("b43_18_8", new husuperobject(43,18,8,"Felele Jézus: Mondtam néktek, hogy én vagyok [az.] Azért, ha engem kerestek, ezeket bocsássátok el;"));
husuperobject.biblemap.put("b43_18_9", new husuperobject(43,18,9,"Hogy beteljesüljön a beszéd, a melyet mondott: Azok közül, a kiket nékem adtál, senkit sem vesztettem el."));
husuperobject.biblemap.put("b43_18_10", new husuperobject(43,18,10,"Simon Péter pedig, a kinek szablyája vala, kirántá azt, és megüté a fõpap szolgáját, és levágá annak jobb fülét. A szolga neve pedig Málkus vala."));
husuperobject.biblemap.put("b43_18_11", new husuperobject(43,18,11,"Monda azért Jézus Péternek: Tedd hüvelyébe a te szablyádat; avagy nem kell-é kiinnom a pohárt, a melyet az Atya adott nékem?"));
husuperobject.biblemap.put("b43_18_12", new husuperobject(43,18,12,"A csapat azért és az ezredes és a zsidók szolgái megfogák Jézust, és megkötözék õt,"));
husuperobject.biblemap.put("b43_18_13", new husuperobject(43,18,13,"És vivék õt elõször Annáshoz; mert ipa vala [ez] Kajafásnak, a ki abban az esztendõben fõpap vala."));
husuperobject.biblemap.put("b43_18_14", new husuperobject(43,18,14,"Kajafás pedig az vala, a ki tanácsolta vala a zsidóknak, hogy jobb, hogy egy ember veszszen el a népért."));
husuperobject.biblemap.put("b43_18_15", new husuperobject(43,18,15,"Simon Péter pedig, és egy másik tanítvány követi vala Jézust. Ez a tanítvány pedig ismerõs vala a fõpappal, és beméne Jézussal együtt a fõpap udvarába,"));
husuperobject.biblemap.put("b43_18_16", new husuperobject(43,18,16,"Péter pedig kívül áll vala az ajtónál. Kiméne azért ama másik tanítvány, a ki a fõpappal ismerõs vala, és szóla az ajtóõrzõnek, és bevivé Pétert."));
husuperobject.biblemap.put("b43_18_17", new husuperobject(43,18,17,"Szóla azért Péterhez az ajtóõrzõ leány: Nemde, te is ez ember tanítványai közül való vagy? Monda õ: Nem vagyok."));
husuperobject.biblemap.put("b43_18_18", new husuperobject(43,18,18,"A szolgák pedig és a poroszlók [ott] állnak vala, szítván a tüzet, mivelhogy hûvös vala, és melegszenek vala. [Ott] áll vala pedig Péter is õ velök együtt, és melegszik vala."));
husuperobject.biblemap.put("b43_18_19", new husuperobject(43,18,19,"A fõpap azért kérdezé Jézust az õ tanítványai felõl, és az õ tudománya felõl."));
husuperobject.biblemap.put("b43_18_20", new husuperobject(43,18,20,"Felele néki Jézus: Én nyilván szólottam a világnak, én mindenkor tanítottam a zsinagógában és a templomban, a hol a zsidók mindenünnen összegyülekeznek; és titkon semmit sem szólottam."));
husuperobject.biblemap.put("b43_18_21", new husuperobject(43,18,21,"Mit kérdesz engem? Kérdezd azokat, a kik hallották, mit szóltam nékik: ímé õk tudják, a miket nékik szólottam."));
husuperobject.biblemap.put("b43_18_22", new husuperobject(43,18,22,"Mikor pedig õ ezeket mondja vala, egy a poroszlók közül, a ki ott áll vala, arczul üté Jézust, mondván: így felelsz-é a fõpapnak?"));
husuperobject.biblemap.put("b43_18_23", new husuperobject(43,18,23,"Felele néki Jézus: Ha gonoszul szóltam, tégy bizonyságot a gonoszságról; ha pedig jól, miért versz engem."));
husuperobject.biblemap.put("b43_18_24", new husuperobject(43,18,24,"Elküldé õt Annás megkötözve Kajafáshoz, a fõpaphoz."));
husuperobject.biblemap.put("b43_18_25", new husuperobject(43,18,25,"Simon Péter pedig [ott] áll vala és melegszik vala. Mondának azért néki: Nemde, te is ennek a tanítványai közül való vagy? Megtagadá õ, és monda: Nem vagyok."));
husuperobject.biblemap.put("b43_18_26", new husuperobject(43,18,26,"Monda egy a fõpap szolgái közül, rokona annak, a kinek a fülét Péter levágta: Nem láttalak-e én téged õ vele együtt a kertben?"));
husuperobject.biblemap.put("b43_18_27", new husuperobject(43,18,27,"Ismét megtagadá azért Péter; és a kakas azonnal megszólala."));
husuperobject.biblemap.put("b43_18_28", new husuperobject(43,18,28,"Vivék azért Jézust Kajafástól a törvényházba. Vala pedig reggel. És õk nem menének be a törvényházba, hogy meg ne fertõztessenek, hanem hogy megehessék a husvétibárányt."));
husuperobject.biblemap.put("b43_18_29", new husuperobject(43,18,29,"Kiméne azért Pilátus õ hozzájok, és monda: Micsoda vádat hoztok fel ez ember ellen?"));
husuperobject.biblemap.put("b43_18_30", new husuperobject(43,18,30,"Felelének és mondának néki: Ha gonosztevõ nem volna ez, nem adtuk volna õt a te kezedbe."));
husuperobject.biblemap.put("b43_18_31", new husuperobject(43,18,31,"Monda azért nékik Pilátus: Vigyétek el õt ti, és ítéljétek meg õt a ti törvényeitek szerint. Mondának azért néki a zsidók: Nékünk senkit sem szabad megölnünk;"));
husuperobject.biblemap.put("b43_18_32", new husuperobject(43,18,32,"Hogy beteljesedjék a Jézus szava, a melyet monda, a mikor jelenti vala, hogy milyen halállal kell majd meghalnia."));
husuperobject.biblemap.put("b43_18_33", new husuperobject(43,18,33,"Ismét beméne azért Pilátus a törvényházba, és szólítja vala Jézust, és monda néki: Te vagy a Zsidók királya?"));
husuperobject.biblemap.put("b43_18_34", new husuperobject(43,18,34,"Felele néki Jézus: Magadtól mondod-é te ezt, vagy mások beszélték néked én felõlem?"));
husuperobject.biblemap.put("b43_18_35", new husuperobject(43,18,35,"Felele Pilátus: Avagy zsidó vagyok-e én? A te néped és a papifejedelmek adtak téged az én kezembe: mit cselekedtél?"));
husuperobject.biblemap.put("b43_18_36", new husuperobject(43,18,36,"Felele Jézus: Az én országom nem e világból való. Ha e világból való volna az én országom, az én szolgáim vitézkednének, hogy át ne adassam a zsidóknak. Ámde az én országom nem innen való."));
husuperobject.biblemap.put("b43_18_37", new husuperobject(43,18,37,"Monda azért néki Pilátus: Király vagy-é hát te csakugyan? Felele Jézus: Te mondod, hogy én király vagyok. Én azért születtem, és azért jöttem e világra, hogy bizonyságot tegyek az igazságról. Mindaz, a ki az igazságból való, hallgat az én szómra."));
husuperobject.biblemap.put("b43_18_38", new husuperobject(43,18,38,"Monda néki Pilátus: Micsoda az igazság? És a mint ezt mondá, újra kiméne a zsidókhoz, és monda nékik: Én nem találok benne semmi bûnt."));
husuperobject.biblemap.put("b43_18_39", new husuperobject(43,18,39,"Szokás pedig az nálatok, hogy elbocsássak néktek egyet a husvétünnepen: akarjátok-é azért, hogy elbocsássam néktek a zsidók királyát?"));
husuperobject.biblemap.put("b43_18_40", new husuperobject(43,18,40,"Kiáltának azért viszont mindnyájan, mondván: Nem ezt, hanem Barabbást. Ez a Barabbás pedig tolvaj vala."));
husuperobject.biblemap.put("b43_19_1", new husuperobject(43,19,1,"Akkor azért elõfogá Pilátus Jézust, és megostoroztatá."));
husuperobject.biblemap.put("b43_19_2", new husuperobject(43,19,2,"És a vitézek tövisbõl koronát fonván, a fejére tevék, és bíbor köntöst adának reá,"));
husuperobject.biblemap.put("b43_19_3", new husuperobject(43,19,3,"És mondának: Üdvöz légy zsidók királya! És arczul csapdossák vala õt."));
husuperobject.biblemap.put("b43_19_4", new husuperobject(43,19,4,"Majd ismét kiméne Pilátus, és monda nékik: Ímé kihozom õt néktek, hogy értsétek meg, hogy nem találok benne semmi bûnt."));
husuperobject.biblemap.put("b43_19_5", new husuperobject(43,19,5,"Kiméne azért Jézus a töviskoronát és a bíbor köntöst viselve. És monda nékik [Pilátus:] Ímhol az ember!"));
husuperobject.biblemap.put("b43_19_6", new husuperobject(43,19,6,"Mikor azért látják vala õt a papifejedelmek és a szolgák, kiáltozának, mondván: Feszítsd meg, feszítsd meg! Monda nékik Pilátus: Vigyétek el õt ti és feszítsétek meg, mert én nem találok bûnt õ benne."));
husuperobject.biblemap.put("b43_19_7", new husuperobject(43,19,7,"Felelének néki a zsidók: Nékünk törvényünk van, és a mi törvényünk szerint meg kell halnia, mivelhogy Isten Fiává tette magát."));
husuperobject.biblemap.put("b43_19_8", new husuperobject(43,19,8,"Mikor pedig ezt a beszédet hallotta Pilátus, még inkább megrémül vala;"));
husuperobject.biblemap.put("b43_19_9", new husuperobject(43,19,9,"És ismét beméne a törvényházba, és szóla Jézusnak: Honnét való vagy te? De Jézus nem felelt néki."));
husuperobject.biblemap.put("b43_19_10", new husuperobject(43,19,10,"Monda azért néki Pilátus: Nékem nem szólsz-é? Nem tudod-é hogy hatalmam van arra, hogy megfeszítselek, és hatalmam van arra, hogy szabadon bocsássalak?"));
husuperobject.biblemap.put("b43_19_11", new husuperobject(43,19,11,"Felele Jézus: Semmi hatalmad sem volna rajtam, ha felülrõl nem adatott volna néked: nagyobb bûne van azért annak, a ki a te kezedbe adott engem."));
husuperobject.biblemap.put("b43_19_12", new husuperobject(43,19,12,"Ettõl fogva igyekszik vala Pilátus õt szabadon bocsátani; de a zsidók kiáltozának, mondván: Ha ezt szabadon bocsátod, nem vagy a császár barátja; valaki magát királylyá teszi, ellene mond a császárnak!"));
husuperobject.biblemap.put("b43_19_13", new husuperobject(43,19,13,"Pilátus azért, a mikor hallja vala e beszédet, kihozá Jézust, és ûle a törvénytevõ székbe azon a helyen, a melyet Kõpadolatnak hívtak, zsidóul pedig Gabbathának."));
husuperobject.biblemap.put("b43_19_14", new husuperobject(43,19,14,"Vala pedig a husvét péntekje; és mintegy hat óra. És monda a zsidóknak: Ímhol a ti királyotok!"));
husuperobject.biblemap.put("b43_19_15", new husuperobject(43,19,15,"Azok pedig kiáltoznak vala: Vidd el, vidd el, feszítsd meg õt! Monda nékik Pilátus: A ti királyotokat feszítsem meg? Felelének a papifejedelmek: Nem királyunk van, hanem császárunk!"));
husuperobject.biblemap.put("b43_19_16", new husuperobject(43,19,16,"Akkor azért nékik adá õt, hogy megfeszíttessék. Átvevék azért Jézust és elvivék."));
husuperobject.biblemap.put("b43_19_17", new husuperobject(43,19,17,"És emelvén az õ keresztfáját, méne az úgynevezett Koponya helyére, a melyet héberül Golgothának hívnak:"));
husuperobject.biblemap.put("b43_19_18", new husuperobject(43,19,18,"A hol megfeszíték õt, és õ vele más kettõt, egyfelõl, és másfelõl, középen pedig Jézust."));
husuperobject.biblemap.put("b43_19_19", new husuperobject(43,19,19,"Pilátus pedig czímet is íra, és feltevé a keresztfára. Ez vala pedig az írás: A NÁZÁRETI JÉZUS, A ZSIDÓK KIRÁLYA."));
husuperobject.biblemap.put("b43_19_20", new husuperobject(43,19,20,"Sokan olvasák azért e czímet a zsidók közül; mivelhogy közel vala a városhoz az a hely, a hol Jézus megfeszíttetett vala: és héberül, görögül és latinul vala [az] írva."));
husuperobject.biblemap.put("b43_19_21", new husuperobject(43,19,21,"Mondának azért Pilátusnak a zsidók papifejedelmei: Ne írd: A zsidók királya; hanem hogy õ mondotta: A zsidók királya vagyok."));
husuperobject.biblemap.put("b43_19_22", new husuperobject(43,19,22,"Felele Pilátus: A mit megírtam, megírtam."));
husuperobject.biblemap.put("b43_19_23", new husuperobject(43,19,23,"A vitézek azért, mikor megfeszítették Jézust, vevék az õ ruháit, és négy részre oszták, egy részt mindenik vitéznek, és a köntösét. A köntös pedig varrástalan vala, felülrõl mindvégig szövött."));
husuperobject.biblemap.put("b43_19_24", new husuperobject(43,19,24,"Mondának azért egymásnak: Ezt ne hasogassuk el, hanem vessünk sorsot reá, kié legyen. Hogy beteljesedjék az írás, a mely ezt mondja: Megosztoztak ruháimon, és a köntösömre sorsot vetettek. A vitézek tehát ezeket mûvelék."));
husuperobject.biblemap.put("b43_19_25", new husuperobject(43,19,25,"A Jézus keresztje alatt pedig ott állottak vala az õ anyja, és az õ anyjának nõtestvére; Mária, a Kleopás [felesége,] és Mária Magdaléna."));
husuperobject.biblemap.put("b43_19_26", new husuperobject(43,19,26,"Jézus azért, mikor látja vala, hogy [ott] áll az õ anyja és az a tanítvány, a kit szeret vala, monda az õ anyjának: Asszony, ímhol a te fiad!"));
husuperobject.biblemap.put("b43_19_27", new husuperobject(43,19,27,"Azután monda a tanítványnak: Ímhol a te anyád! És ettõl az órától magához fogadá azt az a tanítvány."));
husuperobject.biblemap.put("b43_19_28", new husuperobject(43,19,28,"Ezután tudván Jézus, hogy immár minden elvégeztetett, hogy beteljesedjék az írás, monda: Szomjúhozom."));
husuperobject.biblemap.put("b43_19_29", new husuperobject(43,19,29,"Vala pedig ott egy eczettel teli edény. Azok azért szivacsot töltvén meg eczettel, és izsópra tévén [azt,] oda vivék az õ szájához."));
husuperobject.biblemap.put("b43_19_30", new husuperobject(43,19,30,"Mikor azért elvette Jézus az eczetet, monda: Elvégeztetett! És lehajtván fejét, kibocsátá lelkét."));
husuperobject.biblemap.put("b43_19_31", new husuperobject(43,19,31,"A zsidók pedig, hogy a testek szombaton át a keresztfán ne maradjanak, miután péntek vala, (mert annak a szombatnak napja nagy [nap] vala) kérék Pilátust, hogy törjék meg azoknak lábszárait és vegyék le [õket.]"));
husuperobject.biblemap.put("b43_19_32", new husuperobject(43,19,32,"Eljövének azért a vitézek, és megtörék az elsõnek lábszárait és a másikét [is,] a ki õ vele együtt feszíttetett meg;"));
husuperobject.biblemap.put("b43_19_33", new husuperobject(43,19,33,"Mikor pedig Jézushoz érének és látják vala, hogy õ már halott, nem törék meg az õ lábszárait;"));
husuperobject.biblemap.put("b43_19_34", new husuperobject(43,19,34,"Hanem egy a vitézek közül dárdával döfé meg az õ oldalát, és azonnal vér és víz jöve ki [abból.]"));
husuperobject.biblemap.put("b43_19_35", new husuperobject(43,19,35,"És a ki látta, bizonyságot tett, és igaz az õ tanúbizonysága; és az tudja, hogy õ igazat mond, hogy ti is higyjetek."));
husuperobject.biblemap.put("b43_19_36", new husuperobject(43,19,36,"Mert azért lettek ezek, hogy beteljesedjék az írás: Az õ csontja meg ne törettessék."));
husuperobject.biblemap.put("b43_19_37", new husuperobject(43,19,37,"Másutt ismét így szól az írás: Néznek majd arra, a kit általszegeztek."));
husuperobject.biblemap.put("b43_19_38", new husuperobject(43,19,38,"Ezek után pedig kéré Pilátust az arimathiai József (a ki a Jézus tanítványa vala, de csak titokban, a zsidóktól való félelem miatt), hogy levehesse a Jézus testét. És megengedé Pilátus. Elméne azért és levevé a Jézus testét."));
husuperobject.biblemap.put("b43_19_39", new husuperobject(43,19,39,"Eljöve pedig Nikodémus is (a ki éjszaka ment vala elõször Jézushoz), hozván mirhából és áloéból való kenetet, mintegy száz fontot."));
husuperobject.biblemap.put("b43_19_40", new husuperobject(43,19,40,"Vevék azért a Jézus testét, és begöngyölgeték azt lepedõkbe illatos szerekkel együtt, a mint a zsidóknál szokás temetni."));
husuperobject.biblemap.put("b43_19_41", new husuperobject(43,19,41,"Azon a helyen pedig, a hol megfeszítteték, vala egy kert, és a kertben egy új sír, a melybe még senki sem helyheztetett vala."));
husuperobject.biblemap.put("b43_19_42", new husuperobject(43,19,42,"A zsidók péntekje miatt azért, mivelhogy az a sír közel vala, abba helyhezteték Jézust."));
husuperobject.biblemap.put("b43_20_1", new husuperobject(43,20,1,"A hétnek elsõ napján pedig jó reggel, a mikor még sötétes vala, oda méne Mária Magdaléna a sírhoz, és látá, hogy elvétetett a kõ a sírról."));
husuperobject.biblemap.put("b43_20_2", new husuperobject(43,20,2,"Futa azért és méne Simon Péterhez és ama másik tanítványhoz, a kit Jézus szeret vala, és monda nékik: Elvitték az Urat a sírból, és nem tudjuk, hová tették õt."));
husuperobject.biblemap.put("b43_20_3", new husuperobject(43,20,3,"Kiméne azért Péter és a másik tanítvány, és menének a sírhoz."));
husuperobject.biblemap.put("b43_20_4", new husuperobject(43,20,4,"Együtt futnak vala pedig mindketten: de ama másik tanítvány hamar megelõzé Pétert, és elõbb juta a sírhoz;"));
husuperobject.biblemap.put("b43_20_5", new husuperobject(43,20,5,"És lehajolván, látá, hogy ott vannak a lepedõk; mindazáltal nem megy vala be."));
husuperobject.biblemap.put("b43_20_6", new husuperobject(43,20,6,"Megjöve azután Simon Péter [is] nyomban utána, és beméne a sírba: és látá, hogy a lepedõk ott vannak."));
husuperobject.biblemap.put("b43_20_7", new husuperobject(43,20,7,"És a keszkenõ, a mely az õ fején volt, nem együtt van a lepedõkkel, hanem külön összegöngyölítve egy helyen."));
husuperobject.biblemap.put("b43_20_8", new husuperobject(43,20,8,"Akkor aztán beméne a másik tanítvány is, a ki elõször jutott a sírhoz, és lát és hisz vala."));
husuperobject.biblemap.put("b43_20_9", new husuperobject(43,20,9,"Mert nem tudják vala még az írást, hogy fel kell támadnia a halálból."));
husuperobject.biblemap.put("b43_20_10", new husuperobject(43,20,10,"Visszamenének azért a tanítványok az övéikhez."));
husuperobject.biblemap.put("b43_20_11", new husuperobject(43,20,11,"Mária pedig künn áll vala a sírnál sírva. A míg azonban siránkozék, behajol vala a sírba;"));
husuperobject.biblemap.put("b43_20_12", new husuperobject(43,20,12,"És láta két angyalt fehér ruhában ülni, egyiket fejtõl, másikat lábtól, a hol a Jézus teste feküdt vala."));
husuperobject.biblemap.put("b43_20_13", new husuperobject(43,20,13,"És mondának azok néki: Asszony mit sírsz? Monda nékik: Mert elvitték az én Uramat, és nem tudom, hova tették õt."));
husuperobject.biblemap.put("b43_20_14", new husuperobject(43,20,14,"És mikor ezeket mondotta, hátra fordula, és látá Jézust [ott] állani, és nem tudja vala, hogy Jézus az."));
husuperobject.biblemap.put("b43_20_15", new husuperobject(43,20,15,"Monda néki Jézus: Asszony, mit sírsz? kit keressz? Az pedig azt gondolván, hogy a kertész az, monda néki: Uram, ha te vitted el õt, mondd meg nékem, hová tetted õt, és én elviszem õt."));
husuperobject.biblemap.put("b43_20_16", new husuperobject(43,20,16,"Monda néki Jézus: Mária! Az megfordulván, monda néki: Rabbóni! a mi azt teszi: Mester!"));
husuperobject.biblemap.put("b43_20_17", new husuperobject(43,20,17,"Monda néki Jézus: Ne illess engem; mert nem mentem még fel az én Atyámhoz; hanem menj az én atyámfiaihoz és mondd nékik: Felmegyek az én Atyámhoz és a ti Atyátokhoz, és az én Istenemhez, és a ti Istenetekhez."));
husuperobject.biblemap.put("b43_20_18", new husuperobject(43,20,18,"Elméne Mária Magdaléna, hirdetvén a tanítványoknak, hogy látta az Urat, és [hogy] ezeket mondotta néki."));
husuperobject.biblemap.put("b43_20_19", new husuperobject(43,20,19,"Mikor azért estve vala, azon a napon, a hétnek elsõ napján, és mikor az ajtók zárva valának, a hol egybegyûltek vala a tanítványok, a zsidóktól való félelem miatt, eljöve Jézus és megálla a középen, és monda nékik: Békesség néktek!"));
husuperobject.biblemap.put("b43_20_20", new husuperobject(43,20,20,"És ezt mondván, megmutatá nékik a kezeit és az oldalát. Örvendezének azért a tanítványok, hogy látják vala az Urat."));
husuperobject.biblemap.put("b43_20_21", new husuperobject(43,20,21,"Ismét monda azért nékik Jézus: Békesség néktek! A miként engem küldött vala az Atya, én is akképen küldelek titeket."));
husuperobject.biblemap.put("b43_20_22", new husuperobject(43,20,22,"És mikor ezt mondta, rájuk lehelle, és monda nékik: Vegyetek Szent Lelket:"));
husuperobject.biblemap.put("b43_20_23", new husuperobject(43,20,23,"A kiknek bûneit megbocsátjátok, megbocsáttatnak azoknak; a kikéit megtartjátok, megtartatnak."));
husuperobject.biblemap.put("b43_20_24", new husuperobject(43,20,24,"Tamás pedig, egy a tizenkettõ közül, a kit Kettõsnek hívtak, nem vala õ velök, a mikor eljött vala Jézus."));
husuperobject.biblemap.put("b43_20_25", new husuperobject(43,20,25,"Mondának azért néki a többi tanítványok: Láttuk az Urat. Õ pedig monda nékik: Ha nem látom az õ kezein a szegek helyeit, és be nem bocsátom ujjaimat a szegek helyébe, és az én kezemet be nem bocsátom az õ oldalába, semmiképen el nem hiszem."));
husuperobject.biblemap.put("b43_20_26", new husuperobject(43,20,26,"És nyolcz nap múlva ismét benn valának az õ tanítványai, Tamás is õ velök. Noha az ajtó zárva vala, beméne Jézus, és megálla a középen és monda: Békesség néktek!"));
husuperobject.biblemap.put("b43_20_27", new husuperobject(43,20,27,"Azután monda Tamásnak: Hozd ide a te ujjadat és nézd meg az én kezeimet; és hozd [ide] a te kezedet, és bocsássad az én oldalamba: és ne légy hitetlen, hanem hívõ."));
husuperobject.biblemap.put("b43_20_28", new husuperobject(43,20,28,"És felele Tamás és monda néki: Én Uram és én Istenem!"));
husuperobject.biblemap.put("b43_20_29", new husuperobject(43,20,29,"Monda néki Jézus: Mivelhogy láttál engem, Tamás, hittél: boldogok, a kik nem látnak és hisznek."));
husuperobject.biblemap.put("b43_20_30", new husuperobject(43,20,30,"Sok más jelt is mívelt ugyan Jézus az õ tanítványai elõtt, a melyek nincsenek megírva ebben a könyvben;"));
husuperobject.biblemap.put("b43_20_31", new husuperobject(43,20,31,"Ezek pedig azért irattak meg, hogy higyjétek, hogy Jézus a Krisztus, az Istennek Fia, és hogy [ezt] hívén, életetek legyen az õ nevében."));
husuperobject.biblemap.put("b43_21_1", new husuperobject(43,21,1,"Ezek után ismét megjelentette magát Jézus a tanítványoknak a Tibériás tengerénél; megjelentette pedig ekképen:"));
husuperobject.biblemap.put("b43_21_2", new husuperobject(43,21,2,"Együtt valának Simon Péter, és Tamás, a kit Kettõsnek hívtak, és Nátánáel, a galileai Kánából való, és a Zebedeus fiai, és más kettõ [is] az õ tanítványai közül."));
husuperobject.biblemap.put("b43_21_3", new husuperobject(43,21,3,"Monda nékik Simon Péter: Elmegyek halászni. Mondának néki: Elmegyünk mi is te veled. Elmenének és azonnal a hajóba szállának; és azon az éjszakán nem fogtak semmit."));
husuperobject.biblemap.put("b43_21_4", new husuperobject(43,21,4,"Mikor pedig immár reggeledék, megálla Jézus a parton; a tanítványok azonban nem ismerék meg, hogy Jézus van [ott.]"));
husuperobject.biblemap.put("b43_21_5", new husuperobject(43,21,5,"Monda azért nékik Jézus: Fiaim! Van-é valami ennivalótok? Felelének néki: Nincsen!"));
husuperobject.biblemap.put("b43_21_6", new husuperobject(43,21,6,"Õ pedig monda nékik: Vessétek a hálót a hajónak jobb oldala felõl, és találtok. Oda veték azért, és kivonni már nem bírták azt a halaknak sokasága miatt."));
husuperobject.biblemap.put("b43_21_7", new husuperobject(43,21,7,"Szóla azért az a tanítvány, a kit Jézus szeret vala, Péternek: Az Úr van [ott]! Simon Péter azért, a mikor hallja vala, hogy [ott] van az Úr, magára vevé az ingét (mert mezítelen vala), és beveté magát a tengerbe."));
husuperobject.biblemap.put("b43_21_8", new husuperobject(43,21,8,"A többi tanítványok pedig a hajón menének (mert nem messze valának a parttól, hanem mintegy kétszáz singnyire), és vonszszák vala a hálót a halakkal."));
husuperobject.biblemap.put("b43_21_9", new husuperobject(43,21,9,"Mikor azért a partra szállának, látják, hogy parázs van ott, és azon felül hal és kenyér."));
husuperobject.biblemap.put("b43_21_10", new husuperobject(43,21,10,"Monda nékik Jézus: Hozzatok a halakból, a melyeket most fogtatok."));
husuperobject.biblemap.put("b43_21_11", new husuperobject(43,21,11,"Felszálla Simon Péter, és kivoná a hálót a partra, a mely tele volt nagy halakkal, százötvenhárommal; és noha ennyi vala, nem szakadozik vala a háló."));
husuperobject.biblemap.put("b43_21_12", new husuperobject(43,21,12,"Monda nékik Jézus: Jertek, ebédeljetek. A tanítványok közül pedig senki sem meri vala tõle megkérdezni: Ki vagy te? Mivelhogy tudják vala, hogy az Úr õ."));
husuperobject.biblemap.put("b43_21_13", new husuperobject(43,21,13,"Oda méne azért Jézus, és vevé a kenyeret és adá nékik, és hasonlóképen a halat is."));
husuperobject.biblemap.put("b43_21_14", new husuperobject(43,21,14,"Ezzel már harmadszor jelent meg Jézus az õ tanítványainak, minekutána feltámadt a halálból."));
husuperobject.biblemap.put("b43_21_15", new husuperobject(43,21,15,"Mikor aztán megebédelének, monda Jézus Simon Péternek: Simon, Jónának [fia:] jobban szeretsz-é engem ezeknél? Monda néki: Igen, Uram; te tudod, hogy szeretlek téged! Monda néki: Legeltesd az én bárányaimat!"));
husuperobject.biblemap.put("b43_21_16", new husuperobject(43,21,16,"Monda néki ismét másodszor is: Simon, Jónának [fia], szeretsz-é engem? Monda néki: Igen, Uram; te tudod, hogy én szeretlek téged. Monda néki: Õrizd az én juhaimat!"));
husuperobject.biblemap.put("b43_21_17", new husuperobject(43,21,17,"Monda néki harmadszor [is]: Simon, Jónának [fia], szeretsz-é engem? Megszomorodék Péter, hogy harmadszor [is] mondotta vala néki: Szeretsz-é engem? És monda néki: Uram, te mindent tudsz; te tudod, hogy én szeretlek téged. Monda néki Jézus: Legeltesd az én juhaimat!"));
husuperobject.biblemap.put("b43_21_18", new husuperobject(43,21,18,"Bizony, bizony mondom néked, a mikor ifjabb valál, felövezéd magadat, és oda mégy vala, a hova akarád; mikor pedig megöregszel, kinyújtod a te kezedet és más övez fel téged, és oda visz, a hová nem akarod."));
husuperobject.biblemap.put("b43_21_19", new husuperobject(43,21,19,"Ezt pedig azért mondá, hogy jelentse, milyen halállal dicsõíti majd meg az Istent. És ezt mondván, szóla néki: Kövess engem!"));
husuperobject.biblemap.put("b43_21_20", new husuperobject(43,21,20,"Péter pedig megfordulván, látja, hogy követi az a tanítvány, a kit szeret vala Jézus, a ki nyugodott is ama vacsora közben az õ kebelén és mondá: Uram! ki az, a ki elárul téged?"));
husuperobject.biblemap.put("b43_21_21", new husuperobject(43,21,21,"Ezt látván Péter, monda Jézusnak: Uram, ez pedig mint [lészen?]"));
husuperobject.biblemap.put("b43_21_22", new husuperobject(43,21,22,"Monda néki Jézus: Ha akarom, hogy õ megmaradjon, a míg eljövök, mi közöd hozzá? Te kövess engem!"));
husuperobject.biblemap.put("b43_21_23", new husuperobject(43,21,23,"Kiméne azért e beszéd az atyafiak közé, hogy az a tanítvány nem hal meg: pedig Jézus nem mondta néki, hogy nem hal meg; hanem: Ha akarom, hogy ez megmaradjon, a míg eljövök, mi közöd hozzá?"));
husuperobject.biblemap.put("b43_21_24", new husuperobject(43,21,24,"Ez az a tanítvány, a ki bizonyságot tesz ezekrõl, és a ki megírta ezeket, és tudjuk, hogy az õ bizonyságtétele igaz."));
husuperobject.biblemap.put("b43_21_25", new husuperobject(43,21,25,"De van sok egyéb is, a miket Jézus cselekedett vala, a melyek, ha egyenként megiratnának, azt vélem, hogy maga a világ sem foghatná be a könyveket, a melyeket írnának. Ámen."));

}

}