package hu.bible;

public class hubible49 {

public hubible49() {

putmapbible49();

}

private void putmapbible49() {

husuperobject.biblemap.put("b49_1_1", new husuperobject(49,1,1,"Pál, Jézus Krisztus apostola Isten akaratjából, az Efézusban lévõ és Krisztus Jézusban hívõ szenteknek."));
husuperobject.biblemap.put("b49_1_2", new husuperobject(49,1,2,"Kegyelem néktek és békesség Istentõl, a mi Atyánktól és az Úr Jézus Krisztustól."));
husuperobject.biblemap.put("b49_1_3", new husuperobject(49,1,3,"Áldott legyen az Isten, és a mi Urunknak, Jézus Krisztusnak Atyja, a ki megáldott minket minden lelki áldással a mennyekben a Krisztusban,"));
husuperobject.biblemap.put("b49_1_4", new husuperobject(49,1,4,"A szerint, a mint magának kiválasztott minket Õ benne a világ teremtetése elõtt, hogy legyünk mi szentek és feddhetetlenek Õ elõtte szeretet által,"));
husuperobject.biblemap.put("b49_1_5", new husuperobject(49,1,5,"Eleve elhatározván, hogy minket a maga fiaivá fogad Jézus Krisztus által az Õ akaratjának jó kedve szerint,"));
husuperobject.biblemap.put("b49_1_6", new husuperobject(49,1,6,"Kegyelme dicsõségének magasztalására, a melylyel megajándékozott minket ama Szerelmesben,"));
husuperobject.biblemap.put("b49_1_7", new husuperobject(49,1,7,"A kiben van a mi váltságunk az Õ vére által, a bûnöknek bocsánata az Õ kegyelmének gazdagsága szerint."));
husuperobject.biblemap.put("b49_1_8", new husuperobject(49,1,8,"Melyet nagy bõséggel közlött velünk minden bölcsességgel és értelemmel,"));
husuperobject.biblemap.put("b49_1_9", new husuperobject(49,1,9,"Megismertetvén velünk az Õ akaratjának titkát az Õ jó kedve szerint, melyet eleve elrendelt magában,"));
husuperobject.biblemap.put("b49_1_10", new husuperobject(49,1,10,"Az idõk teljességének rendjére nézve, hogy ismét egybeszerkeszt magának mindeneket a Krisztusban, mind a melyek a mennyekben vannak, mind a melyek e földön vannak;"));
husuperobject.biblemap.put("b49_1_11", new husuperobject(49,1,11,"Õ benne, a kiben vettük is az örökséget, eleve elrendeltetvén annak eleveelvégzése szerint, a ki mindent az õ akaratjának tanácsából cselekszik,"));
husuperobject.biblemap.put("b49_1_12", new husuperobject(49,1,12,"Hogy legyünk mi magasztalására az Õ dicsõségének, a kik elõre reménykedtünk a Krisztusban:"));
husuperobject.biblemap.put("b49_1_13", new husuperobject(49,1,13,"A kiben ti is, minekutána hallottátok az igazságnak beszédét, idvességetek evangyéliomát, a melyben hittetek is, megpecsételtettetek az ígéretnek ama Szent Lelkével,"));
husuperobject.biblemap.put("b49_1_14", new husuperobject(49,1,14,"A ki záloga a mi örökségünknek [Isten] tulajdon [népé]nek megváltatására, az Õ dicsõségének magasztalására."));
husuperobject.biblemap.put("b49_1_15", new husuperobject(49,1,15,"Annakokáért én is, hallván a ti hiteteket az Úr Jézusban, és minden szentekhez való szerelmeteket,"));
husuperobject.biblemap.put("b49_1_16", new husuperobject(49,1,16,"Nem szûnöm meg hálát adni tiérettetek, emlékezvén reátok az én könyörgéseimben;"));
husuperobject.biblemap.put("b49_1_17", new husuperobject(49,1,17,"Hogy a mi Urunk Jézus Krisztusnak Istene, a dicsõségnek Atyja adjon néktek bölcseségnek és kijelentésnek Lelkét az Õ megismerésében;"));
husuperobject.biblemap.put("b49_1_18", new husuperobject(49,1,18,"És világosítsa meg értelmetek szemeit, hogy tudhassátok, hogy mi az Õ elhívásának a reménysége, mi az Õ öröksége dicsõségének a gazdagsága a szentek között,"));
husuperobject.biblemap.put("b49_1_19", new husuperobject(49,1,19,"És mi az Õ hatalmának felséges nagysága irántunk, a kik hiszünk, az Õ hatalma erejének ama munkája szerint,"));
husuperobject.biblemap.put("b49_1_20", new husuperobject(49,1,20,"A melyet megmutatott a Krisztusban, mikor feltámasztotta Õt a halálból, és ültette Õt a maga jobbjára a mennyekben."));
husuperobject.biblemap.put("b49_1_21", new husuperobject(49,1,21,"Felül minden fejedelemségen és hatalmasságon és erõn és uraságon és minden néven, mely neveztetik nemcsak e világon, hanem a következendõben is:"));
husuperobject.biblemap.put("b49_1_22", new husuperobject(49,1,22,"És mindeneket vetett az Õ lábai alá, és Õt tette mindeneknek fölötte az anyaszentegyháznak fejévé,"));
husuperobject.biblemap.put("b49_1_23", new husuperobject(49,1,23,"Mely az Õ teste, teljessége Õ néki, a ki mindeneket betölt mindenekkel."));
husuperobject.biblemap.put("b49_2_1", new husuperobject(49,2,1,"Titeket is [megelevenített,] a kik holtak valátok a ti vétkeitek és bûneitek miatt,"));
husuperobject.biblemap.put("b49_2_2", new husuperobject(49,2,2,"Melyekben jártatok egykor e világ folyása szerint, a levegõbeli hatalmasság fejedelme szerint, ama lélek szerint, mely most az engedetlenség fiaiban munkálkodik;"));
husuperobject.biblemap.put("b49_2_3", new husuperobject(49,2,3,"A kik között forgolódtunk egykor mi is mindnyájan a mi testünk kívánságaiban, cselekedvén a testnek és a gondolatoknak akaratját, és természet szerint haragnak fiai valánk, mint egyebek is:"));
husuperobject.biblemap.put("b49_2_4", new husuperobject(49,2,4,"De az Isten gazdag lévén irgalmasságban, az Õ nagy szerelmébõl, melylyel minket szeretett,"));
husuperobject.biblemap.put("b49_2_5", new husuperobject(49,2,5,"Minket, kik meg voltunk halva a vétkek miatt, megelevenített együtt a Krisztussal, (kegyelembõl tartattatok meg!)"));
husuperobject.biblemap.put("b49_2_6", new husuperobject(49,2,6,"És együtt feltámasztott és együtt ültetett a mennyekben, Krisztus Jézusban:"));
husuperobject.biblemap.put("b49_2_7", new husuperobject(49,2,7,"Hogy megmutassa a következendõ idõkben az Õ kegyelmének felséges gazdagságát hozzánk való jóságából a Krisztus Jézusban."));
husuperobject.biblemap.put("b49_2_8", new husuperobject(49,2,8,"Mert kegyelembõl tartattatok meg, hit által; és ez nem tõletek van: Isten ajándéka ez;"));
husuperobject.biblemap.put("b49_2_9", new husuperobject(49,2,9,"Nem cselekedetekbõl, hogy senki ne kérkedjék."));
husuperobject.biblemap.put("b49_2_10", new husuperobject(49,2,10,"Mert az Õ alkotása vagyunk, teremtetvén [Általa] a Krisztus Jézusban jó cselekedetekre, a melyeket elõre elkészített az Isten, hogy azokban járjunk."));
husuperobject.biblemap.put("b49_2_11", new husuperobject(49,2,11,"Annakokáért emlékezzetek meg arról, hogy egykor ti a testben pogányok, kiket körülmetéletlenségnek nevez vala amaz úgynevezett [s] a testen kézzel megcsinált körülmetélkedés,"));
husuperobject.biblemap.put("b49_2_12", new husuperobject(49,2,12,"Hogy ti, [mondom], abban az idõben Krisztus nélkül valók voltatok, Izráel társaságától idegenek, és az ígéret szövetségeitõl távolvalók, reménységetek nem vala, és Isten nélkül valók voltatok e világon;"));
husuperobject.biblemap.put("b49_2_13", new husuperobject(49,2,13,"Most pedig a Krisztus Jézusban ti, kik egykor távol valátok, közelvalókká lettetek a Krisztus vére által."));
husuperobject.biblemap.put("b49_2_14", new husuperobject(49,2,14,"Mert Õ a mi békességünk, ki egygyé tette mind a két [nemzetséget,] és lerontotta a közbevetett választófalat,"));
husuperobject.biblemap.put("b49_2_15", new husuperobject(49,2,15,"Az ellenségeskedést az Õ testében, a parancsolatoknak tételekben való törvényét eltörölvén; hogy ama kettõt egy új emberré teremtse Õ magában, békességet szerezvén;"));
husuperobject.biblemap.put("b49_2_16", new husuperobject(49,2,16,"És hogy megbékéltesse az Istennel mind a kettõt, egy testben a keresztfa által, megölvén ezen az ellenségeskedést."));
husuperobject.biblemap.put("b49_2_17", new husuperobject(49,2,17,"És eljövén, békességet hirdetett néktek, a távol valóknak és a közel valóknak."));
husuperobject.biblemap.put("b49_2_18", new husuperobject(49,2,18,"Mert Õ általa van menetelünk mindkettõnknek egy Lélekben az Atyához."));
husuperobject.biblemap.put("b49_2_19", new husuperobject(49,2,19,"Azért immár nem vagytok jövevények és zsellérek, hanem polgártársai a szenteknek és cselédei az Istennek,"));
husuperobject.biblemap.put("b49_2_20", new husuperobject(49,2,20,"Kik fölépíttettetek az apostoloknak és prófétáknak alapkövén, lévén a szegletkõ maga Jézus Krisztus,"));
husuperobject.biblemap.put("b49_2_21", new husuperobject(49,2,21,"A kiben az egész épület szép renddel rakattatván, nevekedik szent templommá az Úrban;"));
husuperobject.biblemap.put("b49_2_22", new husuperobject(49,2,22,"A kiben ti is együtt építtettek Isten hajlékává a Lélek által."));
husuperobject.biblemap.put("b49_3_1", new husuperobject(49,3,1,"Ezért [vagyok] én, Pál, a Krisztusnak foglya ti érettetek, a pogányokért;"));
husuperobject.biblemap.put("b49_3_2", new husuperobject(49,3,2,"Ha ugyan hallottátok Isten kegyelmének rendelkezését, melyet nékem adott a ti érdeketekben;"));
husuperobject.biblemap.put("b49_3_3", new husuperobject(49,3,3,"Hogy [tudniillik] kijelentés útján ismertette meg velem a titkot, a szerint, a mint az elébb megírám röviden,"));
husuperobject.biblemap.put("b49_3_4", new husuperobject(49,3,4,"Melynek olvasásából megérthetitek, hogy micsoda az én értelmem a Krisztus titka felõl;"));
husuperobject.biblemap.put("b49_3_5", new husuperobject(49,3,5,"A mely egyéb idõkben meg nem ismertettetett az emberek fiaival úgy, a hogy most kijelentetett az Õ szent apostolainak és prófétáinak a Lélek által:"));
husuperobject.biblemap.put("b49_3_6", new husuperobject(49,3,6,"Hogy [tudniillik] a pogányok örökös társak és egyugyanazon test tagjai és részesei az õ ígéretének a Krisztus Jézusban az evangyéliom által."));
husuperobject.biblemap.put("b49_3_7", new husuperobject(49,3,7,"A melynek szolgájává lettem az Isten ama kegyelmének ajándékából, mely adatott nékem az Õ hatalmának munkája szerint."));
husuperobject.biblemap.put("b49_3_8", new husuperobject(49,3,8,"Nékem, minden szentek között a legeslegkisebbnek adatott ez a kegyelem, hogy a pogányoknak hirdessem a Krisztus végére mehetetlen gazdagságát;"));
husuperobject.biblemap.put("b49_3_9", new husuperobject(49,3,9,"És hogy megvilágosítsam mindeneknek, hogy miképen rendelkezett Isten ama titok felõl, a mely elrejtetett vala örök idõktõl fogva az Istenben, a ki mindeneket teremtett a Jézus Krisztus által;"));
husuperobject.biblemap.put("b49_3_10", new husuperobject(49,3,10,"Azért, hogy megismertettessék most a mennybeli fejedelemségekkel és hatalmasságokkal az egyház által az Istennek sokféle bölcsesége,"));
husuperobject.biblemap.put("b49_3_11", new husuperobject(49,3,11,"Amaz örök eleve-elvégezés szerint, a melyet megcselekedett a Krisztus Jézusban, a mi Urunkban:"));
husuperobject.biblemap.put("b49_3_12", new husuperobject(49,3,12,"A kiben van a mi bátorságunk és bizodalommal való menetelünk [Istenhez] az Õ benne való hit által."));
husuperobject.biblemap.put("b49_3_13", new husuperobject(49,3,13,"Azért kérlek, hogy ne csüggedjetek el az én ti érettetek való nyomorúságaim miatt, a melyek néktek dicsõségtekre vannak."));
husuperobject.biblemap.put("b49_3_14", new husuperobject(49,3,14,"Ezokáért meghajtom térdeimet a mi Urunk Jézus Krisztusnak Atyja elõtt,"));
husuperobject.biblemap.put("b49_3_15", new husuperobject(49,3,15,"A kirõl neveztetik minden nemzetség, mennyen és földön,"));
husuperobject.biblemap.put("b49_3_16", new husuperobject(49,3,16,"Hogy adja meg néktek az Õ dicsõsége gazdagságáért, hogy hatalmasan megerõsödjetek az Õ Lelke által a belsõ emberben;"));
husuperobject.biblemap.put("b49_3_17", new husuperobject(49,3,17,"Hogy lakozzék a Krisztus a hit által a ti szívetekben;"));
husuperobject.biblemap.put("b49_3_18", new husuperobject(49,3,18,"A szeretetben meggyökerezvén és alapot vevén, hogy megérthessétek minden szentekkel egybe, mi a szélessége és hosszúsága és mélysége és magassága [az Isten jóvoltának,]"));
husuperobject.biblemap.put("b49_3_19", new husuperobject(49,3,19,"És megismerjétek a Krisztusnak minden ismeretet felül haladó szeretetét, hogy [ekképen] beteljesedjetek az Istennek egész teljességéig."));
husuperobject.biblemap.put("b49_3_20", new husuperobject(49,3,20,"Annak pedig, a ki véghetetlen bõséggel mindeneket megcselekedhetik, feljebb hogynem mint kérjük vagy elgondoljuk, a mi bennünk munkálkodó erõ szerint,"));
husuperobject.biblemap.put("b49_3_21", new husuperobject(49,3,21,"Annak légyen dicsõség az egyházban a Krisztus Jézusban nemzetségrõl nemzetségre örökkön örökké. Ámen!"));
husuperobject.biblemap.put("b49_4_1", new husuperobject(49,4,1,"Kérlek azért titeket én, ki fogoly vagyok az Úrban, hogy járjatok úgy, mint illik elhívatásotokhoz, melylyel elhívattatok."));
husuperobject.biblemap.put("b49_4_2", new husuperobject(49,4,2,"Teljes alázatossággal és szelídséggel, hosszútûréssel, elszenvedvén egymást szeretetben,"));
husuperobject.biblemap.put("b49_4_3", new husuperobject(49,4,3,"Igyekezvén megtartani a Lélek egységét a békességnek kötelében."));
husuperobject.biblemap.put("b49_4_4", new husuperobject(49,4,4,"Egy a test és egy a Lélek, miképen elhívatástoknak egy reménységében hívattatok el is;"));
husuperobject.biblemap.put("b49_4_5", new husuperobject(49,4,5,"Egy az Úr, egy a hit, egy a keresztség;"));
husuperobject.biblemap.put("b49_4_6", new husuperobject(49,4,6,"Egy az Isten és mindeneknek Atyja, a ki mindeneknek felette van és mindenek által és mindnyájatokban munkálkodik."));
husuperobject.biblemap.put("b49_4_7", new husuperobject(49,4,7,"Mindenikünknek pedig adatott a kegyelem a Krisztustól osztott ajándéknak mértéke szerint."));
husuperobject.biblemap.put("b49_4_8", new husuperobject(49,4,8,"Ezokáért mondja az [Írás:] Fölmenvén a magasságba foglyokat vitt fogva, és adott ajándékokat az embereknek."));
husuperobject.biblemap.put("b49_4_9", new husuperobject(49,4,9,"(Az pedig, hogy fölment, mit jelentene mást, mint hogy elõbb le is szállott a föld alsóbb részeire?"));
husuperobject.biblemap.put("b49_4_10", new husuperobject(49,4,10,"A ki leszállott vala, ugyanaz, a ki fel is ment, feljebb minden egeknél, hogy mindeneket betöltsön.)"));
husuperobject.biblemap.put("b49_4_11", new husuperobject(49,4,11,"És Õ adott némelyeket apostolokul, némelyeket prófétákul, némelyeket evangyélistákul, némelyeket pedig pásztorokul és tanítókul:"));
husuperobject.biblemap.put("b49_4_12", new husuperobject(49,4,12,"A szentek tökéletesbbítése czéljából szolgálat munkájára, a Krisztus testének építésére:"));
husuperobject.biblemap.put("b49_4_13", new husuperobject(49,4,13,"Míg eljutunk mindnyájan az Isten Fiában való hitnek és az Õ megismerésének egységére, érett férfiúságra, a Krisztus teljességével ékeskedõ kornak mértékére:"));
husuperobject.biblemap.put("b49_4_14", new husuperobject(49,4,14,"Hogy többé ne legyünk gyermekek, kiket ide s tova hány a hab és hajt a tanításnak akármi szele, az embereknek álnoksága által, a tévelygés ravaszságához való csalárdság által;"));
husuperobject.biblemap.put("b49_4_15", new husuperobject(49,4,15,"Hanem az igazságot követvén szeretetben, mindenestõl fogva nevekedjünk Abban, a ki a fej, a Krisztusban;"));
husuperobject.biblemap.put("b49_4_16", new husuperobject(49,4,16,"A kibõl az egész test, szép renddel egyberakatván és egybeszerkesztetvén az [Õ] segedelmének minden kapcsaival, minden egyes tagnak mértéke szerint való munkássággal teljesíti a testnek nevekedését a maga fölépítésére szeretetben."));
husuperobject.biblemap.put("b49_4_17", new husuperobject(49,4,17,"Ezt mondom annakokáért és bizonyságot teszek az Úrban, hogy ti többé ne járjatok úgy, mint egyéb pogányok is járnak az õ elméjöknek hiábavalóságában,"));
husuperobject.biblemap.put("b49_4_18", new husuperobject(49,4,18,"Kik értelmökben meghomályosodtak, elidegenültek az isteni élettõl a tudatlanság miatt, mely az õ szívök keménysége miatt van bennök;"));
husuperobject.biblemap.put("b49_4_19", new husuperobject(49,4,19,"Kik erkölcsi érzés nélkül, önmagukat a bujálkodásra adták, minden tisztátalanságnak nagy nyereséggel való cselekedésére."));
husuperobject.biblemap.put("b49_4_20", new husuperobject(49,4,20,"Ti pedig nem így tanultátok a Krisztust;"));
husuperobject.biblemap.put("b49_4_21", new husuperobject(49,4,21,"Ha ugyan Õt megértettétek és Õ benne megtaníttattatok, úgy a mint az igazság a Jézusban:"));
husuperobject.biblemap.put("b49_4_22", new husuperobject(49,4,22,"Hogy levetkezzétek ama régi élet szerint való ó embert, mely meg van romolva a csalárdság kívánságai miatt;"));
husuperobject.biblemap.put("b49_4_23", new husuperobject(49,4,23,"Megújuljatok pedig a ti elméteknek lelke szerint,"));
husuperobject.biblemap.put("b49_4_24", new husuperobject(49,4,24,"És felöltözzétek amaz új embert, mely Isten szerint teremtetett igazságban és valóságos szentségben."));
husuperobject.biblemap.put("b49_4_25", new husuperobject(49,4,25,"Azért levetvén a hazugságot, szóljatok igazságot, kiki az õ felebarátjával: mert egymásnak tagjai vagyunk."));
husuperobject.biblemap.put("b49_4_26", new husuperobject(49,4,26,"Ám haragudjatok, de ne vétkezzetek: a nap le ne menjen a ti haragotokon;"));
husuperobject.biblemap.put("b49_4_27", new husuperobject(49,4,27,"Se pedig az ördögnek ne adjatok helyet."));
husuperobject.biblemap.put("b49_4_28", new husuperobject(49,4,28,"A ki oroz vala, többé ne orozzon; hanem inkább munkálkodjék, cselekedvén az õ kezeivel azt, a mi jó, hogy legyen mit adnia a szûkölködõnek."));
husuperobject.biblemap.put("b49_4_29", new husuperobject(49,4,29,"Semmi rothadt beszéd a ti szátokból ki ne származzék, hanem csak a mely hasznos a szükséges építésre, hogy áldásos legyen a hallgatóknak."));
husuperobject.biblemap.put("b49_4_30", new husuperobject(49,4,30,"És meg ne szomorítsátok az Istennek ama Szent Lelkét, a ki által megpecsételtettetek a teljes váltságnak napjára."));
husuperobject.biblemap.put("b49_4_31", new husuperobject(49,4,31,"Minden mérgesség és fölgerjedés és harag és lárma és káromkodás kivettessék közületek minden gonoszsággal együtt;"));
husuperobject.biblemap.put("b49_4_32", new husuperobject(49,4,32,"Legyetek pedig egymáshoz jóságosak, irgalmasok, megengedvén egymásnak, miképen az Isten is a Krisztusban megengedett néktek."));
husuperobject.biblemap.put("b49_5_1", new husuperobject(49,5,1,"Legyetek annakokáért követõi az Istennek, mint szeretett gyermekek:"));
husuperobject.biblemap.put("b49_5_2", new husuperobject(49,5,2,"És járjatok szeretetben, miképen a Krisztus is szeretett minket, és adta Önmagát miérettünk ajándékul és áldozatul az Istennek, kedves jó illatul."));
husuperobject.biblemap.put("b49_5_3", new husuperobject(49,5,3,"Paráznaság pedig és akármely tisztátalanság vagy fösvénység ne is neveztessék ti közöttetek, a mint szentekhez illik;"));
husuperobject.biblemap.put("b49_5_4", new husuperobject(49,5,4,"Sem undokság, vagy bolond beszéd, vagy trágárság, melyek nem illenek: hanem inkább hálaadás."));
husuperobject.biblemap.put("b49_5_5", new husuperobject(49,5,5,"Mert azt jól tudjátok, hogy egy paráznának is, vagy tisztátalannak, vagy fösvénynek, ki bálványimádó, nincs öröksége a Krisztusnak és Istennek országában."));
husuperobject.biblemap.put("b49_5_6", new husuperobject(49,5,6,"Senki titeket meg ne csaljon üres beszédekkel; mert ezekért jõ az Isten haragja a hitetlenség fiaira."));
husuperobject.biblemap.put("b49_5_7", new husuperobject(49,5,7,"Annakokáért ne legyetek részesei ezeknek;"));
husuperobject.biblemap.put("b49_5_8", new husuperobject(49,5,8,"Mert valátok régen sötétség, most pedig világosság az Úrban: mint világosságnak fiai úgy járjatok."));
husuperobject.biblemap.put("b49_5_9", new husuperobject(49,5,9,"(Mert a világosságnak gyümölcse minden jóságban és igazságban és valóságban van),"));
husuperobject.biblemap.put("b49_5_10", new husuperobject(49,5,10,"Meggondolván, mi legyen kedves az Úrnak."));
husuperobject.biblemap.put("b49_5_11", new husuperobject(49,5,11,"És ne legyen közösségtek a sötétségnek gyümölcstelen cselekedeteivel, hanem inkább meg is feddjétek azokat;"));
husuperobject.biblemap.put("b49_5_12", new husuperobject(49,5,12,"Mert a melyeket azok titokban cselekesznek, éktelen dolog csak mondani is."));
husuperobject.biblemap.put("b49_5_13", new husuperobject(49,5,13,"Mindezek pedig megfeddetvén, a világosság által napvilágra jõnek; mert minden, a mi napvilágra jõ, világosság."));
husuperobject.biblemap.put("b49_5_14", new husuperobject(49,5,14,"Annakokáért mondja: Serkenj föl, a ki aluszol és támadj fel a halálból, és felragyogott tenéked a Krisztus."));
husuperobject.biblemap.put("b49_5_15", new husuperobject(49,5,15,"Meglássátok annakokáért, hogy mimódon okkal járjatok, nem mint bolondok, hanem mint bölcsek:"));
husuperobject.biblemap.put("b49_5_16", new husuperobject(49,5,16,"Áron is megvegyétek az alkalmatosságot, mert a napok gonoszok."));
husuperobject.biblemap.put("b49_5_17", new husuperobject(49,5,17,"Annakokáért ne legyetek esztelenek, hanem megértsétek, mi legyen az Úrnak akaratja."));
husuperobject.biblemap.put("b49_5_18", new husuperobject(49,5,18,"És meg ne részegedjetek bortól, miben kicsapongás van: hanem teljesedjetek be Szent Lélekkel,"));
husuperobject.biblemap.put("b49_5_19", new husuperobject(49,5,19,"Beszélgetvén egymás között zsoltárokban és dícséretekben és lelki énekekben, énekelvén és dícséretet mondván szívetekben az Úrnak."));
husuperobject.biblemap.put("b49_5_20", new husuperobject(49,5,20,"Hálákat adván mindenkor mindenekért a mi Urunk Jézus Krisztusnak nevében az Istennek és Atyának."));
husuperobject.biblemap.put("b49_5_21", new husuperobject(49,5,21,"Engedelmesek [legyetek] egymásnak Isten félelmében."));
husuperobject.biblemap.put("b49_5_22", new husuperobject(49,5,22,"Ti asszonyok a ti saját férjeteknek engedelmesek legyetek, mint az Úrnak."));
husuperobject.biblemap.put("b49_5_23", new husuperobject(49,5,23,"Mert a férj feje a feleségének, mint a Krisztus is feje az egyháznak, és ugyanõ megtartója a testnek."));
husuperobject.biblemap.put("b49_5_24", new husuperobject(49,5,24,"De miképen az egyház engedelmes a Krisztusnak, azonképen az asszonyok is [engedelmesek legyenek] férjöknek mindenben."));
husuperobject.biblemap.put("b49_5_25", new husuperobject(49,5,25,"Ti férfiak, szeressétek a ti feleségeteket, miképen a Krisztus is szerette az egyházat, és Önmagát adta azért;"));
husuperobject.biblemap.put("b49_5_26", new husuperobject(49,5,26,"Hogy azt megszentelje, megtisztítván a víznek feredõjével az íge által,"));
husuperobject.biblemap.put("b49_5_27", new husuperobject(49,5,27,"Hogy majd Önmaga elébe állítsa dicsõségben az egyházat, úgy hogy azon ne legyen szeplõ, vagy sömörgözés, vagy valami afféle; hanem hogy legyen szent és feddhetetlen."));
husuperobject.biblemap.put("b49_5_28", new husuperobject(49,5,28,"Úgy kell a férfiaknak szeretni az õ feleségöket, mint az õ tulajdon testöket. A ki szereti az õ feleségét, önmagát szereti."));
husuperobject.biblemap.put("b49_5_29", new husuperobject(49,5,29,"Mert soha senki az õ tulajdon testét nem gyûlölte; hanem táplálgatja és ápolgatja azt, miképen az Úr is az egyházat;"));
husuperobject.biblemap.put("b49_5_30", new husuperobject(49,5,30,"Mert az Õ testének tagjai vagyunk, az Õ testébõl és az Õ csontjaiból valók."));
husuperobject.biblemap.put("b49_5_31", new husuperobject(49,5,31,"Annakokáért elhagyja az ember atyját és anyját, és ragaszkodik az õ feleségéhez; és lesznek ketten egy testté."));
husuperobject.biblemap.put("b49_5_32", new husuperobject(49,5,32,"Felette nagy titok ez: de én a Krisztusról és az egyházról szólok."));
husuperobject.biblemap.put("b49_5_33", new husuperobject(49,5,33,"Hanem azért ti is egyen-egyen, ki-ki az õ feleségét úgy szeresse, mint önmagát; az asszony pedig [meglássa,] hogy félje a férjét."));
husuperobject.biblemap.put("b49_6_1", new husuperobject(49,6,1,"Ti gyermekek szót fogadjatok a ti szüleiteknek az Úrban; mert ez az igaz."));
husuperobject.biblemap.put("b49_6_2", new husuperobject(49,6,2,"Tiszteljed a te atyádat és a te anyádat (a mi az elsõ parancsolat ígérettel)."));
husuperobject.biblemap.put("b49_6_3", new husuperobject(49,6,3,"Hogy jól legyen néked dolgod és hosszú életû légy e földön."));
husuperobject.biblemap.put("b49_6_4", new husuperobject(49,6,4,"Ti is atyák ne ingereljétek gyermekeiteket, hanem neveljétek azokat az Úr tanítása és intése szerint."));
husuperobject.biblemap.put("b49_6_5", new husuperobject(49,6,5,"Ti szolgák, engedelmesek legyetek a ti test szerint való uraitoknak félelemmel és rettegéssel, szíveteknek egyenességében, mint a Krisztusnak;"));
husuperobject.biblemap.put("b49_6_6", new husuperobject(49,6,6,"Nem a szemnek szolgálván, mint a kik embereknek akarnak tetszeni, hanem mint Krisztus szolgái, cselekedvén az Istennek akaratját lélekbõl,"));
husuperobject.biblemap.put("b49_6_7", new husuperobject(49,6,7,"Jó akarattal, mint a kik az Úrnak szolgálnak és nem embereknek;"));
husuperobject.biblemap.put("b49_6_8", new husuperobject(49,6,8,"Tudván, hogy kiki a mi jót cselekszik, azt veendi az Úrtól, akár szolga legyen, akár szabados."));
husuperobject.biblemap.put("b49_6_9", new husuperobject(49,6,9,"Ti is urak, ugyanazt cselekedjétek õ velök, elhegyván a fenyegetést; tudván, hogy a ti Uratok is mennyben van, és személyválogatás nincsen Õ nála."));
husuperobject.biblemap.put("b49_6_10", new husuperobject(49,6,10,"Végezetre, atyámfiai, legyetek erõsek az Úrban, és az õ hatalmas erejében."));
husuperobject.biblemap.put("b49_6_11", new husuperobject(49,6,11,"Öltözzétek föl az Isten minden fegyverét, hogy megállhassatok az ördögnek minden ravaszságával szemben."));
husuperobject.biblemap.put("b49_6_12", new husuperobject(49,6,12,"Mert nem vér és test ellen van nékünk tusakodásunk, hanem a fejedelemségek ellen, a hatalmasságok ellen, ez élet sötétségének világbírói ellen, a gonoszság lelkei ellen, melyek a magasságban vannak."));
husuperobject.biblemap.put("b49_6_13", new husuperobject(49,6,13,"Annakokáért vegyétek föl az Istennek minden fegyverét, hogy ellentállhassatok ama gonosz napon, és mindeneket elvégezvén megállhassatok."));
husuperobject.biblemap.put("b49_6_14", new husuperobject(49,6,14,"Álljatok hát elõ, körül övezvén derekatokat igazlelkûséggel, és felöltözvén az igazságnak mellvasába,"));
husuperobject.biblemap.put("b49_6_15", new husuperobject(49,6,15,"És felsarúzván lábaitokat a békesség evangyéliomának készségével;"));
husuperobject.biblemap.put("b49_6_16", new husuperobject(49,6,16,"Mindezekhez fölvevén a hitnek paizsát, a melylyel ama gonosznak minden tüzes nyilát megolthatjátok;"));
husuperobject.biblemap.put("b49_6_17", new husuperobject(49,6,17,"Az idvesség sisakját is fölvegyétek, és a Léleknek kardját, a mely az Isten beszéde:"));
husuperobject.biblemap.put("b49_6_18", new husuperobject(49,6,18,"Minden imádsággal és könyörgéssel imádkozván minden idõben a Lélek által, és ugyanezen dologban vigyázván minden állhatatossággal és könyörgéssel minden szentekért,"));
husuperobject.biblemap.put("b49_6_19", new husuperobject(49,6,19,"Én érettem is, hogy adassék nékem szó számnak megnyitásakor, hogy bátorsággal ösmertessem meg az evangyéliom titkát,"));
husuperobject.biblemap.put("b49_6_20", new husuperobject(49,6,20,"A melyért követséget viselek lánczok között; hogy bátran szóljak arról, a miképen kell szólanom."));
husuperobject.biblemap.put("b49_6_21", new husuperobject(49,6,21,"Hogy pedig ti is megtudjátok dolgaimat, hogy mit cselekeszem, mindent megismertet veletek Tikhikus, a szeretett atyafi és hív szolga az Úrban;"));
husuperobject.biblemap.put("b49_6_22", new husuperobject(49,6,22,"Kit éppen azért küldöttem hozzátok, hogy megismerjétek a mi dolgainkat, és megvígasztalja a ti szíveteket."));
husuperobject.biblemap.put("b49_6_23", new husuperobject(49,6,23,"Békesség az atyafiaknak és szeretet hittel egybe az Atya Istentõl és az Úr Jézus Krisztustól!"));
husuperobject.biblemap.put("b49_6_24", new husuperobject(49,6,24,"A kegyelem legyen mindazokkal, a kik szeretik a mi Urunk Jézus Krisztust romlatlanságban. Ámen."));

}

}