package hu.bible;

public class hubible51 {

public hubible51() {

putmapbible51();

}

private void putmapbible51() {

husuperobject.biblemap.put("b51_1_1", new husuperobject(51,1,1,"Pál, Jézus Krisztusnak apostola Isten akaratjából, és Timótheus atyafi."));
husuperobject.biblemap.put("b51_1_2", new husuperobject(51,1,2,"A Kolosséban levõ szenteknek és hívõ atyafiaknak a Krisztusban: kegyelem néktek és békesség Istentõl, a mi Atyánktól, és az Úr Jézus Krisztustól."));
husuperobject.biblemap.put("b51_1_3", new husuperobject(51,1,3,"Hálát adunk az Istennek és a mi Urunk Jézus Krisztus Atyjának, mindenkor ti értetek könyörögvén,"));
husuperobject.biblemap.put("b51_1_4", new husuperobject(51,1,4,"Mivelhogy hallottuk a ti hiteteket a Krisztus Jézusban, és a szeretetet, a melylyel minden szentekhez vagytok,"));
husuperobject.biblemap.put("b51_1_5", new husuperobject(51,1,5,"A mennyekben néktek eltett reménységért, a melyet [már] elõbb hallottatok az igazság beszédében, mely az evangyéliom:"));
husuperobject.biblemap.put("b51_1_6", new husuperobject(51,1,6,"Mely eljutott hozzátok, miképen az egész világra is, és gyümölcsöt terem, úgy mint nálatok is a naptól fogva, melyen hallottátok és megismertétek az Isten kegyelmét igazán:"));
husuperobject.biblemap.put("b51_1_7", new husuperobject(51,1,7,"Úgy a hogy tanultátok is Epafrástól, a mi szerelmes szolgatársunktól, a ki hív szolgája ti érettetek a Krisztusnak,"));
husuperobject.biblemap.put("b51_1_8", new husuperobject(51,1,8,"A ki meg is jelentette nékünk a ti Lélekben való szereteteteket."));
husuperobject.biblemap.put("b51_1_9", new husuperobject(51,1,9,"Azért mi is, a mely naptól fogva [ezeket] hallottuk, nem szûnünk meg érettetek imádkozni, és kérni, hogy betöltessetek az [Isten ]akaratának megismerésével minden lelki bölcseségben és értelemben,"));
husuperobject.biblemap.put("b51_1_10", new husuperobject(51,1,10,"Hogy járjatok méltóan az Úrhoz, teljes tetszésére, minden jó cselekedettel gyümölcsöt teremvén és nevekedvén az Isten megismerésében;"));
husuperobject.biblemap.put("b51_1_11", new husuperobject(51,1,11,"Minden erõvel megerõsíttetvén az Õ dicsõségének hatalma szerint minden kitartásra és hosszútûrésre örömmel;"));
husuperobject.biblemap.put("b51_1_12", new husuperobject(51,1,12,"Hálákat adván az Atyának, ki alkalmasakká tett minket a szentek örökségében való részvételre a világosságban;"));
husuperobject.biblemap.put("b51_1_13", new husuperobject(51,1,13,"A ki megszabadított minket a sötétség hatalmából, és általvitt az Õ szerelmes Fiának országába;"));
husuperobject.biblemap.put("b51_1_14", new husuperobject(51,1,14,"Kiben van a mi váltságunk az Õ vére által, bûneinknek bocsánata;"));
husuperobject.biblemap.put("b51_1_15", new husuperobject(51,1,15,"A ki képe a láthatatlan Istennek, minden teremtménynek elõtte született;"));
husuperobject.biblemap.put("b51_1_16", new husuperobject(51,1,16,"Mert Õ benne teremtetett minden, a mi van a mennyekben és a földön, láthatók és láthatatlanok, akár királyi székek, akár uraságok, akár fejedelemségek, akár hatalmasságok; mindenek Õ általa és Õ reá nézve teremttettek;"));
husuperobject.biblemap.put("b51_1_17", new husuperobject(51,1,17,"És Õ elõbb volt mindennél, és minden Õ benne áll fenn."));
husuperobject.biblemap.put("b51_1_18", new husuperobject(51,1,18,"És Õ a feje a testnek, az egyháznak: a ki a kezdet, elsõszülött a halottak közül; hogy mindenekben Õ legyen az elsõ;"));
husuperobject.biblemap.put("b51_1_19", new husuperobject(51,1,19,"Mert tetszett [az Atyának,] hogy Õ benne lakozzék az egész teljesség;"));
husuperobject.biblemap.put("b51_1_20", new husuperobject(51,1,20,"És hogy Õ általa békéltessen meg mindent Magával, békességet szerezvén az Õ keresztjének vére által; Õ általa mindent, a mi csak van, akár a földön, akár a mennyekben."));
husuperobject.biblemap.put("b51_1_21", new husuperobject(51,1,21,"Titeket is, kik hajdan elidegenültek és ellenségek valátok gonosz cselekedetekben [gyönyörködõ] értelmetek miatt, most mégis megbékéltetett."));
husuperobject.biblemap.put("b51_1_22", new husuperobject(51,1,22,"Az Õ emberi testében a halál által, hogy mint szenteket, tisztákat és feddhetetleneket állasson titeket Õ maga elé:"));
husuperobject.biblemap.put("b51_1_23", new husuperobject(51,1,23,"Ha ugyan megmaradtok a hitben alaposan és erõsen, és el nem távoztok az evangyéliom reménységétõl, a melyet hallottatok, a mely hirdettetett minden teremtménynek az ég alatt; a melynek lettem én, Pál, szolgájává."));
husuperobject.biblemap.put("b51_1_24", new husuperobject(51,1,24,"Most örülök a ti érettetek való szenvedéseimnek, és a magam részérõl betöltöm a mi híja van a Krisztus szenvedéseinek az én testemben az Õ testéért, a mi az egyház;"));
husuperobject.biblemap.put("b51_1_25", new husuperobject(51,1,25,"A melynek lettem én szolgájává az Isten sáfársága szerint, a melyet nékem adott ti rátok nézve, hogy betöltsem az Isten ígéjét,"));
husuperobject.biblemap.put("b51_1_26", new husuperobject(51,1,26,"[Tudniillik] ama titkot, mely el vala rejtve õsidõk óta és nemzetségek óta, most pedig megjelentetett az Õ szenteinek,"));
husuperobject.biblemap.put("b51_1_27", new husuperobject(51,1,27,"A kikkel az Isten meg akarta ismertetni azt, hogy milyen nagy a pogányok között eme titok dicsõségének gazdagsága, az [tudniillik,] hogy a Krisztus ti köztetek van, a dicsõségnek ama reménysége:"));
husuperobject.biblemap.put("b51_1_28", new husuperobject(51,1,28,"A kit mi prédikálunk, intvén minden embert, és tanítván minden embert minden bölcseséggel, hogy minden embert tökéletesnek állassunk elõ a Krisztus Jézusban;"));
husuperobject.biblemap.put("b51_1_29", new husuperobject(51,1,29,"A mire igyekezem is, tusakodván az Õ ereje szerint, mely én bennem hatalmasan munkálkodik."));
husuperobject.biblemap.put("b51_2_1", new husuperobject(51,2,1,"Mert akarom, hogy tudtotokra legyen, hogy milyen nagy tusakodásom van ti érettetek, és azokért, kik Laodiczeában vannak, és mindazokért, a kik nem láttak engem személy szerint e testben;"));
husuperobject.biblemap.put("b51_2_2", new husuperobject(51,2,2,"Hogy vígasztalást vegyen az õ szívök, egybeköttetvén a szeretetben, és [hogy eljussanak] az értelem meggyõzõdésének teljes gazdagságára, az Isten és az Atya és a Krisztus ama titkának megismerésére,"));
husuperobject.biblemap.put("b51_2_3", new husuperobject(51,2,3,"A melyben van a bölcsességnek és ismeretnek minden kincse elrejtve."));
husuperobject.biblemap.put("b51_2_4", new husuperobject(51,2,4,"Ezt pedig azért mondom, hogy valaki titeket rá ne szedjen hitetõ beszéddel."));
husuperobject.biblemap.put("b51_2_5", new husuperobject(51,2,5,"Mert jóllehet testben távol vagyok tõletek, mindazáltal lélekben veletek vagyok, örülvén és látván ti köztetek a jó rendet és Krisztusba vetett hiteteknek erõsségét."));
husuperobject.biblemap.put("b51_2_6", new husuperobject(51,2,6,"Azért, a miképen vettétek a Krisztus Jézust, az Urat, [akképen ]járjatok Õ benne,"));
husuperobject.biblemap.put("b51_2_7", new husuperobject(51,2,7,"Meggyökerezvén és tovább épülvén Õ benne, és megerõsödvén a hitben, a miképen [arra] taníttattatok, bõvölködvén abban hálaadással."));
husuperobject.biblemap.put("b51_2_8", new husuperobject(51,2,8,"Meglássátok, hogy senki ne legyen, a ki bennetek zsákmányt vet a bölcselkedés és üres csalás által, mely emberek rendelése szerint, a világ elemi tanításai szerint, és nem a Krisztus szerint való:"));
husuperobject.biblemap.put("b51_2_9", new husuperobject(51,2,9,"Mert Õ benne lakozik az istenségnek egész teljessége testileg,"));
husuperobject.biblemap.put("b51_2_10", new husuperobject(51,2,10,"És [ti] Õ benne vagytok bételjesedve, a ki feje minden fejedelemségnek és hatalmasságnak;"));
husuperobject.biblemap.put("b51_2_11", new husuperobject(51,2,11,"A kiben körül is metéltettetek kéz nélkül való körülmetéléssel, levetkezvén az érzéki bûnök testét a Krisztus körülmetélésében;"));
husuperobject.biblemap.put("b51_2_12", new husuperobject(51,2,12,"Eltemettetvén Õ vele együtt a keresztségben, a kiben egyetemben fel is támasztattatok az Isten erejébe vetett hit által, a ki feltámasztá Õt a halálból."));
husuperobject.biblemap.put("b51_2_13", new husuperobject(51,2,13,"És titeket, kik holtak valátok a bûnökben és a ti testeteknek körülmetéletlenségében, megelevenített együtt Õ vele, megbocsátván minden bûnötöket,"));
husuperobject.biblemap.put("b51_2_14", new husuperobject(51,2,14,"Az által, hogy eltörölte a parancsolatokban ellenünk szóló kézírást, a mely ellenünkre volt nekünk, és azt eltette az útból, odaszegezvén azt a keresztfára;"));
husuperobject.biblemap.put("b51_2_15", new husuperobject(51,2,15,"Lefegyverezvén a fejedelemségeket és a hatalmasságokat, õket bátran mutogatta, diadalt vévén rajtok abban."));
husuperobject.biblemap.put("b51_2_16", new husuperobject(51,2,16,"Senki azért titeket meg ne ítéljen evésért, vagy ivásért, avagy ünnep, vagy újhold, vagy szombat dolgában:"));
husuperobject.biblemap.put("b51_2_17", new husuperobject(51,2,17,"Melyek [csak] árnyékai a következendõ dolgoknak, de a valóság a Krisztusé."));
husuperobject.biblemap.put("b51_2_18", new husuperobject(51,2,18,"Senki tõletek a pálmát el ne vegye, kedvét találván alázatoskodásban és az angyalok tisztelésében, a melyeket nem látott, olyakat tudakozván, ok nélkül felfuvalkodván az õ testének értelmével."));
husuperobject.biblemap.put("b51_2_19", new husuperobject(51,2,19,"És nem ragaszkodván a Fõhöz, a Kibõl az egész test, a kapcsok és kötelek által segedelmet vévén és egybeszerkesztetvén, nevekedik az Isten szerint való nevekedéssel."));
husuperobject.biblemap.put("b51_2_20", new husuperobject(51,2,20,"Ha meghalván a Krisztussal, [megszabadultatok] e világ elemi tanításaitól, miért terheltetitek magatokat, mintha e világban élõk volnátok, efféle rendelésekkel:"));
husuperobject.biblemap.put("b51_2_21", new husuperobject(51,2,21,"Ne fogd meg, meg se kóstold, még csak ne is illesd."));
husuperobject.biblemap.put("b51_2_22", new husuperobject(51,2,22,"(A mik mind a velök való élés által elfogyasztásra vannak rendelve), az emberek parancsolatai és tanításai szerint?"));
husuperobject.biblemap.put("b51_2_23", new husuperobject(51,2,23,"A melyek bölcsességnek látszanak ugyan a magaválasztotta istentiszteletben és alázatoskodásban és a test gyötrésében; de nincs bennök semmi becsülni való, [mivelhogy] a test hízlalására valók."));
husuperobject.biblemap.put("b51_3_1", new husuperobject(51,3,1,"Annakokáért ha feltámadtatok a Krisztussal, az odafelvalókat keressétek, a hol a Krisztus van, az Istennek jobbján ülvén,"));
husuperobject.biblemap.put("b51_3_2", new husuperobject(51,3,2,"Az odafelvalókkal törõdjetek, nem a földiekkel."));
husuperobject.biblemap.put("b51_3_3", new husuperobject(51,3,3,"Mert meghaltatok, és a ti éltetek el van rejtve együtt a Krisztussal az Istenben."));
husuperobject.biblemap.put("b51_3_4", new husuperobject(51,3,4,"Mikor a Krisztus, a mi életünk, megjelen, akkor majd ti is, Õ vele együtt, megjelentek dicsõségben."));
husuperobject.biblemap.put("b51_3_5", new husuperobject(51,3,5,"Öldököljétek meg azért a ti földi tagjaitokat, paráznaságot, tisztátalanságot, bujaságot, gonosz kívánságot és a fösvénységet, a mi bálványimádás;"));
husuperobject.biblemap.put("b51_3_6", new husuperobject(51,3,6,"Melyek miatt jõ az Isten haragja az engedetlenség fiaira;"));
husuperobject.biblemap.put("b51_3_7", new husuperobject(51,3,7,"Melyekben ti is jártatok régenten, mikor éltetek azokban."));
husuperobject.biblemap.put("b51_3_8", new husuperobject(51,3,8,"Most pedig vessétek el magatoktól ti is mindazokat; haragot, fölgerjedést, gonoszságot és szátokból a káromkodást és gyalázatos beszédet."));
husuperobject.biblemap.put("b51_3_9", new husuperobject(51,3,9,"Ne hazudjatok egymás ellen, mivelhogy levetkeztétek amaz ó embert, az õ cselekedeteivel együtt."));
husuperobject.biblemap.put("b51_3_10", new husuperobject(51,3,10,"És felöltöztétek amaz új [embert,] melynek újulása van Annak ábrázatja szerint való ismeretre, a ki teremtette azt:"));
husuperobject.biblemap.put("b51_3_11", new husuperobject(51,3,11,"A hol nincs többé görög és zsidó: körülmetélkedés és körülmetélkedetlenség, idegen, scithiai, szolga, szabad, hanem minden és mindenekben Krisztus."));
husuperobject.biblemap.put("b51_3_12", new husuperobject(51,3,12,"Öltözzetek föl azért mint az Istennek választottai, szentek és szeretettek, könyörületes szívet, jóságosságot, alázatosságot, szelídséget, hosszútûrést;"));
husuperobject.biblemap.put("b51_3_13", new husuperobject(51,3,13,"Elszenvedvén egymást és megbocsátván kölcsönösen egymásnak, ha valakinek valaki ellen panasza volna; miképen a Krisztus is megbocsátott néktek, akképen ti is;"));
husuperobject.biblemap.put("b51_3_14", new husuperobject(51,3,14,"Mindezeknek fölébe pedig [öltözzétek föl] a szeretetet, mint a mely a tökéletességnek kötele."));
husuperobject.biblemap.put("b51_3_15", new husuperobject(51,3,15,"És az Istennek békessége uralkodjék a ti szívetekben, amelyre el is hívattatok egy testben; és háládatosak legyetek."));
husuperobject.biblemap.put("b51_3_16", new husuperobject(51,3,16,"A Krisztusnak beszéde lakozzék ti bennetek gazdagon, minden bölcsességben; tanítván és intvén egymást zsoltárokkal, dícséretekkel, lelki énekekkel, hálával zengedezvén a ti szívetekben az Úrnak."));
husuperobject.biblemap.put("b51_3_17", new husuperobject(51,3,17,"És mindent, a mit csak cselekesztek szóval vagy tettel, mindent az Úr Jézusnak nevében [cselekedjetek,] hálát adván az Istennek és Atyának Õ általa."));
husuperobject.biblemap.put("b51_3_18", new husuperobject(51,3,18,"Ti asszonyok, engedelmeskedjetek a ti férjeteknek, a miképen illik az Úrban."));
husuperobject.biblemap.put("b51_3_19", new husuperobject(51,3,19,"Ti férfiak, szeressétek a ti feleségeteket, és ne legyetek irántok keserû kedvûek."));
husuperobject.biblemap.put("b51_3_20", new husuperobject(51,3,20,"Ti gyermekek, szót fogadjatok a ti szüleiteknek mindenben; mert ez kedves az Úrnak."));
husuperobject.biblemap.put("b51_3_21", new husuperobject(51,3,21,"Ti atyák, ne bosszantsátok a ti gyermekeiteket, hogy kétségbe ne essenek."));
husuperobject.biblemap.put("b51_3_22", new husuperobject(51,3,22,"Ti szolgák, szót fogadjatok mindenben a ti test szerint való uraitoknak, nem a szemnek szolgálván, mint a kik embereknek akarnak tetszeni, hanem szíveteknek egyenességében, félvén az Istent."));
husuperobject.biblemap.put("b51_3_23", new husuperobject(51,3,23,"És valamit tesztek, lélekbõl cselekedjétek, mint az Úrnak és nem embereknek;"));
husuperobject.biblemap.put("b51_3_24", new husuperobject(51,3,24,"Tudván, hogy ti az Úrtól veszitek az örökségnek jutalmát: mert az Úr Krisztusnak szolgáltok."));
husuperobject.biblemap.put("b51_3_25", new husuperobject(51,3,25,"A ki pedig igazságtalanságot cselekszik, jutalmát veszi igazságtalanságának; és nincsen személyválogatás."));
husuperobject.biblemap.put("b51_4_1", new husuperobject(51,4,1,"Ti urak, a mi igazságos és méltányos, a ti szolgáitoknak megadjátok, tudván, hogy néktek is van Uratok mennyekben."));
husuperobject.biblemap.put("b51_4_2", new husuperobject(51,4,2,"Az imádságban állhatatosak legyetek, vigyázván abban hálaadással;"));
husuperobject.biblemap.put("b51_4_3", new husuperobject(51,4,3,"Imádkozván egyszersmind mi érettünk is, hogy az Isten nyissa meg elõttünk az íge ajtaját, hogy szólhassuk a Krisztus titkát, a melyért fogoly is vagyok;"));
husuperobject.biblemap.put("b51_4_4", new husuperobject(51,4,4,"Hogy nyilvánvalóvá tegyem azt úgy, a mint nékem szólnom kell."));
husuperobject.biblemap.put("b51_4_5", new husuperobject(51,4,5,"Bölcsen viseljétek magatokat a kívül valók irányában, a jó alkalmatosságot áron is megváltván."));
husuperobject.biblemap.put("b51_4_6", new husuperobject(51,4,6,"A ti beszédetek mindenkor kellemetes [legyen,] sóval fûszerezett; hogy tudjátok, hogy mimódon kell néktek kinek-kinek megfelelnetek."));
husuperobject.biblemap.put("b51_4_7", new husuperobject(51,4,7,"Minden én dolgaimat megismerteti veletek Tikhikus, a szeretett atyafi és hív szolga és szolgatárs az Úrban;"));
husuperobject.biblemap.put("b51_4_8", new husuperobject(51,4,8,"A kit épen a végett küldtem hozzátok, hogy megismerje a ti dolgaitokat és megvígasztalja a ti szíveteket,"));
husuperobject.biblemap.put("b51_4_9", new husuperobject(51,4,9,"Onézimussal együtt, a hû és szeretett atyafival, ki ti közületek való; minden itt való dolgot megismertetnek õk veletek."));
husuperobject.biblemap.put("b51_4_10", new husuperobject(51,4,10,"Köszönt titeket Aristárkhus, az én fogolytársam, és Márk, a Barnabás unokatestvére, (ki felõl parancsolatokat vettetek: ha hozzátok megy, fogadjátok õt szívesen),"));
husuperobject.biblemap.put("b51_4_11", new husuperobject(51,4,11,"És Jézus, kit Justusnak [is] hívnak, kik a zsidók közül valók: csak ezek azok a munkatársaim az Isten országában, a kik nékem vígasztalásomra voltak."));
husuperobject.biblemap.put("b51_4_12", new husuperobject(51,4,12,"Köszönt titeket Epafrás, ki ti közületek való, Krisztusnak szolgája, mindenkor tusakodván ti érettetek imádságaiban, hogy megállhassatok tökéletesen és teljes meggyõzõdéssel az Istennek minden akaratjában."));
husuperobject.biblemap.put("b51_4_13", new husuperobject(51,4,13,"Mert bizonyságot teszek õ felõle, hogy sokat fárad érettetek és azokért, kik Laodiczeában és Jerápolisban vannak."));
husuperobject.biblemap.put("b51_4_14", new husuperobject(51,4,14,"Köszönt titeket Lukács, ama szeretett orvos, és Démás."));
husuperobject.biblemap.put("b51_4_15", new husuperobject(51,4,15,"Köszöntsétek az atyafiakat, kik Laodiczeában vannak, és Nimfást és a gyülekezetet, mely az õ házánál van."));
husuperobject.biblemap.put("b51_4_16", new husuperobject(51,4,16,"És mikor felolvastatik nálatok e levél, cselekedjétek meg, hogy a laodiczeaiak gyülekezetében is felolvastassék, és hogy a Laodiczeából [átveendõ] levelet ti is felolvassátok."));
husuperobject.biblemap.put("b51_4_17", new husuperobject(51,4,17,"És mondjátok meg Arkhippusnak: Vigyázz a szolgálatra, melyre vállalkoztál az Úrban, hogy azt betöltsed!"));
husuperobject.biblemap.put("b51_4_18", new husuperobject(51,4,18,"A köszöntés a saját kezemmel, a Páléval, [van írva]. Emlékezzetek meg az én bilincseimrõl! A kegyelem veletek. Ámen."));

}

}