package hu.bible;

public class hubible52 {

public hubible52() {

putmapbible52();

}

private void putmapbible52() {

husuperobject.biblemap.put("b52_1_1", new husuperobject(52,1,1,"Pál, Silvánus és Timótheus a Thessalonikabeliek gyülekezetének, [a] [mely] [van] az Atya Istenben és az Úr Jézus Krisztusban: kegyelem néktek és békesség Istentõl, a mi Atyánktól, és az Úr Jézus Krisztustól."));
husuperobject.biblemap.put("b52_1_2", new husuperobject(52,1,2,"Hálát adunk az Istennek mindenkor mindnyájatokért, emlékezvén rólatok a mi imádságainkban;"));
husuperobject.biblemap.put("b52_1_3", new husuperobject(52,1,3,"Szüntelenül emlegetve a ti hitetek munkáját, és a ti szeretetetek fáradozását, és a mi Urunk Jézus Krisztus felõl való reménységeteknek állhatatosságát, az Isten elõtt, a mi Atyánk elõtt:"));
husuperobject.biblemap.put("b52_1_4", new husuperobject(52,1,4,"Tudván, Istentõl szeretett atyámfiai, hogy ti ki vagytok választva;"));
husuperobject.biblemap.put("b52_1_5", new husuperobject(52,1,5,"Hogy a mi evangyéliomunk ti nálatok nem áll csak szóban, hanem [isteni] erõkben is, Szent Lélekben is, sok bizodalomban is; a miképen tudjátok, hogy milyenek voltunk közöttetek ti érettetek."));
husuperobject.biblemap.put("b52_1_6", new husuperobject(52,1,6,"És ti a mi követõinkké lettetek és az Úréi, befogadván az ígét sokféle szorongattatás között, Szent Lélek örömével;"));
husuperobject.biblemap.put("b52_1_7", new husuperobject(52,1,7,"Úgy hogy példaképekké lettetek Maczedóniában és Akhájában minden hívõre nézve."));
husuperobject.biblemap.put("b52_1_8", new husuperobject(52,1,8,"Mert nemcsak Maczedóniában és Akhájában zendült ki tõletek az Úr beszéde, hanem minden helyen is híre terjedt a ti Istenben vetett hiteteknek, annyira, hogy szükségtelen arról valamit szólnunk."));
husuperobject.biblemap.put("b52_1_9", new husuperobject(52,1,9,"Mert azok magok hirdetik felõlünk, milyen volt a mi hozzátok való menetelünk, és miként tértetek meg az Istenhez a bálványoktól, hogy az élõ és igaz Istennek szolgáljatok,"));
husuperobject.biblemap.put("b52_1_10", new husuperobject(52,1,10,"És várjátok az Õ Fiát az égbõl, a kit feltámasztott a halálból, a Jézust, a ki megszabadít minket amaz eljövendõ haragtól."));
husuperobject.biblemap.put("b52_2_1", new husuperobject(52,2,1,"Mert magatok tudjátok, atyámfiai, hogy a mi ti hozzátok való menetelünk nem volt hiábavaló;"));
husuperobject.biblemap.put("b52_2_2", new husuperobject(52,2,2,"Sõt inkább, noha elébb már háborúságot és bosszúságot szenvedtünk volt Filippiben, a mint tudjátok, volt bátorságunk a mi Istenünkben, hogy közöttetek [is] hirdessük az Isten evangyéliomát sok tusakodással."));
husuperobject.biblemap.put("b52_2_3", new husuperobject(52,2,3,"Mert a mi buzdításunk nem hitetésbõl [van,] sem nem tisztátalanságból, sem nem álnokságból:"));
husuperobject.biblemap.put("b52_2_4", new husuperobject(52,2,4,"Hanem a miképen az Isten méltatott minket arra, hogy reánk bízza az evangyéliomot, akképen szólunk; nem úgy, hogy embereknek tessünk hanem az Istennek, a ki megvizsgálja a mi szívünket."));
husuperobject.biblemap.put("b52_2_5", new husuperobject(52,2,5,"Mert sem hízelkedõ beszéddel, a mint tudjátok, sem telhetetlenség színében nem léptünk fel soha, Isten a bizonyság;"));
husuperobject.biblemap.put("b52_2_6", new husuperobject(52,2,6,"Sem emberektõl való dicsõítést nem kerestünk, sem tõletek, sem másoktól, holott terhetekre lehettünk volna, mint Krisztus apostolai."));
husuperobject.biblemap.put("b52_2_7", new husuperobject(52,2,7,"De szívélyesek valánk ti közöttetek, a miként a dajka dajkálgatja az õ gyermekeit."));
husuperobject.biblemap.put("b52_2_8", new husuperobject(52,2,8,"Így felbuzdulva irántatok, készek valánk közleni veletek nemcsak az Isten evangyéliomát, hanem a mi magunk lelkét is, mivelhogy szeretteinkké lettetek nékünk."));
husuperobject.biblemap.put("b52_2_9", new husuperobject(52,2,9,"Emlékez[het]tek ugyanis atyámfiai a mi fáradozásunkra és bajlakodásunkra: mert éjjel-nappal munkálkodva hirdettük néktek az Isten evangyéliomát, csakhogy senkit meg ne terheljünk közületek."));
husuperobject.biblemap.put("b52_2_10", new husuperobject(52,2,10,"Ti [vagytok] bizonyságok és az Isten, milyen szentül, igazán és feddhetetlenül éltünk elõttetek, a kik hisztek."));
husuperobject.biblemap.put("b52_2_11", new husuperobject(52,2,11,"Valamint tudjátok, hogy miként atya az õ gyermekeit, [úgy] intettünk és buzdítgattunk egyenként mindnyájatokat."));
husuperobject.biblemap.put("b52_2_12", new husuperobject(52,2,12,"És kérve kértünk, hogy Istenhez méltóan viseljétek magatokat, a ki az õ országába és dicsõségébe hív titeket."));
husuperobject.biblemap.put("b52_2_13", new husuperobject(52,2,13,"Ugyanazért mi is hálát adunk az Istennek szüntelenül, hogy ti befogadván az Istennek általunk hirdetett beszédét, nem úgy fogadtátok, mint emberek beszédét, hanem mint Isten beszédét (a minthogy valósággal az [is]), a mely munkálkodik is ti bennetek, a kik hisztek."));
husuperobject.biblemap.put("b52_2_14", new husuperobject(52,2,14,"Mert ti, atyámfiai, követõi lettetek az Isten gyülekezeteinek, a melyek Júdeában vannak a Krisztus Jézusban, mivelhogy ugyanúgy szenvedtetek ti is a saját honfitársaitoktól, miként azok is a zsidóktól,"));
husuperobject.biblemap.put("b52_2_15", new husuperobject(52,2,15,"A kik megölték az Úr Jézust is és a saját prófétáikat, és minket [is] üldöznek, és az Istennek nem tetszenek, és minden embernek ellenségei;"));
husuperobject.biblemap.put("b52_2_16", new husuperobject(52,2,16,"A kik megtiltják nékünk, hogy a pogányoknak ne prédikáljunk hogy üdvözüljenek; hogy mindenkor betöltsék bûneiket; de végre utólérte õket az [Isten] haragja."));
husuperobject.biblemap.put("b52_2_17", new husuperobject(52,2,17,"Mi pedig, atyámfiai, a mint elszakasztatánk tõletek egy kevés ideig, arczra, nem szívre nézve, annál buzgóságosabban, nagy kívánsággal igyekeztünk, hogy szemtõl-szemben láthassunk titeket."));
husuperobject.biblemap.put("b52_2_18", new husuperobject(52,2,18,"Azért menni [is] akartunk hozzátok, kiváltképen én Pál, egyszer is, kétszer is, de megakadályozott minket a Sátán."));
husuperobject.biblemap.put("b52_2_19", new husuperobject(52,2,19,"Mert kicsoda a mi reménységünk, örömünk és dicsekedésünk koronája? Avagy nem azok [lesztek-]é ti is a mi Urunk Jézus Krisztus elõtt az õ eljövetelekor?"));
husuperobject.biblemap.put("b52_2_20", new husuperobject(52,2,20,"Bizony ti vagytok a mi dicsõségünk és örömünk."));
husuperobject.biblemap.put("b52_3_1", new husuperobject(52,3,1,"Annakokáért, mivelhogy tovább már el nem tûrhetõk, jónak ítélénk, hogy magunk maradjunk Athénében,"));
husuperobject.biblemap.put("b52_3_2", new husuperobject(52,3,2,"És elküldöttük Timótheust, a mi atyánkfiát és Istennek szolgáját és munkatársunkat a Krisztus evangyéliomának [hirdetésében,] hogy erõsítsen titeket és intsen titeket a ti hitetek felõl;"));
husuperobject.biblemap.put("b52_3_3", new husuperobject(52,3,3,"Hogy senki meg ne tántorodjék ama szorongattatások között; mert ti magatok tudjátok, hogy mi arra rendeltettünk."));
husuperobject.biblemap.put("b52_3_4", new husuperobject(52,3,4,"Mert mikor közöttetek valánk is, elõre megmondtuk néktek, hogy szorongattatásnak leszünk kitéve; a mint meg is történt, és tudjátok."));
husuperobject.biblemap.put("b52_3_5", new husuperobject(52,3,5,"Annakokáért én is, mivelhogy tovább már nem tûrhetém, elküldék, hogy megismerjem a ti hiteteket, ha nem kísértett-é meg valami módon titeket a kísértõ, és nem lett-é hiábavaló a mi fáradságunk?"));
husuperobject.biblemap.put("b52_3_6", new husuperobject(52,3,6,"Most pedig, a mikor megérkezett hozzánk Timótheus ti tõletek, és örömhírt hozott nékünk a ti hitetek és szeretetetek felõl, és arról, hogy jó emlékezéssel vagytok irántunk, mindenkor kívánván látni minket, miképen mi is titeket;"));
husuperobject.biblemap.put("b52_3_7", new husuperobject(52,3,7,"Ezáltal megvígasztalódtunk reátok nézve, atyámfiai, minden mi szorongattatásunk és szükségünk mellett is, a ti hitetek által:"));
husuperobject.biblemap.put("b52_3_8", new husuperobject(52,3,8,"Mert szinte megelevenedünk, ha ti erõsek vagytok az Úrban."));
husuperobject.biblemap.put("b52_3_9", new husuperobject(52,3,9,"Mert milyen hálával is fizethetünk az Istennek ti érettetek, mindazért az örömért, a melylyel örvendezünk miattatok a mi Istenünk elõtt?!"));
husuperobject.biblemap.put("b52_3_10", new husuperobject(52,3,10,"Mikor éjjel-nappal nagy buzgón esedezünk, hogy megláthassuk a ti orczátokat, és kipótolhassuk a ti hitetek hiányait."));
husuperobject.biblemap.put("b52_3_11", new husuperobject(52,3,11,"Maga pedig az Isten és a mi Atyánk, és a mi Urunk a Jézus Krisztus egyengesse meg a mi útunkat ti hozzátok!"));
husuperobject.biblemap.put("b52_3_12", new husuperobject(52,3,12,"Titeket pedig gyarapítson az Úr és tegyen bõségesekké az egymás iránt és mindenki iránt való szeretetben, a milyenek vagyunk mi is ti irántatok;"));
husuperobject.biblemap.put("b52_3_13", new husuperobject(52,3,13,"Hogy erõsekké tegye a ti szíveteket, feddhetetlenekké a szentségben, a mi Istenünk és Atyánk elõtt, a mikor eljõ a mi Urunk Jézus Krisztus minden õ szenteivel egyetemben."));
husuperobject.biblemap.put("b52_4_1", new husuperobject(52,4,1,"Továbbá pedig kérünk titeket, atyámfiai, és intünk az Úr Jézusban, hogy a szerint, a mint tõlünk tanultátok, mimódon kell forgolódnotok és Istennek tetszenetek: mindinkább gyarapodjatok."));
husuperobject.biblemap.put("b52_4_2", new husuperobject(52,4,2,"Mert tudjátok, milyen parancsolatokat adtunk néktek az Úr Jézus által."));
husuperobject.biblemap.put("b52_4_3", new husuperobject(52,4,3,"Mert ez az Isten akaratja, a ti szentté lételetek, hogy magatokat a paráznaságtól megtartóztassátok;"));
husuperobject.biblemap.put("b52_4_4", new husuperobject(52,4,4,"Hogy mindenitek szentségben és tisztességben tudja bírni a maga edényét,"));
husuperobject.biblemap.put("b52_4_5", new husuperobject(52,4,5,"Nem kívánság gerjedelmével, mint a pogányok, a kik nem ismerik az Istent;"));
husuperobject.biblemap.put("b52_4_6", new husuperobject(52,4,6,"Hogy senki túl ne lépjen és meg ne károsítsa valamely dologban az õ atyjafiát: mert bosszút áll az Úr mindezekért, a mint elébb is mondottuk néktek és bizonyságot tettünk."));
husuperobject.biblemap.put("b52_4_7", new husuperobject(52,4,7,"Mert nem tisztátalanságra, hanem szentségre hívott el minket az Isten."));
husuperobject.biblemap.put("b52_4_8", new husuperobject(52,4,8,"A ki azért megveti [ezeket,] nem embert vet meg, hanem az Istent, a ki Szent Lelkét is közlé velünk."));
husuperobject.biblemap.put("b52_4_9", new husuperobject(52,4,9,"Az atyafiúi szeretetrõl pedig nem [is] szükség írnom néktek: mert titeket Isten maga tanított meg arra, hogy egymást szeressétek;"));
husuperobject.biblemap.put("b52_4_10", new husuperobject(52,4,10,"Sõt gyakoroljátok is azt mindamaz atyafiak iránt, a kik egész Maczedóniában vannak. Kérünk azonban titeket atyámfiai, hogy mindinkább gyarapodjatok;"));
husuperobject.biblemap.put("b52_4_11", new husuperobject(52,4,11,"És becsületbeli dolognak tartsátok, hogy csendes életet folytassatok, saját dolgaitoknak utána lássatok, és tulajdon kezeitekkel munkálkodjatok, a miként rendeltük néktek;"));
husuperobject.biblemap.put("b52_4_12", new husuperobject(52,4,12,"Hogy a kívülvalók iránt tisztességesen viselkedjetek, és semmi szükséget ne érezzetek."));
husuperobject.biblemap.put("b52_4_13", new husuperobject(52,4,13,"Nem akarom továbbá, atyámfiai, hogy tudatlanságban legyetek azok felõl, a kik elaludtak, hogy ne bánkódjatok, mint a többiek, a kiknek nincsen reménységök."));
husuperobject.biblemap.put("b52_4_14", new husuperobject(52,4,14,"Mert ha hisszük, hogy Jézus meghalt és feltámadott, azonképen az Isten is elõhozza azokat, a kik elaludtak, a Jézus által õ vele együtt."));
husuperobject.biblemap.put("b52_4_15", new husuperobject(52,4,15,"Mert ezt mondjuk néktek az Úr szavával, hogy mi, a kik élünk, a kik megmaradunk az Úr eljöveteléig, épen nem elõzzük meg azokat, a kik elaludtak."));
husuperobject.biblemap.put("b52_4_16", new husuperobject(52,4,16,"Mert maga az Úr riadóval, arkangyal szózatával és isteni harsonával leszáll az égbõl: és feltámadnak elõször a kik meghaltak volt a Krisztusban;"));
husuperobject.biblemap.put("b52_4_17", new husuperobject(52,4,17,"Azután mi, a kik élünk, a kik megmaradunk, elragadtatunk azokkal együtt a felhõkön az Úr elébe a levegõbe; és ekképen mindenkor az Úrral leszünk."));
husuperobject.biblemap.put("b52_4_18", new husuperobject(52,4,18,"Annakokáért vígasztaljátok egymást e beszédekkel."));
husuperobject.biblemap.put("b52_5_1", new husuperobject(52,5,1,"Az idõkrõl és idõszakokról pedig, atyámfiai, nem szükség, hogy írjak néktek;"));
husuperobject.biblemap.put("b52_5_2", new husuperobject(52,5,2,"Mert igen jól tudjátok ti magatok, hogy az Úrnak napja úgy jõ el, mint a tolvaj éjjel."));
husuperobject.biblemap.put("b52_5_3", new husuperobject(52,5,3,"Mert a mikor ezt mondják: Békesség és biztonság, akkor hirtelen veszedelem jön rájok, mint a szülési fájdalom a terhes asszonyra; és semmiképen meg nem menekednek."));
husuperobject.biblemap.put("b52_5_4", new husuperobject(52,5,4,"De ti, atyámfiai, nem vagytok sötétségben, hogy [az] a nap tolvaj módra lep[het]ne meg titeket."));
husuperobject.biblemap.put("b52_5_5", new husuperobject(52,5,5,"Ti mindnyájan világosság fiai vagytok és nappal fiai; nem vagyunk az éjszakáé, sem a sötétségé!"));
husuperobject.biblemap.put("b52_5_6", new husuperobject(52,5,6,"Ne is aludjunk azért, mint egyebek, hanem legyünk éberek és józanok."));
husuperobject.biblemap.put("b52_5_7", new husuperobject(52,5,7,"Mert a kik alusznak, éjjel alusznak; és a kik részegek, éjjel részegednek meg."));
husuperobject.biblemap.put("b52_5_8", new husuperobject(52,5,8,"Mi azonban, a kik nappaliak vagyunk, legyünk éberek, felöltözvén a hitnek és szeretetnek mellvasába, és sisak gyanánt az üdvösségnek reménységébe."));
husuperobject.biblemap.put("b52_5_9", new husuperobject(52,5,9,"Mert nem haragra rendelt minket az Isten, hanem arra, hogy üdvösséget szerezzünk a mi Urunk Jézus Krisztus által,"));
husuperobject.biblemap.put("b52_5_10", new husuperobject(52,5,10,"A ki meghalt érettünk, hogy akár ébren vagyunk, akár aluszunk, együtt éljünk õ vele."));
husuperobject.biblemap.put("b52_5_11", new husuperobject(52,5,11,"Vígasztaljátok azért egymást, és építse egyik a másikat, a miképen cselekeszitek is."));
husuperobject.biblemap.put("b52_5_12", new husuperobject(52,5,12,"Kérünk továbbá titeket atyámfiai, hogy becsüljétek azokat, a kik fáradoznak közöttetek, és elõljáróitok az Úrban, és intenek titeket;"));
husuperobject.biblemap.put("b52_5_13", new husuperobject(52,5,13,"És az õ munkájokért viseltessetek irántok megkülönböztetett szeretettel. Egymással békességben éljetek."));
husuperobject.biblemap.put("b52_5_14", new husuperobject(52,5,14,"Kérünk továbbá titeket, atyámfiai, intsétek a rendetleneket, bátorítsátok a félelmes szívûeket, gyámolítsátok az erõteleneket, türelmesek legyetek mindenki iránt."));
husuperobject.biblemap.put("b52_5_15", new husuperobject(52,5,15,"Vigyázzatok, hogy senki senkinek rosszért rosszal ne fizessen; hanem mindenkor jóra törekedjetek úgy egymás iránt, mint mindenki iránt."));
husuperobject.biblemap.put("b52_5_16", new husuperobject(52,5,16,"Mindenkor örüljetek."));
husuperobject.biblemap.put("b52_5_17", new husuperobject(52,5,17,"Szüntelen imádkozzatok."));
husuperobject.biblemap.put("b52_5_18", new husuperobject(52,5,18,"Mindenben hálákat adjatok; mert ez az Isten akarata a Krisztus Jézus által ti hozzátok."));
husuperobject.biblemap.put("b52_5_19", new husuperobject(52,5,19,"A Lelket meg ne oltsátok."));
husuperobject.biblemap.put("b52_5_20", new husuperobject(52,5,20,"A prófétálást meg ne vessétek,"));
husuperobject.biblemap.put("b52_5_21", new husuperobject(52,5,21,"Mindent megpróbáljatok; a mi jó, azt megtartsátok!"));
husuperobject.biblemap.put("b52_5_22", new husuperobject(52,5,22,"Mindentõl, a mi gonosznak látszik, õrizkedjetek!"));
husuperobject.biblemap.put("b52_5_23", new husuperobject(52,5,23,"Maga pedig a békességnek Istene szenteljen meg titeket mindenestõl; és a ti egész valótok, mind lelketek, mind testetek feddhetetlenül õriztessék meg a mi Urunk Jézus Krisztus eljövetelére."));
husuperobject.biblemap.put("b52_5_24", new husuperobject(52,5,24,"Hû az, a ki elhivott titeket és õ meg is cselekszi [azt.]"));
husuperobject.biblemap.put("b52_5_25", new husuperobject(52,5,25,"Atyámfiai, imádkozzatok érettünk."));
husuperobject.biblemap.put("b52_5_26", new husuperobject(52,5,26,"Köszöntsétek az összes atyafiakat szent csókolással."));
husuperobject.biblemap.put("b52_5_27", new husuperobject(52,5,27,"Kényszerítlek titeket az Úrra, hogy olvastassék fel e levél minden szent atyafi elõtt."));
husuperobject.biblemap.put("b52_5_28", new husuperobject(52,5,28,"A mi Urunk Jézus Krisztusnak kegyelme veletek! Ámen."));

}

}