package hu.bible;

public class hubible53 {

public hubible53() {

putmapbible53();

}

private void putmapbible53() {

husuperobject.biblemap.put("b53_1_1", new husuperobject(53,1,1,"Pál és Silvánus és Timótheus a Thessalonikabeli gyülekezetnek, Istenben, a mi Atyánkban, és az Úr Jézus Krisztusban:"));
husuperobject.biblemap.put("b53_1_2", new husuperobject(53,1,2,"Kegyelem néktek és békesség Istentõl, a mi Atyánktól, és az Úr Jézus Krisztustól."));
husuperobject.biblemap.put("b53_1_3", new husuperobject(53,1,3,"Mindenkor hálaadással tartozunk az Istennek, atyámfiai, ti érettetek, a miképen méltó [is,] mivelhogy felettébb megnövekedék a ti hitetek, és mindnyájatokban bõvölködik az egymáshoz való szeretet;"));
husuperobject.biblemap.put("b53_1_4", new husuperobject(53,1,4,"Annyira, hogy mi magunk dicsekszünk veletek az Isten gyülekezeteiben, a ti kitartástok és hitetek felõl, minden ti üldöztetéstek és szorongattatástok között, a melyeket szenvedtek:"));
husuperobject.biblemap.put("b53_1_5", new husuperobject(53,1,5,"Bizonyságául az Isten igazságos ítéletének; hogy méltóknak ítéltessetek az Isten országára, a melyért szenvedtek is."));
husuperobject.biblemap.put("b53_1_6", new husuperobject(53,1,6,"Mert igazságos dolog az az Isten elõtt, hogy szorongattatással fizessen azoknak, a kik titeket szorongatnak."));
husuperobject.biblemap.put("b53_1_7", new husuperobject(53,1,7,"Néktek pedig, a kik szorongattattok, nyugodalommal mivelünk együtt, a mikor megjelenik az Úr Jézus az égbõl az õ hatalmának angyalaival."));
husuperobject.biblemap.put("b53_1_8", new husuperobject(53,1,8,"Tûznek lángjában, ki bosszút áll azokon, a kik nem ismerik az Istent, és a kik nem engedelmeskednek a mi Urunk Jézus Krisztus evangyéliomának."));
husuperobject.biblemap.put("b53_1_9", new husuperobject(53,1,9,"A kik meg fognak lakolni örök veszedelemmel az Úr ábrázatától, és az õ hatalmának dicsõségétõl,"));
husuperobject.biblemap.put("b53_1_10", new husuperobject(53,1,10,"A mikor eljõ majd, hogy megdicsõíttessék az õ szenteiben, és csodáltassék mindazokban, a kik hisznek (mivelhogy a mi tanúbizonyságunknak hitele volt ti nálatok) ama napon."));
husuperobject.biblemap.put("b53_1_11", new husuperobject(53,1,11,"Mivégbõl imádkozunk is mindenkor ti érettetek, hogy a mi Istenünk méltóknak tartson titeket az elhívásra, és töltsön be titeket a jóban való teljes gyönyörûséggel, és a hitnek hathatós munkálásával."));
husuperobject.biblemap.put("b53_1_12", new husuperobject(53,1,12,"Hogy dicsõíttessék meg a mi Urunk Jézus Krisztusnak neve ti bennetek, és ti [is] õ benne, a mi Istenünknek és az Úr Jézus Krisztusnak kegyelmébõl."));
husuperobject.biblemap.put("b53_2_1", new husuperobject(53,2,1,"Kérünk pedig titeket, atyámfiai, a mi Urunk Jézus Krisztus eljövetelére és a mi õ hozzá leendõ egybegyûlésünkre nézve,"));
husuperobject.biblemap.put("b53_2_2", new husuperobject(53,2,2,"Hogy ne tántoríttassatok el egyhamar a ti értelmetektõl, se ne háboríttassatok meg, se lélek által, se beszéd által, se nékünk [tulajdonított ]levél által, mintha itt volna már a Krisztusnak ama napja."));
husuperobject.biblemap.put("b53_2_3", new husuperobject(53,2,3,"Ne csaljon meg titeket senki semmiképen. Mert nem [jön az el addig,] mígnem bekövetkezik elébb a szakadás, és megjelenik a bûn embere, a veszedelemnek fia,"));
husuperobject.biblemap.put("b53_2_4", new husuperobject(53,2,4,"A ki ellene veti és fölébe emeli magát mindannak, a mi Istennek vagy istentiszteletre méltónak mondatik, annyira, hogy maga ül be mint Isten az Isten templomába, Isten gyanánt mutogatván magát."));
husuperobject.biblemap.put("b53_2_5", new husuperobject(53,2,5,"Nem emlékeztek-é, hogy megmondtam néktek ezeket, a mikor még ti nálatok valék?"));
husuperobject.biblemap.put("b53_2_6", new husuperobject(53,2,6,"És most tudjátok, mi tartja vissza, a miért [csak] a maga idejében fog az megjelenni."));
husuperobject.biblemap.put("b53_2_7", new husuperobject(53,2,7,"Mûködik ugyan már a törvényszegés titkos [bûne:] csakhogy annak, a ki [azt] még most visszatartja, félre kell az útból tolatnia."));
husuperobject.biblemap.put("b53_2_8", new husuperobject(53,2,8,"És akkor fog megjelenni a törvénytaposó, a kit megemészt az Úr az õ szájának lehelletével, és megsemmisít az õ megjelenésének feltûnésével;"));
husuperobject.biblemap.put("b53_2_9", new husuperobject(53,2,9,"A kinek eljövetele a Sátán ereje által van, a hazugságnak minden hatalmával, jeleivel és csodáival,"));
husuperobject.biblemap.put("b53_2_10", new husuperobject(53,2,10,"És a gonoszságnak minden csalárdságával azok között, a kik elvesznek; mivelhogy nem fogadták be az igazságnak szeretetét az õ idvességökre."));
husuperobject.biblemap.put("b53_2_11", new husuperobject(53,2,11,"És azért bocsátja reájok Isten a tévelygés erejét, hogy higyjenek a hazugságnak;"));
husuperobject.biblemap.put("b53_2_12", new husuperobject(53,2,12,"Hogy kárhoztattassanak mindazok, a kik nem hittek az igazságnak, hanem gyönyörködtek az igazságtalanságban."));
husuperobject.biblemap.put("b53_2_13", new husuperobject(53,2,13,"Mi pedig mindenkor hálaadással tartozunk az Istennek ti érettetek atyámfiai, a kiket szeret az Úr, hogy kezdettõl fogva kiválasztott titeket Isten az üdvösségre, a Lélek szentelésében és az igazság hitében;"));
husuperobject.biblemap.put("b53_2_14", new husuperobject(53,2,14,"A mire elhívott titeket a mi evangyéliomunk által, a mi Urunk Jézus Krisztus dicsõségének elvételére."));
husuperobject.biblemap.put("b53_2_15", new husuperobject(53,2,15,"Miért is atyámfiai, legyetek állhatatosak és tartsátok meg a tudományt, a melyre akár beszédünk, akár levelünk által taníttattatok."));
husuperobject.biblemap.put("b53_2_16", new husuperobject(53,2,16,"Maga pedig a mi Urunk Jézus Krisztus, és az Isten a mi Atyánk, a ki szeretett minket és kegyelmébõl örök vígasztalással és jó reménységgel ajándékozott meg,"));
husuperobject.biblemap.put("b53_2_17", new husuperobject(53,2,17,"Vígasztalja meg a ti szíveteket, és erõsítsen meg titeket minden tudományban és jó cselekedetben."));
husuperobject.biblemap.put("b53_3_1", new husuperobject(53,3,1,"Végezetre imádkozzatok értünk atyámfiai, hogy az Úrnak beszéde terjedjen és dicsõíttessék, a miként ti köztetek is."));
husuperobject.biblemap.put("b53_3_2", new husuperobject(53,3,2,"És hogy meneküljünk meg az alkalmatlan és gonosz emberektõl. Mert nem mindenkié a hit!"));
husuperobject.biblemap.put("b53_3_3", new husuperobject(53,3,3,"De hû az Úr, a ki megerõsít titeket és megõriz a gonosztól."));
husuperobject.biblemap.put("b53_3_4", new husuperobject(53,3,4,"Bízunk is az Úrban reátok nézve, hogy megteszitek és meg is fogjátok tenni azokat, a miket parancsolunk."));
husuperobject.biblemap.put("b53_3_5", new husuperobject(53,3,5,"Az Úr pedig igazgassa a ti szíveteket az Isten iránt való szeretetre, és Krisztus iránt való állhatatosságra."));
husuperobject.biblemap.put("b53_3_6", new husuperobject(53,3,6,"A mi Urunk Jézus Krisztus nevében pedig, rendeljük néktek, atyámfiai, hogy vonjátok el magatokat minden atyafitól, a ki rendetlenül él, és nem ama utasítás szerint, a melyet mi tõlünk kapott."));
husuperobject.biblemap.put("b53_3_7", new husuperobject(53,3,7,"Magatok is tudjátok, mimódon kell minket követni; mert nem viseltük magunkat közöttetek rendetlenül."));
husuperobject.biblemap.put("b53_3_8", new husuperobject(53,3,8,"Sem ingyen kenyeret nem ettünk senkinél, hanem munkával és fáradsággal, éjjel-nappal dolgozva, hogy közületek senkinek se legyünk terhére."));
husuperobject.biblemap.put("b53_3_9", new husuperobject(53,3,9,"Nem azért, mintha nem volna rá jogunk, hanem hogy magunkat például adjuk néktek, hogy minket kövessetek."));
husuperobject.biblemap.put("b53_3_10", new husuperobject(53,3,10,"Mert a mikor nálatok voltunk is, azt rendeltük néktek, hogy ha valaki nem akar dolgozni, ne is egyék."));
husuperobject.biblemap.put("b53_3_11", new husuperobject(53,3,11,"Mert halljuk, hogy némelyek rendetlenül élnek közületek, a kik semmit nem dolgoznak, hanem nyughatatlankodnak."));
husuperobject.biblemap.put("b53_3_12", new husuperobject(53,3,12,"Az ilyeneknek azonban rendeljük és kérjük õket a mi Urunk Jézus Krisztusra, hogy csendesen munkálkodva, a magok kenyerét egyék."));
husuperobject.biblemap.put("b53_3_13", new husuperobject(53,3,13,"Ti pedig, atyámfiai, meg ne restüljetek a jó cselekvésben."));
husuperobject.biblemap.put("b53_3_14", new husuperobject(53,3,14,"Ha pedig valaki nem engedelmeskedik a mi levél által való beszédünknek, azt jegyezzétek meg; és ne társalkodjatok vele, hogy megszégyenüljön."));
husuperobject.biblemap.put("b53_3_15", new husuperobject(53,3,15,"De ne tartsátok ellenségnek, hanem intsétek, mint atyafit."));
husuperobject.biblemap.put("b53_3_16", new husuperobject(53,3,16,"Maga pedig a békességnek Ura adjon néktek mindenkor minden tekintetben békességet. Az Úr [legyen] mindnyájatokkal!"));
husuperobject.biblemap.put("b53_3_17", new husuperobject(53,3,17,"A köszöntést én Pál [írom] a saját kezemmel, a mi ismertetõ jegye minden levelemnek. Így írok."));
husuperobject.biblemap.put("b53_3_18", new husuperobject(53,3,18,"A mi Urunk Jézus Krisztusnak kegyelme mindnyájatokkal! Ámen."));

}

}