package hu.bible;

public class hubible55 {

public hubible55() {

putmapbible55();

}

private void putmapbible55() {

husuperobject.biblemap.put("b55_1_1", new husuperobject(55,1,1,"Pál, Jézus Krisztusnak apostola, Isten akaratából, a Krisztus Jézusban való életnek ígérete szerint,"));
husuperobject.biblemap.put("b55_1_2", new husuperobject(55,1,2,"Timótheusnak, az én szeretett fiamnak: kegyelem, irgalmasság, békesség az Atya Istentõl és Krisztus Jézustól, a mi Urunktól."));
husuperobject.biblemap.put("b55_1_3", new husuperobject(55,1,3,"Hálát adok az Istennek, a kinek szolgálok õseimtõl fogva tiszta lelkiismerettel, hogy szüntelen gondolok reád könyörgéseimben éjjel és nappal,"));
husuperobject.biblemap.put("b55_1_4", new husuperobject(55,1,4,"Kívánván téged látni, megemlékezvén a te könyhullatásaidról, hogy örömmel teljesedjem be;"));
husuperobject.biblemap.put("b55_1_5", new husuperobject(55,1,5,"Eszembe jutván a benned levõ, képmutatás nélkül való hit, a mely lakozott elõször a te nagyanyádban Loisban és anyádban Eunikában; meg vagyok azonban gyõzõdve, hogy benned is."));
husuperobject.biblemap.put("b55_1_6", new husuperobject(55,1,6,"Minekokáért emlékeztetlek téged, hogy gerjeszd fel az Isten kegyelmi ajándékát, a mely benned van az én kezeimnek rád tétele által."));
husuperobject.biblemap.put("b55_1_7", new husuperobject(55,1,7,"Mert nem félelemnek lelkét adott nékünk az Isten; hanem erõnek és szeretetnek és józanságnak lelkét."));
husuperobject.biblemap.put("b55_1_8", new husuperobject(55,1,8,"Ne szégyeneld hát a mi Urunk bizonyságtételét, se engem az õ foglyát; hanem együtt szenvedj az evangyéliomért Istennek hatalma szerint."));
husuperobject.biblemap.put("b55_1_9", new husuperobject(55,1,9,"A ki megtartott minket és hívott szent hívással, nem a mi cselekedeteink szerint, hanem az õ saját végezése és kegyelme szerint, mely adatott nékünk Krisztus Jézusban örök idõknek elõtte,"));
husuperobject.biblemap.put("b55_1_10", new husuperobject(55,1,10,"Megjelentetett pedig most a mi Megtartónknak, Jézus Krisztusnak megjelenése által, a ki eltörölte a halált, világosságra hozta pedig az életet és halhatatlanságot az evangyéliom által,"));
husuperobject.biblemap.put("b55_1_11", new husuperobject(55,1,11,"Amelyre nézve tétettem én hirdetõvé és apostollá és pogányok tanítójává."));
husuperobject.biblemap.put("b55_1_12", new husuperobject(55,1,12,"A miért szenvedem ezeket is: de nem szégyenlem; mert tudom, kinek hittem, és bizonyos vagyok benne, hogy õ az én nála letett kincsemet meg tudja õrizni ama napra."));
husuperobject.biblemap.put("b55_1_13", new husuperobject(55,1,13,"Az egészséges beszédeknek példáját megtartsd, a miket én tõlem hallottál, a Krisztus Jézusban való hitben és szeretetben."));
husuperobject.biblemap.put("b55_1_14", new husuperobject(55,1,14,"A rád bízott drága kincset õrizd meg a bennünk lakozó Szent Lélek által."));
husuperobject.biblemap.put("b55_1_15", new husuperobject(55,1,15,"Azt tudod, hogy elfordultak tõlem az Ázsiabeliek mind, kik közül való Figellus és Hermogénes."));
husuperobject.biblemap.put("b55_1_16", new husuperobject(55,1,16,"Az Úr legyen irgalmas az Onesiforus házanépének: mert gyakorta megvidámított engem, és az én bilincsemet nem szégyenlette;"));
husuperobject.biblemap.put("b55_1_17", new husuperobject(55,1,17,"Sõt mikor Rómában volt, buzgón keresett engem, meg is talált."));
husuperobject.biblemap.put("b55_1_18", new husuperobject(55,1,18,"Az Úr engedje meg néki, hogy találjon irgalmasságot az Úrnál ama napon. És hogy mily nagy szolgálatot tett Efézusban, te jobban tudod."));
husuperobject.biblemap.put("b55_2_1", new husuperobject(55,2,1,"Te annakokáért, én fiam, erõsödjél meg a Krisztus Jézusban való kegyelemben;"));
husuperobject.biblemap.put("b55_2_2", new husuperobject(55,2,2,"És a miket tõlem hallottál sok bizonyság által, azokat bízzad hív emberekre, a kik másoknak a tanítására is alkalmasak lesznek."));
husuperobject.biblemap.put("b55_2_3", new husuperobject(55,2,3,"Te azért a munkának terhét hordozzad, mint a Jézus Krisztus jó vitéze."));
husuperobject.biblemap.put("b55_2_4", new husuperobject(55,2,4,"Egy harczos sem elegyedik bele az élet dolgaiba; hogy tessék annak, a ki õt harczossá avatta."));
husuperobject.biblemap.put("b55_2_5", new husuperobject(55,2,5,"Ha pedig küzd is valaki, nem koronáztatik meg, ha nem szabályszerûen küzd."));
husuperobject.biblemap.put("b55_2_6", new husuperobject(55,2,6,"A ki munkálkodik, a földmívelõnek kell a gyümölcsökben elõször részesülnie."));
husuperobject.biblemap.put("b55_2_7", new husuperobject(55,2,7,"Értsd meg a mit mondok; adjon azért az Úr néked belátást mindenekben."));
husuperobject.biblemap.put("b55_2_8", new husuperobject(55,2,8,"Emlékezzél meg, hogy Jézus Krisztus feltámadott a halálból, ki a Dávid magvából való az én evangyéliomom szerint:"));
husuperobject.biblemap.put("b55_2_9", new husuperobject(55,2,9,"A melyért, mint egy gonosztevõ, szenvedek mind a fogságig; de az Istennek beszéde nincs bilincsbe verve."));
husuperobject.biblemap.put("b55_2_10", new husuperobject(55,2,10,"Annakokáért mindent elszenvedek a választottakért, hogy õk is elnyerjék a Krisztus Jézusban való idvességet örök dicsõséggel egyben."));
husuperobject.biblemap.put("b55_2_11", new husuperobject(55,2,11,"Igaz beszéd ez. Mert ha vele együtt meghaltunk, vele együtt fogunk élni is."));
husuperobject.biblemap.put("b55_2_12", new husuperobject(55,2,12,"Ha tûrünk, vele együtt fogunk uralkodni is: ha megtagadjuk, õ is megtagad minket;"));
husuperobject.biblemap.put("b55_2_13", new husuperobject(55,2,13,"Ha hitetlenkedünk, õ hû marad: õ magát meg nem tagadhatja."));
husuperobject.biblemap.put("b55_2_14", new husuperobject(55,2,14,"Ezekre emlékeztesd, kérvén kérve [õket] az Úr színe elõtt, hogy ne vitatkozzanak haszontalanul, a hallgatóknak romlására."));
husuperobject.biblemap.put("b55_2_15", new husuperobject(55,2,15,"Igyekezzél, hogy Isten elõtt becsületesen megállj, mint oly munkás, a ki szégyent nem vall, a ki helyesen hasogatja az igazságnak beszédét."));
husuperobject.biblemap.put("b55_2_16", new husuperobject(55,2,16,"A szentségtelen üres lármákat pedig kerüld, mert mind nagyobb istentelenségre növekednek."));
husuperobject.biblemap.put("b55_2_17", new husuperobject(55,2,17,"És az õ beszédök mint a rákfekély terjed; közülök való Himenéus és Filétus."));
husuperobject.biblemap.put("b55_2_18", new husuperobject(55,2,18,"A kik az igazság mellõl eltévelyedtek, azt mondván, hogy a feltámadás már megtörtént, és feldúlják némelyeknek a hitét."));
husuperobject.biblemap.put("b55_2_19", new husuperobject(55,2,19,"Mindazáltal megáll az Istennek erõs fundamentoma, melynek pecséte ez: Ismeri az Úr az övéit; és: Álljon el a hamisságtól minden, a ki Krisztus nevét vallja."));
husuperobject.biblemap.put("b55_2_20", new husuperobject(55,2,20,"Nagy házban pedig nemcsak arany- és ezüstedények vannak, hanem fából és cserépbõl valók is; és azok közül némelyek tisztességre, némelyek pedig gyalázatra valók."));
husuperobject.biblemap.put("b55_2_21", new husuperobject(55,2,21,"Ha tehát valaki magát ezektõl tisztán tartja, tisztességre való edény lesz, megszentelt, és hasznos a gazdának, minden jó cselekedetre alkalmas."));
husuperobject.biblemap.put("b55_2_22", new husuperobject(55,2,22,"Az ifjúkori kivánságokat pedig kerüld; hanem kövessed az igazságot, a hitet, a szeretetet, a békességet azokkal egyetembe, a kik segítségül hívják az Urat tiszta szívbõl."));
husuperobject.biblemap.put("b55_2_23", new husuperobject(55,2,23,"A botor és gyermekes vitatkozásokat pedig kerüld, tudván, hogy azok háborúságokat szülnek."));
husuperobject.biblemap.put("b55_2_24", new husuperobject(55,2,24,"Az Úr szolgájának pedig nem kell torzsalkodni, hanem legyen mindenkihez nyájas, tanításra alkalmas, türelmes."));
husuperobject.biblemap.put("b55_2_25", new husuperobject(55,2,25,"A ki szelíden fenyíti az ellenszegülõket; ha talán adna nékik az Isten megtérést az igazság megismerésére,"));
husuperobject.biblemap.put("b55_2_26", new husuperobject(55,2,26,"És felocsudnának az ördög tõrébõl, foglyokká tétetvén az [Úr szolgája] által az [Isten] akaratára."));
husuperobject.biblemap.put("b55_3_1", new husuperobject(55,3,1,"Azt pedig tudd meg, hogy az utolsó napokban nehéz idõk állanak be."));
husuperobject.biblemap.put("b55_3_2", new husuperobject(55,3,2,"Mert lesznek az emberek magukat szeretõk, pénzsóvárgók, kérkedõk, kevélyek, káromkodók, szüleik iránt engedetlenek, háládatlanok, tisztátalanok,"));
husuperobject.biblemap.put("b55_3_3", new husuperobject(55,3,3,"Szeretet nélkül valók, kérlelhetetlenek, rágalmazók, mértékletlenek, kegyetlenek, a jónak nem kedvelõi."));
husuperobject.biblemap.put("b55_3_4", new husuperobject(55,3,4,"Árulók, vakmerõk, felfuvalkodottak, inkább a gyönyörnek, mint Istennek szeretõi."));
husuperobject.biblemap.put("b55_3_5", new husuperobject(55,3,5,"Kiknél megvan a kegyességnek látszata, de megtagadják annak erejét. És ezeket kerüld."));
husuperobject.biblemap.put("b55_3_6", new husuperobject(55,3,6,"Mert ezek közül valók azok, a kik betolakodnak a házakba, és foglyul ejtik a bûnökkel megterhelt és sokféle kívánságoktól ûzött asszonykákat,"));
husuperobject.biblemap.put("b55_3_7", new husuperobject(55,3,7,"Kik mindenkor tanulnak, de az igazság megismerésére soha el nem juthatnak."));
husuperobject.biblemap.put("b55_3_8", new husuperobject(55,3,8,"Miképen pedig Jánnes és Jámbres ellene állottak Mózesnek, akképen ezek is ellene állanak az igazságnak; megromlott elméjû, a hitre nézve nem becsületes emberek."));
husuperobject.biblemap.put("b55_3_9", new husuperobject(55,3,9,"De többre nem mennek: mert esztelenségök nyilvánvaló lesz mindenek elõtt, a miképen amazoké is az lett."));
husuperobject.biblemap.put("b55_3_10", new husuperobject(55,3,10,"Te pedig követted az én tanításomat, életmódomat, szándékomat, hitemet, hosszútûrésemet, szeretetemet, türelmemet,"));
husuperobject.biblemap.put("b55_3_11", new husuperobject(55,3,11,"Üldöztetéseimet, szenvedéseimet, a melyek rajtam estek Antiókhiában, Ikóniumban, Listrában: minémû üldöztetéseket szenvedtem! de mindezekbõl megszabadított engem az Úr."));
husuperobject.biblemap.put("b55_3_12", new husuperobject(55,3,12,"De mindazok is, a kik kegyesen akarnak élni Krisztus Jézusban, üldöztetni fognak."));
husuperobject.biblemap.put("b55_3_13", new husuperobject(55,3,13,"A gonosz emberek pedig és az ámítók nevekednek a rosszaságban, eltévelyítvén és eltévelyedvén."));
husuperobject.biblemap.put("b55_3_14", new husuperobject(55,3,14,"De te maradj meg azokban, a miket tanultál és a mik reád bízattak, tudván kitõl tanultad,"));
husuperobject.biblemap.put("b55_3_15", new husuperobject(55,3,15,"És hogy gyermekségedtõl fogva tudod a szent írásokat, melyek téged bölcscsé tehetnek az idvességre a Krisztus Jézusban való hit által."));
husuperobject.biblemap.put("b55_3_16", new husuperobject(55,3,16,"A teljes írás Istentõl ihletett és hasznos a tanításra, a feddésre, a megjobbításra, az igazságban való nevelésre,"));
husuperobject.biblemap.put("b55_3_17", new husuperobject(55,3,17,"Hogy tökéletes legyen az Isten embere, minden jó cselekedetre felkészített."));
husuperobject.biblemap.put("b55_4_1", new husuperobject(55,4,1,"Kérlek azért az Isten és Krisztus Jézus színe elõtt, a ki ítélni fog élõket és holtakat az õ eljövetelekor és az õ országában."));
husuperobject.biblemap.put("b55_4_2", new husuperobject(55,4,2,"Hirdesd az ígét, állj elõ vele alkalmatos, alkalmatlan idõben, ints, feddj, buzdíts teljes béketûréssel és tanítással."));
husuperobject.biblemap.put("b55_4_3", new husuperobject(55,4,3,"Mert lesz idõ, mikor az egészséges tudományt el nem szenvedik, hanem a saját kívánságaik szerint gyûjtenek magoknak tanítókat, mert viszket a fülök;"));
husuperobject.biblemap.put("b55_4_4", new husuperobject(55,4,4,"És az igazságtól elfordítják az õ fülöket, de a mesékhez oda fordulnak."));
husuperobject.biblemap.put("b55_4_5", new husuperobject(55,4,5,"De te józan légy mindenekben, szenvedj, az evangyélista munkáját cselekedd, szolgálatodat teljesen betöltsd."));
husuperobject.biblemap.put("b55_4_6", new husuperobject(55,4,6,"Mert én immár megáldoztatom, és az én elköltözésem ideje beállott."));
husuperobject.biblemap.put("b55_4_7", new husuperobject(55,4,7,"Ama nemes harczot megharczoltam, futásomat elvégeztem, a hitet megtartottam:"));
husuperobject.biblemap.put("b55_4_8", new husuperobject(55,4,8,"Végezetre eltétetett nékem az igazság koronája, melyet megád nékem az Úr ama napon, az igaz Bíró; nemcsak nékem pedig, hanem mindazoknak is, a kik vágyva várják az õ megjelenését."));
husuperobject.biblemap.put("b55_4_9", new husuperobject(55,4,9,"Igyekezzél hozzám jõni hamar."));
husuperobject.biblemap.put("b55_4_10", new husuperobject(55,4,10,"Mert Démás engem elhagyott, e jelen való világhoz ragaszkodván, és elment Thessalónikába: Kresczens Galátziába, Titus Dalmátziába."));
husuperobject.biblemap.put("b55_4_11", new husuperobject(55,4,11,"Egyedül Lukács van velem. Márkust magadhoz vévén, hozd magaddal: mert nekem alkalmas a szolgálatra."));
husuperobject.biblemap.put("b55_4_12", new husuperobject(55,4,12,"Tikhikust pedig Efézusba küldöttem."));
husuperobject.biblemap.put("b55_4_13", new husuperobject(55,4,13,"A felsõruhámat, melyet Troásban Kárpusnál hagytam, jöttödben hozd el, a könyveket is, kiváltképen a hártyákat."));
husuperobject.biblemap.put("b55_4_14", new husuperobject(55,4,14,"Az érczmíves Sándor sok bajt szerzett nékem: fizessen meg az Úr néki cselekedetei szerint."));
husuperobject.biblemap.put("b55_4_15", new husuperobject(55,4,15,"Tõle te is õrizkedjél, mert szerfelett ellenállott a mi beszédinknek."));
husuperobject.biblemap.put("b55_4_16", new husuperobject(55,4,16,"Elsõ védekezésem alkalmával senki sem volt mellettem, sõt mindnyájan elhagytak; ne számíttassék be nékik."));
husuperobject.biblemap.put("b55_4_17", new husuperobject(55,4,17,"De az Úr mellettem állott, és megerõsített engem; hogy teljesen bevégezzem az igehirdetést, és hallják meg azt az összes pogányok: és megszabadultam az oroszlán szájából."));
husuperobject.biblemap.put("b55_4_18", new husuperobject(55,4,18,"És megszabadít engem az Úr minden gonosz cselekedettõl, és megtart az õ mennyei országára; a kinek dicsõség örökkön örökké! Ámen."));
husuperobject.biblemap.put("b55_4_19", new husuperobject(55,4,19,"Köszöntsed Priszkát és Akvilát, és az Onesifórus háznépét."));
husuperobject.biblemap.put("b55_4_20", new husuperobject(55,4,20,"Erástus Korinthusban maradt; Trófimust pedig Milétumban hagytam betegen."));
husuperobject.biblemap.put("b55_4_21", new husuperobject(55,4,21,"Igyekezzél tél elõtt eljõni. Köszönt téged Eubulus és Pudens és Linus és Klaudia, és mind az atyafiak."));
husuperobject.biblemap.put("b55_4_22", new husuperobject(55,4,22,"Az Úr Jézus Krisztus a te lelkeddel. Kegyelem veletek! Ámen."));

}

}