husuperobject.biblemap.put("b56_1_1", new husuperobject(56,1,1,"Pál, Istennek szolgája, Jézus Krisztusnak pedig apostola, az Isten választottainak hite és a kegyesség szerint való igazságnak megismerése szerint,"));
husuperobject.biblemap.put("b56_1_2", new husuperobject(56,1,2,"Az örök élet reménységére, melyet megígért az igazmondó Isten örök idõknek elõtte,"));
husuperobject.biblemap.put("b56_1_3", new husuperobject(56,1,3,"Megjelentette pedig a maga idejében az õ beszédét a prédikálás által, a mely reám bízatott a mi megtartó Istenünknek parancsolata szerint; Titusnak, a közös hit szerint való igaz fiamnak:"));
husuperobject.biblemap.put("b56_1_4", new husuperobject(56,1,4,"Kegyelem, irgalmasság és békesség az Atya Istentõl és az Úr Jézus Krisztustól, a mi Megtartónktól."));
husuperobject.biblemap.put("b56_1_5", new husuperobject(56,1,5,"A végett hagytalak téged Krétában, hogy a hátramaradt dolgokat hozd rendbe, és rendelj városonként presbitereket, a miképen én néked meghagytam;"));
husuperobject.biblemap.put("b56_1_6", new husuperobject(56,1,6,"Ha van feddhetetlen, egy feleségû férfiú, a kinek hívõ, nem kicsapongással vádolt avagy engedetlen gyermekei vannak."));
husuperobject.biblemap.put("b56_1_7", new husuperobject(56,1,7,"Mert szükséges, hogy a püspök feddhetetlen legyen, mint Isten sáfára; nem akaratos, nem haragos, nem részeges, nem verekedõ, nem rút nyerészkedõ;"));
husuperobject.biblemap.put("b56_1_8", new husuperobject(56,1,8,"Hanem vendégszeretõ, jónak kedvelõje, mértékletes, igaz, tiszta, maga tûrtetõ;"));
husuperobject.biblemap.put("b56_1_9", new husuperobject(56,1,9,"A ki a tudomány szerint való igaz beszédhez tartja magát, hogy inthessen az egészséges tudománnyal és meggyõzhesse az ellenkezõket."));
husuperobject.biblemap.put("b56_1_10", new husuperobject(56,1,10,"Mert van sok engedetlen, hiába való beszédû és csaló, kiváltképen a körülmetélkedésbõl valók,"));
husuperobject.biblemap.put("b56_1_11", new husuperobject(56,1,11,"A kiknek be kell dugni a szájokat; a kik egész házakat feldúlnak, tanítván rút nyereség okáért, a miket nem kellene."));
husuperobject.biblemap.put("b56_1_12", new husuperobject(56,1,12,"Azt mondta valaki közülök, az õ saját prófétájok: A krétaiak mindig hazugok, gonosz vadak, rest hasak."));
husuperobject.biblemap.put("b56_1_13", new husuperobject(56,1,13,"E bizonyság igaz: annakokáért fedd õket kímélés nélkül, hogy a hitben épek legyenek,"));
husuperobject.biblemap.put("b56_1_14", new husuperobject(56,1,14,"Nem ügyelvén zsidó mesékre, és az igazságot megvetõ emberek parancsolataira."));
husuperobject.biblemap.put("b56_1_15", new husuperobject(56,1,15,"Minden tiszta a tisztáknak: de a megfertõztetetteknek és hitetleneknek semmi sem tiszta; hanem megfertõztetett azoknak mind elméjök, mind lelkiismeretök."));
husuperobject.biblemap.put("b56_1_16", new husuperobject(56,1,16,"Vallják, hogy Istent ismerik, de cselekedeteikkel tagadják, mivelhogy útálatosak és hitetlenek és minden jó cselekedetre méltatlanok."));
husuperobject.biblemap.put("b56_2_1", new husuperobject(56,2,1,"Te pedig azokat szóljad, a mik az egészséges tudományhoz illenek."));
husuperobject.biblemap.put("b56_2_2", new husuperobject(56,2,2,"Hogy a vén emberek józanok legyenek, tisztességesek, mértékletesek; a hitben, szeretetben, tûrésben épek."));
husuperobject.biblemap.put("b56_2_3", new husuperobject(56,2,3,"Hasonlóképen a vén asszonyok szentekhez illõ magaviseletûek legyenek, nem patvarkodók, sem sok borivás rabjai, jóra oktatók;"));
husuperobject.biblemap.put("b56_2_4", new husuperobject(56,2,4,"Hogy megokosítsák az ifjú asszonyokat, hogy férjöket és magzataikat szeressék,"));
husuperobject.biblemap.put("b56_2_5", new husuperobject(56,2,5,"[Legyenek] mértékletesek, tiszták, háziasak, jók, férjöknek engedelmesek, hogy az Isten beszéde ne káromoltassék."));
husuperobject.biblemap.put("b56_2_6", new husuperobject(56,2,6,"Az ifjakat hasonlóképen intsed, hogy legyenek mértékletesek:"));
husuperobject.biblemap.put("b56_2_7", new husuperobject(56,2,7,"Mindenben tenmagadat adván példaképül a jó cselekedetekben; a tudományban romlatlanságot, méltóságot [mutatván,]"));
husuperobject.biblemap.put("b56_2_8", new husuperobject(56,2,8,"Egészséges, feddhetetlen beszédet; hogy az ellenfél megszégyenüljön, semmi gonoszt sem tudván rólatok mondani."));
husuperobject.biblemap.put("b56_2_9", new husuperobject(56,2,9,"A szolgákat [intsed,] hogy az õ uraiknak engedelmeskedjenek, mindenben kedvöket keressék, ne ellenkezzenek,"));
husuperobject.biblemap.put("b56_2_10", new husuperobject(56,2,10,"Ne tolvajkodjanak, hanem teljes jó hûséget tanusítsanak; hogy a mi megtartó Istenünknek tudományát ékesítsék mindenben."));
husuperobject.biblemap.put("b56_2_11", new husuperobject(56,2,11,"Mert megjelent az Isten idvezítõ kegyelme minden embernek,"));
husuperobject.biblemap.put("b56_2_12", new husuperobject(56,2,12,"A mely arra tanít minket, hogy megtagadván a hitetlenséget és a világi kivánságokat, mértékletesen, igazán és szentül éljünk a jelenvaló világon:"));
husuperobject.biblemap.put("b56_2_13", new husuperobject(56,2,13,"Várván ama boldog reménységet és a nagy Istennek és megtartó Jézus Krisztusunknak dicsõsége megjelenését;"));
husuperobject.biblemap.put("b56_2_14", new husuperobject(56,2,14,"A ki önmagát adta mi érettünk, hogy megváltson minket minden hamisságtól, és tisztítson önmagának kiváltképen való népet, jó cselekedetekre igyekezõt."));
husuperobject.biblemap.put("b56_2_15", new husuperobject(56,2,15,"Ezeket szóljad, és ints és feddj teljes méltósággal; senki téged meg ne vessen."));
husuperobject.biblemap.put("b56_3_1", new husuperobject(56,3,1,"Emlékeztessed õket, hogy a fejedelemségeknek és hatalmasságoknak engedelmeskedjenek, hódoljanak, minden jó cselekedetre készek legyenek,"));
husuperobject.biblemap.put("b56_3_2", new husuperobject(56,3,2,"Senkit ne szidalmazzanak, ne veszekedjenek, gyöngédek legyenek, teljes szelídséget tanusítván minden ember iránt."));
husuperobject.biblemap.put("b56_3_3", new husuperobject(56,3,3,"Mert régenten mi is esztelenek, engedetlenek, tévelygõk, különbözõ kívánságoknak és gyönyöröknek szolgái, gonoszságban és irígységben élõk, gyûlölségesek, egymást gyûlölõk valánk."));
husuperobject.biblemap.put("b56_3_4", new husuperobject(56,3,4,"De mikor a mi megtartó Istenünknek jóvolta és az emberekhez való szeretete megjelent,"));
husuperobject.biblemap.put("b56_3_5", new husuperobject(56,3,5,"Nem az igazságnak cselekedeteibõl, a melyeket mi cselekedtünk, hanem az õ irgalmasságából tartott meg minket az újjászületésnek fürdõje és a Szent Lélek megújítása által,"));
husuperobject.biblemap.put("b56_3_6", new husuperobject(56,3,6,"Melyet kitöltött reánk bõséggel a mi megtartó Jézus Krisztusunk által;"));
husuperobject.biblemap.put("b56_3_7", new husuperobject(56,3,7,"Hogy az õ kegyelmébõl megigazulván, örökösök legyünk az örök élet reménysége szerint."));
husuperobject.biblemap.put("b56_3_8", new husuperobject(56,3,8,"Igaz ez a beszéd, és akarom, hogy ezeket erõsítsed, hogy igyekezzenek jó cselekedetekkel elõljárni azok, a kik Istenben hívõkké lettek. Ezek jók és hasznosak az embereknek;"));
husuperobject.biblemap.put("b56_3_9", new husuperobject(56,3,9,"A balgatag vitatkozásokat azonban és a nemzetségekrõl való tudakozásokat, és a civakodást és a törvény felõl való harczokat kerüld; mert haszontalanok és hiábavalók."));
husuperobject.biblemap.put("b56_3_10", new husuperobject(56,3,10,"Az eretnek embert egy vagy két intés után kerüld;"));
husuperobject.biblemap.put("b56_3_11", new husuperobject(56,3,11,"Tudván, hogy az ilyen romlott, és vétkezik, önmaga is kárhoztatván magát."));
husuperobject.biblemap.put("b56_3_12", new husuperobject(56,3,12,"Mikor Ártemást vagy Tikhikust hozzád küldöm, siess hozzám jõni Nikápolyba; mert elhatároztam, hogy ott töltöm a telet."));
husuperobject.biblemap.put("b56_3_13", new husuperobject(56,3,13,"A törvénytudó Zénást és Apollóst gondosan indítsd útnak, hogy semmiben se legyen fogyatkozásuk."));
husuperobject.biblemap.put("b56_3_14", new husuperobject(56,3,14,"Tanulják meg pedig a mieink is, hogy jó cselekedetekkel járjanak elõl a szükséges hasznokra, hogy ne legyenek gyümölcstelenek."));
husuperobject.biblemap.put("b56_3_15", new husuperobject(56,3,15,"Köszöntenek téged a velem levõk mindnyájan. Köszöntsd azokat, a kik szeretnek minket hitben. Kegyelem mindnyájatokkal! Ámen."));

