package hu.bible;

public class hubible57 {

public hubible57() {

putmapbible57();

}

private void putmapbible57() {

husuperobject.biblemap.put("b57_1_1", new husuperobject(57,1,1,"Pál, Krisztus Jézusnak foglya, és Timótheus, az atyafi, Filemonnak, a mi szeretett munkatársunknak,"));
husuperobject.biblemap.put("b57_1_2", new husuperobject(57,1,2,"És Appiának, a szeretettnek, és Arkhippusnak, a mi bajtársunknak, és a te házadnál való gyülekezetnek:"));
husuperobject.biblemap.put("b57_1_3", new husuperobject(57,1,3,"Kegyelem néktek és békesség Istentõl, a mi Atyánktól, és az Úr Jézus Krisztustól."));
husuperobject.biblemap.put("b57_1_4", new husuperobject(57,1,4,"Hálát adok az én Istenemnek mindenkor, emlegetvén téged az én imádságaimban,"));
husuperobject.biblemap.put("b57_1_5", new husuperobject(57,1,5,"Mert hallom a te szeretetedet és ama te hitedet, mely van benned az Úr Jézushoz, és minden szentek irányában,"));
husuperobject.biblemap.put("b57_1_6", new husuperobject(57,1,6,"Hogy a te hitedben való közösség hathatós legyen, a Krisztus ügyében, minden bennetek levõ jónak megismerése által."));
husuperobject.biblemap.put("b57_1_7", new husuperobject(57,1,7,"Mert sok örömünk és vígasztalásunk van a te szeretetedben, hogy a szenteknek szíveik megvídámodtak te általad, atyámfia."));
husuperobject.biblemap.put("b57_1_8", new husuperobject(57,1,8,"Annakokáért jóllehet nagy bátorságom van a Krisztusban, megparancsolni néked azt, a mi illendõ,"));
husuperobject.biblemap.put("b57_1_9", new husuperobject(57,1,9,"A szeretetért inkább kérlek, ilyen lévén, mint Pál, a megvénhedett, most pedig foglya is a Jézus Krisztusnak;"));
husuperobject.biblemap.put("b57_1_10", new husuperobject(57,1,10,"Kérlek téged az én fiamért, a kit fogságomban szûltem, Onésimusért,"));
husuperobject.biblemap.put("b57_1_11", new husuperobject(57,1,11,"A ki egykor tenéked haszontalan volt, most pedig mind néked, mind nékem nagyon hasznos,"));
husuperobject.biblemap.put("b57_1_12", new husuperobject(57,1,12,"Kit visszaküldöttem; te pedig õt, azaz az én szívemet, fogadd magadhoz!"));
husuperobject.biblemap.put("b57_1_13", new husuperobject(57,1,13,"Õt én magamnál akartam tartani, hogy te helyetted szolgáljon nékem az evangyéliomért [szenvedett] fogságomban;"));
husuperobject.biblemap.put("b57_1_14", new husuperobject(57,1,14,"De a te megkérdezésed nélkül semmit sem akartam cselekedni, hogy jótéteményed ne kényszerítésbõl, hanem szabad akaratból való legyen."));
husuperobject.biblemap.put("b57_1_15", new husuperobject(57,1,15,"Mert talán azért vált meg [tõled] ideig-óráig, hogy õt, mint örökkévalót kapd vissza;"));
husuperobject.biblemap.put("b57_1_16", new husuperobject(57,1,16,"Nem úgy immár mint szolgát, hanem szolgánál nagyobbat, [mint] szeretett atyafit, kiváltképen nékem, mennyivel inkább pedig néked, mind testben, mind az Úrban."));
husuperobject.biblemap.put("b57_1_17", new husuperobject(57,1,17,"Azért, ha engem részestársadnak tartasz, úgy fogadd õt magadhoz, mint engemet."));
husuperobject.biblemap.put("b57_1_18", new husuperobject(57,1,18,"Ha pedig valamit vétett ellened, avagy adós, ezt nekem róvd fel."));
husuperobject.biblemap.put("b57_1_19", new husuperobject(57,1,19,"Én Pál írtam az én kezemmel, én megfizetem: hogy [azt] ne mondjam néked, hogy te magaddal is adós vagy ezen felül nékem."));
husuperobject.biblemap.put("b57_1_20", new husuperobject(57,1,20,"Bizonyára, atyámfia, jótéteményt várnék tõled az Úrban: vídámítsd meg az én szívemet az Úrban!"));
husuperobject.biblemap.put("b57_1_21", new husuperobject(57,1,21,"Bízván a te engedelmességedben, [így] írtam néked, tudván, hogy annál, a mit mondok, többet is fogsz cselekedni."));
husuperobject.biblemap.put("b57_1_22", new husuperobject(57,1,22,"Egyúttal pedig készíts nékem szállást; mert reménylem, hogy a ti imádságaitokért néktek ajándékoztatom."));
husuperobject.biblemap.put("b57_1_23", new husuperobject(57,1,23,"Köszönt téged Epafrás, az én fogolytársam a Krisztus Jézusban,"));
husuperobject.biblemap.put("b57_1_24", new husuperobject(57,1,24,"[Tovább]á Márk, Aristárkhus, Démás [és] Lukács, az én munkatársaim."));
husuperobject.biblemap.put("b57_1_25", new husuperobject(57,1,25,"A mi Urunk Jézus Krisztusnak kegyelme [legyen] a ti lelketekkel!"));

}

}