package hu.bible;

public class hubible61 {

public hubible61() {

putmapbible61();

}

private void putmapbible61() {

husuperobject.biblemap.put("b61_1_1", new husuperobject(61,1,1,"Simon Péter, Jézus Krisztus szolgája és apostola, azoknak, a kik velünk egyenlõ drága hitet nyertek a mi Istenünknek és megtartónknak Jézus Krisztusnak igazságában:"));
husuperobject.biblemap.put("b61_1_2", new husuperobject(61,1,2,"Kegyelem és békesség adassék néktek bõségesen az Istennek és Jézusnak a mi Urunknak megismerésében."));
husuperobject.biblemap.put("b61_1_3", new husuperobject(61,1,3,"Mivelhogy az õ isteni ereje mindennel megajándékozott minket, a mi az életre és kegyességre való, Annak megismerése által, a ki minket a saját dicsõségével és hatalmával elhívott;"));
husuperobject.biblemap.put("b61_1_4", new husuperobject(61,1,4,"A melyek által igen nagy és becses ígéretekkel ajándékozott meg bennünket; hogy azok által isteni természet részeseivé legyetek, kikerülvén a romlottságot, a mely a kívánságban van e világon."));
husuperobject.biblemap.put("b61_1_5", new husuperobject(61,1,5,"Ugyanerre pedig teljes igyekezetet is fordítván, a ti hitetek mellé ragasszatok jó cselekedetet, a jó cselekedet mellé tudományt,"));
husuperobject.biblemap.put("b61_1_6", new husuperobject(61,1,6,"A tudomány mellé pedig mértékletességet, a mértékletesség mellé pedig tûrést, a tûrés mellé pedig kegyességet,"));
husuperobject.biblemap.put("b61_1_7", new husuperobject(61,1,7,"A kegyesség mellé pedig atyafiakhoz való hajlandóságot, az atyafiakhoz való hajlandóság mellé pedig szeretetet."));
husuperobject.biblemap.put("b61_1_8", new husuperobject(61,1,8,"Mert ha ezek megvannak és gyarapodnak bennetek, nem tesznek titeket hivalkodókká, sem gyümölcstelenekké a mi Urunk Jézus Krisztus megismerésére nézve."));
husuperobject.biblemap.put("b61_1_9", new husuperobject(61,1,9,"Mert a kiben ezek nincsenek meg, az vak, rövidlátó, elfelejtkezvén a régi bûneibõl való megtisztulásáról."));
husuperobject.biblemap.put("b61_1_10", new husuperobject(61,1,10,"Annakokáért, atyámfiai, igyekezzetek inkább a ti elhívatástokat és kiválasztatásotokat erõssé tenni; mert ha ezeket cselekszitek, nem ütköztök meg soha."));
husuperobject.biblemap.put("b61_1_11", new husuperobject(61,1,11,"Mert ekképen gazdagon adatik majd néktek a mi Urunknak és megtartónknak, a Jézus Krisztusnak örök országába való bemenetel."));
husuperobject.biblemap.put("b61_1_12", new husuperobject(61,1,12,"Annakokáért nem mulasztom el, hogy mindenkor emlékeztesselek titeket ezekre, hogy tudjátok [ezeket], és erõsek vagytok a jelenvaló igazságban."));
husuperobject.biblemap.put("b61_1_13", new husuperobject(61,1,13,"Méltónak vélem pedig, a míg ebben a sátorban vagyok, hogy emlékeztetés által ébresztgesselek titeket;"));
husuperobject.biblemap.put("b61_1_14", new husuperobject(61,1,14,"Mint a ki tudom, hogy hamar leteszem sátoromat, a miképen a mi Urunk Jézus Krisztus is megjelentette nékem."));
husuperobject.biblemap.put("b61_1_15", new husuperobject(61,1,15,"De igyekezni fogok azon, hogy ti az én halálom után is mindenkor megemlékezhessetek ezekrõl."));
husuperobject.biblemap.put("b61_1_16", new husuperobject(61,1,16,"Mert nem mesterkélt meséket követve ismertettük meg veletek a mi Urunk Jézus Krisztus hatalmát és eljövetelét; hanem mint a kik szemlélõi voltunk az õ nagyságának."));
husuperobject.biblemap.put("b61_1_17", new husuperobject(61,1,17,"Mert a mikor az Atya Istentõl azt a tisztességet és dicsõséget nyerte, hogy hozzá a felséges dicsõség ilyen szózata jutott: Ez az én szeretett Fiam, a kiben én gyönyörködöm:"));
husuperobject.biblemap.put("b61_1_18", new husuperobject(61,1,18,"Ezt az égbõl jövõ szózatot mi hallottuk, együtt lévén vele a szent hegyen."));
husuperobject.biblemap.put("b61_1_19", new husuperobject(61,1,19,"És igen biztos nálunk a prófétai beszéd [is], a melyre jól teszitek, ha figyelmeztek, mint sötét helyen világító szövétnekre, míg nappal virrad, és hajnalcsillag kél fel szívetekben;"));
husuperobject.biblemap.put("b61_1_20", new husuperobject(61,1,20,"Tudván elõször azt, hogy az írásban egy prófétai szó sem támad saját magyarázatból."));
husuperobject.biblemap.put("b61_1_21", new husuperobject(61,1,21,"Mert sohasem ember akaratából származott a prófétai szó; hanem a Szent Lélektõl indíttatva szólottak az Istennek szent emberei."));
husuperobject.biblemap.put("b61_2_1", new husuperobject(61,2,1,"Valának pedig hamis próféták is a nép között, a miképen ti köztetek is lesznek hamis tanítók, a kik veszedelmes eretnekségeket fognak becsempészni, és az Urat, a ki megváltotta õket, megtagadván, önmagokra hirtelen való veszedelmet hoznak."));
husuperobject.biblemap.put("b61_2_2", new husuperobject(61,2,2,"És sokan fogják követni azoknak romlottságát; a kik miatt az igazság útja káromoltatni fog."));
husuperobject.biblemap.put("b61_2_3", new husuperobject(61,2,3,"És a telhetetlenség miatt költött beszédekkel vásárt ûznek belõletek; kiknek kárhoztatásuk régtõl fogva nem szünetel, és romlásuk nem szunnyad."));
husuperobject.biblemap.put("b61_2_4", new husuperobject(61,2,4,"Mert ha nem kedvezett az Isten a bûnbe esett angyaloknak, hanem mélységbe taszítván, a sötétség lánczaira adta oda õket, hogy fenntartassanak az ítéletre;"));
husuperobject.biblemap.put("b61_2_5", new husuperobject(61,2,5,"És [ha] a régi világnak sem kedvezett, de Nóét az igazság hirdetõjét, nyolczad magával megõrizte, özönvízzel borítván el az istentelenek világát;"));
husuperobject.biblemap.put("b61_2_6", new husuperobject(61,2,6,"És [ha] Sodoma és Gomora városait elhamvasztotta, végromlásra kárhoztatta, például tévén azokra nézve, a kik istentelenkedni fognak;"));
husuperobject.biblemap.put("b61_2_7", new husuperobject(61,2,7,"És [ha] megszabadította az igaz Lótot, a ki az istenteleneknek fajtalanságban való forgolódása miatt elfáradt;"));
husuperobject.biblemap.put("b61_2_8", new husuperobject(61,2,8,"(Mert amaz igaz, azok között lakván, a gonosz cselekedeteket látva és hallva, napról-napra gyötri vala az õ igaz lelkét):"));
husuperobject.biblemap.put("b61_2_9", new husuperobject(61,2,9,"Meg tudja szabadítani az Úr a kegyeseket a kísértésekbõl, a gonoszokat pedig az ítélet napjára büntetésre fenntartani."));
husuperobject.biblemap.put("b61_2_10", new husuperobject(61,2,10,"Fõképen pedig azokat, a kik a testet követvén, tisztátalan kívánságban járnak, és a hatalmasságot megvetik. Vakmerõk, magoknak kedveskedõk, a kik a méltóságokat káromolni nem rettegnek:"));
husuperobject.biblemap.put("b61_2_11", new husuperobject(61,2,11,"Holott az angyalok, a kik erõre és hatalomra nézve nagyobbak, nem szólnak azok ellen az Úr elõtt káromló ítéletet."));
husuperobject.biblemap.put("b61_2_12", new husuperobject(61,2,12,"De ezek, mint oktalan természeti állatok, a melyek megfogatásra és elpusztításra valók, azokat, a miket nem ismernek, káromolván, azoknak pusztulásával fognak el is pusztulni,"));
husuperobject.biblemap.put("b61_2_13", new husuperobject(61,2,13,"Megkapván gonoszságuk díját, mint a kik gyönyörûségnek tartják a naponkénti dobzódást; undokságok és fertelmek, a kik kéjelegnek az õ csalárdságukban, mikor együtt lakmároznak veletek;"));
husuperobject.biblemap.put("b61_2_14", new husuperobject(61,2,14,"A kiknek szemei paráznasággal telvék, bûnnel telhetetlenek; elhitetik az állhatatlan lelkeket, szívök gyakorlott a telhetetlenségben, átok gyermekei;"));
husuperobject.biblemap.put("b61_2_15", new husuperobject(61,2,15,"A kik elhagyván az egyenes útat, eltévelyedtek, követvén Bálámnak, Bosor fiának útját, a ki a gonoszság díját kedvelte."));
husuperobject.biblemap.put("b61_2_16", new husuperobject(61,2,16,"De megfeddetett az õ törvénytelenségéért: egy igavonó néma állat emberi szóval szólván, megakadályozta a próféta esztelenségét."));
husuperobject.biblemap.put("b61_2_17", new husuperobject(61,2,17,"Ezek víztelen kútfõk, széltõl hányatott fellegek, a kiknek a sötétség homálya van fenntartva örökre."));
husuperobject.biblemap.put("b61_2_18", new husuperobject(61,2,18,"Mert hiábavalóság kevély [szavait] szólván, testi kívánsággal, bujálkodással elhitetik azokat, a kik valóban elszakadtak a tévelygésben élõktõl,"));
husuperobject.biblemap.put("b61_2_19", new husuperobject(61,2,19,"Szabadságot ígérvén azoknak, holott õk magok a romlottság szolgái; mert a kit valaki legyõzött, az annak szolgájává lett."));
husuperobject.biblemap.put("b61_2_20", new husuperobject(61,2,20,"Mert ha az Úrnak, a megtartó Jézus Krisztusnak megismerése által a világ fertelmeit elkerülték, de ezekbe ismét belekeveredve legyõzetnek, az õ utolsó állapotjuk gonoszabbá lett az elsõnél."));
husuperobject.biblemap.put("b61_2_21", new husuperobject(61,2,21,"Mert jobb volna rájok nézve, ha meg sem ismerték volna az igazság útját, mint hogy megismervén, elpártoljanak a nekik adott szent parancsolattól."));
husuperobject.biblemap.put("b61_2_22", new husuperobject(61,2,22,"De betelt rajtok az igaz példabeszéd [szava:] Az eb visszatért a saját okádására, és a megmosódott disznó a sárnak fertõjébe."));
husuperobject.biblemap.put("b61_3_1", new husuperobject(61,3,1,"Ez immár második levélírásom néktek, szeretteim, amelylyel a ti tiszta gondolkozástokat emlékeztetés által serkentgetem;"));
husuperobject.biblemap.put("b61_3_2", new husuperobject(61,3,2,"Hogy megemlékezzetek a szent prófétáktól ezelõtt mondott beszédekrõl, és az Úrnak és Megtartónak általunk, az apostolok által közölt parancsolatjáról:"));
husuperobject.biblemap.put("b61_3_3", new husuperobject(61,3,3,"Tudván elõször azt, hogy az utolsó idõben csúfolkodók támadnak, a kik saját kívánságaik szerint járnak,"));
husuperobject.biblemap.put("b61_3_4", new husuperobject(61,3,4,"És ezt mondják: Hol van az õ eljövetelének ígérete? Mert a mióta az atyák elhunytak, minden azonképen marad a teremtés kezdetétõl fogva."));
husuperobject.biblemap.put("b61_3_5", new husuperobject(61,3,5,"Mert kész-akarva nem tudják azt, hogy egek régtõl fogva voltak, és föld, mely vízbõl és víz által állott elõ az Isten szavára;"));
husuperobject.biblemap.put("b61_3_6", new husuperobject(61,3,6,"A melyek által az akkori világ vízzel elboríttatván elveszett:"));
husuperobject.biblemap.put("b61_3_7", new husuperobject(61,3,7,"A mostani egek pedig és a föld, ugyanazon szó által megkíméltettek, tûznek tartatván fenn, az ítéletnek és az istentelen emberek romlásának napjára."));
husuperobject.biblemap.put("b61_3_8", new husuperobject(61,3,8,"Ez az egy azonban ne legyen elrejtve elõttetek, szeretteim, hogy egy nap az Úrnál olyan, mint ezer esztendõ, és ezer esztendõ mint egy nap."));
husuperobject.biblemap.put("b61_3_9", new husuperobject(61,3,9,"Nem késik el az ígérettel az Úr, mint némelyek késedelemnek tartják; hanem hosszan tûr érettünk, nem akarván, hogy némelyek elveszszenek, hanem hogy mindenki megtérésre jusson."));
husuperobject.biblemap.put("b61_3_10", new husuperobject(61,3,10,"Az Úr napja pedig úgy jõ majd el, mint éjjeli tolvaj, a mikor az egek ropogva elmúlnak, az elemek pedig megégve felbomlanak, és a föld és a rajta lévõ dolgok is megégnek."));
husuperobject.biblemap.put("b61_3_11", new husuperobject(61,3,11,"Mivelhogy azért mindezek felbomlanak, milyeneknek kell lennetek néktek szent életben és kegyességben,"));
husuperobject.biblemap.put("b61_3_12", new husuperobject(61,3,12,"A kik várjátok és sóvárogjátok az Isten napjának eljövetelét, a melyért az egek tûzbe borulva felbomlanak, és az elemek égve megolvadnak!"));
husuperobject.biblemap.put("b61_3_13", new husuperobject(61,3,13,"De új eget és új földet várunk az õ ígérete szerint, a melyekben igazság lakozik."));
husuperobject.biblemap.put("b61_3_14", new husuperobject(61,3,14,"Annakokáért szeretteim, ezeket várván , igyekezzetek, hogy szeplõ nélkül és hiba nélkül valóknak találjon titeket békességben."));
husuperobject.biblemap.put("b61_3_15", new husuperobject(61,3,15,"És a mi Urunknak hosszútûrését idvességnek tartsátok; a miképen a mi szeretett atyánkfia Pál is írt néktek a néki adott bölcsesség szerint."));
husuperobject.biblemap.put("b61_3_16", new husuperobject(61,3,16,"Szinte minden levelében is, a mikor ezekrõl beszél azokban; a melyekben vannak némely nehezen érthetõ dolgok, a miket a tudatlanok és állhatatlanok elcsûrnek-csavarnak, mint egyéb írásokat is, a magok vesztére."));
husuperobject.biblemap.put("b61_3_17", new husuperobject(61,3,17,"Ti azért szeretteim elõre tudván [ezt], õrizkedjetek, hogy az istentelenek tévelygéseitõl elragadtatva, a saját erõsségetekbõl ki ne essetek;"));
husuperobject.biblemap.put("b61_3_18", new husuperobject(61,3,18,"Hanem növekedjetek a kegyelemben és a mi Urunknak és megtartó Jézus Krisztusunknak ismeretében. Néki [legyen] dicsõség, mind most, mind örökkön-örökké. Ámen."));

}

}