japsuperobject.biblemap.put("b16_10_8", new japsuperobject(16,10,8,"マアジヤ、ビルガイ、シマヤで、これらは祭司である。"));
japsuperobject.biblemap.put("b16_10_9", new japsuperobject(16,10,9,"レビびとではアザニヤの子エシュア、ヘナダデの子らのうちのビンヌイ、カデミエル、"));
japsuperobject.biblemap.put("b16_10_10", new japsuperobject(16,10,10,"およびその兄弟シバニヤ、ホデヤ、ケリタ、ペラヤ、ハナン、"));
japsuperobject.biblemap.put("b16_10_11", new japsuperobject(16,10,11,"ミカ、レホブ、ハシャビヤ、"));
japsuperobject.biblemap.put("b16_10_12", new japsuperobject(16,10,12,"ザックル、セレビヤ、シバニヤ、"));
japsuperobject.biblemap.put("b16_10_13", new japsuperobject(16,10,13,"ホデヤ、バニ、ベニヌである。"));
japsuperobject.biblemap.put("b16_10_14", new japsuperobject(16,10,14,"民のかしらではパロシ、パハテ・モアブ、エラム、ザット、バニ、"));
