japsuperobject.biblemap.put("b6_15_43", new japsuperobject(6,15,43,"イフタ、アシナ、ネジブ、"));
japsuperobject.biblemap.put("b6_15_44", new japsuperobject(6,15,44,"ケイラ、アクジブ、マレシャ。すなわち九つの町々と、それに属する村々。"));
japsuperobject.biblemap.put("b6_15_45", new japsuperobject(6,15,45,"エクロンと、その町々、および村々。"));
japsuperobject.biblemap.put("b6_15_46", new japsuperobject(6,15,46,"エクロンから海まで、すべてアシドドのほとりにある町々、およびそれに属する村々。"));
japsuperobject.biblemap.put("b6_15_47", new japsuperobject(6,15,47,"アシドドとその町々および村々。ガザとその町々および村々。エジプトの川と大海の海岸までが、その境であった。"));
japsuperobject.biblemap.put("b6_15_48", new japsuperobject(6,15,48,"山地では、シャミル、ヤッテル、ソコ、"));
japsuperobject.biblemap.put("b6_15_49", new japsuperobject(6,15,49,"ダンナ、キリアテ・サンナすなわちデビル、"));
japsuperobject.biblemap.put("b6_15_50", new japsuperobject(6,15,50,"アナブ、エシテモ、アニム、"));
japsuperobject.biblemap.put("b6_15_51", new japsuperobject(6,15,51,"ゴセン、ホロン、ギロ。すなわち十一の町々と、それに属する村々。"));
japsuperobject.biblemap.put("b6_15_52", new japsuperobject(6,15,52,"アラブ、ドマ、エシャン、"));
