rusuperobject.biblemap.put("b13_1_16", new rusuperobject(13,1,16,"Арвадея, Цемарея и Хамафея. "));
rusuperobject.biblemap.put("b13_1_17", new rusuperobject(13,1,17,"Сыновья Сима: Елам, Ассур, Арфаксад, Луд и Арам. Сыновья Арама: Уц, Хул, Гефер и Мешех. "));
rusuperobject.biblemap.put("b13_1_18", new rusuperobject(13,1,18,"Арфаксад родил Каинана, Каинан же родил Салу, Сала же родил Евера. "));
rusuperobject.biblemap.put("b13_1_19", new rusuperobject(13,1,19,"У Евера родились два сына: имя одному Фалек, потому что во дни его разделилась земля; имя брату его Иоктан. "));
rusuperobject.biblemap.put("b13_1_20", new rusuperobject(13,1,20,"Иоктан родил Алмодада, Шалефа, Хацармавета, Иераха, "));
rusuperobject.biblemap.put("b13_1_21", new rusuperobject(13,1,21,"Гадорама, Узала, Диклу, "));
