rusuperobject.biblemap.put("b13_2_13", new rusuperobject(13,2,13,"Иессей родил первенца своего Елиава, второго - Аминадава, третьего - Самму, "));
rusuperobject.biblemap.put("b13_2_14", new rusuperobject(13,2,14,"четвертого - Нафанаила, пятого - Раддая, "));
rusuperobject.biblemap.put("b13_2_15", new rusuperobject(13,2,15,"шестого - Оцема, седьмого - Давида. "));
rusuperobject.biblemap.put("b13_2_16", new rusuperobject(13,2,16,"Сестры их: Саруия и Авигея. Сыновья Саруии: Авесса, Иоав и Азаил, трое. "));
rusuperobject.biblemap.put("b13_2_17", new rusuperobject(13,2,17,"Авигея родила Амессу; отец же Амессы - Иефер, Измаильтянин. "));
rusuperobject.biblemap.put("b13_2_18", new rusuperobject(13,2,18,"Халев, сын Есрома, родил от Азувы, жены своей, и от Иериофы, и вот сыновья его: Иешер, Шовав и Ардон. "));
rusuperobject.biblemap.put("b13_2_19", new rusuperobject(13,2,19,"И умерла Азува; и взял себе Халев жену Ефрафу, и она родила ему Хура. "));
rusuperobject.biblemap.put("b13_2_20", new rusuperobject(13,2,20,"Хур родил Урия, Урий родил Веселиила. "));
rusuperobject.biblemap.put("b13_2_21", new rusuperobject(13,2,21,"После Есром вошел к дочери Махира, отца Галаадова, и взял ее, будучи шестидесяти лет, и она родила ему Сегува. "));
rusuperobject.biblemap.put("b13_2_22", new rusuperobject(13,2,22,"Сегув родил Иаира, и было у него двадцать три города в земле Галаадской. "));
rusuperobject.biblemap.put("b13_2_23", new rusuperobject(13,2,23,"Но Гессуряне и Сирияне взяли у них селения Иаира, Кенаф и зависящие от него города, - шестьдесят городов. Все эти города сыновей Махира, отца Галаадова. "));
rusuperobject.biblemap.put("b13_2_24", new rusuperobject(13,2,24,"По смерти Есрома в Халев-Ефрафе, жена Есромова, Авия, родила ему Ашхура, отца Фекои. "));
rusuperobject.biblemap.put("b13_2_25", new rusuperobject(13,2,25,"Сыновья Иерахмеила, первенца Есромова, были: первенец Рам, за ним Вуна, Орен, Оцем и Ахия. "));
rusuperobject.biblemap.put("b13_2_26", new rusuperobject(13,2,26,"Была у Иерахмеила и другая жена, имя ее Афара; она мать Онама. "));
rusuperobject.biblemap.put("b13_2_27", new rusuperobject(13,2,27,"Сыновья Рама, первенца Иерахмеилова, были: Маац, Иамин и Екер. "));
rusuperobject.biblemap.put("b13_2_28", new rusuperobject(13,2,28,"Сыновья Онама были: Шаммай и Иада. Сыновья Шаммая: Надав и Авишур. "));
rusuperobject.biblemap.put("b13_2_29", new rusuperobject(13,2,29,"Имя жене Авишуровой Авихаиль, и она родила ему Ахбана и Молида. "));
rusuperobject.biblemap.put("b13_2_30", new rusuperobject(13,2,30,"Сыновья Надава: Селед и Афаим. И умер Селед бездетным. "));
rusuperobject.biblemap.put("b13_2_31", new rusuperobject(13,2,31,"Сын Афаима: Иший. Сын Ишия: Шешан. Сын Шешана: Ахлай. "));
rusuperobject.biblemap.put("b13_2_32", new rusuperobject(13,2,32,"Сыновья Иады, брата Шаммаева: Иефер и Ионафан. Иефер умер бездетным. "));
rusuperobject.biblemap.put("b13_2_33", new rusuperobject(13,2,33,"Сыновья Ионафана: Пелеф и Заза. Это сыновья Иерахмеила. "));
rusuperobject.biblemap.put("b13_2_34", new rusuperobject(13,2,34,"У Шешана не было сыновей, а только дочери. У Шешана был раб, Египтянин, имя его Иарха; "));
rusuperobject.biblemap.put("b13_2_35", new rusuperobject(13,2,35,"Шешан отдал дочь свою Иархе рабу своему в жену: и она родила ему Аттая. "));
rusuperobject.biblemap.put("b13_2_36", new rusuperobject(13,2,36,"Аттай родил Нафана, Нафан родил Завада; "));
rusuperobject.biblemap.put("b13_2_37", new rusuperobject(13,2,37,"Завад родил Ефлала, Ефлал родил Овида; "));
rusuperobject.biblemap.put("b13_2_38", new rusuperobject(13,2,38,"Овид родил Иеуя, Иеуй родил Азарию; "));
rusuperobject.biblemap.put("b13_2_39", new rusuperobject(13,2,39,"Азария родил Хелеца, Хелец родил Елеасу; "));
rusuperobject.biblemap.put("b13_2_40", new rusuperobject(13,2,40,"Елеаса родил Сисмая, Сисмай родил Саллума; "));
rusuperobject.biblemap.put("b13_2_41", new rusuperobject(13,2,41,"Саллум родил Иекамию, Иекамия родил Елишаму. "));
rusuperobject.biblemap.put("b13_2_42", new rusuperobject(13,2,42,"Сыновья Халева, брата Иерахмеилова: Меша, первенец его, - он отец Зифа; и сыновья Мареши, отца Хеврона. "));
rusuperobject.biblemap.put("b13_2_43", new rusuperobject(13,2,43,"Сыновья Хеврона: Корей и Таппуах, и Рекем и Шема. "));
rusuperobject.biblemap.put("b13_2_44", new rusuperobject(13,2,44,"Шема родил Рахама, отца Иоркеамова, а Рекем родил Шаммая. "));
rusuperobject.biblemap.put("b13_2_45", new rusuperobject(13,2,45,"Сын Шаммая Маон, а Маон - отец Беф-Цура. "));
rusuperobject.biblemap.put("b13_2_46", new rusuperobject(13,2,46,"И Ефа, наложница Халевова, родила Харана, Моцу и Газеза. И Харан родил Газеза. "));
rusuperobject.biblemap.put("b13_2_47", new rusuperobject(13,2,47,"Сыновья Иегдая: Регем, Иофам, Гешан, Пелет, Ефа и Шааф. "));
rusuperobject.biblemap.put("b13_2_48", new rusuperobject(13,2,48,"Наложница Халевова, Мааха, родила Шевера и Фирхану; "));
rusuperobject.biblemap.put("b13_2_49", new rusuperobject(13,2,49,"она же родила Шаафа, отца Мадманны, Шеву, отца Махбены и отца Гивеи. Дочь же Халева - Ахса. "));
rusuperobject.biblemap.put("b13_2_50", new rusuperobject(13,2,50,"Вот сыновья Халева: сын Хур, первенец Ефрафы; Шовал, отец Кириаф-Иарима; "));
rusuperobject.biblemap.put("b13_2_51", new rusuperobject(13,2,51,"Салма, отец Вифлеема; Хареф, отец Бефгадера. "));
rusuperobject.biblemap.put("b13_2_52", new rusuperobject(13,2,52,"У Шовала, отца Кириаф-Иарима, были сыновья: Гарое, Хаци, Галменюхот. "));
rusuperobject.biblemap.put("b13_2_53", new rusuperobject(13,2,53,"Племена Кириаф-Иарима: Ифрияне, Футияне, Шумафане и Мидраитяне. От сих произошли Цоряне и Ештаоляне. "));
rusuperobject.biblemap.put("b13_2_54", new rusuperobject(13,2,54,"Сыновья Салмы: Вифлеемляне и Нетофафяне, венец дома Иоавова и половина Менухотян - Цоряне, "));
rusuperobject.biblemap.put("b13_2_55", new rusuperobject(13,2,55,"и племена Соферийцев, живших в Иабеце, Тирейцы, Шимейцы, Сухайцы: это Кинеяне, происшедшие от Хамафа, отца Бетрехава. "));
rusuperobject.biblemap.put("b13_3_1", new rusuperobject(13,3,1,"Сыновья Давида, родившиеся у него в Хевроне, были: первенец Амнон, от Ахиноамы Изреелитянки; второй - Далуия, от Авигеи Кармилитянки; "));
rusuperobject.biblemap.put("b13_3_2", new rusuperobject(13,3,2,"третий - Авессалом, сын Маахи, дочери Фалмая, царя Гессурского; четвертый - Адония, сын Аггифы; "));
rusuperobject.biblemap.put("b13_3_3", new rusuperobject(13,3,3,"пятый - Сафатия, от Авиталы; шестой - Ифреам, от Аглаи, жены его, - "));
rusuperobject.biblemap.put("b13_3_4", new rusuperobject(13,3,4,"шесть родившихся у него в Хевроне; царствовал же он там семь лет и шесть месяцев; а тридцать три года царствовал в Иерусалиме. "));
rusuperobject.biblemap.put("b13_3_5", new rusuperobject(13,3,5,"А сии родились у него в Иерусалиме: Шима, Шовав, Нафан и Соломон, четверо от Вирсавии, дочери Аммииловой; "));
rusuperobject.biblemap.put("b13_3_6", new rusuperobject(13,3,6,"Ивхар, Елишама, Елифелет, "));
rusuperobject.biblemap.put("b13_3_7", new rusuperobject(13,3,7,"Ногаг, Нефег, Иафиа, "));
rusuperobject.biblemap.put("b13_3_8", new rusuperobject(13,3,8,"Елишама, Елиада и Елифелет - девятеро. "));
rusuperobject.biblemap.put("b13_3_9", new rusuperobject(13,3,9,"Вот все сыновья Давида, кроме сыновей от наложниц. Сестра их Фамарь. "));
rusuperobject.biblemap.put("b13_3_10", new rusuperobject(13,3,10,"Сын Соломона Ровоам; его сын Авия, его сын Аса, его сын Иосафат, "));
rusuperobject.biblemap.put("b13_3_11", new rusuperobject(13,3,11,"его сын Иорам, его сын Охозия, его сын Иоас, "));
rusuperobject.biblemap.put("b13_3_12", new rusuperobject(13,3,12,"его сын Амасия, его сын Азария, его сын Иофам, "));
rusuperobject.biblemap.put("b13_3_13", new rusuperobject(13,3,13,"его сын Ахаз, его сын Езекия, его сын Манассия, "));
rusuperobject.biblemap.put("b13_3_14", new rusuperobject(13,3,14,"его сын Амон, его сын Иосия. "));
rusuperobject.biblemap.put("b13_3_15", new rusuperobject(13,3,15,"Сыновья Иосии: первенец Иоахаз, второй Иоаким, третий Седекия, четвертый Селлум. "));
rusuperobject.biblemap.put("b13_3_16", new rusuperobject(13,3,16,"Сыновья Иоакима: Иехония, сын его; Седекия, сын его. "));
rusuperobject.biblemap.put("b13_3_17", new rusuperobject(13,3,17,"Сыновья Иехонии: Асир, Салафиил, сын его; "));
rusuperobject.biblemap.put("b13_3_18", new rusuperobject(13,3,18,"Малкирам, Федаия, Шенацар, Иезекия, Гошама и Савадия. "));
rusuperobject.biblemap.put("b13_3_19", new rusuperobject(13,3,19,"И сыновья Федаии: Зоровавель и Шимей. Сыновья же Зоровавеля: Мешуллам и Ханания, и Шеломиф, сестра их, "));
rusuperobject.biblemap.put("b13_3_20", new rusuperobject(13,3,20,"и еще пять: Хашува, Огел, Берехия, Хасадия и Иушав-Хесед. "));
rusuperobject.biblemap.put("b13_3_21", new rusuperobject(13,3,21,"И сыновья Ханании: Фелатия и Исаия; его сын Рефаия, его сын Арнан, его сын Овадия, его сын Шехания. "));
rusuperobject.biblemap.put("b13_3_22", new rusuperobject(13,3,22,"Сын Шехании: Шемаия; сыновья Шемаии: Хаттуш, Игеал, Бариах, Неария и Шафат, шестеро. "));
rusuperobject.biblemap.put("b13_3_23", new rusuperobject(13,3,23,"Сыновья Неарии: Елиоенай, Езекия и Азрикам, трое. "));
rusuperobject.biblemap.put("b13_3_24", new rusuperobject(13,3,24,"Сыновья Елиоеная: Годавьягу, Елеашив, Фелаия, Аккув, Иоханан, Делаия и Анани, семеро. "));
rusuperobject.biblemap.put("b13_4_1", new rusuperobject(13,4,1,"Сыновья Иуды: Фарес, Есром, Харми, Хур и Шовал. "));
rusuperobject.biblemap.put("b13_4_2", new rusuperobject(13,4,2,"Реаия, сын Шовала, родил Иахафа; Иахаф родил Ахума и Лагада: от них племена Цорян. "));
rusuperobject.biblemap.put("b13_4_3", new rusuperobject(13,4,3,"И сии сыновья Етама: Изреель, Ишма и Идбаш, и сестра их, по имени Гацлелпони, "));
rusuperobject.biblemap.put("b13_4_4", new rusuperobject(13,4,4,"Пенуел, отец Гедора, и Езер, отец Хуша. Вот сыновья Хура, первенца Ефрафы, отца Вифлеема. "));
rusuperobject.biblemap.put("b13_4_5", new rusuperobject(13,4,5,"У Ахшура, отца Фекои, были две жены: Хела и Наара. "));
rusuperobject.biblemap.put("b13_4_6", new rusuperobject(13,4,6,"И родила ему Наара Ахузама, Хефера, Фимни и Ахашфари; это сыновья Наары. "));
rusuperobject.biblemap.put("b13_4_7", new rusuperobject(13,4,7,"Сыновья Хелы: Цереф, Цохар и Ефнан. "));
rusuperobject.biblemap.put("b13_4_8", new rusuperobject(13,4,8,"Коц родил: Анува и Цовева и племена Ахархела, сына Гарумова. "));
rusuperobject.biblemap.put("b13_4_9", new rusuperobject(13,4,9,"Иавис был знаменитее своих братьев. Мать дала ему имя Иавис, сказав: я родила его с болезнью. "));
rusuperobject.biblemap.put("b13_4_10", new rusuperobject(13,4,10,"И воззвал Иавис к Богу Израилеву и сказал: о, если бы Ты благословил меня Твоим благословением, распространил пределы мои, и рука Твоя была со мною, охраняя меня от зла, чтобы я не горевал!.. И Бог ниспослал ему, чего он просил. "));
rusuperobject.biblemap.put("b13_4_11", new rusuperobject(13,4,11,"Хелув же, брат Шухи, родил Махира; он есть отец Ештона. "));
rusuperobject.biblemap.put("b13_4_12", new rusuperobject(13,4,12,"Ештон родил Беф-Рафу, Пасеаха и Техинну, отца города Нааса брата Селома Кенезиина и Ахазова; это жители Рехи. "));
rusuperobject.biblemap.put("b13_4_13", new rusuperobject(13,4,13,"Сыновья Кеназа: Гофониил и Сераия. Сын Гофониила: Хафаф. "));
rusuperobject.biblemap.put("b13_4_14", new rusuperobject(13,4,14,"Меонофай родил Офру, а Сераия родил Иоава, родоначальника долины плотников, потому что они были плотники. "));
rusuperobject.biblemap.put("b13_4_15", new rusuperobject(13,4,15,"Сыновья Халева, сына Иефонниина: Ир, Ила и Наам. Сын Илы: Кеназ. "));
rusuperobject.biblemap.put("b13_4_16", new rusuperobject(13,4,16,"Сыновья Иегаллелела: Зиф, Зифа, Фирия и Асареел. "));
rusuperobject.biblemap.put("b13_4_17", new rusuperobject(13,4,17,"Сыновья Езры: Иефер, Меред, Ефер и Иалон; Иефер же родил Мерома, Шаммая и Ишбаха, отца Ешфемои. "));
rusuperobject.biblemap.put("b13_4_18", new rusuperobject(13,4,18,"И жена его Иудия родила Иереда, отца Гедора, и Хевера, отца Сохо, и Иекуфиила, отца Занаоха. Это сыновья Бифьи, дочери фараоновой, которую взял Меред. "));
rusuperobject.biblemap.put("b13_4_19", new rusuperobject(13,4,19,"Сыновья жены его Годии, сестры Нахама, отца Кеилы: Гарми и Ешфемоа - Маахатянин. "));
rusuperobject.biblemap.put("b13_4_20", new rusuperobject(13,4,20,"Сыновья Симеона: Амнон, Ринна, Бенханан и Филон. Сыновья Ишия: Зохеф и Бензохеф. "));
rusuperobject.biblemap.put("b13_4_21", new rusuperobject(13,4,21,"Сыновья Силома, сына Иудина: Ир, отец Лехи, и Лаеда, отец Мареши, и семейства выделывавших виссон, из дома Ашбеи, "));
rusuperobject.biblemap.put("b13_4_22", new rusuperobject(13,4,22,"и Иоким, и жители Хозевы, и Иоаш и Сараф, которые имели владение в Моаве, и Иашувилехем; но это события древние. "));
rusuperobject.biblemap.put("b13_4_23", new rusuperobject(13,4,23,"Они были горшечники, и жили при садах и в огородах; у царя для работ его жили они там. "));
rusuperobject.biblemap.put("b13_4_24", new rusuperobject(13,4,24,"Сыновья Симеона: Немуил, Иамин, Иарив, Зерах и Саул. "));
rusuperobject.biblemap.put("b13_4_25", new rusuperobject(13,4,25,"Шаллум сын его; его сын Мивсам; его сын Мишма. "));
rusuperobject.biblemap.put("b13_4_26", new rusuperobject(13,4,26,"Сыновья Мишмы: Хаммуил, сын его; его сын Закур; его сын Шимей. "));
rusuperobject.biblemap.put("b13_4_27", new rusuperobject(13,4,27,"У Шимея было шестнадцать сыновей и шесть дочерей; у братьев же его сыновей было немного, и все племя их не так было многочисленно, как племя сынов Иуды. "));
rusuperobject.biblemap.put("b13_4_28", new rusuperobject(13,4,28,"Они жили в Вирсавии, Моладе, Хацаршуале, "));
rusuperobject.biblemap.put("b13_4_29", new rusuperobject(13,4,29,"в Билге, в Ецеме, в Фоладе, "));
rusuperobject.biblemap.put("b13_4_30", new rusuperobject(13,4,30,"в Вефуиле, в Хорме, в Циклаге, "));
rusuperobject.biblemap.put("b13_4_31", new rusuperobject(13,4,31,"в Беф-Маркавофе, в Хацарсусиме, в Беф-Биреи и в Шаариме. Вот города их до царствования Давидова, "));
rusuperobject.biblemap.put("b13_4_32", new rusuperobject(13,4,32,"с селами их: Етам, Аин, Риммон, Фокен и Ашан, - пять городов. "));
rusuperobject.biblemap.put("b13_4_33", new rusuperobject(13,4,33,"И все селения их, которые находились вокруг сих городов до Ваала; вот места жительства их и родословия их. "));
rusuperobject.biblemap.put("b13_4_34", new rusuperobject(13,4,34,"Мешовав, Иамлех и Иосия, сын Амассии, "));
rusuperobject.biblemap.put("b13_4_35", new rusuperobject(13,4,35,"Иоил и Иегу, сын Иошиви, сына Сераии, сына Асиилова, "));
rusuperobject.biblemap.put("b13_4_36", new rusuperobject(13,4,36,"Елиоенай, Иакова, Ишохаия, Асаия, Адиил, Ишимиил и Ванея, "));
rusuperobject.biblemap.put("b13_4_37", new rusuperobject(13,4,37,"и Зиза, сын Шифия, сын Аллона, сын Иедаии, сын Шимрия, сын Шемаии. "));
rusuperobject.biblemap.put("b13_4_38", new rusuperobject(13,4,38,"Сии поименованные были князьями племен своих, и дом отцов их разделился на многие отрасли. "));
rusuperobject.biblemap.put("b13_4_39", new rusuperobject(13,4,39,"Они доходили до Герары и до восточной стороны долины, чтобы найти пастбища для стад своих; "));
rusuperobject.biblemap.put("b13_4_40", new rusuperobject(13,4,40,"и нашли пастбища тучные и хорошие и землю обширную, спокойную и безопасную, потому что до них жило там только немного Хамитян. "));
rusuperobject.biblemap.put("b13_4_41", new rusuperobject(13,4,41,"И пришли сии, по именам записанные, во дни Езекии, царя Иудейского, и перебили кочующих и оседлых, которые там находились, и истребили их навсегда и поселились на месте их, ибо там были пастбища для стад их. "));
rusuperobject.biblemap.put("b13_4_42", new rusuperobject(13,4,42,"Из них же, из сынов Симеоновых, пошли к горе Сеир пятьсот человек: Фелатия, Неария, Рефаия и Узиил, сыновья Ишия, были во главе их; "));
rusuperobject.biblemap.put("b13_4_43", new rusuperobject(13,4,43,"и побили уцелевший там остаток Амаликитян, и живут там до сего дня. "));
rusuperobject.biblemap.put("b13_5_1", new rusuperobject(13,5,1,"Сыновья Рувима, первенца Израилева, - он первенец; но, когда осквернил он постель отца своего, первенство его отдано сыновьям Иосифа, сына Израилева, с тем однакож, чтобы не писаться им первородными; "));
rusuperobject.biblemap.put("b13_5_2", new rusuperobject(13,5,2,"потому что Иуда был сильнейшим из братьев своих, и вождь от него, но первенство перенесено на Иосифа. "));
rusuperobject.biblemap.put("b13_5_3", new rusuperobject(13,5,3,"Сыновья Рувима, первенца Израилева: Ханох, Фаллу, Хецрон и Харми. "));
rusuperobject.biblemap.put("b13_5_4", new rusuperobject(13,5,4,"Сыновья Иоиля: Шемая, сын его; его сын Гог, его сын Шимей, "));
rusuperobject.biblemap.put("b13_5_5", new rusuperobject(13,5,5,"его сын Миха, его сын Реаия, его сын Ваал, "));
rusuperobject.biblemap.put("b13_5_6", new rusuperobject(13,5,6,"его сын Беера, которого отвел в плен Феглафелласар, царь Ассирийский. Он был князем Рувимлян. "));
rusuperobject.biblemap.put("b13_5_7", new rusuperobject(13,5,7,"И братья его, по племенам их, по родословному списку их, были: главный Иеиель, потом Захария, "));
rusuperobject.biblemap.put("b13_5_8", new rusuperobject(13,5,8,"и Бела, сын Азаза, сына Шемы, сына Иоиля. Он обитал в Ароере до Нево и Ваал-Меона; "));
rusuperobject.biblemap.put("b13_5_9", new rusuperobject(13,5,9,"а к востоку он обитал до входа в пустыню, идущую от реки Евфрата, потому что стада их были многочисленны в земле Галаадской. "));
rusuperobject.biblemap.put("b13_5_10", new rusuperobject(13,5,10,"Во дни Саула они вели войну с Агарянами, которые пали от рук их, а они стали жить в шатрах и по всей восточной стороне Галаада. "));
rusuperobject.biblemap.put("b13_5_11", new rusuperobject(13,5,11,"Сыновья Гада жили напротив их в земле Васанской до Салхи: "));
rusuperobject.biblemap.put("b13_5_12", new rusuperobject(13,5,12,"в Васане Иоиль был главный, Шафан второй, потом Иаанай и Шафат. "));
rusuperobject.biblemap.put("b13_5_13", new rusuperobject(13,5,13,"Братьев их с семействами их было семь: Михаил, Мешуллам, Шева, Иорай, Иаакан, Зия и Евер. "));
rusuperobject.biblemap.put("b13_5_14", new rusuperobject(13,5,14,"Вот сыновья Авихаила, сына Хурия, сына Иароаха, сына Галаада, сына Михаила, сына Иешишая, сына Иахдо, сына Буза. "));
rusuperobject.biblemap.put("b13_5_15", new rusuperobject(13,5,15,"Ахи, сын Авдиила, сына Гуниева, был главою своего рода. "));
rusuperobject.biblemap.put("b13_5_16", new rusuperobject(13,5,16,"Они жили в Галааде, в Васане и в зависящих от него городах и во всех окрестностях Сарона, до исхода их. "));
rusuperobject.biblemap.put("b13_5_17", new rusuperobject(13,5,17,"Все они перечислены во дни Иоафама, царя Иудейского, и во дни Иеровоама, царя Израильского. "));
rusuperobject.biblemap.put("b13_5_18", new rusuperobject(13,5,18,"У потомков Рувима и Гада и полуплемени Манассиина было людей воинственных, мужей носящих щит и меч, стреляющих из лука и приученных к битве, сорок четыре тысячи семьсот шестьдесят, выходящих на войну. "));
rusuperobject.biblemap.put("b13_5_19", new rusuperobject(13,5,19,"И воевали они с Агарянами, Иетуром, Нафишем и Надавом. "));
rusuperobject.biblemap.put("b13_5_20", new rusuperobject(13,5,20,"И подана была им помощь против них, и преданы были в руки их Агаряне и все, что у них было, потому что они во время сражения воззвали к Богу, и Он услышал их, за то, что они уповали на Него. "));
rusuperobject.biblemap.put("b13_5_21", new rusuperobject(13,5,21,"И взяли они стада их: верблюдов пятьдесят тысяч, из мелкого скота двести пятьдесят тысяч, ослов две тысячи, и сто тысяч душ людей, "));
rusuperobject.biblemap.put("b13_5_22", new rusuperobject(13,5,22,"потому что много пало убитых, так как от Бога было сражение сие. И жили они на месте их до переселения. "));
rusuperobject.biblemap.put("b13_5_23", new rusuperobject(13,5,23,"Потомки полуколена Манассиина жили в той земле, от Васана до Ваал-Ермона и Сенира и до горы Ермона; и их было много. "));
rusuperobject.biblemap.put("b13_5_24", new rusuperobject(13,5,24,"И вот главы поколений их: Ефер, Ишьи, Елиил, Азриил, Иеремия, Годавия и Иагдиил, мужи мощные, мужи именитые, главы родов своих. "));
rusuperobject.biblemap.put("b13_5_25", new rusuperobject(13,5,25,"Но когда они согрешили против Бога отцов своих и стали блудно ходить вслед богов народов той земли, которых изгнал Бог от лица их, "));
rusuperobject.biblemap.put("b13_5_26", new rusuperobject(13,5,26,"тогда Бог Израилев возбудил дух Фула, царя Ассирийского, и дух Феглафелласара, царя Ассирийского, и он выселил Рувимлян и Гадитян и половину колена Манассиина, и отвел их в Халах, и Хавор, и Ару, и на реку Гозан, - где они до сего дня. "));
rusuperobject.biblemap.put("b13_6_1", new rusuperobject(13,6,1,"Сыновья Левия: Гирсон, Кааф и Мерари. "));
rusuperobject.biblemap.put("b13_6_2", new rusuperobject(13,6,2,"Сыновья Каафа: Амрам, Ицгар, Хеврон и Узиил. "));
rusuperobject.biblemap.put("b13_6_3", new rusuperobject(13,6,3,"Дети Амрама: Аарон, Моисей и Мариам. Сыновья Аарона: Надав, Авиуд, Елеазар и Ифамар. "));
rusuperobject.biblemap.put("b13_6_4", new rusuperobject(13,6,4,"Елеазар родил Финееса, Финеес родил Авишуя; "));
rusuperobject.biblemap.put("b13_6_5", new rusuperobject(13,6,5,"Авишуй родил Буккия, Буккий родил Озию; "));
rusuperobject.biblemap.put("b13_6_6", new rusuperobject(13,6,6,"Озия родил Зерахию, Зерахия родил Мераиофа; "));
rusuperobject.biblemap.put("b13_6_7", new rusuperobject(13,6,7,"Мераиоф родил Амарию, Амария родил Ахитува; "));
rusuperobject.biblemap.put("b13_6_8", new rusuperobject(13,6,8,"Ахитув родил Садока, Садок родил Ахимааса; "));
rusuperobject.biblemap.put("b13_6_9", new rusuperobject(13,6,9,"Ахимаас родил Азарию, Азария родил Иоанана; "));
rusuperobject.biblemap.put("b13_6_10", new rusuperobject(13,6,10,"Иоанан родил Азарию, - это тот, который был священником в храме, построенном Соломоном в Иерусалиме. "));
rusuperobject.biblemap.put("b13_6_11", new rusuperobject(13,6,11,"И родил Азария Амарию, Амария родил Ахитува; "));
rusuperobject.biblemap.put("b13_6_12", new rusuperobject(13,6,12,"Ахитув родил Садока, Садок родил Селлума; "));
rusuperobject.biblemap.put("b13_6_13", new rusuperobject(13,6,13,"Селлум родил Хелкию, Хелкия родил Азарию; "));
rusuperobject.biblemap.put("b13_6_14", new rusuperobject(13,6,14,"Азария родил Сераию, Сераия родил Иоседека. "));
rusuperobject.biblemap.put("b13_6_15", new rusuperobject(13,6,15,"Иоседек пошел в плен, когда Господь переселил Иудеев и Иерусалимлян рукою Навуходоносора. "));
rusuperobject.biblemap.put("b13_6_16", new rusuperobject(13,6,16,"Итак сыновья Левия: Гирсон, Кааф и Мерари. "));
rusuperobject.biblemap.put("b13_6_17", new rusuperobject(13,6,17,"Вот имена сыновей Гирсоновых: Ливни и Шимей. "));
rusuperobject.biblemap.put("b13_6_18", new rusuperobject(13,6,18,"Сыновья Каафа: Амрам, Ицгар, Хеврон и Узиил. "));
rusuperobject.biblemap.put("b13_6_19", new rusuperobject(13,6,19,"Сыновья Мерари: Махли и Муши. Вот потомки Левия по родам их. "));
rusuperobject.biblemap.put("b13_6_20", new rusuperobject(13,6,20,"У Гирсона: Ливни, сын его; Иахав, сын его; Зимма, сын его; "));
rusuperobject.biblemap.put("b13_6_21", new rusuperobject(13,6,21,"Иоах, сын его; Иддо, сын его; Зерах, сын его; Иеафрай, сын его. "));
rusuperobject.biblemap.put("b13_6_22", new rusuperobject(13,6,22,"Сыновья Каафа: Аминадав, сын его; Корей, сын его; Асир, сын его; "));
rusuperobject.biblemap.put("b13_6_23", new rusuperobject(13,6,23,"Елкана, сын его; Евиасаф, сын его; Асир, сын его; "));
rusuperobject.biblemap.put("b13_6_24", new rusuperobject(13,6,24,"Тахаф, сын его; Уриил, сын его; Узия, сын его; Саул, сын его. "));
rusuperobject.biblemap.put("b13_6_25", new rusuperobject(13,6,25,"Сыновья Елканы: Амасай и Ахимоф. "));
rusuperobject.biblemap.put("b13_6_26", new rusuperobject(13,6,26,"Елкана, сын его; Цофай, сын его; Нахаф, сын его; "));
rusuperobject.biblemap.put("b13_6_27", new rusuperobject(13,6,27,"Елиаф, сын его; Иерохам, сын его, Елкана, сын его; Самуил, сын его. "));
rusuperobject.biblemap.put("b13_6_28", new rusuperobject(13,6,28,"Сыновья Самуила: первенец Иоиль, второй Авия. "));
rusuperobject.biblemap.put("b13_6_29", new rusuperobject(13,6,29,"Сыновья Мерари: Махли; Ливни, сын его; Шимей, сын его; Уза, сын его; "));
rusuperobject.biblemap.put("b13_6_30", new rusuperobject(13,6,30,"Шима, сын его; Хаггия, сын его; Асаия, сын его. "));
rusuperobject.biblemap.put("b13_6_31", new rusuperobject(13,6,31,"Вот те, которых Давид поставил начальниками над певцами в доме Господнем, со времени поставления в нем ковчега. "));
rusuperobject.biblemap.put("b13_6_32", new rusuperobject(13,6,32,"Они служили певцами пред скиниею собрания, доколе Соломон не построил дома Господня в Иерусалиме. И они становились на службу свою по уставу своему. "));
rusuperobject.biblemap.put("b13_6_33", new rusuperobject(13,6,33,"Вот те, которые становились с сыновьями своими: из сыновей Каафовых - Еман певец, сын Иоиля, сын Самуила, "));
rusuperobject.biblemap.put("b13_6_34", new rusuperobject(13,6,34,"сын Елканы, сын Иерохама, сын Елиила, сын Тоаха, "));
rusuperobject.biblemap.put("b13_6_35", new rusuperobject(13,6,35,"сын Цуфа, сын Елканы, сын Махафа, сын Амасая, "));
rusuperobject.biblemap.put("b13_6_36", new rusuperobject(13,6,36,"сын Елканы, сын Иоиля, сын Азарии, сын Цефании, "));
rusuperobject.biblemap.put("b13_6_37", new rusuperobject(13,6,37,"сын Тахафа, сын Асира, сын Авиасафа, сын Корея, "));
rusuperobject.biblemap.put("b13_6_38", new rusuperobject(13,6,38,"сын Ицгара, сын Каафа, сын Левия, сын Израиля; "));
rusuperobject.biblemap.put("b13_6_39", new rusuperobject(13,6,39,"и брат его Асаф, стоявший на правой стороне его, - Асаф, сын Берехии, сын Шимы, "));
rusuperobject.biblemap.put("b13_6_40", new rusuperobject(13,6,40,"сын Михаила, сын Ваасеи, сын Малхии, "));
rusuperobject.biblemap.put("b13_6_41", new rusuperobject(13,6,41,"сын Ефния, сын Зераха, сын Адаии, "));
rusuperobject.biblemap.put("b13_6_42", new rusuperobject(13,6,42,"сын Ефана, сын Зиммы, сын Шимия, "));
rusuperobject.biblemap.put("b13_6_43", new rusuperobject(13,6,43,"сын Иахафа, сын Гирсона, сын Левия. "));
rusuperobject.biblemap.put("b13_6_44", new rusuperobject(13,6,44,"А из сыновей Мерари, братьев их, - на левой стороне: Ефан, сын Кишия, сын Авдия, сын Маллуха, "));
rusuperobject.biblemap.put("b13_6_45", new rusuperobject(13,6,45,"сын Хашавии, сын Амасии, сын Хелкии, "));
rusuperobject.biblemap.put("b13_6_46", new rusuperobject(13,6,46,"сын Амция, сын Вания, сын Шемера, "));
rusuperobject.biblemap.put("b13_6_47", new rusuperobject(13,6,47,"сын Махлия, сын Мушия, сын Мерари, сын Левия. "));
rusuperobject.biblemap.put("b13_6_48", new rusuperobject(13,6,48,"Братья их левиты определены на всякие службы при доме Божием; "));
rusuperobject.biblemap.put("b13_6_49", new rusuperobject(13,6,49,"Аарон же и сыновья его сожигали на жертвеннике всесожжения и на жертвеннике кадильном, и совершали всякое священнодействие во Святом Святых и для очищения Израиля во всем, как заповедал раб Божий Моисей. "));
rusuperobject.biblemap.put("b13_6_50", new rusuperobject(13,6,50,"Вот сыновья Аарона: Елеазар, сын его; Финеес, сын его; Авиуд, сын его; "));
rusuperobject.biblemap.put("b13_6_51", new rusuperobject(13,6,51,"Буккий, сын его; Уззий, сын его; Зерахия, сын его; "));
rusuperobject.biblemap.put("b13_6_52", new rusuperobject(13,6,52,"Мераиоф, сын его; Амария, сын его; Ахитув, сын его; "));
rusuperobject.biblemap.put("b13_6_53", new rusuperobject(13,6,53,"Садок, сын его; Ахимаас, сын его. "));
rusuperobject.biblemap.put("b13_6_54", new rusuperobject(13,6,54,"И вот жилища их по селениям их в пределах их: сыновьям Аарона из племени Каафова, так как жребий выпал им, "));
rusuperobject.biblemap.put("b13_6_55", new rusuperobject(13,6,55,"дали Хеврон, в земле Иудиной, и предместья его вокруг его; "));
rusuperobject.biblemap.put("b13_6_56", new rusuperobject(13,6,56,"поля же сего города и села его отдали Халеву, сыну Иефонниину. "));
rusuperobject.biblemap.put("b13_6_57", new rusuperobject(13,6,57,"Сыновьям Аарона дали также города убежищ: Хеврон и Ливну с их предместьями, Иаттир и Ештемоа и предместья его, "));
rusuperobject.biblemap.put("b13_6_58", new rusuperobject(13,6,58,"и Хилен и предместья его, Давир и предместья его, "));
rusuperobject.biblemap.put("b13_6_59", new rusuperobject(13,6,59,"и Ашан и предместья его, Вефсамис и предместья его, "));
rusuperobject.biblemap.put("b13_6_60", new rusuperobject(13,6,60,"а от колена Вениаминова - Геву и предместья ее, и Аллемеф и предместья его, и Анафоф и предместья его: всех городов их в племенах их тринадцать городов. "));
rusuperobject.biblemap.put("b13_6_61", new rusuperobject(13,6,61,"Остальным сыновьям Каафа, из семейств этого колена, дано по жребию десять городов из удела половины колена Манассиина. "));
rusuperobject.biblemap.put("b13_6_62", new rusuperobject(13,6,62,"Сыновьям Гирсона по племенам их, от колена Иссахарова, и от колена Асирова, и от колена Неффалимова, и от колена Манассиина в Васане, дано тринадцать городов. "));
rusuperobject.biblemap.put("b13_6_63", new rusuperobject(13,6,63,"Сыновьям Мерари по племенам их, от колена Рувимова, и от колена Гадова, и от колена Завулонова, дано по жребию двенадцать городов. "));
rusuperobject.biblemap.put("b13_6_64", new rusuperobject(13,6,64,"Так дали сыны Израилевы левитам города и предместья их. "));
rusuperobject.biblemap.put("b13_6_65", new rusuperobject(13,6,65,"Дали они по жребию от колена сыновей Иудиных, и от колена сыновей Симеоновых, и от колена сыновей Вениаминовых те города, которые они назвали по именам. "));
rusuperobject.biblemap.put("b13_6_66", new rusuperobject(13,6,66,"Некоторым же племенам сыновей Каафовых даны были города от колена Ефремова. "));
rusuperobject.biblemap.put("b13_6_67", new rusuperobject(13,6,67,"И дали им города убежищ: Сихем и предместья его на горе Ефремовой, и Гезер и предместья его, "));
rusuperobject.biblemap.put("b13_6_68", new rusuperobject(13,6,68,"и Иокмеам и предместья его, и Беф-Орон и предместья его, "));
rusuperobject.biblemap.put("b13_6_69", new rusuperobject(13,6,69,"и Аиалон и предместья его, и Гаф-Риммон и предместья его; "));
rusuperobject.biblemap.put("b13_6_70", new rusuperobject(13,6,70,"от половины колена Манассиина - Анер и предместья его, Билеам и предместья его. Это поколению остальных сыновей Каафовых. "));
rusuperobject.biblemap.put("b13_6_71", new rusuperobject(13,6,71,"Сыновьям Гирсона от племени полуколена Манассиина дали Голан в Васане и предместья его, и Аштароф и предместья его. "));
rusuperobject.biblemap.put("b13_6_72", new rusuperobject(13,6,72,"От колена Иссахарова - Кедес и предместья его, Давраф и предместья его, "));
rusuperobject.biblemap.put("b13_6_73", new rusuperobject(13,6,73,"и Рамоф и предместья его, и Анем и предместья его; "));
rusuperobject.biblemap.put("b13_6_74", new rusuperobject(13,6,74,"от колена Асирова - Машал и предместья его, и Авдон и предместья его, "));
rusuperobject.biblemap.put("b13_6_75", new rusuperobject(13,6,75,"и Хукок и предместья его, и Рехов и предместья его; "));
rusuperobject.biblemap.put("b13_6_76", new rusuperobject(13,6,76,"от колена Неффалимова - Кедес в Галилее и предместья его, и Хаммон и предместья его, и Кириафаим и предместья его. "));
rusuperobject.biblemap.put("b13_6_77", new rusuperobject(13,6,77,"А прочим сыновьям Мерариным - от колена Завулонова Риммон и предместья его, Фавор и предместья его. "));
rusuperobject.biblemap.put("b13_6_78", new rusuperobject(13,6,78,"По ту сторону Иордана, против Иерихона, на восток от Иордана, от колена Рувимова дали Восор в пустыне и предместья его, и Иаацу и предместья ее, "));
rusuperobject.biblemap.put("b13_6_79", new rusuperobject(13,6,79,"и Кедемоф и предместья его, и Мефааф и предместья его; "));
rusuperobject.biblemap.put("b13_6_80", new rusuperobject(13,6,80,"от колена Гадова - Рамоф в Галааде и предместья его, и Маханаим и предместья его, "));
rusuperobject.biblemap.put("b13_6_81", new rusuperobject(13,6,81,"и Есевон и предместья его, и Иазер и предместья его. "));
rusuperobject.biblemap.put("b13_7_1", new rusuperobject(13,7,1,"Сыновья Иссахара: Фола, Фуа, Иашув и Шимрон, четверо. "));
rusuperobject.biblemap.put("b13_7_2", new rusuperobject(13,7,2,"Сыновья Фолы: Уззий, Рефаия, Иериил, Иахмай, Ивсам и Самуил, главные в поколениях Фолы, люди воинственные в своих поколениях; число их во дни Давида было двадцать две тысячи и шестьсот. "));
rusuperobject.biblemap.put("b13_7_3", new rusuperobject(13,7,3,"Сын Уззия: Израхия; а сыновья Израхии: Михаил, Овадиа, Иоиль и Ишшия, пятеро. Все они главные. "));
rusuperobject.biblemap.put("b13_7_4", new rusuperobject(13,7,4,"У них, по родам их, по поколениям их, было готово к сражению войска тридцать шесть тысяч; потому что у них было много жен и сыновей. "));
rusuperobject.biblemap.put("b13_7_5", new rusuperobject(13,7,5,"Братьев же их, во всех поколениях Иссахаровых, людей воинственных, было восемьдесят семь тысяч, внесенных в родословные записи. "));
rusuperobject.biblemap.put("b13_7_6", new rusuperobject(13,7,6,"У Вениамина: Бела, Бехер и Иедиаил, трое. "));
rusuperobject.biblemap.put("b13_7_7", new rusuperobject(13,7,7,"Сыновья Белы: Ецбон, Уззий, Уззиил, Иеримоф и Ири, пятеро, главы поколений, люди воинственные. В родословных списках записано их двадцать две тысячи тридцать четыре. "));
rusuperobject.biblemap.put("b13_7_8", new rusuperobject(13,7,8,"Сыновья Бехера: Земира, Иоаш, Елиезер, Елиоенай, Омри, Иремоф, Авия, Анафоф и Алемеф: все эти сыновья Бехера. "));
rusuperobject.biblemap.put("b13_7_9", new rusuperobject(13,7,9,"В родословных списках записано их по родам их, по главам поколений, людей воинственных - двадцать тысяч и двести. "));
rusuperobject.biblemap.put("b13_7_10", new rusuperobject(13,7,10,"Сын Иедиаила: Билган. Сыновья Билгана: Иеус, Вениамин, Егуд, Хенаана, Зефан, Фарсис и Ахишахар. "));
rusuperobject.biblemap.put("b13_7_11", new rusuperobject(13,7,11,"Все эти сыновья Иедиаила были главами поколений, люди воинственные; семнадцать тысяч и двести было выходящих на войну. "));
rusuperobject.biblemap.put("b13_7_12", new rusuperobject(13,7,12,"И Шупим и Хупим, сыновья Ира; Хушим, сын Ахера; "));
rusuperobject.biblemap.put("b13_7_13", new rusuperobject(13,7,13,"сыновья Неффалима: Иахцеил, Гуни, Иецер и Шиллем, дети Валлы. "));
rusuperobject.biblemap.put("b13_7_14", new rusuperobject(13,7,14,"Сыновья Манассии: Асриил, которого родила наложница его Арамеянка; она же родила Махира, отца Галаадова. "));
rusuperobject.biblemap.put("b13_7_15", new rusuperobject(13,7,15,"Махир взял в жену сестру Хупима и Шупима, - имя сестры их Мааха; имя второму Салпаад. У Салпаада были только дочери. "));
rusuperobject.biblemap.put("b13_7_16", new rusuperobject(13,7,16,"Мааха, жена Махирова, родила сына и нарекла ему имя Кереш, а имя брату его Шереш. Сыновья его: Улам и Рекем. "));
rusuperobject.biblemap.put("b13_7_17", new rusuperobject(13,7,17,"Сын Улама: Бедан. Вот сыновья Галаада, сына Махира, сына Манассиина. "));
rusuperobject.biblemap.put("b13_7_18", new rusuperobject(13,7,18,"Сестра его Молехеф родила Ишгода, Авиезера и Махлу. "));
rusuperobject.biblemap.put("b13_7_19", new rusuperobject(13,7,19,"Сыновья Шемиды были: Ахиан, Шехем, Ликхи и Аниам. "));
rusuperobject.biblemap.put("b13_7_20", new rusuperobject(13,7,20,"Сыновья Ефрема: Шутелах, и Беред, сын его, и Фахаф, сын его, и Елеада, сын его, и Фахаф, сын его, "));
rusuperobject.biblemap.put("b13_7_21", new rusuperobject(13,7,21,"и Завад, сын его, и Шутелах, сын его, и Езер и Елеад. И убили их жители Гефа, уроженцы той земли, за то, что они пошли захватить стада их. "));
rusuperobject.biblemap.put("b13_7_22", new rusuperobject(13,7,22,"И плакал о них Ефрем, отец их, много дней, и приходили братья его утешать его. "));
rusuperobject.biblemap.put("b13_7_23", new rusuperobject(13,7,23,"Потом он вошел к жене своей, и она зачала и родила сына, и он нарек ему имя: Берия, потому что несчастье постигло дом его. "));
rusuperobject.biblemap.put("b13_7_24", new rusuperobject(13,7,24,"И дочь у него была Шеера. Она построила Беф-Орон нижний и верхний и Уззен-Шееру. "));
rusuperobject.biblemap.put("b13_7_25", new rusuperobject(13,7,25,"И Рефай, сын его, и Решеф, и Фелах, сын его, и Фахан, сын его, "));
rusuperobject.biblemap.put("b13_7_26", new rusuperobject(13,7,26,"Лаедан, сын его, Аммиуд, сын его, Елишама, сын его, "));
rusuperobject.biblemap.put("b13_7_27", new rusuperobject(13,7,27,"Нон, сын его, Иисус, сын его. "));
rusuperobject.biblemap.put("b13_7_28", new rusuperobject(13,7,28,"Владения их и места жительства их были: Вефиль и зависящие от него города; к востоку Нааран, к западу Гезер и зависящие от него города; Сихем и зависящие от него города до Газы и зависящих от нее городов. "));
rusuperobject.biblemap.put("b13_7_29", new rusuperobject(13,7,29,"А со стороны сыновей Манассииных: Беф-Сан и зависящие от него города, Фаанах и зависящие от него города, Мегиддо и зависящие от него города, Дор и зависящие от него города. В них жили сыновья Иосифа, сына Израилева. "));
rusuperobject.biblemap.put("b13_7_30", new rusuperobject(13,7,30,"Сыновья Асира: Имна, Ишва, Ишви и Берия, и сестра их Серах. "));
rusuperobject.biblemap.put("b13_7_31", new rusuperobject(13,7,31,"Сыновья Берии: Хевер и Малхиил. Он отец Бирзаифа. "));
rusuperobject.biblemap.put("b13_7_32", new rusuperobject(13,7,32,"Хевер родил Иафлета, Шомера и Хофама, и Шую, сестру их. "));
rusuperobject.biblemap.put("b13_7_33", new rusuperobject(13,7,33,"Сыновья Иафлета: Пасах, Бимгал и Ашваф. Вот сыновья Иафлета. "));
rusuperobject.biblemap.put("b13_7_34", new rusuperobject(13,7,34,"Сыновья Шемера: Ахи, Рохга, Ихубба и Арам. "));
rusuperobject.biblemap.put("b13_7_35", new rusuperobject(13,7,35,"Сыновья Гелема, брата его: Цофах, Имна, Шелеш и Амал. "));
rusuperobject.biblemap.put("b13_7_36", new rusuperobject(13,7,36,"Сыновья Цофаха: Суах, Харнефер, Шуал, Бери, Имра, "));
rusuperobject.biblemap.put("b13_7_37", new rusuperobject(13,7,37,"Бецер, Год, Шамма, Шилша, Ифран и Беера. "));
rusuperobject.biblemap.put("b13_7_38", new rusuperobject(13,7,38,"Сыновья Иефера: Иефунни, Фиспа и Ара. "));
rusuperobject.biblemap.put("b13_7_39", new rusuperobject(13,7,39,"Сыновья Уллы: Арах, Ханниил и Риция. "));
rusuperobject.biblemap.put("b13_7_40", new rusuperobject(13,7,40,"Все эти сыновья Асира, главы поколений, люди отборные, воинственные, главные начальники. Записано у них в родословных списках в войске, для войны, по счету двадцать шесть тысяч человек. "));
rusuperobject.biblemap.put("b13_8_1", new rusuperobject(13,8,1,"Вениамин родил Белу, первенца своего, второго Ашбела, третьего Ахрая, "));
rusuperobject.biblemap.put("b13_8_2", new rusuperobject(13,8,2,"четвертого Ноху и пятого Рафу. "));
rusuperobject.biblemap.put("b13_8_3", new rusuperobject(13,8,3,"Сыновья Белы были: Аддар, Гера, Авиуд, "));
rusuperobject.biblemap.put("b13_8_4", new rusuperobject(13,8,4,"Авишуа, Нааман, Ахоах, "));
rusuperobject.biblemap.put("b13_8_5", new rusuperobject(13,8,5,"Гера, Шефуфан и Хурам. "));
rusuperobject.biblemap.put("b13_8_6", new rusuperobject(13,8,6,"И вот сыновья Егуда, которые были главами родов, живших в Геве и переселенных в Манахаф: "));
rusuperobject.biblemap.put("b13_8_7", new rusuperobject(13,8,7,"Нааман, Ахия и Гера, который переселил их; он родил Уззу и Ахихуда. "));
rusuperobject.biblemap.put("b13_8_8", new rusuperobject(13,8,8,"Шегараим родил детей в земле Моавитской после того, как отпустил от себя Хушиму и Баару, жен своих. "));
rusuperobject.biblemap.put("b13_8_9", new rusuperobject(13,8,9,"И родил он от Ходеши, жены своей, Иовава, Цивию, Мешу, Малхама, "));
rusuperobject.biblemap.put("b13_8_10", new rusuperobject(13,8,10,"Иеуца, Шахию и Мирму: вот сыновья его, главы поколений. "));
rusuperobject.biblemap.put("b13_8_11", new rusuperobject(13,8,11,"От Хушимы родил он Авитува и Елпаала. "));
rusuperobject.biblemap.put("b13_8_12", new rusuperobject(13,8,12,"Сыновья Елпаала: Евер, Мишам и Шемер, который построил Оно и Лод и зависящие от него города, - "));
rusuperobject.biblemap.put("b13_8_13", new rusuperobject(13,8,13,"и Берия и Шема. Они были главами поколений жителей Аиалона. Они выгнали жителей Гефа. "));
rusuperobject.biblemap.put("b13_8_14", new rusuperobject(13,8,14,"Ахио, Шашак, Иремоф, "));
rusuperobject.biblemap.put("b13_8_15", new rusuperobject(13,8,15,"Зевадия, Арад, Едер, "));
rusuperobject.biblemap.put("b13_8_16", new rusuperobject(13,8,16,"Михаил, Ишфа и Иоха - сыновья Берии. "));
rusuperobject.biblemap.put("b13_8_17", new rusuperobject(13,8,17,"Зевадия, Мешуллам, Хизкий, Хевер, "));
rusuperobject.biblemap.put("b13_8_18", new rusuperobject(13,8,18,"Ишмерай, Излия и Иовав - сыновья Елпаала. "));
rusuperobject.biblemap.put("b13_8_19", new rusuperobject(13,8,19,"Иаким, Зихрий, Завдий, "));
rusuperobject.biblemap.put("b13_8_20", new rusuperobject(13,8,20,"Елиенай, Цилфай, Елиил, "));
rusuperobject.biblemap.put("b13_8_21", new rusuperobject(13,8,21,"Адаия, Бераия и Шимраф - сыновья Шимея. "));
rusuperobject.biblemap.put("b13_8_22", new rusuperobject(13,8,22,"Ишпан, Евер, Елиил, "));
rusuperobject.biblemap.put("b13_8_23", new rusuperobject(13,8,23,"Авдон, Зихрий, Ханан, "));
rusuperobject.biblemap.put("b13_8_24", new rusuperobject(13,8,24,"Ханания, Елам, Антофия, "));
rusuperobject.biblemap.put("b13_8_25", new rusuperobject(13,8,25,"Ифдия и Фенуил - сыновья Шашака. "));
rusuperobject.biblemap.put("b13_8_26", new rusuperobject(13,8,26,"Шамшерай, Шехария, Афалия, "));
rusuperobject.biblemap.put("b13_8_27", new rusuperobject(13,8,27,"Иаарешия, Елия и Зихрий, сыновья Иерохама. "));
rusuperobject.biblemap.put("b13_8_28", new rusuperobject(13,8,28,"Это главы поколений, в родах своих главные. Они жили в Иерусалиме. "));
rusuperobject.biblemap.put("b13_8_29", new rusuperobject(13,8,29,"В Гаваоне жили: Иеил, отец Гаваонитян, - имя жены его Мааха, - "));
rusuperobject.biblemap.put("b13_8_30", new rusuperobject(13,8,30,"и сын его, первенец Авдон, за ним Цур, Кис, Ваал, Надав, Нер, "));
rusuperobject.biblemap.put("b13_8_31", new rusuperobject(13,8,31,"Гедор, Ахио, Зехер и Миклоф. "));
rusuperobject.biblemap.put("b13_8_32", new rusuperobject(13,8,32,"Миклоф родил Шимея. И они подле братьев своих жили в Иерусалиме, вместе с братьями своими. "));
rusuperobject.biblemap.put("b13_8_33", new rusuperobject(13,8,33,"Нер родил Киса; Кис родил Саула; Саул родил Иоанафана, Мелхисуя, Авинадава и Ешбаала. "));
rusuperobject.biblemap.put("b13_8_34", new rusuperobject(13,8,34,"Сын Ионафана Мериббаал; Мериббаал родил Миху. "));
rusuperobject.biblemap.put("b13_8_35", new rusuperobject(13,8,35,"Сыновья Михи: Пифон, Мелег, Фаарея и Ахаз. "));
rusuperobject.biblemap.put("b13_8_36", new rusuperobject(13,8,36,"Ахаз родил Иоиадду; Иоиадда родил Алемефа, Азмавефа и Замврия; Замврий родил Моцу; "));
rusuperobject.biblemap.put("b13_8_37", new rusuperobject(13,8,37,"Моца родил Бинею. Рефаия, сын его; Елеаса, сын его; Ацел, сын его. "));
rusuperobject.biblemap.put("b13_8_38", new rusuperobject(13,8,38,"У Ацела шесть сыновей, и вот имена их: Азрикам, Бохру, Исмаил, Шеария, Овадия и Ханан; все они сыновья Ацела. "));
rusuperobject.biblemap.put("b13_8_39", new rusuperobject(13,8,39,"Сыновья Ешека, брата его: Улам, первенец его, второй Иеуш, третий Елифелет. "));
rusuperobject.biblemap.put("b13_8_40", new rusuperobject(13,8,40,"Сыновья Улама были люди воинственные, стрелявшие из лука, имевшие много сыновей и внуков: сто пятьдесят. Все они от сынов Вениамина. "));
rusuperobject.biblemap.put("b13_9_1", new rusuperobject(13,9,1,"Так были перечислены по родам своим все Израильтяне, и вот они записаны в книге царей Израильских. Иудеи же за беззакония свои переселены в Вавилон. "));
rusuperobject.biblemap.put("b13_9_2", new rusuperobject(13,9,2,"Первые жители, которые жили во владениях своих, по городам Израильским, были Израильтяне, священники, левиты и нефинеи. "));
rusuperobject.biblemap.put("b13_9_3", new rusuperobject(13,9,3,"В Иерусалиме жили некоторые из сынов Иудиных и из сынов Вениаминовых, и из сынов Ефремовых и Манассииных: "));
rusuperobject.biblemap.put("b13_9_4", new rusuperobject(13,9,4,"Уфай, сын Аммиуда, сын Омри, сын Имрия, сын Вания, - из сыновей Фареса, сына Иудина; "));
rusuperobject.biblemap.put("b13_9_5", new rusuperobject(13,9,5,"из сыновей Шилона - Асаия первенец и сыновья его; "));
rusuperobject.biblemap.put("b13_9_6", new rusuperobject(13,9,6,"из сыновей Зары - Иеуил и братья их, - шестьсот девяносто; "));
rusuperobject.biblemap.put("b13_9_7", new rusuperobject(13,9,7,"из сыновей Вениаминовых Саллу, сын Мешуллама, сын Годавии, сын Гассенуи; "));
rusuperobject.biblemap.put("b13_9_8", new rusuperobject(13,9,8,"и Ивния, сын Иерохама, и Эла, сын Уззия, сына Михриева, и Мешуллам, сын Шефатии, сына Регуила, сына Ивнии, "));
rusuperobject.biblemap.put("b13_9_9", new rusuperobject(13,9,9,"и братья их, по родам их: девятьсот пятьдесят шесть, - все сии мужи были главы родов в поколениях своих. "));
rusuperobject.biblemap.put("b13_9_10", new rusuperobject(13,9,10,"А из священников: Иедаия, Иоиарив, Иахин, "));
rusuperobject.biblemap.put("b13_9_11", new rusuperobject(13,9,11,"и Азария, сын Хелкии, сын Мешуллама, сын Садока, сын Мераиофа, сын Ахитува, начальствующий в доме Божием; "));
rusuperobject.biblemap.put("b13_9_12", new rusuperobject(13,9,12,"и Адаия, сын Иерохама, сын Пашхура, сын Малхии; и Маасай, сын Адиела, сын Иахзера, сын Мешуллама, сын Мешиллемифа, сын Иммера; "));
rusuperobject.biblemap.put("b13_9_13", new rusuperobject(13,9,13,"и братья их, главы родов своих: тысяча семьсот шестьдесят, - люди отличные в деле служения в доме Божием. "));
rusuperobject.biblemap.put("b13_9_14", new rusuperobject(13,9,14,"А из левитов: Шемаия, сын Хашува, сын Азрикама, сын Хашавии, - из сыновей Мерариных; "));
rusuperobject.biblemap.put("b13_9_15", new rusuperobject(13,9,15,"и Вакбакар, Хереш, Галал, и Матфания, сын Михи, сын Зихрия, сын Асафа; "));
rusuperobject.biblemap.put("b13_9_16", new rusuperobject(13,9,16,"и Овадия, сын Шемаии, сын Галала, сын Идифуна, и Берехия, сын Асы, сын Елканы, живший в селениях Нетофафских. "));
rusuperobject.biblemap.put("b13_9_17", new rusuperobject(13,9,17,"А привратники: Шаллум, Аккуб, Талмон и Ахиман, и братья их; Шаллум был главным. "));
rusuperobject.biblemap.put("b13_9_18", new rusuperobject(13,9,18,"И доныне сии привратники у ворот царских, к востоку, содержат стражу сынов Левииных. "));
rusuperobject.biblemap.put("b13_9_19", new rusuperobject(13,9,19,"Шаллум, сын Коре, сын Евиасафа, сын Корея, и братья его из рода его, Кореяне, по делу служения своего, были стражами у порогов скинии, а отцы их охраняли вход в стан Господень. "));
rusuperobject.biblemap.put("b13_9_20", new rusuperobject(13,9,20,"Финеес, сын Елеазаров, был прежде начальником над ними, и Господь был с ним. "));
rusuperobject.biblemap.put("b13_9_21", new rusuperobject(13,9,21,"Захария, сын Мешелемии, был привратником у дверей скинии собрания. "));
rusuperobject.biblemap.put("b13_9_22", new rusuperobject(13,9,22,"Всех их, выбранных в привратники к порогам, было двести двенадцать. Они внесены в список по селениям своим. Их поставил Давид и Самуил-прозорливец за верность их. "));
rusuperobject.biblemap.put("b13_9_23", new rusuperobject(13,9,23,"И они и сыновья их были на страже у ворот дома Господня, при доме скинии. "));
rusuperobject.biblemap.put("b13_9_24", new rusuperobject(13,9,24,"На четырех сторонах находились привратники: на восточной, западной, северной и южной. "));
rusuperobject.biblemap.put("b13_9_25", new rusuperobject(13,9,25,"Братья же их жили в селениях своих, приходя к ним от времени до времени на семь дней. "));
rusuperobject.biblemap.put("b13_9_26", new rusuperobject(13,9,26,"Сии четыре начальника привратников, левиты, были в доверенности; они же были приставлены к жилищам и к сокровищам дома Божия. "));
rusuperobject.biblemap.put("b13_9_27", new rusuperobject(13,9,27,"Вокруг дома Божия они и ночь проводили, потому что на них лежало охранение, и они должны были каждое утро отпирать двери. "));
rusuperobject.biblemap.put("b13_9_28", new rusuperobject(13,9,28,"Одни из них были приставлены к служебным сосудам, так что счетом принимали их и счетом выдавали. "));
rusuperobject.biblemap.put("b13_9_29", new rusuperobject(13,9,29,"Другим из них поручена была прочая утварь и все священные потребности: мука лучшая, и вино, и елей, и ладан, и благовония. "));
rusuperobject.biblemap.put("b13_9_30", new rusuperobject(13,9,30,"А из сыновей священнических некоторые составляли миро из веществ благовонных. "));
rusuperobject.biblemap.put("b13_9_31", new rusuperobject(13,9,31,"Маттафии из левитов, - он первенец Селлума Кореянина, - вверено было приготовляемое на сковородах. "));
rusuperobject.biblemap.put("b13_9_32", new rusuperobject(13,9,32,"Некоторым из братьев их, из сынов Каафовых, поручено было заготовление хлебов предложения, чтобы представлять их каждую субботу. "));
rusuperobject.biblemap.put("b13_9_33", new rusuperobject(13,9,33,"Певцы же, главные в поколениях левитских, в комнатах храма свободны были от занятий, потому что день и ночь они обязаны были заниматься искусством своим. "));
rusuperobject.biblemap.put("b13_9_34", new rusuperobject(13,9,34,"Это главы поколений левитских, в родах своих главные. Они жили в Иерусалиме. "));
rusuperobject.biblemap.put("b13_9_35", new rusuperobject(13,9,35,"В Гаваоне жили: отец Гаваонитян Иеил, - имя жены его Мааха, "));
rusuperobject.biblemap.put("b13_9_36", new rusuperobject(13,9,36,"и сын его первенец Авдон, за ним Цур, Кис, Ваал, Нер, Надав, "));
rusuperobject.biblemap.put("b13_9_37", new rusuperobject(13,9,37,"Гедор, Ахио, Захария и Миклоф. "));
rusuperobject.biblemap.put("b13_9_38", new rusuperobject(13,9,38,"Миклоф родил Шимеама. И они подле братьев своих жили в Иерусалиме вместе с братьями своими. "));
rusuperobject.biblemap.put("b13_9_39", new rusuperobject(13,9,39,"Нер родил Киса, Кис родил Саула, Саул родил Ионафана, Мелхисуя, Авинадава и Ешбаала. "));
rusuperobject.biblemap.put("b13_9_40", new rusuperobject(13,9,40,"Сын Ионафана Мериббаал; Мериббаал родил Миху. "));
rusuperobject.biblemap.put("b13_9_41", new rusuperobject(13,9,41,"Сыновья Михи: Пифон, Мелех, Фарей и Ахаз. "));
rusuperobject.biblemap.put("b13_9_42", new rusuperobject(13,9,42,"Ахаз родил Иаеру; Иаера родил Алемефа, Азмавефа и Замврия; Замврий родил Моцу; "));
rusuperobject.biblemap.put("b13_9_43", new rusuperobject(13,9,43,"Моца родил Бинею: Рефаия, сын его; Елеаса, сын его; Ацел, сын его. "));
rusuperobject.biblemap.put("b13_9_44", new rusuperobject(13,9,44,"У Ацела шесть сыновей, и вот имена их: Азрикам, Бохру, Исмаил, Шеария, Овадия и Ханан. Это сыновья Ацела. "));
rusuperobject.biblemap.put("b13_10_1", new rusuperobject(13,10,1,"Филистимляне воевали с Израилем, и побежали Израильтяне от Филистимлян, и падали пораженные на горе Гелвуе. "));
rusuperobject.biblemap.put("b13_10_2", new rusuperobject(13,10,2,"И погнались Филистимляне за Саулом и сыновьями его, и убили Филистимляне Ионафана и Авинадава и Мелхисуя, сыновей Сауловых. "));
rusuperobject.biblemap.put("b13_10_3", new rusuperobject(13,10,3,"Сражение против Саула усилилось, и стрелки устремились на него, так что он изранен был стрелками. "));
rusuperobject.biblemap.put("b13_10_4", new rusuperobject(13,10,4,"И сказал Саул оруженосцу своему: обнажи меч твой и заколи меня им, чтобы не пришли эти необрезанные и не надругались надо мною. Но оруженосец не решился, потому что очень испугался. Тогда Саул взял меч и пал на него. "));
rusuperobject.biblemap.put("b13_10_5", new rusuperobject(13,10,5,"Оруженосец его, увидев, что Саул умер, и сам пал на меч и умер. "));
rusuperobject.biblemap.put("b13_10_6", new rusuperobject(13,10,6,"И умер Саул, и три сына его, и весь дом его вместе с ним умер. "));
rusuperobject.biblemap.put("b13_10_7", new rusuperobject(13,10,7,"Когда увидели Израильтяне, которые были в долине, что все бегут и что Саул и сыновья его умерли, то оставили города свои и разбежались; а Филистимляне пришли и поселились в них. "));
rusuperobject.biblemap.put("b13_10_8", new rusuperobject(13,10,8,"На другой день пришли Филистимляне обирать убитых, и нашли Саула и сыновей его, павших на горе Гелвуйской, "));
rusuperobject.biblemap.put("b13_10_9", new rusuperobject(13,10,9,"и раздели его, и сняли с него голову его и оружие его, и послали по земле Филистимской, чтобы возвестить о сем пред идолами их и пред народом. "));
rusuperobject.biblemap.put("b13_10_10", new rusuperobject(13,10,10,"И положили оружие его в капище богов своих, и голову его воткнули в доме Дагона. "));
rusuperobject.biblemap.put("b13_10_11", new rusuperobject(13,10,11,"И услышал весь Иавис Галаадский все, что сделали Филистимляне с Саулом. "));
rusuperobject.biblemap.put("b13_10_12", new rusuperobject(13,10,12,"И поднялись все люди сильные, взяли тело Саулово и тела сыновей его, и принесли их в Иавис, и похоронили кости их под дубом в Иависе, и постились семь дней. "));
rusuperobject.biblemap.put("b13_10_13", new rusuperobject(13,10,13,"Так умер Саул за свое беззаконие, которое он сделал пред Господом, за то, что не соблюл слова Господня и обратился к волшебнице с вопросом, "));
rusuperobject.biblemap.put("b13_10_14", new rusuperobject(13,10,14,"а не взыскал Господа. За то Он и умертвил его, и передал царство Давиду, сыну Иессееву. "));
rusuperobject.biblemap.put("b13_11_1", new rusuperobject(13,11,1,"И собрались все Израильтяне к Давиду в Хеврон и сказали: вот, мы кость твоя и плоть твоя; "));
rusuperobject.biblemap.put("b13_11_2", new rusuperobject(13,11,2,"и вчера, и третьего дня, когда еще Саул был царем, ты выводил и вводил Израиля, и Господь Бог твой сказал тебе: «ты будешь пасти народ Мой, Израиля и ты будешь вождем народа Моего Израиля». "));
rusuperobject.biblemap.put("b13_11_3", new rusuperobject(13,11,3,"И пришли все старейшины Израилевы к царю в Хеврон, и заключил с ними Давид завет в Хевроне пред лицем Господним; и они помазали Давида в царя над Израилем, по слову Господню, чрез Самуила. "));
rusuperobject.biblemap.put("b13_11_4", new rusuperobject(13,11,4,"И пошел Давид и весь Израиль к Иерусалиму, то есть к Иевусу. А там были Иевусеи, жители той земли. "));
rusuperobject.biblemap.put("b13_11_5", new rusuperobject(13,11,5,"И сказали жители Иевуса Давиду: не войдешь сюда. Но Давид взял крепость Сион; это город Давидов. "));
rusuperobject.biblemap.put("b13_11_6", new rusuperobject(13,11,6,"И сказал Давид: кто прежде всех поразит Иевусеев, тот будет главою и военачальником. И взошел прежде всех Иоав, сын Саруи, и сделался главою. "));
rusuperobject.biblemap.put("b13_11_7", new rusuperobject(13,11,7,"Давид жил в той крепости, потому и называли ее городом Давидовым. "));
rusuperobject.biblemap.put("b13_11_8", new rusuperobject(13,11,8,"И он обстроил город кругом, начиная от Милло, всю окружность, а Иоав возобновил остальные части города. "));
rusuperobject.biblemap.put("b13_11_9", new rusuperobject(13,11,9,"И преуспевал Давид, и возвышался более и более, и Господь Саваоф был с ним. "));
rusuperobject.biblemap.put("b13_11_10", new rusuperobject(13,11,10,"Вот главные из сильных у Давида, которые крепко подвизались с ним в царстве его, вместе со всем Израилем, чтобы воцарить его, по слову Господню, над Израилем, "));
rusuperobject.biblemap.put("b13_11_11", new rusuperobject(13,11,11,"и вот число храбрых, которые были у Давида: Иесваал, сын Ахамани, главный из тридцати. Он поднял копье свое на триста человек и поразил их в один раз. "));
rusuperobject.biblemap.put("b13_11_12", new rusuperobject(13,11,12,"По нем Елеазар, сын Додо Ахохиянина, из трех храбрых: "));
rusuperobject.biblemap.put("b13_11_13", new rusuperobject(13,11,13,"он был с Давидом в Фасдамиме, куда Филистимляне собрались на войну. Там часть поля была засеяна ячменем, и народ побежал от Филистимлян; "));
rusuperobject.biblemap.put("b13_11_14", new rusuperobject(13,11,14,"но они стали среди поля, сберегли его и поразили Филистимлян. И даровал Господь спасение великое! "));
rusuperobject.biblemap.put("b13_11_15", new rusuperobject(13,11,15,"Трое сих главных из тридцати вождей взошли на скалу к Давиду, в пещеру Одоллам, когда стан Филистимлян был расположен в долине Рефаимов. "));
rusuperobject.biblemap.put("b13_11_16", new rusuperobject(13,11,16,"Давид тогда был в укрепленном месте, а охранное войско Филистимлян было тогда в Вифлееме. "));
rusuperobject.biblemap.put("b13_11_17", new rusuperobject(13,11,17,"И сильно захотелось пить Давиду, и он сказал: кто напоит меня водою из колодезя Вифлеемского, что у ворот? "));
rusuperobject.biblemap.put("b13_11_18", new rusuperobject(13,11,18,"Тогда эти трое пробились сквозь стан Филистимский и почерпнули воды из колодезя Вифлеемского, что у ворот, и взяли, и принесли Давиду. Но Давид не захотел пить ее и вылил ее во славу Господа, "));
rusuperobject.biblemap.put("b13_11_19", new rusuperobject(13,11,19,"и сказал: сохрани меня Господь, чтоб я сделал это! Стану ли я пить кровь мужей сих, полагавших души свои! Ибо с опасностью собственной жизни они принесли воду. И не захотел пить ее. Вот что сделали трое этих храбрых. "));
rusuperobject.biblemap.put("b13_11_20", new rusuperobject(13,11,20,"И Авесса, брат Иоава, был главным из трех: он убил копьем своим триста человек, и был в славе у тех троих. "));
rusuperobject.biblemap.put("b13_11_21", new rusuperobject(13,11,21,"Из трех он был знатнейшим и был начальником; но с теми тремя не равнялся. "));
rusuperobject.biblemap.put("b13_11_22", new rusuperobject(13,11,22,"Ванея, сын Иодая, мужа храброго, великий по делам, из Кавцеила: он поразил двух Ариилов Моавитских; он же сошел и убил льва во рве, в снежное время; "));
rusuperobject.biblemap.put("b13_11_23", new rusuperobject(13,11,23,"он же убил Египтянина, человека ростом в пять локтей: в руке Египтянина было копье, как навой у ткачей, а он подошел к нему с палкою и, вырвав копье из руки Египтянина, убил его его же копьем: "));
rusuperobject.biblemap.put("b13_11_24", new rusuperobject(13,11,24,"вот что сделал Ванея, сын Иодая. И он был в славе у тех троих храбрых; "));
rusuperobject.biblemap.put("b13_11_25", new rusuperobject(13,11,25,"он был знатнее тридцати, но с тремя не равнялся, и Давид поставил его ближайшим исполнителем своих приказаний. "));
rusuperobject.biblemap.put("b13_11_26", new rusuperobject(13,11,26,"А главные из воинов: Асаил, брат Иоава; Елханан, сын Додо, из Вифлеема; "));
rusuperobject.biblemap.put("b13_11_27", new rusuperobject(13,11,27,"Шамма Гародитянин; Херец Пелонитянин; "));
rusuperobject.biblemap.put("b13_11_28", new rusuperobject(13,11,28,"Ира, сын Икеша, Фекоитянин; Евиезер Анафофянин; "));
rusuperobject.biblemap.put("b13_11_29", new rusuperobject(13,11,29,"Сивхай Хушатянин; Илай Ахохиянин; "));
rusuperobject.biblemap.put("b13_11_30", new rusuperobject(13,11,30,"Магарай Нетофафянин; Хелед, сын Вааны, Нетофафянин; "));
rusuperobject.biblemap.put("b13_11_31", new rusuperobject(13,11,31,"Иттай, сын Рибая, из Гивы Вениаминовой; Ванея Пирафонянин; "));
rusuperobject.biblemap.put("b13_11_32", new rusuperobject(13,11,32,"Хурай из Нагале-Гааша; Авиел из Аравы; "));
rusuperobject.biblemap.put("b13_11_33", new rusuperobject(13,11,33,"Азмавеф Бахарумиянин; Елияхба Шаалбонянин. "));
rusuperobject.biblemap.put("b13_11_34", new rusuperobject(13,11,34,"Сыновья Гашема Гизонитянина: Ионафан, сын Шаге, Гараритянин; "));
rusuperobject.biblemap.put("b13_11_35", new rusuperobject(13,11,35,"Ахиам, сын Сахара, Гараритянин; Елифал, сын Уры; "));
rusuperobject.biblemap.put("b13_11_36", new rusuperobject(13,11,36,"Хефер из Махеры; Ахиа Пелонитянин; "));
rusuperobject.biblemap.put("b13_11_37", new rusuperobject(13,11,37,"Хецрой Кармилитянин; Наарай, сын Езбая; "));
rusuperobject.biblemap.put("b13_11_38", new rusuperobject(13,11,38,"Иоиль, брат Нафана; Мивхар, сын Гагрия; "));
rusuperobject.biblemap.put("b13_11_39", new rusuperobject(13,11,39,"Целек Аммонитянин; Нахарай Берофянин, оруженосец Иоава, сына Саруи; "));
rusuperobject.biblemap.put("b13_11_40", new rusuperobject(13,11,40,"Ира Ифриянин; Гареб Ифриянин; "));
rusuperobject.biblemap.put("b13_11_41", new rusuperobject(13,11,41,"Урия Хеттеянин; Завад, сын Ахлая; "));
rusuperobject.biblemap.put("b13_11_42", new rusuperobject(13,11,42,"Адина, сын Шизы, Рувимлянин, глава Рувимлян, и у него было тридцать; "));
rusuperobject.biblemap.put("b13_11_43", new rusuperobject(13,11,43,"Ханан, сын Маахи; Иосафат Мифниянин; "));
rusuperobject.biblemap.put("b13_11_44", new rusuperobject(13,11,44,"Уззия Аштерофянин; Шама и Иеиел, сыновья Хофама Ароерянина; "));
rusuperobject.biblemap.put("b13_11_45", new rusuperobject(13,11,45,"Иедиаел, сын Шимрия, и Иоха, брат его, Фициянин; "));
rusuperobject.biblemap.put("b13_11_46", new rusuperobject(13,11,46,"Елиел из Махавима, и Иеривай и Иошавия, сыновья Елнаама, и Ифма Моавитянин; "));
rusuperobject.biblemap.put("b13_11_47", new rusuperobject(13,11,47,"Елиел, Овед и Иасиел из Мецоваи. "));
rusuperobject.biblemap.put("b13_12_1", new rusuperobject(13,12,1,"И сии также пришли к Давиду в Секелаг, когда он еще укрывался от Саула, сына Кисова, и были из храбрых, помогавших в сражении. "));
rusuperobject.biblemap.put("b13_12_2", new rusuperobject(13,12,2,"Вооруженные луком, правою и левою рукою бросавшие каменья и стрелявшие стрелами из лука, - из братьев Саула, от Вениамина: "));
rusuperobject.biblemap.put("b13_12_3", new rusuperobject(13,12,3,"главный Ахиезер, за ним Иоас, сыновья Шемаи, из Гивы; Иезиел и Фелет, сыновья Азмавефа; Бераха и Иегу из Анафофа; "));
rusuperobject.biblemap.put("b13_12_4", new rusuperobject(13,12,4,"Ишмаия Гаваонитянин, храбрый из тридцати и начальствовавший над тридцатью; Иеремия, Иахазиил, Иоханан и Иозавад из Гедеры. "));
rusuperobject.biblemap.put("b13_12_5", new rusuperobject(13,12,5,"Елузай, Иеримоф, Веалия, Шемария, Сафатия Харифиянин; "));
rusuperobject.biblemap.put("b13_12_6", new rusuperobject(13,12,6,"Елкана, Ишшияху, Азариил, Иоезер и Иошавам, Кореяне; "));
rusuperobject.biblemap.put("b13_12_7", new rusuperobject(13,12,7,"и Иоела и Зевадия, сыновья Иерохама, из Гедора. "));
rusuperobject.biblemap.put("b13_12_8", new rusuperobject(13,12,8,"И из Гадитян перешли к Давиду в укрепление, в пустыню, люди мужественные, воинственные, вооруженные щитом и копьем; лица львиные - лица их, и они быстры как серны на горах. "));
rusuperobject.biblemap.put("b13_12_9", new rusuperobject(13,12,9,"Главный Езер, второй Овадия, третий Елиав, "));
rusuperobject.biblemap.put("b13_12_10", new rusuperobject(13,12,10,"четвертый Мишманна, пятый Иеремия, "));
rusuperobject.biblemap.put("b13_12_11", new rusuperobject(13,12,11,"шестой Афай, седьмой Елиел, "));
rusuperobject.biblemap.put("b13_12_12", new rusuperobject(13,12,12,"восьмой Иоханан, девятый Елзавад, "));
rusuperobject.biblemap.put("b13_12_13", new rusuperobject(13,12,13,"десятый Иеремия, одиннадцатый Махбанай. "));
rusuperobject.biblemap.put("b13_12_14", new rusuperobject(13,12,14,"Они из сыновей Гадовых были главами в войске: меньший над сотнею, и больший над тысячею. "));
rusuperobject.biblemap.put("b13_12_15", new rusuperobject(13,12,15,"Они-то перешли Иордан в первый месяц, когда он выступает из берегов своих, и разогнали всех живших в долинах к востоку и западу. "));
rusuperobject.biblemap.put("b13_12_16", new rusuperobject(13,12,16,"Пришли также и из сыновей Вениаминовых и Иудиных в укрепление к Давиду. "));
rusuperobject.biblemap.put("b13_12_17", new rusuperobject(13,12,17,"Давид вышел навстречу им и сказал им: если с миром пришли вы ко мне, чтобы помогать мне, то да будет у меня с вами одно сердце; а если для того, чтобы коварно предать меня врагам моим, тогда как нет порока на руках моих, то да видит Бог отцов наших и рассудит. "));
rusuperobject.biblemap.put("b13_12_18", new rusuperobject(13,12,18,"И объял дух Амасая, главу тридцати, и сказал он: мир тебе Давид, и с тобою, сын Иессеев; мир тебе, и мир помощникам твоим; ибо помогает тебе Бог твой. Тогда принял их Давид и поставил их во главе войска. "));
rusuperobject.biblemap.put("b13_12_19", new rusuperobject(13,12,19,"И из колена Манассиина перешли некоторые к Давиду, когда он шел с Филистимлянами на войну против Саула, но не помогал им, потому что предводители Филистимские, посоветовавшись, отослали его, говоря: на нашу голову он перейдет к господину своему Саулу. "));
rusuperobject.biblemap.put("b13_12_20", new rusuperobject(13,12,20,"Когда он возвращался в Секелаг, тогда перешли к нему из Манассиян: Аднах, Иозавад, Иедиаел, Михаил, Иозавад, Елигу и Цилльфай, тысяченачальники у Манассиян. "));
rusuperobject.biblemap.put("b13_12_21", new rusuperobject(13,12,21,"И они помогали Давиду против полчищ, ибо все это были люди храбрые и были начальниками в войске. "));
rusuperobject.biblemap.put("b13_12_22", new rusuperobject(13,12,22,"Так с каждым днем приходили к Давиду на помощь до того, что его ополчение стало велико, как ополчение Божие. "));
rusuperobject.biblemap.put("b13_12_23", new rusuperobject(13,12,23,"Вот число главных в войске, которые пришли к Давиду в Хеврон, чтобы передать ему царство Саулово, по слову Господню: "));
rusuperobject.biblemap.put("b13_12_24", new rusuperobject(13,12,24,"сыновей Иудиных, носящих щит и копье, было шесть тысяч восемьсот готовых к войне; "));
rusuperobject.biblemap.put("b13_12_25", new rusuperobject(13,12,25,"из сыновей Симеоновых, людей храбрых, в войске было семь тысяч и сто; "));
rusuperobject.biblemap.put("b13_12_26", new rusuperobject(13,12,26,"из сыновей Левииных четыре тысячи шестьсот; "));
rusuperobject.biblemap.put("b13_12_27", new rusuperobject(13,12,27,"и Иоддай, князь от племени Аарона, и с ним три тысячи семьсот; "));
rusuperobject.biblemap.put("b13_12_28", new rusuperobject(13,12,28,"и Садок, мужественный юноша, и род его, двадцать два начальника; "));
rusuperobject.biblemap.put("b13_12_29", new rusuperobject(13,12,29,"из сыновей Вениаминовых, братьев Сауловых, три тысячи, - но еще многие из них держались дома Саулова; "));
rusuperobject.biblemap.put("b13_12_30", new rusuperobject(13,12,30,"из сыновей Ефремовых двадцать тысяч восемьсот людей мужественных, людей именитых в родах своих; "));
rusuperobject.biblemap.put("b13_12_31", new rusuperobject(13,12,31,"из полуколена Манассиина восемнадцать тысяч, которые вызваны были поименно, чтобы пойти воцарить Давида; "));
rusuperobject.biblemap.put("b13_12_32", new rusuperobject(13,12,32,"из сынов Иссахаровых пришли люди разумные, которые знали, что когда надлежало делать Израилю, - их было двести главных, и все братья их следовали слову их; "));
rusuperobject.biblemap.put("b13_12_33", new rusuperobject(13,12,33,"из колена Завулонова готовых к сражению, вооруженных всякими военными оружиями, пятьдесят тысяч, в строю, единодушных; "));
rusuperobject.biblemap.put("b13_12_34", new rusuperobject(13,12,34,"из колена Неффалимова тысяча вождей и с ними тридцать семь тысяч с щитами и копьями; "));
rusuperobject.biblemap.put("b13_12_35", new rusuperobject(13,12,35,"из колена Данова готовых к войне двадцать восемь тысяч шестьсот; "));
rusuperobject.biblemap.put("b13_12_36", new rusuperobject(13,12,36,"от Асира воинов, готовых к сражению, сорок тысяч; "));
rusuperobject.biblemap.put("b13_12_37", new rusuperobject(13,12,37,"из-за Иордана, от колена Рувимова, Гадова и полуколена Манассиина, сто двадцать тысяч, со всяким воинским оружием. "));
rusuperobject.biblemap.put("b13_12_38", new rusuperobject(13,12,38,"Все эти воины, в строю, от полного сердца пришли в Хеврон воцарить Давида над всем Израилем. Да и все прочие Израильтяне были единодушны, чтобы воцарить Давида. "));
rusuperobject.biblemap.put("b13_12_39", new rusuperobject(13,12,39,"И пробыли там у Давида три дня, ели и пили, потому что братья их все приготовили для них; "));
rusuperobject.biblemap.put("b13_12_40", new rusuperobject(13,12,40,"да и близкие к ним, даже до колена Иссахарова, Завулонова и Неффалимова, привозили все съестное на ослах, и верблюдах, и мулах, и волах: муку, смоквы, и изюм, и вино, и елей, и крупного и мелкого скота множество, так как радость была для Израиля. "));
rusuperobject.biblemap.put("b13_13_1", new rusuperobject(13,13,1,"И советовался Давид с тысяченачальниками, сотниками и со всеми вождями, "));
rusuperobject.biblemap.put("b13_13_2", new rusuperobject(13,13,2,"и сказал Давид всему собранию Израильтян: если угодно вам, и если на то будет воля Господа Бога нашего, пошлем повсюду к прочим братьям нашим, по всей земле Израильской, и вместе с ними к священникам и левитам, в города и селения их, чтобы они собрались к нам; "));
rusuperobject.biblemap.put("b13_13_3", new rusuperobject(13,13,3,"и перенесем к себе ковчег Бога нашего, потому что во дни Саула мы не обращались к нему. "));
rusuperobject.biblemap.put("b13_13_4", new rusuperobject(13,13,4,"И сказало все собрание: «да будет так», потому что это дело всему народу казалось справедливым. "));
rusuperobject.biblemap.put("b13_13_5", new rusuperobject(13,13,5,"Так собрал Давид всех Израильтян, от Шихора Египетского до входа в Емаф, чтобы перенести ковчег Божий из Кириаф-Иарима. "));
rusuperobject.biblemap.put("b13_13_6", new rusuperobject(13,13,6,"И пошел Давид и весь Израиль в Кириаф-Иарим, что в Иудее, чтобы перенести оттуда ковчег Бога, Господа, седящего на Херувимах, на котором нарицается имя Его. "));
rusuperobject.biblemap.put("b13_13_7", new rusuperobject(13,13,7,"И повезли ковчег Божий на новой колеснице из дома Авинадава; и Оза и Ахия вели колесницу. "));
rusuperobject.biblemap.put("b13_13_8", new rusuperobject(13,13,8,"Давид же и все Израильтяне играли пред Богом из всей силы, с пением, на цитрах и псалтирях, и тимпанах, и кимвалах и трубах. "));
rusuperobject.biblemap.put("b13_13_9", new rusuperobject(13,13,9,"Когда дошли до гумна Хидона, Оза простер руку свою, чтобы придержать ковчег, ибо волы наклонили его. "));
rusuperobject.biblemap.put("b13_13_10", new rusuperobject(13,13,10,"Но Господь разгневался на Озу, и поразил его за то, что он простер руку свою к ковчегу; и он умер тут же пред лицем Божиим. "));
rusuperobject.biblemap.put("b13_13_11", new rusuperobject(13,13,11,"И опечалился Давид, что Господь поразил Озу. И назвал то место поражением Озы; так называется оно и до сего дня. "));
rusuperobject.biblemap.put("b13_13_12", new rusuperobject(13,13,12,"И устрашился Давид Бога в день тот, и сказал: как я внесу к себе ковчег Божий? "));
rusuperobject.biblemap.put("b13_13_13", new rusuperobject(13,13,13,"И не повез Давид ковчега к себе, в город Давидов, а обратил его к дому Аведдара Гефянина. "));
rusuperobject.biblemap.put("b13_13_14", new rusuperobject(13,13,14,"И оставался ковчег Божий у Аведдара, в доме его, три месяца, и благословил Господь дом Аведдара и все, что у него. "));
rusuperobject.biblemap.put("b13_14_1", new rusuperobject(13,14,1,"И послал Хирам, царь Тирский, к Давиду послов, и кедровые деревья, и каменщиков, и плотников, чтобы построить ему дом. "));
rusuperobject.biblemap.put("b13_14_2", new rusuperobject(13,14,2,"Когда узнал Давид, что утвердил его Господь царем над Израилем, что вознесено высоко царство его, ради народа его Израиля, "));
rusuperobject.biblemap.put("b13_14_3", new rusuperobject(13,14,3,"тогда взял Давид еще жен в Иерусалиме, и родил Давид еще сыновей и дочерей. "));
rusuperobject.biblemap.put("b13_14_4", new rusuperobject(13,14,4,"И вот имена родившихся у него в Иерусалиме: Самус, Совав, Нафан, Соломон, "));
rusuperobject.biblemap.put("b13_14_5", new rusuperobject(13,14,5,"Евеар, Елисуа, Елфалет, "));
rusuperobject.biblemap.put("b13_14_6", new rusuperobject(13,14,6,"Ногах, Нафек, Иафиа, "));
rusuperobject.biblemap.put("b13_14_7", new rusuperobject(13,14,7,"и Елисама, Веелиада и Елифалеф. "));
rusuperobject.biblemap.put("b13_14_8", new rusuperobject(13,14,8,"И услышали Филистимляне, что помазан Давид в царя над всем Израилем, и поднялись все Филистимляне искать Давида. И услышал Давид об этом и пошел против них. "));
rusuperobject.biblemap.put("b13_14_9", new rusuperobject(13,14,9,"И Филистимляне пришли и расположились в долине Рефаимов. "));
rusuperobject.biblemap.put("b13_14_10", new rusuperobject(13,14,10,"И вопросил Давид Бога, говоря: идти ли мне против Филистимлян, и предашь ли их в руки мои? И сказал ему Господь: иди, и Я предам их в руки твои. "));
rusuperobject.biblemap.put("b13_14_11", new rusuperobject(13,14,11,"И пошли они в Ваал-Перацим, и поразил их там Давид; и сказал Давид: сломил Бог врагов моих рукою моею, как прорыв воды. Посему и дали имя месту тому: Ваал-Перацим. "));
rusuperobject.biblemap.put("b13_14_12", new rusuperobject(13,14,12,"И оставили там Филистимляне богов своих, и повелел Давид, и сожжены они огнем. "));
rusuperobject.biblemap.put("b13_14_13", new rusuperobject(13,14,13,"И пришли опять Филистимляне и расположились по долине. "));
rusuperobject.biblemap.put("b13_14_14", new rusuperobject(13,14,14,"И еще вопросил Давид Бога, и сказал ему Бог: не ходи прямо на них, уклонись от них и иди к ним со стороны тутовых дерев; "));
rusuperobject.biblemap.put("b13_14_15", new rusuperobject(13,14,15,"и когда услышишь шум как бы шагов на вершинах тутовых дерев, тогда вступи в битву, ибо вышел Бог пред тобою, чтобы поразить стан Филистимлян. "));
rusuperobject.biblemap.put("b13_14_16", new rusuperobject(13,14,16,"И сделал Давид, как повелел ему Бог; и поразили стан Филистимский, от Гаваона до Газера. "));
rusuperobject.biblemap.put("b13_14_17", new rusuperobject(13,14,17,"И пронеслось имя Давидово по всем землям, и Господь сделал его страшным для всех народов. "));
rusuperobject.biblemap.put("b13_15_1", new rusuperobject(13,15,1,"И построил он себе домы в городе Давидовом, и приготовил место для ковчега Божия, и устроил для него скинию. "));
rusuperobject.biblemap.put("b13_15_2", new rusuperobject(13,15,2,"Тогда сказал Давид: никто не должен носить ковчега Божия, кроме левитов, потому что их избрал Господь на то, чтобы носить ковчег Божий и служить Ему во веки. "));
rusuperobject.biblemap.put("b13_15_3", new rusuperobject(13,15,3,"И собрал Давид всех Израильтян в Иерусалим, чтобы внести ковчег Господень на место его, которое он для него приготовил. "));
rusuperobject.biblemap.put("b13_15_4", new rusuperobject(13,15,4,"И созвал Давид сыновей Аароновых и левитов: "));
rusuperobject.biblemap.put("b13_15_5", new rusuperobject(13,15,5,"из сыновей Каафовых, Уриила начальника и братьев его - сто двадцать человек; "));
rusuperobject.biblemap.put("b13_15_6", new rusuperobject(13,15,6,"из сыновей Мерариных, Асаию начальника и братьев его - двести двадцать человек; "));
rusuperobject.biblemap.put("b13_15_7", new rusuperobject(13,15,7,"из сыновей Гирсоновых, Иоиля начальника и братьев его - сто тридцать человек; "));
rusuperobject.biblemap.put("b13_15_8", new rusuperobject(13,15,8,"из сыновей Елисафановых, Шемаию начальника и братьев его - двести; "));
rusuperobject.biblemap.put("b13_15_9", new rusuperobject(13,15,9,"из сыновей Хевроновых, Елиела начальника и братьев его - восемьдесят; "));
rusuperobject.biblemap.put("b13_15_10", new rusuperobject(13,15,10,"из сыновей Уззииловых, Аминадава начальника и братьев его - сто двенадцать. "));
rusuperobject.biblemap.put("b13_15_11", new rusuperobject(13,15,11,"И призвал Давид священников: Садока и Авиафара, и левитов: Уриила, Асаию, Иоиля, Шемаию, Елиела и Аминадава, "));
rusuperobject.biblemap.put("b13_15_12", new rusuperobject(13,15,12,"и сказал им: вы, начальники родов левитских, освятитесь сами и братья ваши, и принесите ковчег Господа Бога Израилева на место, которое я приготовил для него; "));
rusuperobject.biblemap.put("b13_15_13", new rusuperobject(13,15,13,"ибо как прежде не вы это делали, то Господь Бог наш поразил нас за то, что мы не взыскали Его, как должно. "));
rusuperobject.biblemap.put("b13_15_14", new rusuperobject(13,15,14,"И освятились священники и левиты для того, чтобы нести ковчег Господа, Бога Израилева. "));
rusuperobject.biblemap.put("b13_15_15", new rusuperobject(13,15,15,"И понесли сыновья левитов ковчег Божий, как заповедал Моисей по слову Господа, на плечах, на шестах. "));
rusuperobject.biblemap.put("b13_15_16", new rusuperobject(13,15,16,"И приказал Давид начальникам левитов поставить братьев своих певцов с музыкальными орудиями, с псалтирями и цитрами и кимвалами, чтобы они громко возвещали глас радования. "));
rusuperobject.biblemap.put("b13_15_17", new rusuperobject(13,15,17,"И поставили левиты Емана, сына Иоилева, и из братьев его, Асафа, сына Верехиина, а из сыновей Мерариных, братьев их, Ефана, сына Кушаии; "));
rusuperobject.biblemap.put("b13_15_18", new rusuperobject(13,15,18,"и с ними братьев их второстепенных: Захарию, Бена, Иаазиила, Шемирамофа, Иехиила, Унния, Елиава, Ванею, Маасея, Маттафию, Елифлеуя, Микнея и Овед-Едома и Иеиела, привратников. "));
rusuperobject.biblemap.put("b13_15_19", new rusuperobject(13,15,19,"Еман, Асаф и Ефан играли громко на медных кимвалах, "));
rusuperobject.biblemap.put("b13_15_20", new rusuperobject(13,15,20,"а Захария, Азиил, Шемирамоф, Иехиил, Унний, Елиав, Маасей и Ванея - на псалтирях, тонким голосом. "));
rusuperobject.biblemap.put("b13_15_21", new rusuperobject(13,15,21,"Маттафия же, Елифлеуй, Микней, Овед-Едом, Иеиел и Азазия - на цитрах, чтобы делать начало. "));
rusuperobject.biblemap.put("b13_15_22", new rusuperobject(13,15,22,"А Хенания, начальник левитов, был учитель пения, потому что был искусен в нем. "));
rusuperobject.biblemap.put("b13_15_23", new rusuperobject(13,15,23,"Верехия и Елкана были придверниками у ковчега. "));
rusuperobject.biblemap.put("b13_15_24", new rusuperobject(13,15,24,"Шевания, Иосафат, Нафанаил, Амасай, Захария, Ванея и Елиезер, священники, трубили трубами пред ковчегом Божиим. Овед-Едом и Иехия были придверниками у ковчега. "));
rusuperobject.biblemap.put("b13_15_25", new rusuperobject(13,15,25,"Так Давид и старейшины Израилевы и тысяченачальники пошли перенести ковчег завета Господня из дома Овед-Едомова с веселием. "));
rusuperobject.biblemap.put("b13_15_26", new rusuperobject(13,15,26,"И когда Бог помог левитам, несшим ковчег завета Господня, тогда закололи в жертву семь тельцов и семь овнов. "));
rusuperobject.biblemap.put("b13_15_27", new rusuperobject(13,15,27,"Давид был одет в виссонную одежду, а также и все левиты, несшие ковчег, и певцы, и Хенания начальник музыкантов и певцов. На Давиде же был еще льняной ефод. "));
rusuperobject.biblemap.put("b13_15_28", new rusuperobject(13,15,28,"Так весь Израиль вносил ковчег завета Господня с восклицанием, при звуке рога и труб и кимвалов, играя на псалтирях и цитрах. "));
rusuperobject.biblemap.put("b13_15_29", new rusuperobject(13,15,29,"Когда ковчег завета Господня входил в город Давидов, Мелхола, дочь Саулова, смотрела в окно и, увидев царя Давида, скачущего и веселящегося, уничижила его в сердце своем. "));
rusuperobject.biblemap.put("b13_16_1", new rusuperobject(13,16,1,"И принесли ковчег Божий, и поставили его среди скинии, которую устроил для него Давид, и вознесли Богу всесожжения и мирные жертвы. "));
rusuperobject.biblemap.put("b13_16_2", new rusuperobject(13,16,2,"Когда Давид окончил всесожжения и приношение мирных жертв, то благословил народ именем Господа "));
rusuperobject.biblemap.put("b13_16_3", new rusuperobject(13,16,3,"и роздал всем Израильтянам, и мужчинам и женщинам, по одному хлебу и по куску мяса и по кружке вина, "));
rusuperobject.biblemap.put("b13_16_4", new rusuperobject(13,16,4,"и поставил на службу пред ковчегом Господним некоторых из левитов, чтобы они славословили, благодарили и превозносили Господа Бога Израилева: "));
rusuperobject.biblemap.put("b13_16_5", new rusuperobject(13,16,5,"Асафа главным, вторым по нем Захарию, Иеиела, Шемирамофа, Иехиила, Маттафию, Елиава, и Ванею, Овед-Едома и Иеиела с псалтирями и цитрами, и Асафа для игры на кимвалах, "));
rusuperobject.biblemap.put("b13_16_6", new rusuperobject(13,16,6,"а Ванею и Озиила, священников, чтобы постоянно трубили пред ковчегом завета Божия. "));
rusuperobject.biblemap.put("b13_16_7", new rusuperobject(13,16,7,"В этот день Давид в первый раз дал псалом для славословия Господу чрез Асафа и братьев его: "));
rusuperobject.biblemap.put("b13_16_8", new rusuperobject(13,16,8,"славьте Господа, провозглашайте имя Его; возвещайте в народах дела Его; "));
rusuperobject.biblemap.put("b13_16_9", new rusuperobject(13,16,9,"пойте Ему, бряцайте Ему; поведайте о всех чудесах Его; "));
rusuperobject.biblemap.put("b13_16_10", new rusuperobject(13,16,10,"хвалитесь именем Его святым; да веселится сердце ищущих Господа; "));
rusuperobject.biblemap.put("b13_16_11", new rusuperobject(13,16,11,"взыщите Господа и силы Его, ищите непрестанно лица Его; "));
rusuperobject.biblemap.put("b13_16_12", new rusuperobject(13,16,12,"поминайте чудеса, которые Он сотворил, знамения Его и суды уст Его, "));
rusuperobject.biblemap.put("b13_16_13", new rusuperobject(13,16,13,"вы, семя Израилево, рабы Его, сыны Иакова, избранные Его! "));
rusuperobject.biblemap.put("b13_16_14", new rusuperobject(13,16,14,"Он Господь Бог наш; суды Его по всей земле. "));
rusuperobject.biblemap.put("b13_16_15", new rusuperobject(13,16,15,"Помните вечно завет Его, слово, которое Он заповедал в тысячу родов, "));
rusuperobject.biblemap.put("b13_16_16", new rusuperobject(13,16,16,"то, что завещал Аврааму, и в чем клялся Исааку, "));
rusuperobject.biblemap.put("b13_16_17", new rusuperobject(13,16,17,"и что поставил Иакову в закон и Израилю в завет вечный, "));
rusuperobject.biblemap.put("b13_16_18", new rusuperobject(13,16,18,"говоря: «тебе дам Я землю Ханаанскую, в наследственный удел вам». "));
rusuperobject.biblemap.put("b13_16_19", new rusuperobject(13,16,19,"Они были тогда малочисленны и ничтожны, и пришельцы в ней, "));
rusuperobject.biblemap.put("b13_16_20", new rusuperobject(13,16,20,"и переходили от народа к народу и из одного царства к другому народу; "));
rusuperobject.biblemap.put("b13_16_21", new rusuperobject(13,16,21,"но Он никому не позволил обижать их, и обличал за них царей: "));
rusuperobject.biblemap.put("b13_16_22", new rusuperobject(13,16,22,"«Не прикасайтеся к помазанным Моим, и пророкам Моим не делайте зла». "));
rusuperobject.biblemap.put("b13_16_23", new rusuperobject(13,16,23,"Пойте Господу, вся земля, благовествуйте изо дня в день спасение Его. "));
rusuperobject.biblemap.put("b13_16_24", new rusuperobject(13,16,24,"Возвещайте язычникам славу Его, всем народам чудеса Его, "));
rusuperobject.biblemap.put("b13_16_25", new rusuperobject(13,16,25,"ибо велик Господь и достохвален, страшен паче всех богов. "));
rusuperobject.biblemap.put("b13_16_26", new rusuperobject(13,16,26,"Ибо все боги народов ничто, а Господь небеса сотворил. "));
rusuperobject.biblemap.put("b13_16_27", new rusuperobject(13,16,27,"Слава и величие пред лицем Его, могущество и радость на месте святом Его. "));
rusuperobject.biblemap.put("b13_16_28", new rusuperobject(13,16,28,"Воздайте Господу, племена народов, воздайте Господу славу и честь, "));
rusuperobject.biblemap.put("b13_16_29", new rusuperobject(13,16,29,"воздайте Господу славу имени Его. Возьмите дар, идите пред лице Его, поклонитесь Господу в благолепии святыни Его. "));
rusuperobject.biblemap.put("b13_16_30", new rusuperobject(13,16,30,"Трепещи пред Ним, вся земля, ибо Он основал вселенную, она не поколеблется. "));
rusuperobject.biblemap.put("b13_16_31", new rusuperobject(13,16,31,"Да веселятся небеса, да торжествует земля, и да скажут в народах: Господь царствует! "));
rusuperobject.biblemap.put("b13_16_32", new rusuperobject(13,16,32,"Да плещет море и что наполняет его, да радуется поле и все, что на нем. "));
rusuperobject.biblemap.put("b13_16_33", new rusuperobject(13,16,33,"Да ликуют вместе все дерева дубравные пред лицем Господа, ибо Он идет судить землю. "));
rusuperobject.biblemap.put("b13_16_34", new rusuperobject(13,16,34,"Славьте Господа, ибо вовек милость Его, "));
rusuperobject.biblemap.put("b13_16_35", new rusuperobject(13,16,35,"и скажите: спаси нас, Боже, Спаситель наш! Собери нас и избавь нас от народов, да славим святое имя Твое и да хвалимся славою Твоею! "));
rusuperobject.biblemap.put("b13_16_36", new rusuperobject(13,16,36,"Благословен Господь Бог Израилев, от века и до века! И сказал весь народ: аминь! аллилуия! "));
rusuperobject.biblemap.put("b13_16_37", new rusuperobject(13,16,37,"Давид оставил там, пред ковчегом завета Господня, Асафа и братьев его, чтоб они служили пред ковчегом постоянно, каждый день, "));
rusuperobject.biblemap.put("b13_16_38", new rusuperobject(13,16,38,"и Овед-Едома и братьев его, шестьдесят восемь человек; Овед-Едома, сына Идифунова, и Хосу - привратниками, "));
rusuperobject.biblemap.put("b13_16_39", new rusuperobject(13,16,39,"а Садока священника и братьев его священников пред жилищем Господним, что на высоте в Гаваоне, "));
rusuperobject.biblemap.put("b13_16_40", new rusuperobject(13,16,40,"для возношения всесожжений Господу на жертвеннике всесожжения постоянно, утром и вечером, и для всего, что написано в законе Господа, который Он заповедал Израилю; "));
rusuperobject.biblemap.put("b13_16_41", new rusuperobject(13,16,41,"и с ними Емана и Идифуна и прочих избранных, которые назначены поименно, чтобы славить Господа, ибо навек милость Его. "));
rusuperobject.biblemap.put("b13_16_42", new rusuperobject(13,16,42,"При них Еман и Идифун прославляли Бога, играя на трубах, кимвалах и разных музыкальных орудиях; сыновей же Идифуна поставил при вратах. "));
rusuperobject.biblemap.put("b13_16_43", new rusuperobject(13,16,43,"И пошел весь народ, каждый в свой дом; возвратился и Давид, чтобы благословить дом свой. "));
rusuperobject.biblemap.put("b13_17_1", new rusuperobject(13,17,1,"Когда Давид жил в доме своем, то сказал Давид Нафану пророку: вот, я живу в доме кедровом, а ковчег завета Господня под шатром. "));
rusuperobject.biblemap.put("b13_17_2", new rusuperobject(13,17,2,"И сказал Нафан Давиду: все, что у тебя на сердце, делай, ибо с тобою Бог. "));
rusuperobject.biblemap.put("b13_17_3", new rusuperobject(13,17,3,"Но в ту же ночь было слово Божие к Нафану: "));
rusuperobject.biblemap.put("b13_17_4", new rusuperobject(13,17,4,"пойди и скажи рабу Моему Давиду: так говорит Господь: не ты построишь Мне дом для обитания, "));
rusuperobject.biblemap.put("b13_17_5", new rusuperobject(13,17,5,"ибо Я не жил в доме с того дня, как вывел сынов Израиля, и до сего дня, а ходил из скинии в скинию и из жилища в жилище. "));
rusuperobject.biblemap.put("b13_17_6", new rusuperobject(13,17,6,"Где ни ходил Я со всем Израилем, сказал ли Я хотя слово которому-либо из судей Израильских, которым Я повелел пасти народ Мой: зачем вы не построите Мне дома кедрового? "));
rusuperobject.biblemap.put("b13_17_7", new rusuperobject(13,17,7,"И теперь так скажи рабу Моему Давиду: так говорит Господь Саваоф: Я взял тебя от стада овец, чтобы ты был вождем народа Моего Израиля; "));
rusuperobject.biblemap.put("b13_17_8", new rusuperobject(13,17,8,"и был с тобою везде, куда ты ни ходил, и истребил всех врагов твоих пред лицем твоим, и сделал имя твое, как имя великих на земле; "));
rusuperobject.biblemap.put("b13_17_9", new rusuperobject(13,17,9,"и Я устроил место для народа Моего Израиля, и укоренил его, и будет он спокойно жить на месте своем, и не будет более тревожим, и нечестивые не станут больше теснить его, как прежде, "));
rusuperobject.biblemap.put("b13_17_10", new rusuperobject(13,17,10,"в те дни, когда Я поставил судей над народом Моим Израилем, и Я смирил всех врагов твоих, и возвещаю тебе, что Господь устроит тебе дом. "));
rusuperobject.biblemap.put("b13_17_11", new rusuperobject(13,17,11,"Когда исполнятся дни твои, и ты отойдешь к отцам твоим, тогда Я восставлю семя твое после тебя, которое будет из сынов твоих, и утвержу царство его. "));
rusuperobject.biblemap.put("b13_17_12", new rusuperobject(13,17,12,"Он построит Мне дом, и утвержу престол его на веки. "));
rusuperobject.biblemap.put("b13_17_13", new rusuperobject(13,17,13,"Я буду ему отцом, и он будет Мне сыном, - и милости Моей не отниму от него, как Я отнял от того, который был прежде тебя. "));
rusuperobject.biblemap.put("b13_17_14", new rusuperobject(13,17,14,"Я поставлю его в доме Моем и в царстве Моем на веки, и престол его будет тверд вечно. "));
rusuperobject.biblemap.put("b13_17_15", new rusuperobject(13,17,15,"Все эти слова и все видение точно пересказал Нафан Давиду. "));
rusuperobject.biblemap.put("b13_17_16", new rusuperobject(13,17,16,"И пришел царь Давид, и стал пред лицем Господним, и сказал: кто я, Господи Боже, и что такое дом мой, что Ты так возвысил меня? "));
rusuperobject.biblemap.put("b13_17_17", new rusuperobject(13,17,17,"Но и этого еще мало показалось в очах Твоих, Боже; Ты возвещаешь о доме раба Твоего вдаль, и взираешь на меня, как на человека великого, Господи Боже! "));
rusuperobject.biblemap.put("b13_17_18", new rusuperobject(13,17,18,"Что еще может прибавить пред Тобою Давид для возвеличения раба Твоего? Ты знаешь раба Твоего! "));
rusuperobject.biblemap.put("b13_17_19", new rusuperobject(13,17,19,"Господи! для раба Твоего, по сердцу Твоему, Ты делаешь все это великое, чтобы явить всякое величие. "));
rusuperobject.biblemap.put("b13_17_20", new rusuperobject(13,17,20,"Господи! Нет подобного Тебе, и нет Бога, кроме Тебя, по всему, что слышали мы своими ушами. "));
rusuperobject.biblemap.put("b13_17_21", new rusuperobject(13,17,21,"И кто подобен народу Твоему Израилю, единственному народу на земле, к которому приходил Бог, чтоб искупить его Себе в народ, сделать Себе имя великим и страшным делом - прогнанием народов от лица народа Твоего, который Ты избавил из Египта. "));
rusuperobject.biblemap.put("b13_17_22", new rusuperobject(13,17,22,"Ты соделал народ Твой Израиля Своим собственным народом навек, и Ты, Господи, стал Богом его. "));
rusuperobject.biblemap.put("b13_17_23", new rusuperobject(13,17,23,"Итак теперь, о, Господи, слово, которое Ты сказал о рабе Твоем и о доме его, утверди навек, и сделай, как Ты сказал. "));
rusuperobject.biblemap.put("b13_17_24", new rusuperobject(13,17,24,"И да пребудет и возвеличится имя Твое во веки, чтобы говорили: Господь Саваоф, Бог Израилев, есть Бог над Израилем, и дом раба Твоего Давида да будет тверд пред лицем Твоим. "));
rusuperobject.biblemap.put("b13_17_25", new rusuperobject(13,17,25,"Ибо Ты, Боже мой, открыл рабу Твоему, что Ты устроишь ему дом, поэтому раб Твой и дерзнул молиться пред Тобою. "));
rusuperobject.biblemap.put("b13_17_26", new rusuperobject(13,17,26,"И ныне, Господи, Ты Бог, и Ты сказал о рабе Твоем такое благо. "));
rusuperobject.biblemap.put("b13_17_27", new rusuperobject(13,17,27,"Начни же благословлять дом раба Твоего, чтоб он был вечно пред лицем Твоим. Ибо если Ты, Господи, благословишь, то будет он благословен вовек. "));
rusuperobject.biblemap.put("b13_18_1", new rusuperobject(13,18,1,"После сего Давид поразил Филистимлян и смирил их, и взял Геф и зависящие от него города из руки Филистимлян. "));
rusuperobject.biblemap.put("b13_18_2", new rusuperobject(13,18,2,"Он поразил также Моавитян, - и сделались Моавитяне рабами Давида, принося ему дань. "));
rusuperobject.biblemap.put("b13_18_3", new rusuperobject(13,18,3,"И поразил Давид Адраазара, царя Сувского, в Емафе, когда тот шел утвердить власть свою при реке Евфрате. "));
rusuperobject.biblemap.put("b13_18_4", new rusuperobject(13,18,4,"И взял Давид у него тысячу колесниц, семь тысяч всадников и двадцать тысяч пеших, и разрушил Давид все колесницы, оставив из них только сто. "));
rusuperobject.biblemap.put("b13_18_5", new rusuperobject(13,18,5,"Сирияне Дамасские пришли было на помощь к Адраазару, царю Сувскому, но Давид поразил двадцать две тысячи Сириян. "));
rusuperobject.biblemap.put("b13_18_6", new rusuperobject(13,18,6,"И поставил Давид охранное войско в Сирии Дамасской, и сделались Сирияне рабами Давида, принося ему дань. И помогал Господь Давиду везде, куда он ни ходил. "));
rusuperobject.biblemap.put("b13_18_7", new rusuperobject(13,18,7,"И взял Давид золотые щиты, которые были у рабов Адраазара, и принес их в Иерусалим. "));
rusuperobject.biblemap.put("b13_18_8", new rusuperobject(13,18,8,"А из Тивхавы и Куна, городов Адраазаровых, взял Давид весьма много меди. Из нее Соломон сделал медное море и столбы и медные сосуды. "));
rusuperobject.biblemap.put("b13_18_9", new rusuperobject(13,18,9,"И услышал Фой, царь Имафа, что Давид поразил все войско Адраазара, царя Сувского. "));
rusuperobject.biblemap.put("b13_18_10", new rusuperobject(13,18,10,"И послал Иорама, сына своего, к царю Давиду, приветствовать его и благодарить за то, что он воевал с Адраазаром и поразил его, ибо Фой был в войне с Адраазаром, - и с ним всякие сосуды золотые, серебряные и медные. "));
rusuperobject.biblemap.put("b13_18_11", new rusuperobject(13,18,11,"И посвятил их царь Давид Господу вместе с серебром и золотом, которое он взял от всех народов: от Идумеян, Моавитян, Аммонитян, Филистимлян и от Амаликитян. "));
rusuperobject.biblemap.put("b13_18_12", new rusuperobject(13,18,12,"И Авесса, сын Саруи, поразил Идумеян на долине Соляной восемнадцать тысяч; "));
rusuperobject.biblemap.put("b13_18_13", new rusuperobject(13,18,13,"и поставил в Идумее охранное войско, и сделались все Идумеяне рабами Давиду. Господь помогал Давиду везде, куда он ни ходил. "));
rusuperobject.biblemap.put("b13_18_14", new rusuperobject(13,18,14,"И царствовал Давид над всем Израилем, и творил суд и правду всему народу своему. "));
rusuperobject.biblemap.put("b13_18_15", new rusuperobject(13,18,15,"Иоав, сын Саруи, был начальником войска, Иосафат, сын Ахилуда, дееписателем, "));
rusuperobject.biblemap.put("b13_18_16", new rusuperobject(13,18,16,"Садок, сын Ахитува, и Авимелех, сын Авиафара, священниками, а Суса писцом, "));
rusuperobject.biblemap.put("b13_18_17", new rusuperobject(13,18,17,"Ванея, сын Иодая, над Хелефеями и Фелефеями, а сыновья Давидовы - первыми при царе. "));
rusuperobject.biblemap.put("b13_19_1", new rusuperobject(13,19,1,"После сего умер Наас, царь Аммонитский, и воцарился сын его вместо него. "));
rusuperobject.biblemap.put("b13_19_2", new rusuperobject(13,19,2,"И сказал Давид: окажу я милость Аннону, сыну Наасову, за благодеяние, которое отец его оказал мне. И послал Давид послов утешить его об отце его; и пришли слуги Давидовы в землю Аммонитскую, к Аннону, чтобы утешить его. "));
rusuperobject.biblemap.put("b13_19_3", new rusuperobject(13,19,3,"Но князья Аммонитские сказали Аннону: неужели ты думаешь, что Давид из уважения к отцу твоему прислал к тебе утешителей? Не для того ли пришли слуги его к тебе, чтобы разведать и высмотреть землю и разорить ее? "));
rusuperobject.biblemap.put("b13_19_4", new rusuperobject(13,19,4,"И взял Аннон слуг Давидовых и обрил их, и обрезал одежды их наполовину до чресл и отпустил их. "));
rusuperobject.biblemap.put("b13_19_5", new rusuperobject(13,19,5,"И пошли они. И донесено было Давиду о людях сих, и он послал им навстречу, так как они были очень обесчещены; и сказал царь: останьтесь в Иерихоне, пока отрастут бороды ваши, и тогда возвратитесь. "));
rusuperobject.biblemap.put("b13_19_6", new rusuperobject(13,19,6,"Когда Аммонитяне увидели, что они сделались ненавистными Давиду, тогда послал Аннон и Аммонитяне тысячу талантов серебра, чтобы нанять себе колесниц и всадников из Сирии Месопотамской и из Сирии Мааха и из Сувы. "));
rusuperobject.biblemap.put("b13_19_7", new rusuperobject(13,19,7,"И наняли себе тридцать две тысячи колесниц и царя Мааха с народом его, которые пришли и расположились станом пред Медевою. И Аммонитяне собрались из городов своих и выступили на войну. "));
rusuperobject.biblemap.put("b13_19_8", new rusuperobject(13,19,8,"Когда услышал об этом Давид, то послал Иоава со всем войском храбрых. "));
rusuperobject.biblemap.put("b13_19_9", new rusuperobject(13,19,9,"И выступили Аммонитяне и выстроились к сражению у ворот города, а цари, которые пришли, отдельно в поле. "));
rusuperobject.biblemap.put("b13_19_10", new rusuperobject(13,19,10,"Иоав, видя, что предстоит ему сражение спереди и сзади, избрал воинов из всех отборных в Израиле и выстроил их против Сириян. "));
rusuperobject.biblemap.put("b13_19_11", new rusuperobject(13,19,11,"А остальную часть народа поручил Авессе, брату своему, чтоб они выстроились против Аммонитян. "));
rusuperobject.biblemap.put("b13_19_12", new rusuperobject(13,19,12,"И сказал он: если Сирияне будут одолевать меня, то ты поможешь мне, а если Аммонитяне будут одолевать тебя, то я помогу тебе. "));
rusuperobject.biblemap.put("b13_19_13", new rusuperobject(13,19,13,"Будь мужествен, и будем твердо стоять за народ наш и за города Бога нашего, - и Господь пусть сделает, что ему угодно. "));
rusuperobject.biblemap.put("b13_19_14", new rusuperobject(13,19,14,"И вступил Иоав и люди, которые были у него, в сражение с Сириянами, и они побежали от него. "));
rusuperobject.biblemap.put("b13_19_15", new rusuperobject(13,19,15,"Аммонитяне же, увидев, что Сирияне бегут, и сами побежали от Авессы, брата его, и ушли в город. И пришел Иоав в Иерусалим. "));
rusuperobject.biblemap.put("b13_19_16", new rusuperobject(13,19,16,"Сирияне, видя, что они поражены Израильтянами, отправили послов и вывели Сириян, которые были по ту сторону реки, и Совак, военачальник Адраазаров, предводительствовал ими. "));
rusuperobject.biblemap.put("b13_19_17", new rusuperobject(13,19,17,"Когда донесли об этом Давиду, он собрал всех Израильтян, перешел Иордан и, придя к ним, выстроился против них; и вступил Давид в сражение с Сириянами, и они сразились с ним. "));
rusuperobject.biblemap.put("b13_19_18", new rusuperobject(13,19,18,"И Сирияне побежали от Израильтян, и истребил Давид у Сириян семь тысяч колесниц и сорок тысяч пеших, и Совака военачальника умертвил. "));
rusuperobject.biblemap.put("b13_19_19", new rusuperobject(13,19,19,"Когда увидели слуги Адраазара, что они поражены Израильтянами, заключили с Давидом мир и подчинились ему. И не хотели Сирияне помогать более Аммонитянам. "));
rusuperobject.biblemap.put("b13_20_1", new rusuperobject(13,20,1,"Через год, в то время когда цари выходят на войну, вывел Иоав войско и стал разорять землю Аммонитян, и пришел и осадил Равву. Давид же оставался в Иерусалиме. Иоав, завоевав Равву, разрушил ее. "));
rusuperobject.biblemap.put("b13_20_2", new rusuperobject(13,20,2,"И взял Давид венец царя их с головы его, и в нем оказалось весу талант золота, и драгоценные камни были на нем; и был он возложен на голову Давида. И добычи очень много вынес из города. "));
rusuperobject.biblemap.put("b13_20_3", new rusuperobject(13,20,3,"А народ, который был в нем, вывел и умерщвлял их пилами, железными молотилами и секирами. Так поступил Давид со всеми городами Аммонитян, и возвратился Давид и весь народ в Иерусалим. "));
rusuperobject.biblemap.put("b13_20_4", new rusuperobject(13,20,4,"После того началась война с Филистимлянами в Газере. Тогда Совохай Хушатянин поразил Сафа, одного из потомков Рефаимов. И они усмирились. "));
rusuperobject.biblemap.put("b13_20_5", new rusuperobject(13,20,5,"И опять была война с Филистимлянами. Тогда Елханам, сын Иаира, поразил Лахмия, брата Голиафова, Гефянина, у которого древко копья было, как навой у ткачей. "));
rusuperobject.biblemap.put("b13_20_6", new rusuperobject(13,20,6,"Было еще сражение в Гефе. Там был один рослый человек, у которого было по шести пальцев, всего двадцать четыре. И он также был из потомков Рефаимов. "));
rusuperobject.biblemap.put("b13_20_7", new rusuperobject(13,20,7,"Он поносил Израиля, но Ионафан, сын Шимы, брата Давидова, поразил его. "));
rusuperobject.biblemap.put("b13_20_8", new rusuperobject(13,20,8,"Это были родившиеся от Рефаимов в Гефе, и пали от руки Давида и от руки слуг его. "));
rusuperobject.biblemap.put("b13_21_1", new rusuperobject(13,21,1,"И восстал сатана на Израиля, и возбудил Давида сделать счисление Израильтян. "));
rusuperobject.biblemap.put("b13_21_2", new rusuperobject(13,21,2,"И сказал Давид Иоаву и начальствующим в народе: пойдите исчислите Израильтян, от Вирсавии до Дана, и представьте мне, чтоб я знал число их. "));
rusuperobject.biblemap.put("b13_21_3", new rusuperobject(13,21,3,"И сказал Иоав: да умножит Господь народ Свой во сто раз против того, сколько есть его. Не все ли они, господин мой царь, рабы господина моего? Для чего же требует сего господин мой? Чтобы вменилось это в вину Израилю? "));
rusuperobject.biblemap.put("b13_21_4", new rusuperobject(13,21,4,"Но царское слово превозмогло Иоава; и пошел Иоав, и обошел всего Израиля, и пришел в Иерусалим. "));
rusuperobject.biblemap.put("b13_21_5", new rusuperobject(13,21,5,"И подал Иоав Давиду список народной переписи, и было всех Израильтян тысяча тысяч, и сто тысяч мужей, обнажающих меч, и Иудеев - четыреста семьдесят тысяч, обнажающих меч. "));
rusuperobject.biblemap.put("b13_21_6", new rusuperobject(13,21,6,"А левитов и Вениаминян он не исчислял между ними, потому что царское слово противно было Иоаву. "));
rusuperobject.biblemap.put("b13_21_7", new rusuperobject(13,21,7,"И не угодно было в очах Божиих дело сие, и Он поразил Израиля. "));
rusuperobject.biblemap.put("b13_21_8", new rusuperobject(13,21,8,"И сказал Давид Богу: весьма согрешил я, что сделал это. И ныне прости вину раба Твоего, ибо я поступил очень безрассудно. "));
rusuperobject.biblemap.put("b13_21_9", new rusuperobject(13,21,9,"И говорил Господь Гаду, прозорливцу Давидову, и сказал: "));
rusuperobject.biblemap.put("b13_21_10", new rusuperobject(13,21,10,"пойди и скажи Давиду: так говорит Господь: три наказания Я предлагаю тебе, избери себе одно из них, - и Я пошлю его на тебя. "));
rusuperobject.biblemap.put("b13_21_11", new rusuperobject(13,21,11,"И пришел Гад к Давиду и сказал ему: так говорит Господь: избирай себе: "));
rusuperobject.biblemap.put("b13_21_12", new rusuperobject(13,21,12,"или три года - голод, или три месяца будешь ты преследуем неприятелями твоими и меч врагов твоих будет досягать до тебя; или три дня - меч Господень и язва на земле и Ангел Господень, истребляющий во всех пределах Израиля. Итак, рассмотри, что мне отвечать Пославшему меня с словом. "));
rusuperobject.biblemap.put("b13_21_13", new rusuperobject(13,21,13,"И сказал Давид Гаду: тяжело мне очень, но пусть лучше впаду в руки Господа, ибо весьма велико милосердие Его, только бы не впасть мне в руки человеческие. "));
rusuperobject.biblemap.put("b13_21_14", new rusuperobject(13,21,14,"И послал Господь язву на Израиля, и умерло Израильтян семьдесят тысяч человек. "));
rusuperobject.biblemap.put("b13_21_15", new rusuperobject(13,21,15,"И послал Бог Ангела в Иерусалим, чтобы истреблять его. И когда он начал истреблять, увидел Господь и пожалел о сем бедствии, и сказал Ангелу-истребителю: довольно! теперь опусти руку твою. Ангел же Господень стоял тогда над гумном Орны Иевусеянина. "));
rusuperobject.biblemap.put("b13_21_16", new rusuperobject(13,21,16,"И поднял Давид глаза свои, и увидел Ангела Господня, стоящего между землею и небом, с обнаженным в руке его мечом, простертым на Иерусалим; и пал Давид и старейшины, покрытые вретищем, на лица свои. "));
rusuperobject.biblemap.put("b13_21_17", new rusuperobject(13,21,17,"И сказал Давид Богу: не я ли велел исчислить народ? я согрешил, я сделал зло, а эти овцы что сделали? Господи, Боже мой! да будет рука Твоя на мне и на доме отца моего, а не на народе Твоем, чтобы погубить его. "));
rusuperobject.biblemap.put("b13_21_18", new rusuperobject(13,21,18,"И Ангел Господень сказал Гаду, чтобы тот сказал Давиду: пусть Давид придет и поставит жертвенник Господу на гумне Орны Иевусеянина. "));
rusuperobject.biblemap.put("b13_21_19", new rusuperobject(13,21,19,"И пошел Давид, по слову Гада, которое он говорил именем Господним. "));
rusuperobject.biblemap.put("b13_21_20", new rusuperobject(13,21,20,"Орна обратился, увидел Ангела, и четыре сына его с ним скрылись. Орна молотил тогда пшеницу. "));
rusuperobject.biblemap.put("b13_21_21", new rusuperobject(13,21,21,"И пришел Давид к Орне. Орна, взглянув и увидев Давида, вышел из гумна и поклонился Давиду лицем до земли. "));
rusuperobject.biblemap.put("b13_21_22", new rusuperobject(13,21,22,"И сказал Давид Орне: отдай мне место под гумном, я построю на нем жертвенник Господу; за настоящую цену отдай мне его, чтобы прекратилось истребление народа. "));
rusuperobject.biblemap.put("b13_21_23", new rusuperobject(13,21,23,"И сказал Орна Давиду: возьми себе; пусть делает господин мой царь что ему угодно; вот я отдаю и волов на всесожжение, и молотильные орудия на дрова, и пшеницу на приношение; все это отдаю даром. "));
rusuperobject.biblemap.put("b13_21_24", new rusuperobject(13,21,24,"И сказал царь Давид Орне: нет, я хочу купить у тебя за настоящую цену, ибо не стану я приносить твоей собственности Господу, и не буду приносить во всесожжение взятого даром. "));
rusuperobject.biblemap.put("b13_21_25", new rusuperobject(13,21,25,"И дал Давид Орне за это место шестьсот сиклей золота. "));
rusuperobject.biblemap.put("b13_21_26", new rusuperobject(13,21,26,"И соорудил там Давид жертвенник Господу и вознес всесожжения и мирные жертвы; и призвал Господа, и Он услышал его, послав огонь с неба на жертвенник всесожжения. "));
rusuperobject.biblemap.put("b13_21_27", new rusuperobject(13,21,27,"И сказал Господь Ангелу: возврати меч твой в ножны его. "));
rusuperobject.biblemap.put("b13_21_28", new rusuperobject(13,21,28,"В это время Давид, видя, что Господь услышал его на гумне Орны Иевусеянина, принес там жертву. "));
rusuperobject.biblemap.put("b13_21_29", new rusuperobject(13,21,29,"Скиния же Господня, которую сделал Моисей в пустыне, и жертвенник всесожжения находились в то время на высоте в Гаваоне. "));
rusuperobject.biblemap.put("b13_21_30", new rusuperobject(13,21,30,"И не мог Давид пойти туда, чтобы взыскать Бога, потому что устрашен был мечом Ангела Господня. "));
rusuperobject.biblemap.put("b13_22_1", new rusuperobject(13,22,1,"И сказал Давид: вот дом Господа Бога и вот жертвенник для всесожжений Израиля. "));
rusuperobject.biblemap.put("b13_22_2", new rusuperobject(13,22,2,"И приказал Давид собрать пришельцев, находившихся в земле Израильской, и поставил каменотесов, чтобы обтесывать камни для построения дома Божия. "));
rusuperobject.biblemap.put("b13_22_3", new rusuperobject(13,22,3,"И множество железа для гвоздей к дверям ворот и для связей заготовил Давид, и множество меди без весу, "));
rusuperobject.biblemap.put("b13_22_4", new rusuperobject(13,22,4,"и кедровых дерев без счету, потому что Сидоняне и Тиряне доставили Давиду множество кедровых дерев. "));
rusuperobject.biblemap.put("b13_22_5", new rusuperobject(13,22,5,"И сказал Давид: Соломон, сын мой, молод и малосилен, а дом, который следует выстроить для Господа, должен быть весьма величествен, на славу и украшение пред всеми землями: итак буду я заготовлять для него. И заготовил Давид до смерти своей много. "));
rusuperobject.biblemap.put("b13_22_6", new rusuperobject(13,22,6,"И призвал Соломона, сына своего, и завещал ему построить дом Господу Богу Израилеву. "));
rusuperobject.biblemap.put("b13_22_7", new rusuperobject(13,22,7,"И сказал Давид Соломону: сын мой! у меня было на сердце построить дом во имя Господа, Бога моего, "));
rusuperobject.biblemap.put("b13_22_8", new rusuperobject(13,22,8,"но было ко мне слово Господне, и сказано: «ты пролил много крови и вел большие войны; ты не должен строить дома имени Моему, потому что пролил много крови на землю пред лицем Моим. "));
rusuperobject.biblemap.put("b13_22_9", new rusuperobject(13,22,9,"Вот, у тебя родится сын: он будет человек мирный; Я дам ему покой от всех врагов его кругом: посему имя ему будет Соломон. И мир и покой дам Израилю во дни его. "));
rusuperobject.biblemap.put("b13_22_10", new rusuperobject(13,22,10,"Он построит дом имени Моему, и он будет Мне сыном, а Я ему отцом, и утвержу престол царства его над Израилем навек». "));
rusuperobject.biblemap.put("b13_22_11", new rusuperobject(13,22,11,"И ныне, сын мой! да будет Господь с тобою, чтобы ты был благоуспешен и построил дом Господу Богу твоему, как Он говорил о тебе. "));
rusuperobject.biblemap.put("b13_22_12", new rusuperobject(13,22,12,"Да даст тебе Господь смысл и разум, и поставит тебя над Израилем; и соблюди закон Господа Бога твоего. "));
rusuperobject.biblemap.put("b13_22_13", new rusuperobject(13,22,13,"Тогда ты будешь благоуспешен, если будешь стараться исполнять уставы и законы, которые заповедал Господь Моисею для Израиля. Будь тверд и мужествен, не бойся и не унывай. "));
rusuperobject.biblemap.put("b13_22_14", new rusuperobject(13,22,14,"И вот, я при скудости моей приготовил для дома Господня сто тысяч талантов золота и тысячу тысяч талантов серебра, а меди и железу нет веса, потому что их множество; и дерева и камни я также заготовил, а ты еще прибавь к этому. "));
rusuperobject.biblemap.put("b13_22_15", new rusuperobject(13,22,15,"У тебя множество рабочих, и каменотесов, резчиков и плотников, и всяких способных на всякое дело; "));
rusuperobject.biblemap.put("b13_22_16", new rusuperobject(13,22,16,"золоту, серебру и меди и железу нет счета: начни и делай; Господь будет с тобою. "));
rusuperobject.biblemap.put("b13_22_17", new rusuperobject(13,22,17,"И завещал Давид всем князьям Израилевым помогать Соломону, сыну его: "));
rusuperobject.biblemap.put("b13_22_18", new rusuperobject(13,22,18,"не с вами ли Господь Бог наш, давший вам покой со всех сторон? потому что Он предал в руки мои жителей земли, и покорилась земля пред Господом и пред народом Его. "));
rusuperobject.biblemap.put("b13_22_19", new rusuperobject(13,22,19,"Итак расположите сердце ваше и душу вашу к тому, чтобы взыскать Господа Бога вашего. Встаньте и постройте святилище Господу Богу, чтобы перенести ковчег завета Господня и священные сосуды Божии в дом, созидаемый имени Господню. "));
rusuperobject.biblemap.put("b13_23_1", new rusuperobject(13,23,1,"Давид, состарившись и насытившись жизнью, воцарил над Израилем сына своего Соломона. "));
rusuperobject.biblemap.put("b13_23_2", new rusuperobject(13,23,2,"И собрал всех князей Израилевых и священников и левитов, "));
rusuperobject.biblemap.put("b13_23_3", new rusuperobject(13,23,3,"и исчислены были левиты, от тридцати лет и выше, и было число их, считая поголовно, тридцать восемь тысяч человек. "));
rusuperobject.biblemap.put("b13_23_4", new rusuperobject(13,23,4,"Из них назначены для дела в доме Господнем двадцать четыре тысячи, писцов же и судей шесть тысяч, "));
rusuperobject.biblemap.put("b13_23_5", new rusuperobject(13,23,5,"и четыре тысячи привратников, и четыре тысячи прославляющих Господа на музыкальных орудиях, которые он сделал для прославления. "));
rusuperobject.biblemap.put("b13_23_6", new rusuperobject(13,23,6,"И разделил их Давид на череды по сынам Левия - Гирсону, Каафу и Мерари. "));
rusuperobject.biblemap.put("b13_23_7", new rusuperobject(13,23,7,"Из Гирсонян - Лаедан и Шимей. "));
rusuperobject.biblemap.put("b13_23_8", new rusuperobject(13,23,8,"Сыновья Лаедана: первый Иехиил, Зефам и Иоиль, трое. "));
rusuperobject.biblemap.put("b13_23_9", new rusuperobject(13,23,9,"Сыновья Шимея: Шеломиф, Хазиил и Гаран, трое. Они главы поколений Лаедановых. "));
rusuperobject.biblemap.put("b13_23_10", new rusuperobject(13,23,10,"Еще сыновья Шимея: Иахаф, Зиза, Иеуш и Берия. Это сыновья Шимея, четверо. "));
rusuperobject.biblemap.put("b13_23_11", new rusuperobject(13,23,11,"Иахаф был главным, Зиза вторым; Иеуш и Берия имели детей немного, и потому они были в одном счете при доме отца. "));
rusuperobject.biblemap.put("b13_23_12", new rusuperobject(13,23,12,"Сыновья Каафа: Амрам, Ицгар, Хеврон и Озиил, четверо. "));
rusuperobject.biblemap.put("b13_23_13", new rusuperobject(13,23,13,"Сыновья Амрама: Аарон и Моисей. Аарон отделен был на посвящение ко Святому Святых, он и сыновья его, на веки, чтобы совершать курение пред лицем Господа, чтобы служить Ему и благословлять именем Его на веки. "));
rusuperobject.biblemap.put("b13_23_14", new rusuperobject(13,23,14,"А Моисей, человек Божий, и сыновья его причтены к колену Левиину. "));
rusuperobject.biblemap.put("b13_23_15", new rusuperobject(13,23,15,"Сыновья Моисея: Гирсон и Елиезер. "));
rusuperobject.biblemap.put("b13_23_16", new rusuperobject(13,23,16,"Сыновья Гирсона: первый был Шевуил. "));
rusuperobject.biblemap.put("b13_23_17", new rusuperobject(13,23,17,"Сыновья Елиезера были: первый Рехавия. И не было у Елиезера других сыновей; у Рехавии же было очень много сыновей. "));
rusuperobject.biblemap.put("b13_23_18", new rusuperobject(13,23,18,"Сыновья Ицгара: первый Шеломиф. "));
rusuperobject.biblemap.put("b13_23_19", new rusuperobject(13,23,19,"Сыновья Хеврона: первый Иерия и второй Амария, третий Иахазиил и четвертый Иекамам. "));
rusuperobject.biblemap.put("b13_23_20", new rusuperobject(13,23,20,"Сыновья Озиила: первый Миха и второй Ишшия. "));
rusuperobject.biblemap.put("b13_23_21", new rusuperobject(13,23,21,"Сыновья Мерарины: Махли и Муши. Сыновья Махлия: Елеазар и Кис. "));
rusuperobject.biblemap.put("b13_23_22", new rusuperobject(13,23,22,"И умер Елеазар, и не было у него сыновей, а только дочери; и взяли их за себя сыновья Киса, братья их. "));
rusuperobject.biblemap.put("b13_23_23", new rusuperobject(13,23,23,"Сыновья Мушия: Махли, Едер и Иремоф - трое. "));
rusuperobject.biblemap.put("b13_23_24", new rusuperobject(13,23,24,"Вот сыновья Левиины, по домам отцов их, главы семейств, по именному счислению их поголовно, которые отправляли дела служения в доме Господнем, от двадцати лет и выше. "));
rusuperobject.biblemap.put("b13_23_25", new rusuperobject(13,23,25,"Ибо Давид сказал: Господь, Бог Израилев, дал покой народу Своему и водворил его в Иерусалиме на веки, "));
rusuperobject.biblemap.put("b13_23_26", new rusuperobject(13,23,26,"и левитам не нужно носить скинию и всякие вещи ее для служения в ней. "));
rusuperobject.biblemap.put("b13_23_27", new rusuperobject(13,23,27,"Посему, по последним повелениям Давида, исчислены левиты от двадцати лет и выше, "));
rusuperobject.biblemap.put("b13_23_28", new rusuperobject(13,23,28,"чтоб они были при сынах Аароновых, для служения дому Господню, во дворе и в пристройках, для соблюдения чистоты всего святилища и для исполнения всякой службы при доме Божием, "));
rusuperobject.biblemap.put("b13_23_29", new rusuperobject(13,23,29,"для наблюдения за хлебами предложения и пшеничною мукою для хлебного приношения и пресными лепешками, за печеным, жареным и за всякою мерою и весом, "));
rusuperobject.biblemap.put("b13_23_30", new rusuperobject(13,23,30,"и чтобы становились каждое утро благодарить и славословить Господа, также и вечером, "));
rusuperobject.biblemap.put("b13_23_31", new rusuperobject(13,23,31,"и при всех всесожжениях, возносимых Господу в субботы, в новомесячия и в праздники по числу, как предписано о них, - постоянно пред лицем Господа, "));
rusuperobject.biblemap.put("b13_23_32", new rusuperobject(13,23,32,"и чтобы охраняли скинию откровения и святилище и сынов Аароновых, братьев своих, при службах дому Господню. "));
rusuperobject.biblemap.put("b13_24_1", new rusuperobject(13,24,1,"И вот распределения сыновей Аароновых: сыновья Аарона: Надав, Авиуд, Елеазар и Ифамар. "));
rusuperobject.biblemap.put("b13_24_2", new rusuperobject(13,24,2,"Надав и Авиуд умерли прежде отца своего, сыновей же не было у них, и потому священствовали Елеазар и Ифамар. "));
rusuperobject.biblemap.put("b13_24_3", new rusuperobject(13,24,3,"И распределил их Давид - Садока из сыновей Елеазара, и Ахимелеха из сыновей Ифамара, поочередно на службу их. "));
rusuperobject.biblemap.put("b13_24_4", new rusuperobject(13,24,4,"И нашлось, что между сынами Елеазара глав поколений более, нежели между сынами Ифамара. И он распределил их так: из сынов Елеазара шестнадцать глав семейств, а из сынов Ифамара восемь. "));
rusuperobject.biblemap.put("b13_24_5", new rusuperobject(13,24,5,"Распределял же их по жребиям, потому что главными во святилище и главными пред Богом были из сынов Елеазара и из сынов Ифамара, "));
rusuperobject.biblemap.put("b13_24_6", new rusuperobject(13,24,6,"и записывал их Шемаия, сын Нафанаила, писец из левитов, пред лицем царя и князей и пред священником Садоком и Ахимелехом, сыном Авиафара, и пред главами семейств священнических и левитских: брали при бросании жребия одно семейство из рода Елеазарова, потом брали из рода Ифамарова. "));
rusuperobject.biblemap.put("b13_24_7", new rusuperobject(13,24,7,"И вышел первый жребий Иегоиариву, второй Иедаии, "));
rusuperobject.biblemap.put("b13_24_8", new rusuperobject(13,24,8,"третий Хариму, четвертый Сеориму, "));
rusuperobject.biblemap.put("b13_24_9", new rusuperobject(13,24,9,"пятый Малхию, шестой Миямину, "));
rusuperobject.biblemap.put("b13_24_10", new rusuperobject(13,24,10,"седьмой Гаккоцу, восьмой Авии, "));
rusuperobject.biblemap.put("b13_24_11", new rusuperobject(13,24,11,"девятый Иешую, десятый Шехании, "));
rusuperobject.biblemap.put("b13_24_12", new rusuperobject(13,24,12,"одиннадцатый Елиашиву, двенадцатый Иакиму, "));
rusuperobject.biblemap.put("b13_24_13", new rusuperobject(13,24,13,"тринадцатый Хушаю, четырнадцатый Иешеваву, "));
rusuperobject.biblemap.put("b13_24_14", new rusuperobject(13,24,14,"пятнадцатый Вилге, шестнадцатый Имеру, "));
rusuperobject.biblemap.put("b13_24_15", new rusuperobject(13,24,15,"семнадцатый Хезиру, восемнадцатый Гапицецу, "));
rusuperobject.biblemap.put("b13_24_16", new rusuperobject(13,24,16,"девятнадцатый Петахии, двадцатый Иезекиилю, "));
rusuperobject.biblemap.put("b13_24_17", new rusuperobject(13,24,17,"двадцать первый Иахину, двадцать второй Гамулу, "));
rusuperobject.biblemap.put("b13_24_18", new rusuperobject(13,24,18,"двадцать третий Делаии, двадцать четвертый Маазии. "));
rusuperobject.biblemap.put("b13_24_19", new rusuperobject(13,24,19,"Вот порядок их при служении их, как им приходить в дом Господень, по уставу их чрез Аарона, отца их, как заповедал ему Господь Бог Израилев. "));
rusuperobject.biblemap.put("b13_24_20", new rusuperobject(13,24,20,"У прочих сыновей Левия - распределение: из сынов Амрама: Шуваил; из сынов Шуваила: Иедия; "));
rusuperobject.biblemap.put("b13_24_21", new rusuperobject(13,24,21,"от Рехавии: из сынов Рехавии Ишшия был первый; "));
rusuperobject.biblemap.put("b13_24_22", new rusuperobject(13,24,22,"от Ицгара: Шеломоф; из сыновей Шеломофа: Иахав; "));
rusuperobject.biblemap.put("b13_24_23", new rusuperobject(13,24,23,"из сыновей Хеврона: первый Иерия, второй Амария, третий Иахазиил, четвертый Иекамам. "));
rusuperobject.biblemap.put("b13_24_24", new rusuperobject(13,24,24,"Из сыновей Озиила: Миха; из сыновей Михи: Шамир. "));
rusuperobject.biblemap.put("b13_24_25", new rusuperobject(13,24,25,"Брат Михи Ишшия; из сыновей Ишшии: Захария. "));
rusuperobject.biblemap.put("b13_24_26", new rusuperobject(13,24,26,"Сыновья Мерари: Махли и Муши; из сыновей Иаазии: Бено. "));
rusuperobject.biblemap.put("b13_24_27", new rusuperobject(13,24,27,"Из сыновей Мерари у Иаазии: Бено и Шогам, и Заккур и Иври. "));
rusuperobject.biblemap.put("b13_24_28", new rusuperobject(13,24,28,"У Махлия - Елеазар; у него сыновей не было. "));
rusuperobject.biblemap.put("b13_24_29", new rusuperobject(13,24,29,"У Киса: из сыновей Киса: Иерахмиил; "));
rusuperobject.biblemap.put("b13_24_30", new rusuperobject(13,24,30,"сыновья Мушия: Махли, Едер и Иеримоф. Вот сыновья левитов по поколениям их. "));
rusuperobject.biblemap.put("b13_24_31", new rusuperobject(13,24,31,"Бросали и они жребий, наравне с братьями своими, сыновьями Аароновыми, пред лицем царя Давида и Садока и Ахимелеха, и глав семейств священнических и левитских: глава семейства наравне с меньшим братом своим. "));
rusuperobject.biblemap.put("b13_25_1", new rusuperobject(13,25,1,"И отделил Давид и начальники войска на службу сыновей Асафа, Емана и Идифуна, чтобы они провещавали на цитрах, псалтирях и кимвалах; и были отчислены они на дело служения своего: "));
rusuperobject.biblemap.put("b13_25_2", new rusuperobject(13,25,2,"из сыновей Асафа: Заккур, Иосиф, Нефания и Ашарела сыновья Асафа, под руководством Асафа, игравшего по наставлению царя. "));
rusuperobject.biblemap.put("b13_25_3", new rusuperobject(13,25,3,"От Идифуна сыновья Идифуна: Гедалия, Цери, Исаия, Семей, Хашавия и Маттафия, шестеро, под руководством отца своего Идифуна, игравшего на цитре во славу и хвалу Господа. "));
rusuperobject.biblemap.put("b13_25_4", new rusuperobject(13,25,4,"От Емана сыновья Емана: Буккия, Матфания, Озиил, Шевуил и Иеримоф, Ханания, Ханани, Елиафа, Гиддалти, Ромамти-Езер, Иошбекаша, Маллофи, Гофир и Махазиоф. "));
rusuperobject.biblemap.put("b13_25_5", new rusuperobject(13,25,5,"Все эти сыновья Емана, прозорливца царского, по словам Божиим, чтобы возвышать славу его. И дал Бог Еману четырнадцать сыновей и трех дочерей. "));
rusuperobject.biblemap.put("b13_25_6", new rusuperobject(13,25,6,"Все они под руководством отца своего пели в доме Господнем с кимвалами, псалтирями и цитрами в служении в доме Божием, по указанию царя, или Асафа, Идифуна и Емана. "));
rusuperobject.biblemap.put("b13_25_7", new rusuperobject(13,25,7,"И было число их с братьями их, обученными петь пред Господом, всех знающих сие дело, двести восемьдесят восемь. "));
rusuperobject.biblemap.put("b13_25_8", new rusuperobject(13,25,8,"И бросили они жребий о череде служения, малый наравне с большим, учители наравне с учениками. "));
rusuperobject.biblemap.put("b13_25_9", new rusuperobject(13,25,9,"И вышел первый жребий Асафу, для Иосифа; второй Гедалии с братьями его и сыновьями его; их было двенадцать; "));
rusuperobject.biblemap.put("b13_25_10", new rusuperobject(13,25,10,"третий Заккуру с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_11", new rusuperobject(13,25,11,"четвертый Ицрию с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_12", new rusuperobject(13,25,12,"пятый Нефании с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_13", new rusuperobject(13,25,13,"шестой Буккии с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_14", new rusuperobject(13,25,14,"седьмой Иесареле с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_15", new rusuperobject(13,25,15,"восьмой Исаии с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_16", new rusuperobject(13,25,16,"девятый Матфании с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_17", new rusuperobject(13,25,17,"десятый Шимею с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_18", new rusuperobject(13,25,18,"одиннадцатый Азариилу с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_19", new rusuperobject(13,25,19,"двенадцатый Хашавии с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_20", new rusuperobject(13,25,20,"тринадцатый Шуваилу с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_21", new rusuperobject(13,25,21,"четырнадцатый Маттафии с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_22", new rusuperobject(13,25,22,"пятнадцатый Иеримофу с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_23", new rusuperobject(13,25,23,"шестнадцатый Ханании с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_24", new rusuperobject(13,25,24,"семнадцатый Иошбекаше с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_25", new rusuperobject(13,25,25,"восемнадцатый Ханани с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_26", new rusuperobject(13,25,26,"девятнадцатый Маллофию с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_27", new rusuperobject(13,25,27,"двадцатый Елиафе с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_28", new rusuperobject(13,25,28,"двадцать первый Гофиру с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_29", new rusuperobject(13,25,29,"двадцать второй Гиддалтию с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_30", new rusuperobject(13,25,30,"двадцать третий Махазиофу с сыновьями его и братьями его; их - двенадцать; "));
rusuperobject.biblemap.put("b13_25_31", new rusuperobject(13,25,31,"двадцать четвертый Ромамти-Езеру с сыновьями его и братьями его; их - двенадцать. "));
rusuperobject.biblemap.put("b13_26_1", new rusuperobject(13,26,1,"Вот распределение привратников: из Кореян: Мешелемия, сын Корея, из сыновей Асафовых. "));
rusuperobject.biblemap.put("b13_26_2", new rusuperobject(13,26,2,"Сыновья Мешелемии: первенец Захария, второй Иедиаил, третий Зевадия, четвертый Иафниил, "));
rusuperobject.biblemap.put("b13_26_3", new rusuperobject(13,26,3,"пятый Елам, шестой Иегоханан, седьмой Елиегоэнай. "));
rusuperobject.biblemap.put("b13_26_4", new rusuperobject(13,26,4,"Сыновья Овед-Едома: первенец Шемаия, второй Иегозавад, третий Иоах, четвертый Сахар, пятый Нафанаил, "));
rusuperobject.biblemap.put("b13_26_5", new rusuperobject(13,26,5,"шестой Аммиил, седьмой Иссахар, восьмой Пеульфай, потому что Бог благословил его. "));
rusuperobject.biblemap.put("b13_26_6", new rusuperobject(13,26,6,"У сына его Шемаии родились также сыновья, начальствовавшие в своем роде, потому что они были люди сильные. "));
rusuperobject.biblemap.put("b13_26_7", new rusuperobject(13,26,7,"Сыновья Шемаии: Офни, Рефаил, Овед и Елзавад, братья его, люди сильные, Елия, Семахия и Иеваком. "));
rusuperobject.biblemap.put("b13_26_8", new rusuperobject(13,26,8,"Все они из сыновей Овед-Едома; они и сыновья их, и братья их были люди прилежные и к службе способные: их было у Овед-Едома шестьдесят два. "));
rusuperobject.biblemap.put("b13_26_9", new rusuperobject(13,26,9,"У Мешелемии сыновей и братьев, людей способных, было восемнадцать. "));
rusuperobject.biblemap.put("b13_26_10", new rusuperobject(13,26,10,"У Хосы, из сыновей Мерариных, сыновья: Шимри главный, - хотя он не был первенцем, но отец его поставил его главным; "));
rusuperobject.biblemap.put("b13_26_11", new rusuperobject(13,26,11,"второй Хелкия, третий Тевалия, четвертый Захария; всех сыновей и братьев у Хосы было тринадцать. "));
rusuperobject.biblemap.put("b13_26_12", new rusuperobject(13,26,12,"Вот распределение привратников по главам семейств, способных на службу вместе с братьями их, для служения в доме Господнем. "));
rusuperobject.biblemap.put("b13_26_13", new rusuperobject(13,26,13,"И бросили они жребии, как малый, так и большой, по своим семействам, на каждые ворота. "));
rusuperobject.biblemap.put("b13_26_14", new rusuperobject(13,26,14,"И выпал жребий на восток Шелемии; и Захарии, сыну его, умному советнику, бросили жребий, и вышел ему жребий на север; "));
rusuperobject.biblemap.put("b13_26_15", new rusuperobject(13,26,15,"Овед-Едому на юг, а сыновьям его при кладовых. "));
rusuperobject.biblemap.put("b13_26_16", new rusuperobject(13,26,16,"Шупиму и Хосе на запад, у ворот Шаллехет, где дорога поднимается и где стража против стражи. "));
rusuperobject.biblemap.put("b13_26_17", new rusuperobject(13,26,17,"К востоку по шести левитов, к северу по четыре, к югу по четыре, а у кладовых по два. "));
rusuperobject.biblemap.put("b13_26_18", new rusuperobject(13,26,18,"К западу у притвора на дороге по четыре, а у самого притвора по два. "));
rusuperobject.biblemap.put("b13_26_19", new rusuperobject(13,26,19,"Вот распределение привратников из сыновей Кореевых и сыновей Мерариных. "));
rusuperobject.biblemap.put("b13_26_20", new rusuperobject(13,26,20,"Левиты же, братья их, смотрели за сокровищами дома Божия и за сокровищницами посвященных вещей. "));
rusuperobject.biblemap.put("b13_26_21", new rusuperobject(13,26,21,"Сыновья Лаедана, сына Герсонова - от Лаедана, главы семейств от Лаедана Герсонского: Иехиел. "));
rusuperobject.biblemap.put("b13_26_22", new rusuperobject(13,26,22,"Сыновья Иехиела: Зефам и Иоиль, брат его, смотрели за сокровищами дома Господня, "));
rusuperobject.biblemap.put("b13_26_23", new rusuperobject(13,26,23,"вместе с потомками Амрама, Ицгара, Хеврона, Озиила. "));
rusuperobject.biblemap.put("b13_26_24", new rusuperobject(13,26,24,"Шевуил, сын Гирсона, сына Моисеева, был главным смотрителем за сокровищницами. "));
rusuperobject.biblemap.put("b13_26_25", new rusuperobject(13,26,25,"У брата его Елиезера сын Рехавия, у него сын Исаия, у него сын Иорам, у него сын Зихрий, у него сын Шеломиф. "));
rusuperobject.biblemap.put("b13_26_26", new rusuperobject(13,26,26,"Шеломиф и братья его смотрели за всеми сокровищницами посвященных вещей, которые посвятил царь Давид и главы семейств и тысяченачальники, стоначальники и предводители войска. "));
rusuperobject.biblemap.put("b13_26_27", new rusuperobject(13,26,27,"Из завоеваний и из добыч они посвящали на поддержание дома Господня. "));
rusuperobject.biblemap.put("b13_26_28", new rusuperobject(13,26,28,"И все, что посвятил Самуил пророк, и Саул, сын Киса, и Авенир, сын Нира, и Иоав, сын Саруи, все посвященное было на руках у Шеломифа и братьев его. "));
rusuperobject.biblemap.put("b13_26_29", new rusuperobject(13,26,29,"Из племени Ицгарова: Хенания и сыновья его определены на внешнее служение у Израильтян, писцами и судьями. "));
rusuperobject.biblemap.put("b13_26_30", new rusuperobject(13,26,30,"Из племени Хевронова: Хашавия и братья его, люди мужественные, тысяча семьсот, имели надзор над Израилем по эту сторону Иордана к западу, по всяким делам служения Господня и по службе царской. "));
rusuperobject.biblemap.put("b13_26_31", new rusuperobject(13,26,31,"У племени Хевронова Иерия был главою Хевронян, в их родах, в поколениях. В сороковой год царствования Давида они исчислены, и найдены между ними люди мужественные в Иазере Галаадском. "));
rusuperobject.biblemap.put("b13_26_32", new rusuperobject(13,26,32,"И братья его, люди способные, две тысячи семьсот, были главы семейств. Их поставил царь Давид над коленом Рувимовым и Гадовым и полуколеном Манассииным, по всем делам Божиим и делам царя. "));
rusuperobject.biblemap.put("b13_27_1", new rusuperobject(13,27,1,"Вот сыны Израилевы по числу их, главы семейств, тысяченачальники и стоначальники и управители, которые по отделениям служили царю во всех делах, приходя и отходя каждый месяц, во все месяцы года. В каждом отделении было их по двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_2", new rusuperobject(13,27,2,"Над первым отделением, для первого месяца, начальствовал Иашовам, сын Завдиила; в его отделении было двадцать четыре тысячи; "));
rusuperobject.biblemap.put("b13_27_3", new rusuperobject(13,27,3,"он был из сынов Фареса, главный над всеми военачальниками в первый месяц. "));
rusuperobject.biblemap.put("b13_27_4", new rusuperobject(13,27,4,"Над отделением второго месяца был Додай Ахохиянин; в отделении его был и князь Миклоф, и в его отделении было двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_5", new rusuperobject(13,27,5,"Третий главный военачальник, для третьего месяца, Ванея, сын Иодая, священника, и в его отделении было двадцать четыре тысячи: "));
rusuperobject.biblemap.put("b13_27_6", new rusuperobject(13,27,6,"этот Ванея - один из тридцати храбрых и начальник над ними, и в его отделе находился Аммизавад, сын его. "));
rusuperobject.biblemap.put("b13_27_7", new rusuperobject(13,27,7,"Четвертый, для четвертого месяца, был Асаил, брат Иоава, и по нем Завадия, сын его, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_8", new rusuperobject(13,27,8,"Пятый, для пятого месяца, князь Шамгуф Израхитянин, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_9", new rusuperobject(13,27,9,"Шестой, для шестого месяца, Ира, сын Иккеша, Фекоянин, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_10", new rusuperobject(13,27,10,"Седьмой, для седьмого месяца, Хелец Пелонитянин, из сынов Ефремовых, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_11", new rusuperobject(13,27,11,"Восьмой, для восьмого месяца, Совохай Хушатянин, из племени Зары, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_12", new rusuperobject(13,27,12,"Девятый, для девятого месяца, Авиезер Анафофянин, из сыновей Вениаминовых, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_13", new rusuperobject(13,27,13,"Десятый, для десятого месяца, Магарай Нетофафянин, из племени Зары, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_14", new rusuperobject(13,27,14,"Одиннадцатый, для одиннадцатого месяца, Ванея Пирафонянин, из сынов Ефремовых, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_15", new rusuperobject(13,27,15,"Двенадцатый, для двенадцатого месяца, Хелдай Нетофафянин, из потомков Гофониила, и в его отделении двадцать четыре тысячи. "));
rusuperobject.biblemap.put("b13_27_16", new rusuperobject(13,27,16,"А над коленами Израилевыми, - у Рувимлян главным начальником был Елиезер, сын Зихри; у Симеона - Сафатия, сын Маахи; "));
rusuperobject.biblemap.put("b13_27_17", new rusuperobject(13,27,17,"у Левия - Хашавия, сын Кемуила; у Аарона - Садок; "));
rusuperobject.biblemap.put("b13_27_18", new rusuperobject(13,27,18,"у Иуды - Елиав, из братьев Давида; у Иссахара - Омри, сын Михаила; "));
rusuperobject.biblemap.put("b13_27_19", new rusuperobject(13,27,19,"у Завулона - Ишмаия, сын Овадии; у Неффалима - Иеримоф, сын Азриила; "));
rusuperobject.biblemap.put("b13_27_20", new rusuperobject(13,27,20,"у сыновей Ефремовых - Осия, сын Азазии; у полуколена Манассиина - Иоиль, сын Федаии; "));
rusuperobject.biblemap.put("b13_27_21", new rusuperobject(13,27,21,"у полуколена Манассии в Галааде - Иддо, сын Захарии; у Вениамина - Иаасиил, сын Авенира; "));
rusuperobject.biblemap.put("b13_27_22", new rusuperobject(13,27,22,"у Дана - Азариил, сын Иерохама. Вот вожди колен Израилевых. "));
rusuperobject.biblemap.put("b13_27_23", new rusuperobject(13,27,23,"Давид не делал счисления тех, которые были от двадцати лет и ниже, потому что Господь сказал, что Он умножит Израиля, как звезды небесные. "));
rusuperobject.biblemap.put("b13_27_24", new rusuperobject(13,27,24,"Иоав, сын Саруи, начал делать счисление, но не кончил. И был за это гнев Божий на Израиля, и не вошло то счисление в летопись царя Давида. "));
rusuperobject.biblemap.put("b13_27_25", new rusuperobject(13,27,25,"Над сокровищами царскими был Азмавеф, сын Адиилов, а над запасами в поле, в городах, и в селах и в башнях - Ионафан, сын Уззии; "));
rusuperobject.biblemap.put("b13_27_26", new rusuperobject(13,27,26,"над занимающимися полевыми работами, земледелием - Езрий, сын Хелува; "));
rusuperobject.biblemap.put("b13_27_27", new rusuperobject(13,27,27,"над виноградниками - Шимей из Рамы, а над запасами вина в виноградниках - Завдий из Шефама; "));
rusuperobject.biblemap.put("b13_27_28", new rusuperobject(13,27,28,"над маслинами и смоковницами в долине - Баал-Ханан Гедеритянин, а над запасами деревянного масла - Иоас; "));
rusuperobject.biblemap.put("b13_27_29", new rusuperobject(13,27,29,"над крупным скотом, пасущимся в Шароне - Шитрай Шаронянин, а над скотом в долинах - Шафат, сын Адлая; "));
rusuperobject.biblemap.put("b13_27_30", new rusuperobject(13,27,30,"над верблюдами - Овил Исмаильтянин; над ослицами - Иехдия Меронифянин; "));
rusuperobject.biblemap.put("b13_27_31", new rusuperobject(13,27,31,"над мелким скотом - Иазиз Агаритянин. Все эти были начальниками над имением, которое было у царя Давида. "));
rusuperobject.biblemap.put("b13_27_32", new rusuperobject(13,27,32,"Ионафан, дядя Давидов, был советником, человек умный и писец; Иехиил, сын Хахмониев, был при сыновьях царя; "));
rusuperobject.biblemap.put("b13_27_33", new rusuperobject(13,27,33,"Ахитофел был советником царя; Хусий Архитянин - другом царя; "));
rusuperobject.biblemap.put("b13_27_34", new rusuperobject(13,27,34,"после же Ахитофела Иодай, сын Ванеи, и Авиафар, а Иоав был военачальником у царя. "));
rusuperobject.biblemap.put("b13_28_1", new rusuperobject(13,28,1,"И собрал Давид в Иерусалим всех вождей Израильских, начальников колен и начальников отделов, служивших царю, и тысяченачальников, и стоначальников, и заведывавших всем имением и стадами царя и сыновей его с евнухами, военачальников и всех храбрых мужей. "));
rusuperobject.biblemap.put("b13_28_2", new rusuperobject(13,28,2,"И стал Давид царь на ноги свои и сказал: послушайте меня, братья мои и народ мой! было у меня на сердце построить дом покоя для ковчега завета Господня и в подножие ногам Бога нашего, и потребное для строения я приготовил. "));
rusuperobject.biblemap.put("b13_28_3", new rusuperobject(13,28,3,"Но Бог сказал мне: не строй дома имени Моему, потому что ты человек воинственный и проливал кровь. "));
rusuperobject.biblemap.put("b13_28_4", new rusuperobject(13,28,4,"Однакоже избрал Господь Бог Израилев меня из всего дома отца моего, чтоб быть мне царем над Израилем вечно, потому что Иуду избрал Он князем, а в доме Иуды дом отца моего, а из сыновей отца моего меня благоволил поставить царем над всем Израилем, "));
rusuperobject.biblemap.put("b13_28_5", new rusuperobject(13,28,5,"из всех же сыновей моих, - ибо много сыновей дал мне Господь, - Он избрал Соломона, сына моего, сидеть на престоле царства Господня над Израилем, "));
rusuperobject.biblemap.put("b13_28_6", new rusuperobject(13,28,6,"и сказал мне: Соломон, сын твой, построит дом Мой и дворы Мои, потому что Я избрал его Себе в сына, и Я буду ему Отцом; "));
rusuperobject.biblemap.put("b13_28_7", new rusuperobject(13,28,7,"и утвержу царство его на веки, если он будет тверд в исполнении заповедей Моих и уставов Моих, как до сего дня. "));
rusuperobject.biblemap.put("b13_28_8", new rusuperobject(13,28,8,"И теперь пред очами всего Израиля, собрания Господня, и во уши Бога нашего говорю: соблюдайте и держитесь всех заповедей Господа Бога вашего, чтобы владеть вам сею доброю землею и оставить ее после себя в наследство детям своим на век; "));
rusuperobject.biblemap.put("b13_28_9", new rusuperobject(13,28,9,"и ты, Соломон, сын мой, знай Бога отца твоего и служи Ему от всего сердца и от всей души, ибо Господь испытует все сердца и знает все движения мыслей. Если будешь искать Его, то найдешь Его, а если оставишь Его, Он оставит тебя навсегда. "));
rusuperobject.biblemap.put("b13_28_10", new rusuperobject(13,28,10,"Смотри же, когда Господь избрал тебя построить дом для святилища, будь тверд и делай. "));
rusuperobject.biblemap.put("b13_28_11", new rusuperobject(13,28,11,"И отдал Давид Соломону, сыну своему, чертеж притвора и домов его, и кладовых его, и горниц его, и внутренних покоев его, и дома для ковчега, "));
rusuperobject.biblemap.put("b13_28_12", new rusuperobject(13,28,12,"и чертеж всего, что было у него на душе, дворов дома Господня и всех комнат кругом, сокровищниц дома Божия и сокровищниц вещей посвященных, "));
rusuperobject.biblemap.put("b13_28_13", new rusuperobject(13,28,13,"и священнических и левитских отделений, и всякого служебного дела в доме Господнем, и всех служебных сосудов дома Господня, "));
rusuperobject.biblemap.put("b13_28_14", new rusuperobject(13,28,14,"золотых вещей, с означением веса, для всякого из служебных сосудов, всех вещей серебряных, с означением веса, для всякого из сосудов служебных. "));
rusuperobject.biblemap.put("b13_28_15", new rusuperobject(13,28,15,"И дал золота для светильников и золотых лампад их, с означением веса каждого из светильников и лампад его, также светильников серебряных, с означением веса каждого из светильников и лампад его, смотря по служебному назначению каждого светильника; "));
rusuperobject.biblemap.put("b13_28_16", new rusuperobject(13,28,16,"и золота для столов предложения хлебов, для каждого золотого стола, и серебра для столов серебряных, "));
rusuperobject.biblemap.put("b13_28_17", new rusuperobject(13,28,17,"и вилок, и чаш и кропильниц из чистого золота, и золотых блюд, с означением веса каждого блюда, и серебряных блюд, с означением веса каждого блюда, "));
rusuperobject.biblemap.put("b13_28_18", new rusuperobject(13,28,18,"и для жертвенника курения из литого золота с означением веса, и устройства колесницы с золотыми херувимами, распростирающими крылья и покрывающими ковчег завета Господня. "));
rusuperobject.biblemap.put("b13_28_19", new rusuperobject(13,28,19,"Все сие в письмени от Господа, говорил Давид, как Он вразумил меня на все дела постройки. "));
rusuperobject.biblemap.put("b13_28_20", new rusuperobject(13,28,20,"И сказал Давид сыну своему Соломону: будь тверд и мужествен, и приступай к делу, не бойся и не ужасайся, ибо Господь Бог, Бог мой, с тобою; Он не отступит от тебя и не оставит тебя, доколе не совершишь всего дела, требуемого для дома Господня. "));
rusuperobject.biblemap.put("b13_28_21", new rusuperobject(13,28,21,"И вот отделы священников и левитов, для всякой службы при Доме Божием. И у тебя есть для всякого дела усердные люди, искусные для всякой работы, и начальники и весь народ готовы на все твои приказания. "));
rusuperobject.biblemap.put("b13_29_1", new rusuperobject(13,29,1,"И сказал царь Давид всему собранию: Соломон, сын мой, которого одного избрал Бог, молод и малосилен, а дело сие велико, потому что не для человека здание сие, а для Господа Бога. "));
rusuperobject.biblemap.put("b13_29_2", new rusuperobject(13,29,2,"Всеми силами я заготовил для дома Бога моего золото для золотых вещей и серебро для серебряных, и медь для медных, железо для железных, и дерева для деревянных, камни оникса и камни вставные, камни красивые и разноцветные, и всякие дорогие камни, и множество мрамора; "));
rusuperobject.biblemap.put("b13_29_3", new rusuperobject(13,29,3,"и еще по любви моей к дому Бога моего, есть у меня сокровище собственное из золота и серебра, и его я отдаю для дома Бога моего, сверх всего, что заготовил я для святого дома: "));
rusuperobject.biblemap.put("b13_29_4", new rusuperobject(13,29,4,"три тысячи талантов золота, золота Офирского, и семь тысяч талантов серебра чистого, для обложения стен в домах, "));
rusuperobject.biblemap.put("b13_29_5", new rusuperobject(13,29,5,"для каждой из золотых вещей, и для каждой из серебряных, и для всякого изделия рук художнических. Не поусердствует ли еще кто жертвовать сегодня для Господа? "));
rusuperobject.biblemap.put("b13_29_6", new rusuperobject(13,29,6,"И стали жертвовать начальники семейств и начальники колен Израилевых, и начальники тысяч и сотен, и начальники над имениями царя. "));
rusuperobject.biblemap.put("b13_29_7", new rusuperobject(13,29,7,"И дали на устроение дома Божия пять тысяч талантов и десять тысяч драхм золота, и серебра десять тысяч талантов, и меди восемнадцать тысяч талантов, и железа сто тысяч талантов. "));
rusuperobject.biblemap.put("b13_29_8", new rusuperobject(13,29,8,"И у кого нашлись дорогие камни, те отдавали и их в сокровищницу дома Господня, на руки Иехиилу Герсонитянину. "));
rusuperobject.biblemap.put("b13_29_9", new rusuperobject(13,29,9,"И радовался народ усердию их, потому что они от всего сердца жертвовали Господу, также и царь Давид весьма радовался. "));
rusuperobject.biblemap.put("b13_29_10", new rusuperobject(13,29,10,"И благословил Давид Господа пред всем собранием, и сказал Давид: благословен Ты, Господи Боже Израиля, отца нашего, от века и до века! "));
rusuperobject.biblemap.put("b13_29_11", new rusuperobject(13,29,11,"Твое, Господи, величие, и могущество, и слава, и победа и великолепие, и все, что на небе и на земле, Твое: Твое, Господи, царство, и Ты превыше всего, как Владычествующий. "));
rusuperobject.biblemap.put("b13_29_12", new rusuperobject(13,29,12,"И богатство и слава от лица Твоего, и Ты владычествуешь над всем, и в руке Твоей сила и могущество, и во власти Твоей возвеличить и укрепить все. "));
rusuperobject.biblemap.put("b13_29_13", new rusuperobject(13,29,13,"И ныне, Боже наш, мы славословим Тебя и хвалим величественное имя Твое. "));
rusuperobject.biblemap.put("b13_29_14", new rusuperobject(13,29,14,"Ибо кто я и кто народ мой, что мы имели возможность так жертвовать? Но от Тебя все, и от руки Твоей полученное мы отдали Тебе, "));
rusuperobject.biblemap.put("b13_29_15", new rusuperobject(13,29,15,"потому что странники мы пред Тобою и пришельцы, как и все отцы наши, как тень дни наши на земле, и нет ничего прочного. "));
rusuperobject.biblemap.put("b13_29_16", new rusuperobject(13,29,16,"Господи Боже наш! все это множество, которое приготовили мы для построения дома Тебе, святому имени Твоему, от руки Твоей оно, и все Твое. "));
rusuperobject.biblemap.put("b13_29_17", new rusuperobject(13,29,17,"Знаю, Боже мой, что Ты испытуешь сердце и любишь чистосердечие; я от чистого сердца моего пожертвовал все сие, и ныне вижу, что и народ Твой, здесь находящийся, с радостью жертвует Тебе. "));
rusuperobject.biblemap.put("b13_29_18", new rusuperobject(13,29,18,"Господи, Боже Авраама, Исаака и Израиля, отцов наших! сохрани сие навек, сие расположение мыслей сердца народа Твоего, и направь сердце их к Тебе. "));
rusuperobject.biblemap.put("b13_29_19", new rusuperobject(13,29,19,"Соломону же, сыну моему, дай сердце правое, чтобы соблюдать заповеди Твои, откровения Твои и уставы Твои, и исполнить все это и построить здание, для которого я сделал приготовление. "));
rusuperobject.biblemap.put("b13_29_20", new rusuperobject(13,29,20,"И сказал Давид всему собранию: благословите Господа Бога нашего. - И благословило все собрание Господа Бога отцов своих, и пало, и поклонилось Господу и царю. "));
rusuperobject.biblemap.put("b13_29_21", new rusuperobject(13,29,21,"И принесли Господу жертвы, и вознесли всесожжения Господу, на другой после сего день: тысячу тельцов, тысячу овнов, тысячу агнцев с их возлияниями, и множество жертв от всего Израиля. "));
rusuperobject.biblemap.put("b13_29_22", new rusuperobject(13,29,22,"И ели и пили пред Господом в тот день, с великою радостью; и в другой раз воцарили Соломона, сына Давидова, и помазали пред Господом в правителя верховного, а Садока во священника. "));
rusuperobject.biblemap.put("b13_29_23", new rusuperobject(13,29,23,"И сел Соломон на престоле Господнем, как царь, вместо Давида, отца своего, и был благоуспешен, и весь Израиль повиновался ему. "));
rusuperobject.biblemap.put("b13_29_24", new rusuperobject(13,29,24,"И все начальники и сильные, также и все сыновья царя Давида подчинились Соломону царю. "));
