rusuperobject.biblemap.put("b15_2_32", new rusuperobject(15,2,32,"сыновей Харима триста двадцать; "));
rusuperobject.biblemap.put("b15_2_33", new rusuperobject(15,2,33,"уроженцев Лидды, Хадида и Оно семьсот двадцать пять; "));
rusuperobject.biblemap.put("b15_2_34", new rusuperobject(15,2,34,"уроженцев Иерихона триста сорок пять; "));
