rusuperobject.biblemap.put("b15_2_36", new rusuperobject(15,2,36,"Священников: сыновей Иедаии, из дома Иисусова, девятьсот семьдесят три; "));
rusuperobject.biblemap.put("b15_2_37", new rusuperobject(15,2,37,"сыновей Иммера тысяча пятьдесят два; "));
