rusuperobject.biblemap.put("b15_2_40", new rusuperobject(15,2,40,"Левитов: сыновей Иисуса и Кадмиила, из сыновей Годавии, семьдесят четыре; "));
rusuperobject.biblemap.put("b15_2_41", new rusuperobject(15,2,41,"певцов: сыновей Асафа сто двадцать восемь; "));
rusuperobject.biblemap.put("b15_2_42", new rusuperobject(15,2,42,"сыновей привратников: сыновья Шаллума, сыновья Атера, сыновья Талмона, сыновья Аккува, сыновья Хатиты, сыновья Шовая, - всего сто тридцать девять. "));
