rusuperobject.biblemap.put("b15_2_44", new rusuperobject(15,2,44,"сыновья Кероса, сыновья Сиаги, сыновья Фадона, "));
rusuperobject.biblemap.put("b15_2_45", new rusuperobject(15,2,45,"сыновья Лебаны, сыновья Хагабы, сыновья Аккува, "));
rusuperobject.biblemap.put("b15_2_46", new rusuperobject(15,2,46,"сыновья Хагава, сыновья Шамлая, сыновья Ханана, "));
