package ru.bible;

public class rubible25 {

public rubible25() {

putmapbible25();

}

private void putmapbible25() {

rusuperobject.biblemap.put("b25_1_1", new rusuperobject(25,1,1,"Как одиноко сидит город, некогда многолюдный! он стал, как вдова; великий между народами, князь над областями сделался данником. "));
rusuperobject.biblemap.put("b25_1_2", new rusuperobject(25,1,2,"Горько плачет он ночью, и слезы его на ланитах его. Нет у него утешителя из всех, любивших его; все друзья его изменили ему, сделались врагами ему. "));
rusuperobject.biblemap.put("b25_1_3", new rusuperobject(25,1,3,"Иуда переселился по причине бедствия и тяжкого рабства, поселился среди язычников, и не нашел покоя; все, преследовавшие его, настигли его в тесных местах. "));
rusuperobject.biblemap.put("b25_1_4", new rusuperobject(25,1,4,"Пути Сиона сетуют, потому что нет идущих на праздник; все ворота его опустели; священники его вздыхают, девицы его печальны, горько и ему самому. "));
rusuperobject.biblemap.put("b25_1_5", new rusuperobject(25,1,5,"Враги его стали во главе, неприятели его благоденствуют, потому что Господь наслал на него горе за множество беззаконий его; дети его пошли в плен впереди врага. "));
rusuperobject.biblemap.put("b25_1_6", new rusuperobject(25,1,6,"И отошло от дщери Сиона все ее великолепие; князья ее - как олени, не находящие пажити; обессиленные они пошли вперед погонщика. "));
rusuperobject.biblemap.put("b25_1_7", new rusuperobject(25,1,7,"Вспомнил Иерусалим, во дни бедствия своего и страданий своих, о всех драгоценностях своих, какие были у него в прежние дни, тогда как народ его пал от руки врага, и никто не помогает ему; неприятели смотрят на него и смеются над его субботами. "));
rusuperobject.biblemap.put("b25_1_8", new rusuperobject(25,1,8,"Тяжко согрешил Иерусалим, за то и сделался отвратительным; все, прославлявшие его, смотрят на него с презрением, потому что увидели наготу его; и сам он вздыхает и отворачивается назад. "));
rusuperobject.biblemap.put("b25_1_9", new rusuperobject(25,1,9,"На подоле у него была нечистота, но он не помышлял о будущности своей, и поэтому необыкновенно унизился, и нет у него утешителя. «Воззри, Господи, на бедствие мое, ибо враг возвеличился!» "));
rusuperobject.biblemap.put("b25_1_10", new rusuperobject(25,1,10,"Враг простер руку свою на все самое драгоценное его; он видит, как язычники входят во святилище его, о котором Ты заповедал, чтобы они не вступали в собрание Твое. "));
rusuperobject.biblemap.put("b25_1_11", new rusuperobject(25,1,11,"Весь народ его вздыхает, ища хлеба, отдает драгоценности свои за пищу, чтобы подкрепить душу. «Воззри, Господи, и посмотри, как я унижен!» "));
rusuperobject.biblemap.put("b25_1_12", new rusuperobject(25,1,12,"Да не будет этого с вами, все проходящие путем! взгляните и посмотрите, есть ли болезнь, как моя болезнь, какая постигла меня, какую наслал на меня Господь в день пламенного гнева Своего? "));
rusuperobject.biblemap.put("b25_1_13", new rusuperobject(25,1,13,"Свыше послал Он огонь в кости мои, и он овладел ими; раскинул сеть для ног моих, опрокинул меня, сделал меня бедным и томящимся всякий день. "));
rusuperobject.biblemap.put("b25_1_14", new rusuperobject(25,1,14,"Ярмо беззаконий моих связано в руке Его; они сплетены и поднялись на шею мою; Он ослабил силы мои. Господь отдал меня в руки, из которых не могу подняться. "));
rusuperobject.biblemap.put("b25_1_15", new rusuperobject(25,1,15,"Всех сильных моих Господь низложил среди меня, созвал против меня собрание, чтобы истребить юношей моих; как в точиле, истоптал Господь деву, дочь Иуды. "));
rusuperobject.biblemap.put("b25_1_16", new rusuperobject(25,1,16,"Об этом плачу я; око мое, око мое изливает воды, ибо далеко от меня утешитель, который оживил бы душу мою; дети мои разорены, потому что враг превозмог. "));
rusuperobject.biblemap.put("b25_1_17", new rusuperobject(25,1,17,"Сион простирает руки свои, но утешителя нет ему. Господь дал повеление о Иакове врагам его окружить его; Иерусалим сделался мерзостью среди них. "));
rusuperobject.biblemap.put("b25_1_18", new rusuperobject(25,1,18,"Праведен Господь, ибо я непокорен был слову Его. Послушайте, все народы, и взгляните на болезнь мою: девы мои и юноши мои пошли в плен. "));
rusuperobject.biblemap.put("b25_1_19", new rusuperobject(25,1,19,"Зову друзей моих, но они обманули меня; священники мои и старцы мои издыхают в городе, ища пищи себе, чтобы подкрепить душу свою. "));
rusuperobject.biblemap.put("b25_1_20", new rusuperobject(25,1,20,"Воззри, Господи, ибо мне тесно, волнуется во мне внутренность, сердце мое перевернулось во мне за то, что я упорно противился Тебе; отвне обесчадил меня меч, а дома - как смерть. "));
rusuperobject.biblemap.put("b25_1_21", new rusuperobject(25,1,21,"Услышали, что я стенаю, а утешителя у меня нет; услышали все враги мои о бедствии моем и обрадовались, что Ты соделал это: о, если бы Ты повелел наступить дню, предреченному Тобою, и они стали бы подобными мне! "));
rusuperobject.biblemap.put("b25_1_22", new rusuperobject(25,1,22,"Да предстанет пред лице Твое вся злоба их; и поступи с ними так же, как Ты поступил со мною за все грехи мои, ибо тяжки стоны мои, и сердце мое изнемогает. "));
rusuperobject.biblemap.put("b25_2_1", new rusuperobject(25,2,1,"Как помрачил Господь во гневе Своем дщерь Сиона! с небес поверг на землю красу Израиля и не вспомнил о подножии ног Своих в день гнева Своего. "));
rusuperobject.biblemap.put("b25_2_2", new rusuperobject(25,2,2,"Погубил Господь все жилища Иакова, не пощадил, разрушил в ярости Своей укрепления дщери Иудиной, поверг на землю, отверг царство и князей его, как нечистых: "));
rusuperobject.biblemap.put("b25_2_3", new rusuperobject(25,2,3,"в пылу гнева сломил все роги Израилевы, отвел десницу Свою от неприятеля и воспылал в Иакове, как палящий огонь, пожиравший все вокруг; "));
rusuperobject.biblemap.put("b25_2_4", new rusuperobject(25,2,4,"натянул лук Свой, как неприятель, направил десницу Свою, как враг, и убил все, вожделенное для глаз; на скинию дщери Сиона излил ярость Свою, как огонь. "));
rusuperobject.biblemap.put("b25_2_5", new rusuperobject(25,2,5,"Господь стал как неприятель, истребил Израиля, разорил все чертоги его, разрушил укрепления его и распространил у дщери Иудиной сетование и плач. "));
rusuperobject.biblemap.put("b25_2_6", new rusuperobject(25,2,6,"И отнял ограду Свою, как у сада; разорил Свое место собраний, заставил Господь забыть на Сионе празднества и субботы; и в негодовании гнева Своего отверг царя и священника. "));
rusuperobject.biblemap.put("b25_2_7", new rusuperobject(25,2,7,"Отверг Господь жертвенник Свой, отвратил сердце Свое от святилища Своего, предал в руки врагов стены чертогов его; в доме Господнем они шумели, как в праздничный день. "));
rusuperobject.biblemap.put("b25_2_8", new rusuperobject(25,2,8,"Господь определил разрушить стену дщери Сиона, протянул вервь, не отклонил руки Своей от разорения; истребил внешние укрепления, и стены вместе разрушены. "));
rusuperobject.biblemap.put("b25_2_9", new rusuperobject(25,2,9,"Ворота ее вдались в землю; Он разрушил и сокрушил запоры их; царь ее и князья ее - среди язычников; не стало закона, и пророки ее не сподобляются видений от Господа. "));
rusuperobject.biblemap.put("b25_2_10", new rusuperobject(25,2,10,"Сидят на земле безмолвно старцы дщери Сионовой, посыпали пеплом свои головы, препоясались вретищем; опустили к земле головы свои девы Иерусалимские. "));
rusuperobject.biblemap.put("b25_2_11", new rusuperobject(25,2,11,"Истощились от слез глаза мои, волнуется во мне внутренность моя, изливается на землю печень моя от гибели дщери народа моего, когда дети и грудные младенцы умирают от голода среди городских улиц. "));
rusuperobject.biblemap.put("b25_2_12", new rusuperobject(25,2,12,"Матерям своим говорят они: «где хлеб и вино?», умирая, подобно раненым, на улицах городских, изливая души свои в лоно матерей своих. "));
rusuperobject.biblemap.put("b25_2_13", new rusuperobject(25,2,13,"Что мне сказать тебе, с чем сравнить тебя, дщерь Иерусалима? чему уподобить тебя, чтобы утешить тебя, дева, дщерь Сиона? ибо рана твоя велика, как море; кто может исцелить тебя? "));
rusuperobject.biblemap.put("b25_2_14", new rusuperobject(25,2,14,"Пророки твои провещали тебе пустое и ложное и не раскрывали твоего беззакония, чтобы отвратить твое пленение, и изрекали тебе откровения ложные и приведшие тебя к изгнанию. "));
rusuperobject.biblemap.put("b25_2_15", new rusuperobject(25,2,15,"Руками всплескивают о тебе все проходящие путем, свищут и качают головою своею о дщери Иерусалима, говоря: «это ли город, который называли совершенством красоты, радостью всей земли?» "));
rusuperobject.biblemap.put("b25_2_16", new rusuperobject(25,2,16,"Разинули на тебя пасть свою все враги твои, свищут и скрежещут зубами, говорят: «поглотили мы его, только этого дня и ждали мы, дождались, увидели!» "));
rusuperobject.biblemap.put("b25_2_17", new rusuperobject(25,2,17,"Совершил Господь, что определил, исполнил слово Свое, изреченное в древние дни, разорил без пощады и дал врагу порадоваться над тобою, вознес рог неприятелей твоих. "));
rusuperobject.biblemap.put("b25_2_18", new rusuperobject(25,2,18,"Сердце их вопиет к Господу: стена дщери Сиона! лей ручьем слезы день и ночь, не давай себе покоя, не спускай зениц очей твоих. "));
rusuperobject.biblemap.put("b25_2_19", new rusuperobject(25,2,19,"Вставай, взывай ночью, при начале каждой стражи; изливай, как воду, сердце твое пред лицем Господа; простирай к Нему руки твои о душе детей твоих, издыхающих от голода на углах всех улиц. "));
rusuperobject.biblemap.put("b25_2_20", new rusuperobject(25,2,20,"«Воззри, Господи, и посмотри: кому Ты сделал так, чтобы женщины ели плод свой, младенцев, вскормленных ими? чтобы убиваемы были в святилище Господнем священник и пророк? "));
rusuperobject.biblemap.put("b25_2_21", new rusuperobject(25,2,21,"Дети и старцы лежат на земле по улицам; девы мои и юноши мои пали от меча; Ты убивал их в день гнева Твоего, заколал без пощады. "));
rusuperobject.biblemap.put("b25_2_22", new rusuperobject(25,2,22,"Ты созвал отовсюду, как на праздник, ужасы мои, и в день гнева Господня никто не спасся, никто не уцелел; тех, которые были мною вскормлены и вырощены, враг мой истребил». "));
rusuperobject.biblemap.put("b25_3_1", new rusuperobject(25,3,1,"Я человек, испытавший горе от жезла гнева Его. "));
rusuperobject.biblemap.put("b25_3_2", new rusuperobject(25,3,2,"Он повел меня и ввел во тьму, а не во свет. "));
rusuperobject.biblemap.put("b25_3_3", new rusuperobject(25,3,3,"Так, Он обратился на меня и весь день обращает руку Свою; "));
rusuperobject.biblemap.put("b25_3_4", new rusuperobject(25,3,4,"измождил плоть мою и кожу мою, сокрушил кости мои; "));
rusuperobject.biblemap.put("b25_3_5", new rusuperobject(25,3,5,"огородил меня и обложил горечью и тяготою; "));
rusuperobject.biblemap.put("b25_3_6", new rusuperobject(25,3,6,"посадил меня в темное место, как давно умерших; "));
rusuperobject.biblemap.put("b25_3_7", new rusuperobject(25,3,7,"окружил меня стеною, чтобы я не вышел, отяготил оковы мои, "));
rusuperobject.biblemap.put("b25_3_8", new rusuperobject(25,3,8,"и когда я взывал и вопиял, задерживал молитву мою; "));
rusuperobject.biblemap.put("b25_3_9", new rusuperobject(25,3,9,"каменьями преградил дороги мои, извратил стези мои. "));
rusuperobject.biblemap.put("b25_3_10", new rusuperobject(25,3,10,"Он стал для меня как бы медведь в засаде, как бы лев в скрытном месте; "));
rusuperobject.biblemap.put("b25_3_11", new rusuperobject(25,3,11,"извратил пути мои и растерзал меня, привел меня в ничто; "));
rusuperobject.biblemap.put("b25_3_12", new rusuperobject(25,3,12,"натянул лук Свой и поставил меня как бы целью для стрел; "));
rusuperobject.biblemap.put("b25_3_13", new rusuperobject(25,3,13,"послал в почки мои стрелы из колчана Своего. "));
rusuperobject.biblemap.put("b25_3_14", new rusuperobject(25,3,14,"Я стал посмешищем для всего народа моего, вседневною песнью их. "));
rusuperobject.biblemap.put("b25_3_15", new rusuperobject(25,3,15,"Он пресытил меня горечью, напоил меня полынью. "));
rusuperobject.biblemap.put("b25_3_16", new rusuperobject(25,3,16,"Сокрушил камнями зубы мои, покрыл меня пеплом. "));
rusuperobject.biblemap.put("b25_3_17", new rusuperobject(25,3,17,"И удалился мир от души моей; я забыл о благоденствии, "));
rusuperobject.biblemap.put("b25_3_18", new rusuperobject(25,3,18,"и сказал я: погибла сила моя и надежда моя на Господа. "));
rusuperobject.biblemap.put("b25_3_19", new rusuperobject(25,3,19,"Помысли о моем страдании и бедствии моем, о полыни и желчи. "));
rusuperobject.biblemap.put("b25_3_20", new rusuperobject(25,3,20,"Твердо помнит это душа моя и падает во мне. "));
rusuperobject.biblemap.put("b25_3_21", new rusuperobject(25,3,21,"Вот что я отвечаю сердцу моему и потому уповаю: "));
rusuperobject.biblemap.put("b25_3_22", new rusuperobject(25,3,22,"по милости Господа мы не исчезли, ибо милосердие Его не истощилось. "));
rusuperobject.biblemap.put("b25_3_23", new rusuperobject(25,3,23,"Оно обновляется каждое утро; велика верность Твоя! "));
rusuperobject.biblemap.put("b25_3_24", new rusuperobject(25,3,24,"Господь часть моя, говорит душа моя, итак буду надеяться на Него. "));
rusuperobject.biblemap.put("b25_3_25", new rusuperobject(25,3,25,"Благ Господь к надеющимся на Него, к душе, ищущей Его. "));
rusuperobject.biblemap.put("b25_3_26", new rusuperobject(25,3,26,"Благо тому, кто терпеливо ожидает спасения от Господа. "));
rusuperobject.biblemap.put("b25_3_27", new rusuperobject(25,3,27,"Благо человеку, когда он несет иго в юности своей; "));
rusuperobject.biblemap.put("b25_3_28", new rusuperobject(25,3,28,"сидит уединенно и молчит, ибо Он наложил его на него; "));
rusuperobject.biblemap.put("b25_3_29", new rusuperobject(25,3,29,"полагает уста свои в прах, помышляя: «может быть, еще есть надежда»; "));
rusuperobject.biblemap.put("b25_3_30", new rusuperobject(25,3,30,"подставляет ланиту свою биющему его, пресыщается поношением, "));
rusuperobject.biblemap.put("b25_3_31", new rusuperobject(25,3,31,"ибо не навек оставляет Господь. "));
rusuperobject.biblemap.put("b25_3_32", new rusuperobject(25,3,32,"Но послал горе, и помилует по великой благости Своей. "));
rusuperobject.biblemap.put("b25_3_33", new rusuperobject(25,3,33,"Ибо Он не по изволению сердца Своего наказывает и огорчает сынов человеческих. "));
rusuperobject.biblemap.put("b25_3_34", new rusuperobject(25,3,34,"Но, когда попирают ногами своими всех узников земли, "));
rusuperobject.biblemap.put("b25_3_35", new rusuperobject(25,3,35,"когда неправедно судят человека пред лицем Всевышнего, "));
rusuperobject.biblemap.put("b25_3_36", new rusuperobject(25,3,36,"когда притесняют человека в деле его: разве не видит Господь? "));
rusuperobject.biblemap.put("b25_3_37", new rusuperobject(25,3,37,"Кто это говорит: «и то бывает, чему Господь не повелел быть»? "));
rusuperobject.biblemap.put("b25_3_38", new rusuperobject(25,3,38,"Не от уст ли Всевышнего происходит бедствие и благополучие? "));
rusuperobject.biblemap.put("b25_3_39", new rusuperobject(25,3,39,"Зачем сетует человек живущий? всякий сетуй на грехи свои. "));
rusuperobject.biblemap.put("b25_3_40", new rusuperobject(25,3,40,"Испытаем и исследуем пути свои, и обратимся к Господу. "));
rusuperobject.biblemap.put("b25_3_41", new rusuperobject(25,3,41,"Вознесем сердце наше и руки к Богу, сущему на небесах: "));
rusuperobject.biblemap.put("b25_3_42", new rusuperobject(25,3,42,"мы отпали и упорствовали; Ты не пощадил. "));
rusuperobject.biblemap.put("b25_3_43", new rusuperobject(25,3,43,"Ты покрыл Себя гневом и преследовал нас, умерщвлял, не щадил; "));
rusuperobject.biblemap.put("b25_3_44", new rusuperobject(25,3,44,"Ты закрыл Себя облаком, чтобы не доходила молитва наша; "));
rusuperobject.biblemap.put("b25_3_45", new rusuperobject(25,3,45,"сором и мерзостью Ты сделал нас среди народов. "));
rusuperobject.biblemap.put("b25_3_46", new rusuperobject(25,3,46,"Разинули на нас пасть свою все враги наши. "));
rusuperobject.biblemap.put("b25_3_47", new rusuperobject(25,3,47,"Ужас и яма, опустошение и разорение - доля наша. "));
rusuperobject.biblemap.put("b25_3_48", new rusuperobject(25,3,48,"Потоки вод изливает око мое о гибели дщери народа моего. "));
rusuperobject.biblemap.put("b25_3_49", new rusuperobject(25,3,49,"Око мое изливается и не перестает, ибо нет облегчения, "));
rusuperobject.biblemap.put("b25_3_50", new rusuperobject(25,3,50,"доколе не призрит и не увидит Господь с небес. "));
rusuperobject.biblemap.put("b25_3_51", new rusuperobject(25,3,51,"Око мое опечаливает душу мою ради всех дщерей моего города. "));
rusuperobject.biblemap.put("b25_3_52", new rusuperobject(25,3,52,"Всячески усиливались уловить меня, как птичку, враги мои, без всякой причины; "));
rusuperobject.biblemap.put("b25_3_53", new rusuperobject(25,3,53,"повергли жизнь мою в яму и закидали меня камнями. "));
rusuperobject.biblemap.put("b25_3_54", new rusuperobject(25,3,54,"Воды поднялись до головы моей; я сказал: «погиб я». "));
rusuperobject.biblemap.put("b25_3_55", new rusuperobject(25,3,55,"Я призывал имя Твое, Господи, из ямы глубокой. "));
rusuperobject.biblemap.put("b25_3_56", new rusuperobject(25,3,56,"Ты слышал голос мой; не закрой уха Твоего от воздыхания моего, от вопля моего. "));
rusuperobject.biblemap.put("b25_3_57", new rusuperobject(25,3,57,"Ты приближался, когда я взывал к Тебе, и говорил: «не бойся». "));
rusuperobject.biblemap.put("b25_3_58", new rusuperobject(25,3,58,"Ты защищал, Господи, дело души моей; искуплял жизнь мою. "));
rusuperobject.biblemap.put("b25_3_59", new rusuperobject(25,3,59,"Ты видишь, Господи, обиду мою; рассуди дело мое. "));
rusuperobject.biblemap.put("b25_3_60", new rusuperobject(25,3,60,"Ты видишь всю мстительность их, все замыслы их против меня. "));
rusuperobject.biblemap.put("b25_3_61", new rusuperobject(25,3,61,"Ты слышишь, Господи, ругательство их, все замыслы их против меня, "));
rusuperobject.biblemap.put("b25_3_62", new rusuperobject(25,3,62,"речи восстающих на меня и их ухищрения против меня всякий день. "));
rusuperobject.biblemap.put("b25_3_63", new rusuperobject(25,3,63,"Воззри, сидят ли они, встают ли, я для них - песнь. "));
rusuperobject.biblemap.put("b25_3_64", new rusuperobject(25,3,64,"Воздай им, Господи, по делам рук их; "));
rusuperobject.biblemap.put("b25_3_65", new rusuperobject(25,3,65,"пошли им помрачение сердца и проклятие Твое на них; "));
rusuperobject.biblemap.put("b25_3_66", new rusuperobject(25,3,66,"преследуй их, Господи, гневом, и истреби их из поднебесной. "));
rusuperobject.biblemap.put("b25_4_1", new rusuperobject(25,4,1,"Как потускло золото, изменилось золото наилучшее! камни святилища раскиданы по всем перекресткам. "));
rusuperobject.biblemap.put("b25_4_2", new rusuperobject(25,4,2,"Сыны Сиона драгоценные, равноценные чистейшему золоту, как они сравнены с глиняною посудою, изделием рук горшечника! "));
rusuperobject.biblemap.put("b25_4_3", new rusuperobject(25,4,3,"И чудовища подают сосцы и кормят своих детенышей, а дщерь народа моего стала жестока подобно страусам в пустыне. "));
rusuperobject.biblemap.put("b25_4_4", new rusuperobject(25,4,4,"Язык грудного младенца прилипает к гортани его от жажды; дети просят хлеба, и никто не подает им. "));
rusuperobject.biblemap.put("b25_4_5", new rusuperobject(25,4,5,"Евшие сладкое истаевают на улицах; воспитанные на багрянице жмутся к навозу. "));
rusuperobject.biblemap.put("b25_4_6", new rusuperobject(25,4,6,"Наказание нечестия дщери народа моего превышает казнь за грехи Содома: тот низринут мгновенно, и руки человеческие не касались его. "));
rusuperobject.biblemap.put("b25_4_7", new rusuperobject(25,4,7,"Князья ее были в ней чище снега, белее молока; они были телом краше коралла, вид их был, как сапфир; "));
rusuperobject.biblemap.put("b25_4_8", new rusuperobject(25,4,8,"а теперь темнее всего черного лице их; не узнают их на улицах; кожа их прилипла к костям их, стала суха, как дерево. "));
rusuperobject.biblemap.put("b25_4_9", new rusuperobject(25,4,9,"Умерщвляемые мечом счастливее умерщвляемых голодом, потому что сии истаевают, поражаемые недостатком плодов полевых. "));
rusuperobject.biblemap.put("b25_4_10", new rusuperobject(25,4,10,"Руки мягкосердых женщин варили детей своих, чтобы они были для них пищею во время гибели дщери народа моего. "));
rusuperobject.biblemap.put("b25_4_11", new rusuperobject(25,4,11,"Совершил Господь гнев Свой, излил ярость гнева Своего и зажег на Сионе огонь, который пожрал основания его. "));
rusuperobject.biblemap.put("b25_4_12", new rusuperobject(25,4,12,"Не верили цари земли и все живущие во вселенной, чтобы враг и неприятель вошел во врата Иерусалима. "));
rusuperobject.biblemap.put("b25_4_13", new rusuperobject(25,4,13,"Все это - за грехи лжепророков его, за беззакония священников его, которые среди него проливали кровь праведников; "));
rusuperobject.biblemap.put("b25_4_14", new rusuperobject(25,4,14,"бродили как слепые по улицам, осквернялись кровью, так что невозможно было прикоснуться к одеждам их. "));
rusuperobject.biblemap.put("b25_4_15", new rusuperobject(25,4,15,"«Сторонитесь! нечистый!» кричали им; «сторонитесь, сторонитесь, не прикасайтесь»; и они уходили в смущении; а между народом говорили: «их более не будет! "));
rusuperobject.biblemap.put("b25_4_16", new rusuperobject(25,4,16,"лице Господне рассеет их; Он уже не призрит на них», потому что они лица священников не уважают, старцев не милуют. "));
rusuperobject.biblemap.put("b25_4_17", new rusuperobject(25,4,17,"Наши глаза истомлены в напрасном ожидании помощи; со сторожевой башни нашей мы ожидали народ, который не мог спасти нас. "));
rusuperobject.biblemap.put("b25_4_18", new rusuperobject(25,4,18,"А они подстерегали шаги наши, чтобы мы не могли ходить по улицам нашим; приблизился конец наш, дни наши исполнились; пришел конец наш. "));
rusuperobject.biblemap.put("b25_4_19", new rusuperobject(25,4,19,"Преследовавшие нас были быстрее орлов небесных; гонялись за нами по горам, ставили засаду для нас в пустыне. "));
rusuperobject.biblemap.put("b25_4_20", new rusuperobject(25,4,20,"Дыхание жизни нашей, помазанник Господень пойман в ямы их, тот, о котором мы говорили: «под тенью его будем жить среди народов». "));
rusuperobject.biblemap.put("b25_4_21", new rusuperobject(25,4,21,"Радуйся и веселись, дочь Едома, обитательница земли Уц! И до тебя дойдет чаша; напьешься допьяна и обнажишься. "));
rusuperobject.biblemap.put("b25_4_22", new rusuperobject(25,4,22,"Дщерь Сиона! наказание за беззаконие твое кончилось; Он не будет более изгонять тебя; но твое беззаконие, дочь Едома, Он посетит и обнаружит грехи твои. "));
rusuperobject.biblemap.put("b25_5_1", new rusuperobject(25,5,1,"Вспомни, Господи, что над нами совершилось; призри и посмотри на поругание наше. "));
rusuperobject.biblemap.put("b25_5_2", new rusuperobject(25,5,2,"Наследие наше перешло к чужим, домы наши - к иноплеменным; "));
rusuperobject.biblemap.put("b25_5_3", new rusuperobject(25,5,3,"мы сделались сиротами, без отца; матери наши - как вдовы. "));
rusuperobject.biblemap.put("b25_5_4", new rusuperobject(25,5,4,"Воду свою пьем за серебро, дрова наши достаются нам за деньги. "));
rusuperobject.biblemap.put("b25_5_5", new rusuperobject(25,5,5,"Нас погоняют в шею, мы работаем, и не имеем отдыха. "));
rusuperobject.biblemap.put("b25_5_6", new rusuperobject(25,5,6,"Протягиваем руку к Египтянам, к Ассириянам, чтобы насытиться хлебом. "));
rusuperobject.biblemap.put("b25_5_7", new rusuperobject(25,5,7,"Отцы наши грешили: их уже нет, а мы несем наказание за беззакония их. "));
rusuperobject.biblemap.put("b25_5_8", new rusuperobject(25,5,8,"Рабы господствуют над нами, и некому избавить от руки их. "));
rusuperobject.biblemap.put("b25_5_9", new rusuperobject(25,5,9,"С опасностью жизни от меча, в пустыне достаем хлеб себе. "));
rusuperobject.biblemap.put("b25_5_10", new rusuperobject(25,5,10,"Кожа наша почернела, как печь, от жгучего голода. "));
rusuperobject.biblemap.put("b25_5_11", new rusuperobject(25,5,11,"Жен бесчестят на Сионе, девиц - в городах Иудейских. "));
rusuperobject.biblemap.put("b25_5_12", new rusuperobject(25,5,12,"Князья повешены руками их, лица старцев не уважены. "));
rusuperobject.biblemap.put("b25_5_13", new rusuperobject(25,5,13,"Юношей берут к жерновам, и отроки падают под ношами дров. "));
rusuperobject.biblemap.put("b25_5_14", new rusuperobject(25,5,14,"Старцы уже не сидят у ворот; юноши не поют. "));
rusuperobject.biblemap.put("b25_5_15", new rusuperobject(25,5,15,"Прекратилась радость сердца нашего; хороводы наши обратились в сетование. "));
rusuperobject.biblemap.put("b25_5_16", new rusuperobject(25,5,16,"Упал венец с головы нашей; горе нам, что мы согрешили! "));
rusuperobject.biblemap.put("b25_5_17", new rusuperobject(25,5,17,"От сего-то изнывает сердце наше; от сего померкли глаза наши. "));
rusuperobject.biblemap.put("b25_5_18", new rusuperobject(25,5,18,"Оттого, что опустела гора Сион, лисицы ходят по ней. "));
rusuperobject.biblemap.put("b25_5_19", new rusuperobject(25,5,19,"Ты, Господи, пребываешь во веки; престол Твой - в род и род. "));
rusuperobject.biblemap.put("b25_5_20", new rusuperobject(25,5,20,"Для чего совсем забываешь нас, оставляешь нас на долгое время? "));
rusuperobject.biblemap.put("b25_5_21", new rusuperobject(25,5,21,"Обрати нас к Тебе, Господи, и мы обратимся; обнови дни наши, как древле. "));
rusuperobject.biblemap.put("b25_5_22", new rusuperobject(25,5,22,"Неужели Ты совсем отверг нас, прогневался на нас безмерно?"));

}

}