package ru.bible;

public class rubible27 {

public rubible27() {

putmapbible27();

}

private void putmapbible27() {

rusuperobject.biblemap.put("b27_1_1", new rusuperobject(27,1,1,"В третий год царствования Иоакима, царя Иудейского, пришел Навуходоносор, царь Вавилонский, к Иерусалиму и осадил его. "));
rusuperobject.biblemap.put("b27_1_2", new rusuperobject(27,1,2,"И предал Господь в руку его Иоакима, царя Иудейского, и часть сосудов дома Божия, и он отправил их в землю Сеннаар, в дом бога своего, и внес эти сосуды в сокровищницу бога своего. "));
rusuperobject.biblemap.put("b27_1_3", new rusuperobject(27,1,3,"И сказал царь Асфеназу, начальнику евнухов своих, чтобы он из сынов Израилевых, из рода царского и княжеского, привел "));
rusuperobject.biblemap.put("b27_1_4", new rusuperobject(27,1,4,"отроков, у которых нет никакого телесного недостатка, красивых видом, и понятливых для всякой науки, и разумеющих науки, и смышленых и годных служить в чертогах царских, и чтобы научил их книгам и языку Халдейскому. "));
rusuperobject.biblemap.put("b27_1_5", new rusuperobject(27,1,5,"И назначил им царь ежедневную пищу с царского стола и вино, которое сам пил, и велел воспитывать их три года, по истечении которых они должны были предстать пред царя. "));
rusuperobject.biblemap.put("b27_1_6", new rusuperobject(27,1,6,"Между ними были из сынов Иудиных Даниил, Анания, Мисаил и Азария. "));
rusuperobject.biblemap.put("b27_1_7", new rusuperobject(27,1,7,"И переименовал их начальник евнухов - Даниила Валтасаром, Ананию Седрахом, Мисаила Мисахом и Азарию Авденаго. "));
rusuperobject.biblemap.put("b27_1_8", new rusuperobject(27,1,8,"Даниил положил в сердце своем не оскверняться яствами со стола царского и вином, какое пьет царь, и потому просил начальника евнухов о том, чтобы не оскверняться ему. "));
rusuperobject.biblemap.put("b27_1_9", new rusuperobject(27,1,9,"Бог даровал Даниилу милость и благорасположение начальника евнухов; "));
rusuperobject.biblemap.put("b27_1_10", new rusuperobject(27,1,10,"и начальник евнухов сказал Даниилу: боюсь я господина моего, царя, который сам назначил вам пищу и питье; если он увидит лица ваши худощавее, нежели у отроков, сверстников ваших, то вы сделаете голову мою виновною перед царем. "));
rusuperobject.biblemap.put("b27_1_11", new rusuperobject(27,1,11,"Тогда сказал Даниил Амелсару, которого начальник евнухов приставил к Даниилу, Анании, Мисаилу и Азарии: "));
rusuperobject.biblemap.put("b27_1_12", new rusuperobject(27,1,12,"сделай опыт над рабами твоими в течение десяти дней; пусть дают нам в пищу овощи и воду для питья; "));
rusuperobject.biblemap.put("b27_1_13", new rusuperobject(27,1,13,"и потом пусть явятся перед тобою лица наши и лица тех отроков, которые питаются царскою пищею, и затем поступай с рабами твоими, как увидишь. "));
rusuperobject.biblemap.put("b27_1_14", new rusuperobject(27,1,14,"Он послушался их в этом и испытывал их десять дней. "));
rusuperobject.biblemap.put("b27_1_15", new rusuperobject(27,1,15,"По истечении же десяти дней лица их оказались красивее, и телом они были полнее всех тех отроков, которые питались царскими яствами. "));
rusuperobject.biblemap.put("b27_1_16", new rusuperobject(27,1,16,"Тогда Амелсар брал их кушанье и вино для питья и давал им овощи. "));
rusuperobject.biblemap.put("b27_1_17", new rusuperobject(27,1,17,"И даровал Бог четырем сим отрокам знание и разумение всякой книги и мудрости, а Даниилу еще даровал разуметь и всякие видения и сны. "));
rusuperobject.biblemap.put("b27_1_18", new rusuperobject(27,1,18,"По окончании тех дней, когда царь приказал представить их, начальник евнухов представил их Навуходоносору. "));
rusuperobject.biblemap.put("b27_1_19", new rusuperobject(27,1,19,"И царь говорил с ними, и из всех отроков не нашлось подобных Даниилу, Анании, Мисаилу и Азарии, и стали они служить пред царем. "));
rusuperobject.biblemap.put("b27_1_20", new rusuperobject(27,1,20,"И во всяком деле мудрого уразумения, о чем ни спрашивал их царь, он находил их в десять раз выше всех тайноведцев и волхвов, какие были во всем царстве его. "));
rusuperobject.biblemap.put("b27_1_21", new rusuperobject(27,1,21,"И был там Даниил до первого года царя Кира. "));
rusuperobject.biblemap.put("b27_2_1", new rusuperobject(27,2,1,"Во второй год царствования Навуходоносора снились Навуходоносору сны, и возмутился дух его, и сон удалился от него. "));
rusuperobject.biblemap.put("b27_2_2", new rusuperobject(27,2,2,"И велел царь созвать тайноведцев, и гадателей, и чародеев, и Халдеев, чтобы они рассказали царю сновидения его. Они пришли, и стали перед царем. "));
rusuperobject.biblemap.put("b27_2_3", new rusuperobject(27,2,3,"И сказал им царь: сон снился мне, и тревожится дух мой; желаю знать этот сон. "));
rusuperobject.biblemap.put("b27_2_4", new rusuperobject(27,2,4,"И сказали Халдеи царю по-арамейски: царь! вовеки живи! скажи сон рабам твоим, и мы объясним значение его. "));
rusuperobject.biblemap.put("b27_2_5", new rusuperobject(27,2,5,"Отвечал царь и сказал Халдеям: слово отступило от меня; если вы не скажете мне сновидения и значения его, то в куски будете изрублены, и домы ваши обратятся в развалины. "));
rusuperobject.biblemap.put("b27_2_6", new rusuperobject(27,2,6,"Если же расскажете сон и значение его, то получите от меня дары, награду и великую почесть; итак скажите мне сон и значение его. "));
rusuperobject.biblemap.put("b27_2_7", new rusuperobject(27,2,7,"Они вторично отвечали и сказали: да скажет царь рабам своим сновидение, и мы объясним его значение. "));
rusuperobject.biblemap.put("b27_2_8", new rusuperobject(27,2,8,"Отвечал царь и сказал: верно знаю, что вы хотите выиграть время, потому что видите, что слово отступило от меня. "));
rusuperobject.biblemap.put("b27_2_9", new rusuperobject(27,2,9,"Так как вы не объявляете мне сновидения, то у вас один умысел: вы собираетесь сказать мне ложь и обман, пока минет время; итак расскажите мне сон, и тогда я узнаю, что вы можете объяснить мне и значение его. "));
rusuperobject.biblemap.put("b27_2_10", new rusuperobject(27,2,10,"Халдеи отвечали царю и сказали: нет на земле человека, который мог бы открыть это дело царю, и потому ни один царь, великий и могущественный, не требовал подобного ни от какого тайноведца, гадателя и Халдея. "));
rusuperobject.biblemap.put("b27_2_11", new rusuperobject(27,2,11,"Дело, которого царь требует, так трудно, что никто другой не может открыть его царю, кроме богов, которых обитание не с плотью. "));
rusuperobject.biblemap.put("b27_2_12", new rusuperobject(27,2,12,"Рассвирепел царь и сильно разгневался на это, и приказал истребить всех мудрецов Вавилонских. "));
rusuperobject.biblemap.put("b27_2_13", new rusuperobject(27,2,13,"Когда вышло это повеление, чтобы убивать мудрецов, искали Даниила и товарищей его, чтобы умертвить их. "));
rusuperobject.biblemap.put("b27_2_14", new rusuperobject(27,2,14,"Тогда Даниил обратился с советом и мудростью к Ариоху, начальнику царских телохранителей, который вышел убивать мудрецов Вавилонских; "));
rusuperobject.biblemap.put("b27_2_15", new rusuperobject(27,2,15,"и спросил Ариоха, сильного при царе: «почему такое грозное повеление от царя?» Тогда Ариох рассказал все дело Даниилу. "));
rusuperobject.biblemap.put("b27_2_16", new rusuperobject(27,2,16,"И Даниил вошел, и упросил царя дать ему время, и он представит царю толкование сна. "));
rusuperobject.biblemap.put("b27_2_17", new rusuperobject(27,2,17,"Даниил пришел в дом свой, и рассказал дело Анании, Мисаилу и Азарии, товарищам своим, "));
rusuperobject.biblemap.put("b27_2_18", new rusuperobject(27,2,18,"чтобы они просили милости у Бога небесного об этой тайне, дабы Даниил и товарищи его не погибли с прочими мудрецами Вавилонскими. "));
rusuperobject.biblemap.put("b27_2_19", new rusuperobject(27,2,19,"И тогда открыта была тайна Даниилу в ночном видении, и Даниил благословил Бога небесного. "));
rusuperobject.biblemap.put("b27_2_20", new rusuperobject(27,2,20,"И сказал Даниил: да будет благословенно имя Господа от века и до века! ибо у Него мудрость и сила; "));
rusuperobject.biblemap.put("b27_2_21", new rusuperobject(27,2,21,"Он изменяет времена и лета, низлагает царей и поставляет царей; дает мудрость мудрым и разумение разумным; "));
rusuperobject.biblemap.put("b27_2_22", new rusuperobject(27,2,22,"он открывает глубокое и сокровенное, знает, что во мраке, и свет обитает с Ним. "));
rusuperobject.biblemap.put("b27_2_23", new rusuperobject(27,2,23,"Славлю и величаю Тебя, Боже отцов моих, что Ты даровал мне мудрость и силу и открыл мне то, о чем мы молили Тебя; ибо Ты открыл нам дело царя. "));
rusuperobject.biblemap.put("b27_2_24", new rusuperobject(27,2,24,"После сего Даниил вошел к Ариоху, которому царь повелел умертвить мудрецов Вавилонских, пришел и сказал ему: не убивай мудрецов Вавилонских; введи меня к царю, и я открою значение сна. "));
rusuperobject.biblemap.put("b27_2_25", new rusuperobject(27,2,25,"Тогда Ариох немедленно привел Даниила к царю и сказал ему: я нашел из пленных сынов Иудеи человека, который может открыть царю значение сна. "));
rusuperobject.biblemap.put("b27_2_26", new rusuperobject(27,2,26,"Царь сказал Даниилу, который назван был Валтасаром: можешь ли ты сказать мне сон, который я видел, и значение его? "));
rusuperobject.biblemap.put("b27_2_27", new rusuperobject(27,2,27,"Даниил отвечал царю и сказал: тайны, о которой царь спрашивает, не могут открыть царю ни мудрецы, ни обаятели, ни тайноведцы, ни гадатели. "));
rusuperobject.biblemap.put("b27_2_28", new rusuperobject(27,2,28,"Но есть на небесах Бог, открывающий тайны; и Он открыл царю Навуходоносору, что будет в последние дни. Сон твой и видения главы твоей на ложе твоем были такие: "));
rusuperobject.biblemap.put("b27_2_29", new rusuperobject(27,2,29,"ты, царь, на ложе твоем думал о том, что будет после сего? и Открывающий тайны показал тебе то, что будет. "));
rusuperobject.biblemap.put("b27_2_30", new rusuperobject(27,2,30,"А мне тайна сия открыта не потому, чтобы я был мудрее всех живущих, но для того, чтобы открыто было царю разумение и чтобы ты узнал помышления сердца твоего. "));
rusuperobject.biblemap.put("b27_2_31", new rusuperobject(27,2,31,"Тебе, царь, было такое видение: вот, какой-то большой истукан; огромный был этот истукан, в чрезвычайном блеске стоял он пред тобою, и страшен был вид его. "));
rusuperobject.biblemap.put("b27_2_32", new rusuperobject(27,2,32,"У этого истукана голова была из чистого золота, грудь его и руки его - из серебра, чрево его и бедра его медные, "));
rusuperobject.biblemap.put("b27_2_33", new rusuperobject(27,2,33,"голени его железные, ноги его частью железные, частью глиняные. "));
rusuperobject.biblemap.put("b27_2_34", new rusuperobject(27,2,34,"Ты видел его, доколе камень не оторвался от горы без содействия рук, ударил в истукана, в железные и глиняные ноги его, и разбил их. "));
rusuperobject.biblemap.put("b27_2_35", new rusuperobject(27,2,35,"Тогда все вместе раздробилось: железо, глина, медь, серебро и золото сделались как прах на летних гумнах, и ветер унес их, и следа не осталось от них; а камень, разбивший истукана, сделался великою горою и наполнил всю землю. "));
rusuperobject.biblemap.put("b27_2_36", new rusuperobject(27,2,36,"Вот сон! Скажем пред царем и значение его. "));
rusuperobject.biblemap.put("b27_2_37", new rusuperobject(27,2,37,"Ты, царь, царь царей, которому Бог небесный даровал царство, власть, силу и славу, "));
rusuperobject.biblemap.put("b27_2_38", new rusuperobject(27,2,38,"и всех сынов человеческих, где бы они ни жили, зверей земных и птиц небесных Он отдал в твои руки и поставил тебя владыкою над всеми ими. Ты - это золотая голова! "));
rusuperobject.biblemap.put("b27_2_39", new rusuperobject(27,2,39,"После тебя восстанет другое царство, ниже твоего, и еще третье царство, медное, которое будет владычествовать над всею землею. "));
rusuperobject.biblemap.put("b27_2_40", new rusuperobject(27,2,40,"А четвертое царство будет крепко, как железо; ибо как железо разбивает и раздробляет все, так и оно, подобно всесокрушающему железу, будет раздроблять и сокрушать. "));
rusuperobject.biblemap.put("b27_2_41", new rusuperobject(27,2,41,"А что ты видел ноги и пальцы на ногах частью из глины горшечной, а частью из железа, то будет царство разделенное, и в нем останется несколько крепости железа, так как ты видел железо, смешанное с горшечною глиною. "));
rusuperobject.biblemap.put("b27_2_42", new rusuperobject(27,2,42,"И как персты ног были частью из железа, а частью из глины, так и царство будет частью крепкое, частью хрупкое. "));
rusuperobject.biblemap.put("b27_2_43", new rusuperobject(27,2,43,"А что ты видел железо, смешанное с глиною горшечною, это значит, что они смешаются через семя человеческое, но не сольются одно с другим, как железо не смешивается с глиною. "));
rusuperobject.biblemap.put("b27_2_44", new rusuperobject(27,2,44,"И во дни тех царств Бог небесный воздвигнет царство, которое вовеки не разрушится, и царство это не будет передано другому народу; оно сокрушит и разрушит все царства, а само будет стоять вечно, "));
rusuperobject.biblemap.put("b27_2_45", new rusuperobject(27,2,45,"так как ты видел, что камень отторгнут был от горы не руками и раздробил железо, медь, глину, серебро и золото. Великий Бог дал знать царю, что будет после сего. И верен этот сон, и точно истолкование его! "));
rusuperobject.biblemap.put("b27_2_46", new rusuperobject(27,2,46,"Тогда царь Навуходоносор пал на лице свое и поклонился Даниилу, и велел принести ему дары и благовонные курения. "));
rusuperobject.biblemap.put("b27_2_47", new rusuperobject(27,2,47,"И сказал царь Даниилу: истинно Бог ваш есть Бог богов и Владыка царей, открывающий тайны, когда ты мог открыть эту тайну! "));
rusuperobject.biblemap.put("b27_2_48", new rusuperobject(27,2,48,"Тогда возвысил царь Даниила и дал ему много больших подарков, и поставил его над всею областью Вавилонскою и главным начальником над всеми мудрецами Вавилонскими. "));
rusuperobject.biblemap.put("b27_2_49", new rusuperobject(27,2,49,"Но Даниил просил царя, и он поставил Седраха, Мисаха и Авденаго над делами страны Вавилонской, а Даниил остался при дворе царя. "));
rusuperobject.biblemap.put("b27_3_1", new rusuperobject(27,3,1,"Царь Навуходоносор сделал золотой истукан, вышиною в шестьдесят локтей, шириною в шесть локтей, поставил его на поле Деире, в области Вавилонской. "));
rusuperobject.biblemap.put("b27_3_2", new rusuperobject(27,3,2,"И послал царь Навуходоносор собрать сатрапов, наместников, воевод, верховных судей, казнохранителей, законоведцев, блюстителей суда и всех областных правителей, чтобы они пришли на торжественное открытие истукана, которого поставил царь Навуходоносор. "));
rusuperobject.biblemap.put("b27_3_3", new rusuperobject(27,3,3,"И собрались сатрапы, наместники, военачальники, верховные судьи, казнохранители, законоведцы, блюстители суда и все областные правители на открытие истукана, которого Навуходоносор царь поставил, и стали перед истуканом, которого воздвиг Навуходоносор. "));
rusuperobject.biblemap.put("b27_3_4", new rusuperobject(27,3,4,"Тогда глашатай громко воскликнул: объявляется вам, народы, племена и языки: "));
rusuperobject.biblemap.put("b27_3_5", new rusuperobject(27,3,5,"в то время, как услышите звук трубы, свирели, цитры, цевницы, гуслей и симфонии и всяких музыкальных орудий, падите и поклонитесь золотому истукану, которого поставил царь Навуходоносор. "));
rusuperobject.biblemap.put("b27_3_6", new rusuperobject(27,3,6,"А кто не падет и не поклонится, тотчас брошен будет в печь, раскаленную огнем. "));
rusuperobject.biblemap.put("b27_3_7", new rusuperobject(27,3,7,"Посему, когда все народы услышали звук трубы, свирели, цитры, цевницы, гуслей и всякого рода музыкальных орудий, то пали все народы, племена и языки, и поклонились золотому истукану, которого поставил Навуходоносор царь. "));
rusuperobject.biblemap.put("b27_3_8", new rusuperobject(27,3,8,"В это самое время приступили некоторые из Халдеев и донесли на Иудеев. "));
rusuperobject.biblemap.put("b27_3_9", new rusuperobject(27,3,9,"Они сказали царю Навуходоносору: царь, вовеки живи! "));
rusuperobject.biblemap.put("b27_3_10", new rusuperobject(27,3,10,"Ты, царь, дал повеление, чтобы каждый человек, который услышит звук трубы, свирели, цитры, цевницы, гуслей и симфонии и всякого рода музыкальных орудий, пал и поклонился золотому истукану; "));
rusuperobject.biblemap.put("b27_3_11", new rusuperobject(27,3,11,"а кто не падет и не поклонится, тот должен быть брошен в печь, раскаленную огнем. "));
rusuperobject.biblemap.put("b27_3_12", new rusuperobject(27,3,12,"Есть мужи Иудейские, которых ты поставил над делами страны Вавилонской: Седрах, Мисах и Авденаго; эти мужи не повинуются повелению твоему, царь, богам твоим не служат и золотому истукану, которого ты поставил, не поклоняются. "));
rusuperobject.biblemap.put("b27_3_13", new rusuperobject(27,3,13,"Тогда Навуходоносор во гневе и ярости повелел привести Седраха, Мисаха и Авденаго; и приведены были эти мужи к царю. "));
rusuperobject.biblemap.put("b27_3_14", new rusuperobject(27,3,14,"Навуходоносор сказал им: с умыслом ли вы, Седрах, Мисах и Авденаго, богам моим не служите, и золотому истукану, которого я поставил, не поклоняетесь? "));
rusuperobject.biblemap.put("b27_3_15", new rusuperobject(27,3,15,"Отныне, если вы готовы, как скоро услышите звук трубы, свирели, цитры, цевницы, гуслей, симфонии и всякого рода музыкальных орудий, падите и поклонитесь истукану, которого я сделал; если же не поклонитесь, то в тот же час брошены будете в печь, раскаленную огнем, и тогда какой Бог избавит вас от руки моей? "));
rusuperobject.biblemap.put("b27_3_16", new rusuperobject(27,3,16,"И отвечали Седрах, Мисах и Авденаго, и сказали царю Навуходоносору: нет нужды нам отвечать тебе на это. "));
rusuperobject.biblemap.put("b27_3_17", new rusuperobject(27,3,17,"Бог наш, Которому мы служим, силен спасти нас от печи, раскаленной огнем, и от руки твоей, царь, избавит. "));
rusuperobject.biblemap.put("b27_3_18", new rusuperobject(27,3,18,"Если же и не будет того, то да будет известно тебе, царь, что мы богам твоим служить не будем и золотому истукану, которого ты поставил, не поклонимся. "));
rusuperobject.biblemap.put("b27_3_19", new rusuperobject(27,3,19,"Тогда Навуходоносор исполнился ярости, и вид лица его изменился на Седраха, Мисаха и Авденаго, и он повелел разжечь печь в семь раз сильнее, нежели как обыкновенно разжигали ее, "));
rusuperobject.biblemap.put("b27_3_20", new rusuperobject(27,3,20,"и самым сильным мужам из войска своего приказал связать Седраха, Мисаха и Авденаго и бросить их в печь, раскаленную огнем. "));
rusuperobject.biblemap.put("b27_3_21", new rusuperobject(27,3,21,"Тогда мужи сии связаны были в исподнем и верхнем платье своем, в головных повязках и в прочих одеждах своих, и брошены в печь, раскаленную огнем. "));
rusuperobject.biblemap.put("b27_3_22", new rusuperobject(27,3,22,"И как повеление царя было строго, и печь раскалена была чрезвычайно, то пламя огня убило тех людей, которые бросали Седраха, Мисаха и Авденаго. "));
rusuperobject.biblemap.put("b27_3_23", new rusuperobject(27,3,23,"А сии три мужа, Седрах, Мисах и Авденаго, упали в раскаленную огнем печь связанные. "));
rusuperobject.biblemap.put("b27_3_24", new rusuperobject(27,3,24,"И ходили посреди пламени, воспевая Бога и благословляя Господа. "));
rusuperobject.biblemap.put("b27_3_25", new rusuperobject(27,3,25,"И став Азария молился и, открыв уста свои среди огня, возгласил: "));
rusuperobject.biblemap.put("b27_3_26", new rusuperobject(27,3,26,"«Благословен Ты, Господи Боже отцов наших, хвально и прославлено имя Твое вовеки. "));
rusuperobject.biblemap.put("b27_3_27", new rusuperobject(27,3,27,"Ибо праведен Ты во всем, что соделал с нами, и все дела Твои истинны и пути Твои правы, и все суды Твои истинны. "));
rusuperobject.biblemap.put("b27_3_28", new rusuperobject(27,3,28,"Ты совершил истинные суды во всем, что навел на нас и на святый град отцов наших Иерусалим, потому что по истине и по суду навел Ты все это на нас за грехи наши. "));
rusuperobject.biblemap.put("b27_3_29", new rusuperobject(27,3,29,"Ибо согрешили мы, и поступили беззаконно, отступив от Тебя, и во всем согрешили. "));
rusuperobject.biblemap.put("b27_3_30", new rusuperobject(27,3,30,"Заповедей Твоих не слушали и не соблюдали их, и не поступали, как Ты повелел нам, чтобы благо нам было. "));
rusuperobject.biblemap.put("b27_3_31", new rusuperobject(27,3,31,"И все, что Ты навел на нас, и все, что Ты соделал с нами, соделал по истинному суду. "));
rusuperobject.biblemap.put("b27_3_32", new rusuperobject(27,3,32,"И предал нас в руки врагов беззаконных, ненавистнейших отступников, и царю неправосудному и злейшему на всей земле. "));
rusuperobject.biblemap.put("b27_3_33", new rusuperobject(27,3,33,"И ныне мы не можем открыть уст наших; мы сделались стыдом и поношением для рабов Твоих и чтущих Тебя. "));
rusuperobject.biblemap.put("b27_3_34", new rusuperobject(27,3,34,"Но не предай нас навсегда ради имени Твоего, и не разруши завета Твоего. "));
rusuperobject.biblemap.put("b27_3_35", new rusuperobject(27,3,35,"Не отними от нас милости Твоей ради Авраама, возлюбленного Тобою, ради Исаака, раба Твоего, и Израиля, святого Твоего, "));
rusuperobject.biblemap.put("b27_3_36", new rusuperobject(27,3,36,"которым Ты говорил, что умножишь семя их, как звезды небесные и как песок на берегу моря. "));
rusuperobject.biblemap.put("b27_3_37", new rusuperobject(27,3,37,"Мы умалены, Господи, паче всех народов, и унижены ныне на всей земле за грехи наши, "));
rusuperobject.biblemap.put("b27_3_38", new rusuperobject(27,3,38,"и нет у нас в настоящее время ни князя, ни пророка, ни вождя, ни всесожжения, ни жертвы, ни приношения, ни фимиама, ни места, чтобы нам принести жертву Тебе и обрести милость Твою. "));
rusuperobject.biblemap.put("b27_3_39", new rusuperobject(27,3,39,"Но с сокрушенным сердцем и смиренным духом да будем приняты. "));
rusuperobject.biblemap.put("b27_3_40", new rusuperobject(27,3,40,"Как при всесожжении овнов и тельцов и как при тысячах тучных агнцев, так да будет жертва наша пред Тобою ныне благоугодною Тебе; ибо нет стыда уповающим на Тебя. "));
rusuperobject.biblemap.put("b27_3_41", new rusuperobject(27,3,41,"И ныне мы следуем за Тобою всем сердцем и боимся Тебя и ищем лица Твоего. "));
rusuperobject.biblemap.put("b27_3_42", new rusuperobject(27,3,42,"Не посрами нас, но сотвори с нами по снисхождению Твоему и по множеству милости Твоей "));
rusuperobject.biblemap.put("b27_3_43", new rusuperobject(27,3,43,"и избави нас силою чудес Твоих, и дай славу имени Твоему, Господи, "));
rusuperobject.biblemap.put("b27_3_44", new rusuperobject(27,3,44,"и да постыдятся все, делающие рабам Твоим зло, и да постыдятся со всем могуществом, и сила их да сокрушится, "));
rusuperobject.biblemap.put("b27_3_45", new rusuperobject(27,3,45,"и да познают, что Ты Господь Бог един и славен по всей вселенной». "));
rusuperobject.biblemap.put("b27_3_46", new rusuperobject(27,3,46,"А между тем слуги царя, ввергшие их, не переставали разжигать печь нефтью, смолою, паклею и хворостом, "));
rusuperobject.biblemap.put("b27_3_47", new rusuperobject(27,3,47,"и поднимался пламень над печью на сорок девять локтей "));
rusuperobject.biblemap.put("b27_3_48", new rusuperobject(27,3,48,"и вырывался, и сожигал тех из Халдеев, которых достигал около печи. "));
rusuperobject.biblemap.put("b27_3_49", new rusuperobject(27,3,49,"Но Ангел Господень сошел в печь вместе с Азариею и бывшими с ним "));
rusuperobject.biblemap.put("b27_3_50", new rusuperobject(27,3,50,"и выбросил пламень огня из печи, и сделал, что в средине печи был как бы шумящий влажный ветер, и огонь нисколько не прикоснулся к ним, и не повредил им, и не смутил их. "));
rusuperobject.biblemap.put("b27_3_51", new rusuperobject(27,3,51,"Тогда сии трое, как бы одними устами, воспели в печи, и благословили и прославили Бога: "));
rusuperobject.biblemap.put("b27_3_52", new rusuperobject(27,3,52,"«Благословен Ты, Господи Боже отцов наших, и хвальный и превозносимый вовеки, и благословенно имя славы Твоей, святое и прехвальное и превозносимое во веки. "));
rusuperobject.biblemap.put("b27_3_53", new rusuperobject(27,3,53,"Благословен Ты в храме святой славы Твоей, и прехвальный и преславный во веки. "));
rusuperobject.biblemap.put("b27_3_54", new rusuperobject(27,3,54,"Благословен Ты, видящий бездны, восседающий на Херувимах, и прехвальный и превозносимый во веки. "));
rusuperobject.biblemap.put("b27_3_55", new rusuperobject(27,3,55,"Благословен Ты на престоле славы царства Твоего, и прехвальный и превозносимый во веки. "));
rusuperobject.biblemap.put("b27_3_56", new rusuperobject(27,3,56,"Благословен Ты на тверди небесной, и прехвальный и превозносимый во веки. "));
rusuperobject.biblemap.put("b27_3_57", new rusuperobject(27,3,57,"Благословите, все дела Господни. Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_58", new rusuperobject(27,3,58,"Благословите, Ангелы Господни, Господа, пойте и превозносите Его вовеки. "));
rusuperobject.biblemap.put("b27_3_59", new rusuperobject(27,3,59,"Благословите, небеса, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_60", new rusuperobject(27,3,60,"Благословите Господа, все воды, которые превыше небес, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_61", new rusuperobject(27,3,61,"Благословите, все силы Господни, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_62", new rusuperobject(27,3,62,"Благословите, солнце и луна, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_63", new rusuperobject(27,3,63,"Благословите, звезды небесные, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_64", new rusuperobject(27,3,64,"Благословите, всякий дождь и роса, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_65", new rusuperobject(27,3,65,"Благословите, все ветры, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_66", new rusuperobject(27,3,66,"Благословите, огонь и жар, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_67", new rusuperobject(27,3,67,"Благословите, холод и зной, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_68", new rusuperobject(27,3,68,"Благословите, росы и инеи, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_69", new rusuperobject(27,3,69,"Благословите, ночи и дни, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_70", new rusuperobject(27,3,70,"Благословите, свет и тьма, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_71", new rusuperobject(27,3,71,"Благословите, лед и мороз, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_72", new rusuperobject(27,3,72,"Благословите, иней и снег, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_73", new rusuperobject(27,3,73,"Благословите, молнии и облака, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_74", new rusuperobject(27,3,74,"Да благословит земля Господа, да поет и превозносит Его во веки. "));
rusuperobject.biblemap.put("b27_3_75", new rusuperobject(27,3,75,"Благословите, горы и холмы, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_76", new rusuperobject(27,3,76,"Благословите Господа, все произрастания на земле, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_77", new rusuperobject(27,3,77,"Благословите, источники, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_78", new rusuperobject(27,3,78,"Благословите, моря и реки, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_79", new rusuperobject(27,3,79,"Благословите Господа, киты и все, движущееся в водах, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_80", new rusuperobject(27,3,80,"Благословите, все птицы небесные, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_81", new rusuperobject(27,3,81,"Благословите Господа, звери и весь скот, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_82", new rusuperobject(27,3,82,"Благословите, сыны человеческие, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_83", new rusuperobject(27,3,83,"Благослови, Израиль, Господа, пой и превозноси Его во веки. "));
rusuperobject.biblemap.put("b27_3_84", new rusuperobject(27,3,84,"Благословите, священники Господни, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_85", new rusuperobject(27,3,85,"Благословите, рабы Господни, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_86", new rusuperobject(27,3,86,"Благословите, духи и души праведных, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_87", new rusuperobject(27,3,87,"Благословите, праведные и смиренные сердцем, Господа, пойте и превозносите Его во веки. "));
rusuperobject.biblemap.put("b27_3_88", new rusuperobject(27,3,88,"Благословите, Анания, Азария и Мисаил, Господа, пойте и превозносите Его во веки; ибо Он извлек нас из ада и спас нас от руки смерти, и избавил нас из среды печи горящего пламени, и из среды огня избавил нас. "));
rusuperobject.biblemap.put("b27_3_89", new rusuperobject(27,3,89,"Славьте Господа, ибо Он благ, ибо вовек милость Его. "));
rusuperobject.biblemap.put("b27_3_90", new rusuperobject(27,3,90,"Благословите, все чтущие Господа, Бога богов, пойте и славьте, ибо вовек милость Его». "));
rusuperobject.biblemap.put("b27_3_91", new rusuperobject(27,3,91,"Навуходоносор царь, услышав, что они поют, изумился, и поспешно встал, и сказал вельможам своим: не троих ли мужей бросили мы в огонь связанными? Они в ответ сказали царю: истинно так, царь! "));
rusuperobject.biblemap.put("b27_3_92", new rusuperobject(27,3,92,"На это он сказал: вот, я вижу четырех мужей несвязанных, ходящих среди огня, и нет им вреда; и вид четвертого подобен сыну Божию. "));
rusuperobject.biblemap.put("b27_3_93", new rusuperobject(27,3,93,"Тогда подошел Навуходоносор к устью печи, раскаленной огнем, и сказал: Седрах, Мисах и Авденаго, рабы Бога Всевышнего! выйдите и подойдите! Тогда Седрах, Мисах и Авденаго вышли из среды огня. "));
rusuperobject.biblemap.put("b27_3_94", new rusuperobject(27,3,94,"И, собравшись, сатрапы, наместники, военачальники и советники царя усмотрели, что над телами мужей сих огонь не имел силы, и волосы на голове не опалены, и одежды их не изменились, и даже запаха огня не было от них. "));
rusuperobject.biblemap.put("b27_3_95", new rusuperobject(27,3,95,"Тогда Навуходоносор сказал: благословен Бог Седраха, Мисаха и Авденаго, Который послал Ангела Своего и избавил рабов Своих, которые надеялись на Него и не послушались царского повеления, и предали тела свои огню, чтобы не служить и не поклоняться иному богу, кроме Бога своего! "));
rusuperobject.biblemap.put("b27_3_96", new rusuperobject(27,3,96,"И от меня дается повеление, чтобы из всякого народа, племени и языка кто произнесет хулу на Бога Седраха, Мисаха и Авденаго, был изрублен в куски, и дом его обращен в развалины, ибо нет иного бога, который мог бы так спасать. "));
rusuperobject.biblemap.put("b27_3_97", new rusuperobject(27,3,97,"Тогда царь возвысил Седраха, Мисаха и Авденаго в стране Вавилонской и возвеличил их и удостоил их начальства над прочими Иудеями в его царстве. "));
rusuperobject.biblemap.put("b27_3_98", new rusuperobject(27,3,98,"Навуходоносор царь всем народам, племенам и языкам, живущим по всей земле: мир вам да умножится! "));
rusuperobject.biblemap.put("b27_3_99", new rusuperobject(27,3,99,"Знамения и чудеса, какие совершил надо мною Всевышний Бог, угодно мне возвестить вам. "));
rusuperobject.biblemap.put("b27_3_100", new rusuperobject(27,3,100,"Как велики знамения Его и как могущественны чудеса Его! Царство Его - царство вечное, и владычество Его - в роды и роды. "));
rusuperobject.biblemap.put("b27_4_1", new rusuperobject(27,4,1,"Я, Навуходоносор, спокоен был в доме моем и благоденствовал в чертогах моих. "));
rusuperobject.biblemap.put("b27_4_2", new rusuperobject(27,4,2,"Но я видел сон, который устрашил меня, и размышления на ложе моем и видения головы моей смутили меня. "));
rusuperobject.biblemap.put("b27_4_3", new rusuperobject(27,4,3,"И дано было мною повеление привести ко мне всех мудрецов Вавилонских, чтобы они сказали мне значение сна. "));
rusuperobject.biblemap.put("b27_4_4", new rusuperobject(27,4,4,"Тогда пришли тайноведцы, обаятели, Халдеи и гадатели; я рассказал им сон, но они не могли мне объяснить значения его. "));
rusuperobject.biblemap.put("b27_4_5", new rusuperobject(27,4,5,"Наконец вошел ко мне Даниил, которому имя было Валтасар, по имени бога моего, и в котором дух святого Бога; ему рассказал я сон. "));
rusuperobject.biblemap.put("b27_4_6", new rusuperobject(27,4,6,"Валтасар, глава мудрецов! я знаю, что в тебе дух святого Бога, и никакая тайна не затрудняет тебя; объясни мне видения сна моего, который я видел, и значение его. "));
rusuperobject.biblemap.put("b27_4_7", new rusuperobject(27,4,7,"Видения же головы моей на ложе моем были такие: я видел, вот, среди земли дерево весьма высокое. "));
rusuperobject.biblemap.put("b27_4_8", new rusuperobject(27,4,8,"Большое было это дерево и крепкое, и высота его достигала до неба, и оно видимо было до краев всей земли. "));
rusuperobject.biblemap.put("b27_4_9", new rusuperobject(27,4,9,"Листья его прекрасные, и плодов на нем множество, и пища на нем для всех; под ним находили тень полевые звери, и в ветвях его гнездились птицы небесные, и от него питалась всякая плоть. "));
rusuperobject.biblemap.put("b27_4_10", new rusuperobject(27,4,10,"И видел я в видениях головы моей на ложе моем, и вот, нисшел с небес Бодрствующий и Святый. "));
rusuperobject.biblemap.put("b27_4_11", new rusuperobject(27,4,11,"Воскликнув громко, Он сказал: «срубите это дерево, обрубите ветви его, стрясите листья с него и разбросайте плоды его; пусть удалятся звери из-под него и птицы с ветвей его; "));
rusuperobject.biblemap.put("b27_4_12", new rusuperobject(27,4,12,"но главный корень его оставьте в земле, и пусть он в узах железных и медных среди полевой травы орошается небесною росою, и с животными пусть будет часть его в траве земной. "));
rusuperobject.biblemap.put("b27_4_13", new rusuperobject(27,4,13,"Сердце человеческое отнимется от него и дастся ему сердце звериное, и пройдут над ним семь времен. "));
rusuperobject.biblemap.put("b27_4_14", new rusuperobject(27,4,14,"Повелением Бодрствующих это определено, и по приговору Святых назначено, дабы знали живущие, что Всевышний владычествует над царством человеческим, и дает его, кому хочет, и поставляет над ним уничиженного между людьми». "));
rusuperobject.biblemap.put("b27_4_15", new rusuperobject(27,4,15,"Такой сон видел я, царь Навуходоносор; а ты, Валтасар, скажи значение его, так как никто из мудрецов в моем царстве не мог объяснить его значения, а ты можешь, потому что дух святого Бога в тебе. "));
rusuperobject.biblemap.put("b27_4_16", new rusuperobject(27,4,16,"Тогда Даниил, которому имя Валтасар, около часа пробыл в изумлении, и мысли его смущали его. Царь начал говорить и сказал: Валтасар! да не смущает тебя этот сон и значение его. Валтасар отвечал и сказал: господин мой! твоим бы ненавистникам этот сон, и врагам твоим значение его! "));
rusuperobject.biblemap.put("b27_4_17", new rusuperobject(27,4,17,"Дерево, которое ты видел, которое было большое и крепкое, высотою своею достигало до небес и видимо было по всей земле, "));
rusuperobject.biblemap.put("b27_4_18", new rusuperobject(27,4,18,"на котором листья были прекрасные и множество плодов и пропитание для всех, под которым обитали звери полевые и в ветвях которого гнездились птицы небесные, "));
rusuperobject.biblemap.put("b27_4_19", new rusuperobject(27,4,19,"это ты, царь, возвеличившийся и укрепившийся, и величие твое возросло и достигло до небес, и власть твоя - до краев земли. "));
rusuperobject.biblemap.put("b27_4_20", new rusuperobject(27,4,20,"А что царь видел Бодрствующего и Святого, сходящего с небес, Который сказал: «срубите дерево и истребите его, только главный корень его оставьте в земле, и пусть он в узах железных и медных, среди полевой травы, орошается росою небесною, и с полевыми зверями пусть будет часть его, доколе не пройдут над ним семь времен», - "));
rusuperobject.biblemap.put("b27_4_21", new rusuperobject(27,4,21,"то вот значение этого, царь, и вот определение Всевышнего, которое постигнет господина моего, царя: "));
rusuperobject.biblemap.put("b27_4_22", new rusuperobject(27,4,22,"тебя отлучат от людей, и обитание твое будет с полевыми зверями; травою будут кормить тебя, как вола, росою небесною ты будешь орошаем, и семь времен пройдут над тобою, доколе познаешь, что Всевышний владычествует над царством человеческим и дает его, кому хочет. "));
rusuperobject.biblemap.put("b27_4_23", new rusuperobject(27,4,23,"А что повелено было оставить главный корень дерева, это значит, что царство твое останется при тебе, когда ты познаешь власть небесную. "));
rusuperobject.biblemap.put("b27_4_24", new rusuperobject(27,4,24,"Посему, царь, да будет благоугоден тебе совет мой: искупи грехи твои правдою и беззакония твои милосердием к бедным; вот чем может продлиться мир твой. "));
rusuperobject.biblemap.put("b27_4_25", new rusuperobject(27,4,25,"Все это сбылось над царем Навуходоносором. "));
rusuperobject.biblemap.put("b27_4_26", new rusuperobject(27,4,26,"По прошествии двенадцати месяцев, расхаживая по царским чертогам в Вавилоне, "));
rusuperobject.biblemap.put("b27_4_27", new rusuperobject(27,4,27,"царь сказал: это ли не величественный Вавилон, который построил я в дом царства силою моего могущества и в славу моего величия! "));
rusuperobject.biblemap.put("b27_4_28", new rusuperobject(27,4,28,"Еще речь сия была в устах царя, как был с неба голос: «тебе говорят, царь Навуходоносор: царство отошло от тебя! "));
rusuperobject.biblemap.put("b27_4_29", new rusuperobject(27,4,29,"И отлучат тебя от людей, и будет обитание твое с полевыми зверями; травою будут кормить тебя, как вола, и семь времен пройдут над тобою, доколе познаешь, что Всевышний владычествует над царством человеческим и дает его, кому хочет!» "));
rusuperobject.biblemap.put("b27_4_30", new rusuperobject(27,4,30,"Тотчас и исполнилось это слово над Навуходоносором, и отлучен он был от людей, ел траву, как вол, и орошалось тело его росою небесною, так что волосы у него выросли как у льва, и ногти у него - как у птицы. "));
rusuperobject.biblemap.put("b27_4_31", new rusuperobject(27,4,31,"По окончании же дней тех, я, Навуходоносор, возвел глаза мои к небу, и разум мой возвратился ко мне; и благословил я Всевышнего, восхвалил и прославил Присносущего, Которого владычество - владычество вечное, и Которого царство - в роды и роды. "));
rusuperobject.biblemap.put("b27_4_32", new rusuperobject(27,4,32,"И все, живущие на земле, ничего не значат; по воле Своей Он действует как в небесном воинстве, так и у живущих на земле; и нет никого, кто мог бы противиться руке Его и сказать Ему: «что Ты сделал?» "));
rusuperobject.biblemap.put("b27_4_33", new rusuperobject(27,4,33,"В то время возвратился ко мне разум мой, и к славе царства моего возвратились ко мне сановитость и прежний вид мой; тогда взыскали меня советники мои и вельможи мои, и я восстановлен на царство мое, и величие мое еще более возвысилось. "));
rusuperobject.biblemap.put("b27_4_34", new rusuperobject(27,4,34,"Ныне я, Навуходоносор, славлю, превозношу и величаю Царя Небесного, Которого все дела истинны и пути праведны, и Который силен смирить ходящих гордо. "));
rusuperobject.biblemap.put("b27_5_1", new rusuperobject(27,5,1,"Валтасар царь сделал большое пиршество для тысячи вельмож своих и перед глазами тысячи пил вино. "));
rusuperobject.biblemap.put("b27_5_2", new rusuperobject(27,5,2,"Вкусив вина, Валтасар приказал принести золотые и серебряные сосуды, которые Навуходоносор, отец его, вынес из храма Иерусалимского, чтобы пить из них царю, вельможам его, женам его и наложницам его. "));
rusuperobject.biblemap.put("b27_5_3", new rusuperobject(27,5,3,"Тогда принесли золотые сосуды, которые взяты были из святилища дома Божия в Иерусалиме; и пили из них царь и вельможи его, жены его и наложницы его. "));
rusuperobject.biblemap.put("b27_5_4", new rusuperobject(27,5,4,"Пили вино, и славили богов золотых и серебряных, медных, железных, деревянных и каменных. "));
rusuperobject.biblemap.put("b27_5_5", new rusuperobject(27,5,5,"В тот самый час вышли персты руки человеческой и писали против лампады на извести стены чертога царского, и царь видел кисть руки, которая писала. "));
rusuperobject.biblemap.put("b27_5_6", new rusuperobject(27,5,6,"Тогда царь изменился в лице своем; мысли его смутили его, связи чресл его ослабели, и колени его стали биться одно о другое. "));
rusuperobject.biblemap.put("b27_5_7", new rusuperobject(27,5,7,"Сильно закричал царь, чтобы привели обаятелей, Халдеев и гадателей. Царь начал говорить, и сказал мудрецам Вавилонским: кто прочитает это написанное и объяснит мне значение его, тот будет облечен в багряницу, и золотая цепь будет на шее у него, и третьим властелином будет в царстве. "));
rusuperobject.biblemap.put("b27_5_8", new rusuperobject(27,5,8,"И вошли все мудрецы царя, но не могли прочитать написанного и объяснить царю значения его. "));
rusuperobject.biblemap.put("b27_5_9", new rusuperobject(27,5,9,"Царь Валтасар чрезвычайно встревожился, и вид лица его изменился на нем, и вельможи его смутились. "));
rusuperobject.biblemap.put("b27_5_10", new rusuperobject(27,5,10,"Царица же, по поводу слов царя и вельмож его, вошла в палату пиршества; начала говорить царица и сказала: царь, вовеки живи! да не смущают тебя мысли твои, и да не изменяется вид лица твоего! "));
rusuperobject.biblemap.put("b27_5_11", new rusuperobject(27,5,11,"Есть в царстве твоем муж, в котором дух святого Бога; во дни отца твоего найдены были в нем свет, разум и мудрость, подобная мудрости богов, и царь Навуходоносор, отец твой, поставил его главою тайноведцев, обаятелей, Халдеев и гадателей, - сам отец твой, царь, "));
rusuperobject.biblemap.put("b27_5_12", new rusuperobject(27,5,12,"потому что в нем, в Данииле, которого царь переименовал Валтасаром, оказались высокий дух, ведение и разум, способный изъяснять сны, толковать загадочное и разрешать узлы. Итак пусть призовут Даниила и он объяснит значение. "));
rusuperobject.biblemap.put("b27_5_13", new rusuperobject(27,5,13,"Тогда введен был Даниил пред царя, и царь начал речь и сказал Даниилу: ты ли Даниил, один из пленных сынов Иудейских, которых отец мой, царь, привел из Иудеи? "));
rusuperobject.biblemap.put("b27_5_14", new rusuperobject(27,5,14,"Я слышал о тебе, что дух Божий в тебе и свет, и разум, и высокая мудрость найдена в тебе. "));
rusuperobject.biblemap.put("b27_5_15", new rusuperobject(27,5,15,"Вот, приведены были ко мне мудрецы и обаятели, чтобы прочитать это написанное и объяснить мне значение его; но они не могли объяснить мне этого. "));
rusuperobject.biblemap.put("b27_5_16", new rusuperobject(27,5,16,"А о тебе я слышал, что ты можешь объяснять значение и разрешать узлы; итак, если можешь прочитать это написанное и объяснить мне значение его, то облечен будешь в багряницу, и золотая цепь будет на шее твоей, и третьим властелином будешь в царстве. "));
rusuperobject.biblemap.put("b27_5_17", new rusuperobject(27,5,17,"Тогда отвечал Даниил, и сказал царю: дары твои пусть останутся у тебя, и почести отдай другому; а написанное я прочитаю царю и значение объясню ему. "));
rusuperobject.biblemap.put("b27_5_18", new rusuperobject(27,5,18,"Царь! Всевышний Бог даровал отцу твоему Навуходоносору царство, величие, честь и славу. "));
rusuperobject.biblemap.put("b27_5_19", new rusuperobject(27,5,19,"Пред величием, которое Он дал ему, все народы, племена и языки трепетали и страшились его: кого хотел, он убивал, и кого хотел, оставлял в живых; кого хотел, возвышал, и кого хотел, унижал. "));
rusuperobject.biblemap.put("b27_5_20", new rusuperobject(27,5,20,"Но когда сердце его надмилось и дух его ожесточился до дерзости, он был свержен с царского престола своего и лишен славы своей, "));
rusuperobject.biblemap.put("b27_5_21", new rusuperobject(27,5,21,"и отлучен был от сынов человеческих, и сердце его уподобилось звериному, и жил он с дикими ослами; кормили его травою, как вола, и тело его орошаемо было небесною росою, доколе он познал, что над царством человеческим владычествует Всевышний Бог и поставляет над ним, кого хочет. "));
rusuperobject.biblemap.put("b27_5_22", new rusuperobject(27,5,22,"И ты, сын его Валтасар, не смирил сердца твоего, хотя знал все это, "));
rusuperobject.biblemap.put("b27_5_23", new rusuperobject(27,5,23,"но вознесся против Господа небес, и сосуды дома Его принесли к тебе, и ты и вельможи твои, жены твои и наложницы твои пили из них вино, и ты славил богов серебряных и золотых, медных, железных, деревянных и каменных, которые ни видят, ни слышат, ни разумеют; а Бога, в руке Которого дыхание твое и у Которого все пути твои, ты не прославил. "));
rusuperobject.biblemap.put("b27_5_24", new rusuperobject(27,5,24,"За это и послана от Него кисть руки, и начертано это писание. "));
rusuperobject.biblemap.put("b27_5_25", new rusuperobject(27,5,25,"И вот что начертано: мене, мене, текел, упарсин. "));
rusuperobject.biblemap.put("b27_5_26", new rusuperobject(27,5,26,"Вот и значение слов: мене - исчислил Бог царство твое и положил конец ему; "));
rusuperobject.biblemap.put("b27_5_27", new rusuperobject(27,5,27,"Текел - ты взвешен на весах и найден очень легким; "));
rusuperobject.biblemap.put("b27_5_28", new rusuperobject(27,5,28,"Перес - разделено царство твое и дано Мидянам и Персам. "));
rusuperobject.biblemap.put("b27_5_29", new rusuperobject(27,5,29,"Тогда по повелению Валтасара облекли Даниила в багряницу и возложили золотую цепь на шею его, и провозгласили его третьим властелином в царстве. "));
rusuperobject.biblemap.put("b27_5_30", new rusuperobject(27,5,30,"В ту же самую ночь Валтасар, царь Халдейский, был убит, "));
rusuperobject.biblemap.put("b27_5_31", new rusuperobject(27,5,31,"и Дарий Мидянин принял царство, будучи шестидесяти двух лет. "));
rusuperobject.biblemap.put("b27_6_1", new rusuperobject(27,6,1,"Угодно было Дарию поставить над царством сто двадцать сатрапов, чтобы они были во всем царстве, "));
rusuperobject.biblemap.put("b27_6_2", new rusuperobject(27,6,2,"а над ними трех князей, - из которых один был Даниил, - чтобы сатрапы давали им отчет и чтобы царю не было никакого обременения. "));
rusuperobject.biblemap.put("b27_6_3", new rusuperobject(27,6,3,"Даниил превосходил прочих князей и сатрапов, потому что в нем был высокий дух, и царь помышлял уже поставить его над всем царством. "));
rusuperobject.biblemap.put("b27_6_4", new rusuperobject(27,6,4,"Тогда князья и сатрапы начали искать предлога к обвинению Даниила по управлению царством; но никакого предлога и погрешностей не могли найти, потому что он был верен, и никакой погрешности или вины не оказывалось в нем. "));
rusuperobject.biblemap.put("b27_6_5", new rusuperobject(27,6,5,"И эти люди сказали: не найти нам предлога против Даниила, если мы не найдем его против него в законе Бога его. "));
rusuperobject.biblemap.put("b27_6_6", new rusuperobject(27,6,6,"Тогда эти князья и сатрапы приступили к царю и так сказали ему: царь Дарий! вовеки живи! "));
rusuperobject.biblemap.put("b27_6_7", new rusuperobject(27,6,7,"Все князья царства, наместники, сатрапы, советники и военачальники согласились между собою, чтобы сделано было царское постановление и издано повеление, чтобы, кто в течение тридцати дней будет просить какого-либо бога или человека, кроме тебя, царь, того бросить в львиный ров. "));
rusuperobject.biblemap.put("b27_6_8", new rusuperobject(27,6,8,"Итак утверди, царь, это определение и подпиши указ, чтобы он был неизменен, как закон Мидийский и Персидский, и чтобы он не был нарушен. "));
rusuperobject.biblemap.put("b27_6_9", new rusuperobject(27,6,9,"Царь Дарий подписал указ и это повеление. "));
rusuperobject.biblemap.put("b27_6_10", new rusuperobject(27,6,10,"Даниил же, узнав, что подписан такой указ, пошел в дом свой; окна же в горнице его были открыты против Иерусалима, и он три раза в день преклонял колени, и молился своему Богу, и славословил Его, как это делал он и прежде того. "));
rusuperobject.biblemap.put("b27_6_11", new rusuperobject(27,6,11,"Тогда эти люди подсмотрели и нашли Даниила молящегося и просящего милости пред Богом своим, "));
rusuperobject.biblemap.put("b27_6_12", new rusuperobject(27,6,12,"потом пришли и сказали царю о царском повелении: не ты ли подписал указ, чтобы всякого человека, который в течение тридцати дней будет просить какого-либо бога или человека, кроме тебя, царь, бросать в львиный ров? Царь отвечал и сказал: это слово твердо, как закон Мидян и Персов, не допускающий изменения. "));
rusuperobject.biblemap.put("b27_6_13", new rusuperobject(27,6,13,"Тогда отвечали они и сказали царю, что Даниил, который из пленных сынов Иудеи, не обращает внимания ни на тебя, царь, ни на указ, тобою подписанный, но три раза в день молится своими молитвами. "));
rusuperobject.biblemap.put("b27_6_14", new rusuperobject(27,6,14,"Царь, услышав это, сильно опечалился и положил в сердце своем спасти Даниила, и даже до захождения солнца усиленно старался избавить его. "));
rusuperobject.biblemap.put("b27_6_15", new rusuperobject(27,6,15,"Но те люди приступили к царю и сказали ему: знай, царь, что по закону Мидян и Персов никакое определение или постановление, утвержденное царем, не может быть изменено. "));
rusuperobject.biblemap.put("b27_6_16", new rusuperobject(27,6,16,"Тогда царь повелел, и привели Даниила, и бросили в ров львиный; при этом царь сказал Даниилу: Бог твой, Которому ты неизменно служишь, Он спасет тебя! "));
rusuperobject.biblemap.put("b27_6_17", new rusuperobject(27,6,17,"И принесен был камень и положен на отверстие рва, и царь запечатал его перстнем своим, и перстнем вельмож своих, чтобы ничто не переменилось в распоряжении о Данииле. "));
rusuperobject.biblemap.put("b27_6_18", new rusuperobject(27,6,18,"Затем царь пошел в свой дворец, лег спать без ужина, и даже не велел вносить к нему пищи, и сон бежал от него. "));
rusuperobject.biblemap.put("b27_6_19", new rusuperobject(27,6,19,"Поутру же царь встал на рассвете и поспешно пошел ко рву львиному, "));
rusuperobject.biblemap.put("b27_6_20", new rusuperobject(27,6,20,"и, подойдя ко рву, жалобным голосом кликнул Даниила, и сказал царь Даниилу: Даниил, раб Бога живого! Бог твой, Которому ты неизменно служишь, мог ли спасти тебя от львов? "));
rusuperobject.biblemap.put("b27_6_21", new rusuperobject(27,6,21,"Тогда Даниил сказал царю: царь! вовеки живи! "));
rusuperobject.biblemap.put("b27_6_22", new rusuperobject(27,6,22,"Бог мой послал Ангела Своего и заградил пасть львам, и они не повредили мне, потому что я оказался пред Ним чист, да и перед тобою, царь, я не сделал преступления. "));
rusuperobject.biblemap.put("b27_6_23", new rusuperobject(27,6,23,"Тогда царь чрезвычайно возрадовался о нем и повелел поднять Даниила изо рва; и поднят был Даниил изо рва, и никакого повреждения не оказалось на нем, потому что он веровал в Бога своего. "));
rusuperobject.biblemap.put("b27_6_24", new rusuperobject(27,6,24,"И приказал царь, и приведены были те люди, которые обвиняли Даниила, и брошены в львиный ров, как они сами, так и дети их и жены их; и они не достигли до дна рва, как львы овладели ими и сокрушили все кости их. "));
rusuperobject.biblemap.put("b27_6_25", new rusuperobject(27,6,25,"После того царь Дарий написал всем народам, племенам и языкам, живущим по всей земле: «Мир вам да умножится! "));
rusuperobject.biblemap.put("b27_6_26", new rusuperobject(27,6,26,"Мною дается повеление, чтобы во всякой области царства моего трепетали и благоговели пред Богом Данииловым, потому что Он есть Бог живый и присносущий, и царство Его несокрушимо, и владычество Его бесконечно. "));
rusuperobject.biblemap.put("b27_6_27", new rusuperobject(27,6,27,"Он избавляет и спасает, и совершает чудеса и знамения на небе и на земле; Он избавил Даниила от силы львов». "));
rusuperobject.biblemap.put("b27_6_28", new rusuperobject(27,6,28,"И Даниил благоуспевал и в царствование Дария, и в царствование Кира Персидского. "));
rusuperobject.biblemap.put("b27_7_1", new rusuperobject(27,7,1,"В первый год Валтасара, царя Вавилонского, Даниил видел сон и пророческие видения головы своей на ложе своем. Тогда он записал этот сон, изложив сущность дела. "));
rusuperobject.biblemap.put("b27_7_2", new rusuperobject(27,7,2,"Начав речь, Даниил сказал: видел я в ночном видении моем, и вот, четыре ветра небесных боролись на великом море, "));
rusuperobject.biblemap.put("b27_7_3", new rusuperobject(27,7,3,"и четыре больших зверя вышли из моря, непохожие один на другого. "));
rusuperobject.biblemap.put("b27_7_4", new rusuperobject(27,7,4,"Первый - как лев, но у него крылья орлиные; я смотрел, доколе не вырваны были у него крылья, и он поднят был от земли, и стал на ноги, как человек, и сердце человеческое дано ему. "));
rusuperobject.biblemap.put("b27_7_5", new rusuperobject(27,7,5,"И вот еще зверь, второй, похожий на медведя, стоял с одной стороны, и три клыка во рту у него, между зубами его; ему сказано так: «встань, ешь мяса много!» "));
rusuperobject.biblemap.put("b27_7_6", new rusuperobject(27,7,6,"Затем видел я, вот еще зверь, как барс; на спине у него четыре птичьих крыла, и четыре головы были у зверя сего, и власть дана была ему. "));
rusuperobject.biblemap.put("b27_7_7", new rusuperobject(27,7,7,"После сего видел я в ночных видениях, и вот зверь четвертый, страшный и ужасный и весьма сильный; у него большие железные зубы; он пожирает и сокрушает, остатки же попирает ногами; он отличен был от всех прежних зверей, и десять рогов было у него. "));
rusuperobject.biblemap.put("b27_7_8", new rusuperobject(27,7,8,"Я смотрел на эти рога, и вот, вышел между ними еще небольшой рог, и три из прежних рогов с корнем исторгнуты были перед ним, и вот, в этом роге были глаза, как глаза человеческие, и уста, говорящие высокомерно. "));
rusuperobject.biblemap.put("b27_7_9", new rusuperobject(27,7,9,"Видел я, наконец, что поставлены были престолы, и воссел Ветхий днями; одеяние на Нем было бело, как снег, и волосы главы Его - как чистая волна; престол Его - как пламя огня, колеса Его - пылающий огонь. "));
rusuperobject.biblemap.put("b27_7_10", new rusuperobject(27,7,10,"Огненная река выходила и проходила пред Ним; тысячи тысяч служили Ему и тьмы тем предстояли пред Ним; судьи сели, и раскрылись книги. "));
rusuperobject.biblemap.put("b27_7_11", new rusuperobject(27,7,11,"Видел я тогда, что за изречение высокомерных слов, какие говорил рог, зверь был убит в глазах моих, и тело его сокрушено и предано на сожжение огню. "));
rusuperobject.biblemap.put("b27_7_12", new rusuperobject(27,7,12,"И у прочих зверей отнята власть их, и продолжение жизни дано им только на время и на срок. "));
rusuperobject.biblemap.put("b27_7_13", new rusuperobject(27,7,13,"Видел я в ночных видениях, вот, с облаками небесными шел как бы Сын человеческий, дошел до Ветхого днями и подведен был к Нему. "));
rusuperobject.biblemap.put("b27_7_14", new rusuperobject(27,7,14,"И Ему дана власть, слава и царство, чтобы все народы, племена и языки служили Ему; владычество Его - владычество вечное, которое не прейдет, и царство Его не разрушится. "));
rusuperobject.biblemap.put("b27_7_15", new rusuperobject(27,7,15,"Вострепетал дух мой во мне, Данииле, в теле моем, и видения головы моей смутили меня. "));
rusuperobject.biblemap.put("b27_7_16", new rusuperobject(27,7,16,"Я подошел к одному из предстоящих и спросил у него об истинном значении всего этого, и он стал говорить со мною, и объяснил мне смысл сказанного: "));
rusuperobject.biblemap.put("b27_7_17", new rusuperobject(27,7,17,"«эти большие звери, которых четыре, означают, что четыре царя восстанут от земли. "));
rusuperobject.biblemap.put("b27_7_18", new rusuperobject(27,7,18,"Потом примут царство святые Всевышнего и будут владеть царством вовек и вовеки веков». "));
rusuperobject.biblemap.put("b27_7_19", new rusuperobject(27,7,19,"Тогда пожелал я точного объяснения о четвертом звере, который был отличен от всех и очень страшен, с зубами железными и когтями медными, пожирал и сокрушал, а остатки попирал ногами, "));
rusuperobject.biblemap.put("b27_7_20", new rusuperobject(27,7,20,"и о десяти рогах, которые были на голове у него, и о другом, вновь вышедшем, перед которым выпали три, о том самом роге, у которого были глаза и уста, говорящие высокомерно, и который по виду стал больше прочих. "));
rusuperobject.biblemap.put("b27_7_21", new rusuperobject(27,7,21,"Я видел, как этот рог вел брань со святыми и превозмогал их, "));
rusuperobject.biblemap.put("b27_7_22", new rusuperobject(27,7,22,"доколе не пришел Ветхий днями, и суд дан был святым Всевышнего, и наступило время, чтобы царством овладели святые. "));
rusuperobject.biblemap.put("b27_7_23", new rusuperobject(27,7,23,"Об этом он сказал: зверь четвертый - четвертое царство будет на земле, отличное от всех царств, которое будет пожирать всю землю, попирать и сокрушать ее. "));
rusuperobject.biblemap.put("b27_7_24", new rusuperobject(27,7,24,"А десять рогов значат, что из этого царства восстанут десять царей, и после них восстанет иной, отличный от прежних, и уничижит трех царей, "));
rusuperobject.biblemap.put("b27_7_25", new rusuperobject(27,7,25,"и против Всевышнего будет произносить слова и угнетать святых Всевышнего; даже возмечтает отменить у них праздничные времена и закон, и они преданы будут в руку его до времени и времен и полувремени. "));
rusuperobject.biblemap.put("b27_7_26", new rusuperobject(27,7,26,"Затем воссядут судьи и отнимут у него власть губить и истреблять до конца. "));
rusuperobject.biblemap.put("b27_7_27", new rusuperobject(27,7,27,"Царство же и власть и величие царственное во всей поднебесной дано будет народу святых Всевышнего, Которого царство - царство вечное, и все властители будут служить и повиноваться Ему. "));
rusuperobject.biblemap.put("b27_7_28", new rusuperobject(27,7,28,"Здесь конец слова. Меня, Даниила, сильно смущали размышления мои, и лице мое изменилось на мне; но слово я сохранил в сердце моем. "));
rusuperobject.biblemap.put("b27_8_1", new rusuperobject(27,8,1,"В третий год царствования Валтасара царя явилось мне, Даниилу, видение после того, которое явилось мне прежде. "));
rusuperobject.biblemap.put("b27_8_2", new rusuperobject(27,8,2,"И видел я в видении, и когда видел, я был в Сузах, престольном городе в области Еламской, и видел я в видении, - как бы я был у реки Улая. "));
rusuperobject.biblemap.put("b27_8_3", new rusuperobject(27,8,3,"Поднял я глаза мои и увидел: вот, один овен стоит у реки; у него два рога, и рога высокие, но один выше другого, и высший поднялся после. "));
rusuperobject.biblemap.put("b27_8_4", new rusuperobject(27,8,4,"Видел я, как этот овен бодал к западу и к северу и к югу, и никакой зверь не мог устоять против него, и никто не мог спасти от него; он делал, что хотел, и величался. "));
rusuperobject.biblemap.put("b27_8_5", new rusuperobject(27,8,5,"Я внимательно смотрел на это, и вот, с запада шел козел по лицу всей земли, не касаясь земли; у этого козла был видный рог между его глазами. "));
rusuperobject.biblemap.put("b27_8_6", new rusuperobject(27,8,6,"Он пошел на того овна, имеющего рога, которого я видел стоящим у реки, и бросился на него в сильной ярости своей. "));
rusuperobject.biblemap.put("b27_8_7", new rusuperobject(27,8,7,"И я видел, как он, приблизившись к овну, рассвирепел на него и поразил овна, и сломил у него оба рога; и недостало силы у овна устоять против него, и он поверг его на землю и растоптал его, и не было никого, кто мог бы спасти овна от него. "));
rusuperobject.biblemap.put("b27_8_8", new rusuperobject(27,8,8,"Тогда козел чрезвычайно возвеличился; но когда он усилился, то сломился большой рог, и на место его вышли четыре, обращенные на четыре ветра небесных. "));
rusuperobject.biblemap.put("b27_8_9", new rusuperobject(27,8,9,"От одного из них вышел небольшой рог, который чрезвычайно разросся к югу и к востоку и к прекрасной стране, "));
rusuperobject.biblemap.put("b27_8_10", new rusuperobject(27,8,10,"и вознесся до воинства небесного, и низринул на землю часть сего воинства и звезд, и попрал их, "));
rusuperobject.biblemap.put("b27_8_11", new rusuperobject(27,8,11,"и даже вознесся на Вождя воинства сего, и отнята была у Него ежедневная жертва, и поругано было место святыни Его. "));
rusuperobject.biblemap.put("b27_8_12", new rusuperobject(27,8,12,"И воинство предано вместе с ежедневною жертвою за нечестие, и он, повергая истину на землю, действовал и успевал. "));
rusuperobject.biblemap.put("b27_8_13", new rusuperobject(27,8,13,"И услышал я одного святого говорящего, и сказал этот святой кому-то, вопрошавшему: «на сколько времени простирается это видение о ежедневной жертве и об опустошительном нечестии, когда святыня и воинство будут попираемы?» "));
rusuperobject.biblemap.put("b27_8_14", new rusuperobject(27,8,14,"И сказал мне: «на две тысячи триста вечеров и утр; и тогда святилище очистится». "));
rusuperobject.biblemap.put("b27_8_15", new rusuperobject(27,8,15,"И было: когда я, Даниил, увидел это видение и искал значения его, вот, стал предо мною как облик мужа. "));
rusuperobject.biblemap.put("b27_8_16", new rusuperobject(27,8,16,"И услышал я от средины Улая голос человеческий, который воззвал и сказал: «Гавриил! объясни ему это видение!» "));
rusuperobject.biblemap.put("b27_8_17", new rusuperobject(27,8,17,"И он подошел к тому месту, где я стоял, и когда он пришел, я ужаснулся и пал на лице мое; и сказал он мне: «знай, сын человеческий, что видение относится к концу времени!» "));
rusuperobject.biblemap.put("b27_8_18", new rusuperobject(27,8,18,"И когда он говорил со мною, я без чувств лежал лицем моим на земле; но он прикоснулся ко мне и поставил меня на место мое, "));
rusuperobject.biblemap.put("b27_8_19", new rusuperobject(27,8,19,"и сказал: «вот, я открываю тебе, что будет в последние дни гнева; ибо это относится к концу определенного времени. "));
rusuperobject.biblemap.put("b27_8_20", new rusuperobject(27,8,20,"Овен, которого ты видел с двумя рогами, это цари Мидийский и Персидский. "));
rusuperobject.biblemap.put("b27_8_21", new rusuperobject(27,8,21,"А козел косматый - царь Греции, а большой рог, который между глазами его, это первый ее царь; "));
rusuperobject.biblemap.put("b27_8_22", new rusuperobject(27,8,22,"он сломился, и вместо него вышли другие четыре: это - четыре царства восстанут из этого народа, но не с его силою. "));
rusuperobject.biblemap.put("b27_8_23", new rusuperobject(27,8,23,"Под конец же царства их, когда отступники исполнят меру беззаконий своих, восстанет царь наглый и искусный в коварстве; "));
rusuperobject.biblemap.put("b27_8_24", new rusuperobject(27,8,24,"и укрепится сила его, хотя и не его силою, и он будет производить удивительные опустошения и успевать и действовать и губить сильных и народ святых, "));
rusuperobject.biblemap.put("b27_8_25", new rusuperobject(27,8,25,"и при уме его и коварство будет иметь успех в руке его, и сердцем своим он превознесется, и среди мира погубит многих, и против Владыки владык восстанет, но будет сокрушен - не рукою. "));
rusuperobject.biblemap.put("b27_8_26", new rusuperobject(27,8,26,"Видение же о вечере и утре, о котором сказано, истинно; но ты сокрой это видение, ибо оно относится к отдаленным временам». "));
rusuperobject.biblemap.put("b27_8_27", new rusuperobject(27,8,27,"И я, Даниил, изнемог, и болел несколько дней; потом встал и начал заниматься царскими делами; я изумлен был видением сим и не понимал его. "));
rusuperobject.biblemap.put("b27_9_1", new rusuperobject(27,9,1,"В первый год Дария, сына Ассуирова, из рода Мидийского, который поставлен был царем над царством Халдейским, "));
rusuperobject.biblemap.put("b27_9_2", new rusuperobject(27,9,2,"в первый год царствования его я, Даниил, сообразил по книгам число лет, о котором было слово Господне к Иеремии пророку, что семьдесят лет исполнятся над опустошением Иерусалима. "));
rusuperobject.biblemap.put("b27_9_3", new rusuperobject(27,9,3,"И обратил я лице мое к Господу Богу с молитвою и молением, в посте и вретище и пепле. "));
rusuperobject.biblemap.put("b27_9_4", new rusuperobject(27,9,4,"И молился я Господу Богу моему, и исповедывался и сказал: «Молю Тебя, Господи Боже великий и дивный, хранящий завет и милость любящим Тебя и соблюдающим повеления Твои! "));
rusuperobject.biblemap.put("b27_9_5", new rusuperobject(27,9,5,"Согрешили мы, поступали беззаконно, действовали нечестиво, упорствовали и отступили от заповедей Твоих и от постановлений Твоих; "));
rusuperobject.biblemap.put("b27_9_6", new rusuperobject(27,9,6,"и не слушали рабов Твоих, пророков, которые Твоим именем говорили царям нашим, и вельможам нашим, и отцам нашим, и всему народу страны. "));
rusuperobject.biblemap.put("b27_9_7", new rusuperobject(27,9,7,"У Тебя, Господи, правда, а у нас на лицах стыд, как день сей, у каждого Иудея, у жителей Иерусалима и у всего Израиля, у ближних и дальних, во всех странах, куда Ты изгнал их за отступление их, с каким они отступили от Тебя. "));
rusuperobject.biblemap.put("b27_9_8", new rusuperobject(27,9,8,"Господи! у нас на лицах стыд, у царей наших, у князей наших и у отцов наших, потому что мы согрешили пред Тобою. "));
rusuperobject.biblemap.put("b27_9_9", new rusuperobject(27,9,9,"А у Господа Бога нашего милосердие и прощение, ибо мы возмутились против Него "));
rusuperobject.biblemap.put("b27_9_10", new rusuperobject(27,9,10,"и не слушали гласа Господа Бога нашего, чтобы поступать по законам Его, которые Он дал нам через рабов Своих, пророков. "));
rusuperobject.biblemap.put("b27_9_11", new rusuperobject(27,9,11,"И весь Израиль преступил закон Твой и отвратился, чтобы не слушать гласа Твоего; и за то излились на нас проклятие и клятва, которые написаны в законе Моисея, раба Божия: ибо мы согрешили пред Ним. "));
rusuperobject.biblemap.put("b27_9_12", new rusuperobject(27,9,12,"И Он исполнил слова Свои, которые изрек на нас и на судей наших, судивших нас, наведя на нас великое бедствие, какого не бывало под небесами и какое совершилось над Иерусалимом. "));
rusuperobject.biblemap.put("b27_9_13", new rusuperobject(27,9,13,"Как написано в законе Моисея, так все это бедствие постигло нас; но мы не умоляли Господа Бога нашего, чтобы нам обратиться от беззаконий наших и уразуметь истину Твою. "));
rusuperobject.biblemap.put("b27_9_14", new rusuperobject(27,9,14,"Наблюдал Господь это бедствие и навел его на нас: ибо праведен Господь Бог наш во всех делах Своих, которые совершает, но мы не слушали гласа Его. "));
rusuperobject.biblemap.put("b27_9_15", new rusuperobject(27,9,15,"И ныне, Господи Боже наш, изведший народ Твой из земли Египетской рукою сильною и явивший славу Твою, как день сей! согрешили мы, поступали нечестиво. "));
rusuperobject.biblemap.put("b27_9_16", new rusuperobject(27,9,16,"Господи! по всей правде Твоей да отвратится гнев Твой и негодование Твое от града Твоего, Иерусалима, от святой горы Твоей; ибо за грехи наши и беззакония отцов наших Иерусалим и народ Твой в поругании у всех, окружающих нас. "));
rusuperobject.biblemap.put("b27_9_17", new rusuperobject(27,9,17,"И ныне услыши, Боже наш, молитву раба Твоего и моление его и воззри светлым лицем Твоим на опустошенное святилище Твое, ради Тебя, Господи. "));
rusuperobject.biblemap.put("b27_9_18", new rusuperobject(27,9,18,"Приклони, Боже мой, ухо Твое и услыши, открой очи Твои и воззри на опустошения наши и на город, на котором наречено имя Твое; ибо мы повергаем моления наши пред Тобою, уповая не на праведность нашу, но на Твое великое милосердие. "));
rusuperobject.biblemap.put("b27_9_19", new rusuperobject(27,9,19,"Господи! услыши; Господи! прости; Господи! внемли и соверши, не умедли ради Тебя Самого, Боже мой, ибо Твое имя наречено на городе Твоем и на народе Твоем». "));
rusuperobject.biblemap.put("b27_9_20", new rusuperobject(27,9,20,"И когда я еще говорил и молился, и исповедывал грехи мои и грехи народа моего, Израиля, и повергал мольбу мою пред Господом Богом моим о святой горе Бога моего; "));
rusuperobject.biblemap.put("b27_9_21", new rusuperobject(27,9,21,"когда я еще продолжал молитву, муж Гавриил, которого я видел прежде в видении, быстро прилетев, коснулся меня около времени вечерней жертвы "));
rusuperobject.biblemap.put("b27_9_22", new rusuperobject(27,9,22,"и вразумлял меня, говорил со мною и сказал: «Даниил! теперь я исшел, чтобы научить тебя разумению. "));
rusuperobject.biblemap.put("b27_9_23", new rusuperobject(27,9,23,"В начале моления твоего вышло слово, и я пришел возвестить его тебе, ибо ты муж желаний; итак вникни в слово и уразумей видение. "));
rusuperobject.biblemap.put("b27_9_24", new rusuperobject(27,9,24,"Семьдесят седмин определены для народа твоего и святого города твоего, чтобы покрыто было преступление, запечатаны были грехи и заглажены беззакония, и чтобы приведена была правда вечная, и запечатаны были видение и пророк, и помазан был Святый святых. "));
rusuperobject.biblemap.put("b27_9_25", new rusuperobject(27,9,25,"Итак знай и разумей: с того времени, как выйдет повеление о восстановлении Иерусалима, до Христа Владыки семь седмин и шестьдесят две седмины; и возвратится народ и обстроятся улицы и стены, но в трудные времена. "));
rusuperobject.biblemap.put("b27_9_26", new rusuperobject(27,9,26,"И по истечении шестидесяти двух седмин предан будет смерти Христос, и не будет; а город и святилище разрушены будут народом вождя, который придет, и конец его будет как от наводнения, и до конца войны будут опустошения. "));
rusuperobject.biblemap.put("b27_9_27", new rusuperobject(27,9,27,"И утвердит завет для многих одна седмина, а в половине седмины прекратится жертва и приношение, и на крыле святилища будет мерзость запустения, и окончательная предопределенная гибель постигнет опустошителя». "));
rusuperobject.biblemap.put("b27_10_1", new rusuperobject(27,10,1,"В третий год Кира, царя Персидского, было откровение Даниилу, который назывался именем Валтасара; и истинно было это откровение и великой силы. Он понял это откровение и уразумел это видение. "));
rusuperobject.biblemap.put("b27_10_2", new rusuperobject(27,10,2,"В эти дни я, Даниил, был в сетовании три седмицы дней. "));
rusuperobject.biblemap.put("b27_10_3", new rusuperobject(27,10,3,"Вкусного хлеба я не ел; мясо и вино не входило в уста мои, и мастями я не умащал себя до исполнения трех седмиц дней. "));
rusuperobject.biblemap.put("b27_10_4", new rusuperobject(27,10,4,"А в двадцать четвертый день первого месяца был я на берегу большой реки Тигра, "));
rusuperobject.biblemap.put("b27_10_5", new rusuperobject(27,10,5,"и поднял глаза мои, и увидел: вот один муж, облеченный в льняную одежду, и чресла его опоясаны золотом из Уфаза. "));
rusuperobject.biblemap.put("b27_10_6", new rusuperobject(27,10,6,"Тело его - как топаз, лице его - как вид молнии; очи его - как горящие светильники, руки его и ноги его по виду - как блестящая медь, и глас речей его - как голос множества людей. "));
rusuperobject.biblemap.put("b27_10_7", new rusuperobject(27,10,7,"И только один я, Даниил, видел это видение, а бывшие со мною люди не видели этого видения; но сильный страх напал на них и они убежали, чтобы скрыться. "));
rusuperobject.biblemap.put("b27_10_8", new rusuperobject(27,10,8,"И остался я один и смотрел на это великое видение, но во мне не осталось крепости, и вид лица моего чрезвычайно изменился, не стало во мне бодрости. "));
rusuperobject.biblemap.put("b27_10_9", new rusuperobject(27,10,9,"И услышал я глас слов его; и как только услышал глас слов его, в оцепенении пал я на лице мое и лежал лицем к земле. "));
rusuperobject.biblemap.put("b27_10_10", new rusuperobject(27,10,10,"Но вот, коснулась меня рука и поставила меня на колени мои и на длани рук моих. "));
rusuperobject.biblemap.put("b27_10_11", new rusuperobject(27,10,11,"И сказал он мне: «Даниил, муж желаний! вникни в слова, которые я скажу тебе, и стань прямо на ноги твои; ибо к тебе я послан ныне». Когда он сказал мне эти слова, я встал с трепетом. "));
rusuperobject.biblemap.put("b27_10_12", new rusuperobject(27,10,12,"Но он сказал мне: «не бойся, Даниил; с первого дня, как ты расположил сердце твое, чтобы достигнуть разумения и смирить тебя пред Богом твоим, слова твои услышаны, и я пришел бы по словам твоим. "));
rusuperobject.biblemap.put("b27_10_13", new rusuperobject(27,10,13,"Но князь царства Персидского стоял против меня двадцать один день; но вот, Михаил, один из первых князей, пришел помочь мне, и я остался там при царях Персидских. "));
rusuperobject.biblemap.put("b27_10_14", new rusuperobject(27,10,14,"А теперь я пришел возвестить тебе, что будет с народом твоим в последние времена, так как видение относится к отдаленным дням». "));
rusuperobject.biblemap.put("b27_10_15", new rusuperobject(27,10,15,"Когда он говорил мне такие слова, я припал лицем моим к земле и онемел. "));
rusuperobject.biblemap.put("b27_10_16", new rusuperobject(27,10,16,"Но вот, некто, по виду похожий на сынов человеческих, коснулся уст моих, и я открыл уста мои, стал говорить и сказал стоящему передо мною: «господин мой! от этого видения внутренности мои повернулись во мне, и не стало во мне силы. "));
rusuperobject.biblemap.put("b27_10_17", new rusuperobject(27,10,17,"И как может говорить раб такого господина моего с таким господином моим? ибо во мне нет силы, и дыхание замерло во мне». "));
rusuperobject.biblemap.put("b27_10_18", new rusuperobject(27,10,18,"Тогда снова прикоснулся ко мне тот человеческий облик и укрепил меня "));
rusuperobject.biblemap.put("b27_10_19", new rusuperobject(27,10,19,"и сказал: «не бойся, муж желаний! мир тебе; мужайся, мужайся!» И когда он говорил со мною, я укрепился и сказал: «говори, господин мой; ибо ты укрепил меня». "));
rusuperobject.biblemap.put("b27_10_20", new rusuperobject(27,10,20,"И он сказал: «знаешь ли, для чего я пришел к тебе? Теперь я возвращусь, чтобы бороться с князем Персидским; а когда я выйду, то вот, придет князь Греции. "));
rusuperobject.biblemap.put("b27_10_21", new rusuperobject(27,10,21,"Впрочем я возвещу тебе, что начертано в истинном писании; и нет никого, кто поддерживал бы меня в том, кроме Михаила, князя вашего. "));
rusuperobject.biblemap.put("b27_11_1", new rusuperobject(27,11,1,"Итак я с первого года Дария Мидянина стал ему подпорою и подкреплением. "));
rusuperobject.biblemap.put("b27_11_2", new rusuperobject(27,11,2,"Теперь возвещу тебе истину: вот, еще три царя восстанут в Персии; потом четвертый превзойдет всех великим богатством, и когда усилится богатством своим, то поднимет всех против царства Греческого. "));
rusuperobject.biblemap.put("b27_11_3", new rusuperobject(27,11,3,"И восстанет царь могущественный, который будет владычествовать с великою властью, и будет действовать по своей воле. "));
rusuperobject.biblemap.put("b27_11_4", new rusuperobject(27,11,4,"Но когда он восстанет, царство его разрушится и разделится по четырем ветрам небесным, и не к его потомкам перейдет, и не с тою властью, с какою он владычествовал; ибо раздробится царство его и достанется другим, кроме этих. "));
rusuperobject.biblemap.put("b27_11_5", new rusuperobject(27,11,5,"И усилится южный царь и один из князей его пересилит его и будет владычествовать, и велико будет владычество его. "));
rusuperobject.biblemap.put("b27_11_6", new rusuperobject(27,11,6,"Но через несколько лет они сблизятся, и дочь южного царя придет к царю северному, чтобы установить правильные отношения между ними; но она не удержит силы в руках своих, не устоит и род ее, но преданы будут как она, так и сопровождавшие ее, и рожденный ею, и помогавшие ей в те времена. "));
rusuperobject.biblemap.put("b27_11_7", new rusuperobject(27,11,7,"Но восстанет отрасль от корня ее, придет к войску и войдет в укрепления царя северного, и будет действовать в них, и усилится. "));
rusuperobject.biblemap.put("b27_11_8", new rusuperobject(27,11,8,"Даже и богов их, истуканы их с драгоценными сосудами их, серебряными и золотыми, увезет в плен в Египет и на несколько лет будет стоять выше царя северного. "));
rusuperobject.biblemap.put("b27_11_9", new rusuperobject(27,11,9,"Хотя этот и сделает нашествие на царство южного царя, но возвратится в свою землю. "));
rusuperobject.biblemap.put("b27_11_10", new rusuperobject(27,11,10,"Потом вооружатся сыновья его и соберут многочисленное войско, и один из них быстро пойдет, наводнит и пройдет, и потом, возвращаясь, будет сражаться с ним до укреплений его. "));
rusuperobject.biblemap.put("b27_11_11", new rusuperobject(27,11,11,"И раздражится южный царь, и выступит, сразится с ним, с царем северным, и выставит большое войско, и предано будет войско в руки его. "));
rusuperobject.biblemap.put("b27_11_12", new rusuperobject(27,11,12,"И ободрится войско, и сердце царя вознесется; он низложит многие тысячи, но от этого не будет сильнее. "));
rusuperobject.biblemap.put("b27_11_13", new rusuperobject(27,11,13,"Ибо царь северный возвратится и выставит войско больше прежнего, и через несколько лет быстро придет с огромным войском и большим богатством. "));
rusuperobject.biblemap.put("b27_11_14", new rusuperobject(27,11,14,"В те времена многие восстанут против южного царя, и мятежные из сынов твоего народа поднимутся, чтобы исполнилось видение, и падут. "));
rusuperobject.biblemap.put("b27_11_15", new rusuperobject(27,11,15,"И придет царь северный, устроит вал и овладеет укрепленным городом, и не устоят мышцы юга, ни отборное войско его; недостанет силы противостоять. "));
rusuperobject.biblemap.put("b27_11_16", new rusuperobject(27,11,16,"И кто выйдет к нему, будет действовать по воле его, и никто не устоит перед ним; и на славной земле поставит стан свой, и она пострадает от руки его. "));
rusuperobject.biblemap.put("b27_11_17", new rusuperobject(27,11,17,"И вознамерится войти со всеми силами царства своего, и праведные с ним, и совершит это; и дочь жен отдаст ему, на погибель ее, но этот замысел не состоится, и ему не будет пользы из того. "));
rusuperobject.biblemap.put("b27_11_18", new rusuperobject(27,11,18,"Потом обратит лице свое к островам и овладеет многими; но некий вождь прекратит нанесенный им позор и даже свой позор обратит на него. "));
rusuperobject.biblemap.put("b27_11_19", new rusuperobject(27,11,19,"Затем он обратит лице свое на крепости своей земли; но споткнется, падет и не станет его. "));
rusuperobject.biblemap.put("b27_11_20", new rusuperobject(27,11,20,"На место его восстанет некий, который пошлет сборщика податей, пройти по царству славы; но и он после немногих дней погибнет, и не от возмущения и не в сражении. "));
rusuperobject.biblemap.put("b27_11_21", new rusuperobject(27,11,21,"И восстанет на место его презренный, и не воздадут ему царских почестей, но он придет без шума и лестью овладеет царством. "));
rusuperobject.biblemap.put("b27_11_22", new rusuperobject(27,11,22,"И всепотопляющие полчища будут потоплены и сокрушены им, даже и сам вождь завета. "));
rusuperobject.biblemap.put("b27_11_23", new rusuperobject(27,11,23,"Ибо после того, как он вступит в союз с ним, он будет действовать обманом, и взойдет, и одержит верх с малым народом. "));
rusuperobject.biblemap.put("b27_11_24", new rusuperobject(27,11,24,"Он войдет в мирные и плодоносные страны, и совершит то, чего не делали отцы его и отцы отцов его; добычу, награбленное имущество и богатство будет расточать своим и на крепости будет иметь замыслы свои, но только до времени. "));
rusuperobject.biblemap.put("b27_11_25", new rusuperobject(27,11,25,"Потом возбудит силы свои и дух свой с многочисленным войском против царя южного, и южный царь выступит на войну с великим и еще более сильным войском, но не устоит, потому что будет против него коварство. "));
rusuperobject.biblemap.put("b27_11_26", new rusuperobject(27,11,26,"Даже участники трапезы его погубят его, и войско его разольется, и падет много убитых. "));
rusuperobject.biblemap.put("b27_11_27", new rusuperobject(27,11,27,"У обоих царей сих на сердце будет коварство, и за одним столом будут говорить ложь, но успеха не будет, потому что конец еще отложен до времени. "));
rusuperobject.biblemap.put("b27_11_28", new rusuperobject(27,11,28,"И отправится он в землю свою с великим богатством и враждебным намерением против святого завета, и он исполнит его, и возвратится в свою землю. "));
rusuperobject.biblemap.put("b27_11_29", new rusuperobject(27,11,29,"В назначенное время опять пойдет он на юг; но последний поход не такой будет, как прежний, "));
rusuperobject.biblemap.put("b27_11_30", new rusuperobject(27,11,30,"ибо в одно время с ним придут корабли Киттимские; и он упадет духом, и возвратится, и озлобится на святый завет, и исполнит свое намерение, и опять войдет в соглашение с отступниками от святого завета. "));
rusuperobject.biblemap.put("b27_11_31", new rusuperobject(27,11,31,"И поставлена будет им часть войска, которая осквернит святилище могущества, и прекратит ежедневную жертву, и поставит мерзость запустения. "));
rusuperobject.biblemap.put("b27_11_32", new rusuperobject(27,11,32,"Поступающих нечестиво против завета он привлечет к себе лестью; но люди, чтущие своего Бога, усилятся и будут действовать. "));
rusuperobject.biblemap.put("b27_11_33", new rusuperobject(27,11,33,"И разумные из народа вразумят многих, хотя будут несколько времени страдать от меча и огня, от плена и грабежа; "));
rusuperobject.biblemap.put("b27_11_34", new rusuperobject(27,11,34,"и во время страдания своего будут иметь некоторую помощь, и многие присоединятся к ним, но притворно. "));
rusuperobject.biblemap.put("b27_11_35", new rusuperobject(27,11,35,"Пострадают некоторые и из разумных для испытания их, очищения и для убеления к последнему времени; ибо есть еще время до срока. "));
rusuperobject.biblemap.put("b27_11_36", new rusuperobject(27,11,36,"И будет поступать царь тот по своему произволу, и вознесется и возвеличится выше всякого божества, и о Боге богов станет говорить хульное и будет иметь успех, доколе не совершится гнев: ибо, что предопределено, то исполнится. "));
rusuperobject.biblemap.put("b27_11_37", new rusuperobject(27,11,37,"И о богах отцов своих он не помыслит, и ни желания жен, ни даже божества никакого не уважит; ибо возвеличит себя выше всех. "));
rusuperobject.biblemap.put("b27_11_38", new rusuperobject(27,11,38,"Но богу крепостей на месте его будет он воздавать честь, и этого бога, которого не знали отцы его, он будет чествовать золотом и серебром, и дорогими камнями, и разными драгоценностями, "));
rusuperobject.biblemap.put("b27_11_39", new rusuperobject(27,11,39,"и устроит твердую крепость с чужим богом: которые признают его, тем увеличит почести и даст власть над многими, и землю раздаст в награду. "));
rusuperobject.biblemap.put("b27_11_40", new rusuperobject(27,11,40,"Под конец же времени сразится с ним царь южный, и царь северный устремится как буря на него с колесницами, всадниками и многочисленными кораблями, и нападет на области, наводнит их, и пройдет через них. "));
rusuperobject.biblemap.put("b27_11_41", new rusuperobject(27,11,41,"И войдет он в прекраснейшую из земель, и многие области пострадают и спасутся от руки его только Едом, Моав и большая часть сынов Аммоновых. "));
rusuperobject.biblemap.put("b27_11_42", new rusuperobject(27,11,42,"И прострет руку свою на разные страны; не спасется и земля Египетская. "));
rusuperobject.biblemap.put("b27_11_43", new rusuperobject(27,11,43,"И завладеет он сокровищами золота и серебра и разными драгоценностями Египта; Ливийцы и Ефиопляне последуют за ним. "));
rusuperobject.biblemap.put("b27_11_44", new rusuperobject(27,11,44,"Но слухи с востока и севера встревожат его, и выйдет он в величайшей ярости, чтобы истреблять и губить многих, "));
rusuperobject.biblemap.put("b27_11_45", new rusuperobject(27,11,45,"и раскинет он царские шатры свои между морем и горою преславного святилища; но придет к своему концу, и никто не поможет ему. "));
rusuperobject.biblemap.put("b27_12_1", new rusuperobject(27,12,1,"И восстанет в то время Михаил, князь великий, стоящий за сынов народа твоего; и наступит время тяжкое, какого не бывало с тех пор, как существуют люди, до сего времени; но спасутся в это время из народа твоего все, которые найдены будут записанными в книге. "));
rusuperobject.biblemap.put("b27_12_2", new rusuperobject(27,12,2,"И многие из спящих в прахе земли пробудятся, одни для жизни вечной, другие на вечное поругание и посрамление. "));
rusuperobject.biblemap.put("b27_12_3", new rusuperobject(27,12,3,"И разумные будут сиять, как светила на тверди, и обратившие многих к правде - как звезды, вовеки, навсегда. "));
rusuperobject.biblemap.put("b27_12_4", new rusuperobject(27,12,4,"А ты, Даниил, сокрой слова сии и запечатай книгу сию до последнего времени; многие прочитают ее, и умножится ведение». "));
rusuperobject.biblemap.put("b27_12_5", new rusuperobject(27,12,5,"Тогда я, Даниил, посмотрел, и вот, стоят двое других, один на этом берегу реки, другой на том берегу реки. "));
rusuperobject.biblemap.put("b27_12_6", new rusuperobject(27,12,6,"И один сказал мужу в льняной одежде, который стоял над водами реки: «когда будет конец этих чудных происшествий?» "));
rusuperobject.biblemap.put("b27_12_7", new rusuperobject(27,12,7,"И слышал я, как муж в льняной одежде, находившийся над водами реки, подняв правую и левую руку к небу, клялся Живущим вовеки, что к концу времени и времен и полувремени, и по совершенном низложении силы народа святого, все это совершится. "));
rusuperobject.biblemap.put("b27_12_8", new rusuperobject(27,12,8,"Я слышал это, но не понял, и потому сказал: «господин мой! что же после этого будет?» "));
rusuperobject.biblemap.put("b27_12_9", new rusuperobject(27,12,9,"И отвечал он: «иди, Даниил; ибо сокрыты и запечатаны слова сии до последнего времени. "));
rusuperobject.biblemap.put("b27_12_10", new rusuperobject(27,12,10,"Многие очистятся, убелятся и переплавлены будут в искушении; нечестивые же будут поступать нечестиво, и не уразумеет сего никто из нечестивых, а мудрые уразумеют. "));
rusuperobject.biblemap.put("b27_12_11", new rusuperobject(27,12,11,"Со времени прекращения ежедневной жертвы и поставления мерзости запустения пройдет тысяча двести девяносто дней. "));
rusuperobject.biblemap.put("b27_12_12", new rusuperobject(27,12,12,"Блажен, кто ожидает и достигнет тысячи трехсот тридцати пяти дней. "));
rusuperobject.biblemap.put("b27_12_13", new rusuperobject(27,12,13,"А ты иди к твоему концу и упокоишься, и восстанешь для получения твоего жребия в конце дней». "));
rusuperobject.biblemap.put("b27_13_1", new rusuperobject(27,13,1,"В Вавилоне жил муж, по имени Иоаким. "));
rusuperobject.biblemap.put("b27_13_2", new rusuperobject(27,13,2,"И взял он жену, по имени Сусанну, дочь Хелкия, очень красивую и богобоязненную. "));
rusuperobject.biblemap.put("b27_13_3", new rusuperobject(27,13,3,"Родители ее были праведные и научили дочь свою закону Моисееву. "));
rusuperobject.biblemap.put("b27_13_4", new rusuperobject(27,13,4,"Иоаким был очень богат, и был у него сад близ дома его; и сходились к нему Иудеи, потому что он был почетнейший из всех. "));
rusuperobject.biblemap.put("b27_13_5", new rusuperobject(27,13,5,"И были поставлены два старца из народа судьями в том году, о которых Господь сказал, что беззаконие вышло из Вавилона от старейшин-судей, которые казались управляющими народом. "));
rusuperobject.biblemap.put("b27_13_6", new rusuperobject(27,13,6,"Они постоянно бывали в доме Иоакима, и к ним приходили все, имевшие спорные дела. "));
rusuperobject.biblemap.put("b27_13_7", new rusuperobject(27,13,7,"Когда народ уходил около полудня, Сусанна входила в сад своего мужа для прогулки. "));
rusuperobject.biblemap.put("b27_13_8", new rusuperobject(27,13,8,"И видели ее оба старейшины всякий день приходящую и прогуливающуюся, и в них родилась похоть к ней, "));
rusuperobject.biblemap.put("b27_13_9", new rusuperobject(27,13,9,"и извратили ум свой, и уклонили глаза свои, чтобы не смотреть на небо и не вспоминать о праведных судах. "));
rusuperobject.biblemap.put("b27_13_10", new rusuperobject(27,13,10,"Оба они были уязвлены похотью к ней, но не открывали друг другу боли своей, "));
rusuperobject.biblemap.put("b27_13_11", new rusuperobject(27,13,11,"потому что стыдились объявить о вожделении своем, что хотели совокупиться с нею. "));
rusuperobject.biblemap.put("b27_13_12", new rusuperobject(27,13,12,"И они прилежно сторожили каждый день, чтобы видеть ее, и говорили друг другу: "));
rusuperobject.biblemap.put("b27_13_13", new rusuperobject(27,13,13,"пойдем домой, потому что час обеда», - и, выйдя, расходились друг от друга, "));
rusuperobject.biblemap.put("b27_13_14", new rusuperobject(27,13,14,"и, возвратившись, приходили на то же самое место, и когда допытывались друг у друга о причине того, признались в похоти своей, и тогда вместе назначили время, когда могли бы найти ее одну. "));
rusuperobject.biblemap.put("b27_13_15", new rusuperobject(27,13,15,"И было, когда они выжидали удобного дня, Сусанна вошла, как вчера и третьего дня, с двумя только служанками и захотела мыться в саду, потому что было жарко. "));
rusuperobject.biblemap.put("b27_13_16", new rusuperobject(27,13,16,"И не было там никого, кроме двух старейшин, которые спрятались и сторожили ее. "));
rusuperobject.biblemap.put("b27_13_17", new rusuperobject(27,13,17,"И сказала она служанкам: принесите мне масла и мыла, и заприте двери сада, чтобы мне помыться. "));
rusuperobject.biblemap.put("b27_13_18", new rusuperobject(27,13,18,"Они так и сделали, как она сказала: заперли двери сада и вышли боковыми дверями, чтобы принести, что приказано было им, и не видали старейшин, потому что они спрятались. "));
rusuperobject.biblemap.put("b27_13_19", new rusuperobject(27,13,19,"И вот, когда служанки вышли, встали оба старейшины, и прибежали к ней, и сказали: "));
rusuperobject.biblemap.put("b27_13_20", new rusuperobject(27,13,20,"Вот, двери сада заперты и никто нас не видит, и мы имеем похотение к тебе, поэтому согласись с нами и побудь с нами. "));
rusuperobject.biblemap.put("b27_13_21", new rusuperobject(27,13,21,"Если же не так, то мы будем свидетельствовать против тебя, что с тобою был юноша, и ты поэтому отослала от себя служанок твоих. "));
rusuperobject.biblemap.put("b27_13_22", new rusuperobject(27,13,22,"Тогда застонала Сусанна и сказала: тесно мне отовсюду; ибо, если я сделаю это, смерть мне, а если не сделаю, то не избегну от рук ваших. "));
rusuperobject.biblemap.put("b27_13_23", new rusuperobject(27,13,23,"Лучше для меня не сделать этого и впасть в руки ваши, нежели согрешить пред Господом. "));
rusuperobject.biblemap.put("b27_13_24", new rusuperobject(27,13,24,"И закричала Сусанна громким голосом; закричали также и оба старейшины против нее, "));
rusuperobject.biblemap.put("b27_13_25", new rusuperobject(27,13,25,"и один побежал и отворил двери сада. "));
rusuperobject.biblemap.put("b27_13_26", new rusuperobject(27,13,26,"Когда же находившиеся в доме услышали крик в саду, вскочили боковыми дверями, чтобы видеть, что случилось с нею. "));
rusuperobject.biblemap.put("b27_13_27", new rusuperobject(27,13,27,"И когда старейшины сказали слова свои, слуги ее чрезвычайно были пристыжены, потому что никогда ничего такого о Сусанне говорено не было. "));
rusuperobject.biblemap.put("b27_13_28", new rusuperobject(27,13,28,"И было на другой день, когда собрался народ к Иоакиму, мужу ее, пришли и оба старейшины, полные беззаконного умысла против Сусанны, чтобы предать ее смерти. "));
rusuperobject.biblemap.put("b27_13_29", new rusuperobject(27,13,29,"И сказали они перед народом: пошлите за Сусанною, дочерью Хелкия, женою Иоакима. И послали. "));
rusuperobject.biblemap.put("b27_13_30", new rusuperobject(27,13,30,"И пришла она, и родители ее, и дети ее, и все родственники ее. "));
rusuperobject.biblemap.put("b27_13_31", new rusuperobject(27,13,31,"Сусанна была очень нежна и красива лицем, "));
rusuperobject.biblemap.put("b27_13_32", new rusuperobject(27,13,32,"и эти беззаконники приказали открыть лице ее, так как оно было закрыто, чтобы насытиться красотою ее. "));
rusuperobject.biblemap.put("b27_13_33", new rusuperobject(27,13,33,"Родственники же и все, которые смотрели на нее, плакали. "));
rusuperobject.biblemap.put("b27_13_34", new rusuperobject(27,13,34,"А оба старейшины, встав посреди народа, положили руки на голову ее. "));
rusuperobject.biblemap.put("b27_13_35", new rusuperobject(27,13,35,"Она же в слезах смотрела на небо, ибо сердце ее уповало на Господа. "));
rusuperobject.biblemap.put("b27_13_36", new rusuperobject(27,13,36,"И сказали старейшины: когда мы ходили по саду одни, вошла эта с двумя служанками и затворила двери сада, и отослала служанок; "));
rusuperobject.biblemap.put("b27_13_37", new rusuperobject(27,13,37,"и пришел к ней юноша, который скрывался там, и лег с нею. "));
rusuperobject.biblemap.put("b27_13_38", new rusuperobject(27,13,38,"Мы находясь в углу сада и видя такое беззаконие, побежали на них, "));
rusuperobject.biblemap.put("b27_13_39", new rusuperobject(27,13,39,"и увидели их совокупляющимися, и того не могли удержать, потому что он был сильнее нас и, отворив двери, выскочил. "));
rusuperobject.biblemap.put("b27_13_40", new rusuperobject(27,13,40,"Но эту мы схватили и допрашивали: кто был этот юноша? но она не хотела объявить нам. Об этом мы свидетельствуем. "));
rusuperobject.biblemap.put("b27_13_41", new rusuperobject(27,13,41,"И поверило им собрание, как старейшинам народа и судьям, и осудили ее на смерть. "));
rusuperobject.biblemap.put("b27_13_42", new rusuperobject(27,13,42,"Возопила Сусанна громким голосом и сказала: Боже вечный, ведающий сокровенное и знающий все прежде бытия его! "));
rusuperobject.biblemap.put("b27_13_43", new rusuperobject(27,13,43,"Ты знаешь, что они ложно свидетельствовали против меня, и вот, я умираю, не сделав ничего, что эти люди злостно выдумали на меня. "));
rusuperobject.biblemap.put("b27_13_44", new rusuperobject(27,13,44,"И услышал Господь голос ее. "));
rusuperobject.biblemap.put("b27_13_45", new rusuperobject(27,13,45,"И когда она ведена была на смерть, возбудил Бог святой дух молодого юноши, но имени Даниила, "));
rusuperobject.biblemap.put("b27_13_46", new rusuperobject(27,13,46,"и он закричал громким голосом: чист я от крови ее! "));
rusuperobject.biblemap.put("b27_13_47", new rusuperobject(27,13,47,"Тогда обратился к нему весь народ и сказал: что это за слово, которое ты сказал? "));
rusuperobject.biblemap.put("b27_13_48", new rusuperobject(27,13,48,"Тогда он, став посреди них, сказал: так ли вы неразумны, сыны Израиля, что, не исследовав и не узнав истины, осудили дочь Израиля? "));
rusuperobject.biblemap.put("b27_13_49", new rusuperobject(27,13,49,"Возвратитесь в суд, ибо эти ложно против нее засвидетельствовали. "));
rusuperobject.biblemap.put("b27_13_50", new rusuperobject(27,13,50,"И тотчас весь народ возвратился, и сказали ему старейшины: садись посреди нас и объяви нам, потому что Бог дал тебе старейшинство. "));
rusuperobject.biblemap.put("b27_13_51", new rusuperobject(27,13,51,"И сказал им Даниил: отделите их друг от друга подальше, и я допрошу их. "));
rusuperobject.biblemap.put("b27_13_52", new rusuperobject(27,13,52,"Когда же они отделены были один от другого, призвал одного из них и сказал ему: состарившийся в злых днях! ныне обнаружились грехи твои, которые те делал прежде, "));
rusuperobject.biblemap.put("b27_13_53", new rusuperobject(27,13,53,"производя суды неправедные, осуждая невинных и оправдывая виновных, тогда как Господь говорит: «невинного и правого не умерщвляй». "));
rusuperobject.biblemap.put("b27_13_54", new rusuperobject(27,13,54,"Итак, если ты сию видел, скажи, под каким деревом видел ты их разговаривающими друг с другом? Он сказал: под мастиковым. "));
rusuperobject.biblemap.put("b27_13_55", new rusuperobject(27,13,55,"Даниил сказал: точно, солгал ты на твою голову; ибо вот, Ангел Божий, приняв решение от Бога, рассечет тебя пополам. "));
rusuperobject.biblemap.put("b27_13_56", new rusuperobject(27,13,56,"Удалив его, он приказал привести другого и сказал ему: племя Ханаана, а не Иуды! красота прельстила тебя, и похоть развратила сердце твое. "));
rusuperobject.biblemap.put("b27_13_57", new rusuperobject(27,13,57,"Так поступали вы с дочерями Израиля, и они из страха имели общение с вами; но дочь Иуды не потерпела беззакония вашего. "));
rusuperobject.biblemap.put("b27_13_58", new rusuperobject(27,13,58,"Итак скажи мне: под каким деревом ты застал их разговаривающими между собою? Он сказал: под зеленым дубом. "));
rusuperobject.biblemap.put("b27_13_59", new rusuperobject(27,13,59,"Даниил сказал ему: точно, солгал ты на твою голову; ибо Ангел Божий с мечом ждет, чтобы рассечь тебя пополам, чтобы истребить вас. "));
rusuperobject.biblemap.put("b27_13_60", new rusuperobject(27,13,60,"Тогда все собрание закричало громким голосом, и благословили Бога, спасающего надеющихся на Него, "));
rusuperobject.biblemap.put("b27_13_61", new rusuperobject(27,13,61,"и восстали на обоих старейшин, потому что Даниил их устами обличил их, что они ложно свидетельствовали; "));
rusuperobject.biblemap.put("b27_13_62", new rusuperobject(27,13,62,"и поступили с ними так, как они злоумыслили против ближнего, по закону Моисееву, и умертвили их; и спасена была в тот день кровь невинная. "));
rusuperobject.biblemap.put("b27_13_63", new rusuperobject(27,13,63,"Хелкия же и жена его прославили Бога за дочь свою Сусанну с Иоакимом, мужем ее, и со всеми родственниками, потому что не найдено было в ней постыдного дела. "));
rusuperobject.biblemap.put("b27_13_64", new rusuperobject(27,13,64,"И Даниил стал велик перед народом с того дня и потом. "));
rusuperobject.biblemap.put("b27_14_1", new rusuperobject(27,14,1,"Царь Астиаг приложился к отцам своим, и Кир, Персиянин, принял царство его. "));
rusuperobject.biblemap.put("b27_14_2", new rusuperobject(27,14,2,"И Даниил жил вместе с царем и был славнее всех друзей его. "));
rusuperobject.biblemap.put("b27_14_3", new rusuperobject(27,14,3,"Был у Вавилонян идол, по имени Вил, и издерживали на него каждый день двадцать больших мер пшеничной муки, сорок овец и вина шесть мер. "));
rusuperobject.biblemap.put("b27_14_4", new rusuperobject(27,14,4,"Царь чтил его и ходил каждый день поклоняться ему; Даниил же поклонялся Богу своему. И сказал ему царь: почему ты не поклоняешься Вилу? "));
rusuperobject.biblemap.put("b27_14_5", new rusuperobject(27,14,5,"Он отвечал: потому что я не поклоняюсь идолам, сделанным руками, но поклоняюсь живому Богу, сотворившему небо и землю и владычествующему над всякою плотью. "));
rusuperobject.biblemap.put("b27_14_6", new rusuperobject(27,14,6,"Царь сказал: не думаешь ли ты, что Вил неживой бог? не видишь ли, сколько он ест и пьет каждый день? "));
rusuperobject.biblemap.put("b27_14_7", new rusuperobject(27,14,7,"Даниил, улыбнувшись, сказал: не обманывайся, царь; ибо он внутри глина, а снаружи медь, и никогда ни ел, ни пил. "));
rusuperobject.biblemap.put("b27_14_8", new rusuperobject(27,14,8,"Тогда царь, разгневавшись, призвал жрецов своих и сказал им: если вы не скажете мне, кто съедает все это, то умрете. "));
rusuperobject.biblemap.put("b27_14_9", new rusuperobject(27,14,9,"Если же вы докажете мне, что съедает это Вил, то умрет Даниил, потому что произнес хулу на Вила. И сказал Даниил царю: да будет по слову твоему. "));
rusuperobject.biblemap.put("b27_14_10", new rusuperobject(27,14,10,"Жрецов Вила было семьдесят, кроме жен и детей. "));
rusuperobject.biblemap.put("b27_14_11", new rusuperobject(27,14,11,"И пришел царь с Даниилом в храм Вила, и сказали жрецы Вила: вот, мы выйдем вон, а ты, царь, поставь пищу и, налив вина, запри двери и запечатай перстнем твоим. "));
rusuperobject.biblemap.put("b27_14_12", new rusuperobject(27,14,12,"И если завтра ты придешь и не найдешь, что все съедено Вилом, мы умрем, или Даниил, который солгал на нас. "));
rusuperobject.biblemap.put("b27_14_13", new rusuperobject(27,14,13,"Они не обращали на это внимания, потому что под столом сделали потаенный вход, и им всегда входили, и съедали это. "));
rusuperobject.biblemap.put("b27_14_14", new rusuperobject(27,14,14,"Когда они вышли, царь поставил пищу перед Вилом, а Даниил приказал слугам своим, и они принесли пепел, и посыпали весь храм в присутствии одного царя, и, выйдя, заперли двери, и запечатали царским перстнем, и отошли. "));
rusuperobject.biblemap.put("b27_14_15", new rusuperobject(27,14,15,"Жрецы же, по обычаю своему, пришли ночью с женами и детьми своими, и все съели и выпили. "));
rusuperobject.biblemap.put("b27_14_16", new rusuperobject(27,14,16,"На другой день царь встал рано и Даниил с ним, "));
rusuperobject.biblemap.put("b27_14_17", new rusuperobject(27,14,17,"и сказал: целы ли печати, Даниил? Он сказал: целы, царь. "));
rusuperobject.biblemap.put("b27_14_18", new rusuperobject(27,14,18,"И как скоро отворены были двери, царь, взглянув на стол, воскликнул громким голосом: велик ты, Вил, и нет никакого обмана в тебе! "));
rusuperobject.biblemap.put("b27_14_19", new rusuperobject(27,14,19,"Даниил, улыбнувшись, удержал царя, чтобы он не входил внутрь, и сказал: посмотри на пол и заметь, чьи это следы. "));
rusuperobject.biblemap.put("b27_14_20", new rusuperobject(27,14,20,"Царь сказал: вижу следы мужчин, женщин и детей. "));
rusuperobject.biblemap.put("b27_14_21", new rusuperobject(27,14,21,"И, разгневавшись, царь приказал схватить жрецов, жен их и детей и они показали потаенные двери, которыми они входили и съедали, что было на столе. "));
rusuperobject.biblemap.put("b27_14_22", new rusuperobject(27,14,22,"Тогда царь повелел умертвить их и отдал Вила Даниилу, и он разрушил его и храм его. "));
rusuperobject.biblemap.put("b27_14_23", new rusuperobject(27,14,23,"Был на том месте большой дракон, и Вавилоняне чтили его. "));
rusuperobject.biblemap.put("b27_14_24", new rusuperobject(27,14,24,"И сказал царь Даниилу: не скажешь ли и об этом, что он медь? вот, он живой, и ест и пьет; ты не можешь сказать, что этот бог неживой; итак поклонись ему. "));
rusuperobject.biblemap.put("b27_14_25", new rusuperobject(27,14,25,"Даниил сказал: Господу Богу моему поклоняюсь, потому что Он Бог живой. "));
rusuperobject.biblemap.put("b27_14_26", new rusuperobject(27,14,26,"Но ты, царь, дай мне позволение, и я умерщвлю дракона без меча и жезла. Царь сказал: даю тебе. "));
rusuperobject.biblemap.put("b27_14_27", new rusuperobject(27,14,27,"Тогда Даниил взял смолы, жира и волос, сварил это вместе и, сделав из этого ком, бросил его в пасть дракону, и дракон расселся. И сказал Даниил: вот ваши святыни! "));
rusuperobject.biblemap.put("b27_14_28", new rusuperobject(27,14,28,"Когда же Вавилоняне услышали о том, сильно вознегодовали и восстали против царя, и сказали: царь сделался Иудеем, Вила разрушил и убил дракона, и предал смерти жрецов, "));
rusuperobject.biblemap.put("b27_14_29", new rusuperobject(27,14,29,"и, придя к царю, сказали: предай нам Даниила, иначе мы умертвим тебя и дом твой. "));
rusuperobject.biblemap.put("b27_14_30", new rusuperobject(27,14,30,"И когда царь увидел, что они сильно настаивают, принужден был предать им Даниила, "));
rusuperobject.biblemap.put("b27_14_31", new rusuperobject(27,14,31,"они же бросили его в ров львиный, и он пробыл там шесть дней. "));
rusuperobject.biblemap.put("b27_14_32", new rusuperobject(27,14,32,"Во рве было семь львов, и давалось им каждый день по два тела и по две овцы; в это время им не давали их, чтобы они съели Даниила. "));
rusuperobject.biblemap.put("b27_14_33", new rusuperobject(27,14,33,"Был в Иудее пророк Аввакум, который, сварив похлебку и накрошив хлеба в блюдо, шел на поле, чтобы отнести это жрецам. "));
rusuperobject.biblemap.put("b27_14_34", new rusuperobject(27,14,34,"Но Ангел Господень сказал Аввакуму: отнеси этот обед, который у тебя, в Вавилон к Даниилу, в ров львиный. "));
rusuperobject.biblemap.put("b27_14_35", new rusuperobject(27,14,35,"Аввакум сказал: господин! Вавилона я никогда не видал и рва не знаю. "));
rusuperobject.biblemap.put("b27_14_36", new rusuperobject(27,14,36,"Тогда Ангел Господень взял его за темя и, подняв его за волосы головы его, поставил его в Вавилоне над рвом силою духа своего. "));
rusuperobject.biblemap.put("b27_14_37", new rusuperobject(27,14,37,"И воззвал Аввакум и сказал: Даниил! Даниил! возьми обед, который Бог послал тебе. "));
rusuperobject.biblemap.put("b27_14_38", new rusuperobject(27,14,38,"Даниил сказал: вспомнил Ты обо мне, Боже, и не оставил любящих Тебя. "));
rusuperobject.biblemap.put("b27_14_39", new rusuperobject(27,14,39,"И встал Даниил и ел; Ангел же Божий мгновенно поставил Аввакума на его место. "));
rusuperobject.biblemap.put("b27_14_40", new rusuperobject(27,14,40,"В седьмой день пришел царь, чтобы поскорбеть о Данииле и, подойдя ко рву, взглянул в него, и вот, Даниил сидел. "));
rusuperobject.biblemap.put("b27_14_41", new rusuperobject(27,14,41,"И воскликнул царь громким голосом, и сказал: велик Ты, Господь Бог Даниилов, и нет иного кроме Тебя! "));
rusuperobject.biblemap.put("b27_14_42", new rusuperobject(27,14,42,"И приказал вынуть Даниила, а виновников его погубления бросить в ров, - и они тотчас были съедены в присутствии его."));

}

}